INSERT INTO `VWR INTERNAL Australian&African recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'DE9DB187D48ADF4F8215C3B1D3437480', X'4414A16D6BCF3A4EAE2E795EDC9EE002', X'4414A16D6BCF3A4EAE2E795EDC9EE002', 8, NULL, NULL, 2, NULL, 0, '2 cups basmati rice (rinsed to remove excess starch)');
INSERT INTO `VWR INTERNAL Australian&African recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'ED1F743A27E938429CF60CD2C7F058E7', X'4414A16D6BCF3A4EAE2E795EDC9EE002', X'4414A16D6BCF3A4EAE2E795EDC9EE002', 9, NULL, NULL, 2, NULL, 0, '1 cup coconut milk');
INSERT INTO `VWR INTERNAL Australian&African recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'6FF42A863118AD47AF815BE08C443B9E', X'4414A16D6BCF3A4EAE2E795EDC9EE002', X'4414A16D6BCF3A4EAE2E795EDC9EE002', 10, NULL, NULL, 2, NULL, 0, '2 cups vegetable stock or boiling water');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22B52470DB5B94469E824192EA565638', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37513BB3DF21414BAA34BA71B9B09DAF', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 1, NULL, NULL, 2, NULL, 0, 'Soak the skewers in water while you marinate the beef.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1000EE076A5D2F4887B6FDD88D94EA09', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 2, NULL, NULL, 2, NULL, 0, 'Slice the sirloin into thin strips and place them into a medium-sized bowl with the marinade mixture. Stir to coat the meat well. Cover and refrigerate for 2 hours.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C62D0E46C94B7C4181CE04BF9F592C16', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the satay sauce. Add the peanut butter to a small pot over a medium heat. Stir it until it melts. Add the red curry paste and stir well to combine.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F1018DD1194354CA3D626CF2DCA7E8E', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 4, NULL, NULL, 2, NULL, 0, 'Add the coconut milk to the pot and keep stirring. Turn the heat down low and add the sugar and fish sauce. Stir well.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7A80725DDF3EE46A6515D3ECE671022', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 5, NULL, NULL, 2, NULL, 0, 'Once the sauce thickens add the chicken stock or water and stir to combine. Turn the heat off and set the pot aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5981DEC21281504EB9686732B2F4D9D5', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 6, NULL, NULL, 2, NULL, 0, 'Skewer the beef. Reserve the marinade and set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B5B7CB056E60B4EB569F7052169AEC5', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 7, NULL, NULL, 2, NULL, 0, 'If you are using a barbecue to cook the satay then heat it to a medium-high heat. If you are frying the satay, then heat 1/2 tablespoon of vegetable oil in a griddle or fry pan over a medium-high heat.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CDF577BBC919748B6CB6ECE581D6711', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 8, NULL, NULL, 2, NULL, 0, 'Cook the skewers about 5 minutes, turning and brushing with marinade mixture until cooked.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'376FC47C81DE124CB29872F63452D76E', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 9, NULL, NULL, 2, NULL, 0, 'Meanwhile, when you are on to cooking your last few skewers, return the satay sauce pot to the stove and heat it up. Stir the sauce well and then serve in a small bowl.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BBBCA73E9430D498A40FF63BCC7E766', X'FDB9BB7708F5944D9F20D084C03FDB91', X'FDB9BB7708F5944D9F20D084C03FDB91', 10, NULL, NULL, 2, NULL, 0, 'Arrange beef satay on a serving plate and serve with a side of satay sauce for dipping. Serve shallots and cucumber in a small, separate serving bowl. They are eaten as an accompaniment to the beef.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A09D0C8B21D935478E669E1C73F03032', X'D102471EF60A3049875B223CCC870DD4', X'D102471EF60A3049875B223CCC870DD4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA690286F7B7AF4AA956058368D9B1E8', X'D102471EF60A3049875B223CCC870DD4', X'D102471EF60A3049875B223CCC870DD4', 1, NULL, NULL, 2, NULL, 0, 'Prepare the aioli by combining the garlic, cilantro, mayonnaise and lemon juice in a bowl. Stir well to combine then cover and set aside until serving time.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F8846AECFE96840BB7AD3B31F23DF7B', X'D102471EF60A3049875B223CCC870DD4', X'D102471EF60A3049875B223CCC870DD4', 2, NULL, NULL, 2, NULL, 0, 'Rinse whitebait under cold, running water and gently pat dry with paper towels.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24718BB49AE6E24DB0FE44DE7ABA73A1', X'D102471EF60A3049875B223CCC870DD4', X'D102471EF60A3049875B223CCC870DD4', 3, NULL, NULL, 2, NULL, 0, 'Place flour and sea salt in a bowl and mix well.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C9E461471AA7B4496BAB27F8A07CBCF', X'D102471EF60A3049875B223CCC870DD4', X'D102471EF60A3049875B223CCC870DD4', 4, NULL, NULL, 2, NULL, 0, 'Heat oil in a large saucepan until surface begins to shimmer. Test to see if the oil is ready by dropping a pinch of flour into it. The flour should fry immediately.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3567A9087EC119469522E84F1C369441', X'D102471EF60A3049875B223CCC870DD4', X'D102471EF60A3049875B223CCC870DD4', 5, NULL, NULL, 2, NULL, 0, 'Toss fish in seasoned flour, ensuring they are well coated. Remove and shake off excess flour, then deep-fry in batches for 40 seconds or until crisp and golden.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10494F4333966D4F9F3E871AFEDD094C', X'D102471EF60A3049875B223CCC870DD4', X'D102471EF60A3049875B223CCC870DD4', 6, NULL, NULL, 2, NULL, 0, 'Serve whitebait with a sprinkle of sea salt and aioli.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB4D968B2C5C72488A79ACB5B75F6C88', X'0FE3BDA3D411B848844BCA6084969CD8', X'0FE3BDA3D411B848844BCA6084969CD8', 0, NULL, NULL, 2, NULL, 0, 'Heat a small, heavy-based saucepan over medium heat. Add the sugar and macadamia nuts to the pan and stir.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF0B862AB9AA9843BF7ED67928B63CD1', X'0FE3BDA3D411B848844BCA6084969CD8', X'0FE3BDA3D411B848844BCA6084969CD8', 1, NULL, NULL, 2, NULL, 0, 'Continuing stirring gently until the sugar has melted and the macadamia nuts have caramelized.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7062FB661B25B8408047141148C8C0EE', X'0FE3BDA3D411B848844BCA6084969CD8', X'0FE3BDA3D411B848844BCA6084969CD8', 2, NULL, NULL, 2, NULL, 0, 'Carefully transfer nuts onto waxed paper. Allow to cool and then break into pieces. When completely cool, store in a tightly covered container.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E9000B6CE9A6140BCE099B92111649F', X'F3EFF1D9063A93408B8E5D5982D63CAE', X'F3EFF1D9063A93408B8E5D5982D63CAE', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 375 F (190 C).');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E53C23813F1FA489769AC8194009762', X'F3EFF1D9063A93408B8E5D5982D63CAE', X'F3EFF1D9063A93408B8E5D5982D63CAE', 1, NULL, NULL, 2, NULL, 0, 'Pat the chicken dry. Place lemon halves and bay leaves in the chicken cavity. Tie the legs together with kitchen string.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CA314A522A8EA41A2232EB471C23329', X'F3EFF1D9063A93408B8E5D5982D63CAE', X'F3EFF1D9063A93408B8E5D5982D63CAE', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the olive oil, rosemary, and salt. Rub mixture over the entire chicken.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54DFEF1A24484D41B743804138818082', X'F3EFF1D9063A93408B8E5D5982D63CAE', X'F3EFF1D9063A93408B8E5D5982D63CAE', 3, NULL, NULL, 2, NULL, 0, 'Place the chicken in a large baking dish. Set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EC1E91F953D354AB648A8136179E68B', X'F3EFF1D9063A93408B8E5D5982D63CAE', X'F3EFF1D9063A93408B8E5D5982D63CAE', 4, NULL, NULL, 2, NULL, 0, 'Bring the chicken stock, white wine and garlic cloves to a simmer in a medium saucepan. Pour the liquid and garlic cloves into the baking dish around the chicken. Cover baking dish with foil, crimping the edges tightly around the dish so that no steam can escape. Roast for 1 hour 20 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AF7A9B2DB55BE4FA5CC91FBCAAB07F1', X'F3EFF1D9063A93408B8E5D5982D63CAE', X'F3EFF1D9063A93408B8E5D5982D63CAE', 5, NULL, NULL, 2, NULL, 0, 'After this time, remove chicken from oven and remove the foil. Place chicken back in the oven, and roast for another 20 minutes or until the chicken is golden brown.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90ED1509BD1A41438B17B52323617588', X'F3EFF1D9063A93408B8E5D5982D63CAE', X'F3EFF1D9063A93408B8E5D5982D63CAE', 6, NULL, NULL, 2, NULL, 0, 'Remove chicken from the oven and loosely cover it in tinfoil. Let it rest for about 15 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3619FA98F56AFF4A9DAF58D7CD19C355', X'F3EFF1D9063A93408B8E5D5982D63CAE', X'F3EFF1D9063A93408B8E5D5982D63CAE', 7, NULL, NULL, 2, NULL, 0, 'Spoon the sauce from the baking dish into a saucepan, and keep it warm. You can strain some of the garlic cloves into the sauce, or just keep the cloves whole.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1C3CAE45CFDE648A050E1FDE1B944CE', X'F3EFF1D9063A93408B8E5D5982D63CAE', X'F3EFF1D9063A93408B8E5D5982D63CAE', 8, NULL, NULL, 2, NULL, 0, 'Serve chicken with garlic sauce on the side. Place a few garlic cloves on each plate so that guests can squeeze garlic onto the chicken if they wish.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA1BBFF8813E3E44902EAEDC4FD8E72E', X'706993D87E1B9143A24E51BA7F7CCA4A', X'706993D87E1B9143A24E51BA7F7CCA4A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 350 F/180 C. Place a baking tray in the oven.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D78DF3C145A2A346BDDD536A6B149070', X'706993D87E1B9143A24E51BA7F7CCA4A', X'706993D87E1B9143A24E51BA7F7CCA4A', 1, NULL, NULL, 2, NULL, 0, 'To make the pastry shell, use pastry to line the base and side of a fluted flan tin. Gently press the pastry up against the sides, making sure there are no air bubbles. Trim to fit and prick the base with a fork. Place in the freezer for 15 minutes or until firm.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8394ED4A18D474FA1C2797BC1AF033A', X'706993D87E1B9143A24E51BA7F7CCA4A', X'706993D87E1B9143A24E51BA7F7CCA4A', 2, NULL, NULL, 2, NULL, 0, 'Line the pastry with foil and add baking beads or uncooked rice to blind bake. Place the tin on the preheated baking tray in the oven and bake for 10 to 15 minutes or until golden. Remove from the oven.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BC31E68AB8C5A4AA40EEC1E2D6B92EC', X'706993D87E1B9143A24E51BA7F7CCA4A', X'706993D87E1B9143A24E51BA7F7CCA4A', 3, NULL, NULL, 2, NULL, 0, 'Carefully remove the foil and baking beads and set the pastry shell aside to cool.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB6B0FBA2DF3A44A9F2148A14BEC389B', X'706993D87E1B9143A24E51BA7F7CCA4A', X'706993D87E1B9143A24E51BA7F7CCA4A', 4, NULL, NULL, 2, NULL, 0, 'Spread out the vegetables in a large baking dish. Coat well in oil and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A53BB10FD3FE9E488244117783C0307D', X'706993D87E1B9143A24E51BA7F7CCA4A', X'706993D87E1B9143A24E51BA7F7CCA4A', 5, NULL, NULL, 2, NULL, 0, 'Roast in the oven for about 25 to 30 minutes until very slightly charred. Set aside and cool slightly, separating the onion layers.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAE0BE4467F1C54387E3B9A640D5E0AF', X'706993D87E1B9143A24E51BA7F7CCA4A', X'706993D87E1B9143A24E51BA7F7CCA4A', 6, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the eggs with the milk and cream. Stir in the cheeses, parsley, and salt. Transfer egg mixture to a jug.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB6FEBB96542E746A24978E955746E94', X'706993D87E1B9143A24E51BA7F7CCA4A', X'706993D87E1B9143A24E51BA7F7CCA4A', 7, NULL, NULL, 2, NULL, 0, 'Spread the roasted vegetables over the bottom of the pastry shell (which is still on the baking tray to catch any drips during baking). Carefully pour the egg mixture over top. Bake for 30 to 35 minutes or until just set.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0199B87F653A7429AFE1EBDECAA032F', X'706993D87E1B9143A24E51BA7F7CCA4A', X'706993D87E1B9143A24E51BA7F7CCA4A', 8, NULL, NULL, 2, NULL, 0, 'Let cool for a few minutes and serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E265A40FE28DF4FAC59F6AA03E28646', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB4C8FA2FFBE0142B0214D0CF42C4517', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F. Cut the white and sweet potatoes into 1-inch-thick slices.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBFA70189763DB43809147C67702D643', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 2, NULL, NULL, 2, NULL, 0, 'Cut the onions in half and then place all of the vegetables in a roasting pan in one even layer.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6D56DF8212E074F9A68A21030525988', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 3, NULL, NULL, 2, NULL, 0, 'Drizzle the vegetables with 2 tablespoons of the olive oil.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48E215F6B1886942A1A7D207BFABD4CD', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle with 1 teaspoon salt and toss to combine.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EBD85075054DD41A58AAEB0A41D68F8', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 5, NULL, NULL, 2, NULL, 0, 'Place the lamb on top of the vegetables and rub the lamb with the remaining 2 tablespoons olive oil.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'873A391742E8DC46B081A3DC7EA3DC0B', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle the lamb with the rest of the sea salt.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6D4551DA81B7D4FB75A1D6BF4536CF5', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 7, NULL, NULL, 2, NULL, 0, 'Use the point of a sharp knife to make small incisions all over the lamb.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE0AED9553C81A4F946D6E00314121FE', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 8, NULL, NULL, 2, NULL, 0, 'Place the garlic slivers and rosemary springs in the slits.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC14B5FB9B2FC04EBD36A5DD835F78A4', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 9, NULL, NULL, 2, NULL, 0, 'Place the roasting pan on the middle oven rack and roast for 1 1/2 hours, or until a meat thermometer placed in the thickest part of the lamb reads 140 F for rare, 160 F for medium, and 170 F for well done.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B01EAAA199A2694A97B7CCCFEDFEAE2D', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 10, NULL, NULL, 2, NULL, 0, 'Remove from the oven and transfer the lamb to a plate to rest.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93CD91BE15F15540B6F407B82E8247A5', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 11, NULL, NULL, 2, NULL, 0, 'Cover the lamb with aluminum foil and let it sit for 10 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'084B1E78492114408465ECF01BC3F5D1', X'967467719E9EB343B331A5CB7E70E1A8', X'967467719E9EB343B331A5CB7E70E1A8', 12, NULL, NULL, 2, NULL, 0, 'Serve the lamb with the roasted potatoes and onions, and mint sauce on the side.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D86D84B28EF7CE4094DEB765407F87B8', X'D5AE9C93F5C94946B3710CA44C04BFCD', X'D5AE9C93F5C94946B3710CA44C04BFCD', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl mix together the ground kangaroo meat, egg, bread crumbs, chopped onion, 1 tablespoon chopped parsley and the oregano. Form into 4 burgers. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A54EE5CF50659E4C863AB61069E868D8', X'D5AE9C93F5C94946B3710CA44C04BFCD', X'D5AE9C93F5C94946B3710CA44C04BFCD', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the mayonnaise, 2 tablespoons chopped parsley and garlic clove.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52E0AA04970E164E873C1223ACBEB618', X'D5AE9C93F5C94946B3710CA44C04BFCD', X'D5AE9C93F5C94946B3710CA44C04BFCD', 2, NULL, NULL, 2, NULL, 0, 'Heat grill to high. Put the burgers on the grill. Because the meat is so lean (only 2% fat), they won''t take long to cook, and like most game meats, you don''t want to over-cook them. Flip the burgers every 2 minutes to ensure even cooking. Depending on the thickness you''ve made them, the burgers will be done in 6 to 8 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D3E5704F7B2C5479EB003699876E3E8', X'D5AE9C93F5C94946B3710CA44C04BFCD', X'D5AE9C93F5C94946B3710CA44C04BFCD', 3, NULL, NULL, 2, NULL, 0, 'While the burgers are cooking, grill one side of the onion slices until grill marks appear. Flip the onion slices and top each with a piece of Camembert. Spread some of the mayonnaise on each bun. Put a burger on the bottom half of the bun. Top each burger with a Camembert-onion slice, and then the top half of the bun.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7C44A3AF30C28469587CA95F4E970A9', X'107F3FB27F136B4685D7738D1BAE5CE8', X'107F3FB27F136B4685D7738D1BAE5CE8', 0, NULL, NULL, 2, NULL, 0, 'Combine the marinade ingredients together in a large, non-reactive bowl. Add the lamb backstraps, and mix well to coat. Cover and refrigerate for at least 2 hours or up to 24 hours.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE569915A3504C43976E555AED27C923', X'107F3FB27F136B4685D7738D1BAE5CE8', X'107F3FB27F136B4685D7738D1BAE5CE8', 1, NULL, NULL, 2, NULL, 0, 'Prepare the cucumber yogurt just before cooking the lamb. Combine yogurt, cucumber, mint, lemon juice, and salt together in a small bowl. Stir well, and set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC40504C51ED2542BAF5BC2C01441288', X'107F3FB27F136B4685D7738D1BAE5CE8', X'107F3FB27F136B4685D7738D1BAE5CE8', 2, NULL, NULL, 2, NULL, 0, 'Heat grill or barbecue on a high heat. Cook lamb backstraps on all sides until medium rare - about 8 to 10 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF699BD450FAC44AACEE9160C7B23E10', X'107F3FB27F136B4685D7738D1BAE5CE8', X'107F3FB27F136B4685D7738D1BAE5CE8', 3, NULL, NULL, 2, NULL, 0, 'Transfer lamb to a plate and cover loosely with foil for 5 minutes. After this time slice the lamb diagonally and then serve with cucumber yogurt and two lemon wedges.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43234EFA545A2A47BF686ABEC394A587', X'D0F050C2B9B97E418A6EB8673DB50577', X'D0F050C2B9B97E418A6EB8673DB50577', 0, NULL, NULL, 2, NULL, 0, 'Boil some water in a large pot for the pasta. Cook the pasta according to packet instructions and have it ready around the same time as the clams or just a bit before.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1526A59102FD9A4FB033B93452B7AEC0', X'D0F050C2B9B97E418A6EB8673DB50577', X'D0F050C2B9B97E418A6EB8673DB50577', 1, NULL, NULL, 2, NULL, 0, 'Soak the clams in a bowl of cold water for about 10 minutes. Drain and rinse under cold running water.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBEA34AF10B2D34B8F5A5C08F640F119', X'D0F050C2B9B97E418A6EB8673DB50577', X'D0F050C2B9B97E418A6EB8673DB50577', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil and butter over medium heat in a large, heavy-based pot. Add the onion and fry for 2 to 3 minutes, stirring occasionally, until translucent. Add the garlic, chili flakes and salt and stir for about 20 seconds.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'927EA1FA7B8362428F8B5F2C011AA3B6', X'D0F050C2B9B97E418A6EB8673DB50577', X'D0F050C2B9B97E418A6EB8673DB50577', 3, NULL, NULL, 2, NULL, 0, 'Add the chopped tomatoes and fry for about 7 minutes until their juice starts to evaporate.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BB14D1F559C6E499208D78587FEB2D6', X'D0F050C2B9B97E418A6EB8673DB50577', X'D0F050C2B9B97E418A6EB8673DB50577', 4, NULL, NULL, 2, NULL, 0, 'Turn the heat up to medium-high and add the clams to the pot. Cover with a lid. After about 2 to 3 minutes add the wine to the pot. Cover again and shake the pot from side to side.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88B6D1426D710E48B7BE6E18C0B6C0EC', X'D0F050C2B9B97E418A6EB8673DB50577', X'D0F050C2B9B97E418A6EB8673DB50577', 5, NULL, NULL, 2, NULL, 0, 'Cook the clams for a further 5 minutes or until they open. Add the parsley and stir with a wooden spoon. Cover and shake pot again.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25D05FB4C368E94DBE2B610B6F81A25E', X'D0F050C2B9B97E418A6EB8673DB50577', X'D0F050C2B9B97E418A6EB8673DB50577', 6, NULL, NULL, 2, NULL, 0, 'Drain the pasta and then pour the clam mixture on top. Stir well and serve immediately.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C121DB4C00957749A0F2F0B6FE0643F4', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3541F66765607141930D9782E7920A0A', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 1, NULL, NULL, 2, NULL, 0, 'Prepare the marinade by grinding the garlic, mint, cumin, salt, and paprika with a mortar and pestle. Alternatively, you can use a food processor. Grind ingredients into a paste and then add the olive oil and lemon juice. Mix well.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B7D30DC68BB13429CC98E425EA84360', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 2, NULL, NULL, 2, NULL, 0, 'Make little incisions in the lamb and then coat it well with the marinade. Make sure you coat the inside and outside of the lamb thoroughly.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB02486D6B9EAA40B7F86978CB7DF32A', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 3, NULL, NULL, 2, NULL, 0, 'Place lamb in a bowl and cover. Place it in the fridge for at least 2 hours or even overnight.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C79B89858D23D74387AB42824E4E4FAD', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 4, NULL, NULL, 2, NULL, 0, 'Remove the lamb from the fridge 1 hour prior to cooking. Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E802803F0F0FEA40A8B3199C870C2EC6', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 5, NULL, NULL, 2, NULL, 0, 'Place the lamb in a baking dish and roast for 1 hour and 20 minutes for pink meat. Roast the lamb for an extra 20 minutes for medium to well-done meat.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8099F689B915014D9CFAF27F5B0A261C', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare the cucumber sauce. Mix together the yogurt, cucumber, garlic, lemon juice, mint, and cilantro. Add the salt and mix well. Set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D87F50082D20A54E836C1E052DBF3053', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 7, NULL, NULL, 2, NULL, 0, 'Prepare the couscous about 20 minutes before serving the lamb. Bring the vegetable stock to a boil in a small pot. Remove the pot from the heat and add the couscous. Cover and let it sit for about 5 minutes. Transfer to a serving bowl.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13D7E06A4ABED34A81600E6BDC0689E5', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 8, NULL, NULL, 2, NULL, 0, 'Add 1 tablespoon of olive oil to the couscous. Stir well with a fork. Add the curry powder and stir again. Next, add a pinch of salt.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4A3294982CDF34B9668F142412EDCCC', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 9, NULL, NULL, 2, NULL, 0, 'Add the raisins and pine nuts to the couscous and stir with the fork.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FD52D3A5F4C2746B853DD056D29358D', X'0DB5DEF19730904BAAD1152EB5B89E93', X'0DB5DEF19730904BAAD1152EB5B89E93', 10, NULL, NULL, 2, NULL, 0, 'Remove the lamb when ready and slice it. Spoon some of the liquid from the pan onto the lamb. Serve on a bed of couscous and spoon the yogurt sauce on top.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'576C3EBF8476DA4CA4B28BB9440D77C9', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 0, NULL, NULL, 2, NULL, 0, 'Place the potatoes in a large, heavy saucepan. Add enough cold water to fully cover the potatoes. Bring to a boil. Reduce heat and simmer, partially covered, until potatoes are tender; about 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13401EF3BFDAB047A582203B27B8ABA3', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 1, NULL, NULL, 2, NULL, 0, 'Drain the potatoes in a colander, then return to pan and add the milk, butter, and salt.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF7106E7D62978459E1DF49C62641FA2', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 2, NULL, NULL, 2, NULL, 0, 'Use an electric mixer to beat the potatoes until smooth and creamy. Alternatively, cream using a potato masher. Set aside to cool.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92945030F1FB154CBDF5B4894D2F9E27', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 3, NULL, NULL, 2, NULL, 0, 'To make the beef filling, heat the oil in a saucepan over medium-high heat. Add the onion and fry for 3 to 4 minutes or until soft and translucent.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A9CA84C7FA3844BAE7EF230E5E384D6', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 4, NULL, NULL, 2, NULL, 0, 'Add ground beef and cook for 3 to 4 minutes, stirring and breaking up with a wooden spoon until browned.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1C9F7F48FFB9441A44AC72F55468140', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 5, NULL, NULL, 2, NULL, 0, 'In a separate bowl, combine cornstarch and 1 tablespoon of beef stock and stir well. Set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF96C4CDADFC5145BEC0B7FD1457FE63', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 6, NULL, NULL, 2, NULL, 0, 'Add remaining beef stock, tomato paste, Worcestershire sauce, and Vegemite to beef. Stir well to combine. Add cornstarch mixture and stir. Bring to a boil. Reduce heat to low and simmer for 10 minutes or until thick. Remove from heat and cool.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4875ACB31CED824683B7FD00D7145B20', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 7, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 F / 220 C.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13704D5BC011B045BA19EC295EB224B9', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 8, NULL, NULL, 2, NULL, 0, 'Place pie tin, face-down, on the first pastry sheet and cut a circle that''s slightly larger than the tin (about 1/2 inch bigger)this is the pie base. Repeat process to make another 3 bases.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE8AFF7B88872F43A0892473EB9EFFDE', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 9, NULL, NULL, 2, NULL, 0, 'Press base pastry into pie tins and gently press pastry up the sides. Fill with meat mixture. Brush rims with water.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BAD914D1262D34286B2DEFD24A09066', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 10, NULL, NULL, 2, NULL, 0, 'Spoon mashed potato into a piping bag fitted with a nozzle and squeeze potato topping in a pinwheel motion on each pie to cover the meat filling.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3E048CF196EDB46A4195541834ABB00', X'315D753A50D9A44BAA6C5A65DA032673', X'315D753A50D9A44BAA6C5A65DA032673', 11, NULL, NULL, 2, NULL, 0, 'Place pies on a baking tray and bake for 20 minutes or until the potato filling is slightly golden. Serve with ketchup or mushy peas with additional gravy.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A860126D4EE034281260BD394CC8036', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350F.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F49D4DC7CCD064FA21E67CD9D0DA985', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 1, NULL, NULL, 2, NULL, 0, 'Lightly butter an 8 inch square cake tin. Set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D1D392A7AC43143859C217BDD0A426B', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, sift together the flour, baking powder, and salt. Set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A29F2B1474B31408B538CC4CDCE8FF7', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 3, NULL, NULL, 2, NULL, 0, 'In a separate bowl, use an electric beater to cream the butter and sugar mixture together until pale and fluffy.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C6DCA33AD68DE41AE1357EB2FC92BE9', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 4, NULL, NULL, 2, NULL, 0, 'Add the eggs one at a time to the butter/sugar mixture. Beat well after adding each egg.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8F28176D808724ABF36D51B9090B1B0', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 5, NULL, NULL, 2, NULL, 0, 'Add the vanilla to the mixture and mix well to combine.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AB8C18EFF8AFE4ABFB954F8BC58915B', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 6, NULL, NULL, 2, NULL, 0, 'Next, use a spatula to alternately add the flour mixture and milk, in three additions, starting and finishing with the flour.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28F87D028E4C624AABDDFA564C8F3FE5', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 7, NULL, NULL, 2, NULL, 0, 'Spread the batter into the cake tin, making sure it''s evenly spread.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CFE24C556C30E44A57B03E4B908FAF8', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 8, NULL, NULL, 2, NULL, 0, 'Bake in the oven for about 30 minutes. Test the center of the cake with a toothpick and make sure it comes out clean.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7215F2CE02EDD43AB43145EF97A9E76', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 9, NULL, NULL, 2, NULL, 0, 'Cool the cake in the tin for about five minutes and then invert it onto a wire rack to cool.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C89171F0B9CAD4AA1E93DE234EA66DF', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 10, NULL, NULL, 2, NULL, 0, 'Once the cake has cooled cut it into squares of the desired size and place them in an airtight container. Pop the container in the fridge for at least two hours or even overnight.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A656A7EC6078742AB6244AE096C7CD4', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 11, NULL, NULL, 2, NULL, 0, 'Now for the icing. Place the icing sugar, cocoa powder, butter and milk in a heatproof bowl over a saucepan of simmering water.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'410C162B2EAB9540BA017ACD496C6805', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 12, NULL, NULL, 2, NULL, 0, 'Stir the mixture until it is smooth but still a bit thick. You don''t want the liquid to get too thin otherwise the sponge cake won''t absorb the coating.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5017DEA66275D48B5A94889567FC947', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 13, NULL, NULL, 2, NULL, 0, 'Now it''s time to assemble the Lamingtons. Put out some newspaper under wire racks to catch any mess. Place the cake pieces on the racks and have your chocolate icing and desiccated coconut ready.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5B632217BED214EAAEFDBBF1DFCD3D6', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 14, NULL, NULL, 2, NULL, 0, 'Quickly coat the sponge cake on all sides in the icing mixture and then gently roll the cake in the coconut. Repeat the process.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32143BBB774F624E9135CA8D0D56938B', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', X'0D43EF1C1A4ECD4EA9EABCF522B59A9E', 15, NULL, NULL, 2, NULL, 0, 'The Lamingtons can be stored in an airtight container for five days.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'183F46FE9735794281BA94712DC7F1AD', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13C16860D3247D46B14E71C2C6B76D23', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', 1, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoon whipping cream and chocolate to a clean metal bowl. Place bowl on top of a saucepan filled with enough water to simmer for several minutes. Make sure the water doesn''t touch the bowl.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ABB345072C25D40A8AF348E7106ED10', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', 2, NULL, NULL, 2, NULL, 0, 'Heat gently and stir the chocolate cream mixture with a metal spoon until the chocolate is melted and smooth.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA144F4044901C45A5EEFBAE5041C543', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', 3, NULL, NULL, 2, NULL, 0, 'Remove the chocolate mixture from the stove to cool just slightly. Add the egg yolk to the chocolate mixture while stirring until combined and smooth. The residual heat from the chocolate will cook the egg yolk. Place the bowl over the saucepan filled with hot water to keep the chocolate warm but don''t turn the heat back on.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C4271E343A0F544A3DACB3F2A150D91', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', 4, NULL, NULL, 2, NULL, 0, 'Whip the whipping cream until soft peaks form. Add half of the melted chocolate to the whipped cream with the metal spoon and fold until just combined. Fold in the remaining chocolate mixture but be careful not to over mix or deflate the volume. Set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F80F6BE96D8E37448447F22C1CEF61DD', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', 5, NULL, NULL, 2, NULL, 0, 'Use a clean electric mixer to beat the egg white until soft peaks form and then fold it into the chocolate mousse mixture. Spoon mousse into serving glasses. Cover and refrigerate for 2 hours.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DB441C7ECE20745AACCF4DD8BB075B3', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', 6, NULL, NULL, 2, NULL, 0, 'Serve with chocolate shavings or fresh raspberries.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'915B4ABF1798754FB0C3CC28692FBB6E', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', X'2EE4FFEC736CF5458A4B0CC1F5DCF413', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'603FAAA952116B418C890DD4DAF528EF', X'8EAE425A00BF8547AEA20EB2AC4A040E', X'8EAE425A00BF8547AEA20EB2AC4A040E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C588FA0F76D73A4FB7FAC2A67B823289', X'8EAE425A00BF8547AEA20EB2AC4A040E', X'8EAE425A00BF8547AEA20EB2AC4A040E', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F/180 C. Grease an 8- by 4-inch loaf pan with a little butter. Set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89176C89602B594CA4149DE223D57EAF', X'8EAE425A00BF8547AEA20EB2AC4A040E', X'8EAE425A00BF8547AEA20EB2AC4A040E', 2, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, beat the butter and sugar with an electric mixer until pale and fluffy. Add eggs one at a time beating well after each addition.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5190E6ECE126D24B9B1975A172084BAB', X'8EAE425A00BF8547AEA20EB2AC4A040E', X'8EAE425A00BF8547AEA20EB2AC4A040E', 3, NULL, NULL, 2, NULL, 0, 'Use a metal spoon to fold in the mashed bananas and yogurt.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F821B716D6BEA145A935CDAF96CA9E11', X'8EAE425A00BF8547AEA20EB2AC4A040E', X'8EAE425A00BF8547AEA20EB2AC4A040E', 4, NULL, NULL, 2, NULL, 0, 'Sift together the flour and baking powder, then fold gently into the banana/yogurt mixture until just combined. Don''t over mix.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E0FB1B1211B1A44AEBA8EFBCB09D9D4', X'8EAE425A00BF8547AEA20EB2AC4A040E', X'8EAE425A00BF8547AEA20EB2AC4A040E', 5, NULL, NULL, 2, NULL, 0, 'Spoon the mixture into the greased pan and bake for 50 to 60 minutes or until a skewer inserted into the middle of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE3225C566CEA6448D80D67CAC1FABC2', X'8EAE425A00BF8547AEA20EB2AC4A040E', X'8EAE425A00BF8547AEA20EB2AC4A040E', 6, NULL, NULL, 2, NULL, 0, 'Remove cake from the oven and allow it to stand for 5 minutes in the pan. Then gently turn the cake out of the tin and cool on a wire rack.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CDBB2FF9B01614F8D3028D4F60EBB44', X'8EAE425A00BF8547AEA20EB2AC4A040E', X'8EAE425A00BF8547AEA20EB2AC4A040E', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9063465E2AA5314F835DA85DF8AD6FDB', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 150C/300F.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04AC9DBCEA92224EAAF65D531ADDE377', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 1, NULL, NULL, 2, NULL, 0, 'Lightly grease an 11-inch x 7-inch rectangular cake tin. Line tin with baking paper and allow the paper to hang over the edges a little to facilitate the cakes removal once its baked.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9DE7DBDF24B2240988EB0299C7D6D5A', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 2, NULL, NULL, 2, NULL, 0, 'To make the cake: In a mixing bowl cream together the butter and sugar until light and fluffy. Add three egg yolks, one at a time, beating well after each addition.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C7A1D7CA6CFD24FB0FF63F82BA8C9DD', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 3, NULL, NULL, 2, NULL, 0, 'Sift flour and baking powder together. Fold into the creamed butter-sugar mixture. The dough will seem quite crumbly, but this is normal. Press dough into the bottom of the lined cake tin.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4E47A381178AE4595B3A4F89D139855', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 4, NULL, NULL, 2, NULL, 0, 'To make the coconut-meringue topping: In a clean bowl, use an electric mixer to beat the three egg whites until they form soft peaks.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F68D374897F138498C63420D25D1A42B', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 5, NULL, NULL, 2, NULL, 0, 'Gradually add 1/4 cup castor sugar, one tablespoon at a time, while continuing to beat the whites until they form stiff, glossy peaks. Use a spatula to gently fold in the desiccated coconut and vanilla essence (extract).');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96D8826C5F6B5B4BA9B560AF0AD147B6', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 6, NULL, NULL, 2, NULL, 0, 'Using a spatula, spread a thin layer of jam over the dough in the pan.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C943A8E0902FAA41864A3D3AAF501979', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 7, NULL, NULL, 2, NULL, 0, 'Next, spoon and spread the coconut meringue over the jam, making sure the meringue completely covers the jam.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2FB404CE2FA8541A115FC660C3BE5F3', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 8, NULL, NULL, 2, NULL, 0, 'Bake for 20 to 25 minutes or until the meringue top is a soft pink eggshell color. It is normal for the meringue to crack, so don''t panic.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3709122E17DDA947BE47ECE63DF453FD', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 9, NULL, NULL, 2, NULL, 0, 'Remove from the oven and cool in tin for two to three minutes. Carefully remove the cake from the tin by holding onto the baking paper and gently lifting. Cool on a wire rack.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FC649C473113D4694F776100476A5C5', X'F323AA5C7066DD4B88DED779B4000ACF', X'F323AA5C7066DD4B88DED779B4000ACF', 10, NULL, NULL, 2, NULL, 0, 'Once cooled, cut cake into squares and serve topped with fresh raspberries, if desired. The Louise Cake can be kept in an airtight container for up to one week.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14934C5563CC024E8AFF6FBC2293ADDD', X'4207153F9F55704AA7EFCEE4D1DF9348', X'4207153F9F55704AA7EFCEE4D1DF9348', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6DF47FE99D5C848AA424E0674EF71B9', X'4207153F9F55704AA7EFCEE4D1DF9348', X'4207153F9F55704AA7EFCEE4D1DF9348', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F/180 C. Line a baking sheet with baking paper. Set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C9CC0D0E5105648B44DD0C40B57E03A', X'4207153F9F55704AA7EFCEE4D1DF9348', X'4207153F9F55704AA7EFCEE4D1DF9348', 2, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'002D8314ED59C5438A402B8DFE9F2AB2', X'4207153F9F55704AA7EFCEE4D1DF9348', X'4207153F9F55704AA7EFCEE4D1DF9348', 3, NULL, NULL, 2, NULL, 0, 'Sift together the flour and cocoa powder and mix into butter mixture with a wooden spoon.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE9A0CFEB5023B44B7669DA7960B4125', X'4207153F9F55704AA7EFCEE4D1DF9348', X'4207153F9F55704AA7EFCEE4D1DF9348', 4, NULL, NULL, 2, NULL, 0, 'Fold in cornflakes and don''t worry if they crumble.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F2F545581A9FD4BB42FE84A21C56FDE', X'4207153F9F55704AA7EFCEE4D1DF9348', X'4207153F9F55704AA7EFCEE4D1DF9348', 5, NULL, NULL, 2, NULL, 0, 'Roll or press 1 1/2 teaspoons of the dough into balls and flatten them slightly. Place them about 2 inches apart on the baking sheet.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D28312497A16D840B89E9873F29B99A3', X'4207153F9F55704AA7EFCEE4D1DF9348', X'4207153F9F55704AA7EFCEE4D1DF9348', 6, NULL, NULL, 2, NULL, 0, 'Bake in the oven for 10 to 15 minutes. Remove from oven, and cool on a wire rack.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9413DBE234DF9A47AD18C3B8D821301A', X'4207153F9F55704AA7EFCEE4D1DF9348', X'4207153F9F55704AA7EFCEE4D1DF9348', 7, NULL, NULL, 2, NULL, 0, 'Prepare the icing by combining the icing sugar, unsweetened cocoa powder, and water in a bowl.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F96C7EDF235BB48A2BF0B4EAC5819D5', X'4207153F9F55704AA7EFCEE4D1DF9348', X'4207153F9F55704AA7EFCEE4D1DF9348', 8, NULL, NULL, 2, NULL, 0, 'Mix well until the mixture is free of lumps and of a creamy consistency.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5662F93109DE8A4AA38990418810A4F6', X'4207153F9F55704AA7EFCEE4D1DF9348', X'4207153F9F55704AA7EFCEE4D1DF9348', 9, NULL, NULL, 2, NULL, 0, 'Spoon a little icing on each cookie, and decorate with flaked almonds.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E745B81FC2E8784DB53C30864EF5247E', X'62282E60067A144BAEE8DE1A53ED84DB', X'62282E60067A144BAEE8DE1A53ED84DB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F. Line a shallow jelly roll pan with parchment paper.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'939B449989C4E042A73BFD495339D1AA', X'62282E60067A144BAEE8DE1A53ED84DB', X'62282E60067A144BAEE8DE1A53ED84DB', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle cream of tartar over egg whites and beat until stiff. Whisk together sugar and cornstarch. Add the mixture to the egg whites a tablespoon at a time while beating on medium speed until all is combined. The meringue should be shiny, white, and stiff. Fold in vanilla extract.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DD142F9D6D7CC448312496C7EF1B977', X'62282E60067A144BAEE8DE1A53ED84DB', X'62282E60067A144BAEE8DE1A53ED84DB', 2, NULL, NULL, 2, NULL, 0, 'Reduce oven heat to 200 F. Mound the meringue on the pan in a round shape about 6 inches in diameter. Bake for 1 hour. Turn off oven, leaving meringue in oven until it is cold, or overnight.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C7CF8E21F91C8429CF89CA69BF8C675', X'62282E60067A144BAEE8DE1A53ED84DB', X'62282E60067A144BAEE8DE1A53ED84DB', 3, NULL, NULL, 2, NULL, 0, 'Arrange fruit over the base of the pavlova meringue, top with whipped cream and sprinkle with chips, crushed or small candies and/or additional fruit.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F623CBD20475B489E8986CEAC0F2B88', X'62282E60067A144BAEE8DE1A53ED84DB', X'62282E60067A144BAEE8DE1A53ED84DB', 4, NULL, NULL, 2, NULL, 0, 'Use a serrated knife to gently saw slices. Store leftovers in a covered container.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE7F0542F755CE4FBCFE73ED80533703', X'52F7E5595481FA4B80CC22957F175819', X'52F7E5595481FA4B80CC22957F175819', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4186869973405488503634458D7A59D', X'52F7E5595481FA4B80CC22957F175819', X'52F7E5595481FA4B80CC22957F175819', 1, NULL, NULL, 2, NULL, 0, 'Line a baking tray with waxed parchment paper. Set aside');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF12495656B120438F1A99539F740D3F', X'52F7E5595481FA4B80CC22957F175819', X'52F7E5595481FA4B80CC22957F175819', 2, NULL, NULL, 2, NULL, 0, 'Heat the 5 tablespoons of sugar and 2 tablespoons golden syrup or light corn syrup in a small saucepan over medium heat.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97A35A8B44B5A745A36C69A6E90B7820', X'52F7E5595481FA4B80CC22957F175819', X'52F7E5595481FA4B80CC22957F175819', 3, NULL, NULL, 2, NULL, 0, 'Melt the mixture and stir occasionally. Bring to a boil for 5 or 6 minutesmake sure you stir continuously so the syrup doesn''t burn.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BE49A0A4AEEA84CAE8C65161180ADEC', X'52F7E5595481FA4B80CC22957F175819', X'52F7E5595481FA4B80CC22957F175819', 4, NULL, NULL, 2, NULL, 0, 'Take the pot off the stove and stir in the baking soda (bicarbonate of soda). The mixture will froth up and become pale. Use a spatula to scrape and pour the mixture onto the waxed parchment paper.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'283A487056B73448BB3DA4FC383ECD37', X'52F7E5595481FA4B80CC22957F175819', X'52F7E5595481FA4B80CC22957F175819', 5, NULL, NULL, 2, NULL, 0, 'Allow the honeycomb to set for 30 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B20F556AAC6AE439A12741D5B99F273', X'52F7E5595481FA4B80CC22957F175819', X'52F7E5595481FA4B80CC22957F175819', 6, NULL, NULL, 2, NULL, 0, 'Once set, wrap the honeycomb loosely in parchment paper and use a rolling pin to break it into small pieces.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65A9D6A84DF0EF43A596128B77B6D270', X'52F7E5595481FA4B80CC22957F175819', X'52F7E5595481FA4B80CC22957F175819', 7, NULL, NULL, 2, NULL, 0, 'Scoop ice cream into two serving bowls and sprinkle with hokey pokey honeycomb. Serve immediately.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6A22B1037090447A466C61559AF669B', X'52F7E5595481FA4B80CC22957F175819', X'52F7E5595481FA4B80CC22957F175819', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7576068BA269B46B9DB56CA68258118', X'5AF97D70644DBC4F8E4F1C2E68976494', X'5AF97D70644DBC4F8E4F1C2E68976494', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BDEB1033F41914DA60A08B50A5B258F', X'5AF97D70644DBC4F8E4F1C2E68976494', X'5AF97D70644DBC4F8E4F1C2E68976494', 1, NULL, NULL, 2, NULL, 0, 'Sterilize two 1-quart jars and lids by boiling in water for 2 minutes. Let air dry on a clean, lint-free dish towel and set aside until needed.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6207C919577714792D2DAD74BD0C65C', X'5AF97D70644DBC4F8E4F1C2E68976494', X'5AF97D70644DBC4F8E4F1C2E68976494', 2, NULL, NULL, 2, NULL, 0, 'Use a small, sharp knife to cut a small X shape at the base of each peach. Place peaches in a large, deep heatproof bowl and cover with boiling water. Allow peaches to sit for 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37132243E0D7A0439EF857EEBA78E63B', X'5AF97D70644DBC4F8E4F1C2E68976494', X'5AF97D70644DBC4F8E4F1C2E68976494', 3, NULL, NULL, 2, NULL, 0, 'Using tongs, transfer one peach to a plate. Try to peel the skin off. If it comes off easily, then drain and peel the remaining peaches. If not, let them sit in the boiling water for a further minute or so.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'612FA183C8CFD648A12B83206B89D9C2', X'5AF97D70644DBC4F8E4F1C2E68976494', X'5AF97D70644DBC4F8E4F1C2E68976494', 4, NULL, NULL, 2, NULL, 0, 'Peel the peaches and cut in half and remove the pits. Cut each half into 4 pieces, so each peach yields 8 slices.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2D595EB4F21DD4B9CDB53114C025E00', X'5AF97D70644DBC4F8E4F1C2E68976494', X'5AF97D70644DBC4F8E4F1C2E68976494', 5, NULL, NULL, 2, NULL, 0, 'Arrange peaches in layers in the jars alternating layers of caster sugar. Place half of a split vanilla bean down the side of each jar.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A28E8C922C54842B74638F130B61E56', X'5AF97D70644DBC4F8E4F1C2E68976494', X'5AF97D70644DBC4F8E4F1C2E68976494', 6, NULL, NULL, 2, NULL, 0, 'Pour over the brandy making sure the peaches are completely covered. Seal tightly then shake gently to help dissolve the sugar.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7EDF8F2B2C0DF48BA6CAE6058F5CDCF', X'5AF97D70644DBC4F8E4F1C2E68976494', X'5AF97D70644DBC4F8E4F1C2E68976494', 7, NULL, NULL, 2, NULL, 0, 'Place jars in a cool, dark place for 3 months. Make sure you shake the jar gently once a week.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4C24F8542C6F546BE111393F235B21C', X'5AF97D70644DBC4F8E4F1C2E68976494', X'5AF97D70644DBC4F8E4F1C2E68976494', 8, NULL, NULL, 2, NULL, 0, 'After 3 months serve brandied peaches with pancakes, ice cream or enjoy with a dollop of clotted cream for dessert.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5608937A2C3DC6419F04F8453C27BE52', X'5AF97D70644DBC4F8E4F1C2E68976494', X'5AF97D70644DBC4F8E4F1C2E68976494', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB3EE0111C04EE4AAC5014FAB66F74AA', X'DB751DE41356B040BF26D000237E8D06', X'DB751DE41356B040BF26D000237E8D06', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15E5D9C8E5623D48B8900C031E62A1E6', X'DB751DE41356B040BF26D000237E8D06', X'DB751DE41356B040BF26D000237E8D06', 1, NULL, NULL, 2, NULL, 0, 'Lightly grease a baking sheet and set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C867C804741FB4E87F7D5C69B63F2C3', X'DB751DE41356B040BF26D000237E8D06', X'DB751DE41356B040BF26D000237E8D06', 2, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, cream together the butter and sugar until pale and fluffy?for about 2 minutes. Add the egg and continue to mix until combined and smooth.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88DC1B581E3FA0449753590589031D5C', X'DB751DE41356B040BF26D000237E8D06', X'DB751DE41356B040BF26D000237E8D06', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, sift together the flour, baking powder, and salt. Add about 1/2 cup of flour mixture to the butter mixture, stirring continuously with a wooden spoon until smooth. Add the remaining flour mixture and stir until smooth.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'400848C0572E9D4EA8FBCB663D831A82', X'DB751DE41356B040BF26D000237E8D06', X'DB751DE41356B040BF26D000237E8D06', 4, NULL, NULL, 2, NULL, 0, 'Add the ground almonds and vanilla essence and stir until combined.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51C98828F4C9C3439DC8B98181419D60', X'DB751DE41356B040BF26D000237E8D06', X'DB751DE41356B040BF26D000237E8D06', 5, NULL, NULL, 2, NULL, 0, 'Roll small balls of dough and place them on the baking sheet about 1 inch (2.5cm) apart.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F18B2BD937CE44483FED813F35CFA80', X'DB751DE41356B040BF26D000237E8D06', X'DB751DE41356B040BF26D000237E8D06', 6, NULL, NULL, 2, NULL, 0, 'Dip your thumb in some flour and then gently press down on the dough to make well in the middle of each cookie.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9355D23D1EF11C4FB62923757CD5B60C', X'DB751DE41356B040BF26D000237E8D06', X'DB751DE41356B040BF26D000237E8D06', 7, NULL, NULL, 2, NULL, 0, 'Spoon a small amount of jam into each well. Bake the cookies for 10 minutes or until they are a light golden color. Remove from oven and cool on a wire rack. Serve.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF804A7E52776D48B56C4948BF6D9481', X'DB751DE41356B040BF26D000237E8D06', X'DB751DE41356B040BF26D000237E8D06', 8, NULL, NULL, 2, NULL, 0, 'Store cookies in an airtight container for up to 2 weeks.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75EA222E0DFB4349A8907776DDC112A6', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13174674F5F76B409E4802775D9C06F7', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', 1, NULL, NULL, 2, NULL, 0, 'Place the milk in a small saucepan over medium-low heat. Add the vanilla extract and stir.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'093C6A14DC3F5B4FB839587ECA0B60D4', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', 2, NULL, NULL, 2, NULL, 0, 'Once the milk starts to steam, sprinkle in the rice and stir gently with a wooden spoon. Turn the heat up slightly and cook for 5 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92F88A7FB4AB234EBF389FD18873BB54', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', 3, NULL, NULL, 2, NULL, 0, 'Once the rice grains start to swell, place a lid on the pot and lower the heat. Keep an eye on the rice and stir occasionally for 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB6CD3985945D641AAC0E6E51CE9BD94', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', 4, NULL, NULL, 2, NULL, 0, 'Once the rice is cooked, remove the saucepan from heat and add the sugar, lemon zest, and a pinch of salt. Stir gently and taste. Add a little more sugar if necessary.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB492C4562517B4EA1EFB0CB5CC89AAA', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', X'870EC52FA81FE14EB67A6AAD8CE9D9AE', 5, NULL, NULL, 2, NULL, 0, 'Serve hot, at room temperature, or chilled. Sprinkle with cinnamon just before serving.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71A0960673555A4AA2D80DEF7834FCDC', X'E39F43462E660D4784FEEF0656BF36C8', X'E39F43462E660D4784FEEF0656BF36C8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F9AAE07A693C942ACD87ACCB889AA88', X'E39F43462E660D4784FEEF0656BF36C8', X'E39F43462E660D4784FEEF0656BF36C8', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F. Line a baking tray with parchment paper and set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BFE7AA0584C0B46AF5EAE98A11F4017', X'E39F43462E660D4784FEEF0656BF36C8', X'E39F43462E660D4784FEEF0656BF36C8', 2, NULL, NULL, 2, NULL, 0, 'In a small saucepan, melt 12 1/2 tablespoons butter over medium heat. Add 1 tablespoon golden syrup and 1 teaspoon baking soda to the butter and stir well. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C45D8868AD822A4CA1977F704CA0496A', X'E39F43462E660D4784FEEF0656BF36C8', X'E39F43462E660D4784FEEF0656BF36C8', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, mix together 1 cup all-purpose flour, 1 cup rolled oats, 1 cup sugar, 1 cup desiccated coconut and 1/4 teaspoon salt.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E12DE9B731081A4BA09F9B5A3B86D080', X'E39F43462E660D4784FEEF0656BF36C8', X'E39F43462E660D4784FEEF0656BF36C8', 4, NULL, NULL, 2, NULL, 0, 'Add the melted butter-syrup-soda mixture to the dry ingredients and stir with a wooden spoon to combine ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1638F7F917B7DA448A8360DFF5B801F3', X'E39F43462E660D4784FEEF0656BF36C8', X'E39F43462E660D4784FEEF0656BF36C8', 5, NULL, NULL, 2, NULL, 0, 'Roll about 1 1/2 tablespoons of dough into small balls and flatten between the palms of your hands. Place dough on baking tray about 1.5 inches apart to allow room for spreading.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'444163ECDB29604981DAC89C73E4F72A', X'E39F43462E660D4784FEEF0656BF36C8', X'E39F43462E660D4784FEEF0656BF36C8', 6, NULL, NULL, 2, NULL, 0, 'Bake the biscuits for about 10 to 12 minutes or until golden brown. Remove the biscuits from the oven and let them sit on the baking tray for 5 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E04840D007B7E45BF7CC8FF4170A29D', X'E39F43462E660D4784FEEF0656BF36C8', X'E39F43462E660D4784FEEF0656BF36C8', 7, NULL, NULL, 2, NULL, 0, 'Remove the biscuits from the tray and cool on a wire rack. Store cooled biscuits in an airtight container.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D341ED49D27D294A931BAA5D66EE0B1F', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D08767A8AD7D46428C4A2DA85500EB29', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 340 F/ 170 C.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'077D4A4F25ADEB4091C6809826D969D3', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 2, NULL, NULL, 2, NULL, 0, 'In a large saut pan, fry the onions, garlic, and ginger in the butter until soft and golden brown.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'212D1D0D611727499AC07E61F44A0131', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 3, NULL, NULL, 2, NULL, 0, 'Add the spices, chutney, minced beef, vinegar, Worcestershire sauce, and stock cube.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E5DF44CD45FAF44844DDD6C2B51A202', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 4, NULL, NULL, 2, NULL, 0, 'When the mince has browned, add the pre-soaked bread and work into the mixture.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5373D10935704E419636E9DCD867750B', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 5, NULL, NULL, 2, NULL, 0, 'Transfer the mixture to a baking dish and bake, covered, in the oven for 40 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A1B48FEACE053498D4822AE48A73A0C', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, beat the egg, milk, and turmeric to make the savory custard mix.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB12E28E42FC0F46915D2E9EB4CAE32C', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 7, NULL, NULL, 2, NULL, 0, 'Add cream for an extra rich custard topping.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDF28955EDA98042AEFC8CCA9132E4D6', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 8, NULL, NULL, 2, NULL, 0, 'Season with a little salt and pepper.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'371418B9FAD36B42928963CF4F8E1480', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 9, NULL, NULL, 2, NULL, 0, 'Remove the mince mixture from the oven, uncover, then pour the egg mix over.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85898C0EAB6F4047A7E779545F8FF6A6', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 10, NULL, NULL, 2, NULL, 0, 'Arrange the bay leaves on top then return to the oven for a further 15 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCA7227DE3DA1A4C81D083E22F2C78BF', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 11, NULL, NULL, 2, NULL, 0, 'The bay leaves should be fragrant and the surface of the custard, golden brown.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'846B712E589C124B8EB9ED5F34184D42', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 12, NULL, NULL, 2, NULL, 0, 'Serve with rice and top with additional chutney, if desired.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A5C669AF1AFFC4B99A0F1B70CE338AD', X'6E63DFAD85DDBA4795F45DBBAE092C54', X'6E63DFAD85DDBA4795F45DBBAE092C54', 13, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A780DF4EB3941499813E0F87C36DA37', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FEF8CBB22538145A12BC03C4F33F316', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 1, NULL, NULL, 2, NULL, 0, 'Soak the dried apricots whole in just enough boiling water to cover them; allow to soak for about an hour. This allows them to rehydrate and become plump.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE4DD6460CAE7348A0ED822B2BE80709', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 2, NULL, NULL, 2, NULL, 0, 'Chop them into chunks, reserving the water to aid in dissolving the sugar.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88B91A79CB96894494CEA17AC07C9FBD', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 3, NULL, NULL, 2, NULL, 0, 'Blanch the peaches in boiling water and then place them into a pot of cold water in order to remove the skins.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4495C910BA4CD14FA9BF921891634736', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 4, NULL, NULL, 2, NULL, 0, 'Chop into large chunks.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4589C1ACE93FBA49A54B0993CB508BA0', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 5, NULL, NULL, 2, NULL, 0, 'Chop or dice the onions.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CDB991FDF0518468678C8183C57E5D4', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 6, NULL, NULL, 2, NULL, 0, 'Place all ingredients into a pot and heat gently for 20 minutes. (This action primarily allows the sugar to dissolve thoroughly.)');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEC0E53D5C393D4B8C961F642B0C55D7', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 7, NULL, NULL, 2, NULL, 0, 'Now allow the chutney to simmer at medium heat for about one hour without covering and stirring occasionally. Do not worry if the mixture still appears to be runny; it will thicken once cooled.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0705EB4D7395F34582D2580CB049C10D', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 8, NULL, NULL, 2, NULL, 0, 'Once ready, allow it to cool for 10 to 15 minutes prior to bottling into hot, sterilized jars.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3C11EBB8516E74B891E6F068A5D5F86', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 9, NULL, NULL, 2, NULL, 0, 'Keep sealed and allow to mature for two weeks to a month prior to consuming.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E079067F995994C8CB9E4FE677CE8B2', X'816312F18F83994F974FB7EBE1649246', X'816312F18F83994F974FB7EBE1649246', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A023F42721687846A5D0099D9C290609', X'AF44EF9B6E950B4D8483C0E7137EAEBB', X'AF44EF9B6E950B4D8483C0E7137EAEBB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'136A54E966D6694C8E8DBBE194FE884A', X'AF44EF9B6E950B4D8483C0E7137EAEBB', X'AF44EF9B6E950B4D8483C0E7137EAEBB', 1, NULL, NULL, 2, NULL, 0, 'Place the fermented corn or millet dough into a medium pot.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C148F8F2DF21374FAE147547B45EBE9C', X'AF44EF9B6E950B4D8483C0E7137EAEBB', X'AF44EF9B6E950B4D8483C0E7137EAEBB', 2, NULL, NULL, 2, NULL, 0, 'Add 1 cup of cold water and crumble up the fermented dough to make a smooth paste. Place over high heat and bring to a simmer, stirring occasionally.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'856633F5C2B7F946BA62B5D4ED781E6A', X'AF44EF9B6E950B4D8483C0E7137EAEBB', X'AF44EF9B6E950B4D8483C0E7137EAEBB', 3, NULL, NULL, 2, NULL, 0, 'Add 2 cups of boiling water to the pot and bring to a boil, stirring continuously. At this point, the mixture may form lumps. Whisk lumps out to keep the porridge smooth.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC7F61DED7DFE24D878BBC630B16C506', X'AF44EF9B6E950B4D8483C0E7137EAEBB', X'AF44EF9B6E950B4D8483C0E7137EAEBB', 4, NULL, NULL, 2, NULL, 0, 'Add a pinch of salt, the ground cloves, ginger, and chili powder. Stir and allow to simmer for 10 minutes over low heat.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12676600BF18CB4481D43A70ECE66723', X'AF44EF9B6E950B4D8483C0E7137EAEBB', X'AF44EF9B6E950B4D8483C0E7137EAEBB', 5, NULL, NULL, 2, NULL, 0, 'When ready to serve, add the desired amount of sugar, stir and enjoy.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4973958B03DAB34BB19684BF38EE54EA', X'D31E460FF8D3844D9A3CFB05869FCA6B', X'D31E460FF8D3844D9A3CFB05869FCA6B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0115CD229202D3409E3329D8F56C1D69', X'D31E460FF8D3844D9A3CFB05869FCA6B', X'D31E460FF8D3844D9A3CFB05869FCA6B', 1, NULL, NULL, 2, NULL, 0, 'In a clean bowl, mix the flour, sugar, yeast, and salt (if using) together.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD38C2528FD0A84AA276B1E533E6E5B3', X'D31E460FF8D3844D9A3CFB05869FCA6B', X'D31E460FF8D3844D9A3CFB05869FCA6B', 2, NULL, NULL, 2, NULL, 0, 'Add the warm water and vegetable oil and begin to mix into a soft dough.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1845D701FAE0754B85FD5607AED7E375', X'D31E460FF8D3844D9A3CFB05869FCA6B', X'D31E460FF8D3844D9A3CFB05869FCA6B', 3, NULL, NULL, 2, NULL, 0, 'Once the dough has been mixed very well, cover with a tea towel and set aside to rise for an hour. There''s no need to knead the dough.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C58FD3C00589B84BA71D5E232A0F0A9C', X'D31E460FF8D3844D9A3CFB05869FCA6B', X'D31E460FF8D3844D9A3CFB05869FCA6B', 4, NULL, NULL, 2, NULL, 0, 'After an hour, the dough should have doubled in size. Mix the dough againthis helps to achieve softer dough. After mixing, allow the dough to rest for a further 10 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10C919E3F6E45946B9639BBE19DB464C', X'D31E460FF8D3844D9A3CFB05869FCA6B', X'D31E460FF8D3844D9A3CFB05869FCA6B', 5, NULL, NULL, 2, NULL, 0, 'In the meantime, heat up enough vegetable oil in a pot for deep frying over medium heat.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93365DA4F195B4448C99F49C945D5D73', X'D31E460FF8D3844D9A3CFB05869FCA6B', X'D31E460FF8D3844D9A3CFB05869FCA6B', 6, NULL, NULL, 2, NULL, 0, 'Scoop out some batter with a large spoon and drop it into the hot oil. Use a second spoon to push the dough into the hot oil if needed. Ensure the oil is being heated under a medium flame to prevent the magwinya from browning too quickly without fully cooking on the inside. Fry the doughnuts until golden brown, turning halfway to ensure even browning.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C5A80B40780334887CA1EFC035BD66A', X'D31E460FF8D3844D9A3CFB05869FCA6B', X'D31E460FF8D3844D9A3CFB05869FCA6B', 7, NULL, NULL, 2, NULL, 0, 'Once ready, scoop the magwinya out of the pot and place onto absorbent kitchen paper to drain the excess oil.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFA9C0F065BFAE4DB65A339DC4C7AE13', X'D31E460FF8D3844D9A3CFB05869FCA6B', X'D31E460FF8D3844D9A3CFB05869FCA6B', 8, NULL, NULL, 2, NULL, 0, 'The magwinya can be served alone, but it also goes well with a cup of tea. For extra indulgence, roll it in jam and sugar. Serve immediately and enjoy!');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'136DA43FA750BB4BA69DD5011FB8C532', X'01B71BD67C0BAE4DB75E84CBB43F61D8', X'01B71BD67C0BAE4DB75E84CBB43F61D8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7442DB1B2980AB4DBE5FCDB8D3D3D13A', X'01B71BD67C0BAE4DB75E84CBB43F61D8', X'01B71BD67C0BAE4DB75E84CBB43F61D8', 1, NULL, NULL, 2, NULL, 0, 'In a heavy based pan, melt the butter in the vegetable oil, then add the spices to temper and become fragrant. Be careful not to sear or burn the spices, but allow the gentle heat to do its work for about a minute.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF3F462D43E71B4E97B48DEC1C5768E8', X'01B71BD67C0BAE4DB75E84CBB43F61D8', X'01B71BD67C0BAE4DB75E84CBB43F61D8', 2, NULL, NULL, 2, NULL, 0, 'Add the onions, garlic and ginger and fry until softened. This should take about 3 minutes on a medium heat.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAA06CBB05E5A649BA18EB235D0FC5C0', X'01B71BD67C0BAE4DB75E84CBB43F61D8', X'01B71BD67C0BAE4DB75E84CBB43F61D8', 3, NULL, NULL, 2, NULL, 0, 'Turn the heat up to high and add the chicken pieces. Chicken on the bone such a thighs, drumsticks, or wings work best with this recipe as the bones enrich the flavor and the chicken remains moist. Allow the meat to brown and coat the pieces in the onions and spices very well.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B4CDC58F5A66E48A785AD715B2555F5', X'01B71BD67C0BAE4DB75E84CBB43F61D8', X'01B71BD67C0BAE4DB75E84CBB43F61D8', 4, NULL, NULL, 2, NULL, 0, 'After about 5 minutes, add the tomato puree and chopped tomatoes and give the pot a good stir.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'019195F8B8F4144CB48DD09679A5F70F', X'01B71BD67C0BAE4DB75E84CBB43F61D8', X'01B71BD67C0BAE4DB75E84CBB43F61D8', 5, NULL, NULL, 2, NULL, 0, 'Throw in the diced potatoes, rutabaga, chicken stock, and curry leaves. Check for seasoning as it may require a pinch or two of salt at this stage if necessary, then allow to simmer for about 30 minutes on a low to medium heat.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'740151E1F492C643A7DAD7750759C6C6', X'01B71BD67C0BAE4DB75E84CBB43F61D8', X'01B71BD67C0BAE4DB75E84CBB43F61D8', 6, NULL, NULL, 2, NULL, 0, 'When ready, garnish with the fresh coriander leaves and serve on a bed of basmati rice and a sambal on the side.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40175BF340D2AC41B29B5856CB910921', X'3F46804798E7744FB63C795577F1C286', X'3F46804798E7744FB63C795577F1C286', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F5AA61853EAEE42B0E4CBA24D9DA563', X'3F46804798E7744FB63C795577F1C286', X'3F46804798E7744FB63C795577F1C286', 1, NULL, NULL, 2, NULL, 0, 'Take 1 cup of cornmeal and add it to a pot. Add 1 cup of cold water to wet this and mix to create a paste.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E49C3EADCEA8346A2993F917EF6AA59', X'3F46804798E7744FB63C795577F1C286', X'3F46804798E7744FB63C795577F1C286', 2, NULL, NULL, 2, NULL, 0, 'Add boiling water, then place the pot over high heat. The amount of water required will depend on the type and quality of cornmeal, but 3 to 4 cups is a good measure. Extra liquid can be added or cooked off as needed to achieve your ideal consistency.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE88DB24BBBA0C4FA72D318A3F16AB6C', X'3F46804798E7744FB63C795577F1C286', X'3F46804798E7744FB63C795577F1C286', 3, NULL, NULL, 2, NULL, 0, 'Bring the mixture to a boil and reduce to low heat to simmer for 15 minutes. You will notice it thickens rapidly.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'442E1561B709064183E25EAE97F54E9B', X'3F46804798E7744FB63C795577F1C286', X'3F46804798E7744FB63C795577F1C286', 4, NULL, NULL, 2, NULL, 0, 'If it is too thick for your liking, add a few teaspoons of boiling water.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BAB4E1AF01BF843A85E7A7A75EE3578', X'3F46804798E7744FB63C795577F1C286', X'3F46804798E7744FB63C795577F1C286', 5, NULL, NULL, 2, NULL, 0, 'Transfer cooked porridge to a bowl and mix in 1 to 2 tablespoons of peanut butter until well-incorporated.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'884117B042A77A4AAE8828E290F0DF6D', X'3F46804798E7744FB63C795577F1C286', X'3F46804798E7744FB63C795577F1C286', 6, NULL, NULL, 2, NULL, 0, 'Add sugar to taste if desired, and enjoy with or without milk and sliced fruit.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59DC01887868234481912EA77C575458', X'C59B585EFCFF4A4082536E74CAF87C3E', X'C59B585EFCFF4A4082536E74CAF87C3E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'427F5F5668635944871E6E57553621A1', X'C59B585EFCFF4A4082536E74CAF87C3E', X'C59B585EFCFF4A4082536E74CAF87C3E', 1, NULL, NULL, 2, NULL, 0, 'Marinate the chicken pieces in the lemon juice.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CD520DD9D5EF34391A6F61A1C742C5B', X'C59B585EFCFF4A4082536E74CAF87C3E', X'C59B585EFCFF4A4082536E74CAF87C3E', 2, NULL, NULL, 2, NULL, 0, 'Heat 2 tablespoons of niter kibbeh, ghee, or vegetable oil in a large, heavy-bottomed pot over medium-low heat, add the onions and cook gently for an hour until reduced into a sweet paste. Stir occasionally to prevent burning.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C073E6885E744A47A63ECF4BBDFB6A19', X'C59B585EFCFF4A4082536E74CAF87C3E', X'C59B585EFCFF4A4082536E74CAF87C3E', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining niter kibbeh, ghee, or vegetable oil. Add the berbere spice, followed by the ginger and garlic and fry until fragrant. More berbere spice can be added depending on how much heat is desired.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1316D40CC7AB5E48B3F7DD14DCD3BE20', X'C59B585EFCFF4A4082536E74CAF87C3E', X'C59B585EFCFF4A4082536E74CAF87C3E', 4, NULL, NULL, 2, NULL, 0, 'Add the chicken pieces to the pot along with the water or stock. Simmer on low heat for 40 minutes until the chicken is cooked. Halfway through, sprinkle the garam masala over the wat and continue cooking, stirring occasionally.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CF276C98F9764409E54989E55BE746C', X'C59B585EFCFF4A4082536E74CAF87C3E', X'C59B585EFCFF4A4082536E74CAF87C3E', 5, NULL, NULL, 2, NULL, 0, 'Add the boiled eggs to the wat and stir to coat completely. Serve the doro wat on top of injera to enjoy it the traditional way. Alternatively, this can be enjoyed with Indian flatbread or rice.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8653C65AC7A6447B6F2995BA09A8D34', X'F3CB64737BE946489F00D67B9047581A', X'F3CB64737BE946489F00D67B9047581A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D68C8F22110D004F88BBD3B738352833', X'F3CB64737BE946489F00D67B9047581A', X'F3CB64737BE946489F00D67B9047581A', 1, NULL, NULL, 2, NULL, 0, 'Soak the dried peaches and apricots whole in the vinegar overnight. This allows them to re-hydrate and become plump');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21781A00426F9D48A79C25F2F6E154BF', X'F3CB64737BE946489F00D67B9047581A', X'F3CB64737BE946489F00D67B9047581A', 2, NULL, NULL, 2, NULL, 0, 'Blitz or chop them into small pieces with a food processor.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF5AA2A635C9764DB48825C35A0EC00D', X'F3CB64737BE946489F00D67B9047581A', X'F3CB64737BE946489F00D67B9047581A', 3, NULL, NULL, 2, NULL, 0, 'Chop the onions in a food processor.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1476B9EA4CE80C4CB6B7A1EDD81D8D19', X'F3CB64737BE946489F00D67B9047581A', X'F3CB64737BE946489F00D67B9047581A', 4, NULL, NULL, 2, NULL, 0, 'Place all ingredients into a pot and heat gently for 20 minutes to allow the sugar to dissolve thoroughly.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0098FBB13AB5294087A6417B88953F66', X'F3CB64737BE946489F00D67B9047581A', X'F3CB64737BE946489F00D67B9047581A', 5, NULL, NULL, 2, NULL, 0, 'Allow to simmer at a medium heat for about 1 hour without covering, stirring occasionally.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5D958FB11958249B8669346636AC81C', X'F3CB64737BE946489F00D67B9047581A', X'F3CB64737BE946489F00D67B9047581A', 6, NULL, NULL, 2, NULL, 0, 'Once ready, allow sit to cool for 10 to 15 minutes prior to bottling into hot, sterilized jars.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4823973CCA13DC4991563C8DEB5462D4', X'1718AE2E775F3C41B980CDDFDF73D4C3', X'1718AE2E775F3C41B980CDDFDF73D4C3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0774FDFDAB5E644A7F371D3C2E66163', X'1718AE2E775F3C41B980CDDFDF73D4C3', X'1718AE2E775F3C41B980CDDFDF73D4C3', 1, NULL, NULL, 2, NULL, 0, 'The first step is to pre-soak the bambara nuts and peanuts separately in cold water, preferably for at least 12 hours or overnight.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53453CBD74D2C44E879B4F915905A139', X'1718AE2E775F3C41B980CDDFDF73D4C3', X'1718AE2E775F3C41B980CDDFDF73D4C3', 2, NULL, NULL, 2, NULL, 0, 'When ready to cook, drain the bambara nuts and top up with water and 1 teaspoon of salt. Bring this to a boil and simmer for 50 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3D5E26E9676C14A8468920B0567B6CC', X'1718AE2E775F3C41B980CDDFDF73D4C3', X'1718AE2E775F3C41B980CDDFDF73D4C3', 3, NULL, NULL, 2, NULL, 0, 'At this point, drain, then add the peanuts, remaining salt, and additional water to the bambara beans and boil for the remaining 1 hour and 10 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A45AF9943A62EE478DFFA84B3BFEA31C', X'1718AE2E775F3C41B980CDDFDF73D4C3', X'1718AE2E775F3C41B980CDDFDF73D4C3', 4, NULL, NULL, 2, NULL, 0, 'After the cooking process is complete, taste for seasoning and ensure the peanuts and beans are cooked well.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3EC95ED00C4F9499EEA6E6EAB62795D', X'1718AE2E775F3C41B980CDDFDF73D4C3', X'1718AE2E775F3C41B980CDDFDF73D4C3', 5, NULL, NULL, 2, NULL, 0, 'Enjoy as a snack or side dish.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA7D1B4A8A55C94083EA35E50FE4BB76', X'36AB438D6B0BA4479510515D45227F4E', X'36AB438D6B0BA4479510515D45227F4E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B0D1E854DDEA14989EEBE239E29A4FC', X'36AB438D6B0BA4479510515D45227F4E', X'36AB438D6B0BA4479510515D45227F4E', 1, NULL, NULL, 2, NULL, 0, 'Grate the coconut flesh from the shelled pieces using the large teeth of your grater, as larger shreds will contribute a chewy texture. Do this carefully, as the oil from the coconut may make your fingers slippery. Set aside.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'854C53744B2A5540AEF1808ED16774D7', X'36AB438D6B0BA4479510515D45227F4E', X'36AB438D6B0BA4479510515D45227F4E', 2, NULL, NULL, 2, NULL, 0, 'Make the caramel by dissolving the sugar in the water and lemon juice in a small pot over low heat, then bring to a boil.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'035002E7B4B5854F854A13A645314470', X'36AB438D6B0BA4479510515D45227F4E', X'36AB438D6B0BA4479510515D45227F4E', 3, NULL, NULL, 2, NULL, 0, 'Allow the caramel to boil from 5 to 10 minutes without stirring, which will prevent granules from forming. Keep an eye on the mixture, as caramel can burn quickly.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1841D4F3DB78FE4AB60A03F71D75A09C', X'36AB438D6B0BA4479510515D45227F4E', X'36AB438D6B0BA4479510515D45227F4E', 4, NULL, NULL, 2, NULL, 0, 'Once the caramel is deep brown, add the shredded coconut and cook for 10 more minutes, stirring occasionally. The coconut oils and natural moisture will release, and the mixture will become easier to handle.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F8A97E4DD77CD4C9BBF3E86E046049C', X'36AB438D6B0BA4479510515D45227F4E', X'36AB438D6B0BA4479510515D45227F4E', 5, NULL, NULL, 2, NULL, 0, 'When the mixture has developed a toasty coconut aroma, remove from the heat and allow it to cool slightly.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BBE8B199A599A4881DFE99FF0FCE84A', X'36AB438D6B0BA4479510515D45227F4E', X'36AB438D6B0BA4479510515D45227F4E', 6, NULL, NULL, 2, NULL, 0, 'Grease a plate with butter or margarine.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFFF8C6F4E201048B398D86EA9D116BD', X'36AB438D6B0BA4479510515D45227F4E', X'36AB438D6B0BA4479510515D45227F4E', 7, NULL, NULL, 2, NULL, 0, 'Handling the caramel with care, form balls with your hands, two spoons, or a small scoop. Place on the greased plate and allow to cool completely.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A00CDE88D38FF4468396F6E2A59C958B', X'36AB438D6B0BA4479510515D45227F4E', X'36AB438D6B0BA4479510515D45227F4E', 8, NULL, NULL, 2, NULL, 0, 'Serve at room temperature (or microwave for 5-10 seconds to serve warm).');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC492E5B9852564DB6F30CAD05F2140C', X'36AB438D6B0BA4479510515D45227F4E', X'36AB438D6B0BA4479510515D45227F4E', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'268B76265B8D2A43AC89C90F3A21CFCB', X'800543D747C0A24E9DEB5F5E62FCD7DF', X'800543D747C0A24E9DEB5F5E62FCD7DF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F573788340003D4A9A87C2DE68AED442', X'800543D747C0A24E9DEB5F5E62FCD7DF', X'800543D747C0A24E9DEB5F5E62FCD7DF', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil on medium heat in a large saucepan, and saute the onions until softened (about 5 minutes).');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'350658CF1EBAB848969E358191224E82', X'800543D747C0A24E9DEB5F5E62FCD7DF', X'800543D747C0A24E9DEB5F5E62FCD7DF', 2, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, garlic, pepper, and ginger to the pot and stir. Allow to cook until the tomatoes reduce (about 15 minutes).');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24392692F9412846BB765CED20C651BE', X'800543D747C0A24E9DEB5F5E62FCD7DF', X'800543D747C0A24E9DEB5F5E62FCD7DF', 3, NULL, NULL, 2, NULL, 0, 'Add the okra to the pot, followed by the prawns, squid, and fish. Add the stock and stir. Reduce the heat and allow to simmer for 5 to 10 minutes or until the okra is just tender, then remove from heat to prevent overcooking.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06907B5C133B09449FD4FB9235A38443', X'800543D747C0A24E9DEB5F5E62FCD7DF', X'800543D747C0A24E9DEB5F5E62FCD7DF', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F. Place the lobster tails flesh side-up on a baking sheet and brush generously with butter.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E6D794101F40D4583BFB9567B5BF1E9', X'800543D747C0A24E9DEB5F5E62FCD7DF', X'800543D747C0A24E9DEB5F5E62FCD7DF', 5, NULL, NULL, 2, NULL, 0, 'Bake for 12-15 minutes (depending on size), then remove from the oven, and brush with a little extra butter.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'489D0B312154E34E8FC1F1EF731ECDA9', X'800543D747C0A24E9DEB5F5E62FCD7DF', X'800543D747C0A24E9DEB5F5E62FCD7DF', 6, NULL, NULL, 2, NULL, 0, 'Transfer the okra stew into a serving dish, garnish with the spring onions, then top with the lobster tails.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'441009690564C84EA7B252269B36147F', X'800543D747C0A24E9DEB5F5E62FCD7DF', X'800543D747C0A24E9DEB5F5E62FCD7DF', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C62AFB496EFB44AB4E932469CBE5166', X'DB973A15247CFB4DA55961AEA50709C3', X'DB973A15247CFB4DA55961AEA50709C3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'014ECAEED9341347851D199845DE09F8', X'DB973A15247CFB4DA55961AEA50709C3', X'DB973A15247CFB4DA55961AEA50709C3', 1, NULL, NULL, 2, NULL, 0, 'Finely chop the onions, crush the garlic and grate the fresh ginger. Place your pot on the stove top, add 1 to 2 tablespoons of palm oil into the pan and fry the onion, ginger, and garlic.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2655FD80D0C45D43835702214980FF6D', X'DB973A15247CFB4DA55961AEA50709C3', X'DB973A15247CFB4DA55961AEA50709C3', 2, NULL, NULL, 2, NULL, 0, 'When the onions begin to brown, add the diced steak and allow to brown for only a couple of minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4331F846BC5EB4096951BCB0B8AF6BB', X'DB973A15247CFB4DA55961AEA50709C3', X'DB973A15247CFB4DA55961AEA50709C3', 3, NULL, NULL, 2, NULL, 0, 'Add the chopped tomatoes and scotch bonnet pepper. Scotch bonnet peppers can add quite a lot of spicy heat so if you wish to tone it down, add the pepper into the stew whole.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A65EBF4856D0C341818FEB33E9DDD98E', X'DB973A15247CFB4DA55961AEA50709C3', X'DB973A15247CFB4DA55961AEA50709C3', 4, NULL, NULL, 2, NULL, 0, 'Mix half a cup of the agushie powder with water, enough to form a paste. Set aside for a few minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E19160C96E8024E816268DFDCEB1F81', X'DB973A15247CFB4DA55961AEA50709C3', X'DB973A15247CFB4DA55961AEA50709C3', 5, NULL, NULL, 2, NULL, 0, 'After the tomatoes begin to break down, 5 to 7 minutes, pour in the agushie paste without stirring. Cover the stew and simmer on a low to medium heat for about 20 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9B482B6DE51CC49A9C067C2CF6E7129', X'DB973A15247CFB4DA55961AEA50709C3', X'DB973A15247CFB4DA55961AEA50709C3', 6, NULL, NULL, 2, NULL, 0, 'For those who are adventurous and wish to use dried anchovies, soak them in warm water for about 10 minutes. Agitate the water, pour out and rinse the fish.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62D56AEA871BAA4AB0D1EB3312B7613E', X'DB973A15247CFB4DA55961AEA50709C3', X'DB973A15247CFB4DA55961AEA50709C3', 7, NULL, NULL, 2, NULL, 0, 'After 20 minutes, uncover the pot. You will notice that the agushie has cooked and created a solid cake similar to bean curd or tofu. You may now break this apart and stir into the stew.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'126B9B8306C3EA43B0A98569EB84A39D', X'DB973A15247CFB4DA55961AEA50709C3', X'DB973A15247CFB4DA55961AEA50709C3', 8, NULL, NULL, 2, NULL, 0, 'Add the fish, followed by the spinach and allow to simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'313E82B23FB83740990FA307A2AB29D3', X'DB973A15247CFB4DA55961AEA50709C3', X'DB973A15247CFB4DA55961AEA50709C3', 9, NULL, NULL, 2, NULL, 0, 'Check seasoning prior to serving. This dish can be enjoyed with rice or a more traditional side of boiled yams.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA7193BEA5A6F64F892393A10E9235BB', X'EF57736967DC77408CFF4203799E60C8', X'EF57736967DC77408CFF4203799E60C8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D477376FE943549933D5EC294AE02D3', X'EF57736967DC77408CFF4203799E60C8', X'EF57736967DC77408CFF4203799E60C8', 1, NULL, NULL, 2, NULL, 0, 'Bring the water to a simmer in a large pot, and add the spinach.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2287141A0D0094D9CB4737C4E0E1AE4', X'EF57736967DC77408CFF4203799E60C8', X'EF57736967DC77408CFF4203799E60C8', 2, NULL, NULL, 2, NULL, 0, 'Once they have reduced in volume by about half, add the spring onions, garlic, and tomatoes and continue to simmer. Crumble the chicken stock cube into the pot and mix well.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'161B4F93591B8344882E5DB6CE79A306', X'EF57736967DC77408CFF4203799E60C8', X'EF57736967DC77408CFF4203799E60C8', 3, NULL, NULL, 2, NULL, 0, 'Make sure all bones and skin have been removed from the smoked fish, then add them to the pot. Allow to simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D952ABB039DB384484124D48A17FAE3A', X'EF57736967DC77408CFF4203799E60C8', X'EF57736967DC77408CFF4203799E60C8', 4, NULL, NULL, 2, NULL, 0, 'Add 3 to 4 tablespoons of red palm oil into the pot.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9772D89C8D9DDC41A3F914F3984592A5', X'EF57736967DC77408CFF4203799E60C8', X'EF57736967DC77408CFF4203799E60C8', 5, NULL, NULL, 2, NULL, 0, 'Add the peanut butter and allow to melt over gentle heat. Stir it into the dish and allow to simmer for 10 minutes until ready to serve.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8BA809943CADB4A9E16815E5945880F', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCCD1ED9919DEE428F5663F3B39E4505', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 1, NULL, NULL, 2, NULL, 0, 'Prepare the beans by soaking and de-hulling two days in advance.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F01D6F6BAE8EAA479C4148D39F28FCC4', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 2, NULL, NULL, 2, NULL, 0, 'Place the beans into a sturdy blender and begin to pulse until smooth. Add 1/4 cup of water at a time to facilitate the blending process.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B02FD795A7AB3439D53D5984275FF57', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 3, NULL, NULL, 2, NULL, 0, 'Add the onion, ginger, and scotch bonnet pepper to the blender.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F08A27C51B7724489116923B50BE6B4', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 4, NULL, NULL, 2, NULL, 0, 'Pulse until smooth.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35D910149B6A3D439C493D628C043A2E', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 5, NULL, NULL, 2, NULL, 0, 'Add the sea salt flakes and mix them into the batter.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'905EA83A59B86D4FB61163D4C6FC966A', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 6, NULL, NULL, 2, NULL, 0, 'Heat up the cooking oil in a small to medium-sized saucepan.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6E7A06FE574BE47B97E818FFAE20F3C', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 7, NULL, NULL, 2, NULL, 0, 'Test the readiness of the oil by adding a shaved piece of ginger. If it sizzles and rises to the top, the oil is hot enough.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82E82E9B9E39C1498704963DC423F8E7', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 8, NULL, NULL, 2, NULL, 0, 'Using a tablespoon to scoop the batter, spoon about five tablespoons of batter into the oil. You will see them puff up into round balls.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B097B446EFE3084DBCAE723BD6A088A7', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 9, NULL, NULL, 2, NULL, 0, 'Allow to fry for three to four minutes. Turn them halfway through to ensure they are evenly browned.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2A3116969A2D844B7D5CA144EC0840A', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 10, NULL, NULL, 2, NULL, 0, 'Once cooked, scoop them out of the oil using a slotted spoon or spider, and allow to drain on a paper towel.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F2CD8A8A313784CA777825D3DD71BFC', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 11, NULL, NULL, 2, NULL, 0, 'Serve as a snack with hot pepper sauce or tomato gravy. You can also enjoy them the traditional Hausa breakfast way, with porridge known as hausa koko.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93BA88F9BA561C408AAA2860A14A3D54', X'4763153161008247BF19BBB4BA4BF421', X'4763153161008247BF19BBB4BA4BF421', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87870122DDED1C4EB71D29226F084575', X'92C288D51F9EF24194239CCD8281E0FD', X'92C288D51F9EF24194239CCD8281E0FD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6669D6DFF8A2D94DA4E9B24F40C1F493', X'92C288D51F9EF24194239CCD8281E0FD', X'92C288D51F9EF24194239CCD8281E0FD', 1, NULL, NULL, 2, NULL, 0, 'Chop up all the ingredients into chunks. If using a pestle and mortar or asanka as your manual tool, then chopping them into small pieces will make it much easier to grind down into a sauce.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF5C5FDE73566D40BFC56A9D1F15FBF5', X'92C288D51F9EF24194239CCD8281E0FD', X'92C288D51F9EF24194239CCD8281E0FD', 2, NULL, NULL, 2, NULL, 0, 'Place the ingredients into your blender (manual or electric) and grind until the ingredients are mixed will into a sauce. You will notice a few chunks of tomato or onion in the sauce. This is OK as it adds to the texture of the sauce.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78C6EE9C8CB1D04483F8EBF43FA8952D', X'92C288D51F9EF24194239CCD8281E0FD', X'92C288D51F9EF24194239CCD8281E0FD', 3, NULL, NULL, 2, NULL, 0, 'Pour out into the serving bowl of your choice and enjoy with a can of sardines or corned beef and gari balls for a quick meal, or serve as a full meal with grilled tilapia and kenkey or banku.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2938634B795E444BDAC26CF0A9E9D0F', X'67B634148B18834CA68779C019BAFFEE', X'67B634148B18834CA68779C019BAFFEE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1073AC6B70138148A2835D5B771ABE8C', X'67B634148B18834CA68779C019BAFFEE', X'67B634148B18834CA68779C019BAFFEE', 1, NULL, NULL, 2, NULL, 0, 'Beat or whisk all the ingredients together in a bowl. Alternatively, mix them together with a hand-held blender.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CFCCF0A5FADC9478DE4087D191B0D09', X'67B634148B18834CA68779C019BAFFEE', X'67B634148B18834CA68779C019BAFFEE', 2, NULL, NULL, 2, NULL, 0, 'Heat up a frying pan with enough butter or oil to lightly coat the surface.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'296715D7933A8F44B7B16CE79A5579A7', X'67B634148B18834CA68779C019BAFFEE', X'67B634148B18834CA68779C019BAFFEE', 3, NULL, NULL, 2, NULL, 0, 'Ladle some batter into the pan and swirl such that the batter distributes to make a thin layer which spreads towards the curving sides of the pan');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C86AA1ACA1CD04390FDBEB6D86DF9CD', X'67B634148B18834CA68779C019BAFFEE', X'67B634148B18834CA68779C019BAFFEE', 4, NULL, NULL, 2, NULL, 0, 'Fry for about a minute or until lightly golden, then flip over and fry another minute or so on the other side.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0E7DD214412B54F87ECA3DC80723498', X'67B634148B18834CA68779C019BAFFEE', X'67B634148B18834CA68779C019BAFFEE', 5, NULL, NULL, 2, NULL, 0, 'Serve the malawah by spreading melted butter and honey on top or sprinkling some sugar on top.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76449DA08E1D6F43895DB563DEC670D0', X'85000B400F35F5468E2393DCEE03776B', X'85000B400F35F5468E2393DCEE03776B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'026F67D245D8184F9AE760893CE2A268', X'85000B400F35F5468E2393DCEE03776B', X'85000B400F35F5468E2393DCEE03776B', 1, NULL, NULL, 2, NULL, 0, 'Wash the rice and soak it in water until ready for use (at least 10 minutes).');
