INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0950B6EB2D886240B6CA87AF4FEFB847', X'85000B400F35F5468E2393DCEE03776B', X'85000B400F35F5468E2393DCEE03776B', 3, NULL, NULL, 2, NULL, 0, 'Fry the onions until lightly caramelized, about 15 minutes.');
INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'373E2300EDD37A4F80632A2162E0A03A', X'85000B400F35F5468E2393DCEE03776B', X'85000B400F35F5468E2393DCEE03776B', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, grind the dry spices add to the onions, along with the garlic. Cook for a minute, until fragrant.');
