INSERT INTO `VWR INTERNAL Australian&African recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'207503E399848145B112CB6402D23E69', X'85000B400F35F5468E2393DCEE03776B', X'85000B400F35F5468E2393DCEE03776B', 6, NULL, NULL, 2, NULL, 0, 'Add the lamb back to the pot.');
