INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F893B9D28867E94BA02AAB6F09A607A0', X'615856463730CD448DB3519DBD7A3D51', X'615856463730CD448DB3519DBD7A3D51', 0, 'javascript:', 'Braised Mixed Vegetables with Gingko Nuts', 2, NULL, 0, 'Braised Mixed Vegetables with Gingko Nuts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2145', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons sesame oil<BR>20 small black mushrooms, soaked, stems removed and discarded, each cut in half<BR>1 can gingko nuts, drained<BR>1 can white asparagus, spears drained and cut across in half<BR>5 small <I>gai lan,</I> quartered the long way<BR>2 canned Roma tomatoes, each one quartered<BR>1 Tablespoon chicken fat, rendered<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon mushroom soy<BR>1 Tablespoon Chinese black vinegar<BR>1 teaspoon salt<BR>2 cups vegetable stock<BR>2 Tablespoons cornstarch<BR><I><B>Prearation:</B></I><BR>1. Heat wok or fry pan, then add the oil, and stir-fry the mushrooms for four minutes.<BR>2. Add the gingko nuts, asparagus pieces, <I>gai lan,</I> and the tomatoes, and stir fry for two minutes then add the chicken fat, soy sauces, vinegar, salt and the stock, and simmer for three minutes.<BR>3. Raise temperature to high, mix cornstarch with two tablespoons cold water, and stirt-fry until thickened, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3E99A374DBF28D419C4D22CA3901F8AD', X'1CE1C217D31F2944BB751529CBE3137B', X'1CE1C217D31F2944BB751529CBE3137B', 0, 'javascript:', 'Braised Tofu and Mushrooms', 2, NULL, 0, 'Braised Tofu and Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1414', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 package (about 14 ounces) firm tofu<BR>2/3 cup vegetable broth<BR>2 teaspoons black bean garlic sauce<BR>2 teaspoons oyster sauce<BR>1/2 teaspoon sesame oil<BR>1 teaspoon sugar<BR>2 Tablespoons cooking oil<BR>1/2 pound small white mushrooms<BR>1/4 pound oyster mushrooms<BR>6 fresh medium-size shiitake mushrooms<BR>1 and 1/2 teaspoons cornstarch mixed with one tablespoon of water<BR><B><I>Preparation:</B></I><BR>1. Cut the tofu in half horizontally, then using a biscuit cutter, make 2 inch circles from each half; should yield twelve circles (squares are fine, too, but not as pretty).<BR>2. Combine broth, black bean and oyster sauces and sesame oil and sugar. Set this sauce mixture aside.<BR>3. Place a large fry pan on medium heat until hot. Add 1 Tablespoon oil and add the tofu and cook until brown, about two minutes per side. Remove tofu and set aside.<BR>4. Add remaining one tablespoon oil and all the mushrooms; stir-fry them one minute then add sauce, reduce heat to low and simmer until the mushrooms are tender (about five minutes). <BR>5. Add cornstarch mixture and cook until sauce boils, thickens and clears.<BR>6. Arrange tofu in a circle around the edge of a serving plate. Place mushroom mixture in center and serve.<BR>Makes four to six servings.<BR>_____<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CCF2832380D67F4684F08580F833FDD0', X'B5D2E6C37057264780B19291D187DC08', X'B5D2E6C37057264780B19291D187DC08', 0, 'javascript:', 'Broccoli with Oyster Sauce', 2, NULL, 0, 'Broccoli with Oyster Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1829', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound Chinese or regular broccoli, cut into one-inch pieces<BR>1 teaspoon salt<BR>1 teaspoon baking soda<BR>1 teaspoon grated ginger<BR>4 Tablespoons chicken broth<BR>3 Tablespoons oyster sauce<BR>2 Tablespoons red wine<BR>1 teaspoon sugar<BR>1/2 teaspoon garlic powder<BR><B><I>Preparation:</B></I><BR>1. Place broccoli, salt, and ginger in a saucepan, and cover with water, then bring it to boil. Cover and cook until the broccoli stalks are tender, about five minutes.<BR>2. Drain then rinse in cold water and drain again.<BR>3. Place in a serving platter and set aside.<BR>4. In a small saucepan combine all the remaining ingredients and bring to a boil. Remove from heat and pour evenly over the broccoli and serve.<BR><B>Note:</B> This recipe can be made with any other green vegetable.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'62C6AFB54341A54D96FBAAD8D0435151', X'C182DA6E5FEFD14F908F8F133841B514', X'C182DA6E5FEFD14F908F8F133841B514', 0, 'javascript:', 'Brussel Sprouts Stuffed with Shrimp Paste', 2, NULL, 0, 'Brussel Sprouts Stuffed with Shrimp Paste', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=995', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 large Brussel sprouts<BR>3 Tablespoons crab eggs (roe)<BR>1/3 pound shrimp, shells and veins removed, and chopped into a paste<BR>1/4 teaspoon salt<BR>1/4 teaspoon sugar<BR>4 individual teaspoons cornstarch<BR>dash ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Slit cross in the sprouts and steam them for five to eight minutes. Remove them, remove the center part of each and use for another purpose; then let them cool. Spread a few leaves out to make the sprout look like a flower, and put one teaspoon of the cornstarch into the center of each one.<BR>2. Scald the crab roe in very fine strainer for half minute, at most (we left this raw, but did bring it to room temperature).<BR>3. Mix shrimp paste with salt, sugar, one teaspoon of the cornstarch and the ground white pepper, then stuff this into the hollowed out cornstarch dusted Brussel sprouts.<BR>4. Put crab roe on top, and steam for eight minutes.<BR>5. Pull a very few leaves out and put the sprouts on individual plates (see illustration). Mix remaining two teaspoons of cornstarch with half cup of water, bring to the boil, and pour over the filled sprouts, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3B40A7DA8B0CB04EBC5FFD7B0337267C', X'4413D0F3AA15844E8E9CAAEFE1904B41', X'4413D0F3AA15844E8E9CAAEFE1904B41', 0, 'javascript:', 'Buddha''s Delight I', 2, NULL, 0, 'Buddha''s Delight I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1432', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>8 medium-size black mushrooms <BR>1/4 cup cloud ear mushrooms <BR>2 Tablespoons peanut or vegetable oil <BR>8 thin slices of fresh ginger, shredded <BR>1 scallion, chopped <BR>20 Golden Needles (dried day lily flowers)<BR>1 cup, sliced half-inch thick, winter bamboo shoots <BR>2 stalks celery, sliced diagonally into half-inch thick pieces <BR>1 cup peeled baby carrots <BR>1/2 pound Napa cabbage, cut into bite-size pieces <BR>2 ounces cellophane noodles <BR>3 Tablespoons thin soy sauce <BR>1 and 1/2 teaspoons sugar <BR>salt and pepper, to taste <BR>1/2 to 1 Tablespoons sesame oil <BR><B><I>Preparation</B></I><BR>1. Rinse black mushrooms and soak in warm water until soft, about half an hour. Drain the mushrooms and save the mushroom water; then slice or shred them.<BR>2. Soak the cloud ear fungus in hot water for fifteen to thirty minutes, then discard that water and rinse them thoroughly.<BR>3. Soak cellophane noodles in hot water until soft, then cut them into three-inch lengths.<BR>4 Soak the golden needles in cold water for fifteen to thirty minutes. Cut away any hard ends, then cut them in half. Discard their water.<BR>5. Mix soy sauce, sugar, salt, and pepper with a cup of the black mushroom water and set this aside.<BR>6. Heal oil in a wok or fry pan, then brown the ginger and scallions for half minute. Add the mushrooms and stir until fragrant, then add celery, carrot, bamboo soots, and the cabbage, and stir for two minutes.<BR>7. Add the cloud ear fungi and the golden needles, and stir well. Then add the mushroom water and cook for five minutes. Add more water, if needed.<BR>8. Add the noodles and mix well, then add the sesame oil, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8F91C4417EB58649B85D023FBFDB9369', X'37C16C2BED27C947BA66F3073F5A739B', X'37C16C2BED27C947BA66F3073F5A739B', 0, 'javascript:', 'Buddha''s Delight II', 2, NULL, 0, 'Buddha''s Delight II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=98', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons peanut or vegetable oil<BR>8 thin slices fresh ginger, shredded<BR>1 scallion, chopped<BR>8 medum sized black mushrooms<BR>1/4 cup cloud ear mushroooms<BR>20 golden needles (tiger lily flowers)<BR>1 cup, sliced half inch thick, winter bamboo shoots<BR>2 stalks celery, sliced diagonally into half-inch pieces<BR>1 cup baby carrots, peeled<BR>1/2 pound Napa cabbage, cut into bite-sized pieces<BR>2 ounces cellophane noodles<BR>3 Tablepoons thin soy sauce<BR>1 and 1/2 teaspoons sugar<BR>salt and pepper to taste<BR>1/2 to 1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Rinse mushrooms and soak in warn water until soft, about half an hour. Drain the mushrooms and save the mushroom water, then shred or slice them.<BR>2. Soak cloud ears in hot water for fiteen to thirty minutes, then discard the water and rinse them thoroughly.<BR>3. Soak cellophane noodles in hot water until soft, then cut them into three inch lengths.<BR>4. Soak golden needles in cold water for fiteen to thirty minutes. Cut away any hard ends, cut them in half, and discard the water.<BR>5. Mix soy sauce, sugar, salt and pepper with a cup of the mushroom water and set aside.<BR>6. Heat oil in a wok and brown the ginger and scallions for thirty seconds. Add the mushrooms and stir until fragrant, then add the celery, carrots, bamboo shoots, and the cabbage and stir cooking this for two minutes.<BR>7. Add the cloud ear fungi and the golden needles, stir well and then add the mushroom water and cook for five minutes. Add more water, if necessary.<BR>8. Add the noodles and mix well, then add the sesame oil and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F3814D0E84F9604FA02C424F2838FD67', X'831219937028FC4781F53759F0EBC0B2', X'831219937028FC4781F53759F0EBC0B2', 0, 'javascript:', 'Buddha''s Delight with Vegetarian Abalone', 2, NULL, 0, 'Buddha''s Delight with Vegetarian Abalone', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=99', '<TD><RECIPE>Find and follow the Buddha''s Delight recipe, then do the following:<BR>1. Reduce the soy sauce, salt and sugar amounts by half, and omit the sesame oil.<BR>2. Add a ten ounce can of <I>Chai pow yu</I> which is vegetarian abalone. Do so at the same time you add the cellophane noodles.<BR>3. Stir this well and cook for one or two minutes. Serve hot or cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2E17D1FB10B5CF4D869ADA00D159A0B6', X'2C23D91DD7C3CD4D882E4257B07221B5', X'2C23D91DD7C3CD4D882E4257B07221B5', 0, 'javascript:', 'Buddha''s Delight, Four Season Style', 2, NULL, 0, 'Buddha''s Delight, Four Season Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=491', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons peanut oil<BR>1/2 small celery cabbage, cut into two-inch lengths<BR>2 ribs celery, strings removed<BR>1/2 pound bok choy, cut into two-inch lengths<BR>1/4 pound small snow peas, strings removed<BR>1 carrot, peeled and cut long way into thin slices then cross-wise into two-inch slices<BR>2 squares firm bean curd, cutting thickness in half, then cut each of these into one-inch cubes<BR>1 Tablespoon sesame oil<BR>1 Tablespoon soy sauce<BR><B><I>Preparation:</B></I><BR>1. Heat peanut oil and stir-fry the vegetables for two minutes, then add the bean curd cubes, and stir.<BR>2. Add the sesame oil and soy sauce and stir an additional minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DC228D6E2B79A94AACA840E8AAB999D4', X'53B248E7288B0247A79DC6A333CD18E7', X'53B248E7288B0247A79DC6A333CD18E7', 0, 'javascript:', 'Buddhist''s Bean Curd Rolls', 2, NULL, 0, 'Buddhist''s Bean Curd Rolls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=302', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 cup glutinous rice, soaked in water for one hour<BR>6 dried black mushrooms<BR>1 Tablespoon cooking oil<BR>1 large clove garlic, minced<BR>1 small carrot, julienned into one inch pieces<BR>6 asparagus, cut into half inch pieces<BR>2 scallions, cut into half inch pieces<BR>2 Tablespoons oyster sauce<BR>1 Tablespoon hoisin sauce<BR>1 teaspoons sesame oil<BR>2 sheets dried bean curd (approximately seven by twenty-one inches)<BR>2 Tablespoons flour <BR>2 Tablespoons corn oil<BR><I><B>Preparation:</B></I><BR>1. Drain rice and add equal amount of water and simmer for twenty to twenty-five minutes until rice is tender.<BR>2. Heat one tablespoon oil and fry garlic for half minute then add carrots and asparagus and stir fry for two minutes, then add oyster and hoisin sauce and sesame oil and fry another two minutes then remove from the pan and add the scallions.<BR>3. Soak beancurd skin in barely warm water for fifteen minutes then cut into three squares per sheet.<BR>4. Mix flour with a Tablespoon of water to make a paste then fill each seven inch square sheet with about two Tablespoons of the filling. and seal with a little paste.<BR>5. Heat rest of the oil, preferably in a non stick pan, and fry three minutes per side. If frying in another type pan, add more oil, as needed.<BR>6. Remove from heat, cut each roll into thirds, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9CC684ED96014F49BA6229ED8C249BDA', X'8119B3BCB1381A468CAA60793638B21D', X'8119B3BCB1381A468CAA60793638B21D', 0, 'javascript:', 'Burdock and Carrots', 2, NULL, 0, 'Burdock and Carrots', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2086', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1 burdock root, peeled or brushed to get rid of its skin<BR>2 Tablespoons white wine vinegar<BR>1 carrot, the same size as the burdock, peeled<BR>1 to 2 Tablespoons sesame oil<BR>1 teaspoon granulated sugar<BR>1 Tablespoon rice wine<BR>2 Tablespoons thin soy sauce<BR>1 teaspoon white sesame seeds<BR><I><B>Preparation:</I></B><BR>1. Cut the burdock into thin matchstick-size pieces two inches long, and soak it in the wine vinegar for fifteen or twenty minutes, then dry them with a cloth or paper towels.<BR>2. Cut the carrot into pieces to match the burdock pieces.<BR>3. Heat the sesame oil in a wok or a fry pan and stir fry the burdock for two minutes, add the carrot pieces and stir-fry them together for three minutes longer.<BR>4. Add the sugar, rice wine, and the soy sauce and tree tablespoons cold water, reduce the heat, and stir-fry until all the liquid evaporates.<BR>5. Put the vegetables into a pre-heated serving bowl, toss with the sesame seeds, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5818914C049B1B40BBD771EFDAA1E772', X'74CC16DBE1992B48BC379372A3323141', X'74CC16DBE1992B48BC379372A3323141', 0, 'javascript:', 'Cabbage Wraps', 2, NULL, 0, 'Cabbage Wraps', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=125', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>8 large cabbage leaves <BR>3 wood ear/tree fungus, soaked in warm water<BR>3 dried black mushrooms, soaked in warm water<BR>2 cups cooked dark chicken meat, sliced thin<BR>1 shredded carrot<BR>3 shredded water chestnuts<BR>2 Tablespoons shredded ham (optional)<BR>2 cups chicken stock<BR>1 Tablespoon corn oil<BR>2 teaspoons water <BR>1 teaspoon cornstarch<BR><B><I>Preparation:</I></B><BR>1. Boil cabbage leaves a minute to soften.<BR>2. Soak the mushrooms and wood ears separately, each for thwenty minutes in warm water. Discard the stems and shred them.<BR>3. Bring stock to a boil, lower heat and simmer.<BR>4. Parboil the chicken in the stock for ten minutes. Remove the meat, let cool, and shred it. Keep the soup for later use.<BR>5. Heat a wok, add oil and stir-fry the mushrooms and tree/wood ear fungus until fragrant. Add carrots, water chestnuts, and ham and cook until any liquid has evaporated. Mix in shredded chicken and thicken with cornstarch mixed with cold water.<BR>6. Divide the mixture into eight equal portions and roll each into a cabbage leaf. Then steam them over high heat for five minutes, remove, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'084A5F087CB50C469449891999F51827', X'FB2B0525A0D2C04791C9F19670054D88', X'FB2B0525A0D2C04791C9F19670054D88', 0, 'javascript:', 'Cabbbage, Green Chilies, and Coconut', 2, NULL, 0, 'Cabbbage, Green Chilies, and Coconut', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=574', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small green cabbage, about one and a quarter pounds<BR>4 fresh green chili peppers<BR>1 teaspoon salt, or to taste<BR>1/2 teaspoon turmeric<BR>2 Tablespoons corn or canola oil<BR>1 teaspoon mustard seeds<BR>1 hot red chili pepper, halved<BR>A few curry leaves (optional)<BR>3/4 cup freshly grated coconut<BR><B><I>Preparation:</B></I><BR>1. Cut the cabbage in half. Cut out the thick core and cut the leaves finely, as you would for coleslaw. A food processor will do this job very well.<BR>2. Place the shredded cabbage in a colander, rinse it under running water, and drain.<BR>3. Cut the green chili peppers into thin strips and combine them with the cabbage, then sprinkle the salt and turmeric over the shredded cabbage, and mix well.<BR>4. Heat the oil in a heavy-bottomed wok or medium to large skillet and add mustard seeds. When they start spluttering, add the red pepper, curry leaves, and cabbage, and stir-fry for a two or three minutes. Reduce the heat to low, sprinkle a tablespoon of water over the cabbage and cover the pan. After two minutes, remove the cover and stir gently. Sauté cabbage for five to eight minutes more, stirring occasionally. When the cabbage is well cooked, sprinkle the grated coconut on top and stir gently, then serve when hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F6BE69DAAD50A54E8B3160C09FD9A78E', X'B145E8F068931C45A7C85E551882F76F', X'B145E8F068931C45A7C85E551882F76F', 0, 'javascript:', 'Carrot and Daikon in Ginger Sauce', 2, NULL, 0, 'Carrot and Daikon in Ginger Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1592', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 carrot, peeled then cut into three-inch long thin strips<BR>1/2 daikon, peeled then cut into three-inch long thin strips<BR>1 stalk Chinese celery, veins removed, then cut into three-inch long thin strips<BR>1 Tablespoon black Zhejiang vinegar<BR>2 Tablespoons Swatow rice vinegar<BR>1 Tablespoon sugar<BR>1 Tablespoon finely shredded fresh ginger<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Toss carrot, daikon, and celery strips together.<BR>2. Mix both vinegars, sugar, shredded fresh ginger, and the sesame oil, and set this aside for five minutes. Then toss with the vegetables, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7C323FD92C67414392298F7A2F529AE9', X'01D09922E553824692C65368F2A85488', X'01D09922E553824692C65368F2A85488', 0, 'javascript:', 'Carrots and Ginger', 2, NULL, 0, 'Carrots and Ginger', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2064', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon lard or oil<BR>1/4 pound boneless lamb, cut into thin strips<BR>3 Tablespoons fresh ginger, cut into thin strips<BR>½ pound carrots or another root vegetable, peeled and slivered<BR>½ teaspoon salt<BR>1 Tablespoon soy sauce or sauce above made for meat<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or fry pan, add the lard or oil and the meat, and stir only until the meat loses its pink color.<BR>2. Now add the ginger, carrots or the other root vegetable, the salt and salt and the sauce, and stir until the vegetables start to get soft. Serve hot, warm, or at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3ECA0F9C3493D64A8FB1BC1EF5C7D22D', X'D61E648E364A6843813DCE40652254FE', X'D61E648E364A6843813DCE40652254FE', 0, 'javascript:', 'Carrots and Peanuts', 2, NULL, 0, 'Carrots and Peanuts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2105', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 carrots, peeled and sliced thin<BR>1 teaspoon ground cumin<BR>2 teaspoons ground coriander<BR>2 teaspoons ground turmeric<BR>2 Tablespoons ground peanuts<BR>2 Tablespoons chopped peanuts<BR>2 Tablespoons vegetable oil<BR>2 Tablespoons tofu, mashed<BR><I><B>Preparation</B></I><BR>1. Mix carrots, cumin, coriander, and the turmeric together; do the same for both peanut preparations.<BR>2. Heat a wok or a fry pan, then add the carrot mixture and stir-fry ofr one minute before adding both peanut preparations and the mashed tofu. Fry this for three minutes, stir well, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EDC64B245C7CF64B8BE58E2303210F44', X'491148FD8A31A142BA8B610776BE2D54', X'491148FD8A31A142BA8B610776BE2D54', 0, 'javascript:', 'Cashews, Mushrooms, and Vegetables', 2, NULL, 0, 'Cashews, Mushrooms, and Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=691', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup corn oil<BR>2 ounces cashew nuts<BR>4 cups mixed fresh and soaked dried mushrooms<BR>1 cup snow peas<BR>1 cup sugar snap peas<BR>2 Tablespoons fresh ginger, minced<BR>1 teaspoon sugar<BR>1 Tablespoon mushroom soy sauce<BR>1 teaspoon chili paste with garlic<BR>1 teaspoon peanut butter or sesame paste<BR><B><I>Preparation:</B></I><BR>1. Put corn oil and cashews in a wok and slowly heat them until the temperature reaches 300 degrees F. Then, when nuts are a light golden color, remove them and set them on a paper towel to drain.<BR>2. Fry mushrooms for two minutes, then drain them.<BR>3. Keeping one tablespoon oil in wok and reserving the rest for another use, fry bot kinds of peas, the ginger, and the sugar for one minute, return mushrooms and cashews, and stir.<BR>4. Mix soy sauce, chili paste, and peanut butter or sesame paste, and add to the mushroom mixture, stir-fry one more minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'271DEC8A75881C4EB0D71B06101F8206', X'92E082F8A43A8F4DA4AA27750D64BF52', X'92E082F8A43A8F4DA4AA27750D64BF52', 0, 'javascript:', 'Cauliflower with Jinhua Ham', 2, NULL, 0, 'Cauliflower with Jinhua Ham', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=592', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 and 1/4 pounds cauliflower, cut into two-inch pieces<BR>1 Tablespoon corn oil<BR>1 clove garlic, peeled<BR>3/4 cup chicken broth<BR>1/4 cup evaporated milk<BR>1 teaspoon salt<BR>2 Tablespoons cornstarch mixed with three tablespoons cold water<BR>1 Tablespoon Jinhua or Smithfield ham, minced<BR><B><I>Preparation:</B></I><BR>1. Bring two quarts of tater to the boil and put the cauliflower into it keeping it boiling for three minutes, then drain.<BR>2. Heat oil and fry garlic for one minute, then add broth, milk, and salt and simmer for three minutes. Remove the cauliflower to a serving bowl.<BR>3. Add cornstarch mixture, and stir and when it thickens and clears, pour it over the cauliflower. Sprinkle with the ham and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ED54894E44113643BA6DFD4C9434A8DD', X'D22568EEDF1C9947877B6FEDE79688E8', X'D22568EEDF1C9947877B6FEDE79688E8', 0, 'javascript:', 'Cauliflower, Manchurian', 2, NULL, 0, 'Cauliflower, Manchurian', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=791', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 cups fresh cauliflower, cut into one to two inch tree-like pieces<BR>1 egg, beaten well<BR>1/4 cup flour or water chestnut flour, or a mixture of both<BR>1 Tablespoon chili paste with garlic<BR>1 teaspoon black bean paste with garlic<BR>3 cups oil<BR>3 slices fresh ginger, minced<BR>1 green chili, seeded and minced<BR>1/2 large green pepper, cut into one-inch pieces<BR>1/2 large sweet red pepper, cut into one-inch pieces<BR>1 Tablespoon soy sauce<BR>1/4 teaspoon salt and white pepper, mixed<BR>1/4 teaspoon ground turmeric<BR>1 Tablespoon minced fresh coriander<BR><B><I>Preparation:</B></I><BR>1. Mix cauliflower pieces, beaten egg, flour, and two tablespoons water.<BR>2. Heat oil, remove about half the cauliflower pieces from the flour batter, allow some draining, then deep fry them until very light brown. Drain and set them on paper towels. Repeat with the rest of the cauliflower. Set aside the oil for this and other uses, after straining it.<BR>3. Mix all the remaining ingredients. Heat one tablespoon of the remaining oil, and fry this mixture for one minute, then add cauliflower, stir well, and remove all to a bowl and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'22B272FCF8DEFE45A5597F6B7A46039A', X'5AA13BE5024D2341A944C9F7E6CB4071', X'5AA13BE5024D2341A944C9F7E6CB4071', 0, 'javascript:', 'Celtuce and Straw Mushrooms', 2, NULL, 0, 'Celtuce and Straw Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1534', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 celtuce, peeled, then cut into two-inch long pieces, the thicker bottom parts can be cut in half or quartered<BR>1 cup canned straw mushrooms, (half not peeled and half peeled to add texture, optional)<BR>1 Tablespoon vegetable oil<BR>1 red pepper, seeded and cut into half-inch squares<BR>1 Tablespoon vegetarian oyster sauce<BR>1/2 teaspoon mushroom bouillon<BR>1 teaspoon sesame oil<BR>1 teaspoon cornstarch mixed with two teaspoons cold water<BR><B><I>Preparation:</B></I><BR>1. Rinse celtuce pieces, and set aside. Do likewise to both straw mushrooms, drain them, and mix the mushrooms together.<BR>2. Heat wok or fry pan, add the oil and stir-fry the celtuce for one minute, then add mushrooms and the red pepper pieces and stir-fry for another minute or two, until all are heated through.<BR>3. Then add the vegetarian oyster sauce, bouillon powder, and the sesame oil, and stir for half minute. Bring this to the boil, then add cornstarch mixture and stir until the sauce clears, about half minute. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'302820D1B023684581067979238F522C', X'07FE4FF80B4B5942BE8EAA940EF231A9', X'07FE4FF80B4B5942BE8EAA940EF231A9', 0, 'javascript:', 'Celtuce Seasoned', 2, NULL, 0, 'Celtuce Seasoned', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2066', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 or more celtuce stems, those freshly harvested are best<BR>1 Tablespoon lard or lamb fat<BR>3 cloves fresh garlic, peeled and slivered<BR>2 shallots, peeled and minced<BR>2 scallions, minced<BR>½ cup any meat or vegetable broth<BR><B><I>Preparation:</B></I><BR>1. Rinse and peel the celtuce and cut into half-inch pieces.<BR>2. Heat fat in a work or pan, and add the garlic, shallots, and scallions and stir-fry for one minute. Next add the celtuce pieces and the broth and reduce the heat. Simmer for three minutes; then serve with green sauce on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'193D8C1B9CEF1345ABF5176CE4453113', X'1B98CDE13F2F2A4AA992BF18E4187C27', X'1B98CDE13F2F2A4AA992BF18E4187C27', 0, 'javascript:', 'Celtuce with Cloud Ears', 2, NULL, 0, 'Celtuce with Cloud Ears', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1141', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound celtuce (also known as stem lettuce)<BR>1/4 cup dry cloud ear fungi, soaked in warm water for half an hour or until very soft<BR>1 Tablespoon vegetable oil<BR>3 cloves garlic, peeled and minced<BR>3 slices peeled fresh ginger, minced<BR>2 scallions, minced, green and white parts separated<BR>2 Tablespoons rice wine<BR>1 Tablespoon chili bean or black bean sauce<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon dark soy sauce<BR>1 Tablespoon mushroom soy<BR>1 teaspoon sugar<BR>1 teaspoon freshly ground Sichuan peppercorns<BR><B><I>Preparation:</B></I><BR>1. Separate leaves and stem from celtuce. Peel the stem then cut them into two-inch matchstick size pieces.<BR>2. Cut cloud ear fungi into thin strips the thickness of the celtuce stems.<BR>3. Heat wok or fry pan, add oil, and then stir-fry garlic and ginger for one minute before adding the rice wine, chili bean sauce, and all three soy sauces, the match stick pieces of the celtuce and the cloud ear fungi. Stir them for one more minute.<BR>4. Add sugar, celtuce leaves, and the Sichuan peppercorns. Toss the entire mixture three or four times, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B103967E87B50A4C86EFBBC506CEA70E', X'240A4B988E16154684E048D60A5F1002', X'240A4B988E16154684E048D60A5F1002', 0, 'javascript:', 'Celtuce, Garlic, and Black Beans, Hakka Style', 2, NULL, 0, 'Celtuce, Garlic, and Black Beans, Hakka Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1762', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 ounces celtuce tops or romaine lettuce<BR>1 Tablespoon vegetable oil<BR>8 cloves garlic<BR>1 Tablespoon fermented black beans, rinsed and coarsely chopped<BR>1 teaspoon minced fresh ginger<BR>1 teaspoon soy sauce, or to taste<BR><B><I>Preparation:</B></I><BR>1. Trim and discard the stem ends of the celtuce or lettuce, and separate the leaves cutting larger ones in half lengthwise and crosswise. Rinse and drain well.<BR>2. Set a fourteen-inch wok or twelve-inch fry pan over high heat. When hot, add the oil and rotate the pan to spread it around. Then add the garlic and stir-fry it until it begins to brown. Next add the black beans and ginger; stir well, then add the greens and one tablespoon of water. Stir-fry just until the greens wilt slightly, about one minute. Stir in soy sauce, stir, then pour this mixture into a bowl and serve.<BR><B>Note:</B> Serves four or five as part of a multi-course meal.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DC6F650799899845A453C577A73CEAA4', X'4B8FAA95C3A4C04BB553611B892535C3', X'4B8FAA95C3A4C04BB553611B892535C3', 0, 'javascript:', 'Chanpuru', 2, NULL, 0, 'Chanpuru', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=102', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>2 Tablespoons oil<BR>1 eight-ounce block of tofu<BR>2 teaspoons salt<BR>2 scallions, sliced<BR>4 ounces bean sprouts<BR>4 ounces bitter melon, peeled, seeded, and sliced<BR>8 ounces Chinese cabbage, cut bite-sized<BR>4 ounces daikon, peeled and cut bite-sized<BR>8 ounces green papaya, grated<BR><I><B>Preparation:</B></I><BR>1. heat oil in wok or large deep skillet and brown the tofu breaking it into pieces and salting them.<BR>2. Add scallions and all the vegetables and stir-fry until tender. Add a tablespoon of water or stock, if needed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B4EF1EA549C2B440AEA763B5F1624756', X'B8F2F2BD04B52E4D8609EF3FE78BE2FE', X'B8F2F2BD04B52E4D8609EF3FE78BE2FE', 0, 'javascript:', 'Chickpea and Carrot Pancakes', 2, NULL, 0, 'Chickpea and Carrot Pancakes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2068', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup dried chickpeas, soaked overnight in lots of water, then drained, their water discarded<BR>1 teaspoon coarse salt<BR>1/4 teaspoon coarsely ground pepper<BR>3 carrots, peeled and coarsely grated<BR>1 cup lard or vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Mash chick peas, add the salt and pepper, and the carrots, and mix well. Then shape into small pancakes.<BR>2. Heat oil, and fry about one third of the pancakes on one side then the other side until they are crisp, then drain them and set aside on paper towels. Repeat until all are fried, then serve with green sauce on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A2840FDE58C3E442BAAD8D03E1A36870', X'C89F0AE84518A148B1FA47767D1E7BC3', X'C89F0AE84518A148B1FA47767D1E7BC3', 0, 'javascript:', 'Deep-fried Chestnuts', 2, NULL, 0, 'Deep-fried Chestnuts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2252', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds whole and peeled pre-cooked and packaged chestnuts<BR>2 ounces white rock sugar<BR>2 ounces granulated sugar<BR>2 Tablespoons molasses<BR>2 Tablespoons vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Bring the chestnuts, both sugars, molasses, and four cups of water to a boil, then lower the heat and simmer for twenty minutes; most of the liquid will have boiled away.<BR>2. Put the chestnuts in a strainer basket and let them cool.<BR>3. Heat a wok or large pot, add the oil, and the chestnuts, and stir-fry them until they are golden brown. Drain them and let them cool. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CFAB29BDC10F1347892CF057402CE286', X'888BBAAAFEFE2B40A424107EE518EE8C', X'888BBAAAFEFE2B40A424107EE518EE8C', 0, 'javascript:', 'Dinghu Temple Food', 2, NULL, 0, 'Dinghu Temple Food', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2075', '<TD><RECIPE><B><I>Ingredients</I></B><BR>1/2 cup each of five or more different dried mushrooms, each soaked separately in half cup hot water<BR>1/2 cup carrots, peeled, sliced, and simmered for three minutes, then drained and set aside<BR>5 small Shanghai cabbages, each cut in half and boiled for two minutes, then drained<BR>2 Tablespoons canned lotus seeds<BR>20 canned gingko nuts<BR>1/2 cup fresh bean sprouts, tails removed and discarded<BR>1 Tablespoon cornstarch<BR><B><I>Preparation</I></B><BR>1. Reserve the water from each rained mushroom batch, nd combine them. Put one cup in a pot and simmer for ten minutes, reserve the rest for other uses.<BR>2. Put carrot slices around the outside of a platter, the mushrooms mixed and in the center of the platter.<BR>3. Put the Shanghai cabbage, cut side down on the carrot slices.<BR>4. Mix cornstarch with the cup of mushroom water and thicken, then pour over the items on the platter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3946AA53FF28004BA0DDA30662E16F86', X'4CE2534CFFB362408273D93CD5FF92E4', X'4CE2534CFFB362408273D93CD5FF92E4', 0, 'javascript:', 'Fried Bamboo Fungus', 2, NULL, 0, 'Fried Bamboo Fungus', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2061', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 piece, about five tablespoons, cured pork, minced<BR>10 pieces dried bamboo fungus, soaked for ten minutes, then drained and the water discarded <BR>½ cup breadcrumbs such as Panko<BR>5 Tablespoons minced baby <I>bok cai</I> leaves<BR>1 Tablespoon each red and green peppers, minced<BR>1 Tablespoon cilantro<BR>½ teaspoon coarse salt<BR>1 teaspoon chicken bouillon powder<BR>1 teaspoon oyster sauce<BR>1 teaspoon XO sauce<BR>1 teaspoon fresh coriander, minced<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or fry pan, add the oil and the cured pork, and stir-fry for two minutes. Next remove from the oil and set aside.<BR>2, Roll the bamboo fungus pieces in the bread crumbs, and fry until crisp, about three minutes, removing them and draining them on paper towels.<BR>3. Add <I>bok cai</I> leaves and the minced peppers, and stir-fry for one minute before adding the cilantro, salt, bouillon powder, oyster and XO sauces. Stir-fry another minute before returning the belly pork pieces and the bamboo fungus and stir-frying everything another minute or two, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'701A5A3D2C23DC4EAD5197BAE9D69C11', X'DB7C7B95603DC34EA391E634571AE0E9', X'DB7C7B95603DC34EA391E634571AE0E9', 0, 'javascript:', 'Fried Vegetarian Eel', 2, NULL, 0, 'Fried Vegetarian Eel', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2037', '<TD><RECIPE><B><I>Ingredients</B></I><BR>1/4 cup pre-cooked peeled chestnuts, sliced<BR>2 medium steamed carrots, peeled and thinly sliced<BR>3 baby corn, thinly sliced<BR>2 sticks bean curd, soaked until soft<BR>1 Tablespoon fresh ginger, grated<BR>2 seaweed sheets (each about six by six inches)<BR>2 cups vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Mix prepared chestnuts, carrots, baby corn, bean curd sticks well; then roll into the seaweed sheets wetting the last edge. Let this rest ten minutes on this edge.<BR>2. Heat oil in wok or pan and deep fry these rolls until crisp, then drain on paper towels.<BR>3. Cut them on an angle into four or five pieces. Serve on a flat plate.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0ABF610A8F221F4B800B60DF977548C9', X'A9A619051A59F342B42580FBD2E6E346', X'A9A619051A59F342B42580FBD2E6E346', 0, 'javascript:', 'Garlic-stuffed Mushrooms', 2, NULL, 0, 'Garlic-stuffed Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2217', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>20 medium fresh shiitake mushrooms, stems discarded<BR>2 teaspoons cornstarch or water chestnut flour<BR>½ pound ground turkey, ground chicken, or ground firm <I>doufu</I><BR>5 cloves fresh garlic, peeled and ground with the poultry and/or <I>doufu</I><BR>1/4 cup scallions, minced coarsely<BR>3 Tablespoons thin soy sauce<BR>1 Tablespoon minced fresh ginger<BR>1/4 chili pepper, seeded and minced<BR>1 egg white<BR><B><I>Preparation:</B></I><BR>1. Dust gill-side of the mushrooms with the cornstarch or water chestnut flour.<BR>2. Mix all other ingredients except the mushrooms and divide this into twenty batches and put one batch on each flour-side of a mushroom.<BR>3. Broil the mushrooms about four inches from the heat source, for about five minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A413EB7AABED2945B9BF4187BF728F97', X'34AB6CAB19F31345AB88364390AE35FD', X'34AB6CAB19F31345AB88364390AE35FD', 0, 'javascript:', 'Glutinous Rice, Pine Nuts, and Mushrooms', 2, NULL, 0, 'Glutinous Rice, Pine Nuts, and Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2151', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups glutinous rice, soaked in three cups of warm water for half an hour, then set this aside.<BR>4 black mushrooms, soaked in warm water for twenty minutes, stems discarded, water set aside, caps diced, everything set aside<BR>3 Tablespoons peanut oil<BR>3 slices fresh ginger, peeled and chopped<BR>1 Tablespoon oyster sauce<BR>1/4 teaspoon salt<BR>2 Chinese sausages, thin sliced or diced<BR>½ cup pine nuts, lightly toasted<BR>a small bunch cilantro, minced<BR><B><I>Preparation:</B></I><BR>1. Heat wok, add peanut oil, then mushrooms, ginger, pieces of sausage, oyster sauce, and salt and stir-fry a few times until very aromatic.<BR>2. Add rice and its liquid and the pine nuts and stir-fry a few more times, add the mushroom pieces mixing well, then cover and simmer for twenty minutes or until rice is tender.<BR>3. Transfer to a pre-heated bowl or deep platter, garnish with the cilantro, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9790A23021D34C4CA162074FE14642C4', X'C2D35E83B94E904B99D6E9652B211CD5', X'C2D35E83B94E904B99D6E9652B211CD5', 0, 'javascript:', 'Golden Cup Lily Bowls', 2, NULL, 0, 'Golden Cup Lily Bowls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2074', '<TD><RECIPE><B><I>Ingredients</B></I><BR>1 cup flour<BR>1/2 cup cornstarch<BR>1 to 2 cups another oil for deep frying<BR>2 fresh lily bulbs<BR>1/2 red sweet pepper<BR>1/2 cup green peas<BR>10 canned gingko nuts<BR>1 day lily stem<BR>2 slices fresh ginger, minced<BR>2 Chinese black mushrooms, soaked, stems discarded, and thinly sliced<BR>1/2 teaspoon coarse salt<BR>1 teaspoon sesame oil<BR>1/2 scallion, slivered<BR><B><I>Preparation</I></B><BR>1. Heat oil for deep fryng.<BR>2. Mix flour, cornstarch, corn oil, and half cup of water. Then did the outside of a metal cup or form into this until its top edge and deep fry in the hot oil until it falls off the cup or mold and fry until golden inside and out, then drain on paper towels making five to ten of these cups, then set all but one tablespoon of the oil aside for another use. 3. Heat the tablespoon of oil and add the lily bulb pieces, and fry one minute before adding all other solids and the sesame oil, and after two minues rmove to a glass or ceramic bowl then put some of these solids into every cup of fried batter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4EC55A52FFFE94418DD9F1D07D926E70', X'8A616B2679C1E94093C85A50E2B34456', X'8A616B2679C1E94093C85A50E2B34456', 0, 'javascript:', 'Hand-torn Cabbage', 2, NULL, 0, 'Hand-torn Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2054', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1½ pounds napa or any young white cabbage<BR>3 Tablespoons vegetable oil<BR>1 Tablespoon Sichuan peppercorns<BR>1 Tablespoon chili peppers, seeded<BR>1 Tablespoon peeled and thinly sliced fresh garlic<BR>1 teaspoon chicken bouillon powder<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Separate the cabbage leaves and tear them into one- to two-inch pieces.<BR>2. Heat a wok or a large fry pan, when hot, then add the vegetable oil, and when that is hot, reduce the heat and add the Sichuan peppercorns. Stir-fry them for two minutes, then remove and discard them.<BR>3. Add the chili peppers and the garlic and stir-fry them for two minutes; do not let them burn, then remove and discard them.<BR>4. Add the cabbage pieces, stir well, cover the wok or fry-pan and leave it steam in the flavored oil for three minutes, add the sesame oil, stir well, then remove from the wok or fry pan and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C262D70EE1B6E746A81E70EEF372F0B8', X'80040765E561AD46BD86A7B87B4FC275', X'80040765E561AD46BD86A7B87B4FC275', 0, 'javascript:', 'Hunan Pickled Cabbage', 2, NULL, 0, 'Hunan Pickled Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2121', '<TD><RECIPE><B><I>Ingredients: </B></I><BR>1 pound white or napa cabbage, rinsed and dried, then cut into one-inch or smaller pieces<BR>1 peeled carrot cut into small pieces or slices<BR>1 small seedless cucumber, peeled, rinsed, and dried, then diced small<BR>2 chili pepper steeped in wine, seeded and cut into large seedless pieces<BR>1 inch fresh ginger, peeled and sliced<BR>3 cloves garlic, peeled and each one cut in half<BR>10 Sichuan peppercorns, rinsed and dried<BR>½ half cup Chinese rice or sorghum wine<BR>2 Tablespoons white vinegar, Chinese or American<BR>1 Tablespoon coarse salt<BR>2 to 4 cups of ice water<BR><B><I>Preparation:</B></I><BR>1. Sterilize a one quart canning jar or two pint ones packed with a mix of cabbage, carrot, and cucumber pieces.<BR>2. Mix the chili peppers, ginger, garlic and the peppercorns with the wine, vinegar, and the salt and pour over the vegetables.<BR>3. Fill the jar or jars with the water, seal, and store in the refrigerator for twelve to twenty-four hours before using.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FAB6CC494E135F43B012F481746CF3D2', X'316668BB8760CA4EB878ACADAE63940A', X'316668BB8760CA4EB878ACADAE63940A', 0, 'javascript:', 'Lo Han Clay Pot', 2, NULL, 0, 'Lo Han Clay Pot', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2073', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound fresh soft or firm bean curd cut into two-inch squares half-inch thick<BR>2 cups vegetable oil<BR>1/2 head Tianjin cabbage, cut into two-inch sections<BR>3 ounces fresh golden mushrooms, each cut in half<BR>10 fresh button mushrooms<BR>10 medium Chinese black mushrooms, soaked stems discarded, and cut into quarters<BR>1/4 cup dried wood ear fungi, soaked and then torn into pieces<BR>2 or 3 white or snow fungi, prepared as the wood ear fungi<BR>1 Tablespoon sesame oil<BR>10 canned gingko nuts<BR>20 goji berries<BR>20 eaves from hollow stem vegetables 1 stalk celery, cut in half-inch pieces<BR>2 cups vegetable stock of one cube vegetable bouillon with two cups boiling water<BR>2 Tablespoons vegetarian oyster sauce<BR>1 teaspoon salt<BR>1 tablespoon mushroom soy sauce<BR><I><B>Preparation</B></I><BR>1. In a wok or deep fry pan, fry the bean curd in hot oil for about four minutes or until golden, then drain it on paper towels and set it aside. reserving the oil for another use.<BR>2. In the same wok or fry pan, mix the cabbage, all the mushrooms (and fungi) for one minute, then add the gingko nuts, stock, oyster sauce, salt, and the soy sauces, and simmer for thirty minutes. Serve now or put the soup in a tureen and reheat it and serve it at the table.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1A8266CC189FF54A83A728C37337C185', X'34EAF59C3EAB6C45A305A6D416EEE2C2', X'34EAF59C3EAB6C45A305A6D416EEE2C2', 0, 'javascript:', 'Long Beans with Ya Cai', 2, NULL, 0, 'Long Beans with Ya Cai', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2243', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pound long beans, washed<BR>2 Tablespoons vegetable oil, divided<BR>2 Tablespoons minced pork<BR>1 large clove garlic, peeled and minced<BR>3 slices fresh ginger, peeled and minced<BR>2 chili peppers, seeded and minced<BR>2 Tablespoons fermented black beans, chopped<BR>! Tablespoon minced packaged <I>ya cai</I><BR>2 scallions, Minced, separate white and green parts<BR><B><I>Preparation:</B></I><BR>1. Discard ends of the long beans and cut them into two-inch pieces.<BR>2. Heat wok or fry pan, add half the oil, and stir-fry the pork for one minute until no longer pink, then remove it from the wok and set it aside.<BR>3. Put the rest of the oil in the pan, and stir-fry the long beans for three minutes, then remove then from the pan and set them aside.<BR>4. Fry the garlic and ginger for one minute, add the chili pepper pieces for another minute, then add the Sichuan peppercorns, the <I>ya cai</I>, and the white parts of the scallion, toss and then add th pork and the long beans and stir-fry another minute or two, then remove to a preheated bowl, sprinkle the scallion greens on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'58C9F20C60318444A37E1AD0A27A66E4', X'21BAD98B552BE840BE6273FD013021DF', X'21BAD98B552BE840BE6273FD013021DF', 0, 'javascript:', 'Nori with Scallions', 2, NULL, 0, 'Nori with Scallions', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2144', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>2 sheets nori (or kombu) stacked and then slivered<BR>2 tablespoons thick soy jam<BR>2 scallions, angle cut<BR><B><I>Preparation:</B></I><BR>1. Soak the nori or kombu in warm water for half an hour, then drain and dry somewhat.<BR>2. Mix the seaweed with the soy jam and the scallion pieces and toss well.<BR>3. Serve as soon as they are tossed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DB99204D59401A4695F21D14CE1E8F68', X'3D6409A2591B554FB1E752F2F8F8D56B', X'3D6409A2591B554FB1E752F2F8F8D56B', 0, 'javascript:', 'Pickled Mustard Greens', 2, NULL, 0, 'Pickled Mustard Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2244', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pound mustard greens, coarsely chopped<BR>1 teaspoon dry chili powder<BR>2 teaspoons coarse salt<BR>5 Tablespoons dark brown sugar<BR>3 Tablespoons Chinese rice wine<BR><B><I>Preparation:</B></I><BR>1. Rinse the chopped green and dry them with a clean towel.<BR>2. Mix the chili powder, salt, sugar, and the wine and toss with the mustard greens.<BR>3. Store in clean sterilized glass canning jar and cover with a lid and ring boiled for ten minutes. Keep this in a dark place for one or two weeks, and stir twice a week with a sterilized spoon. Then refrigerate for up to two weeks, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6A1733F2391C774E9A9880F38BDA30B4', X'D338FF96BF65C9438A9F8E35636F965E', X'D338FF96BF65C9438A9F8E35636F965E', 0, 'javascript:', 'Potato Shreds with Picked Mustard Greens', 2, NULL, 0, 'Potato Shreds with Picked Mustard Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2146', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 medium potatoes, one shredded the other grated<BR>1 Tablespoon vegetable oil<BR>2 pickled piquant red peppers, seeded and diced<BR>2 cloves garlic. Peeled and minced<BR>1/4 cup picked mustard green knob, diced<BR>1 teaspoon chili oil<BR>1 Tablespoon sesame oil<BR>1 Tablespoon granulated sugar<BR>1 small head cabbage, boiled for two minutes, then well drained<BR><B><I>Preparation:</B></I><BR>1. Squeeze as much water as possible from the grated potato, then mix it with the shredded potato.<BR>2. Heat wok or fry pan, add vegetable oil, and when hot add the piquant pepper pieces and the garlic and stir fry for one minute.<BR>3. Add mustard green pieces, chili and sesame oils, and the sugar and stir fry for two minutes. Serve around the outside of a pre-heated plate and put the cabbage, cut into eight wedges in its center.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'709CD031647D104F9D32513EFA563644', X'0C834733F0C1FA4DB9F1EA17D1982D73', X'0C834733F0C1FA4DB9F1EA17D1982D73', 0, 'javascript:', 'Potato Shreds with Pickled Mustard Greens', 2, NULL, 0, 'Potato Shreds with Pickled Mustard Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2047', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 medium potatoes, 1 shredded and 1 grated<BR>1 Tablespoon vegetable oil<BR>2 pickled piquant seeded red peppers, diced<BR>2 cloves fresh garlic. peeled and minced<BR>1/4 cup pickled mustard green, sliced from a knob<BR>1 teaspoon chili oil<BR>1 Tablespoon sesame oil<BR>1 Tablespoon granulated sugar<BR><I><B>Preparation</B></I><BR>1. Squeeze as much water as possible from the grated potato, them mic=x with the shredded potato.<BR>2. Heat wok or fry pan, add the vegetable oi, and when it is hot, add the red peppers, the garlic, and stir-fry one minute before adding both potatoes; stir-fry these together for two minutes. 3. Add the pieces of mustard greens, both the chili and sesame oils, and the sugar and stir-fry for two minutes. Serve around a large pre-heated plate, putting the whole cabbage in the next recipe cut into eight wedges in its center.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7B13672D737DBB41A88126CD19A63C97', X'941882AF6672194996D46A378A258543', X'941882AF6672194996D46A378A258543', 0, 'javascript:', 'Potato Slices', 2, NULL, 0, 'Potato Slices', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2067', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup lard or another fat<BR>2 pounds potatoes, peeled if desired, and sliced thinly<BR>1 cup mint leaves or 3 Tablespoons green sauce<BR>1 to 2 teaspoon coarse salt<BR>dash of or up to 1/4 teaspoon Sichuan peppercorns, crushed coarsely<BR><B><I>Preparation:</B></I><BR>1. Heat wok or a fry pan, add the lard or other fat, then the potato slices, and stir-fry until they begin to brown, then stir them another two minutes.<BR>2. Add mint, salt, and Sichuan peppercorns, and stir one more minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D0B5EE84DA88A740969AACE1E8AF5A5A', X'9E723C9DF04B3F429CA1471D545E0850', X'9E723C9DF04B3F429CA1471D545E0850', 0, 'javascript:', 'Red Chilies Steeped in Wine, Hunan Style', 2, NULL, 0, 'Red Chilies Steeped in Wine, Hunan Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2120', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound red hot (piquant) chilies<BR>½ cup Chinese white wine<BR>3 Tablespoons granulated sugar<BR>1/4 cup peeled and crushed fresh ginger<BR>2 Tablespoons fermented black beans<BR>2 Tablespoons coarse salt<BR><B><I>Preparation:</B></I><BR>1. Seed the chili peppers, then rinse and dry them and put them into a sterilize canning jar setting its cover in boiling water. Fill the jar with the wine and the other ingredients at room temperature, then fill it with previously boiled and then cooled water, and seal the jar.<BR>2. Leave this jar at room temperature in a dark place for one week, then move it to the rear of your refrigerator in a paper bag taped shut for two months or more before using them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'610213B6B41F7747A09102C714966582', X'9B630A0655F32A4FA036C18CCD4F3AA6', X'9B630A0655F32A4FA036C18CCD4F3AA6', 0, 'javascript:', 'Seaweed Rolls', 2, NULL, 0, 'Seaweed Rolls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2141', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 or 3 sheets of dried bean curd, soaked between wet towels<BR>2 or 3 dried sea weed sheets called nori, wet between paper towels<BR>2 large Chinese mushrooms, soaked, stems discarded, then cut into thin sticks, and fried for one minute, then drained on paper towels<BR>1 Tablespoon vegetable oil<BR>1 small thin cucumber, cut into thin sticks<BR>1 carrot, peeled and cut into thin sticks<BR>1 teaspoon to 1 Tablespoon red chili<BR>½ teaspoon five-spice powder<BR>salt and pepper, to taste<BR><B><I>Preparation:</B></I><BR>1. Spread bean curd sheets on a bamboo place mat and put a seaweed sheet on top.<BR>2. Mix all vegetable sticks and put these together at the long end of them.<BR>3. Mix chili, five-spice powder, and salt and pepper and sprinkle this on top of the vegetables, then roll tightly, and cut on an angle into four pieces.<BR>4. Put some of these standing on end, others lying down, on a platter, refrigerate for an hour, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AC27A05575155D44ACCFC23EBC5E4EB8', X'5C0B70B1B4CEE94784D062F442397158', X'5C0B70B1B4CEE94784D062F442397158', 0, 'javascript:', 'Shredded Potatoes and Pickled Mustard Greens', 2, NULL, 0, 'Shredded Potatoes and Pickled Mustard Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2060', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 medium potatoes, peeled and shredded, then blanched for one minute and then put into a bowl of cold water<BR>2 cloves garlic, peeled and minced<BR>1 small hot red pepper, seeded and minced<BR>1/4 cup rinsed then minced pickled mustard greens*<BR>1 teaspoon chicken bouillon powder<BR>1 teaspoon sesame oil<BR>1 teaspoon hot pepper oil (optional)<BR>1 teaspoon granulated sugar<BR>1 cup vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Drain the potato shreds and roll them in a clean dish towel to remove any remaining water.<BR>2. Mix the potatoes, bouillon powder, sesame oil, the hot pepper oil if using it, and the sugar.<BR>3. Heat oil in a wok or large fry pan, add the potatoes, and fry them turning frequently until they are lightly browned, then drain and serve.<BR><B>*NOTE:</B> One can pickle these at home if no commercial ones are available by making them some days in advance mincing or shredding fresh mustard greens and soaking them overnight or for a day or two in salt water or pickle juice; then rinse and drain them, and mix them well with half teaspoon chili powder, two tablespoons brown sugar, and two tablespoons rice wine. Put this in a clean sterilized jar covered tightly with a clean cloth, a rubber band works well, and put the jar in a dark corner for three to seven days. They can stay for two or three weeks in the refrigerator.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1486165B86E0C648B173DE1FE10EB744', X'A1B9661401DD8C45B650935C242DC8E0', X'A1B9661401DD8C45B650935C242DC8E0', 0, 'javascript:', 'Smoked Vegetarean Eel', 2, NULL, 0, 'Smoked Vegetarean Eel', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2038', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound fresh bean curd, most water squeezed out, the shredded<BR>3 large black Chinese mushrooms, soaked until soft, stems discarded, then shredded<BR>1 cup enoki mushrooms, cut in small pieces<BR>2 Tablespoons fresh ginger, shredded<BR>½ teaspoon salt and ground black pepper<BR>2 Tablespoon flour<BR>1 teaspoon goji berries, chopped<BR>1/4 cup sugar<BR>½ cup dry rice<BR>½ cup tea leaves<BR>2 seaweed sheets<BR><B><I>Preparations:</B></I><BR>1. Mix prepared bean curd, black and enoki mushrooms, and the ginger, and the goji berries and flour; then roll this mixture into the two seaweed sheets, wet to paste the edges, and let rest ten minutes on these edges.<BR>2. Steam these rolls for fifteen minutes, seam side down in a steamer basket over boiling water for twelve minutes.<BR>3. Mix sugar, rice, and tea leaves and put them into a wok, put the rolls on this and cover and smoke for about fifteen minutes until they give off a smoky aroma.<BR>4. Cut each roll into four pieces, and serve. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'188330392347D3448655ED4AF47D1F20', X'A110F3C6A316EF49A118529374DC3109', X'A110F3C6A316EF49A118529374DC3109', 0, 'javascript:', 'Stir-fried Assorted Mushrooms', 2, NULL, 0, 'Stir-fried Assorted Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2254', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 large button mushrooms, stem ends removed and diced, each mushroom cut into quarters<BR>1 king oyster mushroom, sliced thin, each slice cut in half<BR>1 bunch hon-shimeji mushrooms, stem ends cut away and discarded<BR>1 bunch enokitake mushrooms, their ends cut away and discarded<BR>4 abalone mushrooms, each cut into quarters top to bottom<BR>2 spring onions, cut into one-inch pieces<BR>2 large cloves fresh garlic, peeled and chopped<BR>2 Tablespoons mock oyster sauce<BR>2 teaspoons sesame oil, in two parts<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or a fry pan, add the sesame oil, scallion pieces, and the garlic and stir-fry for one minute, then add all the mushrooms and the oyster sauce and two tablespoons cold water, stirring well.<BR>2. Stir-fry for two minutes, remove to a pre-heated bowl and serve sprinkling the other half of the sesame oil over them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A937CEDAE3FBAC4FA3AB616549A7E3F8', X'25AE0011D44DC9498DDEC8BC4EF393DB', X'25AE0011D44DC9498DDEC8BC4EF393DB', 0, 'javascript:', 'Sugar Snaps and Other Vegetables', 2, NULL, 0, 'Sugar Snaps and Other Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2253', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound broccoli, cut into small florets<BR>1 cup sugar snap peas, ends and strings removed and discarded, each cut in half at an angle<BR>1/4 cup peeled carrots, roll-cut on an angle<BR>5 cloves fresh garlic, peeled and cut in thick slices<BR>20 ginkgo nuts, cores removed<BR>1 Tablespoon coarse salt<BR>3 Tablespoons vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or fry pan and add the oil, and in half a minute, all the vegetables and toss them.<BR>2. Add two tablespoons of water and the salt, and stir-fry for one to two minutes, then serve in a pre-heated bowl.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0A7B9961706BDC47A679A19D3EE4337B', X'339A42152641EA4AA62870E1074EA300', X'339A42152641EA4AA62870E1074EA300', 0, 'javascript:', 'Tofu with Red and Green Peppers', 2, NULL, 0, 'Tofu with Red and Green Peppers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2232', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>1 Tablespoon fresh ginger, grated<BR>½ cup fermented black beans, rinsed and drained<BR>10 ounces pressed tofu, shredded or cubed<BR>1 cup green and red bell peppers, seeded and cut one-quarter-inch wide, each, angle cut to two<BR>½ red chili pepper, seeded and cut tin<BR>1 Tablespoon dark soy sauce<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add oil, then the ginger, and stir-fry up to two minutes. Then add the black beans and fry for one more minute.<BR>2. Next, add tofu and bell peppers, and stir-fry two minutes more until almost done. Next, add chili pepper and the soy sauce; toss briefly, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'09A5AB211CA9EB49A2F7ACB77D44DA98', X'45D8BF30244B3744A6A6D77F144C4AA3', X'45D8BF30244B3744A6A6D77F144C4AA3', 0, 'javascript:', 'Vegetarian Frogs Legs', 2, NULL, 0, 'Vegetarian Frogs Legs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2112', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 to 2 cups enoki mushrooms, dried in paper towels<BR>1 cup vegetable oil<BR>4 canned baby corns, each cut lengthwise into four<BR>1 red piquant chili pepper, seeded and slivered lengthwise<BR>1/2 teaspoon fermented black beans (optional)<BR>1/2 green pepper, seeded and cut into thin pieces lengthwise<BR>2 Tablespoons soy sauce<BR>2 Tablespoons cornstarch<BR>1 Tablespoon granulated sugar<BR>4 sprigs fresh coriander, each cut into four pieces<BR><B><I>Preparation:</B></I><BR>1.Heat oil in a wok or a deep pot and fry the mushrooms until they turn tan, then remove and save the oil for another use.<BR>2. Into the drained but not dried wok or fry pan, fry the corn, chili pepper, and the black beans if using them, the green pepper slivers, and stir-fry for one minute, then add the soy sauce, cornstarch minced with two tablespoons of cold water, and the sugar, and stir-fry for one more minute.<BR>3. Plate into a pre-heated serving bowl, stirring in half the coriander and putting the rest on top; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CB1F5A3E2BA4AE44872CD2B6169019E5', X'799BDCB778AFAF4BB1A3D1E8054C8816', X'799BDCB778AFAF4BB1A3D1E8054C8816', 0, 'javascript:', 'Vegetarian or Mock Eel', 2, NULL, 0, 'Vegetarian or Mock Eel', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2036', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 large Chinese mushrooms, soaked until very soft, stems removed and discarded, and minced<BR>3 Tablespoons cornstarch<BR>1 cup vegetable oil<BR>1 Tablespoon chopped fresh ginger<BR>1 Tablespoon dark soy sauce<BR>½ teaspoon sugar<BR>1 Tablespoon white vinegar<BR>½ teaspoon ground white pepper<BR>½ teaspoon five-spice powder<BR>½ cup vegetarian soup stock<BR>1 Tablespoon white sesame seeds, ground<BR><B><I>Preparation:</B></I><BR>1. Toss mushrooms with cornstarch, shape like eel pieces, then deep fry until crisp.<BR>2. Remove all but one to two tablespoons of the oil, then stir-fry the ginger for half a minute, then add the rest of the ingredients, mix well and use as is or add to other vegetarian items, as desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9A5EF404EB6E1446AC5119F93A852C40', X'9625C1CE21EC9349B1CD25FF245A83AF', X'9625C1CE21EC9349B1CD25FF245A83AF', 0, 'javascript:', 'Wet and Dry Green and Black', 2, NULL, 0, 'Wet and Dry Green and Black', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2063', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup dry tree ear fungi, soaked for half an hour<BR>2 tablespoons rice vinegar<BR>½ teaspoon coarse salt<BR>1 teaspoon sesame seed oil<BR>1 cucumber, chopped coarsely<BR>2 Tablespoons coarsely minced fresh ginger<BR><B><I>Preparation:</B></I><BR>1. Heat the vinegar with one tablespoon water, and stir in the salt until dissolved, then add the soaked tree fungi. Then remove from the heat.<BR>2. When removed from the heat, add the cucumber and the minced ginger. Let cool for half an hour, then serve or serve later at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AB9BB62CB0C1114FA22DA4091EB47EF1', X'8B9301DDA65A0A43A1BBB7D3E438E94C', X'8B9301DDA65A0A43A1BBB7D3E438E94C', 0, 'javascript:', 'Chayote, Tofu, and Red Pepper', 2, NULL, 0, 'Chayote, Tofu, and Red Pepper', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1902', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon lard<BR>6 cloves garlic, peeled and sliced thin<BR>1/2 pound firm tofu, cubed<BR>2 whole chayote, peeled, then diced into half-inch pieces<BR>1 small hot red pepper, seeded and diced finely<BR>4 sliced fresh ginger<BR>salt and pepper to taste<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the lard, and the garlic and stir-fry for one minute before adding the tofu pieces and fry for one minute, then add the chayote pieces, hot pepper cubes, and the ginger.<BR>2. Stir-fry one minute, then reduce the heat, add the salt and pepper, and stir-fry for five to ten minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B417EB7D046E2B4BA499F18B5ABD0F8F', X'543AA4F759ED074D94C5C4260DF70B78', X'543AA4F759ED074D94C5C4260DF70B78', 0, 'javascript:', 'Chestnuts and Celery Cabbage', 2, NULL, 0, 'Chestnuts and Celery Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=639', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1/2 pound fresh peeled chestnuts, or boiled and reconstituted dried ones<BR>10 large dried Chinese mushrooms, soaked in two cups warm water, drained and liquid saved, their stems removed<BR>3 Tablespoons corn oil<BR>4 to 6 cups celery cabbage, cut in one-inch wide pieces<BR>1 teaspoon sugar<BR>1/2 teaspoon coarse salt<BR>1 Tablespoon vegetarian oyster sauce<BR><B><I>Preparation:</B></I><BR>1. Blanch chestnuts in hot water for three minutes, drain, and cut each one in half.<BR>2. Cut mushrooms in quarters. Strain any solids out of the reserved mushroom water.<BR>3. Heat oil, and stir-fry the mushrooms for two minutes, then add half the celery cabbage pieces and stir-fry them for about four or five minutes, each minute or two adding some from the other half of the celery cabbage pieces.<BR>4. Add the mushroom water, sugar, salt, and vegetarian oyster sauce, cover, and simmer for three more minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'255411B7DD59FA4E86AF235342955CAB', X'88BF15C23D405F48BB25C13E831A3F99', X'88BF15C23D405F48BB25C13E831A3F99', 0, 'javascript:', 'Chestnuts and Chinese Yams', 2, NULL, 0, 'Chestnuts and Chinese Yams', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=565', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup peeled dry chestnuts<BR>1/2 cup corn oil<BR>1 pound Chinese yams, peeled and cut into one-inch or larger cubes<BR>1 envelope powdered tea, half teaspoon or two grams in weight<BR>1/2 cup yellow rock sugar<BR>1 Tablespoon maltose<BR><B><I>Preparation:</B></I><BR>1. Simmer the chestnuts for an hour, then drain them.<BR>2. Heat oil and fry the yam cubes until slightly browned on all sides, drain and set aside, and then fry chestnuts for two minutes and drain them. Set oil aside for another purpose.<BR>3. Put half cup of water into a wok or pot and bring it to the boil. Add the sugar and maltose and lower heat simmering this until it is sticky, about five minutes, then add the yams and the chestnuts and mix well.<BR>4. Sprinkle the powdered tea on this, stir, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FF799E3BDA1D254EAD85D04E46AA5808', X'8A17C77A539AC24A9B723836E984724D', X'8A17C77A539AC24A9B723836E984724D', 0, 'javascript:', 'Chestnuts and Hair Vegetable', 2, NULL, 0, 'Chestnuts and Hair Vegetable', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=636', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup hair vegetable<BR>1 Tablespoon corn oil<BR>2 slices fresh ginger, each cut into three or four pieces<BR>1/2 pound pork tenderloin or boneless chops cut into three-inch pieces, then tossed with one tablespoon water chestnut flour (or cornstarch)<BR>1 Tablespoon dark or mushroom soy sauce<BR>1 Tablespoon oyster or vegetarian oyster sauce<BR>1 teaspoon sugar<BR>1/2 teaspoon salt<BR>1/2 pound peeled fresh chestnuts<BR>2 Tablespoons cornstarch mixed with two tablespoons cold water<BR>2 Tablespoons coarsely chopped fresh coriander<BR><B><I>Preparation:</B></I><BR>1.Blanch hair vegetable in boiling water for one minute, drain, then soak in cold water for fifteen minutes. Then cut these strands into two- or three-inch pieces.<BR>2. Heat oil in wok or heat-proof casserole and fry ginger for thirty seconds. Add flour-coated pork pieces and cook stirring, until browned, about three minutes.<BR>3. Add both sauces, sugar, salt, and chestnuts and bring almost to the boil, reduce the heat, and simmer for one hour, then add hair vegetable and simmer another half hour.<BR>4. Thicken with cornstarch mixture and stir-fry until thick, then if not already in a serving casserole, pour this into a pre-warmed one, sprinkle coriander on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AB25D23EA56C154790263D9B47943835', X'FBC0113C1D675D4EBB11AF126065FC0B', X'FBC0113C1D675D4EBB11AF126065FC0B', 0, 'javascript:', 'Chinese Candied Yams', 2, NULL, 0, 'Chinese Candied Yams', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1716', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound Chinese yams, peeled and sliced thinly<BR>3 cups vegetable oil for deep-frying<BR>1 cup granulated sugar<BR>2 Tablespoons sesame seeds<BR><B><I>Preparation:</B></I><BR>1. Toss the yam slices in a little oil to keep them from sticking while heating the oil in a wok or deep pot.<BR>2. Fry the yam slices until medium brown.<BR>3. In a small pot, heat the sugar until it melts, turns light brown, and almost boils. Then dip the yam slices in it, one by one, and touch each one on one side to the sesame seeds. Lay them on paper towels, the side with the seeds up, and allow to dry for one hour or more, or serve them hot before they dry.<BR><B>Note:</B> They can be served hot or at room temperature, as desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BC8B4F3426839E4094C9536992230EB2', X'58E901CB614A6540B15228B8D8B73642', X'58E901CB614A6540B15228B8D8B73642', 0, 'javascript:', 'Chinese Chestnuts and Celery', 2, NULL, 0, 'Chinese Chestnuts and Celery', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=692', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound Chinese chestnuts<BR>12 large dried Chinese black mushrooms, soaked for one hour in two cups warm water, discard their stems, and reserve one-quarter cup of the mushroom water<BR>1/4 cup corn oil<BR>4 cups Chinese celery, washed, dried, and cut in one-inch lengths<BR>1 teaspoon sugar<BR>3 Tablespoons thin soy sauce<BR>1/2 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Cut slit in one side of each chestnut. Then put in pot of boiling water and lower the heat. Simmer for half hour, then peel while hot.<BR>2. Cut each mushroom in quarters.<BR>3. Heat oil, add mushrooms and celery pieces and stir-fry for two minutes, then add sugar, quarter cup of mushroom water and soy sauce, cover, and simmer for five minutes.<BR>4. Add chestnuts, soy sauce and salt and bring to the boil. Boil until all liquid evaporates, three to five minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'43C02F529912A9458F6903B23F250A6C', X'561B0631D2560A4897133E054842449D', X'561B0631D2560A4897133E054842449D', 0, 'javascript:', 'Chrysanthemum Greens', 2, NULL, 0, 'Chrysanthemum Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1330', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds chrysanthemum greens, washed and drained<BR>1 square brown bean curd, (2 x 2 inches by ½ inch)<BR>1 Tablespoon sugar<BR>1 Tablespoon Chinese rice wine<BR>3 Tablespoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Cut the greens into two-inch long pieces, and blanch them in boiling water for one minute then immediately into ice water, drain, squeeze out any remaining water, and then toss with chopsticks to separate the pieces.<BR>2. Mix slivers of bean curd with the chrysanthemum leaves, toss and then add the sugar, rice wine, and sesame oil and toss again, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'010747C8A6B6324DB14724A938657789', X'AD05E65CBD7EFC459AEE4C5ECAAC21F6', X'AD05E65CBD7EFC459AEE4C5ECAAC21F6', 0, 'javascript:', 'Chrysanthemum Stuffed Cabbage', 2, NULL, 0, 'Chrysanthemum Stuffed Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1329', '<TD><RECIPE><I><B>Ingredients: </B></I><BR>10 large pieces of cabbage<BR>1 Tablespoon granulated sugar<BR>2 Tablespoons vegetable oil, divided in half<BR>3 Chinese black mushrooms, soaked, stems discarded, and minced<BR>1 Tablespoon fresh ginger, peeled and minced<BR>1 teaspoon Chinese brown sugar<BR>1 tablespoon thin soy sauce<BR>1 Tablespoon water chestnuts, minced<BR>1 Tablespoon goji berries, coarsely chopped<BR>1/2 cup minced chrysanthemum leaves<BR>1 sheet dried bean curd, soaked for five minutes in warm water, then cut into ten pieces<BR>1/2 teaspoon salt<BR>1/4 teaspoon ground white pepper<BR>1/2 cup chicken or vegetable stock<BR><B><I>Preparation:</B></I><BR>1. Bring three cups of water to the boil, add the granulated sugar and one tablespoon of the oil, then the cabbage leaves, turn off the heat and allow to stand for eight minutes, then drain the leaves and set then aside.<BR>2. Heat other tablespoon of the oil and stir-fry the mushrooms and the minced ginger for one minute, then add the brown sugar, soy sauce, water chestnuts, the <I>goji</I> berries, and the minced chrysanthemum leaves and stir-fry for two minutes, then set aside to cool somewhat.<BR>3. Lay one cabbage on a board or plate, and cut away the thick center part of the leaf. Put one piece of the bean curd on it in the center, and two scant tablespoons of the mushroom mixture in the center of this. Roll partway, fold in the sides and roll completely and put edge side down on a heat-proof deep plate.<BR>4. Pour the stock over the cabbage rolls and steam over boiling water for five minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5EB3225C6E3871429BC14C56B3034C15', X'95C7E55F81D5E24698CDDB23FCF7F2E7', X'95C7E55F81D5E24698CDDB23FCF7F2E7', 0, 'javascript:', 'Cold Dried Bean Curd', 2, NULL, 0, 'Cold Dried Bean Curd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1301', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup bean curd sticks (also called dried bean curd skin), soaked in cold water for one hour<BR>1/2 cup peanuts (if salted, rise them)<BR>1 star anise<BR>10 Sichuan peppercorns<BR>4 stalks Chinese celery, cut into pieces the same size as the bean curd<BR>1 small carrot cut the same way as the bean curd or three tablespoons of goji berries<BR>2 teaspoons sesame oil<BR>1/4 teaspoon salt<BR>1/2 teaspoon Chinese black vinegar<BR><B><I>Preparation:</B></I><BR>1. Cut bean curd sticks in half the long way, then into one-quarter-inch pieces.<BR>2. Put bean curd pieces and the peanuts into a small pot of water, bring to the boil, reduce the heat, and simmer for five minutes, the bean curd should have no hard part left. Remove with a slotted spoon and set aside in a small serving bowl lined with a paper towel.<BR>3. Add star anise, Sichuan peppercorns, celery, carrot or goji berries and simmer that for three more minutes, then drain and add to the bean curd mixture. Using a paper towel, dry the solids in this bowl then add sesame oil, salt, and black vinegar. Stir well, then refrigerate for one to two hours. Serve cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'115E21CED7583941BBF26A0D179821BE', X'A89FB82C381BED4C8C1A043147EA8D7F', X'A89FB82C381BED4C8C1A043147EA8D7F', 0, 'javascript:', 'Cold Mustard Greens', 2, NULL, 0, 'Cold Mustard Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1835', '<TD><RECIPE><B><I>Ingredients: </B></I><BR>1 pound mustard greens<BR>1/2 cup soy beans<BR>1 carrot, peeled and diced<BR>1 Tablespoon soy sauce<BR>1 Tablespoon sesame oil<BR>1 teaspoon salt<BR>1 teaspoon black vinegar<BR><B><I>Preparation;</B></I><BR>1. Cut mustard greens into one inch pieces, then simmer them in one quart of boiling water for five minutes, then drain and reserve the water, if desired, for another use.<BR>2.Add soy beans, carrots, soy sauce, sesame oil, and the vinegar, cover, and simmer for three minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'92CBB1DE5416CF49B47187B0E8B2FF73', X'1E2DA24B534FA540AA0BFA02A0CD1D75', X'1E2DA24B534FA540AA0BFA02A0CD1D75', 0, 'javascript:', 'Creamy Simmered Broccoli Stems', 2, NULL, 0, 'Creamy Simmered Broccoli Stems', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1257', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 broccoli stems, thick exteriors peeled off and discarded<BR>4 crab legs, boiled, shells discarded, and each cut into two-inch pieces<BR>1 piece swordfish, about three ounces, cut into thick pieces<BR>2 scallops, each cut in half<BR>4 shrimp, shelled, veins removed<BR>2 ounces cuttlefish, make shallow angle-cuts on surface, then cut in thin slices<BR>1/4 teaspoon salt and 1/4 teaspoon ground white pepper, mixed then divided into half<BR>1 and 1/4 Tablespoons potato starch<BR>2 Tablespoons vegetable oil<BR>1 small wood ear mushroom, soaked and slivered<BR>1/2 scallion or leek, cut into one-half-inch pieces<BR>2 thin slices fresh ginger, slivered<BR>1 cup chicken or another soup stock<BR>1 Tablespoon sake<BR>dash of sugar<BR>2 Tablespoons cornstarch mixed with same amount of cold water<BR><B><I>Preparation:</B></I><BR>1. Cut broccoli in half, then into half-inch pieces.<BR>2. Mix all foods of the sea with half the salt/pepper mixture, then add in potato starch and mix well.<BR>3. Heat oil in a wok or fry pan and stir-fry the broccoli for one minute, then remove them from the oil.<BR>4. Add all sea foods to the oil, and stir-fry the scallion or leek pieces and the ginger, and stir-fry for one minute before adding soup stock, mushrooms, and the other half of the salt and pepper mixture.<BR>5. Add the well-mixed cornstarch paste, stir until it thickens, then pour into individual bowls or a large soup tureen, and serve.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3A8DC7B9C3253B42B28BC053C9B6CDDC', X'EA62583645CC5E4AABD2584806C9AB0A', X'EA62583645CC5E4AABD2584806C9AB0A', 0, 'javascript:', 'Crispy Seaweed', 2, NULL, 0, 'Crispy Seaweed', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=346', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 and 1/2 pounds <I>bok cai</I><BR>4 cups peanut oil<BR>1 teaspoon coarse salt<BR>2 teaspoons sugar<BR>1/2 cup pine nuts, lightly roasted in the oven until pale tan<BR><B><I>Preparation:</B></I><BR>1. Separate stalks from the leaves and set aside for another use.<BR>2. Wash the leaves well, drain thoroughly, and dry in a salad spinner. Then roll them tightly and finely shred them.<BR>3. Preheat an oven to 250 degrees F, and put the shredded leaves in for fifteen minutes. Remove and cool, then divide into four batches.<BR>4. Heat oil and deep fry one batch for half a minute, no longer, and remove and drain on paper towels. Repeat until all are done.<BR>5. When cool, toss with salt and sugar and the pine nuts, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2FCCEB2203AA3141932C8320F5897E83', X'796250BB9DAF954BB89792433FC4473E', X'796250BB9DAF954BB89792433FC4473E', 0, 'javascript:', 'Cucumber on Rice-noodle Sheets', 2, NULL, 0, 'Cucumber on Rice-noodle Sheets', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=511', '<TD><RECIPE><B><I>Ingredients for cucumber salad:</B></I><BR>1 cucumber, peeled, leaving some strips of dark green, seeded and cut into thin two inch strips<BR>1 Tablespoon mature black rice vinegar<BR>1 Tablespoon white rice vinegar, Swatow rice vinegar preferred<BR>1 teaspoon ginger juice<BR><B><I>Ingredients for the rice noodle sheets:</B></I><BR>1 cup rice flour<BR>1/2 cup cornstarch<BR>1/2 cup potato starch<BR>1/4 cup corn or another light vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Mix all ingredients for the cucumber salad and let this stay, not refrigerated, for two hours.<BR>2. Mix rice flour and the two starches and add two cups of water. Mix very well being sure there are no lumps and let this pancake mixture rest for two hours.<BR>3. Take a baking sheet and lightly oil it. Prepare a large pot cover to use with it.<BR>4. Heat an eight- or nine-inch omelet pan and drizzle a few drops of oil on it. Spread this around with a pastry brush, then put one-quarter cup of the pancake batter on it and tilt the pan to spread this batter evenly. Be careful not to have the heat too high. Cover the batter and make a pancake cooking it only on one side. It takes about two minutes until the top is set. Turn this upside down onto the baking sheet and put the pot cover over it. After two minutes, put the pancake on a plate oiling the other side of it. Repeat until all the pancakes are made. They can be stored in the refrigerator overnight.<BR>5. Thinly slice about half of the pancakes made, tossing them with a tablespoon of the oil. Heat them carefully stirring them in a wok or a fry pan. Slice then add the rest of the pancakes, and when hot, put them all into a serving bowl. Put the cucumber mixture on top, then serve. Toss just before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AE5555C45A8D484797FA7D70AAFBB3D3', X'0F2C10AAD2DB8746AEC7749962886F1C', X'0F2C10AAD2DB8746AEC7749962886F1C', 0, 'javascript:', 'Curry Pumpkin and Potato', 2, NULL, 0, 'Curry Pumpkin and Potato', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1133', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound Chinese pumpkin, peeled and cut into two-inch squares<BR>1 large potato, peeled and cut into two-inch squares<BR>1/8 head cabbage, cut into two-inch squares<BR>1/4 onion, shredded<BR>1 Tablespoon vegetable oil<BR>1 teaspoon minced garlic<BR>1 teaspoon minces shallot<BR>1 Tablespoon curry powder<BR>1/4 teaspoon chili powder<BR>1 teaspoon fish sauce<BR>2 Tablespoons coconut milk<BR>5 dried prunes<BR><B><I>Preparation:</B></I><BR>1. Put pumpkin, potato, cabbage and onion into a large bowl, and set aside.<BR>2. Heat wok or fry pan, add oil, and stir-fry minced garlic and shallot for one minute. Then add pumpkin mixture, and continue to stir-fry for three or four minutes.<BR>3. Add curry and chili powders and continuing to stir-fry for another minute; then add half cup of water, and simmer for twenty minutes.<BR>4. Add fish sauce, coconut milk, and the prunes, and boil for two minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F8B3822129A55F43BEB01D82480FBE26', X'FD0F0111DBC2FB459310A102365DBCEA', X'FD0F0111DBC2FB459310A102365DBCEA', 0, 'javascript:', 'Dai Tomato-mint Salad', 2, NULL, 0, 'Dai Tomato-mint Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1664', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoon vegetable oil<BR>1 dried chili pepper, seeded and chopped fine<BR>3 cloves garlic, peeled and minced<BR>3 cups loosely packed mint leaves and stems and tops coarsely chopped<BR>2 cups cherry tomatoes, halved<BR>1 and 1/2 Tablespoons Chinese white rice vinegar<BR>1/2 teaspoon coarse salt<BR><B><I>Preparation:</B></I><BR>1. Heat wok, add oil, chilies, and garlic, stir for a minute then remove them from the heat and allow to cool to room temperature.<BR>2. In large bowl, mix mint and tomatoes, add cooled chili oil and stir. Then add the vinegar and salt and stir until mint leaves and tomatoes are evenly coated before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C6430EFF3C11394BBA94C6AF1E01E141', X'99F4E0349BFE964D9CE65A6F78970F52', X'99F4E0349BFE964D9CE65A6F78970F52', 0, 'javascript:', 'Deep-fried Stuffed Eggplant', 2, NULL, 0, 'Deep-fried Stuffed Eggplant', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=450', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 ounces peeled shrimp, chopped or ground<BR>1 scallion top, minced fine<BR>1/2 teaspoon salt<BR>1 teaspoon rice wine<BR>1 teaspoon cornstarch<BR>1 pound thin Japanese-style eggplants<BR>1/4 teaspoon salt<BR>3 Tablespoons cornstarch<BR>1 Tablespoon four<BR>2 cups corn oil<BR>1 whole scallion, thinly sliced on the angle<BR>1/2 cup chicken broth<BR>1 teaspoon fermented black beans, mashed slightly<BR>1/2 teaspoon thin soy sauce or any flavored soy sauce<BR>1/4 teaspoon sugar<BR>1 teaspoon cornstarch mixed with one tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Prepare the filling by mixing shrimp, green scallion top, half teaspoon salt, rice wine and the teaspoon of cornstarch, then set it aside.<BR>2. Cut off the ends of the eggplant and angle-cut them into one inch thick ovals. Take a small one-quarter-inch wedge out of each one; be careful not to cut in more than two-thirds of the way through. Salt the eggplant pieces with the remaining quarter teaspoon and let this rest for twenty minutes. While waiting, preheat the oven to 200 degrees Fahrenheit.. Then wipe each eggplant slice dry with a paper towel, and stuff each one with some of the shrimp mixture.<BR>3. Mix flour and cornstarch and dip each eggplant slice in it.<BR>4. Deep fry a few eggplant pieces until lightly browned. Drain and put on a metal sheet and keep them warm in the oven. Repeat until all are fried and drained. Remove oil to another container.<BR>5. Heat one teaspoon of the oil that was set aside and add the pieces of the whole scallion and fry for half a minute. Add the chicken broth, black beans, soy sauce, and sugar and a soon as it boils, add cornstarch water and cook until thickened, about a minute.<BR>6. Put eggplant pieces on a serving dish, drizzle the sauce on them and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8B454A531F0C4643AE7A0FD1D68E4518', X'DBCBBCDBF682304FA8AB667DB4D65B0F', X'DBCBBCDBF682304FA8AB667DB4D65B0F', 0, 'javascript:', 'Deep-fried Vegetarian Eel', 2, NULL, 0, 'Deep-fried Vegetarian Eel', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1220', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 large dried Chinese black mushrooms, soaked until soft, stems removed<BR>2 Tablespoons cornstarch<BR>1 teaspoon salt<BR>1 egg<BR>2 Tablespoons Shaoxing wine<BR>2 cups vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Cut along rim of each mushroom into thick spiral strips about two inches long. Squeeze out the soaking water and marinate in salt, egg, and wine mixture for two hours.<BR>2. Coat each mushroom piece in starch and shake away excess starch; repeat this process four times.<BR>3. Heat oil and deep fry mushroom strips until golden and crispy. Drain and serve with a mix of pepper and salt.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B953224428F1504E8430E8DA2B9B2153', X'F87A93E601829B48B031DCF7B4AEE34D', X'F87A93E601829B48B031DCF7B4AEE34D', 0, 'javascript:', 'Dong Vegetables', 2, NULL, 0, 'Dong Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1014', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons Chinese clear hard liquor<BR>2 Tablespoons coarse or pickling salt<BR>2 Tablespoons Chinese rice wine<BR>1 teaspoon rock or granulated sugar<BR>2 cups Chinese white radish or celery cabbage<BR>1 chili pepper (optional)<BR><B><I>Preparation:</B></I><BR>1. Bring six cups water to the boil. Use two of them to rinse out a clean mason jar and its cover, and set the others aside to cool to lukewarm.<BR>2. Put liquor, salt, rice wine, and sugar into the jar along with up to one cup of the somewhat cooled water. Stir until salt and sugar are completely dissolved. Then pack in the radish or the celery cabbage and fill with the water that was set aside. If using the chili pepper, rise it with some of the set aside boiled water and put it in the jar before adding the vegetables. Cover and put the jar in a cool dark place for twenty-four to thirty-six hours.<BR>3. Remove the vegetables, drain well, and serve.<BR><B>Note:</B> Additional vegetables can be put into the jar, the first four ingredients replenished, if and as needed, and another batch of vegetables inserted and set aside to sour. It is best to use the soured vegetables in one or two days.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6C173CEC0016B943AC149BE7AC3D4840', X'4DA71DB640E9484887F1FBC6A7037C2F', X'4DA71DB640E9484887F1FBC6A7037C2F', 0, 'javascript:', 'Dongbei Cai', 2, NULL, 0, 'Dongbei Cai', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1335', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup vegetable oil<BR>1 leek, white part only, cut into half-inch pieces<BR>1/2 pound finely shredded green cabbage<BR>1 cucumber, seeded, quartered the long way, and thinly sliced<BR>1 carrot, peeled then shredded<BR>1/4 cup roasted peanuts, paper skins removed<BR>1 cup fresh bean sprouts, their tails removed<BR>1/2 cup shredded form tofu or brown or white tofu noodles<BR>2 teaspoons Chinese black vinegar<BR>1 teaspoon thin soy sauce<BR>1 teaspoon granulated or crushed Chinese brown sugar<BR><B><I>Preparation:</B></I><BR>1. In small sauce pan, heat vegetable oil and just before it smokes, remove from heat, drop in pieces of leek, and stir for one minute, then set aside for half an hour. Strain out the leeks, and use or discard, as preferred. If using them, put them into a medium size bowl with two teaspoons of the oil. Save the rest of the oil for another use, or use it for cooking a vegetable dish.<BR>2. Put all other ingredients into the bowl with the leeks, if using them, and the oil, toss well, and serve as a cold vegetable.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'64E856BF94762E4DB8E7F001500E7913', X'3231BA86FF485D4FB900EE59C6E474B9', X'3231BA86FF485D4FB900EE59C6E474B9', 0, 'javascript:', 'Dongbei Sour Cabbage Dumplings', 2, NULL, 0, 'Dongbei Sour Cabbage Dumplings', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=939', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup canned or fresh pickled cabbage (sauerkraut), rinsed for two minutes under cold running water<BR>1/4 cup minced lamb<BR>1 scallion, white part only<BR>1 slice fresh ginger, peeled and minced<BR>1/2 teaspoon rice wine<BR>1 cup flour<BR>1/2 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Coarsely mince the pickled sour cabbage, then mix it with the lamb, scallion, and rice wine. 2. Mix flour with half cup water, and add more by the tablespoon until a firm dough is formed. Slap this against a hard surface a few time, then divide the dough into ten pieces and roll each one out until a five-inch circle. Repeat until all are rolled out. 3. Divide the cabbage mixture into ten parts, and put one tenth on each circle, wet the edges, fold in half, and pinch them together with two or three pleats. 4. Cook these dumplings in five or six cups of salted boiling water for five minutes, add a cup of cold water, and when they rise to the top, remove with a slotted spoon. Serve alone or with a dip of half soy sauce and half Chinese black vinegar.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5E954BD54FA4E940B1B1537A52727451', X'24FBE557D357784A8FDFC337ABB58B0F', X'24FBE557D357784A8FDFC337ABB58B0F', 0, 'javascript:', 'Dou Miao with Fermented Bean Curd', 2, NULL, 0, 'Dou Miao with Fermented Bean Curd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=329', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 cups chicken broth<BR>2 cups, lightly packed, large or small <I>dou miao</I>, torn (about two ounces)<BR>3 Tablespoons freshly mashed fermented bean curd square or grated Parmesan cheese<BR>dash of white pepper<BR>1 lemon, quartered <BR><B><I>Preparation:</B></I><BR>1. In a saucepan, bring the broth to a boil, then add the dou miao and remove the pan from the heat.<BR>2. As soon as the <I>dou miao</I> wilts, stir in fermented bean curd or the Parmesan cheese, and the white pepper.<BR>3. Serve with the lemon wedges, but advise your guests that the soup is good with or without them; each person can taste it both ways.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'84A093261E508C45BC704283F98D49A7', X'C27698BA17A32547B64B924BAE2B8C26', X'C27698BA17A32547B64B924BAE2B8C26', 0, 'javascript:', 'Dou Miao with Garlic', 2, NULL, 0, 'Dou Miao with Garlic', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1225', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons vegetable oil<BR>6 to 8 cloves garlic, peeled and slivered<BR>1 pound </I>dou miao</I><BR>1/2 teaspoon mixed salt and sugar<BR><B><I>Preparation:</B></I><BR>1. In a large wok or saucepan, heat oil, and when it ripples, add the garlic and stir-fry until fragrant, less than one minute.<BR>2. Add <I>dou miao</I> and the salt and sugar mixture and when it wilts, three tablespoons water and stir for one minute or until the water evaporates. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A598CBB8C668ED4BA157E0115259D7C8', X'194257A062E0B244A38674FDB3F61E2D', X'194257A062E0B244A38674FDB3F61E2D', 0, 'javascript:', 'Double Fungi with Celery', 2, NULL, 0, 'Double Fungi with Celery', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=404', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 stalks celery, their strings removed<BR>1 carrot, peeled and thin sliced the long way<BR>1/4 cup tree ear fungus, soaked in a quart of warm water<BR>1/4 cup silver ear fungus (tremella), soaked in a quart of warm water<BR>1/2 cup corn oil<BR>1/4 teaspoon salt<BR>1/4 teaspoon sugar<BR>1 teaspoon white wine, vodka, or gin<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Angle cut the celery into half inch pieces. If the stalks are thick, cut them in half the long way before doing the angle cutting. Do the same with the carrots.<BR>2. Drain the tree ears and pat them dry with paper towels. Tear apart any that are exceptionally large; pieces about one inch will look the best in the finished dish.<BR>3. Heat oil and stir-fry the celery, carrots, and both kinds of fungi for two minutes, then drain well. Put them back into the dried wok or fry pan and add one tablespoon of water. Once it boils, add the salt, sugar, and sesame oil and stir-fry one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B4E4247B166C26479C0F0F7ED041B0D9', X'9E93D0A2BAA7D74BA65A9222D50642ED', X'9E93D0A2BAA7D74BA65A9222D50642ED', 0, 'javascript:', 'Dried Bamboo Shoots I', 2, NULL, 0, 'Dried Bamboo Shoots I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1460', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 bamboo shoot, cut in one inch strips when fresh, then dry them in the sun<BR>1 Tablespoon tea oil<BR>1/2 teaspoon salt<BR>1/2 teaspoon soy sauce<BR><B><I>Preparation:</B></I><BR>1. Put the dried bamboo shoot strips into rapidly boiling water and simmer them for five minutes.<BR>2. Heat a wok or fry pan, add the tea oil, and then stir-fry the bamboo shoots for three to five minutes. Then add the salt and continue to stir-fry for another two minutes before adding the soy sauce. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1999C963EEBD7448A459F0653E90C6AA', X'9B7615377A72E94094EDB9EC2669CCE4', X'9B7615377A72E94094EDB9EC2669CCE4', 0, 'javascript:', 'Dried Bamboo Shoots II', 2, NULL, 0, 'Dried Bamboo Shoots II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1203', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 bamboo shoot, cut in one inch strips when fresh, then dry them in the sun<BR>1 Tablespoon tea oil<BR>1/2 teaspoon salt<BR>1/2 teaspoon soy sauce<BR><B><I>Preparation:</B></I><BR>1. Put the dried bamboo shoot strips into rapidly boiling water and simmer them for five minutes.<BR>2. Heat a wok or fry pan, add the tea oil, and then stir-fry the bamboo shoots for three to five minutes. Then add the salt and continue to stir-fry for another two minutes before adding the soy sauce. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A635441DB492064B87391F5AA4ACECEE', X'D7174FBB72EACB4F960AAAF2FF9BB6A4', X'D7174FBB72EACB4F960AAAF2FF9BB6A4', 0, 'javascript:', 'Eggplant in Spicy Sauce I', 2, NULL, 0, 'Eggplant in Spicy Sauce I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1385', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups oil for frying<BR>1 pound eggplant, cut into sticks the shape of french fries<BR>1 Tablespoon soy sauce<BR>1 Tablespoon light brown sugar<BR>1 Tablespoon spicy bean sauce<BR>1 Tablespoon rice vinegar<BR>1 Tablespoon rice wine<BR>1 teaspoon Asian sesame oil<BR>3 cloves fresh garlic, peeled and minced<BR>1 Tablespoon chopped fresh ginger<BR>4 whole chilies, soaked in water for ten minutes, dried with paper towels, then chopped<BR>1 cup thinly sliced pork tenderloin<BR>3 scallions, thinly sliced<BR><B><I>Preparation:</B></I><BR>1. Heat wok over medium-high heat, until the oil reaches 360 degrees F. Then and add half the eggplant pieces. Fry and stir for five minutes until the eggplant is limp, then remove it with a slotted spoon and drain on paper towels. Repeat with the rest of the eggplant. Discard all but two tablespoons of the oil.<BR>2. In a small bowl, combine soy sauce, brown sugar, bean sauce, rice vinegar, rice wine, sesame oil, and the cornstarch, and set this aside.<BR>3. Heat wok and the two tablespoons of oil, then add the garlic, ginger, and the chilies and stir-fry for one minute.<BR>4. Add the pork and stir-fry for two minutes, then return all the eggplant to the wok. Stir the sauce and add it next, and boil until the sauce thickens, about two minutes, then remove to a bowl or a platter with low sides.<BR>5. Garnish with the scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'194543DEBD0FA541850ACEF3F05C2D4B', X'60DD5B8B14682D43BC4F3F99A8739967', X'60DD5B8B14682D43BC4F3F99A8739967', 0, 'javascript:', 'Eggplant in Spicy Sauce II', 2, NULL, 0, 'Eggplant in Spicy Sauce II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1398', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups oil for frying<BR>1 pound eggplant, cut into sticks the shape of french fries<BR>1 Tablespoon soy sauce<BR>1 Tablespoon light brown sugar<BR>1 Tablespoon spicy bean sauce<BR>1 Tablespoon rice vinegar<BR>1 Tablespoon rice wine<BR>1 teaspoon Asian sesame oil<BR>3 cloves fresh garlic, peeled and minced<BR>1 Tablespoon chopped fresh ginger<BR>4 whole chilies, soaked in water for ten minutes, dried with paper towel, then chopped<BR>1 cup thinly sliced pork tenderloin<BR>3 scallions, thinly sliced<BR><B><I>Preparation:</B></I><BR>1. Heat wok over medium-high heat, heat the oil to 360 degrees F, and add half the eggplant pieces. Fry and stir for five minutes until the eggplant is limp, then remove it with a slotted spoon and drain on paper towels. Repeat with the rest of the eggplant. Discard all but two tablespoons of the oil.<BR>2. In a small bowl, combine soy sauce, brown sugar, bean sauce, rice vinegar, rice wine, sesame oil, and the cornstarch, and set this aside.<BR>3. Heat wok and the two tablespoons of oil, then add the garlic, ginger, and the chilies and stir-fry for one minute.<BR>4. Add the pork and stir-fry for two minutes, then return all the eggplant to the wok. Stir the sauce and add it next, and boil until the sauce thickens, about two minutes, then remove to a bowl or a platter with low sides.<BR>5. Garnish with the scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0BF4A54049EA544E958D811118E5F25B', X'105467007E328742956B1BAE95465B2E', X'105467007E328742956B1BAE95465B2E', 0, 'javascript:', 'Eggplant with Ginger Juiced Oil', 2, NULL, 0, 'Eggplant with Ginger Juiced Oil', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=167', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 eggplant, about a pound, peeled <BR>3 Tablespoons oil, peanut preferred<BR>2 teaspoons ginger juiced oil (see below)<BR>3 Tablespoons plain or shrimp-flavored soy sauce<BR>1 teaspoon sugar<BR><B><I>Preparation:</I></B><BR>1. Slice eggplant into eight pieces and then cut each of these in half.<BR>2. Heat oil and fry the eggplant for two minutes, stir-frying constantly. <BR>3. Add ginger juiced oil and stir. Then add the rest of the ingredients and half a cup of water. Simmer for fifteen minutes or until the eggplant is tender, then serve. <P><B>To make Ginger Juiced Oil:</B><BR><I><B>Ingredients:</I></B><BR>1 two inch piece of ginger<BR>6 Tablespoons corn oil<BR>2 teaspoons sugar<BR>1/4 teaspoon salt<BR><B><I>Preparation:</I></B><BR>1. Mince ginger root well.<BR>2. Heat oil until just before smoking, remove from the heat and add all the ingredients and stir well, then allow to cool. Strain out all solids and store in the refrigerator until needed.</P></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4435798B4CC80F4582D95C809CA0E2AD', X'9AA050975F297448A81BA7C54273F1B0', X'9AA050975F297448A81BA7C54273F1B0', 0, 'javascript:', 'Eggplant with Pork Sung', 2, NULL, 0, 'Eggplant with Pork Sung', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=719', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 thin purple Asian eggplants, about seven ounces each<BR>1 teaspoon coarse salt<BR>1/2 cup vegetable oil<BR>1 large clove garlic, minced<BR>1 Tablespoon sacha or fish sauce<BR>1 green pepper. Cut into half-inch pieces<BR>1 chili pepper, minced<BR>1 teaspoon granulated or crushed Chinese brown sugar<BR>1 teaspoon cornstarch mixed with one-quarter cup water<BR>2 teaspoons sesame oil<BR>4 Tablespoons pork sung<BR><B><I>Preparation:</B></I><BR>1. Cut the eggplants in half and rub each half with coarse salt. Let them stand for ten to fifteen minutes, then rinse and dry them. Make cuts, half inch apart, cross-hatching the flat surface of each eggplant only a quarter-inch deep.<BR>2. Heat oil and fry the garlic for half minute, then add the eggplant pieces and fry first on one side then the other until they are lightly browned on both sides. Remove them from the oil, leaving any remaining oil in the pan. Put the eggplant pieces on paper towels to drain, and allow them to sit for two or three minutes. Then put them in a single layer on a large plate or platter.<BR>3. Fry the sacha sauce and both peppers for one minute, then add the cornstarch water and stir-fry, stirring, for another minute. Next add sesame oil and stir-fry one more minute. Pour this over the eggplant pieces, sprinkle with the pork sung, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7EE5E30FEF14D04BB802A414AC6F0475', X'D9520A89DE75E543BB1C35C42B490A0C', X'D9520A89DE75E543BB1C35C42B490A0C', 0, 'javascript:', 'Eggplant, Cauliflower, and Potatoes, Dongbei-style', 2, NULL, 0, 'Eggplant, Cauliflower, and Potatoes, Dongbei-style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1238', '<TD><RECIPE><B><I>Ingredients: </B></I><BR>2 Tablespoons vegetable oil<BR>2 cloves garlic, peeled and minced<BR>2 Tablespoons minced fresh ginger<BR>2 Tablespoons minced fresh coriander<BR>2 Tablespoons ground blanched almonds<BR>1/2 teaspoon ground fennel<BR>1/2 teaspoon ground cayenne pepper<BR>1/4 teaspoon turmeric<BR>1 teaspoon paprika<BR>1 small eggplant (about one pound), cut in one-inch cubes<BR>1 small cauliflower, heavy center core removed, then cut into one-inch cubes<BR>3 medium potatoes, peeled and cut into one-inch cubes<BR>1/2 cup tomato puree<BR>2 teaspoons ground cumin powder<BR>1 teaspoon coarse salt<BR><B><I>Preparation:</B></I><BR>1. Heat large fry pan, add oil, then stir and fry the garlic and ginger for one minute before adding the coriander and ground almonds and cook stirring for two minutes more.<BR>2. Stir in the ground fennel, cayenne pepper, turmeric and paprika and cook stirring for two minutes.<BR>3. Add two cups of water, the eggplant, cauliflower, and the potatoes, and the tomato puree and bring this to the boil, reduce the heat , and simmer covered for half an hour before adding the ground cumin and the salt.<BR>4. Remove from the heat and let stand half an hour or until ready to serve. Reheat as necessary, on the stove or in a microwave.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D4151731AF3D30429E40222D20175249', X'1F8A35B25457D34FBACD6517B8E949B1', X'1F8A35B25457D34FBACD6517B8E949B1', 0, 'javascript:', 'Eggplant, Cumin, and Black Bean Salad', 2, NULL, 0, 'Eggplant, Cumin, and Black Bean Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=996', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon cumin seeds<BR>2 Tablespoons salted black beans, rinsed<BR>2 Tablespoons Chinese black vinegar<BR>1 Tablespoon thin soy sauce<BR>1 teaspoon sesame oil<BR>1/2 teaspoon superfine sugar<BR>1 garlic clove, peeled and crushed<BR>2 cups vegetable oil<BR>1 large eggplant, cut into three-quarter-inch cubes (about one pound)<BR>2 ripe tomatoes, cut into three-quarter-inch pieces<BR>2 scallions, finely shredded<BR>6 to 8 coriander sprigs, cut in one to one-and-a-half inch lengths<BR><B><I>Preparation:</B></I><BR>1. Put cumin seeds in small fry-pan over high heat, and shake it until they begin to crackle; then immediately remove from the heat and allow them to cool before grinding them in a mortar and pestle or a spice grinder.<BR>2. Mix the ground cumin, black beans, vinegar, soy sauce, sesame oil, sugar, and garlic stirring until the sugar dissolves. Set this dressing aside.<BR>3. Heat oil in a wok or large pot and when it shimmers, add half the eggplant and cook for two minutes or until golden. Drain on paper towels and repeat with the other half of the eggplant. Discard the oil or strain and set aside for another purpose.<BR>4. Put eggplant, tomatoes, scallions, and coriander in a bowl and toss well before setting this out on a serving platter. Pour dressing over this, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B53F1E6C90B104478662137D1DF86ED9', X'856CE4BC5D692B47BE67B57311B5FFD1', X'856CE4BC5D692B47BE67B57311B5FFD1', 0, 'javascript:', 'Eggplant, Sichuan Style', 2, NULL, 0, 'Eggplant, Sichuan Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1553', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons red bean paste<BR>1 Tablespoon soy sauce<BR>2 teaspoons vinegar<BR>2 teaspoons sugar<BR>1/2 teaspoon chilli flakes<BR>6 Tablespoons sesame oil<BR>1 pound Asian eggplant, peeled and cut into two-inch wedges<BR>2 cloves garlic, crushed<BR>1 Tablespoon grated fresh ginger<BR>1 teaspoon cornstarch mixed with one tablespoon of water<BR>2 Tablespoons finely chopped onions<BR><B><I>Preparation:</B></I><BR>1. Mix bean paste, soy sauce, vinegar, sugar and chili and set aside.<BR>2. Heat sesame oil in a wok or fry pan, then add eggplant, garlic, and the ginger and stir-fry over medium heat for five minutes.<BR>3. Add bean paste mixture, cornstarch, and onions and stir-fry for two minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ABA4C0531AF03A4CB70A2F78CE128B53', X'7096CB4E00533E4782943EF0FBD50B1C', X'7096CB4E00533E4782943EF0FBD50B1C', 0, 'javascript:', 'Five-flavored Tofu', 2, NULL, 0, 'Five-flavored Tofu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=662', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound firm tofu, cut into squares, and set on paper towels for five minutes<BR>1 cup corn oil<BR>1 Tablespoon minced basil<BR>1 Tablespoon minced fresh ginger<BR>1 Tablespoon minced sweet red bell pepper<BR>1 Tablespoon fresh coriander, minced<BR>1 Tablespoon celery, minced<BR>1 Tablespoon black vinegar<BR>1 Tablespoon vegetarian oyster sauce<BR>1 teaspoon sugar or corn syrup<BR>1 Tablespoon sesame oil<BR>1 Tablespoon catsup<BR><B><I>Preparation:</B></I><BR>1. Remove tofu to new dry paper towels and let set another five minutes.<BR>2. Heat oil and deep fry the tofu in three or four batches. Drain well, and set in a paper-towel-lined serving bowl. Repeat until all are fried and golden in color.<BR>3. Mix all the rest of the ingredients, and heat in a small pot until boiling. Remove the paper towel gently so as not to break the tofu apart, and pour the multi-ingredient sauce over the tofu and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0C77FD11FB57A74A8B982250AD1B7957', X'ECEFCF8CD72B0E41A77DD7ED5E05FCA3', X'ECEFCF8CD72B0E41A77DD7ED5E05FCA3', 0, 'javascript:', 'Flying Dragon Silk Squash', 2, NULL, 0, 'Flying Dragon Silk Squash', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1089', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 and 1/2 pounds silk squash (or large zucchini)<BR>2 Tablespoons peanut oil<BR>6 thin slices fresh ginger, cut into thin strips<BR>2 garlic cloves, peeled and finely chopped<BR>2 Tablespoons Shaoxing wine<BR>2 Tablespoons thin soy sauce<BR>1/2 cup chicken stock or water<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Prepare the vegetable by peeling and removing the ribs and some outer skin from the silk squash. Then cut each one in half crossways. Carefully score each piece at one-quarter inch intervals not cutting through to the bottom (using one chopstick on each side of the vegetable is a useful way to prevent cutting through the entire piece). This makes the ''flying dragon'' look.<BR>2. Heat wok, add the oil, and then add the ginger, garlic, and the vegetable and stir0fry for three minutes.<BR>3. Next, add wine, soy sauce, and chicken stock and simmer gently, uncovered, for five minutes or until the vegetable is tender.<BR>4. Drizzle with sesame oil and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'26BC6F52D765B64B90020EC93A0FAFEA', X'7D7B599258489345BF8CA3AA5382FABF', X'7D7B599258489345BF8CA3AA5382FABF', 0, 'javascript:', 'Fragrant Sweet Potato Balls', 2, NULL, 0, 'Fragrant Sweet Potato Balls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=244', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 medium sweet potato (about half pound), peeled and diced<BR>1/2 cup sweet potato powder<BR>2 Tablespoons sugar<BR>1/2 cup mashed brown bean or sweetened bean paste<BR>1 cup oil<BR>4 Tablespoons blanched chopped toasted almonds<BR><I><B>Preparation:</I></B><BR>1. Steam sweet potato in a rice cooker or boil them for fifteen minutes. Then set them aside and allow them to come to room temperature, then mash.<BR>2. Mix sweet potato powder and sugar with half cup of warm water and knead until a paste has formed. Then mix in the mashed sweet potato and divide into eight portions, flattening each one.<BR>3. Take one tablespoon brown bean paste and put it on one flattened sweet potato, directly in the center. Repeat until all are done, then close the sweet potato mixture around the bean paste. With your finger, slightly wet the top of each ball then press it into the chopped nuts.<BR>4. Heat oil and fry half of the balls, in about two minutes they will float to the top, when they do, remove them to paper towels and fry the second batch.<BR>Serve when hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A1BA9F133838C44FA3A75D74A2FA3639', X'16B41C8A7796574598AD05175EF45536', X'16B41C8A7796574598AD05175EF45536', 0, 'javascript:', 'Fried Bean Curd', 2, NULL, 0, 'Fried Bean Curd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1341', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>3 Tablespoons minced bamboo shoots<BR>3 Tablespoons soaked bamboo pith, minced<BR>1 teaspoon thin soy sauce<BR>1/2 teaspoon coarse salt<BR>1/8 teaspoon ground black pepper<BR>1 Tablespoon soaked tangerine peel, minced<BR>3 Tablespoons straw mushrooms, minced<BR>1/4 red bell pepper, minced<BR>1 pound soft bean curd<BR>1 Tablespoon water chestnut, arrowroot, or all-purpose flour<BR>1 Tablespoon cornstarch<BR>1 teaspoon of <I>sa cha</I> or another hot sauce<BR>1/4 cup vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry-pan, add the tablespoon of vegetable oil and then the pieces of bamboo shoot and bamboo pith and stir-fry for one minute. Remove from the pan and set aside.<BR>2. Add soy sauce, salt, and pepper to the fried items and stir well; then add the tangerine peel, mushrooms, and the bell pepper pieces and set aside.<BR>3. Mash the bean curd and mix in the flour and cornstarch, and then the hot sauce. Mix this with the other ingredients and stir well.<BR>4. Heat half of the rest of the vegetable oil and drop two tablespoons of the entire mixture in and fry first on one side then the other, flattening somewhat early in the frying process. Fry each side until golden brown, remove and drain, and repeat until all are fried. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B7A2723F7A9FF8428D64EBFA22C93F33', X'E855F900431E644EB67AF92F151CF9D7', X'E855F900431E644EB67AF92F151CF9D7', 0, 'javascript:', 'Fried Bean Jelly', 2, NULL, 0, 'Fried Bean Jelly', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1656', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 tablespoons cloud ear fungi<BR>1 pound firm bean curd, cut into one-inch squares and press dry with paper towels<BR>1/2 cup vegetable oil<BR>2 shallots, peeled and minced<BR>1/2 cup vegetable stock<BR>1 Tablespoon thick soy<BR>2 Tablespoons vegetarian oyster sauce<BR>1 teaspoon sugar<BR>1/2 teaspoon coarse salt<BR>2 Tablespoons Chinese black vinegar<BR>1/2 carrot, peeled and sliced<BR>1 Tablespoon cornstarch mixed with one tablespoon cold water<BR>5 Napa cabbage leaves, blanched<BR><B><I>Preparation:</B></I><BR>1. Soaked fungi in two cups water for twenty minutes, then discard the water and rinse them in warm water and drain them. Next, cut them into one-inch pieces, and set aside.<BR>2. Heat wok, add the oil, and deep-fry the tofu until golden, stirring slowly and carefully so as not to destroy their shape. When golden, drain on paper towels, and set aside. Remove oil and discard all but one tablespoon of the oil, or reserve it for another use after filtering it.Br&gt; 3. Reheat the tablespoon of oil and fry the shallots until starting to crisp, then add vegetable stock, thick soy, vegetarian oyster soy sauce, sugar, salt, and the black vinegar. Bring to the boil, then add tofu and the carrots and simmer for four minutes.<BR>4. Line serving bowl with the cabbage leaves. 5. Add cornstarch mixture to the wok, bring to the boil, and when thick, pour this mixture into that bowl and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AA93F064A305214E9D0F1B02277BB177', X'5FC803B6F0B679479F600762A95DAFE5', X'5FC803B6F0B679479F600762A95DAFE5', 0, 'javascript:', 'Fried Buddhist Beancurd', 2, NULL, 0, 'Fried Buddhist Beancurd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=47', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 cups corn oil<BR>2 large soft beancurd cakes (about one pound each)<BR>1 medium green pepper (approx. 1 cup sliced)<BR>1 small fresh carrot<BR>1/2 ounce black mushrooms<BR>4 Tablespoons lard<BR>6 ounces chicken broth<BR>1 and 1/2 Tablespoons soy sauce<BR>2 Tablespoons oyster sauce<BR>2 Tablespoons cornstarch<BR>2 Tablespoons cold water<BR><I><B>Preparation:</I></B><BR>1. Heat the oil to a temperature of at least 325 degrees F.<BR>2. Cut the beancurd into slices about one inch long and one inch wide.<BR>3. Fry the slices in the deep oil until brown.<BR>4. Slice the green peppers and carrots into thin slices and cut the mushrooms in half.<BR>5. In a frying pan, heat the lard and fry the vegetables for two minutes.<BR>6. Add the chicken broth; then the bean curd.<BR>7. Add the soy sauce and oyster sauce. Cover the pan and cook for about five minutes.<BR>8. Mix cornstarch with the cold water and stir for one minute. Serve immediately.<BR><B>Note #1:</B> To meet other dietary needs and modify the recipe further, do the following:<BR>1. Use a coated non-stick pan and do not deep fry the beancurd, pan fry it.<BR>2. Reduce the amount of corn oil to two tablespoons and the amount of lard to one tablespoon.<BR>3. Use a low sodium soy sauce in place of the regular soy sauce.<BR><B>Note #2:</B>To lower the sodium content even further, use a low sodium chicken broth and only one tablespoon of oyster sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8C8C447110B38F4C8C6CFB74CDF5BA56', X'AF0896FAA4222C449A0A65DF9861B9CC', X'AF0896FAA4222C449A0A65DF9861B9CC', 0, 'javascript:', 'Fried Gluten I', 2, NULL, 0, 'Fried Gluten I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1003', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound gluten, torn in small pieces<BR>3 Tablespoons vegetable oil<BR>1 Tablespoon dark soy sauce<BR>1 Tablespoon sugar<BR>1 teaspoon five-spice powder<BR>2 Tablespoons rice wine<BR>1 Tablespoon sweet bean paste<BR>1 slice fresh ginger, minced<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Boil gluten in two quarts of water for five minutes, then drain and discard the water.<BR>2. Heat oil and fry the gluten for two minutes then add all the rest of the ingredients. Simmer them for fifteen minutes or until all the liquid evaporates. Serve hot or warm.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'20D165F49542A847ABFF600843D88666', X'F1A5B406C4A1004CA93F68F15FC6D494', X'F1A5B406C4A1004CA93F68F15FC6D494', 0, 'javascript:', 'Fried Gluten II', 2, NULL, 0, 'Fried Gluten II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1455', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound gluten, torn in small pieces<BR>3 Tablespoons vegetable oil<BR>1 Tablespoon dark soy sauce<BR>1 Tablespoon sugar<BR>1 teaspoon five-spice powder<BR>2 Tablespoons rice wine<BR>1 Tablespoon sweet bean paste<BR>1 slice fresh ginger, minced<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Boil gluten in two quarts of water for five minutes, then drain and discard the water.<BR>2. Heat oil and fry the gluten for two minutes then add all the rest of the ingredients. Simmer them for fifteen minutes or until all the liquid evaporates. Serve hot or warm.<BR><I>Approximate nutrient analysis</I> when serving six persons. Each serving has 245 calories, 9 g carbohydrate, 29 g protein, 252 mg sodium, 10 g total fat, 1g saturated fat, and no cholesterol.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'22E2A68B0C2F0B4DACACFADECF8FF2DC', X'FA2F53D16C77DF4FB3929A193FB546BC', X'FA2F53D16C77DF4FB3929A193FB546BC', 0, 'javascript:', 'Fried Golden Winter Mushrooms', 2, NULL, 0, 'Fried Golden Winter Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1371', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup golden winter mushrooms<BR>3 Tablespoons silver ear fungi<BR>2 Tablespoons vegetable oil<BR>1/2 cup canned pineapple pieces, cubed<BR>1 gherkin, minced<BR>1 teaspoon salt<BR>1 Tablespoon sugar<BR>2 Tablespoons cornstarch mixed with one-quarter of a cup of cold water<BR><B><I>Preparation:</B></I><BR>1. Soak golden winter mushrooms in cold water for ten minutes, then rinse and drain and cut into half-inch slices.<BR>2. Soak silver ear fungi for ten minutes, then put into two cups cold water, bring to the boil, and simmer for half an hour, then drain and discard the water. Tear these fungi into half-inch pieces.<BR>3. Heat wok or fry pan, add the oil, and fry both kinds of fungi together for two minutes before adding the pineapple and gherkin pieces. Fry for another minute.<BR>4. Mix salt, sugar, and the cornstarch water, stir into the wok or fry pan and keep stirring until thickened, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C5B14207A1BD1A448ABF3EB1225A50DC', X'2F173127D1C6E145A076F7697DB6174C', X'2F173127D1C6E145A076F7697DB6174C', 0, 'javascript:', 'Fried Peppers and Potatoes', 2, NULL, 0, 'Fried Peppers and Potatoes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1235', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound potatoes, peeled and sliced very thinly<BR>1/2 teaspoon sesame oil<BR>2 Tablespoons vegetable oil<BR>1 large green pepper, seeded and sliced thin<BR>2 green hot peppers, seeds discarded, and sliced thinly<BR>1/2 teaspoon salt<BR>1 teaspoon mushroom or thin soy sauce (optional)<BR><B><I>Preparation:</B></I><BR>1. Bring a pot of water to the boil and boil the potatoes for five minutes, then drain and toss with sesame oil and set aside.<BR>2. Heat wok or fry pan, add vegetable oil, the drained potatoes and peppers, and stir fry for one minute, then add the salt, and soy sauce I(If using it) and simmer for one minute before adding half cup of water. Bring to the boil, reduce heat, and simmer one more minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1799AE7D45D7BA45AFCE94204D9AA033', X'8E7237685A07B24EB5377803F51B906A', X'8E7237685A07B24EB5377803F51B906A', 0, 'javascript:', 'Fried Rape, Beijing Style', 2, NULL, 0, 'Fried Rape, Beijing Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1531', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound rape<BR>1 teaspoon lard<BR>1/4 teaspoon ginger, smashed<BR>1/2 teaspoon cooking oil<BR>1/2 teaspoon soy sauce<BR>1/4 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Clean rape and cut this vegetable into one-inch pieces and set aside. Cut the lard into small pieces.<BR>2. Heat a wok or fry pan and when hot, add lard and then the rape. Fry this until eighty present done, then add bruised ginger, soy sauce, and salt. Stir-fry for three to five minutes, then serve. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'80BBB5AAD9A64242A7A472F925788AC9', X'EC6F459E8F1FAB4385282C34E564AE67', X'EC6F459E8F1FAB4385282C34E564AE67', 0, 'javascript:', 'Fried Shallots I', 2, NULL, 0, 'Fried Shallots I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1797', '<TD><RECIPE>Cut both ends off each shallot and discard them, then cut each shallot in kalf top to bottom, and remove the paper or outer peel. Heat one to two cups of vegetable or peanut oil over medium heat. Before it begins to smoke, turn the heat to low, add the shallot halves and stir continuously until they are golden brown. This takes about ten to fifteen minutes, and be careful not to burn them. Use a slotted spoon and remove them to a plate lined with several layers of paper towels. Allow them to cool, then refrigerate in a glass jar. Now strain the oil and refrigerate it for another use. Use the shallots and the oil, as needed.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D602C23DFDBABB4FB4CA01885B643453', X'D9DF6A651F047A45B3E7D56E44C67F1E', X'D9DF6A651F047A45B3E7D56E44C67F1E', 0, 'javascript:', 'Fried Shallots II', 2, NULL, 0, 'Fried Shallots II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1931', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound or more shallots<BR>2 cups vegetable oil<BR><I><B>Preparation:</B><I><BR>1.Cut both ends off each shallot and discard them, then cut each one in half top to bottom, and remove the paper or outer peel.<BR>2. Heat one to two cups of vegetable or peanut oil over medium heat. Before it begins to smoke, turn the heat to low, add the shallot halves and stir continuously until they are golden brown, about ten to fifteen minutes. Be careful not to burn them.<BR>3. Use a slotted spoon and remove them to a plate lined with several layers of paper towels, and allow them to cool. Refrigerate them in a glass jar. b&gt;Note:</B> Strain the oil and refrigerate it for another use; and use the shallots and the oil, as needed.<BR></I></I></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'501B6F0048ED1B40A644A8824691986C', X'BAFB9937104B114CAB10190C00D626F4', X'BAFB9937104B114CAB10190C00D626F4', 0, 'javascript:', 'Frozen Bean Curd and Sprouts--circa 1939', 2, NULL, 0, 'Frozen Bean Curd and Sprouts--circa 1939', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1074', '<TD><RECIPE>Freeze six firm bean curd squares (one and a half pounds) on an oiled cookie sheet for twelve hours or overnight. Remove them one by one, and cut each one into eight rectangular pieces. Put them in a pot or wok with two cups of beef broth, an equal amount of water, four tablespoons of dark soy sauce, and one scallion, cut in slivers. Simmer for twenty minutes. Then add half pound of bean sprouts and cook another five minutes. Serve just the solids or serve the entire contents as a soup.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4D7EA2E398BB944CA7033867AF77272E', X'C0FF131E5F96984FB50C23C10313CB36', X'C0FF131E5F96984FB50C23C10313CB36', 0, 'javascript:', 'Fruit and Vegetable Chop Suey', 2, NULL, 0, 'Fruit and Vegetable Chop Suey', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=654', '<TD><RECIPE>This recipe is as printed in, and taken from Richmonds: <I>The Art of Chinese Cookery</I>: 4 tablespoons cooking oil<BR>1/2 teaspoon black pepper<BR>2 tablespoons heavy soy sauce<BR>1 tablespoon light soy sauce<BR>2 large onions, diced<BR>1/2 pint stock<BR>1/2 lb. chopped mushrooms<BR>2 ounces dried fruit<BR>1 teaspoon Ve-Tsin (this is MSG)<BR>2 teaspoons salt<BR>1 large tin peach caps, drained<BR>1 lb. diced vegetables<BR>2 tablespoons fruit syrup (from peaches)<BR>1 small tin bean sprouts<BR>4 and a 1/2 tablespoons cornflour<BR>2 oz. Toasted almonds<BR>1 lb. Plain boiled rice <P>Heat the oil in a large saucepan and sauté the mushrooms for two minutes. Add the diced vegetables, also the onions, and all seasonings. Cook for three minutes, stirring all the time, then add the heated stock and fruit syrup. Bring to the boil and cook for a further two minutes with the lid on then add the diced peaches, bean sprouts, and the dried fruit (cut up small where necessary). Stir and then add the cornflour mixed with a little cold water. Cook slowly until sauce thickens then serve on a bed of plain boiled rice, garnished with the roasted almonds. <P>There are many variations of the above recipe. For instance, try a large tin of pineapple instead of the peaches, or two tins of mandarin oranges. For added texture add one cup chopped raw apples just before adding the cornflour.<BR></P></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'517F8852C1F14345B383808D63196A05', X'DAE9D33103E9E74FA9881C0D288EA8D6', X'DAE9D33103E9E74FA9881C0D288EA8D6', 0, 'javascript:', 'Fruit-flavored Mock Fish', 2, NULL, 0, 'Fruit-flavored Mock Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1036', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>2 sheets (10 by 15 inches) of fresh bean curd<BR>1 cooked sweet potato, skin discarded, then mashed<BR>2 large Chinese black mushrooms, soaked, stems discarded, and minced<BR>1/4 cup canned water chestnuts, minced<BR>1 small red chili, seeds discarded, and minced<BR>2 cups vegetable oil<BR>1 Tablespoon thin soy sauce<BR>2 slices fresh ginger, minced<BR>1 scallion, minced<BR>1 teaspoon cornstarch mixed with same amount of cold water<BR>10 litchi or longan fruits<BR><I><B>Preparation:</B></I><BR>1. Soak bean curd sheets in tepid water for five minutes.<BR>2. Mix sweet potato, mushrooms, water chestnuts and chili pieces, and put on the drained bean curd sheets, shaping it like a fish, head to tail. Wrap bean curd sheets seam side down, and discard any excess.<BR>3. Heat oil in a fry pan, and carefully slide mock fish into the oil and fry until crisp on one side, then carefully turn it over with two spatulas and fry the other side until crisp. Remove to two paper towels, and drain.<BR>4. Put fried fish on a platter and slice it into pieces.<BR>5. Put fruit in a small pot, add soy sauce, ginger, scallion, and the cornstarch water. Bring to the boil and as soon as the sauce clears, about half a minute, pour this over the mock fish, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DD82768CE0C267499D0B9543CCEF4221', X'C1F8A7BA9995644DB63236F4F311D8EC', X'C1F8A7BA9995644DB63236F4F311D8EC', 0, 'javascript:', 'Garlic Chive Flower Stalks with Peanuts', 2, NULL, 0, 'Garlic Chive Flower Stalks with Peanuts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1522', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>6 ounces garlic chive flower stalks, cut in two-inch sections<BR>2 ounces Sichuan preserved turnip, thinly sliced<BR>1/3 cup roasted peanuts, slightly crushed<BR>1 piece seasoned and pressed tofu, thinly sliced<BR>2 Tablespoons vegetables oil<BR>1 Tablespoon vegetarian oyster-flavored sauce<BR>1/4 cup water or vegetable broth<BR><B><I>Preparation:</B></I><BR>1. Place a stir-fry pan over high heat until hot. Add the oil, swirling to coat the sides.<BR>2. Add garlic chive spikes to cook, stirring for about one and a half minutes or until fragrant.<BR>3. Add preserved turnip and pressed tofu, continue stirring for another minute.<BR>4. Add oyster sauce and water and stir well for thirty seconds, then add peanuts, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BB96B9DEB0E4024E896704EC1CB70B79', X'2561526260E9FD4BB21372BBE95B9549', X'2561526260E9FD4BB21372BBE95B9549', 0, 'javascript:', 'Ginger and Carrot Stir-fry', 2, NULL, 0, 'Ginger and Carrot Stir-fry', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1130', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons peanut oil or lard <BR>1 Tablespoon minced garlic<BR>1/3 pound boneless pork (butt, shoulder, or loin), thinly sliced then cut into 1/2 by one and one half-inch strips<BR>1 and 3/4 cups peeled carrots, cut into matchstick-size pieces<BR>1 cup fresh ginger, peeled and cut into matchstick-size pieces<BR>10 Sichuan peppercorns, lightly crushed or coarsely ground<BR>2 Tablespoons thin soy sauce, or to taste<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry-pan, the add oil or lard and coat the pan before tossing in the garlic. Stir-fry this for ten seconds, then add pork and the chili peppers; and stir-fry another minute.<BR>2. Add carrots and ginger, stir-fry one minute then add salt and stir-fry another minute.<BR>3. Add one cup water, cover, and boil for three minutes, then remove the lid and let liquid boil for another two minutes before adding Sichuan peppercorns and soy sauce. Stir-fry another minute, then transfer to a shallow bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FB9B3E1F96F24649B93335DFA599AB45', X'229A68D9C8EF214B86BC9D5955455A57', X'229A68D9C8EF214B86BC9D5955455A57', 0, 'javascript:', 'Gingko Nuts, Cabbage, and Bamboo Shoots', 2, NULL, 0, 'Gingko Nuts, Cabbage, and Bamboo Shoots', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=736', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil 1/2 head Napa cabbage, cut into strips the long way, then across into three-inch pieces<BR>1/4 pound ginkgo nuts, precooked or canned<BR>1/2 cup bamboo shoots, sliced into pieces the same size as the cabbage<BR>2 Tablespoons soy sauce<BR>1 Tablespoon sugar<BR>1 Tablespoon cornstarch mixed with one tablespoon of cold water<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat wok, add oil and stir-fry the cabbage until it starts to wilt. Add the ginkgo nuts and the bamboo shoot strips and stir-fry two minutes.<BR>2. Then add half cup of water and all the remaining ingredients. Cook until it starts to thicken, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E8A29073B7301D4B9823634714770987', X'F1B0F4189336E04EA7F250CED3D31986', X'F1B0F4189336E04EA7F250CED3D31986', 0, 'javascript:', 'Glazed Sweet Potato Balls', 2, NULL, 0, 'Glazed Sweet Potato Balls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=363', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pound sweet potatoes<BR>2 Tablespoons brown sugar<BR>1 Tablespoon granulated sugar<BR>1 cup flour<BR>1 teaspoon sesame oil<BR>1/4 cup sesame seeds<BR>1 quart corn oil<BR><B><I>Preparation:</B></I><BR>1.Peel, quarter and simmer sweet potatoes until soft in a pot of hot water. Then drain and mash them.<BR>2. Mix them with the sugars and half of the flour and the sesame oil. Roll into one-inch balls.<BR>3. Mix flour with one-quarter cup of water mixing making a thin batter.<BR>4. Heat corn oil until about 325 degrees F, then dip a few balls in the batter, then dip the top of the ball into the sesame seeds and deep fry until light golden in color. Drain and repeat until all of the ball are fried, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'60655020C1984141AC8B7633A7347F66', X'AD675B4DF20D8347A94A8FD4D8B19186', X'AD675B4DF20D8347A94A8FD4D8B19186', 0, 'javascript:', 'Golden Hook with Silver Strip', 2, NULL, 0, 'Golden Hook with Silver Strip', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1026', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon vegetable oil<BR>1/2 pound bean sprouts, heads and tails removed<BR>1 Tablespoon Chinese rice wine<BR>1/2 to 1 teaspoon salt<BR>1/2 teaspoon minced garlic<BR>1 Tablespoon dried shrimp, minced<BR>1 teaspoon minced ginger<BR>small amount of fresh hot pepper<BR>1 teaspoon each, red and green sweet pepper, slivered<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan and fry the ginger in dry fry-pan until aromatic, about half minute. Then add the bean sprouts, rice wine, and salt and fry for one minute then remove everything from the wok, and put in a strainer to drain.<BR>2. Fry garlic in the same pan, add the shrimp, ginger, and hot pepper and toss well before returning the bean sprouts to the pan and stir fry half minute.<BR>3. Add red and green pepper slivers, toss well, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E8325975EA4A5645BE376336353B5B69', X'3D4BE9A445636B4B8DDB2691301E113F', X'3D4BE9A445636B4B8DDB2691301E113F', 0, 'javascript:', 'Golden Vegetable Wraps', 2, NULL, 0, 'Golden Vegetable Wraps', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1046', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1 Tablespoon dried gorgon fruit<BR>1 teaspoon vegetable oil<BR>1 clove garlic, slivered<BR>2 Tablespoons slivered taro<BR>5 sugar snap peas, strings removed and slivered the long way<BR>10 enoki mushrooms<BR>1 teaspoon shredded wax gourd or another gourd<BR>2 teaspoons shredded purple yam<BR>1 large Chinese black mushroom, soaked until soft, stem discarded, and slivered<BR>3 Tablespoons slivered Chinese pumpkin<BR>2 asparagus, cut in two-inch lengths, then slivered<BR>1/2 baby bok cai, cut in two-inch lengths, and slivered<BR>1 teaspoon qoji berries, each cut in three or four, the long way<BR>salt and ground white pepper, to taste<BR>2 spring roll wrappers<BR><B><I>Preparation:</B></I><BR>1. Soak gorgon fruit in warm water for half an hour, then steam for fifteen minutes over rapidly boiling water. Cool, and sliver.<BR>2. Heat wok or large fry pan, fry garlic for one minute until fragrant, and remove from the oil and put into a large bowl.<BR>3. Mix gorgon fruit, garlic, and the slivered taro, sugar snap peas, enoki mushrooms, wax gourd, purple yam, black mushroom, asparagus, bok cai, and the giji berries, and put half on the edge of each spring roll, Fold one end in and roll closed, narrower at the folded end, wider at the other which will be the top of the wrap. Try not to put too much filling at the wider end.<BR>4. Reheat wok or fry pan and fry the wraps until golden and crisp. Then serve. <P></P></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F2ACC230FCBB074C9392F9E4C9E19DA7', X'BAE7AEBA94730F4B917249965D06F651', X'BAE7AEBA94730F4B917249965D06F651', 0, 'javascript:', 'Green Beans, Sichuan Style', 2, NULL, 0, 'Green Beans, Sichuan Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1550', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup vegetable oil<BR>1/2 pound ground pork<BR>2 pounds string beans, trimmed, broken in one- to two-inch pieces, then deep fried in oil for one minute, then drained<BR>6 cloves garlic, crushed<BR>1/4 cup soy sauce<BR>1 Tablespoon sugar<BR>1/2 teaspoon chilli flakes<BR>2 teaspoons sesame oil<BR>1 cup chopped green onions<BR><B><I>Preparation:</B></I><BR>1. Heat oil in a wok or heavy fry pan, add the pork and stir-fry pork over medium/high heat for four minutes. 2. Add beans and garlic and stir-fry four more minutes, then stir in one-quarter cup water and the remaining ingredients and stir-fry for two more minutes, then serve on a pre-heated platter or bowl.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5B48518551525542BE7752B9DC280EC3', X'B8D61A1552DE19468D3C92FD54A4866E', X'B8D61A1552DE19468D3C92FD54A4866E', 0, 'javascript:', 'Green Tomatoes and Eggs', 2, NULL, 0, 'Green Tomatoes and Eggs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=514', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 large eggs<BR>1/2 teaspoon ground white pepper<BR>1/2 teaspoon salt <BR>1/4 teaspoon ground black pepper<BR>1 Tablespoon corn oil<BR>3 green tomatoes, cut into half-inch pieces<BR>2 scallions, sliced thin<BR><B><I>Preparation:</B></I><BR>1. Break eggs and beat well with salt and peppers.<BR>2. Heat oil and fry tomatoes for two minutes, then add eggs and stir continuously, just until they start to set. Add scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'020E3F8623B5EB41B9EB6BAA20DB295E', X'677E3411B258354989DA7715EACAE31C', X'677E3411B258354989DA7715EACAE31C', 0, 'javascript:', 'Grilled Eggplant with Savory Mushrooms', 2, NULL, 0, 'Grilled Eggplant with Savory Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=417', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large eggplant, cut into three equally thick slices, from stem to end<BR>1 teaspoon salt<BR>1/4 teaspoon ground white pepper<BR>1 Tablespoon corn oil<BR>1 can abalone mushrooms, drained<BR>1/4 teaspoon sugar<BR>1/4 teaspoon salt<BR>1/4 cup seasoned or regular rice vinegar<BR>1/4 cup rice wine<BR>1 Tablespoon oyster sauce<BR><B><I>Preparation:</B></I><BR>1. Season eggplant slices with salt and pepper, then brush with the oil.<BR>2. Mix mushrooms and the five remaining seasonings and simmer over low heat until almost all the liquid is absorbed. Stir often.<BR>3. While that is cooking, grill the eggplant slices over medium heat for four minutes per side or until the eggplant is tender, not overly soft.<BR>4. Put eggplant on a platter and pour mushrooms over it.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6363799FEFE67243AD98378900D49919', X'42CBC67697167442AFFC087FADF03345', X'42CBC67697167442AFFC087FADF03345', 0, 'javascript:', 'Hakka Pickled Vegetables', 2, NULL, 0, 'Hakka Pickled Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1312', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cucumber, cut into two-inch-long matchstick thin strips<BR>1 carrot, peeled and cut into two-inch-long matchstick thin strips<BR>1/2 medium-size white raddish, peeled and cut into two-inch-long matchstick thin strips<BR>1/2 pound mustard green, cut into two-inch-long matchstick thin slivers<BR>1/2 cup white rice or regular vinegar<BR>1/3 cup sugar<BR><B><I>Preparation:</B></I><BR>1. Mix cucumber, carrot, radish, and mustard green strips. Pour six cups boiling water over them, drain well, and allow to come to room temperature.<BR>2. Bring two quarts of water to the boil, add vinegar and sugar, and let cool to room temperature.<BR>3. Mix the cooled vegetables with the cooled vinegar water, and set in a cool dark place overnight.<BR>4. Remove the amount of vegetables needed, and serve or cook with them. The rest of the vegetables can remain another day or two. Another batch of cut vegetables with fresh boiled water poured over them, drained, and cooled can be added to the vinegar water when empty, then rested overnight, and then used.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3AF46F75008BB44CB554011AF4083B06', X'92B5BC2FFFB9F844893C60AE73D7F2E2', X'92B5BC2FFFB9F844893C60AE73D7F2E2', 0, 'javascript:', 'Harbin Di San Xian Vegetable Melange', 2, NULL, 0, 'Harbin Di San Xian Vegetable Melange', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1864', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 large potatoes, peeled and cut into one- and one half-inch cubes<BR>1 cup vegetable oil<BR>2 eggplants, diced in two-inch cubes<BR>1 large green pepper, seeded and cut into one-incg cubes<BR>3 large peeled cloves garlic, coarsely minced<BR>4 scallions, white part only, coarsely chopped<BR>2 Tablespoons thin soy sauce<BR>2 teaspoons dark soy sauce<BR>1 Tablespoon granulated sugar<BR>1/2 cup chicken stock<BR>1 teaspoon chicken bouillon powder<BR>1/2 teaspoon Sichuan peppercorns<BR>1 Tablespoon cornstarch mixed with twice that of cold water<BR><B><I>Preparation:</B></I><BR>1. Mix potato chunks and the oil and heat and fry stirring until potaot pieces are light brown, then remove them from the oil and drain them on paper towels.<BR>2. Add eggplant to the oil and fry them until light brown, drain and remove them to other paper towels.<BR>3. Add green pepper pieces, garlic, and scallion pieces and drain them similarly.<BR>4. Pour out the oil and reserve for another use, then add the stock and bouillon powder, both soy sauces, and the sugar and mix well.<BR>5. Return all the vegetables to the wok, add the Sichuan peppercorns and stir for one minute, then add and thicken this sauce with the cornstarch mixture stirring until it clears and is reduced by half, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'76894A1A7D56EB42BEA15F21832DC6A3', X'310EFF87B585334EA8253FEA4C6DFE59', X'310EFF87B585334EA8253FEA4C6DFE59', 0, 'javascript:', 'Henan Style Sweet Potatoes', 2, NULL, 0, 'Henan Style Sweet Potatoes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1653', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds sweet potatoes, peeled and cut into two-inch chunks<BR>2 garlic cloves, peeled and mashed<BR>2 Tablespoons Chinese red vinegar<BR>1 cup crushed Chinese brown slab sugar<BR>1/2 cup superfine or bar sugar<BR>1/2 cup rose or chrysanthemum petals<BR><B><I>Preparation:</B></I><BR>1. Put sweet potatoes in a sauce pan with the garlic and the vinegar, and bring to the boil. Immediately reduce the heat, and simmer this for twenty minutes.<BR>2. In another pan, put both sugars and three tablespoons cold water. Bring to the boil, stirring constantly, until the sugars melt and come to the boil.<BR>3. Drain the sweet potatoes and mash them before adding the sugar solution. Then put them n a pre-heated serving dish. Mix half the flower petals into the potatoes and scatter the rest of the flower petals over them, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4DE65E1C1CBCBC4DB6F10154987013E4', X'218A7840EB761A4F8671D84EF8C88D04', X'218A7840EB761A4F8671D84EF8C88D04', 0, 'javascript:', 'Home-made Bean Curd', 2, NULL, 0, 'Home-made Bean Curd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=37', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 cups dried soy beans (about a pound)<BR>1 heaping Tablespoon Epsom salts (or use Nigeri or another coagulant)<BR><I><B>Preparation:</I></B><BR>1. Soak the beans in eight to ten cups of water overnight.<BR>2. Rinse well. (You can pour off the soaking liquid and use it for soup, it is high in nutrients.)<BR>3. Take one cup of beans and 3 cups of cold water and put in a blender, liquify this and repeat until all the soaked beans are made into liquid pulp.<BR>4. Mix the Epsom salts or other coagulant with 1/4 cup warm water to dissolve it.<BR>5. Boil the pulpy water for three minutes. Remove from the heat and let the temperature cool to about 190 degrees F. then slowly stir in the dissolved coagulant water. Let this rest 10-15 minutes.<BR>6. Gently pour through a double layer of cheesecloth, squeeze the curd very, very gently then mold it to shape and let drain two hours. If you want a firmer curd, put a light weight (wrapped in plastic wrap) on the cheesecloth wrapped curd.<BR>7. Cut into squares and gently place them into a glass or ceramic container filled with cold water.<BR>Keep in the refrigerator until ready to use.<BR><B>Note:</B> Store beancurd in the refigerator and change the water daily. Keep it tightly covered and it will stay about three or four days. If each time you change the water you add a little non-iodized salt (i.e.: kosher salt) to it, then it can stay a week.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B1880FE757F9584DADFCE621B6B9F2E8', X'2097FDE92A502E4BB0A8FAA7F7D53D8D', X'2097FDE92A502E4BB0A8FAA7F7D53D8D', 0, 'javascript:', 'Honeyed Potatoes', 2, NULL, 0, 'Honeyed Potatoes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=765', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons corn oil<BR>3 Tablespoons sugar<BR>3 Tablespoons honey<BR>1 pound white or sweet potatoes, peeled and cut into thin sticks<BR>1 Tablespoon cornstarch<BR>1 Tablespoon sesame seeds<BR><B><I>Preparation:</B></I><BR>1. Mix oil and sugar and cook on low heat until golden.<BR>2. Add honey and simmer for two minutes, then add potatoes and stir-fry for two minutes.<BR>3. Mix cornstarch and sesame seeds and sprinkle over the potatoes, stirring continuously, and stir-fry another two minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'69D89CD95FD55A4E92F69A5DF9A35AE7', X'8AD2949D3CA87145BC29A107E895F019', X'8AD2949D3CA87145BC29A107E895F019', 0, 'javascript:', 'Hot and Spicy Diced Potatoes', 2, NULL, 0, 'Hot and Spicy Diced Potatoes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1253', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound potatoes, peeled and diced<BR>4 cups cooking oil<BR>1 Tablespoon and 1 teaspoon chili powder<BR>1/2 teaspoon ground Sichuan peppercorns, crushed<BR>2 Tablespoons chili sauce<BR>1 teaspoon salt<BR>1/4 teaspoon MSG (optional)<BR>1 teaspoon cornstarch<BR><B><I>Preparation:</B></I><BR>1. Soak diced potatoes in water for fifteen minutes, then remove, drain, and dry them in a towel.<BR>2. Heat oil in a deep pot and deep-fry the diced potatoes. Then drain and discard all but one tablespoon of the oil.<BR>3. Mix the drained potatoes with the chili powder, Sichuan peppercorns, salt, MSG and the cornstarch, and mix well.<BR>4. Heat the tablespoon of reserved oil, fry the potato mixture for a minute or two, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'35C385A0D066C6469C064111F4AEEFF2', X'1521A4728210BC4E9C069215E66367E5', X'1521A4728210BC4E9C069215E66367E5', 0, 'javascript:', 'Hot Potatoes', 2, NULL, 0, 'Hot Potatoes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1237', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound potatoes, peeled and sliced<BR>1 teaspoon coarse salt<BR>1 teaspoon fermented black beans, smashed or chopped minimally<BR>1 chili pepper seeded and minced<BR>2 Tablespoons vegetable oil<BR>1 small tomato, coarsely chopped<BR>1 teaspoon potato starch<BR><B><I>Preparation:</B></I><BR>1. Mix potato slices with salt, black beans, and the chili pepper.<BR>2. Heat pan or wok, add oil, and then add potato mixture and fry for five minutes, stirring several times.<BR>3. Add chopped tomato and the potato starch and simmer until the potatoes are cooked to desired firmness, about another minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AD8D09B20194EE4BAF8546DD4C7DD902', X'6E854756DC81E04DA991C7FD2DFE5B31', X'6E854756DC81E04DA991C7FD2DFE5B31', 0, 'javascript:', 'Iced Bean Curd, Sui-Yuan', 2, NULL, 0, 'Iced Bean Curd, Sui-Yuan', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1289', '<TD><RECIPE><I><B>Ingredients:</B></I> and the <B><I>Preparation, as given:</B></I><BR>Freeze a piece of medium do-fu overnight, then cut it into small square pieces. Put it (blanch) it in boiling water to rid of its original flavor, then put it in a pot and add some chicken soup, ham soup, and meat stock, and simmer for a while. Then add fragrant tan shoots (maybe cinnamon) and bamboo shoots. If you simmer it for fifteen or twenty minutes, it becomes tough and is full of honeycombs. Saute it in oil to maintain its tenderness.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'37C6B4CC6874C94198D57620F9AD70CB', X'A009484D17B133409BC0150FE8A0FDDF', X'A009484D17B133409BC0150FE8A0FDDF', 0, 'javascript:', 'Jade Rice Noodle Wraps', 2, NULL, 0, 'Jade Rice Noodle Wraps', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=121', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 flat rice noodle sheets, fresh preferred<BR>8 ounces broccoli. cut into flowerettes<BR>4 ounces asparagus, julliened<BR>3 canned bamboo shoots, jullienned<BR>8 dried shiitake mushrooms, soaked and sliced<BR>1 teaspoon sesame oil<BR>1/2 teaspoon of light soy sauce<BR>1/2 teaspoon dry sherry<BR>1/4 teaspoon hoisin sauce<BR>1/4 teaspoon chili sauce (optional)<BR>1 teaspoon oil, to oil the plate used for steaming<BR><B><I>Preparation:</I></B><BR>1. If not fresh, steam the rice-noodle sheets just until soft (about a minute or two).<BR>2. Steam broccoli until bright green and starting to soften, then arrange as the outer circle on a plate. <BR>3. Parboil asparagus, bamboo shoots and mushrooms together for a minute and rinse under cold water to cool; then drain and mix with the rest of the ingredients then divide into eight portions.<BR>4. Cut each rice noodle sheet into four sheets. Place one portion of the filling on the noodle and roll it into cylinder. Repeat the process until all are rolled. Put them on an oiled plate Steam for 6 minutes. <BR>5. Arrange the rolls in the center of the broccoli and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C15FFFF1CE7D3B48834321EC63F323B8', X'8268016EF114634B8733449EA56F67C4', X'8268016EF114634B8733449EA56F67C4', 0, 'javascript:', 'Jadeite Balls', 2, NULL, 0, 'Jadeite Balls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1587', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound fresh or frozen peas<BR>1/2 cup granulated sugar<BR>1/2 cup glutinous rice flour<BR>1 teaspoon osmanthus flower, mashed or minced<BR>1/2 cup Chinese red dates, pits removed and discarded, and mashed, and divided into twenty batches<BR>1 cup lard<BR><B><I>Preparation:</B></I><BR>1. In a steamer, steam the peas in a covered bowl over rapidly boiling water for half an hour. Remove and then mash them well.<BR>2. Add the sugar, rice flour, and the osmanthus flower, and one to two tablespoons of boiling water. Remove from the bowl and knead, then roll this into a cigar-shaped piece. Next, cut it into twenty pieces and roll each into a small circle.<BR>3. Put one mashed date piece into the center of each circle, and form this into a ball.<BR>4. Heat lard, and fry the balls until crisp. Remove from the lard, and drain them on paper towels. Serve them n a pre-heated plate.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7E9719E23CA0FB4B9D75E4DC5D6942B6', X'D823EAD8EA37FA4FBAC290A43B335C57', X'D823EAD8EA37FA4FBAC290A43B335C57', 0, 'javascript:', 'Jai (called Buddha''s Delight) II', 2, NULL, 0, 'Jai (called Buddha''s Delight) II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=689', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup bean curd stick, soaked for ten minutes in warm water<BR>1 cup bamboo pith, soaked in warm water for five minutes, hard ends removed<BR>1/2 cup cloud ear fungus, soaked in warm water for one hour<BR>1/4 cup white or snow fungus, soaked in warm water for half hour<BR>8 black mushrooms, soaked in warm water for half an hour, stems removed<BR>1 cup glutinous rice, soaked in warm water for one hour<BR>3 Tablespoons corn or another vegetable oil<BR>1 pound firm shredded tofu noodles, cut into two-inch lengths<BR>1 cup carrots, shredded<BR>1 cup mustard green, <I>ba cai</I>, or bok choy, sliced thin<BR>1/2 cup bamboo shoots, shredded<BR>1/2 cup water chestnuts, shredded<BR>1/2 cup cashew nuts<BR>2 Tablespoons crushed slab sugar, or brown sugar<BR>2 Tablespoons mushroom soy<BR>2 Tablespoons vegetarian oyster sauce<BR>2 Tablespoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Keeping each item separate, drain each soaked item. Cut all but the rice into slivers.<BR>2. Heat a wok, add the corn oil and fry the tofu noodles for one minute, and remove.<BR>3. Add carrots, mustard or other green, bamboo shoots, water chestnuts, and the cashews, and stir-fry for two minutes, then add all the other ingredients except the sesame oil, including those soaked or set aside, and stir one more minute.<BR>4. Add one quart of boiling water, stir well, reduce the heat to simmer, cover, and cook for half an hour.<BR>5. Remove the cover, stir and place in a heated serving dish. Pour sesame oil over it, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4232FA9BECCA2949AB0D1B0305054F75', X'A1474E08B4D91F4EB94928C60C128C54', X'A1474E08B4D91F4EB94928C60C128C54', 0, 'javascript:', 'Jai I', 2, NULL, 0, 'Jai I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=943', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>Amounts of the following are persoanl preference:<BR>Black mushrooms, straw mushrooms, black fungus, white fungus, sand fungus, stone fungus, yellow fungus, fried bean cakes, bean curd skins, black moss, ginkgo seeds, dried lily flowers, Chinese cabbage, carrots, snap peas, bean threads (clear vermicelli), gluten, and seedless dried red dates<BR><B><I>Preparation:</B></I><BR>1. Put all the black mushrooms, black moss, and lily flowers, dried red dates and all types of fungus in water. Soak them for couple of days to make them soft.<BR>2. Put bean threads in water, too, not for too long, just enough to get them soft.<BR>3. Saute the mushrooms and the fungus with oil and ginger separately, then mix them together and set aside.<BR>4. Cook Chinese cabbage in boiling water, take out, drain, and set aside for later use.<BR>5. Use a Chinese wok or large fry pan, and when hot add oil, ginger and red bean cake paste. Mix everything together, add vegetables and cover with a lid. Simmer for about thirty minutes, add some sugar and soy sauce to taste, turn the heat up high, stir, then serve as the dish is now ready to eat.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'10FE9DE3D792544082A678964B27D738', X'AAF3A027140DF84DBB55123282943EA7', X'AAF3A027140DF84DBB55123282943EA7', 0, 'javascript:', 'Jai--Buddha''s Delight III', 2, NULL, 0, 'Jai--Buddha''s Delight III', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=944', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 tablespoons vegetable oil<BR>3 cloves garlic, peeled and sliced then slivered<BR>4 to 6 slices ginger root, slivered<BR>8 Chinese black mushrooms, soaked, stems removed, left whole or cut in quarters, the mushroom water reserved<BR>3 tablespoons cloud ear fungus, soaked in warm water for twenty minutes, then cut into half-inch pieces<BR>1 white fungus, soaked for twenty minutes in warm water, and cut into half-inch pieces<BR>1/4 to 1/2 cup each of fifteen other vegetables such as sliced bamboo shoots, carrot cubes, firm bean curd cubes, one-inch pieces of snow or melting mouth peas, soy sprouts their very thin tails removed, three or four scallions cut into one-inch pieces, napa cabbage and/or lettuce cut into one-inch pieces, sliced water chestnuts, half-inch pieces of canned baby corn, hair vegetable, soaked and simmered or canned ginkgo nuts, almonds or cashews, etc.<BR>2 ounces bean thread noodles, soaked in warm water for ten minutes then cut into four-inch lengths<BR><B><I>Preparation:</B></I><BR>1. Heat wok or large pan, add oil, and stir-fry garlic and ginger root for half minute.<BR>2. Add mushrooms and then both of the pre-cut pieces of fungus and stir-fry for two minutes.<BR>3. Then, add vegetables one by one, the firmest to the softest, stirring each for half minute before adding the next one. Be sure to add the nuts last. 4. Add mushroom water and the bean thread noodles, cover, and simmer for three to five minutes, then serve.<BR><B>Note</B>: This dish can be served plain or with one or more seasonings items such as soy sauce, sesame oil, and ground white pepper mixed in or served on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6742105CF5F4DF49957CC2C14A87E6FB', X'45D933D7CFF5D54F8BC6C43E822709AD', X'45D933D7CFF5D54F8BC6C43E822709AD', 0, 'javascript:', 'Kelp with Golden Mushrooms I', 2, NULL, 0, 'Kelp with Golden Mushrooms I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1210', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 or 2 pieces kelp<BR>1/3 ounce agar-agar<BR>6 ounces of golden mushrooms<BR>2 Tablespoons shredded carrot<BR>1 rib Chinese celery, shredded<BR>2 Tablespoons light soy<BR>1 teaspoon sugar<BR>2 teaspoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Soak kelp thoroughly until swollen and then simmer in boiling water for two minutes. Set aside in the pot it was boiled in for twenty minutes, then take it out and rinse with boiled water; drain and shred.<BR>2. Soak agar-agar until tender, then rinse it with boiled water; drain and section.<BR>3. Cut away tail part of golden mushrooms and rinse them, then scald them in water until tender; take out and drain.<BR>4. In a bowl blend agar-agar, shredded kelp, golden mushroom, shredded carrot, celery, soy, sugar, and oil. Serve sprinkled with sesame oil.<BR><B>Note:</B> To make this a more substantial dish, cold noodles, strips of cooked chicken or slices of smoked bean curd may be added to the above ingredients.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3FA9B88C33F1644EBC37A2DADEE0CDB6', X'B1E53CDA581E604E93173DBB33DB4F03', X'B1E53CDA581E604E93173DBB33DB4F03', 0, 'javascript:', 'Kelp. Golden Mushrooms, and Vegetables', 2, NULL, 0, 'Kelp. Golden Mushrooms, and Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=355', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 to 8 inches of kelp, soaked in boiling water for two hours, then thin sliced<BR>1/4 pound agar agar blanched in boiling water, then drained and shredded<BR>2 stalks Chinese Celery, cut into one-inch by 1/4-inch pieces, blanched half a minute<BR>1/4 pound golden mushrooms, lowest 1/4-inch removed<BR>1 carrot, cut into one-inch by 1/4-inch pieces and blanched for one minute 2 Tablespoons thin soy sauce<BR>2 Tablespoons sesame oil<BR>1 Tablespoon rice vinegar<BR><B><I>Preparation:</B></I><BR>1. Mix all solid ingredients, tossing lightly so as not to break them.<BR>2. Beat or whip soy sauce, sesame oil, and vinegar, then toss with the vegetables and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6D548427236B46478C6AE67FAD423ED1', X'D57A470D6FB42144B3B7486485A1305E', X'D57A470D6FB42144B3B7486485A1305E', 0, 'javascript:', 'King Oyster Mushrooms with Tomatoes', 2, NULL, 0, 'King Oyster Mushrooms with Tomatoes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1484', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 king oyster mushrooms<BR>1/2 cup all-purpose flour<BR>2 Tablespoons water chestnut flour<BR>2 cups vegetable oil, for deep frying<BR>1 Tablespoon minced fresh ginger<BR>1 small hot chili pepper, seeded, then minced<BR>1/2 sheet black or purple seaweed, minced<BR>1 teaspoon black sesame seeds<BR>1 teaspoon toasted white sesame seeds<BR>1 Tablespoon oyster sauce<BR>1 teaspoon crushed Chinese brown sugar<BR>1 teaspoon sesame oil<BR>3 Tablespoons minced Chinese celery<BR>3 ripe plum tomatoes, blanched for fifteen seconds, then peeled and sliced the long way<BR><B><I>Preparation:</B></I><BR>1. Slice the mushrooms on a slight angle, into half-inch wide slices.<BR>2. Mix flour with three tablespoons cool water, beating to make a thin batter, then dip the mushroom slices in it coating them well.<BR>3. Heat oil in a deep pot, then deep-fry a few mushroom slices at a time until they are golden, and drain them on paper towels.<BR>4. Make a sauce mixing the minced ginger, chili pepper, seaweed, and both sesame seeds. Then add oyster sauce, crushed sugar, and the minced celery, and one tablespoon cold water.<BR>5. Put a fried mushroom slice on a slice of tomato, and then pour the sauce mixture over them, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3B5221E504EAC343A028CD61AED41576', X'C30D12DFF896D5429D2BB2F0EE65AFFC', X'C30D12DFF896D5429D2BB2F0EE65AFFC', 0, 'javascript:', 'King Oyster Mushrooms, Tofu, and Peas', 2, NULL, 0, 'King Oyster Mushrooms, Tofu, and Peas', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=838', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 ounce dried Chinese black mushrooms, soaked for half hour in one cup of warm water<BR>1 pound firm tofu, cut into one- to two-inch cubes<BR>3 Tablespoons cornstarch<BR>3 Tablespoons corn oil<BR>2 to 3 almond/king oyster mushrooms, each cut into three to four pieces<BR>3 cloves garlic, peeled and coarsely minced<BR>2 slices peeled fresh ginger, each cut into eight pieces<BR>1/2 cup fresh or frozen peas<BR><B><I>Preparation:</B></I><BR>1. Remove stems from the black mushrooms and discard, then cut each one in half or quarters. Squeeze them, over a glass or small bowl, of any remaining water, and reserve it.<BR>2. Put black mushrooms and tofu in a bowl, mix, then add cornstarch and mix again.<BR>3. Heat wok, add oil, them fry oyster/king mushroom for one half minute, then add black mushrooms and tofu and fry in two batches, each about two minutes, then remove solids leaving oil in the wok.<BR>4. Reheat oil and fry the garlic and ginger for half a minute, add all ingredients, used and not yet used, and fry for one more minute, remove with a slotted spoon, draining well, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'644190D07AC95A4D9D793AB1CA9AF2EB', X'9C8125D67572AE42B9FA9A6703FF72B9', X'9C8125D67572AE42B9FA9A6703FF72B9', 0, 'javascript:', 'Konjac Rolls with Sesame Sauce', 2, NULL, 0, 'Konjac Rolls with Sesame Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1148', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 konjac rolls<BR>1 sheet seaweed, cut in half then slivered<BR>1 Tablespoon sesame seeds, toasted<BR>1 teaspoon sesame paste<BR>2 Tablespoons hot green tea<BR>1/2 teaspoon granulated sugar<BR>1 teaspoon thin soy sauce<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Put konjac rolls in a large stainer, then pour one cup boiling water over them. Drain well and remove to a flat serving bowl lining them up in two rows.<BR>2. In same strainer, pour one cup boiling water over the seaweed slivers, drain, and dry with paper towels. Spread these over the rolls of konjac.<BR>3. Mix sesame paste and hot green tea until no lumps are left, then add the sugar and the soy sauce, and stir well before stirring in the sesame oil. Pour this over the seaweed and konjac noodles and serve slightly warm or cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'72DEBC142D59A04498851D75353ED0AB', X'34E1C61F10802E44AC677400B87E1437', X'34E1C61F10802E44AC677400B87E1437', 0, 'javascript:', 'Long Beans and Sauce', 2, NULL, 0, 'Long Beans and Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=891', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound long beans, ends removed, then cut into three-inch lengths<BR>5 Tablespoons vegetable oil<BR>1/4 pound minced sirloin beef (or minced pickled mustard green)<BR>1 Tablespoons <I>sa cha</I> or chili sauce<BR>2 teaspoons Chinese rice wine<BR><B><I>Preparation:</B></I><BR>1. Heat wok and add one cup water and the beans and boil until all the water is evaporated. Then slowly add the oil and stir-fry for two minutes, remove the beans and drain on paper towels, and let then sit for ten minutes. Discard all but a teaspoon of the oil.<BR>2. Reheat wok with the oil and add beef or mustard greens and stir-fry one minute then add sacha sauce and stir-fry another minute before adding the rice wine. Boil until it evaporates, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CC6EB3CF137B88429DA9C3014D9B0443', X'3C0B7A327C99164F854930B34722384D', X'3C0B7A327C99164F854930B34722384D', 0, 'javascript:', 'Long Beans, Bean Sprouts, and Red Peppers', 2, NULL, 0, 'Long Beans, Bean Sprouts, and Red Peppers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=888', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound long beans<BR>1 Tablespoon vegetable oil<BR>1 pound bean sprouts, their heads and tails removed<BR>1/4 teaspoon salt and ground white pepper, mixed<BR>1/2 fresh red chili pepper, minced<BR><B><I>Preparation:</B></I><BR>1. Cut ends off beans, and cut them into two- to three-inch lengths, and blanch in boiling water for one minute, then immerse in ice water. Drain well when very cold.<BR>2. Heat wok or fry pan, add oil, then add cut long beans and stir -fry for two minutes, then add bean sprouts and continue cooking another minute. Add the salt and pepper, and the chili pepper pieces, stir once or twice, remove to a platter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D60FBD4051FF2143BA75C27EBCD0E4F7', X'8A59967D4554604CA87A00AC2CEB9F2D', X'8A59967D4554604CA87A00AC2CEB9F2D', 0, 'javascript:', 'Long Beans, Fennel, and Basil, Chinese Style', 2, NULL, 0, 'Long Beans, Fennel, and Basil, Chinese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=887', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound Chinese long beans<BR>1 medium fennel bulb<BR>1 cup soybean or light olive oil<BR>1 small sweet red bell pepper, cored, seeded, and cut in julienne strips<BR>2 shallots, thinly sliced<BR>2 teaspoons dried shrimp, soaked for fifteen minutes, drained, and minced to a paste (optional)<BR>1 clove garlic, minced<BR>1/2 cup chicken or vegetable broth<BR>2 teaspoons fresh lemon or lime juice<BR>1 Tablespoon soy sauce<BR>1/2 cup fresh basil leaves, cut into thin strips<BR>1/4 teaspoon kosher salt<BR>1/4 teaspoon ground black pepper<BR><B><I>Preparation:</B></I> 1. Remove ends from the beans, and cut then into two-inch lengths.<BR>2. Cut fennel in half, cut out the core, and remove any tough outer leaves, Halve again. The cut into one-quarter-inch thick lengthwise slices.<BR>3. Heat oil until it reaches 375 degrees F. Add beans, red pepper, and shallots and stir-fry for about one minute until beans and pepper are slightly softened. Then drain in a colander set over a large bowl.<BR>4. Return one tablespoon of the oil to the pan, add the dried shrimp if using them, and the minced garlic and saute for thirty seconds.<BR>5. Add fennel and the stock, reduce heat to low, and stir-fry for about five minutes or until fennel is tender.<BR>6. Add the bean mixture, lime juice, and soy sauce and cook until vegetables are firm-tender, about two minutes.<BR>7. Turn off heat, mix in the basil, and season to taste with the salt and pepper; and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5A35C5934B0B4F47869D054B3FC438B2', X'02007F2A7760644BB3FF16B690F7EC30', X'02007F2A7760644BB3FF16B690F7EC30', 0, 'javascript:', 'Lotus and Oyster Mushroom Pancakes', 2, NULL, 0, 'Lotus and Oyster Mushroom Pancakes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=318', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 ounces dried oyster mushrooms, soaked until soft<BR>8 ounces peeled raw lotus roots, finely grated<BR>1 ounce Chinese bacon, minced fine<BR>1 egg, beaten<BR>1/4 teaspoon salt<BR>dash white pepper<BR>1 teaspoon sesame oil<BR>3 Tablespoons water chestnut flour or corn starch<BR>1 cup corn oil<BR>2 teaspoons hoisin sauce<BR>1/4 cup cold tea<BR><B><I>Preparation:</B></I><BR>1. Mix mushrooms, lotus root puree, and bacon. Add egg, salt, pepper, sesame oil, and then two-thirds of the flour.<BR>2. Divide the dough in six parts and shape into balls, then flatten them dusting them on both sides with the remaining flour.<BR>3. Heat oil in a wok or fry pan and fry on one side until light brown turn over and fry on the second side, then remove and drain.<BR>4. Mix hoisin sauce and tea and put into a bowl. Serve pancakes with dipping sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'494B1CAE929EAE4A851ACA3878D37C90', X'806FD243A38AAC419B25CFA07563D299', X'806FD243A38AAC419B25CFA07563D299', 0, 'javascript:', 'Lucky Sea Vegetable Rolls', 2, NULL, 0, 'Lucky Sea Vegetable Rolls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=775', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup hair vegetable, soaked in warm water for an hour, then drained<BR>1 cup potato, peeled, diced, cooked, then mashed and cooled<BR>1/2 cup carrot, peeled and diced, and cooked for ten minutes, then drained<BR>4 water chestnuts, minced<BR>1 stalk Chinese celery, minced<BR>1 egg white<BR>2 Chinese black mushrooms, stems removed and ground fine in a blender<BR>1 teaspoon Chinese brown slab sugar<BR>2 Tablespoons cornstarch<BR><B><I>Preparation:</B></I><BR>1.Drain hair vegetable well, and divide it into four parts and spread it out evenly and thinly on four sheets of plastic wrap. Each should be about five inches square.<BR>2.Mix mashed potato, carrots, water chestnuts, Chinese celery, egg white, mushroom powder, brown sugar, and cornstarch and then divide this mixture into four parts. Make a cigar-shaped roll, four inches long with each of them and put in the center of each batch of hair vegetable.<BR>3. Using the plastic wrap, enclose the potato mixture on all sides rolled in the hair vegetable. Squeeze gently to remove any pockets of air.<BR>4. Steam over boiling water for twelve minutes, then remove and discard the plastic wrap.<BR>5. Cut each roll in half on a sharp angle, stand in a small bowl wth sides, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D35B617FC33A8B468C39BF2F065DAC9D', X'2664EC44C4CC7C4794FFFDCB5C125B52', X'2664EC44C4CC7C4794FFFDCB5C125B52', 0, 'javascript:', 'Ma Po Bean Curd, Sichuan Style', 2, NULL, 0, 'Ma Po Bean Curd, Sichuan Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1529', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound firm bean curd, cut into half-inch cubes<BR>1/4 pound silken bean curd, mashed<BR>2 Tablespoons vegetable oil<BR>1/4 pound finely-minced pork loin<BR>1 Tablespoon chili paste with garlic<BR>1 teaspoon hot bean paste<BR>1 Tablespoon minced fresh ginger<BR>2 cloves garlic, peeled and minced<BR>1 Tablespoon thin soy sauce<BR>2 teaspoons Chinese rice wine<BR>1/2 teaspoon ground white pepper<BR>1/2 cup chicken stock<BR>1 Tablespoon cornstarch mixed with one tablespoon cold water<BR>1 teaspoon sesame oil<BR>1 Tablespoon minced scallion, green part only<BR><B><I>Preparation:</B></I><BR>1. Boil two cups of water and blanch firm bean curd cubes for one minute, then drain and discard the water. Gently stir the silken bean curd.<BR>2. Heat wok or fry pan, add oil, then fry pork just until it loses its pink color. Then add the chili and hot bean pastes and stir well before adding the minced ginger and garlic. Stir-fry for one minute, then add soy sauce, rice wine, ground white pepper, and the bean curd mixture and stir for one minute then add the chicken stock and the cornstarch mixture, and stir-fry until thickened.<BR>3. Add sesame oil and the minced scallion, stir, put into a bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1F504C24523D874A87BAE0E477554694', X'6369F4C27EC48C4A8A197892AFC34F98', X'6369F4C27EC48C4A8A197892AFC34F98', 0, 'javascript:', 'Macanese Vegetarian Samosas', 2, NULL, 0, 'Macanese Vegetarian Samosas', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=665', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 onion, finely chopped<BR>1 clove garlic, finely chopped<BR>3 Tablespoons olive oil<BR>1 teaspoon finely chopped fresh ginger<BR>2 fresh chilies, finely chopped<BR>1 carrot, finely diced<BR>2 potatoes, finely diced<BR>1/2 cup green peas<BR>1 Tablespoon ground coriander<BR>2 teaspoons curry powder<BR>1/2 teaspoon saffron<BR>1 teaspoon salt<BR>1 Tablespoon lemon juice<BR>2 Tablespoons bread crumbs<BR>20 spring roll wrappers<BR>1 egg white<BR>2 cups vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Saute onion and garlic in olive oil until soft. Stir in ginger and chilies and cook for two more minutes before adding carrots, potatoes, and peas.<BR>2. Mix in coriander, curry, saffron, and salt and cook over very low heat for about an hour until all the vegetables are cooked and the mixture has lost its liquid.<BR>3. Add lemon juice and breadcrumbs and set aside to cool.<BR>4. Cut each spring roll in half, and then fold in any amount, if needed, so that there are strips about three by six inches.<BR>5. Put heaping teaspoon filling at one end of a strip, fold it as triangle, as the American flag is folded, over and over until the mixture is enclosed and the final shape is a triangle. Repeat until all are folded, sealing the final edge with egg white.<BR>6. Heat vegetable oil and deep fry the twenty somosas for about five minutes or until golden brown.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E0C3323E40016247A7048A85EF492039', X'9A7AB9575FE7C54596F72E9304958349', X'9A7AB9575FE7C54596F72E9304958349', 0, 'javascript:', 'Manchu Potato Shreds', 2, NULL, 0, 'Manchu Potato Shreds', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1230', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound potato, peeled and shredded<BR>1 Tablespoon vegetable oil<BR>1/2 each, red and green peppers, seeded and cut into thin strips<BR>1/4 cup wood ear fungus, soaked for twenty minutes in warm water, then drained and cut into thin strips<BR>2 teaspoons hot chili oil<BR>1 teaspoon bouillon powder<BR>2 Tablespoons minced fresh garlic<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Blanch potato strips in boiling water for one minute, drain and set aside.<BR>2. Heat wok or fry pan, add oil, and stir-fry the potato and pepper strips and the wood ear fungus for two minutes.<BR>3. Mix then add chili oil, bouillon powder, and minced garlic and stir-fry for another two or three minutes before adding the sesame oil, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'48F236DD3320C64284439FBC2D67C1FB', X'80A4A5DBB251E246B1FCD1C72FB2CF14', X'80A4A5DBB251E246B1FCD1C72FB2CF14', 0, 'javascript:', 'Many Mushrooms', 2, NULL, 0, 'Many Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1232', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>1 Tablespoon sesame oil<BR>8 fresh Chinese mushrooms, cut in thin slices<BR>1 fresh oyster mushroom, cut in thin strips<BR>1 package (one ounce) fresh button mushrooms, ends removed<BR>3 cloves fresh garlic, peeled and minced<BR>1 small carrot, peeled and sliced thin on an angle<BR>1/2 teaspoon sugar<BR>1/2 teaspoon salt<BR>dash ground white pepper<BR>1 Tablespoon oyster sauce<BR>1 teaspoon ABC or <I>sa cha</I> sauce<BR>1/4 cup snow peas, ends trimmed, each cut in half on an angle<BR><B><I>Preparation:</B></I><BR>1. Heat wok or frypan, add vegetable and sesame oils and all the mushrooms. Stir-fry for two minutes.<BR>2. Add garlic, carrot slices, sugar, salt, and pepper and stir-fry another minute, then add both sauces and the snow peas and stir for one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'623D6636E9049A4CBBFBE4A86A43C780', X'152A5D6227102345BF8B986E838D37C7', X'152A5D6227102345BF8B986E838D37C7', 0, 'javascript:', 'Mapo Doufu', 2, NULL, 0, 'Mapo Doufu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=984', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 one-pound boxes of firm tofu<BR>2 Tablespoons of vegetable oil, separated into a tablespoon each<BR>1/2 pound ground pork (vegetarians use pickled Sichuan cabbage)<BR>2 Tablespoons garlic sauce with chili<BR>1 Tablespoon oyster sauce<BR>1 Tablespoon soy sauce<BR>1/2 teaspoon sugar<BR>1 Tablespoon arrowroot flour or cornstarch mixed with one tablespoon of cold water<BR>1 or 2 scallions, sliced, for garnish<BR><B><I>Preparation:</I></B><BR>1. Cut the tofu into cubes or slice them as you wish. Soak in hot, lightly salted water for about ten minutes, then pat dry with paper towels.<BR>2. Heat a wok and add one tablespoon of oil, then the ground meat mixed with the arrowroot flour. Next, add the three sauces and stir-fry just until the meat loses its pink color; then drain and set aside.<BR>3. Drain the tofu on paper towels, then add the other teaspoon of the oil and fry the tofu until golden. Then add the set aside meat mixture and stir-fry until heated thoroughly. Add sugar and the arrowroot mixture and simmer until the sauce has thickened. Put into a serving bowl, sprinkle scallions on to, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A4606D9B93E7C540B248B39726F44FAF', X'ABA09FC6CC3F554D9D5F2CAF47988951', X'ABA09FC6CC3F554D9D5F2CAF47988951', 0, 'javascript:', 'Marinated Bitter Melon I', 2, NULL, 0, 'Marinated Bitter Melon I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=535', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound, or two bitter melons<BR>1 Tablespoon fermented black beans<BR>2 Tablespoons corn oil<BR>2 cloves garlic, minced<BR>1 teaspoon thin soy sauce<BR>2 teaspoons sesame oil<BR>1 teaspoon sugar<BR>dash of MSG, optional<BR><B><I>Preparation:</B></I><BR>1. Cut melon in half length-wise and remove and discard the seeds and pulp. Cut melon halves into onw-inch thick pieces. Parboil them in boiling water for one minute, then drain.<BR>2. Place the black beans in a small bowl, cover with cool water, stir, and drain. Repeat one more time.<BR>3. Heat the oil in a wok, add half of the chopped garlic, stir for a few seconds, add the black beans and stir for half a minute then add all the remaining ingredients and continue stirring for another half a minute.<BR>4. Put it all into a dish, cover, and refrigerate for twenty-four hours, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'675F2A45DE7F954F9BF632000F40147D', X'E5DCE4885B1FCA438DEB5D978E5CC0ED', X'E5DCE4885B1FCA438DEB5D978E5CC0ED', 0, 'javascript:', 'Marinated Bitter Melon II', 2, NULL, 0, 'Marinated Bitter Melon II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1443', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound, or two bitter melons<BR>1 Tablespoon fermented black beans<BR>2 Tablespoons corn oil<BR>2 cloves garlic, minced<BR>1 teaspoon thin soy sauce<BR>2 teaspoons sesame oil<BR>1 teaspoon sugar<BR>dash of MSG, optional<BR><B><I>Preparation:</B></I><BR>1. Cut melon in half length-wise and remove and discard the seeds and pulp. Cut melon halves into onw-inch thick pieces. Parboil them in boiling water for one minute, then drain.<BR>2. Place the black beans in a small bowl, cover with cool water, stir, and drain. Repeat one more time.<BR>3. Heat the oil in a wok, add half of the chopped garlic, stir for a few seconds, add the black beans and stir for half a minute then add all the remaining ingredients and continue stirring for another half a minute.<BR>4. Put it all into a dish, cover, and refrigerate for twenty-four hours, then serve.<BR><I>Approximate nutrient analysis</I> when serving four persons, and assuming almost no liquid is consumed. Each serving has 342 calories, 2 g carbohydrate, 37 g protein, 250 mg sodium, 5 g total fat, and 137 mg cholesterol. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'286211D354295F449AB7AAA9DCBED9E0', X'26B9D2FDF7CBC847AE53B165BC449F9A', X'26B9D2FDF7CBC847AE53B165BC449F9A', 0, 'javascript:', 'Mashed Potatoes with Curry', 2, NULL, 0, 'Mashed Potatoes with Curry', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=916', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 potatoes, peeled and cut into cubes<BR>1 Tablespoon sugar<BR>1/2 cup onions, minced<BR>2 Tablespoons fresh cilantro, minced<BR>2 chili peppers, seeds removed, then minced<BR>1 teaspoon vegetable oil<BR>3 pounds finely minced or ground pork<BR>1 Tablespoon yellow curry powder<BR>2 teaspoons salt<BR>1 teaspoon ground white pepper<BR>1 egg, beaten until lemon yellow<BR>1 cup flour<BR>1 cup breadcrumbs<BR>12 cups vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Steam potatoes until soft. Remove them and add the sugar, the minced onion, cilantro, and chili pepper.<BR>2. Heat the oil and stir-fry the pork until no longer pink, then mix in the curry powder, salt, and pepper. Add this to the potato mixture, and allow it to cool. When cool, roll into ten balls.<BR>3. Coat the balls with flour, then dip them into the beaten egg. Next, coat them with the bread crumbs.<BR>4. Heat the one-and-a-half cups of oil and deep-fry the potato balls until golden. Drain on paper towels, then put them into a bowl. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F86ABE0857FE5D47BA3990BF5B92A24B', X'3E2BB5223F141740AF6E5728C84348D5', X'3E2BB5223F141740AF6E5728C84348D5', 0, 'javascript:', 'Mint and Tomatoes, Dai Style', 2, NULL, 0, 'Mint and Tomatoes, Dai Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1660', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 cloves garlic, minced<BR>1/2 teaspoon salt<BR>1/4 fresh chili pepper, minced<BR>3 scallions, cut lengthwise into half-inch very thin slivers<BR>1 cup mint leaves, coarsely chopped<BR>6 green-turning red tomatoes, diced<BR>2 teaspoons hot chili oil<BR><B><I>Preparation:</B></I><BR>1. In a mortar and pestle, pound garlic and salt until partially mashed.<BR>2. Add chili pepper and scallion pieces and mash for half minute more, then add tomatoes and continue to mash for another half minute, then stir thoroughly.<BR>3. Add chili oil, stir well, and then remove solids to a serving bowl. Pour remaining liquid over this mixture, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'76EFA92448468C41BE6992F9611F621F', X'89F55B0DA1AB0F41886CBD8898004FD4', X'89F55B0DA1AB0F41886CBD8898004FD4', 0, 'javascript:', 'Mock Chicken', 2, NULL, 0, 'Mock Chicken', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=907', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup flour<BR>3 large almond mushrooms, cut in quarter- inch long sticks<BR>1 cup vegetable oil<BR>1 teaspoon minced peeled fresh ginger<BR>1 teaspoon minced fresh peeled garlic<BR>1 sheet black or purple seaweed, crumbled<BR>1 small chili pepper, seeded and minced<BR>1 teaspoon white sesame seeds<BR>1 Tablespoon oyster sauce<BR>1 teaspoon sugar<BR>1 teaspoon sesame oil<BR>2 large lettuce leaves, one shredded, the other left whole<BR><B><I>Preparation:</B></I><BR>1. Mix flour with two tablespoons water to make a batter.<BR>2. Heat oil in a wok or pan, Take about eight thin mushroom strips and holding them together, dip them into the batter, remove, and deep fry turning once or twice until golden. Drain on paper towels. Repeat doing several bundles at a time until all are fried and drained. Discard all but one teaspoon of the oil, leaving it in the wok or pan.<BR>3. Stir-fry the ginger and garlic in the remaining teaspoon of oil for half minute, then add chili pepper pieces and stir before adding sesame seeds, oyster sauce, sugar, and sesame oil. Remove from the heat.<BR>4. Put whole lettuce leaf on a platter. Add shredded lettuce, then top with the mushroom bundles. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EF02E1D4A0C1B84A8862ADC1C33E4662', X'3068ACB70D212B439A55026C1D17DD52', X'3068ACB70D212B439A55026C1D17DD52', 0, 'javascript:', 'Mock Fish in Sweet and Sour Sauce', 2, NULL, 0, 'Mock Fish in Sweet and Sour Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1343', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 thin sheets bean curd<BR>1 large potato, cooked until soft, then mashed<BR>2 water chestnuts, minced<BR>3 Tablespoons minced bamboo shoots<BR>2 Tablespoons minced coriander leaves<BR>1/2 sheet purple or black seaweed laver, moistened<BR>1 cup cornstarch<BR>2 Tablespoons all-purpose flour<BR>1 very small Chinese black mushroom, soaked, stem discarded<BR>2 cups vegetable oil<BR>1/2 cup vegetable stock<BR>1 Tablespoon Chinese black vinegar<BR>2 Tablespoons granulated or crushed Chinese brown sugar slab<BR><B><I>Preparation:</B></I><BR>1. Wet thin bean curd sheets brushing them with warm water, then spread mashed potato across the center, the long way. Top this sprinkling water chestnuts, bamboo shoots, and coriander leaves evenly over the potato.<BR>2. Roll the seaweed into a long thin roll making this roll the length of the potato mixture. Press it into the potato so it is centered down its length.<BR>3. Beat cornstarch and flour with half cup of cold water until there are no lumps left.<BR>4. Fold the edges of the bean curd sheet, one by one, over the potato mixture, wetting each edge with a little of the cornstarch, flour mixture, and brush some more on both sides of this potato package shaping one end to a point to resemble the head, and cut the other end in an inch and separate that to look like a fish tail. Using the cornstarch mixture, paste the mushroom at the fish head end for its eye. Allow to dry for half an hour, then add more of the cornstarch mixture to the other side and allow this to dry, as well. Now cut one-inch slits about one inch apart and down one side of the mock fish, as one would for a real one.<BR>5. In a deep large fry-pan, heat the oil and deep fry the mock fish until crisp and golden in color, then drain on paper towels and put on a platter.<BR>6. In a small sauce pan, mix the stock, vinegar, and brown sugar, and two tablespoons of the cornstarch mixture and bring to the boil. Pour over the fried fish, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6464F4EEBC37CA4E82EE72A854EC3184', X'DBDB5983543AAB449AB4FD4ACE0B133D', X'DBDB5983543AAB449AB4FD4ACE0B133D', 0, 'javascript:', 'Mock Oysters', 2, NULL, 0, 'Mock Oysters', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=339', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound fresh or canned straw mushrooms<BR>1 Tablespoon cornstarch<BR>1 Tablespoon rice wine<BR>1 Tablespoon vegetarian oyster sauce<BR>1/4 cup arrowroot or water chestnut flour<BR>1 teaspoon baking powder<BR>1 Tablespoon corn oil<BR>1/2 cup corn oil<BR><B><I>Preparation:</I></B><BR>1. Blanch straw mushrooms for one minute then rinse in cold water; put them in a small pot and mix them with cornstarch, rice wine, oyster sauce and ½ cup cold water and bring to the boil, then simmer them for four or five minutes, drain and set them on paper towels to drain.<BR>2. Mix the arrowroot powder, baking powder, and oil making a batter and toss with the straw mushrooms.<BR>3. Heat half cup of oil and fry five or six mushrooms at a time just until the batter on them is golden. Set in a warmed plate and repeat until all are fried, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5C6271E267C41045B52B8425640D43FD', X'A9240044061086458B7C14D413121434', X'A9240044061086458B7C14D413121434', 0, 'javascript:', 'Monkey Head Mushrooms in White Sauce', 2, NULL, 0, 'Monkey Head Mushrooms in White Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=54', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 pound monkey head (or other) mushrooms<BR>1/2 pound chicken breast<BR>2 Tablespoons Yunnan or Smithfield ham<BR>1 Tablespoon oil<BR>2 shallots, minced<BR>2 slices ginger root<BR>2 Tablespoons Shaoxing wine<BR>2 Tablespoons chicken fat<BR>1 teaspoon sugar<BR>1/4 cup milk mixed with <BR>3 Tablespoons cornstarch<BR>1/2 pound bok choy or other greens<BR><I><B>Preparation:</I></B><BR>1. Rinse mushrooms in warm water to remove any soil or sand, then cut them into quarter-inch inch slices.<BR>2. Slice chicken breasts the same size then mix them with ham and mushrooms. Put all in a bowl, cover and steam for thirty minutes. Uncover, drain, and reserve the liquid.<BR>3. Heat wok and add oil then fry shallots and ginger for one minute. Add the mushroom mixture and fry another minute.<BR>4. Mix all other ingredients except the greens and add to the wok. Boil one minute until thick and set aside.<BR>5. Steam green vegetables until crisp tender, about 4 minutes. Plate mushroom mixture and put greens around.<BR>Serves six.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'140695C03443664293B2401752EDEFB5', X'4386CF8849C7D549ACB08EB197CD5AF5', X'4386CF8849C7D549ACB08EB197CD5AF5', 0, 'javascript:', 'Monkey Head Mushrooms, Crispy and Spicy', 2, NULL, 0, 'Monkey Head Mushrooms, Crispy and Spicy', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1372', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 to 8 dried monkey head mushrooms, soaked for one hour in tepid water<BR>1 Tablespoon ginger juice<BR>3 Tablespoons Chinese yellow rice wine<BR>1 egg white<BR>salt and sugar, to taste<BR>3 Tablespoons lotus root flour<BR>1 cup vegetable oil<BR>2 cloves fresh garlic, peeled and sliced<BR>1 Tablespoon fermented black beans, rinsed then chopped<BR>3 small hot pickled chili peppers<BR>a few lettuce leaves put on a platter<BR><B><I>Preparation:</B></I><BR>1. Gently squeeze water out of the monkey head mushrooms, then slice them thinly.<BR>2. Soak these mushroom slices in the ginger juice, rice wine, and the egg white, and the salt and sugar for half an hour, stirring two or three times during this time.<BR>3. Coat the mushrooms with the lotus root flour, stirring them gently.<BR>4. Heat a wok or a fry pan, add the oil, and fry the mushrooms until they are crisp, then remove them from the oil and drain on paper towels.<BR>5. Into the oil still in the wok, fry the garlic, black beans, and the chili peppers, and when all is really hot, add the mushrooms, and stir gently for one minute, then remove from the oil, put the lettuce leaves on a platter, and the mushroom mixture on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9B6C0DED94C709489D704538DBD1EEA7', X'07ABA21DE95F334AAD4095CE0FF02025', X'07ABA21DE95F334AAD4095CE0FF02025', 0, 'javascript:', 'Monks Meal', 2, NULL, 0, 'Monks Meal', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1345', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup chestnuts, peeled and cooked ten cut in half<BR>1 small can lotus seeds<BR>1 small can bambo soots, diced in large pieces<BR>20 water chestnuts, cut in quarters<BR>3 Tablespoons vegetable oil<BR>1 cup soaked and sliced Chinese black mushrooms, their stems discarded<BR>2 cups cooked glutinous rice<BR>1 cup napa cabbage, cut into quarter-inch slices<BR>1/2 cup purple seaweed, cut into one-quarter-inch by two-inch pieces<BR>1 quart vegetable or mushroom stock, or a mixture of both<BR>1/4 cup thin soy sauce<BR>1/4 cup rice wine or vegetable stock<BR>3 Tablespoons cornstarch<BR><B><I>Preparation:</B></I><BR>1. In one pot, boil chestnuts for five minutes, in another pot do the same for the lotus seeds. Drain them both, and set the ingredients aside in small bowls. Do the same for the bambo shoots and the water chestnuts.<BR>2. Heat wok or a large pot, add the oil, and then the mushrooms, and stir-fry for two minutes. Then add the chestnuts, lotus seeds, bamboo shoots, and the water chestnuts and stir-fry for two more minutes.<BR>3. Add all the rest of the ingredients and simmer until the cabbage is soft, then serve in iindividual bowls or in a large tureen.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B68CAF1EF4D2E846BE265147BC062C7F', X'E5043B277AFAAA4E9CE1CB486247412A', X'E5043B277AFAAA4E9CE1CB486247412A', 0, 'javascript:', 'Mushroom Platter Delight', 2, NULL, 0, 'Mushroom Platter Delight', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=247', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 ounce dried black tree ear fungi, rinsed well<BR>1 ounce dried white tree ear fungi, rinsed well<BR>1 ounce dried shiitake mushrooms, soaked in warm water<BR>1 ounce dried bamboo mushrooms<BR>1 cup vegetarian stock<BR>1 ounce fresh straw mushrooms<BR>1 ounce canned gluten, rinsed with boiling water<BR>1 ounce dried Chinese hawthorn or wolfberries<BR>2 Tablespoons thin or mushroom soy sauce<BR>1 Tablespoon Chinese rice wine<BR>2 Tablespoons cornstarch<BR>2 Tablespoons water chestnut flour<BR><I><B>Preparation:</I></B><BR>1. In separate pots, add boiling water to the tree fungi, return to the boil, cover and remove from the heat. Let stand at least half an hour, rinse each well, and cut away the thick stem-like parts. They can then be diced, if desired.<BR>2. After the shiitake mushrooms have soaked for at least twenty minutes, strain their liquid into the pot, discard the sandy remainder, and put the mushrooms in the soup pot; dicing them is optional.<BR>3. Pour boiling water over the bamboo mushrooms, drain well, rinse with cold water, cut into one inch pieces, and set aside. Do likewise with the gluten, which can be cut into simmer pieces if desired.<BR>4. Bring the stock to the boil, pour over the shiitake mushrooms, then add the bamboo mushrooms and the gluten, also the berries, soy sauce and wine and bring this back to the boil. <BR>5. Mix corn starch and water chestnut flour with one-quarter cup of cold water. Add it and the tree ears, bamboos mushroom and gluten pieces and stir until thick and clear, then serve on a somewhat flat platter with sloping sides.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'46273694FE12C7499BA851114185A292', X'87210F087B71E44D9664047C4DD0E866', X'87210F087B71E44D9664047C4DD0E866', 0, 'javascript:', 'Mushroom Tapenade I', 2, NULL, 0, 'Mushroom Tapenade I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1033', '<TD><RECIPE><B>Ingredients:</B><BR>1/3 cup vegetable oil<BR>12 ounces assorted mushrooms, finely chopped (we used half Chinese black forest ones and half Chinese straw mushrooms<BR>1/3 cup soy sauce<BR>1/3 cup sesame oil<BR>24 saltine crackers or squares of toasted bread<BR><I><B>Preparation:</B></I><BR>1. Heat oil in wok or saucepan, add mushrooms and stir well. Before adding soy sauce and sesame oil, coo until the mushrooms are dark and glossy and have given up much of their liquid (about ten minutes)<BR>2. Transfer to a serving bowl and serve with crackers or toast points, and a Lychee-tini or another of alcoholic beverage.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'63F8C3B4B206B84D849C7C8F443C95CF', X'55F88558E8405A4B8D1E104DBF899686', X'55F88558E8405A4B8D1E104DBF899686', 0, 'javascript:', 'Mushroom Tapenade II', 2, NULL, 0, 'Mushroom Tapenade II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1518', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/3 cup vegetable oil<BR>12 ounces assorted mushrooms, finely chopped such as half Chinese black and half Chinese straw mushrooms<BR>1/3 cup soy sauce<BR>1/3 cup sesame oil<BR>24 saltine crackers or squares of toasted bread<BR><B><I>Preparation:</B></I><BR>1. Heat oil in wok or saucepan, add mushrooms and stir well. Before adding soy sauce and sesame oil. Cook until mushrooms are dark and glossy and have given up much of their liquid; about ten minutes or so.<BR>2. Transfer to a serving bowl and serve with crackers or toast points, and a Lychee-tini or another of his alcoholic beverages.<BR><I>Approximate nutrient analysis</I> per salting cracker spread with this mushroom mixture, and assuming one tablespoon oil is absorbed. Each cracker with its toping has 17 calories, 1 g carbohydrate, half gram of protein, 47 mg sodium, 1 g fat, a trace of saturated fat, and no cholesterol.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6C829D404C30EA4B9AFC824B610D68BD', X'628DD1DFA4F2D8469554844297294FE8', X'628DD1DFA4F2D8469554844297294FE8', 0, 'javascript:', 'Mushrooms and Hair Vegetable', 2, NULL, 0, 'Mushrooms and Hair Vegetable', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1539', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 medium-size dried Chinese black mushrooms, stems removed and discarded<BR>2 Tablespoons Chinese rice wine such as shaoxing wine<BR>1 teaspoon coarse salt<BR>1 teaspoon sugar<BR>1 Tablespoon vegetable oil<BR>1 can straw mushrooms, drained<BR>1 cup chicken or vegetable stock<BR>1 cup button mushrooms<BR>1 large leaf fresh lettuce<BR>1 Tablespoon oyster sauce or an imitation oyster sauce<BR>1 Tablespoon cornstarch mixed with two teaspoons cold water<BR>1 teaspoon rendered chicken fat or a solid shortening<BR>1 ounce hair vegetable (<I>fa cai</I>)<BR>1/4 cup enoki mushrooms, cut just above where they attach to each other, and blanched for half minute in boiling water<BR><B><I>Preparation:</B></I><BR>1. Put black mushrooms, rice wine, salt, and sugar in a small bowl and steam over boiling water for half an hour.<BR>2. Heat wok or fry pan, add oil, then the straw mushrooms, stock, and the button mushrooms and stir-fry for fifteen minutes, then add the cornstarch mixture, and stir-fry until thickened. Then drain and reserve that liquid before mixing with oyster sauce and chicken fat. Drain once again, just leaving enough to coat the mushrooms. Set them on the lettuce leaf on a platter.<BR>3. Rinse and drain the hair vegetable, simmer for two minutes in the reserved liquid, put this over the mushrooms, and top with the enoki mushrooms, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F7BA6C786B935841BE0D30E1837216A4', X'1B7423A64236FE48BB42DE00740A6A9A', X'1B7423A64236FE48BB42DE00740A6A9A', 0, 'javascript:', 'Mushrooms, Sausages, and Eggs Over Rice', 2, NULL, 0, 'Mushrooms, Sausages, and Eggs Over Rice', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=260', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>8 dried <I>shiitake</I> mushrooms<BR>2 or 3 Chinese sausages (optional)<BR>1 Tablespoon corn oil<BR>3 eggs<BR>2 cups raw rice, cooked and cooled to room temperature<BR>2 Tablespoons sesame oil<BR>salt and white pepper, to taste<BR>1 scallion, minced<BR><I><B>Preparation:</I></B><BR>1. Soak mushrooms in one cup warm water, drain and sliver them. Strain and reserve half the liquid.<BR>2. Slice sausages (if using them), heat corn oil in a wok and stir-fry them for one minute, remove them from pan and drain on a paper towel.<BR>3. Beat eggs and add sesame oil and beat until combined then add to oil left in the wok and fry, stirring, for one minute until almost set, add mushrooms and remove from the heat immediately.<BR>4. Bring reserved mushroom water to boil, add rice, stir, and heat through and transfer to a large warm serving platter or bowl tossing with salt and pepper, if needed. Remember, two cups of rice makes six cups of cooked rice.<BR>5. Pour egg mixture over the rice, sprinkle with scallion pieces and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'80E61BA2CEB2D545B8CE3FE98DA2C347', X'8D523E4829C3BE459957F3B2FE719E93', X'8D523E4829C3BE459957F3B2FE719E93', 0, 'javascript:', 'Mustard Greens with Dried bean Curd', 2, NULL, 0, 'Mustard Greens with Dried bean Curd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1839', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound fresh or reconstituted dried mustard greens, blanched in boiling water for two minutes, then drained<BR>3 squares of dried bean curd, coarsely diced<BR>1 teaspoon vegetable oil<BR>1/4 pound shrimp, peeled with their veins removed, then sliced in half the long way<BR>1 small fresh or dried chili pepper, seeds removed and discarded, and coarsely chopped<BR>2 fresh cloves garlic, peeled and minced<BR>1/2 teaspoon thin soy sauce<BR>1/4 teaspoon ground white pepper<BR>1/2 teaspoon granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Mix blanched mustard greens and the dried bean curd and set aside.<BR>2. Heat a wok or fry pan and add the oil, then stir-fry the greens and the bean curd for two minutes.<BR>3. Add all the remaining ingredients and stir-fry for two minutes, then serve in a pre-heated bowl.<BR>pickled mustard greens Ingredients: 2 pounds fresh mustard greens 3 tablespoons coarse salt 3 cups Chinese rice vinegar 1 cup granulated sugar 6 slices peeled fresh ginger 6 red chili peppers Preparation: 1. Cut mustard greens into two-inch pieces and toss with half the salt and set aside for two hours. 2 In a wok or saucepan, add the remaining salt, rice vinegar, and sugar. Bring to the boil and stir until the sugar dissolves, then add the ginger and the chili peppers and stir. Remove from the heat and allow this to cool. 3. Drain the mustard green pieces and add them to the sterilized jars. Pour the sugar water over them, shake the jars to allow any bubbles to rise to the top, then cover and refrigerate. 4. Shake the jars once or twice every day. They are ready to eat after three days, best in a week, and good for up to three weeks.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8CA19DD21C02704C8651BD5D2FBCBB63', X'4FA20951AE39A743BBC6EC293ED8DA40', X'4FA20951AE39A743BBC6EC293ED8DA40', 0, 'javascript:', 'Mustard Greens, Simple and Easy', 2, NULL, 0, 'Mustard Greens, Simple and Easy', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1831', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>1/2 cup thinly sliced onions<BR>3 cloves fresh garlic, peeled and chopped finely<BR>8 cups <I>cong cai</I> or any other type of mustard green, rinsed and cut or torn into two-inch pieces<BR>3 Tablespoons chicken stock<BR>1/2 teaspoon mixed salt and pepper<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Cut away center ribs and sliver them, tear rest of the leaves into one-inch pieces.<BR>2. Heat a wok or large sauce pan, then add the oil, and just before it smokes, add the onions and the garlic, reduce the heat, and stir-fry for about eight minutes until these vegetables start to brown.<BR>3. Add the bigger pieces of the mustard greens and the stock, and stir-fry for about three minutes until the greens wilt and most liquid evaporates. Then push them to one side and add slivers of their stems, salt and pepper, and the sesame oil, and stir-fry for one minute before tossing everything together. Then serve everything together on pre-heated plates.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'50397ED7AA5D1D489BEE5E41AF43E341', X'29C3057D16C99E4D827979FDFA597226', X'29C3057D16C99E4D827979FDFA597226', 0, 'javascript:', 'Noodles with Green Mustard Leaves and Tofu', 2, NULL, 0, 'Noodles with Green Mustard Leaves and Tofu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=160', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 large bunch of broccoli, cut into flowerettes<BR>2 cups sliced carrots<BR>12 to 15 large sliced mushrooms<BR>1 and a 1/2 cups diced fresh tofu<BR>1 cup mustard green leaves<BR>9 ounces soba (buckwheat noodles)<BR>1 cup misoyaki sauce*<BR>3 Tablespoons sesame oil<BR>1 Tablespoon or more toasted sesame seeds<BR><I><B>Preparation:</I></B><BR>1. Steam all vegetables together, except the mustard green leaves, with the tofu until `al dente.`<BR>2. Bring eight cups of water to the boil, put the soba noodles in for thirty seconds, then drain and pour the misoyaki sauce over them and toss.<BR>3. Add the raw mustard green leaves and mix well. Toss with the sesame oil and garnish with the sesame seeds.<BR>4. Serve with a good chili sauce.<BR><B>Note</B> *This is available at Japanese food stores.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DE779E7C756F4045BFB4DC11AC1640A4', X'0D457B72FA06E64087DDD95EA6D65597', X'0D457B72FA06E64087DDD95EA6D65597', 0, 'javascript:', 'Northern Napa Cabbage', 2, NULL, 0, 'Northern Napa Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1336', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small napa cabbage, about one pound<BR>1 cup vegetable oil<BR>1/4 carrot<BR>1 teaspoon Chinese rice wine<BR>1 teaspoon coarse salt<BR>1 cup chicken or vegetable broth<BR>1 teaspoon sesame oil<BR>1 Tablespoon cornstarch mixed with two tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Put cabbage in a heat-proof bowl. Heat the oil and pour it over the cabbage and after ten minutes turn it over, and let rest another fifteen minutes, then remove and discard the oil or use it for another purpose, and return the cabbage to the heat-proof bowl.<BR>2. Make one end of the carrot come to a point, then make hole in the stem end of the cabbage and insert the carrot there. Then put the bowl and the cabbage and the liquid into a steamer over rapidly boiling water, and steam for one hour. Then remove the cabbage and set it on a serving plate, and pour one half cup of the liquid into a small saucepan.<BR>3. Pour the cornstarch mixture into the sauce pan, bring to the boil stirring and when thick, pour this over the cabbage, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'107D8DE287DB0B42BAE7B96F63A63AB0', X'78EFC9944A51B745B9F5E83C3663DC18', X'78EFC9944A51B745B9F5E83C3663DC18', 0, 'javascript:', 'Oil-blanched Vegetables with Mint', 2, NULL, 0, 'Oil-blanched Vegetables with Mint', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1006', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups vegetable oil<BR>1 Tablespoon cornstarch<BR>5 thin Asian eggplants (about two pounds), roll-cut into two-inch pieces<BR>1 green pepper, seeded and angle cut to two-inch pieces<BR>1 large onion, cut into two-inch wedges<BR>1 cup Asian basil, leaves pulled off the stems<BR>6 cloves garlic, peeled and each one lightly smashed<BR>1 fresh hot pepper, seeded and minced<BR>2 Tablespoons oyster sauce<BR>1/4 teaspoon Hakka garlic-chili sauce (see its recipe)<BR>1/4 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat vegetable oil.<BR>2. Dust cornstarch on the eggplant and put them in the hot oil for two minutes, remove and drain.<BR>3. Reheat the oil and oil-blanch the green pepper and the onion pieces for one minute, then drain.<BR>4. Reheat the oil and fry basil for twenty seconds until crisp, remove and drain. Discard all but one teaspoon of the oil.<BR>5. Reheat remaining oil, fry garlic and hot pepper pieces for half minute, then add oyster and chili sauces and the eggplant, green pepper, and the onion pieces and stir-fry for one minute, remove to a serving bowl and pour sesame oil over them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F4BCDEEB7399DE42AC4F1CB8CF0B5280', X'984D405CAD7B074088E555724DF1215B', X'984D405CAD7B074088E555724DF1215B', 0, 'javascript:', 'Olive Kernels, Gingko Nuts, and Vegtables', 2, NULL, 0, 'Olive Kernels, Gingko Nuts, and Vegtables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=695', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup corn oil<BR>2 slices fresh ginger, slivered<BR>2 black mushrooms, soaked, stems removed, then diced<BR>1/2 cup button mushrooms, diced<BR>1/2 red pepper, diced<BR>1/2 green pepper, diced<BR>1/4 cup olive kernels<BR>2 chicken thigh meat cut into one-inch pieces<BR>1/2 can gingko nuts, drained<BR>3 Tablespoons corn kernels<BR>1 Tablespoon mushroom soy sauce mixed with two teaspoons cornstarch and one teaspoon cold water<BR><B><I>Preparation:</B></I><BR>1. Heat oil in a wok, fry ginger half minute until fragrant, then add mushrooms, peppers, and olive kernels, and fry for two minutes until mushrooms are somewhat crisp and olive kernels are light tan. Then drain and set aside.<BR>2. Be sure the oil is again hot, and fry the chicken pieces for two minutes, drain and discard all but one tablespoon of the oil.<BR>3. Reheat the tablespoon of oil, add mushroom mixture, the chicken, gingko nuts, corn, and mushroom soy mixture, and stir-fry for two minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'488A272D7D6EAB418C1D8D705F8B5FEC', X'FBBF9C8443B1164FBAA4F3BDA981A350', X'FBBF9C8443B1164FBAA4F3BDA981A350', 0, 'javascript:', 'One-minute Bean Sprouts', 2, NULL, 0, 'One-minute Bean Sprouts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=918', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons peanut oil<BR>4 slices fresh ginger, shredded<BR>1 scallion, cut into one-inch pieces<BR>1 teaspoon salt<BR>1/2 teaspoon sugar<BR>1 pound bean sprouts, their tails removed<BR><B><I>Preparation:</B></I><BR>1. Heat wok, add oil, then the ginger and the scallion, and stir-fry a few seconds, before adding the bean sprouts. Stir-fry them for half a minute.<BR>2. Add salt and sugar and stir well several times. They are ready to serve in one minute and will be crisp.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D9E387951A023243B903DB241E5E2CE9', X'DC2F4900DF327744828A86E29D5191CA', X'DC2F4900DF327744828A86E29D5191CA', 0, 'javascript:', 'Pan-fried Silk Squash', 2, NULL, 0, 'Pan-fried Silk Squash', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1624', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 silk squash, about half a pound<BR>2 Tablespoons vegetable oil, divided<BR>1 scallion, minced<BR>1/4 cup chicken or vegetable stock<BR>2 Tablespoons shelled peanuts, paper coverings removed<BR>1 large clove garlic, peeled and minced<BR>4 eggs, beaten until a light lemon color<BR><B><I>Preparation:</B></I><BR>1. Using a peeler, remove ridges from the entire length of the silk squash, then cut it into eight pieces, and cut these into a dozen strips.<BR>2. Heat half the oil and stir-fry the scallions pieces until fragrant, then add the silk squash and stir fry for one minute before adding the chicken stock and bringing to the boil, then immediately remove all to a bowl.<BR>3. Add the remaining tablespoon of oil and stir-fry the garlic and peanuts for two minutes.<BR>4. Return the silk squash and stock and then add in the beaten eggs; and using a pair of chopsticks, stir the mixture until the eggs begin to set.<BR>5. Turn the eggs over with a spatula, and stir again with the chopsticks for another minute, then serve on a pre-heated plate.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'33DBF3E9FC55814A966979809873BB7F', X'E82BC4997327E84CA56E22BD86D3A186', X'E82BC4997327E84CA56E22BD86D3A186', 0, 'javascript:', 'Peas and Mushrooms', 2, NULL, 0, 'Peas and Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1541', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound snow peas (or melting mouth peas) strings removed<BR>1/2 cup sliced bamboo shoots<BR>1 small can abalone mushrooms, drained, each cut in half, if they seem too big<BR>1 small can golden mushrooms, drained<BR>12 button mushrooms, each cut in half<BR>1/2 cup each of other mushrooms of choice; it they are canned, drain them<BR>1/2 cup cold chicken stock<BR>3 Tablespoons oyster sauce<BR>1 teaspoon sugar<BR>2 Tablespoons cornstarch<BR>1 Tablespoon dark or mushroom soy sauce<BR>2 Tablespoons vegetable oil or lard or chicken fat<BR><B><I>Preparation:</B></I><BR>1. Rinse and dry peas, and mix them with the bamboo shoots, and set aside.<BR>2. Dry all mushrooms, if canned, with paper towels and mix them together with the peas mixture above.<BR>3. Mix chicken stock, oyster sauce, sugar, and the cornstarch and set aside.<BR>4. Heat wok or fry pan, add oil or another fat, and fry the pea/mushroom mixture for three minutes, then add the chicken stock. Stir-fry until thickened, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F77989E85528EF4B94958846626A094D', X'834C8896BEEDAA47A4FD554B86174C0F', X'834C8896BEEDAA47A4FD554B86174C0F', 0, 'javascript:', 'Pickled Carrots', 2, NULL, 0, 'Pickled Carrots', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1785', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup white rice vinegar<BR>2 Tablespoons granulated sugar<BR>1/2 teaspoon salt<BR>1 pound peeled baby carrots, cut into 0ne-quarter inch rounds<BR><B><I>Preparation:</B></I><BR>1. Put vinegar, sugar, and salt into an enamel pot and bring to the boil, then allow this to cool covered overnight.<BR>2. Add the carrots and let them sit in this enamel pan for three hours. Then transfer them and the liquid to sterilized jars, cover with sterilized lids, refrigerate for one week, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'77EA2BD689DC5A4E8ED34A9C73C55B33', X'ED2C7F6E8BA76043809306DB1ACDCAE9', X'ED2C7F6E8BA76043809306DB1ACDCAE9', 0, 'javascript:', 'Pickled Eggpant, Sichuan-style', 2, NULL, 0, 'Pickled Eggpant, Sichuan-style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=789', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups thin purple-skinned Japanese-style eggplant<BR>1 cup white-skinned eggplant<BR>1 cup green-skinned eggplant<BR>1 teaspoon sesame oil<BR>1/4 cup coarsely minced cilantro<BR>1/2 teaspoon coarse salt<BR>1 teaspoon sugar<BR>4 Tablespoons clear rice vinegar<BR>2 Tablespoon red rice vinegar<BR>2 Tablespoons thin or white soy sauce<BR>2 Tablespoons finely minced fresh ginger<BR>2 Tablespoons finely minced fresh garlic<BR>1 Tablespoon whole turmeric seeds, smashed with wooden handle of a cleaver<BR>3 fresh red chili peppers, seeded, and minced<BR>1 Tablespoon toasted sesame seeds, as garnish<BR><B><I>Preparation:</B></I><BR>1. Wash, dry, then cut all the eggplants in half-inch by two-inch strips. Then line steamer basket with an all-white paper towel and place then on it.<BR>2. Steam over boiling water for twenty minutes, remove to a strainer basket, and cool in the refrigerator, covered for one hour.<BR>3. Mix rest of the ingredients, except the sesame seeds, in a glass or other non-reactive bowl, then toss with the eggplant pieces. Cover and set aside for one hour. Then drain and sprinkle on sesame seeds and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'55CCE92368115B40B170AF1F53BCC444', X'D4E5E8E796F543479D2A2644339E7820', X'D4E5E8E796F543479D2A2644339E7820', 0, 'javascript:', 'Pickled Long Beans', 2, NULL, 0, 'Pickled Long Beans', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=889', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup quarter-inch sliced long beans, ends removed<BR>1/2 cup Chinese red rice vinegar<BR>1 Tablespoon coarse salt<BR>1/4 pound minced or ground beef (optional)<BR>1 Tablespoon mushroom soy sauce<BR>2 Tablespoons vegetable oil<BR>1 large clove garlic, minced finely<BR>1 chili pepper, seeded then minced finely<BR>1 teaspoon sugar<BR>1 teaspoon cornstarch mixed with one tablespoon of water<BR><B><I>Preparation:</B></I><BR>1. Marinate long beans in vinegar and salt solution for six hours or overnight, covered and in the refrigerator.<BR>2. Remove beans and drain well.<BR>3. Mix meat and soy sauce and let sit for fifteen minutes.<BR>4. Heat oil and stir-fry the beef mixture for one minute before adding the garlic and the chili pepper pieces, and stir-fry another minute, then add the beans and the sugar, and continue to cook another two minutes before adding the cornstarch water. Stir-fry another minute until beans and meat, if used, are coated and starch solution is clear, Serve hot, warm, or cold, as desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6DD8C20AD8C4B54086191DA571318CB5', X'950369FB511F954A8863604700257608', X'950369FB511F954A8863604700257608', 0, 'javascript:', 'Pickled Mustard Greens I', 2, NULL, 0, 'Pickled Mustard Greens I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=421', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>8 cups of mustard greens, each leaf twisted and bruised<BR>3 Tablespoons coarse salt<BR>1 Tablespoon sugar<BR>2 Tablespoons white vinegar<BR><I><B>Preparation:</B></I><BR>1. Wash greens with hot water and pack them into a crock or glass container. 2. Mix salt, sugar, and vinegar and one cup of hot water. Pour this mixture over the greens, and if not enough to cover, add more water until they are covered with water.<BR>3. Put a plate on top and a full unopened can on the plate. Set aside in a draft-free location for three days. Then remove and rinse the greens, saving the brine. Rinse the crock and put brine and greens back into it and refrigerate until ready to use.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B8EFCED888792D4685BA5FA1ABB1C98F', X'BBCE42AC775C314298394703783DF22A', X'BBCE42AC775C314298394703783DF22A', 0, 'javascript:', 'Pickled Mustard Greens II', 2, NULL, 0, 'Pickled Mustard Greens II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1840', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds fresh mustard greens<BR>3 tablespoons coarse salt<BR>3 cups Chinese rice vinegar<BR>1 cup granulated sugar<BR>6 slices peeled fresh ginger<BR>6 red chili peppers<BR><B><I>Preparation:</B></I><BR>1. Cut mustard greens into two-inch pieces and toss with half the salt and set aside for two hours.<BR>2 In a wok or saucepan, add the remaining salt, rice vinegar, and sugar. Bring to the boil and stir until the sugar dissolves, then add the ginger and the chili peppers and stir. Remove from the heat and allow this to cool.<BR>3. Drain the mustard green pieces and add them to the sterilized jars. Pour the sugar water over them, shake the jars to allow any bubbles to rise to the top, then cover and refrigerate.<BR>4. Shake the jars once or twice every day. They are ready to eat after three days, best in a week, and good for up to three weeks.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E1A160E285A52648B42185DABB6AFA1E', X'BFE2103714FFFD49925213DA5A9B9A7F', X'BFE2103714FFFD49925213DA5A9B9A7F', 0, 'javascript:', 'Pickled Radishes', 2, NULL, 0, 'Pickled Radishes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1853', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large white radish, the kind Japanese call <I>daikon</I><BR>1/2 teaspoon coarse salt<BR>15 medlar which are wolfberries, also known as goji berries<BR>1/2 teaspoon granulated sugar<BR>1 teaspoon thin soy sauce<BR>1/2 cup Chinese rice vinegar<BR>1/2 teaspoon hot oil<BR>2 Tablespoons honey<BR><B><I>Preparation:</B></I><BR>1. Peel, then shred the radish in fine strips.<BR>2. Mix radish with salt, and set aside for one hour, then rinse and dry these strips with a dry dish towel.<BR>3. Finely mince the goji berries and mix them with the sugar and set them aside in a separate bowl for half an hour.<BR>4. Add the soy sauce, rice vinegar, hot oil, and honey to the dried radish strips and mix them, hen allow to rest twenty to thirty minutes before adding the minced goji berries and serving it.<BR><B>Note:</B> This pickled vegetable dish can be made the night before or earlier in the day; and it can be made using carrots, potatoes, lotus, or other roots or rhizome food items.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'42D057536E6CDC488D1AF15615124863', X'B008EDEC78CF1042A5E6A666CC58E490', X'B008EDEC78CF1042A5E6A666CC58E490', 0, 'javascript:', 'Pine Nuts and Crispy Bok Cai', 2, NULL, 0, 'Pine Nuts and Crispy Bok Cai', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=697', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 cups bokcai or spinach leaves, washed and dried<BR>3 cups peanut oil<BR>4 ounces pine nuts<BR><B><I>Preparation:</B></I><BR>1. Shred greens very thin.<BR>2. Heat oil to 400 degrees F. and deep fry one-fourth of the leaves for half a minute, remove, and drain on paper towel. Repeat until all greens are fried and crisp.<BR>3. Fry the pine nuts just until they start to turn tan, and drain them.<BR>4. Put greens and nuts in a bowl, toss, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'017BE542B36FDA4D8CB99A500803E837', X'754C4EB8FD7E9D42AA72419C0B235430', X'754C4EB8FD7E9D42AA72419C0B235430', 0, 'javascript:', 'Potato and Kimchi Pancakes', 2, NULL, 0, 'Potato and Kimchi Pancakes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1565', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound Idaho-type potatoes, peeled and grated<BR>1/2 cup <I>suan cai</I> which is napa cabbage kimchi, minced fine<BR>5 scallions, green part only, minced fine<BR>3 Tablespoons potato flour<BR>1 teaspoon coarse salt<BR>1 Tablespoon thin soy sauce<BR>3 cups oil for deep frying<BR><B><I>Preparation:</B></I><BR>1. In a large bowl, mix the first six ingredients.<BR>2. In wok or deep fry pan, heat the oil and take a ladle full of six mixed ingredients and carefully put it into the oil, flattening the top. Repeat until several are in the pan, not crowding any of them, and fry the pancakes until golden on one side, turn them over and fry on the other side until golden, then remove to a heat-proof platter and put into a 200 degree oven. Repeat until all are fried. Serve with the chili sauce on the side for dipping.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'37796B940636C44D80B4747B5F72411F', X'6418BC10CD1C80438ADD87EE3D74178C', X'6418BC10CD1C80438ADD87EE3D74178C', 0, 'javascript:', 'Potato Cake and Bottle Gourd', 2, NULL, 0, 'Potato Cake and Bottle Gourd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1240', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound potatoes, peeled and shredded<BR>1 small bottle gourd, peeled, and shredded<BR>2 eggs, beaten<BR>1/2 teaspoon salt<BR>1/4 cup flour<BR>3 Tablespoons potato starch<BR>1 scallion, minced<BR>1 cup vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Mix the potato and bottle gourd shreds, eggs, salt, flour, potato starch. Then lightly mix in the minced scallions.<BR>2. Heat oil in a wok or fry pan, and put mixture in flattening it into a pancake. Fry until golden, turn it over and fry it on the other side until golden. Drain on a paper towel, cut into pie-shaped pieces, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'610A46A0E0EAE44288EAF0E0A90F6336', X'76ED5E852AAB7449BE8C5C578B73D497', X'76ED5E852AAB7449BE8C5C578B73D497', 0, 'javascript:', 'Potato Strings in Black Vinegar', 2, NULL, 0, 'Potato Strings in Black Vinegar', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1591', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 to 2 large potatoes, cut into thin slices then into thin strips<BR>1 Tablespoon coarse salt<BR>2 Tablespoons vegetable oil<BR>2 large cloves garlic, sliced then cut into very thin strips<BR>1/4 green sweet pepper, seeded and cut into thin strips<BR>1/4 fresh red hot pepper, seeded and cut into thin strips<BR>2 teaspoons granulated sugar<BR>2 Tablespoons black Zhejiang vinegar<BR>1 teaspoon thin soy sauce<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix potato strips and salt and two tablespoons cold water, and set aside for fifteen minutes. Then drain.<BR>2. Heat wok or fry pan, add the vegetable oil and the garlic strips and stir fry for half minute, then add the potato strips and the green pepper strips and stir-fry for three minutes before adding the fresh hot red pepper strips. Stir-fry for one minute more, then remove to a serving bowl.<BR>3. Toss with the sugar, vinegar, soy sauce, and sesame oil, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'348F151B4D8DC54CB8CA4D07989B4E15', X'8CF56C4FE352C541AAC954A5650EB027', X'8CF56C4FE352C541AAC954A5650EB027', 0, 'javascript:', 'Potatoes and Pickled Cabbage', 2, NULL, 0, 'Potatoes and Pickled Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1275', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons sesame oil<BR>1 pound potatoes, peeled and cut into thin noodle-like strips, dried in a towel<BR>2 dried chili peppers (optional)<BR>1/2 teaspoon salt<BR>1/2 teaspoon sugar<BR>1 recipe Duck with <I>mi liang fan</I> or one made with fish<BR>1 cup shredded pickled cabbage<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add sesame oil and when it is very hot, add potato strips and the chili peppers, salt, and sugar, and stir-fry for four minutes until just soft. Spread these evenly on a pre-heated plate as the base for the rest of the dish.<BR>2. In the same wok, reheat the prepared Duck with Mi Liang Fan or fish recipe and mix it with the pickled cabbage. Stir until barely heated through, then put on top of the shredded potatoes, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3DEAF9910E75284FB1B54F6FA8257518', X'89F90BD197D30A4C86FC3ABF3C031E61', X'89F90BD197D30A4C86FC3ABF3C031E61', 0, 'javascript:', 'Potatoes Twice, with Chinese Celery', 2, NULL, 0, 'Potatoes Twice, with Chinese Celery', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=882', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 sweet potatoes, peeled and cut into two-inch chunks, about three-quarters of a pound<BR>2 white potatoes, peeled and cut into one-inch chinks, about three-quarters of a pound<BR>1/4 cup vegetable oil<BR>2 scallions, coarsely minced<BR>4 stalks Chinese celery, diced<BR>1/2 teaspoon ground white pepper<BR>1 teaspoon salt<BR>4 Tablespoons Chinese celery leaves, coarsely minced<BR><B><I>Preparation:</B></I><BR>1. Boil sweet potatoes for fifteen minutes, drain, and set aside reserving their water.<BR>2. Boil white potatoes for twenty-five minutes, drain and set aside.<BR>3. Heat oil in wok or deep fry-pan and add white potatoes and stir-fry until they start to color, then add sweet potatoes and continue to stir and fry until all are golden, then drain and remove from the oil, set oil aside, and do not wash or rinse the pan.<BR>4. Reheat the pan, str-fry the scallion for one half minute, then add celery pieces and stir-fry another minute before adding pepper and salt, then the reserved potatoes and half cup of the sweet potato water. Stir gently for two minutes, then add chopped leaves, toss well, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9707227437724B4FBC52866102978946', X'06FD346449120942B3840D46F10CCEDF', X'06FD346449120942B3840D46F10CCEDF', 0, 'javascript:', 'Preserved Duck Eggs and Bitter Melon', 2, NULL, 0, 'Preserved Duck Eggs and Bitter Melon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=708', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 bitter melon, pith and seeds removed<BR>2 preserved duck eggs, salted or <I>pidan</I>-type<BR>3 Tablespoons sesame seeds<BR>1 teaspoon hoisin or broad bean paste<BR>2 Teaspoons thin soy sauce<BR>2 teaspoons sesame paste<BR>2 teaspoons hot tea<BR><B><I>Preparation:</B></I><BR>1. Cut bitter melon into thin two-inch strips and blanch in three changes of boiling water.<BR>2. Peel eggs and using a hot knife dice into half-inch pieces.<BR>3. Toast sesame seeds in a dry pan until lightly colored.<BR>4. Mix broad bean paste, soy sauce, sesame paste, and hot tea. Then add duck eggs and bitter melon pieces and stir well. Refrigerate covered for an hour, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'77F7000511CFC04EB99580BC7132C0CE', X'48BEC9987D73E84186F5124494C0F08B', X'48BEC9987D73E84186F5124494C0F08B', 0, 'javascript:', 'Pumpkin in Coconut Cream', 2, NULL, 0, 'Pumpkin in Coconut Cream', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1781', '<TD><RECIPE><B><I>Ingredients: </B></I><BR>1 small Asian or kobacha pumpkin, peeled and cut into one-inch cubes<BR>1 cup taro root, peeled and cubed into one-inch cubes<BR>10 medium-size wood ear fungi, soaked and chopped coarsely<BR>1/2 cup roasted peanuts, their paper exterior shells removed and discarded<BR>1 cup peeled silk squash, cut in one-inch cubes<BR>1 cup stirred canned coconut cream<BR>2 teaspoons sugar<BR>1 teaspoon coarse salt<BR>2 Tablespoons vegetable oil<BR>2 Tablespoons coarsely chopped mint or coriander leaves<BR><B><I>Preparation:</B></I><BR>1. Put the pumpkin and the taro root cubes in two cups of cold water in a medium-size pot. Bring this to the boil, reduce the heat, and simmer for ten minutes.<BR>2. Next, add the fungi, peanuts, silk squash, and the coconut cream, also the sugar and the salt, and simmer for five minutes more.<BR>3. Now bring this to the boil for three minutes, then drain, and discard the liquid.<BR>4. Heat a wok or fry pan, add the oil, and when hot, add the drained vegetables, and stir-fry for three minutes. Put everything cooked into a serving bowl, sprinkle the mint or coriander leaves on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A3F1FF1C0B990F4589D6886748833A4D', X'D4D2CF141EEF8340A5BA7CE4E78BCD51', X'D4D2CF141EEF8340A5BA7CE4E78BCD51', 0, 'javascript:', 'Pumpkin Sticks', 2, NULL, 0, 'Pumpkin Sticks', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1748', '<TD><RECIPE>This recipe requires peeling a green-skinned flat-topped kobacha squash, then cutting its flesh into sticks as if making French-fried potatoes. After that, heat half to three-quarters of a cup of vegetable oil in a wok or large pot, and squash sticks and fry them for two minutes, stirring continuously. Next, mash two salted duck egg yolks. Remove the squash sticks to a paper-towel-lined small bowl, and while the squash sticks are still hot, mix them with the mashed salted duck egg yolks and refry them for another minute; then toss them with a little coarse salt. Serve them hot.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DDC31BCBCE79DE4194ADC19093D7A6D8', X'BAAE06006673424EB6448DC19DAFAAB6', X'BAAE06006673424EB6448DC19DAFAAB6', 0, 'javascript:', 'Pumpkin, Bitter Melon, and Small Dried Fish', 2, NULL, 0, 'Pumpkin, Bitter Melon, and Small Dried Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1025', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 bitter melon, cut in half, seeds and pith removed, then cut into two-inch by half-inch strips<BR>1 teaspoon coarse salt<BR>1 pound Chinese pumpkin, peeled and cut into two-inch by half-inch strips<BR>1 hundred year preserved duck egg, diced<BR>1 cup vegetable oil<BR>1 cup small dried fish<BR>1 teaspoon broad bean paste<BR>1/2 teaspoon sugar<BR>1 teaspoon thin soy sauce<BR>1 teaspoon sesame oil<BR>1 teaspoon sesame seeds<BR><B><I>Preparation:</B></I><BR>1. Soak bitter melon pieces in two cups of water and coarse salt for half an hour. Drain, rinse, and drain again.<BR>2. Put bitter melon into boiling water for three minutes, drain and put into a serving bowl.<BR>3, Put pumpkin into boiling water and simmer for fifteen minutes, drain, and put into same bowl with the bitter melon.<BR>4 . Heat wok, add oil, and deep fry the small fish until crispy. Drain on paper towels, and add to the items in the bowl.<BR>5. Mix broad bean paste, sugar, soy sauce, and sesame oil and pour over ingredients in the bowl. Toss them, sprinkle with sesame seeds and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D803A832D62F304A994AB49DD3136A7C', X'E225E4380D41AA43B1C66B80AC0858F7', X'E225E4380D41AA43B1C66B80AC0858F7', 0, 'javascript:', 'Quick and Easy Pickled Cabbage I', 2, NULL, 0, 'Quick and Easy Pickled Cabbage I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1364', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 head of cabbage (about 3 pounds)<BR>8 ounces catsup<BR>2 ounces salt<BR>4 ounces sugar<BR>4 ounces crushed chili pepper<BR><B><I>Preparation:</B></I><BR>1. Cut cabbage into rectangular pieces, about two-inches square.<BR>2. Mix all ingredients in a big bowl; and do wear disposable gloves if mixing by hand.<BR>3. Stuff cabbage into a large pickling jar; packing it tightly.<BR>4. Place jar in a cool place. In thirty minutes, the volume will be reduced by one-third. Then cover the jar with lid and place in the refrigerator. By the end of two hours, the volume will be further reduced to one half; then the pickled cabbage is ready to be served.<BR><B>Note:</B> Store any unused pickled cabbage in the refrigerator; it keeps well for a week.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1AA8D1D95C815B4BA7AEDF5E5AF7E00E', X'6B729AEA2FC0C34584738D9BCBD26DB8', X'6B729AEA2FC0C34584738D9BCBD26DB8', 0, 'javascript:', 'Quick and Easy Pickled Cabbage II', 2, NULL, 0, 'Quick and Easy Pickled Cabbage II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1481', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 head of cabbage, about three pounds<BR>8 ounces catsup<BR>2 ounces salt<BR>4 ounces sugar<BR>4 ounces crushed chili pepper<BR><B><I>Preparation:</B></I><BR>1. Cut cabbage into rectangular pieces, about two-inches square.<BR>2. Mix all ingredients in a big bowl; and do wear disposable gloves if mixing by hand.<BR>3. Stuff cabbage into a large pickling jar; packing it tightly.<BR>4. Place jar in a cool place. In thirty minutes, the volume will be reduced by one-third. Then cover the jar with lid and place in the refrigerator. By the end of two hours, the volume will be further reduced to one half; then the pickled cabbage is ready to be served.<BR><B>Note:</B> Store any unused pickled cabbage in the refrigerator; it keeps well for a week.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5F18838324FA084CBBDDCF992AB4D5ED', X'2531CAB5013EAC4CBA8D2ADB3CDC175E', X'2531CAB5013EAC4CBA8D2ADB3CDC175E', 0, 'javascript:', 'Rice and Mustard Greens', 2, NULL, 0, 'Rice and Mustard Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1568', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups long grain rice<BR>3 Tablespoons sesame oil<BR>1 Tablespoon mustard seeds<BR>1 and 1/2 cups salted mustard greens, chopped<BR>salt and pepper, to taste<BR>1 cup chopped ripe tomatoes, optional<BR>1 Tablespoon sesame seeds<BR><B><I>Preparation:</B></I><BR>1. Put rice in a rice cooker or large pan with three cups cold water, cover, and steam for twenty minutes. Then let it rest another twenty minutes. Next, fluff the rice with a fork and transfer it to a bowl and wash the wok.<BR>2. Heat oil in the wok or a fry-pan, add mustard seeds and stir-fry half minute before adding the rice, the mustard greens, and salt and pepper to taste; and we recommend two teaspoons salt and half teaspoon of ground white pepper. Stir well.<BR>3. Put this mixture into individual rice bowls, top with tomatoes, if using, and a sprinkling of the sesame seeds, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0845CABF209A9942B4B372EC95789E0A', X'458C9180C0AFFB45B45524281D659E15', X'458C9180C0AFFB45B45524281D659E15', 0, 'javascript:', 'Rice Rolled in Lettuce', 2, NULL, 0, 'Rice Rolled in Lettuce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1459', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound sticky rice<BR>1 teaspoon salt<BR>1/2 teaspoon ground pepper<BR>1 Tablespoon vegetable oil<BR>1 scallion, cut in half-inch pieces<BR>5 pieces of lettuce<BR>1/2 teaspoon vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Cook the sticky rice.<BR>2. Heat a wok or fry pan and then add the vegetable oil. Add the cooked rice and stir-fry it for three minutes before adding the salt and pepper. Continue to stir-fry it another two minutes, then add the chopped scallion pieces. Roll the rice into cakes and put them in a plate.<BR>3. Blanch the lettuce for half a minute, drain and put it into cold water, drain again and set aside.<BR>4. Fill each piece of lettuce in the center with one rice cake and wrap it up. 5. Oil a clean heat-proof dish and place the cakes on it. Steam this by putting it over rapidly boiling water for five minutes. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'06F3D3C41E16A94E95596AAB4E129172', X'C44756221A9D894B8F7F02632D630C2D', X'C44756221A9D894B8F7F02632D630C2D', 0, 'javascript:', 'Rice-stuffed Lotus Stems', 2, NULL, 0, 'Rice-stuffed Lotus Stems', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=970', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 sections fresh lotus stems, ends removed and peeled<BR>1/4 cup glutinous rice, soaked in one cup of water overnight<BR>2 Tablespoons sugar<BR>1 teaspoon rose water (optional)<BR>1/2 cup cooked (for forty-five minutes) or canned lotus seeds<BR><B><I>Preparation:</B></I><BR>1. Rinse lotus stems, then mix drained rice and the sugar, and stuff this into all the holes. Gently push the rice in with a chop stick, but do not pack it too tightly.<BR>2. Steam the stuffed lotus stems over simmering water for ninety minutes, remove, and cool.<BR>3. Mix prepared or canned lotus seeds with the rose water and let sit for half an hour.<BR>4. Dice lotus stems into one-quarter-inch pieces, and put them into a bowl. Add lotus seeds and rose water, stir once or twice, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A7E42277D0501742B2484DD5B4907566', X'9DB62C4DF9BBD748BA014B2B2823335E', X'9DB62C4DF9BBD748BA014B2B2823335E', 0, 'javascript:', 'Rojak', 2, NULL, 0, 'Rojak', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1718', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound spinach, blanched for one minute, then cooled quickly in ice water, and drained well<BR>1/2 pound bean sprouts, heads and tails removed<BR>1/2 pound canned pineapple, thinly sliced<BR>1 green papaya, peeled and thinly sliced<BR>1/2 pound <I>doufu</I>, sliced and deep-fried<BR>1 cucumber, partially peeked and cubed<BR>1/2 cup roasted peanuts, paper shells removed and discarded, then deep fried until light tan, and drained<BR>1 Tablespoon sesame seeds<BR>2 ounces tamarind, soaked in hot water, squeezed, and only the juice retained<BR>1 teaspoon dried hot pepper pieces<BR>3 Tablespoons granulated sugar<BR>1 Tablespoon brown sugar<BR><B><I>Preparation:</B></I><BR>1. Put all the solid ingredients in a large bowl and toss well.<BR>2. Put the tamarind water, the dried pepper pieces, and the two sugars together and mix until the sugars are dissolved, then toss this with the vegetables and fruits. Chill, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'847F822ABA98CE4698AC93D598E50D76', X'941721B2F8AA454BAA1CA5C7A9EC48B0', X'941721B2F8AA454BAA1CA5C7A9EC48B0', 0, 'javascript:', 'Rolled Stuffed Dry Bean Curd', 2, NULL, 0, 'Rolled Stuffed Dry Bean Curd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1332', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 sheets dry bean curd (each 20 by 10 inches)<BR>1/2 cup dark soy sauce<BR>1/2 slab Chinese brown sugar<BR>1/2 cup vegetable oil<BR>1 cup chrysanthemum petals<BR>about one yard of string<BR><B><I>Preparation:</B></I><BR>1. Soak bean curd sheets in warm water for twenty minutes, then drain and set aside in a large deep bowl.<BR>2. Bring soy sauce, sugar, and vegetable oil to the boil, and pour over the bean curd sheets, and let rest for half an hour or until they can be handled.<BR>3. Remove the bean curd sheets from the bowl and sprinkle chrysanthemum petals evenly over each one, then roll them up one by one, but not too tightly. Tie them at the ends and once or twice in the middle, then return the rolls to the bowl, edge down, into the soy sauce mixture and steam over boiling water for one hour.<BR>4. Remove the rolls, discard the liquid in the bowl, cut the strings and discard, and thinly slice the rolls and serve hot, warm, or cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8CF478873017AE4CB5E0A62CE2551D06', X'21AF51304A03BF4780C0C1E8BC9D2DE0', X'21AF51304A03BF4780C0C1E8BC9D2DE0', 0, 'javascript:', 'Royal Fern with Pork', 2, NULL, 0, 'Royal Fern with Pork', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1900', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound raw bacon<BR>1/2 pound royal fern or any other fern, if dried, then soaked for ten minutes in hot water, then drained and slivered<BR>1 Tablespoon lard<BR>2 large shallots, peeled and minced<BR>3 sliced fresh ginger, minced<BR>1/4 teaspoon ground cinnamon<BR>1/2 teaspoon ground five-spice powder<BR><B><I>Preparation:</B></I><BR>1. Slice the fresh bacon into one-quarter inch pieces, and put it on the lard, with the meat on it, all on a heat-proof deep plate.<BR>2. Spread the greens over the meat, and then sprinkle the shallots, ginger, cinnamon, and the five-spice powder over all of it.<BR>3. Put this in a steamer, and cover the steamer for half an hour over boiling water. Then uncover, and serve, thickening the liquid, if desired with a cornstarch-water mixture.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7650157C07921E40903BB90B54FED733', X'8070A80B17C09E409D2F73CE7C5AFF2C', X'8070A80B17C09E409D2F73CE7C5AFF2C', 0, 'javascript:', 'Safflower, Tofu, Chives, and Vegetarian Sea Bass', 2, NULL, 0, 'Safflower, Tofu, Chives, and Vegetarian Sea Bass', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1561', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon to 1/2 cup safflower flowers<BR>8 ounces vegetarian ''sea bass'' steaks; six pieces<BR>6 ounces silken tofu, cut into large cubes<BR>2 ounces garlic chives, cut into one-inch pieces<BR>1 Tablespoon fresh ginger juice<BR>2 Tablespoons cooking wine<BR>1 Tablespoon soy sauce<BR>2 Tablespoons cornstarch<BR>5 ounces <I>jiu zao</I> which is the residue of fermented sweet rice wine, optional<BR>1 Tablespoon vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Place flowers of safflower in a strainer and rinse with cold water, then soak them in a cup of warm water.<BR>2. Blot liquid off mock fish steaks using paper towels.<BR>3. In a flat fry pan, heat oil and pan-fry the mock fish filets until golden then turn over and fry until golden on the other side.<BR>4. Season the filets with ginger juice, wine, and soy sauce. Then add the tofu, <I>jiu zao</I>, if using this ingredient, and the soaking liquid and safflowers, and bring to the boil, then reduce the heat and cook covered for ten minutes.<BR>5. Add garlic chives, re-cover the mixture and let rest for two to three minutes, before serving in a pre-heated deep dish.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0D10FC72BC138049ABC7BFC03BE21902', X'61749027D2575D4095FD9D09175537D5', X'61749027D2575D4095FD9D09175537D5', 0, 'javascript:', 'Salted Taro', 2, NULL, 0, 'Salted Taro', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=582', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds large taro<BR>2 Tablespoons glutinous rice flour<BR>2 Tablespoons rice flour<BR>1 teaspoon alt<BR>1/4 teaspoon pepper<BR>4 cups oil<BR><B><I>Preparation:</B></I><BR>1. Pare the taro, wash, and wipe it dry. Shred it in long thin strips. Place them on a plate.<BR>2. Mix both flours and salt and peppper. Sprinkle this flour mixture evenly over the shredded taro. Form this into flattened balls.<BR>3. Heat pan, add the oil, and when hot, add a few balls at a time and fry until borwn and crips. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7F3E535A460F8D43AA2E9370F534D96C', X'C8DCA9C9F508204AB0BC497EB0B1026A', X'C8DCA9C9F508204AB0BC497EB0B1026A', 0, 'javascript:', 'Sauteed Dou Miao with Garlic', 2, NULL, 0, 'Sauteed Dou Miao with Garlic', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=328', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1/4 cup vegetable oil<BR>6 to 8 large garlic cloves, crushed and peeled<BR>1 pound dou miao, preferably large<BR>3/4 teaspoon salt<BR>1/4 cup water<BR><B><I>Preparation:</B></I><BR>1. In a large saute pan or wok, heat oil on high. When it ripples, add the garlic and toss quickly until fragrant. Then, add the dou miao and salt, alternately and in batches. Letting each batch of dou miao wilt slightly before adding more.<BR>2. When the moisture from the leaves evaporates, add the water. Toss just until all the leaves are wilted.<BR>3. Remove immediately from heat.<BR>Serves 6.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2596D344452FEB4D8350BFBE9F0765C4', X'5DD2C5A879C44F43AFB74176E340AEF9', X'5DD2C5A879C44F43AFB74176E340AEF9', 0, 'javascript:', 'Sauteed Mixed Mushrooms', 2, NULL, 0, 'Sauteed Mixed Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1061', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon sesame oil<BR>1 Tablespoon soy sauce<BR>2 Tablespoons oyster sauce<BR>1/4 cup plus one tablespoon Shao Xing wine<BR>1/2 cup plus two tablespoons peanut oil<BR>1/2 pound king-oyster mushrooms (<I>Pleurotus eryngli</I>), cut into one-eighth inch slices<BR>1/2 pound fresh Chinese black mushrooms, cut into one-eighth inch slices<BR>1/2 pound oyster mushrooms, quartered<BR>1/2 pound cremini mushrooms, stems removed and caps cut in half<BR>4 cloves garlic, peeled and thinly sliced<BR><B><I>Preparation:</B></I> <BR>1. Whisk sesame oil, soy sauce, oyster sauce, and one tablespoon of the wine in a small bowl.<BR>2. Heat wok or fry pan, then add two tablespoons peanut oil., stir and add king-oyster mushrooms and stir and toss them until they release their moisture and begin to brown; then transfer them to a large bowl.<BR>3. Repeat step two with each kind of mushrooms, one by one.<BR>4. Add the last two tablespoons of peanut oil, then add garlic and stir-fry until golden. Then add all the mushrooms and their juices and the remaining quarter cup of wine. Stir-fry about twenty seconds, then pour in the sauce made in step number one. Toss well, then immediately remove form the heat, put out on a platter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A331559975A8A74BBDED7BA86C7458E2', X'F620EE576AC2BF4897E3DD8C1F14C374', X'F620EE576AC2BF4897E3DD8C1F14C374', 0, 'javascript:', 'Sayur Lodeh or Vegetable Stew', 2, NULL, 0, 'Sayur Lodeh or Vegetable Stew', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=74', '<TD><RECIPE><B><I>Ingredients</I></B><BR>1 medium-sized eggplant<BR>2 Tablespoons salt<BR>1/4 pound very young string beans<BR>1/4 pound bamboo shoots, sliced thin<BR>3 scallions minced fine<BR>1 teaspoon <I>trassi</I><BR>4 canlenuts or macadamia nuts<BR>3 cloves garlic, minced fine<BR>1 chili pepper, minced<BR>6 shallots or 1 small onion, minced<BR>1 cup stock or water<BR>1 Tablespoon dry shrimp (optional)<BR>1/4 teaspoon brown sugar<BR>1 and 1/2 teaspoons ground ginger<BR>2 bay leaves<BR>2 to 3 Tablespoons greens<BR>2 cups thick coconut milk<BR><B><I>Preparation</I></B><BR>1. Slice eggplant and sprinkle with salt. Let stand for half an hour. Then rinse well, and dry with paper towels.<BR>2. Mix the bamboo shoots, scallions, chili, and shall, and the stock and simmer for five minutes. Then, add shrimp, if used, spices, and sugar, and cook for five more minutes.<BR>3. Add beans and coconut milk and simmer gently, stirring continuously until hot. Remove from the heat and discard the bay leaves and greens. Serve hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B425FD3BE0ECF7428556968328D8CA7C', X'6CA6C70C0D2B9E48B8DCBEB36E5457B8', X'6CA6C70C0D2B9E48B8DCBEB36E5457B8', 0, 'javascript:', 'Shoe-string Potatoes, Chinese Style', 2, NULL, 0, 'Shoe-string Potatoes, Chinese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1092', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 and 1/2 pounds potatoes<BR>1/4 to 1/2 cup peanut oil<BR>1/2 or 1 whole star anise<BR>3 Tablespoons white vinegar<BR>1 teaspoon coarse salt 1/2 teaspoon chicken accent, MSG, or chicken bouillon<BR>1/2 teaspoon sugar<BR>2 ounces shredded Jinan or Virginia ham<BR>1 green hot pepper, minced, optional<BR>1 teaspoon hot red oil, optional<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Peel the potatoes, then shred them with a Benriner (see note below); it cuts them into very thin sticks.<BR>2. Soak these strings in cold water for one to two hours to reduce the starch in/on the potato strings; then drain, and the potatoes are ready for use.<BR>3. Heat peanut oil in a wok or deep pan using medium heat. Add the star anise and stir until it turns black. Remove it with a slotted spoon and discard. Then, add the potato strings, and immediately add vinegar, salt, chicken accent or a substitute, sugar, and shredded ham and stir this mixture vigorously.<BR>4. Add the sesame oil just before serving, and do serve them hot, warm, or cold. Keep in mind that the total cooking time should not be more than three minutes, not a minute longer.<BR><B>Note:</B> A Benriner is a Japanese tool used to thin slice or shred items. It is often used for the preparation of sushi. It is equipped with one or more horizontal blades for slicing and a medium- or large-toothed diagonal blade that slices and sheds at the same time. The medium tooth blade has about two dozen teeth and it is only a mite wider than two inches. This blade shreds the potato into very thin strips, sometimes called shoe-strings.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4C79E11A5C706644AB3450854808B24F', X'F4C8F327F9B1864E8F0D7404228BABF8', X'F4C8F327F9B1864E8F0D7404228BABF8', 0, 'javascript:', 'Shredded Sour Potatoes', 2, NULL, 0, 'Shredded Sour Potatoes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=941', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large potato, shredded then soaked in ice water<BR>1 scallion, green part only, shredded<BR>1 chili pepper, seeds removed, and shredded<BR>2 Tablespoons red Chinese rice vinegar<BR>1/2 teaspoon salt<BR>1 Tablespoon white Chinese rice wine<BR>1 Tablespoon vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Drop potatoes in three cups of boiling water, and after one minute, drain and put into a bowl.<BR>2. Mix scallion and chili pepper shreds with the potatoes, then add the vinegar, salt, and rice wine and stir well.<BR>3. Heat wok or fry pan, add oil, and then add potato mixture, being careful as it will splatter. Stir-fry for one minute, remove to a serving brown and serve hot, warm, or at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8FD75E1BDF55CA40B0F519B83B511E5B', X'3D1F2C612E12FA4A84B89E1D293A6A76', X'3D1F2C612E12FA4A84B89E1D293A6A76', 0, 'javascript:', 'Shrimp-sauced Spinach, Shanghai Style', 2, NULL, 0, 'Shrimp-sauced Spinach, Shanghai Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1532', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound spinach <BR>1/10 pound bamboo shoots<BR>1 teaspoon lard<BR>1/2 teaspoon shrimp sauce<BR>1/4 teaspoon salt<BR>1/4 teaspoon monosodium glutamate (optional)<BR><B><I>Preparation:</B></I><BR>1. Wash spinach and cut into one and a half- inch pieces. Also, cut bamboo shoots into small pieces.<BR>2. Use a wok or fry pan, add lard, and just as it begins to smoke, add spinach and bamboo shoots. If the juice of the spinach is not enough, add some soup-stock.<BR>3. Stir-fry until spinach is deep green and shiny, add the shrimp sauce, salt, and monosodium glutamate, if using it; then serve. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'39A3F2F2DCB99A468F40E55941CB8468', X'22F0086E7FD4664F9811D2FA26014465', X'22F0086E7FD4664F9811D2FA26014465', 0, 'javascript:', 'Sichuan Cucumber Salad', 2, NULL, 0, 'Sichuan Cucumber Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1746', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large cucumber, do not peel, but scrub it well<BR>1 teaspoon salt<BR>1 teaspoon peanut or vegetable oil<BR>1 Tablespoon peeled then minced garlic<BR>2 teaspoons cider vinegar<BR>1 teaspoon soy sauce<BR>1 teaspoon sesame oil<BR>1 teaspoon sugar<BR>1/4 teaspoon crushed red pepper flakes<BR><B><I>Preparation:</B></I><BR>1. Cut cucumber into quarters lengthwise, then scoop out any large seeds before cutting each into pieces about an inch and a half long.<BR>2. Pound these pieces lightly with the side of the cleaver to slightly smash them.<BR>3. In a large bowl, toss the cucumbers with salt and let them rest for twenty minutes, then discard any liquid in the bowl.<BR>4. Heat the peanut oil in a small skillet or wok, add the garlic and stir about half a minute before transferring the garlic to a small bowl. Mix in the cider vinegar, soy sauce, sesame oil, sugar, and the red pepper flakes and stir well.<BR>5. Drain the cucumber pieces well. Then toss them with the cider vinegar mixture. Serve at room temperature or chill for a couple of hours before serving them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'49653DAD6C898F459F721C3ACA1BEA24', X'6C0B6B3964A2544D93A3D7107F63DDE0', X'6C0B6B3964A2544D93A3D7107F63DDE0', 0, 'javascript:', 'Sichuan Pickled Vegetables', 2, NULL, 0, 'Sichuan Pickled Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1403', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons coarse salt<BR>3 Tablespoons Sichuan peppercorns<BR>2 red chili peppers, cut in quarters the long way, seeded, their membranes removed<BR>1 small knob of fresh ginger, peeled, and cut into long very thin strips<BR>2 Tablespoons Mao tai or any Chinese liquor<BR>3 Tablespoons Chinese rice wine<BR>6 carrots, peeled, then cut into two-inch by half-inch strips<BR>3 Chinese thin cucumbers (do not peel them), cut into two-inch by half-inch strips<BR>1 small Chinese white turnip, peeled, then cut into two-inch by half-inch strips<BR>1/2 small head of cabbage, core removed, then cut into two-inch by half-inch strips<BR><B><I>Preparation:</B></I><BR>1. Rinse a crock or very large jar, and dry it with paper towels, then add two quarts of boiling water, the salt, Sichuan peppercorns, chili peppers, and the ginger and allow to cool overnight.<BR>2. Put all the vegetables (as many as fit at one time) into a large strainer basket and pour one quart of boiling water for each strainer basket full, over the cut vegetables, drain well, and then put these into the crock or jar, continuing until all have been rinsed with and then drained from their boiling water.<BR>3. Cover loosely, stir the vegetables every six hours with a large spoon that has been rinsed with boiling water, and the vegetables are ready to eat after eighteen hours. Serve the vegetables drained, and at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C42A4E1D6E1CC9429D8E03A6182CB9B5', X'AD32AF44E9C0E149A9352C1284500E5C', X'AD32AF44E9C0E149A9352C1284500E5C', 0, 'javascript:', 'Silk Road Eggplant', 2, NULL, 0, 'Silk Road Eggplant', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=496', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 to 8 Tablespoons corn oil<BR>2 pounds thin eggplant, peeled and sliced thin<BR>1 teaspoon cumin seeds<BR>1/4 teaspoon ground cumin<BR>1 teaspoon coriander seeds, their pods discarded<BR>1/4 teaspoon ground coriander<BR>1 large quill cinnamon<BR>1/4 teaspoon ground cinnamon<BR>1 large onion, sliced thin<BR>6 cloves garlic, minced<BR>2 tomatoes, peeled and chopped<BR>2 Tablespoons lemon juice<BR>2 Tablespoons pomegranate molasses<BR>1/4 teaspoon ground black pepper<BR>1 Tablespoon sugar<BR>1 teaspoon salt<BR>2 Tablespoons cornstarch or another thickener mixed with two tablespoons water<BR><B><I>Preparation:</B></I><BR>1. Heat two tablespoons oil and fry eggplant in three or four batches until golden brown, then remove and drain.<BR>2. Add both cumins, corianders, both whole and ground cinnamon, onion slices, and garlic and reduce the heat and fry for fifteen minutes, then add the tomatoes, lemon juice, pomegranate molasses, pepper, salt, and sugar, and cover and simmer for fifteen minutes.<BR>3. Remove the cinnamon quill and put the eggplant slices carefully on the top, overlapping them. Starting in circles from the outside to the center. And simmer another ten minutes. Chill covered overnight in the refrigerator.<BR>4. Reheat on low until heated thoroughly, the add the cornstarch mixture and bring to the boil and remove from the heat; do not stir. Let this rest ten minutes then turn it over into a deep bowl so that the eggplant is on the bottom, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'704B979B9CB70D489D612957FEC7EB8B', X'9B4F77BCD2730844845F6DEB2507C598', X'9B4F77BCD2730844845F6DEB2507C598', 0, 'javascript:', 'Silk Squash with Nuts', 2, NULL, 0, 'Silk Squash with Nuts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=509', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 silk squash<BR>2 fresh lily bulbs, petals separated, rinsed, and dried<BR>1/2 cup pine nuts (or olive nuts--these are from their pits, or cooked gingko nuts, these latter two items need be cut in half)<BR>1 teaspoon sesame oil<BR>1/4 teaspoon salt<BR>1/4 teaspoon ground white pepper<BR>1 small green hot pepper, seeded and minced<BR>1 small dried red hot pepper, seeded and minced<BR>1 Tablespoon corn oil<BR>1 slice fresh ginger, minced<BR>1 Tablespoon cornstarch mixed with two tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Cut the silk squash across the narrow way to make four sections. Cut each of these in half the long way and angle cut each strip.<BR>2. Blanch the silk squash, lily petals, and the nuts in boiling water for one minute, then drain.<BR>3. Mix sesame oil, salt and pepper, and red and green hot pepper pieces.<BR>4. Heat corn oil and fry ginger until fragrant, then add hot pepper mixture and fry another minute, being careful not to let them burn.<BR>5. Add silk squash, lily bulb petal pieces and the nuts and stir-fry for three minutes before adding the cornstarch mixture. Bring to the boil and cook until the sauce clears, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'88EED06BF7112E45BDEE817002256892', X'E7C2FDC5EB0C6C42B31E6D6A61AAF258', X'E7C2FDC5EB0C6C42B31E6D6A61AAF258', 0, 'javascript:', 'Silk Squash, Fried Crullers, and Golden Mushrooms', 2, NULL, 0, 'Silk Squash, Fried Crullers, and Golden Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=508', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 silk squash<BR>2 fried crullers, cut into half-inch cubes<BR>1/2 cup corn oil<BR>2 slices fresh ginger, slivered<BR>1/2 cup golden mushrooms<BR>1 Tablespoon rice wine<BR>1 teaspoon sesame oil<BR>1/2 teaspoon sugar<BR>1/4 teaspoon salt<BR>1/2 cup cold chicken broth<BR>1 Tablespoon cornstarch<BR><B><I>Preparation:</B></I><BR>1. Cut the silk squash across the narrow way to make four sections. Cut each of these in half the long way and angle cut each strip.<BR>2. Heat oil. Fry cruller cubes until crisp, then remove and drain them. Reserve one tablespoon of the oil.<BR>3. Heat reserved oil and fry the ginger half a minute. Then add the silk squash and one cup of water. Simmer this for five minutes.<BR>4. Add the crullers, mushrooms, rice wine, sesame oil, sugar and salt and simmer another two minutes.<BR>5. Mix chicken broth and cornstarch, bring the silk squash mixture to the boil and add it. Cook until the liquid clears or is totally absorbed, about one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8E19BB67966E6B49B7AFB11A497EA4AF', X'B857E505BA743248BE15C57985047F39', X'B857E505BA743248BE15C57985047F39', 0, 'javascript:', 'Silk Squash, Sichuan Style', 2, NULL, 0, 'Silk Squash, Sichuan Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=506', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>24 small clams, covered in warm water to rest for half an hour<BR>2 silk squash, peeled<BR>2 small dried chili peppers, seeded and minced fine<BR>1/4 teaspoon Sichuan pepper<BR>1 to 2 cloves garlic, minced<BR>1 Tablespoon rice vinegar<BR>1/2 teaspoon salt<BR>1 Tablespoon sugar<BR><B><I>Preparation:</B></I><BR>1. Put the clams in a bowl and the bowl in a steamer. Steam them until they open then remove them from their shells setting aside 1/4 cup clam juice. Rinse the clams with boiling water to be sure any remaining sand is removed.<BR>2. Cut the silk squash across the narrow way to make four sections about four to six inches in length. Cut each of these pieces in half the long way, then angle cut each strip and put them into another bowl.<BR>3. Steam the silk squash for eight minutes, then drain.<BR>4. Mix all ingredients including the clam juice and refrigerate for at least two but not more than four hours. Then, stir and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'549410EFB8EA78498A4F3FDEE7FB83AA', X'A273AACFC33DCF4289BE38FA12EACCF2', X'A273AACFC33DCF4289BE38FA12EACCF2', 0, 'javascript:', 'Simple Cauliflower, Bai Style', 2, NULL, 0, 'Simple Cauliflower, Bai Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1678', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small head of cauliflower<BR>2 Tablespoons coarse salt<BR>3 Tablespoons vegetable oil<BR>6 cloves garlic, peeled and smashed with the side of a cleaver, then divided into two batches<BR><B><I>Preparation:</B></I><BR>1. Discard, leaves, and the core from the cauliflower and divide it into florets.<BR>2. Bring four cups of water to the boil in a large pot or a wok. Add one tablespoon of the salt and the cauliflower florets and boil for two minutes, then drain and rinse with cold water to stop the cooking. Discard the hot water and with a paper towel, dry the wok or pot, and add the oil and turn the heat to high.<BR>3. Add the garlic and stir-fry for one to two minutes until the garlic browns, but does not burn. Set half the browned garlic aside and add the cauliflower and stir-fry for one minute before adding the remaining salt, stir-frying for half a minute, then put the cauliflower into a pre-heated serving dish.<BR>4. Top with the rest of the browned garlic, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F9E5866A34D0C342BE064A2DE49BEB1A', X'EC8F04C2FABDD84B957F6A6174D5BEBB', X'EC8F04C2FABDD84B957F6A6174D5BEBB', 0, 'javascript:', 'Simple Celtuce', 2, NULL, 0, 'Simple Celtuce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1870', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 celtuce stems<BR>2 Tablespoons vegetable oil<BR>1 teaspoon coarse salt<BR>1 teaspoon thin soy sauce<BR>1/4 teaspoon hot oil<BR>2 cloves fresh garlic, peeled and minced<BR>1/4 carrot or daikon, or both, peeled and cut into thin strips<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Peel thick covering on the stems of the celtuce, and then angle cut them into five-inch pieces, slices, or strips.<BR>2. Heat wok or fry pan and then add the vegetable oil, and when almost smoking, add the celtuce pieces and stir-fry them for two to three minutes before adding the salt and soy sauce. The add the garlic and stir-fry another minute, then add the carrot and/or daikon and stir-fry one or two minutes more.<BR>3. Mix in the sesame oil. Serve in a pre-heated bowl.<BR>4, If using the leaves, cut into one-inch wide pieces and add them after step two. Stir-fry another minute, no longer.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'12FCE15E6EE412418D77A824F73A9990', X'004F3D73FC6DD143848BEA6F3E52A0F0', X'004F3D73FC6DD143848BEA6F3E52A0F0', 0, 'javascript:', 'Smashed Cucumbers--Pai Huanggua', 2, NULL, 0, 'Smashed Cucumbers--Pai Huanggua', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1185', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cucumbers<BR>4 cloves garlic, minced<BR>1 Tablespoon sesame oil<BR>2 teaspoons Chinese black vinegar<BR>1/4 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Slice the cucumbers crosswise into two-inch pieces. Using the flat side of a cleaver, whack each piece once or twice so it breaks into smaller pieces.<BR>2. Transfer all the pieces into a bowl and toss them with the garlic, sesame oil, vinegar, and the salt. Marinate them for at least fifteen minutes or up to several hours. Then drain and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'441E38DC28BD0F46853E84924B0CD1A4', X'F67B7F6105D5F24C8D1539CEC409C2AE', X'F67B7F6105D5F24C8D1539CEC409C2AE', 0, 'javascript:', 'Snow Peas and Pine Mushrooms', 2, NULL, 0, 'Snow Peas and Pine Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1486', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound pine mushrooms<BR>1 teaspoon salt<BR>1 Tablespoon Chinese Swatow rice wine<BR>2 slices fresh ginger, smashed with side of a cleaver<BR>1/2 teaspoon sesame oil<BR>1 pound romaine lettuce, sliced into two inch pieces<BR>2 Tablespoons vegetable oil, separated into two parts<BR>10 quail eggs<BR>2 scallions, cut into one-inch pieces<BR>2 Tablespoons oyster sauce<BR>6 cups chicken stock or water<BR>2 Tablespoons cornstarch mixed with one tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Rinse mushrooms in two cups cold water mixed with the salt, and after ten minutes, discard the water and add rice wine, ginger, and the sesame oil, stir gently and let rest for ten minutes.<BR>2. Bring four cups cold water to the boil, add one half the vegetable oil and the lettuce, stir then drain and set lettuce aside.<BR>3. Bring one cup water to the boil, add the quail eggs, reduce heat and simmer for ten minutes. Remove the eggs, discard the water, and when cool enough to handle, peel the eggs carefully not tearing the white part. Then cut them in half and set aside.<BR>4. Heat other half of the vegetable oil, and stir fry the scallions, then add the mushroom mixture and stir-fry for one minute before adding the oyster sauce and then six cups chicken broth or water, and then the cornstarch mixture, stirring until the broth or water clears before adding the lettuce and the quail eggs, simmer for one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B4B0C758D8C4CB4891C83570D734BC5B', X'F359EEE50897D840AFDEB1CBAF546C21', X'F359EEE50897D840AFDEB1CBAF546C21', 0, 'javascript:', 'Sour Chinese Cabbage, Bai Style', 2, NULL, 0, 'Sour Chinese Cabbage, Bai Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1641', '<TD><RECIPE><I><B>Ingredients: </B></I><BR>1 medium-size head of cabbage, napa preferred<BR>3 Tablespoons vegetable oil<BR>6 cloves garlic, peeled; slice three and mash the other three cloves<BR>3 Tablespoons Chinese black vinegar<BR>1/2 teaspoon coarse salt<BR>2 Tablespoons cornstarch mixed with one tablespoon cold water or the soy-type mushroom sauce<BR><B><I>Preparation:</B></I><BR>1. Cut the cabbage in half the long way, then slice crosswise into very thin strips.<BR>2. Heat wok or fr pan, add the oil and stir-fry the garlic slices for half minute, then add the cabbage and stir-fry for two minutes, then add the vinegar and the salt, and the mashed garlic and stir-fry another minute.<BR>3. Stir in the well-mixed cornstarch/water mix and stir until it thickens and cats the cabbage. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'230A00EC3DD6FE4AB0DD358D0D2F1430', X'24F3BB0415F59C41ACE61DB28E55AD63', X'24F3BB0415F59C41ACE61DB28E55AD63', 0, 'javascript:', 'Sour Guizhou Vegetables', 2, NULL, 0, 'Sour Guizhou Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1726', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small red pepper, seeded and cut in thin strips<BR>1 medium green pepper, seeded and cut in thin strips<BR>1 large onion, cut in wedges, top to bottom<BR>1 cup mustard strips green, blanched for one minute, then cut in half-inch by two-inch strips<BR>2 Tablespoons vegetable oil<BR>1/3 cup Chinese brown slab sugar<BR>1/3 cup Chinese black vinegar<BR>2 Tablespoons cornstarch mixed with the same amount of cold water<BR><B><I>Preparation:</B></I><BR>1. Bring two quarts water to the boil, and add the peppers, onion, and mustard green pieces, and boil them for four minutes, drain and set them aside.<BR>2. Heat wok or heavy fry pan, add the oil and the drained vegetables. Stir-fry for one to two minutes until they are heated through.<BR>3. Add the brown sugar and the vinegar, stir for half minute, then add the cornstarch mixture, and stir until vegetables are coated, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'38B9D767012D1343AAD5B7FCB4C6A5C2', X'AE70E03F1156E84991F6A6593AFC99FA', X'AE70E03F1156E84991F6A6593AFC99FA', 0, 'javascript:', 'Spiced Cabbage', 2, NULL, 0, 'Spiced Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=780', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large Chinese celery cabbage (about one pound), cut into two-inch pieces, or one-inch slices<BR>1 Tablespoon coarse salt<BR>2 Tablespoons peanut oil<BR>4 slices fresh ginger, slivered<BR>1 Tablespoon mustard seeds<BR>2 Tablespoons sugar<BR>1 teaspoon chili oil, optional<BR><B><I>Preparation:</B></I><BR>1. Mix cabbage and salt and set aside for six hours, then rinse and spin or towel dry.<BR>2. Heat oil, stir-fry the ginger and mustard seeds for one minute, and toss with the cabbage and the sugar. Set aside for two hours, and do not refrigerate.<BR>3. Just before serving, toss with chili oil, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'412C35EA5CFD36479E7C016BE122E5B3', X'C1495E1D65D564469A46477462A74537', X'C1495E1D65D564469A46477462A74537', 0, 'javascript:', 'Spiced Vegetarian Meatballs', 2, NULL, 0, 'Spiced Vegetarian Meatballs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=561', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 cups quick-cooking oatmeal<BR>1 can cream of mushroom soup or a cup and a half of cooled thick white sauce mixed with two tablespoons diced white mushrooms<BR>4 cloves garlic, minced<BR>1 onion, minced<BR>1 cup chopped Chinese olive pits, pine nuts, or walnuts<BR>3 eggs, beaten<BR>1 envelope chicken or ham bouillon powder<BR>1 cup corn oil<BR>1 green pepper, seeded and cut into one-inch pieces<BR>1 red pepper, seeded and cut into one-inch pieces<BR>2 cucumbers, seeded and cut into one-inch pieces<BR>1 cup canned pineapple chunks, drained (optional)<BR>1 can baby corn, drained and cut into one-inch pieces<BR>2 Tablespoons chili paste with garlic<BR>2 Tablespoons vegetarian oyster sauce<BR>1 Tablespoon sugar<BR>1 tomato, peeled and seeded, then diced into half-inch pieces<BR>4 Tablespoons cornstarch mixed with six tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Mix oatmeal, mushroom soup or the white sauce and mushroom mixture, garlic, onion, nuts, eggs, and bouillon powder. Let stand for half an hour, then shape into one-inch balls.<BR>2. Heat oil and fry the oatmeal balls in three or four batches. Drain them on paper towels, reserving one tablespoon of the oil.<BR>3. Heat the reserved oil and fry the peppers, cucumbers, pineapple, and the corn until heated through, then return the oatmeal balls to the wok or pan. 4. Add the rest of the ingredients and boil until thickened. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BB0374856182D3479143DE00F410FD1A', X'6D274E2ED6788648A0628B66440402FF', X'6D274E2ED6788648A0628B66440402FF', 0, 'javascript:', 'Spicy Bean Curd', 2, NULL, 0, 'Spicy Bean Curd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=931', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 firm bean curd (one pound)<BR>1 teaspoon spicy salt (salt mixed with ground Sichuan pepper)<BR>4 ounces self-raising flour<BR>2 cups vegetable oil<BR>1 scallion, minced fine<BR>1 chili pepper, seeded and minced fine<BR>1/2 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Quarter the bean curd and toss each piece with the spicy salt.<BR>2. Make a batter with the flour and seven ounces of ice water. Mix gently until well blended and set aside for twenty minutes.<BR>3. Heat oil then drain about half the bean curd and toss them with half the scallion and chili pieces. Quickly dip them into the batter, let excess drain off, and deep-fry them in hot oil until golden brown stirring often. Drain on paper towels and set them on a pre-heated plate. Repeat with the remaining ingredients. Serve.<BR><BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BC35CD48B7D4C94A8482AAF02A554C1D', X'6B2ECADDB6FFC24C8917A27054BFEEF9', X'6B2ECADDB6FFC24C8917A27054BFEEF9', 0, 'javascript:', 'Spicy Cabbage, Genghis Khan (Baistsaa)', 2, NULL, 0, 'Spicy Cabbage, Genghis Khan (Baistsaa)', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=385', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 cups diced red cabbage<BR>1 and 1/2 teaspoons salt<BR>1 cup diced onions<BR>3 Tablespoons vegetable oil<BR>1 cup sliced mushrooms<BR>1/4 cup sliced green bell peppers<BR>1/4 cup sliced yellow bell peppers<BR>1/2 teaspoon Chinese five-spices powder<BR>2/3 cup water, or vegetable broth<BR>1 Tablespoon cornstarch<BR>1 Tablespoon mushroom soy sauce<BR>1 Tablespoon crushed red chili peppers, or to taste<BR><B><I>Preparation:</B></I><BR>1.Spread the diced cabbage on a tray and sprinkle with salt. And force the salt into the cabbage by running a rolling pin over it. Let stand for forty minutes.<BR>2.Meanwhile, sauté the onions in the oil until they become transparent, then stir in the mushrooms, cabbage, and bell peppers, and add the five-spice powder and stir.<BR>3. When the cabbage begins to soften, mix the water with cornstarch and soy sauce. Stir it into the cabbage with the chili pepper. Gently stir until the mixture thickens and serve with the latter two dishes.<BR><B>Note:</B> The dish has fallen into obscurity in Mongolia but is still enjoyed in Tibet. The recipe works best when made with red cabbage, and does make a delightful side dish with lamb dishes and <I>dresil</I> to create a harmonious well-balanced meal. It is interesting to note that it was introduced to Tibet by Mongolian warlords who were conquered by the peaceful ways of Tibetan Buddhism, which was itself, introduced into Mongolia by Altan Khan in the sixteenth century.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8096639B67D22D4DA9CADCF76AC27F46', X'A4CDF1594C2ECF419C024BC5BCE9CD01', X'A4CDF1594C2ECF419C024BC5BCE9CD01', 0, 'javascript:', 'Spicy Pickled Cucumbers, Carrots, and Daikon', 2, NULL, 0, 'Spicy Pickled Cucumbers, Carrots, and Daikon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=232', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 medium cucumber, peeled with seeds removed<BR>2 medium carrots, peeled<BR>1 daikon radish (about one and a half pounds), peeled<BR>1 Tablespoon coarse salt<BR>10 slices fresh ginger<BR>1/2 teaspoon hot red pepper flakes<BR>1/4 cup sugar<BR>1/2 cup white rice vinegar <BR><I><B>Preparation:</I></B><BR>1. Cut vegetables into bite size pieces then toss them with salt. Set aside for one hour, stirring occasionally.<BR>2. In a small stainless steel saucepan, combine ginger, pepper flakes, sugar, and vinegar. Bring to a low boil to dissolve the sugar, about five minutes, then let cool completely.<BR>3. Place vegetables in a colander and rinse thoroughly with water. Press vegetables gently with a towel to squeeze out any excess moisture. Then add the vegetables to the above pickling liquid and refrigerate for at least one hour.<BR><B>Note:</B>It can keep in the refrigerator for up to a week.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'489755B0FF1A404DA6D1B2F2D56D18DB', X'E385B66CC0080C42B79F39380B883740', X'E385B66CC0080C42B79F39380B883740', 0, 'javascript:', 'Spicy Tofu, Sichuan Style', 2, NULL, 0, 'Spicy Tofu, Sichuan Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1554', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons sesame oil<BR>1 Tablespoon grated fresh ginger<BR>2 cloves garlic, crushed<BR>4 Tablespoons finely chopped scallions<BR>2 Tablespoons Sichuan hot bean paste<BR>2 pounds extra firm tofu, cut into one-inch cubes<BR>2 Tablespoons soy sauce<BR>1 teaspoon sugar<BR>1/2 teaspoon salt<BR>1 Tablespoon cornstarch mixed with three tablespoons of cold water<BR>1/4 teaspoon cayenne<BR><B><I>Preparation:</B></I><BR>1. Heat oil in wok or fry pan, then add ginger, garlic and two tablespoons of the scallions and stir-fry for two minutes. <BR>2. Add bean paste and tofu and stir-fry for another minute then add soy sauce, sugar, salt and half-cup of water. Bring this to the boil before adding the cornstarch mixture and the cayenne. Stir-fry for another minute then transfer to a serving platter, garnish with remaining scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FC5BF1B80595EF4888C7AAADBE284D40', X'A5B9778F01E0B64F873B235A0640BDB0', X'A5B9778F01E0B64F873B235A0640BDB0', 0, 'javascript:', 'Spinach Soy Milk Custard', 2, NULL, 0, 'Spinach Soy Milk Custard', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=861', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 eggs<BR>1 cup soy milk<BR>1/2 teaspoon sugar<BR>1 teaspoon salt<BR>dash of ground white pepper<BR>1/2 teaspoon vegetable oil<BR>1/4 cup packed finely chopped spinach<BR>1 egg yolk<BR>1/4 cup verjus (a mix of crab apple, sour grape, and other unripe fruit juices)<BR>1/2 teaspoon sugar<BR>pinch of sea salt<BR>pinch of ground white pepper<BR>4 raw quail eggs<BR>2 teaspoons black caviar<BR><B><I>Preparation:</I></B><BR>1. In a small bowl, whisk eggs and soy milk, then pass mixture through mesh strainer before adding half teaspoon sugar, and teaspoon of salt and dash of ground white pepper. Pour mixture, divided evenly, into four oiled ramekins or custard cups about two and a half inches wide. Then sprinkle a tablespoon of chopped spinach on each one.<BR>2. Place ramekins in steamer over simmering, not boiling water, and steam for twenty to twenty-five minutes. Then remove and cool at room temperature., before covering with plastic wrap and refrigerating.<BR>3. Make a Sabayon sauce by bringing a sauce pan with two to three inches of water to a simmer. Mix egg yolk, verjus, half teaspoon of sugar and the salt and pepper and whisk in a metal bowl. Place this bowl over the simmering water and whisk for five minutes until it is barely warm and very frothy.<BR>4. Remove custards from their molds and put them on chilled plates. Use a melon ball tool or spoon and scoop a quarter-inch deep hollow out of the center of each. Fill with raw quail egg-yolk, and then pour one-quarter of the warm Sabayon sauce over each. Sprinkle with caviar, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F02C4F02C6B31F4CB1C8EE89DCDE0580', X'1ADC0C00ADBA2B47A828EF9974E17195', X'1ADC0C00ADBA2B47A828EF9974E17195', 0, 'javascript:', 'Spinach with Tangy Soy and Sesame dressing', 2, NULL, 0, 'Spinach with Tangy Soy and Sesame dressing', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=739', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cloves garlic, minced<BR>1/2 teaspoon salt<BR>2 Tablespoons soy sauce<BR>1 Tablespoon white vinegar<BR>2 teaspoons chili paste<BR>2 teaspoons sesame oil<BR>2 teaspoons sesame seeds, toasted then crushed while warm<BR>2 teaspoons brown sugar<BR>1 and 1/2 pounds Chinese malabar spinach<BR>1 teaspoon coarse salt<BR><B><I>Preparation:</B></I><BR>1.Crush garlic and salt together and stir in soy sauce, vinegar, chili paste, sesame oil, sesame seeds, and sugar. Mix well and set this dressing aside.<BR>2. Wash spinach and spin or towel dry, then add the leaves to a pot of boiling water and salt. Blanch for no more than one minute, then drain, and rinse with cold water. Mince coarsely, if desired.<BR>3. Mix spinach with dressing and serve six persons.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F4E7428C4DFED445AD155C21ED0019B5', X'8B22500BFFFA7841A7B0089B93D5D7D1', X'8B22500BFFFA7841A7B0089B93D5D7D1', 0, 'javascript:', 'Steamed Eggplant', 2, NULL, 0, 'Steamed Eggplant', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=950', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound thin eggplants, stems removed<BR>2 Tablespoons peanut oil<BR>1/2 onion, cut diced into half-inch pieces<BR>1 Tablespoon sesame seed paste<BR>1/4 cup vegetable stock<BR>3 red chilies, seeded and minced<BR>1 Tablespoon Chinese rice wine or sherry<BR>1 and 1/2 teaspoons sugar<BR>3/4 teaspoon salt<BR>1 teaspoon Chinese white rice wine vinegar<BR>1 Tablespoon sesame oil<BR>3 scallions, green part only, sliced<BR><B><I>Preparation:</B></I><BR>1. Steam eggplant(s) for six minutes or until a chopstick easily pierces the flesh. The remove and put them in a long dish.<BR>2. Using a chopstick, open each of them in half, and move the chopstick top to bottom until many long strips are made.<BR>3. Heat wok or small pan, then add oil and coat the pan. Add onions and cook for three minutes. Then add sesame seed paste, stock, and chili peppers and stir until well mixed and the liquid boils. Add the rice wine, sugar, salt, and sesame oil and mix well, then pour this over the eggplant. Sprinkle with scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2A07D573C5F96445A2396E1383E37BB3', X'4A56EC9154D53C4BB6B242E32635FDBD', X'4A56EC9154D53C4BB6B242E32635FDBD', 0, 'javascript:', 'Steamed Moss Pie', 2, NULL, 0, 'Steamed Moss Pie', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1659', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 bamboo pith mushrooms<BR>1 and 1/2 cups dried black moss, in Chinese, <I>fa cai</I><BR>10 Shanghai cabbage, their lower ends sliced off<BR>1/4 teaspoon salt<BR>1/2 teaspoon sugar<BR>dash ground white pepper<BR>5 black mushrooms, soaked, stems discarded, and sliced in quarter-inch pieces<BR>1 teaspoon finely minced fresh ginger<BR>1 Tablespoon ginger juice<BR>2 Tablespoons Yunnan ham, slivered<BR>10 long scallion pieces<BR>1 Tablespoon oil<BR>1 and 1/2 cups chicken stock<BR>1 teaspoon oyster sauce<BR>1 teaspoon thin soy sauce<BR>2 Tablespoons Chinese rice wine<BR>2 Tablespoons cornstarch mixed with one tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Soak bamboo pith in warm water for ten minutes, then cut both ends off each one, cut them into one-inch squares and blanch in boiling water for one minute, drain and squeeze out excess water.<BR>2. Soak black moss for one hour in cold water, rinse it and drain it well. Then blanch it in boiling water for one minute, drain and squeeze out excess water.<BR>3. Cut cabbages in half the long way, and blanch in boiling water for one minute, then drain and squeeze out any excess water. 4. Make a mixture of mushroom slices, fresh ginger, and ham and divide it into ten batches.<BR>5. Wrap two halves of the cabbage around one-batch of the mushroom mixture and tie each with one piece of scallion.<BR>6. Put some black moss on each package, and place on an oiled plate on a steamer basket and steam over boiling water for twenty minutes, then remove to a serving platter.<BR>7. Heat the stock, oyster and soy sauces and the wine and the cornstarch and water mixture until it thickens, then pour this over the moss pies and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5705ABF2B48E674AB966E0B73DA6AC98', X'03DFCBB418E6474486B3677D6D8F4ED7', X'03DFCBB418E6474486B3677D6D8F4ED7', 0, 'javascript:', 'Stewed Gluten with Vegetables I', 2, NULL, 0, 'Stewed Gluten with Vegetables I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1002', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 squares of wheat gluten, torn into small pieces (about five ounces)<BR>3 Tablespoons vegetable oil<BR>5 Chinese black mushrooms, soaked and coarsely minced<BR>1/2 cup canned bamboo shoots, coarsely minced or sliced<BR>1 Tablespoon soaked and minced cloud ear fungus<BR>25 pieces dried lily buds, soaked, then each one knotted<BR>1/2 cup fresh soy beans, blanched for five minutes<BR>1 scallion, minced coarsely<BR>2 slices fresh ginger, minced<BR>3 Tablespoons dark soy sauce<BR>1 Tablespoon sugar<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix gluten and the oil, and fry until gluten is browned, then drain.<BR>2. Reheat the oil and stir-fry the mushrooms, bamboo shoots, cloud ear fungus, knotted lily buds, soy beans, scallion, and ginger for two minutes, then add soy sauce, sugar, and sesame oil and stir-fry for five to eight minutes more; then serve warm or at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9D3711D259227E43A18BCADC08AD0983', X'383D0F1B05A28E47855E15A91DFE6B0D', X'383D0F1B05A28E47855E15A91DFE6B0D', 0, 'javascript:', 'Stewed Gluten, with Vegetables II', 2, NULL, 0, 'Stewed Gluten, with Vegetables II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1454', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 squares of wheat gluten, torn into small pieces (about five ounces)<BR>3 Tablespoons vegetable oil<BR>5 Chinese black mushrooms, soaked and coarsely minced<BR>1/2 cup canned bamboo shoots, coarsely minced or sliced<BR>1 Tablespoon soaked and minced cloud ear fungus<BR>25 pieces dried lily buds, soaked, then each one knotted<BR>1/2 cup fresh soy beans, blanched for five minutes<BR>1 scallion, minced coarsely<BR>2 slices fresh ginger, minced<BR>3 Tablespoons dark soy sauce<BR>1 Tablespoon sugar<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix gluten and the oil, and fry until gluten is browned, then drain.<BR>2. Reheat the oil and stir-fry the mushrooms, bamboo shoots, cloud ear fungus, knotted lily buds, soy beans, scallion, and ginger for two minutes, then add soy sauce, sugar, and sesame oil and stir-fry for five to eight minutes more. Serve warm or at room temperature.<BR><I>No nutrient analysis</I> because the program we use has none for dried lily buds.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'09CADA23DDC6B34D9B4A6366092FDE29', X'C2B774500C4A5843A85D5F1DCD2ECB22', X'C2B774500C4A5843A85D5F1DCD2ECB22', 0, 'javascript:', 'Stir-fired Bagel with Cabbage and Bacon', 2, NULL, 0, 'Stir-fired Bagel with Cabbage and Bacon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1381', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon soy sauce<BR>1 Tablespoon rice vinegar (we used Chinese black vinegar)<BR>2 Tablespoons vegetable oil<BR>1 dried red chili pepper, snipped at one end<BR>2 slices fresh ginger, smashed<BR>1/2 teaspoon Sichuan peppercorns<BR>5 slices bacon, cut cross-wise into half-inch cubes<BR>1 Tablespoon coarsely chopped garlic<BR>6 cups cubed green cabbage (about a pound)<BR>2 large plain bagels, cut into half-inch cubes<BR>1/2 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Combine soy sauce and vinegar in a small bowl.<BR>2. Heat 14-inch flat bottomed wok over high heat until a bead of water vaporized in one to two seconds, then swirl in the oil, and add the chili, ginger, and peppercorns, and stir-fry ten seconds or until aromatic, then add the bacon, reduce the heat to medium, and stir-fry three minutes or until the bacon is slightly crispy on its edges; and then add the garlic and stir-fry for ten seconds until just fragrant.<BR>3. Add the cabbage leaves and stir until they wilt, then swirl in the soy sauce mixture and the bagel cubes and stir-fry for one minute or until well-combined.<BR>4. Cover and reduce the heat to medium-low and cook one minute or until the bagel cubes have softened. Then uncover, sprinkle on the salt, and stir-fry just until well-combined, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'52932882D194AA4EB27A1B664DCAD101', X'B32E8E34243E9A43A4A14D7E8577F3A2', X'B32E8E34243E9A43A4A14D7E8577F3A2', 0, 'javascript:', 'Stir-fried Chinese Cabbage', 2, NULL, 0, 'Stir-fried Chinese Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=919', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound Chinese cabbage<BR>2 Tablespoons peanut oil<BR>1/2 teaspoon salt<BR>1 clove garlic, peeled and minced<BR>1/2 cup chicken broth<BR>2 teaspoons dark soy sauce<BR>1 teaspoon thin soy sauce<BR>1 teaspoon sugar<BR>1/2 to 1 teaspoon fresh ginger, minced<BR>1 teaspoon rice wine or dry sherry<BR>1 and 1/2 teaspoons cornstarch<BR><B><I>Preparation:</B></I><BR>1. Cut leaves and stems into one-inch pieces, keeping white stems separate from the leaf parts.<BR>2. Heat wok or fry pan, add oil and the salt and garlic and stir-fry about fifteen seconds before adding white cabbage parts and stir-frying them for two minutes. Then add leaf parts and stir-fry another minute.<BR>3. While that is cooking, mix stock, both soy sauces, sugar, ginger, rice wine and cornstarch and add to the wok, mix well, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'34762E86F1934C43B08CE872CD197B93', X'655C0F6AC1C7D94DBF1472B63E19213B', X'655C0F6AC1C7D94DBF1472B63E19213B', 0, 'javascript:', 'Stir-fried Chinese Mustard Greens', 2, NULL, 0, 'Stir-fried Chinese Mustard Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=130', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 Tablespoons vegetable oil<BR>6 large cloves garlic, minced<BR>6 slices fresh ginger, minced<BR>1 pound mustard greens, cut in bite-sized pieces<BR>1/2 cup chicken broth<BR>1 teaspoon sugar<BR><I><B>Preparation:</I></B><BR>1. Heat oil in non-stick pan or wok and add garlic and ginger and fry a few seconds.<BR>2. Add mustard greens and continue to fry half a minute then add stock, salt and sugar. Cook on high heat about four or five minutes until the liquid is almost evaporated.<BR><B>Note:</B> This recipe can be served hot or cold.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'350C45DA9CE067479FC8B15514751F1C', X'F0D5E90F2207484EA1469F2F0EDFDC3E', X'F0D5E90F2207484EA1469F2F0EDFDC3E', 0, 'javascript:', 'Stir-fried Festival Gourd', 2, NULL, 0, 'Stir-fried Festival Gourd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=724', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>1 onion, peeled and cut in sections, top to bottom<BR>2 gourds, about a pound each, seeded and sliced, then cut at an angle into half-inch by one-inch pieces<BR>1 Tablespoon soy sauce<BR>5 Sichuan peppercorns, smashed with side of the cleaver<BR>1/2 teaspoon salt<BR>1 sheet <I>fen pi</I> (mung bean sheet), broken into irregular pieces, about an inch by an inch<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat wok, add oil then the onion and stir-fry onion until lightly browned, then add gourd pieces and brown them somewhat.<BR>2. Add soy sauce, Sichuan peppercorns, salt, and one cup water, and stir. Reduce the heat and add the <I>fen pi</I> pieces. Cover and simmer for five minutes, stirring once or twice. If the mixture becomes too dry, add additional one-quater cup of water.<BR>3. Remove the cover, and add the sesame oil, stir, and serve. Makes four servings.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0A07FF2F7ED7AA498F47A0AF83252C54', X'D37BBFC8A69704409E3CF862808A6E19', X'D37BBFC8A69704409E3CF862808A6E19', 0, 'javascript:', 'Stir-fried Konjac and Vegetables', 2, NULL, 0, 'Stir-fried Konjac and Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1151', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>2 cloves garlic, peeled and sliced<BR>1 small onion, shredded or sliced<BR>1 large carrot, peeled and shredded<BR>1 large dried black mushroom, soaked, stem discarded, the cap sliced<BR>4 ounces of konjac, sliced or shredded, as desired<BR>3 ounces bean sprouts, heads and tails removed and discarded is optional<BR>1/4 pound fresh spinach, stems and leaves sliced<BR>1 teaspoon thin soy sauce<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat wok or large fry pan, and add the oil, garlic and onions and stir-fry for one minute or until lightly browned.<BR>2. Add onion, carrot and onion pieces and continue to stir-fry for two minutes, then add the konjac, bean spouts, spinach, soy sauce, and sesame oil and stir-fry one more minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F4267290C4924B46BF2B79F126D453CE', X'80FA8C63B53FF941ADA369A7593FDACC', X'80FA8C63B53FF941ADA369A7593FDACC', 0, 'javascript:', 'Stir-fried Taro', 2, NULL, 0, 'Stir-fried Taro', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1981', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup vegetable oil<BR>1 onion, thinly sliced<BR>1 piece fresh ginger, peeled and shredded<BR>1 pound taro, peeled and shredded<BR>1 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or a fry pan, add the oil and brown the onion and the ginger, then add the taro and salt and stir-fry this for two minutes.<BR>2. Add half cup of cold water, bring this to the boil and reduce the heat. Simmer until the taro is soft, about eight minutes. Serve hot as is or mash before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'02D50BDF59382741975413634041D800', X'4066149D37CC0542A899DB2E8EACD4B0', X'4066149D37CC0542A899DB2E8EACD4B0', 0, 'javascript:', 'Stir-fried Tomatoes with Eggs', 2, NULL, 0, 'Stir-fried Tomatoes with Eggs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=21', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>6 Tablespoons oil<BR>1 Tablespoon chopped scallions, white part only<BR>1 large tomato, cut in cubes<BR>2 teaspoons oyster sauce<BR>salt to taste<BR>1 Tablespoon chopped scallions, green part only<BR>5 large eggs, beaten well<BR><I><B>Preparation:</I></B><BR>1. Heat one tablespoon oil and sizzle scallions until light brown.<BR>2. Add tomato and oyster sauce and a dash of salt. Remove and set aside.<BR>3. Heat the rest of the oil and fry the green scallion parts a few seconds and then pour in the eggs. Let them bubble a few seconds, then stir allowing the egg to set slowly.<BR>4. Add the tomato mixture, mix thoroughly, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A0917AD168AC064A8836897B6574998B', X'8532C3DF50118840814799975AB520E5', X'8532C3DF50118840814799975AB520E5', 0, 'javascript:', 'Stir-fried Wintermelon', 2, NULL, 0, 'Stir-fried Wintermelon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1672', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>1/4 cup almonds, blanched and peeled<BR>1 large carrot, shredded<BR>1 small daikon, peeled and cut into strips<BR>1 small celeriac, peeled and cut into small strips<BR>1 pound winter melon, peeled and cut into small strips<BR>1 cup savoy cabbage, cut into thin strips<BR>1 Tablespoon peeled and minced fresh ginger<BR>1 teaspoon <I>sa cha</I> sauce<BR>1 Tablespoon oyster sauce<BR>1 scallion, slivered<BR>1 teaspoon sesame seeds<BR><B><I>Preparation:</B></I><BR>1. Heat vegetable oil and stir-fry almonds until lightly tanned, then remove them from the oil and set aside on paper towels.<BR>2. Add carrot, daikon, and celeriac strips and stir fry for three minutes, then add winter melon and cabbage strips and stir-fry another one minute more until cabbage is wilted at which time add ginger root and stir fry an additional minute.<BR>3. Add <I>sa cha</I> and oyster sauces and the scallion, and stir-fry half a minute before transferring everything to a pre-heated bowl.<BR>4. Top with sesame seeds, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'91FAD3D8DF5A10488AE4A23F482A6764', X'8DD9F8A4492A6843A39DF51960130088', X'8DD9F8A4492A6843A39DF51960130088', 0, 'javascript:', 'Straw Mushroom Casserole', 2, NULL, 0, 'Straw Mushroom Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1346', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespon vegetable oil<BR>3 Chinese black mushrooms, slaked, stems discarded, and minced<BR>4 cups whole straw mushrroms<BR>2 Tablespoons fermented black beans, rinced and mashed<BR>2 teaspoons sugar<BR>1 Tablespoon mushroom soy sauce<BR>1 Tablespoon thin soy sauce<BR><B><I>Preparation:</B></I><BR>1. Heat oil in a casserole on medium heat, then add Chinese black mushrooms and stir-fry them for three minutes until they no longer sizzle.<BR>2. Add straw mushrooms and themashed black beans, the sugar and obth soy sauces. Reduce heat, cover the casserole, and simmer for five minutes, then stir, and recover and simmer for fifteen minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D91DD8889BBBB2499DDF2F459360370E', X'19948048E5C58849B1F061CA908B0542', X'19948048E5C58849B1F061CA908B0542', 0, 'javascript:', 'Straw Mushrooms Braised with with Chinese Celery', 2, NULL, 0, 'Straw Mushrooms Braised with with Chinese Celery', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=881', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>2 cloves garlic, peeled and minced<BR>1 cup canned whole straw mushrooms, rinsed<BR>1 Tablespoon fermented black beans, rinsed and lightly crushed<BR>1 teaspoon sugar<BR>2 Tablespoons mushroom soy sauce<BR>4 stalks Chinese celery, cut into one-inch pieces<BR>2 teaspoons Chinese celery leaves, coarsely chopped<BR><B><I>Preparation:</B></I><BR>1. Heat oil in a clay pot on the top of the stove. Add garlic and stir for one minute.<BR>2. Then add mushrooms, black beans, sugar, and soy sauce. Cover the clay pot and simmer for fifteen minutes.<BR>3. Add celery pieces and simmer another three minutes, stir in the leaves, simmer another minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'073ED701DE9FE54082012C6F175F80E8', X'99815F4727A1314BB087F12B063D0AA5', X'99815F4727A1314BB087F12B063D0AA5', 0, 'javascript:', 'String Beans, Fermented Black Beans, and Ya Cai', 2, NULL, 0, 'String Beans, Fermented Black Beans, and Ya Cai', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1833', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound long beans or string beans<BR>3 Tablespoons vegetable oil, separated<BR>3 Tablespoons minced pork<BR>1 large clove garlic, peeled and minced<BR>2 sliced fresh ginger, peeled and minced<BR>3 chili peppers, seeded and cut into small pieces<BR>1 and 1/2 Tablespoons fermented black beans<BR>1 Tablespoon crushed Sichuan peppercorns<BR>2 Tablespoons <I>ya cai</I><BR>1/2 cup of chicken broth<BR>2 scallions minced and separated into white and green pieces<BR><B><I>Preparation:</B></I><BR>1. Cut ends off the green beans and then cut them into two-inch pieces.<BR>2. Heat wok or fry pan, add one tablespoon of the oil and stir-fry the pork for one minute until no longer pink. Then take it out and set it aside on a small plate or bowl.<BR>3. Add another tablespoon of the oil, then add the string beans and stir-fry about two or three minutes. Remove them from the pan and set them aside on a different plate.<BR>4. Add the last tablespoon of the oil and stir-fry the minced garlic and ginger for one minute, then add the chili pepper pieces and the black beans and stir-fry another minute. Next add the Sichuan peppercorns, <I>ya cai</I>, broth, white parts of the scallions, pork, and the green beans. Stir-fry for one to two minutes before removing everything to a serving bowl. Sprinkle the scallion greens on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5A4DD7EC20762144A7A1FB0F76EC330D', X'D2E238F74A715F44A29106BADF64FFAF', X'D2E238F74A715F44A29106BADF64FFAF', 0, 'javascript:', 'Stuffed Bamboo Fungus with Tofu', 2, NULL, 0, 'Stuffed Bamboo Fungus with Tofu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=833', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 bamboo fungus, hard end removed, then soaked until swollen and soft<BR>1 square firm tofu, mashed well<BR>1 Chinese black mushroom, soaked, stem removed, and minced finely<BR>1/4 teaspoon coarse salt<BR>1/4 teaspoon ground white pepper<BR>2 teaspoons cornstarch, divided in half<BR>10 dried golden needles, soaked in warm water for ten minutes<BR>1/2 teaspoon vegetable oil<BR>5 Shanghai cabbage, each cut in half the long way<BR>1/4 teaspoon vegetable oil<BR>dash of salt<BR>dash of ground white pepper<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Squeeze excess water from each bamboo fungus, then insert a finger and gently widen the opening.<BR>2. Mix mashed tofu, minced mushrooms, measured amounts of salt and pepper, and one teaspoon of the cornstarch. Then gently stuff each bamboo fungus until about ninety percent full. Tie the open end in a single know with golden needle mushroom. Repeat until all are filled and tied.<BR>3. Oil a plate and put these tied packages on half that plate, all facing the same way. Steam for ten minutes, then remove plate onto a serving platter.<BR>4. Blanch cabbage in boiling water for two minutes, drain well, and set on the same serving plate, on the other end.<BR>5. Heat oil in a small pan. Add dashes of salt and pepper and the sesame oil, and stir. When hot, drizzle over the bamboo fungus and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3D78140607ACDA40AB61D42CB82E72BA', X'602315A9CD7FA049B3DED25A4A9CA19F', X'602315A9CD7FA049B3DED25A4A9CA19F', 0, 'javascript:', 'Stuffed Bitter Melon I', 2, NULL, 0, 'Stuffed Bitter Melon I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1059', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>2 cloves garlic, peeled and minced<BR>2 Tablespoons fermented black beans, mashed with side of the cleaver<BR>3 shrimp, shells and veins removed, and minced<BR>1/4 pound ground pork<BR>2 scallions, minced<BR>2 teaspoons thin soy sauce<BR>1 teaspoon cornstarch<BR>1 teaspoon sugar<BR>1 large bitter melon, cut in half, seeds and pulp removed and discarded<BR>1/4 teaspoon ground white pepper<BR>2 Tablespoons Chinese rice wine<BR>1/2 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Heat oil and stir-fry the garlic and the fermented beans for one minute, then remove from the wok or fry-pan and stir in the shrimp and the pork, mixing well. Then add the scallions. Soy sauce, cornstarch, and sugar.<BR>2. Stuff the meat-shrimp mixture into the two bitter melon halves and add the rice wine, pepper and salt and one-quarter cup water. Add it to the wok or fry-pan, meat-side down, cover, and simmer for fifteen minutes. Remove the cover, and reduce the remaining liquid by half.<BR>3. Put the bitter melon pieces on a cutting board, cut them into half-inch pieces. Place then cut-side up on a pre-heated platter, pour any liquid remaining over them, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'356856DB40D9AA44B2C5D5CB5A6A0ABC', X'7CB1CDB9F531D04196A0DB647F5347EA', X'7CB1CDB9F531D04196A0DB647F5347EA', 0, 'javascript:', 'Stuffed Bitter Melon II', 2, NULL, 0, 'Stuffed Bitter Melon II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1442', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pound, or two bitter melons<BR>1 Tablespoon fermented black beans<BR>1 pound ground pork<BR>1 scallion, minced<BR>12 dried shrimp, optional<BR>2 cloves garlic, minced<BR>1 teaspoon soy sauce<BR>1 Tablespoon dry sherry<BR>1 teaspoon corn starch<BR>1 teaspoon thin soy sauce<BR>2 teaspoons sugar<BR>1/8 teaspoon pepper<BR>1/8 teaspoon salt<BR>2 Tablespoons peanut oil<BR>2 teaspoons corn starch mixed with two tablespoons water<BR><B><I>Preparation:</I></B><BR>1. Cut the bitter melon across the narrow way into one-inch sections. Remove the seeds and pulp and discard. Set the circles aside.<BR>2. Rinse black beans in cold water, drain, and discard the water.<BR>3. Soak the dried shrimp in warm water for half an hour or until soft. Drain and mince them.<BR>4. Combine ground pork, shrimp, scallion, soy sauce, corn starch, half the sugar, and the pepper and salt. Mix with a fork or chopsticks in one direction for half a minute. Stuff this mixture into the center of the bitter melon rings.<BR>5. Mix sherry, light soy sauce, sugar, and one cup of water.<BR>6. Heat oil and brown the stuffed bitter melon rings on both sides. Then add garlic and black beans; stir-fry a few seconds and add the sherry-soy sauce mixture. Bring to a boil, lower the heat and simmer for twenty minutes. Remove the stuffed melon rings to a serving dish. Reserve the sauce.<BR>7. Add the cornstarch-water mixture to the remaining sauce. Heat until thickened, then pour over the stuffed bitter melon, and serve.<BR><I>Approximate nutrient analysis</I> when serving four persons. Each serving has 399 calories, 8 g carbohydrate, 20 g protein, 456 mg sodium, 31 g total fat, and 82 mg cholesterol.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6F489B40AA06DC419D55B349A44910AD', X'63F625A99E2A2441BBF73B6C1B2572E4', X'63F625A99E2A2441BBF73B6C1B2572E4', 0, 'javascript:', 'Stuffed Chinese Pumpkin', 2, NULL, 0, 'Stuffed Chinese Pumpkin', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1023', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 half pound finely minced beef<BR>1/4 pound coarsely hand-chopped pork<BR>1/4 pound shrimp, shell and veins removed, then coarsely chopped<BR>1/4 cup canned water chestnuts, drained and coarsely chopped<BR>1 Tablespoon dark soy sauce<BR>2 teaspoons sesame oil<BR>1/4 teaspoon coarse salt<BR>1/2 teaspoon ground white pepper<BR>2 Tablespoons coarsely chopped scallion, green tops only<BR>2 Tablespoons fresh coriander, coarsely chopped<BR>1 teaspoon cumin seeds<BR>1 Chinese pumpkin (about a pound), center part of top cut out and reserved, seeds removed<BR>1 piece fresh coriander or a few cumin seeds, for garnish<BR><B><I>Preparation:</B></I><BR>1. Gently mix pork, beef, and shrimp.<BR>2. Stir together water chestnuts, soy sauce, sesame oil, salt, pepper, scallion greens, coriander, and cumin seeds. Then stir this into the meat mixture.<BR>3. Stuff into the pumpkin, and set its cover on it. Put this into a bowl and then into a steamer over rapidly boiling water. Steam it for seventy-five minutes.<BR>4. Carefully remove from the steamer and slide pumpkin onto a large flat serving plate, discarding any liquid in the bowl. Discard the pumpkin cover, as well.<BR>5. Cut down every inch or inch and a quarter from top to one inch from the bottom Do not cut though bottom part of the pumpkin. When you do the last cut, the pumpkin will fall out like a flower. Put garnish on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2D535A178D1BB94881770B83BD7CE9CE', X'E1846A5803C4CD469D2077192BF15A08', X'E1846A5803C4CD469D2077192BF15A08', 0, 'javascript:', 'Stuffed Marrow, Chinese Style', 2, NULL, 0, 'Stuffed Marrow, Chinese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1745', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 Chinese marrow, also known as hairy vegetable<BR>5 Chinese black mushrooms, soaked in hot water for half an hour<BR>5 baby corn, each cut into four pieces across their width<BR>5 Tablespoons green peas<BR>½ carrot, peeled and cut into half-inch dice<BR>2 Tablespoons fermented bean curd, mashed<BR>2 teaspoons sugar<BR>1 Tablespoon vegetable oil<BR>3 cloves fresh garlic, peeled and chopped<BR><B><I>Preparation:</B></I><BR>1. Peel marrow and cut them in half the long way, cut off both ends and remove seeds and some of the flesh leaving half-inch exterior of shape of each marrow half.<BR>2. Cut off and discard mushroom steams and cut each one into medium or half-inch dice.<BR>3. Mix fermented bean curd and the sugar, and set aside.<BR>4.Heat vegetable oil and stir-fry the garlic for one minute, then add the mushrooms, corn, peas, and carrot pieces and stir-fry for two minutes before mixing these vegetables with half the mashed fermented bean curd sauce. Put them into the half marrow boats and on to a heat-proof platter.<BR>5. Steam over boiling water for eight to ten minutes, stir the vegetables with the remaining mashed sauce mixture, then carefully remove the platter on to a cool one, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'670D21920364124CA336FCA74AC811C9', X'FAB108BA2FAC4041A156D9C98AA534A0', X'FAB108BA2FAC4041A156D9C98AA534A0', 0, 'javascript:', 'Stuffed Okra', 2, NULL, 0, 'Stuffed Okra', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1361', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 fresh okras, cut in half the long way, seeds removed<BR>1/4 pound fish paste<BR>10 <I>goji</I> berries, cut in half<BR>1/4 cup chicken stock<BR>dash each of salt, ground white pepper, and sugar<BR>1/2 teaspoon cornstarch<BR>dash sesame oil<BR><B><I>Preparation:</B></I><BR>1. Fill okra (or another small vegetable) with the fish paste, and put half a <I>goji</I> berry cut the long way in the center of each of them.<BR>2. Put them on a heat-proof plate, stuffed side up, and steam ver rapidly bing water for five minutes.<BR>3. While they steam, mix chicken stock, ground white pepper, and sugar, the cornstarch, and the sesame oil and bring to the boil and stir until thickened.<BR>4. Put steamed vegetables on a platter, pour the thickened hot chicken stock mixture over them and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'16D251C0B3EC844892A0503A30C13BD4', X'7B182325545D6A4D9FB96C983DD3070F', X'7B182325545D6A4D9FB96C983DD3070F', 0, 'javascript:', 'Sumptuous Succulent Mushrooms', 2, NULL, 0, 'Sumptuous Succulent Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=259', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>18 dry <I>shiitake</I> mushrooms<BR>dash of salt<BR>1 teaspoon sugar<BR>2 Tablespoons Chinese wine or dry sherry<BR>1 Tablespoon oyster sauce (optional)<BR>2 Tablespoons yam flour, cornstarch, or other thickener<BR><I><B>Preparation:</I></B><BR>1. Soak shiitake mushrooms in two cups warm water for thirty minutes. Remove them from the liquid, strain the liquid though a fine strainer, and set it aside.<BR>2. Cut stems away from mushroom caps and discard.<BR>3. Bring large portion of mushroom liquid to the boil then add salt, and sugar, reduce the heat and let simmer for a minute or two, then add wine (and oyster sauce) and the mushrooms and simmer for twenty minutes.<BR>4. Mix flour or cornstarch with set aside liquid, end bring mushrooms back to the boil before adding it. Allow to boil for two minutes, then remove from heat and allow mushrooms to cool in the liquid. Serve warm or at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CEB24A3AF0B469408D8E2998E9698F8D', X'9FC56745090C7646A85CB61FBB9CD1B8', X'9FC56745090C7646A85CB61FBB9CD1B8', 0, 'javascript:', 'Suryani''s Vegetarian Chicken I', 2, NULL, 0, 'Suryani''s Vegetarian Chicken I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1434', '<TD><RECIPE><B><I>Ingredients</B></I><BR>1 pound gluten sticks (<I>mien jing</I>), roll cut into bite-size pieces <BR>1/2 teaspoon turmeric powder <BR>1/2 teaspoon salt <BR>3 Tablespoons vegetable oil <BR>1 cup finely chopped onions <BR>10 dried chili peppers <BR>1 star anise <BR>1 piece stick cinnamon, three inches long <BR>1 cup canned unsweetened coconut milk <BR>1 cup tomato catsup <BR>2 fresh tomatoes, cut in wedges<BR>1 cup frozen peas <BR>salt to taste <BR><B><I>Preparation:</B></I><BR>1. Mix the gluten pieces with the turmeric and salt and set aside for half an hour.<BR>2. Heat oil, then brown onions and ginger for ne minute. Add chili peppers and star anise and tir for one more minute, then add the gluten pieces.<BR>3. Add the coconut milk and bring to the boil. Then add the cinnamon stick, catsup, and sugar, and simmer for two minutes.<BR>4. Add tomato wedges and the peas and cook another minute, then add the salt.<BR><B>Note:</B> Excellent served with hot rice.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3192E59693091942BE40A021A5C6F98C', X'5877D419903E5745A89F62CF986C95FB', X'5877D419903E5745A89F62CF986C95FB', 0, 'javascript:', 'Suyani''s Vegetarian Chicken II', 2, NULL, 0, 'Suyani''s Vegetarian Chicken II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=100', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound gluten sticks (<I>mien jing</I>), roll cut into bite-sized pieces<BR>1/2 teaspoon turmeric powder<BR>1/2 teaspoon salt<BR>1 cup finely chopped onions<BR>10 dried chili peppers<BR>1 staranise<BR>1 piece stick cinnamon, about 3 inches long<BR>1 can unsweetened coconut milk<BR>1 cup tomato catsup<BR>2 fresh tomatoes, cut into wedges<BR>1 cup frozen peas<BR>salt to taste<BR>3 Tablespoons vegetable oil<BR><I><B>Preparation:</I></B><BR>1. Mix the gluten pieces with the turmeric and salt and set aside for half an hour.<BR>2. Heat oil, then brown onions and ginger for a minute. Add the chili peppers and star anise and stir for a minute longer, then add the gluten pieces and mix well.<BR>3. Add coconut milk and bring to the boil along with the cinnamon stick, catsup, and sugar. Stir while simmering for one or two minutes.<BR>4. Add tomato wedges and oeas and cook another minute or tow. Then add salt to taste and serve with hot rice.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'592EAF57E3242F4684ECB490AC6BCB9C', X'C7E01B180765D6408C86F6A745D87BE9', X'C7E01B180765D6408C86F6A745D87BE9', 0, 'javascript:', 'Sweet and Sour Peppers and Eggplant', 2, NULL, 0, 'Sweet and Sour Peppers and Eggplant', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=141', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 Japanese eggplants, cut in half lengthwise and thirds crosswise.<BR>1 cup oil<BR>1 teaspoon chopped garlic<BR>1/2 onion, sliced<BR>1/2 red bell pepper, sliced<BR>1/2 green bell pepper, sliced<BR>1/3 cup chicken stock<BR>1/4 cup oyster sauce<BR>1/3 cup white vinegar<BR>3 Tablespoons sugar<BR>1 Tablespoon rice wine<BR>1 teaspoon sesame oil<BR>1 teaspoon cornstarch mixed with one tablespoon water<BR>1 teaspoon toasted white sesame seeds<BR><B><I>Preparation:</I></B><BR>1. Fry eggplant in oil in a wok or deep pan for about four or five minutes until golden brown then drain on paper towels. Remove all oil but one tablespoon from the wok or pan.<BR>2. Fry garlic, onion, and both peppers until tender but still crisp. Then add everything else except the sesame seeds and bring to the boil.<BR>3. Return eggplant to the pan, toss to reheat and coat with the sauce.<BR>4. Top with sesame seeds and serve.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2A0754E7CAC6194BB2F31714B9A35F04', X'52D488FBD4459D4FB2D3192B6A677EB3', X'52D488FBD4459D4FB2D3192B6A677EB3', 0, 'javascript:', 'Sweet and Sour Pickled Cabbage', 2, NULL, 0, 'Sweet and Sour Pickled Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=788', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons corn oil<BR>1 Tablespoon sesame oil<BR>4 cups washed and dried Napa cabbage, cut into one-inch wide pieces<BR>4 red chili peppers, seeded and cut into very thin strips<BR>2 Tablespoons thin or white soy sauce<BR>1 Tablespoon Chinese black vinegar<BR>1 Tablespoon Chinese red rice vinegar<BR>2 Tablespoons sugar<BR>1 teaspoon turmeric seeds<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat corn oil and the tablespoon of sesame oil in a wok or very large deep skillet. Then add cabbage and stir-fry for one minute.<BR>2. Add chili pepper strips, soy sauce, both vinegars, sugar and turmeric seeds. Stir-fry for another two minutes, then drain and refrigerate for one to two hours.<BR>3. Toss with sesame oil, put into a clean bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2145F3A8E659324085066686EB9A727F', X'C995A9F1477BFC4DA969D863A3DE6FA7', X'C995A9F1477BFC4DA969D863A3DE6FA7', 0, 'javascript:', 'Sweet Corn Kernels with Green Peppers', 2, NULL, 0, 'Sweet Corn Kernels with Green Peppers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=569', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 or 3 small green bell peppers<BR>2 ears fresh corn on the cob or fourteen ounces corn kernels<BR>3 Tablespoons peanut oil<BR>1/4 teaspoon salt, sea salt preferred<BR><B><I>Preparation:</B></I><BR>1. Dice peppers to match the size of the corn kernels.<BR>2. Remove kernels from the cobs.<BR>3. Heat oil and add both vegetables. Stir-fry them for about five minutes until the corn kernels are tender, then add salt and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2D754A7CD8071E47A700CA43FB2D3442', X'72781C27F7724B4ABCD768E7B794B0C9', X'72781C27F7724B4ABCD768E7B794B0C9', 0, 'javascript:', 'Sweet Potato and Taro Dumplings', 2, NULL, 0, 'Sweet Potato and Taro Dumplings', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=443', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound taro, peeled and cut into one-inch pieces<BR>1/2 pound sweet potatoes, peeled and cut into one-inch pieces<BR>1 Tablespoon lard or any hydrogenated fat<BR>1 teaspoon sugar<BR>1/4 teaspoon salt<BR>4 salted egg yolks, each cut in half<BR>8 sheets rice paper<BR>1 sheet purple or green laver cut into eight thin strips<BR>3 Tablespoons sweet potato flour or cornstarch<BR>1 cup corn oil<BR><B><I>Preparation:</B></I><BR>1. Cook taro and sweet potatoes separately until soft, then mash them and mix them together adding the lard or other fat, the sugar and the salt. Flatten the mixture into a four by eight inch piece, and let them cool about ten minutes. Next, cut into eight sections.<BR>2. Put half a salted egg yolk in the center of each section. Then take a section and cover the yolk with the mixture making the item into a sausage-shaped item about half-inch thick. Roll thin into the rice paper shaping it to resemble a small egg-roll.<BR>3. Take the strip of seaweed and make a belt around the center, slightly wetting the end to make it stick to itself.<BR>4. Dip eat one in the flour and deep fry in heated oil until golden, about one to two minutes. Drain and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6E4A877742C42D48AF7EAF2585DD8C48', X'58B001BB21ABEE419AD638E41B97C774', X'58B001BB21ABEE419AD638E41B97C774', 0, 'javascript:', 'Sweet Potatoes, Black Rice, and Ginger', 2, NULL, 0, 'Sweet Potatoes, Black Rice, and Ginger', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=647', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup Chinese black rice<BR>4 teaspoons corn oil<BR>1 cup minced scallions<BR>2 Tablespoons minced fresh ginger<BR>2 Tablespoons minced crystalized ginger<BR>2 medium sweet potatoes, cut into half-inch cubes<BR>1/2 teaspoon salt<BR>1/4 teaspoon white pepper<BR>2 Tablespoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Bring two cups water to the boil, add rice, cover and reduce heat and simmer for forty minutes, turn off the heat and allow to stand for ten minutes.<BR>2. Heat corn oil and fry scallions for five minutes, then add both types of ginger and the sweet potatoes and stir-fry for five minutes more.<BR>3, Remove from the heat and add salt and pepper and the sesame oil, and cover until the rice is ready.<BR>4. Mix rice and sweet potato mixture, let stand covered for ten minutes, toss again, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'45094828BD15184A85F7845BA93F7CD0', X'46B53F7FFDA64E4681644BDC9ABFE520', X'46B53F7FFDA64E4681644BDC9ABFE520', 0, 'javascript:', 'Sweetened Eggplant', 2, NULL, 0, 'Sweetened Eggplant', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=801', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 long thin eggplants, about twelve ounces<BR>2 teaspoons rice vinegar<BR>2 Tablespoons cornstarch<BR>1/2 teaspoon white pepper<BR>1/2 teaspoon cinnamon<BR>1 Tablespoon sugar<BR>1 teaspoon fermented glutinous rice<BR>3 Tablespoons cornstarch<BR>1 cup corn oil<BR>1 teaspoon sesame oil<BR>1 Tablespoon minced scallions, green part only<BR><B><I>Preparation:</B></I><BR>1. Coarsely chop the eggplant, put it into a bowl, and soak in two cups of ice water mixed with the rice vinegar for ten minutes. Then drain well.<BR>2. Mix eggplant with the two tablespoons of cornstarch, the white pepper, cinnamon, sugar, and the fermented rice. Steam over boiling water for ten minutes, then remove from the steamer, drain, and allow to cool.<BR>3. Mix the three tablespoons of cornstarch with the eggplant mixture, and shape into two-inch flat patties.<BR>4. Heat half of the oil, and fry half of the patties on one side until lightly brown, turn over and fry them on the other side until lightly brown. Remove and drain on paper towels while the other half of the oil is heated and the rest of the patties are fried on both sides.<BR>5. Put the patties on a heated plate, sprinke with sesame oil and the scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B364EEAF0024D94AA45C82387838527B', X'2E0DDC15755BB546AF35336600A687A5', X'2E0DDC15755BB546AF35336600A687A5', 0, 'javascript:', 'Taiwan''s Birds Nest Fern with Tree Seeds', 2, NULL, 0, 'Taiwan''s Birds Nest Fern with Tree Seeds', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1901', '<TD><RECIPE><B><I>Ingredients</B></I><BR>1 pound <I>shansu</I>, washed, trimmed, stems cut away, and cut into one- to two-inch pieces<BR>10 small pieces dried boneless fish, soaked in warm water for fifteen minutes<BR>10 to 20 tree seeds with two tablespoons of their picking juice, pitted if desired<BR>2 Tablespoons vegetable oil<BR>2 cloves garlic, peeled and thinly sliced<BR>2 to 3 slices fresh ginger, slivered<BR>1 scallion, slivered or 1 small hot pepper, seeded and slivered<BR>salt and pepper, to taste<BR>a few drops of sesame oil<BR><B><I>Preparation:</B></I><BR>1. Blanch the vegetable in boiling water for one minute, then rinse and drain with cold water.<BR>2. Drain soaked dried fish.<BR>3. Heat a wok or fry pan, then add the vegetable oil. Stir-fry the garlic, scallion, ginger, and dried fish for about three minutes.<BR>4. Then, add the rest of the ingredients, stir once or twice, and then serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'73A9D54346FE2448AFF4A9E77A527176', X'23CD5C212C3D7E42B34B8FC5D617AB18', X'23CD5C212C3D7E42B34B8FC5D617AB18', 0, 'javascript:', 'Taro with Honey, Anhui Style', 2, NULL, 0, 'Taro with Honey, Anhui Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1765', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound large piece of taro<BR>1/2 cup honey<BR>1/4 cup crushed brown sugar<BR><B><I>Preparation:</B></I><BR>1. Peel then cut the taro into thin match-stick strips. Put them in a large bowl on the top layer of a steamer with two inches of water in its bottom. Pour the honey over the taro and sprinkle the sugar over it next. Simmer for one hour or until the taro is soft.<BR>2. Remove the taro with a slotted spoon to a pre-heated soup-type bowl. Put the liquid into a small pot and bring this to a boil, stirring every few minutes until only about two or three tablespoons of liquid are left. Pour this over the taro and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3F1B00E43D3C564CBB68F76F09D067F3', X'63FC898CC2976746903680F59846F346', X'63FC898CC2976746903680F59846F346', 0, 'javascript:', 'Taro-stuffed Oyster Mushrooms', 2, NULL, 0, 'Taro-stuffed Oyster Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1485', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 large oyster mushrooms<BR>1 Tablespoon dried shrimp, soaked for one hour in half cup boiling water, then drained and minced, ooptional<BR>2 Tablespoons Chinese rice wine<BR>1/8 teaspoon ground white pepper<BR>1 scallion, white part only, minced<BR>1/2 teaspoon salt<BR>1 teaspoon sugar<BR>1/2 pound taro, steamed until soft<BR>2 Tablespoons cornstarch<BR>1 cup vegetable oil, for frying<BR><B><I>Ingredients:</B></I><BR>1. Soak mushrooms with soaked and minced shrimp, rice wine, ground white pepper, minced scallion, salt and sugar for half an hour. Remove mushrooms and dry them with paper towels, and set marinade aside.<BR>2. Mash taro and mix with marinade that was set aside.<BR>3. Dust top the top side of each mushroom with cornstarch, removing any excess, and spread one tenth of the taro mixture on each mushroom. Allow to rest for one hour, then dust both sides of each mushroom with any remaining cornstarch and let rest for ten minutes.<BR>4. Heat oil in a wok or a large pan and fry the mushrooms, filling side down until crisp, then turn over and fry for another minute. Then drain and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B2369BEEBB283249A04C86C7DA6B7A25', X'AB66B7BFB8D3B04498B7481AE85482FE', X'AB66B7BFB8D3B04498B7481AE85482FE', 0, 'javascript:', 'Tea Leaves and Corn', 2, NULL, 0, 'Tea Leaves and Corn', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=174', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 teaspoon Tung Ting tea, ground<BR>1 can baby corn, blanched in boiling water just before use<BR>1 small hot pepper, minced<BR>dash of salt<BR>1/2 teaspoon sugar<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</I></B><BR>1. Cut each corn in half lengthwise.<BR>2. Mix the warm corn and all the other ingredients and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B58663F9B86B2A4D88EA6A7E0DAAB8CB', X'443B8B0797ED1C4991DE4811A60F170E', X'443B8B0797ED1C4991DE4811A60F170E', 0, 'javascript:', 'Tea-smoked Beancurd', 2, NULL, 0, 'Tea-smoked Beancurd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=966', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 squares soft or silken bean curd squares, cut into one-inch pieces<BR>1 Tablespoon Lapsang Souchong tea leaves simmered for five minutes in one cup of water, then boiled for one minute, and then drained, the tea leaves discarded<BR>1 teaspoon coarse salt<BR>1 Tablespoon sesame oil<BR>1 sprig fresh coriander, diced<BR><B><I>Preparation:</B></I><BR>1. Put bean curd pieces, water from the brewed tea, salt, and sesame oil in a ceramic bowl.<BR>2. Set aside for one hour in the refrigerator. Remove to the counter and carefully discard the liquid. Serve the bean curd in a bowl or a deep dish, and sprinkle the coriander on it just before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CF8E4BC6D9E90F419CD8A324466EE21A', X'366316420BA7F845BF9054E0314081AE', X'366316420BA7F845BF9054E0314081AE', 0, 'javascript:', 'Three Ways with Water Spinach', 2, NULL, 0, 'Three Ways with Water Spinach', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=603', '<TD><RECIPE><B><I>Ingedients:</I></B><BR>1 pound water spinach (also known as hollow-stem vegetable)<BR>1 Tablespoon vegetable oil<BR>2 cloves garlic, minced<BR>1 teaspoon minced fresh ginger<BR>1 scallion, green and white parts minced<BR>2 teaspoons flavoring of choice such as: shrimp paste or two tablespoons pickled tofu with chili (reviewers choice), or two tablespoons fish sauce such as <I>nuoc mam</I> or <I>nam pla</I><BR><I><B>Preparation:</I></B><BR>1. Wash and trim the spinach. Separate the stems from the leaves and keep them separated.<BR>2. Heat the oil in a wok and add garlic, ginger and a scallion. Stir-fry for thirty seconds.<BR>3. Add flavoring of choice, one-quarter cup water, and spinach stems. Reduce the heat and cover the pan; simmer about one minute or until the stems are tender.<BR>4. Add the leaves and stir-fry to wilt, tossing constantly. Cook three to five minutes or until the spinach is soft and strongly flavored.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0780950A70F30C4E940322C0CF613F1F', X'0171888700746247B05683E1113A2D52', X'0171888700746247B05683E1113A2D52', 0, 'javascript:', 'Tofu and Mushrooms, Braised', 2, NULL, 0, 'Tofu and Mushrooms, Braised', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=59', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 package (about 14 ounces) firm tofu<BR>2/3 cup vegetable broth<BR>2 teaspoons black bean garlic sauce<BR>2 teaspoons oyster sauce<BR>1/2 teaspoon sesame oil<BR>1 teaspoon sugar<BR>2 Tablespoons cooking oil<BR>1/2 pound small white mushrooms<BR>1/4 pound oyster mushrooms<BR>6 fresh medium-size shiitake mushrooms<BR>1 and 1/2 teaspoons cornstarch mixed with <BR>1 Tablespoon water<BR><B><I>Preparation:</I><BR>1. Cut the tofu in half horizontally, then using a biscuit cutter, make two inch circles from each half; should yield twelve circles (squares are fine, too, but not as pretty).<BR>2. Combine broth, black bean and oyster sauces and sesame oil and sugar. Set this sauce mixture aside.<BR>3. Place a large fry pan on medium heat until hot. Add 1 Tablespoon oil and add the tofu and cook until brown, about 2 minutes per side. Remove tofu and set aside.<BR>4. Add remaining opne Tablespoon of oil and all the mushrooms; stir-fry them one minute then add sauce, reduce heat to low and simmer until the mushrooms are tender (about five minutes).<BR>5. Add cornstarch mixture and cook until sauce boils, thickens and clears.<BR>6. Arrange tofu in a circle around the edge of a serving plate. Place mushroom mixture in center and serve.<BR>Makes four to six servings.<BR></B></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'17038202AC501D4DAEBEE58837588702', X'079E4A0B10FE3B43A648F9C4959FC5B0', X'079E4A0B10FE3B43A648F9C4959FC5B0', 0, 'javascript:', 'Tofu Crepe Wraps', 2, NULL, 0, 'Tofu Crepe Wraps', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=122', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>10 ounces all purpose flour<BR>1 ounce Shiitake mushrooms<BR>3 sheets beancurd skin<BR>1 cup hot water<BR>5 Tablespoons minced scallions<BR>2 teaspoons sesame oil<BR>1 Tablespoon soy sauce<BR>1 teaspoon. sugar<BR>3 cups water<BR>2 Tablespoons sweet bean paste<BR>2 teaspoons sugar<BR>2 Tablespoons all purpose flour<BR><B><I>Preparation:</I></B><BR>1. Soak mushrooms in warm water until soft, discard the stems and thinly slice. Reserve the mushroom water for later use.<BR>2. Heat a wok, then add a Tablespoon of oil. Stir-fry the mushrooms until fragrant, add half cup of the mushroom water, the soy sauce and the sugar and cook until the sauce is completely evaporated. Remove and divide into three portions.<BR>3. Mix flour with an equal amount of hot water and knead into a smooth dough. Divide the dough into three equal portions. Press each portion flat and brush on a layer of sesame oil. Press two dough portions together gently and roll into a four-inch diameter thin crepe. Fry the crepe on both sides in a dry pan over low heat until light brown. Remove from the pan and separate into two crepes. Repeat until all crepes are made.<BR>4. Cut beancurd sheets in half. Spread a sheet of beancurd skin and arrange a portion of shredded black mushrooms into a strip a little shorter then beancurd skin. Fold the bean curd skin over and seal the opening with warm flour-water. Repeat until each beancurd sheet is rolled.<BR>5. Heat a wok, add two tablespoons of oil. Fry the rolls until golden and crispy on all sides. Remove and cut into seven pieces.<BR>6. Put a mushroom roll on a crepe, sprinkle on minced green onion, roll and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'23C35797EC3CE942A9D764543C688DD6', X'484B88F45BFB424997A8B229A74C9DF5', X'484B88F45BFB424997A8B229A74C9DF5', 0, 'javascript:', 'Tofu in Rice wine, Hakka Style', 2, NULL, 0, 'Tofu in Rice wine, Hakka Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1760', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 teaspoons red yeast rice (optional)<BR>1/3 cup chicken broth<BR>1/3 cup fermented sweet wine rice, including its liquid<BR>2 Tablespoons Chinese rice wine such as Shaoxing, or use a dry sherry<BR>1 Tablespoon soy sauce<BR>1 teaspoon cornstarch<BR>1/2 teaspoon salt<BR>1/4 teaspoon ground black pepper<BR>14 to 16 ounces soft tofu<BR>4 ounces skinless and boneless chicken thighs or pork butt (see note below)<BR>4 ounces peeled medium-size shrimp, their veins removed and discarded<BR>2 Tablespoons vegetable oil<BR>1 Tablespoon minced garlic<BR>3 Tablespoons thinly sliced scallions<BR><B><I>Preparation:</B></I><BR>1. For the sauce, place the red yeast rice in a fine strainer and rinse well. Pour into a small bowl and cover with boiling water, and let stand until soft, about five minutes. Stir in the broth, wine rice, wine, soy sauce, cornstarch, salt, and pepper.<BR>2. Next, in a three- to four-quart pan over high heat, bring two quarts water to a boil. Then drain the tofu and invert it on to a rimless dinner plate or a wooden board. Cut the tofu into half-inch cubes and gently slide them into the boiling water. When the water begins to barely simmer, remove the pan from the heat, and let the tofu stand in this hot water until ready to stir-fry it.<BR>3. Finely chop the chicken or pork, and shrimp; then gently drain the tofu into a colander.<BR>4. Set a fourteen-inch wok or twelve-inch fry pan over high heat; when hot, add the oil and spread it around. Then add garlic, chicken, and shrimp and stir-fry until slightly brown and crumbly, about a minute or two. Stir the sauce mixture, add it to the pan, and stir until it boils, then add the drained tofu and cook stirring occasionally and gently, until the tofu absorbs some sauce, about a minute or two. Sprinkle scallion pieces over the tofu, put in a bowl, and serve.<BR><B>Note:</B> Fermented sweet wine rice is, in some markets, called rice pudding or rice sauce. Look for it in the refrigerated section of most Asian markets. It resembles soft white rice in a slightly cloudy liquid, its wine. Dried red yeast rice adds color and helps balance the wines sweetness. If you can not find these products, make a less complex sauce mixing one-third cup chicken broth, one-third cup cream sherry, three tablespoons water, two tablespoons dry sherry, one tablespoon soy sauce, two teaspoons cornstarch, half-teaspoon salt, and one-quarter teaspoon ground black pepper. This dish serves three to four people as a main dish in a multi-course meal.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5672A24D05DAA747BC93169767593A16', X'75A5BE32BE53BC4BABC051740CB21972', X'75A5BE32BE53BC4BABC051740CB21972', 0, 'javascript:', 'Vegetable Harvest', 2, NULL, 0, 'Vegetable Harvest', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=843', '<TD><RECIPE><B><I>Vegetable ingredients:</B></I><BR>1 European cucumber, cut in three by half-inch sticks<BR>1/2 white radish (daikon), peeled and cut same as cucumber<BR>1 carrot, cut same as cucumber and white radish<BR>12 red radishes, ends removed<BR>1/4 head iceberg lettuce, washed<BR>1/2 cup cherry tomatoes, washed<BR><B><I>Sauce ingredients:</B></I><BR>2 Tablespoons corn oil<BR>1/4 pound ground pork<BR>1 Tablespoon chili powder<BR>1/4 cup chopped scallions<BR>2/3 cup hoisin sauce<BR>2 Tablespoons sugar<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat a ten-inch fry-pan on medium heat, and when hot add the ground pork. Stir-fry until no longer pink, about three minutes.<BR>2. Add chili powder and cook until the oil turns red, then add scallions, sugar, and hoisin sauce, and stir well before adding two tablespoons of cold water and the sesame oil. Mix well, then serve surrounded by the raw vegetables listed in this recipe.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'12F3A8A71707A54DBC170B7CAD1C93F4', X'5D91CD0AD3C29D4EAD2F418936997807', X'5D91CD0AD3C29D4EAD2F418936997807', 0, 'javascript:', 'Vegetable Hot Pot', 2, NULL, 0, 'Vegetable Hot Pot', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=67', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>6 ounces bean thread noodles<BR>8 cakes bean curd, cubed<BR>1 pound spinach<BR>3 bunches water cress<BR>1 pound romaine lettuce<BR>1 head fennel<BR>2 cups broccoli stems<BR>1 pound cabbage<BR>8 cups vegetable stock<BR>1 piece of ginger, about two by two inches<BR>4 cloves garlic<BR>1/2 cup chopped coriander<BR>1 Tablespoon sesame oil<BR>1 Tablespoon scallion oil<BR><I><B>Preparation:</I></B><BR>1. Soak noodles half hour in warm water then drain and cut into five-inch lengths.<BR>2. Cut all the vegetables into two-inch pieces.<BR>3. Heat half the stock in a hot pot or deep pot (an electric one allows use at the table). When it boils, add ginger, coriander, garlic, sesame oil, and scallion oil (made by cooking ten scallions--mincing the whites and cutting the green part into two-inch pieces in two cups of oil until they turn brown, then drain and store in your refrigerator).<BR>4. Cook the vegetables of your choice and the beancurd in the boiling broth until it wilts and is done to your taste. Use strainer-like spoons or the baskets made for hot pot to remove items from the broth. Do not put these into your mouth, they will be too hot, and it is not a sanitary way to share the food, if you do.<BR>5. Replenish the stock as needed. after all is consumed, pour used stock into each persons bowl and enjoy it.<BR>6. Many dipping sauces can be used for the vegetables when cooked. The author recommends her Ginger Soy Sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7ED12ED2DBECFB458DC3448D1BB37204', X'627287EFC5262747B9D8E91ACBA5E741', X'627287EFC5262747B9D8E91ACBA5E741', 0, 'javascript:', 'Vegetable Packages with Durian', 2, NULL, 0, 'Vegetable Packages with Durian', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=857', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups finely shredded peeled taro root<BR>2 Tablespoons arrowroot or water chestnut flour<BR>1 egg, beaten<BR>3 or 4 sections (about 1 cup) of fresh or frozen durian, cut in half the long way<BR>1/2 cup flour<BR>2 cups vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Mix shredded taro with flour and egg and stir well.<BR>2. Roll each piece of durian in the taro mixture until completely coated. Then roll into flour, leaving it lightly dusted with the four. Shake off any excess. Squeeze this mixture gently to shape it into pointed rolls.<BR>3. Heat oil and deep fry half the rolls until golden brown, then drain on paper towels and repeat until done.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5F6ADA7B75758546ACFA2F3C9B90DAF5', X'45731796BA902E4690FA145306F60DF4', X'45731796BA902E4690FA145306F60DF4', 0, 'javascript:', 'Vegetables and Egg Noodles', 2, NULL, 0, 'Vegetables and Egg Noodles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1799', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons cloud ear fungi, soaked and chopped<BR>3 gluten puffs, cut into quarters<BR>5 dried medium-size Chinese mushrooms, soaked for half an hour<BR>5 white button mushrooms, cut in quarters<BR>1 carrot, peeled and cut into thick two-inch slices<BR>4 baby corn, cut in half lengthwise<BR>5 slices fresh ginger, slivered<BR>2 Tablespoons toasted pine nuts<BR>2 Tablespoons peach kernels, toasted<BR>1/2 cup chicken stock<BR>1 teaspoon dark soy sauce<BR>1 teaspoon sesame oil<BR>2 teaspoons granulated sugar<BR>1/2 teaspoon salt<BR>2 Tablespoons vegetable oil<BR>1/4 pound egg noodles, freshly cooked and drained<BR>2 teaspoons cornstarch mixed with like amount of cold water<BR>2 Tablespoons fresh mint, coarsely chopped<BR><B><I>Preparation:</B></I><BR>1. Prepare all the vegetables including removing hard pieces from the cloud ear fungi and discarding them; pouring two cups of boiling water over the gluten puffs and when cool squeezing out and discarding the water, discarding the stems of the Chinese black mushrooms and cutting them into quarters; them mixing the cloud ear fungi, gluten puffs, Chinese black mushrooms, white mushrooms, corn, and the carrots, and setting them aside in a bowl.<BR>2 Mix both nuts in a small bowl and set them aside.<BR>3. Mix chicken stock, dark soy sauce, sesame oil, sugar, and salt and set these aside.<BR>4. Heat the vegetable oil and add the mushroom mixture and stir fry for two minutes, then pour the chicken stock mixture over this, and add the cornstarch mixture and when thick, pour this on top of the hot cooked noodles, Top with the toasted nuts, then the chopped mint. Toss this when everyone is sitting at the table, then serve it.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1CA5ABA5A81C3C45AB34DE60416C9E87', X'FA2E082D5E734645A488568BCFFB683D', X'FA2E082D5E734645A488568BCFFB683D', 0, 'javascript:', 'Vegetarian Curry', 2, NULL, 0, 'Vegetarian Curry', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=601', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound imitation lamb or beef cubes, soaked for half hour in tepid water<BR>2 Tablespoons corn oil<BR>1/2 pound peeled potatoes, cut into half inch pieces, and boiled for two minutes, then drained<BR>2 green tomatoes, cut into half-inch wedges<BR>4 chili peppers<BR>1 teaspoon ground coriander<BR>1/4 teaspoon smashed whole coriander pieces<BR>1 teaspoon ground fennel<BR>1/4 teaspoon whole fennel<BR>1 teaspoon ground cumin<BR>1/4 teaspoon whole cumin<BR>3 whole star anise<BR>1 quill cinnamon, about two inches long<BR>2 Tablespoons chili paste with garlic<BR>2 Tablespoons thin soy sauce<BR><B><I>Preparation:</B></I><BR>1. Heat wok, add oil, and fry vegetarian meat cubes until almost golden brown, then add potatoes and fry another minute.<BR>2. Add one cup of water and simmer for ten minutes. Add all the remaining ingredients and simmer five minutes longer, then serve.<BR><B>Note:</B> Many people like this dish served on thin cellophane noodles; a one ounce package, deep-fried in half-cup oil for half-minute or less turning them quickly when they greatly expand (that is in about ten seconds). At that point, immediately remove and drain well. This can be done early in the day and set aside.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EB879FD4B8E053479292B4422A087DBD', X'127045136008224493A5FC5AF3045ACA', X'127045136008224493A5FC5AF3045ACA', 0, 'javascript:', 'Vegetarian Duck', 2, NULL, 0, 'Vegetarian Duck', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=755', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 Chinese black mushrooms, soaked, stems removed<BR>1/2 cup canned bamboo shoots<BR>6 dried bean curd sheets<BR>1 Tablespoon vegetable oil<BR>1 teaspoon minced fresh ginger<BR>1 clove garlic, minced<BR>1/2 teaspoon coarse salt<BR>2 Tablespoons sesame oil<BR>2 Tablespoons Chinese rice wine<BR>1 teaspoon soy sauce<BR>1/2 teaspoon five-spice powder<BR>1 cup corn oil<BR><B><I>Preparation:</B></I><BR>1. Sliver mushrooms and bamboo shoots and mix them together.<BR>2. Soak bean curd sheets in tepid water for fifteen minutes, then drain.<BR>3. Heat the tablespoon of vegetable oil and fry mushroom mixture, ginger, and garlic for one minute. Remove and drain. Then add salt, sesame oil, rice wine, soy sauce, and five-spice powder and mix well.<BR>4. Put sheets of bean curd one on top of the other, brush each layer with liquid from the mushroom mixture. Put the solids at one end of the top sheet and roll, folding in the ends making a package about two inches wide.<BR>5. Heat steamer, and oil a plate, and put the roll seam side down and steam for five minutes over boiling water.<BR>6. Heat the corn oil and fry the roll until golden, slice on an angle into ten one-inch sections and serve alone on the side with dishes such as Vegetarian Duck Soup.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DE4F2045B8FA4041AB6547CA41064F2D', X'821F8EA85AC39F448959629D9A33192F', X'821F8EA85AC39F448959629D9A33192F', 0, 'javascript:', 'Vegetarian Gold Coins', 2, NULL, 0, 'Vegetarian Gold Coins', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=22', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 cup grated potatoes or taro root<BR>1 cup chopped onions<BR>1 cup grated carrots<BR>1/2 cup Bisquick<BR>2 large eggs, beaten separately<BR>1 Tablespoon sesame oil<BR>1 teaspoon salt<BR>1/2 cup sugar<BR>1/2 cup flour<BR>1 teaspoon sesame oil<BR>1 cups oil for deep frying<BR><B><I>Preparation:</B></I><BR>1. Preheat oven to 350 degrees F.<BR>2. Steam potatoes or taro for fifteen minutes until tender, then squeeze out extra moisture.<BR>3. In a large mixing bow, mix all vegetables, Bisquick, on egg, sesame oil, salt, and sugar. Blend well until a dough forms that is soft and smooth.<BR>4. Line cookie sheet with aluminum foil and shape dough into small balls, about thirty of them. Bake them for twenty minutes or until firm. Flatten each ball into the shape of a coin.<BR>5. Allow the vegetables to cool slightly, while they do, make a batter of the other egg, half cup of water, the flour and the rest of the sesame oil. Coat each coin-shaped piece with batter.<BR>6. Deep-fry the battered vegetable coins in small batches until golden brown. Drain well and serve immediately.<BR><B>Note:</B> Cooked left-overs can be heated in your oven, uncooked ones frozen for future use.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FAA158E229DF744E9511C7A6F361A1A8', X'118E7386F80E7D4887883CF69D777851', X'118E7386F80E7D4887883CF69D777851', 0, 'javascript:', 'Vegetarian Kidney with Celery', 2, NULL, 0, 'Vegetarian Kidney with Celery', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=661', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 thick shiitake mushrooms soaked for twenty minutes in warm water<BR>1/2 cup cornstarch<BR>1 cup corn oil<BR>1 stalk celery, Chinese preferred, minced<BR>1 Tablespoon soy sauce<BR>1 Tablespoon rock sugar, crushed<BR>1 teaspoon sesame oil<BR>1/2 teaspoon five-spice powder<BR>1 teaspoon catsup<BR><B><I>Preparation:</B></I><BR>1. Score the mushrooms on their top surface, in both directions. Be careful not to cut all they way through them. Remove the stems from the mushrooms with a scissor, then squeeze out excess water.<BR>2. Dip the mushrooms in cornstarch and be sure to coat them down in the scored places. Then roll each mushroom like a cigar, scored side out. Set aside for ten minutes until the cornstarch wets itself.<BR>3. Heat the oil and fry the mushrooms cigars until golden, then remove with a slotted spoon and put them on two or three paper towels to drain. Oil blanch the celery in the same oil, and drain it. Set the oil aside for a future use. 4. Mix soy sauce, sugar, sesame oil, five spice powder, and catsup.<BR>5. Return the mushrooms to the wok or pan and dry-fry for half a minute, then add celery and toss well, and then the soy sauce mixture. Mix for half to one minute, then remove to a platter or bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'88BC07F74C960545BAFE9389B6221B32', X'359A65D443A33841A2D3D1EF4DFEFA7C', X'359A65D443A33841A2D3D1EF4DFEFA7C', 0, 'javascript:', 'Vegetarian Lamb Curry', 2, NULL, 0, 'Vegetarian Lamb Curry', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=504', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound dry gluten cubes<BR>1/4 cup corn oil<BR>1/2 pound Chinese yams or white potatoes, cut into two-inch pieces<BR>1 or 2 red chili peppers, seeds removed and minced<BR>1 Tablespoon curry powder<BR>2 curry leaves or an additional half-teaspoon curry powder<BR>1 teaspoon whole cumin<BR>1 teaspoon whole fennel<BR>2 two-inch quills or sticks of cinnamon<BR>1 teaspoon chili paste with garlic<BR>1 green pepper, seeded and cut into one-inch pieces<BR>1 carrot, peeled and cut into one-inch pieces<BR>1/2 cup fresh or frozen peas<BR>1 can baby corn cut into one-inch pieces<BR><B><I>Preparation:</B></I><BR>1. Soak gluten in warm water for one hour, then squeeze it of all of its water.<BR>2. Heat oil and fry the gluten pieces until lightly browned, then drain and set the gluten aside. Also set the oil aside.<BR>3. Bring two cups of water to the boil, add potatoes and cook them for twenty minutes, then drain and discard all but one cup of the potato water.<BR>4. Reheat oil and fry chili pepper for one minute, then add all the other spices and stir fry them for one minute. Then add the gluten and stir fry it for one minute, add the drained potatoes and fry another minute.<BR>5. Add the reserved water and simmer for ten minutes. Then add pepper, carrots, and peas and bring this to the boil, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'930B0021201F21449281FDBD7D98496C', X'7E02177665CA9F4DA25FCE99E0CD802D', X'7E02177665CA9F4DA25FCE99E0CD802D', 0, 'javascript:', 'Vegetarian Rice Rolls', 2, NULL, 0, 'Vegetarian Rice Rolls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=149', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 sheets rice paper (about twelve inches square)<BR>4 eggs, beaten<BR>1/2 teaspoon margarine<BR>10 medium button mushrooms, sliced thin<BR>1/4 teaspoon sesame oil<BR>1/4 teaspoon salt<BR>1/2 teaspoon margarine<BR>2 stalks celery, sliced thin<BR>2 medium tomatoes, diced<BR>1/8 teaspoon sesame oil<BR>1 teaspoon vinegar<BR>1 teaspoon sugar<BR>2 cups water<BR>1/4 teaspoon white pepper<BR>1 Tablespoon chopped peanuts<BR>parsley for decor (optional)<BR><B><I>Preparation:</I></B><BR>1. Cut rice paper into quarters and set aside.<BR>2. Heat the margarine, fry mushrooms lightly. Add the sesame oil, salt, and the eggs and fry lightly making a flat pancake; do not over cook them, they should be soft. Slice the omelet into one by six inch strips. <BR>3. Take one rice sheet, put egg piece on top and roll, sushi style.<BR>4. Take other margarine and stir-fry celery about a minute then add tomatoes and sesame oil and fry another minute, then add all but the parsley and bring to the boil then remove from the heat and pour over the rice rolls and serve. Garnish with peanuts; add parsley, if desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C9B553B2F934854D9C5CF4FE6B9D0C78', X'42EC92BDC3A6FD4F945298626744944E', X'42EC92BDC3A6FD4F945298626744944E', 0, 'javascript:', 'Vegetarian Shark''s Fins', 2, NULL, 0, 'Vegetarian Shark''s Fins', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=728', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 ounces daylily buds<BR>2 Tablespoons vegetable oil<BR>4 ounces fresh coriander<BR>2 Tablespoons cornstarch<BR>1 Tablespoon flour<BR>1 cup vegetable oil<BR>2 Tablespoons sherry<BR>2 cups chicken broth<BR>1/2 ounce Chinese black mushrooms, soaked, stems removed, and shredded<BR>1/2 cup bamboo shoots, sliced and shredded<BR>1/2 medium carrot, pared, sliced, and shredded<BR>1 ounce wheat gluten shredded and mixed with onr teaspoon cornstarch<BR><B><I>Preparation:</B></I><BR>1. Revive daylily buds in boiling water, remove their short foot or pedicel, then push a needle through end to end, two or three times.<BR>2. Heat oil and stir-fry coriander until lightly brown. Discard vegetable and reserve the oil.<BR>3. Mix cornstarch and flour with one-quarter cup cold water&gt; Dip each daylily bud in the batter and deep fry in oil, then drain and put them in a bowl. Add sherry, salt and one cup chicken broth and steam for fifteen minutes. Drain and put daylilies on a platter.<BR>4. Mix mushroom, bamboo shoot, carrot, and wheat gluten shreds in a small pot. Add half cup of chicken broth and simmer for two minutes. Drain, discard the liquid, and put this on top of the day lilies.<BR>5. Make sauce with remaining chicken broth and the coriander-flavored oil and pour over the dish, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7CFCAD9396ED9F41BA5657893E9E74C9', X'032C16CA55D1464AAED4690BF52C02F9', X'032C16CA55D1464AAED4690BF52C02F9', 0, 'javascript:', 'Vegetarian Steak with Mushrooms and Bamboo Shoots', 2, NULL, 0, 'Vegetarian Steak with Mushrooms and Bamboo Shoots', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=559', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons peanut oil<BR>2 scallions, minced<BR>2 slices fresh ginger, minced<BR>1 ten-ounce can vegetarian steak, cut into bite-sized pieces<BR>1 cup diced bamboo shoots<BR>1 cup cloud ear mushrooms, soaked in warm water for thirty minutes, then drained<BR>30 Chinese dried black mushrooms, soaked in warm water for half an hour, the water squeezed out, stemd discarded, the mushrooms reserved<BR>1 cup dried lily flowers, soaked in cold water for an hour, the water discarded, the hard tips cut away and discarded, the rest cut in half<BR>2 Tablespoons soy sauce<BR>1 teaspoon sugar<BR>1 teaspoon salt<BR>1 teaspoon cornstarch mixed with one cup of water<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat oil, then stir-fry scallions and ginger for a few seconds, then add vegetarian steak, bamboo shoots, all the mushrooms, and the lily flowers and stir-fry for two minutes.<BR>2. Add soy sauce, sugar, salt and three tablespoons of the black mushroom water. Cover and simmer for five minutes, remove the cover and stir in cornstarch water and stir until the liquid thickens. Remove from heat and add sesame oil and mix thoroughly.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9F5B331ABBE305458ECFB352EE634893', X'3E0E6F35190D6D45A1278D43581E1738', X'3E0E6F35190D6D45A1278D43581E1738', 0, 'javascript:', 'Vegetarian Stewed Turtle', 2, NULL, 0, 'Vegetarian Stewed Turtle', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=16', '<TD><RECIPE><I></B>Ingredients:</I></B><BR>12 dried shiitake mushrooms<BR>8 oounces fresh lotus root<BR>1 teaspoon five-spice powder<BR>pinch of white pepper<BR>2 teaspoons oil (optional)<BR>4 ounces snow peas<BR>1 teaspoon sugar<BR>1 Tablespoon rice wine or dry sherry<BR>1 Tablespoon cornstarch<BR><I><B>Pre-preparation:</I></B><BR>1. Soak mushrooms in warm water for thirty minutes. Then squeeze, retaining 1 cup of mushroom water, and cut off their stems.<BR>2. Peel lotus root and slice it thinly. Keep it in cold water to prevent darkening.<BR>3. Blanch snow peas for two minutes, drain, rinse in cold water, re-drain, and reserve.<BR>4. Mix cornstarch reserved mushroom water, wine, and half of the sugar.<BR><B><I>Preparation:</I></B><BR>1. Arrange mushrooms, then lotus root slices in shallow dish that fits in steamer. Cover with paper towel to absorb water, then steam for thirty minutes.<BR>2. Boil cornstarch mixture until clear and pour over the lotus root mixture.<BR>3. Arrange snow peas around this and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8767A0A43510834FAC8EF6E6FBA1A31C', X'742DBC3B18BE624F844DF712B14490B4', X'742DBC3B18BE624F844DF712B14490B4', 0, 'javascript:', 'Vegetarian Wraps', 2, NULL, 0, 'Vegetarian Wraps', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=120', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>3 dried shiitake mushrooms<BR>2 carrots, peeled and shredded<BR>1 cup vegetable (or chicken) stock<BR>1/2 teaspoon sesame oil<BR>1 teaspoon soy sauce<BR>1 large nappa cabbage, about 1 pound<BR>1/2 teaspoon salt<BR>1/2 teaspoon sesame oil<BR>1 Tablespoon shredded ginger<BR>1 teaspoon cornstarch<BR><B><I>Preparation:</B></I><BR>1.Soak black mushrooms in warm water until soft. Discard the stems, then thinly slice them. <BR>2. Peel the carrots and shred them.<BR>3. Braise both in a sauce of stock, sesame oil, and soy sauce for ten minutes, then drain and set aside, reserving the liquid. Keep it warm.<BR>4. Boil a large pot of water.<BR>5. Wash the nappa cabbage, separate twelve outer leaves and put them into the boiling water a few at a time for 1 minute, then drain. If coarse end is very thick, after draining then, cut away the thickness by slicing it in half.<BR>6. Shred the remaining leaves and put them into the reserved hot stock and let them sit there for five minutes, then drain.<BR>7. Mix the shredded mushrooms, carrots, and shredded cabbage with salt, ginger root and sesame oil. <BR>8. Place one twelfth of the shredded mixture or a limp cabbage leaf and roll from the thick end to the thin to form a cylinder. Place on a plate, seam side down.<BR>9. Steam the rolls for ten minutes, remove and serve. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8C8326E651F0D74C9BDAFBD1F8618D13', X'03F494C0BE100948B0E8B148F8CCAE3F', X'03F494C0BE100948B0E8B148F8CCAE3F', 0, 'javascript:', 'Winter Melon Pumpkin Chips', 2, NULL, 0, 'Winter Melon Pumpkin Chips', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1669', '<TD><RECIPE><B><I>Ingredients: </B></I><BR>1/2 pound fresh winter melon, peeled and sliced into one-eight inch thin slices, each about an inch square<BR>1/2 pound fresh pumpkin, peeled and sliced into one-eighth-inch thin slices, each about an inch square<BR>1/2 cup flour<BR>1 Tablespoon sugar<BR>1/4 teaspoon salt<BR>1 Tablespoon white and black sesame seeds<BR>2 cups vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Toss winter melon and pumpkin pieces in the flour, sugar, and salt. Let them rest for ten minutes, then toss them again. Discard any extra flour mixture.<BR>2. Heat oil in a deep pot or wok, and put about one half of the vegetable pieces in to deep fry. When tan and crisp, remove and drain them on paper towels. Repeating with the remaining slices.<BR>3. Immediately or when almost ready to serve, put the vegetable chips on a rack on a baking sheet and into a 500 degree oven for five minutes, then into a serving bowl.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'112EBEF7CA9D0B449EBBB6DDEEB76B99', X'0BDD3C35B2F2F546A81FD15E90C76AF5', X'0BDD3C35B2F2F546A81FD15E90C76AF5', 0, 'javascript:', 'Winter Melon with Nira', 2, NULL, 0, 'Winter Melon with Nira', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=222', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 Tablespoon corn oil<BR>1/4 teaspoon each minced fresh ginger, garlic, and shallots<BR>1 Tablespoon dried shrimp, soaked for ten minutes, then drained and minced<BR>12 ounces winter melon, peeled and seeded and cut into two-inch squares<BR>1/2 teaspoon rice wine<BR>1 teaspoon low sodium bouillon<BR>1/2 teaspoon low sodium soy sauce<BR>1/2 teaspoon sugar<BR>1/2 Tablespoon corn oil<BR>1 bunch Chinese nira or chives (about one-quarter pound)<BR>1 hot chili pepper, seeded and minced<BR>1/2 teaspoon cornstarch mixed with one tablespoon cold water<BR><I><B>Preparation:</I></B><BR>1. Heat the corn oil and fry the garlic, ginger, shallots, and dried shrimp for half a minute.<BR>2.Add the wintermelon, wine, bouillon, soy sauce, sugar and one tablespoon of water. Cover and simmer for ten minutes, then uncover, remove from the heat, and set aside.<BR>3. Heat the remaining oil and stir-fry the nira and the chili pepper for one minute before adding the winter melon mixture.<BR>4. Stir the cornstarch mixture, add it, and stir until the sauce thickens, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8C9703B77132374A9A88C1E6A878F41C', X'DBD6A83DE4D85649835700CE113E49EE', X'DBD6A83DE4D85649835700CE113E49EE', 0, 'javascript:', 'Wintermelon and Egg Whites', 2, NULL, 0, 'Wintermelon and Egg Whites', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1674', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound winter or fuzzy melon, peeled, seeded, and coarsely grated<BR>1 teaspoon fresh peeled ginger, coarsely grated<BR>2 teaspoons Chinese rice wine<BR>1 teaspoon chicken fat or lard<BR>2 shallots, peeled and minced<BR>2 cups chicken stock<BR>1 Tablespoon cornstarch mixed with one Tablespoon of cold water<BR>4 egg whites, beaten until foamed and almost stiff<BR>2 teaspoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix melon, ginger, and rice wine and set aside in a heat-proof bowl for fifteen minutes, then steam them in that bowl over boiling water for fifteen minutes more.<BR>2. Heat chicken fat or lard and brown the shallots for three minutes until they start to caramelize.<BR>3. Add melon to shallot mixture and the stock and bring to the boil, then add cornstarch mixture and stir until it thickens. Then add egg whites and the sesame oil, and stir for one minute. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D37DACCC92F9D448ACD2934EAF15199D', X'48F47E5178FC024488A4CFEBCC6D092D', X'48F47E5178FC024488A4CFEBCC6D092D', 0, 'javascript:', 'Wok-seared Spinach', 2, NULL, 0, 'Wok-seared Spinach', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1254', '<TD><RECIPE>Wok-seared Spinach <B><I>Ingredients:</B></I><BR>1 pound spinach or amaranth, washed three times, then drained<BR>2 Tablespoons vegetable oil<BR>2 garlic cloves, peeled and crushed<BR>1/2 teaspoon salt<BR>1 teaspoon sugar<BR><B><I>Preparation:</B></I><BR>1. Separate large spinach leaves from their stalks.<BR>2. Heat wok or fry-pan, add the oil, the garlic, and the salt and when it sizzles, add the greens and stir-fry them until they begin to wilt.<BR>3. Add the sugar, and continue to stir-fry another minute or two, then spread on a shallow platter and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3C36D81E25FC9C42857556EE98139DB7', X'56A7CF6CB94C99478FF248CF58E4F068', X'56A7CF6CB94C99478FF248CF58E4F068', 0, 'javascript:', 'Xian-style Eggplant and Pomegranate', 2, NULL, 0, 'Xian-style Eggplant and Pomegranate', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=484', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 long thin eggplants<BR>3 Tablespoons corn oil<BR>2 medium onions, peeled and thinly sliced<BR>1 large tomato, peeled and sliced<BR>3 Tablespoons pomegranate molasses<BR>2 teaspoons salt<BR>1 teaspoon sugar<BR>1 Tablespoon sesame oil<BR>1/2 teaspoon ground black pepper<BR>1/2 cup chopped fresh mint leaves<BR>1 teaspoon of a mixture of cumin seeds, fennel seeds, nigella seeds, and/or mustard seeds, optional<BR><B><I>Preparation:</B></I><BR>1. Cut eggplant into one-inch cubes.<BR>2. Heat oil and fry the onions and garlic until the onions are translucent. Then add the eggplant and fry an additional tem minutes before adding the tomato, pomegranate molasses, salt, sugar, pepper, and mint. Reduce heat and simmer this for ten minutes. If using the optional seeds, stir them in when adding the tomato mixture.<BR>3. Add the sesame oil, and stir well. Serve hot or cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8950C6CB3ADDBE4B8E5D624FCEB511ED', X'7A75C17C9C2D8C44BCC35BC638542009', X'7A75C17C9C2D8C44BCC35BC638542009', 0, 'javascript:', 'XO Sauce with Tofu', 2, NULL, 0, 'XO Sauce with Tofu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=248', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 pieces firm tofu<BR>1/2 piece silken tofu<BR>2 Tablespoons XO sauce<BR>1/4 teaspoon salt<BR>1 teaspoon sesame oil<BR>2 eggs, beaten until light<BR>1/2 cup chicken or vegetable broth<BR>2 Tablespoons minced Chinese chives<BR>2 Tablespoons peanut or corn oil<BR>1 Tablespoon cornstarch mixed with one tablespoon water<BR>4 Tablespoons minced coriander<BR><I><B>Preparation:</I></B><BR>1. Cut firm tofu into one-inch pieces and mash the silken tofu mixing both with the XO sauce, salt, and sesame oil. Then add the beaten eggs, broth, and chives.<BR>2. Heat peanut oil and add the egg mixture and stir as it starts to set. Then stir in the cornstarch mixture and the coriander and cook until it thickens, about a minute--but no more than two. Serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3A1E60B32DB35D46BAC201AF781FDD50', X'FEF567D3386734439A3950604742DC52', X'FEF567D3386734439A3950604742DC52', 0, 'javascript:', 'XO Sauced Cucumber and Bitter Melon', 2, NULL, 0, 'XO Sauced Cucumber and Bitter Melon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=641', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 bitter melon, seeded, white membrane removed, cut in fourths the long way, then cut into two-inch lengths about half-inch wide<BR>1 cucumber, cut in quarters and cut to match bitter melon<BR>1/2 carrot, peeled and slivered<BR>1 clove garlic, minced<BR>1/2 teaspoon salt<BR>1 dried red chili, seeded, and crushed<BR>2 Tablespoons XO sauce<BR>1/2 Tablespoon corn oil<BR>2 slices fresh ginger, slivered<BR>1 Tablespoon sugar<BR>1 Tablespoon sesame oil<BR>1 teaspoon mushroom soy sauce<BR><B><I>Preparation:</B></I><BR>1. Soak bitter melon in ice water for half an hour, then blanch it for two minutes, and drain.<BR>2. Mix bitter melon, cucumber, carrot, garlic, salt, chili pepper pieces, and XO sauce and set aside for thirty minutes, then drain any liquid and discard.<BR>3. Heat corn oil and fry ginger for half minute. Add vegetable mixture and stir-fry until cucumber and bitter melon are wilted and soft. Add sugar, sesame oil, and mushroom soy, fry another minute, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'574A907203BA5E46863618FA68501110', X'B37E31DBBDA92246A04CB508D7289B24', X'B37E31DBBDA92246A04CB508D7289B24', 0, 'javascript:', 'Yam Balls', 2, NULL, 0, 'Yam Balls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=283', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound of yams (or sweet potatoes)<BR>1/2 pound glutinous rice flour or yam flour<BR>1/2 pound red bean paste<BR>2 Tablespoons additional glutinous rice flour<BR>2 cups corn oil for frying<BR><I><B>Preparation:</I></B><BR>1. Steam yams, then mash them when hot and mix them with the flour and knead. as you would a dough, until smooth. Divide into twenty-four pieces, and flatten each of them.<BR>2. Put one tablespoon of bean paste on each yam dough circle. Wrap it into a ball hiding all the bean paste within. Continue until all are made, then dust them with the additional flour.<BR>3. Heat oil and deep fry the balls until golden, drain, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'12CD9E53FD02FC4FA3A84C1998A1C8E5', X'8F2BDD891ED9784EB90DBF40D64FB899', X'8F2BDD891ED9784EB90DBF40D64FB899', 0, 'javascript:', 'Yam Cakes', 2, NULL, 0, 'Yam Cakes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1712', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Chinese sausage, minced<BR>4 Tablespoons dried shrimp, soaked for one hour in warm water, then drained, and minced<BR>1 Tablespoon vegetable oil<BR>2 pounds peeled Chinese yams, diced in quarter-inch cubes, then steamed for ten minutes over boiling water<BR>1 cup water chestnut flour<BR>1/2 cup glutinous rice flour<BR>1 teaspoon salt<BR>2 Tablespoons thin soy sauce<BR>2 Tablespoons sesame oil, divided into two parts<BR>1 teaspoon mixed ground white and black pepper<BR>2 Tablespoons black bean chili sauce<BR>2 scallions, minced, and divided into two parts<BR><B><I>Preparation:</B></I><BR>1. Mix sausage and shrimp.<BR>2. Heat oil in a wok or a fry-pan and stir-fry the above mixture for three minutes, then drain and set aside.<BR>3. Mash steamed yam cubes then mix this with both flours, salt, soy sauce, half the sesame oil, both ground peppers, black bean chili sauce, and half the minced scallions. Stir-fry this mixture in the same wok or fry pan for two minutes.<BR>4. Oil a heat-proof casserole, brush with the other half of the sesame oil, and add the yam mixture, and top with the other half of the scallion, pressing them lightly into it.<BR>5. Steam for fifteen minutes over boiling water, remove from the steamer and cool covered and in the refrigerator overnight.<BR>6. Cut into wedges or triangle, and serve at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'35AD55557B34704493DC435CA9EAB3F9', X'3FE3A0674D533840802D661281BC0F1F', X'3FE3A0674D533840802D661281BC0F1F', 0, 'javascript:', 'Yao Chili Mushrooms', 2, NULL, 0, 'Yao Chili Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1121', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons dried cloud ear mushrooms, soaked, and coarsely chopped<BR>2 Tablespoons dried wood ear mushrooms, soaked, and coarsely chopped<BR>2 Tablespoons dried Chinese black mushrooms, soaked, and coarsely chopped<BR>2 Tablespoons dried swordbelt mushrooms, soaked, and coarsely chopped<BR>2 Tablespoons dried abalone mushrooms, soaked, and coarsely chopped<BR>2 Tablespoons vegetable oil<BR>2 wild onions, cut into half-inch pieces<BR>1/2 carrot, peeled and diced<BR>2 Tablespoons corn kernels<BR>1 teaspoon chili paste with garlic<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix these mushrooms (or substitute others or add to this mix).<BR>2. Heat a wok or fry pan, add the oil, and then stir-fry the mushrooms for three to five minutes before adding the onions, carrots, and kernels of corn. Continue to stir-fry another two minutes before adding the chili paste and three tablespoon of cold water and then bring this to the boil.<BR>3. Add the sesame oil, stir once, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune 4 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FFBBFCB92C95914BBA879D48E5A851CE', X'0B7D6DEFB2A1D344BB34AF5A4C431EB9', X'0B7D6DEFB2A1D344BB34AF5A4C431EB9', 0, 'javascript:', 'Yunnan Cabbage and Potatoes', 2, NULL, 0, 'Yunnan Cabbage and Potatoes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=512', '<TD><RECIPE><B><I>Ingredients for the cabbage:</B></I><BR>1 Tablespoon corn oil<BR>1 small savoy or Chinese cabbage, about a pound, chopped finely<BR>1 slice pork butt or bacon, about two ounces, cut into one-quarter-inch pieces<BR>3 cloves garlic, minced<BR>1 hot red chili, seeds removed and mnced<BR>1 star anise<BR>1 Tablespoon thin soy sauce<BR><I><B>Preparation for the cabbage:</B></I><BR>1. Heat oil and stir-fry the cabbage and the pork or bacon pieces for about three or four minutes.<BR>2. Add the rest of the ingredients to the cabbage mixture and cook until the cabbage is soft and has lost its crunchy texture. Put this on half of a deep serving platter. Then prepare the potato mixture.<BR><B><I>Ingredients for the potatoes:</B></I><BR>1 pound potatoes, peel and cut them into half-inch diced pieces<BR>2 Tablespoons corn oil<BR>1 cup of a mixture of chives, scallions, and garlic shoots, minced<BR>1 teaspoon thin soy sauce<BR><I><B>Preparation for the potatoes:</B></I><BR>1. Boil potatoes for about six or seven minutes, until almost fully cooked. Drain and toss in hot pot to be sure to dry the pieces.<BR>2. Heat oil and fry the chive mixture about two minutes, then add the potatoes and cook this another two minutes, stirring at all times. Put out on the other half of the deep serving platter. Then serve.<BR></TD>');
