INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2BD1CE1E88BA6546BC9BED42A22B8DFD', X'0AB83F26380F27439769B525120F6F38', X'0AB83F26380F27439769B525120F6F38', 0, 'javascript:', 'Fish with Bitter Melon', 2, NULL, 0, 'Fish with Bitter Melon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1057', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>3 cloves of garlic, peeled and smashed with the flat blade side of a cleaver<BR>1 small onion, cut top to root in half-inch sections<BR>1 pound firm bean curd, cut into two-inch cubes<BR>2 bitter melons, seeds and pulp discarded, sliced into half-inch circles<BR>1/2 pound of any skinless and boneless white fish fillet<BR>1 Tablespoon minced peeled fresh ginger<BR>1/2 teaspoon coarse salt<BR>1 cup chicken or fish broth or water<BR>2 teaspoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan and add the oil, then the garlic and onion pieces and stir-fry for one minute, before adding the bean curd. Let that fry until golden brown, then turn bean curd over to fry on its other side.<BR>2. Sprinkle bitter melon pieces on the bean curd and top with the fish fillet; then scatter ginger and salt over this, and pour the broth down the side of the wok or pan, and cover it and simmer for three minutes before removing the cover and adding the sesame oil.<BR>3. Transfer to a pre-heated bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8BBEBC0C80D6234D9D80CF7DEE46C4FA', X'4B82381B77C80246BA6CAD4D7CB0E6D8', X'4B82381B77C80246BA6CAD4D7CB0E6D8', 0, 'javascript:', 'Fish with Black Bean Sauce', 2, NULL, 0, 'Fish with Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1877', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 small fish such as snapper or trout<BR>3 Tablespoons vegetable oil<BR>2 hot chili peppers, cut in half, seeded then chopped<BR>2 Tablespoons fermented black beans, rinsed and slightly mashed, if desired<BR>5 cloves fresh garlic, peeled and chopped<BR>3 Tablespoons thin soy sauce<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon salt<BR>1 Tablespoon granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Scale and clean the fish, remove the gills and entrails and discard them. Then rinse and pat the fish dry with paper towels.<BR>2. Heat wok or fry pan, add the oil and the fish and fry it on one side for two minutes, turn the fish over and do the same on the other side, then remove it to a pre-heated platter, and serve.<BR>3. Stir in the chili peppers and the black beans, then the garlic, and stir-fry for one minute before adding the soy sauce, rice wine, salt, and sugar. Continue stirring, then return the fish to the pan, cover, and simmer for two minutes. Remove the cover, put the fish on a pre-heated platter, then bring the remaining sauce to the boil and pour it over the fish, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'502BC56CB9D102489C6739E3F0D3C456', X'FA07ECAC7811A34CB68D4849986B99F0', X'FA07ECAC7811A34CB68D4849986B99F0', 0, 'javascript:', 'Fish with Egg White Puffs', 2, NULL, 0, 'Fish with Egg White Puffs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2258', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound of whitebait or any white fish<BR>6 egg whites<BR>1½ teaspoons coarse salt<BR>1 Tablespoon <I>Shao Xing</I> rice wine<BR>1/4 cup fresh or tinned milk<BR>a dash of ground pepper<BR>1 cup fish or chicken stock<BR>1 cup vegetable or soy bean oil<BR>½ teaspoon minced shallots<BR>½ teaspoon minced fresh ginger<BR><B><I>Preparation:</B></I><BR>1. Clean and discard heads, tails, scales, and guts of the fish; then rinse and dry them with paper towels before mashing them to a pulp.<BR>2. Whip egg whites with salt, wine, milk, pepper, stock, and the cornstarch until stiff.<BR>3. Heat wok or fry pan, add a two tablespoons of the oil, and when hot, add one quarter cup of the fish mixture and flatten it making a square or round pancake-shaped item. When light tan and set, turn it over, add a few sprinkles of the shallots and ginger, and allow this side to get light tan, too.<BR>4. Remove this cooked pancake to a paper-towel-lined pre-heated platter. Use the rest of the fish mixture the same way, then discard the paper towel, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C325B76A63CF6A4FA83F538ECF5C99E5', X'661621964D978D4F90C88C392A7BC406', X'661621964D978D4F90C88C392A7BC406', 0, 'javascript:', 'Fish with Mustard Seeds', 2, NULL, 0, 'Fish with Mustard Seeds', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=782', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 two to three pound sea bass, scaled and gutted<BR>6 black mushrooms, soaked for twenty minutes, stems removed<BR>3 Tablespoons thin soy sauce<BR>2 tablespoons Chinese rice wine<BR>1 teaspoon sugar<BR>1 Tablespoon whole mustard seeds<BR>1 Tablespoon cornstarch mixed with one cup chicken broth<BR>1/2 cup corn oil<BR><B><I>Preparation:</B></I><BR>1. Rinse and dry the fish, and make three or four slits partway down to the bone on each side.<BR>2. Cut mushrooms in quarters, if large, in half if small, and set aside.<BR>3. Mix soy sauce, rice wine, sugar, mustard seeds, and cornstarch mixture and stir well.<BR>4. Heat oil and fry fish on each side for one to two minutes, then remove from oil, set aside, and discard the oil.<BR>5. Put mushrooms, and the sauce mixture into the wok and gently slide in the fish spooning sauce over it. Reduce heat, cover, and simmer twenty minutes, spooning some liquid over the fish every five minutes. Then gently remove to deep serving platter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D8503063918F8B419518612EBCD0DEF0', X'208E8C29DAF48840AA353689A512423F', X'208E8C29DAF48840AA353689A512423F', 0, 'javascript:', 'Fish with Pickled Greens I', 2, NULL, 0, 'Fish with Pickled Greens I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1250', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 egg white<BR>1/4 teaspoon salt<BR>2 Tablespoons cornstarch<BR>1/2 pound boneless and skinless carp, sliced thinly<BR>1 cup oil<BR>2 ounces pickled mustard greens, cut into one-inch by two-inch strips, then rinsed in warm water, and drained well<BR>1/4 cup pickled or plain bamboo shoots, cut very thin<BR>2 Tablespoons Chinese rice wine<BR>1/2 cup chicken broth<BR>1 Tablespoon lard or rendered chicken fat<BR>1/2 teaspoon <I>zao</I> pepper or any pepper flakes or a half-teaspoon home-made chili paste<BR><B><I>Preparation:</B></I><BR>1. Mix egg white, salt, and cornstarch, and gently mix with the slices of carp. Drain and discard excess coating, should there be any.<BR>2. Heat oil, then deep fry the fish slices, when golden remove from the oil and drain on paper towels. Reserve one tablespoon of the oil, and save the rest for another use.<BR>3. Heat the reserved oil, add the mustard greens and stir-fry for one minute, then add the bamboo shoots and cook another half minute before adding the wine, chicken broth, and the lard. When hot (about half minute, add the fish, stir carefully so as not breaking the fish in pieces, then serve sprinkling the fish with the flakes of hot pepper.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5E8B8468CB92A741A8E57A8FE3B8E775', X'944003CB0E707E4F9B69EC7205C000C1', X'944003CB0E707E4F9B69EC7205C000C1', 0, 'javascript:', 'Fish with Pickled Greens II', 2, NULL, 0, 'Fish with Pickled Greens II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1463', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 egg white<BR>1/4 teaspoon salt<BR>2 Tablespoons cornstarch<BR>1/2 pound boneless and skinless carp, sliced thinly<BR>1 cup vegetable oil<BR>2 ounces pickled mustard greens, cut into one-inch by two-inch strips, then rinsed in warm water, and then drained well 1/4 cup pickled or plain bamboo shoots, cut very thin<BR>1/2 cup chicken broth<BR>1 Tablespoon lard<BR>1/3 teaspoon Zao pepper flakes or 1/2 teaspon homemade chili paste<BR><B><I>Preparation:</B></I><BR>1. Mix egg white, salt, and cornstarch, and gently mix with the sloces of carp. Remove any excess coating.<BR>2. Heat oil, they deep-fry the fish slices, and when golden remove from the oil and drain on paper towerls. Reserve one tablespoon of the oil, and save the rest for another use.<BR>3. Heat the resrved oil, and add the mustard greens and stir-fry for one minute, then add the bamboo shoots and cook aother half minute before adding the wine, chicken broth, and the lard. When hot, about half a minute, add the fish and stir carefully not to break it into pieces. Serve sprinking the fish with the flakes of the Zao pepper or the chili paste.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'55B2E9516C22A94A82A1E9FDA31DD556', X'A3C2F887BB88104BA4AE8B6B9CE5C563', X'A3C2F887BB88104BA4AE8B6B9CE5C563', 0, 'javascript:', 'Fish with Red Wine Lees', 2, NULL, 0, 'Fish with Red Wine Lees', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=446', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound of fish fillets, cut into four pieces<BR>2 Tablespoons sweet potato flour<BR>1/4 teaspoon salt<BR>1/4 teaspoon sugar<BR>3 tablespoons red wine lees<BR>2 Tablespoons corn oil<BR>1 Tablespoon minced ginger<BR><B><I>Preparation:</B></I><BR>1. Sprinkle fish with sweet potato flour, salt, and sugar.<BR>2. Brush all sides of the fish with the red wine lees.<BR>3. Heat oil and fry the ginger for one minute, add the fish and continue frying, about two minutes for the first side and one minute for the second one until the fish is almost cooked through. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'397D13B348D20F429ED6D6422BF637E9', X'5E2D8BA115D29B4D8B25B2DB970EEC81', X'5E2D8BA115D29B4D8B25B2DB970EEC81', 0, 'javascript:', 'Fish with Sour Vegetables', 2, NULL, 0, 'Fish with Sour Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1727', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>1 pound boneless fish filets, cut into four-inch pieces<BR>1 recipe cooked Sour Guizhou Vegetables<BR><B><I>Preparation:</B></I><BR>1. Heat wok or heavy fry-pan, then add the oil and when it is hot, add the pieces of fish, and fry for one minute.<BR>2. Turn them over and fry for another minute, then remove to a pre-heated serving plate.<BR>3. Top with the hot vegetables, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A684C3DF4E871D448138EEA5E11731AF', X'1A82ECA9AB8AAF4F902FF2AE01056824', X'1A82ECA9AB8AAF4F902FF2AE01056824', 0, 'javascript:', 'Fish with Spicy Bean Sauce', 2, NULL, 0, 'Fish with Spicy Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=115', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 and a half pound sea bass or porgy<BR>1 teaspoon salt<BR>1/3 cup vegetable oil<BR>1/2 to 1 Tablespoon spicy chuili bean sauce<BR>3 slices ginger, shredded<BR>1 scallion<BR>1 teaspoon cornstarch<BR><I><B>Preparation:</B></I><BR>1. Slit the fish diagonally, about one to one and a half inches apart, then rub it with salt inside and out.<BR>2. Place the fish on aluminum foil in pan for broiling, and pour the oil over the fish. Then cover the pan tightly with aluminum foil.<BR>3. Cut the scallion into one- to two-inch pieces.<BR>4. Mix both sauces with the ginger, scallion, and cornstarch and stir in one-quarter cup water and set aside. If you want a spicy dish, add the extra chili sauce here.<BR>5. Broil the fish about twenty-five minutes, turn it over, and broil twenty minutes more. Remove the foil immediately.<BR>6. Place the sauce mixture in a pot and cook until thickened, then pour it over the fish and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'005EB2E01D5C8444A156C53758F498A3', X'5487201D65E0A346960489BF71D7CBB0', X'5487201D65E0A346960489BF71D7CBB0', 0, 'javascript:', 'Fish with Tree Seeds', 2, NULL, 0, 'Fish with Tree Seeds', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1572', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon sesame oil<BR>4 small whole fish, gutted, scales removed, and dried with paper towel<BR>1 Tablespoon Chinese rice wine<BR>1 small can or jar pickled cucumber, liquid reserved<BR>1 small can or jar tree seeds, liquid reserved<BR>2 Tablespoons shredded pickled ginger<BR>1/2 teaspoon coarse salt<BR>1 scallion, coarsely minced<BR><B><I>Preparation:</B></I><BR>1. Spread oil on a heat-proof flat bowl with sides.<BR>2. Brush fish, inside and out, with wine, and put it on the platter.<BR>3. Mix three tablespoons liquid from the pickled cucumbers and three tablespoons of liquid from the tree seeds, the ginger and salt and pour this over the fish.<BR>4. Mince three tablespoons pickled cucumber and sprinkle on top of the fish. 5. Steam for ten minutes over boiling water. Drain and discard half the liquid, sprinkle what is left with scallion, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5593DC91021C7040870ABDB170AC5548', X'F42C7A98162C784EBC9903ADDEAA6BE7', X'F42C7A98162C784EBC9903ADDEAA6BE7', 0, 'javascript:', 'Fish Wrapped in Celtuce Leaves', 2, NULL, 0, 'Fish Wrapped in Celtuce Leaves', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1872', '<TD><RECIPE><B><I>Ingredients: </B></I><BR>1 pound boneless and skinless butter fish or yellowtail<BR>3 Tablespoons Jinhua or Smithfield ham, minced<BR>1 Tablespoon chicken fat<BR>1/2 teaspoon sesame oil<BR>1/2 teaspoon salt<BR>1/4 teaspoon ground white pepper<BR>1/2 scallion, minced<BR>8 large celtuce leaves<BR>1/2 egg, beaten<BR>1 cup vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Cut fish in finger-size pieces.<BR>2. Mix ham and chicken fat with the sesame oil, salt, pepper, and minced scallion, and spread this on top of the pieces of fish that are wrapped in a leaf and sealed with a little egg.<BR>3. Heat half the oil in a wok or fry pan and fry one-half of the wrapped pieces for four minutes, then drain them on paper towels and put them in a low oven to keep them warm. Repeat until all the wrapped fish are fried, then serve them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5756C75376142C49AB88321DB7EDD03D', X'D2BF6A006C16AD4591444289702367B9', X'D2BF6A006C16AD4591444289702367B9', 0, 'javascript:', 'Fish, Olive Kernels, and Mushrooms', 2, NULL, 0, 'Fish, Olive Kernels, and Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=921', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound any firm-fleshed white fish<BR>1/2 teaspoon salt<BR>1/2 teaspoon ground white pepper<BR>1 egg white<BR>2 teaspoons water chestnut or lotus root flour<BR>3 Tablespoons vegetable oil<BR>3 ounces olive kernels<BR>1/2 cup angle-sliced Chinese celery<BR>6 slices fresh ginger, peeled and slivered<BR>3 cloves garlic, peeled and sliced thinly<BR>6 black mushrooms, soaked in one cup water, stems removed, mushrooms quartered, the liquid reserved<BR>1/2 carrot, peeled and angle-sliced thinly<BR>2 scallions, angle-sliced thinly<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon sesame oil<BR>1 teaspoon thin soy sauce<BR>1/2 teaspoon sugar<BR>1 Tablespoon cornstarch mixed with two tablespoons reserved mushroom water<BR><B><I>Preparation:</B></I><BR>1. Mince fish, then add salt, pepper, egg white, water chestnut flour, and one tablespoon mushroom water. Stir until very sticky.<BR>2. Brush a small amount of the oil on a steam-proof plate. Put the minced fish mixture on it making it into a rectangle no more than one-inch thick. Then steam this over boiling water for ten minutes. Remove, discarding any liquid on the plate. When cool, cut into three pieces across the width. Then make two slashes in each fish piece, lengthwise not cutting all the way to each end. Next twist each fish section as if it were dough making it into a twisted piece of fish.<BR>3. Heat the oil in a wok or pan and fry the olive kernels until they turn golden. Remove them to drain on a paper towel, and leave oil in the wok or pan.<BR>4. Heat half of the remaining oil, setting the other half aside, and fry the celery until it just begins to soften. Set the celery on a strainer over a bowl.<BR>5. Heat the remaining oil in the wok and fry the ginger and garlic for one minute, then add the mushrooms and fry for another minute. Add the twisted fish pieces, carrot and scallion slivers, and stir once before adding the rice wine, sesame oil, soy sauce, sugar, and olive kernels. When that comes to the boil, add the cornstarch-mushroom water mixture and stir for one minute until thickened. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'80E98000C221C941AEB133C86055CD1B', X'5CF22042EE64244D84E7671C628CA92E', X'5CF22042EE64244D84E7671C628CA92E', 0, 'javascript:', 'Five w/Five Oyster Cake, Fujian Style', 2, NULL, 0, 'Five w/Five Oyster Cake, Fujian Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=298', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>5 Tablespoons oysters, cut in half if large<BR>5 Tablespoons shredded carrots<BR>5 Tablespoons shredded Chinese celery cabbage<BR>5 cloves garlic, minced<BR>5 scallions, minced<BR>1 teaspoon soy sauce<BR>1/2 teaspoon sugar<BR>1/4 teaspoon ground white pepper<BR>1/2 cup all-purpose flour<BR>1/2 cup cornstarch<BR>2 tablespoons water<BR>1 cup corn oil <BR><I><B>Preparation:</I></B><BR>1) Combine oysters, carrots, cabbage, garlic, scallions, soy sauce, sugar, and five-spice powder. <BR>2) Mix flour, cornstarch, and water and then mix with the oyster-vegetable mixture.<BR>3) Heat oil, immerse the ladle in the oil until it is hot, then fill the ladle almost full with some of oyster mixture. Immediately hold it just under the surface of the oil until set, then tip it out and fry the cake until golden, turning it several times. Drain and put into a warm, not a hot oven, until all of the rolls are fried.<BR>4) Serve with or without a dipping sauce of your choice or use soy sauce, ginger, and/or garlic.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6ECB7350F3BD16468BA38C703938B2E2', X'DF23F538DBA4A24F9B2D3F6E145F1D73', X'DF23F538DBA4A24F9B2D3F6E145F1D73', 0, 'javascript:', 'Flower-stuffed Sea Cucumbers', 2, NULL, 0, 'Flower-stuffed Sea Cucumbers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2247', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 fresh sea cucumbers, soaked in warm water for half an hour; or dried ones soaked overnight, then drained<BR>2 Tablespoons cornstarch, divided<BR>4 large Chinese black mushrooms, soaked for half an hour in warm water, every stem discarded, then mince two of them<BR>1 banana blossom, half minced, the other half sliced, or a small red pepper, seeded and slivered and the minced half dusted with corn starch<BR>1/4 pound finely minced or ground pork<BR>1/4 pound fish paste or any minced white boneless ad skinless fish<BR>3 water chestnuts, finely minced<BR>1 Tablespoon chicken broth powder<BR>½ teaspoon mixed salt and ground pepper<BR>1 teaspoon sesame oil<BR>1 Tablespoon vegetable oil<BR>4 cloves fresh garlic, peeled and sliced<BR>4 slices fresh ginger, peeled and sliced<BR>1 cup chicken broth<BR>1 Tablespoon oyster sauce<BR><B><I>Preparation&gt;</B></I><BR>1. Remove innards from sea cucumbers, and dust them with half of the cornstarch.<BR>2. Mix minced black mushrooms, mince the banana blossom and dust it with the other half of the corn starch, then add the minced pork, fish paste, minced water chestnuts, chicken broth powder, salt and ground pepper, and divide this mixture in half. Stuff each part into one of the sea cucumbers.<BR>3. Heat vegetable oil in a wok or fry pan, and stir-fry the garlic and ginger for one minute, then add the stuffed sea cucumbers, seam side down, and the chicken broth and the oyster sauce and the two whole black mushrooms, and bring all to the boil, then reduce the heat and simmer for half an hour or until the sauce thickens. Set the two mushrooms aside.<BR>4. Put each sea cucumber on a serving platter cut each one, slicing it into half to three-quarters of an inch slices and gently move them apart. Then sprinkle the sliced banana blossoms over them and put one mushroom on each sea cucumber and spoon the sauce on top. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E5331B9351E9D84895F3808296DAD243', X'D4EEC5C744C8A64EBA9E867795CF293D', X'D4EEC5C744C8A64EBA9E867795CF293D', 0, 'javascript:', 'Four Seasons Seafood Platter', 2, NULL, 0, 'Four Seasons Seafood Platter', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=492', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1/4 cup peanut oil<BR>1/4 pound minced or ground pork<BR>1 teaspoon salted black beans, very lightly mashed<BR>1 clove fresh garlic, minced<BR>1/2 cup chicken stock<BR>4 slices fresh ginger, slivered<BR>1/2 teaspoon sesame oil<BR>1 Tablespoon thin soy sauce<BR>2 scallions, angle-sliced very thin<BR>1 teaspoon cornstarch mixed into one tablespoon cold water<BR>12 large shrimp, peeled and veins removed, and simmered for one minute just before making the sauce<BR>1 cup backfin crab meat, cartilage removed, simmered for one minute just before making the sauce<BR>2 cups lobster chunks, simmered for one minute just before making the sauce<BR>1 egg, beaten<BR><B><I>Preparation:</B></I><BR>1. Prepare sauce heating peanut oil and stir-frying pork, black beans, and garlic for three minutes. Then stir in chicken stock, ginger, sesame oil, and soy sauce and bring to the boil. Add cornstarch water and remove from the heat.<BR>2. Stir in the cooked sea foods and return pan to the heat, add egg, stir gently for one minute, then serve on a pre-heated platter.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8A4647F23AA23C419976351CF1EAF92E', X'1BE04A6B953DB2408D9A55973551C171', X'1BE04A6B953DB2408D9A55973551C171', 0, 'javascript:', 'Fragrant Curry Squid Chan Chen Hai', 2, NULL, 0, 'Fragrant Curry Squid Chan Chen Hai', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=868', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound very fresh squid, cleaned first, then cut into bite-sized pieces<BR>2 teaspoons curry powder<BR>1 teaspoon chili powder<BR>2 petals ginger bud, minced<BR>2 to 3 slices of fresh ginger, peeled and minced<BR>3 cloves garlic, peeled and minced<BR>1 Tablespoon minced leek<BR>1 scallion, minced<BR>1/2 teaspoon fresh chili pepper<BR>1/2 teaspoon chopped fresh mint<BR>1/2 teaspoon coarsely ground green peppercorns (optional)<BR>1/4 teaspoon cornstarch<BR>1 teaspoon spicy bean paste<BR>1/2 teaspoon salt<BR>1 teaspoon sugar<BR><B><I>Preparation:</B></I><BR>1. Mix all ingredients, one by one, and then put them into a heat-proof bowl.<BR>2. Put the bowl in a steamer over rapidly boiling water. Steam for four minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0F8B9022BBF80C49A4F10C63B6813929', X'BC27C166FA45BF4195EE20D5EB4C579A', X'BC27C166FA45BF4195EE20D5EB4C579A', 0, 'javascript:', 'Fresh Squid, Stir-fried', 2, NULL, 0, 'Fresh Squid, Stir-fried', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2020', '<TD><RECIPE>2 pounds fresh squid, beak, bones, and cartilage discarded<BR>2 Tablespoons Chinese rice wine<BR>1 Tablespoon mushroom soy sauce <BR>1 Tablespoon soy sauce<BR>1 Tablespoon cornstarch<BR>4 Chinese black mushrooms, soaked, stems discarded, caps sliced thin<BR>1/2 cup chicken broth<BR>2 Tablespoons vegetable oil<BR>3 cloves fresh garlic, peeled and chopped<BR>2 Tablespoons fresh ginger, peeled and chopped<BR>3 shallots, peeled and minced<BR>1 boneless pork chop, finely minced<BR>3 scallions, cut at an angle<BR>1/2 cup bamboo shoots, thinly sliced<BR>1/2 teaspoon coarse salt<BR>1/4 teaspoon ground white epper<BR>1 Tablespoon Chinese white vinegar<BR><I><B>Preparation</B></I><BR>1. Cut squid body into rings, chop the tentacles, and cross-hatch the outer side of the central body.<BR>2. Put squid in boiling water for half or one minute, drain quickly, then add the rice wine, soy sauce, mushrooms, and the broth and cornstarch, stir and then add the pieces of squid. 3. Heat wok or fry pan, add the oil, then the garlic and ginger, and stir-fry for one minute before adding the ginger and the pork and stir-fry another minute, then add the scallions, shallots, and bamboo shoots, and the salt and pepper and the broth mixture. Stir until it thickens before adding the vinegar and stir, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2DCD07AD72C48B44AA7E825870F6C160', X'47B64D64672D5245AA9D5508D84516F2', X'47B64D64672D5245AA9D5508D84516F2', 0, 'javascript:', 'Fresh Stufed Squid', 2, NULL, 0, 'Fresh Stufed Squid', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=716', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound small fresh squid, heads, wings innards, and tail sections removed<BR>1 Tablespoon corn oil<BR>2 cubes brown bean curd, minced<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon Chinese rice wine<BR>½ Tablespoon sugar<BR>1 Tablespoon dried Sichuan vegetable, rinsed well, and minced<BR>1 Tablespoon cornstarch<BR>toothpicks<BR>1 Tablespoon sesame oil<BR>2 Tablespoons mushroom soy<BR>1 teaspoon honey<BR><B><I>Preparation:</B></I><BR>1. Cut small opening, if needed in each squid body so that there is pocket to stuff. Then mince wings and tail meat of the squid.<BR>2. Heat oil and add minced squid, bean curd pieces, soy sauce, rice wine, sugar, and dried vegetable pieces and stir-fry for three minutes, then allow to cool, and then drain any liquid remaining, and mix solids with the cornstarch and stuff into each squid. Do not over-stuff them. Then insert toothpick to hold in the stuffing.<BR>3. Mix mushroom say and honey.<BR>4. Heat sesame oil and fry the squid turning them often until about half cooked, then brush with soy-honey mixture and cook for two minutes, stirring often, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'70FBB43793476F4DBB7FCC34F37BDDB8', X'D5F94DC9361CB9428FF3C7033979896B', X'D5F94DC9361CB9428FF3C7033979896B', 0, 'javascript:', 'Fried Eel, Shanghai Style', 2, NULL, 0, 'Fried Eel, Shanghai Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1750', '<TD><RECIPE><I><B>Ingredients</I></B><BR>2 eels, skin, insides, and bones removed and discarded<BR>2 cups vegetable oil<BR>2 scallions, diced<BR>4 garlic cloves, peeled and coarsely chopped<BR>2 Tablespoons granulated sugar<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon fresh ginger juice<BR>1 Tablespoon soy sauce<BR>10 drops hot oil<BR>1 teaspoon rice vinegar<BR>1 Tablespoon cornstarch mixed with one tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Cut the eel into six inch long pieces, then each of these into four pieces, the long way.<BR>2.Heat the oil in a wok or deep pan and deep-fry the eel until the pieces are crisp, then remove them with a slotted spoon to paper towels. Discard the oil or save it for another purpose. Do not wipe the wok out.<BR>3. Add the scallion and garlic pieces and stir once or twice then add the sugar, rice wine, ginger juice, hot oil, and vinegar and stir two or three times.<BR>4. Add the cornstarch mixture, and the eel pieces and stir until coated, then remove and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A00332EE6F37EB46B8D41910206FF5F4', X'241BBD0710AEA4419BBD12BF596FCE11', X'241BBD0710AEA4419BBD12BF596FCE11', 0, 'javascript:', 'Fried Oysters', 2, NULL, 0, 'Fried Oysters', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1989', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>8 oysters removed from their shell, the shells discarded<BR>1 cup vegetable oil<BR>1/4 cup all purpose flour<BR>1 cup whole, almond, or evaporated milk<BR>1 chili pepper, seeded and slivered<BR>2 Tablespoons spicy pickles, diced finely<BR>2 Tablespoons pomegranate seeds, lemon slices or juice, all optional<BR><B><I>Preparation:</B></I><BR>1. Heat the oil to medium or a bit beyond, then mix flour, oysters milk, the chili pepper and pickle pieces, add pomegranate seeds or juice if using them.<BR>2. Coat oysters and fry them for two or three minutes.<BR>3. Drain on paper towels, and serve with lemon juice or other optional ingredients, as desired. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FC3AEF300F4B2D41B93CE74339080AAA', X'6A61568D12C4444CAB147B888515A3B4', X'6A61568D12C4444CAB147B888515A3B4', 0, 'javascript:', 'Fried Rice and Seaood in a Pineapple', 2, NULL, 0, 'Fried Rice and Seaood in a Pineapple', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1625', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 small squid, cartilage and heads removed, cut into half-inch pieces<BR>1 fresh pineapple, square top removed and discarded, frond left on, fruit hollowed out<BR>3 cups cooled cooked rice<BR>1/4 pound fresh shrimp, peel and veins removed, cut into half-inch pieces<BR>1 medium onion, diced<BR>1/4 cup fresh crab meat, cartilage removed and cut into half-inch pieces<BR>1/2 red and 1/2 green pepper, each cut into half-inch pieces<BR>3 Tablespoons golden raisons, plumped in boiling water<BR>1 Tablespoon vegetable oil<BR>1/2 Tablespoon curry powder<BR>1 teaspoon sugar<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon oyster sauce<BR>1/2 teaspoon ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Drop squid into boiling water and after one minute, remove, put in ice water for one minute, then drain and set aside.<BR>2. Hollow out the pineapple then cut the part taken out into one-half to one-inch pieces, mix with the rice and the squid, then set these aside.<BR>3. Put the shrimp and the crab meat into boiling water for one minute, drain, put into ice water for half minute, then drain them and mix with the pineapple rice mixture.<BR>4. Mix red and green peppers and the raisons and mix them with the pineapple rice, as well.<BR>5. Heat oil in a wok or fry-pan, add curry powder and stir for two minutes, then add the sugar, and the soy and oyster sauces, Stir-fry another two minutes add the ground white pepper and stir well, then add the pineapple rice mixture and stir-fry for two minutes making sure all is very hot.<BR>6. Pour two cups of boiling water into the pineapple, then pour it out discarding the water and putting the hot pineapple rice mixture into the pre-heated pineapple, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C70A021E1681E441A51DB680491CF5EF', X'F764EDA2962D714EB6B3E5D34ED4998F', X'F764EDA2962D714EB6B3E5D34ED4998F', 0, 'javascript:', 'Fried Shrimp Stuffed with Shanghai Bok Choy', 2, NULL, 0, 'Fried Shrimp Stuffed with Shanghai Bok Choy', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1904', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound shrimp, peeled, their veins removed and both discarded, then finely mince the shrimp<BR>3 and 1 Tablespoons cornstarch, separated<BR>1/2 teaspoon salt<BR>1/2 teaspoon chicken bouillon powder<BR>1/2 teaspoon sesame oil<BR>1/3 teaspoon ground white pepper<BR>1 whole and 1/2 egg white, separated<BR>10 Shanghai bok choy<BR>1/4 cup corn kernels<BR>2 Tablespoons diced carrot<BR>2 Tablespoons vegetable oil<BR>1/2 cup chicken stock<BR><B><I>Preparation:</B></I><BR>1. Mix shrimp with the three tablespoons cornstarch, then rinse and dry them with paper towels.<BR>2. Stir shrimp until sticky,, then mix in the corn kernels, carrot, bouillon powder, sesame oil, ground white pepper, and the remaining egg white.<BR>3. Blanch the bok choy (now spelled <I>bok cai</I>) for two minutes, dry it, then cut each in half making a small depression for the stuffing at the root end. Coat the inside with the half tablespoon of cornstarch, and then stuff each one with a small mound of the stuffing.<BR>2 Heat a wok or fry pan, add the vegetable oil, then put the stuffed vegetable stuffing side down, and fry over low heat for two minutes, add the stock, cover, and simmer for two more minutes.<BR>4. Put the cooked stuffed bok choy on a platter, pour some of the sauce over it, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0F581428112C5845B53EA9C2409E7B16', X'8349F24AE4C4B849B40B8463D987BE6F', X'8349F24AE4C4B849B40B8463D987BE6F', 0, 'javascript:', 'Fujian-style Crab Rolls', 2, NULL, 0, 'Fujian-style Crab Rolls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=297', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 piece caul fat or a package of spring roll skins <BR>1/2 cup crab meat, cartilage removed<BR>1/2 cup dry fine-shredded pork <BR>1/2 cup water chestnuts, minced fine<BR>2 scallions, minced fine<BR>1 Tablespoon soy sauce<BR>1 teaspoon sugar<BR>1 teaspoon sesame oil<BR>1/4 teaspoon ground white pepper<BR>1 egg white<BR>1 Tablespoon corn starch<BR>1 cup corn oil<BR><I><B>Preparation:</I></B><BR>1) Cut fat into six-inch squares or use sheets of dough called swallow skin wrappers.<BR>2) Mix crab meat, pork, water chestnuts, scallions, soy sauce, sugar, sesame oil, and white pepper then put about three tablespoons of the mixture on a wrapper and seal with egg white, dust with cornstarch, and set aside. Repeat until all filling is wrapped and sealed. <BR>3) Heat oil and deep fry the rolls for five minutes or until golden brown turning them several times. Fry only a few at one time. Serve with a dipping sauce of your choice such as a mixture of light soy sauce, sesame oil, and slivers of garlic.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A6C49BE46733BC499D24ADC4735BD363', X'18FE813D2CE6934B9B527368EC878E80', X'18FE813D2CE6934B9B527368EC878E80', 0, 'javascript:', 'Garlic Scallops', 2, NULL, 0, 'Garlic Scallops', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1516', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 leaves dark green part of bokcai, washed and dried<BR>1 cup vegetable oil<BR>1/8 teaspoon salt<BR>12 scallops removed from their shells, the shells and the scallops both dried<BR>2 large cloves of garlic, minced<BR>2 teaspoons thin soy sauce<BR>1 Tablespoon oyster-flavored sauce<BR>1 Tablespoon dry sherry<BR>1 teaspoon cornstarch<BR>2 Tablespoons peanut oil<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Dry the leaves again, so they do not sputter when fried, roll them tightly, and shred finely.<BR>2.Heat vegetable oil and fry about a quarter of the leaves for about a minute until very bright green and very crisp. Remove and drain on paper towels, and repeat until all are fried. Sprinkle with salt and spread on half a flat platter.<BR>3. Heat wok or fry pan, add the peanut oil until just before it smokes, then put in the scallops and sear them for half minute on each side. Arrange them one to a shell, and put the shells on the platter. Leave any remaining oil in the wok or pan.<BR>4. Mix garlic, soy and oyster sauces, and the sherry; and in a separate small bowl, mix cornstarch with one tablespoon of cold water.<BR>5. Reheat wok with its oil, add garlic mixture and stir for fifteen seconds before adding the cornstarch mixture and stir until thickened, pour over the scallops, and serve. Approximate nutrient analysis per scallop, assuming two tablespoons of vegetable oil is absorbed for the whole recipe.<BR><B>Note:</B> Each serving has 66 calories, 1 g carbohydrate, 4 g protein, 141 mg sodium, 5 g fat, 1 g saturated fat, and 6 mg cholesterol.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A2D60671C39DD34796F1063BDA70CB13', X'51368E6F6151664C876AD11976FF0935', X'51368E6F6151664C876AD11976FF0935', 0, 'javascript:', 'Garlic Scallops', 2, NULL, 0, 'Garlic Scallops', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1031', '<TD><RECIPE><B>Ingredients:</B><BR>4 vegetable leaves, the dark green part of <I>bok cai</I>, washed and dried<BR>1 cup vegetable oil<BR>1/8 teaspoon salt<BR>12 scallops removed from their shells, then dry the shell and scallops<BR>2 large cloves of garlic, minced<BR>2 teaspoons thin soy sauce<BR>1 Tablespoon oyster-flavored sauce<BR>1 Tablespoon dry sherry<BR>1 teaspoon cornstarch<BR>2 Tablespoons peanut oil<BR>1 teaspoon sesame oil<BR><B>Preparation:</B><BR>1. Dry the leaves again, so they do not sputter when fried, then roll them tightly, and shred finely.<BR>2.Heat the vegetable oil and fry about a quarter of the leaves for about one minute until very bright green and very crisp. Remove and drain on paper towels, and repeat until all are fried; then sprinkle them with salt and spread on half of a flat platter.<BR>3. Heat wok or fry pan, add the peanut oil until just before it smokes, then put in the scallops and sear them for half minute on each side. Arrange them one to a shell, and put the shells on the platter. Leave any remaining oil in the wok or pan.<BR>4. Mix the garlic, soy and oyster sauces, and the sherry; and in a separate small bowl, mix cornstarch with one tablespoon of cold water.<BR>5. Reheat wok with its oil, add garlic mixture and stir for fifteen seconds before adding the cornstarch mixture and stir them together until thickened, then pour this over the scallops, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C1803F731CC06B40979B8B4600D62D19', X'F5A1264EA8032545B88DB37DC47A0142', X'F5A1264EA8032545B88DB37DC47A0142', 0, 'javascript:', 'Geoduck and Chinese Celery', 2, NULL, 0, 'Geoduck and Chinese Celery', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2081', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound geoduck, blanched for thirty seconds, then put in ice water until cool<BR>½ pound Chinese celery, angle sliced<BR>1 small carrot, peeled, cut in half the long way, then very thinly sliced<BR>1 Tablespoon vegetable oil<BR>1 Tablespoon fresh ginger, peeled and slivered<BR>dash of ground white pepper<BR>½ teaspoon salt<BR>1/4 seeded and minced hot pepper<BR>1 Tablespoon minced red onion<BR>1 Tablespoon diced cucumber<BR>1 Tablespoon diced ripe tomato<BR>1 Tablespoon cornstarch<BR>1 cup and 1/4 concentrated poultry stock, separated<BR><B><I>Preparation:</B></I><BR>1. Remove the outer membrane of the blanched geoduck and cut into three sections, neck, and across the center of its body. Then thinly slice each section.<BR>2. Heat a wok or large fry pan, add the oil, and stir-fry the celery, carrot, and the ginger for one minute. Then remove this from the heat, and add the ground pepper, salt, cornstarch, and the minced piquant pepper, red onion, cucumber, ripe tomato and the one cup of stock.<BR>3. When it boils, remove the wok or fry-pan from the heat, add the geoduck, and the 1/4 cup stock mixed with the rest of the cornstarch, stir, and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9067C3BB812FBC4EA74825CFD72943E4', X'3DE0B59CD709A748A92441CCA25F659F', X'3DE0B59CD709A748A92441CCA25F659F', 0, 'javascript:', 'Geoduck, Stir-fried with Snow Peas', 2, NULL, 0, 'Geoduck, Stir-fried with Snow Peas', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1642', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 geoduck, sliced on an angle<BR>2 Tablespoons cornstarch, separated<BR>1 cup chicken stock<BR>1/2 carrot, sliced in half the long way, then thin sliced<BR>1/4 cup snow peas, strings removed and cut on an angle into half-inch pieces<BR>1 Tablespoon vegetable oil or duck fat<BR>1 teaspoon grated fresh ginger<BR>1 teaspoon XO sauce<BR>1/2 teaspoon ground black pepper<BR>1 black mushroom, soaked until soft, stem discarded, then sliced thin<BR>1/2 scallion, green part only, cut into half-inch pieces<BR><B><I>Preparation:</B></I><BR>1. Blanch geoduck slices in water just below the boiling point for thirty seconds, remove immediately, drain, and toss with one tablespoon of cornstarch.<BR>2. Bring chicken stock to the boil, add the carrot and snow peas pieces and simmer for one to two minutes, then drain them and set aside apart from the stock.<BR>3. Heat wok or fry pan, add the oil or fat and stir-fry the ginger for half minute, then add XO sauce, ground pepper, mushroom slices, and the cooked vegetables and immediately remove the pan from the heat source, and all solids in it to a small bowl.<BR>4. Reheat pan and its oil, add the geoduck slices and stir-fry half minute then return all solids to the pan, vegetables and scallions pieces included.<BR>5. Mix remaining tablespoon of cornstarch with two tablespoons cooled chicken stock; reserve the rest for another sue. Add this to the pan and stir-fry one minute until the cornstarch mixture coats the geoduck slices as it thickens the small amount of sauce in it. Serve immediately on a pre-heated platter.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9CA859162231744CA21A097BC15E335B', X'1B9B50EDF3E10F46B526C74A3D5B5292', X'1B9B50EDF3E10F46B526C74A3D5B5292', 0, 'javascript:', 'Ginger Sauce Eels', 2, NULL, 0, 'Ginger Sauce Eels', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=605', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 pounds skinned and cut up eels<BR>2 and 1/2 Tablespoons light soy sauce<BR>2 Tablespoons rice wine or dry sherry<BR>1 and 1/2 teaspoons sugar<BR>2 Tablespoons vegetable oil<BR>10 slices fresh ginger root<BR>3 tablespoons chopped garlic<BR>1/2 cup chicken stock<BR>1 teaspoon cornstarch<BR>1 teaspoon sesame oil (optional)<BR>2 scallions, minced fine<BR><B><I>Preparation:</I></B><BR>1. Marinate the eel pieces in soy sauce, wine and sugar for half-an-hour.<BR>2. Heat oil in a wok or pan. Add ginger and garlic and saute until fragrant and when they start to turn brown.<BR>3. Add marinated eels and when the liquid is boiled out, add the stock, cover and simmer for twenty minutes.<BR>4. Mix cornstarch with a teaspoon of water and add to the eel mixture to thicken. <BR>5. Add sesame oil, if using it, and garnish with the scallions, then serve.<BR>Be sure to serve this very hot; it tastes best then.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D10368BE85639645A1355756E713C2F4', X'4092801C1BDED343BF39523CA07D66F5', X'4092801C1BDED343BF39523CA07D66F5', 0, 'javascript:', 'Goldberg''s Shrimp-style Toast', 2, NULL, 0, 'Goldberg''s Shrimp-style Toast', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=80', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound filet of flounder or sole, finely chopped<BR>1/2 teaspoon salt<BR>1/2 teaspoon sugar<BR>1 Tablespoon cornstarch<BR>4 water chestnuts, finely chopped<BR>2 scallions, minced fine<BR>1 large egg, beaten<BR>6 slices stale bread, crusts trimmed<BR>Oil for deep frying<BR><I><B>Preparation:</I></B><BR>1. Sprinkle the chopped fish with the salt, sugar, and cornstarch. Mix in the water chestnuts and scallions, then the beaten egg. If the mixture seems too wet to hold together, cover it well, and refrigerate for an hour or two.<BR>2. Divide the mixture into six equal parts. Spead one part on one slice of bread, covering the tops completely. With a sharp knife, cut the slice of bread into four triangles. Repeat with the rest of the fish and bread. Refrigerate until ready to cook.<BR>3. To fry, heat at least one half-inch of oil in a fry pan or wok to 375 degrees F. Carefully place some of the pieces of bread into the oil, fish side down. Do not crowd the oil. When the fish is lightly browned, which takes only a minute or two, turn the bread over with tongs, chopsticks, or a large spoon. Drain well ob paper towels, and repeat until all the pieces are fried. Serve immediately with Chinese mustard and/or duck sauce, as you like.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D3B8BDE56016B0429D4432765C6C1E57', X'49BC339981457A4DBE65B9E1131ABFD6', X'49BC339981457A4DBE65B9E1131ABFD6', 0, 'javascript:', 'Grass Carp and Sea Vegetables', 2, NULL, 0, 'Grass Carp and Sea Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2317', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1/2 pound grass carp fillet<BR>1/4 cup dried sea vegetables, soaked for ten minutes, then chopped<BR>6 slices fresh ginger, shredded<BR>1 Tablespoon vegetable oil<BR>2 Tablespoons thin soy sauce<BR><B><I>Preparation:</B></I><BR>1. Place grass carp on a heat-proof plate and put the sea vegetables and shredded ginger on top.<BR>2. Mix the heated oil and the soy sauce, and sprinkle on top of the fish filet. Cover with plastic wrap and steam for five to six minutes, then uncover and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'64B6C8A6E33E98459D52D4C0E4884007', X'F291ABC4745F5141A26E78F99E55910E', X'F291ABC4745F5141A26E78F99E55910E', 0, 'javascript:', 'Grilled Stuffed Fish, Dai Style', 2, NULL, 0, 'Grilled Stuffed Fish, Dai Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1661', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 two-pound fish, cut in half, the skin not separated, and debone the fish and discard the bones<BR>1/2 teaspoon ground white pepper<BR>1 teaspoon coarse salt<BR>1 cup coarsely chopped scallions<BR>1/2 cup minced mostly fat from a piece of belly pork<BR>1 fresh red chili pepper, seeded and cut into small pieces<BR>3 cloves garlic, coarsely chopped<BR>1 slice onion, chopped<BR>2 Tablespoons chopped fresh coriander, optional<BR>1 to 2 pieces of congrass or banana leaves or aluminum foil and string<BR>1 teaspoon vegetable oil, if using aluminum foil<BR><B><I>Preparation:</B></I><BR>1. Dry fish inside and out, then cut slashes about two-inches apart on the skin side, being careful not to cut more than one-quarter-inch deep, and not all the way through the flesh.<BR>2. Rub both sides first with ground pepper, then with the coarse salt.<BR>3. Using a mortar and pestle, pound scallions, the fat, chili pepper pieces, the chopped garlic and onion, and the coriander, if using it, making a coarse paste.<BR>4. Take one teaspoon of the paste and rub it on the skin side of the fish, spread the remaining paste evenly on one side of the flesh of the fish, and cover with the other side.<BR>5. Pull off a few thin pieces of the congrass or the banana leaf to use as string; or oil one side of the aluminum foil; and wrap one or the other around the fish and tie to hold the filling in and the sides together around the stuffed fish.<BR>6. Grill over charcoal about five minutes per side, but grill each side twice, then remove the wrapping or not, as desired, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8D6DF67ADD38F34FB64CCD2D9C84C172', X'C42A9DFFFA6F7849BD1D345B4E301628', X'C42A9DFFFA6F7849BD1D345B4E301628', 0, 'javascript:', 'Gung Bao Squid', 2, NULL, 0, 'Gung Bao Squid', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1200', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup vegetable oil<BR>2 soaked dried squid, washed, cut in half, and cross-hatched<BR>1/4 cup dried chili peppers, partially cut in half, seeds removed, but left attached<BR>2 cloves garlic, peeled and minced<BR>2 slices fresh ginger, minced<BR>2 teaspoons granulated sugar<BR>1 Tablespoon Chinese black vinegar<BR>1 teaspoon cornstarch mixed with one teaspoon cold water<BR>1 teaspoon sesame oil<BR>1 Tablespoon Chinese rice wine<BR><B><I>Preparation:</B></I><BR>1. Heat oil, and oil-blanch the squid for one minute, remove the squid and drain well, setting the oil aside.<BR>2. Put two tablespoons of the reserved oil into a wok or fry pan, and stir-fry the chili peppers, garlic, and ginger for one minute, then add squid, sugar, vinegar, cornstarch mixture, sesame oil, and the rice wine. Bring to the boil, transfer to a bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'045A0C2D8EDFB5468D9FE4E963D1B792', X'E71938636E9DB64C83240E4E0A7F94A9', X'E71938636E9DB64C83240E4E0A7F94A9', 0, 'javascript:', 'Hakka Shrimp with Rice Wine', 2, NULL, 0, 'Hakka Shrimp with Rice Wine', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1315', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>2 slices fresh ginger, peeled and slivered<BR>1 cup Shaoxing rice wine<BR>1 pound whole shrimp, shell but not the tail section, and veins removed and discarded<BR>some sprigs of fresh coriander<BR><B><I>Preparation:</B></I><BR>1. Heat wok or three-quart pot, then add oil and ginger, and stir-fry for one minute.<BR>2. Add rice wine and two quarts of boiling water, and boil for one minute before adding the shrimp. Turn off the heat source, and let rest five minutes, then pour into individual soup bowls, garnish with one or two sprigs of coriander and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5B5C8EBA1CBA1D40B008D214F8A4CB38', X'ECF53CE55C114643ACA4AE907BF7A998', X'ECF53CE55C114643ACA4AE907BF7A998', 0, 'javascript:', 'Halibut B.V. (Bob''s version>', 2, NULL, 0, 'Halibut B.V. (Bob''s version>', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=979', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 ounces Chinese dried salted olives<BR>1 Tablespoons Chinese salted black beans<BR>4 eight-ounce halibut fillets<BR>salt and pepper, to taste<BR>1 cup cornstarch<BR>6 slices fresh ginger, cut into thin strips<BR>2 and 1/4 Tablespoons soy sauce<BR>2 and 1/2 teaspoons sesame oil<BR>2 and 1/2 Tablespoons Chinese rice wine<BR>2 and 1/2 Tablespoons sugar<BR>1 cup garlic oil (see recipe below)<BR>6 scallions, cut in three sections, each of these slivered<BR>12 sprigs cilantro<BR><B><I>Preparation:</B></I><BR>1. Put salted olives in a bowl and cover with just enough water, set aside. Put black beans in another bowl, and do likewise.<BR>2. Season each halibut fillet with salt and pepper, dust with cornstarch, and put then into a casserole dish.<BR>3. Drain olives and the black beans, mix them with the ginger, and divide mixture into four parts putting each part on a fish fillet.<BR>4. Put casserole on rack in steamer over rapidly boiling water. Cover and steam the fish for three to four minutes.<BR>5. In another bowl, mix soy sauce, oyster sauce, sesame oil, wine, and sugar. Season with salt and pepper and stir until sugar is dissolved.<BR>6. Put garlic oil in small pot, and heat it until it reaches 200 F degrees.<BR>7. Remove fish from the steamer and put each fillet on a preheated plate. Pour one-fourth of the soy sauce mixture over each, then one-fourth of the garlic oil. Garnish with scallions and cilantro, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7BD7429893E46F4B8C6411D2FFCDBDD7', X'E096FA0496B47A41B8C3CC762260295F', X'E096FA0496B47A41B8C3CC762260295F', 0, 'javascript:', 'Hot and Sour Five-flavor Squid Soup', 2, NULL, 0, 'Hot and Sour Five-flavor Squid Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1198', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 fresh squid, cleaned, cut in half, then blanched in boiling water for half minute, and cut into thin strips<BR>1 soaked dried squid, cut in half, cut into half-inch thin strips, each of which is cut as if cutting in half, but not cutting all the way through<BR>2 Tablespoons minced fresh coriander, stems removed and tied into knots<BR>1 scallion, minced<BR>2 slices fresh ginger, minced<BR>2 cloves fresh garlic, peeled and minced<BR>1 Tablespoon chili oil<BR>1 Tablespoon thick soy<BR>1 Tablespoon granulated sugar<BR>1/4 teaspoon ground white pepper<BR>1 small tomato, peeled and seeded, them minced<BR>1 teaspoon sesame oil<BR>8 cups boiling chicken broth<BR>1 or 2 fresh scallops, coarsely chopped<BR><B><I>Preparation:</B></I><BR>1. Mix all ingredients together, and then return to the boil.<BR>2. Pour into a large soup tureen, return to the boil, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DADFEFA70025CD429FB674B351A2241C', X'07F7BCBD45964644A85E3674BB265CDA', X'07F7BCBD45964644A85E3674BB265CDA', 0, 'javascript:', 'Hunan-style Fried Fish Tails', 2, NULL, 0, 'Hunan-style Fried Fish Tails', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=4', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>4 fish tails, cut in half the long way, scales and bones removed.<BR>2 Tablespoons dark soy sauce<BR>1 Tablespoon rice wine<BR>2 Tablespoons Sichuan peppercorns, ground coarsely<BR>1/4 teaspoon coarse salt<BR>2 Tablespoons flour<BR>1/2 cup corn oil<BR>1 Tablespoon pickled vegetable, minced fine<BR><I><B>Preparation:</I></B><BR>1. Marinate the fish tails in soy sauce and rice wine for fifteen minutes, then drain.<BR>2. Mix ground Sichuan peppercorns with salt and flour and coat the fish tails.<BR>3. Heat the oil and fry the fish tails until brown and cooked, about five minutes, then drain and put on a warm platter. Sprinkle the pickled vegetable on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E57F7981B72AE64D9A45252F023A7186', X'14C34A2652C9174DACA6337D6906745A', X'14C34A2652C9174DACA6337D6906745A', 0, 'javascript:', 'Hundred Get-together Shrimp', 2, NULL, 0, 'Hundred Get-together Shrimp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=436', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 Tablespoons corn oil<BR>12 medium shrimp, peeled and cut in half the long way<BR>1 scallion, minced<BR>1 teaspoon minced fresh ginger<BR>24 pieces of fresh or dried lily bulb pieces, cooking the dried ones for fifteen minutes<BR>1 egg, beaten well (or two egg whites)<BR>1/4 teaspoon each of sugar, white pepper, and salt<BR>1 Tablespoon cornstarch or lily powder mixed with one tablespoon cold water.<BR><I><B>Preparation:</B></I><BR>1. Heat oil and cook shrimp for half a minute, then drain well and set aside. Remove all oil, leaving no more than one teaspoon in the wok or pan.<BR>2. Heat remaining oil and fry scallion and ginger for half a minute, add lily bulb and mix well, then add salt and sugar, and stir well before adding the egg. Cook for up to one minute or until the egg just begins to set.<BR>3. Add shrimp and cornstarch mixture and cook until thickened, but not longer than a minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AC29B92846EABB4D8118F772037C78B6', X'1AC92A0F3AEB3140BAAD10E67A5FFBB8', X'1AC92A0F3AEB3140BAAD10E67A5FFBB8', 0, 'javascript:', 'Jelly Fish with Cucumbers and Pi Dan', 2, NULL, 0, 'Jelly Fish with Cucumbers and Pi Dan', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=399', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1/2 pound package shredded jelly fish<BR>1 or 2 cucumbers<BR>4 Tablespoons cider vinegar<BR>2 Tablespoons sugar<BR>1 teaspoon salt<BR>1 teaspoon sesame oil<BR>4 <I>pi dan</I>, shelled and cut one each into six pieces<BR><B><I>Preparation:</B></I><BR>1. Soak jelly fish for one-half hour(<BR>2. Peel cucumbers, discard seeded part and shred the same size as the jelly fish pieces.<BR>3. Mix vinegar, sugar, salt, and sesame oil and mix with jelly fish. Add the cucumbers and the <I>pi dan</I> and fold gently. Serve cold.<BR><B>Note:</B> If shredded jelly fish is not available, cut whole ones into one-quarter- to one-half-inch by two inch slices and blanch for them for thirty seconds; yes, just thirty seconds. Then drain and let rest a few minutes until they are again at room or refrigerated temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4136CB1EB3C5554493B67FDC7CE45175', X'A662E320828B4847B4EB95D9BAD19F8A', X'A662E320828B4847B4EB95D9BAD19F8A', 0, 'javascript:', 'Jellyfish and Cucumbers', 2, NULL, 0, 'Jellyfish and Cucumbers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=851', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 dried jellyfish, rinsed three times, then blanched for ten to fifteen seconds in almost boiling water, and immediately immersed in cold water<BR>2 pickling cucumbers, sliced then cut into thin one-inch strips<BR>4 Tablespoons white vinegar<BR>1 Tablespoon Chinese black vinegar<BR>1 Tablespoon sugar<BR>1 Tablespoon sesame oil<BR>1 Tablespoon thin soy sauce<BR><B><I>Preparation:</B></I><BR>1. Roll blanched jellyfish, then cut it as thinly as possible. Soak in cold water in the refrigerator for an hour, then drain and put in a serving bowl.<BR>2. Mix cucumber strips with jellyfish.<BR>3. Mix vinegars, sugar, sesame oil, and soy sauce, and just before serving, pour over the jellyfish-cucumber mix.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1BD75BC182E5B440A220C9A4150DCC01', X'C30A0AADFD0A17438F46E6C171183142', X'C30A0AADFD0A17438F46E6C171183142', 0, 'javascript:', 'Konjac Stew', 2, NULL, 0, 'Konjac Stew', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1152', '<TD><RECIPE><B></I>Ingredients:</B></I><BR>1/2 cup vegetable oil<BR>1 sea bass, red snapper, or carp fish head, gills removed, rinsed and dried with paper towels<BR>1/2 pound firm tofu, sliced into two-inch long thin pieces<BR>4 Chinese black mushrooms, soaked, stems discarded and thinly sliced<BR>3 scallions, cut in half then into one-inch long pieces<BR>4 slices fresh ginger, peeled<BR>1 Tablespoon Chinese rice wine<BR>2 Tablespoons thin soy sauce<BR>1 Tablespoon oyster sauce <BR>1/4 teaspoon mixed ground white, black, and Sichuan pepper<BR>10 bundles of konjac noodles, rinsed under running water<BR><B><I>Preparation:</B></I><BR>1. Heat wok or deep fry pan and add fish head. Fry it turning three or four times until it is brown and crisp, then remove the head and drain it on paper towels.<BR>2. Reheat the oil and fry the tofu slices until tan and crisp, add the mushrooms and fry for another minute, then remove both and drain on paper towels.<BR>3. Discard all but one tablespoon of the oil, reheat it and fry the scallions and ginger for one minute, then return fish head, tofu, and mushrooms and stir once or twice before adding the rice wine, soy sauce, and oyster sauce, the ground pepper, and one quart of boiling water. Reduce the heat to simmer, and cook for fifteen minutes.<BR>4. Add konjac noodle bundles, gently mix them in and simmer for another eight minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0B7DF654D08B5F4FB72F155DE72868CA', X'02F2AB5AE479EC4E8F0553665CF4356C', X'02F2AB5AE479EC4E8F0553665CF4356C', 0, 'javascript:', 'Laksa with Chinese Influences', 2, NULL, 0, 'Laksa with Chinese Influences', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1717', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound thin rice noodles, soaked until hot water until soft, then put in ice cold water<BR>6 ounces shrimp, peeled, their veins removed, shells reserved, shrimp cut into half-inch pieces<BR>3 Tablespoons vegetable oil<BR>1/2 cup thick coconut milk<BR>3 fried <I>doufu</I> cakes, cut in quarters<BR>2 fish cakes, cut into small cubes<BR>3 scallions, coarsely chopped<BR>2 stalks lemon grass, white parts only<BR>1 knob fresh ginger, peeled and cut into pieces<BR>10 peeled almonds or candlenuts<BR>1 Tablespoon <I>belachan</I><BR>5 dried chili peppers, seeds discarded<BR>1/2 teaspoon ground turmeric<BR>1 onion, chopped<BR>1/2 pound bean sprouts, heads and tails removed, heads reserved, tails discarded<BR>1/2 cucumber, peeled and cut into matchstick-size pieces<BR><B><I>Preparation:</B></I><BR>1. Mix rice noodles, shrimp, shrimp shells in a net bag for easy removal, vegetable oil, coconut milk, three cups of boiling water, <I>doufu</I> and fish cake pieces in a large pot, and set aside for ten minutes.<BR>2. Put scallions and lemon grass pieces, the ginger, almonds, <I>belachan</I>, chili peppers, turmeric, and the onion in a blender and make a paste turning the motor on and off.<BR>3. Add the blended paste to the rice noodle mixture and bring to just below the boil, stir well and sinner for two minutes, not longer, then discard the shrimp shell bag before adding the bean sprout heads, the bean sprouts, and the cucumber pieces. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A34F57E91FF2F0499CC59E7D27CFADD7', X'2FE2564E23E242418F7F04A1F158EA2E', X'2FE2564E23E242418F7F04A1F158EA2E', 0, 'javascript:', 'Lettuce and Shrimp Two Ways', 2, NULL, 0, 'Lettuce and Shrimp Two Ways', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2264', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 head iceberg lettuce, its ten outer leaves only<BR>1/4 cup dried shrimp, soaked for twenty minutes, then drained<BR>3 Tablespoons minced bamboo shoots<BR>1/4 pound fresh shrimp, diced small<BR>½ chicken breast, diced small<BR>2 Tablespoons crab meat, diced small (optional)<BR>10 wide dry rice noodles, crushed, soaked until soft, then fried<BR>2 teaspoon oyster sauce<BR>1 Tablespoon curry powder<BR>½ teaspoon coarse salt<BR>dash of ground white pepper<BR>½ teaspoon granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Rinse, shake off all the water, then set lettuce leaves on a platter.<BR>2. Mix dry and fresh shrimp, bamboo shoots, chicken breast, crab meat, if using it, the oyster sauce and half the fried wide noodles.<BR>3. Mix curry powder, salt and pepper, and the sugar, then toss with the shrimp mixture.<BR>4. Put a few teaspoons of the shrimp mixture in each lettuce leaf, top with the rest of the fried noodles, and roll them then put them back on the platter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'13AECF15666AE24B97CCFE4DE31A0B5E', X'173A612243057B4AB528AE78F5CFF353', X'173A612243057B4AB528AE78F5CFF353', 0, 'javascript:', 'Lettuce Wrap with Clam Meat Stir-fry', 2, NULL, 0, 'Lettuce Wrap with Clam Meat Stir-fry', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1337', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups fresh corn kernels<BR>1 red bell pepper, diced<BR>1 green bell pepper, diced<BR>1/2 cup diced jicama<BR>8 dried soaked black mushrooms, stems discarded, their caps then diced<BR>4 Tablespoons vegetable oil<BR>1 cup fresh boiled baby clams, minced<BR>2 Tablespoons soy sauce<BR>1/2 tsp salt<BR>1/2 tsp sugar<BR>2 scallions, minced<BR>6 sprigs Chinese parsley, minced<BR>lettuce leaves (at least as many as there are people being served)<BR><B><I>Preparation:</B></I><BR>1. Dice or chop all vegetables and set them aside.<BR>2. Place a wok or fry-pan over medium-high heat. Add the oil, swirling to coat the sides, then add all the vegetables and stir-fry until they are tender-crisp, about two minutes.<BR>3. Add clam meat, Chinese parsley, and the scallions, then the soy sauce, salt and sugar. Toss all until well mixed, then quickly transfer to a serving plate.<BR>4. Put lettuce leaves on this or another serving plate. Everyone wraps a few spoonfuls of the clam mixture into one lettuce leaf, folds it in, and enjoys their own efforts and the chef''s expertise.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CB44993BDDCE6A438AB46DEEC5031CCC', X'0B214E9DD7D7D54B95206B0CF779058E', X'0B214E9DD7D7D54B95206B0CF779058E', 0, 'javascript:', 'Lettuce-wrapped Ovsters', 2, NULL, 0, 'Lettuce-wrapped Ovsters', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=767', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>24 large oysters, shells removed<BR>1/2 teaspoon salt<BR>12 toothpicks<BR>2 Tablespoons honey<BR>1 Tablespoon Chinese rose or rice wine<BR>1 Tablespoon black bean paste with garlic<BR>1 cup corn oil<BR>12 small lettuce leaves<BR><B><I>Preparation:</B></I><BR>1. Boil water and oysters and salt and blanch the oysters in it for half minute, no longer, then drain well, and skewer two oysters to a toothpick.<BR>2. Mix honey, wine, and black bean paste, and spread this mixture on the oysters.<BR>3. Heat oil, deep fry the oysters in two batches, for only one minute, ten remove and drain and set on serving platter with the lettuce leaves. Repeat with the second batch. Then serve using the lettuce leaves to hold oysters, removing the tooth picks, and thus eating them wrapped in the leaves.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6D343EC0E3CA2C4AA15F39D91E02025F', X'7E081F34806E2842A45421468FC57CFE', X'7E081F34806E2842A45421468FC57CFE', 0, 'javascript:', 'Li Li''s Scrambled Egg White with Crabmeat', 2, NULL, 0, 'Li Li''s Scrambled Egg White with Crabmeat', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=324', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>7 egg whites<BR>1/2 pound crabmeat<BR>1 Tablespoon cornstarch<BR>1 Tablespoon dry sherry<BR>salt and sugar, to taste<BR>2 cups corn oil<BR>4 slices fresh ginger<BR>1 cup chicken stock<BR>10 thin slices bamboo shoots, mince or cut into thin strips<BR>2 scallions, minced<BR>2 Tablespoons cornstarch mixed with two tablespoons of cold water<BR><B><I>Preparation:</B></I><BR>1. Beat egg whites until stiff. Gently, fold in crabmeat, cornstarch, sherry, salt and sugar.<BR>2. Heat oil, fry ginger for a minute or two then remove and gently add egg white mixture cooking it slowly. When lightly set, lift it out with a fine-meshed strainer; do not remove from the strainer. Remove oil in wok and set aside for another use.<BR>3. Heat wok, add the stock and bring to the boil then add bamboo shoots and cook one minute. Add egg white mixture and warm it for a minute, no more.<BR>4. Mix cornstarch mixture and gently stir it in allowing the mixture to thicken. Remove it to a serving bowl and garnish with minced scallions, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'816E4DEB3FD6FF4CA91D1870089CA469', X'1E996401C7FE6845B8139C2A7754B252', X'1E996401C7FE6845B8139C2A7754B252', 0, 'javascript:', 'Lobster Cantonese', 2, NULL, 0, 'Lobster Cantonese', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2165', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/3 pound minced pork (optional)<BR>1 teaspoon salt<BR>1/4 teaspoon ground back pepper<BR>1 teaspoon granulated sugar<BR>1 Tablespoon cornstarch<BR>2 Tablespoons chopped fermented black beans<BR>1 Tablespoon vegetable oil<BR>1 large clove garlic, peeled and sliced thinly<BR>1 lobster in its shell. chopped into reasonable-size edible parts<BR>1 cup chicken broth<BR>1 Tablespoon cornstarch<BR>2 eggs, beaten well<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix pork, if using it, and the salt, pepper, the sugar, cornstarch, and the fermented black beans. Set this aside.<BR>2. Heat wok or fry pan, add the oil and the garlic and stir-fry one minute, then add the pork mixture and stir-fry for one more minute, stirring continuously.<BR>3. Add the lobster pieces and when they are hot, add the broth and the cornstarch. Mix this with the cornstarch and stir until it starts to thicken, then pour into a pre-heated serving bowl with the beaten eggs. Now stir in the sesame oil, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E7E4E00FBA568649B3836DFD46F350CD', X'7D7B6322FC554348A60ADAB418C5A13E', X'7D7B6322FC554348A60ADAB418C5A13E', 0, 'javascript:', 'Lobster Cantonese', 2, NULL, 0, 'Lobster Cantonese', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2270', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/3 pound minced pork<BR>1 teaspoon salt<BR>1/4 teaspoon ground black pepper<BR>1 teaspoon granulated sugar<BR>1 Tablespoon cornstarch<BR>2 Tablespoons chopped fermented black beans<BR>1 Tablespoon vegetable oil<BR>1 large clove garlic, peeled and sliced thinly<BR>1 lobster in its shell, chopped into edible parts<BR>1 cup chicken broth<BR>1 Tablespoon cornstarch<BR>2 eggs, beaten well<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix pork with the salt and pepper, sugar, cornstarch, and the fermented black beans, and set this aside.<BR>2. Heat wok or fry pan, add the oil and the garlic and stir-fry one minute, then add the pork mixture and stir-fry for one more minute, stirring continuously.<BR>3. Next add the lobster pieces and when hot, add the broth and the cornstarch. Mix this with the pre-heated broth, and stir until thickened. Now pour into a pre-heated serving bowl, add the beaten eggs, and stir in the sesame oil, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6D18B639743BC34E9A951893E58CFAE4', X'31D204B576B9DB4EA4022A793DE4CA0B', X'31D204B576B9DB4EA4022A793DE4CA0B', 0, 'javascript:', 'Lobster Casserole', 2, NULL, 0, 'Lobster Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2167', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>2 scallions, knotted<BR>1 clove garlic, peeled and smashed<BR>1 pound or two cups of lobster meat<BR>1/3 cup chicken broth<BR>1 Tablespoon cornstarch mixed with one-quarter of one cup of cold water<BR>2 eggs, beaten well<BR>1 teaspoon coarse salt<BR><B><I>Preparation:</B></I><BR>1 Heat wok or fry pan, add the oil, then the scallions and garlic and stir-fry until all are light brown.<BR>2. Next add the lobster meat and chicken broth and cover stirring it for two minutes.<BR>3. Add the eggs, and stir, then put into a heat-proof casserole with a cover. Bake for half an hour at 325 degrees F. Then serve.<BR>Lobster stuffed Broccoli Ingredients: 1 small head of broccoli cut into small pieces 1 cup lobster, minced 1 Tablespoon chicken fat 1 clove garlic, peeled and smashed 1 Tablespoon cornstarch 1 teaspoon sesame oil ½ cup chicken stock 1 teaspoon granulated sugar Preparation: 1. Blanch the broccoli for one minute, then put them in cold water for two or three minutes, and then drain and discard the water. 2. Mix lobster, chicken fat, garlic, and cornstarch and coat each piece of broccoli with this. 3. Heat sesame oil, chicken stock, and the sugar, and put this and the broccoli in a small heat-proof bowl and steam over boiling water for three or four minutes. Now drain and discard any liquid, and serve.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'43439D1527C0934CA61B26D2B658B4E8', X'FE3DE2F0DFBEEC4A9FBD9E7E19EA3721', X'FE3DE2F0DFBEEC4A9FBD9E7E19EA3721', 0, 'javascript:', 'Lobster Casserole', 2, NULL, 0, 'Lobster Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2272', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>2 scallions, each one knotted<BR>1 clove garlic, peeled and smashed<BR>1 pound of or two cups of lobster meat<BR>1/3 cup chicken broth<BR>1 Tablespoon cornstarch mixed with one-quarter of a cup of cold water<BR>2 eggs, beaten well<BR>1 teaspoon coarse salt<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, then add scallions and garlic and stir-fry until all are light brown, about two minutes.<BR>2. Then add lobster meat and chicken broth, stir once or twice.<BR>. 3. Add the eggs, and stir, then put this in to a heat-proof casserole with a cover, and bake for half an hour at 325 degrees F. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8F910F7C78398545A22BAD6A37788B2E', X'EF062AA36FAA0E438E62ED16EE2BCA22', X'EF062AA36FAA0E438E62ED16EE2BCA22', 0, 'javascript:', 'Lobster, Chiu Chow Style', 2, NULL, 0, 'Lobster, Chiu Chow Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2269', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 lobster steamed for ten minutes, meat removed from its shells, tail meat cut into two-inch pieces<BR>1 Tablespoon vegetable oil<BR>6 large slices of fresh ginger, peeled<BR>3 scallions, white part only, sliced on an angle<BR>1 teaspoon sugar<BR>½ teaspoon coarse salt<BR>1 teaspoon sesame oil<BR>½ cup chicken broth<BR>1 Tablespoon cornstarch<BR>6 ounces shrimp rice noodles, cooked until soft, then drained and tossed with one tablespoon sesame oil<BR>6 bowls hot chicken broth or stock<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, and fry the lobster pieces for no more than one minute, then remove them from the pan and set them aside.<BR>2. Put oil in a clay pot and add the ginger and scallions, salt, and scallion pieces and stir once or twice, then add salt and the sesame oil.<BR>3. Mix chicken broth and the cornstarch, stir in the pieces of lobster and the cooked noodles. Give each person a bowl of hot broth and a long-handled fork, helping them to lobster from the clay pot to eat with their broth.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7393395E4CF9B243831EFE9FBA39136F', X'214792114AC87847A499123B93149022', X'214792114AC87847A499123B93149022', 0, 'javascript:', 'Lobster-stuffed Broccoli', 2, NULL, 0, 'Lobster-stuffed Broccoli', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2271', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small head of broccoli cut into small pieces<BR>1 cup lobster meat, minced<BR>1 Tablespoon chicken fat<BR>1 clove garlic, peeled and smashed<BR>1 Tablespoon cornstarch<BR>1 teaspoon sesame oil<BR>½ cup chicken stock<BR>1 teaspoon granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Blanch the broccoli for one minute, then put it in cold water for three minutes, drain well, and discard the water.<BR>2. Mix lobster, chicken fat, garlic, and cornstarch and coat each piece of broccoli with this.<BR>3. Heat sesame oil, chicken stock, and sugar, and put this and the broccoli in a small heat-proof bowl and steam over boiling water for three minutes, then drain and discard any liquid, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E260877F9C5CD94FB2A86B948A164894', X'D32A3A6E451BD64B951A227387A075FE', X'D32A3A6E451BD64B951A227387A075FE', 0, 'javascript:', 'Lobster-stuffed Broccoli', 2, NULL, 0, 'Lobster-stuffed Broccoli', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2164', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup lobster meat, minced<BR>1 Tablespoon chicken fat<BR>1 clove garlic, peeled and smashed<BR>1 Tablespoon cornstarch<BR>1 teaspoon sesame oil<BR>1/2 cup chicken stock<BR><B><I>Preparation:</B></I><BR>1. Blanch the broccoli for one minute, then put the florettes in colf water for two or three minutes, then drain.<BR>2. Mix lobster, chicken fat, garlic and cornstarch, and coat each piece of broccoli with this mixture.<BR>3. Heat the sesame oil, chicken stock, and the sugar, and put this and the broccoli in a small heat-proof bowl and steam over boiling water for three or four minutes, Drain ans serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'696D6302D35C9845A07294F42CB9E72F', X'24FA5E0C80D2744CBB65B5F7D8C91EC0', X'24FA5E0C80D2744CBB65B5F7D8C91EC0', 0, 'javascript:', 'Lotus Flower Fish', 2, NULL, 0, 'Lotus Flower Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1353', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 lotus flower, petals removed<BR>1 pound fresh unusual fish, skin and bones removed, fish coarsely minced<BR>2 egg whites<BR>1 teaspoon minced fresh ginger<BR>1 Tablespoon Chinese rice wine<BR>1/4 cup minced bamboos shoots<BR>1/2 teaspoon coarse salt<BR>1/2 cup cornstarch<BR>1 cup lard or vegetable oil<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Spread the lotus petals around the perimeter of a lotus-flower serving platter.<BR>2. Mix the fish with the egg whites, rice wine, bamboo shoots, and the salt, and shape into several small fish, pointed at one end, v-tail at the other. Then dip sprinkle half the cornstarch on these fish and leave to wet for half an hour. Then turn them over and do the same on their other side.<BR>3. Heat wok, add lard or oil, and carefully lift the fish, one at a time into the heated fat and fry until crisp, then turn over and fry on their other sides, the drain on paper towels and carefully place them in the center of the platter just about touching the lotus petals. Pour sesame oil over them, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7D59458FA59FD5438FEB33DFADF731DF', X'96B6C573E57B6D4F87A8DBC3F7C099AD', X'96B6C573E57B6D4F87A8DBC3F7C099AD', 0, 'javascript:', 'Lotus Flower Fish', 2, NULL, 0, 'Lotus Flower Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1475', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 lotus flower, petals removed<BR>1 pound fresh unusual fish, skin and bones removed, fish coarsely minced<BR>2 egg whites<BR>1 teaspoon minced fresh ginger<BR>1 Tablespoon Chinese rice wine<BR>1/4 cup minced bamboos shoots<BR>1/2 teaspoon coarse salt<BR>1/2 cup cornstarch<BR>1 cup lard or vegetable oil<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Spread the lotus petals around the perimeter of a lotus-flower serving platter.<BR>2. Mix the fish with the egg whites, rice wine, bamboo shoots, and the salt, and shape into several small fish, pointed at one end, v-tail at the other. Then dip sprinkle half the cornstarch on these fish and leave to wet for half an hour. Then turn them over and do the same on their other side.<BR>3. Heat wok, add lard or oil, and carefully lift the fish, one at a time into the heated fat and fry until crisp, then turn over and fry on their other sides, the drain on paper towels and carefully place them in the center of the platter just about touching the lotus petals. Pour sesame oil over them, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E9FA616CC971F34D8D974EE36B42C969', X'99CD7B1C69C46043A29AAC1C293128C1', X'99CD7B1C69C46043A29AAC1C293128C1', 0, 'javascript:', 'Lotus-leaf-wrapped Fish', 2, NULL, 0, 'Lotus-leaf-wrapped Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1228', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 dried scallop (also called <I>conpoy</I>)<BR>1 dried lotus leaf<BR>1 pound fillet of fresh fish (flounder works well)<BR>1/2 teaspoon salt<BR>1 Tablespoon vegetable oil<BR>1 Tablespoon cornstarch<BR>3 Tablespoons peeled and minced fresh garlic<BR>1 scallion, slivered<BR>2 Tablespoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. In a small glass bowl, cover the scallop with two inches of water. Put this in the microwave on high for three minutes. Remove, cool, then shred and set aside.<BR>2 Line steamer basket with a lotus leaf, and put the fillet of fish on it.<BR>3. Mix salt, the one tablespoon of vegetable oil, the cornstarch, the garlic, and the shredded scallop. Pour this over the fish fillet, turning it twice so all the fish is coated with this mixture, then cover the fish with the lotus leaf.<BR>4. Steam over rapidly boiling water for five minutes, uncover and transfer the lotus leaf and fish to a bowl. Scatter the scallion pieces over it.<BR>5. Heat sesame oil, and pour it over the fish, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E21F329305B768418F8B665BDDEAA7B9', X'93C85732F1868A46B9101019CA81E566', X'93C85732F1868A46B9101019CA81E566', 0, 'javascript:', 'Mandarin Fish Stew', 2, NULL, 0, 'Mandarin Fish Stew', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2218', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds firm white-fleshed fish, skinless and boneless, cut into two-inch pieces<BR>2 Tablespoons brown sugar<BR>2 teaspoons Siracha sauce<BR>2 Tablespoons vegetable oil<BR>2 medium zucchini, angle-cut<BR>2 stalks celery, angle-cut<BR>1 large carrot, peeled and angle-cut<BR>1 onion, cut in large wedges<BR>8 cloves fresh garlic, peeled cut in halves, then smashed<BR>1 knob fresh ginger, peeled, thick-sliced, then each slice smashed<BR>2 Tablespoons vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Mix pieces of fish with the brown sugar, the Siracha sauce, and the vegetable oil, and let this rest for ten minutes.<BR>2. Heat a wok or fry pan, and fry the fish mixture until light brown, then add all the vegetables, and stir-fry for three minutes, then remove to a bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7424DD45F6F14E41934C7D7E79B758E0', X'45B61B1B58A77A4FBD0D68FFC0FDC67E', X'45B61B1B58A77A4FBD0D68FFC0FDC67E', 0, 'javascript:', 'Mango Fish Rolls', 2, NULL, 0, 'Mango Fish Rolls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1616', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/8 pound boneless and skinless fish filets<BR>1 egg white<BR>1 teaspoon sugar<BR>1/2 teaspoon salt<BR>2 teaspoons cornstarch<BR>2 almost ripe mangos, peeled and sliced thin<BR>3 Tablespoons water chestnut powder<BR>1 cup vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Cut fish into finger-length strips and toss them with egg white, sugar, and salt. Let rest for fifteen minutes, then poach them in boiling water for one minute. Drain on a rack.<BR>2. Roll fish in cornstarch then put one fish strip on the end of a mango slice and roll, holding each one together with a toothpick, then coat generously with the water chestnut powder and set on a rack and continue until all are rolled and coated.<BR>3. Heat oil, and deep-fry the mango rolls for one minute until crisp. Drain, remove the tooth picks, and serve hot or warm.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A60C19B2AE2BE045AA21854410E4FE22', X'4FE1CC57D4C16D4880B183F8E96BCBDD', X'4FE1CC57D4C16D4880B183F8E96BCBDD', 0, 'javascript:', 'Mangosteen, Crab, and Pea Shoots', 2, NULL, 0, 'Mangosteen, Crab, and Pea Shoots', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=760', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound pea or sweet potato shoots<BR>1 Tablespoon corn or another vegetable oil<BR>2 cloves fresh garlic, slivered<BR>1/4 pound fresh crab meat, all bone and cartilage removed<BR>15 mangosteen sections, from about three fresh fruits, seeds removed<BR>1/2 cup chicken stock mixed with two tablespoons water chestnut powder<BR><B><I>Preparation:</B></I><BR>1. Blanch pea shoots, drain, immerse in ice water for one minute, and drain again.<BR>2. Heat wok, add oil, and fry garlic for half minute, then add crab meat, stir once or twice and remove from wok.<BR>3. Add stock, pea shoots, and the crab meat mixture and bring to the boil, stirring constantly, Remove from heat just as it starts to thicken, keep stirring as it is poured into a reheated serving bowl; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6E2B86A41EE2DF4C8D804214D6A8F3EB', X'D2B0CDC829E2664581C2DE83A89906ED', X'D2B0CDC829E2664581C2DE83A89906ED', 0, 'javascript:', 'Minced Crab with Shrimp', 2, NULL, 0, 'Minced Crab with Shrimp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=742', '<TD><RECIPE></B><I>Ingredients:</B></I><BR>1/2 pound cooked crab meat, diced<BR>1/2 pound shrimp, minced fine<BR>2 scallions, whites only, minced, green part slivered<BR>1 egg white<BR>1 teaspoon cornstarch<BR>2 Tablespoons vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Divide crab meat into eight portions, making each of them into a ball and squeezing out any excess liquid.<BR>2. Mix shrimp paste, egg white, scallion whites and cornstarch and then divide this into eight portions, making a flat coin-shape for each of them. Put crab ball in the center and gently close the shrimp mixture around it.<BR>3. Bring two cups of water to the boil, and using a small strainer or a slotted spoon, lower the shrimp balls into the water for one minute, then remove them. This is best done two at a time.<BR>4. Heat oil and fry the shrimp balls stirring very gently, for one minute until pink and heated through. Put them on a preheated serving plate, sprinkle the green scallion slivers on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'84DF40329A7B22408789B1407755867D', X'C3123BB54855F7459D400E425A653D24', X'C3123BB54855F7459D400E425A653D24', 0, 'javascript:', 'Mixed Seafood Cakes with Snails and Other Seafood', 2, NULL, 0, 'Mixed Seafood Cakes with Snails and Other Seafood', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2286', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup snail meat removed from the shells, dried with paper towels, then chopped<BR>1/4 cup white boneless and skinless fish filet, dried with paper towels, then chopped<BR>1/4 cup fresh squid, dried with paper towels, then chopped<BR>1/4 cup peeled shrimp, their vens removed then chop them<BR>1 egg, beaten<BR>1 salted duck egg, steamed for ten minutes, cooled, peeled, and the white chopped, the yolk saved for another purpose<BR>1/4 cup cornstarch, divided in half<BR>1 cup panko breadcrumbs, divided in half<BR>1/4 cup green beans, ends removed, then chopped<BR>3 or 4 sprigs of fresh coriander, chopped<BR>salt and pepper, to taste<BR>1 teaspoon sesame oil<BR>1 cup vegetable oil, for frying<BR><B><I>Preparation:</B></I><BR>1. Mix chopped snail, fish, squid, and shrimp, add the egg and the chopped duck egg white.<BR>2. Next add half the cornstarch and half the panko, the chopped green beans, and the chopped coriander and the sesame oil.<BR>3. Add salt and pepper, as needed, then heat the vegetable oil and fry one teaspoon of this mixture and adding the salt and pepper (as to the salt and pepper, add about half teaspoon of salt and one quarter teaspoon of ground black pepper), then fry one teaspoon of this mixture and check the seasoning, adjust as needed.<BR>4. Make two-inch pancakes about half-inch thick.<BR>5. Next, mix the remaining cornstarch and panko and dust the seafood cakes on both sides and set them aside for ten minutes.<BR>6. Heat the oil and fry these mixed cakes for four or five minutes per side, drain on paper towels, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E6014C30C5B3EC48B88717BD6026F9B0', X'A505DBE6E0FDA44B87A0A2FBCE3B01D2', X'A505DBE6E0FDA44B87A0A2FBCE3B01D2', 0, 'javascript:', 'Mixed Seafood Dumplings', 2, NULL, 0, 'Mixed Seafood Dumplings', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2004', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound of sea scallops, sliced thin<BR>½ pound raw shrimp, peeled, veins removed, and diced<BR>½ cup finely sliced garlic chives<BR>4 large shiitake mushrooms, soaked, stems discarded, and finely diced<BR>1 Tablespoon <I>Shao Xing</I> rice wine<BR>1 Tablespoon oyster sauce<BR>1 Tablespoon soy sauce<BR>1 Tablespoon cornstarch<BR>1/4 teaspoon ground white pepper<BR>½ teaspoon granulated sugar<BR>40 round dumpling wrappers<BR>2 Tablespoons all-purpose flour<BR>Chinese mustard or soy-ginger dipping sauce or one of your own choice<BR><B><I>Preparation:</B></I><BR>1. Mix the cut scallops, shrimp, garlic chives, and the shiitake mushrooms and set them aside.<BR>2. In a separate dish, a glass, mix the wine, oyster sauce, soy sauce, cornstarch, white pepper, and sugar. Then mix them both together and refrigerate covered for half an hour.<BR>3. Fill each wrapper with one tablespoon of the seafood mixture, then wet the edges of a wrapper and fold and pleat it closed. Repeat until all are pleated putting them on a flour-coated platter not touching each other.<BR>4. Bring a large pot of water to the boil and add half the pleated dumplings. When water re-boils, add another half cup of cold water; and when that boils again, add another half cup cold water.<BR>5. When this boils again and dumplings float, drain and serve them. Boil the rest of the dumplings as above. Serve with your own choice of a dipping sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5B2C80CA6854A34F9788DFE05C5271ED', X'DB1EEDE8F97BCF4DBFCCB24A7CD1A5DD', X'DB1EEDE8F97BCF4DBFCCB24A7CD1A5DD', 0, 'javascript:', 'Monkfish with Wasabi Mayonnaise', 2, NULL, 0, 'Monkfish with Wasabi Mayonnaise', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=925', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 ounces monkfish<BR>1 Tablespoon wasabi paste<BR>1 teaspoon thin soy sauce<BR>1/4 cup Japanese mayonnaise or salad dresing<BR><B><I>Preparation:</B></I><BR>1. Cut monkfish into thin slices. Put in boiling water for two minutes, remove, drain, and put in a serving bowl.<BR>2. Mix all ingredients and use as a dipping sauce for the monk fish. It is also good for dipping roasted meats, cooked scallops, and other cooked sea foods.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FF719EFA72E99147A88CA807DD185598', X'24191E6FA37F8F429CAC5103AEDFB5AE', X'24191E6FA37F8F429CAC5103AEDFB5AE', 0, 'javascript:', 'Mussel Omelets', 2, NULL, 0, 'Mussel Omelets', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2000', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>12 mussels removed from their shells, rinsed of any sand<BR>2 Tablespoons vegetable oil<BR>3 eggs, beaten until light<BR>½ teaspoon salt (optional)<BR>1 scallion, the green part only, and angle sliced<BR><B><I>Preparation:</B></I><BR>1. Drain mussels after rinsing them, dry them on paper towels.<BR>2. Heat wok or fry pan, add the oil, and stir-fry the mussels for one minute before adding the beaten eggs. Do not stir them.<BR>3. Add salt, is using it, and the scallion, and as soon as the eggs look like they are beginning to set, turn them over. Serve on a pre-heater serving plate after they are turned over again.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B9E1C841BB915E47ADEF99AE187C1503', X'441AD4705CD4A44BBABDFF476832BAA6', X'441AD4705CD4A44BBABDFF476832BAA6', 0, 'javascript:', 'Mussels and Doufu', 2, NULL, 0, 'Mussels and Doufu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2001', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>12 mussels, scrubbed of any sand, removed from their shells, then dried with paper towels, and minced<BR>½ teaspoon salt<BR>2 Tablespoons vegetable oil<BR>2 cloves fresh garlic, peeled and smashed<BR>4 Chinese black mushrooms, soaked, stems discarded<BR>1 Chinese sausage, simmered for ten minutes, then removed from its casing and discard it, and mince the meat and dry-fry it for two minutes<BR>3 Tablespoons ground pork<BR>1 firm <I>doufu</I>, mashed<BR>1 scallion, angle sliced<BR>2 Tablespoons oyster sauce<BR>4 cilantro sprigs, one left whole for garnish, the others minced<BR><B><I>Preparation</I></B><BR>1. Blanch the mussels in boiling water and remove after one minute, then dry them with paper towels.<BR>2. Mix garlic, mussels, sausage, black mushrooms, pork, <I>doufu,</I> sugar, salt, and the scallion and stir-fry for two minutes. Then put this mixture in a pre-heated bowl, sprinkle with the minced cilantro, and top with the sprig of cilantro, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'53661781CB0D114C99F6B13C29D05775', X'F33305772043EF4E952A236E5C6B5E4A', X'F33305772043EF4E952A236E5C6B5E4A', 0, 'javascript:', 'Mussels and Fermented Black Beans', 2, NULL, 0, 'Mussels and Fermented Black Beans', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2002', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound green-lipped mussels<BR>2 tablespoons vegetable oil<BR>2 scallions, angle sliced<BR>½ cup chicken stock<BR><I><B>Preparation:</I></B><BR>1. Discard any beards on the mussels, scrub the shells, and set them in warm water for five minutes so that they can disgorge any sand.<BR>2. Heat a wok or fry pan, add the oil, then the mussels and stir-fry them for two minutes. Next stir in the scallions and the stock, boil for one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2AEE3D4834D8DA4B9C86CE33E86777F4', X'98634ABB4B9BB844BE755D80DCEA4650', X'98634ABB4B9BB844BE755D80DCEA4650', 0, 'javascript:', 'Mussels with Black Bean Sauce', 2, NULL, 0, 'Mussels with Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2082', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 cups mussels in their shells, beards removed<BR>1/4 to ½ pound Chinese rice noodles<BR>2 Tablespoons vegetable oil, divided into two parts<BR>1 Tablespoon oyster sauce<BR>½ Tablespoon granulated sugar<BR>1 Tablespoon dark soy sauce<BR>1 Tablespoon Chinese rice wine<BR>1 cup chicken stock<BR>1 Tablespoon cornstarch<BR>1 Tablespoon peeled and minced fresh ginger<BR>1 Tablespoon fermented black beans, chopped<BR>1/4 teaspoon siracha or another hot sauce<BR>½ fresh red pepper, minced<BR>4 to 6 leaves chopped <I>bok cai</I> leaves<BR><B><I>Preparation:</B></I><BR>1. Tap each mussel and watch to see if it closes tightly. If not, discard it as it may be dead.<BR>2. Boil noodles for about three minutes or until soft, then drain and let rest in one quart of warm water.<BR>3. Mix half the vegetable oil with the oyster sauce, sugar, soy sauce, rice wine, stock, and cornstarch and set aside.<BR>4. Heat the rest of the oil and stir-fry the ginger, black beans (neither rinsed nor minced), and the hot sauce, then add the red pepper pieces and the <I>bok cai</I> leaves, then add the mussels and stir until most have opened (about four or five minutes), then add the sauce mixture and bring to the boil.<BR>5. Drain the noodles once, then pour one quart of boiling water over them, and drain thoroughly again before putting them in a large serving bowl. Put the mussel mixture on top, and tap any mussel that has not opened. If there is no movement to close even tighter, discard that mussel, then mix the mussel and the noodle mixtures, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6763E933977A2340ACF9083864D78694', X'D834C08B2D142B40B9EBFAC3AF3FD9EF', X'D834C08B2D142B40B9EBFAC3AF3FD9EF', 0, 'javascript:', 'Mussels with Hotbed Leeks', 2, NULL, 0, 'Mussels with Hotbed Leeks', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=730', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound mussels, cleaned with their beards removed<BR>1/4 pound Chinese leek strips, cut into two-inch segments<BR>2 Tablespoons vegetable oil<BR>1/2 cup chicken broth<BR>1 egg white<BR>1 teaspoon salt<BR>2 Tablespoons rice wine<BR>1/4 teaspoon ground Sichuan pepper<BR>1 Tablespoon cornstarch<BR><B><I>Preparation:</B></I><BR>1. Bring four cups of water to a boil, then immerse the mussels for half minute, and drain. This is best done in two batches.<BR>2. Heat wok, then add oil, and add the mussels and fry for one minute then add the leeks and fry one minute more.<BR>3. Mix all the remaining ingredients and stir , then add to the mussel-leek mixture, stir and simmer for half minute, then remove to a pre-warmed platter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B5FDD53F0E1D6641A4F663C05C6DF6D3', X'6CEEF3262CE39847AC9D82897997B7FE', X'6CEEF3262CE39847AC9D82897997B7FE', 0, 'javascript:', 'Mussels, Fuzhou-style', 2, NULL, 0, 'Mussels, Fuzhou-style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2214', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds fresh mussels, scrubbed well, beards removed<BR>1 Tablespoon vegetable oil<BR>3 cloves fresh garlic, peeled and chopped<BR>6 slices fresh ginger, peeled and slivered<BR>2 teaspoons chili paste with garlic<BR>1 teaspoon fermented black beans, mashed well<BR>1 scallion, sliced at an angle<BR>2 Tablespoons cornstarch mixed with a like amount of cold water<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Rinse the mussels, and put them and the oil into a pre-heated wok or fry pan. Add the garlic and ginger, chili paste, and fermented black beans and stir-fry for four minutes; they should be open.<BR>2. Next, add the scallion and the cornstarch mixture and stir-fry for two minutes. Discard any clams that do not open, then add sesame oil, stir two or three times, transfer the mussels to a heated bowl, and serve.<BR>Mussels, spinach, and mushrooms Ingredients: ½ cup spinach leaves, sliced thinly ½ cup diced fresh Chinese black mushroom caps 4 eggs, beaten well ½ cup unsweetened soy milk ½ cup vegetable oil 1 Tablespoon dark soy sauce 1 Tablespoon oyster sauce 1 teaspoon granulated sugar ½ teaspoon salt ½ tablespoon cornstarch mixed with one tablespoon of cold water Preparation: 1. Mix spinach leaves, mushrooms, eggs, and soy milk and put this into a plastic-wrap-lines square heat-proof dish and steam over boiling water for eight minutes, then remove and allow to cool before cutting its contents into one-inch squares, discarding the plastic wrap. 2. Heat a wok or fry pan, add the oil, and fry the tofu squares on both sides until they are golden in color, then remove from the oil and dry on paper towels, and set into a pre-heated serving dish. 3. Add the soy sauce, oyster sauce, sugar, and salt, and one tablespoon of water to the wok, stir, then add the cornstarch mixture and stir until somewhat thickened before pouring this over the tofu squares, and serving.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F58E5D23805DF843B5470FD60F786BF1', X'4B6528100363DC4885C294C0C1471422', X'4B6528100363DC4885C294C0C1471422', 0, 'javascript:', 'Mussels, Spinach, and Mushrooms Squares', 2, NULL, 0, 'Mussels, Spinach, and Mushrooms Squares', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2215', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup spinach leaves, sliced thinly<BR>½ cup diced fresh Chinese black mushroom caps<BR>4 eggs, beaten well<BR>½ cup unsweetened soy milk<BR>½ cup vegetable oil<BR>1 Tablespoon dark soy sauce<BR>1 Tablespoon oyster sauce<BR>1 teaspoon granulated sugar<BR>½ teaspoon salt<BR>½ tablespoon cornstarch mixed with one tablespoon of cold water<BR><B><I>Preparation:</B></I><BR>1. Mix spinach leaves, mushrooms, eggs, and soy milk and put this into a plastic-wrap lined square heat-proof dish, then steam over boiling water for eight minutes. Remove and allow to cool, and discard the plastic wrap.<BR>2. Now cut the contents into one-inch squares.<BR>3. Heat a wok or fry pan, add the oil, and fry the squares on both sides until they are golden in color, then remove them from the oil and dry them on paper towels. Then put them into a pre-heated serving dish.<BR>4. Mix soy sauce, oyster sauce, sugar, and salt, and one tablespoon of water, put this into the wok, stir, add the cornstarch mixture and simmer until somewhat thickened, then pour this over the squares, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F2E55E429EA67349B30A10F936881360', X'4B7C052C1203AF40976A70D30F1A44FF', X'4B7C052C1203AF40976A70D30F1A44FF', 0, 'javascript:', 'Mustard Greens with Crab Meat', 2, NULL, 0, 'Mustard Greens with Crab Meat', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2245', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>3 cups fresh-cut mustard green stems, each up to two inches long<BR>1/2 cup crab meat, all cartilage removed<BR>1/2 cup chicken stock<BR><B><I>reparation:</B></I><BR>1. Discard any leafy parts on the stems.<BR>2. Heat wok or fry pan and add the crab meat and stir-fry for one minute, then remove from the wok or pan, and set aside.<BR>3. Add mustard greens stems and the stock, and bring to the boil, then cover the pan and reduce the heat to a low simmer and cook for two minutes, then stir, and cook another minute, add the crab meat, stir, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'098D585C1CDF50428180282F62BD1125', X'AF9FE0A1F04E91498550DB0FDB686D89', X'AF9FE0A1F04E91498550DB0FDB686D89', 0, 'javascript:', 'Octopus with Cucumbers', 2, NULL, 0, 'Octopus with Cucumbers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2083', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 octopus<BR>2 cucumbers, cut in half the long way, then sliced on an angle<BR>½ teaspoon salt<BR>1 Tablespoon granulated sugar<BR>1 Tablespoon Chinese white vinegar<BR>½ teaspoon dry mustard powder<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Cook the octopus for three minutes in boiling water, drain and put it into cold water; then repeat twice more. Using a sharp knife, cut and see if the octopus is tender, if not repeat these steps one more time and check again. Continue until it is.<BR>2. Mix cucumbers with the salt, sugar, and vinegar and let that rest for ten minutes, then drain the cucumbers.<BR>3. Mix the cooked cold octopus with the mustard powder and sesame oil, and set this in the center of a serving platter, and put the cucumbers around it; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4937010A321AE5418086658A2508067B', X'A886E37F1FFC4A4EA632B9C708465489', X'A886E37F1FFC4A4EA632B9C708465489', 0, 'javascript:', 'Oyster Omelette', 2, NULL, 0, 'Oyster Omelette', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=414', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound of freshly shucked oysters, their juice reserved<BR>3 Tablespoons tapioca flour<BR>1 and 1/2 Tablespoons rice flour<BR>1/2 teaspoon chicken stock granules or powder<BR>3 Tablespoons corn oil, divded into two even batches<BR>4 eggs, lightly beaten, divided into two even batches<BR>3 cloves garlic, minced fine, divided into two even batches<BR>1 Tablespoon soy sauce, divided into two even batches<BR>1 Tablespoon rice winedivided into two even batches<BR>1/16 teaspoon ground white pepperdivided into two even batches<BR>two sprigs of fresh coriander, for garnish<BR><B><I>Preparation:</B></I><BR>1. Rinse the oysters and drain them well.<BR>2. Strain their liquid through a fine strainer and reserve it.<BR>3. Mix both flours and chicken granules or powder with three-quarters of a cup of water to make a batter.<BR>4. Heat half the oil in flat-bottomed pan and pour in half the batter. Swirl it around and when partially set, add half the beaten eggs. Then make a whole in the center and add half each of the garlic, soy sauce and rice wine and ground pepper; then add half the oysters and stir them just until they are heated through, Be careful not to overcook them.<BR>5. Repeat for the second half of the ingredients; and when ready, garish each batch with the coriander. If desired, serve with chili sauce on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9DC84259B780F540B87FBA35CE88C3EF', X'7B248F7244258F4C98F4EE34CD3F730B', X'7B248F7244258F4C98F4EE34CD3F730B', 0, 'javascript:', 'Oyster Sauce made Two Ways', 2, NULL, 0, 'Oyster Sauce made Two Ways', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=148', '<TD><RECIPE><B>Method 1:</B> Grind a pint of raw oysters and bring them, the liquid drained from them, and one-quarter to one half cup of water to the boil. Boil for five minutes, then reduce the heat and simmer for half an hour. Strain and discard the oysters at this point, add three tablespoons soy sauce to the remaining oyster liquid, stir, and use.<BR><B>Note:</B> This can be refrigerated for up to one week, the discarded oysters used in an omelet or another dish. <B>Method 2:</B> Mix one or more pints of raw oysters and the liquid drained from them with one-half cup of chicken stock and two tablespoons of Shaoxing wine or dry sherry. Bring this mixture to the boil then simmer for ten to fifteen minutes. Then add three tablespoons of soy sauce, one teaspoon of sugar, and a teaspoon of corn oil. Stir well and simmer for another ten minutes. Cool. Then strain and discard the oysters.<BR><B>Note:</B> This can be refrigerated for up to one week, the discarded oysters used in an omelet or another dish.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A148408FF952374E8CB0837F2613CC14', X'D77D1E25FE28AB499CE61BC0218E11C4', X'D77D1E25FE28AB499CE61BC0218E11C4', 0, 'javascript:', 'Oyster Sausages', 2, NULL, 0, 'Oyster Sausages', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2139', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 ounces dried oysters rinsed in hot water, then refresh the hot water and allow them them to sit in it for half an hour, then drain<BR>1/4 pound raw fish<BR>1/4 pound fully cooked ham<BR>1/2 cup water chestnuts or bamboo shoots<BR>1 cup soaked shiitake mushrooms, stems removed and discarded<BR>1/4 cup canned bamboo shoots<BR>3 slices fresh ginger<BR>1 egg, beaten<BR>1/4 cup vegetable oil<BR>several sprigs fresh coriander for garnish<BR><B><I>Preparation:</B></I><BR>1. Mince or grind the oysters, with the raw fish, ham, water chestnuts or bamboo shoots, mushrooms, and the ginger, then add the egg and shape into two- or three-inch round patties about one-quarter-inch thick.<BR>2. Heat wok or fry-pan, add the oil, and fry te patties on one side, ten on the other side, until set and browned. Serve then with the garnish on top.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EFAA4676DC67BA499427B5D27162BBEE', X'96B7074127E1BA41A69AFAF2BEE162AC', X'96B7074127E1BA41A69AFAF2BEE162AC', 0, 'javascript:', 'Oysters and Bitter Gourd', 2, NULL, 0, 'Oysters and Bitter Gourd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2136', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup bitter gourd sliced thinly<BR>1 Tablespoon coarse salt<BR>1/4 cup sweet potato flour<BR>1 teaspoon granulated sugar<BR>1 teaspoon chicken bouillon powder<BR>1 cup small oysters<BR>1 egg, beaten until lemon ellow<BR>1 Tablespoon vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Toss the bitter gourd slices with the salt and allow to rest for one hour. Then rinse them and dry the slices well.<BR>2 Mix four, sugar, bouillon powder, and the oysters and beaten egg.<BR>3. Heat a wok or fry pan, add the oil, then the oyster mixture and fry just until set, then turn it over and fry the second side until it is just about to set, then put it on a pre-heated plate or platter, and cut into six or sight wedges, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1416EF9BC7EFB74FB19BBEE13199F43A', X'7CABA8257087B64EA1ABF7F2676CE0FD', X'7CABA8257087B64EA1ABF7F2676CE0FD', 0, 'javascript:', 'Oysters and Crabs in Red Oil', 2, NULL, 0, 'Oysters and Crabs in Red Oil', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1993', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 small crabs, claw meat only, removed from their shells<BR>2 Tablespoons rice wine<BR>1 Tablespoon cornstarch<BR>1 scallion, green part only, minced<BR>1 clove fresh garlic, peeled and minced<BR>10 shucked oysters, each cut in half<BR>1 Tablespoon vegetable oil<BR>½ teaspoon red hot oil<BR><B><I>Preparation:</B></I><BR>1. Steam the crab legs until tender, about five minutes, then remove them from their shells and coarsely dice them.<BR>2. Mix rice wine, cornstarch, scallion pieces, garlic and oyster halves and set aside.<BR>3. Heat a wok or fry pan, add the oil, and then add the oyster mixture and stir-fry for one minute before adding crab meat and red oil. Stir fry for two minutes, then serve on a pre-heated platter.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B90FAC6628E0CC45AAE865913F365B54', X'2C1B59C2F870C7429521087AC6DC9F59', X'2C1B59C2F870C7429521087AC6DC9F59', 0, 'javascript:', 'Oysters and Salmon Roe', 2, NULL, 0, 'Oysters and Salmon Roe', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2016', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound green-lipped mussels with out the shells<BR>1 Tablespoons vegetable oil<BR>2 scallions, angle-sliced<BR>1/2 cup chicken stock<BR><B><I>Preparation:</B></I><BR>1. Discard any bears on the mussels, rinse them well, and set them in warm water for five minutes so they disgorge any sand.<BR>2. Heat a wok or fry pan, then add the oil, and stir-fry the mussels for two minutes, then add the scallions and the stock, and boil for one minute. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'29428CE4B249A04D95006B6B132296EC', X'7E89D4210BFE604282C74E3736F42F8C', X'7E89D4210BFE604282C74E3736F42F8C', 0, 'javascript:', 'Oysters and Tofu with Black Bean Sauce', 2, NULL, 0, 'Oysters and Tofu with Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2134', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>25 oysters, shucked and rinsed well<BR>3 Tablespoons mixed cornstarch and water chestnut flour, divided in half<BR>1/2 pound silken tofu, cut nto one-inch squares<BR>2 Tablespoons vegetable oil<BR>1 heaping Tablespoon fermented black beans rinsed<BR>1 teaspoon fresh ginger, rinsed<BR>1 teaspoon fresh garlic, peeled and minced<BR>1 teaspoon minced green part of a scallion<BR>1 small piquant chili pepper, seeded and minced<BR>2 Tablespoons dark soy sauce<BR>1 Tablespoon Chinese rice wine<BR><B><I>Preparation:</I></B><BR>1. Toss the oysters with half the flour and set them aside.<BR>2. Put tofu in boiling water for one minute, then drain and set this aside.<BR>3. Heat the wok or fry pan, add the oil, and stir-fry the black beans for one minute, then add the ginger, garlic, scallion pieces, and the chili pepper and stir-fry for one more minute then add the oysters and the tofu, and stir-fry another minute. Next, add the soy sauce, rice wine, and the rest of the flour. Toss well and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3E8326A430957645A8CFDCB2C7B4D8D8', X'8540090E0D6B094FB2F4AC294AA8858A', X'8540090E0D6B094FB2F4AC294AA8858A', 0, 'javascript:', 'Oysters in Black Bean Sauce', 2, NULL, 0, 'Oysters in Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2295', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound freshly shucked oysters<BR>1 teaspoon coarse salt<BR>1/4 cup vegetable oil<BR>3 slices fresh ginger<BR>3 garlic cloves, peeled and sliced<BR>3 Tablespoons fermented black beans, soaked and mashed<BR>2 scallions, cut into half-inch pieces<BR>2 Tablespoons oyster sauce<BR>2 teaspoons cornstarch mixed with one tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Mix oysters with the salt, let rest for ten minutes then blanch them in one quart boiling water for half minute, then remove them promptly.<BR>2. Heat wok, add the vegetable oil, then the ginger and the garlic, and stir-fry for one minute before adding the mashed black beans, Stir these for another minute then add the oysters and the cornstarch mixture and stir until thickened. Serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2E67E5B70EA8A74AB10BFC7521154110', X'73906C82F5C2C04F889AADED7688CE0C', X'73906C82F5C2C04F889AADED7688CE0C', 0, 'javascript:', 'Oysters in Black Bean Sauce', 2, NULL, 0, 'Oysters in Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1499', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound freshly shucked oysters<BR>1 teaspoon coarse salt<BR>1/4 cup vegetable oil<BR>3 slices fresh ginger<BR>3 garlic cloves, peeled and sliced<BR>3 Tablespoons fermented black beans, soaked and mashed<BR>2 scallions, cut into half-inch pieces<BR>2 Tablespoons oyster sauce<BR>2 teaspoons cornstarch mixed with one tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Mix oysters with the salt, let rest for ten minutes then blanch them in one quart boiling water for half minute. Remove them promptly.<BR>2. Heat wok, add the vegetable oil, then the ginger and the garlic, and stir-fry for one minute before adding the mashed black beans, Stir these for another minute then add the oysters and the cornstarch mixture and stir until thickened. Serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'27726A7A0D5D1A4890B9304BF1DD3931', X'793CAC1135748340A60377AD14F4AC64', X'793CAC1135748340A60377AD14F4AC64', 0, 'javascript:', 'Oysters in Citrus Honey', 2, NULL, 0, 'Oysters in Citrus Honey', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1399', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 large oysters<BR>1 Tablespoon Worcestershire sauce<BR>1 Tablespoon fish sauce<BR>1 Tablespoon ginger juice<BR>2 Tablespoons Shaoxing wine<BR>1 Tablespoon minced onion<BR>1/4 teaspoon ground white pepper<BR>2 Tablespoons vegetable oil<BR>3 Tablespoons honey<BR>1 Tablespoon orange or tangerine juice<BR>1 sprig fresh coriander cut into one-inch lengths<BR><B><I>Preparation:</B></I><BR>1. Soak oysters in tepid water for one hour, then rinse and remove any sand.<BR>2. Mix Worcestershire and fish sauces, stir in ginger juice, wine, minced onion and ground white pepper, and marinate the oysters for half an hour, then drain and set liquid aside.<BR>3. Heat oil and fry oysters on low heat for one minute, then add honey and citrus juice, increase heat and when sauce bubbles stir and fry them for one to two more minutes, stirring all the time. Transfer to a heated serving latter, then serve topping each oyster with a piece of coriander.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E1ACA1F74A1D7E48AFEAAE8344796705', X'C553CD24491F4F458BBB91D4D8837628', X'C553CD24491F4F458BBB91D4D8837628', 0, 'javascript:', 'Oysters, Deep-fried, and Chinese style', 2, NULL, 0, 'Oysters, Deep-fried, and Chinese style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2133', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>30 freshly shucked oysters, any liquid from them set aside<BR>2 teaspoons ginger, peeled and minced<BR>1/4 cup dry white wine mixed with the liquid from the shucked oysters<BR>1 Tablespoon lemon juice<BR>3 Tablespoons cornstarch<BR>dash of sugar<BR>dash of salt<BR>1 cup sesame seeds<BR>1 cup vegetable oil for deep frying<BR>batter to coat the oysters with (optional) made with 1 teaspoon baking powder, 4 Tablespoons cornstarch, 1 tablespoon melted lard<BR><I><B>Preparation:</B></I><BR>1. Soak oysters with ginger, wine, oyster and lemon juices for half an hour,then drain and mix with the cornstarch, sugar,and salt. Set aside for one hour.<BR>2. Heat wok or fry pan, add the oil, and put the oysters in a strainer and shake them in the shake off excess cornstarch and sesame seeds before putting them in the hot oil for one minute. Remove and drain them on paper towels and serve. <B>Note:</B> for the batter, mix baking powder, cornstarch, and melted lard and coat the oysters, then drain excess batter, and deep fry for one minute, then toss with any excess sesame seeds.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E1FF7F40C0D378438776A7D303224E5B', X'B876E0117BD0394AB86C57E5C139C545', X'B876E0117BD0394AB86C57E5C139C545', 0, 'javascript:', 'Oysters, Taiwanese Style', 2, NULL, 0, 'Oysters, Taiwanese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=927', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup shelled oysters<BR>3 large dried black mushrooms, soaked for twenty minutes<BR>1 green pepper<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon fermented wine lees<BR>1 teaspoon soy sauce<BR>1 teaspoon wasabi paste<BR>1/4 teaspoon wasabi powder<BR>1/4 teaspoon salt<BR>1 teaspoon sugar<BR>1 teaspoon cornstarch mixed with one tablespoon water<BR><B><I>Preparation:</B></I><BR>1. Blanch oysters in boiling water for half a minute. Drain and set aside.<BR>2. Remove soaked mushrooms, reserve half cup of the liquid. Then remove their stems and cut in quarters. Seed the pepper, remove white material and cut into pieces size of the mushrooms.<BR>3. Bring reserved mushroom water, rice wine, and wine lees to the boil, add mushrooms and simmer three minutes, then add peppers, and simmer another minute. Next add soy sauce, wasabi paste and powder, salt, and sugar, and the oysters. Bring to the boil, add cornstarch mixture, stir until thick and clear, pour into a medium-size bowl. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E8BB6154CA5BCD45B47CF40497A01314', X'A25AF26D1C0C5C4FBD1596D371D419C0', X'A25AF26D1C0C5C4FBD1596D371D419C0', 0, 'javascript:', 'P.F. Chang''s Imperial Black Cod', 2, NULL, 0, 'P.F. Chang''s Imperial Black Cod', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=935', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon coarse salt<BR>3 ounces shelled soybeans<BR>8 ounces black cod, sliced in half-inch pieces<BR>1/2 teaspoon Chinese rice wine<BR>1/2 teaspoon cornstarch<BR>1 teaspoon vegetable oil<BR>1/2 teaspoon sesame oil<BR>1/2 teaspoon minced garlic<BR>1 Tablespoon diced red pepper<BR>3 black mushrooms, soaked, stems removed, and diced<BR>2 ounces abalone or oyster sauce<BR>3 Tablespoons toasted pine nuts<BR><B><I>Preparation:</B></I><BR>1. Bring small pot of water to the boil, add salt and the soy beans and simmer for twenty minutes. Then drain and set aside.<BR>2. Mix cod with wine and cornstarch.<BR>3. Heat a wok or fry pan then add vegetable and sesame oils, and then add the garlic and the cod. Stir fry for just half a minute before adding red pepper and mushroom pieces. Stir-fry these together for another half minute, then stir in the abalone or oyster sauce. Toss well, put into a pre-heated platter or bowl and top with pine nuts. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'035AAFB3A3250E4189821FC0284A26E1', X'65D4C5B976F68D4B893C1562E7186607', X'65D4C5B976F68D4B893C1562E7186607', 0, 'javascript:', 'Pan-fried Salmon in a Fragrant Sauce', 2, NULL, 0, 'Pan-fried Salmon in a Fragrant Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1666', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 scallions<BR>3 to 4 Tablespoons cooking oil<BR>1 large salmon filet, about half- to one-inch thick<BR>2 Tablespoons thin soy sauce<BR><B><I>Preparation:</B></I><BR>1. Slice the scallions on the bias into two-inch lengths.<BR>2. Heat the oil in a flat-bottomed pan until it begins to smoke. Add the salmon and pan-fry on one side for about ten seconds or longer if a thick filet, then turn it over and add the scallions.<BR>3. After another ten seconds, cover the pan tightly and turn off the heat. Allow the pan to sit on te burner for one additional minute, then remove from the wok.<BR>4. Sprinkle with soy sauce, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'43BF943A54E8344694BCCB46D8427164', X'51D4E0BFF347CC419A386BD2E6192E11', X'51D4E0BFF347CC419A386BD2E6192E11', 0, 'javascript:', 'Pearl Column Scallops', 2, NULL, 0, 'Pearl Column Scallops', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=811', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>20 large scallops or ten ounces, coarsely chopped<BR>1/2 pound shrimp, shells and veins removed, then minced<BR>4 eggs, separated<BR>1 teaspoon Chinese rice wine<BR>1/2 teaspoon salt<BR>1 teaspoon cornstarch<BR>1 Tablespoon vegetable oil<BR>1 teaspoon sesame oil<BR>1 Tablespoon minced Yunnan or Smithfield ham<BR><B><I>Preparation:</B></I><BR>1. Steam scallops for one minute, drain and set aside.<BR>2. Mix minced shrimp with egg whites, rice wine, salt, and cornstarch.<BR>3. Heat a wok or fry pan and add the oil, then fry the shrimp mixture. Do not stir. When set turn it over and fry another minute then remove to a plate, leaving any remaining oil in the wok.<BR>4. Beat yolks and fry them. Do not stir, and when set turn over for another minute and then slide this on top of the shrimp pancake. Sprinkle scallops on top.<BR>5. Cut the egg mixture into eight pieces, sprinkle the sesame oil and the ham on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'123C0313A83EC44F8EF26A8232CBD5ED', X'27A4B38D9F7CB9408A7EA672AD70E19A', X'27A4B38D9F7CB9408A7EA672AD70E19A', 0, 'javascript:', 'Persimmon and Scallops', 2, NULL, 0, 'Persimmon and Scallops', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=434', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 very ripe persimmons, blanched, peeled, and seeded<BR>15 large sea scallops<BR>2 Tablespoons cornstarch<BR>15 slices of bacon<BR>1/4 teaspoon white pepper<BR>1/4 teaspoon sugar<BR>1 teaspoon minced fresh coriander<BR>2 Tablespoons corn oil<BR><B><I>Preparation:</B></I><BR>1. Mash persimmons with a potato masher mixing in two tablespoons cold water.<BR>2. Rinse scallops and drain well, then mix with the cornstarch and wrap each one around the outside edge with a slice of bacon. Leave the end tucked under the scallop.<BR>3. Mix pepper, sugar, and coriander and set aside.<BR>4. Heat oil and fry the bacon with the end piece on top for one minute, then turn so the bacon is on the bottom. Sprinkle with pepper mixture and continue frying for just two minutes more, then remove to eight warmed serving plates. <BR>5. Heat mashed persimmon mixture in the remaining liquid in the pan for one to two minutes, until boiling, then drizzle this over each set of scallops and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'790FEC3E9E17BE4EB8A417686D6DD9E6', X'D011E6C4846C444DA4C9BA6B49AC639E', X'D011E6C4846C444DA4C9BA6B49AC639E', 0, 'javascript:', 'Pickled Mussels with Garlic', 2, NULL, 0, 'Pickled Mussels with Garlic', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=912', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound fresh mussels in their shells, washed, their beards removed<BR>1 small hot red chili pepper, seeded and slivered<BR>4 cloves fresh garlic, peeled and sliced<BR>3 slices fresh ginger, peeled and slivered<BR>1 Tablespoon thin soy sauce<BR>12 Tablespoons soy jam<BR>1 teaspoon sugar<BR>1 Tablespoon rice wine<BR>2 Tablespoon black vinegar<BR>3 teaspoon licorice powder<BR><B><I>Preparation:</B></I><BR>1. Put mussels in a pot and add water to cover them. Bring the water to the boil, then turn off the heat. Let rest one minute, and remove them from the pot. Discard their shells. Strain and reserve the liquid for soup or another use.<BR>2. Take a medium-size serving bowl and put in the chili pepper, garlic, and ginger. Mix, well, then add soy sauce, soy jam, sugar, rice wine, vinegar, and licorice powder, and mix again.<BR>3. Pour over the mussels, cover, and refrigerate four hours or overnight. Stir once or twice. Drain liquid, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F00BAC96678F5D4C994A4965E4B3DF85', X'41E6E2D53205A44E8D70D93EFA0A9A28', X'41E6E2D53205A44E8D70D93EFA0A9A28', 0, 'javascript:', 'Plain Pan-fried Fish Cheeks', 2, NULL, 0, 'Plain Pan-fried Fish Cheeks', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2328', '<TD><RECIPE><B></I>Ingredients:</B></I><BR>1 Tablespoon flour<BR>salt and pepper, to taste<BR>20 fish cheeks<BR>3 Tablespoons olive oil<BR>3 Tablespoons chopped cilantro<BR>2 teaspoons chicken fat<BR><B><I>Preparation:</B></I><BR>1. Mix the flour, salt, and pepper and dust the cheeks with this and set aside for ten or fifteen minutes.<BR>2, Heat a wok or fry pan, then add the oil and fat, and fry the cheeks for two minutes, turn them over and fry for one minute more, then remove them to paper towels to drain. Put them into a bowl, toss with the cilantro, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4ACE69DEB5D99D478F1177601F9A451C', X'5B9CE17643A01048A0BDCD6B65F52AC2', X'5B9CE17643A01048A0BDCD6B65F52AC2', 0, 'javascript:', 'Prawn Cutlets', 2, NULL, 0, 'Prawn Cutlets', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1907', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound shrimp, shelled and their shells discarded, then minced or ground<BR>7 ounces firm tofu, mashed or cold mashed potatoes<BR>1 Tablespoon sesame oil<BR>1 Tablespoon thin soy sauce<BR>1 teaspoon ground black pepper<BR>2 eggs, lightly beaten<BR>3 Tablespoons cornstarch<BR>vegetable oil for deep frying<BR>accompaniments in individual bowls can include: Dijon or hot mustard, chili sauce, black vinegar, a cucumbers thinly sliced, and/or pickled ginger<BR><B><I>Preparation:</B></I><BR>1. Mix the shrimp, tofu, sesame oil, soy sauce, and pepper until well-blended, then add the beaten eggs.<BR>2. Shape into four patties, then coat each one with cornstarch.<BR>3. Heat the oil and deep-fry the patties until golden brown, then serve immediately with the accompaniments on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'39E6097F8C29F04EAF610236280FE29D', X'4C50AF3138B9DD4387F113A230408574', X'4C50AF3138B9DD4387F113A230408574', 0, 'javascript:', 'Prawns in Sa Cha Sauce', 2, NULL, 0, 'Prawns in Sa Cha Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1494', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 prawns (about half pound)<BR>2 Tablespoons sa cha sauce, divided<BR>3 Tablespoons soy sauce<BR>2 Tablespoons Chinese rice wine<BR>1 and 1/2 Tablespoons granulated sugar<BR>1/2 teaspoon chili oil<BR>4 cups soybean oil<BR>1/2 green pepper, diced<BR>1/2 red pepper diced<BR>1 teaspoon minced fresh ginger<BR>1 teaspoon peeled and minced garlic<BR>1 scallion cut into one-inch pieces<BR>3 ounces water chestnuts, sliced thin<BR>3 ounces sweet onion, coarsely diced<BR>1 Tablespoon cornstarch mixed with one tablespoon cold water<BR>1 teaspoon sesame oil<BR>1 tomato made into a rose and parsley for garnish (optional)<BR><B><I>Preparation:</B></I><BR>1. Peel shrimp and remove black vein from the back, then mix with half the sa cha sauce, soy sauce, wine, sugar, and the chili oil and marinate for fifteen minutes.<BR>2. Heat wok, add the oil and the prawns stirring them until separated, then add the red and green peppers. When the shrimp have curled, about one to two minutes, remove them and the peppers and set aside. Discard all but two tablespoons of the oil reserving the large amount for another use.<BR>3. Reheat this two tablespoons of oil, add the ginger and garlic, the other tablespoon of sa cha sauce, and the scallions, water chestnuts, and the onions and stir for about one minute or until fragrant. Then add the shrimp and the peppers and the cornstarch-water mixture. Stir until thickened, add the sesame oil, and serve on a platter.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2D57C284BDA9D145A6A83AE32115F337', X'266A6D37A3D1D44D825761233D251180', X'266A6D37A3D1D44D825761233D251180', 0, 'javascript:', 'Prawns in Sa Cha Sauce', 2, NULL, 0, 'Prawns in Sa Cha Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1395', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 prawns (about half pound)<BR>2 Tablespoons <I>sa cha</I> sauce, divided<BR>3 Tablespoons soy sauce<BR>2 Tablespoons Chinese rice wine<BR>1 and 1/2 Tablespoons granulated sugar<BR>1/2 teaspoon chili oil<BR>4 cups soybean oil 1/2 green pepper, diced<BR>1/2 red pepper diced<BR>1 teaspoon minced fresh ginger<BR>1 teaspoon peeled and minced garlic<BR>1 scallion cut into one-inch pieces <BR>3 ounces water chestnuts, sliced thin<BR>3 ounces sweet onion, coarsely diced<BR>1 Tablespoon cornstarch mixed with one tablespoon cold water<BR>1 teaspoon sesame oil<BR>1 tomato made into a rose and some parsley for garnish (optional)<BR><B><I>Preparation:</B></I><BR>1. Peel shrimp and remove black vein from the back, then mix with half the <I>sa cha</I> sauce, soy sauce, wine, sugar, and the chili oil and marinate for fifteen minutes.<BR>2. Heat wok, add the oil and the prawns stirring them until separated, then add the red and green peppers. When the shrimp have curled, about one to two minutes, remove them and the peppers and set aside. Discard all but two tablespoons of the oil reserving the large amount for another use.<BR>3. Reheat this two tablespoons of oil, add the ginger and garlic, the other tablespoon of sa cha sauce, and the scallions, water chestnuts, and the onions and stir for about one minute or until fragrant. Then add the shrimp and the peppers and the cornstarch-water mixture. Stir until thickened, add the sesame oil, and serve on a platter. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4CAF7348216B3B448A216F71E3F58AE9', X'52510DB3A2B3034E9C5328FC1D384D8F', X'52510DB3A2B3034E9C5328FC1D384D8F', 0, 'javascript:', 'Prawns on Rice Crusts', 2, NULL, 0, 'Prawns on Rice Crusts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1030', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon vegetable oil<BR>3 cups cooked rice<BR>1 teaspoon and two tablespoons vegetable oil<BR>¼ pound shrimp, peeled, veins removed and discarded<BR>1 egg white, lightly beaten<BR>1 Tablespoon cornstarch<BR>¼ pound chicken breast meat, shredded and boiled for three minutes<BR>1 teaspoon salt<BR>2 teaspoons sugar<BR>2 Tablespoons tomato sauce<BR>2 Tablespoons Chinese rice wine<BR>2 Tablespoon Chinese white vinegar<BR>1 Tablespoon cornstarch mixed with two tablespoons cold water<BR>1 cup vegetable oil for frying<BR>1 to 2 sprigs fresh coriander<BR><B><I>Preparation:</B></I><BR>1. Press cooked rice into a pan pre-oiled with the teaspoon of vegetable oil. The rice should be about half -inch thick. Then bake it in 250 degrees F oven for forty minutes until dry and crisp.<BR>2. Remove pan from oven, take rice out breaking it up into two- to three-inch pieces.<BR>3.Coat shrimp with egg white and cornstarch.<BR>4, Heat two tablespoons of oil and stir-fry the shrimp for one minute. Then add shredded chicken, salt, sugar, tomato sauce, rice wine, and vinegar and bring to the boil. Add cornstarch and water mixture, and stir until thickened.<BR>5. Heat cup of oil, deep fry rice crusts until very crisp and lightly browned. Drain well and put them on a serving platter.<BR>6. Pour shrimp and chicken mixture over the hot rice crusts, garnish with the coriander, and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'915244692749FC4DAEDB7B42B884ACA3', X'1CE450B19B05934B80AB5F475A4D8984', X'1CE450B19B05934B80AB5F475A4D8984', 0, 'javascript:', 'Preserved Sausage and Beancurd Skin Rolls', 2, NULL, 0, 'Preserved Sausage and Beancurd Skin Rolls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=949', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1/2 pound dace or other white fish, minced<BR>1 water chestnut, minced<BR>3 tablespoons tangerine peel, soaked, then minced<BR>1 Tablespoon dried shrimp, soaked and minced<BR>1 Tablespoon oyster sauce<BR>1 teaspoon thin soy sauce<BR>1/2 teaspoon salt<BR>dash ground white pepper<BR>1 teaspoon sesame oil<BR>2 sheets purple or black seaweed<BR>2 Chinese preserved sausages, each about ten inches long<BR>1 large sheet dried or soft bean curd skin, cut into two pieces<BR>1 cup vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Mix minced fish, water chestnut, tangerine peel, and dried shrimp. Add oyster sauce, thin soy sauce, salt, ground white pepper, and sesame oil and two tablespoons cool water.<BR>2. Steam sausages for twenty minutes.<BR>3. On one piece of bean curd skin, spread half the fish mixture. Top with one sheet of seaweed, and put one sausage at one end and roll, but not too tightly, into a sausage-shaped piece. Repeat with the rest of these ingredients.<BR>4. Brush both rolls with about a tablespoon of the oil, and set on a steam-proof plate.<BR>5. Steam over rapidly boiling water for five minutes, remove, and set aside to cool.<BR>6. Heat oil, deep-fry the rolls until golden brown, drain on papertowels, then slice on an angle, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6AD87B2C1703274F86AADE955C7814F7', X'4C2002A3D500DC4D9817556C5EA1F33B', X'4C2002A3D500DC4D9817556C5EA1F33B', 0, 'javascript:', 'Qianlong Gold Coin and Swimming Dragon', 2, NULL, 0, 'Qianlong Gold Coin and Swimming Dragon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1505', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound eel, skinned and boned, head and intestines removed, flesh cut into half-inch by three-inch strips<BR>1 Tablespoon minced fresh coriander<BR>1 teaspoon garlic powder<BR>1 scallion, finely minced<BR>1 teaspoon fresh ginger, finely minced<BR>2 Tablespoons vegetable oil<BR>2 Tablespoons Shaoxing or another Chinese rice wine<BR>1 teaspoon granulated sugar<BR>1 Tablespoon cornstarch mixed with two tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Marinate eel with half teaspoon cold water and the minced coriander, garlic powder, scallion, and ginger for fifteen minutes.<BR>2. Heat wok or fry pan, add the oil, and in half minute, add the eel strips and stir-fry them for two to three minutes before adding the cornstarch mixture. Stir-fry half a minute and it will thicken and cling to the eel. Remove to a pre-heated small platter and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'07EFBFBA162A7046B2E1DF9FAD9B6C35', X'14759A5709061541A1BE0D1D3C63D781', X'14759A5709061541A1BE0D1D3C63D781', 0, 'javascript:', 'Raw Fish--circa 1936', 2, NULL, 0, 'Raw Fish--circa 1936', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1073', '<TD><RECIPE>Slice ulua meat in thin pieces. Pour over it a little cooked peanut oil. Mix prepared mustard with Chinese sauce. Eat ulua by dipping each piece into a small plate of the mustard sauce combination.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'19757AAF8A60FA4D883DC1B96B1382AB', X'3F68CFC917BE8245A08C30644CCD96BE', X'3F68CFC917BE8245A08C30644CCD96BE', 0, 'javascript:', 'Razor Clams and Tripe in Black Bean Sauce', 2, NULL, 0, 'Razor Clams and Tripe in Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=757', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 dried Chinese black mushrooms<BR>2 pounds honeycomb tripe<BR>3 Tablespoons corn oil<BR>2 onions, wedge-sliced, top to bottom<BR>1 cup chicken stock<BR>1 cup rice wine<BR>5 Tablespoons dark soy sauce<BR>2 Tablespoons Chinese black vinegar<BR>2 Tablespoon Chinese maltose<BR>5 Tablespoons Chinese black bean sauce with garlic<BR>1 cup chopped fresh coriander<BR>1 pound razor clams, shells removed<BR>3 Tablespoons cornstarch mixed with same amount of cold water<BR><B><I>Preparation:</B></I><BR>1. Soak mushrooms in warm water for one hour, drain and reserve the water. Remove their stems and quarter.<BR>2. Wash and dry the tripe, cut into long thin strips one inch wide.<BR>3. Heat oil and stir-fry the onions for two minutes, then add tripe and fry another two minutes.<BR>4. Put mushrooms, onions, and tripe in heavy casserole. Stir well, then add stock, wine, soy sauce, vinegar, maltose, black bean sauce, and the reserved mushroom water, then stir again. Put the casserole into a pre-heated 350 degree F oven and cover the casserole. Cook for two hours, stirring every half hour or so.<BR>5. Add coriander and razor clams, and the cornstarch mixture and cook another five minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ED7C316EAAA96D46B4348D068AF1AE0E', X'07B224B69473194DB543601A9158213C', X'07B224B69473194DB543601A9158213C', 0, 'javascript:', 'Red Snapper with Mangosteen', 2, NULL, 0, 'Red Snapper with Mangosteen', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=758', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 fresh mangosteens, opened, sections removed and carefully pitted<BR>1/2 teaspoon sugar<BR>1 red snapper, about two pounds, steamed then cooled<BR>1 teaspoon sesame oil<BR>1/4 teaspoon sugar<BR>2 cups carrots, shredded and blanched for one minute, then chilled in ice water before draining<BR>2 cups green turnip such as kohlrabi or silk squash or butternut squash, peeled and finely shredded<BR>2 Tablespoons fresh squeezed lemon juice<BR>2 Tablespoons Chinese rice wine<BR><B><I>Preparation:</B></I><BR>1. Mix mangosteen section with the sugar and set aside.<BR>2. Be sure all bones are removed from each half of the fish, then brush with sesame oil, before setting aside.<BR>3.Mix sugar and carrots, then toss with turnip. Lemon juice, and rice vinegar. Let rest for fifteen minutes, then drain reserving the liquid.<BR>4. On large platter, evenly spread vegetables around, but not to the edge of the platter. Place fish centered on these, and scatter the mangosteen sections on the top. Lightly moisten with some of the marinade and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1313B5E8E404D247B72F3983633B1928', X'F1AEF053AFC0B84683002EAAE6D5085A', X'F1AEF053AFC0B84683002EAAE6D5085A', 0, 'javascript:', 'River Shrimp in Wine Sauce', 2, NULL, 0, 'River Shrimp in Wine Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=737', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 square fermented bean curd<BR>1 teaspoon black bean paste without garlic<BR>1 teaspoon vegetable oil<BR>2 Tablespoons sesame paste mixed with four tablespoons of hot water<BR>1 Tablespoon sugar<BR>3 Tablespoons Chinese rice wine<BR>1 teaspoon Chinese brandy, such as Maotai<BR>1 pound river shrimp, preferable live ones<BR><B><I>Preparation:</B></I><BR>1. Mash fermented tofu, then mix well with the black bean paste.<BR>2. Heat wok, add the oil, then the sesame paste and water mixture, the sugar, and the rice wine and brandy. Stir and remove, do not make this sauce hot, just warm.<BR>3. Dip one shrimp in the sauce, then suck the meat out of the shell, and discard the shell. Continue until they are all consumed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E571C7AC22CFB140968A9FBEA47F78B9', X'711CCE427F7DCA4181E3DE39AC7FE4EF', X'711CCE427F7DCA4181E3DE39AC7FE4EF', 0, 'javascript:', 'Roasted Fish, Dai Style', 2, NULL, 0, 'Roasted Fish, Dai Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=447', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 small fish, each about one pound<BR>1 Tablespoon sesame oil<BR>1/2 onion, chopped<BR>1/4 cup fresh coriander, chopped<BR>8 pitted preserved sour olives, chopped<BR>1 small hot pepper, seeded and chopped<BR>1/2 cup fresh green seaweed (or one-quarter cup of hair vegetable soaked for ten minutes, then drained)<BR>2 lotus leaves or six banana leaves, soaked for ten minutes<BR>4 feet of white string, cut in half, then soaked for ten minutes<BR>1 teaspoon corn oil<BR><B><I>Preparation:</B></I><BR>1. Cut fish in half head to tail, remove scales, and debone it. Then rub it inside and out with the sesame oil and set it aside for fifteen minutes.<BR>2. Cut the seaweed into one-inch pieces and mix well with all the other vegetables.<BR>3. Put half the vegetables on the inside half of one side of one fish. Cover with the other half of the fish. Wrap in a lotus leaf or half of the banana leaves and tie the wrapped fish, but not too tightly. Repeat with the other fish.<BR>4, Roast on an oiled grate three inches above an open fire for seven minutes, then turn and roast another five minutes. Put the fish on a serving platter, and cut away and discard the strings. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'93DE3ABEEEA51944B2A684B112A36EF7', X'A75AC81575B9614CB26398F1D46678F3', X'A75AC81575B9614CB26398F1D46678F3', 0, 'javascript:', 'Salamander Omelet', 2, NULL, 0, 'Salamander Omelet', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2290', '<TD><RECIPE><B><I>Ingredients</B></I><BR>1 small salamander, diced coarsely, and soaked in cold water. Then drained and dried with paper towels<BR>3 Tablespoons vegetable oil<BR>6 eggs, beaten until light lemon yellow<BR>2 scallions, angle-sliced<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, then the eggs, and stir them until the start to set.<BR>2. Add the salamander pieces and the scallion pieces and when this starts to set, gently turn it over and let it set on the other side, then slide it on to a pre-heated platter, cut it in eight wedges, and serve while the eggs are still setting.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5ECD36184AF1A342A52F7F8FD623E5E4', X'07E34299E93AEE45A85A03675A8FD543', X'07E34299E93AEE45A85A03675A8FD543', 0, 'javascript:', 'Salamander with Chili Peppers', 2, NULL, 0, 'Salamander with Chili Peppers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2292', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound salamander, cleaned and cut into one-quarter-inch pieces<BR>1 Tablespoon cornstarch<BR>1 Tablespoon fermented black beans, rinsed and chopped<BR>3 cloves fresh peeled garlic, chopped<BR>5 slices fresh ginger, peeled and chopped<BR>½ teaspoon coarse salt<BR>3 scallions cut into half-inch pieces<BR>1 red onion, cut into half-inch pieces<BR>2 fresh green chilies, seeds removed, then cut into half-inch pieces<BR>3 Tablespoons vegetable oil<BR>1 teaspoon thin soy sauce<BR>2 Tablespoons oyster sauce<BR>2 Tablespoons <I>Shao Xing</I> rice wine<BR>2 teaspoons sesame oil<BR>1 teaspoon granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Cook salamander pieces in boiling water for half an hour, then rinse in cold water for two minutes.<BR>2. Mix and mash the black beans, garlic, and the ginger, then add the salt and set aside.<BR>3. Heat vegetable oil, then stir-fry the onion cubes for one minute, then add the green chili pieces and the salamander pieces and the black bean mixture and stir-fry for one minute, the add the scallions.<BR>4. Mix the soy sauce, oyster sauce, the wine, sesame oil, and the sugar and pour this over the salamander mixture, stir for half a minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'33E183184B6B344B9400216DF42F78DC', X'F0ED6DA5B6770B4DABC37B1AA911AC0C', X'F0ED6DA5B6770B4DABC37B1AA911AC0C', 0, 'javascript:', 'Salamander with Root Vegetables', 2, NULL, 0, 'Salamander with Root Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2291', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 tablespoons vegetable oil<BR>1 medium salamander or two large conch, diced<BR>2 Tablespoons cornstarch<BR>2 Tablespoons water chestnut flour<BR>2 Tablespoons hoisin sauce<BR>1 cup mixed root vegetables, peeled and diced, and boiled for four minutes, then drained<BR>3 cups spinach, coarsely chopped<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, and then the salamander or conch and stir-fry for two minutes before adding the cornstarch and the water chestnut flour, they stir-fry for another minute. <BR>2. Add one cup cold water and the root vegetables, and stir-fry for three minutes or until they are soft, then add the spinach and stir for two minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F2BB00FE0062884B96CF37E67C027DE7', X'F127D80B70B87A4884818B41C8460FCC', X'F127D80B70B87A4884818B41C8460FCC', 0, 'javascript:', 'Salamander with Vegetables', 2, NULL, 0, 'Salamander with Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2031', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 tablespoons vegetable oil<BR>1 medium salamander, 3 to five nails, or the same number of conch, diced<BR>2 Tablespoons cornstarch<BR>2 Tablespoons water chestnut flour<BR>1 pound spinach, coarsely chopped<BR>2 Tablespoons hoisin sauce<BR>1 cup mixed root vegetables, peeled and diced<BR>3 cups spinach, coarsely chopped<BR><B><I>Preparation:</I></B><BR>1. Heat wok or fry pan, add the oil, and then the salamander or conch and stir-fry for two minutes before adding the cornstarch and the water chestnut flour, they stir-fry for two minutes.<BR>2. Add one cup cold water, the hoisin sauce, and the vegetables, and stir-fry for three minutes or until the root vegetable are beginning to soften. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7C541D3B4D12B24E91B4D1D91EAD8D80', X'6416538CC19C3B4F9276F45E0B5F3E0B', X'6416538CC19C3B4F9276F45E0B5F3E0B', 0, 'javascript:', 'Salamander, Cantonese Style', 2, NULL, 0, 'Salamander, Cantonese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2289', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>1 clove garlic, peeled and smashed<BR>1 teaspoon fermented black beans, coarsely chopped<BR>1/4 pound ground pork<BR>1 cup ground salamander or conch meat<BR>3 Tablespoons chicken stock<BR>5 slices fresh ginger, peeled and slivered<BR>1 teaspoon dark soy sauce<BR>2 teaspoons cornstarch mixed with one Tablespoon cold water<BR>1 egg, beaten<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, garlic, and the fermented beans, and stir for on minute or until pork is no longer pink.<BR>2. Now dd the conch or salamander, and stir once or twice before adding the chicken stock, ginger, and the soy sauce, and bring to the boil; then add the cornstarch mixture and stir until this thickens.<BR>4. Put in a pre-heated bowl, put the egg on top, and stir, then cover for one minute before uncovering and serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BD7E1ED6A5196944B5D484F88474F6D3', X'2DB2AA8E94ABEC40A9E07247C8C98AE3', X'2DB2AA8E94ABEC40A9E07247C8C98AE3', 0, 'javascript:', 'Salamanders Cantonese Style', 2, NULL, 0, 'Salamanders Cantonese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2029', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>1 clove garlic, peeled and smashed:<BR>1 teaspoon fermented black beans, rinsed then coarsely chopped<BR>1/4 pound ground pork<BR>1 cup tenderized snail, conch, or salamander<BR>3 Tablespoons chicken stock<BR>5 slices fresh ginger, peeled and slivered<BR>1 teaspoon dark soy sauce<BR>2 teaspoons cornstarch mixed with one tablespoon cold water<BR>1 egg, beaten<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, garlic, and the fermented beans, and stir for on minute or until pork is no longer pink.<BR>2. Now dd the conch or salamander, and stir once or twice before adding the chicken stock, ginger, and the soy sauce. Bring this to the boil, then add the cornstarch mixture and stir until this thickens somewhat.<BR>3. Put in a pre-heated bowl, put the egg on top, and stir, then cover for one minute before uncovering and serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A21F6D6847D7534CBFDFC6EE1EE2CEAA', X'6B39455597D66F48AE5B1D4819B596F7', X'6B39455597D66F48AE5B1D4819B596F7', 0, 'javascript:', 'Salt and Pepper Prawns', 2, NULL, 0, 'Salt and Pepper Prawns', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2358', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon fine salt<BR>1 teaspoon ground Sichuan pepper<BR>1 teaspoon superfine sugar<BR>1 1/2 generous cups flour<BR>20 frozen shrimp, heads and tails left on<BR>chili sauce<BR>cooking oil<BR><B><I>Preparation:</B></I><BR>1. Mix salt, Sichuan pepper, and sugar in a bowl and set aside.<BR>2. Prepare and heat a deep fryer, put the flour in a shallow dish and dip the shrimp in the flour and carefully lower them into the hot oil and deep fry for five minutes.<BR>3. Drain the shrimp on paper towels. Arrange on a serving dish, then season with the spice mixture. Serve immediately with the chili sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FCDA396091FAFE40A9AFC46C11BF9488', X'FA8636A15E542648800C0BC19A5A21CC', X'FA8636A15E542648800C0BC19A5A21CC', 0, 'javascript:', 'Salted Fish and Rice, Modernized', 2, NULL, 0, 'Salted Fish and Rice, Modernized', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1087', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups rice<BR>1 Tablespoon vegetable oil<BR>1/2 pound dark meat chicken, pounded into a paste<BR>1 cup peeled grated eggplant<BR>2 ounces salted fish, rinsed and mashed<BR>2 slices fresh ginger, minced finely or grated, divided in two parts<BR>2 teaspoons granulated or crushed slab of brown sugar, divided in two parts<BR>1 teaspoon sesame oil, divided in two parts<BR>1 teaspoon water chestnut powder, divided in two parts<BR>1 teaspoon thin soy sauce<BR>1 teaspoon granulated sugar<BR>1 teaspoon coarse salt<BR>1/4 teaspoon ground white pepper<BR>1 Tablespoon scallion greens, chopped fine<BR><B><I>Preparation:</B></I><BR>1. Mix rice and vegetable oil in a bowl, then add four cups of water and steam covered for fifteen minutes. Do not remove the cover, just let it set for an additional ten minutes.<BR>2. Mix chicken with the eggplant, half the fresh ginger, the brown sugar, sesame oil, and water chestnut powder, and set aside for twenty minutes.<BR>3. Mix the fish with the other half of these same four ingredients and set it aside for ten minutes, then put this fish mixture on top of the rice, cover it, and continue to steam for five minutes more. Remove the cover and stir.<BR>4. Next, add the chicken mixture to the top of the mixed rice mixture, cover it, and steam for tem more minutes.<BR>5. Mix soy sauce, sugar, salt, pepper, and scallion, then mix this into the fish-rice mixture, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A30A202B6EE3E04DB118344F25EF71D1', X'7B6C39D591220E48B1524F0F0E6AE265', X'7B6C39D591220E48B1524F0F0E6AE265', 0, 'javascript:', 'Sauteed Scallop and Egg White II', 2, NULL, 0, 'Sauteed Scallop and Egg White II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1084', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 medium eggs<BR>1/2 cup chicken broth<BR>1 teaspoon coarse salt<BR>1 Tablespoon cornstarch <BR>2 cups dry rice<BR>1/2 conpoy (dried scallops)<BR>1 teaspoon vegetable oil<BR>5 scallops, cut in half making ten scallop circles<BR>1/2 cup concentrated chicken stock<BR>1/2 pound pea shoots<BR><B><I>Preparation:</B></I><BR>1. Poke a small hole in the end of one egg,, and drain the egg white into a bowl. The using a long needle, break the yolk in the shell, and drain it into another bowl. Then rinse the hollow egg shell, and repeat until all the egg shells are empty.<BR>2. Add chicken broth, salt, and cornstarch to the egg whites, and fill each egg standing it in the uncooked rice aperture up, in a steamer basket lined with a cloth.<BR>3. Steam the stuffed eggs over low heat for one hour, then remove them to a bowl of water, and carefully remove and discard their shells.<BR>4. Steam the conpoy for half an hour, remove and cool slightly, then tear it into fine strips and set aside.<BR>5. Heat wok, add the oil, and stir-fry the scallop circles for one minute, then remove and put on a warm plate.<BR>6. Reheat the wok, add concentrated chicken stock, and stir-fry/steam the pea shoots just until they wilt, then put them on a pre-warmed serving plate; put the peeled eggs on them, add the hot scallop slices, and top all of this with the shredded conpoy. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'46BE60872DA1374CB02E2AB63DFC494B', X'4C6B24CBFAD57148A587EBB98A0B8B7A', X'4C6B24CBFAD57148A587EBB98A0B8B7A', 0, 'javascript:', 'Sauteed Scallops and Egg Whites I', 2, NULL, 0, 'Sauteed Scallops and Egg Whites I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=490', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>8 eggs<BR>3 dried scallops, steamed in a one cup bowl for one hour, set the liquid aside and cool and shred them<BR>1 pound baby bok choy, washed then blanched for ten seconds<BR>6 cups cooked rice<BR>4 Tablespoons or two medium shrimp, minced very fine<BR>2 Tablespoons cornstarch mixed with 1 Tablespoon cool water<BR>1 Tablespoon rendered chicken fat<BR>2 Table spoons corn oil<BR>2 cups chicken broth<BR>3/4 teaspoons of salt, set aside as three batches<BR>1 Tablespoon rice wine<BR>1 cup scallop water (if not enough, dilute with cool water)<BR>1 teaspoon oyster sauce<BR><B><I>Preparation:</I></B><BR>1. Make a small hole in one end of the egg and using a long thin wooden skewer, empty the white into one bowl, then break the yolks, and remove them. Be careful, and when done rinse the empty shell to remove all traces of the yolk.<BR>2. Spread the rice on a pie pan-shaped container and set the empty egg shells on it.<BR>3. Mix the egg whites with half of the shrimp, half of the cornstarch mixture and half of the chicken fat and ne batch of salt and fill the egg shells. Seal them with a small amount of the set aside shrimp paste, and put each one back on the rice, none touching each other, then steam over rapidly boiling water for ten minutes. Remove the eggs and set aside to cool. Use the rice for congee or some other purpose.<BR>4. Heat remaining chicken fat with corn oil and the chicken broth, salt, and rice wine. Add scallop water and the oyster sauce, and quickly stir-fry the baby bok choy, remove and set them in a circle around an eight- to nine-inch platter. Keep the bottom of the bok choy pieces to the outside of the circle.<BR>5. Carefully peel the eggs and put them in the center of these greens.<BR>6. Heat rest of the chicken fat, corn oil, chicken broth, salt, cornstarch mixture, scallop water, and rice wine. Add steamed dry scallop shreds. When it comes to the boil and thickens slightly, pour over the bok choy and eggs, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'51ED765C7447B246828246D1F81CFE60', X'C33AAB127170A84C9A78D8B995909F7A', X'C33AAB127170A84C9A78D8B995909F7A', 0, 'javascript:', 'Savory Puffs with Prawn Sambal', 2, NULL, 0, 'Savory Puffs with Prawn Sambal', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=415', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 Tablespoons corn oil<BR>8 shallots<BR>2 cloves garlic<BR>2 chili peppers<BR>1 teaspoon belacan<BR>2 onions, chopped<BR>1/3 pound shrimp, diced<BR>2 Tablespoons tamarind juice<BR>salt and pepper to taste<BR>non-stick spray or a teaspoon of butter<BR>8 ounces salted butter<BR>1 and 1/4 cups all-purpose flour<BR>4 eggs<BR>2 teaspoons baking powder<BR><B><I>Preparation:</B></I><BR>1. Heat oil and fry shallots, garlic, chillies, and belacan for one minute. Add the onions fry until they are soft, then add the shrimp and fry for one minute before adding the tamarind juice, salt and sugar. Mix this well, remove from the heat and allow to come to room temperature, then refrigerate.<BR>2. Heat the oven to 450 degrees and prepare a greased baking sheet. either with non-stick spray or use up to a teaspoon of butter.<BR>3. Make pastry puffs putting 3/4 cup water and the butter into a small pot and bring to the boil. Remove from the heat and add flour all at once. Return to a low heat and stir until the dough comes away from the pan. Cool for half an hour, then add the eggs one at a time and stir well until the dough looks shiny. Add baking powder and mix it in thoroughly.<BR>4. Put warm puff dough mixture into a piping pag and pipe onto a baking sheet. Pipe tow to three tablespoons of dough mixture as one batch onto the greased pan. Continue to do so leaving two inches on all sides between each ball of dough until all of it has been used. Then bake them for twenty minutes and then reduce the oven temperature to 350 degrees Farenheit and bake another twenty minutes. Cool.<BR>5. Cut the puffs into two pieces, but do so about one-third of the way down. Using a spoon, put one to two tablespoons of filling on each, cover and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C8C07A08E31740409ABAD3827A35B487', X'6DE47B8E4C30A64B9C48B72BE7DDF294', X'6DE47B8E4C30A64B9C48B72BE7DDF294', 0, 'javascript:', 'Scallops and Banana', 2, NULL, 0, 'Scallops and Banana', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2274', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 banana cut into ten rounds<BR>½ cup shrimp paste<BR>4 scallops, cut in half rounds<BR>½ cup bread crumbs<BR>1 egg yolk beaten with one teaspoon cold water<BR>1 cup oil<BR><B><I>Preparation:</B></I><BR>1. Dry the banana slices on paper towels and spread some of the shrimp paste on one side of each slice. Top with another slice of a scallop, and coat this sandwich with the bread crumbs.<BR>2. Dip one side of the sandwich in egg, then in more bread crumbs.<BR>3. Heat the oil and deep fry each piece, two at a time, until they are tan and crisp. Drain on paper towels and repeat until all are fried.<BR>4. Turn up the heat, and fry them a second time until all are a little darker; then drain again on paper towels before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7F168C80DDD2CF4AB462D41D4D9CC1D3', X'F27394AA8ABBA54EA4C77C88AADAEB85', X'F27394AA8ABBA54EA4C77C88AADAEB85', 0, 'javascript:', 'Scallops and Eggs', 2, NULL, 0, 'Scallops and Eggs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=135', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1 teaspoon lard or oil<BR>1 onion, chopped<BR>2 Tablespoons lard<BR>4 eggs, beaten<BR>salt and pepper, to taste<BR>1/2 pound scallops<BR><B><I>Preparation:</B></I><BR>1. Scald the scallops for five minutes.<BR>2. Tear them apart with two forks.<BR>3. Heat lard in a wok or fry pan, fry the onions, then add the eggs and the seasoning, and when cooked to your liking, serve them immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F83314BDC9BE3041B9F3682984752EBD', X'24EFE58A9DD72C429144EACE5E9217D4', X'24EFE58A9DD72C429144EACE5E9217D4', 0, 'javascript:', 'Scallops in Shell with Garlic and Black Bean Sauce', 2, NULL, 0, 'Scallops in Shell with Garlic and Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1090', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 scallops in their shells, per person, prepared as below<BR>2 scallions, white part only, minced<BR>3 teaspoons vegetable oil, divided by teaspoons<BR>1 Tablespoon thin soy sauce<BR>dash of ground white pepper<BR>½ teaspoon of sesame oil<BR>1 teaspoon black bean sauce, per two scallops<BR>1 teaspoon crushed fried garlic flakes<BR>1 Tablespoon chopped green part of a scallion<BR><B><I>Preparation:</B></I><BR>1. To prepare the scallops, open them or have the fish merchant do so and remove and discard the skirt of each one. Retain the shells, and remove the coral, if any. Save that for another recipe to be made that day or the next. Then remove the tough muscle attached to the shell and discard it; and finally, rinse the scallops, dry them with a paper towel, and set them aside.<BR>2. Scrub the scallop shells and place them in boiling water for five minutes. Drain and cover with cold water, and allow them to cool, then dry them and set aside.<BR>3. Put minced white part of the scallions in a small bowl with two teaspoons of the vegetable oil, soy sauce, white pepper, and sesame oil.<BR>4. Put one scallop on one scallop shell, and top it with some black bean sauce and garlic flakes and steam over boiling water for five minutes. Remove and sprinkle each of them with the minced scallion greens. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9EF01026DCD33441896DF8248529E5B5', X'ACD42BAA10DA3448AC97BE60E5F605FC', X'ACD42BAA10DA3448AC97BE60E5F605FC', 0, 'javascript:', 'Scallops in XO Sauce', 2, NULL, 0, 'Scallops in XO Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2275', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 bay scallops<BR>10 sea scallops, each cut into four pieces<BR>1 teaspoon granulated sugar<BR>½ teaspoon coarse salt<BR>1 Tablespoon potato or water chestnut flour<BR>2 Tablespoons vegetable oil<BR>6 coves fresh garlic, peeled and thinly sliced<BR>1 large fresh red hot chili pepper, seeded and slivered<BR>3 Tablespoons sugar snap peas, strings removed, then slivered<BR>2 Tablespoons XO sauce<BR><B><I>Preparation:</B></I><BR>1. Mix both kinds of scallops with the sugar, salt, and starch and set them aside to dry somewhat.<BR>2. Heat the wok or fry pan then add the oil stir-fry the scallops for just one minute before removing them to a plate.<BR>3. Add the garlic and chili pieces and stir-fry another minute before adding the snap pea slivers, and stir once or twice. Now return the scallops to the pan and stir twice, add the XO sauce and one tablespoon of cold water, and toss well, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6EDD69A0500A8E47A21B48BC3826F750', X'22939EC7A51D4A43ABF10102824B919C', X'22939EC7A51D4A43ABF10102824B919C', 0, 'javascript:', 'Scallops with Black Bean Sauce', 2, NULL, 0, 'Scallops with Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=403', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds sea scallops <BR>3 Tablespoons salted fermented black beans, rinsed and mashed<BR>2 cloves garlic, peeled and crushed<BR>3 large slices fresh ginger, minced<BR>2 teaspoons sugar<BR>2 teaspoons thin soy sauce<BR>2 Tablespoons oyster sauce<BR>2 Tablespoons corn oil<BR>2 scallions, cut into half-inch lengths<BR><B><I>Preparation:</B></I><BR>1. Rinse and drain the scallops, pull off any hard white muscle, if there is any.<BR>2. Mix black beans, garlic, ginger, sugar, soy and oyster sauces in a small bowl and set aside.<BR>3. Heat a wok and add the oil and heat until just before it smokes. Stir-fry the scallops for just one minute, then add the scallion pieces and toss well. Put this mixture in a strainer and drain any juices that may come off.<BR>4. In a clean wok, stir-fry the black bean mixture for one minute until it comes to the boil. Add the drained scallops and scallions, and heat through for no more than one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7F84316888B4274490460875B1F7EBF2', X'00ADA4EF5D22EE438557CF0557CA4119', X'00ADA4EF5D22EE438557CF0557CA4119', 0, 'javascript:', 'Scallops with Chinese Parsley', 2, NULL, 0, 'Scallops with Chinese Parsley', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1846', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 large bunches cilantro rinsed and dried with paper towels<BR>1 teaspoon salt<BR>1 teaspoon cornstarch<BR>2 teaspoons vegetable oil<BR>1 and 1/2 pounds fresh scallops, cut in half so there are two circles from each scallop<BR>2 teaspoons grated fresh ginger<BR>1 teaspoon peeled and grated fresh garlic<BR>2 teaspoons Chinese Shaoxing rice wine<BR>2 teaspoon Chinese sesame oil<BR><B><I>Preparation:</B></I><BR>1. Cut the cilantro/parsley in two-inch pieces. Mix with the salt and cornstarch and set aside.<BR>2. Heat the oil in a wok or large fry pan, and fry the ginger and garlic for one minute.<BR>3. Add the scallops and stir-fry one minute, then add the wine and stir just one more minute. Next stir in the cilantro and stir once or twice, sprinkle with sesame oil, stir, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'09CF709CD03C6B40A9014FC1ED2CF71C', X'40FE301BC245B3438826254629C710D5', X'40FE301BC245B3438826254629C710D5', 0, 'javascript:', 'Scallops with Imitation Shark''s Fin', 2, NULL, 0, 'Scallops with Imitation Shark''s Fin', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=960', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 scallops on their shells or on ones made of aluminum foil<BR>1 Tablespoon vegetable oil<BR>5 Tablespoons soaked mung bean noodles, cut into two-inch lengths<BR>1 Tablespoon fried shallots<BR>1 teaspoon chicken bouillon powder<BR>dash ground white pepper<BR>1 teaspoon sugar<BR>1 Tablespoon oyster sauce<BR>1/2 teaspoon sesame oil<BR>1/2 scallion, green part only, minced<BR><B><I>Preparation:</B></I><BR>1. Rinse the scallops and remove any hard part attached to their shells, then using half the oil, brush insides of the shells or the aluminum foil.<BR>2. Heat a wok or small fry pan, add other half of the vegetable oil and toss in mung beans noodles, fried shallots, bouillon powder, pepper, sugar, and oyster sauce. Stir-fry one minute, no more, remove from heat and add sesame oil. Pour this over the scallops, and steam over boiling water for five minutes.<BR>3. Sprinkle scallions over them all, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8863AF89E263324FAA29D499581CBCAF', X'A8D747EEF4502C48BCC6168912B4B7E0', X'A8D747EEF4502C48BCC6168912B4B7E0', 0, 'javascript:', 'Scallops, Mushrooms, and Water Chestnuts', 2, NULL, 0, 'Scallops, Mushrooms, and Water Chestnuts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2172', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 pound sea scallops<BR>10 Chinese black mushrooms, all soaked in two cups boiling water for half an hour, reserve their water<BR>10 water chestnuts<BR>5 slices fresh ginger, peeled<BR>1 egg<BR>1 Tablespoon Chinese rice wine<BR>1/2 teaspoon salt<BR>1 scallion, angle sliced<BR><B><I>Preparation:</B></I><BR>1. Using half the scallops, mushrooms, water chestnuts, and ginger, grind them in a food processor; mince the other half.<BR>2. Mix the ground half with the egg, rice wine, and salt and make one-inch balls. Make the other half also into one-inch balls. Simmer them all in a pot in two cups of water, for ten minutes.<BR>3. Add the mushroom water, rice wine, and the salt and simmer another five minutes, then add the scallions and bring this to the boil, then serve it in individual soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'65EDFAD575C62749BEFE9E2C3292BF81', X'B14DB2C764C9414399E23A0D118B1E20', X'B14DB2C764C9414399E23A0D118B1E20', 0, 'javascript:', 'Scallops, Mushrooms, and Water Chestnuts', 2, NULL, 0, 'Scallops, Mushrooms, and Water Chestnuts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2273', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound sea scallops, divided<BR>10 Chinese black mushrooms soaked in two cups boiling water for half an hour, drain, and reserve the water<BR>10 water chestnuts<BR>5 slices fresh ginger, peeled<BR>1 egg<BR>1 tablespoon Chinese rice wine<BR>½ teaspoon salt<BR>1 scallion, angle sliced<BR><B><I>Preparation</B></I><BR>1. Using half the scallops, grind one-half of the mushrooms, water chestnuts and ginger in a food processor. Coarsely mince the other half of this mixture.<BR>2. Mix the ground half with the egg, rice wine, and the salt and make into one-inch balls, and simmer them for ten minutes in a large pot in two cups of water.<BR>3. Add the mushroom water, rice wine, and the salt and simmer both sets of balls another three minutes, then add the scallions and bring to the boil and immediately remove from the heat source and serve in soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7B5F6F8A82493B408AE7982731BB7BDE', X'9D51C2E4D62EE940AE5C6C006EE4AD1B', X'9D51C2E4D62EE940AE5C6C006EE4AD1B', 0, 'javascript:', 'Scrambled Eggs and Oysters', 2, NULL, 0, 'Scrambled Eggs and Oysters', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1991', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>4 eggs, beaten well<BR>2 tomatoes, diced in one-half- to one-inch pieces<BR>1 Tablespoon tomato paste<BR>1 Tablespoon fish sauce<BR>1 teaspoon granulated sugar<BR>½ teaspoon coarse salt<BR>6 shucked oysters, each cut in half<BR><B><I>Preparation:</I></B><BR>1. Heat large fry pan, and add the oil, then reduce the heat to medium.<BR>2. Mix eggs, tomatoes, tomato paste, fish sauce, sugar, and salt, then add the oysters and fry just until the eggs solidify, then stir gently, and turn this mixture over. Fry second side for one minute.<BR>3. Serve on pre-heated plates.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B5504FC040FDE343BB4322FABFFF761A', X'F5E93FD97DB89E458EB3DD95C408C778', X'F5E93FD97DB89E458EB3DD95C408C778', 0, 'javascript:', 'Seafood and Rice', 2, NULL, 0, 'Seafood and Rice', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1800', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 soaked dried scallops, known as conpoy, soaked for one hour in one cup of hot water, then shredded into strands<BR>5 mussels, beards removed and discarded<BR>2 teaspoons vegetable oil<BR>2 teaspoons minced fresh ginger<BR>12 shrimp, peeled and their veins removed and discarded<BR>1 teaspoon thin soy sauce<BR>1/2 teaspoon salt<BR>1/2 teaspoon hot oil<BR>1 and 1/2 cups cooked hot rice<BR>1 to 2 Tablespoons honey<BR><B><I>Preparation:</B></I><BR>1. Steam scallop strands for five minutes over boiling water in a small glass bowl with half cup of boiling water in it.<BR>2. Lay the mussels on the steamer shelf next to the scallop dish, and steam the scallop strands and the mussels for four minutes.<BR>3. Heat a wok or fry pan, add the oil and the ginger and stir once or twice, then add the shrimp, and then the scallop pieces, and stir-fry for two minutes until the shrimp are no longer pink.<BR>4. Add the soy sauce, hot oil, and the honey, stir well, then add the rice, and stir for two minutes. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6B95E36231FBBC4C86BD33EB902CC9F8', X'7D72E01890B8CA45B91AE3B541A2774A', X'7D72E01890B8CA45B91AE3B541A2774A', 0, 'javascript:', 'Seafood Fritters', 2, NULL, 0, 'Seafood Fritters', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1779', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound peeled sweet potatoes, grated<BR>1/4 pound shrimp, oysters, and/or clams, each cut into four pieces<BR>1/2 cup rice flour<BR>1/2 cup wheat flour<BR>2 teaspoons granulated sugar<BR>1/4 teaspoon turmeric<BR>1/2 teaspoon coarse salt<BR>1 cup canned coconut milk<BR>1 cup vegetable oil<BR>2 Tablespoons very coarsely chopped fresh mint<BR>2 Tablespoons very coarsely chopped <I>Houttuynia cordata</I><BR><B><I>Preparation:</B></I><BR>1. In a small linen towel, squeeze out as much liquid as possible from the grated sweet potatoes.<BR>2. Dry the shrimp, oysters, and or the clams in paper towels, and set them aside.<BR>3. Mix the flours, sugar, turmeric, and salt and slowly stir then add the coconut cream making a thick batter.<BR>4. Add the potatoes and sea foods and do not thin this thick batter unless it can not be stirred. If this is the case, add more coconut cream a tablespoon at a time.<BR>5. Heat the oil and pour in heaping tablespoons of this batter to make the fritters; do not make too many at one time. Fry them on both sides, then drain them on paper towels. Repeat until all are made, putting them on a pre-heated serving plate. Sprinkle the herbs on them, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E389277964E1A0448995BE82FF32EF87', X'A8C179C508C2A544880E3F7E60BB253E', X'A8C179C508C2A544880E3F7E60BB253E', 0, 'javascript:', 'Seafood Spring Rolls', 2, NULL, 0, 'Seafood Spring Rolls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=243', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound regular or rock shrimp, shells and veins removed<BR>1/2 pound Chilean sea bass fillet, cut into one-inch squares<BR>1/2 pound bay scallops<BR>1 egg<BR>1 Tablespoon finely chopped fresh ginger<BR>1 teaspoon sesame oil<BR>5 scallions, cut into thin slices<BR>1/4 cup fresh cilantro, chopped<BR>1/4 teaspoon salt, or to taste<BR>a dash of ground white pepper, or to taste<BR>20 spring roll wrappers<BR>Egg wash made by beating one egg and one-quarter cup water<BR>2 cups corn oil<BR><B><I>Preparation:</I></B><BR>1. Mix the three sea foods, egg, ginger, and sesame oil in a food processor by turning the machine on and off several times; do not puree them.<BR>2. Add scallions, cilantro, and salt and pepper to taste, and mix this filling gently.<BR>3. Put two tablespoons of filling just inside a corner of one spring roll wrapper. Wrap and seal with a drop of the egg wash. Repeat until all rolled sticks have been made, putting them on a platter seam side down. Then brush their tops with the rest of the egg wash.<BR>4. Heat the oil in a large deep pan until the oil is 315 degrees Fahrenheit. Fry one third of the rolls for about five minutes until golden brown, then drain on paper towels. Repeat until all are fried.<BR>Stand the rolls in glasses and serve with a dipping sauce.<BR><B>Note:</B> Makes twenty spring rolls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EF64363DFBC4DC45AF721C75C83D34B0', X'1D7A0981E772B548B6B96A2946FD8E47', X'1D7A0981E772B548B6B96A2946FD8E47', 0, 'javascript:', 'Shanghai-style Stewed Fish Tails', 2, NULL, 0, 'Shanghai-style Stewed Fish Tails', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=3', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>4 fish tails, each about six to eight inches long<BR>2 Tablespoons dark soy sauce<BR>1/2 teaspoon white pepper<BR>2 scallions, cut into two inch lengths, then into long thin strips<BR>3 cloves garlic, cut in thin slices, then into thin strips<BR>1/2 cup corn oil<BR>2 Tablespoons cornstarch<BR>2 Tablespoons rice wine<BR>2 Tablespoons thin soy sauce<BR>2 Tablespoons sugar<BR><I><B>Preparation:</I></B><BR>1. Cut each tail in half the long way and remove the center bone, then cut it in half again, and dry the pieces.<BR>2. Marinate the fish tails in the dark soy and white pepper for half an hour<BR>3. Mix garlic and scallion strips. Heat corn oil and fry them in it for half to one minute or until crisp, then remove from the oil and drain on a paper towel. Reserve the oil.<BR>4. Mix cornstarch with like amount of water and cost the fish tails with this paste. Do not discard the extra cornstarch paste.<BR>5. Reheat the oil and fry the batter-coated fish tails just until they brown, then add the rice wine, thin soy sauce, and sugar and cover and simmer for five minutes, and remove them to a warm platter.<BR>6. Add the cornstarch mixture and stir until it thickens and pour this over the fish and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CC5BB4757F1D734EAC2D25791CFED678', X'51D3FD486285094CB1DD84A87906CF41', X'51D3FD486285094CB1DD84A87906CF41', 0, 'javascript:', 'Shark''s Fin with Crab Meat', 2, NULL, 0, 'Shark''s Fin with Crab Meat', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=952', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 ounces dried shark''s fin<BR>1 Tablespoon Chinese rice wine<BR>2 slices fresh ginger, minced and mixed with two tablespoons of hot water<BR>1/2 cup crab meat, shredded<BR>2 cups superior chicken stock<BR>1/4 teaspoon bouillon powder<BR>1 Tablespoon cornstarch mixed with one tablespoon of cold water<BR>dash ground white pepper<BR><B><I>Preparation:</B></I><BR>1. In a bowl, steam shark''s fin, wine, fresh ginger water, and one cup of water over boiling water for forty minutes. Remove and cool, then separate the needles, if desired, and set aside.<BR>2. Blanch the shredded crab meat in three cups of boiling water for one minute, drain, and set aside. Strain the water, and set it aside.<BR>3. Heat superior stock, in a heat-proof crock, then add strained crab water and the bouillon powder. Add the cornstarch water and the ground pepper, and as it starts to thicken, add shark''s fin needles and the shredded crab, meat, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1B03B55A9009F74B918EB1F1F2C9100B', X'2FCA20A79F37BB43BC1812171A2B57AF', X'2FCA20A79F37BB43BC1812171A2B57AF', 0, 'javascript:', 'Shark''s Fin with Crab Roe', 2, NULL, 0, 'Shark''s Fin with Crab Roe', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=953', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 ounces dried shark''s fin<BR>1 Tablespoon Chinese rice wine<BR>2 slices fresh ginger, minced and mixed with two tablespoons of hot water<BR>1/4 cup crab roe, blanched in boiling water for half minute, then drained<BR>2 cups superior chicken stock<BR>1/4 teaspoon bouillon powder<BR>1 Tablespoon cornstarch mixed with one tablespoon of cold water<BR>dash ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Steam shark''s fin, wine, fresh ginger water, and one cup of water in a bowl set over boiling water for forty minutes. Remove and cool, then separate the needles, and set aside.<BR>2. Un-clump the roe by pulling it apart into small batches,. Set it aside.<BR>3. Heat superior stock, in a heat-proof crock and add the bouillon powder. Add the cornstarch water and the ground pepper, and as it starts to thicken, add shark''s fin needles and the crab roe, Do not stir, simply serve some needles and roe and liquid to each person.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E2B77C0F7ED4F7409AC21AFEF2AA739A', X'4BC447DF45952748813B62287272270D', X'4BC447DF45952748813B62287272270D', 0, 'javascript:', 'Shiitake Mushrooms with Prawns', 2, NULL, 0, 'Shiitake Mushrooms with Prawns', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=997', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/3 pound dry scallops, one tablespoon wine, and a few pieces of ginseng<BR>8 dry Chinese black mushrooms, large flower ones recommended<BR>2 Tablespoons cornstarch<BR>1 Tablespoon goose fat<BR>1/4 pound shrimp, shelled<BR>1/2 teaspoon sea salt<BR>1 teaspoon brown sugar<BR>1 teaspoon maltose<BR>1 teaspoon ginseng liquor<BR>1 teaspoon walnut oil<BR>1 teaspoon shrimp roe<BR><B><I>Preparation:</B></I><BR>1. Make a stock cooking the scallops, wine, and ginseng roots and three quarts of boiling water. Simmer for five minutes, turn off the heat, and allow to cool. Then mash the scallops and simmer for two hours, then strain and discard the solids.<BR>2. Soak mushrooms for two days in cold water, changing the water every twelve hours. Drain them, remove their stems, rub them with the cornstarch, and rinse them.<BR>3. Put goose fat in a casserole, add the mushrooms and three cups of water. Simmer on the lowest possible heat for eight hours, adding more water as needed. When done, drain and reserve both mushrooms and their liquid separately.<BR>4. Pound or mince the shrimp, then mix them with the salt and stuff the underside of the mushrooms.<BR>5. Make a sauce using one-quarter of the scallop stock (freeze the rest in one-quarter cup amounts for other uses), with the mushroom water, sugar, maltose and liquor. Simmer until volume is reduced to less than half.<BR>6. Heat walnut oil, and fry stuffed mushrooms, prawn side down for up to one minute. Then remove them and arrange on a plate, shrimp side up. Decor with a small amount of shrimp roe on each, pour scallop-mushroom sauce in top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'439F840638FF7146BE26FA9B61BAF5FC', X'35FD67A4E40E3049942304470A07BBE0', X'35FD67A4E40E3049942304470A07BBE0', 0, 'javascript:', 'Shredded Fish Rolls', 2, NULL, 0, 'Shredded Fish Rolls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=741', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound thick boneless fish fillet<BR>1 Tablespoon Chinese rice wine<BR>1/2 teaspoon salt<BR>1/4 teaspoon ground white pepper<BR>3 dried black mushrooms, soaked, stems discarded and sliced thin<BR>3 Tablespoons Yunnan or Smithfield ham, cut in thin strips<BR>3 scallions, cut in thin strips<BR>3 slices fresh ginger,, cut in thin strips<BR>1/2 teaspoon vegetable oil.<BR>1 teaspoon sesame oil mixed with one teaspoon of cornstarch<BR><B><I>Preparation:</B></I><BR>1. Slice fish parallel with the cutting board into five sheet-like pieces, and put them on a large platter lined with pieces of plastic wrap. Sprinkle the fish with rice wine, salt and pepper and set aside for ten minutes.<BR>2. Mix mushrooms, ham, scallion, and ginger strips and dividing that into five portions, put one near one end on each of the fish slices. Roll each one individually and gently using the plastic wrap to help, not tearing the fish.<BR>3. Oil a plate, and put the fish rolls seam side down on the plate, discarding the plastic wrap. Gently brush then with the sesame oil mixture, and steam over boiling water for ten minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'16C636EB7001D543AB9FECF392DFA9E2', X'C5278A0C3BFEA344A5F1BCF5AA8EABAC', X'C5278A0C3BFEA344A5F1BCF5AA8EABAC', 0, 'javascript:', 'Shrimp and Lotus Root Salad', 2, NULL, 0, 'Shrimp and Lotus Root Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1780', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 large shrimp, cooked, peeled, veins removed, each cut in four to six pieces<BR>1 small carrot, grated<BR>1 section of lotus root, peeled, cooked, thinly sliced, and quartered<BR>2 Tablespoons fish sauce<BR>2 Tablespoons <I>Houttuynia cordata</I> or coriander, coarsely chopped<BR>1 chili pepper, seeded and minced<BR>2 Tablespoons fried shallots<BR>1 bunch chives, cut into half-inch lengths<BR><B><I>Preparation:</B></I><BR>1. Mix the shrimp, gated carrot, lotus root pieces, and the fish sauce and mix well.<BR>2. Then add half the <I>Houttuynia cordata</I>, the chili pepper pieces, shallots and chives, and toss again.<BR>3. Put this into a large serving bowl, sprinkle the other half of the last four items, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5C1C1C0DB74D61438DED9530E6BCB3EA', X'48BB88C5A20708428635A67F6FE29DBE', X'48BB88C5A20708428635A67F6FE29DBE', 0, 'javascript:', 'Shrimp and Vegetables for a Crowd, in Batches', 2, NULL, 0, 'Shrimp and Vegetables for a Crowd, in Batches', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1584', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 quart vegetable oil<BR>3 pounds medium shrimp, left whole, their tails left on and their veins removed and discarded; shells left on or not, as preferred<BR>3 Tablespoons Shao Xing wine<BR>1/2 teaspoon ground white pepper<BR>1 teaspoon coarse salt<BR>1 egg white<BR>1/2 cup cornstarch<BR>15 baby <I>bok cai</I>, quartered length-wise<BR>1 pound baby carrots, each cut in half on an angle, then put in a microwave for one minute<BR>1 can straw mushrooms drained very well<BR>1/2 can baby corn, each piece cut in half<BR>1/2 can sliced water chestnuts, drained very well<BR>1/2 can sliced bamboo shoots, drained very well<BR>1 scant teaspoon hot sauce<BR>3 whole scallions, separated, whites and green parts cut in one-inch pieces on an angle<BR>3 to 5 cloves of garlic, minced<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat oil and deep-fry the shrimp just until they begin to crisp and start to turn pink. Do undercook them, then drain them on paper towels. Set aside the oil using as one tablespoon for preparing each batch of vegetables in step #3.<BR>2. Put the drained shrimp in a bowl with the wine, salt, pepper, egg white, and cornstarch, mix these well and set aside. This can be made before the guests come, and if making more, prepare each bactch and keep separately in its own bowl set on ice.<BR>3. Mix one batch of <I>bok cai</I>, pre-prepared baby carrots, straw mushrooms, baby corn, water chestnuts and bamboo shoots with the hot sauce for each batch of shrimp.<BR>4. Heat reserved tablespoon of oil, and stir fry the scallions and garlic for one minute, then add the vegetables and stir-fry two minutes before adding the shrimp and its marinade.<BR>5. Stir-fry for two minutes toss with the vegetables for that batch of shrimp and put this into a large serving bowl. Place it on the serving table. Stir in the sesame oil, add a serving spoon, and that''''s it!<BR><B>Note:</B> When the bowl is three-quarters empty, then prepare another batch of this complete dish and add it to the serving bowl, tossing the new ingredients with the batch in the bowl.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'15A5E776EC80C047BC992CC54A3BD1DD', X'15654E53335ECC428E15C9C897680AFD', X'15654E53335ECC428E15C9C897680AFD', 0, 'javascript:', 'Shrimp Balls in Oyster Sauce', 2, NULL, 0, 'Shrimp Balls in Oyster Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=89', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound shrimp, shelled and deveined<BR>6 water chestnuts<BR>1/4 cup bamboo shoots, chopped<BR>1 egg white<BR>2 Tablespoons cornstarch<BR>1 Tablespoon dry sherrry<BR>1/2 teaspoon salt<BR>dash of ground white pepper<BR>1/4 cup oyster sauce<BR>2 cups corn oil<BR>1 scallion, choopped<BR><B><I>Preparation:</B></I><BR>1. Chop shrimp and mix with all but the last three ingredients. Let rest half an hour in the refrigerator.<BR>2. Heat oil in a large saucepan to about 350 degrees F. Then using tow spoons, make balls of the shrimp mixture and deep fry them until golden; drain them on paper towels then put them on a serving plate.<BR>3. Heat the oster sauce (the microwave oven works wonderfully for this) and pour it over the shrimp balls, then garnis with the minced scallion.<BR>Serves six.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9D460C573097B5489BE8C2F99682F473', X'1691A628F11A6C4EBD76976328049D1F', X'1691A628F11A6C4EBD76976328049D1F', 0, 'javascript:', 'Shrimp Balls, Henan Style', 2, NULL, 0, 'Shrimp Balls, Henan Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2119', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound shrimp, shells and black veins removed and discarded<BR>2 Tablespoons al-purpose flour<BR>9-ounce can of water chestnuts, drained and chopped<BR>2 scallions, minced<BR>1 teaspoon coarse salt<BR>1 egg, beaten<BR>1 Tablespoon granulated sugar<BR>2 cups vegetable oil for deep frying<BR>any dipping sauce desired<BR><B><I>Preparation:</B></I><BR>1. Mix all the ingredients except the oil and make this into one-inch balls. Set them aside for half an hour to dry a little.<BR>2. Heat oil in a wok or deep pot, and add half the shrimp balls and fry them until light golden in color. Drain and set them on paper towels and repeat with the rest of the shrimp balls.<BR>3. Serve with any optional dipping sauce, as desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AAA2DDA9825425438F2B23B945CDBB15', X'8063BA6E49B69C488D4DE870E0D776FF', X'8063BA6E49B69C488D4DE870E0D776FF', 0, 'javascript:', 'Shrimp in Egg', 2, NULL, 0, 'Shrimp in Egg', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1321', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound small shrimp, peel and veins removed<BR>2 cups chicken broth<BR>1 tablespoon Chinese green tea leaves<BR>1 Tablespoon agar agar or a commercial gelatin powder<BR>10 eggs<BR>1 scallion, minced<BR>2 slices ginger, minced<BR>1 tomato, exterior cut into one-quarter-inch cubes<BR>30 very small frozen peas<BR>2 cups small beans for supporting the eggs<BR><B><I>Preparation:</B></I><BR>1. Dice the shrimp into one-quarter-inch pieces and simmer for one minute, then drain, and set them aside.<BR>2. Boil the chicken broth with the tea leaves for five minutes, then remove and discard the tea leaves.<BR>3. Mix agar agar or gelatin in cold water, let sit for five minutes until thickened, then add to the chicken broth and set aside.<BR>4. Remove a small amount of peel (about one-quarter inch circle) from the large end of the egg. Make a pin hole in the other end, and blow out the egg from each one, and refrigerate these for another use, standing each one on the large end to be sure it drains well. Then pour one cup of boiling water into each egg and then drain it out.<BR>5. Put the beans in a soup bowl, then stand the eggs up in then large whole up. Add five pieces of scallion, shrimp, tomato, and peas into each large opening in each egg. Make a paper funnel or use a small one and fill with the cooled chicken stock mixture. Allow these to stand and cool in the refrigerator.<BR>6. Before serving, carefully roll then peel each egg, place the solidified egg mixture in a lovely bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'87B7FA93359A4945ADB9745ECC1074BD', X'BDA022C3FF4CFF4AAC108C1E23D1791C', X'BDA022C3FF4CFF4AAC108C1E23D1791C', 0, 'javascript:', 'Shrimp in Fermented Red Rice Sauce', 2, NULL, 0, 'Shrimp in Fermented Red Rice Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1756', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 and 1/2 pounds of shrimp<BR>1 cup vegetable oil<BR>1/4 cup sesame oil<BR>1 Tablespoon minced peeled garlic<BR>2 Tablespoons minced fresh ginger<BR>3 Tablespoons fermented red rice<BR>2 scallions, minced<BR>1/2 cup Shaoxing rice wine<BR>1/2 teaspoon salt<BR>1 teaspoon sugar<BR>dash of ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Shell, remove the black veins from the shrimp, and discard both of these. Then dry the shrimp with paper towels.<BR>2. In a wok or fry pan, heat both oils and immerse half the shrimp for two minutes, drain them and put them on a platter. Repeat with the other half of the shrimp. Remove all but one tablespoon of the oil mixture, then put the garlic and ginger in and stir-fry them for one minute, then add the fermented red rice and stir-fry another minute or two until this mixture bubbles.<BR>3. Add all the rest of the ingredients, set the shrimp on a serving platter, and cook everything else one more minute. Return the shrimp to the pan, stir, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7D7BD30D09E53E4B9255695C2BC17879', X'5C2D2CC6D61056479DC6E4CA70F3B8F2', X'5C2D2CC6D61056479DC6E4CA70F3B8F2', 0, 'javascript:', 'Shrimp in Nest, Zhejiang Style', 2, NULL, 0, 'Shrimp in Nest, Zhejiang Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1764', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound shrimp<BR>1/2 pound potatoes<BR>1 Tablespoon cornstarch<BR>1/2 cup vegetable oil<BR>1 Tablespoon hair seaweed<BR>3 stalks Chinese celery with leaves, each cut into four pieces<BR>1/4 cup green soybeans or lima beans<BR>1 large black mushroom soaked for half hour, water squeezed out, the stem discarded, and the cap slivered<BR><B><I>Preparation:</B></I><BR>1. Peel the shrimp and the potatoes, remove veins from the shrimp and set them aside. Cut the potato into thin slices. Then toss each of these with half of the cornstarch, keeping them in separate small bowls.<BR>2. Heat the oil and fry each of these separately, the shrimp for one minute, the potatoes for five minutes. Drain each of them on paper towels.<BR>3. Oil blanch the hair seaweed for one minute and drain it on paper towels, as well.<BR>4. Arrange the celery pieces, leaves to the outside, put the hair seaweed in the center, the potatoes around it.<BR>5. Reheat the shrimp in the oil for one minute and drain them and spread them about. Do the same for the soy or lima beans and the mushroom pieces but for two minutes and spread them about after draining them on paper towels. Then serve warm or at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2C4B70F04799D242B9922B885ACB820E', X'33734DFC560C854D8F1C3762AD28EEF5', X'33734DFC560C854D8F1C3762AD28EEF5', 0, 'javascript:', 'Shrimp Surprise', 2, NULL, 0, 'Shrimp Surprise', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=322', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 banana<BR>1/4 pound shrimp, peeled and deveined<BR>dash salt<BR>1/4 teaspoon sugar<BR>2 Tablespoons cornstarch<BR>4 beancurd sheets<BR>1/2 cup cornstarch mixed with three to four tablespoons of water<BR>1 cup oil, for deep frying<BR><B><I>Preparation:</B></I><BR>1. Peel and cut banana into three even-sized pieces. Cut each piece in quarters, first cutting them in half the long way, then cut each in half the long way again. You should have twelve stick-shaped pieces.<BR>2. Cut each shrimp in half the long way, then mix them with the salt, sugar, and cornstarch. Set them aside to marinate for fifteen minutes.<BR>3. Soak beancurd sheets in boiling water for ten minutes, drain and pat dry with paper towels. Then cut them into pieces, each about four inches square.<BR>4. Stir the cornstarch water mixture. Then roll one piece of shrimp and one piece of banana in it, as you would roll an egg roll, point of the skin facing you, sides turned in, then rolled completely. Put a dash of the cornstarch mixture on the point to seal it.<BR>5. Heat oil. Dip half the shrimp rolls in the batter and fry them until crispy, about two minutes. Drain them on paper towels. Repeat with the remaining rolls. When all are drained, serve.<BR><B>Note:</B> They can be sprinkled with confectionary sugar, if desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FACC55A8E42B2447ABBE5014BA6C7042', X'585EC378757EF84F8D968353286537E7', X'585EC378757EF84F8D968353286537E7', 0, 'javascript:', 'Shrimp with Fermented Doufu', 2, NULL, 0, 'Shrimp with Fermented Doufu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2259', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound fresh live shrimp<BR>2 squares fermented doufu, mashed<BR>3 Tablespoons <I>mao tai</I> or a 100 proof Chinese liquor<BR>2 Tablespoons Chinese white vinegar<BR>1 Tablespoon granulated sugar<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Rinse the shrimp, and dry on paper towels, then discard the paper towels.<BR>2. Mix fermented doufu, liquor, vinegar, sugar, and sesame oil.<BR>3. Mix the shrimp with the doufu mixture, and stir well, then refrigerate covered for one hour.<BR>4. Remove from the refrigerator, drain and discard any liquid, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5ACCF6AB542EC648A218B4935431CCCB', X'9D353303CF40C140823C91B01F6D10F0', X'9D353303CF40C140823C91B01F6D10F0', 0, 'javascript:', 'Shrimp with Long Beans and Doufu', 2, NULL, 0, 'Shrimp with Long Beans and Doufu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2265', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 Tablespoon vegetable oil<BR>2 Tablespoons dried shrimp soaked in warm water for twenty minutes, veins and shells removed and discarded<BR>5 cloves fresh garlic, peeled and sliced thinly<BR>3 Tablespoons preserved radish, soaked then sliced<BR>1/4 pound long beans, cut into two-inch pieces<BR>2 one-pound squares firm <I>doufu,</I> cut into one-inch cubes<BR>2 teaspoons thin soy sauce<BR>1 red hot chili pepper, seeded and slivered<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, then stir-fry the soaked dried shrimp, garlic, and the radish and stir-fry for one minute.<BR>2. Next add the long bean pieces and stir-fry another minute or two before adding the <I>doufu</I> and the soy sauce and stir-fry for two minutes.<BR>3. Now add the chili pepper and stir fry one more minute, then serve on a pre-heated platter.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EF35CF56853672428B4E02EBAD880784', X'9EA56695786B4E46A9B7204E9F7C6325', X'9EA56695786B4E46A9B7204E9F7C6325', 0, 'javascript:', 'Shrimp with Long Beans and Doufu', 2, NULL, 0, 'Shrimp with Long Beans and Doufu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2161', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>2 Tablespoons dried shrimp soaked in warm water for twenty minutes, veins and shells removed and discarded<BR>5 cloves fresh garlic, peeled and sliced thinly<BR>3 Tablespoons preserved radish, soaked then sliced<BR>1/4 pound long beans, cut into two-inch pieces<BR>2 one-pound squares firm doufu, cut into one-inch cubes<BR>2 teaspoons thin soy sauce<BR>1 red hot chili pepper, seeded and slivered<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, then stir-fry the soaked dried shrimp, garlic, and the radish and stir-fry for one minute.<BR>2. Next add the long bean pieces and stir-fry another minute or two before adding the doufu and the soy sauce and stir-fry for two minutes.<BR>3. Now add the chili pepper and stir fry one more minute, then serve on a pre-heated platter.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9F875114FCDDE84A90497B21AEB15F03', X'FB905F764D8A3A4A984586757B7FF910', X'FB905F764D8A3A4A984586757B7FF910', 0, 'javascript:', 'Shrimp with Long Jing Tea', 2, NULL, 0, 'Shrimp with Long Jing Tea', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1705', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound large shrimp, peeled with their veins removed<BR>3 Tablespoons vodka or gin<BR>1 egg white<BR>1 Tablespoon cornstarch<BR>1 Tablespoons dried <I>Long Jing</I> tea leaves<BR>3 Tablespoons vegetable oil<BR>2 Tablespoons fresh peeled ginger, cut in thin strips<BR>1 cup snow peas, stringy ends removed<BR>1/2 teaspoon coarse salt<BR><B><I>Preparation:</B></I><BR>1. Rinse the shrimp, dry them with paper towels, then toss with the alcohol of choice, the egg white, and the cornstarch.<BR>2. Bring one cup water to the boil, add tea leaves, and set aside to cool.<BR>3. Heat the vegetable oil, add the shrimp, and stir-fry for one minute. Add the tea water and the leaves, ginger and snow peas, and cook another minute.<BR>4. Discard the liquid, put the snow peas on a serving plate, pile the shrimp in the center, and sprinkle with salt. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'98422ADB8E1D9F4ABE39EC2B9C317212', X'987B283CBFA5B34F800A74806D806F07', X'987B283CBFA5B34F800A74806D806F07', 0, 'javascript:', 'Shrimp with Mango and Bell Fruit', 2, NULL, 0, 'Shrimp with Mango and Bell Fruit', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=249', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound shrimp, shelled and deveined<BR>1/2 teaspoon salt<BR>dash white pepper<BR>1/2 teaspoon sesame oil<BR>2 Tablespoons corn oil<BR>1/2 green pepper, seeded and cut into one-inch pieces<BR>1/4 cup cold chicken broth<BR>1 teaspoon cornstarch<BR>1 bell fruit, cut into one-inch cubes, seed area removed<BR>1 almost ripe mango, seed removed and cut into one-inch cubes<BR><I><B>Preparation:</I></B><BR>1. Mince shrimp, mix with salt, pepper, and sesame oil and make into one-inch balls.<BR>2. Heat corn oil and gently fry the shrimp balls for one minute or until almost but not totally cooked through.<BR>3. Add pepper and fry another half minute.<BR>4. Mix broth and cornstarch and add with the bell fruit to the shrimp ball mixture. As soon as it comes to a boil, add the mango pieces, cook for half a minute until this fruit is warm. Teh serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A631C907D781454FA17CBD8343850C76', X'7661DC348AF7A6439C9480205B92F363', X'7661DC348AF7A6439C9480205B92F363', 0, 'javascript:', 'Shrimp with Mangosteen', 2, NULL, 0, 'Shrimp with Mangosteen', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=759', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 fresh mangosteens, sections removed and carefully pitted<BR>2 scallions, slivered into very small shards, keeping white and green sections separate<BR>1/2 cup fresh coriander, slivered into shards<BR>1 pound peeled raw shrimp, veins removed, and patted dry<BR>1 egg white<BR>1 Tablespoon cornstarch<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon corn oil<BR><B><I>Preparation:</B></I><BR>1. Gently toss mangosteen scallion, and coriander, and set aside.<BR>2. Mix shrimp, egg white, cornstarch, and rice wine, and let stand for fifteen minutes.<BR>3. Heat wok, add oil, and in half mintue add shrimp and stir-fry for half minute until they start to turn pink. Then add the mangosteen mixture and continue to stir-fry for another minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'48C3E0D9BFCF644DBD5415BBCCD69F08', X'ED384BECC53F294EA99A746460A89374', X'ED384BECC53F294EA99A746460A89374', 0, 'javascript:', 'Shrimp with Rambutan', 2, NULL, 0, 'Shrimp with Rambutan', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1079', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 large shrimp, peeled, veins removed, and cut in half<BR>2 teaspoons water chestnut flour or potato starch<BR>dash of ground white pepper<BR>1 Tablespoon vegetable oil<BR>4 asparagus, angle-cut into one-inch sections<BR>16 fresh peeled and pitted or canned rambutan<BR>1 Tablespoon cornstarch mixed with 1 Tablespoon cold water<BR>1 teaspoon crushed brown sugar cubes<BR>16 <I>goji</I> berries mixed with two tablespoons of hot water<BR><B><I>Preparation:</I></B><BR>1. Mix shrimp, water chestnut flour and ground white pepper and set aside for fifteen minutes.<BR>2. Heat wok or fry pan and add oil, and stir-fry the shrimp for one minute, then add asparagus and stir-fry one minute more.<BR>3. Add cornstarch mixture, the sugar, and the goji berries, stir-fry half to one minute more until thick, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E4835F913F16424C97D251B632A9B7E8', X'12ECD0F63553DD4EB14114C312784B28', X'12ECD0F63553DD4EB14114C312784B28', 0, 'javascript:', 'Shrimp with Salted Egg Yolks', 2, NULL, 0, 'Shrimp with Salted Egg Yolks', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1858', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound fresh shrimp, veins removed, then dried with paper towels<BR>2 Tablespoons Shaoxing wine<BR>dash of ground white pepper<BR>1 Tablespoon chicken fat, melted<BR>1/2 cup vegetable oil<BR>5 hot chili peppers, seeds and stems discarded, and minced<BR>1 teaspoon granulated sugar<BR>2 Tablespoons evaporated milk<BR>1 Tablespoon butter, melted<BR>1 teaspoon curry powder<BR>1/2 teaspoon ground turmeric<BR>3 salted egg yolks, mashed<BR><B><I>Preparation:</B></I><BR>1. Mix shrimp, wine, ground white pepper, and the chicken fat, and set aside for ten minutes.<BR>2. Heat a wok or fry pan, add the oil, and deep fry the shrimp for three minutes until pink, then drain them and set aside on paper towels.<BR>3. Discard most of the oil, reserving one to two tablespoons. Put the oil in a small fry pan or pot. Add the chili pepper pieces and sugar, and stir-fry for one minute before adding the evaporated milk, butter, curry powder, and turmeric. Stir well, then add the shrimp and mashed salted egg yolks and stir-fry for one minute until the shrimp are coated with egg yolks, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9678C2EABA925B4EA7C208DDA7AD8F6D', X'6438E60A18162C41B4106CD0F4D888E5', X'6438E60A18162C41B4106CD0F4D888E5', 0, 'javascript:', 'Shrimp with Seaweed', 2, NULL, 0, 'Shrimp with Seaweed', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1912', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound fresh shrimp<BR>2 Tablespoons kelp, soaked in cold water for half an hour, then drained<BR>1 lemon, juiced, pits and peel discarded<BR>2 teaspoons thin soy sauce<BR>1 teaspoon sesame oil<BR>1 teaspoon granulated sugar<BR>1/2 teaspoon coarse salt<BR>2 Tablespoons rice vinegar<BR>1 Tablespoon white sesame seeds<BR><B><I>Preparation:</B></I><BR>1. Simmer the shrimp for three minutes, then remove their shells, put them into ice water for two minutes, drain, and set then aside for three minutes.<BR>2. Soak seaweed for half an hour in cold water, then drain.<BR>3. Toss shrimp and the kelp or another seaweed with lemon juice and set aside covered in the refrigerator for half an hour. Remove, add the soy sauce, sesame oil, sugar, and the rice vinegar, and toss again; then put this on a serving dish.<BR>4. Sprinkle sesame seeds on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'952971B29166D0429C90D1286EA820C3', X'0305D127797DE84986488FE23DA18C4E', X'0305D127797DE84986488FE23DA18C4E', 0, 'javascript:', 'Shrimp with Straw Mushrooms', 2, NULL, 0, 'Shrimp with Straw Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=338', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pound shelled and deveined shrimp<BR>1 egg white<BR>dash of white pepper<BR>1 Tablespoon cornstarch<BR>5 ounces straw mushrooms, fresh or canned, cut in halves<BR>1 carrot, peeled and angle cut<BR>2 scallions, cut into one-inch pieces<BR>5 Tablespoons corn oil<BR>1 Tablespoon rice wine or dry sherry<BR>1 Tablespoon cornstarch mixed with two tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Mix shrimp with egg white, pepper and the one tablespoon of cornstarch and set aside for fifteen minutes.<BR>2. Blanch fresh or canned straw mushrooms and then rinse in cold water and drain well.<BR>3. Boil carrot for half minute, drain, and set aside hot mixing them with the scallions.<BR>4. Heat oil in a wok or pan and fry the shrimp mixture until they just start to turn pink, remove and drain, and then remove all oil from the pan but do not wash or wipe it out.<BR>Add carrot mixture, shrimp, and rice wine and mix well. Then add cornstarch mixture and cook just until it thickens and clears, and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BC1539216A199D4AAAA299B087610133', X'D973278F99619949BFCC8430859DA470', X'D973278F99619949BFCC8430859DA470', 0, 'javascript:', 'Shrimp with Two Fruits', 2, NULL, 0, 'Shrimp with Two Fruits', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1157', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>10 large shrimp, shells and veins removed and discarded, then sliced the long way<BR>1 Tablespoon cornstarch<BR>1/2 teaspoon salt<BR>1 Tablespoon vegetable oil<BR>2 cloves fresh garlic, peeled and minced<BR>5 frsh asparagis, cut into one-inch pieces<BR>10 canned litchi fruit, drained, reserving the liquid<BR><B><I>Preparation:</B></I><BR>1. Mix shrimp, cornstarch and salt. Let rest ten minutes.<BR>2. Heat wok or fry pan, add oil, and stir-fry the minced garlic for half mintue. Then add shrimp and stir-fry another half minute.<BR>3. Add asparagus pieces and stir-fry one minute before adding the litchi fruit. Stir until heated through, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0A2C7242A049244F944C64C5CA3CFF40', X'181034AE8A95D64EB797DC60B49C5768', X'181034AE8A95D64EB797DC60B49C5768', 0, 'javascript:', 'Shrimp Wrapped in Tofu Skin', 2, NULL, 0, 'Shrimp Wrapped in Tofu Skin', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=774', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 sheets died tofu skin, soaked for three minutes<BR>1/2 pound shrimp, peeled, veins removed, and minced coarsely<BR>6 canned, drained water chestnuts, minced coarsely<BR>3 Tablespoons minced fresh coriander<BR>2 Tablespoons minced fresh ginger<BR>1 Tablespoon sesame oil<BR>1/2 teaspoon chili paste with garlic<BR>1 teaspoon corn oil<BR><B><I>Preparation:</B></I><BR>1. Cut each tofu sheet into four squares, about five inches on a side.<BR>2. Mix minced shrimp, water chestnuts, coriander, ginger, sesame oil, and chili paste with garlic and divide this mixture into twelve parts.<BR>3. Put one part of the shrimp mixture in the center of a piece of tofu skin and fold the sides in and then the ends in; and gently flatten this package.<BR>4. Oil a heat-proof plate and put these packages seam side down on it. Steam over boiling water for eight minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'58027D7B05D67E41A5F600FF195A0241', X'A2754EE824DCC64891B2C2342E5E3DF1', X'A2754EE824DCC64891B2C2342E5E3DF1', 0, 'javascript:', 'Shrimp, Peppers, and Olive Kernels', 2, NULL, 0, 'Shrimp, Peppers, and Olive Kernels', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=922', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>1/2 cup olive kernels<BR>1 pound shrimp, shells and black veins removed<BR>1/2 teaspoon salt<BR>1/2 teaspoon ground white pepper<BR>1 green pepper, seeds and veins removed, cut into half-inch pieces<BR>1 chili pepper, seeds and veins removed, and finely diced<BR>1 clove garlic, peeled and minced<BR>2 teaspoons thin soy sauce<BR>2 teaspoons cornstarch<BR>1 teaspoon sugar<BR><B><I>Preparation:</B></I><BR>1. Heat oil in a wok or pan and stir-fry olive kernels until golden, then remove them to paper towels and set aside.<BR>2. Mix shrimp with salt and pepper and set aside for half an hour.<BR>3. Reheat remaining oil and stir-fry green and chili pepper pieces and the garlic for one minute. Add shrimp and stir-fry one minute just until no longer pink, then add olive kernels.<BR>4. Mix soy sauce, cornstarch, and sugar, and add to the shrimp mixture. Stir-fry half minute until thick, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'827B8A866241BB4DAF8CD7D88EAFD1AC', X'D4B000D00253D048B60A09E2CD07954B', X'D4B000D00253D048B60A09E2CD07954B', 0, 'javascript:', 'Shrimp-stiffed Fish Maw', 2, NULL, 0, 'Shrimp-stiffed Fish Maw', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=671', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 ounces fish maw or enough to make ten prepared pieces, two-inch by five-inch strips, soaked (see instructions in article above)<BR>1/2 pound shrimp, minced fine or ground<BR>1 egg white<BR>1 teaspoon sugar<BR>1 teaspoon thin soy sauce<BR>1 Tablespoon corn starch<BR>10 whole scallops<BR>2 Tablespoons cornstarch<BR>2 Tablespoons vegetarian oyster sauce heated for half minute in microwave oven<BR>3 Tablespoons minced fresh coriander<BR><B><I>Preparation:</B></I><BR>1. Heat sesame oil and gently fry the freshly soaked fish maw. one minute per side. Then drain and cool. Press out excess oil with the side of the cleaver.<BR>2. Mix shrimp, egg white, thin soy sauce, sugar, and one tablespoon cornstarch, and divide into ten equal portions.<BR>3. Cut scallops in half and dust one side of each with cornstarch and place one fifth of one of the shrimp portions on the dusted scallop and top it with the other dusted scallop side.<BR>4. Oil a plate for use in the steamer, and prepare a steamer with two inches of water in the bottom; and start to bring it to the boil.<BR>5. Take one of the shrimp paste amounts and spread it evenly on one side of one fish maw. Put the stuffed scallop in the middle, and bring the ends up to cover the scallop. Place it seam side down on the oiled plate. Repeat until all scallops are stuffed. The fish maw slices spread with the shrimp mixture, and they are wrapped around the scallops.<BR>6. Steam the fish maw covered scallops over boiling water, with the steamer covered, for five minutes.<BR>7. Remove the plate with them on it, then put them on another plate. Drizzle with vegetarian oyster sauce, sprinkle the coriander on this, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'33AAB0914E4ACB4EABABF0257FB68A33', X'8A95BEDA83842B488773C7DDE6997F9D', X'8A95BEDA83842B488773C7DDE6997F9D', 0, 'javascript:', 'Shrimp-stuffed Fish', 2, NULL, 0, 'Shrimp-stuffed Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1038', '<TD><RECIPE><B>Ingredients:</B><BR>12 whole canned longan or 20 litchi fruits<BR>2 ounces shrimp, peeled, veins discarded, minced, their tails saved for decor<BR>1/4 cup canned water chestnuts, minced<BR>2 button mushrooms, minced<BR>1 teaspoon cornstarch<BR>1/8 teaspoon salt and ground white pepper combined<BR>3 or 4 lettuce leaves, to hold the fruit upright in the steamer<BR><B>Preparation:</B><BR>1. Check that there is no tiny pieces of pit in the fruit.<BR>2. Mix shrimp, water chestnuts, and the mushrooms and mince a bit more until well combined. Then add cornstarch and salt and pepper, and carefully stuff a little bit into each fruit, being careful not to break it. Push tail piece into the end of the stuffing so that it extends and looks pretty.<BR>3. Crumple lettuce leaves to that they hold the fruit upright in a steamer basket, their tails up. Cover the steamer basket and steam over boiling water for five minutes. Remove to a bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9D164C5DBFACAA48B4220B694700B61E', X'49BA0168EBAB534494B76A04101F0EFB', X'49BA0168EBAB534494B76A04101F0EFB', 0, 'javascript:', 'Shrimp-stuffed Loquats', 2, NULL, 0, 'Shrimp-stuffed Loquats', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=444', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound peeled shrimp, minced fine<BR>1/8 teaspoon ground white pepper<BR>1 teaspoon sweet potato flour or cornstarch<BR>16 well-drained canned loquats<BR>1 Tablespoon crab roe (optional)<BR>1 lettuce leaf as garnish for serving plate<BR><B><I>Preparation:</B></I><BR>1. Mix shrimp with ground pepper and sweet potato flour gently.<BR>2. Stuff the loquats and put tiny bit of crab roe on the top of each one.<BR>3. Steam over boiling water for one and a half minutes, then remove to platter siting them on a raw lettuce leaf; and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2FF7746726BB504CAAD02D97BD1D5CF8', X'9A3B3255441FA741A436DB97043CC162', X'9A3B3255441FA741A436DB97043CC162', 0, 'javascript:', 'Sichuan Crispy Spicy Fish', 2, NULL, 0, 'Sichuan Crispy Spicy Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=7', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>4 fish tails<BR>3 Tablespoons water chestnut flour<BR>1 cup corn oil<BR>2 or 3 whole dried hot peppers<BR>3 cloves garlic, chopped<BR>6 Tablespoons minced scallions<BR>3 Tablespoons minced fresh ginger<BR>1/4 cup small black mushrooms, soaked, stems removed, and cut in half<BR>1/4 cup canned bamboo shoots, rinsed and sliced thinly<BR>2 Tablespoons cornstarch mixed with 4 Tablespoons cool water<BR><I><B>Preparation:</I></B><BR>1. Coat the dry fish tales with the water chestnut flour.<BR>2. Heat oil and deep fry the fish until lightly browned, then remove, drain, and reserve the oil. Put fish on a heated platter or shallow bowl.<BR>3. Put two tablespoons of the reserved oil in a wok and fry the hot peppers, garlic, scallions, and fresh ginger for one minute. You can discard the hot peppers if you do not like things to piquant. Then add the mushrooms and bamboo shoots and fry for another two minutes. Next, add the cornstarch mixture and bring to the boil and simmer until it boils and thickens. Pour this over the fish tails and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'92D3777DF9314E48B02BDE977FF0A3DF', X'CFCA506A4D6351459C11E0452DF17BB9', X'CFCA506A4D6351459C11E0452DF17BB9', 0, 'javascript:', 'Six Item Squares', 2, NULL, 0, 'Six Item Squares', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1634', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup Crucian or another pickled carp or white-fleshed fish skin from the above or from another fish<BR>2 egg whites. Beaten only until beginning to foam<BR>1 cup minced shrimp, peels and veins removed<BR>1/2 cup minced water chestnuts<BR>1/2 cup minced rape or another green vegetable<BR>1/2 cup minced smoked ham<BR>1/2 cup chopped walnuts<BR>2 Tablespoons cornstarch<BR>1 Tablespoon white or purple rice flour<BR>1 cup vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Spread the minced fish on the fish skin and spread about one-quarter of the egg white on it.<BR>2. Mix one quarter of the egg white with the minced shrimp and spread this carefully on to the minced fish.<BR>3. Mix the minced water chestnuts and the rape with another quarter of the egg white and spread this carefully on top of the shrimp mixture.<BR>4. Mix the last quarter of the egg white with the minced ham, walnuts, cornstarch, and the wheat flour and spread this on top of the water chestnut-rape layer.<BR>5. Heat the oil in a wok or large deep fry pan and carefully slide this fish package, skin side down, into the oil using two spatulas. With a ladle, pour hot oil from the wok or pan over the fish package until it is golden, about four or five minutes. Then carefully remove it on to three or more paper towels to drain. After one minute, slide it off the paper towel and on to a cutting board. Cut into squares, transfer to a pre-heated platter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4DF3B2EED9D5D5469BC4833AAA7CFEAE', X'38FB9EB8A50C71419FC797D639389D6E', X'38FB9EB8A50C71419FC797D639389D6E', 0, 'javascript:', 'Sliced Abalone and Baby Bok Cai', 2, NULL, 0, 'Sliced Abalone and Baby Bok Cai', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=679', '<TD><RECIPE><B><I>Ingredients:<B></I><BR>1 Tablespoon sesame oil<BR>2 cloves garlic, sliced, then slivered<BR>2 slices fresh ginger, slivered<BR>2 Tablespoons rice wine<BR>1/4 cup chicken broth<BR>1 Tablespoon cornstarch mixed with one tablespoon cold water<BR>2 Tablespoons vegtarian oyster sauce<BR>1 teaspoon sugar<BR>1 teaspoon sesame oil<BR>1/2 pound baby bokcai<BR><B><I>Preparation&gt;</B></I><BR>1. Drain, rinse in cold water, then slice abalone thinly, and pound each slice with the flat side of the cleaver.<BR>2. Heat sesame oil and fry the garlic and ginger for one minute, then add rice wine and chicken broth and the cornstarch mixture. Bring to the boil stirring until thickened, then add oyster sauce, sugar, and the sesame oil.<BR>3. Plate the <I>bok cai</I> and pour abalone and all its sauce over it, and serve.br&gt;</B></B></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1DFB74AB04D2024B9CAFAB6A6ACF1F60', X'7C319C5C367E434B8B1EDA0101801F25', X'7C319C5C367E434B8B1EDA0101801F25', 0, 'javascript:', 'Smoked Fish', 2, NULL, 0, 'Smoked Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1740', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1 and 1/2 pounds firm white fish such as cod or turbot, whole with scales and gills removed, or as fillets, pat dry with paper towels<BR>1 and 1/2 Tablespoons chopped fresh ginger<BR>1 and 1/2 Tablespoons Shao Xing rice wine<BR>2 Tablespoons light soy sauce<BR>3 Tablespoons finely chopped scallions<BR>peanut or vegetable oil to lubricate the pan<BR>3 Tablespoons brown sugar<BR>3 Tablespoons uncooked long-grain uncooked rice<BR>3 Tablespoons black tea leaves<BR>3 whole star anise, broken into sections<BR>2 teaspoons five-spice powder<BR>2 Tablespoons Sichuan peppercorns<BR>2 pieces Chinese cinnamon bark broken into pieces<BR><B><I>Preparation:</B></I><BR>1. In a small bowl, mix ginger, rice wine, soy sauce, and scallions and mix until almost a paste or put them in a blender or food processor to make the paste. Rub this on all sides of the fish, and set aside for half an hour.<BR>2. In a steamer, wok, or a saucepan, fill with two-inches of water, and put a heat-proof plate on a grate over it. Then bring the water to the boil. Put the fish on lightly oiled heat-proof plate on the grate and carefully lower it onto the grate over the water after it boils. Cover tightly and steam for five minutes. Then remove the plate with the fish, and allow to cool slightly.<BR>3. Line the inside of a wok or fry pan with foil. Mix the sugar, rice, tea, star anise, five-spice powder, Sichuan peppercorns, and the cinnamon, and spread this on the foil. Rub a rack with a little oil and place it over the ingredients, and put the fish on the rack. Heat the wok or pan on high and when the mixture in the bottom starts to smoke, turn the heat down to moderate, cover, and leave fish there for five minutes. Then remove the fish and discard all the smoking ingredients and foil, and serve the fish.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4A06585811B313489793184E5AE42755', X'F28E280595191B4382888CCBC8BB2A7A', X'F28E280595191B4382888CCBC8BB2A7A', 0, 'javascript:', 'Smoked Fish, Shanghai Style', 2, NULL, 0, 'Smoked Fish, Shanghai Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=293', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 pounds black fish or carp<BR>2 Tablespoons thin soy sauce<BR>2 Tablespoons dry sherry<BR>2 scallions<BR>6 slices ginger<BR>1 stick cinnamon<BR>2 star anise<BR>1/2 teaspoon five spice powder<BR>1 and 1/2 Tablespoons sugar<BR>1/4 cup dark soy sauce<BR>1 cup corn oil<BR>1 teaspoon sesame oil<BR><I><B>Preparation:</I></B><BR>1. Cut fish into one-and-a-half-inch by two-inch pieces then dry each of them with paper towels.<BR>2. Coat fish with soy sauce and sherry and refrigerate for half an hour.<BR>3. Mix scallions, ginger, cinnamon, anise, five spice powder, sugar, one-quarter cup soy sauce, and one cup water into a sauce pan and bring to the boil, then lower heat and simmer for half an hour.<BR>4. Drain removing the solids and saving the liquid. Add sesame oil.<BR>5. Heat corn oil in wok or pan and fry fish pieces until brown and crisp, about three or four minutes, then drain on a paper towel and immediately dip in sauce mixture and place into a glass bowl. Pour remaining sauce and the sesame oil over the fish and set aside for half an hour. Remove pieces from sauce, and if desired, sprinkle with a little more five spice powder, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E2EBBD41A80DFB4C84EC26733B453810', X'205F225CEC47D94880EAC3EE949BF1ED', X'205F225CEC47D94880EAC3EE949BF1ED', 0, 'javascript:', 'Smoked Oysters in Lotus LLeaf', 2, NULL, 0, 'Smoked Oysters in Lotus LLeaf', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2140', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups short-grain sweet rice, soaked overnight.<BR>10 medium-size Chinese mushrooms, soaked until soft, stems removed and discarded, sliced, their soaking water reserved<BR>10 scallions, minced<BR>4 Chinese sweet <I>lop xong</I>sausages, quartered engthwise, then thin sliced<BR>2 Tablespoons vegetable oil<BR>1/2 cup Chinese rice wine<BR>2 Tablespoons dark soy sauce<BR>2 Tablespoons granulated sugar<BR>2 Tablespoons sesame oil<BR>3 - 4 lotus leaves soaked for half an hour, the thick part of their center stems cut away, and discarded<BR>5 slices fresh ginger, peeled, then minced<BR>2 three-ounce cans of smoked oysters, drained and chopped<BR>1.4 cup pine nuts, lightly toasted<BR><B><I>Preparation:</B></I><BR>1. Mix rice, mushrooms, and the sausages and stir-fry them in the vegetable oil, then add the rice wine, soy sauce, sugar, sesame oil, and the mushroom water, and the smoked oysters with the rice mixture. 2. Put half the lotus leaves on the bottom of a heat-proof pie plate, and put the rice mixture on them, sprinkling the pine nuts on top, then cover with the rest of the lotus leaves.<BR>3. Put a rack into a steamer over boiling water and steam this for forty-five minutes. Then remove the pie plate and put its contents on a platter. Allow to rest for fifteen minutes, then cut into wedges, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'41A0AF5D21C9264C9DB918A41A58749C', X'06E298B4552A014C9195CF617DB22F3E', X'06E298B4552A014C9195CF617DB22F3E', 0, 'javascript:', 'Snail Pancakes', 2, NULL, 0, 'Snail Pancakes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2024', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound fresh or frozen and defrosted snail meat, sliced and pounded<BR>2 stalks celery, slice thinly on an angle<BR>1 onion, peeled and minced<BR>1 hot red pepper, seeded and minced<BR>1 egg, beaten<BR>1 cup all-purpose flour<BR>1 teaspoon salt<BR>½ cup vegetable oil<BR>hot sauce or oyster sauce for dipping<BR><B><I>Preparation:</I></B><BR>1. Finely mince pounded snail meat, or grind it in a meat grinder using the medium setting; then mix with the celery, onion, and red pepper.<BR>2. Add the beaten egg, flour, and salt as a medium-thick batter.<BR>3. Heat oil in a wok or fry pan and using a small ladle, make a pancake and allow it to set before turning it over and cook until almost done. Make a second pancake, and turn it over when tan on the first side. When both sides are equally tan, remove it to paper towels. Continue until all batter is used. Keep the pancakes in a warm oven until all are finished, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B21FE82E131E3B48AC381ED92169ADDD', X'5B75E56DB65872489A211353678D1969', X'5B75E56DB65872489A211353678D1969', 0, 'javascript:', 'Snail Pancakes', 2, NULL, 0, 'Snail Pancakes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2285', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound snail meat, sliced then pounded<BR>2 stalks celery, slice thinly on an angle<BR>1 onion, minced<BR>1 hot red pepper, seeded and minced<BR>1 egg, beaten<BR>1 cup all-purpose flour<BR>1 teaspoon salt<BR>½ cup vegetable oil<BR>hot sauce or oyster sauce for dipping<BR><B><I>Preparation:</B></I><BR>1. Finely mince the pounded snail meat, or grind it in a meat grinder using the medium setting. Then mix it with the celery, onion, and red pepper.<BR>2. Add the beaten egg, flour, and salt as a medium-thick batter.<BR>3. Heat oil in a wok or fry pan and using a small ladle, make a small pancake and allow it to set before turning it over and cooking the second side until almost done. Next, make a second pancake, and turn it over when tan on the first side. When both sides are equally tan, remove both to paper towels. Continue until all batter is used. Keep the pancakes in a warm oven until all are finished, then serve them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'00A274D14FD1DA42A016D09535F49A0C', X'06AFCA0A6726AB45975FFCFA05F19D06', X'06AFCA0A6726AB45975FFCFA05F19D06', 0, 'javascript:', 'Snails Cantonese Style', 2, NULL, 0, 'Snails Cantonese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2028', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 Tablespoons vegetable oil<BR>1 clove garlic, peeled and smashed<BR>1 teaspoon fermented black beans, coarsely chopped<BR>1/4 pound ground pork<BR>1 cup tenderized snail meat, grinding or mincing very fine<BR>3 Tablespoons chicken stock<BR>5 slices fresh ginger, peeled and slivered<BR>1 teaspoon dark soy sauce<BR>2 teaspoons cornstarch mixed with one tablespoon cold water<BR>1 egg, beaten<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, garlic, and the fermented beans, and stir for on minute or until pork is no longer pink.<BR>2. Now add the snail meat and stir once or twice before adding the chicken stock, ginger, and the soy sauce. Bring this to the boil; add the cornstarch mixture and stir until this thickens.<BR>3. Put this mixture into a pre-heated bowl, put the egg on top, and stir, then cover for one minute before removing the cover, and serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AE086CDF6ACB05448141C4B01D8C2334', X'6DC405EFC23980459484727A88BD6F9D', X'6DC405EFC23980459484727A88BD6F9D', 0, 'javascript:', 'Snails with Vegetables', 2, NULL, 0, 'Snails with Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2025', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound fresh or frozen and defrosted snail meat, sliced and pounded<BR>2 stalks celery, slice thinly on an angle<BR>1 onion, peeled and minced<BR>1 hot red pepper, seeded and minced<BR>1 egg, beaten<BR>1 cup all-purpose flour<BR>1 teaspoon salt<BR>½ cup vegetable oil<BR>hot sauce or oyster sauce for dipping<BR><B><I>Preparation:</I></B><BR>1. Finely mince pounded snail meat, or grind it in a meat grinder using the medium setting; then mix with the celery, onion, and red pepper.<BR>2. Add the beaten egg, flour, and salt as a medium-thick batter.<BR>3. Heat oil in a wok or fry pan and using a small ladle, make a pancake and allow it to set before turning it over and cook until almost done. Make a second pancake, and turn it over when tan on the first side. When both sides are equally tan, remove it to paper towels. Continue until all batter is used. Keep the pancakes in a warm oven until all are finished, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FD2BED6BD244084E8C1E606AA8EFBAE6', X'D37B5BEEC9D55140815777C7F7F1E362', X'D37B5BEEC9D55140815777C7F7F1E362', 0, 'javascript:', 'Snails with Vegetables', 2, NULL, 0, 'Snails with Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2026', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pound snail meat, sliced and minced<BR>2 Tablespoons lemon juice<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon hot sauce<BR>1 cup finely minced dark green vegetables<BR>½ cup all-purpose flour<BR>4 eggs, well-beaten<BR>1 Tablespoon vegetable oil<BR>½ teaspoon sesame oil<BR><B><I>Preparation:</B><I><BR>1. Mix snail meat and the lemon juice and set aside for twenty minutes, then drain and discard all liquid.<BR>2. Add the wine, hot sauce, and the minced vegetables and let this rest for ten minutes.<BR>3. Mix the eggs, stir this into the snail mixture.<BR>4. Heat the wok or fry pan, add the oil, and then put the entire snail mixture into the wok or fry pan and allow to it to set and is brown on one side before turning it over and allowing the other side to brown. As this is a thick egg mixture, do check with a toothpick that it is set into the center. When done, turn it out onto a paper towel, then put it onto a pre-heated platter, and cut it into pie-shaped wedges. Serve.<BR>snails, Cantonese style Ingredients: 2 Tablespoons vegetable oil 1 clove garlic, peeled and smashed: 1 teaspoon fermented black beans, coarsely chopped 1/4 pound ground pork 1 cup tenderized snail meat, grinding it is one way to do so 3 Tablespoons chicken stock 5 slices fresh ginger, peeled and slivered 1 teaspoon dark soy sauce 2 teaspoons cornstarch mixed with 1 Tablespoon cold water 1 egg, beaten Preparation: 1. Heat wok or fry pan, add the oil, garlic, and the fermented beans, and stir for on minute or until pork is no longer pink. 2. Now add the snail meat and stir once or twice before adding the chicken stock, ginger, and the soy sauce. Bring this to the boil; add the cornstarch mixture and stir until this thickens. 4. Put this mixture into a pre-heated bowl, put the egg on top, and stir, then cover for one minute before removing the cover, and serving. Salamanders, giant or otherwise, are amphibians, some are in the order Caudata, and all are lizard-like in appearance with four toes on their front legs, five on those in the rear. Not related to any sea creatures many think, they breathe differently. Some have gills, others have lungs, and still others breathe through their skin or through membranes in their mouth. Anatomically, salamanders have no rear legs, a few do not even have front ones. They are in the order Urodela, and sometimes their legs do get lost. Some can and do regrow them and if they have them, they are very short and mostly in the rear. Often, they have one more toe on their front legs. Chefs tell us with or without legs, they all taste the same, and all are considered yummy. About one-third of them lay their eggs in the water, others on land, and both roe are appreciated. People raising them are called heliculturalists, and all salamanders have variations in their life cycles. Most look like small lizards, and young folk often confuse them with eels, particularly those without or with very tiny legs. Males and females look alike when grown, but when young the males can have external gills. Almost all fertilize their eggs internally and in unusual ways. Females are known to pick up male sperm with their vents and store them there until they lay their own eggs. Only then do they fertilize them. The Giant Salamander, also called the Hellbender or Crytobranchus alleganiensis, is the largest amphibian in the world. Most Chinese love to eat them and smaller ones, but they no longer can because they are considered an endangered species. This is as they have been over-gathered and over-used, particularly in Chinese medicine. Thought to be aphrodisiacs, they have other medicinal uses, one is as a cure for rheumatism. Some people believe salamanders are snakes and they use them as such. One chef told us that he cooks them as if they were, and we should, too. He says they have a strong aroma, and we should cook them in a stock made with chicken, ham, sugarcane, ginger, and tangerine peel if we find');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E588302B226991449BA1E6D0B8BDDE21', X'F55741B8C1AC98458DE2D3403695B54E', X'F55741B8C1AC98458DE2D3403695B54E', 0, 'javascript:', 'Snails with Vegetables', 2, NULL, 0, 'Snails with Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2027', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound snail meat, sliced and minced<BR>2 Tablespoons lemon juice<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon hot sauce<BR>1 cup finely minced dark green vegetables<BR>½ cup all-purpose flour<BR>4 eggs, well-beaten<BR>1 Tablespoon vegetable oil<BR>½ teaspoon sesame oil<BR><B><I>Preparation:</I></B><BR>1. Mix snail meat and the lemon juice and set aside for twenty minutes, then drain and discard all liquid.<BR>2. Add the wine, hot sauce, and the minced vegetables and let this rest for ten minutes.<BR>3. Mix the eggs, stir this into the snail mixture.<BR>4. Heat the wok or fry pan, add the oil, and then put the entire snail mixture into the wok or fry pan and allow to it to set and is brown on one side before turning it over and allowing the other side to brown. As this is a thick egg mixture, do check with a toothpick that it is set into the center. When done, turn it out onto a paper towel, then put it onto a pre-heated platter, and cut it into pie-shaped wedges. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E8DA91B330893947A3A8EF617E737BF0', X'037AEF4795225A4CAEF2EE2EA738F020', X'037AEF4795225A4CAEF2EE2EA738F020', 0, 'javascript:', 'Snails with Vegetables', 2, NULL, 0, 'Snails with Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2287', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound snail meat, sliced and minced<BR>2 Tablespoons lemon juice<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon hot sauce<BR>1 cup finely minced dark green vegetables<BR>½ cup all-purpose flour<BR>4 eggs, well-beaten<BR>1 Tablespoon vegetable oil<BR>½ teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix snail meat and the lemon juice and set aside for twenty minutes, then drain and discard any liquid.<BR>2. Add the wine, hot sauce, and the minced vegetables and let this rest for ten minutes.<BR>3. Mix the eggs, stir this into the snail mixture.<BR>4. Heat the wok or fry pan, add the oil, and then put the snail mixture into the wok or fry pan and allow to it to set and brown on one side then turn it over and allowing the other side to brown and set. As this is a thick egg mixture, do check with a toothpick in the center that it is cooked through. Then turn it out onto a paper towel, put it on a pre-heated platter, and cut it into pie-shaped wedges before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'012700D245968F4683E81959A97AD6CD', X'15AB6D0C2A41914E835E29D28C634AA1', X'15AB6D0C2A41914E835E29D28C634AA1', 0, 'javascript:', 'Snails, Cantonese Style', 2, NULL, 0, 'Snails, Cantonese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2288', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>1 clove garlic, peeled and smashed<BR>1 teaspoon fermented black beans, coarsely chopped<BR>1/4 pound ground pork<BR>1 cup ground snail meat<BR>3 Tablespoons chicken stock<BR>5 slices fresh ginger, peeled and slivered<BR>1 teaspoon dark soy sauce<BR>2 teaspoons cornstarch mixed with one Tablespoon cold water<BR>1 egg, beaten<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, garlic, and the fermented beans, and stir for on minute or until pork is no longer pink.<BR>2. Now add the snail meat and stir once or twice before adding the chicken stock, ginger, and the soy sauce. Bring this to the boil; add the cornstarch mixture and stir until thickened.<BR>4. Put this mixture into a pre-heated bowl, put the egg on top, and stir, then cover for one minute before removing cover and serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3822921226AAB94FB65EC150D631039D', X'E78289992167BE42B43AD59023367471', X'E78289992167BE42B43AD59023367471', 0, 'javascript:', 'Snails, Cantonese-style', 2, NULL, 0, 'Snails, Cantonese-style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2299', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 Tablespoons vegetable oil<BR>1 clove garlic, peeled and smashed<BR>1 teaspoon fermented black beans, coarsely chopped<BR>1/4 pound ground pork<BR>1 cup ground snail meat<BR>3 Tablespoons chicken stock<BR>5 slices fresh ginger, peeled and slivered<BR>1 teaspoon dark soy sauce<BR>2 teaspoons cornstarch mixed with onetablespoon cold water<BR>1 egg, beaten<BR><B><I>Preparation:</I></B><BR>1. Heat wok or fry pan, add the oil, garlic, and the fermented beans, and stir for one minute or until pork is no longer pink.<BR>2. Add the snail meat and stir once or twice before adding the chicken stock, ginger, and the soy sauce, and bring to the boil.<BR>3. Add the cornstarch mixture and stir until this thickens.<BR>4. Put this mixture into a pre-heated bowl, put the egg on top, and stir, then cover for one minute before removing the cover, and serve<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0C63290E0CDAB54AAD9B4FC8B8608F5C', X'5B43D31F330AE945AE945DAE9332743B', X'5B43D31F330AE945AE945DAE9332743B', 0, 'javascript:', 'Snake and Shark''s Fin Casserole', 2, NULL, 0, 'Snake and Shark''s Fin Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=53', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>6 dried shiitake mushrooms<BR>1 piece tangerine peel<BR>5 dried longans<BR>6 slices fresh ginger<BR>2 scallions, cut in eighths<BR>2 Tablespoons corn oil<BR>1 chicken, about four pounds and cut into eight to twelve pieces<BR>4 cups chicken stock<BR>1 snake, head &amp; skin removed<BR>1/2 pound prepared shark''s fin<BR>1 slice ginger root<BR>1/4 cup brown sugar<BR>1 white chrysanthemum<BR>4 Tablespoons cornstarch<BR>2 Tablespoons sesame oil<BR>3 Tablespoons thick soy<BR>1 piece fried Chinese cruller<BR>2 Tablespoons Yunnan or Smithfield ham<BR><I><B>Preparation:</I></B><BR>1. Soak first three ingredients separately in warm water for 20 minutes, drain and discard the water.<BR>2. Fry scallions and ginger in oil for 1 minute then add chicken, tangerine peel, longans, sliced mushrooms, and stock. Simmer for two hours covered.<BR>3. Cut snake into three inch sections, add it to the chicken mixture, and cook one hour longer.<BR>4. Boil shark''s fin with one slice of fresh ginger for three hours. Discard water and shred.<BR>5. Put chicken mixture and sharks fin in stove-top casserole. Add sugar and petals of the chrysanthemum.<BR>6. Mix cornstarch, sesame oil, 1/4 cup water, and thick soy and mix in the casserole. Bring to the boil stirring gently.<BR>7. Slice the cruller and put the pieces and the ham in, stir and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'108783E37D2A8846B45746417D2D3D35', X'64CE4CD94741A24493EFFCA2BF5A1892', X'64CE4CD94741A24493EFFCA2BF5A1892', 0, 'javascript:', 'Sour Carp with Noodles', 2, NULL, 0, 'Sour Carp with Noodles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1655', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 carp, about two pounds, scales, fins, and innards removed and discarded<BR>2 Tablespoons Chinese rice wine<BR>1 Tablespoon coarse salt<BR>1 cup vegetable oil<BR>2 large cloves garlic, peeled and minced 2 scallions, coarsely chopped<BR>1 Tablespoon fresh ginger, peeled and minced<BR>2 Tablespoons broad bean sauce<BR>1 Tablespoon thin soy sauce<BR>2 Tablespoons Chinese black vinegar<BR>1 Tablespoon tomato paste<BR>1 cup chicken or fish broth<BR>1 Tablespoon sugar<BR>4 teaspoons cornstarch mixed with one tablespoon cornstarch<BR>1 scallion, minced<BR><B><I>Preparation:</B></I><BR>1. Rub the fish outside and in with the rice wine and the salt and let it rest for half an hour.<BR>2. Heat the oil in a wok or large pot and deep fry the fish for two minutes on one side and two minutes on the other and remove t paper towels. Set the oil aside.<BR>3. Using one tablespoon of the oil, fry the garlic and ginger for half a minute until very aromatic, than add the broad bean sauce, soy sauce, vinegar, and tomato paste, stir very well before adding the chicken or fish broth, and the sugar and stir once again.<BR>4. Then slide the fish into the wok and reduce the heat and simmer covered for five minutes.<BR>5. Remove fish to a pre-heated serving platter, bring remaining liquid to the boil, add the cornstarch mixture and stir until thickened, then pour over the fish. Sprinkle with the scallions and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'878E1D6636E3024D9872ABB89D79C26E', X'71E7A4210447A9409B9E28FB6B274096', X'71E7A4210447A9409B9E28FB6B274096', 0, 'javascript:', 'Spicy Cuttlefish', 2, NULL, 0, 'Spicy Cuttlefish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2206', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small fresh chili pepper, seeded and minced<BR>1 small fresh garlic, peeled and minced<BR>1 Tablespoons minced fresh ginger<BR>1 Tablespoon vegetable oil<BR>2 fresh cuttlefish, cut into one-inch pieces<BR>1 Tablespoon dark soy sauce<BR>1 Tablespoon granulated sugar<BR>1/23 teaspoon coarse salt<BR>1 Tablespoon chili paste with garlic<BR>2 Tablespoons white vinegar<BR><B><I>Preparation:</B></I><BR>1. Mix chili pepper, garlic, and ginger.<BR>2. Heat a wok or fry pan, add the oil, and in half minute, add the oil and the chili pepper mixture and stir-fry for one minute, the add the cuttlefish, soy sauce, sugar, salt, chili paste, and the vinegar, and stir-fry for three minutes, then put into a pre-heated bowl and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D8ABAA81292FBF4DAF9986F805D6BBD3', X'760ABCD691D4464B952DD77F17C03265', X'760ABCD691D4464B952DD77F17C03265', 0, 'javascript:', 'Spicy Fish with Yam Bean', 2, NULL, 0, 'Spicy Fish with Yam Bean', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=365', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound boneless skinless flat fish fillets<BR>1 egg white<BR>1 Tablespoon ginger juice<BR>1 Tablespoon cornstarch<BR>1 cup corn oil<BR>1 clove garlic, minced coarsely<BR>12 Sichuan peppercorns<BR>4 dried chili peppers, seeded and crushed coarsely<BR>1 small yam bean, peeled and cut into thin strips<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon rice wine<BR>1 Tablespoon Swatow wine vinegar<BR>1 scallion, cut into one-inch pieces<BR>2 Tablespoons coarsely chopped olive pits or almonds<BR><B><I>Preparation:</B></I><BR>1. Cut fish into one-inch by two-inch thin slices.<BR>2. Mix egg white, ginger juice and cornstarch and marinate the fish in this mixture for twenty minutes. Heat wok, add oil then deep fry half of the fish at a time for three minutes. Repeat for the second batch. Do not overcook. When removing the fish from the oil, separate the pieces on paper towels so that they cool quickly. Discard all but one tablespoon of oil from the wok.<BR>3. Heat remaining tablespoon of oil and add garlic, peppercorns, and yam bean and fry for one minute, then add soy sauce and rice wine and the fish and fry another minute until the fish is heated. Stir in the scallion pieces, stir and serve immediately garnished with the olive pits or almonds.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'96358026CF95BB4388D8BCEF0DB7668C', X'23461896EFB41E42873A9C53ADE5EB63', X'23461896EFB41E42873A9C53ADE5EB63', 0, 'javascript:', 'Spicy Shad Slices, Shanghai Style I', 2, NULL, 0, 'Spicy Shad Slices, Shanghai Style I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=475', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 and 1/4 pounds of shad (or another) fish fillet<BR>5 slices ginger root, minced<BR>2 scallions, minced<BR>5 Tablespoons soy sauce<BR>1/2 teaspoon salt<BR>1/4 cup sugar<BR>1 Tablespoon rice wine<BR>5 cups corn or soy oil<BR>1 teaspoon five-spice powder<BR>1/4 cup sugar<BR><B><I>Preparation:</B></I><BR>1. Cut fish fillets into half inch by two and a half inch pieces and lightly cover and refrigerate them for two hours.<BR>2. Mix scallions, ginger, soy sauce, salt, and wine and marinate pieces of shad and cover and put in the refrigerator for four or five hours, then drain any remaining liquid.<BR>3. Mix five spice powder and sugar in a bowl and add one and one-half cups boiling water and stir until dissolved.<BR>4. Heat oil and fry shad pieces about three minutes, they will be dark but not burned, then soak them about three or four minutes in the sugar water. 5. Remove from the sugar mixture and put on a platter. Let cool before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2A5532FE1A0D7742A4C8D57649FB1938', X'5B7E3E1E65427D42BFA4AACDF0FA5A41', X'5B7E3E1E65427D42BFA4AACDF0FA5A41', 0, 'javascript:', 'Spicy Shad Slices, Shanghai Style II', 2, NULL, 0, 'Spicy Shad Slices, Shanghai Style II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1441', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 and 1/4 pounds of shad (or other) fish fillet<BR>5 slices ginger root, minced<BR>2 scallions, minced<BR>5 Tablespoons soy sauce<BR>1/2 teaspoon salt<BR>1/4 cup sugar<BR>1 Tablespoon rice wine<BR>5 cups corn or soy oil<BR>1 teaspoon five-spice powder<BR>1/4 cup sugar<BR><B><I>Preparation:</B></I><BR>1. Cut fish fillets into half inch by two and a half inch pieces and lightly cover and refrigerate them for two hours.<BR>2. Mix scallions, ginger, soy sauce, salt, and wine and marinate pieces of shad and cover and put in the refrigerator for four or five hours, then drain any remaining liquid.<BR>3. Mix five spice powder and sugar in a bowl and add 1½ cups boiling water and stir until dissolved.<BR>4. Heat oil and fry shad pieces about three minutes, they will be dark but not burned, then soak them about three or four minutes in the sugar water.<BR>5. Remove from the sugar mixture and put on a platter. Let cool before serving.<BR><I>Approximate nutrient analysis</I> per person, when serving four people and assuming one quarter of the marinade is absorbed and one tablespoon of the sugar mixture likewise. Each serving has 554 calories, 8 g carbohydrate, 25 g protein, 440 mg sodium, 47 g total fat, 8 g saturated fat, and 106 mg cholesterol.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'39E1DEC75AA61A419142E7DEA594B590', X'7794BE0EE68FF445963C8538713C3497', X'7794BE0EE68FF445963C8538713C3497', 0, 'javascript:', 'Squid and Red Bean Squares', 2, NULL, 0, 'Squid and Red Bean Squares', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2023', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound fresh squid, head and cartilage removed, then mixed with two tablespoons thin soy sauce<BR>2 Tablespoons vegetable oil<BR>½ pound ground pork<BR>2 Tablespoons Chinese rice wine<BR>6 slices fresh ginger, peeled and sliced<BR>1 square or 1 Tablespoons fermented red bean paste, mashed with one Tablespoon cold water<BR>½ cup chicken stock<BR>1 Tablespoon cornstarch mixed with an equal amount of cold water<BR>1 egg white<BR><B><I>Preparation:</I></B><BR>1. Mix squid and soy sauce and set aside for twenty minutes. Then cut as in the recipe above, into rings and cross-cut squares.<BR>2. Heat wok or fry pan and add the oil and stir-fry the pork until no longer pink, then add the rice wine, ginger, fermented red bean paste and water, and the stock and stir-fry for two more minutes.<BR>3. No add the squid and the stock and the cornstarch mixture and stir-fry for another minute or until thick; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D96C71CC6164E54299CF1ED92E53A21B', X'264828E3C93FF04582763064D0971EAC', X'264828E3C93FF04582763064D0971EAC', 0, 'javascript:', 'Squid in Curry Sauce', 2, NULL, 0, 'Squid in Curry Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2019', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 fresh squid, heads remove and discard, beak, bones, and cartilage<BR>1 carrot, peeled and minced<BR>1 onion, peeled and minced<BR>3 cloves fresh garlic, peeled and minced<BR>1 cucumber, peeled, seeds discarded, and diced<BR>2 Tablespoons vegetable oil<BR>2 Tablespoons curry powder<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon coarse salt<BR>1 teaspoon granulated sugar<BR>2 dragon fruit, peeled and diced into one-inch pieces<BR>2 Tablespoons cornstarch mixed with a like amount of cold water<BR><B><I>Preparation:</B></I><BR>1. Prepare the squid removing and discarding beak, bone, and cartilage.<BR>2. Toss the carrot, onion, garlic, and cucumber pieces.<BR>3. Heat a wok or fry pan, add the oil, then the vegetables, and stir-fry for one minute, then add the squid, curry powder, rice wine, salt, and the sugar. Stir two or three times before adding the dragon fruit pieces, and the cornstarch water. Stir for one minute until thickened, then serve in a pre-heated serving bowl.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'32E8BE62717B824AA9625DF2D3C68688', X'3D7DEBF9D6EBC449B6B02A4689EE7084', X'3D7DEBF9D6EBC449B6B02A4689EE7084', 0, 'javascript:', 'Squid in Orange Sauce', 2, NULL, 0, 'Squid in Orange Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1617', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound dried squid, soaked overnight<BR>2 Tablespoons cornstarch<BR>1/4 pound shrimp, peeled and veins removed<BR>1/4 teaspoon salt<BR>1/4 teaspoon sugar<BR>dash of ground white pepper<BR>1 egg white<BR>1 Tablespoon flour<BR>1 cup vegetable oil<BR>1/2 cup orange juice<BR>1 Tablespoon water chestnut flour<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Remove any cartilage in the squid, cut into rectangles two-inches long and one-inch wide. Then cross cut the squid on its underside and dust with the cornstarch on the uncut side.<BR>2. Mince the shrimp into a paste and mix in the salt, sugar, and the ground pepper. Spread this on the uncut side of the squid.<BR>3. Beat egg white and the flour making a batter, and coat the squid-shrimp sell.<BR>4. Heat the vegetable oil and deep fry the squid for four minutes, remove and drain on paper towels.<BR>5. Put the orange juice, water chestnut flour, and the sesame oil in a small pot and bring to the boil. Add the squid rolls, stir, and simmer for one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'27CF4FAB34067D43975505CA7A147B42', X'2016356E2E0D4A4F8CF24F9964961532', X'2016356E2E0D4A4F8CF24F9964961532', 0, 'javascript:', 'Squid in Oyster Sauce', 2, NULL, 0, 'Squid in Oyster Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2282', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons vegetable oil<BR>4 dried squid, soaked overnight, then their head and cartilage discarded<BR>2 Tablespoons oyster sauce<BR>½ cup chicken stock<BR>1 Tablespoons Chinese rice wine<BR>2 slices fresh ginger, peeled and minced<BR>1 scallion, cut on an angle<BR>1 teaspoon baking soda<BR><B><I>Preparation:</B></I><BR>1. Cut squid tentacles into thin rings, their body into one-inch squares. Discard the beaks and any cartilage.<BR>2. Heat a wok or fry pan, add the vegetable oil, then the dried squid, the chicken soup, oyster sauce, rice wine, and the ginger, and stir-fry for one minute or longer or until the squid is tender. Then add the scallion pieces and the baking soda and stir-fry another minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'74A3F109F69CBE4F9EA46A6B8F0BAB55', X'1ED85E8E2F116C4288A8BC314AA30716', X'1ED85E8E2F116C4288A8BC314AA30716', 0, 'javascript:', 'Squid in Oyster Sauce', 2, NULL, 0, 'Squid in Oyster Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2021', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons vegetable oil<BR>4 dried squid, soaked overnight, then beak, head and cartilage discarded<BR>2 Tablespoons oyster sauce<BR>½ cup chicken stock<BR>1 Tablespoons Chinese rice wine<BR>2 slices fresh ginger, peeled and minced<BR>1 scallion, cut on an angle<BR>1 teaspoon baking soda<BR><B><I>Preparation:</B></I><BR>1. Cut squid tentacles into thin rings and the body into one-inch squares after discarding the beaks and any cartilage.<BR>2. Heat a wok or fry pan, and add the vegetable oil, then the dried squid, the chicken soup, oyster sauce, rice wine, and the ginger, and stir-fry for one minute. Next add the scallion pieces and the baking soda and stir-fry another minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4BCA051C86A8FC4384CE07012AB46124', X'C067A301ED1EF64FA98976CDB6DA4BBF', X'C067A301ED1EF64FA98976CDB6DA4BBF', 0, 'javascript:', 'Squid Pancake', 2, NULL, 0, 'Squid Pancake', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1197', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small dried squid, soaked overnight or until soft<BR>1/2 pound ground or minced pork<BR>1 Tablespoon cornstarch<BR>2 slices fresh ginger, minced<BR>1 teaspoon granulated sugar<BR>1 Tablespoon Chinese rice wine<BR>1.2 teaspoon slat and ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Mince squid, then mix it with all the other ingredients, stirring gently when doing so. Then put this in a small flat heat-proof dish with sides making a pancake.<BR>2. Steam over rapidly boiling water for half an hour.<BR>3. Remove any liquid from the bowl. It can be discarded or thickened with one tablespoon of cornstarch mixed with two tablespoons of cold water and brought to the boil.<BR>4. Serve on a clean plate, cutting the pancake into four pieces with or without pouring the thickened sauce over the squid pancake.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B816A852ED17A64DB736BA5852DB26CD', X'3F4CC02A47A01F4189D76185B38A1428', X'3F4CC02A47A01F4189D76185B38A1428', 0, 'javascript:', 'Squid with Black Bean Sauce', 2, NULL, 0, 'Squid with Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1196', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 teaspoons fermented black beans, rinsed and mashed<BR>2 teaspoons Chinese rice wine<BR>2 teaspoons sesame oil<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon brown sugar<BR>1/2 teaspoon granulated sugar<BR>1 Tablespoon vegetable oil<BR>6 cloves garlic, peeled and thinly sliced<BR>2 Tablespoon fresh ginger, minced<BR>1 to 2 red chili peppers, seeded and minced<BR>1 pound fresh squid, cleaned, blanched, cross-hatched, and cut into one-inch pieces<BR>2 sprigs fresh coriander, cut into half-inch pieces<BR><B><I>Preparation:</B></I><BR>1. Mix mashed black beans, rice wine, sesame oil, soy sauce, brown and granulated sugars, and one tablespoon of cold water and set aside.<BR>2. Heat wok or large fry pan, add the oil, and stir-fry the garlic, ginger, and chili peppers for one minute.<BR>3. Add the black bean mixture and stir-fry another minute before adding the squid. Stir-fry one additional minute, no more, then remove to a serving bowl. Sprinkle the coriander on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B29F20F7CA528A45BF4B1239C6338B66', X'87C5EB998770E143B645A7FECE2A1610', X'87C5EB998770E143B645A7FECE2A1610', 0, 'javascript:', 'Squid with Kidneys', 2, NULL, 0, 'Squid with Kidneys', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1449', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large fresh or frozen squid, innards and head removed and discarded<BR>1 pork kidney, cut in half and the veins removed<BR>1/2 green pepper<BR>1/2 carrot<BR>2 Tablespoons corn oil<BR>4 slices fresh garlic<BR>1 scallion, white minced, green part cut in half-inch pieces<BR>1 dried chili pepper, seeds and veins removed (use gloves to do this)<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon dark soy sauce<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon cornstarch mixed with two tablespoons cold water<BR><B><I>Preparation:</I></B><BR>1. Cut squid into two inch pieces and score in cross-hatched pattern on the inner surface. Blanch in boiling water for one minute, then drain.<BR>2. Cut each kidney half into four pieces and score in crosshatch manner on outer surface. Blanch in boiling water for half a minute, then drain.<BR>3. Cut carrot and green pepper into thick slices, and crosshatch each or cut them into some type of design.<BR>4. Heat oil, and fry garlic, white part of the scallion, and chili pepper for half a minute., then add carrot and pepper and stir-fry for one minute, then add both soy sauces and the rice wine and mix before adding the squid and kidney pieces. Stir-fry for one minute.<BR>5. Stir in cornstarch water and green part of the scallion and stir until sauce thickens, about one minute, then serve.<BR><I>Approximate nutrient analysis</I> when serving four people. Each serving has 147 calories, 7 g carbohydrate, 10 g protein, 227 mg sodium, 3 total fat, 1 g saturated fat, and 63 mg cholesterol.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'395A4D436C2EE34484D15B0E94250D85', X'09E69AAA705A6F41858DE45ED2F703A1', X'09E69AAA705A6F41858DE45ED2F703A1', 0, 'javascript:', 'Squid with Mustard Seed and Greens', 2, NULL, 0, 'Squid with Mustard Seed and Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=781', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 dried squid, soaked in glass jar, covered, for twenty-four hours<BR>2 Tablespoons Chinese rice wine<BR>2 Tablespoons cornstarch<BR>1 teaspoon sugar<BR>1 Tablespoon corn oil<BR>1/4 pound pork loin, shredded<BR>1 cup coarsely chopped water chestnuts<BR>1 cup coarsely chopped mustard greens<BR>1 Tablespoon whole mustard seeds<BR>1/2 cup chicken broth<BR>1/2 teaspoon coarse salt<BR><B><I>Preparation:</B></I><BR>1. Remove and remaining any membranes or bony substances from the squid, and cut the flesh into thin slivers.<BR>2. Mix rice wine, cornstarch, and sugar, and set aside.<BR>3. Heat wok or fry pan, add oil, and fry pork pieces for one minute, and remove from wok or pan and add the squid and the rice wine mixture, and fry for one other minute before adding both vegetables, mustard seeds, and the broth. Stir-fry for five to seven minutes, add salt and a few tablespoons water, if too thick, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'132DC3F487B78D44A064A7998DB20049', X'7F3A997D50B1DF46927DF9B92A23CDF8', X'7F3A997D50B1DF46927DF9B92A23CDF8', 0, 'javascript:', 'Squid with Red Bead Squares', 2, NULL, 0, 'Squid with Red Bead Squares', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2284', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound fresh squid, beak, head and cartilage removed, then mixed with two tablespoons thin soy sauce<BR>2 Tablespoons vegetable oil<BR>½ pound ground pork<BR>2 Tablespoons Chinese rice wine<BR>6 slices fresh ginger, peeled and sliced<BR>1 square or one Tablespoons fermented red bean paste, mashed with one Tablespoon cold water<BR>½ cup chicken stock<BR>1 Tablespoon cornstarch mixed with an equal amount of cold water<BR>1 egg white<BR><B><I>Preparation:</B></I><BR>1. Mix squid and soy sauce and set aside for twenty minutes. Then cut th tentacles into rings, cut open and cross-cut the body, then cut it into one-inch squares.<BR>2. Heat wok or fry pan and add the oil and stir-fry the pork until no longer pink, then add the rice wine, ginger, fermented red bean paste, and the water, also the stock, and stir-fry for two more minutes.<BR>3. Now add the squid and the stock and the cornstarch mixture and stir-fry for another minute or until thick; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'507466B5C1D3104CBF13CD40DB059392', X'96B84BB6D4DC0644BAB6A481541AD58B', X'96B84BB6D4DC0644BAB6A481541AD58B', 0, 'javascript:', 'Squid, Fresh and Stir-fried', 2, NULL, 0, 'Squid, Fresh and Stir-fried', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2281', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds fresh squid, heads and cartilage removed and discarded, cut open and cleaned, their main body scored<BR>2 Tablespoons Chinese rice wine<BR>1 Tablespoon mushroom soy sauce<BR>1 Tablespoon cornstarch<BR>4 Chinese black mushrooms, soaked, stems removed and discarded, caps sliced<BR>½ cup chicken broth<BR>2 Tablespoons vegetable oil<BR>3 cloves fresh garlic, peeled and chopped<BR>2 Tablespoons fresh ginger, peeled and minced<BR>3 shallots, peeled and minced<BR>1 boneless pork chop, chopped finely or ground<BR>3 scallions, sliced on an angle<BR>½ cup bamboo shoots, thinly sliced<BR>½ teaspoon coarse salt<BR>1/4 teaspoon ground white pepper<BR>1 Tablespoon Chinese white vinegar<BR><B><I>Preparation:</B></I><BR>1. Cut the squid tentacles into rings ot chop them, cross-hatch the outer side of the squid body.<BR>2. Immerse the squid pieces in boiling water for up to one minute, then drain quickly.<BR>3. Mix rice wine, soy sauce, mushrooms, and the cornstarch, then add the squid.<BR>4. Now heat a wok or fry pan, add the oil, and stir-fry the garlic and the ginger for one minute, then add the shallots and the pork, and stir-fry until the pork is no longer pink, then add the scallions and bamboo shoots, and the salt and pepper, and the chicken broth mixture. Stir for one minute until this thickens; then add the vinegar and the squid, stir, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'08E406BAEEF89446BF4E95A654D87B16', X'6A905692549DD74098557DF233BF8D4E', X'6A905692549DD74098557DF233BF8D4E', 0, 'javascript:', 'Squid, Stir-fried', 2, NULL, 0, 'Squid, Stir-fried', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2084', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds of squid, the bodies cut open, and cleaned<BR>2 Tablespoons Chinese rice wine<BR>1 Tablespoon mushroom soy sauce<BR>1 Tablespoon cornstarch<BR>3 Chinese black mushrooms, stems discarded, their caps sliced<BR>1 cup chicken broth<BR>2 Tablespoon vegetable oil<BR>2 cloves fresh garlic, peeled and minced<BR>2 Tablespoon fresh ginger, peeled and minced<BR>1 boneless pork chop, ground coarsely<BR>3 scallions, sliced on an angle<BR>½ cup bamboo shoots, sliced thinly<BR>½ teaspoon coarse salt<BR>1/4 teaspoon ground white pepper<BR>1 Tablespoon Chinese white vinegar<BR><B><I>Preparation:</B></I><BR>1. Slice tentacles of the squid into rings, the body scored cross-hatched on the top side.<BR>2. Pour boiling water over the squid pieces for only two minutes, then drain well.<BR>3. Mix rice wine, soy sauce, mushrooms, cornstarch, and the squid.<BR>4. Heat wok or fry pan add oil, and stir-fry the garlic and ginger for half a minute, then add the pork and stir-fry for one to two minutes until no longer pink, then add the scallions and bamboo shoots, and the salt and pepper, and the chicken broth mixture and stir for one minute until it thickens, add the vinegar, and the squid, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'089E45201708FB4D86A184EB54F20458', X'C26DB473661BD144B5930F5912527E61', X'C26DB473661BD144B5930F5912527E61', 0, 'javascript:', 'Steamed Carp', 2, NULL, 0, 'Steamed Carp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=599', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 two pound whole carp, scaled and gutted<BR>1/2 cup rice or sweet potato flour<BR>1 cup coriander leaves, half minced fine, the rest coarsely chopped<BR>1 Tablespoon dark soy sauce<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon rice wine<BR>1 teaspoon sugar<BR>1 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Rinse fish in tepid water, then dry it, and coat it generously with the selected flour, then put it on an oiled plate.<BR>2. Steam the fish over rapidly boiling water for fifteen minutes.<BR>3. Bring one-quarter cup water to the boil, add drippings from the fish, both soy sauces, and the wine, salt, and sugar. Bring to the boil then immediately reduce the heat, and add the minced coriander. Simmer for two minutes, then pour this over the fish, scatter the rest if the coriander on it, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8F00DC284E6F754693DD87142FAE1227', X'EA2A64320A914842BE159ECBAF26D1BF', X'EA2A64320A914842BE159ECBAF26D1BF', 0, 'javascript:', 'Steamed Crabs', 2, NULL, 0, 'Steamed Crabs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=145', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>8 Chesapeake Bay crabs<BR>1 can Old Bay Seasoning (made by McCormick &amp; Co.)<BR>1 can beer<BR>6 to 8 ounces fresh ginger <BR>1/4 cup balsamic vinegar<BR>3 cloves fresh garlic, optional<BR>2 Tablespoons thin soy sauce<BR><I><B>Preparation:</I></B><BR>1. Use those rubber-coated cotton gloves to snap off the claws of the crabs, be careful that they do not bite you; the gloves are great protection.<BR>2. Pull the top shell (the back) of the crab off. Discard this and the gills under it. Wash the claws and the crabs to remove all sand and extraneous matter.<BR>3. Place the claws on the bottom of the first deck of the steamer with the shelled crabs on top, one layer at a time. Sprinkle about a teaspoon of Old Bay Seasoning over each crab. Use half of the crabs for the first layer. Repeat the process on the second steamer rack. Next, place enough water into the pot and bring it to a boil; then add a can of beer to the boiling water. Place each tray of crabs over the pot of boiling water, cover and steam. As soon as the crabs turn red from the steaming, steam them for fourteen more minutes. Discard the boiling water, allow the crabs to cool, then serve. They can be served hot, warm, or cold, and with a mild or pungent dipping sauce; which follows, or used in other dishes such as in Crab Meat Egg Foo Yung, below.<BR><I><B>Dipping Sauces:</I></B><BR>1. A simple version is to: Peel and mince two to three tablespoons of fresh ginger. Put this in a small bowl and pour the balsamic vinegar over it.<BR>2. A more savory and pungent version is to: Peel and mince three tablespoons of fresh ginger and three cloves of peeled fresh garlic. Cover these with balsamic vinegar and one or two teaspoons of light soy sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B5F434414557754AA183C3A75F6AEF0E', X'93FE6E11D58B7847B17DE6665985B14C', X'93FE6E11D58B7847B17DE6665985B14C', 0, 'javascript:', 'Steamed Fish Cakes', 2, NULL, 0, 'Steamed Fish Cakes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=999', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound firm white-fleshed fish fillets, finely diced<BR>2 Tablespoons Chinese black mushrooms, soaked, stems discarded, and sliced very thin<BR>2 Tablespoons fresh coriander leaves and stems, minced<BR>1 small carrot, finely slivered, then divided into two batches<BR>6 scallions, finely slivered, then divided into two batches<BR>2 teaspoons cornstarch<BR>2 Tablespoons thin soy sauce<BR>1 Tablespoon fresh ginger, peeled and minced<BR>1 Tablespoon shaoxing wine or dry sherry<BR>2 teaspoons sugar<BR>1 teaspoon oyster sauce<BR>1/4 teaspoon sesame oil<BR>1 medium-size cucumber, peeled, cut in half, and slivered<BR>1/4 cup fresh coriander leaves<BR>1 teaspoon toasted sesame seeds<BR>1 red chili, seeded and slivered<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix fish, mushrooms, minced coriander, half the carrot and scallion slivers, and the corn starch, soy sauce, wine, sugar, oyster sauce, and the quarter teaspoon sesame oil and take one-quarter cup of this mixture and make it into a fish cake. Place on a heat-proof plate. Repeat until all are made. You will need two large plates, one each to be put on a steamer basket/layer.<BR>2. Steam the fish cakes over rapidly boiling water for five or six minutes. If there is not enough steam circulating around these plates, switch the layers top to bottom.<BR>3. While they are steaming, toss cucumber and the remaining carrot and scallion slivers, and put this mixture on a large serving platter.<BR>4. When fish cakes are done, place them on the cucumber mixture, and top with the coriander leaves, sesame seeds, and the chili pepper pieces. Sprinkle the sesame oil, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'047C1E1050F2A5458E6E40AD7E4B5152', X'CCA2F838FC676A4C8439A66D82017A44', X'CCA2F838FC676A4C8439A66D82017A44', 0, 'javascript:', 'Steamed Fish in Sweet Potato Flour', 2, NULL, 0, 'Steamed Fish in Sweet Potato Flour', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=299', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound fish filets, cut into four pieces per filet<BR>1 teaspoon hot sauce<BR>1 teaspoon light soy sauce<BR>1 teaspoon Fukien Low Chiew wine<BR>1/2 teaspoon sugar<BR>1/2 teaspoon sesame oil<BR>1/2 cup sweet potato or yam flour<BR>1/4 cup pickled mustard greens, minced fine<BR><I><B>Preparation:</I></B><BR>1. Dry fish. Mix hot and soy sauces, wine, sugar, and oil and marinate the fish for an hour in the refrigerator.<BR>2. Remove fish from the marinade and coat with sweet potato or yam flour and set aside on a one-inch heat-proof plate (a glass pie pan works well) at room temperature for half an hour, not longer.<BR>3. Top with mustard greens and steam over high heat for eight to ten minutes, depending upon the thickness of the fish (i.e.: flounder needs less time, sea bass needs more). Remove and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'62C2466375408443ACA97A9738C324E8', X'5663D84F596C9344AB74C394B9AFB61D', X'5663D84F596C9344AB74C394B9AFB61D', 0, 'javascript:', 'Steamed Fish with Ginger and Scallions', 2, NULL, 0, 'Steamed Fish with Ginger and Scallions', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=858', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 to 2 pounds of whole fish or fish fillets<BR>1/2 teaspoon kosher salt<BR>2 Tablespoons rice wine or sherry<BR>1 Tablespoon Asian sesame oil<BR>2 tablespoons vegetable oil<BR>1/4 cup fresh ginger, finely shredded<BR>1/4 cup scallions, finely shredded<BR>3 Tablespoons soy sauce<BR>1/2 teaspoon sugar<BR>2 Tablespoons white vinegar or rice wine vinegar<BR>Chopped cilantro for garnish<BR><B><I>Preparation:</B></I><BR>1. Rub fish with wine and salt. Let it rest for fifteen minutes.<BR>2. Put fish on a heat-proof serving plate and on to a steamer rack over boiling water. Steam it between seven and twelve minutes until done, depending on its size.<BR>3. While fish is steaming, heat sesame and vegetable oils in a small pot until they are very hot. Add the ginger and stir-fry for fifteen seconds before adding the scallions. Stir fry another five seconds, turn off the heat (or if on electric burner, remove from the heat source.<BR>4. Add the soy sauce, vinegar and sugar and stir until the sugar dissolves. Keep hot until fish is ready, then sprinkle the fish with the sauce mixture and the cilantro.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9DE5112B0B19B94EB99417411EB85253', X'968CBCB267BCEC43B1EFEE69EFB92BC6', X'968CBCB267BCEC43B1EFEE69EFB92BC6', 0, 'javascript:', 'Steamed Fish with Pickled Lemon', 2, NULL, 0, 'Steamed Fish with Pickled Lemon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=368', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 whole pickled lemons (see instruction #1 below)<BR>1 cup coarse non-iodized salt<BR>1 Tablespoon sugar<BR>2 Tablespoons rice wine<BR>1 whole one and a half pound fish; pompano or lemon sole are recommended<BR>4 slices fresh ginger, peeled and cut in thin strips<BR>4 large scallions, one angle-cut into one-inch pieces<BR>6 black mushrooms, soaked twenty minutes, stem removed, then sliced<BR>3 tablespoons thin soy sauce<BR>1/4 cup coriander<BR>1/4 cup corn oil<BR>salt and pepper, to taste<BR><B><I>Preparation:</B></I><BR>1. To make the pickled lemons, fill a glass jar with the two whole lemons and cover them with salt. Close and set aside for a week, then remove lemons, rinse them thoroughly, when ready to use, slice them and mix with sugar and rice wine and marinate them for ten minutes.<BR>2. Put the three whole scallions on a plate and put the fish on top of them. Put mushroom and lemon slices on the fish along with the marinating sauce.<BR>3. Steam this over boiling water for fifteen minutes. Then remove the fish to a warm serving platter and mix the reserved juices with the soy sauce and pour over the fish.<BR>4. Heat the oil and fry the garlic just until it is fragrant then pour this over the fish, as well. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'47F8DFAF3A49F340AF4F6359E8E937F9', X'89B4B6D0268885499087C1FED8DB6CD3', X'89B4B6D0268885499087C1FED8DB6CD3', 0, 'javascript:', 'Steamed Grouper in Winter Melon Box', 2, NULL, 0, 'Steamed Grouper in Winter Melon Box', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1159', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds winter melon<BR>1/2 pound boneless and skinless grouper flesh, diced<BR>1 small carrot, finely diced<BR>5 Tablespoons cooked shitake mushrooms, finely minced<BR>1 scant teaspoon salt<BR>1 and 1/2 teaspoons chicken stock powder<BR>1/2 teaspoon vegetable oil<BR>1/2 cup chicken stock<BR>1 and 1/2 teaspoons cornstarch mixed with one and a half teaspoons of cold water<BR><B><I>Preparation:</B></I><BR>1. Peel the winter melon and cut into one-to two inch thick slices. Cut these into leaf or diamond shapes and cut one-quarter-inch slice off each of them. Then scoop out flesh of lower part with a melon ball scoop.<BR>2. Blanch tops and bottoms a few minutes or until eighty percent cooked, Drain and set aside.<BR>3. Mix diced fish, carrot, mushrooms, salt, and chicken stock powder. Fill each cooled melon box. Set their covers sideways next to them. Place on an oiled plate and steam for fifteen minutes. Pour liquid into a small pot.<BR>4. Carefully put the cooked boxes on a serving dish, Add the chicken stock and bring to the boil. Add cornstarch mixture, and when thickened pour over the vegetable boxes, cover them slightly ajar with the cut off tops. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'98B8903223763A4BA56BD7472B186888', X'C2BD416277310D4FA8C944522CABEBF2', X'C2BD416277310D4FA8C944522CABEBF2', 0, 'javascript:', 'Steamed Hairy Crabs', 2, NULL, 0, 'Steamed Hairy Crabs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1110', '<TD><RECIPE><B><I>Ingredients:</B></I> <BR>4 hairy crabs, leave tied as purchased<BR>2 dried perilla leaves, soaked in warm water for ten minutes (optional)<BR>1 Tablespoon coarsely chopped ginger<BR>1/4 cup shaoxing rice vinegar<BR><B><I>Preparation:</B></I><BR>1. With abdomen facing up, put crabs then the soaked perilla leaves on top and steam over boiling water, for fifteen minutes if small or medium size, twenty minutes if large.<BR>2. Remove crabs from steamer rack or basket, cut away the strings that tied the crab.<BR>2. Mix ginger with Shaoxing vinegar and put into a small dish. Set this aside for dipping the crab meat.<BR>3. Cut the crabs into several pieces with a strong scissor, and serve them or have scissors for those eating them to do that task. Set on the table with the dipping sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E11C0AD96E395548A45FEEB3FA8F98FE', X'B8AFA4E938FCF048B9C8D820160537AD', X'B8AFA4E938FCF048B9C8D820160537AD', 0, 'javascript:', 'Steamed Scallops in Spiced Butter Sauce', 2, NULL, 0, 'Steamed Scallops in Spiced Butter Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=304', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound sea scallops (with coral, if available)<BR>2 fresh hot chilies, seeded and chopped<BR>1 Tablespoon orange zest<BR>2 teaspoons finely chopped ginger<BR>1 Tablespoon Shaoxing wine or dry sherry<BR>1 teaspoon five spice powder<BR>1/2 teaspoon toasted and ground Sichuan peppercorns (fagara)<BR>1 teaspoon salt<BR>2 ounces chicken stock<BR>2 Tablespoons unsalted butter, cut in small pieces<BR>1 Tablespoon finely chopped chives, as garnish<BR><I><B>Preparation:</I></B><BR>1. Put scallops on a heatproof dish and sprinkle evenly with the chilies, zest, ginger, wine, five spice powder, Sichuan peppercorns, and salt.<BR>2. Bring two inches of water to boil in the bottom of a steamer, put the dish with the scallops on a rack over the water, reduce the heat, cover tightly, and steam for five minutes.<BR>3. Remove dish carefully from the steamer and put the liquid into a pot, add the stock and reduce the liquid by three quarters.<BR>4. Mix in the butter and put the scallops in and warm them; do not raise the temperature. Remove them and garnish with the chives.<BR>Serves 4.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CDA367615979E84181FC053C329321A5', X'BA0E6F6CD001B448A9A600724E0FA198', X'BA0E6F6CD001B448A9A600724E0FA198', 0, 'javascript:', 'Steamed Scallops with Black Bean Sauce', 2, NULL, 0, 'Steamed Scallops with Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=227', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>12 fresh scallops, loosened but left in their shells<BR>1 and 1/2 Tablespoons salted black beans<BR>2 Tablespoons oil<BR>1 teaspoon finely chopped garlic<BR>1 Tablespoon finely chopped ginger<BR>1 Tablespoon finely chopped scallions<BR>1 Tablespoon finely chopped red chilies<BR>1/2 teaspoon finely chopped Sichuan peppercorns<BR>2 Tablespoons light or dark soy sauce<BR>1 Tablespoon rice wine<BR>2 Tablespoons stock<BR>1/2 teaspoon sesame oil<BR><I><B>Preparation:</I></B><BR>1. Scrub the scallops under cold running water. Discard the flat empty half of each shell.<BR>2. Soak the black beans in warm water for five minutes, then drain and mash.<BR>3. Place the scallops in their shells on two separate heat-proof dishes. Place these on the racks of two layers of hot bamboo steamers and steam for eight or nine minutes.<BR>4. While they steam, make sauce by heating the oil over low heat. Add the garlic, ginger, scallions, chilies, Sichuan peppers, and black beans and stir-fry about half a minute until fragrant. <BR>5. Add the soy sauce, wine, stock and continue stirring another twenty seconds until the sauce starts to bubble.<BR>6. Sprinkle on the sesame oil and simmer gently for another minute.<BR>7. Serve with two teaspoons of sauce over each scallop.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FADDB4AF5A038648BADAC7FA88B0436C', X'51F7FD80958DFF40B3170BF1181F5D7D', X'51F7FD80958DFF40B3170BF1181F5D7D', 0, 'javascript:', 'Steamed Sea Bass, Bruce Ho Style', 2, NULL, 0, 'Steamed Sea Bass, Bruce Ho Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=487', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 sea bass close to two pounds, cleaned and its gills removed<BR>1 Tablespoon shredded fresh ginger<BR>1 Tablespoon dry sherry or rice wine<BR>1 teaspoon sesame oil<BR>1/4 cup thin soy sauce<BR>1 teaspoon sugar<BR>2 scallions, minced<BR><B><I>Preparation:</B></I><BR>1. Place fish on a rack in a steamer over boiling water. Steam it for fifteen minutes to half an hour, depending upon its weight.<BR>2. Remove fish to a pre-heated oven proof dish.<BR>3. Heat the remaining ingredients in a small pan and pout this over the fish and place it in a 400 degree F oven and cook it ten more minutes, basting it several times, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DA4A2CC75662224BA92A73EBB631464D', X'25CCE05D5C4BBE4A95797B63D3392785', X'25CCE05D5C4BBE4A95797B63D3392785', 0, 'javascript:', 'Steamed Shad I', 2, NULL, 0, 'Steamed Shad I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=476', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 shad, about one pound<BR>3 small pieces cooked ham, about one-inch square by one-eighth-inch thick<BR>3 slices bamboo shoots, about tha same size as the ham<BR>2 black mushrooms, soaked in warm water for twenty minutes<BR>1 scallion, cut into one-inch pieces<BR>1 Tablespoon thin slices of fresh ginger<BR>2 Tablespoons chicken stock<BR>1/2 Tablespoon rice wine<BR>1 Tablespoon coarse salt<BR>1/4 cup sugar<BR>1 piece pork caul fat, optional, but desired<BR>1 Tablespoon ginger, minced very fine<BR>1 Tablespoons rice vinegar<BR><B><I>Preparation:</B></I><BR>1. Split the shad and gut, do not remove the scales. Then rinse and dry. Drop into boiling water and remove in thrity seconds. Pat dry and put on a platter.<BR>2. Put ham, bamboo shoot pieces, mushrooms, scallion pieces, and ginger slices on the fish.<BR>3. Mix stock, wine, salt, and sugar and pour over the fish, cover with caul fat, and steam over boiling water for fifteen minutes.<BR>4. Mix minced ginger and vinegar and use as dipping sauce for the fish.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4D6C2549D2FD02438C32266FD9DD2B61', X'58B266614E6BFF4FBCA38DBCC082432D', X'58B266614E6BFF4FBCA38DBCC082432D', 0, 'javascript:', 'Steamed Shad II', 2, NULL, 0, 'Steamed Shad II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1440', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 shad, about one pound<BR>3 small pieces cooked ham, about one-inch by one-eighth-inch<BR>3 slices bamboo shoots, about tha same size as the ham<BR>2 black mushrooms, soaked in warm water for twenty minutes<BR>1 scallion, cut into one-inch pieces<BR>1 Tablespoon thin slices of fresh ginger<BR>2 Tablespoons chicken stock<BR>1/2 Tablespoon rice wine<BR>1 Tablespoon coarse salt<BR>1/4 cup sugar<BR>1 piece pork caul fat, optional--but desired<BR>1 Tablespoon ginger, minced very fine<BR>1 Tablespoons rice vinegar<BR><B><I>Preparation:</B></I><BR>1. Split the shad and gut, do not remove the scales. Then rinse and dry. Drop into boiling water and remove in thrity seconds. Pat dry and put on a platter. 2. Put ham, bamboo shoot pieces, mushrooms, scallion pieces, and ginger slices on the fish.<BR>3. Mix stock, wine, salt, and sugar and pour over the fish, cover with caul fat, and steam over boiling water for fifteen minutes.<BR>4. Mix minced ginger and vinegar and use as dipping sauce for the fish.<BR><I>Approximate nutrient analysis</I> per person, when serving four people, assuming all the liquid is consumed equally. Each serving has 388 calories,63 g carbohydrate, 21 g protein, 1325 mg sodium, 16 g total fat, 4 g saturated fat, and 90 mg cholesterol.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2EBD37724C807942AB05B6C079C3616F', X'DF2DC35A1CD5C840A893EA537761B481', X'DF2DC35A1CD5C840A893EA537761B481', 0, 'javascript:', 'Steamed Shark''s Fin Bundles', 2, NULL, 0, 'Steamed Shark''s Fin Bundles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=954', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound dried shark''s fin<BR>1 Tablespoon Chinese rice wine<BR>2 slices fresh ginger, minced and mixed with two tablespoons of hot water<BR>1 Tablespoon chicken bouillon powder<BR>2 scallions, blanched for one minute<BR>1 ounce Yunnan ham, cut into eight sticks<BR>1 cup superior stock<BR>8 pieces bamboo fungus, soaked in hot water for five minutes<BR>1 cup slivered zucchini with peel left on, steamed for one minute<BR>1 Tablespoon cornstarch mixed with one tablespoon of cold water<BR>dash ground white pepper<BR><B><I>Preparation:</B></I><BR>1. In a bowl, steam over boiling water, the shark''s fin, wine, fresh ginger water, bouillon, and one cup of water for forty minutes over boiling water. Remove, drain, and cool, then separate into needles, and set aside.<BR>2. Cut scallions into eight pieces of green.<BR>3. Divide sharks fin needles into eight batches, put one ham strip in each and wrap each batch in the center with a scallion green.<BR>4. Bring superior stock to the boil, and add bamboo fungus and simmer for two minutes. Drain and set aside. Reserve stock in the pot.<BR>5. Add zucchini to the stock, and bring to the boil, then drain it. Add the cornstarch water to the pot, and stir until thickened.<BR>6. Plate the zucchini in the center of a round plate. Alternate bamboo fungus and shark''s fin bundles around the outside of this; and sprinkle the ground pepper all over it. Then pour the thickened stock over the entire plate, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6DE2E92898FEA44F803618678DC260C1', X'C08F6B992F81EB41900A95300128DBC1', X'C08F6B992F81EB41900A95300128DBC1', 0, 'javascript:', 'Steamed Squid with Silk Squash', 2, NULL, 0, 'Steamed Squid with Silk Squash', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1199', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound fresh squid, cleaned, cut in half, cross-hatched, cut into half-inch pieces, and oil or water blanched for half minute<BR>1 teaspoon cornstarch<BR>1/2 teaspoon ground white pepper<BR>1 silk squash, peeled, cut in half the long way, then sliced<BR>4 cloves garlic, peeled and chopped coarsely<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon sesame oil<BR>1 Tablespoon fried shallots<BR><B><I>Preparation:</B></I><BR>1. Mix squid with cornstarch and ground white pepper.<BR>2. Put silk squash on a heat proof wide bowl, and top with the squid. Sprinkle garlic, soy sauce, and sesame oil over them, and steam over rapidly boiling water for five or six minutes, Sprinkle the fried shallots of top, and serve<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'49D6ACDA40F2A14F90C4E000990F8DB9', X'6BB7A71673822F4F9FA813C3D86D2042', X'6BB7A71673822F4F9FA813C3D86D2042', 0, 'javascript:', 'Stewed Abalone and Greens I', 2, NULL, 0, 'Stewed Abalone and Greens I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2013', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 pound dried abalone<BR>1 cup chicken stock<BR>1/2 dried tangerine peel, soaked then slivered<BR>2 slices fresh ginger, smashed and then slivered<BR>2 Tablespoons Chinese rice wine<BR>6 large lettuce leaves or <I>bok cai</I> leaves, sliced<BR>1 teaspoon sesame oil<BR>salt and pepper, to taste<BR><I><B>Preparation:</B></I><BR>1. Soak the dried abalone overnight, then drain and put it in a large clay pot, cover with water, and boil for twenty minutes. Then turn off the heat source, cover the pot, and let it sit over night.<BR>2. In the morning, take the abalone out of the clay pot, rinse, and if the water is sandy, strain it through a very fine wire mesh strainer, slice it, then return it to the pot.<BR>3. Add the stock, peel, ginger, and the wine, and boil until the sauce thickens. Then add the greens, stir for one minute, ten remove them to a platter. Put the abalone slices on top, and the reduced sauce on top of that, Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FC6E11E0A4505740850AD5D56B2115AC', X'F6BD9AF6A31DAC4D8EE8C2A68C0D29C0', X'F6BD9AF6A31DAC4D8EE8C2A68C0D29C0', 0, 'javascript:', 'Stewed Abalone and Greens II', 2, NULL, 0, 'Stewed Abalone and Greens II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1964', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ pound dried abalone<BR>1 cup chicken stock<BR>½ piece dried tangerine peel, slivered<BR>2 slices fresh ginger, smashed then slivered<BR>2 Tablespoons Chinese rice wine<BR>6 large lettuce or <I>bok cai</I> leaves sliced<BR>1 teaspoon sesame oil<BR>salt and pepper. to taste<BR><B><I>Preparation:</B></I><BR>1. Soak the dried abalone over night, then drain it and put it into a large clay pot, cover with water, and boil it for twenty minutes, turn off the heat source, cover the clay pot and let it sit overnight.<BR>2. In the morning, take the abalone out of the clay pot, rinse it, and if the water is sandy, strain it through a very fine strainer, then slice it and return it to the pot.<BR>3. Add the stock, peel, ginger, and the wine and boil until the sauce thickens, then the add the greens, stir for one minute, then remove them to a platter. Put the abalone slices on top, and the reduced sauce on top of that. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F4AB102AFBF6614684EEEC4042DE86FB', X'9882571398A6CA46AAEA5BF810AB5B21', X'9882571398A6CA46AAEA5BF810AB5B21', 0, 'javascript:', 'Stewed Bean Curd with Dried Fish', 2, NULL, 0, 'Stewed Bean Curd with Dried Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=409', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound silken bean curd, cut into ten pieces<BR>1 Tablespoon Xhaoxing rice wine<BR>1 teaspoon sesame oil<BR>1 cup corn oil<BR>1 egg, beaten well<BR>4 Tablespoons water chestnut or arrowroot flour<BR>2 slices fresh ginger<BR>2 shallots, thin sliced<BR>2 cloves garlic, thin sliced<BR>1/2 square fermented red <I>fuyu</I>, mashed<BR>1/2 square fermented white <I>fuyu</I> with chili<BR>1 Tablespoon <I>fuyu</I> liquid<BR>1/4 cup chicken stock<BR>1/4 teaspoon salt<BR>1/8 teaspoon ground white pepper<BR>1 teaspoon thin soy sauce<BR>1 Tablespoon mushroom soy<BR>1 teaspoon sesame oil<BR>1 Tablespoon dried fish or shrimp, soaked in warm water for half an hour<BR>1 Tablespoon cornstarch mixed with two tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Mix silken tofu pieces, Xhaoxing rice wine, and the sesame oil and set aside for half an hour.<BR>2. Heat oil in a wok. Dip pieces of marinated silken tofu in egg and deep fry them, a few at a time, until light golden; then drain them on paper towels. Remove all but one tablespoon of the oil from the wok and discard or reserve the rest for another use.<BR>3. Heat remaining tablespoon of the oil and fry ginger, shallots, and garlic for one minute. Then add both mashed <I>fuyu</I> and the <I>fuyu</I>liquid, the chicken stock, salt and pepper, both soy sauces and the sesame oil, and the drained fish or shrimp and bring this mixture to the boil. Lower the heat and simmer for three minutes.<BR>4. Add cornstarch-water mixture and boil until thickened, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C82D3B8700EED54BBCAC7FF58454AC8A', X'33D4429DB578ED4B895127CF0FBE21F7', X'33D4429DB578ED4B895127CF0FBE21F7', 0, 'javascript:', 'Stewed Eel Shanghai Style', 2, NULL, 0, 'Stewed Eel Shanghai Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=192', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound eel, or more<BR>1 teaspoon coarse salt or one-half teaspoon table salt<BR>6 Tablespoons soy sauce, half light and half dark<BR>2 Tablespoons rice wine<BR>2 cups corn oil<BR>3 ounces pork<BR>6 cloves garlic<BR>6 pieces tangerine peel or one teaspoon tangerine or orange juice<BR>6 scallions<BR>6 shiitake mushrooms, soaked twenty minutes, then drained<BR>1 Tablespoon sugar<BR>dash ground white pepper<BR>3 cups chicken stock<BR>2 Tablespoons cornstarch in like amount of cold water<BR><I><B>Preparation:</I></B><BR>1. Rub eel with salt inside and out, then rinse in cold water and blanch in boiling water for one minute, then drain.<BR>2. Mix eel with soy sauces and wine and set aside in the refrigerator for half an hour, then drain and reserve marinade.<BR>3. Heat oil, remove eel from refrigerator and deep fry the drained eel for one minute, drain and set on paper towels to drain, and remove oil from pan and set aside.<BR>4. Heat two tablespoons of the reserved oil and fry the pork for one minute, then add garlic, scallions, tangerine peel, and mushrooms and fry for another minute, remove from the pan.<BR>5. In stove-top casserole or heavy pan, put in eel, reserved marinade, vegetable ingredients with what they were cooked in, the sugar, pepper, and stock and bring to the boil then simmer for half an hour (more if eel is not yet tender). Remove the eel to a serving dish.<BR>6. Bring remaining liquid back to the boil, then add cornstarch and water mixture and thicken, pour over the eel and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0D48FF7744F3784FB5BBD9A79E828C8B', X'EDCD800225ED9049B8CB729DFB497146', X'EDCD800225ED9049B8CB729DFB497146', 0, 'javascript:', 'Stewed Eel with Garlic', 2, NULL, 0, 'Stewed Eel with Garlic', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2033', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound whole fresh eels boiled for one hour, then skinned, drained, boned, and dried with paper towels, then cut into two-inch thin pieces<BR>1 Tablespoon Chinese rice wine or liquor<BR>2 and 2 Tablespoons thin soy sauce, divided<BR>2 Tablespoons vegetable oil<BR>1 teaspoon ground white pepper<BR>1 teaspoon ginger juice<BR>4 large Chinese black mushrooms, soaked until soft, stems discarded, and sliced thinly<BR>2 slices fresh ginger<BR>6 large cloves garlic, peeled and sliced<BR>1 Tablespoon oyster sauce<BR>2 pieces rock sugar<BR>1 cup chicken stock<BR>dash of ground pepper<BR>2 Tablespoons cornstarch mixed with half to one cup cold water<BR><B><I>Preparation:</B></I><BR>1. Marinate boiled eel in the rice wine or liquor for ten minutes, then add half the soy sauce and let it rest for another ten minutes, then drain well.<BR>2. Heat a wok, add the oil, the eel, the rest of the soy sauce, and the pepper, ginger juice, mushroom slices, ginger, garlic. Oyster sauce, sugar, stock, and the ground pepper, and cover and simmer for two minutes, then bring it to the boil and cook for another three minutes. Thicken with cornstarch mixture. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'25B2DDA8DCD462478F0174D21DC42204', X'65A174F68DAF2149B2AF9526F899554B', X'65A174F68DAF2149B2AF9526F899554B', 0, 'javascript:', 'Stewed Eel, Mushrooms, and Pork', 2, NULL, 0, 'Stewed Eel, Mushrooms, and Pork', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=458', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound eel, skinned, boned, and gutted<BR>6 dry shiitake mushrooms, soaked for half hour, stems removed and quartered<BR>1/4 pound fresh bacon, cut into quarter-inch pieces<BR>1 cup corn oil<BR>6 cloves garlic<BR>4 Tablespoons soaked tangerine pee, slivered<BR>4 scallions, cut into one-inch pieces<BR>2 slices fresh ginger, slivered<BR>3 cups chicken broth<BR>1/4 teaspoon ground white pepper<BR>½ teaspoon salt<BR>2 Tablespoons dark soy sauce<BR>2 Tablespoons Chinese rice wine<BR>2 Tablespoons cornstarch mixed with two tablespoons cold water<BR>2 Tablespoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix eel, mushrooms, and bacon and blanch them in boiling water for one minute, draining them well. Then blanch again for another minute but this time in preheated corn oil. Drain well, and set oil aside, most for another use.<BR>2. Take one teaspoon of the oil and fry garlic, tangerine peel, scallions, and ginger for half a minute then add broth, pepper and salt, soy sauce, and wine and bring to the boil. Add eel and reduce heat and simmer for twenty minutes. 3. Increase heat and add cornstarch mixture and boil until the sauce clears. Put this mixture into a preheated casserole, top with pre-heated sesame oil, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FD929DD1D15CE94B96B8CBBC5E84B398', X'0F8F623D660CF844BDC7AE9F5C43166B', X'0F8F623D660CF844BDC7AE9F5C43166B', 0, 'javascript:', 'Stewed Fish Lips', 2, NULL, 0, 'Stewed Fish Lips', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=474', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound dried shark skin, soaked overnight, covered and refrigerated<BR>3 Tablespoons corn oil<BR>4 slices ginger<BR>2 scallions, each tied into a knot<BR>24 small black forest mushrooms, soaked for half hour in one cup warm water, remove stems and reserve mushroom water<BR>1 Tablespoon Chinese rice wine<BR>4 sprigs fresh coriander, tied in a knot<BR>1 teaspoon sugar<BR>1 cup shredded Chinese greens<BR>6 ounces whole bamboo shoots, cut into thick slices<BR>2 cups chicken broth, reduced to one cup<BR>1 teaspoon mushroom soy sauce<BR>rice or noodles<BR><B><I>Preparation:</B></I><BR>1. Rinse soaked shark skin with fresh cold water and then squeeze out as much water as you can, being careful not to tear the skin. Then cut into pieces about two inches per side. Heat oil and stir shark skin pieces for one minute, drain and set aside, reserving the oil.<BR>2. Heat remaining oil and fry ginger and scallions for one minute. Add mushrooms and fry another two minutes, then add shark skin and stir-fry for another three minutes before adding rice wine, coriander, and sugar. Simmer for five minutes on very low heat.<BR>3. Put shredded green on the bottom of a heat-proof casserole that can go to the table. Put half the bamboo shoots on them then the mushroom mixture followed by the rest of the bamboo shoots. Pour broth and soy sauce over this, stir gently once and bring to the boil, then cover and simmer for twenty minutes. Serve with or over rice or noodles.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D96DA3901CF66247B271DC9BB8606C8F', X'4CE0451FEC346F4E83A9BEE946513606', X'4CE0451FEC346F4E83A9BEE946513606', 0, 'javascript:', 'Stewed Potato, Conpoy, and Preserved Duck Egg', 2, NULL, 0, 'Stewed Potato, Conpoy, and Preserved Duck Egg', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=866', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 dried conpoy (dried scallops), soaked for one hour in a cup of warm water<BR>1 Tablespoon vegetable oil<BR>1 potato, diced<BR>1 tomato, diced<BR>1 preserved duck egg, quartered<BR>1 scallion, cut into one-inch pieces<BR><B><I>Preparation:</B></I><BR>1. Remove scallops and reserve the liquid, and separate the scallop into its own shredded pieces.<BR>2. Heat wok, add oil and fry the scallops for one minute or until fragrant. Then add potato and tomato pieces and the reserved water from soaking the scallops. Simmer for one hour.<BR>3. Add scallops and duck egg pieces and simmer another ten to fifteen minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'68BC74B28CFCBD49A134F1E7178E3B7E', X'48840560ADF5D9438E124CE74AD60FD5', X'48840560ADF5D9438E124CE74AD60FD5', 0, 'javascript:', 'Stewed Seabass with Wolfberries', 2, NULL, 0, 'Stewed Seabass with Wolfberries', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=785', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 two-pound sea bass<BR>3 slices fresh ginger, slivered<BR>3 Tablespoons wolfberries<BR>5 sprigs fresh coriander, minced coarsely<BR>1/2 teaspoon salt<BR>1 Tablespoon Chinese rice wine<BR>4 teaspoons cornstarch or water chestnut flour<BR><B><I>Preparation:</B></I><BR>1. Scale fish, remove viscera, rinse, and dry. Make two cuts on each side through the skin, but not into the flesh.<BR>2. Rub ginger, wolfberries, coriander, and salt into the cavity of the fish. 3. Bring two cups water to the boil in a long narrow fish pot. Simmer the fish, covered, for twenty minutes, remove carefully to a platter, reserving liquid in the fish pot.<BR>4. Mix cornstarch with one tablespoon of cold water, add to the remaining liquid and cook until thickened. Then pour over the fish and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5C26BCD48519F244884FA88ED2C20F67', X'4A0A54539679E245BC59D6381375E6E5', X'4A0A54539679E245BC59D6381375E6E5', 0, 'javascript:', 'Stir-fried Crispy Scallops', 2, NULL, 0, 'Stir-fried Crispy Scallops', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=24', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 pounds fresh scallops<BR>1 beaten egg white<BR>5 teaspoons cornstarch<BR>1 teaspoon chopped fresh green onions<BR>1 teaspoon chopped fresh garlic<BR>1 teaspoon fresh ginger<BR>dash of dry crushed Szechuan peppercorns<BR>4 teaspoon soy sauce<BR>2 teaspoons sugar<BR>1 teaspoon vinegar<BR><I><B>Preparation:</I></B><BR>1. Rinse scallops and dip in beaten egg white and roll in cornstarch.<BR>2. Deep fry the scallops just until crispy; remove them from fat and drain.<BR>3. Reheat wok with about one tablespoon of oil; stir fry onion, garlic, and ginger.<BR>4. Add peppercorn, soy sauce, sugar, and vinegar until fragrant.<BR>5. Add scallops and stir quickly just to mix; and serve immediately.<BR><B>Note:</B> This dish looks great on a bed of steamed broccoli.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ACB4EC6FC9462540908DBB6EBD1F7C42', X'7FDBD799883E1A4E9AEB38F4A329505D', X'7FDBD799883E1A4E9AEB38F4A329505D', 0, 'javascript:', 'Stir-fried Cuttlefish', 2, NULL, 0, 'Stir-fried Cuttlefish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2207', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cuttlefish, tentacles removed and cut into one-inch pieces, body cross-cut, then blanched in boiling water for two minutes, and put in ice water for two minutes, then drained<BR>1 four-inch piece bamboo shoot, peeled then sliced, and boiled for two minutes<BR>1 carrot, angle-sliced and blanched for two minutes, then put in ice water, and in two minutes, drained<BR>12 sugar-snap peas, strings removed, each angle cut in half<BR>1 clove garlic, peeled and minced<BR>1 scallion, angle cut and set aside<BR>3 Tablespoons chicken stock<BR>1 Tablespoon fish sauce<BR>½ teaspoon salt and black pepper mixture<BR>1 teaspoon sesame oil<BR>1 teaspoon black vinegar<BR><B><I>Preparation:</B></I><BR>1. Prepare all ingredients except the oil, as indicated above.<BR>2. Mix the garlic and scallion and set aside, and mix the bamboo shoot slices, carrot pieces, and the scallions and set them aside. Then mix the stock, fish sauce, salt and pepper mixture, and the sesame oil and black vinegar and set them aside, as well.<BR>3. Heat a wok or fry pan, add the oil, then the cuttlefish pieces, and stir-fry for one minute, then add the vegetables and stir-fry for two minute, then toss in the oil and vinegar, stir well, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6BA7003F81168C49BCF992B149823058', X'2452BC5C74AE0341AEF32D61FDBCC136', X'2452BC5C74AE0341AEF32D61FDBCC136', 0, 'javascript:', 'Stir-fried Eel with Peppers', 2, NULL, 0, 'Stir-fried Eel with Peppers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=144', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound eel, skinned and boned, and cut in one-inch sections<BR>1/2 teaspoon coarse salt<BR>1/8 teaspoon ground white pepper<BR>1 teaspoons dry sherry<BR>3 Tablespoons corn oil<BR>2 cloves garlic, minced<BR>2 large green peppers<BR><I><B>Preparation:</I></B><BR>1. Dry the eel with paper towels, then season it with salt, white pepper, and sherry.<BR>2. Heat wok or deep fry pan, then add oil and garlic and stir-fry for half a minute. Next add the eel and continue stir-frying for three minutes.<BR>3. Add peppers and fry one minute more. Serve on a pre-heated plate.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'20A99D0590FD2542BF09F2EB4C7F96B4', X'1FCA0F854FC32249A9F02B9B74A00166', X'1FCA0F854FC32249A9F02B9B74A00166', 0, 'javascript:', 'Stir-fried Fish with Black Bean Sauce', 2, NULL, 0, 'Stir-fried Fish with Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1665', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound fresh firm white fish filets such as cod, halibut, or sea bass<BR>1 teaspoon coarse salt<BR>3 Tablespoons peanut oil<BR>1 and 1/2 Tablespoons coarsely chopped fermented black beans<BR>1 and 1/2 Tablespoons minced garlic<BR>2 teaspoons finely chopped fresh ginger<BR>6 Tablespoons finely chopped scallions, divided in two parts<BR>1 Tablespoon thin soy sauce<BR>1 teaspoon dark soy sauce<BR>1 Tablespoon Shaoxing rice wine or dry sherry<BR>1 teaspoon sugar<BR>2 teaspoons sesame oil<BR><B><I>Preparation:</B></I><BR>1.Cut the fish into one-inch strips, sprinkle with salt, and let it rest for twenty minutes.<BR>2. Heat wok or fry pan, add oil and when is smokes reduce heat and add the fish strips. Gently stir-fry for two minutes until browned on both sides; then remove them to paper towels. Drain all but half of the oil and discard it.<BR>3. Reheat the wok, add the black beans, garlic, ginger, and half the scallions. Stir-fry half a minute before adding both soy sauces, rice wine, sugar, and one tablespoon water. Reduce the heat to a simmer, return fish to the wok or fry pan and cook for one more minute; then add sesame oil and stir gently. Remove all to a pre-heated platter, garnish with the rest of the scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FE179735049C8C44B71DDB752CB870DF', X'9E58F13C7C958E4FB4CFDCD2D8E946CB', X'9E58F13C7C958E4FB4CFDCD2D8E946CB', 0, 'javascript:', 'Stir-fried Frozen Squid I', 2, NULL, 0, 'Stir-fried Frozen Squid I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=711', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound frozen squid, defrosted for an hour in tepid water.<BR>1 cup oil<BR>3 Tablespoons fresh ginger, each slice cut in four<BR>2 scallions, cut in half-inch lengths<BR>1 chili pepper, seeded and cut in six pieces<BR>2 Tablespoons mushroom soy<BR>2 Tablespoons Chinese rice wine<BR><B><I>Preparation:</I></B><BR>1. Remove head and internal organs from each squid, then cut off wings and tail section and cut each squid into two-inch pieces and score each one in a cross-hatched manner on the under side.<BR>2. Heat oil and deep fry all pieces for one minute, drain on paper towels, and remove all but one tablespoon of the oil.<BR>3. Reheat the leftover oil and stir-fry ginger, scallion, and chili pepper pieces for half a minute, then add squid, soy sauce and wine, and stir one minute, then serve over rice or noodles, if desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'81CEE198549EBA4395FE9439376E136D', X'D0790917749122429395EE55BF09EDE1', X'D0790917749122429395EE55BF09EDE1', 0, 'javascript:', 'Stir-fried Frozen Squid II', 2, NULL, 0, 'Stir-fried Frozen Squid II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1448', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound frozen squid, defrosted for an hour in tepid water<BR>1 cup oil<BR>3 Tablespoons fresh ginger, each slice cut in four<BR>2 scallions, cut in half-inch lengths<BR>1 chili pepper, seeded and cut in six pieces<BR>2 Tablespoons mushroom soy sauce<BR>2 Tablespoons Chinese rice wine<BR><B><I>Preparation:</B></I><BR>1. Remove head and internal organs from each squid, then cut off wings and tail section and cut each squid into two-inch pieces and score each one in a cross-hatched manner on the under side.<BR>2. Heat oil and deep fry all pieces for one minute, drain on paper towels, and remove all but one tablespoon of the oil.<BR>3. Reheat the leftover oil and stir-fry ginger, scallion, and chili pepper pieces for half a minute, then add squid, soy sauce and wine, and stir one minute, then serve over rice or noodles, if desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D5562020DF4A2747A0DEA4E5C0A97432', X'68737C98BB76A44CA35E28F82A788356', X'68737C98BB76A44CA35E28F82A788356', 0, 'javascript:', 'Stir-fried Shrimp with Chinese Celery', 2, NULL, 0, 'Stir-fried Shrimp with Chinese Celery', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=879', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound shrimp, shells and veins removed and discarded<BR>1 large clove garlic, mashed or minced very fine<BR>1/2 teaspoon salt<BR>1 Tablespoon vegetable oil<BR>2 Tablespoons peeled and minced fresh ginger<BR>5 stalks Chinese celery, cut in two-inch lengths<BR>2 Tablespoons oyster-flavored sauce<BR>1 Tablespoon cold tea<BR>1 Tablespoon cornstarch mixed with one tablespoon water or stock<BR><B><I>Preparation:</B></I><BR>1. Mix shrimp, garlic, and salt, and set aside for half an hour.<BR>2. Heat wok or pan, then add oil and the ginger, and stir-fry for one minute, then add celery and stir-fry for one minute before adding the reserved shrimp mixture. Stir-fry another one to two minutes, then add oyster sauce and cornstarch mixture, and cook until thickened and clear, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D7026E97ABBA894EAC7AF169C09969E0', X'DA9E7278BCCF544DA7305CA2F3BEA936', X'DA9E7278BCCF544DA7305CA2F3BEA936', 0, 'javascript:', 'Stir-fried Shrimp with Dragon Well Tea Leaves', 2, NULL, 0, 'Stir-fried Shrimp with Dragon Well Tea Leaves', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=291', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound medium shrimp without shells, deveined<BR>1 egg white<BR>1 Tablespoon cornstarch<BR>1 teaspoon salt<BR>1 Tablespoon Dragon Well tea leaves<BR>1/3 cup peanut oil<BR>1 Tablespoon dry sherry<BR>1 scallion, minced<BR>6 fresh waterchestnuts, peeled and sliced<BR><I><B>Preparation:</I></B><BR>1. Dry shrimp with paper towels then mix with egg white, cornstarch and salt.<BR>2. Pour half cup boiling water over the tea leaves, stir and as they start to open, drain reserving the liquid.<BR>3. Heat oil in wok or pan and add the shrimp mixture, stir fry one minute and drain, returning shrimp to the pan.<BR>4. Add tea leaves and tea water and fry one minute then add sherry and mix well, then add scallions and waterchestnuts, mix thoroughly, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6584D69529E256489A32C30BD6B67F82', X'ADFDE4301A8B4044B74CE1D1F4539168', X'ADFDE4301A8B4044B74CE1D1F4539168', 0, 'javascript:', 'Stir-fried Shrimp, Sichuan Style', 2, NULL, 0, 'Stir-fried Shrimp, Sichuan Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1552', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons tomato paste<BR>2 teaspoons vinegar<BR>2 teaspoons sugar<BR>1 Tablespoon soy sauce<BR>1/4 teaspoon salt<BR>1/8 teaspoon cayenne<BR>2 Tablespoons sesame oil<BR>4 cloves garlic, crushed<BR>1 small onion, thinly sliced<BR>2 cups sliced mushrooms<BR>1 pound shrimp, shells removed and discarded<BR><B><I>Preparation:</B></I><BR>1. Thoroughly combine tomato paste mixed with one-quarter cup water, then add vinegar, sugar, soy sauce, salt, and cayenne and set this aside.<BR>2. Heat sesame oil in a wok or fry pan, then add the garlic, onions, and mushrooms and stir-fry until the onion slices are tender.<BR>3. Add shrimp and stir-fry until they turn pink, then add the tomato paste mixture and stir-fry everything for two minutes, put everything on a platter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'47FEB1EF5F1DB34EA51D6C0AB0C7F7D6', X'CFB4E2FF2548C54388C0326E6210BA7E', X'CFB4E2FF2548C54388C0326E6210BA7E', 0, 'javascript:', 'Stir-fried Squid, Cantonese-style', 2, NULL, 0, 'Stir-fried Squid, Cantonese-style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1195', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound fresh squid, cleaned, then blanched in boiling water for half minute, removed and drained, then cut in half and then into very thin strips<BR>1/4 cup cornstarch<BR>1/4 teaspoon Sichuan pepper or black pepper<BR>1/4 teaspoon coarse salt<BR>3 Tablespoons vegetable oil<BR>3 sprigs fresh coriander, stems and leaves minced<BR>1 scallion, minced<BR>1 slice fresh ginger, minced<BR><B><I>Preparation:</B></I><BR>1. Mix squid strips, cornstarch, pepper and salt, and shake to remove any excess starch, and set it aside.<BR>2. In a wok or a large fry-pan, heat oil, and blanch half the squid for half minute, remove and drain; then repeat with the rest of the squid, leaving any remaining oil in the wok.<BR>3. Mix coriander, scallion, and ginger.<BR>4. Reheat wok with its remaining oil, and stir-fry the coriander mixture for one minute. Mix all the squid with one tablespoon of the cornstarch mixture, then add all the fried squid, and stir-fry for half minute, then plate and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E2D90B2E9E924843B99BBAA794C5879A', X'D6AD9D9F9E05134398BFB8B313C9C4E3', X'D6AD9D9F9E05134398BFB8B313C9C4E3', 0, 'javascript:', 'Stir-fried White Eel with Egg White Souffle', 2, NULL, 0, 'Stir-fried White Eel with Egg White Souffle', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1267', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3/4 pound white eel, boned and butterflied<BR>1 teaspoon oyster sauce<BR>1 teaspoon thin soy sauce<BR>1/4 teaspoon salt<BR>1/4 teaspoon sugar<BR>1 Tablespoon oil<BR>1 Tablespoon cornstarch<BR>2 Tablespoons vegetable oil<BR>2 teaspoons finely chopped garlic<BR>1 teaspoon finely chopped fresh ginger<BR>1 Tablespoon sweet soy bean paste<BR>1 Tablespoon broad bean paste<BR>1/4 cup chopped red and green peppers<BR>1 Tablespoon thin soy sauce<BR>1/2 teaspoon sugar<BR>1 teaspoon chicken stock powder<BR>1 teaspoon cornstarch<BR>10 egg whites<BR>1 teaspoon plus 1 Tablespoon flour<BR>2 teaspoons plus 2 Tablespoons cornstarch<BR><B><I>Preparation:</B></I><BR>1. Marinate eel with oyster sauce, the teaspoon of thin soy sauce, salt, sugar, cooked oil, the tablespoon of cornstarch and one teaspoon of water for fifteen minutes.<BR>2. Heat the vegetable oil and stir-fry the garlic, ginger, soy bean, and the broad bean paste, and when fragrant, add the peppers and return the eel and stir-fry for two minutes or until the eel is cooked.<BR>3. Add the tablespoon of thin soy, sugar, chicken powder, and the teaspoon of cornstarch, stir well, and transfer to a pre-heated serving plate.<BR>4. Beat the egg whites until foamy and stiff, and fold in the flour and the rest of the cornstarch. Put this into boiling stock or water and cook until done (set), then place this cooked egg white around the prepared eel, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ABCD3206DA114B4AB1E39501ED2B866C', X'DC95AAA62E1E7F4CA3721AE13339547C', X'DC95AAA62E1E7F4CA3721AE13339547C', 0, 'javascript:', 'Stone Cooked Fish Flllet', 2, NULL, 0, 'Stone Cooked Fish Flllet', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=845', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 and 1/2 pounds fillet of carp or catfish<BR>1/4 cup hot bean paste<BR>2 Tablespoons Chinese or Korean chili powder<BR>1 Tablespoon sugar<BR>3 Tablespoons Chinese rice wine<BR>3 Tablespoons thin soy sauce<BR>1 Tablespoon cornstarch<BR>1/4 cup corn oil<BR>2 Tablespoons fresh ginger, peeled and thin sliced<BR>4 scallions, smashed with side of the cleaver, then cut in three-inch pieces<BR>1/4 pound celery, cut in three-inch lengths<BR>1/4 pound leeks, cut in three-inch lengths<BR>1/4 pound red bell pepper, cut in three-inch lengths<BR>1/8 teaspoon salt and ground white pepper, mixed<BR>3/4 cup corn oil<BR>1 cup Chinese parsley, cut into three-inch long pieces with stems, for garnish<BR><B><I>Preparation:</B></I><BR>1. Marinate the fish in mixture of hot bean paste, chili powder, sugar, rice wine, soy sauce, and the cornstarch for fifteen minutes.<BR>2. Place one large or several smaller stones in the oven at 450 degrees F for thirty minutes.<BR>3. Preheat the pan then add two tablespoons of the oil and add ginger and the vegetables, and the salt and pepper. Remove them to a heat-proof bowl.<BR>4. Place heated rocks in the ceramic bowl on top of cooked vegetables; then add marinated fish fillets on the stones.<BR>5. Preheat the remaining oil and pour it over the fish. Stir a little, then put cilantro on top and serve immediately, placing the dish on a hot tile.<BR><B>Note:</B> If stones are not available, add the marinated fish fillets on top of the cooked vegetables. Pour the hot oil over them and then put the heat-proof bowl in pre-heated 375 degree F oven for five to seven minutes.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F797021684661841B0C61F64B48B8C2B', X'4E0F9249F50AC348A8F266B37718D499', X'4E0F9249F50AC348A8F266B37718D499', 0, 'javascript:', 'Straw Mushrooms and Crab Meat', 2, NULL, 0, 'Straw Mushrooms and Crab Meat', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2298', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound fresh crab meat, all cartilage removed, the large pieces torn into smaller ones<BR>10 small <I>bok cai</I><BR>3 Tablespoons vegetable oil<BR>10 slices fresh ginger, peeled<BR>1 teaspoon salt and ground white pepper<BR>3 scallions, cut into one-inch pieces<BR>1 can (10 ounces) whole straw mushrooms, drained<BR>1 small fresh hot red pepper, seeded and cut into eight pieces<BR>2 Tablespoons Chinese rice wine<BR><B><I>Preparation:</B></I><BR>1. Steam crab over boiling water for eight minutes, stirring once, then remove it and steam the <I>bok cai</I> for two minutes, drain and remove that.<BR>2. Heat wok or fry pan, add oil, and just as it smokes add the ginger and scallion pieces,, stir once, then add the straw mushrooms and stir-fry one to two minutes before adding the hot pepper pieces and the crab meat. Stir, then add the <I>bok cai,</I> stir again, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B334A0CF020C4D4CBE863EAAADEADB64', X'2FE2B42C844E0A4897E46540551FC0CF', X'2FE2B42C844E0A4897E46540551FC0CF', 0, 'javascript:', 'Straw Mushrooms and Crabmeat, Taiwanese Style', 2, NULL, 0, 'Straw Mushrooms and Crabmeat, Taiwanese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1502', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound fresh crab meat, bony structures removed and large pieces torn into smaller ones<BR>10 small <I>bok cai</I><BR>3 Tablespoons vegetable oil<BR>10 slices fresh ginger, peeled<BR>1 teaspoon salt and ground white pepper<BR>3 scallions, cut into one-inch pieces<BR>1 can whole straw mushrooms, drained the liquid discarded<BR>1 small fresh hot red pepper, seeded and cut into eight pieces<BR>2 Tablespoons Chinese rice wine<BR><B><I>Preparation:</B></I><BR>1. Steam crab over boiling water for eight to ten minutes, remove it and steam the <I>bok cai</I> for two minutes, drain and remove.<BR>2. Heat wok or fry pan, add oil, and just as it smokes add the ginger and scallion pieces,, stir once, then add the straw mushrooms and stir-fry one to two minutes before adding the hot pepper pieces and the crab meat. Stir, then add the bok cai, stir again, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2040AAFF4D2BCD498B3638FB16EEBF39', X'5C1E3FA1E6E26F4DBE2B7A7D677913BA', X'5C1E3FA1E6E26F4DBE2B7A7D677913BA', 0, 'javascript:', 'Stuffed You-tai', 2, NULL, 0, 'Stuffed You-tai', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=289', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 pomegranate<BR>1 ripe peach, minced<BR>1 finger of a Buddha''s hand, minced (or two tablespoons lemon rind, minced)<BR>1/2 pound shrimp, shelled with veins removed, then minced<BR>1 egg white<BR>2 Tablespoons cornstarch<BR>3 <I>you-tai</I> (Chinese long breakfast crullers)<BR>1 cup corn oil<BR><B><I>Preparation:</B></I><BR>1. Mix the three fruits with the shrimp, egg white, and cornstarch.<BR>2. Cut the <I>you-tai</I> in half the long way and stuff three of the halves, each with one third of the shrimp-fruit mixture. Cover them with the other half of the <I>you-tai</I>, and cut each one into two-inchsections.<BR>3. Heat the oil in a wok or a deep pot, and fry half the sections for three minutes or until golden and cooked through. Drain well, and repeat until all are fried and drained.<BR><B>Note:</B> These can be served plain, with a hoisin sauce thinned with tea, or any other sweet sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3D193D04DA388A4D905A0FF5A7B8CFB6', X'397CAF4A775B3343AF897EBF924D5EBB', X'397CAF4A775B3343AF897EBF924D5EBB', 0, 'javascript:', 'Stuffed Bitter Melon', 2, NULL, 0, 'Stuffed Bitter Melon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=534', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound, or two bitter melons<BR>1 Tablespoon fermented black beans<BR>1 pound ground pork<BR>1 scallion, minced<BR>12 dried shrimp, optional<BR>2 cloves garlic, minced<BR>1 teaspoon soy sauce<BR>1 Tablespoon dry sherry<BR>1 teaspoon corn starch<BR>1 teaspoon thin soy sauce<BR>2 teaspoons sugar<BR>1/8 teaspoon pepper<BR>1/8 teaspoon salt<BR>2 Tablespoons peanut oil<BR>2 teaspoons corn starch mixed with two tablespoons water<BR><B><I>Preparation:</B></I><BR>1. Cut the bitter melon across the narrow way into one-inch sections. Remove the seeds and pulp and discard. Set the circles aside.<BR>2. Rinse black beans in cold water, drain, and discard the water. 3. Soak the dried shrimp in warm water for half an hour or until soft. Drain and mince them.<BR>4. Combine ground pork, shrimp, scallion, soy sauce, corn starch, half the sugar, and the pepper and salt. Mix with a fork or chopsticks in one direction for half a minute. Stuff this mixture into the center of the bitter melon rings.<BR>5. Mix sherry, light soy sauce, sugar, and one cup of water.<BR>6. Heat oil and brown the stuffed bitter melon rings on both sides. Then add garlic and black beans; stir-fry a few seconds and add the sherry-soy sauce mixture. Bring to a boil, lower the heat and simmer for twenty minutes. Remove the stuffed melon rings to a serving dish. Reserve the sauce.<BR>7. Add the cornstarch-water mixture to the remaining sauce. Heat until thickened, then pour over the stuffed bitter melon, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4E6A71F265533547BBE0BEB57AA09F8C', X'0EFCEFE7EAB2414A8296CE5A63303974', X'0EFCEFE7EAB2414A8296CE5A63303974', 0, 'javascript:', 'Stuffed Deep-fried Crullers', 2, NULL, 0, 'Stuffed Deep-fried Crullers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1608', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pair deep-fried ten- to twelve-inch crullers, each cut in four sections<BR>1 cup minced cuttlefish<BR>1 Tablespoon sa cha sauce<BR>2 Tablespoons cornstarch<BR>3 Tablespoons sesame seeds<BR>2 cups vegetable oil<BR>1/4 cup plum sauce<BR><B><I>Preparation:</B></I><BR>1. Push a chopstick through each section of the cruller.<BR>2. Mix cuttlefish with the sa cha sauce and stuff some into each section, dipping its ends into the cornstarch. Sprinkle some water on the outside of the cruller, just to dampen, then sprinkle the sesame seeds on it.<BR>3. In a large pot, heat the oil, then deep fry the crullers, preferably in two batches, until crisp.<BR>4. Drain on paper towels, then serve while hot or very warm with the plum sauce on the side for those who want to dip them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'36A810F68EC30F449016BFF9A1CB6A39', X'EFD59F7D9F0E034BB5B736ABCC453D46', X'EFD59F7D9F0E034BB5B736ABCC453D46', 0, 'javascript:', 'Stuffed Fish Fillets, Shandong Style', 2, NULL, 0, 'Stuffed Fish Fillets, Shandong Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1528', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound thick fish fillets, sliced horizontally<BR>2 Tablespoons water chestnut flour<BR>1/4 pound chicken breasts, sliced<BR>2 pieces dried bamboo, soaked for one hour, then sliced horizontally<BR>2 teaspoons minced fresh ginger, divided<BR>1/2 teaspoon salt<BR>3 Tablespoons sugar<BR>2 Tablespoons liquid from white wine lees<BR>1 egg white<BR>2 Tablespoons cornstarch mixed with two tablespoons cold water<BR>3 scallions, green parts only, cut into long strings<BR>2 cups vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Using a strainer, dust both sides of the fish fillets with water chestnut flour, and put them on a dry plate.<BR>2. Put pieces of the chicken breast on each fish slice, then top that with a slice of bamboo shoot and sprinkle some minced ginger on top. Then roll the fish enclosing the chicken and bamboo shoot pieces. Tie each fish fillet with a scallion piece to hold it together.<BR>3. Mix salt, sugar, liquid wine lees, and the egg white with the cornstarch mixture beating this into a thin batter.<BR>4. Heat oil in a wok or deep fry pan, then dip each roll into the batter, and fry for four minutes, two on each side, or use more oil and deep fry them for three minutes turning them in the deep oil. Remove the fried fish rolls to a paper towel, drain for half minute, then put then nicely set out, on a serving platter. Do put excess batter into a small pot.<BR>5. Heat contents of the small pot, stirring, until thickened, then pour over the fish rolls, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C060AC47C99AC84A823EC45DACFCAC47', X'63168705CDB8A445B9C1752E588B9493', X'63168705CDB8A445B9C1752E588B9493', 0, 'javascript:', 'Stuffed Fish Maw', 2, NULL, 0, 'Stuffed Fish Maw', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2332', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large fish maw, about half pound or ten by five inches, soaked for half an hour or until soft, then boiled until soft<BR>2 slices fresh ginger, smashed<BR>2 scallions, each tied in a knot<BR>½ pound shrimp, shells and veins removed and discarded, then minced<BR>salt and pepper to taste, or up to 1/4 teaspoon each<BR>2 teaspoons mung bean flour<BR>½ teaspoon granulated sugar<BR>1 egg yolk<BR>1 teaspoon sesame oil<BR>½ cup vegetable oil<BR>optional: 2 Tablespoons hoisin sauce mixed with the same amount of warm tea<BR><B><I>Preparation:</B></I><BR>1. Squeeze out water from the fish maw, and put it in a pot with three cups fresh cold water, the ginger, and scallion knots. Bring to the boil for five minutes, then drain, cool slightly and squeeze out as much water as possible, then cut the maw into pieces two inches long and two inches wide.<BR>2. Squeeze out any water still left in the fish maw, then dip those pieces in a mixture of salt and pepper, mung bean flour, sugar, egg yolk and the sesame oil.<BR>3. Now add minced shrimp to any egg mixture left, and spread the shrimp mixture on the pieces of fish maw, then roll them up, and set them on the seam side down to dry, about ten to twenty minutes.<BR>4. Heat the vegetable oil in a wok or fry pan, put fish maw pieces in the oil their seam side down, and fry until tan (or steam them for five minutes) and then serve on a pre-heated platter plain or with the hoisin sauce mixture on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'829D387132894F49B0ABC574229A7591', X'17EE62728F1A0E4AB0900D8DF3D3DCEB', X'17EE62728F1A0E4AB0900D8DF3D3DCEB', 0, 'javascript:', 'Stuffed Fresh Squid', 2, NULL, 0, 'Stuffed Fresh Squid', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1194', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 fresh squid, cleaned as indicated above, then blanched in boiling water for half minute<BR>1 fresh chili pepper, seeds discarded, and minced<BR>1/4 cup minced peeled large white radish<BR>2 small Kirby-like cucumbers, minced<BR>1/4 cup minced peeled fresh ginger<BR>2 Tablespoons Chinese white vinegar<BR>1 Tablespoon granulated sugar<BR>1 Tablespoon sesame oil<BR>1 small carrot, peeled and minced<BR>1 or 2 leaves lettuce<BR><B><I>Preparation:</B></I><BR>1. Remove tentacles, and mince them, then mix them with the minced chili pepper, white radish, cucumbers, and ginger, and stir in the vinegar, sugar, sesame oil, minced carrot. Let rest covered in the refrigerator for one hour.<BR>2. Remove from the refrigerator and ring out any liquid, then stuff the ingredients tightly into the squid, and refrigerate covered for another hour.<BR>3. Put lettuce on a plate, then using an exceptionally sharp knife, cut the squid into one-inch rings and put them cut side up on a plate. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EFCEE127F6DC634194C6E34DAC967D0C', X'31986F69B0ED2A46AE41B23193DCA16A', X'31986F69B0ED2A46AE41B23193DCA16A', 0, 'javascript:', 'Stuffed Loofah with Oysters and Shrimp', 2, NULL, 0, 'Stuffed Loofah with Oysters and Shrimp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2135', '<TD><RECIPE>2 loofah (silk squash) peeking their exterior strands<BR>1/2 pound small oysters<BR>1/2 pound small shrimp, peeled, their veins removed, and both discarded<BR>2 Tablespoons ground pork<BR>1 egg white<BR>1 teaspoon salt<BR>1/2 teaspoon ground white pepper<BR>2 tablespoons cornstarch, divided<BR>a few lettuce eaves<BR>1/2 cup chicken stock<BR><B><I>Preparation:</B></I><BR>1. Peel the loofah and cut into one-inch circles removing some but not all the seeds with a teaspoon, and discard them.<BR>2. Chop the oyster and the shrimp, and mix them with ground pork, egg white, and salt and pepper, and half the cornstarch. Dust the other half of the cornstarch on one side of each piece of loofah, then stuff a heaping tablespoon on cornstarch side and top with another slice, cornstarch side facing the stuffing.<BR>3. Put these slices on heatproof deep plate and steam over boiling water for eight minutes.<BR>4. Remove the stuffed slices ,ad put any liquid left in the bowl with the stock, and boil reducing this to about three tablespoons. Pour this over the stuffed slices, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'34EC46BAABF5D946A19C44E03522177A', X'436A23B01B635A43A2DDE44ECB3E717C', X'436A23B01B635A43A2DDE44ECB3E717C', 0, 'javascript:', 'Stuffed Pepper w/ Beancurd Sheet and Shrimp', 2, NULL, 0, 'Stuffed Pepper w/ Beancurd Sheet and Shrimp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=159', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound large shrimp, shelled, cleaned and cut into ten large pieces 1 Tablespoon corn oil<BR>3 Tablespoons green peas<BR>3 Tablespoons diced carrots<BR>3 Tablespoons diced soaked black mushrooms<BR>3 Tablespoons rice wine<BR>1/4 teaspoon chili sauce with garlic<BR>1 teaspoon light soy sauce<BR>1/2 teaspoon sugar<BR>1 Tablespoon cornstarch<BR>1 teaspoon sesame oil<BR>dash of ground white pepper<BR>3 bean curd sheets<BR>6 strips of celery to use to tie peppers (or use string)<BR>6 large bell peppers<BR>1 teaspoon corn oil<BR>1/2 cup chicken stock<BR>salt and pepper to taste<BR>1 teaspoon water chestnut flour<BR><I><B>Preparation:</I></B><BR>1. Saute diced shrimp in oil for one minute, then drain and set aside.<BR>2. Simmer peas, carrots, and mushrooms for two minutes, then drain and mix with the shrimp.<BR>3. Mix chili sauce, light soy sauce, sugar, cornstarch, sesame oil and white pepper.<BR>4. Cut the top off each pepper in a zig-zag fashion, remove seeds and pith from both and set aside.<BR>5. Cut each beancurd sheet in half and shape each into a circle. Then stuff each into a pepper and put one-sixth of the filling into it. Tie with celery strip or string; cover with the top of the pepper and put it on lightly greased dish. <BR>6. Steam for eight minutes, then set aside. If using string, cut it and discard.<BR>7. Mix stock, salt and pepper, and water chestnut flour and bring to the boil. Pour over the peppers and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BA8FC1B868653D45943CEF9B3B500212', X'A2E94E6AE78BC349A7131E3386BB49D5', X'A2E94E6AE78BC349A7131E3386BB49D5', 0, 'javascript:', 'Stuffed Sea Cucumber', 2, NULL, 0, 'Stuffed Sea Cucumber', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=680', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 large sea cucumbers<BR>1 Tablespoon sesame oil<BR>4 slices fresh ginger, diced<BR>4 scallions, diced<BR>1 square inch roasted pork belly, diced finely<BR>1 Tablespoon rice wine<BR>1/2 cup corn oil<BR>1 cup peeled, deveined, and diced shrimp<BR>20 quail eggs<BR>1 cup diced roaste duck or goose<BR>10 black mushrooms, soaked, stems removed, and diced<BR>1/2 cup diced bamboo shooots<BR>1/4 cup oyster sauce<BR>1 envelope (one tablespoon) powdered chicken bouillon<BR>1 cup minced greens such as <I>bok cai</I> or spinach<BR>2 Tablespoons cornstarch mixed with six tablepoons cold water<BR>1 Tablespoon dark soy sauce<BR>1 teaspoon ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Soak sea cucumbers, just covering with bottled water, until soft, changing the water every three or four hours. It weill take about two days to get them very soft. Keep them covered and in the refrigerator during this process. As soon as possible, probably during the first day, remove any innards, and rinse well with tap water, then continue the soaking process.<BR>2. Put sea cucumbers in a pot and cover with fresh water, and simmer until they have swollen and are soft to the touch. This may take a couple of hours; it depends upon how long they have been dried. Carefully remove them from the water so as not to tear the sea cucumbers, and set them upside down to drain.<BR>3. Heat sesame oil, and fry ginger and scallions for one minute, then fry pork belly and the rice wine for another minute, then remove from the heat.<BR>4. Heat corn oil to about 300 degrees F. and fry the shrimp just until they turn pink. Remove them and set aside. Then fry the quail eggs, and remove and cool them, then shell them.<BR>5. Return the oil to the heat and add the duck, mushrooms, and bamboo shoots and fry for one minute, then drain well. Add the shrimp to this mixture, and the oyster sauce and chicken powder and mix well. Stuff the sea cucumbers.<BR>6. Set them out on a heated serving plate. Put the quail eggs around them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'23A304B16D99E94DA0B84A323317E992', X'708B210C8C9E08408931ABD1232BFBFD', X'708B210C8C9E08408931ABD1232BFBFD', 0, 'javascript:', 'Sweet and Sour Fish Maw', 2, NULL, 0, 'Sweet and Sour Fish Maw', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1868', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup fish maw, soaked overnight, then squeeze out its water and cut into one to two-inch pieces<BR>3 Tablespoons vegetable oil<BR>3 Tablespoons minced onions<BR>2 Tablespoons minced peeled fresh garlic<BR>1 small red chili pepper, seeded and chopped fine<BR>2 ounces hand-chopped pork<BR>2 ounces raw shrimp<BR>2 Tablespoons tomato sauce<BR>1 Tablespoon tomato paste<BR>1 teaspoon granulated sugar<BR>1 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Put fish maw into a deep fry pan and dry-fry it for one minute, then add the oil and stir-fry another two minutes, then remove it to a small dish.<BR>2. Add the onions and garlic and the chopped pork, and stir-fry another two minutes, and add the chili pepper pieces. Return the fish maw to the pan and add the tomato sauce and paste and one-quarter cup cold water and stir and simmer for five minutes.<BR>3. Stir in the sugar and salt, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6725D6FEB5E63D4387F9454B8E383152', X'72D7EC8E6422B149ABEBF7FD95A22E9D', X'72D7EC8E6422B149ABEBF7FD95A22E9D', 0, 'javascript:', 'Sweet and Sour Rock Fish Fillets', 2, NULL, 0, 'Sweet and Sour Rock Fish Fillets', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=23', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound rock fish fillets<BR>1 Tablespoon cornstarch<BR>1 Tablespoon soybean oil<BR>1 teaspoon sesame oil<BR>6 Tablespoons chicken broth or water<BR>5 Tablespoons soy sauce<BR>4 Tablespoons sugar<BR>5 tablespoons vinegar<BR>1/4 teaspoon five spice powder<BR>3 cloves garlic<BR>2 slices ginger root<BR>2 scallions<BR><I><B>Preparation:</B></I><BR>1. Heat the wok and then add oil until medium-high hot.<BR>2. Put fish into the wok, pan-fry both sides about four minutes, then take fish out, leave the oil in the wok.<BR>3. Put garlic, ginger, and scallions into the hot oil and fry one minute.<BR>4. Mix the broth or water, soy sauce, sugar, vinegar, wine, and spice powder.<BR>5. Add this mixture and the fish to the wok or pan and cook about five minutes.<BR>6. Mix cornstarch with two tablespoons wter, add sesame oil and pour over the fish.<BR>7. Cook until the sauce clears, about one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'394F839EE5138343B0B7B1A8355B4E22', X'7D636EBF993D8A42A08D58D52AE1D433', X'7D636EBF993D8A42A08D58D52AE1D433', 0, 'javascript:', 'Sweet and Sour Shrimp', 2, NULL, 0, 'Sweet and Sour Shrimp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=290', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 cup flour<BR>2 Tablespoons cornstarch<BR>1/2 tsp. baking powder<BR>1/2 teaspoon salt<BR>1 egg<BR>1 cup plus two teaspoons peanut oil<BR>16 large shrimp, shelled except for tail portion and deveined<BR>1/2 cup catsup<BR>2 Tablespoon white vinegar<BR>3 Tablespoon sugar<BR>1 large green pepper, cored, seeded and cut into one-inch pieces<BR>1 large onion, peeled, cut in eighths, and separated into pieces<BR>1 can (fifteen and a half ounces) pineapple chunks in juice, drained<BR>2 teaspoon cornstarch<BR><I><B>Preparation:</B></I><BR>1. Mix flour, cornstarch, baking powder, salt, egg, half cup of water and one teaspoon of the oil in a bowl until smooth.<BR>2. Heat one cup oil in uncovered wok to 375 degrees F.<BR>3. Holding shrimp by tail, dip one at a time into batter, letting excess drip back into bowl.<BR>4. Slide the shrimp into hot oil and fry until golden brown, one to two minutes each. Fry two to three at a time and drain on paper towels.<BR>5. Pour out all oil except one tablespoon, then add one cup of water, a teaspoon of oil, and the catsup, vinegar, and sugar to the wok; and bring this to a boil.<BR>6. Add the green pepper and onion and stir-fry for two minutes, then add the pineapple and bring back to the boil.<BR>7. Stir the cornstarch mixture and add it to wok and stir until it thickens.<BR>8. Add shrimp and stir gently until heated through. Serve immediately.<BR><B>To modify this recipe:</B><BR>1. Use a coated non-stick pan in place of the wok.<BR>2. Reduce the amount of oil to three tablespoons.<BR>3. Use a low sodium catsup in place of regular catsup.<BR>4. Use two beaten egg whites in place of one whole egg.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'11C472992C0B9F4284C8232E5141B5FA', X'2F026B913A5A854EAF00E10C8DCC10C3', X'2F026B913A5A854EAF00E10C8DCC10C3', 0, 'javascript:', 'Swmming Gold Fish', 2, NULL, 0, 'Swmming Gold Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1320', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 boneless duck feet, simmered for one hour, then their bones removed and discarded<BR>1 teaspoon sesame oil<BR>1 Chinese black mushroom, soaked for half an hour, then stem removed and discarded<BR>2 whole fresh chili peppers<BR>1/2 pound fresh shrimp, shells and veins removed and discarded, then minced<BR>1 inch cube pork fat, minced<BR>1 egg white<BR>1 teaspoon Chinese rice wine<BR>dash salt and ground white pepper<BR>1 teaspoon cornstarch<BR>3 Tablespoons chicken broth<BR>1 teaspoon cornstarch<BR>1 teaspoon vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Soak boned duck feet in sesame oil for half an hour.<BR>2. Sliver mushroom and set aside.<BR>3. Cut chili pepper across the width making circles, and set aside.<BR>4. Mix shrimp and fat, add egg white, rice wine, and salt and pepper, and the cornstarch and make into ten oval-shaped pieces. Stuff one boneless duck foot into one end of each one making the tail of the fish. Then make two eyes with the chili pepper slices and push them into the other end of the shrimp mixture, add a short piece of mushroom pushed between them for the nose.<BR>5. Oil a heat-proof plate, and put the swimming fish around, tails facing center. Steam over boiling water for five minutes, remove and drain any liquid, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1503FC77DF39C84CAACA9D3726FE94F3', X'97D235220AC8CB4790EBB2F22EC9B504', X'97D235220AC8CB4790EBB2F22EC9B504', 0, 'javascript:', 'Taro Balls with Shrimp and Chestnuts', 2, NULL, 0, 'Taro Balls with Shrimp and Chestnuts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=738', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound taro root, peeled and steamed for twenty minutes, then cooled<BR>2 tablespoons vegetable oil<BR>1 scallion, minced<BR>20 small shrimp, boiled for one mintue, then drained<BR>10 cooked chestnuts, mashed<BR>1 teaspoon Chinese rice wine<BR>1 teaspoon salt<BR>1 Tablespoon cornstarch mixed with one tablespoon water<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mash taro root and divide into ten parts. Make these into balls then flatten them.<BR>2. Heat wok, add scallions and stir-fry half minute, drain and remove them leaving any oil in the wok. Put a few pieces of the scallions in the center of each flattened piece of taro.<BR>3. Put two small shrimp on each taro piece on the scallion pieces.<BR>4. Mix chestnuts, rice wine, and salt, then put one-tenth on the shrimp. Then close the taro around the things sitting on top of it and shape it into a ball. Put the balls into the wok oil with the oil left from cooking the scallions, Gently heat it and fry the balls until they barely begin to brown. Then add the cornstarch mixture and stir gently until it thickens and the taro balls are heated through. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'006059882572A54E8BFF7937ECA582EA', X'0B2AD0C3BA8E624D92B4FB44F3CC33A0', X'0B2AD0C3BA8E624D92B4FB44F3CC33A0', 0, 'javascript:', 'Tea-powdered Shrimp', 2, NULL, 0, 'Tea-powdered Shrimp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1706', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon powdered green tea<BR>2 Tablespoons fresh lemon juice<BR>1 Tablespoons vegetable oil<BR>20 large shrimp, peeled with tails left on, and veins removed<BR>dipping sauce of choice<BR><B><I>Preparation:</B></I><BR>1. Mix powdered tea, lemon juice, and vegetable oil, and toss with the shrimp. Marinate for one hour in the refrigerator, then skewer them, two to skewer piercing them tail to head end.<BR>2. Heat grill, then grill them about two to three minutes per side, then serve with dipping sauce on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'92AD14D2B0F86B4999EA44AB91381F2B', X'227EF0BDD9DFB44284E600B84D8E4BDD', X'227EF0BDD9DFB44284E600B84D8E4BDD', 0, 'javascript:', 'Three Cups Squid', 2, NULL, 0, 'Three Cups Squid', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=837', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2/3 pound fresh squid<BR>2 Tablespoons black sesame oil<BR>1 or 2 slices fresh ginger<BR>2 chili peppers, seeded and sliced<BR>2 Tablespoons Chinese rice wine<BR>2 Tablespoons soy sauce<BR>1/2 teaspoon salt<BR>1 teaspoon sugar<BR>dash of ground white pepper, or to taste<BR>5 stalks basil, rinsed, leaf groupings removed from the stem<BR><B><I>Preparation:</B></I><BR>1. Clean squid inside and out, and remove the head. Cut the body into half inch rings. Rinse well, and be sure ink sack has been discarded.<BR>2. Heat wok, then add the black sesame oil, and when it is heated, add ginger and fry for half minute before adding chili peppers, rice wine, soy sauce, salt, sugar, and ground white pepper. After one minute, add the squid and the basil and stir-fry another minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7561DC6D41A75648B55416A3F43BF7F5', X'30E542717B3A0D4C9FEA9F19DD48A474', X'30E542717B3A0D4C9FEA9F19DD48A474', 0, 'javascript:', 'Tofu Fish Cake and Wintermelon', 2, NULL, 0, 'Tofu Fish Cake and Wintermelon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1606', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds wintermelon, peeled and cut into cubes the size of the fish tofu cubes<BR>1 pound frozen tofu fish cubes<BR>2 Tablespoons vegetable oil, separated<BR>2 cups chicken stock<BR>1/2 teaspoon salt<BR>1 Tablespoon black bean sauce<BR><B><I>Preparation:</B></I><BR>1. Blanch frozen tofu fish cakes in boiling water for two minutes, then drain for five minutes.<BR>2. Heat wok or fry pan, add one tablespoon oil and pan fry the tofu fish cakes until lightly charred and no longer sizzling (about four minutes). Remove and set side.<BR>3. Add rest of the oil and stir-fry the wintermelon pieces until lightly charred, about three minutes, then add the chicken stock, salt, and black beans, cover, and allow to boil. Stirring every two to three minutes. If it boils dry, add half cup of water, and let boil for ten minutes in total, then turn off the heat and let rest for five minutes before putting in a bowl and serving this dish.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'685CFF3037CC4B44831E7ECA947B6264', X'4A3AF79272B1C1419D32F98BB59C865B', X'4A3AF79272B1C1419D32F98BB59C865B', 0, 'javascript:', 'Tofu in a Pot, Meatless', 2, NULL, 0, 'Tofu in a Pot, Meatless', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1306', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>16 ounces firm tofu<BR>8 ounces napa cabbage, cut into three-quarter-inch strips<BR>2 packages (each 1.3 ounces) cellophane noodles, soaked for twenty minutes, then cut into eight-inch lengths<BR>6 dried Chinese mushrooms, soaked for twenty minutes in hot water, stems removed<BR>12 ounces firm white fish, cut into one-inch pieces<BR>4 whole scallions, chopped<BR>1 Tablespoon fresh ginger, minced<BR>1 Tablespoon garlic, minced<BR>2 Tablespoons oil<BR>1 Tablespoon dried shrimp<BR>3 cups vegetable stock<BR>½ teaspoon ground white pepper<BR>2 Tablespoons soybeans<BR><B><I>Preparation:</B></I><BR>1. Drain tofu and slice into one-and-half-inch by one-quarter-inch pieces.<BR>2. Put cabbage into the bottom of a two-quart saucepan. Add cellophane noodles then the soaked mushrooms, each cut in half, and the tofu on top of them; and the pieces of fish on the top.<BR>3. Heat a wok or large fry-pan and then add oil and when hot add scallions, ginger, garlic, and dried shrimp and stir-fry some twenty seconds until fragrant before adding the vegetable stock and the ground pepper and bring it to the boil; then pour this over the tofu mixture.<BR>4. Bring tofu mixture to the boil, reduce heat and cover the pot and simmer it for eighteen minutes.<BR>5. Sprinkle the soybean around in the pot, recover and simmer two minutes more, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'64C47994B05A23438F50968D00D94707', X'5D1A367EDE2467489B0CF85CC58754CF', X'5D1A367EDE2467489B0CF85CC58754CF', 0, 'javascript:', 'Tomato Sauce Shrimp', 2, NULL, 0, 'Tomato Sauce Shrimp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=613', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pound large shrimp<BR>1/2 egg white<BR>2 teaspoons cornstarch<BR>2 teaspoons sherry<BR>2 Tablespoons vegetable oil<BR>8 slices fresh ginger<BR>2 cloves garlic, chopped<BR>2 scallions, cut into one inch pieces<BR>1 teaspoon sugar<BR>2 teaspoons light soy sauce<BR>3 Tablespoons catsup<BR>2 Tablespoons water<BR><B><I>Preparation:</I></B><BR>1. Marinate the shrimp with the egg white, sherry, and cornstarch. Mix well until all the shrimp are well coated.<BR>2. Mix sugar, soy sauce, catsup and water in a bowl and set aside.<BR>3. Put oil in a non-stick frying pan or wok and heat until the oil is hot. Add ginger and garlic and stir-fry a few seconds.<BR>4. Add shrimp mixture and stir-fry for one minute, then add the catsup mixture and simmer for two minutes. Now serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C17AF3CF3C45AE4A9A8C656DAA2AC729', X'AC799C675069074D8317DEA32B66E691', X'AC799C675069074D8317DEA32B66E691', 0, 'javascript:', 'Tomato Shrimp with Mango', 2, NULL, 0, 'Tomato Shrimp with Mango', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=804', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>2 cloves garlic, crushed<BR>2 slices fresh ginger, peeled and minced<BR>1 teaspoon chili oil<BR>2 Tablespoons tomato ketchup<BR>3/4 pound raw shrimp, peeled and deveined<BR>4 tomatoes, chopped 1 large mango, peeled and cubed<BR>2 scallions, diced<BR><B><I>Preparation:</B></I><BR>1. Heat pan, add oil and add garlic and ginger and stir-fry for one minute, then adding chili oil and ketchup and continuing to stir-fry for another minute.<BR>2. Add the shrimp and stir-fry until they are no longer pink, about two minutes, before adding tomatoes and mango. Stir-fry just until they are warmed, about a minute, them transfer to a serving dish, sprinkle scallions on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F60638152FA521449B5247A21FF62148', X'B8EC85718221F840842E86803B9474D6', X'B8EC85718221F840842E86803B9474D6', 0, 'javascript:', 'Turnip Dian Xing Cake with Shrimp', 2, NULL, 0, 'Turnip Dian Xing Cake with Shrimp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2266', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>4 inches daikon radish, peeled and minced<BR>1 cup chicken stock<BR>12 dry shrimp, soaked for twenty minutes, any veins and shells discarded <BR>6 black mushrooms, soaked until soft in one cup warm water, stems discarded, and minced fine, mushroom water set aside<BR>3 Tablespoons cornstarch<BR>1 Chinese sausage, minced<BR>2 Tablespoons cilantro, minced<BR>1 cup rice flour<BR>½ teaspoon salt<BR>2 Tablespoons vegetable oil<BR>2 Tablespoons oyster sauce (optional)<BR><B><I>Preparation:</B></I><BR>1. Cook the daikon in the chicken stock until almost soft.<BR>2. Mix shrimp, minced mushrooms, mushroom water, cornstarch, rice flour, salt, and daikon and soup it was cooked in, adding the sausage. Bring this to the boil, add the cilantro and salt and pour into a shallow pan, cover, and refrigerate overnight.<BR>3. Cut this set mixture into two-inch squares and brown in the oil. Serve hot, plain or with oyster sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DF506144F0914848B402FF439BD3D0F8', X'091EFB580AE29846A68217CE52DEFEDE', X'091EFB580AE29846A68217CE52DEFEDE', 0, 'javascript:', 'Vinegar Carp', 2, NULL, 0, 'Vinegar Carp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=735', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pound center cut of half a fresh grass carp, scales removed<BR>1 Tablespoon vegetable oil<BR>2 scallions, cut into half-inch slices or smaller<BR>4 slices fresh ginger, slivered, and divided in two<BR>1 teaspoon salt<BR>dash of coarsely ground black pepper<BR>2 Tablespoons thin soy sauce<BR>2 Tablespoons sugar<BR>2 Tablespoons Chinese rice vinegar<BR>1 Tablespoon peeled fresh tomato, juice squeezed out, then minced<BR>1 Tablespoon cornstarch mixed with half-tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Rinse and pat fish dry, then lightly score it on the skin side before putting into a pot of boiling water. Turn of the heat, and let the fish rest for fifteen minutes before removing to a warmed platter.<BR>2. Heat wok, add the oil, then fry the scallions and ginger for one minute before adding all of the remaining ingredients. Stir them for two minutes, then pour over the fish, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'04C53E519725C84CBBECAE09F42E371C', X'49B9449E6571A64BB55AD9914E167E40', X'49B9449E6571A64BB55AD9914E167E40', 0, 'javascript:', 'West Lake Fish', 2, NULL, 0, 'West Lake Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=607', '<TD><RECIPE><B><I>Ingredients</I></B><BR>1 two-pound sea or striped bas (or red snapper or other white fish)<BR>1 Tablespoon dry sherry<BR>5 slices ginger root (one-eighth-inch thick)<BR>1/4 cup sugar<BR>1/3 cup <I>Chinkiang</I> or cider vinegar<BR>1 Tablespoon soy sauce<BR>1 garlic clove (smashed with side of a cleaver)<BR>2 Tablespoons cornstarch<BR><B><I>Preparation:</B></I><BR>1. Rinse the fish and pat it dry, then make three slashes crosswise near the center on both sides of the fish.<BR>2. Bring water to the boil in a larege steamer or roasting pan. Slide in the fish, add the sherry and three of the ginger slices. Cover the pan and remove it from the heat leaving the fish cook in the hot (not boiling) water for twenty minutes.<BR>3. Chop the remaining ginger and put it in a garlic press and squeeze out the ginger juice reserving it; you should get about one-quarter teaspoon of juice.<BR>4. Put the ginger juice in a small saucepan with the sugar, vinegar, soy sauce, garlic, and cornstarch and heat and stir continuously until it thickens. Then discard the garlic.<BR>5. Carefully remove the fish. Use a large spatula and slide it on to a platter. Do not lift the fish. Slide it or it will fall or break apart.<BR>6. Pour the sauce over it, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8A716FD670CF8A42A6961668DE9F3E78', X'267BCFD48EB1A247A2A68389B14253D0', X'267BCFD48EB1A247A2A68389B14253D0', 0, 'javascript:', 'West Lake Fish with Shallot Oil', 2, NULL, 0, 'West Lake Fish with Shallot Oil', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1755', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 two pound croaker or carp<BR>2 Tablespoons vegetable oil<BR>2 Shallots, peeled and minced<BR>2 Tablespoons mushroom soy sauce<BR>2 Tablespoons Shao Xing rice wine<BR>2 slices peeled and minced fresh ginger<BR>1 Tablespoon granulated sugar<BR>1 Tablespoon crushed brown slab sugar<BR>2 Tablespoons rice vinegar<BR>2 Tablespoons cornstarch<BR><B><I>Preparation:</B></I><BR>1. Cut croaker or carp in half, discard the center bone, scale the fish, and discard the entrails.<BR>2. Put the fish halves on a flat plate and slide them into boiling water, simmering for two minutes. Remove them to the flat plate and discard all but half cup of water.<BR>3. Add the oil, shallots, soy sauce, rice wine, ginger, granulated and the crushed brown sugar to the pan.<BR>4. Mix vinegar and the cornstarch and stir into the soy sauce mixture add the fish, and bring to the boil stirring carefully until it boils and thickens.<BR>5. Carefully transfer the fish to a preheated platter, pour the thickened sauce over, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'77609A25A0997945BEC278CFEE5C9CA2', X'80F44C24DA96B3488E015B4FFFA1E2CF', X'80F44C24DA96B3488E015B4FFFA1E2CF', 0, 'javascript:', 'Wine and Honey-flavored Eel', 2, NULL, 0, 'Wine and Honey-flavored Eel', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=457', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound eel, skinned and gutted<BR>2 cups Chinese rice wine<BR>2 Tablespoons honey<BR>1 garlic clove, sliced<BR>½ teaspoon white pepper<BR>1/4 teaspoon salt<BR>1 Tablespoon wolfberries<BR><B><I>Preparation:</B></I><BR>1. Cut eel halves six pieces crossways and blanch them for half minute then when well-drained, put them into a bowl.<BR>2. Mix eel with the honey, garlic, pepper, and salt and let marinate for half an hour.<BR>3. Put into a heat-proof casserole (or a pot), sprinkle the wolfberries on top. Then cover and simmer for fifteen minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'40B973B69103AB4D9EB081E3D50AFB0B', X'927854046E5496419A5C5E300617DC00', X'927854046E5496419A5C5E300617DC00', 0, 'javascript:', 'Wonona''s Eel in Egg Batter', 2, NULL, 0, 'Wonona''s Eel in Egg Batter', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=143', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound skinned eel, cut in two inch sections<BR>3/4 teaspoon salt<BR>dash of ground black pepper<BR>5 Tablespoons flour<BR>1 egg, beaten<BR>2 Tablespoons water<BR>1 cup vegetable oil<BR>1-2 scallions, chopped<BR><B><I>Preparation:</I></B><BR>1. Season eel with salt and pepper.<BR>2. Make batter of flour, egg, and water and let it rest for ten minutes, then put seasoned eel into it.<BR>3. In a deep pan, heat oil to 375 degrees F. Lift pieces of eel out of the batter and deep-fry them for about five minutes. Remove them from the oil and drain on paper towels. Put them on a platter and sprinkle scallions on top; then serve.<BR><BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C16358B26C92D34688D19A1B7B1B21B1', X'201FC8E6B4B7524E912970954309AC1F', X'201FC8E6B4B7524E912970954309AC1F', 0, 'javascript:', 'Wrapped Eel', 2, NULL, 0, 'Wrapped Eel', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=454', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 large dry lotus leaf, soaked for half an hour in warm water<BR>1 sweet potato, peeled and cut into thin slices<BR>1/2 pound eel, skinned and gutted and cut in half the long way, then cut each half into three- to four-inch pieces<BR>2 Tablespoons sweet potato or arrowroot flour<BR>1/2 teaspoon five-spice powder<BR>1 Tablespoon minced fresh ginger<BR>1 scallion, minced<BR>1 Tablespoon thin soy sauce<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Drain lotus leaf and set it into a steamer basket about ten inches wide. Put sweet potato slices over the part in the steamer basket.<BR>2. Dry eel pieces. Mix flour and five-spice powder and coat the eel pieces with the flour, then put them in a single layer on top of the sweet potato slices. Sprinkle the ginger and scallion and then the soy sauce and the sesame oil on the eel. Then close the lotus leaf, keeping the seam side up, Steam this for twelve minutes, Set lotus leaf package in a western-style soup bowl and serve, opening it when all are seated at the table.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5CEFD7FDA33C434D8183A07A7CB986E4', X'B466B17A4E08A845824D26559D553569', X'B466B17A4E08A845824D26559D553569', 0, 'javascript:', 'Yellow Eel with Black Beans', 2, NULL, 0, 'Yellow Eel with Black Beans', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1879', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>2 whole garlic cloves, peeled and coarsely chopped<BR>4 slices peeled fresh ginger, minced<BR>2 green chilies, seeded and cut into thin slivers<BR>1 red chili, seeded and cut into thin slivers<BR>2 Tablespoons fermented black beans<BR>1/2 pound yellow eel, deboned and blanched with hot water, then cut into two-inch pieces<BR>1 teaspoon mushroom soy sauce<BR>1 teaspoon ginger juice<BR>1 teaspoon granulated sugar<BR>dash ground white pepper<BR>1 teaspoon sesame oil<BR>2 Tablespoons Shaoxing wine<BR>2 teaspoons cornstarch mixed with two teaspoons cold water<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil and fry the garlic and ginger for one minute, then add the chili pieces and the fermented black beans and the eel and stir-fry two minutes.<BR>2. Add the soy sauce, ginger juice, sugar, ground pepper, sesame oil, and the wine and toss for one more minute.<BR>3. Add the cornstarch mixture, stir until thickened, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'858ED1F8C3E5AD4787BF2A01E0749B51', X'C3253D221BE7204696FADE7E8E2204FD', X'C3253D221BE7204696FADE7E8E2204FD', 0, 'javascript:', 'Yin-Yang Duo by Chefs Denny and Jack of New Yen Ch', 2, NULL, 0, 'Yin-Yang Duo by Chefs Denny and Jack of New Yen Ch', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1363', '<TD><RECIPE><B><I>Ingredients for the salad:</B></I><BR>2 dried mung bean sheets (about ounce and a half), soaked in hot water for half an hour, then cut into one-inch wide strips<BR>1 medium size carrot, peeled and shredded<BR>1/2 pound spinach, blanched and cut into two-inch sections<BR>1 egg, beaten, fried into a thin sheet, cooled, then finely shredded<BR>8 large shrimp, shells and veins removed and discarded, then cooked<BR>2 squid, cleaned, cooked and cut into thin strips<BR>2 pieces imitation crab legs, shredded<BR>8 dried Chinese black mushrooms, soaked, stems removed, then simmered and cut into thin strips<BR><B><I>Chef Ju Kin Fo''s Salad Dressing ingredients:</B></I><BR>2 ounces Chinese mustard powder<BR>2 ounces cold water<BR>2 ounces white vinegar<BR>1 ounce soy sauce<BR>4 ounces sugar<BR><B><I>Preparation:</B></I><BR>1. Put Chinese mustard powder in a bowl, add water and mix into a smooth paste. <BR>2.Then add other ingredients and stir well.<BR><B><I>Ingredients for the stir-fry:</B></I><BR>4 ounces pork, cut into thin strips<BR>2 scallions, cut into 2-inch sections<BR>1/2 onion, finely sliced<BR>2 ounces shredded carrot<BR>1 Tablespoon vegetable oil<BR><B><I>Ingredients for the sauce:</B></I><BR>1 Tablespoon soy sauce<BR>1/4 teaspoon sugar<BR>Dash ground white pepper<BR>1/4 teaspoon sesame seed oil<BR>1/4 teaspoon minced garlic<BR><B><I>Final preparation for the salad:</B></I><BR>1. Arrange cut mung bean sheets in the middle of a big platter, covering about three quarters of the bottom.<BR>2. Divide the cold ingredients into four parts, and the outer quarter of the platter into four sections, arranging carrot, spinach, egg shreds, prawn, squid, crabmeat, and sea cucumber (or black mushroom) into a nice pattern. Repeat the pattern for all four sections. Then, mix the salad dressing well and drizzle over the entire salad, including the mung bean sheet area.<BR><B><I>Final preparation for the stir-fry: </B></I><BR>1. Mix ingredients for the sauce, and set aside.<BR>2. Heat wok, add oil then the scallions, onions, carrots, and pork and stir-fry for three minutes. Add prepared sauce and stir-fry another two minutes, then remove wok from heat source. Using a spatula, transfer the stir-fry onto the center of the platter, covering the inner circle made of the mung bean sheets.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2BD8A9627FED794588641036ACE4994D', X'62FE5BF6A8384840AF215553AA690266', X'62FE5BF6A8384840AF215553AA690266', 0, 'javascript:', 'Yue Shang Fish with Tea Oil', 2, NULL, 0, 'Yue Shang Fish with Tea Oil', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1015', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound boneless thin flat fish filet, cut into six pieces<BR>2 Tablespoons tea or vegetable oil<BR>1 teaspoon salt<BR>1/2 cup white Chinese rice wine vinegar<BR>6 bowls cooked rice noodles<BR>1/2 teaspoon chili paste with garlic<BR>2Tablespoons puffed or dry roasted peanuts<BR>2 Tablespoons chopped chives<BR><B><I>Preparation:</B></I><BR>1. Rub fish with oil, then the salt. Put it in a bowl and pour rice wine vinegar over it, cover, and set aside in the refrigerator for six hours or overnight. This pickles the fish.<BR>2. Pour boiling water over the noodles and set them aside for a few minutes. When ready to use them, drain, mix with the chili paste, and put some in individual rice bowls.<BR>3. Remove the fish from the refrigerator, drain and discard the vinegar, and put one piece on top of the noodles in each bowl.<BR>4. Sprinkle some peanuts and chives on each piece of fish, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6F2293130F41DF4F8A4E4D3F232F1A50', X'8F6173B650714A4BB31D193A845098CD', X'8F6173B650714A4BB31D193A845098CD', 0, 'javascript:', 'Zhejiang Sweet and Sour Fish Tails', 2, NULL, 0, 'Zhejiang Sweet and Sour Fish Tails', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=5', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>4 fish tails<BR>1 Tablespoon rice wine<BR>1 egg yolk<BR>dash of white pepper<BR>3 Tablespoons cornstarch<BR>1 cup corn oil<BR>1/2 cup onion, pelled and cut into wedges<BR>1/2 cup black mushrooms, soaked and their stems removed<BR>1/2 cup green pepper, seeded with pith removed, cut into wedges<BR>2 Tablespoons sugar<BR>2 Tablespoons Zhejiang Chinese black vinegar<BR>2 Tablespoons hoisin or any mashed bean sauce<BR>2 Tablespoons cornstarch mixed with 4 Tablespoons cool water<BR><I><B>Preparation:</I></B><BR>1. Dry fish pieces and set aside.<BR>2. Mix rice wine, egg yolk, and white pepper and set fish pieces in it and see that all sides are coated well.<BR>3. Heat oil. Coat the fish with the three tablespoons of cornstarch, and deep fry until lightly browned, then remove from the oil and drain on paper towels for one minute then put fish on a preheated platter. Set oil aside in a small bowl, and clean the wok.<BR>4. Take two tablespoons of the oil and put it into the clean wok and heat it, then fry the onion, mushroom and pepper pieces for one minute. Then add the sugar, vinegar, hoisin sauce, and cornstarch mixture. Bring to the boil, and pour this over the fish and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F193303C1E90014B88C546E2C4ABE7D1', X'3CC6F761960E2C41995631CCDB50917A', X'3CC6F761960E2C41995631CCDB50917A', 0, 'javascript:', 'Coltsfoot: A Throat-soothing Herbal Candy', 2, NULL, 0, 'Coltsfoot: A Throat-soothing Herbal Candy', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1723', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>15 cups coltsfoot leaves without any stems<BR>1 pound granulated sugar<BR>2 cups corn syrup<BR>3 Tablespoons butter<BR>pinch of baking soda<BR>1 Tablespoon vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Rinse the coltsfoot leaves well, then put into a large pot with 5 cups of cold water.<BR>2. Bring them to the boil and boil for three minutes, then add the sugar, corn syrup, and the butter and cook until this sugar mixture reaches the hard boil stage or when one drop in cold water turns hard. At that point, remove from the heat, add the baking soda, put it into an electric mixer and beat until it starts to get stiff.<BR>3. Oil an eight or nine inch square pan, pour in the firm sugar mixture, and allow to harden.<BR>4. Break the hardened material into sucking candy size pieces and use them as throat lozenges. Use about than four or five a day.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'55C558C06F9657458B4DA64F7D5ECBE7', X'F542992BE151FB479A5C0D9E8074E816', X'F542992BE151FB479A5C0D9E8074E816', 0, 'javascript:', 'Chicken Chow Mein, UK 2007', 2, NULL, 0, 'Chicken Chow Mein, UK 2007', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1310', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>9 ounces dried egg noodles<BR>2 Tablespoons sunflower oil<BR>10 ounces cooked chicken breasts, shredded<BR>1 garlic clove, peeled and finely chopped<BR>1 red bell pepper, seeded and thinly sliced<BR>3 and ½ ounces Chinese black mushrooms sliced<BR>6 scallions, sliced on an angle<BR>3/4 cup bean sprouts<BR>3 Tablespoons soy sauce<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Place egg noodles in a large bowl and break them up slightly. Pour enough boiling water over them to cover, and let stand while preparing the other ingredients.<BR>2. Preheat a wok over medium heat. Add the sunflower oil and swirl around to coat the sides of the wok. When oil is hot, add chicken, garlic, red pepper, mushrooms, scallions, and bean sprouts and stir-fry about five minutes.<BR>3. Drain the noodles thoroughly, then add to the wok, toss well, and stir-fry an additional five minutes. Drizzle soy sauce and sesame oil and toss until thoroughly combined.<BR>5. Transfer to warmed serving bowls and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'76118DFF540C6E4290D41363456D0072', X'172D629C44096544A245DB5A668D4620', X'172D629C44096544A245DB5A668D4620', 0, 'javascript:', 'Chow Mein in the 1920''s', 2, NULL, 0, 'Chow Mein in the 1920''s', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1308', '<TD><RECIPE>Heat 3 T. Oil in a saucepan over high heat, and saute 1 c. sliced beef or chicken until brown. Add 1 c. sliced celery, 1 c. diced onion, and 1 c. water. Stir and cook until tender, covered. Add 1 can of Chinese vegetables, 3 T. Flour mixed with ½ c. water, 2 T. Soy sauce, 1 t. salt, ½ t. paprika, and 1/4 t. celery salt. Serve on rice and top with 1 can Chinese fried noodles.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8C94D2A13AF5BC45A890D72407705BAA', X'EE967EEEB83C5F46818FB399E3F06F52', X'EE967EEEB83C5F46818FB399E3F06F52', 0, 'javascript:', 'Chow Mein, Guangdong 2010', 2, NULL, 0, 'Chow Mein, Guangdong 2010', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1311', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound dried egg noodles, boiled until almost soft, and set aside<BR>2 cups baby bok cai or another Chinese green, cut into large pieces, blanched for two minutes in hot water, then drained well<BR>3 Tablespoons vegetable oil<BR>2 cloves garlic, peeled and finely minced<BR>1 slice fresh ginger, peeled and finely minced<BR>1 ounce dried Chinese black mushroom, soaked and sliced<BR>6 ounces uncooked chicken breasts, shredded<BR>1/2 red bell pepper, seeded and thinly sliced<BR>2 scallions, sliced on an angle<BR>1/2 cup bean sprouts<BR>1 Tablespoon soy sauce<BR>1/2 Tablespoon oyster sauce<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Preheat a wok over medium heat, add the oil and swirl around to coat the sides of the wok. Add the drained noodles and fry until crisp on one side, turn over, and fry until crisp on the other, drain on a aper towel, and set aside.<BR>2. Set the drained blanched greens around the edges of a serving platter. Put the drained noodles on top, leaving some one-inch of greens showing around the edges of the platter.<BR>3. Reheat whatever oil remains in the wok and stir fry the garlic and ginger about half minute until fragrant. Then add the mushrooms and stir-fry for one minute, then add the shredded chicken and stir-fry another minute before adding the red pepper pieces, scallions, and bean sprouts. Stir-fry one minute, then add soy sauce, oyster sauce, and sesame oil and stir-fry another minute, then pour over the noodles, leaving half inch around the outside of the noodles without any chicken mixture. Remove to the dining table and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F5999994B79BB6498C0DE00881B74B51', X'C05C0ACEA5B0D34E958CF7638F9EC972', X'C05C0ACEA5B0D34E958CF7638F9EC972', 0, 'javascript:', 'Lobster Chow Main', 2, NULL, 0, 'Lobster Chow Main', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1309', '<TD><RECIPE>This recipe appeared in the 1914 book by Bosse and Watanna book and is reproduced as written:<BR>One pound of lobster meat; two eggs; one half pound of noodles; three stalks of celery; one can of bamboo shoots; one onion; one quarter pound of mushrooms; one tablespoon of sweet lard; one quart of peanut oil; three water chestnuts.<BR>First boil the peanut oil and into it throw the noodles, cooking until crisp. Remove and drain, while preparing following: Put teaspoon of sweet lard into frying pan, and when hot turn in half the lobster meat, which has previously been cooked (Canned lobster will do as well.) Cook for a few minutes, then add the onion, mushrooms, and water chestnuts. The bamboo shoots are added last, and all fried for ten minutes. Take a hot platter, and place the crisp noodles as a layer at the bottom of the dish, and spread above ingredients on top. Then take the other half of the lobster meat, and place a layer on top. Garnish with shredded yolks of hard-boiled eggs.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EAF4600533C33040840DE4AB6E751156', X'7D49AAA9C5A60D4799D70F4C531FE79C', X'7D49AAA9C5A60D4799D70F4C531FE79C', 0, 'javascript:', 'Almond Bean Curd with Loquats', 2, NULL, 0, 'Almond Bean Curd with Loquats', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1040', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1 envelope unflavored gelatin<BR>1/2 cup whole evaporated milk<BR>3 Tablespoons sugar<BR>2 teaspoons almond extract<BR>11 loquats, ten cut into quarters, one cut into rings<BR><I><B>Preparation:</I></B><BR>1. Put gelatin and three tablespoons cold water in a small pot, stir, then heat at a very low temperature just until the gelatin dissolves, stirring all the time.<BR>2. Add evaporated milk and two cups and two tablespoons of water, the sugar, and the almond extract. Mix well, then pour into a bread pan and allow to set. When almost completely set, mix in loquat quarters and let set completely.<BR>3. With a soup spoon, remove small amounts at a time and put into a nice serving bowl. Put loquat ring on top of each portion, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DABADF61883E7845A1F6092E1B5EC825', X'EB7240568678844C86F1321CCB349E40', X'EB7240568678844C86F1321CCB349E40', 0, 'javascript:', 'Almond Cookies', 2, NULL, 0, 'Almond Cookies', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1588', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3/4 cup plus 1 Tablespoon all purpose flour<BR>7 Tablespoons room-temperature lard<BR>7 Tablespoons brown sugar<BR>1/4 teaspoon baking soda<BR>1/4 teaspoon baking powder<BR>1 beaten egg, in three equal parts<BR>1/4 teaspoon almond extract<BR>24 whole almonds, blanched<BR><B><I>Preparation:</B></I><BR>1. Mix all the flour and lard, then add the brown sugar, baking soda, and baking powder, and two-thirds of the beaten egg, and knead lightly just until everything is well combined. Then add the almond extract and knead gently until soft but smooth.<BR>2. Roll into 24 small balls, put a thumb print into each one, and press an almond firmly into each depression. Then gently flatten each cookie.<BR>3. Put the cookies on a baking sheet, and using the remaining beaten egg, brush each cookie twice with this egg wash.<BR>4. Bake the cookies in a pre-heated 350 degree F oven for eighteen minutes until the cookie is a deep tan and has crackles all over it. Remove and cool on a cookie sheet, and when at room temperature, serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DBD963C19072A64DABCEBAF69BD1B156', X'233453CBE71F9F438D776E965B184C8C', X'233453CBE71F9F438D776E965B184C8C', 0, 'javascript:', 'Apple Pancakes with Bean Paste I', 2, NULL, 0, 'Apple Pancakes with Bean Paste I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1374', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 apples<BR>4 ounces canned or freshly made red bean paste<BR>1 Tablespoon cornstarch<BR>2 eggs<BR>2 Tablespoons oil<BR><B><I>Preparation:</B></I><BR>1. Peel and core the apples, and cut them into quarters. Make a slit partway into the rounded side of the apple, and fill with about a tablespoon of te bean paste.<BR>2. Mix cornstarch and the eggs, and dip the stuffed apple pieces into the batter and pan fry them on one side, then the other (or they can be deep fired using more oil) until golden on one side then the other. Remove from the oil and drain. Repeat until all stuffed apple quarters are fried, then put them on a platter and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E30163FA051480438E2665D0829385BE', X'44FFE8DFA47F624C8564618F947C0251', X'44FFE8DFA47F624C8564618F947C0251', 0, 'javascript:', 'Apple Pancakes with Bean Paste II', 2, NULL, 0, 'Apple Pancakes with Bean Paste II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1478', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 apples<BR>4 ounces canned or freshly made red bean paste<BR>1 Tablespoon cornstarch<BR>2 eggs<BR>2 Tablespoons oil<BR><B><I>Preparation:</B></I><BR>1. Peel and core the apples, and cut them into quarters. Make a slit partway into the rounded side of the apple, and fill with about a tablespoon of te bean paste.<BR>2. Mix cornstarch and the eggs, and dip the stuffed apple pieces into the batter and pan fry them on one side then the other, or they can be deep fried using more oil, until golden on both sides. Remove from the oil and drain. Repeat until all stuffed apple quarters are fried, then put them on a platter and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F9B7454C1B4BB74F8410E1F70624AD07', X'1E9B89DF7BE6B24385ABC41F49795044', X'1E9B89DF7BE6B24385ABC41F49795044', 0, 'javascript:', 'Asian Pear Cakes', 2, NULL, 0, 'Asian Pear Cakes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1044', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Asian pear, peeled and cored<BR>1 egg, beaten<BR>1/2 cup sweet potato or potato flour<BR>3 Tablespoons red bean paste<BR>2 cups vegetable oil<BR>6 Tablespoons sugar<BR>6 <I>goji</I> berries<BR><B><I>Preparation:</B></I><BR>1. Chop pear and mix with beaten egg and the flour. Divide this pear dough into six parts.<BR>2. Divide bean paste into six parts. Wrap each part with the pear dough and flatten it into a cake shape.<BR>3. Heat oil in a wok or pot and deep fry half the cakes until golden, then remove and drain on paper towels, and repeat with the other half of the pear cakes. Next, put the fried pear cakes in a single layer into a heat-proof bowl.<BR>4. Mix sugar with one-quarter cup of boiling water, and pour this over the pear cakes. Put one <I>goji</I> berry in the center of each pear cake. Steam them over boiling water for six minutes, remove, and serve. <P></P></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E929E0668B3C1A4F8D275340FEFC044F', X'4C8C7905656BC342A09A8510815523CA', X'4C8C7905656BC342A09A8510815523CA', 0, 'javascript:', 'Asian Pear with Chuan bei', 2, NULL, 0, 'Asian Pear with Chuan bei', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1043', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon <I>chuan bei</I> or </I>goji</I> berries<BR>2 Asian pears<BR>2 Tablespoons crushed rock sugar<BR><B><I>Preparation:</B></I><BR>1. Crush <I>chuan bei</I> or cut <I>goji</I> berries in half.<BR>2. Cut top off each pear, and core, but not all the way, leave some pear at bottom to hold liquid.<BR>3. Put <I>chuan bei</I> or <I>goji</I> berry pieces and the rock sugar into the pear cavities, and put each pear into as heat-proof bowl.<BR>4. Put the bowls of pears in a steamer basket over simmering water and steam for one hour. Prepare a small plate with a paper napkin, and remove each bowl on to each an individual plate and serve. <P></P></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'18675E7BECFF19449D643D172990E82B', X'3B9E4CC199765340B6B98A8489633646', X'3B9E4CC199765340B6B98A8489633646', 0, 'javascript:', 'Autumn Olive Jam', 2, NULL, 0, 'Autumn Olive Jam', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=106', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>8 cups ripe Autumn olive fruits (yields about four cups juice)<BR>4 cups granulated sugar<BR>1 Tablespoon lemon juice<BR><B><I>Prepreparation:</B></I><BR>1. Rinse berries, and be sure to remove leaves and stems<BR>2. Put whole berries in a large pot and add about half inch of water. Simmer for half an hour or until the individual fruits are soft and the pit can easily be removed.<BR>3. Stir a small amount of fruit through a food mil discarding the pits and setting the juice aside until all fruit is processed this way.<BR>4. Next, seive the mashed fruit through a fine strainer. You will need a large spoon to press out all the juice.<BR><B><I>Preparation:</B></I><BR>1. Measure four cups of juice and put it in a heavy pot that has a thick bottom. Then add an equal amount of sugar and the lemon juice.<BR>2. Simmer on low heat until the mixture thickens; this takes about one hour or more. Skim off any foam as it is cooking.<BR>3. When at the desired consistency, pour into strilized jars and let cool. Use a funnel or you''ll need to wipe any jam that drips on the edges or sides of the jars. Seal the jars and cool out of a draft.<BR>4. If using commercial canning/jam jars, you should do the above filling process following their guidelines; if you do, the jam is shelf stable. If you do not have or want to use such equipemtn, refrigerate the jam when cool.<BR><B>Note:</B> The juice can be frozen for later use, though I don''t.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'49BD6B1FEC94E5478DB8292145058534', X'6D58C1A017ECA5468762B0271248EF52', X'6D58C1A017ECA5468762B0271248EF52', 0, 'javascript:', 'Banana Gelatin', 2, NULL, 0, 'Banana Gelatin', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2313', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup agar-agar softened in one cup of cool water<BR>½ cup sugar<BR>1 cup evaporated milk<BR>1 teaspoon banana extract<BR>three drops of yellow food coloring<BR><B><I>Preparation:</B></I><BR>1. Bring three cups of cold water to the boil, add the agar-agar and the sugar and stir until it thickens.<BR>2. Add the evaporated milk, the extract, and the food coloring and take the an away from the heat and pour it into a shallow square or rectangular pan, cover, and refrigerate.<BR>3. Cut the gelatin into squares, and serve alone or with preserved fruits between courses or at the end of a meal.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6D54036AE84B7F45B17CC62373D6FBFD', X'B611010E75716646AEB5A0E552350453', X'B611010E75716646AEB5A0E552350453', 0, 'javascript:', 'Banana Pancakes', 2, NULL, 0, 'Banana Pancakes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=320', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>5 Tablespoons flour<BR>1/4 cup milk<BR>2 ripe bananas, peeled and mashed<BR>1 egg, beaten<BR>2 Tablespoons sugar<BR>4 Tablespoons melted butter<BR><B><I>Preparation:</I></B><BR>1. Mix flour and milk, then add mashed bananas, the beaten egg and the sugar and mix well.<BR>2. Heat wok or fry pan and add one quarter of the butter.<BR>3. Using a quarter-cup measure, fill it with batter and pour it into the pan. Then pour in another quarter of a cup of batter to make two pancakes. Fry both until golden, then turn them over and fry on the other side until golden; then remove and put them on a pre-warmed plate. Repeat until all batter is used.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1180D60934AE9646935DB587390EC31E', X'8BB9918F498ACD418F6E1BA2D1245571', X'8BB9918F498ACD418F6E1BA2D1245571', 0, 'javascript:', 'Bean-paste Stuffed Loquats', 2, NULL, 0, 'Bean-paste Stuffed Loquats', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1039', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>20 canned loquats, drained well<BR>1/2 cup red bean paste<BR>2 Tablespoons sugar<BR>25 whole almonds, blanched their peels discarded, five of them coarsely chopped<BR><I><B>Preparation:</I></B><BR>1. Stuff loquats with red bean paste and insert one whole almond down into each one of them.<BR>2. Dip stuffed loquat end into the chopped almonds and put them in a steamer basket.<BR>3. Steam covered over boiling water for eight minutes. Remove to a bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EB8A2080CD3241498A7D087FF20C2747', X'739C41DF5069B247BF0DB90EAFEA1522', X'739C41DF5069B247BF0DB90EAFEA1522', 0, 'javascript:', 'Beijing Dust', 2, NULL, 0, 'Beijing Dust', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1652', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound fresh chestnuts, scored and put into a pot with enough water to cover them<BR>6 Tablespoons sugar<BR>1 teaspoon vanilla extract<BR>2 cups heavy cream, whipped with two tablespoons confectioners sugar, reserving about one-quarter of this for decor<BR>4 Tablespoons chopped candied maraschino cherries<BR><B><I>Preparation:</B></I><BR>1. Bring chestnuts to a boil and cook over medium heat for fifty minutes then drain them and allow to cool somewhat then remove their shells and paper skins.<BR>2. Put the chestnuts, sugar, and the vanilla extract in a food processor and process for half a minute; but do not purée them.<BR>3. Divide the chestnuts into eight dessert dishes and spoon some whipped heavy cream over them.<BR>4. Decorate the chestnuts with a whole or two halves of the cherries and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C37402F3B41C95479C7B45C70108CBF3', X'C04B89B96D140F469E78B0135A305B0C', X'C04B89B96D140F469E78B0135A305B0C', 0, 'javascript:', 'Bird''s Nest with Rock Sugar I', 2, NULL, 0, 'Bird''s Nest with Rock Sugar I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=76', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 ounce refined bird''s nest<BR>1/2 cup rock sugar<BR><B><I>Preparation:</I></B><BR>1. Cover the bird''s nest with one quart of luke-warm water and let it stand for one to two hours until the nest is fully expanded. Then drain off the water.<BR>2. Place the bird''s nest in a covered porcelain bowl and add one quart of water. Steam for one and a half hours.<BR>3. Add rock sugar and steam for another ten minutes. Serve hot or cold, but do not use ice cubes to cool it.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'954A9FCDF363D143AC2CB0476E919291', X'F8697348EC4DC648BE3AEB16BF29F29F', X'F8697348EC4DC648BE3AEB16BF29F29F', 0, 'javascript:', 'Black Date and Walnut Squares', 2, NULL, 0, 'Black Date and Walnut Squares', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1772', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup Chinese black dates<BR>1/2 cup small walnut pieces<BR>3 Tablespoons white rock sugar<BR>3 Tablespoons maltose syrup<BR>1 Tablespoon vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Soak black dates in two cups of boiling water for five minutes, then remove their skins and pits.<BR>2. Put the dates in heat-proof bowl in a 250 degree oven and bake them for half an hour. Next remove them from the oven and cool them on a flat plate.<BR>3. Soak walnuts in boiling water for half an hour, then bake them in the same oven for fifteen minutes.<BR>4. Put dates and three tablespoons of warm water in a small pot, add the rock sugar, maltose, and the dates and simmer stirring until this thickens. Then stir in the walnuts and pour this mixture into a 8x8 inch tray and press evenly. Allow to cool before cutting it into squares. Put them on a lightly greased plate and set them aside for at least an hour before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D994CDD0B0BFA44A914D072368BE5C7F', X'33E693DA8F5D014F94FCC966449BEB13', X'33E693DA8F5D014F94FCC966449BEB13', 0, 'javascript:', 'Boortsog', 2, NULL, 0, 'Boortsog', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1893', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup granulated sugar<BR>1/4 teaspoon coarse salt<BR>3 Tablespoons butter<BR>2 to 3 cups all-purpose flour<BR>2 cups vegetable oil for frying<BR><B><I>Preparation:</B></I><BR>1. Mix sugar, salt, and butter with several tablespoons boiling water, and mix well until the sugar and salt are dissolved, the butter, too.<BR>2. Mix in most of the flour and knead for ten minutes or more, adding more flour as needed, until the dough is soft and smooth, and has no air bubbles.<BR>3. Roll the dough out until close to one inch thick, and cut into two-inch squares. Cut an ''X'' in each one about halfway but not all the way through the dough, then deep fry until golden and set aside on paper towels. <BR>4. Cut a slice in each one, stuff it with some <I>bysaglag</I> or a slice of meat and then eat it is ready to be eaten.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AC94E7499B6E18468E5D9B2984FA6EAD', X'145A5D1EEBF98743BEA42E3A9B49FE6D', X'145A5D1EEBF98743BEA42E3A9B49FE6D', 0, 'javascript:', 'Bread Crumbs in Sour Cream and Sugar (Mïkchïma)', 2, NULL, 0, 'Bread Crumbs in Sour Cream and Sugar (Mïkchïma)', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=529', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>3 cups fine bread crumbs<BR>2 cups sour cream<BR>1 cup granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Pour bread crumbs into a bowl.<BR>2. Add the granulated sugar and sour cream and mix thoroughly. Serve chilled.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FBF4D7242B8315439CF786021BD8D3D5', X'91DBE066BA59734283D217DD0B607F9F', X'91DBE066BA59734283D217DD0B607F9F', 0, 'javascript:', 'Candied Apples', 2, NULL, 0, 'Candied Apples', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2310', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup sugar or ½ cup honey<BR>1/4 cup peanut oil<BR>1 cup four<BR>1 egg, beaten well<BR>2 crisp apples, peeled and cored, and cut into half-inch slices<BR>1 cup vegetable oil<BR>2 teaspoons peanut oil, one brushed on a platter, the other on a bowl<BR><B><I>Preparation:</B></I><BR>1. Heat sugar or honey with the peanut oil until very hot and syrup-like; then set aside until needed.<BR>2. Mix flour with the egg and set this batter aside.<BR>3. Deep fry the apple slices until they are golden in color, then mix them with the hot syrup.<BR>4. Brush peanut oil on a platter to prevent apple slices from sticking and put them on that platter. Then dip them a few at a time into ice water until the syrup hardens, and then put them in the bowl and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8008095D4F8D54418213A1BEB6731D08', X'5E8D590C75B03544BF0F1F250319D666', X'5E8D590C75B03544BF0F1F250319D666', 0, 'javascript:', 'Candied Dates', 2, NULL, 0, 'Candied Dates', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=824', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds fresh or dried Chinese dates<BR>1 Tablespoon coarse salt<BR>1 and 1/2 pounds Chinese brown slab sugar, crushed<BR>1 perilla leaf<BR>3 slices licorice root<BR><B><I>Preparation:</B></I><BR>1. Rinse dates, soak for one hour in cool water, then drain.<BR>2. Add salt to the jujubes, mix well, then soak them again in six quarts of room-temperature water for twenty-four to thirty-six hours. Drain and rinse under cold running water for five minutes. Drain well.<BR>3. Prepare a two quart or larger sterilized jar. Put in a layer of the jujubes, cover with a layer of sugar, another layer of the jujubes, etc. alternating until all are in the jar. Top with the perilla leaf. Cover with several pieces of cheesecloth held in place with a rubber band. Set aside in a cool dark place for two months.<BR>4. Discard the leaf, and set the jujubes out on a wire rack to air dry, turning once or twice. This can take a day or two. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune1 recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B9E65A06B91D33439057768399FDAD0F', X'191446B1B7BFD64CA5767B40D6CBFDA1', X'191446B1B7BFD64CA5767B40D6CBFDA1', 0, 'javascript:', 'Cherry Compote', 2, NULL, 0, 'Cherry Compote', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2376', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups cherries, each one cut in half, their pits removed and discarded<BR>1 cup lychee fruit, peel and discarded their pits and skins, and cut each one in quarters<BR>½ cup granulated sugar<BR>2 Tablespoons Mao Tai liquor<BR>1 star anise<BR>4 whole cloves<BR>½ cup orange juice or tangerine juice<BR><B><I>Preparation:</B></I><BR>1. Coarsely mash half cup of the cherries and mix with lychee fruit and sugar, and set this aside for half an hour to macerate.<BR>2. Add the rest of the ingredients, mix well, then cover and refrigerate for an hour or until ready to serve. Before serving, remove the star anise and the cloves. They can be used for future batches of compote, then serve.<BR></TD>');
