INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2BEF894E67A331489DACE65011CEE718', X'70EB3CED353D0F4C94E3E91F20633E06', X'70EB3CED353D0F4C94E3E91F20633E06', 0, 'javascript:', 'Xiamen Fried Noodles', 2, NULL, 0, 'Xiamen Fried Noodles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2095', '<TD><RECIPE><I><B>Ingredients</B></I><BR>1 pound fresh thin wheat-flour noodles<BR>2 Tablespoons vegetable oil, separated into two parts<BR>1/4 pound small shrimp, veins removed and discarded, shells reserved<BR>1/8 pound belly pork or ground lean pork<BR>½ cup slivered bamboo shoots<BR>1 baby carrot, slivered<BR>2 large dried black mushrooms, soaked, their caps slivered<BR>1 scallion, green part only, slivered<BR>2 large garlic cloves, peel discarded, the cloves slivered<BR>1 teaspoon ground white pepper<BR>1 teaspoon coarse salt<BR>3 Tablespoons Chinese rice wine<BR>1 Tablespoon soy sauce<BR><B><I>Preparation:</B></I><BR>1. Boil the noodles until barely tender, drain, and set them aside tossed with one tablespoon of the oil.<BR>2. Simmer the shrimp shells in half cup of water for twenty minutes, then remove and discard the shells.<BR>3. Heat wok or fry pan, add the other half of the oil, and fry the pork, the slivered bamboo shoots, carrot, mushroom slivers, scallions, and garlic until almost but not quite crisp, then add the shrimp, pepper, salt, rice wine, soy sauce, and the remaining shrimp water. Stir-fry this for three minutes.<BR>4. Mix the above mixture with the wheat-flour noodles tossing well, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F6A4A7BD7DB419479DB4546274BE7F5A', X'10458FB09AD01E45BF9198D6DFE1B550', X'10458FB09AD01E45BF9198D6DFE1B550', 0, 'javascript:', 'Yangzhou Fried Rice', 2, NULL, 0, 'Yangzhou Fried Rice', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2159', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 dried black mushrooms, soaked, stems discarded, caps diced<BR>2 eggs, beaten<BR>4 ounces roast pork, coarsely diced<BR>4 cups cooked cold rice<BR>1 Tablespoon Chinese rice wine<BR>1/4 pound fresh shrimp, peeled, their shells and veins discarded<BR>½ chicken breast, cooked and diced<BR>½ cup bamboo shoots diced<BR>½ cup frozen peas<BR>3 Tablespoons vegetable oil<BR>2 Tablespoons thin soy sauce<BR>3 scallions, coarsely chopped<BR>salt and pepper to taste<BR><B><I>Preparation:</B></I><BR>1. Mix black mushrooms, eggs, roast pork, cold rice, rice wine, shrimp, chicken, bamboos shoots, and the peas.<BR>2. Heat wok or fry pan, add the oil, and when hot, add the rice mixture and stir-fry for two minutes, then add the soy sauce, scallions, and the salt and pepper, and stir-fry until there are no lumps in the rice; about two to three minutes Serve hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3EE667F729F98940BA74EB66B87FEC9C', X'B51E658F9BA1BB4A91B785D3A791EDE7', X'B51E658F9BA1BB4A91B785D3A791EDE7', 0, 'javascript:', 'Yangzhou Fried Rice', 2, NULL, 0, 'Yangzhou Fried Rice', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2263', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 dried black mushrooms, soaked, stems discarded and diced<BR>2 eggs, beaten<BR>4 ounces roast pork, coarsely diced<BR>4 cups cooked cold rice<BR>1 Tablespoon Chinese rice wine<BR>1/4 pound fresh shrimp, peeled, their veins removed and discarded<BR>½ chicken breast, cooked and diced<BR>½ cup bamboo shoots diced<BR>½ cup frozen peas<BR>3 Tablespoons vegetable oil<BR>2 Tablespoons thin soy sauce<BR>3 scallions, coarsely chopped<BR>salt and pepper to taste<BR><B><I>Preparation:</B></I><BR>1. Mix black mushrooms, eggs, roast pork, cold rice, rice wine, shrimp, chicken, bamboos shoots, and the peas.<BR>2. Heat a wok or fry pan, add the oil and when it is hot, add the rice mixture and stir-fry for two minutes, then add soy sauce, scallions, and the salt and pepper, and stir-fry until there are no lumps in the rice; about two to three minutes, stirring well. Then serve hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AC3FE1A34ECA30408AC35EA21388B034', X'47FECDDCE1FDC4489CF99057382C6E2E', X'47FECDDCE1FDC4489CF99057382C6E2E', 0, 'javascript:', 'Yangzhou Fried Rice I', 2, NULL, 0, 'Yangzhou Fried Rice I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=186', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 Tablespoons chopped cooked shrimp<BR>1 Tablespoon minced Smithfield ham<BR>1 teaspoon rice flour<BR>2 Tablespoons corn oil<BR>1 scallion, minced<BR>1 slice fresh ginger, minced<BR>1 clove garlic, minced<BR>2 cups cooked rice, that is at room temperature or cold<BR>1 teaspoon sesame oil<BR><I><B>Preparation:</I></B><BR>1. Mix shrimp, ham, and rice flour.<BR>2. Heat oil in a wok and stir-fry scallion, ginger, and garlic just until fragrant. Then add the shrimp-ham mixture and stir-fry it one minute.<BR>3. Add the rice to this and stir-fry until heated through'' then add the sesame oil, toss and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A9B988CE49C88447ADF0C25443B8DB0B', X'C97CA86B922EA947B32B10FE4ADB1489', X'C97CA86B922EA947B32B10FE4ADB1489', 0, 'javascript:', 'Yangzhou Fried Rice II', 2, NULL, 0, 'Yangzhou Fried Rice II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=557', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 Chinese dried black mushrooms<BR>1 cup frozen peas<BR>3 Tablespoons peanut oil<BR>1 cup bamboo shoots, diced<BR>1 scallion, chopped<BR>2 eggs, well-beaten<BR>1 Chinese sausage, diced<BR>4 cups cold cooked rice<BR>1 cup shrimp, diced<BR>1 and 1/2 Tablespoons soy sauce<BR>1 cup roast pork or cooked ham, diced<BR>1/4 teaspoon salt<BR>1/8 teaspoon ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Rinse mushrooms, then cover them with warm water and soak for half an hour or until soft; then dice them.<BR>2. Heat two tablespoons of oil in a wok. Then add the scallion and stir-fry a few seconds, then add the sausage and shrimp and stir-fry for thirty seconds more before adding the meat, peas, shoots, and mushrooms. Stir-fry this mixture for two or three minutes, then remove from the pan and set it aside.<BR>3. Heat the remaining tablespoon of oil in the wok, then add the eggs and scramble them. Stir in the rice and heat thoroughly. Add the meat and vegetable mixture, then the seasonings, and mix well. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CC8B4D2720275A46B8DC1EBC18554F59', X'CBAFB9E137823F41A401BDCC7B03BC30', X'CBAFB9E137823F41A401BDCC7B03BC30', 0, 'javascript:', 'Yangzhou Fried Rice III', 2, NULL, 0, 'Yangzhou Fried Rice III', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1223', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons oil, divided<BR>4 ounces small shrimp, peeled and veins removed<BR>2 eggs<BR>2 bowls cooked rice<BR>2 ounces diced ham<BR>2 ounces BBQ pork, diced<BR>1 scallion, minced<BR>1 Tablespoon soy sauce<BR><B><I>Preparation:</B></I><BR>1. Heat wok, add one tablespoon oil and add shrimp quickly frying them until pink, then scoop them out.<BR>2. In a clean wok, add one tablespoon oil and scramble the eggs, then set them aside.<BR>3. Clean wok and re heat it. Add last tablespoon oil, the ham, pork, scallion, shrimp,and the rice and quickly stir-fry until aromatic and hot. Add soy sauce to taste, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'51FE83FBDAD1BF4F8ED72CA854577381', X'3C6A607CBF9B124984131D2F767D98CE', X'3C6A607CBF9B124984131D2F767D98CE', 0, 'javascript:', 'Young''s Yin Yang Rice Shang Palace Style', 2, NULL, 0, 'Young''s Yin Yang Rice Shang Palace Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=701', '<TD><RECIPE><B></I>Ingredients:</B></I><BR>1 Tablespoon dried pitted wolfberries<BR>3 Tablespoons corn oil<BR>8 fresh shiitake mushrooms, stemmed and thinly sliced<BR>1/4 cup chicken broth<BR>1 and 1/4 teaspoons salt<BR>1/4 teaspoon ground white pepper<BR>2 cups cold cooked black rice<BR>1/4 cup minced scallions, green part only<BR>1/4 cup minced scallions, white part only<BR>2 cups cold cooked long-grain white rice<BR>1/3 cup minced celery<BR>1 large egg white, beaten<BR>4 ounces lump crabmeat, cartilage removed<BR><B><I>Preparation:</B></I><BR>1. Put wolfberries in bowl, cover with cold water, and set aside for five minutes, then drain.<BR>2. Heat wok over high heat until a drop of water sizzles and vaporizes almost immediately then swirl in one tablespoon of the oil and add the mushrooms. Stir-fry for half minute then add the broth and cook for another half minute.<BR>3. Stir in one-quarter teaspoon of the salt and half the pepper, then transfer this to a plate. Then wash and dry the wok.<BR>4. Reheat the wok as done before, then add one tablespoon of the oil and the black rice. Stir-fry one minute breaking up the rice. Then add scallion greens, half the mushrooms, the wolfberries, and half teaspoon of the salt. Fry one minute and then transfer to a plate. Wash and dry the wok.<BR>5.Reheat the wok as before, then add the remaining tablespoon of oil, and then the white rice and the celery breaking up the rice. Stir-fry for one to two minutes until the rice is heated through before adding egg white, crab meat, the white part of the scallions, and the remaining mushrooms, half teaspoon of salt, and the pepper. Stir-fry just fifteen seconds. Remove from the heat and continue stirring for another half minute.<BR>6. Put white rice mixture on one side of a platter, black rice mixture on the other side, and take one tablespoon of each rice mixture and ut it on top of the other rice side, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FC97EBF7601A424C8A219A6B3F4947D7', X'4AED52E67B79DA49ACF7F15D46F03EC2', X'4AED52E67B79DA49ACF7F15D46F03EC2', 0, 'javascript:', 'Zhejiang Mein', 2, NULL, 0, 'Zhejiang Mein', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=976', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup chicken stock<BR>3 Tablespoons hoisin sauce<BR>2 Tablespoons shaoxing rice wine<BR>1 Tablespoon chili bean sauce<BR>1 Tablespoon peanut oil<BR>2 garlic cloves, peeled, crushed, and finely chopped<BR>12 ounces ground beef<BR>7 ounces thin rice noodles<BR>1 scallion, finely minced<BR>1 small handful of cilantro leaves, finely minced<BR>1 small carrot, sliced into thin long strips<BR><B><I>Preparation:</B></I><BR>1. Mix chicken stock, hoisin sauce, rice wine, and chili bean sauce, and set aside.<BR>2. Heat wok, add the oil, then the garlic and stir-fry for one minute until lightly browned, then add the beef and stir-fry for one minute.<BR>3. Add chicken stock mixture, bring to the boil, and cook for about four minutes or until the sauce reduces to less than half the amount; then transfer it into a clean bowl, cover with aluminum foil to keep it warm.<BR>4. Cook the noodles according to their package instructions, drain, and put them into two bowls. Spoon half the meat mixture over each batch of noodles, then sprinkle with scallions, cilantro, and carrot strips, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B547519B0E21BE40B3BD9F1FFBF69244', X'172C3D978B2EBB4F9F6D783F1ACA6CF3', X'172C3D978B2EBB4F9F6D783F1ACA6CF3', 0, 'javascript:', 'Zodiac Noodles', 2, NULL, 0, 'Zodiac Noodles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1016', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 ounces rice noodles<BR>3 teaspoons vegetable oil, divided in teaspoon amounts<BR>8 whole chili peppers<BR>3 ounces pork loin, cut into thin strips<BR>1 teaspoon garlic, minced<BR>1 and 1/2 ounces Yunnan ham (or use a Smithfield or Serrano ham) cut into strips<BR>2 scallions, cut into two- to three-inch pieces<BR>1/4 cup sliced cabbage, cut into thin strips<BR>1/4 cup Chinese black mushrooms, soaked, stems discarded, and cut into strips<BR>1/8 teaspoon salt<BR>2 Tablespoons soy sauce<BR>1 teaspoon oyster sauce<BR>1 teaspoon sugar<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Soak Vermicelli in very hot water for one hour.<BR>2. Bring a pot of water to the boil, add noodles, boil them for one minute, then drain and rinse under warm water for one minute before draining them well. 2. Mix noodles with one teaspoon of the vegetable oil.<BR>3. Heat a wok or fry pan over high heat, then add the rest of the vegetable oil, then add the chili peppers and the pork, and stir-fry until the pork is almost done.<BR>4. Add the garlic, ham, scallion pieces, cabbage and mushrooms, and stir-fry for ten ot fifteen seconds before adding the drained noodles, salt, soy and oyster sauces, sugar and sesame oil. Stir-fry until all ingredients are mixed well. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5674CC1BB28994478E442EFC29FC2F37', X'CFBA913CF40839428BD76A47814B562E', X'CFBA913CF40839428BD76A47814B562E', 0, 'javascript:', 'Agar Agar Salad', 2, NULL, 0, 'Agar Agar Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=13', '<TD><RECIPE><I></B>Ingredients:</I></B><BR>4 cups of two and one-half-inch agar agar strips<BR>1 large cucumber<BR>1/2 to 1 cup baked Virginia ham<BR>2 Tablesppons sugar<BR>3 Tablespoons cider vinegar<BR>2 to 3 teaspoons sesame oil<BR>1/2 teaspoon salt<BR>1/2 teaspoon prepared mustard (optional)<BR><I><B>Preparation:</I></B><BR>1. Wash agar agar with cold water until well separated, then rinse under cold water; drain, and squeeze out excess water.<BR>2. Cut cucumber lengthwise, use a spoon to scoop out and discard the seeds, and then cut diagonally into two and a half inch strips.<BR>3. Cut ham into two and a half inch strips.<BR>4. Combine agar agar, cucumber and ham in a colander, toss well, then put in a bowl.<BR>5. Mix the rest of the ingredients, pour over the mixture; mix and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B37257B8EA4BCD42B6E1D7B190397E0D', X'1C48E74AC9FE504FBC3B691E293E8370', X'1C48E74AC9FE504FBC3B691E293E8370', 0, 'javascript:', 'Bao Cai', 2, NULL, 0, 'Bao Cai', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=530', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 head of cabbage, trimmed and cut into two-inch squares<BR>1/2 large daikon, peeled and sliced<BR>1/4 large carrot, peeled and sliced<BR>3 Tablespoons sea salt or kosher salt<BR>1/2 cup sugar<BR>1/2 cup rice-wine vinegar<BR>2 slices fresh peeled ginger<BR><B><I>Preparation for the simplified method:</B></I><BR>1. Mix all three vegetables and sprinkle them with one tablespoon of salt. Stir well and allow to rest for half an hour, then squeeze the vegetables to remove any excess water. 2. Add the rest of the ingredients and a cup of cold boiled water, then refrigerate for one or two days, drain and serve.<BR><B><I>Preparation when reusing the liquid one or two more times:</I></B><BR>1. Follow step one above.<BR>2. Boil one cup of water, remove from heat and stir in remaining sugar and salt. Allow to cool and add rice vinegar<BR>3. Put prepared vegetables in a two-quart jar, Pour liquid over them and cover. Set aside in a cool dark place overnight. Refrigerate the next day; where it can stay for up to two or three days.<BR>4. When reusing the liquid for a new batch of prepared vegetables, pour reserved liquid over them and keep the pickles refrigerated at all times.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7A666E7DC3F4384A9FB93B0A3293D8F7', X'F093D64F58B2D548BBC9382E04BFB379', X'F093D64F58B2D548BBC9382E04BFB379', 0, 'javascript:', 'Beijing Salad', 2, NULL, 0, 'Beijing Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=12', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>4 cups green cabbage, cut bite size<BR>2 cups small cauliflower florets<BR>1 cup thinly sliced carrot<BR>2 Tablespoons fresh ginger, shredded<BR>1 teaspoon salt<BR>3 Tablespoons cider vinegar<BR>2 Tablespoons sugar<BR>2 or 3 Tablespoons sesame oil<BR>1/2 teaspoon crushed red pepper (optional)<BR><B><I>Preparation:</I></B><BR>1. Combine cabbage, cauliflower, carrots, and ginger in a colander and evenly sprinkle salt over them and toss well.<BR>2. Leave the vegetables in the colander for three hours tossing them periodically.<BR>3. Place vegetables in a bowl and add sugar, vinegar, sesame oil, (and any optional ingredients) and mix well. Marinate overnight in the refrigerator. <BR>4. Stir everything well and serve cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E3377A66C664A34F8D77583D2BB1EEA9', X'D4F290D545D28246B872F9CA88BF9C1A', X'D4F290D545D28246B872F9CA88BF9C1A', 0, 'javascript:', 'Chilled Seaweed', 2, NULL, 0, 'Chilled Seaweed', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1173', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 sheets dird black seaweed<BR>1 sheet dried green seaweed<BR>1 Tablespoon peeled shredded green onion<BR>2 Tablespoons prepared pink ginger, slivered<BR>1 clove garlic, peeled and crushed<BR>1 small dried hot red pepper, seeded and slivered<BR>1 Tablespoon granulated sugar<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Soak sheets of seaweed in warm water for fifteen minutes. Drain, shred, and mix them with all the other ingredients. Refrigerate for two or more hours, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'11BE54EBACDA224FAD153F435CDFCD9E', X'FE308CFB6E48BF4892DE4097A8D2C6AA', X'FE308CFB6E48BF4892DE4097A8D2C6AA', 0, 'javascript:', 'Chinese Potato Salad', 2, NULL, 0, 'Chinese Potato Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=570', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 potatoes, about one pound<BR>2 eggs<BR>3 Tablespoons mayonnaise<BR>1 and 1 half Tablespoons soy sauce<BR>1 and a half teaspoons chopped cilantro leaves<BR>3/4 teaspoon hot mustard sauce (See note below)<BR>3/4 teaspoon sugar<BR>1/4 teaspoon plus a few drops sesame oil<BR>1 cup shredded napa cabbage<BR>1/3 cup chopped red onion<BR><B><I>Preparation:</B></I><BR>1. Boil potatoes until almost soft, then peel them bite-sized squares.<BR>2. Hard cook the eggs, about fifteen minutes, and peel and slice them. Peel and slice the eggs.<BR>3. Mix mayonnaise, soy sauce, cilantro, mustard sauce, and sugar then stir in sesame oil.<BR>4. Mix potatoes, eggs, cabbage, and red onion then mix in the mayonnaise mixture. Serve or keep in a sealed container until ready to do so.<BR><B>Note:</B> For the Hot Mustard Sauce, mix one teaspoon dry mustard powder, one teaspoon rice vinegar, one-quarter teaspoon brown sugar, one teaspoon corn oil, and three drops of sesame oil. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'63717D508F7B4F40B63866ED1AD1B7D1', X'5691E762371AF8449CCF864B2D55E7F1', X'5691E762371AF8449CCF864B2D55E7F1', 0, 'javascript:', 'Chinese-style Kimchi', 2, NULL, 0, 'Chinese-style Kimchi', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=532', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound napa cabbage, cut into half-inch slices<BR>1 Tablespoon salt<BR>1 scallion, minced<BR>1 clove garlic, minced<BR>4 Tablespoons thin soy sauce<BR>1 teaspoon sugar<BR>1 teaspoon chili flakes<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix cabbage and salt and set aside overnight. Then rinse and squeeze dry.<BR>2. Mix all the other ingredients and put into a glass jar or covered bowl and refrigerate for two days before using.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0EC7B95896EAAD4AA6102B885FAF3503', X'C9DB66A45D758147BE9CEAC04C1C23CD', X'C9DB66A45D758147BE9CEAC04C1C23CD', 0, 'javascript:', 'Cold Eggplant', 2, NULL, 0, 'Cold Eggplant', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1176', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 thin eggplants, peeled and shredded, and tossed with two tablespoons of red wine vinegar<BR>3 Tablespoons thin soy sauce<BR>2 Tablespoons granulated sugar<BR>1 Tablespoon vegetable oil<BR>2 large cloves garlic, peeled and finely minced<BR>1 Tablespoon fresh ginger, peeled and finely minced<BR>2 teaspoons Shaoxing wine or dry sherry<BR>2 Tablespoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Steam eggplant over rapidly boiling water for twenty minutes, then drain and squeeze out any excess water and vinegar.<BR>2. Mix soy sauce and sugar. Heat the oil and stir-fry the garlic and ginger for one minute, then add soy sauce mixture, the wine and the sesame oil. Bring to the boil. Add the shredded eggplant, refrigerate or serve at room temperature.<BR>3. Refrigerate for two or more hours, drain, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'221CC0439B421B4CB779B96A0C72BBF6', X'E293994FBB98024EB1A4E5B226228899', X'E293994FBB98024EB1A4E5B226228899', 0, 'javascript:', 'Crispy-skin Chicken with BlackVinegar and Shallot', 2, NULL, 0, 'Crispy-skin Chicken with BlackVinegar and Shallot', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=567', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 cups Chinese rice wine<BR>6 shallots, peeled and trimmed<BR>12 cloves garlic, peeled and smashed<BR>1 and 1/2 cups ginger slivers<BR>4 Tablespoons sea salt<BR>1 three to four pound chicken<BR>1 quart corn oil<BR>2 Tablespoons peanut oil<BR>2 Tablespoons finely chopped scallion tops<BR>1/8 teaspoon ground Sichuan pepper<BR>1/8 teaspoon salt<BR><B><I>For the sauce:</B></I><BR>1/3 cup finely sliced shallots<BR>1/3 cup finely sliced ginger<BR>1/3 cup finely sliced fresh coriander stems<BR>1/3 cup <I>kecap manis</I><BR>1/3 cup thin soy sauce<BR>1/3 cup black vinegar<BR>1/2 teaspoon chili oil<BR>1/4 cup sesame oil<BR><B><I>Preparation:</B></I><BR>1. Put six cups of water into a very large pot and add wine, shallots, garlic, ginger slivers, and sea salt. Bring to the boil and quickly lower the heat and simmer this stock for fifteen minutes.<BR>2. Put chicken in the stock and simmer fourteen minutes, then remove pot from the heat and allow chicken to sit in it for one hour, then drain and refrigerate the chicken, very lightly covered for two hours or overnight to dry the skin. Cut chicken in half.<BR>3. Heat the oil and lower one half chicken, breast side down. Into the oil. Deep fry it for five minutes, spooning oil over any part not submerged in oil. Drain on paper towels and fry the second half, also for five minutes. Chop each half into seven or eight pieces and put them on to a deep serving plate.<BR>4. Make sauce by just mixing the ingredients together. Pour this over the fried chicken pieces.<BR>5. Heat the peanut oil in a small pot. Pour this over the chicken, as well. Then garnish with greens, pepper, and salt, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'518C67093B5ACB49AE8874BE70B9420C', X'5B0D3F7AA8279A4D99AFB20EC37808BC', X'5B0D3F7AA8279A4D99AFB20EC37808BC', 0, 'javascript:', 'Crunchy and Tangy Fresh Lotus Root Salad', 2, NULL, 0, 'Crunchy and Tangy Fresh Lotus Root Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=803', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon oil<BR>2 fresh lotus roots, washed, peeled and sliced crosswise<BR>2 scallions, sliced<BR>1/2 teaspoon salt<BR>1 teaspoon sugar<BR>1 Tablespoon Chinese rice vinegar<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan and add the oil, then the lotus root slices, scallions, salt and half of the sugar, and continue to stir-fry for three minutes. Then add half-cup of water and reduce heat, and cover the wok or pan and cook for five minutes. Then remove the vegetables with a slotted spoon to a serving dish.<BR>2. Drizzle with the vinegar and sprinkle the remaining sugar on top, and serve hot or cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E4B09379640F394391A84876CBC09A2D', X'8C00AB6F0FA7BA4AAE7AC793518CA9C3', X'8C00AB6F0FA7BA4AAE7AC793518CA9C3', 0, 'javascript:', 'Cucumber with Chicken Shreds', 2, NULL, 0, 'Cucumber with Chicken Shreds', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1171', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 to 4 cucumbers, peeled, and shredded (removal of seeds, optional)<BR>1 cup cooked white meat chicken, shredded<BR>1 teaspoon to 1 Tablespoon Chinese rice vinegar<BR>1 teaspoon Chinese sesame oil<BR>1 teaspoon granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Mix all ingredients together, and let rest for half an hour or more. Serve at any desired temperature.<BR><B>Note:</B> Blanched, slivered jelly fish can be added.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B3945DB3183A744D81099BAB2BEE301D', X'670163A1DCD5D7439B2AA32B3B552378', X'670163A1DCD5D7439B2AA32B3B552378', 0, 'javascript:', 'Green Papaya Salad', 2, NULL, 0, 'Green Papaya Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=97', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small green papaya, about one pound<BR>2 Tablespoons rice vinegar<BR>2 Tablespoons sugar<BR>1 teapsoon salt<BR><B><I>Preparation:</B></I><BR>1. Peel, seed, and cut papaya into quarters. Then julienne into thin strips<BR>2. Mix it and all ingredients thoroughly and refrigerate four hours or overnight. Serve cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'00839DBCF64AFF4092A351E6152DD38B', X'1AC1D800CACCB540ABF06EBEBB72DFA2', X'1AC1D800CACCB540ABF06EBEBB72DFA2', 0, 'javascript:', 'Guizhou Pickled Cabbage', 2, NULL, 0, 'Guizhou Pickled Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1270', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup coarse non-iodized salt<BR>1/4 cup Chinese brown slab sugar<BR>1/4 cup Maotai or gin<BR>1/4 cup fermented rice, liquid preferred and not the rice<BR>1/4 cup Shaoxing yellow rice wine<BR>1/4 cup dried chili peppers, lightly crushed with side of the cleaver<BR>1 whole nutmeg, partially crushed with handle of a cleaver<BR>3 whole star anise<BR>1 Tablespoon whole Sichuan peppercorns<BR>1 whole white peppercorns<BR>3 pounds Chinese or any leafy cabbage, shredded, and air dried overnight<BR><B><I>Preparation:</B></I><BR>1. Bring six cups of water to the boil and pour it into a clean sterilized jar and add the salt and brown sugar and stir until dissolved, then cool this to lukewarm, add Maotai and the fermented rice liquid, and set aside to come to room temperature.<BR>2. Put chili peppers, nutmeg, star anise, Sichuan peppercorns, and white peppers in a large cloth spice bag and tie it closed. Pour two cups boiling water over it washing the spices, then put in the pickling jar. And stir once or twice.<BR>3. Add the partially air-dried shredded cabbage to the pickling jar, stir a few times, and cover with a plate or something that will keep the cabbage submerged. Set aside outdoors, in the shade, for five to seven days,. Take out the amount of cabbage needed and store the rest in a cool dark place.<BR><B>Note:</B> A second batch of air-dried shredded cabbage can be put into the pickling liquid when all of the previous cabbage has been removed, it will pickle but not be as sour as the first batch.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C5E2EA10C6E9B344A70E964A4D1C9EB1', X'A584AC6A2EF44546BB002148C75B5F35', X'A584AC6A2EF44546BB002148C75B5F35', 0, 'javascript:', 'Iced Bean Curd', 2, NULL, 0, 'Iced Bean Curd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=400', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>3 cakes of soft or firm bean curd<BR><B><I>Preparation:</B></I><BR>1. Freeze the bean curd cakes on pieces of aluminum foil for at least twenty-four, preferably for forty-eight hours.<BR>2. Defrost, then squeeze out as much water as you can. Next, drop them in boiling water for two minutes. remove, and drain. When cool squeeze out the water again, then use them in any recipe that cooks them for at least twenty minutes.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B828467B08CC48498B32986B16031024', X'4C62A33C7029974DB01FE83F3304EAD0', X'4C62A33C7029974DB01FE83F3304EAD0', 0, 'javascript:', 'Iced Bean Curd (from Yuan Mei)', 2, NULL, 0, 'Iced Bean Curd (from Yuan Mei)', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=401', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 defrosted and blanched bean curd cakes<BR>1 Tablespoon corn oil<BR>6 black mushrooms, soaked for half an hour in warm water, stems removed, and diced into quarter-inch pieces<BR>1/2 half cup fresh bamboo shoots, boiled for twenty minutes, then diced into quarter-inch pieces<BR>1/2 cup mixed diced carrots and water chestnuts, diced as above<BR>1 Tablespoon mushroom soy sauce<BR>3 cups strained stock made with at least half cup each of shrimp shells, chicken bones, and pork bones<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</I></B><BR>1. Squeeze out as much water as you can from the bean curd and dice it into one-inch pieces.<BR>2. Heat the oil, then fry the bean curd and the mushrooms for three minutes stirring constantly, then add the vegetables and the soy sauce and stir thoroughly before adding the stock.<BR>3. Bring to just below the boil, then reduce the heat and simmer uncovered on low heat for forty minutes. Be sure to stir it during the last half of the cooking time so that it does not burn or stick to the pan.<BR>4. Add sesame oil, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'77E5E3625CC8284A98FBAAA51BBA5C13', X'A8B38D659E5F6D4ABFC7E621739F867A', X'A8B38D659E5F6D4ABFC7E621739F867A', 0, 'javascript:', 'Jelly Fish and Cucumber Salad', 2, NULL, 0, 'Jelly Fish and Cucumber Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=105', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1/3 pound package of jelly fish<BR>1 cucumber<BR>3 Tablespoons Swatow or other light wine vinegar<BR>3 Tablespoons sugar<BR>1 teaspoon salt<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Sliver the jelly fish and then cut these into two or three inch long pieces. Soak them in warm water for half an hour, then drain well.<BR>2. Peel the cucumber then shred it into pieces equally long as the jelly fish.<BR>3. Mix vinegar, sugar, salt, and sesame oil and toss with the jelly fish and the cucumber shreds. Serve cold.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5F9B5ED82BFAEC4086222A023E3C33DE', X'2EA15ABE471FEA4BB82B1713732F39A9', X'2EA15ABE471FEA4BB82B1713732F39A9', 0, 'javascript:', 'Lotus Root Salad', 2, NULL, 0, 'Lotus Root Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2092', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 ounces lotus root, peeled and thinly sliced in circles<BR>2 teaspoons minced fresh ginger<BR>6 scallions, finely chopped<BR>1 Tablespoon white rice vinegar<BR>salt and freshly ground black pepper<BR>2 teaspoons granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Put lotus root into a large bowl.<BR>2. Add the rest of the ingredients, mix, and set aside for fifteen minutes. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D5721024A0012A4495CA6AD6732EB021', X'320911A2BBD3AE48A39574B08385C7AE', X'320911A2BBD3AE48A39574B08385C7AE', 0, 'javascript:', 'Lychee Roast Duck Salad', 2, NULL, 0, 'Lychee Roast Duck Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=104', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>Half of a Cantonese Roast Dusk, about ten ounces.<BR>1 teaspoon sesame oil<BR>2 teaspoons soy sauce<BR>10 lychees (fresh, peeled and seeded, or canned)<BR>a few spigs cilantro<BR><I><B>Preparation:</B></I><BR>1. Bone the duck and shred it.<BR>2.Mix soy sauce and sesame oil and toss with the duck shreds. Put on a platter.<BR>3. Top with the lychees and garnish with the cilantro, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1CE8B397E5744645B93DEB30E98D63C4', X'D11E7385B2CAB840AC94C8D4E836764D', X'D11E7385B2CAB840AC94C8D4E836764D', 0, 'javascript:', 'Mixed Greens, Chinese Style', 2, NULL, 0, 'Mixed Greens, Chinese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1172', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small head Chinese cabbage, shredded (or any single or mixed greens)<BR>1 cup raw bean spouts, tails removed<BR>1/4 pound green beans, slivered and cut into two-inch lengths<BR>4 scallions, slivered and cut into two-inch lengths<BR>1 Tablespoon Chinese rice vinegar<BR>dash hot oil<BR>1/4 teaspoon sesame paste mixed with two tablespoons of hot tea<BR><B><I>Preparation:</B></I><BR>1. Blanch the Chinese cabbage alone or with other greens. Drain well, and set aside on a large flat serving plate.<BR>2. Put the bean sprouts around the edge of this platter.<BR>3. Blanch the green beans for one minute, drain well, then mix with the scallions, rice vinegar, hot oil, and the sesame paste mixture. Put the bean mixture in the center of the platter of greens. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8BC69E65214DBB4FB5186EF61C7AF595', X'6C85464EC508F6458A55B77129DDEE70', X'6C85464EC508F6458A55B77129DDEE70', 0, 'javascript:', 'Mixed Pickles', 2, NULL, 0, 'Mixed Pickles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1216', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 carrot, peeled and cut into two-inch lengths, then into thin strips<BR>1 large daikon or large white radish, peeled and cut into two-inch squares, then into thin strips<BR>1 Tablespoon coarse salt<BR>2 Tablespoons cloud ear mushrooms, soaked in warm water for twenty minutes, then drained and cut into slivers<BR>1 packet (one ounce) cellophane noodles, soaked in warm water for half an hour, drained, then cut into four-inch pieces<BR>2 stalks celery, cut into two-inch pieces then thin strips<BR>1 teaspoon sugar<BR>1/4 cup thin soy sauce<BR>1/4 cup white Chinese rice vinegar<BR>2 Tablespoons sesame oil<BR>1 Tablespoon dry mustard<BR><B><I>Preparation:</B></I><BR>1. Mix carrot and daikon pieces with salt and set aside for half an hour, then drain and rinse with cold water, and drain again.<BR>2. Bring water to boil and simmer cloud ear mushrooms for ten minutes, drain and set aside.<BR>3. Bring fresh water to boil, add cellophane noodles and simmer for tem minutes. Drain and set aside.<BR>4. Blanch celery for half minute, drain, rinse with cold water and drain again.<BR>5. Mix sugar, soy sauce, vinegar. Sesame oil, and dry mustard stirring until mustard is thoroughly dissolved, then add all vegetables, mushrooms, and cellophane noodles, stir well and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DDE5486EC9F0DB49A1DC18E20C13AEA8', X'E407A7DD1FDE0142AB236F6D857B6A78', X'E407A7DD1FDE0142AB236F6D857B6A78', 0, 'javascript:', 'Pao Cai', 2, NULL, 0, 'Pao Cai', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1214', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 hot red pepper, seeded and minced fine<BR>1 teaspoon sugar<BR>1 teaspoon coarse salt<BR>1/2 cup rice vinegar<BR>1 pound Chinese cabbage, cut into one- by two-inch pieces<BR>1 pound Chinese celery cabbage, cut into one- by two-inch pieces<BR>1/4 cup corn oil<BR><B><I>Preparation:</B></I><BR>1. Mix red pepper, sugar, salt, and vinegar and refrigerate overnight, drain cabbages and reserve vinegar mixture.<BR>2. Heat oil, fry both cabbages for one minute, remove cabbages and set aside for one hour, then drain well.<BR>3. Mix cabbages with vinegar mixture, let rest for an hour, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'106D1C702E10834EB0F5FEB6E691326C', X'9753D388AEA0FF499B5EBE61C50BA2BA', X'9753D388AEA0FF499B5EBE61C50BA2BA', 0, 'javascript:', 'Pickled Cabbage', 2, NULL, 0, 'Pickled Cabbage', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=629', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small head white cabbage; about one and a half pounds<BR>1 Tablespoon coarse salt<BR>4 dried red chili peppers, seeded and crushed<BR>1 teaspoon cumin<BR><B><I>Preparation:</B></I><BR>1. Cut cabbage into one to two inch pieces, then rinse and shake them dry.<BR>2. Toss cabbage pieces with salt and let rest for an hour.<BR>3. Mix peppers and cumin, then mix well with the cabbage.<BR>4. Pack them tightly into a clean jar (washed out just before using with boiling water). Put in a dark place, such as a closet, for five to seven days, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'91CF1C89F8ECCC4F89C8BCFDD7535680', X'7C644C508C2CBD48876E261AD80C24D3', X'7C644C508C2CBD48876E261AD80C24D3', 0, 'javascript:', 'Pickled Cabbage, Sichuan-style', 2, NULL, 0, 'Pickled Cabbage, Sichuan-style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=533', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 head Napa or Savoy Cabbage, trimmed and cut into two-inch squares<BR>3 Tablespoons Kosher or sea salt<BR>1 Tablespoon whole Sichuan peppercorns, roasted and cooled<BR>2 Tablespooons Chinese rice wine or dry sherry<BR>3 dried red chili pappers<BR>2 slices fresh ginger, peeled<BR><B><I>Preparation:</B></I><BR>1. Put cabbage in a large bowl and srinkle with one tablespoon salt. Mix well and allow to rest for one hour. Squeeze cabbage to remove any excess water.<BR>2. Boil two quasrts of water, remove from the heat and dissolve remaining salt and sugar in this water. Then add Sichuan peppercorns and allow to cool. Then add rice wine, chilies, and ginger.<BR>3. Put cabbage in a two-quart jar and pour liquid over it and cover. Set aside in a cool dark place overnight. The refrigerate and serve in a day or two.<BR><B>Note:</B> For those who want to reuse the liquid, bring it to the boil, add two teaspoons salt, and cool. Then add two teaspoons rice wine. Put new batch of vegetables in a cleaned two-quart jar and continue to follow Step 3, above.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3E200D606E28A24D827358955E0D4973', X'79E404A7F10D1A4CA7B4474861088280', X'79E404A7F10D1A4CA7B4474861088280', 0, 'javascript:', 'Pickled Chinese Celery with Dried Shrimp', 2, NULL, 0, 'Pickled Chinese Celery with Dried Shrimp', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1174', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 bunch Chinese celery, cut into half-inch pieces<BR>10 dried shrimp, soaked for one hour in warm water, their legs removed, then finely minced<BR>1 teaspoon coarse salt<BR>1 teaspoon granulated sugar<BR>1 teaspoon Chinese red rice vinegar<BR>2 teaspoons white rice vinegar<BR>3 cloves garlic, peeled and minced<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix celery and soaked minced shrimp with the salt and the sugar. Let rest for half an hour.<BR>2. Add rice vinegars and the minced garlic, cover, and refrigerate for two or more hours.<BR>3. Toss with the sesame oil and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EB8C763C07D51E4DB7F585949DE6FD7E', X'96DDD412D210904E8971614AE8446722', X'96DDD412D210904E8971614AE8446722', 0, 'javascript:', 'Pickled Lotus Root', 2, NULL, 0, 'Pickled Lotus Root', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1319', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 sections lotus root, peeled and cut into thin slices<BR>2 thin slices fresh ginger, peeled<BR>1/2 teaspoon sugar<BR>1/2 teaspoon black vinegar<BR>1/2 teaspoon sesame oil<BR>1 small fresh chili pepper, seeded and cut as a flower<BR>2 leaves crisp lettuce, shredded<BR><B><I>Preparation:</B></I><BR>1. Blanch lotus root slices for one minute, then immerse in ice water until chilled, then drain.<BR>2. Mix all ingredients except the lettuce and the chili pepper. Toss this with the lotus root, and set aside for one hour.<BR>3. Put lettuce and into a lovely small bowl, top with the lotus root, then the chili pepper, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A5BA35FDF104D347AA26F2975FE50CB2', X'F73FF54C8BBAAA4FA4851F606C1D0E38', X'F73FF54C8BBAAA4FA4851F606C1D0E38', 0, 'javascript:', 'Pickled Mixed vegetables', 2, NULL, 0, 'Pickled Mixed vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1557', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons coarse non-iodized salt<BR>1 Tablespoon Sichuan peppercorns<BR>2 small chili peppers<BR>4 slices fresh ginger, peeled<BR>1 Tablespoon clear Chinese rice wine or liquor, gin, or vodka<BR>4 to 8 cups of any combination of peeled broccoli stems, napa cabbage, peeled carrots, cauliflower, string beans, peeled daikon, and red and green peppers, all cut into one- to two-inch pieces, each a half-inch to one-inch thick<BR><B><I>Preparation:</B></I><BR>1. In a very clean two-quart ceramic or glass jar, add one cup of boiling water and the salt and stir until dissolved, then add the peppercorns and the chili peppers. Let this rest half an hour.<BR>2. Boil two quarts of water, and set it aside to cool to room temperature. <BR>3. Add any mixture of prepared vegetables and the ginger and rice wine into the jar in step number one.<BR>4. Pour the cooled water over the vegetables and fill the jar, then cover loosely with plastic wrap and set it in a warm corner of the kitchen. Leave the jar there for one to two days, then serve its contents.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'54B45611CB85E840B94ADFC5DAC03EDD', X'E0CF53A2A6A70E46B03A45E5F208C604', X'E0CF53A2A6A70E46B03A45E5F208C604', 0, 'javascript:', 'Pickled Peaches', 2, NULL, 0, 'Pickled Peaches', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=220', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 pounds fresh peaches (about eight that are hard but with rosy color)<BR>2 cups white rice vinegar<BR>2/3 cup sugar<BR>2 and 1/4 teaspoon salt<BR><I><B>Preparation:</I></B><BR>1. Wash and dry the peaches, do not slice or peel them. <BR>2. In an oversized jar, put the vinegar, sugar and water and add three and three quarters of a cup cold water. Stir well, then add the peaches. Then cover the jar tightly and refrigerate for three days before using.<BR><B>Note:</B> These peaches will keep for at least six months in the refrigerator.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E30C69E7752DE84A916BDD826965ADC9', X'C64B0B56D52FAB4184FACFCB096308F5', X'C64B0B56D52FAB4184FACFCB096308F5', 0, 'javascript:', 'Potato and Pomelo Salad', 2, NULL, 0, 'Potato and Pomelo Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1239', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup potato, peeled and sliced thin<BR>1 small pomelo, peeled, sectioned, and cut in half-inch pieces<BR>1/2 cup cooked shrimp or crabmeat, dice coarsely<BR>1/2 cup cooked chicken breast, cut into half-inch pieces<BR>1/2 cup cucumber cut in half the long way then sliced<BR>3 Tablespoons minced roasted unsalted peanuts<BR>1 Tablespoon minced coriander leaves<BR>1 Tablespoon minced fried shallots<BR>1 teaspoon rice vinegar<BR>2 cloves garlic, peeled and minced<BR>1 small dried red chili, seeded and minced fine<BR>1 Tablespoon <I>sa cha</I> sauce<BR>1 Tablespoon lime juice<BR><B><I>Preparation:</B></I><BR>1. Put potato into a pot of boiling water and boil for ten minutes, then drain and put into a large bowl.<BR>2. Add pieces of pomelo, shrimp or crabmeat, chicken, cucumber, peanuts, coriander leaves, and fried shallots to the potatoes and mix well.<BR>3. In a small bottle, put the rice vinegar, minced garlic, dried chili pieces, sa cha sauce and lime juice, and shake well; then pour over the pomelo-potato mixture and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8E608098AE00A949AE7ADEFE5285E111', X'DD0F67CA514B9C4EBFBC1CE6BD34A93C', X'DD0F67CA514B9C4EBFBC1CE6BD34A93C', 0, 'javascript:', 'Quick-chilled Cucumber Pickle', 2, NULL, 0, 'Quick-chilled Cucumber Pickle', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1215', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 long English cucumber, cut in half lengthwise then into thin slices<BR>2 Tablespoons Chinese sesame paste mixed with two tablespoons hot tea<BR>1/2 teaspoon coarse salt<BR>1 Tablespoon toasted sesame seeds<BR>4 to 5 crushed Sichuan peppers<BR>1/2 teaspoon garlic, minced<BR>1 teaspoon sesame oil<BR>few sprigs of coriander<BR><B><I>Preparation:</B></I><BR>1. Mix all of the ingredients except the coriander sprigs and refrigerate for an hour.<BR>2. Remove from refrigerator, drain, and put on a platter, put coriander sprigs on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7488155B0E26F8498D74815E3418CC03', X'47FF288C226F3942AEAC11DF643A6858', X'47FF288C226F3942AEAC11DF643A6858', 0, 'javascript:', 'Red and Black Dou Miao Salad', 2, NULL, 0, 'Red and Black Dou Miao Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=330', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>4 cups tightly packed, or 4 ounces, small <I>dou miao</I>, torn into several sections, each<BR>1/2 cup thinly sliced red onion, cut into one- to two-inch lengths<BR>1/4 cup finely chopped black olives, preferably oil-cured (about 18 olives)<BR>half a lemon<BR>freshly ground black pepper<BR>2 Tablespoons extra-virgin olive oil<BR><B><I>Preparation:</I></B><BR>1. Toss the <I>dou miao</I>, onion, and olives thoroughly.<BR>2. Drizzle with them lemon juice and pepper, then toss again with the olive oil. This is good both freshly made and after the dou miao wilts from the lemon juice.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A963C34D9A94AE4990AF64E57724B79A', X'A36359C25CC70D46831BEB9E13F85FE7', X'A36359C25CC70D46831BEB9E13F85FE7', 0, 'javascript:', 'Rojak', 2, NULL, 0, 'Rojak', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=140', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 Tablespoon chili paste with garlic<BR>1 Tablespoon blachan (dry shrimp paste)<BR>1/3 cup sugar<BR>1 Tablespoon tamarind, rinsed in warm water 2 teaspoons lemon juice<BR>2 cucumbers, seeded and diced in half-inch cubes<BR>1 cup bean spouts, heads and tails removed<BR>1/2 cup diced peeled daikon or kohlrabi<BR>1/2 cup canned pineapple, drained well<BR>1/4 cup chopped peanuts<BR>3 squares fried bean curd, cut into half-inch cubes<BR><I><B>Preparation:</I></B><BR>1. Mix the first four ingredients with four ounces of water mashing the tamarind until it combines with the other items, then add the lemon juice and mix well.<BR>2. Add the rest of the ingredients, toss thoroughly, and serve as a cold side dish or salad.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ECE59476E0F80C459DD6227615567097', X'150921831FAF394E8E130EA7804F229D', X'150921831FAF394E8E130EA7804F229D', 0, 'javascript:', 'Sea-vegetable Salad', 2, NULL, 0, 'Sea-vegetable Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1207', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>10 ounces agar-agar strands<BR>1 bundle flowering garlic chives, rinsed, their tough ends cut off and discarded<BR>3 eggs<BR>salt and pepper to taste<BR>1 Tablespoon peanut oil<BR>1/2 pound fresh bean sprouts, their tails removed<BR>1 clove garlic<BR>1 teaspoon sugar<BR>2 Tablespoons sesame oil<BR>2 teaspoons honey<BR><B><I>Preparation:</B></I><BR>1. Soak the agar-agar strands in cold water for half an hour.<BR>2. Chop two inches off the ends of the chives and mince, then set them aside. Cut the rest of the chives into bite-sized lengths and drop into boiling water for half a minute, drain, and immerse these pieces in ice water. Drain them as soon as they are chilled.<BR>3. Beat the eggs and season them with salt and pepper, then stir in chopped chives.<BR>4. Heat a wok or a heavy fry pan, brush with a little oil, and cook one-quarter of the egg mixture. When barely set, empty the egg onto a plate. Rub the pan lightly with oil before making another. Put each egg sheet on its own plate, and continue until all egg is used.<BR>5. Roll the egg sheets and then cut them into thin strips.<BR>6. Drain the agar-agar and cut it into bite-sized strips.<BR>7. Mix with the eggs and bean sprouts, cover and refrigerate.<BR>8. Whisk together the garlic, sugar, sesame oil, and the honey and pour over the vegetables. Toss and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'622F41FC5501D14DAC07D69D9F62380A', X'9EBBD2B2321CDB4EB24B23415D8C8607', X'9EBBD2B2321CDB4EB24B23415D8C8607', 0, 'javascript:', 'Shanghainese Cold Egg Noodles', 2, NULL, 0, 'Shanghainese Cold Egg Noodles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=568', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cubes fermented bean curd<BR>2 Tablespoons of the brine from the fermented bean curd<BR>2 scallions, washed and trimmed and sliced<BR>1 cup mung bean sprouts, root ends removed<BR>1/2 cup roasted unsalted peanuts<BR>1/2 cup fermented black bean and garlic sauce<BR>1/3 cup thin soy sauce<BR>1/4 cup white rice vinegar<BR>1/4 cup hot chili oil<BR>1 pound fresh thin round egg noodles<BR><B><I>Preparation:</B></I><BR>1. Mash fermented bean curd and their brine.<BR>2. Put scallions, mung bean sprouts, peanuts, black bean sauce, soy sauce, vinegar sesame oil, and hot chili oil into individual bowls, each with a serving spoon.<BR>3. Bring six quarts of water to the boil and add the noodles. Reduce the heat just a bit so that the water in the pot does not boil over, yet still keep it boiling. Cook the noodles three minutes, drain and rinse quickly under cold running water.<BR>4. Divide the noodles into six to eight individual bowls and serve. Have guests add whatever items they prefer or some of each of the condiments and toss them with the noodles, and enjoy.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F94D57C91EC04E4C929CDFA29CFFCF65', X'8D67B765CA26E64BACAFC2C87D838785', X'8D67B765CA26E64BACAFC2C87D838785', 0, 'javascript:', 'Shrimp and Vegetable Salad with Herb Dressing', 2, NULL, 0, 'Shrimp and Vegetable Salad with Herb Dressing', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=199', '<TD><RECIPE><I><B>Salad ingredients:</I></B><BR>1 pound medium shrimp, shelled and deveined <BR>1/3 pound thin rice noodles, softened in hot water for fifteen to twenty minutes then drained <BR>3 carrots, peeled and grated (about 2½ cups) <BR>2 and 1/2 cups rinsed leafy lettuce, drained and cut into thin julienne shreds <BR>2 and 1/2 cups bean sprouts, rinsed and drained <BR>1/3 cup coarsely chopped fresh cilantro <BR>1/3 cup coarsely chopped fresh basil <BR>1 and 1/2 cups finely chopped scallions, tops only<BR><I><B>Dressing ingredients:</I></B><BR>1 and 1/4 teaspoons crushed dried chiles or dried chile flakes<BR>5 limes or 2 and 1/2 lemons, juiced (about 2/3 cup)<BR>1/3 cup fish sauce, or more to taste<BR>1/3 cup sugar <BR>1 and 1/2 Tablespoons minced garlic<BR><B><I>Preparation:</I></B><BR>1. Slice the shrimp in half lengthwise along the back. <BR>2. Boil four cups water then add the shrimp. When the water returns to a boil, turn off the heat. Let the shrimp stay in that water only for two minutes, then drain and rinse under cold water. Be sure they are well drained.<BR>3. Boil two quarts of water and add the softened rice stick noodles. Mix them while they cook until just tender, about ten seconds or so, then drain thoroughly and rinse them under cold water and drain again. Cut the noodles into three-inch lengths and arrange on a deep serving platter.<BR>4. Arrange the shrimp in the center with the carrots, lettuce, and bean sprouts in concentric circles around them. Leave some noodles to be the outside concentric circle.<BR>5. For the dressing, soak the chiles in the lime juice for two or three minutes. Add the remaining dressing ingredients and stir until the sugar is dissolved, then pour into a serving bowl. It can be used at room temperature or used when chilled.<BR>6. Sprinkle the chopped cilantro, basil, and scallions on top of the shrimp and vegetables, then spoon the dressing over the salad. If preferred, it can be serve it on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'15CDCDFD096DB74B82800D0E4445C231', X'34209C7F6AA29F4283E2CF68D0675C48', X'34209C7F6AA29F4283E2CF68D0675C48', 0, 'javascript:', 'Sichuan Pickled Cucumbers', 2, NULL, 0, 'Sichuan Pickled Cucumbers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=531', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 cucumbers, kirby or another pickling variety works best<BR>1 teaspoon sea salt or kosher salt<BR>2 Tablespoons corn or peanut oil<BR>1 teaspoon whole Sichuan pepper, roasted and cooled<BR>1 teaspoon chili-bean paste<BR>1 Tablespoon black vinegar<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon sugar<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Cut cucumbers in quarters the log way and the cut each of these into one to two-inch pieces.<BR>2. Mix cucumbers with salt and set aside for two hours. Then rinse and dry them with paper towels.<BR>3. Heat corn oil in a wok or fry pan and fry the Sichuan pepper for one minute, turn off the heat and allow to cool for ten minutes. Discard the peppers, add all the other ingredients to the oil, mix well, and cover and refrigerate for two to four days, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'01359EBCAD107D44917AFB1860265609', X'D4677B6E31B24D4E8C2F2255C9CD9197', X'D4677B6E31B24D4E8C2F2255C9CD9197', 0, 'javascript:', 'Sichuan Pickled Vegetables I', 2, NULL, 0, 'Sichuan Pickled Vegetables I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=555', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 cups napa cabbage, cut into half inch chunks<BR>6 sliced fresh ginger<BR>2 cups white turnips, cut into thin slices, each slice cut in half<BR>6 hot red chili peppers<BR>3 Tablespoons Chinese white rice vinegar<BR>2 Tablespoons coarse salt (a non-iodized variety)<BR>1 Tablespoon granulated sugar<BR>Preparation:</B></I><BR>1. Put all ingredients into a large clean glass or ceramic bowl and mix well.<BR>2. Cover tightly and store in a cool place for three days, then put this container in the refrigerator. It will be ready to eat after three more days; and can stay a few more than that, if desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CEE4B4CE12E7624A8129FAD852DD9325', X'46953CD34774574EA7125CFC028E1F94', X'46953CD34774574EA7125CFC028E1F94', 0, 'javascript:', 'Sichuan Pickled Vegetables II', 2, NULL, 0, 'Sichuan Pickled Vegetables II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1512', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons coarse salt<BR>3 Tablespoons Sichuan peppercorns<BR>2 red chili peppers, cut in quarters the long way, seeded, their membranes removed<BR>1 small knob of fresh ginger, peeled, and cut into long very thin strips<BR>2 Tablespoons Mao tai or any Chinese liquor<BR>3 Tablespoons Chinese rice wine<BR>6 carrots, peeled, then cut into two-inch by half-inch strips<BR>3 Chinese thin cucumbers (do not peel them), cut into two-inch by half-inch strips<BR>1 small Chinese white turnip, peeled, then cut into two-inch by half-inch strips<BR>1/2 small head of cabbage, core removed, then cut into two-inch by half-inch strips<BR><B><I>Preparation:</B></I><BR>1. Rinse a crock or very large jar, and dry it with paper towels, then add two quarts of boiling water, the salt, Sichuan peppercorns, chili peppers, and the ginger and allow to cool overnight.<BR>2. Put all the vegetables (as many as fit at one time) into a large strainer basket and pour one quart of boiling water for each strainer basket full, over the cut vegetables, drain well, and then put these into the crock or jar, continuing until all have been rinsed with and then drained from their boiling water.<BR>3. Cover loosely, stir the vegetables every six hours with a large spoon that has been rinsed with boiling water, and the vegetables are ready to eat after eighteen hours. Serve the vegetables drained, and at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'43AF458175764B438D704FB6E0A39DB5', X'EFAF133F1F01AA428E498C7012C8F5AA', X'EFAF133F1F01AA428E498C7012C8F5AA', 0, 'javascript:', 'Simple Kyrgyz Salad (Jönököy Salat)', 2, NULL, 0, 'Simple Kyrgyz Salad (Jönököy Salat)', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=525', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 cups fresh cabbage, grated<BR>1 medium carrot, peeled and grated<BR>2 cloves garlic, finely minced<BR>8 ounces sour cream<BR>3 Tablespoons fresh dill, chopped<BR>1 teaspoon salt<BR>1 teaspoon ground mild red pepper<BR>1 teaspoon ground black pepper<BR>1 Tablespoon fresh dill, chopped<BR><B><I>Preparation:</B></I><BR>1. Put grated cabbage and carrot into a large bowl.<BR>2. Add the garlic to the cabbage mixture.<BR>3. Next, add sour cream, salt, red pepper, and black pepper, and mix well. Serve garnished with dill.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A79BB9C4BD7C3742A02D89A53FBB2D43', X'E0F675232187B0498020897AF8B21BA5', X'E0F675232187B0498020897AF8B21BA5', 0, 'javascript:', 'Smashed Radish Pickles', 2, NULL, 0, 'Smashed Radish Pickles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1217', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 bunch red radishes, ends removed<BR>1 small daikon, peeled and cut into one-inch cubes<BR>1 teaspoon salt<BR>1 Tablespoon crushed Chinese brown slab sugar<BR>2 Tablespoons Chinese rice wine<BR>1 Tablespoon sesame oil<BR>1 Tablespoon corn oil<BR><B><I>Preparation:</B></I><BR>1. Smash each radish or radish piece with the side of a cleaver.<BR>2. Put them in a bowl and sprinkle with salt and let sit for half an hour, drain, and put into a clean bowl with sugar and rice wine.<BR>3. Heat both of the oils and toss with the radishes and all other ingredients; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8A012161BC023A4EB2A38206FBA2FC45', X'F15C2F663835DD40B1F0CC62E2749378', X'F15C2F663835DD40B1F0CC62E2749378', 0, 'javascript:', 'Tofu Salad with Peapods and Peppers', 2, NULL, 0, 'Tofu Salad with Peapods and Peppers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=150', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/4 pound fresh peapods<BR>1 large extra-firm tofu, about ten ounces<BR>1/2 yellow or sweet red pepper, seeded, and cut into half inch pieces<BR>2 Tablespoons diced scallions<BR>1 teaspoon minced garlic<BR>and the recipe for Tofu Salad Dressing below<BR><I><B>Preparation:</I></B><BR>1. Wash the pea pods, string if needed, snip off the tops, and cut them crosswise into half inch pieces.<BR>2. Put them into two cups of boiling water for one minute, drain and set aside, reserving the water.<BR>3. Cut the tofu into half-inch cubes, put them in, then immediately remove them from the water, drain very well, and set aside.<BR>4. Mix well with the prepared dressing<BR><I><B>Salad Dressing Ingredients:</I></B><BR>1/2 teaspoon cornstarch<BR>1/2 teaspoon sugar<BR>1/8 teaspoon white pepper<BR>2 Tablespoons oyster flavored sauce<BR>2 Tablespoons cold water<BR>1/2 teaspoon sesame oil<BR>1/2 teaspoon cider vinegar<BR><B><I>Preparation:</B></I> MIx all the ingredients for the dressing and serve immediately or chill to serve later.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'47CD22D0DC593A4ABBCA660DD9997CC7', X'5FFD7747BBE83743A2013B0119B7EDA3', X'5FFD7747BBE83743A2013B0119B7EDA3', 0, 'javascript:', 'Watercress Salad with Chicken and Asian Pear', 2, NULL, 0, 'Watercress Salad with Chicken and Asian Pear', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=119', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>2 Tablespoons sliced almonds<BR>1 bunch watercress, washed and dried<BR>1 cup diced roasted chicken, cut into 3/4-inch pieces (white meat preferred)<BR>1 Asian pear (preferably a sweet variety such as Hosui)<BR>2 teaspoons roasted hazelnut oil<BR>Salt and freshly ground pepper, to taste<BR><B><I>Preparation:</I></B><BR>1. In a dry skillet, toast the almond slices, then set them aside.<BR>2. Tear or cut the watercress in half crosswise, discarding the tough lower portion of the stems. Place the greens in a serving bowl. <BR>3. Arrange the chicken over the cress.<BR>4. Peel the Asian pear. Halve and core it. Place the fruit cut-side down on a cutting board. Cut the fruit into 1/2-inch slices. Cut the slices crosswise into 1/2-inch pieces. Add one cup of the cut fruit to the salad, reserving the rest for another use.<BR>5. Drizzle the oil over the salad. Sprinkle with the toasted almonds. Toss well and season to taste with salt and pepper. Serve immediately.<BR><B>Note:</B> Dana Jacobi, author of <I>The Natural Kitchen: SOY!</I> (Prima, 1996) makes this elegant salad, which combines crunchy Asian pear with tangy cress and chicken.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'82CB948A22FD974486510E263587DAC4', X'576D4911CCCF6A48B1CF3FCD9291A0C5', X'576D4911CCCF6A48B1CF3FCD9291A0C5', 0, 'javascript:', 'Western-influenced Dou Miao Salad', 2, NULL, 0, 'Western-influenced Dou Miao Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=331', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>4 cups, lightly packed, small <I>dou miao</I>, torn (about four ounces)<BR>1 cup julienne raw apple (Fuji or Golden Delicious are good choices)<BR>1/4 cup crumbled blue cheese, preferably Roquefort<BR>2 teaspoons red wine vinegar<BR>salt and freshly ground black pepper, to taste<BR><B><I>Preparation:</I></B><BR>1. Combine the <I>dou miao</I>, apple, and blue cheese and toss carefully and thoroughly until cheese is well-distributed.<BR>2. Then toss it with the vinegar, salt, and pepper.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7FE8994815F88345A48BAF7E2744CFEA', X'6EC196DBE2DD5C4797CDDBD5F13DAD33', X'6EC196DBE2DD5C4797CDDBD5F13DAD33', 0, 'javascript:', 'White and Red Radish with Ginger', 2, NULL, 0, 'White and Red Radish with Ginger', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1175', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 inches daikon, peeled and shredded<BR>10 red radishes, trimmed and shredded<BR>1/4 teaspoon coarse salt<BR>1/2 cup red rice vinegar<BR>4 teaspoons granulated sugar<BR>2 Tablespoons peeled fresh or pickled ginger, shredded<BR><B><I>Preparation:</B></I><BR>1. Mix shredded daikon and radishes with coarse salt and allow to rest for half an hour.<BR>2. Mix rice vinegar and sugar, and mix with the shredded vegetables. Refrigerate for two or more hours, then remove from the refrigerator and put into a small bowl.<BR>3. Sprinkle the ginger on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D8EF65B9C1787245A7F69D30272CF20F', X'914C8A17CF945A4E9D76851C3D46A657', X'914C8A17CF945A4E9D76851C3D46A657', 0, 'javascript:', 'Barbecue Sauce', 2, NULL, 0, 'Barbecue Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1583', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon vegetable oil<BR>3 large cloves garlic, minced<BR>2 teaspoons minced fresh ginger<BR>1 Tablespoon ground ginger<BR>1/2 cup brown sugar<BR>1/4 cup dark soy sauce<BR><B><I>Preparation:</B></I><BR>1. Heat small sauce pan, add the oil, then the garlic and fresh ginger, and stir-fry for one minute.<BR>2. Add the ground ginger and stir well, then add the brown sugar and the soy sauce, and as soon as this comes to the boil, reduce the heat and simmer for fifteen minutes.<BR>3. Remove from the heat and allow to cool before refrigerating this sauce.<BR><B>Note:</B> This sauce can stay for a week or two in the refrigerator; but better yet, pour the sauce into an ice cube tray and freeze it. Once frozen, pop the cubes and store them in the freezer in a tightly closed freezer bag. Do take out as many as needed a half an hour earlier, leaving the rest frozen until they are needed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'07E8136011D76442BC1D97DF6F8A8B29', X'E4476DD3637E5141866A0A4B7A5D6143', X'E4476DD3637E5141866A0A4B7A5D6143', 0, 'javascript:', 'Bean Jelly with Hot Sauce, Dong Style', 2, NULL, 0, 'Bean Jelly with Hot Sauce, Dong Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1685', '<TD><RECIPE>Make jelly mixing two and three-quarters of boiling water with half cup mung bean starch and half teaspoon salt. Simmer stirring until very thick, then transfer to a cookie sheet with sides and cool. Then refrigerate for a couple of hours until set. Make the sauce for this mixing two tablespoons soy sauce, one tablespoon Chinkiang vinegar, two teaspoons sugar, and two teaspoons chili oil until sugar is dissolved. Then mix in one teaspoon grated fresh ginger, two large cloves grated garlic, three large scallions slivered, and four inches of peeled daikon cut into matchstick pieces. Now loosen the jelly and cut into thin strips, mix with the sauce or serve the sauce on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D2A12D41A2461C42B932C40F0EBB27D9', X'FCEDCD8AD912B142904F44D95B05FCEF', X'FCEDCD8AD912B142904F44D95B05FCEF', 0, 'javascript:', 'Black Bean Sauce I', 2, NULL, 0, 'Black Bean Sauce I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=807', '<TD><RECIPE><B><I>Ingredients</B></I><BR>1/2 cup fermented black beans<BR>1 Tablespoon corn oil<BR>3 slices fresh ginger, minced<BR>3 cloves garlic, minced<BR>1 Tablespoon tangerine peel soaked, drained, then finely minced<BR>1 teaspoon sugar<BR>1 Tablespoon red rice wine<BR>1 Tablespoon white rice wine<BR>2 Tablespoons black rice wine vinegar<BR><B><I>Preparation:</B></I><BR>1. Rinse the black beans for one minute, in a strainer, and under running warn water.<BR>2. Soak the rinsed beans in two cups of warm water for ten minutes, then drain them, and very gently partially crush them.<BR>3. Heat a wok, add the oil, and fry the ginger and the garlic for one minute, then add the crushed beans and stir-fry for one minute before adding the sugar, minced tangerine peel, rice wines, and vinegar. 4. Next simmer this for five minutes. All the liquid should evaporate.<BR>5. Cool in the wok, then transfer to a clean glass jar and refrigerate them.<BR><B>Note:</B> When making a dish with black beans or a black bean sauce, take two to four tablespoons of the above and add this after adding any garlic and ginger; then continue as the recipe advises.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'762779122CA64240A9D5A8DF6593997D', X'A0758E6D1EA8BA46AED89C03F1667E26', X'A0758E6D1EA8BA46AED89C03F1667E26', 0, 'javascript:', 'Black Bean Sauce II', 2, NULL, 0, 'Black Bean Sauce II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1875', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>3 cloves garlic, peeled and coarsely chopped<BR>2 slices fresh ginger, peeled and minced<BR>3 Tablespoons dried salted black beans<BR>1 teaspoon granulated sugar, optional<BR>2 Tablespoons Chinese rice wine<BR>1 Tablespoon thin soy sauce<BR>1 cup chicken stock<BR>2 teaspoons thick soy jam<BR>1 Tablespoon cornstarch mixed with one tablespoon cold water<BR>2 scallions, slivered<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or fry pan, add the oil, then the garlic and ginger. After stirring for half a minute, add the black beans and stir for another minute. Then add the sugar, rice wine, and the thin soy sauce, and stir for another minute before adding the chicken stock and the thick soy jam mixed with the cornstarch mixture.<BR>2. Reduce the heat, simmer and stir for two more minutes. If using immediately, add the scallions. If not, set this aside; and add them when ready to use this black bean sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B33134141FE8784F80B8957DA3BC8250', X'1EF658D13566504AA9995412C67C3E9A', X'1EF658D13566504AA9995412C67C3E9A', 0, 'javascript:', 'Black Pepper Sauce', 2, NULL, 0, 'Black Pepper Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=809', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons corn oil<BR>5 cloves garlic, sliced<BR>1/4 cup dry-fried shallots<BR>3 Tablespoons brown bean or grinding bean paste<BR>3 Tablespoons coarse black pepper<BR>2 Tablespoons sugar<BR>1 teaspoon salt<BR>1 teaspoon soy sauce<BR>2 Tablespoons cornstarch mixed with two tablespoons of cold water<BR><B><I>Preparation:</B></I> 1. Heat wok, add oil then when hot add garlic, shallots, bean paste, pepper, sugar, salt, and soy sauce and stir-fry for two minutes.<BR>2. Add cornstarch mixture, and simmer until thick; then remove from heat, cool, and put in a glass jar in the refrigerator.<BR><B>Note:</B> Best when used in two to four tablespoon amounts and added to a dish when sauce ingredients are required.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CAB3E892507CDA49AE6EA89ED324FB33', X'C1F0366F91459348B325D2DD754ECBEB', X'C1F0366F91459348B325D2DD754ECBEB', 0, 'javascript:', 'Cantonese Black Bean Cooking Sauce', 2, NULL, 0, 'Cantonese Black Bean Cooking Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=449', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup chicken broth<BR>1/4 cup Chinese rice wine or dry sherry<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon sesame oil<BR>2 teaspoons black bean garlic sauce or one tablespoon salted black beans rinsed and finely chopped<BR>2 teaspoons cornstarch<BR>1 teaspoon sugar<BR>1/2 teaspoon freshly ground black pepper<BR><B><I>Preparation:</B></I><BR>1. Mix ingredients together.<BR>2. Use with your favorite meat, seafood, poultry, or favorite vegetable dish, adding it near the end of cooking.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F24D9AD8A2CC2547BEADB3C5DD513FAF', X'37BCCD99EE438F4AB66A692E9810EEEF', X'37BCCD99EE438F4AB66A692E9810EEEF', 0, 'javascript:', 'Chili Sauce, Korean-Chinese Style', 2, NULL, 0, 'Chili Sauce, Korean-Chinese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1564', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 cloves garlic, peeled and coarsely minced<BR>12 red chili peppers, seeded and coarsely chopped<BR>1 Tablespoon chili pepper<BR>3 Tablespoons sesame oil<BR>3 Tablespoons soy oil or another vegetable oil<BR>2 Tablespoons thin soy sauce<BR><B><I>Preparation:</B></I><BR>1. Put all ingredients into a blender and run it on low until the mixture forms a thick paste.<BR>2. Put into a glass jar, cover with plastic wrap secured with several rubber bands and store in a sunny place for half a day, then remove the cover, mix well, and refrigerate until needed.<BR><B>Note:</B> This can be stored refrigerated for one to three months.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9E0DE417F6011C4091308654F8015601', X'0E5BAD49BD4D6C4DA52053CDB63FA10F', X'0E5BAD49BD4D6C4DA52053CDB63FA10F', 0, 'javascript:', 'Dry XO Sauce', 2, NULL, 0, 'Dry XO Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=720', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 to 3 dry scallops<BR>1/2 cup chicken broth<BR>2 slices fresh ginger<BR>1 scallion, minced<BR>1 teaspoon sesame oil<BR>1 cup corn oil<BR>2 cloves garlic<BR>5 shallots<BR>2 Tablespoons dry shrimp<BR>1/4 pound Yunnan or Smithfield ham, cut in half-inch strips<BR>1 Tablespoon confectioners sugar<BR>1/2 Tablespoon chili powder<BR>1/2 Tablespoon chicken bouillon powder<BR>5 Tablespoons chicken or pork floss<BR><B><I>Preparation:</B></I><BR>1. Soak dry scallops in cold water for twelve hours or overnight, then remove and discard the water.<BR>2. Put scallops in a bowl with the chicken broth, ginger, scallion, and sesame oil, and steam this for forty-five minutes. Remove scallops from liquid, then deep fry them for one minute. Cool and shred them, then mince them finely. Reserve other ingredients for use in another recipe such as a soup or stew.<BR>3. Heat corn oil and deep fry the scallops for one minute. Remove them from the oil and shred, then mince them.<BR>4. Roast garlic, shallots, dried shrimp and ham in very hot oven for five minutes. Remove, cool, and mince well.<BR>5. Mix scallops, minced roasted garlic, shallots, dried shrimp, ham, confectioners sugar, chili powder, bouillon powder, and chicken floss.<BR><B>Note:</B> This recipe can be stored in the refrigerator in a glass jar; it makes about fifty tablespoons. Chef Leong recommends using five of them when making long beans.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3FF4C2160DBEC141A3073A6421982909', X'B5E8534CBE0DBF4A8832DD7A08FDDE4D', X'B5E8534CBE0DBF4A8832DD7A08FDDE4D', 0, 'javascript:', 'Fermented Red Wine Lees I', 2, NULL, 0, 'Fermented Red Wine Lees I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1466', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup red bean curd, mashed<BR>1/4 cup crushed Chinese brown sugar slab<BR>2 Tablespoons thin soy sauce<BR>1 cup Shaoxing wine<BR>2 to 3 teaspoons peeled and minced garlic<BR>2 Tablespoons rice or yam flour<BR><B><I>Preparation</B></I><BR>1. Simmer,stirring, all of the above ingredients for ten minutes.<BR>2. Cool, and then refrigerate in sterilized glass gars, taking out what/when/as needed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B5B18F7A6A53004786389E5674F85A54', X'2A3EF1EA96AD4D4BB4DE8C2EFD22E73A', X'2A3EF1EA96AD4D4BB4DE8C2EFD22E73A', 0, 'javascript:', 'Fermented Red Wine Lees II', 2, NULL, 0, 'Fermented Red Wine Lees II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1244', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup fermented red bean curd, mashed<BR>1/4 cup crushed Chinese brown slab sugar<BR>2 Tablespoons thin soy sauce<BR>1 cup Shaoxing wine<BR>2 to 3 teaspoons peeled and minced garlic<BR>2 Tablespoons rice or yam flour<BR><B><I>Preparation:</B></I><BR>1. Simmer and stir all of the above for ten minutes.<BR>2. Cool, and then refrigerate in a sterilized glass jar. Use as needed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6A357BBAEFBC3D4180BB8C2CC656BD85', X'877B67A509BC2340BCA81A40A56BD28F', X'877B67A509BC2340BCA81A40A56BD28F', 0, 'javascript:', 'Five-spice Chili Tea Rub', 2, NULL, 0, 'Five-spice Chili Tea Rub', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=634', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 cups lapsang souchong tea leaves<BR>1/2 cup sea salt or kosher salt<BR>1/2 cup red pepper flakes<BR>1/2 cup chipolte chili powder<BR>1/2 cup garlic powder<BR>1/4 cup cayenne powder<BR>1/4 cup dried chives or onions<BR>1/4 cup five-price powder<BR><B><I>Preparation:</B></I><BR>1. Combine all the ingredients in a bowl. Set aside all not used for a specific recipe by storing it in a glass jar in a cool dark place.<BR><B>Note 1:</B> We used plain chili powder as a substitute.<BR><B>Note 2:</B> Chef Tsai rubs this on steaks, wraps them in plastic wrap for fifteen minutes before grilling them or pan-frying them in a bit of oil to grease the pan.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C52E2186CB369F4B8E96F0DFF9A661AE', X'721F9D3D31AB2C48AA7DB89321F90EE0', X'721F9D3D31AB2C48AA7DB89321F90EE0', 0, 'javascript:', 'Fujianese Red Wine Paste', 2, NULL, 0, 'Fujianese Red Wine Paste', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=349', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3/4 cup Chinese rice wine<BR>6 Tablespoons Chinese brown sugar<BR>1 Tablespoon mushroom soy sauce<BR>6 squares fermented red tofu (without chili)<BR>2 teaspoons minced peeled ginger<BR>1 teaspoon minced garlic<BR>1 Tablespoon rice flour<BR>3/4 cup cooked glutinous rice, cooled to room temperature<BR><B><I>Preparation:</B></I><BR>1. Mix wine, brown sugar, and soy sauce and stir until brown sugar is dissolved.<BR>2. Put wine mixture in a stainless steel or an enamel pot, one with no chips or cracks in it, and bring this mixture to the simmer. We suggest cooking it on a trivet and not directly on the heat because it can burn easily.<BR>3. Mash the fermented tofu and add to the wine mixture along with the ginger and the garlic. Simmer for fifteen minutes.<BR>4. Smash the cooked rice with the side of a cleaver until the grains are not recognized as such, then add to the wine mixture and simmer another five minutes. Cool to room temperature, then use or store covered in a glass jar in the refrigerator.<BR><B>Note:</B> This mixture can be kept for two or three weeks in the refrigerator, When frozen in an ice cube tray it stays for months. Put some in an ice cube tray with its dividers, and when hard pop the cubes and store them wrapped individually in plastic wrap, sealed in a plastic freezer bag. That way one can defrost small amounts, as needed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4754B529AEDACA4D9C2EB5901CF955EB', X'364C9089C5E37345A89CEBC6CCCA0AAC', X'364C9089C5E37345A89CEBC6CCCA0AAC', 0, 'javascript:', 'Garlic Oil', 2, NULL, 0, 'Garlic Oil', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=980', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 cups corn oil<BR>1 and 1/2 heads garlic, cloves separated, peeled, and chopped<BR>1 thin slice ginger, about two inches long<BR>1/4 white onion, peeled and chopped<BR><B><I>Preparation:</B></I><BR>1. Heat oil in deep saucepan until it reaches 200 degrees F, then add garlic, ginger and onion, reduce the heat and cook for twenty minutes until onion and other pieces are light brown.<BR>2. Turn heat off and let the oil cool. The strain through a fine strainer making sure no solids remain.<BR>3. Transfer to a dry clean glass container and store in the refrigerator. Can stay refrigerated for up to six weeks.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0E5D6512C828C946931F1CEBFD0B3BFC', X'82E425A650B46740BF09FD8D9828EB83', X'82E425A650B46740BF09FD8D9828EB83', 0, 'javascript:', 'Garlic Sauce I', 2, NULL, 0, 'Garlic Sauce I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1233', '<TD><RECIPE><B><I>Ingredients</B></I><BR>1/4 cup minced garlic<BR>1/4 cup rice vinegar<BR>1 Tablespoon sugar<BR><B><I>Preparation:</B></I><BR>1. Mix all the ingredients, stirring them for one minute.<BR>2. Pour boiling water into a glass jar, then drain, and fill with the mixture, and refrigerate.<BR>3. Serve after two hours with any meat, fish, or vegetable dish desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'81EF22772F91F14182E03976138E71DA', X'62C10AD49D772246ADA43AC33AE6CCA5', X'62C10AD49D772246ADA43AC33AE6CCA5', 0, 'javascript:', 'Garlic Sauce II', 2, NULL, 0, 'Garlic Sauce II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1234', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup minced garlic<BR>1/4 cup rice vinegar<BR>1 Tablespoon sugar<BR><B><I>Preparation:</B></I><BR>1. Mix all the ingredients, stirring them for one minute.<BR>2. Pour boiling water into a glass jar, then drain, and fill with the mixture, and refrigerate.<BR>3. Serve after two hours with any meat, fish, or vegetable dish desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'30D3833E7E369A4DAAF73CF09C1EEEE1', X'2881256D3A30DF4AA07B009716ACC26E', X'2881256D3A30DF4AA07B009716ACC26E', 0, 'javascript:', 'Ginger Paste', 2, NULL, 0, 'Ginger Paste', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=793', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound fresh ginger, peeled, rinsed, dried and sliced<BR>2 Tablespoons corn oil<BR>10 teaspoons sesame oil<BR>10 cloves garlic, peeled and mashed<BR>1 teaspoon ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Mince the ginger until it is a thick paste, then add both oils and continue mincing for another two minutes, continually folding in the oils.<BR>2. Add mashed garlic and the ground pepper and mix well; then use as desired. This mixture can stay for two or at most three days, if refrigerated. Do keep it very well covered; we use a sealed plastic container inside another sealed container.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C5A6FF2C66075C4FBBE83C64E01F3E9C', X'8458BA3E5D21D446997B58529E96A901', X'8458BA3E5D21D446997B58529E96A901', 0, 'javascript:', 'Ginger Sauce', 2, NULL, 0, 'Ginger Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1166', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons grated fresh ginger<BR>8 cloves garlic, smashed<BR>2 Tablespoons sesame oil<BR>3 Tablespoons soy sauce<BR>3 Tablespoons vinegar<BR>1 teaspoon pepper<BR>1 teaspoon sugar<BR>1/4 teaspoon cayenne<BR>1/2 cup finely chopped scallions<BR><B><I>Preparation:</I></B><BR>1. Combine all the above ingredients with two tablespoons of cool water, then serve for people to take an amount they wish, or add one teaspoon of sauce to each bowl of mutton soup.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AECCB7E22BEFE74C98F6473A64A39E64', X'DE6F350BC243824F9E87BA72021155E9', X'DE6F350BC243824F9E87BA72021155E9', 0, 'javascript:', 'Ginger Wine Dipping Sauce', 2, NULL, 0, 'Ginger Wine Dipping Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=353', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound fresh ginger<BR>1/2 cup Chinese rice wine<BR><B><I>Preparation:</B></I><BR>1. Peel the ginger, then rinse and dry each piece.<BR>2. But ginger and wine and half-cup of water into a blender and run it at high until all the ginger is finely shredded. Put this mixture into a glass bowl, cover and set aside until morning.<BR>3. Using a clean cloth draped over a bowl, pour the ginger mixture into the bowl and wring the cloth over the bowl so that all the liquid can be contained. Use immediately or store in a glass jar in the refrigerator.<BR><B>Note:</B> This can be kept up to two weeks.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'11F25BD777E0444D922CC18698A7018E', X'B6F62B1016796C41A8363360B58BC612', X'B6F62B1016796C41A8363360B58BC612', 0, 'javascript:', 'Green Sauce for Meat Dishes', 2, NULL, 0, 'Green Sauce for Meat Dishes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2062', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups coriander or 1 Tablespoon each of cumin, coriander, and turmeric<BR>3 Tablespoons minced garlic<BR>3 Tablespoons minced shallots<BR>½ cup coarsely chopped scallions<BR>½ teaspoon coarse salt<BR>2 Tablespoons rice vinegar<BR>1 Tablespoon vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Chop well all the solid and liquid ingredients in a food processor.<BR>2. Remove and mix them with any meat, soup, or another dish in or on the side; this increases the flavors of the dish.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D997E06FDBF2EB4F8BBA7EECE2C131BA', X'837831A0AC4C644AB4CD1F54225D4B97', X'837831A0AC4C644AB4CD1F54225D4B97', 0, 'javascript:', 'Hakka Chili Sauce', 2, NULL, 0, 'Hakka Chili Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1007', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon chili sauce<BR>1 Tablespoon rice vinegar<BR>1 teaspoon sugar<BR><B><I>Preparation:</B></I><BR>1. Mix the chili sauce with the rice vinegar and the sugar.<BR>2. Let this mixture rest for one hour, then refrigerate. Serve when cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7D804B57FBA7624B895D52EDB1A6D009', X'16DE5E281FD0684B94E3EA848C4FC8ED', X'16DE5E281FD0684B94E3EA848C4FC8ED', 0, 'javascript:', 'Hakka Garlic Sauce', 2, NULL, 0, 'Hakka Garlic Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1009', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon minced garlic<BR>1 Tablespoon rice vinegar<BR>1 teaspoon sugar<BR><B><I>Preparation:</B></I><BR>1. Mix the garlic with the rice vinegar and the sugar.<BR>2. Let rest for two hours, then put it into a glass jar and refrigerate. Serve when cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'91FE0A8DCB9B6E4494AF6F304AD9818D', X'AA7477859E7D0649BF491FB698BDF622', X'AA7477859E7D0649BF491FB698BDF622', 0, 'javascript:', 'Hoisin Sauce', 2, NULL, 0, 'Hoisin Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=810', '<TD><RECIPE><B><I>Ingredients</B></I><BR>1 Tablespoon corn oil<BR>5 cloves garlic, minced<BR>5 shallots, minced<BR>5 slices fresh ginger, minced<BR>4 Tablespoons tomato paste<BR>1 cup brown bean paste<BR>3 Tablespoons oyster sauce or <I>sa cha</I> sauce<BR>10 slices dried haw circles<BR>2 teaspoons black rice vinegar<BR>1 Tablespoon honey<BR>1 Tablespoon soy sauce<BR><B><I>Preparation:</B></I><BR>1. Heat wok, add oil, and stir-fry garlic, shallots, and ginger for one minute.<BR>2. Add tomato paste, one-quarter cup cold water, brown bean paste, oyster sauce, haw circles, rice vinegar, honey, and soy sauce. Reduce the heat slightly, and stir for five minutes. Then remove from heat and cool before putting into a clean glass jar.<BR><B>Note:</B> Refrigerate until using; and use twice as much of this hoisin sauce in every recipe that calls for this sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7B192F7DCCDC9648A2BDD726831D04EB', X'CD763FC6367BE642A19CE8EF63C862E0', X'CD763FC6367BE642A19CE8EF63C862E0', 0, 'javascript:', 'Home-made Chili Paste I', 2, NULL, 0, 'Home-made Chili Paste I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1248', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup dried hot chili peppers<BR>1 Tablespoon vegetable oil or lard<BR>3 Tablespoons peeled and minced shallots<BR>1 teaspoon crushed Sichuan peppercorns<BR>2 Tablespoons Chinese red rice vinegar<BR><B><I>Preparation:</B></I><BR>1. Pour one cups boiling water over the chili peppers, and let them rest in this water for two or three hours, then transfer to a blender and blend for one minute.<BR>2. Heat oil or lard and stir-fry the shallots and Sichuan pepper until they are soft, then add them to the blender and blend for half minute. Then pour this chili mixture into a medium-size pot.<BR>3. Simmer the chili mixture on low heat for ten to fifteen minutes, Add the vinegar, then cool and store in a sterilized glass jar in the refrigerator until used.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4229824741F72A4ABB193ABA1278427E', X'7F201DA416A3574AAA6F90F6B5E5B379', X'7F201DA416A3574AAA6F90F6B5E5B379', 0, 'javascript:', 'Home-made Chili Paste II', 2, NULL, 0, 'Home-made Chili Paste II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1464', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup dried hot chili peppers<BR>1 Tablespoon vegetable oil or lard<BR>3 Tablespoons peeled and minced shallots<BR>1 teaspoon crushed Sichuan peppercorns<BR>2 Tablespoons Chinese red rice vinegar<BR><B><I>Preparation:</B></I><BR>1. Pour one cup of boiling water over the chili peppers, and let them rest in this water for two or three hours. Then transfer them to a blender and blend for one minute.<BR>2. Heat oil or lard and stir-fry the shallots and the Sichuan peppr until the shallots are soft, then add this mixture to the blender and blend for half a minute. Next, pour this mixture into a medium-size pot.<BR>3. Simmer the chili mixture on low heat for ten to fifteen minutes, then add the vinegar. Cool and store this in a sterilized glass jar in the refrigerator until needed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'41E3623AEBBEAA428A6EDDCD2149F962', X'61E55F2952C472489CAC79D04874CD42', X'61E55F2952C472489CAC79D04874CD42', 0, 'javascript:', 'Homemade Fermented Red Taro Curd', 2, NULL, 0, 'Homemade Fermented Red Taro Curd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1211', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 and 1/3 pounds white taro, coarsely shredded<BR>1/4 pound flour<BR>1/4 pound coarse salt, mixed with four tablespoons boiling water<BR>1/2 liter soy sauce<BR>1/2 teaspoon red coloring (powdered preferred)<BR>1 teaspoon five-spice powder<BR>2 Tablespoons rose wine<BR><B><I>Preparation:</B></I><BR>1. Rinse the shredded taro, then steam until thoroughly cooked. Drain well, then mash and set aside.<BR>2. Put the flour into a bowl, add a little water (a few tablespoons) and make this into a batter. Put in a heat-proof bowl and steam for fifteen minutes over rapidly boiling water. Remove and allow to cool.<BR>3. In a separate bowl, mix the flour batter with the cooled salt water, and the cooled mashed taro. Knead for five minutes., then put this into a clean bowl and allow it to ferment uncovered at room temperature for half a day or overnight.<BR>4. Next, put this bowl during the day, every day, for two weeks in a sunny spot. Do not ignore it, rather mix the contents every two hours during the day. Use chopsticks and do so from morning to night. The contents should dry out.<BR>5. Pour the dried materials into a square tray such as an ice cube tray, and press them together firmly. Let this rest overnight.<BR>6. Take a ceramic flat container or a bowl or urn and rub with the rose wine. Next, put the cut squares into it in one layer, the pieces close to each other.<BR>7. In a separate small bowl, mix the soy sauce, red coloring, and five-spice powder. Pour this over the taro squares. Cover with cheesecloth, not touching the taro mixture. Set this container aside for one month; then it is ready to use.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'112A66AAC8FC0E41903C70A762AE6A5A', X'86E7357C040EC343B6A64781D9D828C1', X'86E7357C040EC343B6A64781D9D828C1', 0, 'javascript:', 'Homemade Hoisin Sauce', 2, NULL, 0, 'Homemade Hoisin Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1158', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup tomato catsup<BR>1 large clove garlic, finely minced<BR>1 Tablespoon brown sugar<BR>1 teaspoon honey<BR>1 Tablespoon low-sodium soy sauce<BR>1/2 teaspoon ground white pepper<BR>1 scallion, green part only, finely minced<BR>1 teaspoon rice vinegar<BR>1/4 cup Shaoxing wine or dry sherry<BR><B>Note:</B> This recipe requires no preparation. Just mix the ingredients together, stirring well. Then it can be used as a six-hour marinade for roast pork, as Fong-Torres does, or used in place of commercially prepared hoisin sauce in any other recipe that calls for it. For any not used immediately after mixing, refrigerate, preferably in a glass jar. It can keep for a week or two in the refrigerator. Freeze it if not planning to use it that time frame.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6854AFDF6CFBF845A9D176B444236C3D', X'24398438A7873A418ED899F79AB93A4F', X'24398438A7873A418ED899F79AB93A4F', 0, 'javascript:', 'Mango Sauce', 2, NULL, 0, 'Mango Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=808', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 ripe mangos<BR>1 cup cold chicken broth<BR>2 Tablespoons mango pudding mix powder<BR><B><I>Preparation:</B></I><BR>1. Peel both mangos, and cut into one inch pieces. Set half aside to use in the stir-fried dish.<BR>2. Using a fork, scrape any mango clinging to the seed, mix it with the other batch of diced mango, and a half cup of chicken broth, and put this into a blender and blend for one minute before adding the mang pudding mix and reblending it for half minute more.<BR>3. Add rest of the chicken broth.<BR><B>Note:</B> One way to use this sauce is to stir-fry raw chicken breast and thigh meat, water chestnuts halved, and green pepper diced, if desired. for two minutes, add the mango sauce mixture and the mango, and heat until boiling, then serve. There are many others.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'465CA6B1A0A33D4684B73EACC7818EC9', X'4EE2A9FA01B9A54FBC75C24526D8A8B0', X'4EE2A9FA01B9A54FBC75C24526D8A8B0', 0, 'javascript:', 'Mushroom Soy Sauce', 2, NULL, 0, 'Mushroom Soy Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1640', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>20 to 24 large black mushrooms, soaked in three cups warm water overnight<BR>3 Tablespoons coarse salt<BR>1 cup thin soy sauce, optional<BR>2 small bottles, sterilized<BR><B><I>Preparation:</B></I><BR>1. Drain mushrooms reserving the water; strain if sandy and discard the sand. Then, cut away and discard the mushroom stems.<BR>2. Mix mushrooms and salt and let stand for one to two days. Then mash well with the back of a heavy spoon, liquefying as much as possible. Strain and discard solids or reserve them to add to a soup or an omelet.<BR>3. Bring remaining liquid, reserved mushroom water, and the optional soy sauce to the boil, strain if any solids remain, then pour into two sterilized bottles. Refrigerate and use as needed as you would soy sauce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'10075B52722DB64FBEE7688BF8ADB737', X'4CEF8FB556FE774CB9BBD759BFF77CE3', X'4CEF8FB556FE774CB9BBD759BFF77CE3', 0, 'javascript:', 'Non-flower-pepper (or fagara) Mix', 2, NULL, 0, 'Non-flower-pepper (or fagara) Mix', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=792', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons ground white pepper<BR>2 Tablespoons coarsely ground black pepper<BR>1 Tablespoon ground fennel<BR>1 Tablespoon ground five-spice powder<BR>1/2 teaspoon ground cayenne or another hot pepper<BR><B><I>Preparation:</B></I><BR>1. Mix all of the spices, and put into a small glass bottle, such as an empty spice jar.<BR>2. Use as substitute for ground Sichuan pepper, in equal amounts.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'54864E0CFF99384392B71DF43C62F0AA', X'4A82E50BC782B943A3A5FC5740389308', X'4A82E50BC782B943A3A5FC5740389308', 0, 'javascript:', 'Osmanthus Syrup', 2, NULL, 0, 'Osmanthus Syrup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=459', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups dark corn syrup<BR>2 ounces fresh osmanthus flowers, washed and drained<BR><B><I>Preparation:</B></I><BR>1. Pour the corn syrup into a heavy bottom quart or larger sauce pan and put the flowers in it. Bring to the boil and quickly reduce the heat and simmer them, stirring frequently, for fifteen minutes. Should the water from the flowers have diluted the syrup, you may need to simmer longer until the syrup is very thick.<BR>2. Filter the syrup through a very fine mesh screen to remove all the flowers, then bottle hot or cold. This is excellent served with pancakes and similar foods.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9F63BA53F2ABE64BA4271F06EF9DF946', X'DF11CCB06540B84688C4148F544F977E', X'DF11CCB06540B84688C4148F544F977E', 0, 'javascript:', 'Peanut Sauce for the Sate', 2, NULL, 0, 'Peanut Sauce for the Sate', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=96', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>2 Tablespoons minced onions<BR>2 cloves garlic, peeled and minced<BR>1/3 cup chunky peanut butter<BR>1/2 cup coconut milk<BR>1 Tablespoon lime juice<BR>1 teaspoon sugar<BR>salt and pepper to taste<BR><B><I>Preparation:</B></I><BR>1. Heat oil in a saucepan and brown onions and garlic.<BR>2. Add the rest of the ingredients and mix well, then simmer one or two minutes. Remove to a deep dish. This sauce can be served with any vegetable or meat satay.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'32E499A72649C140BC0BCBD502B5A216', X'1D930355FF169B4C889933FE2C877648', X'1D930355FF169B4C889933FE2C877648', 0, 'javascript:', 'Pickled Chilies I', 2, NULL, 0, 'Pickled Chilies I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1604', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 cups small chili peppers, rinsed with boiling water<BR>2 cups rice vinegar<BR>3/4 cups granulated sugar<BR>2 Tablespoons coarse salt<BR>4 whole star anise<BR>8 Sichuan peppercorns<BR><B><I>Preparation:</B></I><BR>1. Sterilize four half-pint canning jars, their covers, and their lids.<BR>2. Dry the chili peppers, then remove their stems but do not cut into the peppers.<BR>3. In an enamel or another non-reactive pot, add the vinegar, sugar, and salt and heat until the sugar is totally dissolved, then bring to the boil and allow to cool somewhat.<BR>4. Pack the chili peppers into the jars, add one star anise and two Sichuan peppercorns into each jar. Pour warm prepared liquid over the chil peppers and jiggle the jars so that all bubbles rise to the top before sealing them tightly. Invert them for two minutes, then right them and set aside in a draft-free place until completely cool. Then set aside for at least two weeks before using them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C564D0CA4928594884936F64CD6AC517', X'ADA0636735AF2948981C3C64DE1F948A', X'ADA0636735AF2948981C3C64DE1F948A', 0, 'javascript:', 'Pickled Chilies II', 2, NULL, 0, 'Pickled Chilies II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1677', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 cups small chili peppers, rinsed with boiling water<BR>2 cups rice vinegar<BR>3/4 cups granulated sugar<BR>2 Tablespoons coarse salt<BR>4 whole star anise<BR>8 Sichuan peppercorns<BR><B><I>Preparation:</B></I><BR>1. Sterilize four half-pint canning jars, their covers, and their lids.<BR>2. Dry the chili peppers, then remove their stems but do not cut into the peppers.<BR>3. In an enamel or another non-reactive pot, add the vinegar, sugar, and salt and heat until the sugar is totally dissolved, then bring to the boil and allow to cool somewhat.<BR>4. Pack the chili peppers into the jars, add one star anise and two Sichuan peppercorns into each jar. Pour warm prepared liquid over the chil peppers and jiggle the jars so that all bubbles rise to the top before sealing them tightly. Invert them for two minutes, then right them and set aside in a draft-free place until completely cool. Then set aside for at least two weeks before using them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AB91AFDD1BD1BD43853209B5965FA5D2', X'37EEFCDB5911D841A1853F87D40D39DB', X'37EEFCDB5911D841A1853F87D40D39DB', 0, 'javascript:', 'Pickled Ginger', 2, NULL, 0, 'Pickled Ginger', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2179', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 and ½ pounds fresh ginger, peeled and very thinly sliced<BR>4 cups Chinese rice vinegar<BR>2 cups granulated sugar<BR>½ cup cider vinegar<BR>½ cup white vinegar<BR>1/4 cup coarse salt<BR><B><I>Preparation:</B></I><BR>1. Put ginger slices in a glass bowl and cover them with boiling water for five minutes, the drain and remove them to another clean glass bowl and add the rice vinegar and the sugar, stirring until the sugar is dissolved. 2. Now add the cider and the white vinegar and the salt and put into the microwave for one minute, then stir. If the salt has yet to dissolve, repeat for another minute. Now cool this.<BR>3. Cover and refrigerate for twenty-four hours. Now it is ready for use. Always store it in the refrigerator.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8CDE3850EA15E24984AB25CD3559BB80', X'02DACAF5D37AED45AF9DDCC227618A94', X'02DACAF5D37AED45AF9DDCC227618A94', 0, 'javascript:', 'Pickled Ginger, Dong Style', 2, NULL, 0, 'Pickled Ginger, Dong Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1684', '<TD><RECIPE>Mix scraped fresh young ginger with crushed chili peppers, and salt. Pack into a jar and seal with plastic wrap and string. Store in a cool dark place for one month before enjoying it.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'24C141B97D0E1C41A6C3FB4CA4F2E419', X'0230B6E142B17A4B9F4E53BFD4B4A697', X'0230B6E142B17A4B9F4E53BFD4B4A697', 0, 'javascript:', 'Plum Sauce', 2, NULL, 0, 'Plum Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1582', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1/2 teaspoon vegetable oil<BR>2 cloves garlic, minced<BR>2 Tablespoons minced fresh ginger<BR>1/2 cup chutney, blended<BR>2 Tablespoons Chinese brown sugar slabs, crushed, or dark brown sugar<BR>2 Tablespoons fresh-squeezed lemon juice<BR>1 Tablespoon thin soy sauce<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add oil and the garlic and ginger, and stir-fry for one minute until very aromatic.<BR>2. Add, chutney and brown sugar, and stir-fry for two minutes, then add lemon juice and soy sauce and let this bubble for one minute. Then remove from the heat and allow this mixture to cool. Then refrigerate it.<BR><B>Note:</B> This sauce stays well in the refrigerator for a couple of weeks. However, better yet pour it into an ice cube tray and freeze; and once frozen, pop the frozen cubes and store them in a thick plastic tightly closed bag in the freezer. Take out one or two or however many needed half hour before use leaving the others frozen.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A1BCFE28B81C13459C4BAF7798E58B72', X'FEDBCC7021F81D46BC517C258DCED6B0', X'FEDBCC7021F81D46BC517C258DCED6B0', 0, 'javascript:', 'Plum Sauce, for now', 2, NULL, 0, 'Plum Sauce, for now', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=580', '<TD><RECIPE>Put four tablespoons each of plum jelly, chutney, and dry mustard, and one-half cup of apricot or plum preserves into a blender. Switch on and off a few times. Then add two tablespoons each of rice vinegar and cornstarch, one teaspoon of chili powder, a teaspoon of garlic powder, and a cup of cold water. Blend until well-mixed, and put this mixture into a bowl. Add up to one teaspoon of coarsely chopped hot red peppers, depending upon how piquant you like your plum sauce, and stir well. Pour into a freezer tray, put this into the freezer and the next day, pop the cubes and put them into a freezer bag; remove excess air, and seal and return to the freeer. Defrost one or more cubes an hour before they are needed.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C42155E7B704B74493FF07F0029F980C', X'E7C0E26CF860454EBCE5B23FBD214643', X'E7C0E26CF860454EBCE5B23FBD214643', 0, 'javascript:', 'Plum Sauce, Made in Advance', 2, NULL, 0, 'Plum Sauce, Made in Advance', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=581', '<TD><RECIPE>Take a pound of plums and peel and pit them. Then chop the pul coarsely and put into a large pot. Add one-quarter cup this soy sauce, one-quareter cup yellow or brown bean sauce, three-quarters of a cup of applesauce, chutney or a combination of both, one-half cup sugar, one-quarter cup rice vinegar or a mixture of rice and black vinegars, and a teaspoon of salt. Mix well, add a cup of cold water and two tablespoons cornstarch. Mix again and slowly bring to the boil. Reduce ehat. Then cover the pan and simmer for an hour, stirring every fifteen minutes or so. When the plum sauce is half cooked, bring eight cups of water to the boil, put in four clean canning jars, covers, and lids, and simmer these for twenty minues, then turn off the heat under this pot. When plum sauce is finished cooking, ladles it into these jars, seal them, and allow them to cool. Then refrigerate for at least three weeks, then use as desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5A853A333849184FB37C535736EF36DA', X'4FEF0694A501F04A923147BAA4FD937C', X'4FEF0694A501F04A923147BAA4FD937C', 0, 'javascript:', 'Poh Piah Spring Roll Sauce', 2, NULL, 0, 'Poh Piah Spring Roll Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1520', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 teaspoon <I>assam</I> (tamarind), seeds removed<BR>2 teaspoons chili powder<BR>1 Tablespoon vegetable oil<BR>3 Tablespoons shallots, peeled and thinly sliced<BR>3 Tablespoons raisons<BR>3 Tablespoons dates, pitted<BR>2 teaspoons chili powder<BR>1/2 teaspoon salt<BR>1 and 1/2 teaspoons sugar<BR><B><I>Preparation:</B></I><BR>1. Break up the tamarind into smaller pieces and soak them in two tablespoons warm water and let it rest there until softened. Check for any remaining seeds by squeezing the pulp between the fingers. This does remove most of them; then strain the liquid.<BR>2. Mix chili powder with two teaspoon of water preparing it as a paste.<BR>3. Heat the oil and fry the shallot slices until brown, stirring so they do not burn and turn black. Then set them aside on a plate, and fry the chili paste until fragrant.<BR>4. In a food processor, blend the chilies, fried shallots, dates, and raisons. Then transfer this to a saucepan and add half a cup of water, the tamarind liquid, salt, and sugar and bring to the boil, reduce the heat, and simmer for five minutes. Serve warm with any <I>poh piah</I>, spring roll, or dumpling-like food.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2D93791E662992438A0E43057B97428A', X'CDEA70EF11CE3D42BB82C3751E7BAEB6', X'CDEA70EF11CE3D42BB82C3751E7BAEB6', 0, 'javascript:', 'Ponzu Sauce', 2, NULL, 0, 'Ponzu Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=747', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>zest of one lemon<BR>1/2 cup mirin (or Swatow or another mild rice vinegar)<BR>1/2 cup lemon juice<BR>6 Tablespoons soy sauce (we suggest the thin variety)<BR>1/2 cup of sugar<BR>1 Tablespoon minced green onion<BR><B><I>Preparation:</B></I><BR>1. Combine all the ingredients in a jar or bowl. Shake or stir until the sugar dissolves. <B>Note:</B> Store this dressing a covered container in the refrigerator for up to a month. Serve it with salads, steamed fish, even coleslaw using Ponzu Sauce instead of the recommended dressing.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6D773A1560C52641A601E07355A32DAD', X'D8333157AF20D84E9D8A9CCA7DCD420A', X'D8333157AF20D84E9D8A9CCA7DCD420A', 0, 'javascript:', 'Red Wine Paste (also known as Red Wine Lees)', 2, NULL, 0, 'Red Wine Paste (also known as Red Wine Lees)', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=296', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 squares fermented red bean paste<BR>1/4 cup cooked glutinous rice<BR>3 Tablespoons mao tai or other hard liquor<BR>1/2 cup Fujian rice wine such as Loh Chiew<BR>1/2 cup Chinese brown wine such as a Shaoxing <BR>3 ounces or a slab of brown sugar, broken into small pieces<BR>2 teaspoons minced garlic<BR>2 teaspoons minced ginger root<BR>2 teaspoons rice or sweet potato flour<BR>1 teaspoon dark soy sauce<BR>2 or 3 drops of red food coloring<BR>1 tangerine peel, soaked then minced very fine<BR><I><B>Preparation:</I></B><BR>1. Pour all ingredients into a blender and let stand for about fifteen minutes until the sugar is wet and dissolved. Then blend for two minutes. Do this in half minute to one minute amounts of time so that you do not burn out the motor.<BR>2. Put the blended items into an enamel or other non-reactive metal pot and simmer on very low heat for half an hour. Cool and store in the refrigerator. This paste will keep about a month in the refrigerator or it can be frozen for up to six months.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D8EB603E2C71C442A2F3A952A9EDAAB3', X'3B46F59511B6134794E7AA1BC28A59E8', X'3B46F59511B6134794E7AA1BC28A59E8', 0, 'javascript:', 'Salted Lemon Paste', 2, NULL, 0, 'Salted Lemon Paste', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1183', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound salted lemon<BR>1 fresh lemon for its juice and zest<BR>6 Tablespoons white vinegar<BR>1/2 pound white sugar<BR><B><I>Preparation:</B></I><BR>1. Coarsely mince the salted lemons; and discard all pits. Put into a non-reactive pot such as an enamel one or one made of stainless steel.<BR>2. In another pot, bring sugar and three tablespoons water to the boil; then pour this into the salted lemon mix, and stir in the lemon juice and the zest and return it to the boil, then reduce the heat and allow to cook until the mixture is very sticky, before setting it aside to cool.<BR>3. Put into a glass jar and refrigerate until needed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3E06280054E6504F8A5EAFDDFED181CB', X'7A7E05B2A7F47945835C0A0C30858B83', X'7A7E05B2A7F47945835C0A0C30858B83', 0, 'javascript:', 'Scallion Oil', 2, NULL, 0, 'Scallion Oil', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=579', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups peanut oil<BR>3 cups scallions, cut into three-inch sections<BR><B><I>Preparation:</I></B><BR>1. Heat a wok over medium heat, then add the peanut oil. When add hot carefully put in the scallion sections. When they turn brown, remove oil from the heat source and cool.<BR>2. Strain the oil through a fine strainer and store in a clean glass jar in a cool place for two days. After that, refrigerate the oil.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6A39FB61B186A74BBC060CEDE71D0AA5', X'3A5D1658DB6A96428F2BD05E4C2BA5E5', X'3A5D1658DB6A96428F2BD05E4C2BA5E5', 0, 'javascript:', 'Sesame Oil Flavored Sauce', 2, NULL, 0, 'Sesame Oil Flavored Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=909', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon vegetable oil<BR>1 clove garlic, peeled and minced<BR>3 slices fresh ginger, peeled and minced<BR>2 Tablespoons sesame oil<BR>2 Tablespoons thin soy sauce<BR>1/2 teaspoon salt<BR>2 Tablespoons cornstarch<BR><B><I>Preparation:</B></I><BR>1. Heat vegetable oil in a small pot, then add garlic and ginger and stir-fry half minute before adding the sesame oil and the soy sauce.<BR>2. Mix salt and cornstarch with half cup cold water. Stir into the sauce mixture and stir until slightly thickened and clear. Serve on or next to dumplings or other dough-wrapped foods.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'472AB0D2D9FA254F8243377A606FE69D', X'F9B88D36AE85024DA3AF20A40DC3019C', X'F9B88D36AE85024DA3AF20A40DC3019C', 0, 'javascript:', 'Sesame Sauce for Noodle Dishes', 2, NULL, 0, 'Sesame Sauce for Noodle Dishes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2150', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon sesame oil<BR>2 Tablespoons sesame paste<BR>2 Tablespoons thin soy sauce<BR>2 Tablespoons cider vinegar<BR>2 teaspoons granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Mix sesame oil and sesame paste until well blended.<BR>2 Add other ingredients and mix until smooth; then pour on any hot noodle dish.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1C63AA5D16819E429C54C130EB12918A', X'5A549534E672EE4F8DFE2F72BF3464DF', X'5A549534E672EE4F8DFE2F72BF3464DF', 0, 'javascript:', 'Soy-based Lu Stock', 2, NULL, 0, 'Soy-based Lu Stock', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=991', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons oil<BR>4 or 5 cloves of garlic, crushed lightly<BR>3 or 4 slices of fresh ginger<BR>2 scallions, cut into one- to two-inch sections<BR>2 cups soy sauce<BR>1/2 to 1 cup Chinese rice wine<BR>2 star anise<BR>1 Tablespoon Sichuan pepper<BR>1 to 2 pieces stick cinnamon<BR>4 or 5 whole cloves<BR>2 0r 3 pieces dried ginger<BR>½ Tablespoon whole cumin<BR>1 or 2 Chinese or western nutmeg<BR>1 piece dried tangerine peel<BR>1 or 2 pieces dried licorice root<BR>2 or 3 bay leaves<BR>2 teaspoon white peppercorns<BR>1 Tablespoon rock sugar<BR>1/2 teaspoon salt<BR><B><I>Preparation:</I></B><BR>1. Heat oil and saute garlic, fresh ginger, and scallions. Then add soy sauce and eight cups of water; then mix in wine and all the rest of the ingredients (which can be placed in a large mesh or cheesecloth bag). Bing to the boil, then reduce heat and simmer twenty minutes.<BR>2. Remove all spices (they can be dried and used another time).<BR>3. Cool and refrigerate. Then use all or some, as desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FBBEA5A9435B1A4FB1393B10C3A9ED10', X'249C5EBE6A98904D92CBD2CB72FEF5A2', X'249C5EBE6A98904D92CBD2CB72FEF5A2', 0, 'javascript:', 'Spice Marianade', 2, NULL, 0, 'Spice Marianade', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1212', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 slices fresh ginger, coarsely chopped<BR>3 shallots, peeled and smashed<BR>1 Tablespoon tangerine peel, sliced thinly<BR>5 whole star anise<BR>1 teaspoon whole cloves<BR>2 cloves garlic, peeled and smashed<BR>2 Tablespoons dry parsley1 whole chili<BR>1 whole <I>tsaoko</I><BR>1 teaspoon red fermented wine lees<BR>1/2 teaspoon sweet basil seeds<BR>3 Tablespoons solid shortening, if needed<BR><B><I>Preparation:</B></I><BR>1. Simmer all of these spices in five cups of water for two hours.<BR>Allow to come to room temperature, then strain and refrigerate. <B>Note:</B> Use within three to five days. If not using in five days, add three tablespoons solid shortening to the spice marinade, return to the boil, add the shortening and before it solidifies, top it off with melted solid shortening, pour into a clean bottle, then refrigerate.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FBB12AA4FA74EB4BA4C5D1E74DE9EEC0', X'5F4B56098A83634ABC65CE8443888CA8', X'5F4B56098A83634ABC65CE8443888CA8', 0, 'javascript:', 'Spicy Dipping Sauce', 2, NULL, 0, 'Spicy Dipping Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=419', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 tablespoons fish sauce<BR>2 or 3 red chili peppers<BR>2 scallions, minced<BR>1/2 cup coriander leaves, minced<BR><B><I>Preparation:</B></I><BR>1. Use a mortar and pestle, grind these ingredients together. Then put this in a small bowl to be added to every dish, as desired.<BR>2. Refrigerate the sauce when not in use.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2258BA9EF9464E4588E31F3487B7DE5E', X'A269618758829D4B8D0E8A6D98463AFB', X'A269618758829D4B8D0E8A6D98463AFB', 0, 'javascript:', 'Swiss Sauce', 2, NULL, 0, 'Swiss Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1580', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon dark soy sauce<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon oyster sauce<BR>5 teaspoons granulated sugar<BR>1/4 teaspoon ground five-spice powder<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix the three sauces then add the sugar and stir until dissolved.<BR>2. Add five-spice powder and the sesame oil, and three tablespoons of cold water, then mix well.<BR>3. Set this mixture aside for ten minutes, then it is ready to be used as a sauce in a stir-fried dish or a marinade for chicken or beef before cooking.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'98C98830DC26594691B781090B28171A', X'A57E2F113F01EA42BA28D59AA5BD5BD6', X'A57E2F113F01EA42BA28D59AA5BD5BD6', 0, 'javascript:', 'Tibetan Hot Sauce (Sipen Mardur)', 2, NULL, 0, 'Tibetan Hot Sauce (Sipen Mardur)', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=381', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 medium tomato<BR>3 teaspoons red chili pepper flakes, or to taste<BR>1 cup plain yogurt<BR>3 cloves garlic, finely minced<BR>salt to taste<BR>1 teaspoon minced ginger<BR>1/2 cup finely chopped cilantro<BR>1 Tablespoon paprika<BR><B><I>Preparation:</B></I><BR>1. Boil the tomato for two minutes then place in a blender along with the red pepper flakes and yogurt. Add the garlic, salt, and ginger and blend for two minutes then pour this into a mixing bowl.<BR>2. Add the cilantro and paprika. Cover with plastic wrap and chill before using.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1C5B4830F38D4E48A80F7E9DF99E4CE1', X'0B42288F5960914EBE2A662400F06383', X'0B42288F5960914EBE2A662400F06383', 0, 'javascript:', 'XO Sauce', 2, NULL, 0, 'XO Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=640', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 dried scallops<BR>2 Tablespoons dried tiny shrimp, shells and vein removed<BR>3 Tablespoons corn oil<BR>3 shallots, peeled and minced (see note below)<BR>5 chili peppers, seeded and crushed<BR>2 Tablespoons chopped Yunnan or Smithfield ham, (or two tablespoons thin soy sauce)<BR><B><I>Preparation:</B></I><BR>1. Steam scallops in one cup of water over rapidly boiling water, for one hour. Then remove them from the water, reserving it, and when cool, tear the scallops into individual strands.<BR>2. Soak shrimp for half an hour, drain, then finely chop them.<BR>3. Heat oil and fry the shallots for two minutes, then drain well and set aside.<BR>4. Fry scallop strands, minced shrimp, peppers, and ham for two minutes.<BR>5. Add the reserved scallop liquid, and enough additional water to make a cup and a half of liquid, into a small pot. Simmer uncovered for half an hour.<BR>6. Sterilize two small jars. Divide the mixture, now called XO sauce, half in each bottle, and refrigerate.<BR><B>Note:</B> Commercially fried shallots can be used.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A2C0C4B28470124FAC52DA1D19853F69', X'9578008E856F59448FCB77A7645020CC', X'9578008E856F59448FCB77A7645020CC', 0, 'javascript:', 'Abalone and Fish Ball Soup', 2, NULL, 0, 'Abalone and Fish Ball Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=423', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound crab meat, cartilage removed<BR>4 ounces Chinese bacon, minced<BR>1/2 pound canned abalone, diced<BR>1 Tablespoon minced Smithfield ham<BR>2 Tablespoons minced fresh coriander leaves<BR>3 egg whites<BR>1 Tablespoon cornstarch<BR>1/4 teaspoon salt<BR>1/8 teaspoon white pepper<BR>8 cups chicken broth<BR><B><I>Preparation:</B></I><BR>1. Gently mix all the ingredients except the chicken broth and then form it into one-inch balls. Put them on a greased bowl and steam them for ten minutes. Be sure when removing them from the steamer to reserve the liquid.<BR>2. Heat chicken broth with the liquid from the bowl of fish balls. When it comes to the boil, add the fish balls and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A4DB9FA2717FE14292B897CA3D27B50B', X'868834D573EEB043B86824A761911A51', X'868834D573EEB043B86824A761911A51', 0, 'javascript:', 'Abalone Soup', 2, NULL, 0, 'Abalone Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1963', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound can of abalone<BR>2 teaspoons cornstarch<BR>2 Teaspoons thin soy sauce<BR>2 teaspoons Chinese rice wine<BR>1 ten-ounce cans condensed chicken broth<BR>1 small cucumber, peeled and sliced thin<BR>salt and ground white pepper, to taste<BR>1 scallion, angle slices in thin slivers<BR><B><I>Preparation:</B></I><BR>1. Cut the abalone into very thin strips, mix with the cornstarch, soy sauce, and the rice wine.<BR>2. In a three or four quart pot, bring this mixture to the boil with two cans of cold water, the cucumber, salt and pepper, and the scallion slivers. Reduce the heat and simmer for two minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'11EA722565DDF7478C2327F0B68D3045', X'3340FAA9A042A94BB65FF805200DC20D', X'3340FAA9A042A94BB65FF805200DC20D', 0, 'javascript:', 'Abalone, Monkey Head, and Chicken Soup', 2, NULL, 0, 'Abalone, Monkey Head, and Chicken Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1488', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 dried monkey-head mushrooms<BR>1 slice fresh ginger, smashed with side of a cleaver, then cut in four<BR>1 scallion, minced<BR>1 abalone, internal organs removed and discarded, then sliced<BR>1/2 black chicken or a regular soup chicken, about one and a half pounds, cut into four pieces<BR>1 pork shank bone, about one-half pound<BR><B><I>Preparation:</B></I><BR>1. Soak dried mushroom with ginger and scallion for one hour, then remove, squeeze out any extra water, and slice them. Reserve the ginger and scallion pieces and add them to the soup just before serving.<BR>2. Put mushrooms, reserved ginger and scallion pieces, abalone, pieces of chicken, and the pork shank bone into a large pot, add ten cups of water, and bring to the boil, but before it does boil, reduce the heat to a simmer, cover the pot, and simmer for three hours.<BR>3. Remove chicken pieces, discard skin and bone, and remove the shank bone to take meat off it. Tear this meat into about ten pieces and return it to the soup. Skim any fat off the soup, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'35AD858F1CE8ED4B93961506816E9578', X'9D19572F1BAB01429319860C89281215', X'9D19572F1BAB01429319860C89281215', 0, 'javascript:', 'Apricot and Bean Curd Soup', 2, NULL, 0, 'Apricot and Bean Curd Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2312', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 bean curd cakes, one firm the other soft, sliced thin<BR>2 quarts chicken stock<BR>1 teaspoon coarse salt<BR>6 ripe apricots, seeds discarded, each cut in eight slices<BR>1 large Chinese black mushroom, soaked in half cup warm water for half an hour, stem discarded, then minced<BR><B><I>Preparation:</B></I><BR>1. Put bean cakes, stock, and salt up to boil, then reduce the heat and simmer for twenty minutes.<BR>2. Add apricot slices and the minced mushrooms, and simmer for three minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2281087AD56F61438D2D91C16635906B', X'5577295029943849A40E01B52908B0CE', X'5577295029943849A40E01B52908B0CE', 0, 'javascript:', 'Apricot and Bean Curd Soup', 2, NULL, 0, 'Apricot and Bean Curd Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2311', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 bean curd cakes, one firm the other soft, sliced thin<BR>2 quarts chicken stock<BR>1 teaspoon coarse salt<BR>6 ripe apricots, seeds discarded, each cut in eight slices<BR>1 large Chinese black mushroom, soaked in half cup warm water for half an hour, stem discarded, then minced<BR><B><I>Preparation:</B></I><BR>1. Put bean cakes, stock, and salt up to boil, then reduce the heat and simmer for twenty minutes.<BR>2. Add apricot slices and the minced mushrooms, and simmer for three minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'88CCE2D02361C642AD4727AC8707BD2F', X'DD1E50FAAEB3EF47BF05C1A0C991CE37', X'DD1E50FAAEB3EF47BF05C1A0C991CE37', 0, 'javascript:', 'Astragalus Pork Soup', 2, NULL, 0, 'Astragalus Pork Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2337', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 puonds pork ribs, cooked for ninety minutes in four cups of water or in a meat broth<BR>1 pre-mixed herbal soup pack, optional<BR>30 goji berries<BR>1/2 cup rice<BR>15 dried longan, pitted<BR>1 Chinese yam, peeled and diced<BR>4 dried red dates, pitted<BR>3 dried slices astragulus roots<BR>10 astragulus seeds, optional<BR><B><I>Preparation:</B></I><BR>1. Cook herbal packer items, if using, with all the other herbal items.<BR>2. Remove and discard all herbs and cut meat off the bones into half-inch pieces, and discard all bones, putting meat back into the soup, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8B0FD1053BA2824781CD552E552C9441', X'1BA766E487C9F94BB52206E12FE1BA47', X'1BA766E487C9F94BB52206E12FE1BA47', 0, 'javascript:', 'Astraguus Fish and Broccoli Soup', 2, NULL, 0, 'Astraguus Fish and Broccoli Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2338', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 astragalus root slices boiled in six cups of water for one hour, then discard the roots, and reserve the liquid<BR>2 fresh Chinese black mushrooms, stems discarded, caps sliced thin<BR>1 cup one-inch pieces of a white fish, skin and bones discarded<BR>2 Tablespoons cornstarch mixed with same amount of cold water<BR>1 cup small broccoli flowerettes<BR>1 tomato, skin removed and discarded, and diced<BR>2 Tablespoons goji berries<BR><B><I>Preparation:</I></B><BR>1. Using the liquid from the roots, add the mushrooms and simmer for twenty minutes, then add the fish and the cornstarch mixture, and bring to the boil and stir.<BR>2. Next add the broccli, tomato, and goji berries and simmer five more minutes, then serve<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2ABE1500761C264ABE888D255B36F9CF', X'1BEC0B2DB2671342A07A15A1D44BAB45', X'1BEC0B2DB2671342A07A15A1D44BAB45', 0, 'javascript:', 'Astralagus Herbal Soup', 2, NULL, 0, 'Astralagus Herbal Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2327', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 Tablespoons olive oil<BR>2 cloves fresh garlic, peeled and slivered<BR>1 large onion, sliced thin<BR>3 Tablespoons fresh ginger, peeled and slivered<BR>10 pieces of dried astralagus<BR>1 medium reishi mushroom, soaked until soft, stem discarded, and sliced thin<BR>salt and pepper to taste<BR>½ teaspoon cayenne powder<BR>1 cup shredded kale<BR>½ cup shredded yellow or green squash<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the olive oil, and saute the garlic, onion, and ginger until soft.<BR>2. Add half cup cold water and the astralagus and the mushroom and simmer for ten more minutes. Then add six cups of boiling water to the wok or fry pan while it is off its heat source; and let it rest for two hours.<BR>3. Remove astralagus and the mushrooms, if preferred, then add the salt, pepper, and cayenne and the two vegetables and simmer together for fifteen minutes. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AE48AF32E51B5C4CA9117693946AF3A8', X'60E16D4A23F12C4F8E359D06F080AB79', X'60E16D4A23F12C4F8E359D06F080AB79', 0, 'javascript:', 'Banquet Soup', 2, NULL, 0, 'Banquet Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=859', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pounds fresh tomatoes<BR>13 medium fresh mushrooms<BR>1/4 pound snow pea pods or bok choy<BR>2 Tablespoon vegetable oil<BR>2 Tablespoon sherry or rice wine<BR>4 cups chicken broth<BR>1/2 teaspoon sugar<BR>1 teaspoon of Asian sesame oil<BR>4 Tablespoon cornstarch dissolved in six tablespoon of water<BR>1 can creamed corn or 1 cup of fresh or frozen corn kernels<BR>1 large egg white, beaten<BR>1/8 cup chopped coriander leaves<BR>salt or soy sauce to taste<BR><B><I>Preparation:</I></B><BR>1. Coarsely chop the tomatoes, mushrooms, and bok choy.<BR>2. Heat oil and then add wine to evaporate the alcohol.<BR>3. Add chicken broth, Asian sesame oil, and sugar and bring to simmer.<BR>4. Add the vegetables, corn and cornstarch mixture and simmer until the peas are just barely cooked.<BR>5. Dribble in the egg white in simmering soup to cook into strings and add coriander at the end. Salt to taste.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3E840F878A110D40B9BF704360CBCB30', X'81369E72F4FA494A86B8864205936A91', X'81369E72F4FA494A86B8864205936A91', 0, 'javascript:', 'Barbecued Pork and Noodle Soup', 2, NULL, 0, 'Barbecued Pork and Noodle Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=659', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound barbecued pork<BR>6 dried black mushrooms, soaked for twenty minutes, stems removed<BR>1/2 cup canned water chestnuts or bamboo shoots, sliced thinly<BR>1 cup Chinese broccoli (<I>gai lan</I> or <I>bok cai</I>), sliced or cut into one-inch pieces<BR>1/2 pound dried egg noodles<BR>6 cups chicken broth<BR>1 teaspoon Chinese rice wine, such as Shaoxing<BR>1/2 teaspoon sesame oil<BR>2 scallions, angle-sliced thinly<BR><B><I>Preparation:</B></I><BR>1. Slice pork and mushrooms thinly, and mix with water chestnut slices.<BR>2. Blanch green vegetable in boiling water for one minute, then immerse in ice water for one minute, and drain well.<BR>3. Cook noodles in boiling water for eight minutes, then drain well, and distribute into eight soup bowls.<BR>4. Distribute pork mixture into these same eight bowls.<BR>5. Bring broth to the boil, add rice wine and sesame oil, and pour into the soup bowls; then top with slivered scallions and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D2C6EF83FEE5A14795FE94C68E11FBF6', X'83369DA7854D9A4BAFDA7E504471D440', X'83369DA7854D9A4BAFDA7E504471D440', 0, 'javascript:', 'Basic Pork Soup Stock', 2, NULL, 0, 'Basic Pork Soup Stock', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=177', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 pounds meaty pork neck bones<BR>half tablespoon salt<BR>12 to 14 cups water<BR>2 Tablespoons yellow rock sugar<BR><I><B>Preparation:</I></B><BR>1. Place ingredients into a large stock pot. Bring to a boil then skim the surface.<BR>2. Add packaged ingredients and simmer for several hours or as directed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B260F5648175524DB2D8E360E383815D', X'9C67B0F786954D40950FDC50838FBBF7', X'9C67B0F786954D40950FDC50838FBBF7', 0, 'javascript:', 'Bean Curd and Black Tea Leaves with Soup', 2, NULL, 0, 'Bean Curd and Black Tea Leaves with Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=175', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 Tablespoon tea, lychee black or Keemun preferred<BR>2 star anise<BR>2 inches of whole cinnamon<BR>2 slices ginger <BR>1 scallion, minced<BR>2 Tablespoons dark or mushroom soy sauce<BR>1 teaspoon Chinese brown sugar, crushed<BR>2 Tablespoons rice wine<BR>1 cup chicken broth<BR>one piece of cheesecloth about twelve inches square<BR>4 squares brown bean curd, cut across the flat way<BR><B><I>Preparation:</I></B><BR>1. Put tea, anise, cinnamon, ginger, and scallion into the cloth; tie it loosely but in such a way that nothing will fall out.<BR>2. Put everything into a saucepan and simmer for twenty minutes. Remove the cheesecloth bag and discard, and put the bean curd in the bottom of each soup bowl and pour the liquid over them. Or serve the bean curd as a side dish and serve the soup separately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BBA7EC54B6EA7344B4FF6B4F6D859A92', X'01F6794BCBCA024C9DB21A07324D6EEC', X'01F6794BCBCA024C9DB21A07324D6EEC', 0, 'javascript:', 'Bean Curd and Shrimp Soup', 2, NULL, 0, 'Bean Curd and Shrimp Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1548', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 cups chicken stock<BR>1/4 pound shelled shrimp<BR>1 cup green peas <BR>1/2 pound bean curd, cut into half-inch cubes<BR>4 Tablespoons ham, cut into one-quarter-inch cubes<BR>2 Tablespoons soy sauce<BR>1 Tablespoon finely chopped pickle<BR>1 Tablespoon cornstarch mixed with one cup cold water<BR>1 egg, beaten<BR>1 teaspoon salt<BR>1/2 teaspoon pepper<BR><B><I>Preparation:</B></I><BR>1. Place chicken stock in a large pot and bring to boil.<BR>2. Add shrimp, peas, bean curd, ham, soy sauce, and the pickle and bring to boil, then reduce heat, cover, and simmer for five minutes.<BR>3. Add cornstarch mixture, egg, salt and pepper, and stir constantly to thicken somewhat. Serve immediately in individual soup bowls or from a soup tureen.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'543C801DE1E8364D80062353D9C53AE5', X'F49A094D8B6FA34E8C7C889BECC2E4E3', X'F49A094D8B6FA34E8C7C889BECC2E4E3', 0, 'javascript:', 'Bean Curd Soup', 2, NULL, 0, 'Bean Curd Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=743', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 dried scallops, soaked for three hours<BR>3 dried cloud-ear mushrooms, soaked in warm water for ten minutes, then cut in thin strips<BR>3 dried Chinese black mushrooms, soaked in warm water for ten minutes, then cut into thin strips<BR>2 Tablespoons sesame oil<BR>2 Tablespoons corn oil<BR>1 dried hot red pepper, seeds removed<BR>1 long Chinese cruller, cut into half-inch slices<BR>1 egg, beaten<BR>1/2 teaspoon water chestnut flour<BR>2 slices fresh ginger, peeled and slivered<BR>1 teaspoon coarsely ground fennel<BR>1 square firm bean curd, cut into half-inch slices<BR>1 Tablespoon white rice wine<BR>1 Tablespoon black vinegar<BR>1 Tablespoon mushroom soy sauce<BR>dash ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Steam dried scallop for half an hour, remove and tear into shreds, and mix with the scallop shreds.<BR>2. Heat both oils and fry hot pepper for half to one minute, depending on how piquant you want the soup to be, then discard the pepper.<BR>3. Fry cruller slices until lightly browned, then drain and set aside on paper towels.<BR>4. Mix egg with water chestnut flour, then fry, turning it once. When lightly set, remove and cut into thin slices.<BR>5. Fry ginger and fennel for one minute, add bean curd and fry for another minute.<BR>6. Add four cups water, the rice wine, vinegar, and soy sauce and bring to just below the boil, then add all reserved ingredients and the ground pepper but not the cruller slices. Reduce the heat, and simmer for ten minutes, add cruller slices, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'54CADA99837308458B80C66E08FC41ED', X'627ACA601F712E4990B34E4C2D3C90A4', X'627ACA601F712E4990B34E4C2D3C90A4', 0, 'javascript:', 'Beef and Egg Congee', 2, NULL, 0, 'Beef and Egg Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=184', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 cup long-grain rice<BR>1/4 cup sweet rice<BR>4 ounces flank steak, slivered<BR>1 teaspoon soy sauce<BR>1 teaspoon rice wine<BR>1 teaspoon sesame oil<BR>8 cups chicken broth<BR>1 egg, beaten<BR>1 teaspoon cornstarch mixed with 1 teaspoon water<BR>1 scallion, minced<BR><B><I>Preparation:</I></B><BR>1. Soak both rice varieties in a cup of cold water for twenty minutes, then drain. Then add three to six cups cold water and cook them for two hours (more water if you like your congee thinner, less if you like it thicker).<BR>2. Mix flank steak with soy sauce, wine, and sesame oil.<BR>3. Heat the broth, but do not boil it. Then add the beef mixture and simmer for two minutes. Add this to the rice, stir well and bring it to the boil. Slowly add the egg, stirring continuously. <BR>4. Add the cornstarch mixture and stir well. Remove from the heat, add the scallion, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E0BF3504B8BDC1428918DCCFFBA05C5C', X'C511F36E793EF441904BD9F8B45EA8CE', X'C511F36E793EF441904BD9F8B45EA8CE', 0, 'javascript:', 'Beef Soup', 2, NULL, 0, 'Beef Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=208', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>4 pounds of marrow bones, preferably from the leg of a cow.<BR>2 pounds beef, round or shoulder, cubed<BR>1 pound of onions, cubed<BR>2 Tablespoons garlic powder<BR>2 teaspoons salt<BR>2 teaspoons ground black pepper<BR><I><B>Preparation:</I></B><BR>1. Put the bones and two gallons of water in a large pot and bring to the boil. Reduce heat to simmer and cook for one hour.<BR>2. Add beef and simmer for an additional hour, then skim and remove the fat. <BR>3. Add onions and the rest of the ingredients and cook for fifteen minutes, then serve up to fifteen people.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4F1C53E888A8924183C40D7BC3C7478F', X'53E1C5904FEB404880CE98BEC41253E8', X'53E1C5904FEB404880CE98BEC41253E8', 0, 'javascript:', 'Beef Tendon and Sea Cucumber Soup', 2, NULL, 0, 'Beef Tendon and Sea Cucumber Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1579', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound beef tendons<BR>1 soaked sea cucumber<BR>1 quart chicken broth<BR>2 Tablespoons <I>gogi</I> berries<BR>1 teaspoon salt<BR>1/4 pound bamboo mushrooms, soaked for ten minutes, and cut into two-inch pieces<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon Shaoxing rice wine<BR><B><I>Preparation:</B></I><BR>1. Soak the tendons overnight in the refrigerator, then cut them into one- to two-inch pieces.<BR>2. Soak the sea cucumber overnight in another bowl, and in the morning remove their innards and cut them into one-inch squares.<BR>3. Put the tendon and sea cucumber pieces in two separate pots, each with two quarts of water. Bring each pot to the boil, then simmer for half an hour. Test the ingredients in each one, and if not soft, continue simmering, testing every fifteen minutes until they are. Then drain and set them aside.<BR>4. Put the broth, the soft tendons and the soft sea cucumber into a large pot with the goji berries, salt, the cut-up bamboo mushrooms, soy sauce, and the rice wine, and bring this to the boil, then reduce the heat to a simmer and simmer for twenty minutes, then serve in a soup tureen or in individual soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'633319C3432CBC4FB8985641023FF845', X'886B9BF2A574DF479B5813FCCE592432', X'886B9BF2A574DF479B5813FCCE592432', 0, 'javascript:', 'Beef Tripe Hot Pot', 2, NULL, 0, 'Beef Tripe Hot Pot', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1951', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons beef fat<BR>1/2 pound beef tripe, washed and cut into thin slivers<BR>1/4 pound beef kidney, cut into thin slivers<BR>1/4 pound beef liver, washed and cut into thin slivers<BR>1/4 pound beef steak, washed and cut into thin slivers<BR>5 Tablespoons spicy broad bean sauce<BR>3 Tablespoons finely minced fresh ginger<BR>1 teaspoon Sichuan peppercorns, crushed using the side of a cleaver<BR>2 Tablespoons seeded minced chili peppers<BR>1 teaspoon coarse salt<BR>1/2 cup rice wine<BR>1 Tablespoon Mao Tai or another Chinese liquor<BR>8 cups beef stock<BR>1/4 cup yak butter or another butter<BR>3 cups total of different kinds or radish and green vegetables<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or a large pan, render the beef fat, and remove it and finely mince the curled pieces, then return them to the wok or pan.<BR>2 Add the tripe, kidney, liver, and steak slivers and stir-fry for only one minute, then remove them to a plate, separating them to allow to cool quickly. And leave any remaining rendered fat in the wok or pan.<BR>3. Add broad bean sauce, ginger, Sichuan peppercorns, and chili peppers and stir-fry for one minute before adding the salt, wine, liquor, stock, and butter. Transfer this to a hot pot and bring to the boil.<BR>4. Put the radish and greens on separate plates around the hot pot, the prepared tripe, kidney, liver, and beef, too.<BR>5. Each diner should have a long-handled fork and a strainer basket, a soup bowl and a soup spoon, and chop sticks. The hot pot should have a ladle available.<BR>6. Cook the meats for a short time in the hot pot on the long handled fork then put into the individual bowls. Then toss some vegetables into the hot stock and remove as desired. Each diner cooks those they want when they want them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1D5EEE6AE537D84F85C6FC89FA501ABC', X'18DCAABA3B4CF8418C5A30C1138E58F9', X'18DCAABA3B4CF8418C5A30C1138E58F9', 0, 'javascript:', 'Bird''s Nest Congee I', 2, NULL, 0, 'Bird''s Nest Congee I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1077', '<TD><RECIPE><B><I>Ingredients</B></I><BR>: 2 ounces bird''s nest<BR>1/4 teaspoon salt<BR>1/2 teaspoon minced fresh ginger<BR>1/2 teaspoon sugar<BR>1 teaspoon Chinese Shaoxing wine<BR>1 teaspoon cornstarch<BR>6 to 8 ounces potatoes slivered, then boiled until soft, and drained<BR>2 ounces chicken breast, minced very finely<BR>4 cups superior stock<BR>1 egg white, lightly beaten<BR>1/4 cup Jinhua ham, minced<BR><B><I>Preparation:</B></I><BR>1. Soak the bird''s nest in cold water for two hours, then drain. In a saucepan, bring four cups of water to the boil, turn off the heat, and add the bird''s nest. Cover and let stand overnight.<BR>2. Drain the bird''s nest using a fine strainer, then sprinkle the salt onto it. Gently squeeze the bird''s nest to remove any remaining water.<BR>3. Inspect the bird''s nest to make sure it is clean and free of any feathers; should you find sone, use a tweezer to remove and discard them.<BR>4. Put the bird''s nest, ginger in a bowl and cover with aluminum foil. Steam overboiling water for thirty to forty-five minutes; remove bowl and stir birds nest into a large pot.<BR>5. Add sugar, wine, cornstarch, potatoes, chicken breast, and superior stock and stir vigorously as it is brought to a boil. Beat in the egg white, then remove the pot from the heat and pour into individual serving bowls. Garnish with a teaspoon of ham, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C9AA9F0C5B2D2047ABA47669D2FE1382', X'2F03D87BB0C50447B9DC80F10948B6C8', X'2F03D87BB0C50447B9DC80F10948B6C8', 0, 'javascript:', 'Bird''s Nest Congee II', 2, NULL, 0, 'Bird''s Nest Congee II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1457', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 ounces bird''s nest<BR>1/4 teaspoon salt<BR>1/2 teaspoon minced fresh ginger<BR>1/2 teaspoon sugar<BR>1 teaspoon Chinese Shaoxing wine<BR>1 teaspoon cornstarch<BR>6 to 8 ounces potatoes slivered, then boiled until soft, and drained<BR>2 ounces chicken breast, minced very finely<BR>4 cups superior stock<BR>1 egg white, lightly beaten<BR>1/4 cup Jinhua ham, minced<BR><B><I>Preparation:</B></I><BR>1. Soak the bird''s nest in cold water for two hours, then drain. In a saucepan, bring four cups of water to the boil, turn off the heat, and add the bird''s nest. Cover and let this stand overnight.<BR>2. Drain the bird''s nest using a fine strainer, then sprinkle the salt onto it. Gently squeeze the bird''s nest to remove any remaining water.<BR>3. Inspect the bird''s nest to make sure it is clean and free of any feathers; should you find sone, use a tweezer to remove and discard them.<BR>4. Put the bird''s nest, ginger in a bowl and cover with aluminum foil. Steam overboiling water for thirty to forty-five minutes; remove bowl and stir birds nest into a large pot.<BR>5. Add sugar, wine, cornstarch potatoes, chicken breast, and superior stock and stir vigorously as it is brought to a boil. Then beat in the egg white, and remove the pot from the heat. Pout the contents into individual serving bowls, and garnish with a teaspoon of the minced ham, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'052C6F1BD5C5E54E95386700C5C1D9E8', X'07979F9814DC034CB0C9A53543620932', X'07979F9814DC034CB0C9A53543620932', 0, 'javascript:', 'Bird''s Nest Dumplings in Soup', 2, NULL, 0, 'Bird''s Nest Dumplings in Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=214', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 ounces bird''s nest pieces<BR>1/2 pound lean pork<BR>1/4 pound shrimp<BR>3 dried shiitake mushrooms<BR>1/2 teaspoon each of salt, sugar, and sesame oil<BR>1 teaspoon thin soy sauce<BR>1 teaspoon cornstarch<BR>1 teaspoon corn oil<BR>1 cup high gluten flour<BR>1 egg<BR>1 egg white<BR>6 cups chicken stock boiled to reduce it to four cups<BR><I><B>Preparation:</I></B><BR>1. In cold water, soak the nest pieces in the refrigerator overnight, then bring to the boil, simmer ten minutes, drain, and cool them. Next, using a tweezer, clean them of all debris. Repeat this process, if needed.<BR>2. Mince pork coarsely with a cleaver, shell then remove veins of the shrimp and mince them.<BR>3. Soak the mushrooms for half an hour in warm water, remove stems and discard, then chop them.<BR>4. Mix pork, shrimp, mushrooms, salt, sugar, soy sauce, sesame oil, soy sauce, cornstarch, and oil.<BR>5. Mix flour, egg, and egg white and knead until it is a soft dough, then let rest covered for half an hour.<BR>6. Roll dough until it is very thin then cut it into six inch circles. Make eight of these circles and fill each with one-eight of the filling and press the edges to seal. Then steam them for twelve minutes.<BR>7. After reducing the stock, keep it boiling. and put each dumpling into a soup bowl and pour boiling stock over it and serve one bowl to each of eight people.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A5EEB52B0E666645B8A2713F1B7E8D70', X'B82C6A2568F7D342B6D29E67C0AD6A8C', X'B82C6A2568F7D342B6D29E67C0AD6A8C', 0, 'javascript:', 'Bird''s Nest, Chicken, and Ham Soup', 2, NULL, 0, 'Bird''s Nest, Chicken, and Ham Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=884', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons dry birds nest<BR>1/4 pound skinless and boneless chicken breast meat, minced<BR>1/2 teaspoon salt<BR>1/2 teaspoon cornstarch<BR>2 Tablespoons Smithfield-type ham, shredded<BR>2 Chinese black mushrooms, soaked in warm water for twenty minutes, stems removed and discarded<BR>3 cups chicken broth<BR>2 teaspoons water chestnut powder<BR>1/4 teaspoon ground white pepper<BR>1 scallion, white part only, minced fine; green part slivered<BR><B><I>Preparation:</B></I><BR>1. Soak the bird''s nest for four hours until swollen. Rinse well, remove any foreign matter, and set it aside.<BR>2. Mix minced chicken with salt and cornstarch and four tablespoons of water.<BR>3. Shred ham and mushrooms into thin slivers.<BR>4. Bring broth to the boil, add all ingredients, reduce heat and simmer for five minutes. Add ground white pepper and scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6302C8F75D544F4EB64372E00D12250D', X'6D1F6AE3D81AE4408878E0F74E60164E', X'6D1F6AE3D81AE4408878E0F74E60164E', 0, 'javascript:', 'Bitter Melon and Pork Soup I', 2, NULL, 0, 'Bitter Melon and Pork Soup I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=536', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 bitter melon<BR>1 quart chicken broth<BR>1 teaspoon soy sauce<BR>1/2 teaspoon sesame oil, optional<BR>1/2 teaspoon salt<BR>1 pound boneless pork loin, cut into thin strips<BR>3 slices fresh ginger, minced<BR>1 egg<BR><B><I>Preparation:</B></I><BR>1. Cut bitter melon in half length-wise, then discard the seeds and pulp. Slice it into one-inch pieces.<BR>2. Mix soy sauce, sesame oil, and salt.<BR>3. In a large suacepan, bring soup stock to a boil. Add pork strips and ginger. Simmer for one minute, then add the bitter melon and cook simmer ten minutes more.<BR>4. Add soy sauce mixture, stir, then bring it to a boil and turn off the heat.<BR>5. Beat the egg and drop it into the soup stirring constantly for half mintute. Then let the soup stand for three minutes before serving in a preheated soup tureen.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E27A1DFA40583E4692E91130B096F9BD', X'E30E93FE7A6D174B91CF10B2A80C5100', X'E30E93FE7A6D174B91CF10B2A80C5100', 0, 'javascript:', 'Bitter Melon and Pork Soup II', 2, NULL, 0, 'Bitter Melon and Pork Soup II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1444', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 bitter melon<BR>1 quart chicken broth<BR>1 teaspoon soy sauce<BR>1/2 teaspoon sesame oil, optional<BR>1/2 teaspoon salt<BR>1 pound boneless pork loin, cut into thin strips<BR>3 slices fresh ginger, minced<BR>1 egg<BR><B><I>Preparation:</B></I><BR>1. Cut bitter melon in half length-wise, then discard the seeds and pulp, and slice it into one-inch pieces.<BR>2. Mix soy sauce, sesame oil, and salt.<BR>3. In a large suacepan, bring soup stock to a boil. Add pork strips and ginger. Simmer for one minute, then add the bitter melon and cook simmer ten minutes more.<BR>4. Add soy sauce mixture, stir, then bring it to a boil and turn off the heat. Beat the egg and drop it into the soup stirring constantly for half mintute. Then let the soup stand for three minutes before serving in a preheated soup tureen.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'45CF89034BDC4545BD2AA36E2DA08485', X'A18F8155EBE63E42A05E89782F4EA18E', X'A18F8155EBE63E42A05E89782F4EA18E', 0, 'javascript:', 'Black and Golden Mushroom Soup', 2, NULL, 0, 'Black and Golden Mushroom Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=341', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>3 shiitake mushrooms, soaked for five minutes, then remove and discard the stems and slice the caps thinly<BR>4 ounces straw mushrooms, sliced thinly the long way<BR>4 ounces golden needle mushrooms<BR>1 teaspoon cornstarch<BR>2 cups chicken stock<BR>1 sprig cilantro, minced<BR><B><I>Preparation:</I></B><BR>1. Mix the tree mushrooms well with the cornstarch.<BR>2. Heat the broth then add the mushrooms and keep just below the boiling point for five minutes. Pour into four heated soup bowls, garnished each one with a little cilantro, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E5DDB9FE7EB0254E9D1B425A791294FC', X'4CF71D069A63264792379192D2216301', X'4CF71D069A63264792379192D2216301', 0, 'javascript:', 'Black Rice Soup', 2, NULL, 0, 'Black Rice Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=800', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/3 pound black rice<BR>3 Tablespoons sugar<BR>1/8 teaspoon ground fennel<BR>1/8 teaspoon ground Chinese cinnamon<BR><B><I>Preparation:</B></I><BR>1. Rinse the black rice three to five times.<BR>2. Put the rice, sugar, fennel, and cinnamon in a large heavy pot and add six cups cold water. Bring to the boil, reduce the heat, cover, and simmer for three hours. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2CD972E0B10D4A4182173AE1CC2701B6', X'EFCDBC0F2B9D404DAB88DB06E4B74231', X'EFCDBC0F2B9D404DAB88DB06E4B74231', 0, 'javascript:', 'Black Sesame Soup', 2, NULL, 0, 'Black Sesame Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=213', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 cup sesame seed soup concentrate<BR>3 cups water<BR>1/2 cup sugar<BR><I><B>Preparation:</I></B><BR>1. Mix the above and boil slowly for five minutes. If it gets too thick, add a little boiling water and boil for five minutes more.<BR>2. Add cooked Tong Yuen with Black Sesame Paste or other black sesame balls and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'23CF8C8B5251A0488B2FDC1449A783C2', X'E172D80F5CCEC748A4EB3A5E5B59884D', X'E172D80F5CCEC748A4EB3A5E5B59884D', 0, 'javascript:', 'Black Sesame Soup Concentrate', 2, NULL, 0, 'Black Sesame Soup Concentrate', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=212', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 pound black sesame seeds<BR>1 cup rice, soaked for at least one hour, then drained well<BR><I><B>Preparation:</I></B><BR>1. Soak the seeds in a large bowl with water, then scoop out only those that float.<BR>2. Put them in a 200 degree Fahrenheit oven, then raise the temperature to 400 degrees. Mix frequently and bake until fragrant, then cool.<BR>3. Put sesame seeds and rice into a blender and add enough water to reach the three and a half cup mark. Blend first on low speed then on high until smooth. If needed add additional water one or two tablespoons at a time. Now add two and a half cups more water (divide into two or more batches if you have a small blender) and blend for an additional minute. Store in refrigerator or freeze in one cup batches until needed. This yields six cups of concentrate.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A429CB2851E7AE4B8EB52458F4847ED3', X'DCCA5E8B74D7C34EA4D09A3067706791', X'DCCA5E8B74D7C34EA4D09A3067706791', 0, 'javascript:', 'Black-chicken Soup', 2, NULL, 0, 'Black-chicken Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=499', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 black chicken, breast meat removed, sliced, and set aside<BR>2 knobs of fresh ginger, mashed with the side of a cleaver<BR>1/2 teaspoon salt<BR>8 whole cloves<BR>1/2 cup corn oil<BR>1 small pickled cucumber, sliced thin<BR>1/2 carrot, peeled and sliced thin<BR>1/2 scallion, white part only, sliced thin<BR>2 slices fresh ginger, sliced thin<BR>1 Tablespoon wolfberries<BR>1 teaspoon thin soy sauce<BR>1 Tablespoon rice wine<BR>1 teaspoon Chinese black vinegar<BR>1 teaspoon sesame oil<BR>1 Tablespoon cornstarch mixed with the same amount of water<BR><B><I>Preparation:&gt;/b&gt;</I><BR>1. Not using the breast meat, cut the rest of the chicken into four or five pieces. Put it into a strainer and pour four cups of boiling water over it. Then bring six other cups of water to the boil, add the chicken, ginger knobs, salt, and cloves and simmer for two hours.<BR>2. Remove chicken and set it aside. Discard the solids; then strain the liquid. There should be about a cup and a half of chicken stock remaining. Discard skin and bones and mince the chicken.<BR>3. Heat oil and stir-fry the chicken slices for one minute, remove and drain, and return two tablespoons oil to the pan.<BR>3. Fry the pickled cucumber, carrot, scallion, and ginger for one minute, then add minced chicken, chicken slices, wolfberries, soy sauce, rice wine, vinegar, sesame oil, and the strained chicken broth. Bring this mixture almost to the boil, then add the cornstarch mixture and boil just until it thickens. Serve<BR></B></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'71188433E03F6441B90AB13C89E49D16', X'0843BF4DCC4C1D499F970D339D4E4DF0', X'0843BF4DCC4C1D499F970D339D4E4DF0', 0, 'javascript:', 'Black-skinned Chicken Soup', 2, NULL, 0, 'Black-skinned Chicken Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=886', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound black beans<BR>2 Tablespoons dried longans, shells removed and discarded<BR>6 slices of dried licorice<BR>1 black-skinned chicken, blanched in boiling water for one minute<BR>1/2 to 1 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Fry black beans in a dry wok until fragrant.<BR>2. Bring three quarts of water to the boil, add the beans, longans, licorice, and chicken and cook over low heat for two and a half to three hours. Season with salt then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'29F03004E8F2EC43954DCD128796B151', X'3B667FF6EB531D4E80299EEA4E7B3250', X'3B667FF6EB531D4E80299EEA4E7B3250', 0, 'javascript:', 'Boiled Amaranth with Preserved Duck Eggs', 2, NULL, 0, 'Boiled Amaranth with Preserved Duck Eggs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=975', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>7 ounces amaranth<BR>1 cup and 2 Tablespoons stock<BR>3 Tablespoons peanut oil<BR>2 cloves garlic, sliced<BR>2 preserved duck eggs, peeled and each cut into six to eight sections<BR>salt to taste<BR><B><I>Preparation:</B></I><BR>1. Discard thick tough stems and wilted leaves from the amaranth and set aside. 2. Bring stock to the boil, then set it aside.<BR>3. Heat wok, then add the oil, and swirl it around before adding the garlic. Let that sizzle a few seconds then add amaranth and stir-fry until the leaves wilt and the stems are tender.<BR>4. Add hot stock and bring it to the boil, then immediately add the egg pieces and boil together for a minute, adding salt to taste. Serve in a deep bowl, and spoon any leftover sauce into individual rice bowls consuming it as soup.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'550372B5CE1F3046B6E424F30E84B0E7', X'58FC45BD8C8F854AB2C3BC8CB4B2DAEB', X'58FC45BD8C8F854AB2C3BC8CB4B2DAEB', 0, 'javascript:', 'Buddha''s Rice Soup', 2, NULL, 0, 'Buddha''s Rice Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1192', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups red beans, soaked for twelve or more hours<BR>2 cups white beans, soaked for twelve or more hours<BR>2 bowls cooked glutinous rice<BR>1 bowl long cooked grain rice<BR>6 cups peeled chestnuts, soaked for two or more hours<BR>1 pound Chinese yams, peeled and chopped fine<BR>2 cups Chinese red dates<BR>2 cups lotus seed candy<BR>1 cup vegetable oil (optional)<BR>1 banana, sliced thin lengthwise (optional)<BR><B><I>Preparation:</B></I><BR>1. Boil red beans and the white beans separately, each for three hours. Cool, drain and remove them, and discard their skins.<BR>2. In a large pot, put in cooked red and white beans, the cooked glutinous and the cooked long grain rice, the chestnuts, chopped yams, red dates, and the lotus seed candy.<BR>3. Put in enough boiling water to cover this mixture by five or six inches, cover the pot, and simmer for four or five hours. Then serve with any garnish on top of each bowl.<BR><B>Optional garnish:</B> Heat oil in a deep pot, and fry two or three slices of the banana at a time, then drain them thoroughly. Stand one up on an angle, straw-like to garnish each bowl.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5E8277F1939FA64FA1C955505F578E79', X'9D1BFB0162D1094BB8CBBC692512D4E8', X'9D1BFB0162D1094BB8CBBC692512D4E8', 0, 'javascript:', 'Cane and Arrowroot Soup', 2, NULL, 0, 'Cane and Arrowroot Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=179', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 package Cane and Arrowroot Stock<BR>1 recipe Basic Pork Soup Stock<BR><B><I>Preparation:</I></B><BR>1. Rinse and soak the packaged ingredients overnight. <BR>2. Add them to the prepared soup stock then simmer for at least two hours.<BR><B>Note:</B> The packaged soup has four ingredients: Sugar cane, imperatae, arrowroot, and dried carrots. Discard the sugar cane and imperatae as they are indigestible after the soup has been made.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CA7951A6CC57134CB5254F344B76BCDB', X'862CB7F35E7C8D419F7A26F45E7FD01C', X'862CB7F35E7C8D419F7A26F45E7FD01C', 0, 'javascript:', 'Cantonese Taro Soup', 2, NULL, 0, 'Cantonese Taro Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1985', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound taro, peeled and cut into large pieces<BR>2 ounces tapioca pearls<BR>3/4 cup granulated sugar<BR>3/4 cup coconut milk<BR><B><I>Preparation:</B></I><BR>1. In a large pot, add two cups of cold water, the taor, and simmer fr one hour.<BR>2. Mash the taro and return it to the pot.<BR>3. In a small pot, add the tapioca and two cups of cold water. Bring this to the boil, then remove the pot from the heat source. Set it aside for ten minutes, then strain in a fine colander under running water.<BR>4. Mix with the mashed taro, and serve hot or cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'88EFDA12AFA00543BCC5825D616A5962', X'555889742B15084F96B7467D447E1E9B', X'555889742B15084F96B7467D447E1E9B', 0, 'javascript:', 'Carp Soup', 2, NULL, 0, 'Carp Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1681', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 fresh carp, or fillets of any good white fish<BR>1 teaspoon Chinese brown pepper, ground<BR>1 teaspoon ground coriander<BR>1 Tablespoon chopped onion<BR>Salt, to taste<BR>Dash of Chinese wine mixed with all the spices<BR>1 teaspoon black pepper<BR>1 teaspoon sprouting fresh young ginger<BR>1 teaspoon ground long pepper, optional<BR>Dash of vinegar<BR><B><I>Preparation:</B></I><BR>1. Marinate the fish in the brown pepper, coriander, onion wine, and salt for about ten minutes.<BR>2. Boil the marinated fish quickly with the rest of the ingredients, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F880C6B0FBDCDD41B12352D0FCFE6D85', X'3685A416F7F2A343822FAD6009034EAD', X'3685A416F7F2A343822FAD6009034EAD', 0, 'javascript:', 'Carp Soup*', 2, NULL, 0, 'Carp Soup*', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2044', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>Large carp, one Adzuki beans, one <I>ho</I><BR>Prepared mandarin orange peel,two <I>chien</I>, their white removed<BR>Chinese flower pepper, to <I>chien</I><BR>Tsaoko cardamon, two<BR><I><B>Preparation:</B></I><BR>1. To ingredients, add five spices and favor evenly, then cook.<BR>2. When done eat on an empty stomach.<BR>* The original says it cures diabetes and edema, jaundice, and foot <I>Qi</I>.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A774ADAF64253E4FBAE5148DF61AA664', X'8951E72D6A664F40A625D115F37E4D8C', X'8951E72D6A664F40A625D115F37E4D8C', 0, 'javascript:', 'Casserole of Hasma and Watermelon', 2, NULL, 0, 'Casserole of Hasma and Watermelon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2109', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 to 3 pound watermelon, rind discarded, and cut into large cubes, and seeded<BR>1 quart chicken stock<BR>3 Tablespoons minced cured ham<BR>3 Tablespoons dried <I>hasma</I> prepared as instructed<BR>3 back Chinese mushrooms soaked stems discarded, and slivered<BR>3 Tablespoons minced roast duck (optional)<BR>2 to 4 shrimp, shells and veins discarded, then minced<BR>3 to 4 inch piece of silk squash, peeled and angle-cut<BR>4 to 5 slices lotus root, boiled until tender, then cut in wedges<BR>1/2 teaspoon salt<BR>1/2 teaspoon ground back pepper<BR>1 teaspoon granulated sugar<BR>1 teaspoon chicken broth paowed<BR><B><I>Preparation:</B></I><BR>1. Prepare all ingredients as indicated above.<BR>2. In a large soup or stock pot, bring chicken stock to the boil, reduce to a simmer, then add the watermelon and all other ingredients, and simmer for twenty minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'48921BDB0DECC142B6341002CA6CE2A9', X'061EDD5173E8034A9E7A6AEC70C5DCFD', X'061EDD5173E8034A9E7A6AEC70C5DCFD', 0, 'javascript:', 'Chayote and Pumpkin Soup', 2, NULL, 0, 'Chayote and Pumpkin Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2314', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 chayote, each sliced then diced; seeded if old<BR>½ pound Chinese pumpkin, peeled and diced<BR>2 tomatoes, stems discarded, cut in half-inch pieces<BR>3 dried scallops, soaked for two hours, then pulled apart, string-like<BR>4 Chinese black mushrooms, soaked for half hour, stems discarded, then diced in 1/4-inch pieces<BR>1 pound pork ribs, scalded in two quarts of water, then strained<BR>4 slices young ginger<BR>½ teaspoon round white pepper<BR>1 Tablespoon thin soy sauce<BR><B><I>Preparation:</B></I><BR>1. Put all ingredients except the soy sauce in two more quarts of tepid water, bring to the boil, reduce the heat, and simmer for two hours.<BR>2. Remove meat from the bones, cut it into half-inch pieces, and discard the bones.<BR>3. Add the soy sauce, stir, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4CB98677E04DF84EA9EACFA758D2CDF5', X'B38D004E6B525A4D9FF852519744634A', X'B38D004E6B525A4D9FF852519744634A', 0, 'javascript:', 'Chestnut and Yam Soup', 2, NULL, 0, 'Chestnut and Yam Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1917', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>12 dried chestnuts<BR>6 fresh chestnuts, peeled<BR>1/4 cup dried Chinese yam<BR>1/2 cup yam flour<BR>1/2 pound lean pork, cut into thin strips, blanched for ten minutes, then drained, the water discarded<BR>1/2 cup fresh yams, peeled and cubed<BR>1/4 cup <I>goji</I> berries<BR><B><I>Preparation:</B></I><BR>1. Soak the dried chestnuts in warm water for one hour, then drain, peel, and discard any outer and inner shell material.<BR>2. Soak the dried yams and simmer them for fifteen minutes.<BR>3. Put both kinds of chestnuts, both kinds of yams, the yam flour, and the pork into a pot, mix well, then add three quarts of cold water and bring to just below the boil. Reduce the heat and simmer for two hours.<BR>4. Add the <I>goji</I> berries, simmer for ten minutes more, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'579DE5B0B74C5248AEACCCF95674AA52', X'0B27F19013F8364E899763DD0879BA50', X'0B27F19013F8364E899763DD0879BA50', 0, 'javascript:', 'Chestnut Soup with Osmanthus Blossoms', 2, NULL, 0, 'Chestnut Soup with Osmanthus Blossoms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1626', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound fresh chestnuts, peel and paper coverings removed<BR>2 Tablespoons lotus root flour<BR>6 candied plums, each one sliced in half<BR>1 teaspoon sweetened osmanthus flowers<BR>3 or 4 rose petals<BR><B><I>Preparation:</B></I><BR>1. Boil pre-cooked or purchased cooked chestnuts for five minutes. Then, remove them from the boiling water, discard it, and cut them into thin slices.<BR>2. Bring two cups water to the boil, add chestnuts, lotus root flour, and the candied plums, then reduce the heat, and simmer for half an hour or longer until the chestnuts are soft. Do stir them frequently.<BR>3. Add the candied plum slices and the osmanthus flowers, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7B1C852EB6A24544A4AFE2EFD82BBD8A', X'1473A28378B0AF4391652FC6D7BC5855', X'1473A28378B0AF4391652FC6D7BC5855', 0, 'javascript:', 'Chicken and Corn Soup', 2, NULL, 0, 'Chicken and Corn Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=998', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 skinless and boneless chicken breast (about four ounces)<BR>2 teaspoons thin soy sauce<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon cornstarch<BR>1 teaspoon sesame oil<BR>1 Tablespoon vegetable oil<BR>1 teaspoon minced fresh ginger<BR>4 cups chicken broth<BR>1 can (about 15 ounces) creamed corn<BR>1 can (about eight ounces) whole kernel corn<BR>2 eggs, beaten<BR>2 scallions, green part only, slivered<BR><B><I>Preparation:</B></I><BR>1. Mince or grind chicken breast, taking care not to over process it, then remove it to a bowl.<BR>2. Stir in soy sauce, rice wine, cornstarch, sesame oil, and one-quarter cup cold water. Cover this seasoning mixture with plastic wrap and set aside for fifteen minutes.<BR>3. Heat vegetable oil in a wok, large pan, or a deep fry pan, add minced ginger and stir-fry for up to half minute or until aromatic, then stir in the seasoning mixture and the broth and both kinds of corn. Bring to just below the boiling point, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7F808F99D3519048AFEB2AFA09B74AF6', X'DB6603E3A17AD544B7F7DA2C214B7754', X'DB6603E3A17AD544B7F7DA2C214B7754', 0, 'javascript:', 'Chicken Ball and Fig Soup', 2, NULL, 0, 'Chicken Ball and Fig Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1081', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1/2 skinless and boneless chicken breast (five to six ounces)<BR>2 dried figs, soaked for two hours, stems removed and discarded<BR>2 Chinese dried black mushrooms, soaked until soft, stems removed and discarded<BR>1 teaspoon dried shrimp, soaked for ten minutes<BR>1/4 cup canned water chestnuts<BR>3 and 1/2 cups chicken broth<BR>2 teaspoons thin soy sauce<BR>1/2 teaspoon oyster flavored sauce<BR>1 Tablespoon cornstarch<BR><B><I>Preparation:</B></I><BR>1. Mince chicken breast, figs, mushrooms, dried shrimp, and water chestnuts separately, then gently mix them and add half cup of broth and the soy and the oyster sauces. Make into four large balls. Dust them with the cornstarch and let them air dry about forty minutes.<BR>2. Bring the remaining chicken broth to a simmer, do not boil it. One by one, put a chicken ball on a spoon, and lower it into the simmering broth. Simmer them for twenty minutes, being sure the broth does not boil. Remove each one to a Chinese soup bowl and ladle the broth over them, Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FD942050CAA8ED4496C986CF7388AC0F', X'7570A37A4566C845885FDA58E1AD6FFD', X'7570A37A4566C845885FDA58E1AD6FFD', 0, 'javascript:', 'Chicken Broth', 2, NULL, 0, 'Chicken Broth', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1264', '<TD><RECIPE><B><I>Ingredients:</B></I> <BR>1 old hen, cut in half, then blanched<BR>1 pound and five ounces ham, blanched<BR>11 ounces lean pork<BR>2 scallions<BR>1 small piece ginger<BR>2 Tablespoons Chinese rice wine<BR><B><I>Preparation:</B></I><BR>1. Put chicken, ham, pork, scallions, and fifteen cups water in a pot, bring to the boil, drizzle with the wine, and reduce the heat and simmer for three hours.<BR>2. Discard all solids. Then strain remaining liquid and use as a broth in needed recipes; and freeze any not used in portions for later use.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9F35F459CF2F2C43A47134348D48011B', X'6CBD3E892D57BA498777C78E7898CB2E', X'6CBD3E892D57BA498777C78E7898CB2E', 0, 'javascript:', 'Chicken Congee', 2, NULL, 0, 'Chicken Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2094', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup uncooked short grain rice, rinsed well<BR>6 cups chicken stock<BR>1 chicken breast<BR>½ teaspoon salt<BR><B><I>Preparation:</B><I><BR>1. Put rice and chicken stock in a three-quart pot, bring to the boil, then reduce heat to low and simmer for two hours. Then add the salt and two tablespoons of water.<BR>2. Remove the skin from the chicken, slice it with the grain, and flatten each piece slapping it with the side of the cleaver.<BR>3. When congee is ready, add the chicken and turn off the heat, and let it stand for five minutes, then serve in soup bowls.<BR></I></I></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C221EE351B985D48AF61CFE505AABDB8', X'2A730DC6C6EBD84699C80B920B06332A', X'2A730DC6C6EBD84699C80B920B06332A', 0, 'javascript:', 'Chicken Congee with Pi Dan', 2, NULL, 0, 'Chicken Congee with Pi Dan', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=398', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 large dried Chinese black mushrooms<BR>1 boneless and skinless chicken breast, cut in cubes<BR>dash of salt or to taste<BR>2 teaspoons dry sherry<BR>1/4 cup minced Virginia ham<BR>1/2 cup glutinous rice<BR>1 thirteen and three-quarter ounce can of chicken broth<BR>4 slices fresh ginger, minced<BR>1 <I>pi dan</I>, shelled and cut into six pieces<BR>2 scallion tops, minced<BR><B><I>Preparation:</B></I><BR>1. Soak mushrooms for fifteen minutes in warm water. Drain and set the water aside. Discard the stem and cut each mushroom in six to eight pieces.<BR>2. Mix chicken cubes with salt and sherry and steam this over boiling water for twenty minutes. Drain and reserve the liquid.<BR>3. Combine mushrooms, ham, rice, chicken broth, ginger, and the reserved mushroom and chicken waters and bring this to the boil. Add a half-cup of water and the <I>pi dan</I> and reduce the heat. Simmer for one hour, them mix in the chicken pieces, sprinkle with the scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'07C32FD10E30044FB0399C614C3776AF', X'CD0D5C8CB4424140822FA41D3BE733FA', X'CD0D5C8CB4424140822FA41D3BE733FA', 0, 'javascript:', 'Chicken Soup with Ginger, Wolfberries, Rice Wine,', 2, NULL, 0, 'Chicken Soup with Ginger, Wolfberries, Rice Wine,', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1252', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound chicken thigh meat<BR>1 Tablespoon wolfberries (also called <I>goji</I> berries)<BR>1/4 cup black sesame oil<BR>2 ounces peeled fresh ginger, old and woody<BR>20 ounces Chinese rice wine<BR>a pinch of salt<BR>½ Tablespoon sugar<BR><B><I>Preparation:</B></I><BR>1. Cut chicken into one-quarter inch cubes<BR>2. Soak wolfberries in water to soften, then drain and discard the water.<BR>3. Heat wok or pot, add sesame oil and then stir-fry the ginger until slightly dry and fragrant before adding the chicken and stir-frying it until almost cooked through.<BR>4. Pour in half the rice wine and bring to the boil. Be careful as it is very flammable; then add the drained wolfberries, reduce the heat to medium, and add the rest of the wine.<BR>5. Simmer for a few minutes until the chicken is fully cooked. Add salt and sugar, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'18961CC257B6094C84064E4769E76D1C', X'552C18B68271FC4DBF20E5CA41E05CD2', X'552C18B68271FC4DBF20E5CA41E05CD2', 0, 'javascript:', 'Chicken Soup, the Easy Way', 2, NULL, 0, 'Chicken Soup, the Easy Way', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=333', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 three to four pound whole chicken<BR>3 scallions<BR>2 pieces of fresh ginger, each about the size of your thumb<BR>salt and pepper, to taste<BR><B><I>Preparation:</B></I><BR>1. Clean the chicken removing any fat and soft tissue inside it, then put it with the neck bone, gizzard, and liver into this easy cook pot. Fill it with water until it covers the chicken.<BR>2. Rinse the ginger, then using a cleaver, smash the ginger so that the skin is broken and its flavor can leach out.<BR>3. Add the ginger, and scallions and bring the contents to the boil. Skim off any fat or other materials and reduce the heat to simmer partially covering the pot.<BR>4. After twenty minutes, tightly cover the pot and put it in the insulated container for four to eight hours.<BR>5. Remove the pot from the insulated container, bring the contents to the boil, add salt and pepper, to taste.<BR><B>Note:</B> We like have added a dipping sauce of sesame oil, soy sauce, and a minced uncooked scallion to go along with the pieces of chicken. You can try that and others that you enjoy, or not use any sauce with this soup.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'65F9B359882BB24C94E822A83E3AACFD', X'3519AAA4DFB16D40B5C0A19647558807', X'3519AAA4DFB16D40B5C0A19647558807', 0, 'javascript:', 'Chinese Yam and Sparerib Soup', 2, NULL, 0, 'Chinese Yam and Sparerib Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=957', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1/2 cup dried Chinese yam, soaked for two hours, then drained<BR>1/2 cup fresh Chinese yam, peeled and diced<BR>1/2 cup Lycium (goji) berries<BR>1/2 cup dried pitted Chinese red dates<BR>2 pounds spareribs, cut in one-inch pieces<BR>1 teaspoon salt<BR>2 Tablespoons cornstarch<BR><B><I>Preparation:</B></I><BR>1. Put ten cups of water into a large pot. Add all the ingredients except the cornstarch and bring water to a boil, then reduce the temperature to simmer, and do so for three hours. Skim, as needed.<BR>2. Mix cornstarch with three tablespoons cold water, and add to the pot. Bring the temperature back to the boil, add the cornstarch mixture, and when thickened, serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A3B81D34A44C0E4B9E188854885D18F8', X'D96B272A5108004990838E0A0D953940', X'D96B272A5108004990838E0A0D953940', 0, 'javascript:', 'Chinese Yam Congee', 2, NULL, 0, 'Chinese Yam Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=956', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup dried Chinese yam slices<BR>1/2 cup jobs tears<BR>1/2 cup lotus seeds<BR>1/2 cup glutinous rice<BR>1/4 cup sugar (optional)<BR><B><I>Preparation:</B></I><BR>1. Soak yam slices, job''s tears, lotus seeds, and rice, each separately, for four hours or overnight. Then drain them.<BR>2. Put ten cups of water in a large pot. Add the four drained items and bring to the boil, then immediately reduce the heat and simmer them for two hours, stirring occasionally.<BR>3. Add sugar, if desired, and serve. Reserve any unused congee covered and in the refrigerator. It keeps about a week.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4B14294D21072841839A15E7A94A6016', X'34FB8F5F399BC2489013C8BD064BF677', X'34FB8F5F399BC2489013C8BD064BF677', 0, 'javascript:', 'Ching Po Leung Soup', 2, NULL, 0, 'Ching Po Leung Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=178', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 package Ching Po Leung <BR>10 honey dates<BR>1/2 of adried Lo-Han-Kuo fruit<BR>1 recipe Basic Pork Soup Stock<BR><I><B>Preparation:</I></B><BR>1. Rinse packaged ingredients and drain.<BR>2. Add to soup stock with remaining ingredients. Dried figs can be substituted for the honey dates, but use only five or six as they are sweeter and larger.<BR>3. Simmer for at least two hours, the longer the better.<BR><B>Note:</B> Lo-Han-Kuo is also known as momordica. It requires a degree of force because the rind is rather hard. Keep in mind that neither the rind nor its seeds are edible. Also, care should be taken with the honey date because the pit is sharp.<BR>In addition: Packaged ingredients for <I>Ching Po Leung</I> are sometimes identified as such in English, but more often than not, they are simply labeled Dried Assorted Vegetables. See the photograph with seven dried ingredients in the hard copy of this issue; it illustrates <I>Dioscorea</I>, lotus seeds, fox nuts, dried longan, dried lily bulb, pearl barley, and <I>polygonatum</I>. The pre-packed ingredients can be bought individually. I regularly add more of each to soup pots as I enjoy eating them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'562A34A4E7D8B34C886994234DCB54B6', X'E15A49750995254091A6DC08D88831E5', X'E15A49750995254091A6DC08D88831E5', 0, 'javascript:', 'Chop Suey Soup', 2, NULL, 0, 'Chop Suey Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=655', '<TD><RECIPE>This recipe is as printed in and is taken from Au Man Sings <I>Chinese Cookbook</I> <P>Cut about a dozen Chinese dried and soaked black mushrooms into thin pieces, together with one-half can bamboo shoots and one-half dozen water chestnuts. <P>Boil all together in prepared soup stock about 10 minutes, then add two beaten eggs, and very finely chopped chicken, using white meat only; season to taste. Serve when eggs are done.<BR></P></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F383BCD22680F14D9A52F323784F0932', X'81E64F023373BF4C837CAD734AC7E0E3', X'81E64F023373BF4C837CAD734AC7E0E3', 0, 'javascript:', 'Cinnamon Beef Noodles', 2, NULL, 0, 'Cinnamon Beef Noodles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=91', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon corn oil<BR>6 scallions, cut into one and a half inch sections, then smashed with the flat side of a cleaver or knife<BR>6 cloves garlic, peeled, smashed as were the scalions, then thinly sliced<BR>6 slices ginger, likewise smashed<BR>1 and 1/2 teaspoons hot chili paste<BR>2 sticks cinnamon<BR>1 teaspoon anise seed<BR>1/2 cup soy sauce<BR>2 pounds beef chuck, trimmed of all fat and gristle, then cut into one and a half inch cubes<BR>10 ounces spinach, trimmed, rinsed, and drained<BR>1/2 pound flat Chinese wheat flour noodles, cooked until tender, and rinsed with warm water<BR>3 Tablespoons minced scallions<BR><B><I>Preparation:</B></I><BR>1. Heat a large pot over medium-high heat, then add oil and heat 30 seconds more.<BR>2. Add scallion sections, garlic, ginger, and stir-fry until fragrant, about fifteen seconds.<BR>3. Add eight and a half cups of water, soy sauce, and beef cubes and bring to the boil, reduce heat to low, cover, and simmer for one and a half hours.<BR>4. Uncover, remove ginger and cinnamon sticks and discard them.<BR>5. Add spinach and return to the boil. Immediately thereafter, divide the noodles into six individual bowls and ladle the meat, spinach and broth over them. Sprinkle with the minced scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8A77899A76D3C64781AD55A4E140F242', X'068E089BADB41343AF8D326D3390292A', X'068E089BADB41343AF8D326D3390292A', 0, 'javascript:', 'Cloud Ear Fruit Soup', 2, NULL, 0, 'Cloud Ear Fruit Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2242', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup glutinous rice, soaked overnight<BR>20 dried longan, boiled for twenty minutes, each cut in half, the pits discarded<BR>1 cup mung beans, boiled for half an hour, then remove and discard their skins<BR>20 <I>goji</I> berries<BR>1 ounce white tree ear fungus, soaked for an hour, any hard centers discarded<BR>½ cup granulated sugar<BR>½ cup barley, simmered for one hour, then drained<BR><B><I>Preparation:</B></I><BR>1. Drain the glutinous rice. Bring six cups of cold water to the boil, add the rice and simmer for one hour.<BR>2. Now add the longans, mung beans, <I>goji</I> berries, white tree fungus, sugar, and cooked barley, and bring to the boil, reduce heat and simmer for an hour.<BR>3. Cool to warm, and serve in individual bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A78EDA3B2D3A0948A8B32B8E254ECA9D', X'B5655C862F45CE4AA70754E97B5D2704', X'B5655C862F45CE4AA70754E97B5D2704', 0, 'javascript:', 'Cold Bean Soup, common for the Cold Food Festival', 2, NULL, 0, 'Cold Bean Soup, common for the Cold Food Festival', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2225', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound green mung beans, simmered for forty minutes<BR>3 Tablespoons tapioca flour<BR>½ cup granulated sugar<BR>½ cup cream<BR>1/4 cup coconut milk<BR>10 <I>goji</I> berries, cooked for five minutes<BR><B><I>Preparation: </B></I><BR>1. Mash the mung beans or put them into a blender with the cream, coconut milk, and the sugar. Then cool this mixture to room temperature in individual small serving bowls.<BR>2. Add one <I>goji</I> berry on top. One cn provide extgra cream or coconut milk, is desired; then each person can some from small pitchers on the side pouring it into their own bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4E9110D7EFBA9345A874F030088FE2A2', X'C48EFD4CECAA1B4C9B746A44F125487A', X'C48EFD4CECAA1B4C9B746A44F125487A', 0, 'javascript:', 'Congee of Beans, Nuts, and Grains', 2, NULL, 0, 'Congee of Beans, Nuts, and Grains', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1692', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup dried red bans, soaked overnight, then drained and rinsed<BR>1/2 cup raw peanuts, shells and paper exteriors removed and discarded<BR>1/2 cup raw cashew nuts<BR>1/2 pound pearl barley<BR>1/2 cup glutinous rice<BR>1/4 pound Chinese slab browbn sugar<BR>1/4 cup <I>goji</I> berries<BR>6 dired hawthorn disks<BR>1 papaya, seeded, peeled, diced, and steamed for five minutes<BR>1/4 cup cooked tapioca pearls, optional<BR><B><I>Preparation:</B></I><BR>1. Bring three quarts of water to the boil, then add the soaked beans, peanuts, cashews, barley, and rice and bring to the boil, then reduce heat and simmer for one hour.<BR>2. Add the sugar and berries and hawthorn disks and simmer another thirty minutes, stirring every ten minutes.<BR>3. Add the papaya, and the tapioca pearls, if using them. Then serve.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'22A735E05F0F70409BF7FE719590D876', X'B9AFDD83AD629849B9C9CDF8F38770DD', X'B9AFDD83AD629849B9C9CDF8F38770DD', 0, 'javascript:', 'Congee with Pumpkin and Greens', 2, NULL, 0, 'Congee with Pumpkin and Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1811', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon sesame oil<BR>1/2 cup white rice, rinsed<BR>2 cups Chinese pumpkin or butternut squash, peeled and diced into small cubes<BR>2 Tablespoons fresh ginger, minced<BR>2 teaspoons coarse salt<BR>1/2 cup slivered scallions and <I>gai lan</I> leaves<BR><B><I>Preparation:</B></I><BR>1. Put sesame oil in a six-quart pot with a heavy bottom. Then add the rice and toss well before adding the cubed pumpkin or squash and the ginger.<BR>2. Bring to the boil, reduce the heat, and simmer for one hour.<BR>3. Then add the salt, and half the scallions, and the <I>gai lan</I> and simmer another twenty minutes, stirring frequently.<BR>4. Add the rest of the greens, stir and simmer another ten minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9FB7E5343C534E4E99FE0EFF4FB2651C', X'31D4F9450C8F3E4292164267B4EE5554', X'31D4F9450C8F3E4292164267B4EE5554', 0, 'javascript:', 'Conpoy and Chicken Congee', 2, NULL, 0, 'Conpoy and Chicken Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2276', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups cooked rice<BR>8 cups chicken broth<BR>1 conpoy, soaked for half an hour in boiling water, then torn into thin strips<BR>1 chicken breast cut into very thin strips<BR>3 fresh scallops, sliced into strips the same size as the conpoy<BR>1 Tablespoon rice wine<BR>1 teaspoon salt<BR>1 Tablespoon cornstarch mixed with an equal amount of cold water<BR>1 egg white, beaten lightly<BR><B><I>Preparation:</B></I><BR>1. Mix rice and broth, then add the conpoy strips and the water the were soaked in and bring to the boil, then quickly turn off the heat source and let this sit for half an hour away from the heat.<BR>2. Add the chicken breast pieces, the fresh scallops, rice wine, salt and cornstarch and simmer for five minutes until the sauce looks clear, then add the rice and stir for one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FE030F9E1FA0964A81428E2BB34A1CEB', X'BB98FA046A59074A9F720549BA2D4E6B', X'BB98FA046A59074A9F720549BA2D4E6B', 0, 'javascript:', 'Cordyceps Chicken Soup', 2, NULL, 0, 'Cordyceps Chicken Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1788', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 three-pound black chicken<BR>1 one-inch knob peeled and crushed fresh ginger<BR>10 cordyceps, rinsed and dried<BR>2 small onions, peeled, each cut in quarters<BR>1 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Clean, rinse, and dry the chicken and cut it into eight pieces.<BR>2. Put it into a five or six quart pot, and all all the other ingredients, and four quarts of water or thin stock.<BR>3. Bring to the oil, reduce heat, and simmer for three hours.<BR>4. Remove all solids and discard all except the chicken meat, but do not discard its skin and bones. Tear the meat into small pieces before returning it to the pot and bringing it almost to the boil. Then serve the meat and the stock.<BR><B>Note:</B> The remaining bones can be boiled for half an hour in two quarts of water, that liquid consumed as a soup.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E7334E3294A13F4695FD92B5B38409FB', X'EA53BFA7CB43C04C91FC36ED86CD8EFB', X'EA53BFA7CB43C04C91FC36ED86CD8EFB', 0, 'javascript:', 'Cordyceps Fish Soup', 2, NULL, 0, 'Cordyceps Fish Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1789', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large fish, about two pounds 1 one-inch knob peeled and crushed fresh ginger<BR>16 cordyceps, rinsed and dried<BR>2 small onions, peeled, each cut in quarters<BR>1 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Clean, rinse, and dry the fish, and scale and gut it.<BR>2. Put it into a five or six quart pot, and all other ingredients, and four quarts of water.<BR>3. Bring to the boil, reduce heat, and simmer for half and hour.<BR>4. Remove the fish and put it into a clean soup tureen. But all else back into the pot and return it to the boil, reduce the heat, and simmer for one hour. Then pour the liquid over the fish, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'21FC33A2F94539469A0069EEC2A22CC2', X'8E6CF60FEC028A479B3604963D92B723', X'8E6CF60FEC028A479B3604963D92B723', 0, 'javascript:', 'Corn and Shrimp Congee', 2, NULL, 0, 'Corn and Shrimp Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2305', '<TD><RECIPE><B><I>Ingredients</B></I><BR>½ pound fresh shrimp, veins and shells removed and discarded<BR>1 teaspoon sugar<BR>2 teaspoons black rice vinegar<BR>2 teaspoons cornstarch<BR>1 ear fresh corn, kernels cut away from the cob<BR>½ cup rice, soaked for half an hour<BR>½ teaspoon coarse salt<BR>5 slices fresh ginger, peeled and slivered<BR><B><I>Preparation:</B></I><BR>1. Cut each shrimp into four pieces and mix with the sugar, rice vinegar, and cornstarch.<BR>2. Put the corn kernels and the rice into four cups of boiling water, reduce the heat and simmer for half an hour, then add the salt and the ginger, and simmer another fifteen minutes. Next, add the shrimp and their marinade.<BR>3. Stir well, add another three cups of boiling water, cook for half an hour longer, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B0BB49B4A2ED9B4F8DCE6D539B3C61F0', X'CA48A811AA4CCF40B161F9D47CEEED17', X'CA48A811AA4CCF40B161F9D47CEEED17', 0, 'javascript:', 'Crab and Fish Maw Soup', 2, NULL, 0, 'Crab and Fish Maw Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1867', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 ounces fish maw, soaked overnight, then cut into one to two-inch pieces<BR>1 Tablespoon vegetable oil<BR>4 to six slices fresh ginger, smashed with the side of a cleaver<BR>4 cups fish or chicken broth<BR>1/4 pound crab meat coarsely chopped, cartilage removed and discarded<BR>2 Tablespoons wood ear fungi, soaked until soft and then coarsely chopped<BR>2 dry shiitake mushrooms, soaked until soft, then squeezed of any water, and cut into quarters or smaller, if preferred<BR>3 Tablespoons cornstarch mixed with six Tablespoons cold water<BR>2 Tablespoons Chinese black vinegar<BR>1/4 teaspoon ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Drain then squeeze water out of the fish maw.<BR>2. Heat oil and stir-fry the ginger for one minute, then add the fish maw and stir-fry another minute, then add the broth, bring to the boil, then reduce heat and simmer for ten minutes.<BR>3. Add the crab meat, wood ear fungi, and the shiitake mushrooms and simmer this for three more minutes. Then bring the soup to the boil and stir in the cornstarch mixture, vinegar and ground pepper, and stir until thickened, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'78527EE988B83E47BF6C2D6C8C93D605', X'84285EF44C37C44D9AC1A196E032D3B5', X'84285EF44C37C44D9AC1A196E032D3B5', 0, 'javascript:', 'Creamy Corn and Crab Soup', 2, NULL, 0, 'Creamy Corn and Crab Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=303', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>10 ounces canned corn niblets<BR>4 cups chicken stock<BR>1 scallion, minced<BR>1 teaspoon minced ginger<BR>1 Tablespoon Shaoxing rice wine or dry sherry<BR>1 Tablespoon thin soy sauce<BR>1 teaspoon salt<BR>1/4 teaspoon ground white pepper<BR>1 teaspoon sugar<BR>2 teaspoons cornstarch mixed with 2 teaspoons water<BR>1/2 pound crab meat, cartilage removed<BR>1 egg<BR>1 teaspoon sesame oil<BR>2 Tablespoons minced coriander<BR><I><B>Preparation:</I></B><BR>1. In a blender, mix corn and half the chicken stock, pour that and the remaining stock into a pot and simmer ten minutes.<BR>2. Add scallion, ginger, rice wine, soy sauce, salt, pepper, and sugar. Bring to the boil then immediately lower heat and simmer five minutes.<BR>3. In a small bowl, mix egg and the sesame oil and slowly pour into the soup mixture mixing gently as you do this. Ladle into a tureen or in to individual soup bowls, add the coriander and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DC743AE41282CE42AE167C5B94B5F3F9', X'72F32882A909A4408AA57948947C6D17', X'72F32882A909A4408AA57948947C6D17', 0, 'javascript:', 'Crocodile Soup', 2, NULL, 0, 'Crocodile Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2101', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>1 pound crocodile meat<BR>½ pound roasted pork belly, sliced thin<BR>1/4 pound fresh ginseng, sliced thin<BR>10 red dates, pits removed and discarded<BR>5 slices fresh ginger<BR>6 fresh Chinese back mushrooms, soaked, their stems discarded<BR>1 whole fried bean curd stick, broken into small pieces<BR>2 teaspoons oyster sauce<BR>2 teaspoon thin soy sauce<BR>2 teaspoons white rock sugar, crushed<BR><I><B>Preparation:</B></I><BR>1. Heat wok, add the oil, and the crocodile meat and the pork belly slices and stir-fry for ten minutes, then add two quarts of boiling water, reduce the heat, and simmer for half an hour or until both meats are tender and can be torn apart with chopsticks.<BR>2. Add the rest of the ingredients and simmer another ten minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B300D2402AE41C49BE6B7DF8E309CB14', X'967F54D77D487B4F8664DF18810BF8C9', X'967F54D77D487B4F8664DF18810BF8C9', 0, 'javascript:', 'Crossing Bridge Rice Noodles', 2, NULL, 0, 'Crossing Bridge Rice Noodles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1602', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 cups chicken broth<BR>3 Tablespoons minced scallions<BR>1 brown bean curd, thinly sliced then cut into strips<BR>2 Tablespoons oil or hot oil<BR>1 or more cups vegetable pieces of your choice<BR>1/2 teaspoon salt<BR>8 thin slices of meat (pork, beef or another)<BR>optional: slices of fish, pepper powder, fresh coriander<BR>1 cup precooked rice noodles<BR><B><I>Preparation:</B></I><BR>1. Bring chicken broth to the boil, add the salt, scallions, bean curd, and the oil.<BR>2. Add slices of meat and vegetables, the optional ingredients, and the rice noodles, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'13CE081DA955B64E865568BF844787F2', X'8628C8E01CB7954E91C52F006D3684C2', X'8628C8E01CB7954E91C52F006D3684C2', 0, 'javascript:', 'Crossing the Bridge Noodles', 2, NULL, 0, 'Crossing the Bridge Noodles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=842', '<TD><RECIPE>Crossing the Bridge Noodles Ingredients: 1 three pound chicken (they say a fat hen) 1/4 duck or twelve ounces of duck meat 3 pork bones 1/4 pound pork filet, sliced thin 1/4 firm fish such as catfish, snake-head or other, about 1/4 pound, cut in thin pieces 1 sheet bean curd, about three ounces, cut into noodle-like threads 1 teaspoon salt 1/4 teaspoon ground white pepper 1 Tablespoon lard 1 teaspoon chili oil 3 Tablespoons sliced leek, white part only 1 scallion, minced 1/2 pound rice noodles few sprigs fresh coriander, for garnish Preparation 1. Put chicken, duck, and pork bones, and the sliced pork in a large pot with the snakehead fish, and cover them with boiling water. Boil the contents for ten minutes, then turn heat off and allow to cool. When cool, remove meat frm bones, and slice the various meats not already sliced. Discard all bones, and reserve all liquid, fat included. 2. Bring reserved liquid plus enough water to make eight cups of liquid, to the boil, then add all meat and fish and all other ingredients except the garnish and simmer until very, very hot, but not boiling water. 3. In another pot of water, boil the rice noodles for two minutes, drain and add to the soup/stew. Stir well, add garnish, and serve. Fish in mango sauce Ingredients: 4 Tablespoons coarsely minced celery 1/2 small carrot, peeled 1/2 green apple, peeled and cored and sliced into thin strips 1 Tablespoon lemon juice 1 pound boneless and skinless fish fillets, sole recommended 1 teaspoon cornstarch mixed with 1 whole egg, beaten 1/2 to 1 cup sesame seeds 2 mangoes, diced and without skin when removing from the pit 1/2 can lemon soda 2 Tablespoons lemon marmalade or bottled Chinese lemon sauce 1 to 2 slices fresh or canned pineapple 1 cup vegetable oil Preparation: 1. Remove strings from celery and cut into long strips. Cut carrot into same-size long strips. Blanch both for one minute in boiling water, and drain. 2. Mix apple pieces with lemon juice. 3. Cut fish into eight pieces and dip one side into the egg cornstarch mixture, then place carrot and celery strips crosswise on each of them. Drain apple pieces reserving liquid, and put these strips on the vegetables, then roll the fish tightly. Roll into the remaining cornstarch egg mixture and then into the sesame seeds, and allow to rest for ten minutes. 4. Set aside one quarter of the diced mango and put the rest of the diced mango into a blender. Add remaining lemon juice, lemon soda, marmalade, and pineapple and blend until just liquified. 5. Deep fry the fish rolls until a light golden color, do not over-fry them. Drain and set out in a circle on a serving plate. Top with reserved diced mango, and pour half of the sauce over the fish, the rest in the center where no fish is, then serve. Dongan duck ingredients: 1 five pound whole duck, quartered 2 Tablespoons corn or other vegetable oil 2 Tablespoons minced fresh ginger 1 teaspoon Sichuan peppers, lightly smashed with side of a cleaver 2 dried chili peppers, seeds removed , then minced 1 teaspoon Chinese rice wine 1 teaspoon Chinese black vinegar ½ teaspoon salt ½ cup each, sweet red and green peppers, cut in one-inch diamonds 2 scallions, minced 1 Tablespoon cornstarch mixed with 1 Tablespoon cold water 1 teaspoon sesame oil Preparation: 1. In a large heavy pot, place duck quarters. Cover with cold water, and bring to the boil, then reduce heat and simmer half hour. Remove duck, reserving the liquid, and let cool somewhat, then remove meat from the bones. 2. Return bones to the liquid and boil until liquid is reduced to half cup. Reserve it after discarding the bones. 3. Heat wok, add the oil, then stir-fry the ginger, Sichuan and chili peppers for half minute. Then add wine, vinegar, salt, and the duck meat, and stir-fry two minutes before adding the reserved liquid from cooking the duck and then the bones. Add the red and green peppers and the cornstarch mixture and continue to stir-fry until the sauce thickens, then serve');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2237831495322A4B8B40C9D32BF998A9', X'66168707CB57834EB11FAD8B6B2F7422', X'66168707CB57834EB11FAD8B6B2F7422', 0, 'javascript:', 'Cuttlefish Soup with Rice Noodles', 2, NULL, 0, 'Cuttlefish Soup with Rice Noodles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2080', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cuttlefish, their membranes and backbone removed and discarded, then cut at an angle vertically and horizontally<BR>2 Tablespoons Chinese rice wine<BR>1 scallion, knotted<BR>3 slices fresh ginger, smashed<BR>½ pound rice noodles, wide ones preferred<BR>4 black mushrooms, soaked, stems removed and discarded<BR>2 celery stalks, slice thinly on an angle<BR>1 carrot, cut in half the long way, then thinly sliced<BR>2 Tablespoons lard or vegetable oil<BR>6 cups chicken or fish stock<BR>1 teaspoon salt<BR>dash of ground white pepper<BR>10 fresh live clams, soaked in salt water for an hour, steamed for three minutes, shells discarded, likewise any clams that do not open<BR><B><I>Preparation:</B></I><BR>1. Blanch the cuttlefish pieces with the rice wine, scallion, and ginger for two minutes, then drain and discard everything but the cuttlefish.<BR>2. Put the mushrooms, celery, and carrot pieces in the oil, and stir-fry for one minute, then add the stock, salt and pepper, and the rice noodles, and simmer about two minutes until the noodles are soft. Then add the clams and cuttlefish pieces and simmer for one to two minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'50AE3DAB6DF96D49800C824DD453C572', X'A64E27E2CFC3EB44A97CA6C6287236F7', X'A64E27E2CFC3EB44A97CA6C6287236F7', 0, 'javascript:', 'Daikon and Conpoy', 2, NULL, 0, 'Daikon and Conpoy', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2277', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 conpoy soaked for two hours, then shredded into thin strips<BR>2 pounds daikon, peeled and made into balls using a melon-ball-tool, or dice into half-inch squares<BR>2 slices fresh ginger, minced<BR>1 teaspoon salt<BR>1 Tablespoon Chinese rice wine<BR>3 Tablespoons sesame oil<BR>1 quart chicken broth<BR><B><I>Preparation:</B></I><BR>1. Drain the shredded conpoy, then re-soak it for another two hours in one cup of hot water. Then drain and set aside.<BR>2. Mince or grind the rest of the daikon and put it and the shredded conpoy, and the soaking liquor, ginger, salt, rice wine, sesame oil, and the broth in a large pot and simmer this for ten minutes.<BR>3. Add the daikon pieces and simmer another five minutes, then serve in soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A445458AAC256E47A9645753E8C5ED27', X'DFD5B3DD97054E4A859D1B49DA3A1DEE', X'DFD5B3DD97054E4A859D1B49DA3A1DEE', 0, 'javascript:', 'Double Wrap in Soup', 2, NULL, 0, 'Double Wrap in Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=124', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>10 cabbage leaves<BR>10 dried bean curd sheets<BR>1/4 pound ground pork<BR>1 Tablespoon cooking wine<BR>3 Tablespoons scallion/ginger water<BR>1 teaspoon salt<BR>a pinch of ground white pepper<BR>1 Tablespoon cornstarch <BR>5 cups chicken stock<BR>1 teaspoon salt<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</I></B><BR>1. Blanch dried bean curd sheets and cabbage leaves separately in boiling water, drain and set aside.<BR>2. Mince ground pork, stir in rest of the ingredients except cabbage and bean curd sheets.<BR>3. Trim off hard leafstalks from cabbage and cut in half. Put two tablespoons of meat mixture on half a bean curd sheet topped with a cabbage leaf, then roll. Repeat until all materials are used.<BR>4. Arrange rolls in deep bowl. Add soup stock and salt. Place in rice cooker. Add two cups cold water to the rice cooker and push the button to start, then cook until the electricity turns off. Remove. Sprinkle with pepper. Add sesame oil and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B02171EB6F43B143832DB7A3EAAB7FF3', X'DE028D02D6FB6B4388726F71D24BFCC6', X'DE028D02D6FB6B4388726F71D24BFCC6', 0, 'javascript:', 'Dragon Soup', 2, NULL, 0, 'Dragon Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1299', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>4 scallions, white and green parts separated, each slivered on the angle<BR>3 slices fresh ginger, peeled and slivered<BR>1/2 teaspoon ground white pepper<BR>1 Tablespoon mushroom soy sauce<BR>1 teaspoon curry powder <BR>1/4 teaspoon ground Sichuan pepper<BR>1 teaspoon hot oil<BR>1 teaspoon sugar<BR>4 cups chicken broth<BR><B><I>Preparation:</B></I><BR>1. Heat oil in a small soup pot, and fry the white parts of the scallions until they are about ready to brown.<BR>2. Add all other ingredients but not the green scallion parts, and bring to below the boiling point, and reduce the heat and all this to simmer for ten minutes before adding the green parts of the scallions.<BR>3. Pour into individual bowls, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'062B536886C32343974631F8DB93734A', X'69C9388461B45C43B2678FBCE80D2D24', X'69C9388461B45C43B2678FBCE80D2D24', 0, 'javascript:', 'Dried Cole Soup', 2, NULL, 0, 'Dried Cole Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=180', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 package dried cole*<BR>2 Tablespoons bitter almond<BR>12 honey dates<BR>2 Tablespoons sweet almond<BR>1 dried orange peel, about 2 by 2 inches<BR>1 recipe prepared Basiv Pork Soup Stock<BR><B><I>Preparation:</I></B><BR>1. Rinse and soak the dried cole overnight. <BR>2. Drain and reserve the liquid. Cut the cole into four-inch pieces.<BR>3. Add cole and reserved liquid to prepared soup stock with the other ingredients. Simmer for at least two hours.<BR><B>Note:</B> Depending on the package, the ends of the bok choy may be somewhat tough so discard them as they are used to provide flavor. Please note that ''cole'' can be a misnomer--perhaps a poor translation because ''dried cole'' is <I>bok choy</I>. The soup can be made with either dried or fresh bok choy. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A60C55B45F05B94AB85EB677ECAC6056', X'2E368158AAEABE43A353665990077994', X'2E368158AAEABE43A353665990077994', 0, 'javascript:', 'Dry Fig Soup', 2, NULL, 0, 'Dry Fig Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1614', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 dried figs, cut into small pieces<BR>6 dried longan, cut into small pieces<BR>1 piece tangerine peels, cut into tiny pieces<BR>1 Tablespoon lotus seeds<BR>1 cup peeled and quartered burdock root<BR>1 lily bulb, each piece of the bulb cut into six pieces<BR>3 Tablespoons <I>goji</I> berries<BR>1 Tablespoon rock sugar<BR><B><I>Preparation:</B></I><BR>1. Soak figs, longan, and tangerine peel in their own one-half cup of boiling water for five minutes, then drain reserving the water from each of them.<BR>2. Strain the individual soaking waters, and put the fruits in a three- or four-quart pot. Add six cups water, the lotus seeds and the lily bulb pieces and bring to just below the boil, reduce the heat and simmer for one hour.<BR>3. Add <I>goji</I> berries and simmer another ten minutes, then add the rock sugar, and after the sugar dissolves completely, serve the soup in individual soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D904941E9273CC4ABF27523AF81FF7E5', X'CD0A817458456145BE1373546A1A968C', X'CD0A817458456145BE1373546A1A968C', 0, 'javascript:', 'Dry Squid and Vegetable Soup', 2, NULL, 0, 'Dry Squid and Vegetable Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1447', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 dried squid, soaked overnight covered and in the refrigerator, changing the water two or three times<BR>8 medium-size Chinese black mushrooms, soaked for half an hour in 1 cup water, stems discarded<BR>2 ounces roast pork<BR>1 Tablespoon sesame oil<BR>2 cloves garlic, minced<BR>2 stalks Chinese celery, cut into one-inch pieces<BR>3 water chestnuts, sliced<BR>1 Tablespoon light soy sauce<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon chili oil<BR>8 cups chicken stock<BR>8 fresh shrimp, peeled with veins removed, cut into four pieces each<BR><B><I>Preparation:</B></I><BR>1. Cut squid into one-inch pieces and score their undersides. Discard head, and cut tail section into four pieces; score that as well. Blanch for two minutes in boiling water, then drain well.<BR>2. Drain the mushrooms, reserving the water, and cut each one into quarters, 3.Cut roast pork into half-inch pieces.<BR>4. Heat sesame oil and fry mushrooms and pork for one minute, then add squid pieces and fry for one minute more.<BR>5. Add garlic, celery, and water chestnuts and stir-fry another minute before adding soy sauce, rice wine, and chili. Stir well, then add the chicken stock. Simmer for half an hour, add the shrimp and simmer another three minutes, then serve.<BR><I>Approximate nutrient analysis</I> when serving ten people. Each serving has 103 calories, 5 g carbohydrate, 9 g protein, 712 mg sodium, 6 total fat, 1 g saturated fat, and 60 mg cholesterol.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9A1DC42526CB4743B3574664A82CE0AE', X'FE7E008ACEFE0E47BABEA4D26D108DEF', X'FE7E008ACEFE0E47BABEA4D26D108DEF', 0, 'javascript:', 'Duck Congee', 2, NULL, 0, 'Duck Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=752', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 roast duck breast meat<BR>1 teaspoon dark soy sauce<BR>1 teaspoon sesame oil<BR>2 dried scallops, simmer in one cup of water for half an hour<BR>1 cup glutinous rice<BR>1/2 cup long grain rice<BR>1 slice fresh ginger<BR>3 scallions<BR>2 Tablespoons fresh coriander, coarsely chopped<BR><B><I>Preparation:</B></I><BR>1. Slice duck meat and mix with soy sauce ans sesame oil and set aside for one hour.<BR>2. Tear scallops into shreds and put in a large pot with both rices and the ginger. Add four quarts boiling water, bring to the boil then quickly lower the heat and simmer covered for two hours, stirring once or twice to prevent sticking.<BR>3. Add duck and simmer another half hour, without the cover, and stir this every ten minutes.<BR>4. Add scallions and simmer another three minutes, then put into a heated soup tureen. Sprinkle coriander leaves on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E61444A5E5F6BE40A4AFEDD268B749A8', X'BD0BDA3436363B488EB0EA8BE6504739', X'BD0BDA3436363B488EB0EA8BE6504739', 0, 'javascript:', 'Duck Eggs and Pork in Soup', 2, NULL, 0, 'Duck Eggs and Pork in Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1938', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound pork loin, thinly sliced, then slivered<BR>1 teaspoon sesame oil<BR>2 teaspoons Chinese rice wine<BR>1 teaspoon thin soy sauce<BR>1 Tablespoon cornstarch<BR>2 quarts chicken stock<BR>2 slices fresh ginger, minced finely<BR>2 raw salted duck eggs<BR>1 salted duck egg steamed until firm, then mashed<BR><B><I>Preparation:</B></I><BR>1. Marinate the pork in the sesame oil, rice wine, soy sauce, and cornstarch for half an hour.<BR>2. Heat the soup stock to below the boil, then add the ginger, duck eggs, and the pork and its marinade, and the mashed duck yolk and stir and keep beating until the stock comes to the boil, about one minute. Pour into a pre-heated soup tureen and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'37AF48D53F45ED4183B6871102DB067F', X'C6175D55594015469594930BDBD91FF8', X'C6175D55594015469594930BDBD91FF8', 0, 'javascript:', 'Duck Soup with Mushrooms', 2, NULL, 0, 'Duck Soup with Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=261', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 fresh duck, cut into four pieces<BR>4 cups chicken broth <BR>20 dried Chinese black forest mushrooms, soaked in two cups warm water for half an hour, cut away the stems; reserve the water<BR>20 straw mushrooms, cut in half<BR>1/4 cup cloud ear mushrooms, soaked twenty minutes in warm water, then rinsed<BR>1/4 cup rice wine<BR>1 teaspoon coarse salt<BR>1/4 cup bamboo shoots, sliced<BR>1 carrot, peeled and sliced<BR>8 shrimp, peeled, remove black vein and cut in half the long way, then in half across these pieces<BR><I><B>Preparation:</I></B><BR>1. Steam duck and broth in a deep bowl for half an hour, remove duck and discard bones, and cut any large pieces into smaller ones.<BR>2. Strain the liquid, rinse the bowl and return duck meat and stock to it. Strain and add the mushroom soaking water.<BR>3. Quarter the Chinese black forest mushrooms and coarsely chop the cloud ears ones before adding them and the straw mushrooms, rice wine, salt, bamboo shoots, and the sliced carrot; steam another half an hour.<BR>4. Add shrimp and let rest one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DAFDB3940B808440A0F20A23192F520E', X'0D50D82F62652D4AA620EB1E799DC433', X'0D50D82F62652D4AA620EB1E799DC433', 0, 'javascript:', 'Egg Drop Soup', 2, NULL, 0, 'Egg Drop Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=705', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 quarts chicken stock<BR>shells from a dozen shrimp<BR>1 pork bone, with about half ounce each of meat and fat left on<BR>1/2 teaspoon sugar<BR>2 Tablespoons cornstarch mixed with 2 Tablespoons cold water<BR>1 egg, beaten with 2 Tablespoons cold water<BR>1 scallion, white part minced and green sliced on the angle<BR><B><I>Preparation:</B></I><BR>1.simmer stock, shrimp shells, and pork bone for one hour, remove solids and filter the remaining liquid.<BR>2. Reheat stock, add sugar and cornstarch mixure, and mix well, and bring close to the boil.<BR>3. Add egg mixture slowly, stir continuously, then add scallion pieces, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'29E391486698734B988FC0CC4D8394C5', X'5D1D7163B859A749B0EA49E1A74E7827', X'5D1D7163B859A749B0EA49E1A74E7827', 0, 'javascript:', 'Eight Treasure Congee', 2, NULL, 0, 'Eight Treasure Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=902', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup uncooked millet, soaked overnight<BR>1/4 cup uncooked red beans, soaked overnight<BR>1/4 cup glutinous rice, soaked for four hours<BR>1/4 cup sorghum, soaked for four hours<BR>1/4 cup peanuts<BR>2 Tablespoons walnuts<BR>1/4 cup regular long grain rice<BR>1/4 cup brown sugar<BR>1/2 teaspoon salt<BR>1/4 teaspoon ground black pepper<BR>1/2 carrot, peeled and diced<BR>1/2 stem of Chinese celery, diced<BR><B><I>Preparation:</B></I><BR>1. Drain and discard the water from the millet, red beans, glutinous rice, and sorghum. Put these items in a pot with three cups of water, bring to the boil and let boil for two minutes, then turn the heat source off and allow to rest for two hours.<BR>2. Bring pot with above five ingredients and ten cups of water to the boil, reduce heat, and simmer one hour before adding the peanuts, walnuts, long grain rice, sugar, salt, and pepper. Simmer for one hour, then add carrot and celery, and simmer another half hour. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A11F797D5CCE66499A9E90CD4F0308C0', X'036DA15AE80DCE40BA8B494B712439AD', X'036DA15AE80DCE40BA8B494B712439AD', 0, 'javascript:', 'Fish and Sour Soup', 2, NULL, 0, 'Fish and Sour Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1269', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 whole fish, one and a half to two pounds, gutted, scaled, fins removed<BR>1 large radish, about half pound<BR>1 square diced mi fan or regular doufu<BR>2 slices fresh ginger, peeled and slivered<BR>1 teaspoon sugar<BR>1/4 teaspoon thin soy sauce<BR>1/2 teaspoon salt<BR>1/2 teaspoon ground Sichuan peppercorns, smashed with the end of the handle of the cleaver<BR>1/2 teaspoon chili peppers or chili sauce<BR>2 Tablespoons chicken or fish bouillon powder<BR>1 Tablespoon white or black rice vinegar<BR>1/4 cup fermented cabbage, cut into one-inch pieces<BR>2 Tablespoons of cornstarch mixed with an equal amount of cold water<BR><B><I>Preparation:</B></I><BR>1. Rinse and dry the fish, and cut it into two-inch pieces.<BR>2. Peel then cut the radish into one-quarter-inch wide strips no more than two inches long.<BR>3. Cut the mi doufu or doufu made with soy milk into two-inch squares and set aside.<BR>4. Bring three quarts of water to the boil, then add the ginger, sugar, soy sauce, salt, Sichuan pepper pieces, chili peppers or chili sauce, and the bouillon powder, and stir well.<BR>5. Reduce the heat, add the pieces of fish and radish and simmer for three minutes. Raise the temperature almost to the boil, add the mi fan or doufu, vinegar, fermented cabbage, and the cornstarch mixture. Stir until thickened, about half minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DD58F9A664D66D47B181008B73C392F5', X'724555F2E8CC874B9E5E698A283164EF', X'724555F2E8CC874B9E5E698A283164EF', 0, 'javascript:', 'Fish Congee', 2, NULL, 0, 'Fish Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=602', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1 cup glutinous rice, soaked for one hour<BR>2 dried scallops, soaked for one hour in half-cup of tepid water, drained, and shredded by hand<BR>2 Tablespoons chicken or shrimp bouillon powder<BR>1/4 cup salted fish, rinsed, boned, cut up, then fried for one minute<BR>1/4 pound chopped or ground pork<BR>1 Tablespoon cornstarch<BR>1/4 cup mixed finely minced ginger and coriander leaves<BR><B><I>Preparation:</B></I><BR>1. Put drained rice and drained scallop pieces into a pot with two quarts of water and bring to the boil, lower the heat and simmer for an hour. Then remove from the heat and allow to sit for an hour.<BR>2. While that cooks, mix bouillon powder, fried fish, pork, cornstarch, and ginger coriander mix and make these into small balls.<BR>3. Bring two quarts of water to the boil and simmer these meat balls for three or four minutes, then drain.<BR>4. Reheat the rice mixture after it sits or when ready to serve it, when it boils, turn off the heat, add the meat balls, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C06A720AFF8CA84D8A1B0C307E885D1D', X'9096A26C8D0EA149AD068D0E7DF0F097', X'9096A26C8D0EA149AD068D0E7DF0F097', 0, 'javascript:', 'Fish Maw Soup', 2, NULL, 0, 'Fish Maw Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=669', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound any white fish<BR>2 to four ounces fish maw, soaked (see instructions in article above)<BR>2 Tablespoons sesame oil<BR>8 shiitake mushrooms, soaked, stems removed, and quartered; reserve two tablespoons of the soaking water<BR>1/4 pound mustard greens, stems part cut into small pieces, leaves in one-inch pieces<BR>1 teaspoon thin soy sauce<BR>1/2 teaspoon sugar<BR>1 teaspoon corn oil<BR>2 scallions, cut into one-inch pieces<BR>2 slices fresh ginger, peeled and slivered<BR>1 Tablespoon rice wine<BR>1/2 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Cut the fish into quarter-inch by two inches pieces.<BR>2. Cut the fish maw into the same size pieces.<BR>3. Heat the sesame oil and fry the maw for one minute, draining it on paper towels; then cool it. When cool, use the side of the cleaver and press out as much oil as possible.<BR>4.Mix soy sauce, sugar and corn oil in a bowl, add the mushrooms and mustard greens, and marinate for fifteen minutes.<BR>5. Heat the wok, add the mushroom water, add fish maw and mushroom mixture and stir for two minutes. Then add four cups of boiling water, half the scallions and all the ginger and fish pieces. Cook for three minutes, pour into a heated serving bowl, sprinkle the remaining scallions on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9F768971B411D84E9E3F9E97128550E6', X'B78CEF0C1215C14A86450F551E85ACA7', X'B78CEF0C1215C14A86450F551E85ACA7', 0, 'javascript:', 'Fish Soup with Greens and Coconut', 2, NULL, 0, 'Fish Soup with Greens and Coconut', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1258', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound fillet of tile fish, cut into one-inch squares<BR>2 teaspoons thin soy sauce<BR>2 teaspoons cornstarch<BR>1 teaspoon sesame oil<BR>2 teaspoons Chinese rice wine<BR>1/4 teaspoon ground white pepper<BR>5 cups chicken broth<BR>2 teaspoons shredded coconut<BR>1 teaspoon minced chopped fresh cilantro<BR><B><I>Preparation:</B></I><BR>1. Mix fish squares with soy sauce and cornstarch and let rest ten minutes.<BR>2. Put fish mixture, sesame oil, rice wine, ground white pepper, and the chicken broth into a pot and heat on medium heat until very hot.<BR>3. Put into soup tureen or individual soup bowls, Sprinkle coconut and minced cilantro on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'222CB4AFB7CEE343815496A59C555B91', X'46B591139FF4D3478F958F6A828B1716', X'46B591139FF4D3478F958F6A828B1716', 0, 'javascript:', 'Fish Soup with Star Fruit', 2, NULL, 0, 'Fish Soup with Star Fruit', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1153', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 whole fish, about one pound, or two smaller ones<BR>1 chayote, peeled, seeded and diced<BR>2 candied Chinese brown dates, each cut in six to eight pieces<BR>1 Tablespoons vegetable oil<BR>2 slices fresh ginger<BR>1/2 teaspoon coarse salt<BR>1 star fruit, sliced<BR><B><I>Preparation:</B></I><BR>1. Scale the fish, gut it, remove fins and the end of the tail.<BR>2. In a large pot, bring eight cups water to the boil. Gently put in the fish, chayote, and cut up dates. Reduce heat to low and simmer for half an hour.<BR>3. Heat oil in a wok or fry pan and stir-fry the ginger slices.<BR>4. Add the stir-fried ginger, salt, and the star fruit to the soup, and simmer another half an hour. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4D67F4A351F7A648B9B879A3BA1FC2D2', X'50F07C1C5AE5064B8026462590DE6647', X'50F07C1C5AE5064B8026462590DE6647', 0, 'javascript:', 'Fish Soup, Hunan Style', 2, NULL, 0, 'Fish Soup, Hunan Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1569', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound fillets of haddock or another white fish<BR>2 Tablespoons Chinese rice wine<BR>1 Tablespoon sesame or vegetable oil 2 <I>yao tai</I>, a fried cruller, sliced<BR>1 Tablespoon sesame seeds, lightly crushed<BR>1/2 cup shredded lettuce<BR>1 scallion, minced<BR>dash ground white pepper<BR>2 teaspoons coarse salt<BR>16 Tablespoons salted rice and mustard greens, optional<BR>2 teaspoons Chinese rice vinegar<BR>8 cups chicken or fish stock<BR><B><I>Preparation:</B></I><BR>1. Cut fish into two-inch pieces, and cut each of them in half horizontally. Then toss them gently with the rice wine and set aside for ten minutes.<BR>2. Heat oil in a wok or small fry pan and fry the slices of the <I>yao tai</I> on both sides, about one minute on each side, until crisp, then remove them to a large serving bowl. Fry the sesame seeds for one minute and remove them to the same bowl.<BR>3. Next, fry the fish for one minute and remove it to the same bowl, and add the shredded lettuce, scallion, pepper and salt, vinegar, and two tablespoons salted rice and mustard greens, if using them.<BR>4. Bring the vinegar and the chicken stock to a rolling boil. Pour it over all of the ingredients in the bowl, let it rest one to two minutes, toss, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EBA4C2AB57727C46A422681696414CE3', X'9F466423251BDD489940638439A60FD0', X'9F466423251BDD489940638439A60FD0', 0, 'javascript:', 'Frogs Leg Soup', 2, NULL, 0, 'Frogs Leg Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=176', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pound frogs legs (if frozen, defrost them) <BR>16-24 cloves garlic, peeled<BR>6 slices ginger, minced fine<BR>1/2 cup Fujian Loh Chiew wine (or other rice wine)<BR>1/4 teaspoon ground white pepper<BR>dash of salt<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</I></B><BR>1. Cut the legs into segments, then blanch them one minute, then drain. Do not put into cold water if using immediately.<BR>2. Put frogs legs into an electric rice cooker with the garlic, ginger, and wine.<BR>3. Bring two quarts of water to the boil and pour it into the rice-cooker. Cook for ten minutes.<BR>Add pepper and salt and sesame oil and serve.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4B6BDDA8EFE57E46B237FFFF00A625F9', X'2F0DA9F2F379F246AD25A59F5625B854', X'2F0DA9F2F379F246AD25A59F5625B854', 0, 'javascript:', 'Fruit and Vegetable Soup with Fish', 2, NULL, 0, 'Fruit and Vegetable Soup with Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=253', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound cod filet, sliced into thin strips<BR>1 teaspoon ginger juice<BR>1/2 teaspoon sugar<BR>1/2 teaspoon salt<BR>1 Tablespoon rice wine<BR>1 Tablespoon cornstarch<BR>1 teaspoon sesame oil<BR>4 cups chicken broth<BR>1 slice fresh ginger, cut into slivers<BR>1/2 cup bamboo shoots, sliced<BR>1/2 cup water chestnuts, sliced<BR>1 cup Chinese cabbage, sliced thin<BR>4 dried shiitake mushrooms, soaked for twenty minutes then sliced, reserving the liquid<BR>1/4 cup mandarin oranges<BR>1 kiwi, peeled, sliced, then diced<BR><B><I>Preparation:</B></I><BR>1. Mix fish, ginger juice, salt, sugar, rice wine, cornstarch, and sesame oil.<BR>2. Bring broth to simmer, add fish mixture, and all other ingredients except the kiwi and heat thoroughly, then add kiwi and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CD80982C1F117545BFF78888FE99608B', X'EDA761FCADA1DC45B4DC865D12A0478E', X'EDA761FCADA1DC45B4DC865D12A0478E', 0, 'javascript:', 'Fruit Soup, Hubei-style', 2, NULL, 0, 'Fruit Soup, Hubei-style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1078', '<TD><RECIPE><B><I>Ingredients</I></B><BR>1/2 pound dried regular or white figs, stems discarded<BR>1/2 pound dried lotus seeds, pits removed and discarded<BR>1/2 pound dried longan pulp<BR>24 Chinese red dates, pits removed and discarded<BR>1/4 pound white rock sugar<BR>2 Tablespoons goji berries<BR><B><I>Preparation:</B></I><BR>1. Soak figs and lotus seeds separately in two cups of water each, for twenty-four hours. Discard the lotus seed water but keep the water from the figs.<BR>2. Boil the lotus seeds in four cups of water for half an hour then simmer them for two hours, or until they are soft, even falling apart. The water will almost boil away.<BR>3. Put the remaining lotus seeds and water, and the figs and their water into a heat-proof bowl. Add the longan pulp and the red dates, and steam, covered over boiling water for twenty minutes. Then add two cups of boiling water and the goji berries and steam everything another five minutes. Serve hot, warm, or cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EC9464A54E54454EB16C2BEBB8E69DF1', X'556DBC230FC0F84FA263EC87B745808E', X'556DBC230FC0F84FA263EC87B745808E', 0, 'javascript:', 'Fujian Spinach Soup', 2, NULL, 0, 'Fujian Spinach Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1686', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 scallions, minced<BR>3 Tablespoons crab meat, cartilage remove, then minced<BR>6 Tablespoons minced beef or pork, or both<BR>1 teaspoon cornstarch<BR>1/2 pound spinach, washed then cut into thin strips<BR>1 teaspoon vegetabel oil<BR>2 egg yolks<BR>1 slice fresh ginger, minced<BR>dash white ground pepper<BR>1 Tablespoon Fujianese wine<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix scallions wth the crab meat.<BR>2. Mix meat with the cornstarch and make small balls, then wrap some crab meat around each ball hiding the meat in its inside.<BR>3. Toss the spinach, oil, egg yolks, and ginger. Heat a wok or fry pan and stir-fry the spinach mixture for one minute, and remove it to a bowl or plate.<BR>4. Bring six cups of water to the boil, reduce the heat and add the crab-meatballs, and simmer for five minutes before returning the spinach to the pot.<BR>5. Add the ground pepper, wine, and sesame oil, simmer another minute, then serve in individual bowls or put in a pre-heated soup tureen and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7F8720E3C9948F40AF8C679A9D0A3771', X'0C5EEBD9BD26BB4782BA7F312124BE87', X'0C5EEBD9BD26BB4782BA7F312124BE87', 0, 'javascript:', 'Fun Si Meatball Soup', 2, NULL, 0, 'Fun Si Meatball Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=128', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 ounces <I>fun si</I>, also known as cellophane noodles<BR>1/2 pound ground pork or beef<BR>1 Tablespoon minced scallions<BR>2 slices gingerroot, minced fine<BR>1 Tablespoon light soy sauce<BR>1 teaspoon sugar<BR>2 teaspoons dry sherry<BR>1/2 teaspoon salt<BR>1 egg white<BR>1 Tablespoon corn starch<BR>1 and a 1/2 cups soup stock<BR>4 sprigs cilantro, also known as Chinese parsley, for garnish<BR><I><B>Preparation:</I></B><BR>1. Soak the <I>fun si</I> noodles in hot water for half hour then drain them and cut into desired lengths.<BR>2. Mix ground meat and all other ingredients except the garnish and form meatballs about an inch in diameter.<BR>3. Bring soup to the boil, add the noodles and bring it back to the boil then add the meatballs and boil once again for five minutes, then ladle into bowls and garnish with cilantro and serve.<BR><B>Note:</B> Adaptations to this recipe can be found on page 106 of <I>An Encyclopedia of Chinese Food and Cooking</I> by Chang and Kutscher. Another adaptation is to mix one-quarter to one-half cup shredded preserved kohlrabi (<I>tsa tsai</I>) when the noodles are added. The best preserved kohlrabi comes from the Sichuan province. Be sure to rinse it in cold running water after shredding. And, if you see a can of preserved turnip, that is most likely kohlrabi, as most are erroneously labelled. Any remaining vegetable should be stored in a glass jar in the refrigerator.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'399778C835499E49A637284D2B0E8EF6', X'A5F0FC28E01ACF4C8D094187C47467F5', X'A5F0FC28E01ACF4C8D094187C47467F5', 0, 'javascript:', 'Gingko Nuts and Dried Bean Curd Soup', 2, NULL, 0, 'Gingko Nuts and Dried Bean Curd Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=693', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 sticks dried bean curd, soaked for fifteen minutes<BR>20 canned or boiled gingko nuts <BR>2 ounces rock sugar, brown preferred<BR>1 egg, beaten<BR><B><I>Preparation:</B></I><BR>1. Cut bean curd sticks into two inch pieces and put into a small heat-proof bowl.<BR>2. Add gingko nuts and rock sugar and 1 cup water, and cover the bowl.<BR>3. Steam for one hour over rapidly boiling water.<BR>4. Remove the cover, fold in the beaten egg and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F065459568A95545B95FFF3CCBA90F26', X'C16E84ECF144CD4E8088AF6A00C32F26', X'C16E84ECF144CD4E8088AF6A00C32F26', 0, 'javascript:', 'Ginkgo and Sweet Rice Soup', 2, NULL, 0, 'Ginkgo and Sweet Rice Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=139', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 cups glutinous rice flour<BR>1 cup assorted peeled and diced fruit such as pear, peach, mandarin orange, litchi, and longan<BR>3 ounces cooked or canned ginkgo nuts<BR>6 Chinese dates (jujubes), cooked for five minutes<BR>1 teaspoon sweet osmanthus<BR>1 Tablespoon Chinese slab or light brown sugar<BR>2 Tablespoons of water chestnut powder or cornstarch mixed with 2 Tablespoons of cold water<BR><I><B>Preparation:</I></B><BR>1. Mix rice flour with two cups of water and cook until very thick, stirring constantly. Chill then make into one inch balls. Simmer them until they float to the surface, then remove, chill, and set aside.<BR>2. Mix all other ingredients and bring to a boil. after it thickens slightly and clears, cool. Then add rice balls and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'34C1E8F872555B4AAADADF680D0D47A0', X'59F3727C84AFAE4D9043B02628972147', X'59F3727C84AFAE4D9043B02628972147', 0, 'javascript:', 'Ginseng Chicken Astragalus Soup', 2, NULL, 0, 'Ginseng Chicken Astragalus Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2336', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 three to four pound young chicken, cut in quarters, internal organs discarded<BR>1 cup glutinous rice, soaked overnight<BR>4 dried milk vetch root slices boiled in four cups of water fr one hour, then drained<BR>3 scallions, knotted<BR>1 one-inch piece ginseng, sliced thinly<BR>1 head garlic, all cloves peeled<BR>4 to 6 red jujubes<BR>1/4 teaspoon ground black pepper<BR><B><I>Preparation:</B></I><BR>1. Rnse and dry chicken pieces with paper towes.<BR>2. Drain rice and the milk vetch, saving three cups of water from each<BR>3. Put chicken pieces in the drained waters, add scallion knots, and simmer for forty minutes, then discard skin and bones from the chicken pieces and cut meat into one-inch pieces.<BR>4. Add rest of the ingredients and simmer half an hour longer Serve with or without garlic cloves.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'058E61A336226D4FAF38FBE48C73CA32', X'6EC86D978BBCDA4CBFE8B5C78BAD30D9', X'6EC86D978BBCDA4CBFE8B5C78BAD30D9', 0, 'javascript:', 'Ginseng Chicken Broth', 2, NULL, 0, 'Ginseng Chicken Broth', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1347', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 four to five pound chicken, insides removed and rinsed<BR>1 Tablespoon coarse salt, divided in half<BR>1 medium to large ginseng root, rinsed well but not peeled<BR>1/2 cup <I>goji</I> berries<BR>1/2 cup Jobs tears<BR>2 large onions, cut in quarters<BR>1 large knob fresh ginger, cut in half, then each piece smashed with the side of a cleaver<BR>3 Tablespoons soy sauce<BR>1 teaspoon sesame oil<BR>1 scallion, minced<BR>1 clove garlic, minced<BR>2 slices fresh ginger, peeled and minced<BR>1/2 teaspoon Chinese black vinegar<BR><B><I>Preparation:</B></I><BR>1. Rub the half portion of salt on the inside and outside of the chicken, and allow to rest for half an hour.<BR>2. In a large soup pot, bring six quarts of water to the boil then carefully add the chicken so as not to get scalded. Reduce the heat and scum, as needed for five minutes then add ginseng root, <I>goji</I> berries, Jobs tears, onions, and the ginger. Simmer for three hours, turning the chicken three or four times. Then remove the chicken to a serving bowl.<BR>3. While the chicken is cooking, mix soy sauce, sesame oil, scallion, garlic, and ginger pieces, and the vinegar, and put into a small bowl to be used for dipping.<BR>4. Spread the other ingredients around the chicken, and eat it tearing off pieces with chopsticks. Dip the pieces of chicken into the sauce, if desired, eat the herbal items plain.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'25446CF72BDD3141A2414C10DA023305', X'097C2B83BA4902498145D3D72D0A9B88', X'097C2B83BA4902498145D3D72D0A9B88', 0, 'javascript:', 'Ginseng Fish Head Soup', 2, NULL, 0, 'Ginseng Fish Head Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=138', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 fish head, about two pounds<BR>1 Tablespoon coarse salt<BR>2 scallions, minced<BR>1 Tablespoon rice vinegar<BR>1/4 teaspoon ground white pepper<BR>1 ounce ginseng, sliced<BR>1 teaspoon sesame oil<BR><I><B>Preparation:</I></B><BR>1. Rub fish head with salt and marinate with scallions, rice vinegar, and white pepper for about half an hour, then drain.<BR>2. Blanch fish head for thirty seconds in boiling water.<BR>3. Bring sliced ginseng an four cups of boiling water to a boil, lower heat and simmer for an hour. Then add fish head and the marinade and simmer another fifteen to twenty minutes. Add the rest of the ingredients and simmer another five minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DF3E18430E65E545A6B7402C3C2F83A6', X'F651D9A54BDBA545B73887FC5B9422E9', X'F651D9A54BDBA545B73887FC5B9422E9', 0, 'javascript:', 'Ginseng, Pork, and Pine Nut Soup', 2, NULL, 0, 'Ginseng, Pork, and Pine Nut Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1348', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound shin pork, cut into two-inch pieces<BR>2 small ginseng roots, about half cup, each cut into one-inch pieces<BR>3 Tablespoons Job''s tears<BR>3 Tablespoons pine nuts<BR>1 piece tangerine peel, soaked until soft<BR>3 candied Chinese brown or black dates, each cut into four pieces<BR>2 zucchini, peeled, each cut into eight pieces<BR>salt and pepper, to taste<BR><B><I>Preparation:</B></I><BR>1. Put the pork into a large soup pot with eight quarts of water, bring to the boil, remove scum, then reduce the heat to simmer.<BR>2. Add the ginseng, jobs tears, pine nuts, tangerine peel, and the cut-up dates and simmer for two hours.<BR>3. Add another two quarts of water and the zucchini and simmer for another forty minutes then season with salt and pepper and simmer another ten minutes. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B7FBA7C37CF99E4F973592F37E78F7AB', X'282F999414A27B4D9B874F48244F72F3', X'282F999414A27B4D9B874F48244F72F3', 0, 'javascript:', 'Gluten and Bean Curd Soup', 2, NULL, 0, 'Gluten and Bean Curd Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1453', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons baking soda<BR>3 bean curd sheets (about three ounces)<BR>2 Tablespoons dried bamboo shoots, soaked<BR>3 dried Chinese black mushrooms, soaked until soft, and minced<BR>2 Tablespoons minced Smithfield ham<BR>2 scallions, minced<BR>2 slices fresh ginger, peeled and minced<BR>3 Tablespoons pork, minced<BR>4 gluten balls, minced<BR>2 quarts vegetarian stock<BR>1 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Add two cups water to the baking soda. Soak the bean curd sheets in this for five minutes. Then rinse and drain them. Discard the water.<BR>2. Mix bamboo shoots, black mushrooms, ham, scallions, and ginger. Spread one-third of this on one bean curd sheet, and top with the minced wheat gluten. Spread one-third of the ground meat on this and roll into a long strip. Repeat with the other two sheets.<BR>3. Steam these three rolls for twenty minutes, remove and slice on an angle.<BR>4. In a large pot, heat stock and season it with the salt. Put three to five stuffed bean curd sheet slices in each individual bowl. Pour soup over them, and serve.<BR><B>Note:</B> Another way is not to mince the gluten balls, but to mince the bean curd sheets. Stuff each gluten ball and steam for twenty minutes. Cut each in half, put two halves in an individual soup bowl, pour soup over them, and serve. <I>No nutrient analysis</I> because the program we use has none for dried bamboo shoots.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C55E34EFA13F1E40AAD8E72D4BC88173', X'F92572DF24EC214491D510760A1AC646', X'F92572DF24EC214491D510760A1AC646', 0, 'javascript:', 'Gluten and Bean Curd Soup', 2, NULL, 0, 'Gluten and Bean Curd Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1001', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons baking soda<BR>3 bean curd sheets (about three ounces)<BR>2 Tablespoons dried bamboo shoots, soaked<BR>3 dried Chinese black mushrooms, soaked until soft, and minced<BR>2 Tablespoons minced Smithfield ham<BR>2 scallions, minced<BR>2 slices fresh ginger, peeled and minced<BR>3 Tablespoons pork, minced<BR>4 gluten balls, minced<BR>2 quarts vegetarian stock<BR>1 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Add two cups water to the baking soda. Soak the bean curd sheets in this for five minutes. Then rinse and drain them. Discard the water.<BR>2. Mix bamboo shoots, black mushrooms, ham, scallions, and ginger. Spread one-third of this on one bean curd sheet, and top with the minced wheat gluten. Spread one-third of the ground meat on this and roll into a long strip. Repeat with the other two sheets.<BR>3. Steam these three rolls for twenty minutes, remove and slice on an angle.<BR>4. In a large pot, heat stock and season it with the salt. Put three to five stuffed bean curd sheet slices in each individual bowl. Pour soup over them, and serve.<BR><B>Note:</B> Another way is not to mince the gluten balls, but to mince the bean curd sheets. Stuff each gluten ball and steam for twenty minutes. Cut each in half, put two halves in an individual soup bowl, pour soup over them, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'80BA97209F33AD4195BA27C71B118FA2', X'0612E87A09BF7643BA5253E145CCA894', X'0612E87A09BF7643BA5253E145CCA894', 0, 'javascript:', 'Goose Carcass Soup', 2, NULL, 0, 'Goose Carcass Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2050', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>I goose carcass, chopped into pieces<BR>2 cups Chinese cabbage, cut into one- to two-inch cubes<BR>2 cakes bean curd, one soft and one firm, slivered<BR>1 half-inch cube of a fermented square of tofu, mashed<BR>2 Tablespoons Chinese rice wine<BR>1 Tablespoon Chinese black vinegar<BR>1 Tablespoons Chinese rice vinegar<BR>2 Tablespoons mushroom soy sauce<BR><B><I>Preparation:</B></I><BR>1. Simmer the carcass for one hour in two quarts of water, remove the bones and tear off any meat and set that aside.<BR>2. Put the cabbage, all the tofu, rice wine, the black and the rice vinegar, and the soy sauce into the cooked stock and stir well. Be sure the mashed fermented tofu is mixed in thoroughly. Simmer for twenty minutes, add the goose meat, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E9D8132F7C61EA4CB0CAAB422B1C8B74', X'40EE1297E9B2D742A59B0BC1D6311070', X'40EE1297E9B2D742A59B0BC1D6311070', 0, 'javascript:', 'Gooseberry Soup', 2, NULL, 0, 'Gooseberry Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2352', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons dried lily bulbs, soaked until soft, then steamed for half an hour<BR>1/4 cup barley, steamed for half an hour, then drained<BR>3 Tablespoons canned or soft-cooked until soft gingko fruits<BR>3 Tablespoons cooked until soft or canned lotus seeds<BR>3 Tablespoons Chinese red dates, cooked until soft<BR>6 preserved plums<BR>5 dried longans, pits removed and discarded, each cut into four pieces<BR>1 ounce haw crab jelly<BR>1/4 cup gooseberries, peeled, each one cut one-inch pieces<BR>6 Tablespoons granulated sugar<BR>2 Tablespoons cornstarch mixed with the same amount of cold water<BR><B><I>Preparation:</B></I><BR>1. Steam lily bulbs, then add the steamed barley, soft ginkgo seeds, lotus seeds, softened red dates, preserved plums, longan, and the haw fruit jelly.<BR>2. Add the gooseberry pieces (now called kiwi fruits), sugar, and the cornstarch mixture, along with three or four cups of water. Bring to the boil, allow to thicken, then serve in small bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'35ABAADD91AC674E905582CC894B10F1', X'CA84C40DD78F154EAAC79C8882AF671B', X'CA84C40DD78F154EAAC79C8882AF671B', 0, 'javascript:', 'Green and Black Soup', 2, NULL, 0, 'Green and Black Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=507', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 silk squash, ribs removed by peeling them<BR>1/4 pound pork loin, cut into thin slices, then each slice into thin strips<BR>1 Tablespoon rice wine<BR>1 Tablespoon water chestnut flour<BR>1/4 teaspoon ground white pepper<BR>1 Tablespoon wolfberries, optional<BR>1 scallion, mince white part and slice the green thinly<BR>1 black mushroom, soaked in warm water twenty minutes and slices thinly<BR>1/2 cup thinly sliced bamboo shoots<BR>4 cups chicken or vegetable broth<BR><B><I>Preparation:</B></I><BR>1. Cut the silk squash across the narrow way to make four pieces. Cut each of these in half the long way and then angle cut each strip.<BR>2. Mix pork strips with wine, water chestnut flour and white pepper and marinate for ten minutes.<BR>3. Heat broth, but do not bring it to the boil. Add silk squash, the pork mixture, wolfberries, the white part of the scallions, the mushrooms, and the bamboo shoots and simmer for fifteen minutes. Add scallion tops, simmer another minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6BF68B96FB0457458B7FC55E40780F2D', X'FE147C4C3B88384AB07E0B258A777E30', X'FE147C4C3B88384AB07E0B258A777E30', 0, 'javascript:', 'Hairy Vegetable Soup', 2, NULL, 0, 'Hairy Vegetable Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1690', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound pork ribs, blanched in boiling water for three minutes<BR>I hairy marrow (also known as Chinese Yam or fuzzy gourd), peeled<BR>4 Chinese black mushrooms, soaked, their stems discarded<BR>1/4 pound any skinless and boneless white fish<BR>1/4 pound shrimp, peeled, their black veins discarded<BR>2 slices fresh ginger<BR>1 small carrot, peeled<BR><B><I>Preparation:</B></I><BR>1. Simmer pork ribs in one quart water for one hour, discard their bones, fat, and any grizzle, and coarsely chop their meat. Reserve the meat and the cooked liquid.<BR>2. Coarsely dice the vegetable, and mince the mushrooms, fish, shrimp, ginger, and the carrot. Add these to the meat and remaining liquid, add another quart of water to this and simmer for half an hour, then serve. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'22EF089C0C613E499A56F3D493ECDC38', X'6FC2FBC734E2F24392B2872D1C36A492', X'6FC2FBC734E2F24392B2872D1C36A492', 0, 'javascript:', 'Hakka Meat and Fish Ball Soup', 2, NULL, 0, 'Hakka Meat and Fish Ball Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1314', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon salt<BR>1 teaspoon sugar<BR>1 teaspoon thin soy sauce<BR>1 teaspoon Maotai or another Chinese liquor<BR>1 teaspoon sesame oil<BR>1 Tablespoon cornstarch<BR>1 egg white<BR>5 ounces finely minced or ground beef, set into a small bowl<BR>5 ounces finely minced or ground white fish, set into a small bowl<BR>5 cups chicken broth<BR>2 slices fresh ginger, slivered<BR>1 scallion, minced and set aside<BR><B><I>Preparation:</B></I><BR>1. Mix salt, sugar, soy sauce, Maotai, sesame oil, cornstarch, and egg white, Pour half into the bowl with the meat, the other half into the bowl with the fish. Mix each separately with a pair of chopsticks until it is sticky then make one-inch balls of the minced/ground beef, and do the same for the minced fish. Set them aside.<BR>2. Bring the broth to the boil, reduce the heat, add the ginger slivers and the beef balls. When they float to the top, add the fish balls, and when they float, add the scallions, and pour the soup into a pre-heated soup tureen and bring it to the table or put some into each of the soup bowls, one per person, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EC6BDD392421A24EAAFC8FE6873E7024', X'22AF5AF0CDA29341B552DD66F47B3936', X'22AF5AF0CDA29341B552DD66F47B3936', 0, 'javascript:', 'Hakka Vegetarian Soup', 2, NULL, 0, 'Hakka Vegetarian Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1688', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Asian eggplant, steamed for ten minutes, peel discarded, and mashed<BR>1 tablespoon oil<BR>1/2 green pepper, seeded and slivered, optional<BR>1 small hot pepper, seeded and slivered<BR>4 Chinese dried mushrooms, soaked with their stems for two hours, then both part minced<BR>1/2 pound firm doufu, minced<BR>2 cloves garlic, peeled and minced<BR>1/2 teaspoon coarse salt<BR>1/4 teaspoon ground white pepper<BR>1 scallion, slivered<BR>1 Tablespoon slated black beans, mashed well<BR>1 Tablespoon minced fresh ginger<BR>1 Tablespoon thin soy sauce<BR>1 quart vegetable stock<BR>1 tablespoon connstarch mixed with the same amount of cold water<BR><B><I>Preparation:</B></I><BR>1. Mix eggplant and the oil and stir-fry one minute, then add the green pepper, if using it, and the hot pepper and the mushrooms. Stir-fry another minute.<BR>2. Add the doufu and all the other ingredients and bring to the boil. reduce the heat and simmer for five minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'34638A8553CAFD4CBA5E2DE01FABB691', X'FE6340D99768734BBB93697BAF982F1A', X'FE6340D99768734BBB93697BAF982F1A', 0, 'javascript:', 'Herbal Soup', 2, NULL, 0, 'Herbal Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2256', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 inched daikon, peeled and cut into one-half-inch cubes<BR>20 Chinese <I>goji</I> berries<BR>15 roots of American ginseng, cut in small pieces<BR>15 red dates, each cut in half, pits removed and discarded<BR>2 Tablespoons vegetable bouillon powder<BR><B><I>Preparation:</B></I><BR>1. Peel the daikon and then cut into one-half-inch cubes.<BR>2. Put these cubes and all other ingredients in a two-quart pot. Bring it to the boil, reduce the heat and simmer for twenty minutes. Then add salt and pepper to taste, if desired, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D2EE0ADF5B45ED408B620E14A2355A32', X'27E6E7EB3647224CA87390DDA9C9BDCB', X'27E6E7EB3647224CA87390DDA9C9BDCB', 0, 'javascript:', 'Herbal Sweet Banquet Soup', 2, NULL, 0, 'Herbal Sweet Banquet Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=440', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound fresh lily bulbs, washed well<BR>1 large white tremella fungus<BR>3 Tablespoons wolfberries (<I>lycium chinensis</I><BR>) 1 ripe banana<BR>1 almost ripe banana, peeled and sliced into one-third-inch pieces<BR>1/2 Chinese rock sugar bar, cut into one-quarter-inch pieces<BR><B><I>Preparation:</B></I><BR>1. Separate lily bulb into individual sections, rewash and dry on paper towels. 2. Tear the tremella fungus into one-quarter-inch pieces.<BR>3. Put all ingredients into a glass or ceramic bowl and steam for half an hour. Serve hot or warm.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4CC332F0A5CE7545A713D20206E4BDC3', X'9D213BC00C606D439CD563736918E46E', X'9D213BC00C606D439CD563736918E46E', 0, 'javascript:', 'Honey Soup Elixir', 2, NULL, 0, 'Honey Soup Elixir', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=762', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons sweet almonds, paper shells removed<BR>2 Tablespoons bitter almonds<BR>2 pounds watercress, washed and dried with paper towels or in a spin dryer<BR>2 Tablespoons wolfberries<BR>1/2 cup honey<BR><B><I>Preparation:</B></I><BR>1. Put almonds and watercress in a large pot. Add two quarts of water, bring to the boil, then reduce heat and simmer for one hour.<BR>2. Strain and set aside for one hour.<BR>3. Reheat and add wolfberries and simmer for fifteen minutes, then add honey and cook another two or three minutes. Can be served warm or cool.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'728DB97B150FD94586DEC80F45E218C6', X'567EBBFAB6CAC648986773ABD835C283', X'567EBBFAB6CAC648986773ABD835C283', 0, 'javascript:', 'Honeyed Congee', 2, NULL, 0, 'Honeyed Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=763', '<TD><RECIPE><B></I>Ingredients:</B></I><BR>1/2 cup black or red rice, soaked for two hours, then drained<BR>1/2 cup white glutinous rice, soaked for two hours, then drained<BR>1/2 cup chopped peanuts<BR>1/2 cup dried pit-free longans<BR>1/2 cup rice wine<BR>1/2 cup honey<BR>1 teaspoon oil<BR><B><I>Preparation:</B></I><BR>1. Mix all ingredients and put in a large heat-proof bowl, then add eight cups of water.<BR>2. Steam over boiling water for two hours, remove from steamer and cool slightly. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1AFF3A7C1D6226459985FBAF2761E474', X'30A34D2A4EB1EC49B4E216BFAC862CCC', X'30A34D2A4EB1EC49B4E216BFAC862CCC', 0, 'javascript:', 'Hot and Sour Chicken Soup', 2, NULL, 0, 'Hot and Sour Chicken Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1369', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 boneless chicken thigh<BR>3 chicken wings, bones removes, each wing turned inside out<BR>3 Tablespoons cloud ear fungus, soaked for twenty minutes in warm water<BR>1 Tablespoon vegetable oil<BR>1 small chili pepper<BR>3 slices peeled fresh ginger<BR>1 scallion, cut into one-half inch pieces<BR>1 Tablespoon Shao Xing wine<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon mushroom soy sauce<BR>1 Tablespoon Chinese black vinegar<BR>2 Tablespoon cornstarch mixed with one tablespoon cold water<BR>6 cups chicken broth<BR><B><I>Preparation:</B></I><BR>1. Cut chicken thighs and inside out wings into one-inch pieces.<BR>2. Remove any debris or thick parts from the fungi, and cut them into one-inch pieces.<BR>3. Heat oil and fry the chili pepper and the ginger for two minutes, then discard them and put the chicken pieces into the oil, and stir-fry for two minutes before adding the scallion pieces, wine, sioy sauce, and vinegar. Stir well, the add cornstarch mixture and the broth and bring to the boil stirring all the time, then put into a soup tureen or ladle into individual soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C651F68E00F5AB4DB27E1477C7DD7ACF', X'CFB532FD60ABE94B90E594D5EBB9062B', X'CFB532FD60ABE94B90E594D5EBB9062B', 0, 'javascript:', 'Hot and Sour Soup', 2, NULL, 0, 'Hot and Sour Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=556', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup shredded preserved Sichuan vegetable<BR>2 dried Chinese black mushrooms, soaked for twenty minutes in warm water<BR>2 Tablespooons cider vinegar<BR>1 teaspoon soy sauce<BR>2 Tablespoons cornstarch<BR>2 pieces of soft doufu, sliced thin<BR>2 thirteen ounce cans of chicken broth<BR>1 cup shredded roast pork or fresh pork<BR>1 cup shredded bamboo shoots<BR><B><I>Preparation:</B></I><BR>1. Wash the red pepper powder off from the preserved vegetable, and shred it.<BR>2. Remove the stems from the mushrooms and shred them.<BR>3. Mix vinegar, soy sauce and cornstarch in a small bowl.<BR>4. Simmer chicken broth and the doufu for five minutes.<BR>5. Add the Sichuan vegetable, pork, bamboo shoots, and the mushrooms and boil these one minute, then add the vinegar mixture. Stir well and serve.<BR><B>Note:</B> The best tasting preserved vegetable is kolhrabi; it comes in chunk form in cans and is sometimes misnamed ''preserved turnip.''</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B01AF4488054584C8FCD626E78B52876', X'F19A3B5FAB1F6A458C93394049001477', X'F19A3B5FAB1F6A458C93394049001477', 0, 'javascript:', 'Hot and Sour Soup with Bai Shao', 2, NULL, 0, 'Hot and Sour Soup with Bai Shao', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1841', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 Tablespoons <I>bai shao</I><BR>1/4 cup pork, slivered<BR>1 Tablespoon dark soy sauce<BR>1 pound firm bean curd, cut into half-inch cubes<BR>1/4 cup wood ear fungi, soaked for fifteen minutes, then drained and finely sliced<BR>1 sweet red pepper, seeded and slivered<BR>1 fresh hot chili pepper or 1 Tbsp Chinese hot chili oil<BR>2 Tablespoons cornstarch mixed with one tablespoon cold water<BR>2 slices fresh ginger, slivered<BR>1/2 teaspoon hot sesame oil<BR>2 Tablespoons Chinese black vinegar<BR>1 large egg, beaten well until almost stiff<BR>1 scallion, cut into one-quarter-inch pieces<BR><B><I>Preparation:</B></I><BR>1. Boil <I>bai shao</I> with two cups water until liquid reduced to one cup. Discard all solids; reserve liquid.<BR>2. Mix pork and soy sauce and marinate fifteen minutes.<BR>3. Put the pork and its marinade into a large soup pot with the minced ginger, scallion, and garlic and ten cups of water. Reduce the heat and add the wood ear fungi and simmer for three to four minutes, then add the cornstarch, ginger and fresh sweet and dried or fresh hot pepper slices and simmer two minutes before adding the sesame oil, black vinegar, and the liquid from the <I>bai shao</I>. Mix well and bring to the boil, then turn off the heat source.<BR>4. Add egg and scallion, stir well for about half a minute. Serve in pre-heated soup tureen or soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'27F6265686CD154B9947023525FD97CE', X'9B05E926EAE88B41B968F1A1B1CC5EBA', X'9B05E926EAE88B41B968F1A1B1CC5EBA', 0, 'javascript:', 'Hot and Sour Soup with Sea Vegetables', 2, NULL, 0, 'Hot and Sour Soup with Sea Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2316', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>4 cups chicken broth or stock<BR>3 large Shiitake mushrooms, stems discarded, tops soaked and sliced<BR>1/4 cup Chinese rice vinegar<BR>2 Tablespoons cornstarch mixed with two tablespoons cold water<BR>2 Tablespoons sesame oil<BR>2 Tablespoons fresh minced peeled ginger<BR>1/2 pound firm doufu, diced<BR>1 ounce bean thread noodles, soaked and broken into two-inch pieces, then drained<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon granulated sugar<BR>1 teaspoon dried crushed sea vegetables<BR><B><I>Preparation:</B></I><BR>1. Heat broth or stock, mushrooms, vinegar, cornstarch mixture, sesame oil, ginger, dooufu, and the drained bean thread noodes, and simmer for five minutes.<BR>2. Add soy sauce, sugar, and the ground sea vegetables, mix well, and simmer three minutes more. Then pour into a pre-heated large soup bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'842D00EAA87E694F977F0854FC753947', X'7D152129E8F7A041BA8B79F939CB6950', X'7D152129E8F7A041BA8B79F939CB6950', 0, 'javascript:', 'Hot and Sour Soup, Beijing Style', 2, NULL, 0, 'Hot and Sour Soup, Beijing Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1645', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup finely chopped cooked chicken<BR>1 cup sliced small mushrooms<BR>1 cup bean sprouts<BR>4 Tablespoons soy sauce<BR>5 cups chicken broth<BR>2 cups chopped tofu<BR>3/4 teaspoon chilli flakes<BR>2 Tablespoons vinegar<BR>2 Tablespoons cornstarch mixed with four tablespoons of cold water<BR>1 egg, beaten<BR>2 Tablespoons sesame oil<BR>2 Tablespoons chopped coriander<BR>4 Tablespoons finely chopped green onions<BR><B><I>Preparation:</B></I><BR>1. Place chicken, mushrooms, bean sprouts, soy sauce and chicken broth in a saucepan then bring to boil. Cover and cook over medium/low heat for five minutes.<BR>2. Stir in remaining ingredients, except coriander and green onions and bring to boil then cook over medium/low heat for about three minutes or until the soup begins to thicken.<BR>3. Place in a serving bowl then garnish with coriander and green onions and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'69C8ED7E4155584BA439B08558D47601', X'5BB57BD83CC54D43937EE1171E5C7044', X'5BB57BD83CC54D43937EE1171E5C7044', 0, 'javascript:', 'Hot and Sour Soup, Harbin Style', 2, NULL, 0, 'Hot and Sour Soup, Harbin Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1146', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups chicken broth<BR>10 bundles konjac<BR>3 Chinese black mushrooms, soaked, stems discarded, then sliced<BR>1/4 cup crab meat (or shrimp), coarsely chopped<BR>3 Tablespoons Yunnan or another Chinese ham, sliced<BR>1/2 firm bean curd, cut thickness in half, then sliced<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon black vinegar<BR>1 teaspoon chili paste<BR>1/2 teaspoon sugar<BR>1/4 teaspoon sea or kosher salt<BR>1 Tablespoon cornstarch mixed with two tablespoons cold water<BR>dash of ground white pepper<BR>1 egg white<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Bring Chicken broth to a boil, then add konjac, black mushroom slices, crab meat, ham, bean curd, and the soy sauce. Reduce heat to simmer and cook for three minutes.<BR>2. Add vinegar, chili paste, sugar, salt, cornstarch mixture, and the pepper, and return to the boil, stirring all the time.<BR>3. Turn off the heat, add the egg white and the sesame oil, stir well, and serve in a pre-heated soup tureen or individually in pre-heated soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'43E48BBAA242AB46ACF32F7AE62DD0FD', X'0A7E97642DCEC347821C3B6FDF7583B7', X'0A7E97642DCEC347821C3B6FDF7583B7', 0, 'javascript:', 'Hot and Sour Soup, the Ancient Way', 2, NULL, 0, 'Hot and Sour Soup, the Ancient Way', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=795', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/8 pound lean pork, slivered<BR>1/2 firm bean curd slivered<BR>3 dried black mushrooms, soaked, stems removed, and slivered<BR>2 cups chicken broth<BR>1 Tablespoon Chinese rice wine<BR>4 Tablespoons coagulated chicken or pork blood, slivered<BR>1/2 teaspoon ground white pepper<BR>2 Tablespoons Chinese black vinegar<BR>2 teaspoons thin soy sauce<BR>2 teaspoons cornstarch mixed with one tablespoon cold water<BR>1/2 egg white<BR><B><I>Preparation:</B></I><BR>1. Mix pork, bean curd, and mushrooms and put into a large pot, then add chicken broth and four cups of water and heat. Allow this to simmer for fifteen minutes.<BR>2. Add all the rest of the ingredients and simmer for five minutes, 3. Beat egg white and stir in and keep stirring, albeit very gently, for one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BFA0F5A447B84C4584E17CB2C5200176', X'CE92A49EB7936B41BF4E089385CCF25C', X'CE92A49EB7936B41BF4E089385CCF25C', 0, 'javascript:', 'Hot Pot Mongolian Style', 2, NULL, 0, 'Hot Pot Mongolian Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=314', '<TD><RECIPE><I><B>Ingredients suggested for six to eight people:</I></B><BR>1 to 2 pounds leg of lamb, boned and very thinly sliced<BR>1 pound celery cabbage<BR>18 small shiitake mushrooms, soaked and stemmed<BR>8 or more knotted scallions<BR>4 or more cups lamb stock<BR>4 ounces mung beans<BR><B><I>Suggested sauces:</B></I><BR>Sesame paste, rice wine, mashed fermented bean cake, chili oil, soy sauce, minced coriander leaves, Chinese chives, black vinegar, minced fresh ginger, sesame oil</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BB2A0399A422924B9BB8F79AF721F84C', X'7D60456A8ECD9742A731657C363896F3', X'7D60456A8ECD9742A731657C363896F3', 0, 'javascript:', 'Hot-pot, Northern Style', 2, NULL, 0, 'Hot-pot, Northern Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=874', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound sirloin steak cut into thin one by three-inch slices<BR>1 Tablespoon thin soy sauce<BR>3 Tablespoons Chinese rice wine<BR>1 teaspoon and one tablespoon sesame oil, used separately<BR>1 Chinese cabbage, cut in two-inch widths<BR>3 cloves garlic, peeled and cut into three or four slices each<BR>3 quarts chicken stock, more if needed to add to the hotpot later<BR>1 or 2 ounces of bean thread noodles, soaked for ten minutes, then drained, and using a scissor, cut into four-inch-length sections, then put into a serving bowl<BR>20 Chinese black mushrooms, soaked, stems removed, then boiled for five minutes, drained, and mushrooms put in a separate serving bowl, mushroom water put into a hotpot or a large pot that can come to the table<BR>1 pound Chinese spinach, stems removed, put on a serving plate<BR>1 pound baby carrots or large ones cut that size, boiled for two minutes, drained and put into a serving bowl<BR>10 raw eggs in their shells<BR>Four small bowls set on a tray, one each for sauces such as soy sauce, chili paste, hoisin sauce, Chinese black vinegar etc.<BR><B><I>Preparation:</B></I><BR>1. Mix beef, soy sauce, half the rice wine, and one teaspoon of the sesame oil, and set this into a large shallow sering bowl or a deep platter.<BR>2. Heat wok, add the one tablespoon of sesame oil, and stir-fry the Chinese cabbage, the garlic, and the remaining rice wine for one minute, remove and put in a hot pot or a large pot that can come to the table, or a deep electric frypan.<BR>3. Set the hot-pot, large pot on a heater, or the electric frypan in the center of the table on top of a large metal tray. Put the platter of marinated meat and the bowls of of bean threads, Chinese mushrooms, spinach, and carrots on the table around the tray with the hot pot, not on it.<BR>4. Pour the chicken stock into the hot pot and add heat it to just under the boiling point, reduce the heat to simmering. Add about one quarter of the Chinese cabbage mixture to the stock and put the rest into a serving bowl.<BR>5. Give each person at the table an empty rice bowl and let them make their own sauce from the four different sauces passed around. Also give them a raw egg to use later.<BR>6. Each person at the table put one or two slices of meat, or a like amount of mushrooms or cabbage their own individual strainer basket and put that wire basket into the almost boiling broth to cook until their own desired doneness. They then remove it and using chopsticks dip the hot meat or vegetables into their own sauce mixture and eat these foods. Then they take others and cook them the same way.<BR>7. Partway through the cooking, they can add spinach, noodles and cabbage directly into the hot broth and take these out with their chopsticks or the wire mesh basket putting them into their own sauce bowl.<BR>8. If the broth seems too reduced, add more, as needed.<BR>9. At the end, raise the heat so that the soup boils, ladle some of it into peoples almost empty soup/sauce bowl. Immediately break one egg into each bowl, stir well, and then people eat this broth mixture as a soup.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AA0E92EE746AB345803A8C4B82035B64', X'3CD3044404BF4D4BB27C5BEF9660EA38', X'3CD3044404BF4D4BB27C5BEF9660EA38', 0, 'javascript:', 'Imitation Shark''s Fin Soup', 2, NULL, 0, 'Imitation Shark''s Fin Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=955', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 ounce dry mung bean noodles<BR>2 Tablespoons imitation meat, cut into slivers<BR>1 teaspoon vegetable oil<BR>1 whole egg, beaten<BR>2 Chinese black mushrooms, soaked, stems removed, and slivered<BR>2 Tablespoons slivered cloud ear mushrooms<BR>4 cups superior chicken stock<BR>2 Tablespoons water chestnut flour mixed with two tablespoons of water<BR>1 Tablespoon imitation oyster sauce<BR><B><I>Preparation:</B></I><BR>1. Soak bean thread noodles in warm water for twenty minutes. Drain, and cut them into two inch lengths, and mix with the slivers of imitation meat.<BR>2. Heat oil, in a fry pan, add egg, and spread it all over the pan. Cook until just set, remove, and cut into thin two inch long slivers.<BR>3. Heat chicken stock, add mushroom and cloud ear slivers and simmer for two minutes, then add imitation meat and bean thread noodles. Stir in the water chestnut flour and the imitation oyster sauce. Bring to the boil, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BE97D66889D43343B82790CC86160BB1', X'26190C5C434FCF4787D957A745B481B8', X'26190C5C434FCF4787D957A745B481B8', 0, 'javascript:', 'Jott Jook', 2, NULL, 0, 'Jott Jook', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=395', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 teaspoons minced dates<BR>1 Tablespoon honey<BR>1/2 cup pitted sliced dates<BR>1/2 cup honey<BR>2 cups short grain rice<BR>1 cup pine nuts<BR><B><I>Preparation:</B></I><BR>1. Soak the minced dates in some of the honey for one hour and the sliced dates in another bowl in a larger amount of the honey for two or three hours.<BR>2. Grind rice, with two cups of water, in a blender until smooth. Remove and add the sliced dates.<BR>3. Put rice mixture in a large pot and add six additional cups of water. Bring this to the boil and simmer for three-quarters of an hour, stirring periodically. If it gets too thick, add more water, a quarter of a cup at a time.<BR>4. Toast the pine nuts in a dry fry pan until lightly colored. Cool, then grind them. This is best done in a mortar and pestle and not in a blender.<BR>5. Add the ground nuts and simmer for five minutes. Serve in individual bowls, topping each with a little of the minced dates and honey.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0714C1E63AE9594093F90B99EE644943', X'3F43FEE1C1750C44B84B50173B26A4C3', X'3F43FEE1C1750C44B84B50173B26A4C3', 0, 'javascript:', 'Kazak Noodle Soup', 2, NULL, 0, 'Kazak Noodle Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=466', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound lamb, cut into match-stick strips<BR>6 cups lamb or chicken broth<BR>2 cups thin noodles<BR>1/8 teaspoon ground white pepper<BR>1/4 teaspoon salt<BR>1/2 cup diced onions<BR>1/2 teaspoon cumin seeds<BR><B><I>Preparation:</B></I><BR>1. Blanch lamb strips into boiling water, rinse and put into a pot with the broth and bring to the boil. Reduce heat and simmer for twenty minutes.<BR>2.Add noodles, pepper and salt, and the onions, and simmer five more minutes or until noodles are done, then serve in individual bowls and sprinkle some of the cumin seeds on each of them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9F1AA40561F5A6419A55BEC060462C67', X'324DD93BE8BA184191A8167840BCF379', X'324DD93BE8BA184191A8167840BCF379', 0, 'javascript:', 'Lamb Soup', 2, NULL, 0, 'Lamb Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2359', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>3 pounds lamb bones and meat scraps<BR>10 longan, each cut in half, their pits discarded<BR>10 Chinese red dates, each cut in half, their pits discarded<BR>4 slices fresh ginger, peeled and smashed<BR>salt and pepper, to taste<BR>1 Tablespoon goji berries<BR>3 scallions, each tied in a knot<BR>½ to 1 teaspoon siracha sauce<BR><B><I>Preparation:</B></I><BR>1. Rinse lamb in boiling water, discarding the water.<BR>2. Simmer lamb in three quarts of boiling water for three hours, then remove and discard the lamb bones, and before one does, cutting off the meat in small pieces and putting them back into the lamb stock.<BR>3. Next, add the longan, dates, ginger, salt and pepper, and simmer ten minutes before adding the goji berries and scallions; then simmer ten more minutes. Then discard the scallion knots, and add the Siracha sauce and stir for two or three minutes,. Then serve the soup in individual bowls.<BR>Cumin lamb <I><B>Ingredients:</B></I><BR>1 Tablespoon Sichuan peppercorns<BR>1 teaspoon whole black peppercorns<BR>2 Tablespoons cumin seeds<BR>1 teaspoon coarse salt<BR>1 whole dried broken up chili pepper, seeds discarded<BR>1 Tablespoon vegetable oil<BR>1 medium onion, peeled and coarsely chopped<BR>1 pound boneless lamb, half sliced, half coarsely chopped<BR>3 cloves fresh garlic, peeled and coarsely chopped<BR>3 Tablespoons dark and thin/light soy sauce<BR>3 Tablespoons Chinese rice wine<BR>3 scallions, coarsely chopped<BR>10 sprigs fresh cilantro, rinsed, drained, then coarsely chopped<BR><I><B>Preparation:</B></I><BR>1. He a wok or fry pan, add the peppercorns, peppers, cumin seeds, salt, and chili pepper pieces and dry-fry them for one to two minutes, do not burn them.<BR>2. Toss the meat with these seasonings, and set it aside.<BR>3. Heat the wok or fry pan, add the oil, then the onions and stir-fry for two to three minutes, then remove them to a bowl or plate.<BR>4. Now add the chopped lamb and stir-fry for two minutes, then the sliced lamb, garlic, soy sauces, rice wine, and the scallions, and simmer for one minute then return the onions to the wok or fry pan, fold in the cilantro, and serve.<BR>Lamb chops and black pepper sauce <I><B>Ingredients:</B></I><BR>½ pound or 6 thinly sliced lamb chops<BR>1 scallion, sliced thinly at an angle<BR>2 cloves fresh garlic, peeled and smashed<BR>1 red chili pepper, seeded and slivered<BR>1 teaspoon granulated sugar<BR>1 Tablespoon dark soy sauce<BR>1 teaspoon cornstarch<BR>2 Tablespoons chicken broth<BR>2 Tablespoons Shao Xing wine<BR>dash ground white pepper<BR>1 Tablespoon sesame oil<BR>! Tablespoon vegetable oil<BR><I><B>Preparation:</B></I><BR>1. Mix lamb chops with the scallion pieces, smashed garlic, chili pepper pieces, sugar soy sauce, cornstarch, broth, wine, pepper, and sesame oil and set this aside for half an hour.<BR>2. Heat a wok or fry pan, add the vegetable oil, and after the meat has rested and fully drained on a wire surface for about five minutes, fry them in the hot oil two minutes per side, then add the drained liquid, and boil them one minute more per side, then serve.<BR>Lamb and scallions <I><B>Ingredients:</B></I><BR>3/4 pound boneless lamb loin, thinly sliced, each slice cut into one-inch cubes<BR>3 scallions, cut into two inch pieces<BR>1 egg white<BR>2 Tablespoons dark soy sauce<BR>2 Tablespoons hoisin sauce<BR>2 Tablespoons cornstarch<BR>2 Tablespoons Chinese rice wine<BR>2 Tablespoons vegetable oil<BR>2 large cloves fresh garlic, slivered<BR>3 slices fresh ginger, slivered<BR>2 Tablespoons sesame oil<BR>½ cup coarsely chopped coriander<BR>3 Tablespoons chicken broth<BR><I><B>Preparation:</B></I><BR>1. Mix lamb slices with scallions, soy and hoisin sauces, cornstarch and rice wine. Let this rest half an hour, then stir it once or twice.<BR>2. Next, heat wok or fry pan, add oil then the garlic and ginger and in one minute, after mixing the meat and the egg white, add the lamb mixed with the soy sauce and corn');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DE6AF34FA960BE4B8090CBC6F90FDC0E', X'A3B02E449B9EC249B7DE5AA11DF1DF12', X'A3B02E449B9EC249B7DE5AA11DF1DF12', 0, 'javascript:', 'Lamb Soup', 2, NULL, 0, 'Lamb Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2360', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>3 pounds lamb bones and meat scraps<BR>10 longan, each cut in half, their pits discarded<BR>10 Chinese red dates, each cut in half, their pits discarded<BR>4 slices fresh ginger, peeled and smashed<BR>salt and pepper, to taste<BR>1 Tablespoon goji berries<BR>3 scallions, each tied in a knot<BR>½ to 1 teaspoon siracha sauce<BR><B><I>Preparation:</B></I><BR>1. Rinse lamb in boiling water, discarding the water.<BR>2. Simmer lamb in three quarts of boiling water for three hours, then remove and discard the lamb bones, and before one does, cutting off the meat in small pieces and putting them back into the lamb stock.<BR>3. Next, add the longan, dates, ginger, salt and pepper, and simmer ten minutes before adding the goji berries and scallions; then simmer ten more minutes. Then discard the scallion knots, and add the Siracha sauce and stir for two or three minutes,. Then serve the soup in individual bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FB9FEDD011992A40BB448E116749700F', X'8C2FC03331334D4295739E056E54F100', X'8C2FC03331334D4295739E056E54F100', 0, 'javascript:', 'Lily Bulb and Mung Bean Congee', 2, NULL, 0, 'Lily Bulb and Mung Bean Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=563', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound mung beans<BR>1/2 cup glutinous rice<BR>2 fresh lily bulbs, separated into their individual sections<BR>2 slices (which is almost a half a pound) of slab sugar, crushed with handle of the cleaver<BR>a dash of salt<BR><B><I>Preparation:</B></I> 1. Rinse mung beans discarding any broken ones and soak overnight; and soak the rice separately, also overnight.<BR>2. Bring two quarts of cold water to the boil and add the rice and simmer for one hour. Then add the beans and cook for another twenty to thirty minutes until almost all the beans have split open.<BR>3. Add lily buds and the sugar and simmer another five minutes until all the sugar is dissolved, then add the salt, stir, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B2F4F28D0C2A4348A8CD5A918AF3A396', X'AB0A37BF80478F47987C3EF0B4BEC1DE', X'AB0A37BF80478F47987C3EF0B4BEC1DE', 0, 'javascript:', 'Lily Bulb Congee', 2, NULL, 0, 'Lily Bulb Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=437', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup glutinous rice<BR>1/4 cup lily bulb pieces, setting five aside<BR>3 Tablespoons sugar<BR>1/4 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Bring six cups water to the boil, then add the rice and all but the five lily bulb pieces, reduce the heat and continue to stir for one minute. Simmer for forty minutes.<BR>2. While this is simmering, bring a cup of water to the boil and add the five pieces and simmer them for twenty minutes, then turn off the heat.<BR>3. Then add salt and reserved lily bulb pieces to the rice mixture and simmer another five minutes.<BR>2. Add sugar, stir until dissolved, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'539AF324A116C741992C47EDFE8B566B', X'214138DF12B36C43AF2AB6C3419D3F76', X'214138DF12B36C43AF2AB6C3419D3F76', 0, 'javascript:', 'Lingzi and Pork Shank Soup', 2, NULL, 0, 'Lingzi and Pork Shank Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=823', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 small dried lingzi mushroom<BR>1/2 pound shank or pork<BR>10 Chinese brown or black dates<BR>1 piece tangerine peel<BR><B><I>Preparation:</B></I><BR>1. Soak mushroom for half an hour, remove any stem, and slice.<BR>2. Blanch mushroom slices twice in boiling water for half minute each time.<BR>3. Blanch pork shank, then simmer it with three quarts of water for one hour. Then strain liquid returning it to the pot.<BR>4. Put all ingredients in a pot, add more boiling water so that altogether there are four quarts of liquid. Cover, reduce heat to simmer, and cook for two more hours. Then remove the meat, and discard all visible fat along with any bones. Tear the meat into small pieces, and put it back into the soup liquid. Discard the tangerine peel, reheat, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FC54902CB51DEC468128443A74D15E86', X'81E9FB8FB9F42547BD409FDE2442560A', X'81E9FB8FB9F42547BD409FDE2442560A', 0, 'javascript:', 'Lobster and Corn Soup', 2, NULL, 0, 'Lobster and Corn Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2163', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 large slices fresh ginger, peeled<BR>3 Tablespoons vegetable oil<BR>½ cup canned or fresh corn kernels, if fresh, boiled for two minutes<BR>1 lobster tail or four claws, cut into small pieces<BR>1 teaspoon Chinese rice wine<BR>1 egg white<BR>1 Tablespoon cornstarch<BR>1 scallion, green part, minced<BR><B><I>Preparation:</B></I><BR>1. Boil ginger in two tablespoons water for one minute, then add the lobster pieces and stir for one minute, then remove them and set them aside.<BR>2. Add corn, salt, sherry, and the egg white mixed with the cornstarch. Stir until it thickens, then return the lobster to the pan and add four cups of boiling water, stir, and then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6AFCA87418F788438273D8458E3681A9', X'2F73B593C2F99844826366AA6B9D74ED', X'2F73B593C2F99844826366AA6B9D74ED', 0, 'javascript:', 'Lobster and Corn Soup', 2, NULL, 0, 'Lobster and Corn Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2268', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 large slices fresh ginger, peeled<BR>3 Tablespoons vegetable oil<BR>½ cup canned or fresh corn kernels, if fresh, boil them for two minutes<BR>1 lobster tail or 4 claws, cut into small pieces<BR>1 teaspoon Chinese rice wine<BR>1 egg white<BR>1 Tablespoon cornstarch<BR>1 scallion, green part, minced<BR><B><I>Preparation:</B></I><BR>1. Boil ginger in two tablespoons water for one minute, then add lobster pieces and stir for one minute, and then remove them and set them aside.<BR>2. Add corn, salt, sherry, and the egg white mixed with the cornstarch, and stir until it thickens, then return the lobster to the pot and add four cups of boiling water, stir, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'73D3F39DFED58C4691945C750E8C7386', X'65CB3C567A15FB47A14F34500FD0AA95', X'65CB3C567A15FB47A14F34500FD0AA95', 0, 'javascript:', 'Lobster and Corn Soup', 2, NULL, 0, 'Lobster and Corn Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2168', '<TD><RECIPE><B><I>Ingredients</I></B><BR>4 large slices fresh ginger, peeled<BR>3 Tablespoons vegetable oil<BR>1/2 cup canned corn kernels<BR>1 lobster tail or four claws cut into small pieces<BR>1 teaspoon Chinese rice wine<BR>1 egg white<BR>1 Tablespoon cornstarch<BR>1 scallion, green part only, minced<BR><B><I>Preparation:</B></I><BR>1. Boil ginger in two tablespoons water for one minute, then add the lobster pieces, stir for one minute, then remove them and set aside.<BR>2. Add corn, salt, sherry, and the egg white mixed with the cornstarch and stir until it thickens. Then return the lobster meat to the pan, add four cups boiling water, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E516F7DC4BAB9141820BBBBDCA78D6EA', X'D4500EBFF600094D8B279C92AAFC3782', X'D4500EBFF600094D8B279C92AAFC3782', 0, 'javascript:', 'Lobster, Chiu Chow Style', 2, NULL, 0, 'Lobster, Chiu Chow Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2166', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 lobster steamed for ten minutes, meat removed from their shells, the tail cut into two-inch pieces<BR>1 Tablespoon vegetable oil<BR>6 large slices of fresh ginger, peeled<BR>3 scallions, white part only, sliced on an angle<BR>1 teaspoon sugar<BR>½ teaspoon coarse salt<BR>1 teaspoon sesame oil<BR>1/2 cup chicken broth<BR>1 Tablespoon cornstarch<BR>6 ounces rice noodles, cooked until soft, then drained and tossed with one tablespoon sesame oil<BR>6 bowls hot chicken broth or stock<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil, and fry the lobster pieces for no more than one minute, then remove them from the pan and set them aside.<BR>2. Put oil in a clay pot and add the ginger and scallions, salt, and scallion pieces and stir once or twice before adding the salt and the sesame oil.<BR>3. Mix chicken broth and the half cup cornstarch, stir in the pieces of lobster and the cooked noodles. Give every one a bowl of hot broth, and a long-handled fork, help them to take pieces of lobster from the clay pot to eat with their broth.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DA9A4B7561DC0844AC71C9A8B6240A59', X'8040C4FC0BC3664F9F9FDC516420A5E4', X'8040C4FC0BC3664F9F9FDC516420A5E4', 0, 'javascript:', 'Longevity Soup', 2, NULL, 0, 'Longevity Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=357', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 duck, skinned and cut into eight pieces<BR>1 chicken, skinned and cut into eight pieces<BR>5 to 10 <I>dong chong xia cao</I> or <I>Cordyseps sinensis</I><BR>2 Tablespoons mushroom soy sauce<BR>15 cups water or chicken stock<BR>1/2 to 1 teaspoon ground white pepper<BR>1/4 teaspoon coarse salt<BR>1 pound pea shoots or spinach, rinsed well<BR><B><I>Preparation:</B></I><BR>1. Put duck and chicken pieces, dong chong xia cao, mushroom soy, and chicken stock in a large pot and heat until just before it boils, add the pepper and reduce the heat to low and simmer covered for two hours.<BR>2. Remove all solids from the stock, discard bones, tear the meat into small pieces, then return them to the pot.<BR>3. Add pepper and salt and heat stock until it almost boils, then add the greens and remove the pot from the heat. Stir well, and serve ladling meat, greens and stock into each individual pre-heated serving bowl.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'46A1F87F5ED35441A1EF2679D4000B4F', X'5580EFB2FBA58248A45ECEF74DCD56E1', X'5580EFB2FBA58248A45ECEF74DCD56E1', 0, 'javascript:', 'Lotus and Dried Fig Soup', 2, NULL, 0, 'Lotus and Dried Fig Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1773', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons lotus seeds<BR>2 Tablespoons lily bulbs<BR>2 Tablespoons <I>goji</I> berries<BR>5 dry longan fruits, without the pits<BR>4 dried figs<BR>1 piece dried tangerine peel<BR>2 Tablespoons chopped brown rock sugar<BR><B><I>Preparation:</B></I><BR>1. Rinse lotus seeds, lily bulbs, <I>goji</I> berries, longans, and tangerine peel before soaking them in one quart of water for half an hour. Next remove the tangerine peel, cut the figs into small pieces, each about the size of a raison, and do the same for the lily bulb pieces. Also dice the tangerine peel into smaller pieces than the figs, scraping off any pith before doing so.<BR>2. Bring six cups of cold water to the boil, add the lotus seeds, lily bulb pieces, goji berries, longan and dried fig, and tangerine peel pieces. Bring this to the boil, reduce the heat to low, and simmer for one and a half hours. 3. Next add the rock sugar pieces and simmer until the sugar is dissolved. Now divide the fruit evenly and serve in small bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B32AC170B0C46144951E7B9B5E854ACD', X'DBB2A0A8978DCD4CB228E6848E24C3C6', X'DBB2A0A8978DCD4CB228E6848E24C3C6', 0, 'javascript:', 'Lotus Leaf Congee', 2, NULL, 0, 'Lotus Leaf Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=972', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups glutinous rice, soaked overnight, then drained<BR>5 young lotus leaves<BR>25 canned or lotus seeds simmered for half an hour<BR>1/4 cup sugar<BR><B><I>Preparation:</B></I><BR>1. Put rice, lotus leaves, and lotus seeds in four quarts of water. Bring to the boil, reduce heat, then simmer uncovered for one hour.<BR>2. Remove the leaves and simmer for another half an hour before adding the sugar. Stir well, and serve hot or warm.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2EA4E01974E0784B84D53F7A66EF1125', X'967DAE5FA5E4CA4DBDD042D9B58E46D6', X'967DAE5FA5E4CA4DBDD042D9B58E46D6', 0, 'javascript:', 'Lotus Root Soup with Osmanthus', 2, NULL, 0, 'Lotus Root Soup with Osmanthus', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=461', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 section lotus root<BR>1 piece silver tree-fungus<BR>20 wolfberry seeds<BR>1 Tablespoon osmanthus syrup (or cassia jam)<BR>1 small piece Chinese white rock sugar (about one tablespoon)<BR>2 Tablespoons rice wine<BR><B><I>Preparation:</B></I><BR>1. Peel and slice lotus root into very thin slices.<BR>2. Soak silver tree-fungus in warm water for one hour. Cut away and hard edges or centers, and tear it into about eight pieces.<BR>3. Blanch wolfberry seeds.<BR>4. Bring six cups water to boil, then add all the ingredients and simmer for fifteen minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9CE819F5B26AD74C87CF15203FB6ABC8', X'35D0B40433B5F249BD1C36044DDB37C4', X'35D0B40433B5F249BD1C36044DDB37C4', 0, 'javascript:', 'Lotus Soup', 2, NULL, 0, 'Lotus Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=380', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 ten ounce can lotus seeds, drained (some cans are labeled lotus nuts)<BR>3 Tablespoons sugar, optional<BR>3 Tablespoons crystallized ginger, minced<BR>2 Tablespoons sesame paste (or tahini)<BR>3 Tablespoons lotus or sweet potato flour<BR>5 Tablespoons glutinous rice flour<BR>2 Tablespoons cornstarch mixed with one-quarter cup cold water<BR>1/4 cup cooked Chinese dates, minced coarsely<BR><B><I>Preparation:</B></I><BR>1. Mix mashed lotus, sugar (if used), ginger, sesame paste, and one-quarter cup boiled water, and stir until sugar is dissolved and all is well mixed. Make this mixture into twenty-four small balls.<BR>2. Mix two cups of cold water with both flours and the cornstarch mixture and slowly bring to the boil stirring constantly. Add the dates and the previously made lotus seed balls and heat through. Serve immediately.<BR><B>Note:</B> If desired, one can make this with four or more cups of cold water and consume it as a tea.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BEE94E3647F05D45BE4DEF09DD940018', X'1DC206FAC54BEC4698ED275D475D3E11', X'1DC206FAC54BEC4698ED275D475D3E11', 0, 'javascript:', 'Luohan and Longan Sweet Soup', 2, NULL, 0, 'Luohan and Longan Sweet Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1020', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 lohan, broken into four to six pieces<BR>1/4 cup dried seedless longan<BR>1 melon, peeled and coarsely grated<BR>12 honey-dates<BR>1/4 cup white or light yellow rock sugar<BR><B><I>Preparation:</B></I><BR>1. Put lohan, longan, melon shreds, rock sugar, and two quarts of water into a four quart pot, Bring this to the boil, then reduce the heat, cover, and simmer for one hour.<BR>2. Strain, if desired (most Chinese do not), and serve warm.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9F92A7AAEEDDFF448EDC97DB90F4DED1', X'1F79F1E40DC2984889407EE970F525B3', X'1F79F1E40DC2984889407EE970F525B3', 0, 'javascript:', 'Madame Song''s Fine Fish Soup', 2, NULL, 0, 'Madame Song''s Fine Fish Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1190', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1/8 pound any fresh fish, slivered<BR>1 Tablespoon frozen peas<BR>4 cups chicken stock<BR>1 Tablespoon red vinegar<BR>dash sesame oil<BR>1 Chinese dried black mushroom, soaked, stem discarded, and slivered<BR>1 Tablespoon smoked ham, slivered<BR>1 egg white or half beaten egg, fried and thinly slivered<BR>3 Tablespoons silken tofu, slivered (optional)<BR>1 one-inch cube canned bamboo shoot, slivered (optional)<BR>1 slice (about a tablespoon) carrot or a water chestnut, slivered (optional)<BR>1 Tablespoon cornstarch mixed with an equal amount of cold water<BR><B><I>Preparation:</B></I><BR>1. In a strainer, blanch the slivers of fish for half minute in boiling water, then drain and set them aside.<BR>2. Blanch the frozen peas in the same boiling water for half minute, drain and set them aside.<BR>3. Heat the chicken stock to just below the boiling point, then add the vinegar and sesame oil followed by the slivers of mushroom, ham, and egg (and the optional tofu, bamboo shoot, and carrot slivers).<BR>4. Bring the soup to just below the boil, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DB0FB5625428B049AB2A3E16D08140B4', X'5ED3287E5DEA1D48866D04889641913E', X'5ED3287E5DEA1D48866D04889641913E', 0, 'javascript:', 'Many Mushroom Soup', 2, NULL, 0, 'Many Mushroom Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1691', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>3 slices fresh ginger, slivered<BR>2 Tablespoons mushroom soy sauce<BR>½ pound fresh button mushrooms, wiped clean, trimmed and sliced<BR>1 fifteen-ounce can straw mushrooms, drained and sliced head to stem<BR>1 pound fresh or canned oyster mushrooms, sliced<BR>1 large fresh shiitake mushroom, stem removed, then sliced<BR>4 dried shiitake mushrooms, soaked until soft, stems discarded, caps sliced<BR>2 slices seaweed sheets, cut in half, then slivered<BR>1/2 cup canned baby corn, sliced the long way<BR>1/2 cup bamboo shoots, cut into long thin slices<BR>1/2 cup sliced water chestnuts, cut into strips<BR>5 cups chicken broth<BR>1 teaspoon sesame oil<BR>1/2 cup fresh coriander leaves and stems, chopped<BR><B><I>Preparation:</B></I><BR>1. Heat oil and stir-fry the ginger for one minute, then add soy sauce and all the other ingredients except the fresh coriander.<BR>2. Bring to the boil, reduce the heat, and simmer for ten minutes, then serve topped with the coriander pieces.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'63E7012E037D5A4CA3E1B9810C847DC7', X'63CCBA614344ED49B2F8A62A09265861', X'63CCBA614344ED49B2F8A62A09265861', 0, 'javascript:', 'Many-shredded Soup with Celtuce', 2, NULL, 0, 'Many-shredded Soup with Celtuce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1873', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Chinese black mushrooms, soaked in warm water until soft, their stems removed and discarded<BR>1/2 cup chicken breast, shredded and blanched for two minutes in boiling water<BR>1/4 cup Jinhua or Smithfield ham, shredded<BR>1 celtuce stem, peeled and cut into thin sticks; one can also shred the leaves if desired<BR>1 small thin pork chop, fat discarded, meat shredded and blanched for four minutes in boiling water<BR>1/2 teaspoon coarse salt<BR>1 Tablespoon Chinese rice wine<BR>5 cups chicken stock<BR><B><I>Preparation:</B></I><BR>1. Shred the black mushrooms and put them in a deep serving bowl.<BR>2. Place the chicken breast, ham and celtuce shreds next to the mushrooms in a deep soup bowl.<BR>3. Mix salt, rice wine and chicken stock and bring to a boil in a medium-size pot, then pour this over the mushroom and other shreds being careful not to disturb the pattern.<BR>4. Allow to sit for two to three minutes, then serve into individual bowls at the table after everyone is seated.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3EF1FECF27D1814C90A4F09D6895239E', X'B50E03922867FF47A7E4857B39EB88CF', X'B50E03922867FF47A7E4857B39EB88CF', 0, 'javascript:', 'Meat and Wintermelon Soup', 2, NULL, 0, 'Meat and Wintermelon Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1671', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 or 2 dried scallops<BR>5 Chinese black mushrooms<BR>1/4 cup smoked ham<BR>1/2 cup chicken<BR>2 chicken livers<BR>2 chicken stomachs, thick parts removed and discarded<BR>1 thousand year egg, cut into small cubes<BR>1/2 cup shrimp, shells and vein discarded<BR>1/2 cup canned or cooked bamboos shoots<BR>3 slices fresh ginger, peeled<BR>6 cups chicken or vegetable stock<BR>5 pound winter melon, top removed, and seeded<BR>2 Tablespoons Chinese rice wine<BR>1 teaspoon salt<BR><B><I>Preparation:</B></I> 1. Steam scallops for twenty minutes or until soft, then cool and tear into thin shreds.<BR>2. Soak mushrooms for twenty minutes, discard the stems, and slice thin.<BR>3. Slice thinly the ham, chicken, chicken livers and stomachs, then blanch each separately for one minute.<BR>4. Cut bamboo shoots and ginger into thin slices or strips.<BR>5. Steam winter melon in its serving bowl for twenty minutes with its cover on.<BR>6. Put all meats, vegetables, and stock into the winter melon, and add the wine and salt. Cover and steam another thirty minutes, then put the bowl on the table and serve soup, its contents, and some of the winter melon flesh to each diner.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0CDBA43DF1291A44BA9BB22137CF5790', X'F596A29D73CF0E40881C7B37E7BF3382', X'F596A29D73CF0E40881C7B37E7BF3382', 0, 'javascript:', 'Melon, Lou Han and Longan Soup', 2, NULL, 0, 'Melon, Lou Han and Longan Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1060', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1 almost ripe hami or another melon, peel discarded, and the fruit shredded<BR>1 luo han fruit, fresh or dried, its peel discarded, its fruit crushed<BR>12 honey-sweetened Chinese brown dates<BR>1/4 cup dried seedless longan<BR>1/2 cup white rock sugar<BR><B><I>Preparation:</B></I><BR>1. Put all four fruit ingredients into a medium-size pot. Add the rock sugar, and bring this to the boil.<BR>2. Reduce heat to a simmer, cover the pot, and simmer at a low temperature for one hour. Strain, if desired, and serve.<BR>.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0ACFEA1B942D68418D5157A779345C0C', X'A78AF3E198B8694F9EE73CA9EB58BE6B', X'A78AF3E198B8694F9EE73CA9EB58BE6B', 0, 'javascript:', 'Millet Congee', 2, NULL, 0, 'Millet Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2046', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1/2 cup millet<BR>1 cup peeled grated fresh pumpkin<BR>1 Tablespoon brown rock sugar<BR>1/2 teaspoon salt<BR><I><B>Preparation</B></I><BR>1. Soak the millet seeds overnight. In the morning, bring them to the boil with two cups of cold water and reduce the heat simmering them for one hour.<BR>2. Add the grated pumpkin, rock sugar, and the salt and simmer until the sugar is dissolved. Serve hot or warm.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'696658A8AB235F4A868754A1B6C4E1CA', X'664C575082CCEA4596139A5C82E90401', X'664C575082CCEA4596139A5C82E90401', 0, 'javascript:', 'Millet Congee', 2, NULL, 0, 'Millet Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2149', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup millet, soaked overnight<BR>1 cup peeled and grated fresh pumpkin<BR>1 Tablespoon crushed brown rock sugar<BR>1/4 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Bring the millet to the boil in four cold water, reduce the heat and simmer for one hour.<BR>2. Add the grated pumpkin, sugar, and the salt and simmer until the sugar is dissolved and the millet soft. Serve hot or warm.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0690A1DA94E40E4E959D0AF2DCAF4675', X'F2C91FA5E53189448907AC11A10CC760', X'F2C91FA5E53189448907AC11A10CC760', 0, 'javascript:', 'Millet Congee', 2, NULL, 0, 'Millet Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1987', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup millet<BR>1 cup peeled fresh pumpkin, grated<BR>1 Tablespoon brown rock sugar<BR>1/4 teaspoon salt<BR>½ cup soy milk (optional)<BR><B><I>Preparation:</B></I><BR>1. Soak millet seeds overnight, then bring them to the boil with two cups of cold water. Reduce the heat and simmer for one hour.<BR>2. Add grated pumpkin, rock sugar, and salt and simmer until the sugar and salt are dissolved. Then add the soy milk, if using it. Serve hot or warm.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1D6AEE1FCCD7EF4D825765DDC678B16F', X'B1EF9BB10FE1C34A8746CEADC59E60BC', X'B1EF9BB10FE1C34A8746CEADC59E60BC', 0, 'javascript:', 'Millet Congee with Brown Rice', 2, NULL, 0, 'Millet Congee with Brown Rice', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=393', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup millet, washed well in cold water<BR>1 teaspoon salt<BR>1 cup brown rice, soaked overnight in lots of water<BR>1/2 teaspoon white pepper<BR>1 teaspoon sesame oil<BR>2 Tablespoons scallions, green tops only, minced very fine<BR>1 teaspoon toasted sesame seeds<BR><B><I>Preparation:</B></I><BR>1. Bring six cups of water to the boil, add the millet and the salt and reduce the heat. Cover the pot part way, and simmer for thirty minutes.<BR>2. Rinse the brown rice six or seven times in cool water, and drain well. Add the rice to the millet. Add more water if less than a quart remains, and covered as before simmer another hour and a half or until both grains are very tender. Turn off the heat and leavecovered at least another half an hour.<BR>3. Add pepper, sesame oil, and scallions. Serve soup in individual bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0257412A0B670B4899EA4B7AB66EDFEA', X'6F4F421E69AC1448A9099CE4FE1B8078', X'6F4F421E69AC1448A9099CE4FE1B8078', 0, 'javascript:', 'Momo Soup', 2, NULL, 0, 'Momo Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=270', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>5 cups chicken stock<BR>12 prepared momos<BR>12 zamba balls<BR>1 cup cooked thin egg noodles<BR>2 cups shredded spinach or mustard green<BR><I><B>Preparation:</I></B><BR>1. Put the stock into a pot and gently bring to the boil.<BR>2. Add the <I>momo''s</I> putting them in gently lowering them with a spoon. Do likewise with the zamba balls.<BR>3. Next put in the noodles and cook for one minute; then add the spinach and serve. <BR><B>Note:</B> Some serve the <I>zamba</I> on the side, others prefer them in the soup.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A63BBB5D383C4F45A4E80590A3FD98DF', X'980D56B9AE962649A63552D779B31E46', X'980D56B9AE962649A63552D779B31E46', 0, 'javascript:', 'Mom''s Chinese Style Borscht', 2, NULL, 0, 'Mom''s Chinese Style Borscht', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1256', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 and 1/2 pounds beef shank<BR>2 teaspoons vegetable oil<BR>2 yellow onions, halved and sliced crosswise<BR>4 carrots, peeled and sliced<BR>2 potatoes, peeled and sliced<BR>2 large tomatoes, cut in wedges<BR>4 ounces mushrooms, sliced<BR>1/2 head of cabbage, cut into large pieces<BR>2 golden beets, sliced (optional)<BR>salt, to taste<BR><B><I>Preparation:</B></I><BR>1. Put meat into two quarts of boiling water and boil it for ten minutes, drain, and rinse with sold water.<BR>2. Heat wok or large pot, add vegetable oil, and saute onions until soft.<BR>3. In a large pot (or a thermal cooker if you have one), put beef shank, sauteed onions, carrots, potatoes, tomatoes, mushrooms, cabbage, and beets (if using them). Cover with two and a half cups water and bring to the boil. Allow to simmer on very low heat for four to six hours.<BR>4. Remove beef shank, let cool, then thinly slice. Salt to taste, then ladle vegetables and soup into a soup bowl or tureen, garnish with the sliced beef, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B5A44B173054784390135F09571AC172', X'2A81419BEF0A184EB407D7B997BEDEED', X'2A81419BEF0A184EB407D7B997BEDEED', 0, 'javascript:', 'Mongolian Noodle Soup', 2, NULL, 0, 'Mongolian Noodle Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=359', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>1/2 teaspoon fenugreek seeds<BR>3 cloves garlic, finely minced<BR>1 teaspoon powdered ginger<BR>1 pound lean cubed beef<BR>1/2 teaspoon cumin<BR>1/2 teaspoon turmeric<BR>salt to taste<BR>1 pound flat egg noodles<BR>1 cup scallions chopped<BR>1/2 cup thinly sliced onions<BR>1/2 cup thinly sliced tomatoes<BR>1 Tablespoon butter or oil (optional)<BR><B><I>Preparation:</B></I><BR>1. Heat the oil in a heavy pan and stir in the fenugreek seeds until they turn dark brown. Then add the garlic and ginger and the meat, cumin, and turmeric. Stir them until the meat is browned, reduce the heat and add the salt. Cover and simmer for five minutes.<BR>2. Add seven cups of water, cover, and cook for ten minutes.<BR>3. In a large pot, boil water and add the noodles, cooking until they are tender. Drain the water and coat the noodles with a little butter or oil, if desired.<BR>4. Place enough noodles in each of ten diner''s bowls filling them halfway. Ladle the meat broth over the noodles, add the scallions, onion slices, and tomatoes.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'042AA0F2846BAA47892927C1102F7E42', X'0E6FDA1C4285664F94BD0483D4ACD2CB', X'0E6FDA1C4285664F94BD0483D4ACD2CB', 0, 'javascript:', 'Noodles Soup, Sichuan Style', 2, NULL, 0, 'Noodles Soup, Sichuan Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=872', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>8 scallions, cut into one-inch lengths<BR>8 cloves garlic, peeled and cut in half the long way<BR>8 slices fresh ginger, cut each slice in half the long way<BR>1 teaspoon chili paste with garlic<BR>2 sticks Chinese cinnamon<BR>3 star anise<BR>1/2 cup thin soy sauce<BR>1 pound boneless beef chuck, cut into one-inch cubes<BR>6 ounces rice noodles, soaked for half an hour<BR>1/4 pound spinach, stems removed<BR><B><I>Preparation:</B></I><BR>1. Heat wok, add oil and stir-fry scallions, garlic, ginger, and chili sauce for one minute, then remove to a pot and add the cinnamon, star anise, and the soy sauce, and let rest for fifteen minutes.<BR>2. Reheat the wok, and stir-fry the beef on very high heat for two minutes, then add it to the pot along with eight cups of water. Bring this to the boil, reduce heat and simmer for five minutes. Then remove the cinnamon sticks and star anise, and discard. Add the spinach and rice noodles, and serve directly into individual soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'513EA4F48343AD43A49E44BA1BD260FE', X'48AF527E188D4E47A899E1B25CEA47DF', X'48AF527E188D4E47A899E1B25CEA47DF', 0, 'javascript:', 'Nourishing Sesame Egg Soup', 2, NULL, 0, 'Nourishing Sesame Egg Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1189', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup long grain rice<BR>5 Tablespoons black sesame seeds<BR>1 cup water chestnut powder<BR>1 cup granulated sugar<BR>1 egg, beaten until light yellow in color<BR>2 chicken or duck egg yolks, each cut in halves<BR><B><I>Preparation:</B></I><BR>1. Soak rice in one cup water overnight.<BR>2. Mix black sesame seeds with the rice and water and blend until thick and the seeds are ground fine.<BR>3. Put the items from the blender into a pot and bring to the boil. Reduce the heat, mix in the water chestnut powder and four cups of warm water, and simmer to thicken.<BR>4. Add sugar, and the beaten egg, bring to the boil. Put soup into four soup bowls, put half a yolk into the center of each one, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5F753625883F6E49B8B8F8430FF30ACC', X'EA33FBD6E5353D4FBB9DD4221942E3F3', X'EA33FBD6E5353D4FBB9DD4221942E3F3', 0, 'javascript:', 'Old Cucumber Moon Scallop Soup', 2, NULL, 0, 'Old Cucumber Moon Scallop Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1405', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup red beans<BR>6 to 8 moon scallops with tails left on<BR>1 old (yellowed) cucumber, seeds removed, sliced, not peeled<BR>1/2 pound bones, blanched, the water discarded<BR>1 two-inch piece tangerine peel, soaked for twenty minutes, water discarded, then diced<BR>1 teaspoon coarse salt<BR><B><I>Preparation:</B></I><BR>1. Soak red beans and moon scallops in water for two hours, rinse, discard the water, and mash the beans.<BR>2. Put cucumber pieces, mashed red beans, moon scallops, and the bones, into three quarts of water. Bring to the boil, lower the heat, skim if needed, then simmer for two hours before discarding the bones.<BR>3. Add tangerine peel pieces and the salt and simmer another half hour, then serve.<BR><B>Note:</B> One can add a half carrot peeled and sliced, half pound Chinese yam also peeled and sliced, half cup corn kernels, five dried seedless longan cut in half, one-quarter teaspoon ground white pepper, three slices fresh ginger minced, and 20 goji berries when adding the tangerine pieces. Then do not simmer but boil for twenty minutes to make this dish a northern stew-like dish.Old cucumber Moon scallop Soup <B><I>ients:</B></I><BR>1/4 cup red beans<BR>6 to 8 moon scallops with tails left on<BR>1 old (yellowed) cucumber, seeds removed, sliced, not peeled<BR>1/2 pound bones, blanched, the water discarded<BR>1 two-inch piece tangerine peel, soaked for twenty minutes, water discarded, then diced<BR>1 teaspoon coarse salt<BR><B><I>Preparation:</B></I><BR>1. Soak red beans and moon scallops in water for two hours, rinse, discard the water, and mash the beans.<BR>2. Put cucumber pieces, mashed red beans, moon scallops, and the bones, into three quarts of water. Bring to the boil, lower the heat, skim if needed, then simmer for two hours before discarding the bones.<BR>3. Add tangerine peel pieces and the salt and simmer another half hour, then serve.<BR><B>Note:</B> One can add a half carrot peeled and sliced, half pound Chinese yam also peeled and sliced, half cup corn kernels, five dried seedless longan cut in half, one-quarter teaspoon ground white pepper, three slices fresh ginger minced, and 20 goji berries when adding the tangerine pieces. Then do not simmer but boil for twenty minutes to make this dish a northern stew-like dish.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A64E74BD6DB61740BFFA45694EBE54DB', X'2CFB089B4B8C254B95687ECD3DA99836', X'2CFB089B4B8C254B95687ECD3DA99836', 0, 'javascript:', 'Oxtail Soup', 2, NULL, 0, 'Oxtail Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1928', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 cups Chicken stock or broth<BR>1 oxtail, cut into two-inch sections<BR>1 pound chicken legs<BR>1/2 pound whole chicken thighs<BR>1-inch piece of fresh ginger, peeled and sliced, then each piece crushed<BR>1 Tablespoon Sichuan peppercorns<BR>3 Tablespoons <I>goji</I> berries<BR>3 Tablespoons Chinese Shao Xing or another Chinese rice wine<BR>1 Tablespoon mushroom soy sauce<BR>1 Tablespoon sesame oil<BR>1 Tablespoon broad bean paste<BR><B><I>Preparation:</B></I><BR>1. Bring chicken stock or broth to the boil, add oxtail and chicken pieces, and the ginger and peppercorns, and simmer for half an hour, skimming often. Remove the poultry and let the oxtail cook another half an hour, then remove it. Allow all to cool about half an hour until they can be handled.<BR>2. While waiting, strain the liquid through a fine sieve or cheesecloth, and remove the poultry from the bones and discard the bones and the skin. Then remove the oxtail pieces, and tear or cut the meat off its bone. Cut the chicken meat into one-inch pieces. Discard the ginger and the peppercorns. 3. Return the chicken and oxtail meat to the pot, and add enough water to have six to eight cups of liquid. Add the <I>goji</I> berries, the wine, soy sauce, sesame oil, and the broad bean paste and stir well, then simmer for ten to fifteen minutes. Serve as is or serve the meat in a bowl on the side and the soup in soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0863F72C69799B4AA8D524384212CC1A', X'0D18017211F2134B9B330D41770A974C', X'0D18017211F2134B9B330D41770A974C', 0, 'javascript:', 'Oysters, Bean Curd, and Huang Qi', 2, NULL, 0, 'Oysters, Bean Curd, and Huang Qi', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1848', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound small fresh oysters<BR>1 pound box of soft or silken bean curd<BR>2 Tablespoons <I>huang qi</I><BR>2 scallions, diced<BR>2 teaspoons thin soy sauce<BR>1/4 teaspoons ground white pepper<BR>2 teaspoons sesame oil<BR>2 teaspoons cornstarch mixed with two teaspoons cold water<BR><B><I>Preparation:</B></I><BR>1. Rinse, drain, and dry on paper towels, the fresh oysters.<BR>2. Cut the bean curd into one-inch pieces.<BR>3. Simmer the <I>huang qi</I> in two cups of cold water until only half cup of the liquid remains.<BR>4. Next, add the oysters, bean curd, soy sauce, ground pepper, and sesame oil and simmer for ten minutes.<BR>5. Thicken by boiling and stirring in the cornstarch mixture. Pour this into a pre-heated bowl and serve.<BR><B>Note:</B> Additional boiling water, up to four cups, can be added at the end to make this dish more soupy.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CE8ABFEC000ECD4C8C8AD8F63F9E6565', X'9293104B2BC8C64DBF7664F535562191', X'9293104B2BC8C64DBF7664F535562191', 0, 'javascript:', 'Paal Kanji', 2, NULL, 0, 'Paal Kanji', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=394', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 cups whole milk<BR>1 cup parboiled rice, rinsed<BR>1/2 cup sugar<BR><B><I>Preparation:</B></I><BR>1. In a heavy bottomed pot, bring milk to a rolling boil, then reduce the heat and simmer for ten minutes, stirring occasionally. Remove the pot from the heat and keep it covered.<BR>2. In another pot, bring three cups of water to the boil and add the rinsed rice, lower the heat and simmer for forty minutes, stirring frequently. Add more water, if needed.<BR>3. Pour the boiled milk into the rice mixture and stir well, and continue to simmer for another ten to fifteen minutes until the rice feels very soft.<BR>4. Add the sugar and keep the rice covered until ready to serve. Just before serving, stir gently.<BR><B>Note</B>: This can be served with fresh coconut slices, jaggery chunks, and/or deep fried pappadums.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B3FE4AC6923DE642B0478D891B1F64B7', X'25FB5BE51FBE7642AFC7E15686E0B6AD', X'25FB5BE51FBE7642AFC7E15686E0B6AD', 0, 'javascript:', 'Papaya in Soup', 2, NULL, 0, 'Papaya in Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=228', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 green papaya<BR>2 large pieces rock sugar, or more or less, to taste<BR>2 ounces white cloud ear fungus, soaked in cold water for one hour.<BR>1 cup chicken stock<BR><I><B>Preparation:</I></B><BR>1. Peel both papaya and remove the seeds. Then dice the flesh into one-inch square-shaped pieces. <BR>2. Bring pieces of sugar and one cup of cold water to the boil and simmer until sugar is totally dissolved.<BR>3. Trim any tough center pieces away from cloud ear fungus. Then add them and chicken stock and another cup of cold water to the pot of sugar water and bring to the boil.<BR>4. Pour this entire mixture into a heat-proof bowl and set in a steamer over two inches of simmering water. Add the papaya and steam for three quarters of an hour. Pour into small bowls and serve immediately.<BR>Serves six.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B41BE055DCFD57468BDCAC889AAAD99F', X'906FE471BF0E64468EB84C48F2AE9DC6', X'906FE471BF0E64468EB84C48F2AE9DC6', 0, 'javascript:', 'Pickled Cucumber and Chicken Soup', 2, NULL, 0, 'Pickled Cucumber and Chicken Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=422', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>2 chicken legs, each chopped into three pieces<BR>2 slices fresh ginger, slivered<BR>4 ounces pickled cucumber<BR>2 Tablespoons juice from the above pickles<BR>3 cloves garlic, each cut in half<BR>6 cups chicken broth<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. But all the ingredients in a pot and bring to the boil.<BR>2. Turn the heat down and simmer for twenty minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'64FF9ADE065F4546AFAC4E9941A37B6F', X'200E8979258A5249B5BB63EC82D940F5', X'200E8979258A5249B5BB63EC82D940F5', 0, 'javascript:', 'Pigeon Soup in Bamboo Bowls', 2, NULL, 0, 'Pigeon Soup in Bamboo Bowls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=279', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 ounces pigeon meat<BR>4 ounces minced pork<BR>3 water chestnuts<BR>1 dried scallop, soaked for an hour, then drained and minced<BR>1 Tablespoon rice wine<BR>1 Tablespoon light soy sauce<BR>salt and pepper to taste<BR><I><B>Preparation:</I></B><BR>1. Mix all the ingredients and put one fourth of each into a tall bamboo cup, often called a bamboo bowl; the correct size holds about six ounces. Then fill the cup to the top with water.<BR>2. Put the cups uncovered in a steamer. Steam them for an hour, remove and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4140A8FF476098448F4953B553B88451', X'937B1EB2D521954FAD2DCB4B6A04E291', X'937B1EB2D521954FAD2DCB4B6A04E291', 0, 'javascript:', 'Plain Tong Yuen', 2, NULL, 0, 'Plain Tong Yuen', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=209', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 cups glutinous rice flour<BR>3/4 cup hot water (approximately)<BR><I><B>Preparation:</I></B><BR>1. Put the flour in a large bowl and using chopsticks, mix the hot water in a little at a time.<BR>2. Knead the dough in the bowl until it is soft and smooth. If it feels a little dry, wet your hands and knead again. Then put the dough into a plastic bag and let it rest for a short time.<BR>3. Remove one quarter of the dough and break off gum-ball size pieces about one-inch in diameter. Repeat with the rest of the dough.<BR>4. Put them one by one into twelve cups of rapidly boiling water, stir a few times to prevent sticking, and boil for seven to eight minutes until they float; then cook one more minute, drain and serve in a light soup.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ACADAC276385EA42B031A813A9AAF02B', X'C139D5131CBBB04BB601DC1A367AACD3', X'C139D5131CBBB04BB601DC1A367AACD3', 0, 'javascript:', 'Pomegranate and Pork Soup', 2, NULL, 0, 'Pomegranate and Pork Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=285', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1/2 pomegranate<BR>1/2 pound pork, diced into half-inch pieces<BR>4 preserved jujubes or dates<BR>dash of salt<BR><I><B>Preparation:</I></B><BR>1. Put all ingredients into a pot with two quarts boiled water.<BR>2. Simmer for two hours. Remove all the solid ingredietns. Discard the pomegranate skin, pith, and pork. Mash the jujubes after discarding their pits, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C59C6B3047294644A959572204ECE24A', X'C7FCC13D9FF58D45965D09670A128C55', X'C7FCC13D9FF58D45965D09670A128C55', 0, 'javascript:', 'Pork and Intestine Soup I', 2, NULL, 0, 'Pork and Intestine Soup I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1249', '<TD><RECIPE><B><I>Ingredients</B></I><BR>2 pounds flank steak of any other tender cut of beef<BR>1 small pork tongue<BR>1 pig lung (optional)<BR>1 Tablespoon Sichuan pepper, crushed finely<BR>1 teaspoon ground star anise<BR>1 Tablespoon Chinese cinnamon, crushed finely<BR>1 teaspoon salt<BR>1/2 cup fermented glutinous rice<BR>1/2 cup liquid from jar of spicy fermented bean curd<BR>1 onion<BR>3 star anise<BR>1 teaspoon whole Sichuan peppers or half-teaspoon home-made chile paste<BR>1/4 cup mushroom or spicy soy sauce<BR>1 cup thin Chinese celery, sliced on an angle<BR><B><I>Preparation:</B></I><BR>1. Marinate steak, tongue, and lung in two cups of water mixed with the crushed Sichuan pepper, ground star anise, cinnamon, and salt for one hour, then drain and discard the liquid and any spices.<BR>2. Put beef, tongue, and lung (if using) in six cups water. Add the fermented rice, spicy bean curd liquid, onion, star anise, whole Sichuan peppers, and soy sauce. Bring this to just below the boil, reduce the heat, and simmer for two hours, then remove the meats, strain the liquid, and discard any solids removed from it including all onion pieces.<BR>3. Slice the beef, peel and slice the tongue, and dice the lung. Return these to the strained liquid, and the celery slices and six more cups of water. Bring to the boil, then serve in a tureen or individual soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'95A524401EC5BC49AB5801F1B0D144CC', X'3209A8C905F9E8429488077731434C26', X'3209A8C905F9E8429488077731434C26', 0, 'javascript:', 'Pork and Intestine Soup II', 2, NULL, 0, 'Pork and Intestine Soup II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1465', '<TD><RECIPE><B><I>Ingredients</B></I><BR>2 pounds flank steak or any other tender cut of beef<BR>1 small pork tongue<BR>1 pig lung (optional)<BR>1 Tablespoon Sichuan peppercors, crushed finely<BR>1 teaspoon ground star anise<BR>1 Tablespoon Chinese cinnamon, crushed finely<BR>1 teaspoon salt<BR>1/2 cup fremented glutinous rice<BR>1/2 cup liquid from jar of spicy fermented bean curd<BR>1 onion<BR>3 star anise<BR>1 teaspoon whole Sichuan peppercorns or half-teaspon home-made chili paste<BR>1/4 cup mushroom or spicy soy sauce<BR>1 cup Chinese clery, sliced on an angle<BR><B><I>Preparation:</B></I><BR>1. Marinate steak, tongue, and lung (if using it) in two cups of water mixed with the crushed Sichuan pepper, ground star anise, cinnamon, and salt for one hour. Drain and discard the liquid and the spices.<BR>2. Put beef, tongue, and lung in six cups of water. Add the fermented rice, spicy bean curd liquid, onion, star anise, whole Sichuan peppercorns and soy sauce, and bring to just below the boil. Reduce the heat and simmer for two hours. Then remove the meats and set aside. Strain the liquid, and discard all the solids.<BR>3. Slice the beef, peel and slice the tongue, and dice the lung. Return these to the strained liquide, add the celery slices and six more cups of water. Bring to the boil, add the sliced meats, and serve in a tureen or in individual soup bowls putting some meat in each indvidual bowl.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8585414A09BA004B97DC92F77DF2248A', X'73898D1CDE150F419B78815F0BAAD6B7', X'73898D1CDE150F419B78815F0BAAD6B7', 0, 'javascript:', 'Pork and Lily Bulb Soup', 2, NULL, 0, 'Pork and Lily Bulb Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=438', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup lily bulb pieces, washed well<BR>1/4 pound boneless pork loin, cut into thin strips<BR>1 teaspoon corn sesame oil<BR>2 slices fresh ginger, cut into very thin strips<BR>1 teaspoon light soy sauce<BR>1 scallion, cut on an angle into slivers<BR><B><I>Preparation:</B></I><BR>1. Put lily bulb pieces and pork into two cups of watter and simmer for half an hour.<BR>2. Heat oil and fry ginger for half a minute, then add three cups of boiling water and the entire pork mixture including the liquid and simmer an additional ten minutes or until lily bulb pieces are tender.<BR>3. Add soy sauce and scallions, simmer an additional minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'694DF98F4A84A54086D73D17B6AE8A4B', X'5429016140B44644A0C8854530589182', X'5429016140B44644A0C8854530589182', 0, 'javascript:', 'Pork and Mustard Green Soup', 2, NULL, 0, 'Pork and Mustard Green Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=420', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 to 2 pounds of boneless pork<BR>1 pork bone<BR>8 cups of pickled mustard greens (see below), cut into one-inch pieces<BR>ground white pepper, to taste<BR>1 stalk lemon grass, soft part minced<BR>1/4 cup coriander leaves<BR><B><I>Preparation:</B></I><BR>1. Cook pork and the pork bone in three cups of boiling water, or more, to cover the meat, for two hours. Remove meat and cool it and the soup.<BR>2. Remove fat and meat from both, discard the fat and cut the meat into one-inch pieces. Return to the stock and add eight cups of boiling water, then add the greens, lemon grass and pepper, and simmer for five to ten minutes. Add the coriander and serve with the dipping sauce on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4F5D2A032495FB46AA73927EAC9CCAAB', X'B0316502848F0148A0AEDD776F7F8A48', X'B0316502848F0148A0AEDD776F7F8A48', 0, 'javascript:', 'Pork Bone Beauty Broth', 2, NULL, 0, 'Pork Bone Beauty Broth', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1191', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 cups leftover bones from roast pork (or bones from roast chicken, or any mixed bones from any roasts)<BR>1 cup shrimp shells (optional)<BR>1 cup short grain rice<BR>1 piece fresh ginger, sliced, then smashed<BR>1 teaspoon coarse salt<BR>1 medium-size potato, peeled, boiled and mashed<BR>4 shallots, peeled and minced finely<BR>2 Tablespoons minced smoked or fresh ham<BR>1/4 cup shredded lettuce, spinach, or other small soft greens, per serving<BR>1/2 teaspoon sesame oil per serving<BR>1 teaspoon roasted peanuts, pine nuts, almonds, or hazel nuts per serving<BR>1/2 Tablespoon frozen peas, at room temperature, per serving<BR><B><I>Preparation:</B></I><BR>1.Bring bones and optional shrimp shells and ten to twelve cups water to the boil, reduce heat and simmer for two to four hours. Remove bones and discard them, and strain the remaining liquid and discard any solids. This can be chilled, it will form a gelatin. Cubes can be removed from the refrigerator as needed.<BR>2. Using half the liquid from the bones, add rice, ginger, salt, the mashed potato, shallots, and ham and simmer for one hour or more until the rice breaks up and no whole grains are visible.<BR>3. Put one serving of lettuce in each Chinese soup bowl. Bring the stock to the boil and pour it over the greens filling the bowl. Top with sesame oil, peanuts or other nuts, and peas, and serve<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'68A2D704652D444E803E2042D7250798', X'636A70FE27D15F47A0DDD7DBD157D751', X'636A70FE27D15F47A0DDD7DBD157D751', 0, 'javascript:', 'Pork Chop Soup Casserole', 2, NULL, 0, 'Pork Chop Soup Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1687', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound Chinese pumpkin, peeled and sliced<BR>1 Tablespoon sesame oil<BR>1 teaspoon coarse salt<BR>1/2 teaspoon ground black pepper<BR>1/2 teaspoon ground white pepper<BR>2 green apples, peeled, cored, and sliced<BR>2 Tablespoons crushed Chinese brown slab sugar<BR>1 teaspoon soaked dried tangerine peel, minced<BR>5 boneless pork chops, each cut in four<BR>1 quart chicken stock<BR><B><I>Preparation:</B></I><BR>1. Toss the pumpkin with the sesame oil, and layer it on the bottom of a thick pot or casserole.<BR>2. Mix the ground black and white peppers, the sliced apples, sugar, and tangerine peel, and put this mixture on the pumpkin slices.<BR>3. Tuck the pork bones around the sides, and the pieces of pork on the top of the apple slices.<BR>4. Pour the stock over this, bring to the boil, cover, and reduce the heat to a simmer and simmer for fifteen minutes. Remove the cover, and simmer for another hour. Remove the bones and discard, if desired, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A15C334A61A09540A40BF9A369C2D144', X'3ED1CAC1650A6B458050CA59715E556F', X'3ED1CAC1650A6B458050CA59715E556F', 0, 'javascript:', 'Pork Ribs and Scallops in Soup', 2, NULL, 0, 'Pork Ribs and Scallops in Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2221', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 dried scallops, boiled for one hour, then drained and cooled, then torn into the thinnest strips possible<BR>1 pound pork ribs cut into one-inch pieces<BR>½ pound daikon, peeled and diced or very thinly sliced<BR>2 large carrots, peeled and diced<BR>1 inch fresh ginger, peeled and sliced, then each slice smashed, and then diced<BR>1 Tablespoon <I>goji</I> berries<BR><B><I>Preparation:</B></I><BR>1. Put pork ribs into one quart of boiling water, and simmer for one hour, then remove them and strain and discard any solids. Next rinse the pot and return the ribs and the strained liquid.<BR>2. Add the rest of the solid ingredients but not the <I>goji</I> berries and add another quart of water. Bring this to the boil for five minutes, then reduce the heat and simmer for thirty minutes, then add the berries and simmer another ten minutes. Now serve in individual soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DDA7826A98701448B49F7BE42D49ADC5', X'10083926DF59E54B908849E889C053D9', X'10083926DF59E54B908849E889C053D9', 0, 'javascript:', 'Pork Ribs and Scallops Soup', 2, NULL, 0, 'Pork Ribs and Scallops Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2180', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 dried scallops, boiled for one hour, then drained and cooled, then finally torn into the thinnest strips possible<BR>1 pound pork ribs cut into one-inch pieces ½ pound Long White radish also known as <I>daikon</I>, peeled and diced or very thinly sliced<BR>2 large carrots, peeled and diced<BR>1 inch fresh ginger, peeled and sliced, then each slice smashed, and then diced<BR>1 Tablespoon <I>goji</I> berries<BR><B><I>Preparation:</B></I><BR>1. Put pork ribs into one quart of boiling water, and simmer for one hour, then remove them and strain the liquid of all solids and discard them, then rinse the pot and return ribs and the strained liquid to it.<BR>2. Add the rest of the solid ingredients but not the berries. Add another quart of water and bring the entire liquid contents and the ribs and boil this for five minutes, then reduce the heat and simmer for another half an hour. Finally, add the berries and simmer another ten minutes, then serve in individual soup bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'65DA4675AE01574DB0A69E713A4175F7', X'25EB117BDE6DCF45B03F0F08D694EC94', X'25EB117BDE6DCF45B03F0F08D694EC94', 0, 'javascript:', 'Pork Soup with Dried Geckoes', 2, NULL, 0, 'Pork Soup with Dried Geckoes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1107', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound pork shin, with bone<BR>2 Chinese brown dates<BR>1 candied Chinese date<BR>1 piece dried tangerine peel, soaked for one hour, then cut into small pieces<BR>2 dried geckoes<BR><B><I>Preparation:</B></I><BR>1. Blanch the pork in boiling water for two minutes, then drain and cut into several large pieces.<BR>2. Put all ingredients into a clay pot, add two quarts of boiling and bring this almost to the boil. Reduce the heat and simmer for several hours until about one and a half cup of liquid remains.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5A1AB7EBF31C5445849F88F70474FFF4', X'44E0FE997A8F334B84CA04D3783C5E9B', X'44E0FE997A8F334B84CA04D3783C5E9B', 0, 'javascript:', 'Pork Soup with Seaweed', 2, NULL, 0, 'Pork Soup with Seaweed', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1801', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon kelp<BR>1 Tablespoon another kind of seaweed<BR>1/3 pound lean pork<BR>3 candied dates<BR><B><I>Preparation:</B></I><BR>1. Soak kelp and seaweed separately until soft, the kelp will take longer. Then rinse each of them to rid any sand or debris, and drain each one.<BR>2. Pour two quarts of boiling water over the pork to rinse it. Then put three quarts of cold water in a clean pot, add the pork, dates, kelp, and the seaweed and bring to the boil. Just when it reaches the boil, reduce the heat to low, and simmer for two hours.<BR>3. Cool in the refrigerator over night, discard any congealed fat, tear the pork pieces into shreds, return the soup to a boil, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6AD2A450612D514ABD4655435AC67808', X'1AA3BEBF68FD06478BF45C4C57EA3B04', X'1AA3BEBF68FD06478BF45C4C57EA3B04', 0, 'javascript:', 'Pork with Star Fruit', 2, NULL, 0, 'Pork with Star Fruit', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1193', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>2 cloves garlic, peeled and minced<BR>6 ounces pork loin, minced<BR>2 star fruit, one sliced to make stars, the other diced<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon oyster sauce<BR>4 to 6 cups chicken broth 2 Tablespoons cornstarch mixed with an equal amount of cold water<BR><B><I>Preparation</B></I><BR>1. Heat wok or large fry pan, add oil, and in half minute, add garlic and stir-fry for half minute.<BR>2. Add pork and stir-fry until brown and crisp, about two minutes, then add diced star fruit and stir-fry another minute.<BR>3. Add rice wine, soy sauce, oyster sauce, and the chicken broth, and when hot, add cornstarch mixture and sliced star fruit and stir until thickened, then serve.<BR><B>Note:</B> This recipe can also be made with beef, or half beef and half pork; though it is not commonly made that way.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BCE077BB1F78E44E9338BC8173870101', X'98E43E4239BF9D499E99D58145FAF783', X'98E43E4239BF9D499E99D58145FAF783', 0, 'javascript:', 'Pulled Noodles and Beef in Soup', 2, NULL, 0, 'Pulled Noodles and Beef in Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=196', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 Tablespoon corn oil<BR>1/2 onion, chopped<BR>1 clove garlic, minced<BR>1 teaspoon minced fresh ginger<BR>1 pound round beef, thinly sliced<BR>1 tomato, chopped<BR>1 four-inch piece daikon, peeled and thinly sliced<BR>8 cups <I>tupka</I> broth, recipe follows<BR>1 cup uncooked Tentuk or pulled dough, recipe follows<BR>1 cup chopped spinach leaves<BR>salt to taste<BR><I><B>Preparation:</I></B><BR>1. Heat oil, then saute onion, garlic, and ginger just until the onion starts to brown.<BR>2. Add beef and stir-fry until no longer pink; do not over cook.<BR>3. Add tomato, daikon, and broth and bring to the boil, then add the pulled dough, turn off the heat, then add the spinach and stir, and the salt to taste. Serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9E3319CCCAC9594086E1F05EE7F55980', X'470DFE53ED37F74A8E4D41F4CDE6716E', X'470DFE53ED37F74A8E4D41F4CDE6716E', 0, 'javascript:', 'Pumpkin Soup', 2, NULL, 0, 'Pumpkin Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1024', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup dry shrimp, soaked for twenty minutes in warm water, then drained<BR>6 shallots, minced<BR>2 red chilies, seeds and veins removed, then minced<BR>1/2 teaspoon shrimp paste<BR>1 pound Chinese pumpkin, peeled and cubed<BR>1 Tablespoon Chinese rice vinegar<BR>1 teaspoon sacha sauce<BR>8 ounces chicken stock<BR>24 Thai basil leaves, coarsely chopped<BR><B><I>Preparation:</B></I><BR>1. Using a blender or a mortar and pestle, blend dry shrimp, shallots, chili peppers, and shrimp paste with three tablespoons cool water. Put into a large pot and mix well.<BR>2. Add pumpkin, rice vinegar, sacha sauce, chicken stock, and two quarts of water. Bring almost to the boil, reduce heat, and simmer for fifteen minutes. Transfer to a pre-heated soup tureen.<BR>3. Sprinkle basil leaves on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2AE23BE1D9BA17449779A9F876623BF4', X'EF84A358E74E1F45B8217A5EAD3506E1', X'EF84A358E74E1F45B8217A5EAD3506E1', 0, 'javascript:', 'Pumpkin, Almond, and Kiwi Soup', 2, NULL, 0, 'Pumpkin, Almond, and Kiwi Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1017', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound Chinese pumpkin, peeled and thinly sliced<BR>1/2 cup almonds, blanched and peeled<BR>3 Tablespoons sugar<BR>1/2 cup peeled and diced kiwi<BR><B><I>Preparation:</B></I><BR>1. Steam the pumpkin for fifteen or twenty minutes, drain, and cool. Then mash pushing the pumpkin through a fine strainer to remove any fibers.<BR>2. Grind the almonds with half cup water, then strain keeping the water and discarding the nut meat.<BR>3. Mix pumpkin, liquid from the almonds (called almond milk), and sugar, and simmer for half an hour.<BR>4. Add kiwi pieces, and six cups of water and bring to the boil, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1F8EC401A98E224C949EAFC8EF982FB0', X'7C67F212A1FB884C80ED901FF6D1BDA5', X'7C67F212A1FB884C80ED901FF6D1BDA5', 0, 'javascript:', 'Red Congee', 2, NULL, 0, 'Red Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1177', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 Tablespoons red beans, soaked for three hours, then drained<BR>2 Tablespoons red rice<BR>3 pieces (about half a tangerine) dried tangerine peel<BR>2 Tablespoons mung beans<BR>3 cups leftover chicken congee with <I>pidan</I> (see Volume 9(1) pp 10) or any white rice congee<BR>3 Tablespoons granulated sugar<BR>1 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Check beans and discard any broken ones, then put them in a bowl with the red rice and six cups of cold water. Allow these to soak overnight.<BR>2. Drain and discard the water, put the beans and rice and the tangerine peel and the mung beans in a pot with six cups of cold water, and bring to the boil, reduce the heat, and simmer covered for three hours.<BR>3. Discard the tangerine peel, and add the congee, and stir well. Bring almost to the boil, reduce the heat, add the sugar and the salt and simmer uncovered stirring often for one hour; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'499271BD325B574486B4BC080CD401B3', X'70F3E9855C07054D868CB9B3771D0D1B', X'70F3E9855C07054D868CB9B3771D0D1B', 0, 'javascript:', 'Red Wine Hot and Sour Sea Cucumber Soup', 2, NULL, 0, 'Red Wine Hot and Sour Sea Cucumber Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=300', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 pound pre-soaked sea cucumber, cut into half-inch cubes<BR>3 ounces pork from the tenderloin, cut into half-inch cubes<BR>1/2 cup corn oil<BR>3 Tablespoons cubed bamboo shoots<BR>4 shiitake mushrooms, soaked then cut into half-inch cubes<BR>4 cups chicken stock<BR>1 Tablespoon red rice wine paste<BR>1/2 teaspoon each of sugar, salt, and thin soy sauce<BR>1/4 teaspoon each of chili oil and sesame oil<BR>4 pigeon eggs, hard-cooked and cut in half with zig-zag edge and top and bottom sliced off (there is an illustration in the hard copy of this issue)<BR>1 chicken egg<BR>1 Tablespoon cornstarch<BR>1/2 teaspoon each black and white vinegars<BR>1 scallion, sliced very thin<BR><I><B>Preparation:</I></B><BR>1. Simmer the sea cucumber in water (or chicken stock) for one hour or until tender.<BR>2. Deep fry the pork for one minute, then drain.<BR>3. Using one Tablespoon of the oil, fry the sea cucumber, pork, bamboo shoots, and mushrooms for one minute, add the stock, red rice wine paste, and all the seasonings. and simmer for half an hour, then add cut hard cooked eggs.<BR>4. Beat the chicken egg with the cornstarch and 1 tablespoon water and stir into the soup in a thin stream. Then add the vinegar and the scallions. Serves four.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B109C595D8EB4348819A22A470BBD13E', X'4D231886351A3A40A9EDCA37AE6D7F97', X'4D231886351A3A40A9EDCA37AE6D7F97', 0, 'javascript:', 'Reishi Mushroom Soup', 2, NULL, 0, 'Reishi Mushroom Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1487', '<TD><RECIPE><B><I>Ingredients</B></I><BR>3 Tablespoons dried slices reishi mushrooms, soaked for three or more hours<BR>1/2 pound pork shank bone<BR>6 Chinese pitted brown dates, cut in small pieces<BR>3 Chinese pitted black dates, cut in small pieces<BR>1 piece, about two-inches square tangerine peel, soaked and then minced<BR>2 Tablespoons granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Blanch reishi mushroom slices, then rinse with cold water, then soak for another two hours.<BR>2. Blanch pork shank bone, put two quarts of boiling water, reduce heat, and simmer for two hours, then remove bone and scrape any meat into the water.<BR>3. Add the drained mushroom pieces, those of the dates, and the tangerine peel and the sugar. Simmer another half hour, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E9F8C6C125C4544FBF44975B68EE5DE2', X'B43D78A8E31F5140B958BDF3F98CC472', X'B43D78A8E31F5140B958BDF3F98CC472', 0, 'javascript:', 'Rejuvenation and Long Life Soup', 2, NULL, 0, 'Rejuvenation and Long Life Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=142', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/3 cup dried <I>shan zha</I><BR>1/3 cup <I>gou ji berries</I><BR>½ cup seedless red Chinese dates<BR>1/4 pound fresh ginger root<BR><I><B>Preparation:</I></B><BR>1. Rinse the <I>shan zha</I> and <I>gou ji</I>. Rinse the ginger and crush it with the side of the cleaver. Combine all ingredients in a large pot and add 3 quarts of water and bring it to a boil. Lower heat and simmer for one and a half hours. <BR>2. Crush the dates in the pot or cut them into small pieces with a scissor and cook the soup for another two hours. <BR>3. Drain reserving the liquid and discard the solids. Cool and refrigerate. It may be served hot or cold. In case it is too concentrated, dilute it to suit your taste.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'567F2BFFA418534F97C156D454C99CD1', X'C2EA2B4C92A3CC40B082ED512A328798', X'C2EA2B4C92A3CC40B082ED512A328798', 0, 'javascript:', 'Rejuvenation and Long Life Soup', 2, NULL, 0, 'Rejuvenation and Long Life Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1435', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/3 cup dried <I>shan zha</I><BR>1/3 cup <I>gou ji</I> berries<BR>1/2 cup seedless red Chinese dates<BR>1/4 pound fresh ginger<BR><B><I>Preparation:</B></I><BR>1. Rinse the <I>shan zha</I> and <I>gou ji</I> berries. Next, rinse the ginger and crush it with the side of the cleaver. Combine all ingredients in a large pot and add three quarts of water and bring to the boil. Lower heat and simmer for one and a half hours.<BR>2. Crush the dates in the pot or cut them into small pieces with a scissor and cook the soup for another two hours.<BR>3. Drain reserving the liquid and discard the solids. Cool and refrigerate.<BR><B>Note:</B> This soup can be served hot or cold. In case it is too concentrated, dilute it to suit your taste.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0C557D1379B7184FB0D78B13367C7A2B', X'E38CAE88BE549444A4ADCB7F286093EC', X'E38CAE88BE549444A4ADCB7F286093EC', 0, 'javascript:', 'Rice Congee with a Salty Duck Egg', 2, NULL, 0, 'Rice Congee with a Salty Duck Egg', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2197', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>½ cup jasmine rice<BR>1 teaspoon vegetable oil<BR>1 teaspoon sea salt<BR>1/4 cup thin soy sauce<BR>1 Tablespoon sesame oil<BR>1 salty duck egg hard cooked, peeled, and cut into eight pieces<BR>2 scallions, angle-slice<BR>1 red chili pepper, seeded and slivered<BR>10 sprigs fresh coriander, stems saved for another purpose<BR><B><I>Preparation:</B></I><BR>1. Bring rice, oil, and salt to the boil, reduce the heat and simmer for ninety minutes stirring often. This volume will be reduced by about half.<BR>2. Set rest of the ingredients out for diners to help themselves or put one portion into each bowl of the hot congee. <B>Note:</B> These amounts are for four diners, double for eight, etc.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AA29C772C6FDEE4CB7D6E60D33091EE8', X'3BCB0591FB35994F905A82230C485AA3', X'3BCB0591FB35994F905A82230C485AA3', 0, 'javascript:', 'Roast Duck Noodle Soup', 2, NULL, 0, 'Roast Duck Noodle Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1186', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon five-spice powder<BR>1 Tablespoon sesame oil<BR>3 Tablespoons hoisin sauce<BR>3 Tablespoons light brown sugar (optional)<BR>1 Tablespoon dark soy sauce<BR>2 duck boneless breasts, skin on<BR>4 cups chicken stock<BR>2 dried Chinese black mushrooms, soaked, then their stems discarded<BR>3 and 1/2 ounces shredded Chinese cabbage<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon Shaoxing rice wine or dry sherry<BR>1 Tablespoon white Chinese vinegar<BR>7 ounces cooked wheat flour noodles<BR>1 scallion, sliced thinly on the angle<BR>1 and 1/2 ounces bean sprouts<BR>1 large handful of fresh coriander leaves and stems, coarsely chopped<BR>salt and freshly ground black pepper, to taste<BR><B><I>Preparation:</B></I><BR>1. Mix five-spice powder, sesame oil, hoisin sauce, the brown sugar, dark soy sauce, and three tablespoons of cool water in a bowl, add the duck breasts, and marinate at least twenty minutes or overnight. Then remove the duck breasts, and pat them dry.<BR>2. Preheat oven to 400 degrees F. Put in a broiling pan to preheat it, and when hot, put the duck breasts on it, skin side down and cook them for one minute. Turn them over and cook the other side about the same amount of time or until brown. Then transfer them to a baking tray skin-side up, and cook them until as done as you like this meat, from ten to twenty minutes.<BR>3. Heat chicken stock to the boil and immediately reduce heat to simmer, then add mushrooms, Chinese cabbage, soy sauce, rice wine, and the vinegar. Bring stock and its ingredients back to the boil. Add the scallion pieces, bean sprouts, and chopped coriander and immediately remove pot from the heat source, and add salt and pepper, to taste.<BR>4. Slice each duck breast into six slices. Put soup into six individual soup bowls, distribute other ingredients in the pot, and put two duck slices on top of each bowl. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3569F2EFB8FA63458D71F291BA5DDE00', X'7F5B200D6425A94BAD58E19CE84A469C', X'7F5B200D6425A94BAD58E19CE84A469C', 0, 'javascript:', 'Salted Egg and Wonton Soup', 2, NULL, 0, 'Salted Egg and Wonton Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=541', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound ground pork<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon thin soy sauce<BR>3 raw Chinese salted eggs, peeled and chopped<BR>24 wonton wrappers<BR>6 cups chicken broth<BR>1/8 teaspoon ground white pepper<BR>24 medlar (optional)<BR><B><I>Preparation:</B></I><BR>1. Mix pork, rice wine, soy sauce, and eggs.<BR>2. Put one teaspoon of the mix on each wonton wrapper, wet edges with wet fingers and seal then fold in your favorite style (pillow, nurses cap, etc.).<BR>3. Bring four cups of water to the boil and boil wontons until they float to the surface of the water, remove with a slotted spoon and ladle three each into eight soup bowls.<BR>2. Bring the broth to the boil, add medlar and white pepper and pour over the wonton, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EC889F8799A0CE4CBA90BA5065153B28', X'FC4E2B357FB9774ABAE39EBE7FA6EF6B', X'FC4E2B357FB9774ABAE39EBE7FA6EF6B', 0, 'javascript:', 'Seafood Congee', 2, NULL, 0, 'Seafood Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2055', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups cooked long grain rice<BR>3 cups chicken stock<BR>10 shrimp, peeled, veins removed, shells and veins discarded<BR>10 clams, shells discarded, liquid reserved, soaked in warm water for fifteen minutes<BR>10 oysters, shells discarded, liquid reserved<BR>1 small cuttlefish, cut into small pieces, their undersides scored<BR>1 four to six-inch piece of any white fish, its skin, scales, and bones discarded, the fish thinly sliced<BR>1 teaspoon coarse salt<BR>2 Tablespoons <I>Shao Xing</I> or another Chinese rice wine<BR>5 sliced fish ginger, peel discarded, ginger minced<BR>1 garlic shoot, minced<BR>1 Tablespoon sesame oil<BR>2 Tablespoons cilantro or Chinese celery, minced<BR><B><I>Preparation:</B></I><BR>1. Boil eight cups of water or fish or goose stock. Add liquid reserved from opening the shell fish with the chicken and/or goose stock, and put these and the rice in a large three-quart or larger glass or heat-proof ceramic bowl.<BR>2. Add the shellfish items, the cuttlefish and fish pieces, and the wine, ginger, and half the minced pieces of the garlic shoot. Set the microwave on high, and cook this for four minutes, then stir it and again cook it on high, this time for five minutes. Next, remove the container and its contents and add the rest of the garlic shoot, the sesame oil, and the minced cilantro or Chinese celery, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'14195892600E0542B36DC8195EF47DB1', X'E525362A22F929488FCF12B5AD7EE900', X'E525362A22F929488FCF12B5AD7EE900', 0, 'javascript:', 'Seaweed Eggdrop Soup', 2, NULL, 0, 'Seaweed Eggdrop Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=354', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 cups chicken broth<BR>2 sheets purple or green laver sheets<BR>1 large square soft or silken tofu, cut into long thin strips<BR>1 Tablespoon rice wine<BR>1 teaspoon fresh ginger, minced<BR>1 egg, beaten<BR>dash white pepper ½ luffa or silk squash, peeled, angle sliced, and blanched for one minute<BR><B><I>Preparation:</B></I><BR>1. Bring broth to the boil, then reduce heat to simmer.<BR>2. Using a scissor, cut the laver sheets into long thin strips, about two inches by quarter of an inch, then add to the broth with the tofu, rice wine, and ginger.<BR>3. Slowly stir in the egg and pepper; and the luffa, if used, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'629C288C06AF4445A92E0637E5CDAF99', X'F04B17C53681B24E96F69FF549235006', X'F04B17C53681B24E96F69FF549235006', 0, 'javascript:', 'Seaweed Soup', 2, NULL, 0, 'Seaweed Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1209', '<TD><RECIPE><B><I>Ingredients</B></I><BR>1 Tablespoon sherry<BR>1 slice fresh ginger, minced<BR>1 egg<BR>3 sheets purple laver, cut into half-inch slivers with a kitchen shears<BR>1 and one-half cups soup stock<BR>2 scallions, sliced<BR><B><I>Preparation:</B></I><BR>1. Mix sherry and ginger and set aside.<BR>2. Mix egg and two tablespoons water; beat lightly.<BR>3. Heat soup stock to the boil; add seaweed and stir, then immediately add the sherry and ginger mixture, and stir well.<BR>4. Bring to boil again, then slowly pour in egg and water mixture, stirring, and remove from the heat.<BR>5. Pour into a soup tureen and garnish with the scallions, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'321E52E25355F64286DCDD1E70987D5C', X'73BE105D6CFEE949943F599D5DE97F0E', X'73BE105D6CFEE949943F599D5DE97F0E', 0, 'javascript:', 'Shark''s Fin Soup I', 2, NULL, 0, 'Shark''s Fin Soup I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=917', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small sharks fin, three to four ounces<BR>1 cup chicken bouillon<BR>2 slices fresh ginger, minced and mixed with two tablespoons hot water<BR>1 Tablespoon good Chinese rice wine<BR>1 Tablespoon chicken bouillon powder<BR>4 cups superior chicken stock<BR>1 Tablespoon minced Yunnan ham<BR>2 teaspoons red wine vinegar<BR>1 teaspoon oyster sauce<BR>1 teaspoon premium thin soy sauce<BR>1/4 teaspoon ground white pepper<BR>1/2 cup mung bean spouts, heads and tails removed<BR>2 Tablespoons coriander, coarsely minced<BR><B><I>Preparation:</B></I><BR>1. Steam sharks fin in chicken bouillon and ginger water for half an hour. Gently remove fin and cut it into four pieces, center to tip. Set aside.<BR>2. Mix rice wine, bouillon powder, superior stock, ham, and vinegar, Put in a clay pot and slowly bring to the boil. Simmer for five minutes then add oyster sauce, soy sauce, and ground pepper and stir well. Next add sharks fin pieces, bean sprouts, and coriander, stir again and serve in the pot the soup was cooked in or a heated soup tureen.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1D1531E268A8494EB2C995A2A10ADE92', X'576880C097B17144B9B04B6ECB7ABA06', X'576880C097B17144B9B04B6ECB7ABA06', 0, 'javascript:', 'Shark''s Fin Soup II', 2, NULL, 0, 'Shark''s Fin Soup II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=951', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small shark''s fin, three to four ounces<BR>1 cup chicken bouillon<BR>2 slices fresh ginger, minced and mixed with two tablespoons of hot water<BR>1 Tablespoon good Chinese rice wine<BR>1 Tablespoon chicken bouillon powder<BR>4 cups superior chicken stock<BR>1 Tablespoon minced Yunnan ham<BR>2 teaspoons red wine vinegar<BR>1 teaspoon oyster sauce<BR>1 teaspoon premium thin soy sauce<BR>1/4 teaspoon ground white pepper<BR>1/2 cup mung bean spouts, heads and tails removed<BR>2 Tablespoons coriander, coarsely minced<BR><B><I>Preparation:</B></I><BR>1. Steam the shark''s fin in chicken bouillon and ginger water for half an hour. Gently remove fin and cut it into four pieces, center to tip. Set aside.<BR>2. Mix rice wine, bouillon powder, superior stock, ham, and vinegar, Put in a clay pot and slowly bring to the boil. Simmer for five minutes then add oyster sauce, soy sauce, and ground pepper and stir well. Next add the shark''s fin pieces, bean sprouts, and coriander, and stir again. Serve this soup in the pot it was cooked in.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9E26E67C3237F348A02B5962D6E86B4C', X'BB34192C2007724F8CDCD139D8CF7993', X'BB34192C2007724F8CDCD139D8CF7993', 0, 'javascript:', 'Sharks Fin with Fresh Sea Crab Meat in Thick Soup', 2, NULL, 0, 'Sharks Fin with Fresh Sea Crab Meat in Thick Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=863', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound sharks fin<BR>1/2 teaspoon ginger juice, divided into two equal amounts<BR>2 teaspoons chicken bouillon powder, divided into two equal batches<BR>4 teaspoons Chinese rice wine<BR>1/4 pound fresh crab meat, all cartilage removed<BR>6 cups supreme chicken stock<BR>1 teaspoon coarse salt<BR>1/2 teaspoon Chinese rice wine<BR>dash ground white pepper<BR>3 Tablespoons cornstarch mixed with two tablespoons cold water<BR><B><I>Preparation</B></I><BR>1. Bring half the ginger juice to a boil, stir in the shark''s fins. Pour into heat proof bowl, add half the bouillon powder and all the wine and two Tablespoons water and steam for fifteen minutes. Using a fork, carefully separate the strands of sharks fin, and pour into a wok.<BR>2. Scald the crab meat with the other half of ginger juice and one cup of boiling water, then drain all liquid and put crab meat into a wok.<BR>3. Add chicken stock, salt, the half teaspoon of rice wine, the other half of the chicken bouillon powder and ground white pepper. Bring to the boil, and immediately add the cornstarch water and stir for half minute; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'40C1428C29DCF24695A202F6824DF18C', X'1EDF648B7C4E03418FA08B50386057D9', X'1EDF648B7C4E03418FA08B50386057D9', 0, 'javascript:', 'Silk Squash Soup', 2, NULL, 0, 'Silk Squash Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=441', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 cups chicken broth<BR>1 silk squash, peeled and cut on an angle into two-inch pieces<BR>1 teaspoon sesame oil<BR>1/2 can ginkgo nuts<BR>20 to 24 lily bulb pieces, washed well, and boiled for five minutes<BR>1 scallion, cut on an angle into one-inch pieces<BR>salt and white pepper, to taste<BR><B><I>Preparation:</B></I><BR>1. Put chicken broth, sesame oil, silk squash, gingko, and lily bulb pieces into a large pot and simmer for fifteen minutes.<BR>2. Add scallion and salt and pepper, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'949685EACF4D644AB293C05E45818FC5', X'465A007BA81D3E46BE7A1E1252946820', X'465A007BA81D3E46BE7A1E1252946820', 0, 'javascript:', 'Silver Ear Soup with jasmine Petals', 2, NULL, 0, 'Silver Ear Soup with jasmine Petals', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1524', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons dried silver ear fungi<BR>30 jasmine flowers, their petals separated<BR>6 cups chicken stock<BR>2 Tablespoons Chinese white wine<BR>1/2 teaspoon salt<BR>3 coriander leaves, slivered<BR><B><I>Preparation:</B></I><BR>1. Soak silver ear fungi in tepid water for two hours, then rinse them several times with cold water. Remove any hard parts and discard them.<BR>2. In a small pot, bring two cups water to the boil, remove from the stove, and immediately soak the silver ear fungi for half hour. Drain them and tear any large pieces into smaller ones, leaving none larger than a half-inch in sizes.<BR>3. Bring chicken stock to the boil, add the drained fungi, reduce the heat, and simmer for five minutes. Add the wine and the salt, stir, then serve this into six individual soup bowls. Sprinkle each with the jasmine petals and the slivers of coriander, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'79026EAACA14B1428AC6CC3FF197F2B3', X'59FB879066B73D4DA0F08D3E71306310', X'59FB879066B73D4DA0F08D3E71306310', 0, 'javascript:', 'Snake, Corn, and Crab Meat Soup', 2, NULL, 0, 'Snake, Corn, and Crab Meat Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=219', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>4 ounces snake, slivered then poached for three minutes and drained<BR>2 ounces fresh or canned crab meat, all tendons removed<BR>1/2 chicken breast, poached for ten minutes, then slivered<BR>1 small can creamed corn, blend one half until very smooth<BR>1 Tablespoon rendered chicken fat<BR>1 Tablespoon thin soy sauce<BR>1 teaspoon sesame oil<BR>dash white pepper<BR>8 cups chicken broth<BR>2 Tablespoons cornstarch mixed with two tablespoons cold water<BR><I><B>Preparation:</I></B><BR>1. Put snake, crab meat, chicken, and both portions of creamed corn into a pot with chicken fat, soy sauce, sesame oil, and white pepper.<BR>2. Heat chicken broth to the boiling point and pour over all the ingredients and simmer for three minutes.<BR>3. Add the cornstarch mixture and boil for one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'26F46A616DF6C249BB800A443DB7528B', X'D2B35BC0CDAC6648820B27B6824F55D4', X'D2B35BC0CDAC6648820B27B6824F55D4', 0, 'javascript:', 'Soup (Shorpo)', 2, NULL, 0, 'Soup (Shorpo)', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=526', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds meat (beef, lamb, or mutton), cut into two-inch cubes<BR>1 onion, peeled and sliced<BR>2 carrots, peeled and sliced<BR>2 or 3 potatoes, peeled and cubed<BR>2 or 3 bay leaves<BR>3 or 4 Tablespoons fresh dill, chopped<BR>1 teaspoon ground black pepper<BR>1 teaspoon ground mild red pepper<BR>1 teaspoon salt, or to taste<BR><B><I>Preparation:</B></I><BR>1. Put meat and three quarts of water into a large pot. Bring to the boil, then simmer for two hours or until the meat is soft.<BR>2. Add the onion, carrots, potatoes, and bay leaves and simmer for twenty minutes or until the vegetables are soft.<BR>3. Add the fresh dill and boil for five minutes. Season with salt, red pepper and black pepper, and serve hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'148D1D66B7497A4C87BEBCB617AEC4BA', X'BF8A87B9779D044284CEF1626A09A042', X'BF8A87B9779D044284CEF1626A09A042', 0, 'javascript:', 'Sour Soup', 2, NULL, 0, 'Sour Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1729', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 recipe Guizhou Sour Vegetables<BR>2 quarts chicken stock, half cup set aside<BR>4 hard-cooked eggs, peeled and cut in half<BR>1 cup boiled hand-pulled la mian noodles<BR>1 Tablespoon hot sauce<BR>2 Tablespoons Chinese black vinegar<BR><B><I>Preparation:</B></I><BR>1. Heat the sour vegetables and the large amount of chicken stock until just below the boiling point.<BR>2. Add the eggs and the noodles.<BR>3, Mix reserved half cup of stock, the hot sauce, and the vinegar.<BR>4. Serve the soup mixture in a bowl or plate it out into individual soup bowls, putting half an egg and some noodles into each bowl.<BR>5. Set the hot sauce on the table with a serving spoon, and let everyone put some into their own soup, as desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C73F267EE2833D439021FC265DFF6408', X'6852B026CDC65D46B8F34F531822CEF4', X'6852B026CDC65D46B8F34F531822CEF4', 0, 'javascript:', 'Sour Spicy Soup with Shark''s Fin and Sea Cucumbers', 2, NULL, 0, 'Sour Spicy Soup with Shark''s Fin and Sea Cucumbers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=862', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons vegetable oil, divided into two equal parts<BR>2 slices fresh ginger<BR>2 scallions, each tied in a knot<BR>1 teaspoon Chinese rice wine<BR>1 small pre-soaked sea cucumber, shredded<BR>1/4 cup pre-soaked sharks fin, pulled apart into individual strips<BR>1 slice shredded fresh ginger<BR>1 large clove peeled garlic, minced<BR>2 teaspoons spicy bean paste<BR>3 Tablespoons shredded bamboo shoot<BR>3 pre-soaked black mushrooms, stems discarded, and slivered<BR>5 cups ham stock or equivalent water and ham bouillon<BR>5 Tablespoons white vinegar<BR>2 Tablespoons thin soy sauce<BR>1 teaspoon sugar<BR>1/2 teaspoon sesame oil<BR>dash or ground white pepper<BR>1 teaspoon chili oil, or to taste<BR>1/4 cup water chestnut powder mixed with one-quarter-cup cool water<BR>3 Tablespoons crab meat, shredded, any cartilage removed<BR>1 small peeled tomato, coarsely diced<BR>1 Tablespoon frozen baby peas<BR><B></I>Preparation:</B></I><BR>1. Heat wok, add half the oil, add the ginger slices, scallions, and wine and simmer these together for one minute before adding one cup of water and the sharks fin and sea cucumber shreds. Simmer for them ten minutes, then remove the solids, and set aside, discarding the ginger and scallions.<BR>2. Heat other half of the oil, and fry shredded ginger and garlic for half minute, then add spicy bean pace and mix well, then add shredded bamboo shoots and stir-fry another minute. Pour in ham stock and add sea cucumber and sharks fin shred and simmer for half an hour.<BR>3. Add vinegar, sugar. soy sauce, sesame oil, ground pepper, and chili oil and heat to the boil before adding the water chestnut/water mixture and the crab meat and stir-fry for half minute.<BR>4. Pour into a pre-heated soup tureen, sprinkle tomato pieces and the peas on top, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DBDDCDC848A03B45AED1589820623C14', X'6BA00CD7A1E6C34D8950130781F74DB1', X'6BA00CD7A1E6C34D8950130781F74DB1', 0, 'javascript:', 'Spare Rib and Bitter Melon Soup', 2, NULL, 0, 'Spare Rib and Bitter Melon Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1863', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>11 ounces spare ribs, cut into one-inch pieces<BR>1 bitter melon, cut in half, seeds and core area removed and discarded, then cut into half-inch slices, or thinner if desired<BR>2 Tablespoons thin soy sauce<BR>1/2 teaspoon ground black pepper<BR>1 Tablespoon dried shrimp, soaked for an hour, then minced<BR>1/2 teaspoon sugar<BR>1 teaspoon lard<BR>chili sauce for dipping<BR><B><I>Preparation:</B></I><BR>1. In a five-quart pot, add the ribs and boiling water to cover, then simmer for an hour until the meat falls off the bones. Then skim off any fat and reduce the liquid to three cups.<BR>2. Next, add the biter melon, soy sauce, black pepper, dried shrimp, sugar, and lard, and then simmer for five more minutes.<BR>3. Serve in individual soup bowls with chili sauce on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'42B63E593F17C146B3512F0F90AEB03D', X'0242814EA0C99447AA86C5BEBA8D4C72', X'0242814EA0C99447AA86C5BEBA8D4C72', 0, 'javascript:', 'Spare Rib Soup with Wolfberries', 2, NULL, 0, 'Spare Rib Soup with Wolfberries', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=784', '<TD><RECIPE><B>Ingredients</B></I><BR>2 pounds spare ribs, have the butcher cut them into one-inch pieces<BR>1 Tablespoon oil<BR>3 Tablespoons wolfberries<BR>10 red Chinese dates, simmered for five minutes, pits removed<BR>3 slices of fresh ginger<BR><B><I>Preparation:</B></I><BR>1. Bring two quarts of water to the boil, and put spare ribs into it and simmer for two minutes, remove spare ribs and discard the water.<BR>2. Heat oil and fry the spare ribs until brown, be careful as they splatter when first put into the oil; remove and drain well.<BR>3. Bring two quarts of water or chicken stock to just below boiling, as spare ribs and all other ingredients, then reduce heat and simmer for ninety minutes. Remove meat from the bones if desired, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4E9D715D5A182746B2D164E1B2B03ED7', X'6902B1598CD102438CAFF4F3B4E5D96D', X'6902B1598CD102438CAFF4F3B4E5D96D', 0, 'javascript:', 'Spare Ribs and Cucumber Soup', 2, NULL, 0, 'Spare Ribs and Cucumber Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=334', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>10 dried shrimp<BR>3 black mushrooms<BR>1 pound spare ribs<BR>1 cucumber<BR>3 slices fresh ginger<BR>2 scallions, minced<BR><B><I>Preparation:</B></I><BR>1. Soak the dried shrimp in warm water for twenty minutes or overnight, and in another bowl, soak the mushrooms in one cup of hot water for twenty minutes or overnight.<BR>2. Cut ribs into two-inch pieces, peel the cucumber and slice it into one-eighth inch thick pieces.<BR>3. Place ribs, ginger, drained dry shrimp, and mushrooms and their water, but not the sand at the bottom of the bowl they are in into the pot. Add five cups additional water and bring this to the boil then simmer for twenty minutes with the lid only half on.<BR>4. Cover tightly and immediately place the pot into the insulated container for at least four hours.<BR>5. Remove from the insulated container, add the cucumber and bring to the boil. Sprinkle the scallions on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0BD9621E59712542975DCD40DFC372F8', X'3223EB2FC016B14A8B6BC6755D85048C', X'3223EB2FC016B14A8B6BC6755D85048C', 0, 'javascript:', 'Sparerib Soup with Watercress and Dried Figs', 2, NULL, 0, 'Sparerib Soup with Watercress and Dried Figs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=805', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound watercress<BR>1 Tablespoon coarse salt<BR>6 dried white figs<BR>4 teaspoons sweet almonds<BR>4 teaspoons bitter almonds<BR>1 pound spareribs<BR>4 slices fresh ginger<BR>salt to taste<BR><B><I>Preparation:</B></I><BR>1. Rinse the watercress and soak it in two quarts of water mixed with the salt for one hour, then rinse well under running water, and set aside.<BR>2. Rinse the spare ribs, then pour two quarts of boiling water over them.<BR>3. Bring three quarts of water to the boil, add all the ingredients and cook just below the boiling point for twenty minutes, then reduce the heat to simmer, and cook for two more hours. Remove all bones, add salt as needed, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'413BBD5F9DB8E2458F9465AE0FE6C296', X'5CC7C63332F48B43A076F10D2D7E6BC7', X'5CC7C63332F48B43A076F10D2D7E6BC7', 0, 'javascript:', 'Spicy Lamb Soup', 2, NULL, 0, 'Spicy Lamb Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1281', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>1 large onion, minced<BR>2 red chili peppers, seeded, and minced<BR>2 large cloves garlic, minced<BR>6 slices fresh ginger, minced<BR>2 pounds lamb, diced<BR>1/2 teaspoon crushed cardamon<BR>1/2 teaspoon ground cinnamon<BR>1 stick cinnamon<BR>1 bay leaf<BR>2 teaspoons crushed or ground cumin<BR>1 teaspoon ground turmeric<BR>1/2 teaspoon Sichuan pepper, crushed<BR>1 teaspoon curry paste<BR>4 Tablespoons Chinese brown slab sugar<BR>2 Tablespoons lemon juice<BR><B><I>Preparation:</B></I><BR>1. Heat wok or large pot, add oil, then the onion and chili peppers.<BR>garlic and ginger, and stir-fry for one minute, then add the lamb and brown for two minutes.<BR>2. Add the cardamon, both kinds of cinnamon, bay leaf, cumin, turmeric, Sichuan peppercorns, and curry paste, and stir for one minute then add eight cups of water, bringing to the boil, then reduce the heat, cover the pan, and simmer for one hour.<BR>3. Stir in the brown sugar and the lemon juice, and simmer for another fifteen minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4144F3B04857254BA3EC6F4F1F0EEEEE', X'1D7FADA96277C2469498D1D0CCDE1C18', X'1D7FADA96277C2469498D1D0CCDE1C18', 0, 'javascript:', 'Steamed Winter Melon with Virginia Ham', 2, NULL, 0, 'Steamed Winter Melon with Virginia Ham', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=42', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound slice of wintermelon<BR>1/2 pound of cooked Virginia ham<BR>3 slices of fresh ginger, minced<BR>2 scallions, minced<BR>Chinese parsley (optional)<BR><I><B>Preparation:</I></B><BR>1. Slice the wintermelon into one and a quarter inch square pieces. Remove the peel. Then slit each piece of melon but do not cut all the way through.<BR>2. Slice the ham thinly then cut into one inch by one inch and a quarter inch strips. Slip the ham strips into the slits in the wintermelon, then place these on a deep platter and sprinkle with ginger, scallions, and Chinese parsley (also known as cilantro).<BR>3. Steam the prepared wintermelon on a heat-proof platter until the melon is translucent, about twenty minutes. Serve hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9DF25D0E7FFDC14BBE2A7FC208E2C452', X'78B9C637C7F7B0448D9F79943817CBCC', X'78B9C637C7F7B0448D9F79943817CBCC', 0, 'javascript:', 'Sun-moon Mollusc, Green Radish, and Carrot Soup', 2, NULL, 0, 'Sun-moon Mollusc, Green Radish, and Carrot Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=652', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 ounces dried sun-moon mollusc (yat yuet yu), soaked for one hour<BR>10 and 1/2 ounces lean pork<BR>1 green radish (about half pound), peeled<BR>1 large carrot, peeled<BR>4 honey dates (mut chou)<BR>1 Tablespoon almonds, peeled<BR>1 Tablespoon bitter almonds, peeled<BR>1 piece dried tangerine peel<BR>1 teaspoon salt (optional)<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon dark soy sauce<BR><B><I>Preparation</I></B><BR>1. Brush sand and any other grit from sun-moon mollusc and rinse and drain. Put them in boiling water and boil for two minutes, drain and set them aside, and discard the water.<BR>2. Put the pork in a fresh batch of boiling water, boil for two minutes, drain, pour several cups of boiling water over the pork, drain and discard that water.<BR>3. Cut the radish and the carrot in finger-width strips, then angle cut them, and set aside in two batches.<BR>4. Bring ten cups of water to the boil, add the molluscs and pork and half the carrots and radishes, the dates, all the almonds, tangerine peel and the salt, if used. Boil for ten minutes, then reduce heat to low and simmer for three hours. Add the remainder of the radishes and carrots, and the soy sauces and simmer for another hour.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C9F992ADF0475F40B5B00ED0387C91D8', X'1481180BF0D82448BD4C640AB628C80E', X'1481180BF0D82448BD4C640AB628C80E', 0, 'javascript:', 'Sweet Potato Scallop Soup', 2, NULL, 0, 'Sweet Potato Scallop Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1556', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 dried scallops, soaked in two cups warm water for two hours. Reserve the water and then shred them.<BR>1 quart chicken stock<BR>1 cup peeled sweet potatoes cut into half-inch cubes<BR>1 carrot, peeled and cut into half-inch cubes<BR>3 Tablespoons fresh lily bulbs, roots discarded, rinsed, and the leaves separated<BR>dash ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Bring chicken stock to the boil, add water from the soaked scallops, and the sweet potato and carrot cubes. Reduce the heat and simmer until the carrots are tender (about half an hour), then mash all the vegetables.<BR>2. Add the lily bulb leaves and the dried scallop shreds and bring to the boil, then reduce the heat and simmer for ten minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'89B756435653B743AC19C0B72510D59F', X'18C987490301344787ECB9C52FEBA8C1', X'18C987490301344787ECB9C52FEBA8C1', 0, 'javascript:', 'Swimming Shrimp Dumplings', 2, NULL, 0, 'Swimming Shrimp Dumplings', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=412', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 quart soy milk<BR>1/4 pound fresh shrimp, without shells and veins, minced fine <BR>3 Tablespoons minced pork<BR>1 teaspoon thin soy sauce<BR>24 wonton skins<BR>1 Tablespoon fresh or frozen peas<BR>a dash of sesame oil<BR>1 teaspoon dry-fried minced red onions (optional)<BR><I><B>Preparation:</I></B><BR>1. Simmer the milk for fifteen minutes.<BR>2. While it is cooking, mix shrimp, pork, and soy sauce. Put a scant tablespoon of this mixture on one skin and fold as you would a wonton, wetting the edges with water before folding them. Repeat until all the dumplings are made.<BR>3. Bring milk to the boil, add the shrimp dumplings and after they have floated to the top, remove the pan from the heat and add peas and sesame oil. Pour this milk into individual soup bowls and sprinkle each of them with a little red onions or another tasty garnish, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'490DC6D8613AE14CA26B97C15007626B', X'0E1B1CE268DFB44987F930BD851E55CA', X'0E1B1CE268DFB44987F930BD851E55CA', 0, 'javascript:', 'Swordbelt Soup', 2, NULL, 0, 'Swordbelt Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1536', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup dried swordbelt mushrooms<BR>1 teaspoon salt<BR>1 cup minced beef<BR>1 teaspoon thin soy sauce<BR>1/2 teaspoon sugar<BR>dash ground white pepper<BR>1 teaspoon cornstarch<BR>1 stalk Chinese celery, cut into one-inch long thin strips<BR>3 two-inch pieces green and white large radish, peeled and thinly sliced, then blanched for one minute until pliable<BR>1/2 cup bok cai leaves<BR><B><I>Preparation:</B></I><BR>1. Soak swordbelt mushrooms with salt and two cups water for one hour, then rinse and discard the water. Set the longest ten mushrooms aside, cutting off their caps and reserving them, mince the other mushrooms.<BR>2. Mix minced beef, soy sauce, sugar, pepper, and cornstarch, and spread this on ten or twelve slices of the blanched radish. Roll them up and tie each one with a mushroom.<BR>3. Bring five cups water to just under a boil, gently add the tied mushroom packages, the celery sticks, and simmer for five minutes, then add the bok cai leaves and simmer another four or five minutes, then serve the mushroom packages, one to a person, some leaves, and some liquid in a soup bowl to each person.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7EAAC7E1617E4440B5F92E965E811AAA', X'A4BD626CDC3C3244B86F40B5BA3985DF', X'A4BD626CDC3C3244B86F40B5BA3985DF', 0, 'javascript:', 'Ta Pian Lu', 2, NULL, 0, 'Ta Pian Lu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=315', '<TD><RECIPE><B><I>Ingredients suggested for eight to ten people:</B></I><BR>1 pound boneless chicken breast, sliced in quarter-inch slices<BR>1/2 pound pork loin, thinly sliced<BR>1/2 pound flank steak, thinly sliced<BR>1/2 pound calves liver, thinly sliced<BR>1/2 pound shrimp, veins removed<BR>1/2 pound Chinese cabbage<BR>1/2 pound spinach<BR>1/2 pound clams, or oysters or both, shells discarded<BR>1/2 pound garland chrysanthemum leaves<BR>2 firm squares tofu, cut into one-inch squares<BR>4 or more cups chicken stock<BR>lots of scallion and Chinese chive knots<BR>2 ounces cellophane noodles, soaked<BR>1 white chrysanthemum<BR><B><I>Suggested sauces:</B></I><BR>Thin and thick soy sauces, red vinegar, sesame oil, Chinese rice wine, oyster sauce, minced scallions, fermented soybean cubes (red, white, or both) mixed with a little water.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6FAF286FC2F3D44DA34D364B2785C465', X'13CFFB7D82A5FA4DB76B4D824136D8F2', X'13CFFB7D82A5FA4DB76B4D824136D8F2', 0, 'javascript:', 'Tea-smoked Congee', 2, NULL, 0, 'Tea-smoked Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=967', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons Lapsang Souchong dry tea leaves<BR>1 cup glutinous rice flour<BR>2 Tablespoons granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Boil the tea leaves with one cup of water for three to five minutes, then strain the leaves and discard them or use them to make some tea.<BR>2. Bring 6 cups of water to the boil, add the tea water, rice flour, and the sugar, and reduce the heat and simmer for twenty to thirty minutes until thick. Pour this into individual rice bowls, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D9CAAA58151C9B4B967B5F51A39CEF8F', X'19BF27B35F3B214A82FE1611EBB53BA1', X'19BF27B35F3B214A82FE1611EBB53BA1', 0, 'javascript:', 'Ten-ingredient Hot Pot', 2, NULL, 0, 'Ten-ingredient Hot Pot', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=316', '<TD><RECIPE><B><I>Ingredients suggested for eight to ten people:</B></I><BR>1 pound flank steak, thinly sliced<BR>1/2 pound pork loin, thinly sliced<BR>2 pork kidneys, veins removed and thinly sliced<BR>1/2 pound pork tripe, cooked for half hour, then cut into one-inch pieces 4 ounces bamboo shoots, thinly sliced<BR>8 large shiitake mushrooms, soaked and stems revoved and simmered for five minutes, thinly sliced<BR>1/2 pound lettuce leaves<BR>1/2 pound spinach leaves<BR>12 or more fish balls or 1/2 pound firm-fleshed white fish, cut in one-inch pieces<BR>4 or more cups beef broth<BR><B><I>Suggested sauces:</B></I><BR>Chili sauce, light soy sauce, rice wine, ground Sichuan peppercorns, mustard sauce made with dry mustard, sesame oil and water, hoisin sauce, minced coriander, minced garlic</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5495F932E8BF3848B285473432EB8534', X'67CC37C10378324F8E8227B4D3FD2C43', X'67CC37C10378324F8E8227B4D3FD2C43', 0, 'javascript:', 'Thupka', 2, NULL, 0, 'Thupka', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=396', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound pork bones with some pork meat still attached<BR>1/4 teaspoon salt<BR>1 cup rice<BR>1 and 1/2 cups thinly sliced onions<BR>2 Tablespoons chopped fresh ginger<BR>1 Tablespoon oil<BR><B><I>Preparation:</B></I><BR>1. Bring eight cups of water ro the boil, add pork bones and skim. After all debris is removed, reduce the heat, add the salt, and simmer uncovered for half an hour.<BR>2. Add rice and all the remaining ingredients and simmer for an additional hour. Stir occasionally, and add extra hot water, if it gets too thick. Also add more salt, if needed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7893D60E5A698545ACA81E9E79913796', X'475E7B534A108B47B0FD5163258EA372', X'475E7B534A108B47B0FD5163258EA372', 0, 'javascript:', 'Tofu Soup, Monk Wen Si Style', 2, NULL, 0, 'Tofu Soup, Monk Wen Si Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1027', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon chicken fat<BR>2 shallots, sliced thin<BR>2 garlic cloves, peeled and sliced thin<BR>1 quart chicken broth<BR>1 pound tofu, cut into thin slices<BR>3 Tablespoons fresh bamboo shoot, cut into cut into matchstick-size pieces<BR>1 cloud ear fungus, soaked and slivered<BR>1 dried Chinese mushroom, soaked, stem discarded, and sliced thin<BR>2 asparagus, cut into two inch lengths, then into matchstick-size pieces<BR>2 Tablespoons vegetarian ham, cut into matchstick-size pieces<BR>1 small carrot, peeled and cut into matchstick-size pieces<BR>1/2 teaspoon salt<BR>2 Tablespoons cornstarch mixed with the same amount of cold water<BR>2 sprigs coriander cut into half-inch lengths<BR>1 teaspoon sesame oil<BR>dash ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Heat large wok or a big heavy pot. Add the chicken fat and the shallot and garlic pieces. Stir-fry for one minute, then remove and discard them.<BR>2. Put the chicken broth into the pot, and bring it to the boil. Add the tofu, bamboo shoot, cloud ear, Chinese mushroom, asparagus, vegetarian ham, and carrot pieces. Reduce the heat and simmer for five minutes.<BR>3. Add the salt, cornstarch mixture, coriander pieces, sesame oil, and the ground white pepper. Stir, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CE477D8C512C164C8ED02F37ACD3C733', X'EB762049E9D9D74EBE718C8095102A1C', X'EB762049E9D9D74EBE718C8095102A1C', 0, 'javascript:', 'Tomato and Lamb Soup', 2, NULL, 0, 'Tomato and Lamb Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2370', '<TD><RECIPE><B><I>Ingredients</I></B><BR>2 Tablespoons vegetable oil<BR>1 Tablespoon minced fresh ginger<BR>3 cloves fresh garlic, minced<BR>1/2 pound minced lamb shoulder<BR>1 teaspoon coarse salt<BR>2 ripe tomatoes, peeled<BR>1/2 pound egg noodles, cooked and drained when almost soft<BR>1 cup chopped fresh spinach<BR>1/4 cup chopped fresh coriander<BR><B><I>Preparation</B></I><BR>1. Heat oil in a large soup pot, and then stir-fry ginger, garlic, and the lamb until the meat is no longer pink.<BR>2. Add salt and tomatoes and six cups of water and bring to the boil, then reduce heat to a simmer.<BR>3. Next, add the noodles and the spinach, and when the spinach has wilted, add the coriander and out all in a pre-heated soup tureen, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FAC24889741BC444B1523A17B6289988', X'9E9872826154FA4AB448FE1E8EA99CCD', X'9E9872826154FA4AB448FE1E8EA99CCD', 0, 'javascript:', 'Tomato, Bean Curd, and Chicken Leg Mushroom Soup', 2, NULL, 0, 'Tomato, Bean Curd, and Chicken Leg Mushroom Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1325', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound tomatoes<BR>4 firm bean curd cakes (one pound), frozen<BR>1 fresh chicken leg mushroom, about five ounces<BR>2 Tablespoons garlic oil (see note below)<BR>1/2 inch thick piece peeled ginger, smashed<BR>3/4 teaspoon salt<BR>1/4 cup diced shallots, made into quarter-inch pieces<BR>2 Tablespoons diced salted turnips, made into quarter-inch pieces<BR>1/8 teaspoon white peppercorns<BR>1/2 cup finely sliced scallions<BR><B><I>Preparation:</B></I><BR>1. Place tomatoes in a large heat-proof bowl and pour four cups boiling water over them. After a minute or two, pour off the hot water and fill the bowl with cold water, then remove their skins and cut them into half-inch pieces, separating the liquid from the pieces.<BR>2. Remove the bean curd from the freezer, allow to thaw for thirty minutes, and cut them into one-third by one-third-inch dice.<BR>3. Heat wok or large pot then add garlic oil, ginger, and salt. Stir for half minute then add shallots, stir, and reduce heat to medium. Cook the shallots for two or three minutes until they soften. Then raise the heat to high and add the tomatoes and mix well. Cook uncovered, stirring occasionally, for five minutes or until the tomatoes soften and break apart. 4. Add stock, mix well, then the tomato liquid, mix again, and bring to the boil, then lower the heat to medium and let tomatoes cook for five minutes or until very soft. Add turnips, mix well, and cook for one minute, then raise heat to high and add bean curd and peppercorns, rerun to the boil, then turn off the heat source. Transfer to a heated tureen and serve.<BR><B>Note:</B> This mushroom is more commonly known as and abalone or king oyster mushroom. To date we have never seen it by this name.)<BR><B><I>For the Garlic Oil:</B></I> Heat wok, add one and a half cups peanut oil and two and one quarter cups (about three heads) of peeled and thinly sliced garlic. Stir and allow oil to come almost to the boil, then immediately reduce heat to low (watch carefully as you may need to lift the pan until the heat source cools somewhat). Simmer on low for ten minutes, then strain. Store the oil in a closed sterilized jar and refrigerate; you can also store the garlic refrigerated in another sterilized jar. Use as needed. The oil stays for up to three months, the garlic pieces for up to two months.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'96D2B03675FE874F98FFA68B3976DAF3', X'8A9FFFAA62F6474A830B415D8DAE343B', X'8A9FFFAA62F6474A830B415D8DAE343B', 0, 'javascript:', 'Tonic Duck Soup', 2, NULL, 0, 'Tonic Duck Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=372', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 whole duck, its skin removed<BR>1 ounce caterpillar fungus<BR>1 Tablespoon dark soy sauce<BR>7 cups chicken stock<BR>1/4 pound spinach, minced and steamed for one minute<BR>salt and pepper to taste<BR><B><I>Preparation:</B></I><BR>1.Put duck, fungus, soy sauce and stock in a large pot, cover and simmer for three hours.<BR>2. Remove duck from the liquid, cool slightly, and tear the meat in small pieces, discard the bones, and return it to the pot. Add spinach and seasonings, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5EC130FF465A4942960AEF1C0A3B3BD1', X'D8B4A1127AF14C419CD674D7EFB14CF7', X'D8B4A1127AF14C419CD674D7EFB14CF7', 0, 'javascript:', 'Tripe Hot Pot', 2, NULL, 0, 'Tripe Hot Pot', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=317', '<TD><RECIPE><B><I>Ingredients suggested for eight to ten people:</B></I><BR>1 pound beef tripe, precooked for half-hour, then cut into two-inch pieces<BR>1 pound pork tripe, precooked for half-hour, then cut into two-inch pieces<BR>1/2 pound brains, simmered for 20 minutes then cut into two-inch cubes<BR>1/2 pound calves liver, cut into one-inch cubes<BR>1/2 pound congealed duck blood, cut in one-inch cubes<BR>1/2 pound flank steak, thin sliced<BR>1/2 pound beef tendons, pre-simmered one hour<BR>1/2 pound firm tofu, cut in one-inch cubes<BR>2 ounces wood ear fungus, soaked and cut, if needed<BR>1 to 2 sections lotus root, peeled and thinly sliced<BR>6 to 8 knotted Chinese leek bundles<BR>4 or more cups spring water<BR>1 pound white or daikon radish, peeled and in one-inch pieces<BR>2 ounces cellophane noodles, soaked for half-hour in warm water<BR>8 scallions, tied in knots<BR>1/4 pound enoki mushrooms<BR><B><I>Suggested Accompaniments:</B></I><BR>Chili oil, minced garlic, minced fresh ginger, chili paste with or without garlic, Sichuan peppercorns coarsely ground, ground white pepper, crushed cloves, mustard sauce made mixing dry mustard with oil or water</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ECD1DBC2E808F542B900D2DA0D0651C2', X'082CBB1C60357549B9531C3F78FED487', X'082CBB1C60357549B9531C3F78FED487', 0, 'javascript:', 'Tupka Broth', 2, NULL, 0, 'Tupka Broth', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=200', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>8 ounces beef, brisket preferred<BR>1/4 teaspoon whole Sichuan peppercorns<BR>6 cloves garlic, smashed with side of a cleaver<BR>2 two-inch pieces fresh ginger, smashed with side of a cleaver<BR>3 quarts water 1/2 teaspoon coarse salt or 1/4 teaspoon table salt<BR><I><B>Preparation:</B></I><BR>1. Boil all ingredients until the meat is soft; about an hour.<BR>2. Cool the meat about an hour, then shred and refrigerate it. Refrigerate the liquid separately, and when cool, remove the fat that congealed on the top.<BR><B>Note:</B> When ready to serve, mix meat and broth, and add cooked <I>tentuk</I>, if desired.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BE9FC24E7533CD42A1462263DEAB3348', X'ADAF178FA92BBA4F978B7EC3279F63B6', X'ADAF178FA92BBA4F978B7EC3279F63B6', 0, 'javascript:', 'Turtle Soup', 2, NULL, 0, 'Turtle Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=682', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 turtle, about one pound, internal organs removed<BR>1 fresh lily bulb, petals separated<BR>12 red dates, pitted and cut in half<BR>12 dried longans<BR>1 Tablespoon rice wine<BR>1/2 teaspoon salt<BR>2 egg whites, beaten very stiff<BR><B><I>Preparation:</B></I><BR>1. Remove the shell of the turtle Cut the body into six to ten pieces.. Cut off the legs and cut each in half. Pour four cups of boiling water over all the pieces, shell included.<BR>2. Bring two quarts of water to the boil, and put in turtle meat and shell, return to the boil, then just before it does, reduce heat to simmer, and simmer uncovered for two hours.<BR>3. Add dates, longans, wine, and salt and another cup of boiling water, and simmer another two hours.<BR>4. Wet your hands and shape the egg whites into two love birds or any other design. Serve the soup hot with the egg-white design on top.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'344A8D0693C72C4BB9CF5D3821A29DAB', X'921760B9FCB01243831AE4F97A2D83C6', X'921760B9FCB01243831AE4F97A2D83C6', 0, 'javascript:', 'Vegetable Bean Curd Soup', 2, NULL, 0, 'Vegetable Bean Curd Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1490', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 cups chicken stock, divided into two and three cup amounts<BR>2 ounces celery cabbage cut in half-inch wide strips<BR>1 ounce carrots, peeled and sliced thinly<BR>1 piece bean curd, cut into quarter-inch slices<BR>1 ounce snow peas, ends removed<BR>1 and 1/2 teaspoons salt<BR>1 Tablespoon Chinese rice wine<BR>1/3 teaspoon ground pepper<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or pot and add two cups chicken soup stock and bring this to the boil. Add celery cabbage, carrots, bean curd pieces, and the snow peas and simmer for one minute, then remove vegetables from the wok or pot and discard the soup.<BR>2. Put the other three cups of chicken soup stock into the wok or pot and bring to the boil. Add the vegetables and bean curd and return the stock to the boil then add salt, wine, and the pepper, and bring to the boil once more before adding the sesame oil. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'FFDD1A5FACB3D14CB169FAFC10D0B1BA', X'5B4C5D169A05C146A3FE063CDE6FA7D9', X'5B4C5D169A05C146A3FE063CDE6FA7D9', 0, 'javascript:', 'Vegetarian Chicken Soup with Lou Han', 2, NULL, 0, 'Vegetarian Chicken Soup with Lou Han', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1298', '<TD><RECIPE></B><I>Ingredients:</B></I><BR>4 ounces vegetarian chicken, cut in uneven chunks<BR>2 cloves garlic, peeled<BR>6 Chinese large black mushrooms, soaked, stems removed, and cut in quarters<BR>1 lou han, soaked, then cut in six to eight pieces<BR>1 Tablespoon dark soy sauce<BR>1 stick cinnamon<BR>4 black dates, pits removed<BR>1 Tablespoon goji berries<BR>3 Tablespoons Chinese yam, diced<BR><B><I>Preparation:</B></I><BR>1. Put all ingredients in a pot with ten cups of water and just bring this to below the boil, then simmer it for one hour.<BR>2. Remove the lou han, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'895733E43926084DAFEFB080590BB078', X'B946F5933869AC41A563E5EB43BB2276', X'B946F5933869AC41A563E5EB43BB2276', 0, 'javascript:', 'Vegetarian Duck and Noodle Soup', 2, NULL, 0, 'Vegetarian Duck and Noodle Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=756', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 section vegetarian duck<BR>2 one-ounce packages Fujiianese (or Amoy-style) very thin wheat noodles (they usually come in one pound packages, use two of the tied amounts)<BR>1/2 pound sweet potato, grated or cut into very thin slivers<BR>5 cups vegetable broth or water <BR><B><I>Preparation:</B></I><BR>1. Sliver outside and inside parts of the vegetarian duck.<BR>2. Heat the stock or water to just below the boiling point. Add vegetarian dusk, the noodles, and the grated sweet potato and simmer for fifteen minutes. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'5DBBFA641E562348BCC34599FE00714F', X'D5A4DC98B468ED478FDBA0B90C098E71', X'D5A4DC98B468ED478FDBA0B90C098E71', 0, 'javascript:', 'Vegetarian Duck Soup with Tonic Flavor', 2, NULL, 0, 'Vegetarian Duck Soup with Tonic Flavor', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=17', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>half package or 1 and 1/2 ounces tonic mix<BR>8 small pieces deep-fried gluten<BR>pinch sugar<BR>1/2 teaspooon white pepper<BR>2 teaspoons light soy sauce<BR>4 ounces winter bamboo shoots<BR>2 teaspoons rice wine<BR>1 or 2 teaspoons toasted sesame oil<BR><B><I>Preparation:</I></B><BR>1. Soak tonic ingredients for fifteen minutes in cool water, then drain them.<BR>2. Add one quart water and all ingredients except the sesame oil into a heavy three quart pot. Bring to the boil, reduce heat and simmer covered for thirty minutes.<BR>3. Add sesame oil and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D5ADD51826FF33469211C0C4F010CAEF', X'21F0817B6A2B374FB111561E93474FB3', X'21F0817B6A2B374FB111561E93474FB3', 0, 'javascript:', 'Vegetarian Hot and Sour Soup', 2, NULL, 0, 'Vegetarian Hot and Sour Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1689', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/3 to 1/2 pound silken doufu, cut into thin slivers<BR>2 Chinese black mushrooms soaked, stem removed, then slivered<BR>1 small carrot, shredded<BR>1/4 cup shredded bamboo shoot<BR>2 stems cilantro, cut into small pieces<BR>1 small stem preserved mustard green, rinsed and minced<BR>1 Tablespoon thin soy sauce<BR>2 Tablespoons Chinese black vinegar<BR>1 teaspoon sugar<BR>1 teaspoon sesame oil<BR>1 Tablespoon cornstarch mixed with one tablespoon cornstarch<BR>1 salted egg yolk, mashed well<BR><B><I>Preparation:</B></I><BR>1. Mix doufu, slivered black mushrooms, shredded carrot and bamboo shoot, cilantro stems, and mustard green pieces.<BR>2. Bring eight cups water to a boil, add the vegetable mixture, then reduce the heat and simmer for four minutes before adding soy sauce, vinegar, sugar, sesame oil, and the cornstarch mixture. Bring back to the boil, stir in the salted egg yolk, and boil for one minute, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BBCEBC73D55C264B99513558776AD91C', X'E178EC9F5F769E4A9B5877138486BED5', X'E178EC9F5F769E4A9B5877138486BED5', 0, 'javascript:', 'Watercress Soup', 2, NULL, 0, 'Watercress Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=82', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 and 1/2 quarts chicken stock<BR>10 Chinese dates (jujubes)<BR>12 very thin slices fresh gingerroot, peeled<BR>1 and 3/4 pounds watercress<BR>1 Tablespoons rice wine or dry sherry<BR>2 Tablespoons light soy sauce<BR>1/4 teaspoon salt, or to taste<BR><B><I>Preparation:</B></I><BR>1. Bring chicken stock to the boil, add dates and ginger root. Lower heat and simmer for twenty minutes.<BR>2. Add the rest of the ingredients and simmer another five monutes or until the watercress is tender. Serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4D21805A25D9494C9DE1C00247681230', X'16F1E9FF855B8F44B29500E7EF89402A', X'16F1E9FF855B8F44B29500E7EF89402A', 0, 'javascript:', 'West Lake Soup I', 2, NULL, 0, 'West Lake Soup I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1976', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound chopped or ground beef<BR>4 cups chicken stock or bouillon<BR>1 teaspoon coarse salt<BR>1/4 teaspoon ground white pepper<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon thin soy sauce<BR>2 Tablespoons cornstarch mixed with one tablespoon of cold water<BR>2 egg whites, beaten until frothy but not stiff<BR>½ cup water shield, fresh and blanched or drained from the bottle it comes in<BR><B><I>Preparation:</B></I><BR>1, Blanch the beef in one cup of the stock, then strain its contents and set it aside.<BR>2. Put the soup and the strained stock or bouillon into a pot. Add the salt and pepper, the rice wine, soy sauce, and the cornstarch mixture and bring this to the boil, then immediately turn the heat to low.<BR>3. Next put the meat back into the liquid, add the egg whites, and in half a minute add the water shield. Stir, and then in one minute, serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CA1572C59CDB8D4A95617324680ACE28', X'DB6C37E1F39D2742BF75262059415AD0', X'DB6C37E1F39D2742BF75262059415AD0', 0, 'javascript:', 'West Lake Soup II', 2, NULL, 0, 'West Lake Soup II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=609', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>6 cups concentrated low-sodium broth<BR>2 Tablespoons cornstarch mixed with one tablespoon cold water<BR>3 to 4 Tablespoons ground beef<BR>2 Tablespoons water chestnut flour<BR>2 Tablespoons thin soy sauce<BR>2 Tablespoons dry sherry<BR>1 teaspoon sesame oil<BR>3 egg whites, beaten until almost thick<BR>1 scallion, minced<BR>1 Tablespoon minced Smithfied ham<BR>1 hard-cooked egg yolk, chopped fine<BR><B><I>Preparation:</B></I><BR>1. Prepare a large steamer that will hold a heat-proof soup tureen.<BR>2. Heat broth and mix in the cornstarch mixture, bring to the boil, then turn off the heat.<BR>3. Mix the beef with the waterchestnut flour, soy sauce, sherry, and sesame oil. Add to the soup mixture. Stir well making sure that there are n lumps in the meat. 4. Bring soup back to serving temperature and put it in the serving tureen. 5. Add the egg whites and stir, then decorate with scallion, ham, and egg yolk. Put this tureen in a steamer for five minutes. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E6C7B9DBEAAF184E90344F1AEB8EF9EB', X'843A0EBAA7FFB14C8C5FCD60DC15C8D9', X'843A0EBAA7FFB14C8C5FCD60DC15C8D9', 0, 'javascript:', 'West Lake Soup with Water Shield', 2, NULL, 0, 'West Lake Soup with Water Shield', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2009', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1/4 pound chopped or ground beef<BR>4 cups chicken stock or broth<BR>1 teaspoon coarse salt<BR>1/4 teaspoon ground white pepper<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon thin soy sauce<BR>2 Tablespoons cornstarch mixed with one tablespoon cold water<BR>2 egg whites, beaten until frothy but not stiff<BR>1/2 cup water shield vegetable, fresh and blanched, or drained from the bottle it comes in, the liquid discarded<BR><B><I>Preparation</I></B><BR>1. Blanch the beef in one cup of the stock or broth, then strain the liquid, and set it aside.<BR>2. But the soup and the strained liquid in a large pot, add the salt and pepper, rice wine, soy sauce, and the cornstarch mixture, and bring to the boil, then immediately turn the heat to low.<BR>3. Now return the beef to the pot, add the egg whites, and in half a minute add the water shield, stir several times, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3A3BD59C5119C047A119D8544578FB99', X'A230D609975BDF4B91ABA535812836C6', X'A230D609975BDF4B91ABA535812836C6', 0, 'javascript:', 'White, Red, and Green Soup', 2, NULL, 0, 'White, Red, and Green Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=288', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1 sheet green seaweed laver<BR>8 fish balls, cut into quarters<BR>1/2 cup lightly mashed silken bean curd<BR>4 cups chicken stock, brought to the boil<BR>1 slice fresh ginger, minced very fine<BR>1/4 cup pomegranate seeds<BR><B><I>Preparation:</B></I><BR>1. Rinse the sheet of seaweed, slightly dry it over a heat source, then shred it.<BR>2. Pour the boiling soup into a tureen or large bowl, add the rest of the ingredietns, and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D43ABD18948BEC43807BC6AB1DBC9D78', X'6B4957BB6D08504A9963E00158920EE1', X'6B4957BB6D08504A9963E00158920EE1', 0, 'javascript:', 'Wild Mushroom Soup', 2, NULL, 0, 'Wild Mushroom Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=820', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound assorted wild mushrooms, cut in quarters<BR>1/2 fresh lingzi mushroom (about a two-inch square piece cut into small pieces)<BR>1 Tablespoon vegetable oil<BR>1 onion, sliced<BR>1 two-inch piece Yunnan or Smithfield ham, minced<BR>1 small Chinese yam, peeled and diced<BR>2 egg yolks, beaten until light yellow<BR>2 Tablespoons garlic shoots, minced<BR>salt and pepper, to taste<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan and add all mushrooms, the onion slices, and the oil. Fry for two minutes, then add the minced ham and fry another minute before adding the yam cubes. Fry this for two minutes, then add six cups of boiling water, and bring to the boil, then immediately reduce the heat.<BR>2. Simmer for half an hour, then add egg yolks, garlic shoots, and salt and pepper, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'222C68263BF86446BBDD9D6423138762', X'C7D663852A419B4D87851215801A3FCE', X'C7D663852A419B4D87851215801A3FCE', 0, 'javascript:', 'Wine Chicken Soup', 2, NULL, 0, 'Wine Chicken Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=8', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 ounce wood ears<BR>6 dried black mushrooms<BR>1 three to five pound chicken, cut into two-inch pieces<BR>12 jujubes (Chinese red dates)<BR>1 three-inch piece of ginger, peeled and cut into half-inch slices<BR>10 cups water<BR>2 cups rice wine<BR>2 teaspoons salt or to taste<BR><B><I>Preparation:</I></B><BR>1. In a small bowl, cover the wood ears with boiling water. Let them soak for twenty minutes or until softened. Remove hard ends and discard. Slice into half-inch pieces and set aside. <BR>2. In a small bowl, cover the black mushrooms with boiling water. Let them soak for twenty minutes or until softened, then remove the stems and slice them in half. Set aside. <BR>3. In a large pot, add chicken, wood ears, mushrooms, jujubes, ginger, water, wine and salt and bring to a boil. Then reduce heat to a simmer. As needed, skim off any foam and fat. Cook for one hour or until the chicken is tender. Serve the chicken and broth in bowls. <BR>Serves 10</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'90FD80A54FF8074792B869D88D9360C2', X'280306AD10C1A4479D099D46C5AE6E43', X'280306AD10C1A4479D099D46C5AE6E43', 0, 'javascript:', 'Winter Melon Casserole', 2, NULL, 0, 'Winter Melon Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2154', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 wintermelon, about the size of a volleyball, cut off the top about one-inch down, discard the seeds and any spongy matter<BR>½ cup dried black mushrooms, soaked for twenty minutes, stems discarded, soaking water reserved<BR>3 ounces canned bamboo shoots<BR>18 ounces chicken soup or stock<BR>4 slices fresh ginger<BR>1 teaspoon bouillon powder<BR>4 ounces medium-size shrimp, each cut in four to eight pieces<BR>3 ounces Virginia ham, slivered<BR>4 ounces chicken breast meat, diced<BR>2 teaspoons cornstarch mixed with one Tablespoon cornstarch<BR><B><I>Preparation:</B></I><BR>1. Put winter melon in a large heat-resistant bowl and put that bowl into a steamer basket.<BR>2 Put the mushrooms, bamboo shoots, chicken soup, ginger, and the bouillon powder into the melon, cover it and steam for two hours.<BR>3. Remove cover from the melon and add the shrimp, ham, chicken, the mushroom water, and the cornstarch mixture. Recover the melon and steam for another fifteen minutes.<BR>4. Serve scooping out some of the melon and its soup and ingredients into every person''s bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'237904C481FBA147B37BD5524FCAC9B1', X'A849E0F953B6524990F736C2776D9072', X'A849E0F953B6524990F736C2776D9072', 0, 'javascript:', 'Winter Melon Casserole', 2, NULL, 0, 'Winter Melon Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=41', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 wintermelon (about the size of a volley ball)<BR>5 dried mushrooms<BR>1 chicken breast<BR>6 medium shrimp<BR>1 teaspoon cornstarch<BR>2 quarts chicken broth, brought to the boil<BR>1/4 cup bamboo shoots<BR>4 slices ginger root<BR>1 Tablespoons Virginia ham slivers<BR>salt and pepper to taste<BR><I><B>Preparation:</I></B><BR>1. Cut off the top one and a half inches from the top of the wintermelon Save it because it will be used as the lid of the melon when cooking it. Remove seeds and spongy pulp from inside of the melon and then place the melon in a large bowl so that it is well supported in an upright position.<BR>2. Soak the mushrooms in hot water for fifteen minutes, then squeeze out the water and cut them into thin slices.<BR>3. Bone and slice the chicken breast.<BR>4. Shell and devein the shrimp, then dice them into one-quarter-inch pieces.<BR>5. Combine chicken breast, shrimp, and cornstarch and mix well.<BR>6. Place a heat-proof bowl with the melon in it into a steamer. Then pour boiling chicken broth into the melon.<BR>7. Add bamboo shoots, soaked drained mushrooms, ginger, and ham and cover the melon with its top. Next cover the steamer and steam this for about four hours or until the melon flesh is soft. Check freqently to see that there is water in the bottom section of the steamer.<BR>8. Remove the lid of the melon and add the chicken/shrimp mixture and the salt and pepper. Replace the melon and steamer lids and steam an additional fifteen minutes. <BR>9. Remove the bowl from the steamer, take off the melon lid, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0A8DF9485D0178499AB8A91E0D5CA762', X'53DDD583A3C84043868DC784CC245A61', X'53DDD583A3C84043868DC784CC245A61', 0, 'javascript:', 'Winter Melon Chowder', 2, NULL, 0, 'Winter Melon Chowder', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1088', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 can abalone (two to a can)<BR>1 box silken tofu<BR>5 ounces dried scallops, soaked in warm water for one hour<BR>6 cups chicken stock<BR>1 small winter melon, peeled and diced<BR>6 Chinese red dates<BR>1 packet (about ten) bamboo fungus, soaked, veils removed, and cut into short lengths<BR>1 handful <I>honshimeiji</I> mushrooms<BR>1 cup green peas<BR><B><I>Preparation:</B></I><BR>1. Dice abalone and the tofu, and shred the soaked scallops.<BR>2. Boil the chicken stock for ten minutes, add the winter melon pieces and the dates, and turn the heat down low. Simmer until the winter melon cubes are translucent.<BR>3. Add the remaining ingredients and return to a boil for one minute. Serve in individual bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EBD930C90F92DD468A80A9244A07EC34', X'04FF171EC323F841A5B3C66EEC81E39D', X'04FF171EC323F841A5B3C66EEC81E39D', 0, 'javascript:', 'Winter Melon Soup', 2, NULL, 0, 'Winter Melon Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1796', '<TD><RECIPE>Peel a piece of winter melon and cut it into two-inch pieces. Cook them in chicken stock or broth along with a few dried shiitake mushrooms cut up, fresh ginger pieces, scallions, and a few Smithfield ham slivers for an hour, then serve.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'190990D5926A244EB08EC1F584E06C9C', X'252B30B091C1584F966BAEBEE6FDDCF3', X'252B30B091C1584F966BAEBEE6FDDCF3', 0, 'javascript:', 'Wintermelon and Seafood Casserole', 2, NULL, 0, 'Wintermelon and Seafood Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1670', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 five-pound winter melon<BR>20 dried lotus seeds, soaked for one hour or until soft<BR>3 Chinese black mushrooms, soaked, their stems discarded, and cut into slivers<BR>1/4 cup Yunnan ham. Slivered<BR>1 chicken liver, cut into thin strips and blanched one minute<BR>2 cups chicken stock<BR>1 Tablespoon thin soy sauce<BR>2 Tablespoons Chinese rice wine<BR>1/2 teaspoon salt<BR>1 cup crab meat, cartilage removed, cut into pieces<BR>6 shrimp, peels and veins discarded, each cut into two pieces, the long way<BR>2 teaspoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Cut top off the melon, seed it, and put it into a serving bowl and steam it with the melon cover for twenty minutes.<BR>2. Remove melon cover and add the lotus seeds and the black mushrooms and ½ cup boiling water and steam for twenty minutes not returning the melon top to the steamer.<BR>3. Add ham, chicken livers, stock, soy sauce, rice wine, and salt and the melon top and steam another hour and fifteen minutes without the melon cover. 4. Add crab meat and shrimp and cover the melon with the melon cover and steam another twenty minutes. Add the sesame oil and bring the melon with its contents and its cover to the table, then serve contents and some melon flesh to each diner.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DCA62A7E9B362A41B3A099A30760C005', X'9A01C8A4256A28419B644429AEBC6EB2', X'9A01C8A4256A28419B644429AEBC6EB2', 0, 'javascript:', 'Wintermelon Soup', 2, NULL, 0, 'Wintermelon Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=343', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pound wintermelon, peeled and cut into two-inch cubes.<BR>6 monkey head mushroomssoaked in warm water for half an hour.<BR>6 cups chicken stock<BR>2 ounces ground lean pork<BR>1 egg white<BR>1 Tablespoon cornstarch<BR>1 slice fresh ginger, minced<BR>2 Tablespoons wolfberries, optional<BR><B><I>Preparation:</I></B><BR>1. Rinse the monkey head mushrooms, then simmer then in two cups of water for half an hour. Drain and cut into half-inch cubes.<BR>2. Put wintermelon, cooked monkey head mushrooms, and the stock into a large pot and bring it to the boil. Simmer for one hour.<BR>3. Mix pork, egg white, and cornstarch and make this mixture into twenty small meatballs then add them to the simmering soup along with the giinger and the wolfberries. Cook an additional fifteen minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1244742B3CF6E342B5AFD870922CB05B', X'32B0E5469A0FE54EBD4E6288EAD2794C', X'32B0E5469A0FE54EBD4E6288EAD2794C', 0, 'javascript:', 'Wolfberries and Matrimony Vine Soup', 2, NULL, 0, 'Wolfberries and Matrimony Vine Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=786', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 cups chicken broth<BR>6 Tablespoons wolfberries<BR>3 cups matrimony vine greens, removed from their stems<BR>1/2 teaspoon salt<BR><B></I>Preparation:</B></I><BR>1. Bring chicken broth to a simmer, add wolfberries, and simmer for ten minutes.<BR>2. Add greens, discarding the stems, and the salt and bring liquid to the boil. Cook two minutes, remove from heat, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A05A4149837FBE498A56C8F4CAA86890', X'84DC4A30837F0F48AAEC063E662530D9', X'84DC4A30837F0F48AAEC063E662530D9', 0, 'javascript:', 'Wonton Noodle Soup', 2, NULL, 0, 'Wonton Noodle Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=524', '<TD><RECIPE><B><I>Ingredients for filling wontons:</B></I><BR>1/2 pound ground fatty pork<BR>1/2 pound small or medium shrimp, peeled, deveined, and coarsely chopped<BR>6 water chestnuts, peeled and finely chopped<BR>1/2 egg white<BR>1 Tablespoon soy sauce<BR>salt and pepper, to taste (1/2 teaspoon each is a good amount)<BR>1 package square then wonton wrappers<BR><B><I>Preparation of the wontons:</I></B><BR>1. In a large mixing bowl, combine pork, shrimp, water chestnuts, egg white, soy sauce, and the salt and pepper.<BR>2. Lay a wonton wrapper on a dry work surface and place one teaspoon of the raw mixture in the center of it. Then lighlty bruch the edges of the wrapper with water and bring up the corners to gently pinch and seal the wonton dough. Repeat with the remaining wrappers.<BR>3. Cover with plastic wrap or a very lighly dampened towel and keep refrigerated until ready for use. The filled wonton wrappers can be frozen on a cookie sheet and them firm, transferred to an airtight container.<BR><B><I>Ingredients for the soup base:</I></B><BR>1 three-pound chicken<BR>1/2 inch piece fresh ginger, peeled and sliced<BR>1 bunch scallions, cleaned and trimmed<BR>1 small onion, peeled and quartered<BR><B><I>Prepartion of the soup base:</B></I><BR>1. Wash and wipe chicken dry, then cut into four pieces and place it in a large stockpot with eight quarts of cold water.<BR>2. Bring to the boil and reduce the heat so that it simmers. Remove any scum that comes to the surface, then simmer for two hours. Remove from heat, allow to cool and then strain. Keep refrigerated overnight and remove excess fat in the morning. Strain soup and use chicken in other dishes, as needed.<BR><B><I>Ingredients for Wonton Soup:</B></I><BR>1/2 of the above reserved soup base<BR>1/4 inch piece of fresh ginger, peeled and sliced<BR>1 eight ounce package of fresh noodles<BR>1 teaspoon sesame oil<BR>24 prepared filled and sealed wontons<BR>1/2 bunch green leafy Chinese vegetable<BR>1/4 bunch scallion, washed and finely sliced, roots removed<BR>salt and ground white pepper, to taste<BR><B><I>Preparation of Wonton Noodle Soup:</B></I><BR>1. In a large saucepan, bring reserved soup to the boil, then add ginger and reduce the heat to a simmer.<BR>2. In another large pot, bring one gallon of water to the boil, add the noodles and cook until tender (about two minutes). Drain and rinse under cool running water, and drizzle with the sesame oil to keep the noodles from sticking.<BR>3. Using the same pot, bring three quarts of water to the boil, add the wontons and simmer over medium heat, stirring occasionally. Simmer until they float to the surface, then remove the wontons and divide them into eight bowls.<BR>4. Dip the noodles into the wonton water, then divide them into the same bowls. Divide the vegetables into the same bows.<BR>5. Remove ginger from the soup and ladle some soup on top of the vegetables in all of the bows, sprinkle with the scallions, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'34A03F028DA05D4EAD18E7D075872CF4', X'4FE7135F8138E54BA690224C103113BC', X'4FE7135F8138E54BA690224C103113BC', 0, 'javascript:', 'Wrapped Congee', 2, NULL, 0, 'Wrapped Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=973', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups glutinous rice, soaked overnight in four cups water<BR>4 tender fresh lotus leaves<BR>30 lotus seeds, pre-cooked or canned<BR>3 Tablespoons sugar<BR>thin string cut into sixteen ten-inch strips<BR><B><I>Preparation:</B></I><BR>1. Put rice, lotus leaves, and lotus seeds in a large pot. Cover them with water, bring to a boil, and simmer uncovered for one hour.<BR>2. Remove leaves and set them aside to cool.<BR>3. Drain rice and lotus seeds, stir, then mash them just a little.<BR>4. Put two stings one each going across the rice bowl in two directions. Then, cut lotus leaves into quarters, and take two to line each of eight rice bowls.<BR>5. Put one-eighth of the rice-lotus seed mixture into each bowl, close the leaves over the rice mixture, and using the stings tie them closing the rice mixture within. Place them on a serving platter, and serve this summer dish at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'52231A02298FF04B94688C5FB9F56CA3', X'8E24284D799A1A4F91102F906B33D43B', X'8E24284D799A1A4F91102F906B33D43B', 0, 'javascript:', 'Yangrou Paomo Bread and Mutton Soup', 2, NULL, 0, 'Yangrou Paomo Bread and Mutton Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1165', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>4 to 6 pounds mutton, lamb or beef bones, broken into smaller pieces<BR>2 medium onions, chopped<BR>6 cloves garlic, peeled and smashed<BR>1 small hot pepper, minced<BR>1 Tablespoon grated fresh ginger<BR>1/2 pound wheat noodles<BR>2 and 1/2 teaspoons salt<BR>1 teaspoon ground pepper<BR>1 teaspoon cumin<BR>1/2 pound lamb, sliced thinly<BR>1 cup green onions, finely minced<BR>1 cup coriander leaves, minced<BR>4 cups hard bread or Chinese crullers, cubed<BR><I>jaio zi</I>, two to four per person<BR><B><I>Preparation:</B></I><BR>1. Place bones, onions, garlic, hot pepper and ginger in a large saucepan, then cover with water two inches above the bones, and bring to boil. Cook covered on medium to low heat for four hours, adding more water as necessary.<BR>2. Cook the noodles according to directions on their package, rinse in cold water, then set them aside.<BR>3. Strain and discard bones and vegetables, and then return the broth to the saucepan, adding water if necessary to make eight cups of liquid. Stir in salt, pepper and cumin and bring this back to the boil, reduce heat and cook over medium heat for five minutes. Let rest for five minutes.<BR>4. Divide remaining ingredients and the drained noodles, except the bread, evenly into eight large soup bowls and then pour the broth over the ingredients, stir in and equal amount of bread into each bowl and serve piping hot with ginger sauce and <I>jiao zi</I> on the side.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EDA736C2CD5BA142AC64B4821D7DD60B', X'B359AE8BF22BF84A93F3CFCB122BEE29', X'B359AE8BF22BF84A93F3CFCB122BEE29', 0, 'javascript:', 'Yellow Eel and Ginger Congee', 2, NULL, 0, 'Yellow Eel and Ginger Congee', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1802', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/3 pound yellow eel, boned, gutted, skin removed and discarded, then rinsed<BR>1/2 teaspoon coarse salt<BR>1 teaspoon thin soy sauce<BR>dash of ground white pepper<BR>1 teaspoon sesame oil<BR>1/4 cup rice<BR>3 Tablespoons cloud ear mushrooms, soaked until soft, their water drained and discarded<BR>2 dried lily flowers, soaked until soft, their water also discarded<BR>1 slice fresh ginger<BR>2 scallions, slivered, and divided<BR><B><I>Preparation:</B></I><BR>1. Cut the eel in half-inch pieces. Mix it with the salt, soy sauce, white pepper, and the sesame oil, and set aside.<BR>2. Rinse the rice, then soak it for half an hour before draining it and discarding its water.<BR>3. Sliver the cloud ear fungi, the ginger, half the scallion, and the pre-soaked lily flower, then mix them together.<BR>4. Put rice in a large pot with eight cups of water. Simmer for forty minutes, then add the eel mixture and the lily flower mixture and simmer another ten minutes. Serve sprinkling the remaining scallion on top.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F5996CA870770F4A89F1106985DD9F98', X'7B944DFFC41CFA439578D4F470954348', X'7B944DFFC41CFA439578D4F470954348', 0, 'javascript:', 'Yellow Soup', 2, NULL, 0, 'Yellow Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1680', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound lamb leg meat<BR>3 large (brown) cardamon<BR>2 cups Basmati rice<BR>3 carrots, cut up<BR>1 lamb rib chop<BR>1 cup cooked chickpeas, skinned if you have the patience<BR>1 teaspoon turmeric<BR>2 teaspoons ground ginger<BR>A pinch of saffron<BR>Salt, to taste<BR>A dash of vinegar, to taste<BR>Cilantro leaves, for garnish<BR><B><I>Preparation:</B></I><BR>1. Boil the lamb with the cardamoms, then add the rice and carrots.<BR>2. When about half cooked, add the small lamb rib chop and the other ingredients except the cilantro.<BR>3. Garnish with the cilantro just before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2480DA27C8456C458C539B374AA04CFE', X'0BA306E4ED04E24BBE1F58E1663238EE', X'0BA306E4ED04E24BBE1F58E1663238EE', 0, 'javascript:', 'Yi Jin''s Dai-inspired Rice Noodle Soup', 2, NULL, 0, 'Yi Jin''s Dai-inspired Rice Noodle Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1741', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound round dried rice noodles<BR>1 and ½ Tablespoons peanut or vegetable oil<BR>2 Tablespoons coarsely chopped fresh garlic<BR>2 Tablespoons coarsely chopped fresh ginger<BR>2 teaspoons dried red chili flakes<BR>4 ounces minced pork<BR>salt and freshly ground black pepper, to taste<BR>4 ounces fresh ripe red tomatoes, cut into quarters<BR>2 pints chicken stock<BR>1 and 1/2 Tablespoons thin soy sauce<BR>2 teaspoons Chinese Shao Xing rice wine<BR>3 Tablespoons finely minced scallions<BR><B><I>Preparation:</B></I><BR>1. Soak rice noodles for twenty-five minutes in warm water, drain, and boil in salted water for five minutes, then drain and set aside.<BR>2. Heat a wok or frypan and when hot, add oil, then the garlic, ginger, and chili flakes and stir-fry for half minute, then add the pork, and salt and pepper, and stir-fry for three minutes.<BR>3. Add the tomatoes, the stock, soy sauce, and the rice wine and reduce the heat, cover, and simmer for five minutes, then uncover and add the rice noodles and simmer for three minutes more. Stir in the scallions, and after one minute, serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7C34A4327CB8B84BA4F697C5120E8FC3', X'2DBB7C67F6C95D4CBB62274DD2A15213', X'2DBB7C67F6C95D4CBB62274DD2A15213', 0, 'javascript:', 'Yin Yang Health Soup', 2, NULL, 0, 'Yin Yang Health Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1370', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound pork shin meat, cut into one-inch cubes<BR>2 Tablespoons minced ginseng root<BR>5 caterpillar/cordyceps fungi, scalded in boiling water<BR>5 Chinese black dates, each cut into four pieces<BR>10 canned lotus seeds<BR>3 slices fresh ginger, cut into thin shoestring pieces<BR>1/4 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Put cubes of pork shin into boiling water and scald them there for two minutes. Rinse them in boiling water and put into a large clean pot.<BR>2. Add two cups of boiling water to the clean pot, and add the ginseng, dates, lotus seeds, ginger, and salt and simmer for ninety minutes, Taste, and add additional salt, if needed, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3EA7860DB6F13243A5D7482CB6F1348F', X'FE57ECB2DCF54B41ACC37416F19F5B31', X'FE57ECB2DCF54B41ACC37416F19F5B31', 0, 'javascript:', 'Zhanjiang Casserole Lauren', 2, NULL, 0, 'Zhanjiang Casserole Lauren', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1048', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large pork chop or four large pork ribs<BR>3 cloves whole garlic, peeled and lightly crushed with side of a cleaver<BR>4 Chinese black mushrooms, soaked, stems removed, and cut in quarters<BR>1 cup leafy <I>goji</I> greens (or substitute amaranth leaves)<BR>1 or 2 preserved duck eggs (<I>pidan</I>), peeled and then diced<BR><B><I>Preparation:</B></I><BR>1. Into a stove-top casserole, add pork, garlic, mushrooms and five cups of water. Cook just below the boil until the meat is extra tender, and the water turns more cloudy than clear. Remove meat and bones and discard both. Strain, if necessary.<BR>2. Add <I>goji</I> greens and cut up duck eggs, and simmer for five minutes, then serve. <P></P></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0BAA11D74E279248AB240A8927592848', X'DDDBB246B6BF23449AA1737534B8B026', X'DDDBB246B6BF23449AA1737534B8B026', 0, 'javascript:', 'Anhui Turtle with Ham', 2, NULL, 0, 'Anhui Turtle with Ham', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1376', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 softshell turtle (no more than two pounds)<BR>1 scallion, tied in a knot<BR>2 slices fresh ginger, peel left on, ginger scored on both sides of each slice<BR>2 Tablespoons Chinese rice wine<BR>6 medium size Chinese black mushrooms, soaked for half an hour, their stems discarded<BR>2 Tablespoons vegetable oil<BR>4 scallions, cut into one-inch pieces<BR>8 cloves fresh garlic, peeled<BR>2 Tablespoons white rock sugar<BR>2 Tablespoons peeled minced fresh ginger<BR>3 Tablespoons thin soy sauce<BR>3 Tablespoons chicken stock<BR>1 Tablespoon Chinese white vinegar<BR>1 Tablespoon tomato paste or ketchup<BR>1/8 teaspoon ground white pepper<BR>3 Tablespoon Yunnan (or Smithfield) ham, minced<BR>2 Tablespoons water chestnut flour mixed with three tablespoons cold water<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Head removed and discarded, then cut in half. Its soft material under shell scraped away and discarded, then blanched for two minutes, that water discarded, as well.<BR>2. Put turtle in deep bowl with knotted scallion, slices of fresh ginger, and rice wine, and steam these together over rapidly boiling water for fifteen to twenty minutes (the longer time if about two pounds, the shorter time if one and a half pounds); then remove from steamer and discard anything other than the turtle, and chop the turtle into three-inch pieces.<BR>3. Heat oil and fry scallion pieces and whole garlic cloves for two minutes, be sure they do not burn, then add the turtle, the whole mushrooms, rock sugar, minced ginger, soy sauce and chicken stock, also the vinegar, tomato paste or ketchup, and the ground white pepper. Stir this well, but gently until all ingredients are well-mixed, then reduce heat and simmer for five minutes or until all liquid is absorbed.<BR>4. Add ham and cornstarch mixture, and stir until thickened and the sauce is clear before putting everything into a pre-heated serving bowl. Sprinkle with the sesame oil, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C750C51E575DFB4380B501B898599406', X'F2446A8600D6744E97BBCDEF86B9F97F', X'F2446A8600D6744E97BBCDEF86B9F97F', 0, 'javascript:', 'Bear''s Paw in Brown Sauce', 2, NULL, 0, 'Bear''s Paw in Brown Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=55', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 Bear''s paw or 1 pound beef or another meat<BR>1 Tablespoon oil<BR>5 scallions, tied in knots<BR>5 slices ginger root<BR>1 pound chicken, dark meat only<BR>3 scallops, shredded<BR>1/2 pound pork, cut it into 10 or 12 pieces<BR>1 Tablespoon diced ham<BR>1/2 cup Shaoxing wine<BR>1 Tablespoon sugar<BR>1/4 teaspoon ground Sichuan pepper<BR>1/4 teaspoon salt<BR>2 Tablespoons cornstarch mixed with an eaual amount of water<BR><I><B>Preparation:</I></B><BR>1. Cut bear paw or meat into two-inch pieces and soak in warm water and covered for twenty-four hours and in the refrigerator. Change water three or four times.<BR>2. Drain then simmer in clean water for about six hours or until tender. Change water at least once early in the cooking. Discard liquid.<BR>3. Heat the wok, add oil and fry scallions and ginger one minute. Next add bear, chicken, scallops, pork, and ham. Fry for one minute then add wine, salt and pepper. Simmer for an hour with 1 cup water (or stock).<BR>4. Add cornstarch mixture. Bring to boil and cook until thickened.<BR>Serves eight to ten.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EAB8D5766315784CB8669D5AE2BA8B72', X'0323305E17AAF04C8EC7D15B4E37AB31', X'0323305E17AAF04C8EC7D15B4E37AB31', 0, 'javascript:', 'Beech Mushrooms and Meat', 2, NULL, 0, 'Beech Mushrooms and Meat', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1609', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound ostrich (or another red meat), chopped<BR>1 teaspoon dark soy sauce<BR>1 teaspoon sugar<BR>1 teaspoon chicken bouillon powder<BR>3 ounces beech mushrooms, rinsed, then coarsely minced<BR>3 Tablespoons cornstarch mixed with two tablespoons cold water<BR>3 sprigs fresh coriander, minced<BR>1 Tablespoon fresh ginger, minced<BR>2 egg whites<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix ostrich, soy sauce, sugar, and bouillon powder and set aside for half an hour.<BR>2. Bring two cups water to the boil, add meat and mushrooms and as soon as water returns to the boil, turn off the heat source and stir in cornstarch mixture and stirring it until thickened.<BR>3. Remove pot from the heat source, stir in minced coriander and fresh ginger and the egg whites and stir for one minute. Then add sesame oil and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D766944DDF42024C8DE4D8E8F792E2A7', X'DA95B956C66B7249B06244B225B918C2', X'DA95B956C66B7249B06244B225B918C2', 0, 'javascript:', 'Beef Tongue, Chinese Style', 2, NULL, 0, 'Beef Tongue, Chinese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=522', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 to 5 pound fresh beef tongue (not a pickled one)<BR>1/2 cup thin soy sauce<BR>2 star anise<BR>1 Tablespoon sugar<BR>3 Tablespoons dry sherry<BR>1 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. In a Dutch oven or a pot with a tight-fitting lid, pour in enough water to cover the tongue, then bring the contents to a boil. After fifteen minutes, remove tongue and cool in cold water. Trim gristle and bone from large end and skin the tongue. Discard both the skin and the water.<BR>2. Place tongue, soy sauce, star anise, sugar and sherry in a large pot or Dutch oven. Bring this to a boil, cover it, and simmer for three hours or until the tongue can be easily punctured using a chopstick. Add water when and as needed, and adjust the salt taste, adding more, if necessary.<BR>3. Cool, then slice the tongue. It can be served hot or cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'934AAF1AA2D4DA498AFCB0BBC82624E4', X'6CC3FD4F56B0924EACDE8BF40954D111', X'6CC3FD4F56B0924EACDE8BF40954D111', 0, 'javascript:', 'Beef Tripe Hot Pot, Mongolian Style', 2, NULL, 0, 'Beef Tripe Hot Pot, Mongolian Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2129', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons beef fat<BR>1/2 pound beef tripe, washed and cut into thin slices<BR>1/4 pound beef kidney, cut into thin slices<BR>1/4 pound beef liver, cut into thin slices<BR>1/4 pound beef steak, cut into thin slices<BR>5 Tablespoons spicy broad bean sauce<BR>3 tablespoons finely minced fresh ginger<BR>4 teaspoons Sichuan peppercorns, crused using the side of the cleave<BR>5 Tablespoons minced chili peppers<BR>1 teaspoon coarse salt<BR>1/2 cup rice wine<BR>1 Tablespoons Mai Tai or another Chinese liquor<BR>8 cups beef stock<BR>1/4 cup yak butter<BR>3 cups different kinds of radish and greed vegetables<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or fry pan and render the beef fat, then remove any left-over pieces, and finely mince these curled pieces, and return them to the wok or pan.<BR>2. Add each of the meats individually, and fry them one at a time, first the tripe, then the kidney, etc., each for about one minute, removing them to a plate when done, allowing them to cool quickly, leaving any rendered fat in the pan.<BR>3. After these meats are barely cooked and cooled, add the broad bean sauce, the ginger, Sichuan peppercorns, and the chili peppers and sir-fry them together for one minute, then add the salt, wine, liquor, stock, and yak butter and then add this to a hot pot and bring to the boil.<BR>4. Put the radish and the greens on separate plates around the hot pot; the slightly cooked meats, as well.<BR>5. Give each diner a long-handled fork and an individually long-handled strainer basket, a soup bowl and soup spoon, and chopsticks; see that the hot pot has a long-handled ladle.<BR>6. Now each diner can cook the meats and vegetables they prefer, to the doneness they desire, and as desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B6538FB9B483E14B99B67A7FE97BDB42', X'9A145A941613D04BA74310938A2CF6D7', X'9A145A941613D04BA74310938A2CF6D7', 0, 'javascript:', 'Beef Tripe with Mustard Sauce', 2, NULL, 0, 'Beef Tripe with Mustard Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1229', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound tripe, beef preferred<BR>1 Tablespoon mustard oil or deli mustard<BR>1 teaspoon chicken bouillon powder<BR>1 Tablespoon black vinegar<BR>I Tablespoon sesame oil<BR>1 Tablespoon sugar<BR>1 Tablespoon vegetable oil<BR>4 cloves fresh garlic, peeled and minced<BR>3 stalks Chinese celery, cut in two-inch lengths, then cut in thin strips<BR>1 red hot chili pepper, seeded and cut in thin strips<BR>2 Tablespoons coarsely chopped fresh coriander<BR><B><I>Preparation:</B></I><BR>1. Blanch tripe for one minute, then put into cold water for three minutes, and drain. Cut into two-inch ling thin strips and set aside.<BR>2. Mix bouillon powder, black vinegar, sesame oil, and sugar.<BR>3. Heat wok or fry pan, add oil, then the garlic and stir-fry for one minute. Add the tripe, celery, and the chili pepper strips and cook for one more minute before adding the bouillon mix. Stir-fry for another minute, add the coriander, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0C17E921BBC92D4797E982441DCF3161', X'FC17BB3C1C1441418560D9EFCD9C3559', X'FC17BB3C1C1441418560D9EFCD9C3559', 0, 'javascript:', 'Bell Fruit and Jellyfish Salad', 2, NULL, 0, 'Bell Fruit and Jellyfish Salad', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=727', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 whole or 1/2 pound jellyfish, soaked in cold water for one hour<BR>3 bell fruits, cut into strips<BR>1/2 teaspoon salt<BR>1/2 teaspoon sugar<BR>1 Tablespoon Chinese black vinegar<BR>1 Tablespoon sesame oil<BR>2 scallions, minced (we slivered ours on an angle)<BR><B><I>Preparation:</B></I><BR>1. Blanch jellyfish in boiling water for thirty seconds, then immediately put in very cold water, drain and again put into very cold water. Refrigerate covered overnight. Drain the next day, and cut into very thin strips.<BR>2. The next day, mix bell fruit strips with salt, sugar, and the black vinegar and and gently stir in the fruit strips, and the jellyfish strips.<BR>3. Heat the sesame oil, pour over the entire mixture, stir well, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F54FFA5F7A42504D9CEDBC3BC8632352', X'3221F03866EDDC4B969BC0AFAD566BAC', X'3221F03866EDDC4B969BC0AFAD566BAC', 0, 'javascript:', 'Bird''s Nest Stew', 2, NULL, 0, 'Bird''s Nest Stew', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1127', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup dry birds nest, soaked overnight<BR>1 cup chicken broth<BR>1 Tablespoon minced Yunnan or Smithfield ham<BR>3 Tablespoons bean spouts, tails removed, and coarsely chopped<BR>1/2 teaspoon salt<BR>1 Tablespoon rendered chicken fat<BR>1 Tablespoon cornstarch mixed with half-tablespoon cold water<BR>1 teaspoon minced fresh coriander leaves<BR><B><I>Preparation:</B></I><BR>1. Simmer birds nest in the chicken broth in a two-quart saucepan for ten minutes, then drain and set aside.<BR>2. When ready to serve, return this to the saucepan and add the ham, bean sprouts, salt, and chicken fat and simmer for three minutes. Then add cornstarch mixture, bring to just below the boil, and pour into a deep bowl.<BR>3. Sprinkle with the coriander and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'81F94777A206354790B2C5A7A033B140', X'66AABF217D2E174BAC82397CAC4E610E', X'66AABF217D2E174BAC82397CAC4E610E', 0, 'javascript:', 'Bird''s Nest Cups', 2, NULL, 0, 'Bird''s Nest Cups', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1546', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 Tablespoons softened butter<BR>3 Tablespoons powdered sugar<BR>1 egg yolk<BR>1/2 cup all-purpose flour<BR>1 Tablespoon dry bird''s nest<BR>2 Tablespoons crushed white rock sugar<BR>3 egg whites<BR>1 cup whole milk<BR>5 Tablespoons granulated sugar<BR><B><I>Preparation:</B></I><BR>1. Knead butter and powdered sugar until shiny, then add egg yolk and the flour and knead for three to four more minutes before covering this and refrigerating it for two hours.<BR>2. Soak birds nest covered in hot water for two hours, then use tweezers to pick out any non-bird''s nest pieces and discard them. <BR>3. Add half cup boiling water and the rock sugar and simmer on low heat for fifteen minutes.<BR>4. Remove dough from the refrigerator and press flat, then roll it and cut into four-inch rounds one-quarter-inch thick. Put these into three-inch fluted tart forms.<BR>5. Cook egg white, granulated sugar and milk until all sugar is dissolved, then cool for half hour before pouring this into the tart shells; do so gently holding a spoon just above the dough.<BR>6. Bake in a pre-heated 350 degree oven for fifteen minutes, remove and cool for half an hour before putting the bird''s nest on top of the baked filling. Serve warm or at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'961D750C470F65468B6EC5381575F17B', X'BD126DFB4C485549B4FE6D55DE2E3B61', X'BD126DFB4C485549B4FE6D55DE2E3B61', 0, 'javascript:', 'Bird''s Nest for Breakfast', 2, NULL, 0, 'Bird''s Nest for Breakfast', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=215', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 ounces bird''s nest pieces<BR>2 conpoy (dried scallops), soaked two hours then steamed for twenty minutes, cooled, and then shred<BR>1 cup long grain rice<BR>4 Tablespoons glutinous rice, soaked for two hours in hot water<BR>2 Tablespoons Smithfied ham, shredded<BR>1 teaspoon sesame oil<BR>1/2 teaspoon salt (optional)<BR>8 pigeon eggs, simmered in water near the boiling point for five minutes, peeled and cut in half<BR>2 Tablespoons coriander leaves, torn into small pieces<BR><I><B>Preparation:</I></B><BR>1. Prepare birds nest as in step one in the recipe above.<BR>2. Bring ten to twelve cups of water to the boil, add the conpoy shreds and both kinds of rice. Reduce heat to simmer, cover and cook for two hours. Do check periodically and add half cup of water at a time, if needed.<BR>3. Add bird''s nest pieces, ham, salt and sesame oil and simmer for ten minutes more.<BR>4. Pour into individual bowls, add one pigeon egg to each and sprinkle coriander leaves on top, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BF44F85F62C7D2409F37DF098BE45E4C', X'EC9F56C9368E004AA768C16FE6516DD6', X'EC9F56C9368E004AA768C16FE6516DD6', 0, 'javascript:', 'Bird''s Nest Omelette', 2, NULL, 0, 'Bird''s Nest Omelette', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1132', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 teaspoons vegetable oil, divided into two batches<BR>3 Tablespoons dried bird''s nest fragments or pieces<BR>4 eggs<BR>1/2 teaspoon sugar<BR>1/2 teaspoon chicken bouillon powder<BR>1 Tablespoon cornstarch<BR>2 Tablespoons slivered or minced fully-cooked smoked ham<BR>1 sprig Chinese coriander leaves, leaves torn into ten small pieces<BR><B><I>Preparation:</B></I><BR>1. Brush the first batch of oil onto ten Chinese ceramic soup spoons )or small molds).<BR>2. Bring two cups water to the boil, add dried bird''s nest pieces, and boil them for three minutes, then remove with a fine strainer, and discard the water. When partially cooled, squeeze out any remaining water.<BR>3. Beat eggs for two minutes, then add sugar, bouillon powder, and cornstarch, and beat another half minute. Add birds nest pieces and stir gently before pouring into the oiled ceramic Chinese soup spoons. Carefully place these filled spoons on a steamer rack and put it over boiling water to steam for three or four minutes, no longer.<BR>4. Heat a wok or small fry pan and add the second batch of oil. Carefully slide the omelettes into the pan and fry them for one minute per side. Remove the omelettes from the pan and line them up in a thin long dish. Sprinkle the ham on them, then the coriander, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9C8811E90C696E4D8DA1A1A51F651EF9', X'F2317D52D9D004419AD156260C19339B', X'F2317D52D9D004419AD156260C19339B', 0, 'javascript:', 'Bird''s Nest Soup I', 2, NULL, 0, 'Bird''s Nest Soup I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=264', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>4 ounces bird''s nest<BR>1 shiitaki mushroom, soaked and sliced very thin<BR>6 cups very rich chicken stock<BR>1 Tablespoon minced Smithfield ham<BR>1/2 teaspoon salt<BR>1 Tablespoon Shaoxing wine<BR>1/8 teaspoon very finely minced ginger root<BR>2 ounces chicken breast minced fine<BR><I><B>Preparation:</I></B><BR>1. Wash the bird''s nest in many changes of water until all extraneous matter is removed.<BR>2. Bring bird''s nest, mushroom, stock, ham, and salt to just below the boil, then simmer until the bird''s nest is soft (about half an hour). 3. Add rest of the ingredients and simmer another ten minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EE226BE7D89E9842BD9ADF29B1002337', X'E0D3C221A71E1749904D1D3B29167459', X'E0D3C221A71E1749904D1D3B29167459', 0, 'javascript:', 'Bird''s Nest Soup II', 2, NULL, 0, 'Bird''s Nest Soup II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1128', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>6 Tablespoons pre-soaked bird''s nest<BR>1 quart chicken stock<BR>3 slices fresh ginger<BR>dash of ground white pepper<BR>1/2 cup boneless chicken breast, minced fine<BR>1 egg white<BR>2 teaspoons cornstarch mixed with one tablespoon cold water<BR>2 Tablespoons Yunnan or Smithfield ham, minced<BR><B><I>Preparation:</B></I><BR>1. Put bird''s nest, chicken stock, ginger, white pepper, and the chicken breast in a large bowl and steam over rapidly boiling water for forty-five minutes.<BR>2. Put chicken stock mixture in a three quart pot and add the egg white and the cornstarch mixture and stir well as this is brought to the boil. Add the ham and simmer for five minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E9AD78F7B3B8014DAF557FE6D97A12BB', X'24AFC1E1D23A4042A1224B7F4E02C196', X'24AFC1E1D23A4042A1224B7F4E02C196', 0, 'javascript:', 'Bird''s Nest with Rock Sugar II', 2, NULL, 0, 'Bird''s Nest with Rock Sugar II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1430', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 ounce refined bird''s nest<BR>1/2 cup rock sugar<BR><B><I>Preparation:</B></I><BR>1. Cover the bird''s nest with one quart of luke-warm water and let it stand for one to two hours until the bird''s nest is fully expanded. Then drain off the water.<BR>2. Place the bird''s nest in a covered porcelain bowl and add one quart of water and steam it for one and a half hours.<BR>3. Add the rock sugar and steam for ten more minutes. Serve hot or cold but do not use ice cubes to cool it.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D9B3E865417D9B4D814BB4BE7D70689E', X'2F8AA77B91BD6D4ABD0474BCD6D3AF58', X'2F8AA77B91BD6D4ABD0474BCD6D3AF58', 0, 'javascript:', 'Bird''s Nest, Chicken, and Tofu', 2, NULL, 0, 'Bird''s Nest, Chicken, and Tofu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1126', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons soaked birds nest pieces, coarsely chopped<BR>2 egg whites, whipped until firm<BR>1/4 pound chicken, white meat only, minced<BR>1/2 pound soft bean curd, mashed<BR>1/4 teaspoon low sodium chicken bouillon powder<BR>1/2 teaspoon sugar<BR>1/2 teaspoon vegetable oil<BR>1 cup chicken stock or water<BR>4 teaspoons cornstarch mixed with the same amount of cold water<BR>1 sprig fresh coriander, coarsely minced<BR><B><I>Preparation:</B></I><BR>1. Set aside one tablespoon of the bird''s nest pieces, and gently fold the rest of them with the egg whites, chicken, bean curd, bouillon powder, and the sugar until well mixed.<BR>2. Oil the insides of four to six Chinese teacups and fill them with the egg white mixture. Steam these over rapidly boiling water for nine minutes, then turn the firmed mixture out on a serving platter.<BR>3. Bring chicken stock and the cornstarch mixture to the boil, and when thickened, pour over the unmolded bird''s nest mixture.<BR>4. Sprinkle the set aside bird''s nest pieces and the coriander on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'03F7471D8585A04D85CFB20F33D91947', X'747203C4B6D4EB4EA23C221178F4FAD8', X'747203C4B6D4EB4EA23C221178F4FAD8', 0, 'javascript:', 'Bitter Melon and Date, Throat Soother', 2, NULL, 0, 'Bitter Melon and Date, Throat Soother', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1960', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup dried shredded bitter melon<BR>8 dried red dates<BR><B><I>Preparation:</B></I><BR>1. Bring six cups of cold water to the boil, then add the shredded bitter melon and the red dates reduce the heat to a medium simmer for half an hour, then reduce it even lower, to a low simmer and cook until the liquid is reduced to two cups.<BR>2. Then remove from the heat source and leave at room temperature or refrigerate, as preferred.<BR><B>Note</B> The Chinese traditional doctors say to drink half cup twice a day until the throat is no longer sore.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F43E2F18D119824C8689189D62DA1819', X'1FBF8969E4B02B4A95216C2639426580', X'1FBF8969E4B02B4A95216C2639426580', 0, 'javascript:', 'Bitter Melon with Conpoy', 2, NULL, 0, 'Bitter Melon with Conpoy', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1956', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 conpoy (dried scallops), soaked overnight<BR>2 bitter melons peeled, pith and seeds removed from one end, leaving the fruit whole before slicing<BR>1 cup chicken stock<BR>2 teaspoons oyster sauce<BR>1 teaspoon dark soy sauce<BR>2 Tablespoons cornstarch<BR><B><I>Preparation:</B></I><BR>1. Shred the conpoy into their natural strips and stuff these into the bitter melons.<BR>2. Put them into a bowl with the chicken stock and steam for half an hour, then remove the bowl and put the liquid into a small pot and allow to cool to close to room temperature.<BR>3. Slice the stuffed bitter melon and plate in a circle on a small platter.<BR>4. Add cornstarch to the liquid in the pot and bring to the boil and stir until it thickens, then pour over the bitter melons, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6E381098211D074C864E05A01D872EF2', X'3AFDC0875A294C4FB71404C23C31B529', X'3AFDC0875A294C4FB71404C23C31B529', 0, 'javascript:', 'Bitter Melon, Bean Curd, and Fish', 2, NULL, 0, 'Bitter Melon, Bean Curd, and Fish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1959', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>3 garlic cloves, peeled and sliced thin<BR>1 small onion, peeled, cut in half, and thin sliced<BR>1 piece of fresh ginger, peeled and minced<BR>1 pound firm doufu<BR>3 bitter melons, peeled, seeds removed, and cut into thick slices<BR>1 pound of carp or tilapia, cut into two-inch pieces<BR>1/2 cup chicken broth<BR>1 teaspoon thin soy sauce<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or fry pan, add the oil, and fry the onion and garlic until they turn brown and are very aromatic, the add the fresh ginger and stir-fry this for one minute.<BR>2. Next, add doufu and brown that it one side.<BR>3. Now, add the bitter melon on top of the doufu and the fish on top of that.<BR>4. Mix the broth, soy sauce, and sesame oil, and pour this over the fish, then cover the wok or fry pan, and simmer for three minutes. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'29BBA0AF5E5C12418446E5F17DE6D552', X'389B95C592B89D4B91686BF3E054386E', X'389B95C592B89D4B91686BF3E054386E', 0, 'javascript:', 'Bitter Melon, Pork, and Black Beans', 2, NULL, 0, 'Bitter Melon, Pork, and Black Beans', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1957', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 bitter melons, ends removed, ends, pith, and seeds discarded<BR>1/2 pound ground or finely minced pork<BR>2 teaspoons fresh garlic, peeled and chopped<BR>1 Tablespoon peeled fresh ginger, chopped<BR>1/4 cup sweet onion, chopped<BR>1/4 cup stems and leaves of fresh coriander, chopped<BR>1 fresh chili pepper, seeds removed and discarded, and minced<BR>2 Tablespoons fermented black beans, minced<BR>1 teaspoon sesame oil<BR>1 cup chicken broth<BR>20 <I>goji</I> berries (optional)<BR><B><I>Preparation:</B></I><BR>1. Blanch the bitter melon slices for two minutes in boiling water, drain, discard the water and put the slices into a bowl of ice water for two minutes. Then drain and discard the water.<BR>2. Put slices in a baking pan, cut sides up next to each other, but not touching one another.<BR>3.Mix prepared pork, garlic, ginger, onion, coriander, black beans, and sesame oil and spoon a heaping tablespoon into the center of each slice.<BR>4. Bring the broth to a boil and carefully pour into the baking pan but not on top of the meat mixture. Put one goji berry on the center top of each slice, then cover with aluminum foil and bake in a 350 degree oven for half an hour. Remove the foil, and put the stuffed slices on a preheated platter and serve.<BR><B>Note:</B> One-half cup peeled chopped shrimp can be added to the meat mixture.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1D90167053AF4A4D9B967F3C9D4A40A9', X'17DFFBD427D28F4AB4C5D4EAB4DF7752', X'17DFFBD427D28F4AB4C5D4EAB4DF7752', 0, 'javascript:', 'Borts, a Mongolian Dish', 2, NULL, 0, 'Borts, a Mongolian Dish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1809', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 or more pounds of camel or goat meat, all fat removed<BR><B><I>Preparation:</B></I><BR>1. Cut meat into strips ten to twelve inches long, two inches wide, and one-half to one inches thick, and check once again that there is no fat because it will turn rancid. <BR>2. Hang these strips on thin hooks and in a cool basement or well-ventilated area, preferably screened so there are no flying or crawling insects.<BR>3. Leave for several months, checking on them daily the first month or two.<BR>4. When ready to use them, reconstitute as many strips as needed by leaving them for half an hour in water that had been boiled and left to cool. Then cut and use as desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ECB607F56B47EA46B75CFEAC4B727212', X'FDB5ACC46D3E2A46BAEECC5A22F9B223', X'FDB5ACC46D3E2A46BAEECC5A22F9B223', 0, 'javascript:', 'Bouyei Blood Stew', 2, NULL, 0, 'Bouyei Blood Stew', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1251', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil or lard<BR>1 teaspoon minced scallion, white part only<BR>1 Tablespoon peeled minced fresh ginger<BR>1 piece brown bean curd, cut in thin slices<BR>1 cup sliced or diced mixed vegetables (i.e.: lotus root, bamboo shoots, green squash, Chinese black mushrooms, Chinese celery, soy beans, etc.)<BR>1 fish head<BR>2 cups chicken broth<BR>1/2 tomato, cut into cubes<BR>6 quail eggs<BR>1 four-inch square coagulated pork blood<BR>1/4 cup bean sprouts, heads removed<BR>1 Tablespoon Chinese black vinegar<BR>1 Tablespoon Chinese red vinegar<BR>1/4 teaspoon Sichuan pepper, coarsely crushed<BR>1 bunch fresh coriander<BR><B><I>Preparation:</B></I><BR>1. Heat oil, and stir-fry the scallion and ginger pieces until aromatic (half to one minute), then add the bean curd and the vegetables and stir-fry them together for another minute.<BR>2. Add fish head and stir once or twice before adding the broth, tomato pieces, and quail eggs. Stir well, then add pork blood and bean sprouts.<BR>3. Add both vinegars and the coarse Sichuan pepper pieces, and the home-made chili paste. Transfer this to a pre-heated soup tureen, put the coriander in the center, spoon some liquid over it, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7F005D8EDD214D4BAD2E6D676F70BFA0', X'06FE4B477A68114584E4D747CD8A7263', X'06FE4B477A68114584E4D747CD8A7263', 0, 'javascript:', 'Braised Abalone, Shandong Style', 2, NULL, 0, 'Braised Abalone, Shandong Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1793', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 teaspoon salt<BR>1 teaspoon rice vinegar<BR>1 teaspoon rice wine<BR>1 teaspoon granulated sugar<BR>2 teaspoons peeled and minced fresh ginger<BR>2 cloves fresh garlic, peeled and minced<BR>1 scallion, minced<BR>1 teaspoon cornstarch<BR>3 Tablespoons cornstarch<BR>3 Tablespoons chicken stock<BR>2 fresh abalone, cleaned and sliced very thin, then each slice cut on the diagonal<BR>2 fresh chili peppers, one red and one green, seeded and cut into thin slices<BR>2 cups vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Mix salt, rice vinegar, rice wine sugar, ginger, ginger, garlic, scallion, cornstarch and stock and bring to the boil, then remove from heat source and stir well.<BR>2. In another pot, heat oil and add the chili pepper slices and fry for half minute before removing them to paper towels to drain before adding abalone slices and frying them for two minutes, then draining them on the paper towels.<BR>3. Set oil aside for another use and return chili peppers and abalone to the pan, add the stock mixture and bring to a quick boil, stir, and serve in a pre-heated bowl.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F3F9A3ABB82F4349A242119B95A476EE', X'2AFE1128F0A4AD408F2476C587D0FC0C', X'2AFE1128F0A4AD408F2476C587D0FC0C', 0, 'javascript:', 'Braised Animal Hooves', 2, NULL, 0, 'Braised Animal Hooves', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1955', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 cow hooves, chopped into two-inch pieces<BR>1 whole soup chicken, chopped into ten or more pieces<BR>1 duck breast on the bone, chopped into four pieces<BR>1 pound spare ribs, cut into six pieces<BR>8 shallots, peeled, their ends removed<BR>1 tablespoon granulated sugar<BR>1/4 cup Shao Xing rice wine<BR>1/4 teaspoon coarsely crushed Sichuan pepper<BR>3 Tablespoons vegetable oil<BR>1 star anise<BR>10 <I>bok cai</I> hearts<BR><B><I>Preparation:</B></I><BR>1. Clean all fur off the hooves. And drop them into boiling water for two minutes, then drain and discard the water.<BR>2. Bring six quarts of water to the boil, add the hooves, chicken pieces, duck pieces, and spare ribs and boil for five minutes, then discard the water and scum, and bring back to the boil, reduce the heat, and simmer for one hour. Then add the shallots, sugar, rice wine, and the Sichuan pepper pieces and reduce the heat and simmer for four to five hours. Cool for one hour, then remove the meat from all the bones and discard them.<BR>3. Pour the liquid through a kitchen towel removing all the scum, small particles, and spices and cut the meat into small pieces if they are too large. Put them and the liquid back into the pot and reheat.<BR>4. In a fry pan, heat the an, add the oil, and the star anise and stir-fry for one minute, then add the hearts and stir-fry them for three minutes and transfer them to a small bowl or the bottom of everyones individual soup bowl.<BR>5. Serve the soup and meat in a tureen or put some on top of a <I>bok cai</I> heart in each individual soup bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DA9D4B9D8B1F964E9F796584EF34C061', X'8B815DC8815C8444AD41441F0A61B229', X'8B815DC8815C8444AD41441F0A61B229', 0, 'javascript:', 'Braised Camel Hoofs', 2, NULL, 0, 'Braised Camel Hoofs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1605', '<TD><RECIPE><B><I>Main material:</B></I><BR>One camel hoof<BR><B><I>Other materials:</I></B><BR>1.5 kg chicken, 500 g spareribs,10 hearts of green vegetables<BR><B><I>Condiments:</B></I><BR>15 g soy sauce, 3 g salt, 20 g Shaoxing wine, a little sugar, 25 g shallots<BR>and ginger, a little pepper ash, 75 g vegetable oil<BR><B><I>Cooking method:</B></I><BR>1. Soak the camel hoof in cold water, and thoroughly clean it. Simmer the camel hoof thoroughly, rub off the black membrane, soak in water again. Get rid of the smell with shallots and ginger and Shaoxing wine.<BR>2. Put camel hoof, chicken and spareribs in a pot, boil thoroughly.<BR>3. Put the wok over heat, pour in some oil, then the clear soup, and then the camel hoof. Add condiments to it. Braise for a while, then move the wok to high heat, then dish out. Edge the plate with vegetable hearts.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C74A7F19171B144580935E30E4BA2B5B', X'C522628D3F5C924F90D32006C44C4A57', X'C522628D3F5C924F90D32006C44C4A57', 0, 'javascript:', 'Braised Duck Tongues in Red Wine Lees', 2, NULL, 0, 'Braised Duck Tongues in Red Wine Lees', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=540', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>24 duck tongues<BR>1/2 cup corn oil<BR>2 cloves garlic, sliced<BR>4 slices fresh ginger, minced fine<BR>1 small hot red chili pepper<BR>1 Tablespoon fermented red wine lees<BR>1 Tablespoon dark soy sauce<BR>1/2 teaspoon sugar<BR>1 teaspoon chicken broth<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon sesame oil<BR>Preparation:</B></I><BR>1. Boil duck tongues in four cups of water for ten minutes. Remove, cool, and then remove bones and cartilage from each tongue.<BR>2. Heat corn oil and fry duck tongues for two minutes, then drain and set aside, reserving one teaspoon corn oil.<BR>3. Heat corn oil and stir-fry garlic and ginger for one minute, then add all the rest of the ingredients and half cup of boiling water. Add the duck tongues and simmer for twenty minutes, stirring during the last five minutes. The liquid should have evaporated and the sauce coated the tongues. If not, raise the heat and reduce the liquid to nil; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0DD741F3A3597E4AA028D4B826971F4A', X'BA388A92879AE14489F88726837852FD', X'BA388A92879AE14489F88726837852FD', 0, 'javascript:', 'Braised Ox Tendon', 2, NULL, 0, 'Braised Ox Tendon', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1623', '<TD><RECIPE><B><I>Ingredients: </B></I><BR>1/2 pound ox tendon<BR>1/2 cup shelled peanuts, paper exteriors removed<BR>1/4 cup peeled and diced carrots<BR>2 Tablespoons angle-sliced leek, green part only<BR>1 Tablespoon peeled and diced fresh ginger<BR>6 shallots, peeled then cut in half<BR>1 dried chili pepper, cut in half, seeds discarded<BR>1/2 teaspoon Sichuan peppercorns<BR>1 Tablespoon powdered chicken extract<BR>1 teaspoon sugar<BR>1 Tablespoon black vinegar<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon sesame oil<BR>1 Tablespoon thin soy sauce<BR>2 teaspoons vegetable oil<BR>1 Tablespoon cornstarch mixed with one tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Blanch ox tendon for one minute in boiling water, drain, and cool, and repeat once more when cooled.<BR>2. Put prepared peanuts, carrots, leeks, ginger, shallots, chili pepper in a small bowl.<BR>3. In another bowl, mix Sichuan peppercorns chicken extract, sugar, vinegar, rice wine, sesame oil, and the soy sauce.<BR>4. Heat vegetable oil then add tendon mixture and stir-fry for three minutes or until tendon is soft, then add the contents of the other bowl and simmer for five more minutes.<BR>5. Add cornstarch mixture and stir until thickened. Transfer to a platter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'941BB6C64AD4CB4E98ED66B0AA45EE7C', X'D5652A9607A838448B9A740F9B0A6778', X'D5652A9607A838448B9A740F9B0A6778', 0, 'javascript:', 'Calf Brain, Lotus Blossom, and Crab Roe', 2, NULL, 0, 'Calf Brain, Lotus Blossom, and Crab Roe', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1954', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 whole calves brains<BR>6 slices fresh ginger<BR>3 scallions, each tied in a knot<BR>1/2 cup Shaoxing wine<BR>2 lotus blossoms, steamed in a bowl in the reserved liquid<BR>1/2 cup crab meat, all cartilage removed<BR>2 egg whites<BR>1/4 cup crab roe<BR>1 large lettuce leaf, turn into one-inch pieces<BR><B><I>Preparation:</B></I><BR>1. Remove the membrane from the brains and put them in a bowl with the ginger, scallions, and the Shaoxing wine into a steamer basket over boiling water, and steam for fifteen minutes, then remove them and allow to cool, pouring out and reserving the liquid. Then cut the brains into two-inch pieces.<BR>2. In another bowl, add the cooled and steamed brains that were cut up. Mix in the crab meat, crab roe, and egg whites, and steam over rapidly boiling water for ten to fifteen minutes, the remove from the heat, stir, and put on a preheated platter lined with the pieces of lettuce leaf, and serve hot or at room temperature.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CCB4AFEA90F7EF4DB02345CB8B297660', X'57A6ECAAE9E05E4B96405FB8AAC932F4', X'57A6ECAAE9E05E4B96405FB8AAC932F4', 0, 'javascript:', 'Camel Paw with Mushrooms', 2, NULL, 0, 'Camel Paw with Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=266', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 camel paw, cleaned and soaked for one hour, whole or chopped into one-inch by four-inch pieces<BR>1 boneless chicken breast, cut into eighths<BR>10 shiitake mushrooms, soaked and cut in half<BR>1 ounce Smithfield ham, slivered<BR>1/4 cup Shaoxing wine<BR>1 and 1/2 cups chicken stock<BR>1 teaspoon salt<BR>1 teaspoon sugar<BR>2 scallions, tied in knots<BR>6 slices ginger root<BR>1/4 teaspoon Sichuan peppercorns<BR>1 whole shiitake mushroom, for garnishing (optional)<BR><I><B>Preparation:</I></B><BR>1. Put all of the ingredients into a wok or deep pot. Bring to just below the boil, reduce the heat, and simmer covered for two hours.<BR>2. Remove from heat, remove ginger root, scallion knots, and peppercorns, and discard them. Then serve.<BR><B>Note:</B> One can thicken the sauce with three tablespoons cornstarch in the same amount of water or remove the solids and reduce the liquid somewhat; and also put the whole mushroom in the center just before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'234B48E36959CC44A97D37E791F2123B', X'330074AA1C606D47B258A317D73B0550', X'330074AA1C606D47B258A317D73B0550', 0, 'javascript:', 'Chestnuts and Kidneys', 2, NULL, 0, 'Chestnuts and Kidneys', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=651', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pair pork, beef, or veal kidneys, separated and all white tissue/veins removed<BR>1 teaspoon salt, coarse-grain preferred<BR>1/2 pound peeled cooked chestnuts<BR>1/2 teaspoon salt, coarse-grain preferred<BR>1/2 teaspoon sugar<BR>1 teaspoon sesame oil<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoons corn oil<BR>3 slices fresh ginger, each cut in quarters<BR>1 carrot, peeled and angle-cut into one-inch sections<BR>2 Tablespoons water chestnut flour and 1 Tablespoon cornstarch mixed with 2 Tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Wash kidney halves in two or three changes of tepid water, then rub them with the teaspoon of coarse salt, and rinse thoroughly. Next with a small sharp knife, make shallow cross-hatched cuts on the smooth outside of each kidney, do not cut through.<BR>2. Blanch chestnuts for five minutes in boiling water, and drain. 3. Mix kidney pieces with half-teaspoon salt, the sugar, rice wine, sesame oil, and the soy sauce and set it aside for ten minutes. Then drain and reserve the liquid.<BR>4. Heat corn oil and stir-fry the ginger and carrots for five minutes. Then add the drained chestnuts and stir-fry another two minutes before adding the drained liquid from the kidneys and two cups of boiling water. Reduce the heat and simmer for half an hour.<BR>5. Add half the flour mixture and stir. If the liquid seems too thin, incorporate as much of the rest of the flour mixture, as needed making a reasonably thick sauce. Then add the kidneys, cover and almost boil for two minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F8C9A260E97A5341A6023599363A97C7', X'0CE8C0AE0B29564991C9BCA8B3D45840', X'0CE8C0AE0B29564991C9BCA8B3D45840', 0, 'javascript:', 'Chicken and Jellyfish', 2, NULL, 0, 'Chicken and Jellyfish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1733', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 chicken breast, simmered for ten minutes, then shredded finely by hand<BR>1 cucumber, peeled, sliced, then slivered into matchstick pieces<BR>8 ounces jellyfish, soaked in cold water for eight hours, then blanched in boiling water thirty seconds, then rinsed in ice water, and then slivered<BR>3 cloves garlic, peeled and slivered<BR>2 Tablespoons thin soy sauce<BR>1/2 teaspoon coarse salt<BR>1 Tablespoon sugar<BR>1 Tablespoon shaoxing rice vinegar<BR>1 teaspoon sesame oil<BR>1/2 teaspoon hot oil<BR><B><I>Preparation:</B></I><BR>1. Spread chicken around the exterior of a flat dinner-size plate.<BR>2. Spread cucumber pieces inside these pieces.<BR>3. Mix garlic, soy sauce, salt, sugar, rice vinegar, and the sesame and hot oils. Toss with the jellyfish, and put in the center of the plate, tossing in a small amount of chicken and cucumber at six and twelve oclock. When all are at the table, toss some at three and nine oclock, then let everyone serve themselves.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D85976938E629B4C9D73CB2B12BBD9DA', X'9C2FFE1581523441B36C982DD5098747', X'9C2FFE1581523441B36C982DD5098747', 0, 'javascript:', 'Chicken, Spare Ribs, and Camel Hoof', 2, NULL, 0, 'Chicken, Spare Ribs, and Camel Hoof', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=519', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 camel hoof, scraped clean then chopped into eight pieces<BR>2 Tablespoon any wine<BR>6 slices fresh ginger<BR>1/2 onion, cut in half<BR>2 to 3 pound whole chicken, cut into quarters<BR>2 pounds spareribs, separated and each rib bone cut in half<BR>2 Tablespoons dark vinegar<BR>2 Tablespoons dark soy sauce<BR>2 Tablespoons Chinese rice wine<BR>1/2 cup corn or another vegetable oil<BR>1 teaspoon sugar<BR>1/4 teaspoon salt<BR>1/4 teaspoon coarsely ground black pepper<BR>1/4 teaspoon crushed Sichuan pepper<BR>2 slices fresh ginger<BR>1 pound Chinese greens such as spinach or bok choi<BR><B><I>Preparation:</B></I><BR>1. Soak the camel parts for two hours in two cups of cold water mixed with the three ingredients that follow them. Then drain and discard the water and put the pieces into fresh water and simmer until tender. Remove any black membrane, and set them aside.<BR>2. Bring water to the boil in a large pot. Add chicken and sparerib pieces and boil for five minutes, then remove them and set them aside and discard the water.<BR>3. Cover the pieces of all three meats with fresh cold water. Add the vinegar, soy sauce, and rice wine and simmer until all meats are tender, about an hour. Remove meats. Strain and reserve the liquid.<BR>4. Heat wok, add corn oil, and when hot add the three meats and fry them until lightly browned. Remove the meat and discard the oil, then put meat back into the wok along with four cups of the reserved cooking liquid set aside in step two. Bring it to the boil, add the vegetables and cook until they are bright green and tender. Serve meats on a platter, soup in individual bowls accompanying them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'44064846C3E0AD4AAC1617C49C660E6A', X'D138F33E9856834BBC5534201D5C8155', X'D138F33E9856834BBC5534201D5C8155', 0, 'javascript:', 'Cold Jellyfish', 2, NULL, 0, 'Cold Jellyfish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1731', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 ounce plastic packet jellyfish, soaked in cold water eight hours, then blanched in boiling water half minute, and rinsed in ice water, then slivered<BR>1 cucumber, peeled and cut into matchstick-size pieces<BR>1 Tablespoon creamy peanut butter<BR>1 Tablespoon thin soy sauce<BR>2 Tablespoons shaoxing wine vinegar<BR>1 Tablespoon sesame oil<BR>6 drops hot oil<BR><B><I>Preparation:</B></I><BR>1. Prepare the jelly fish as indicated in the ingredients list.<BR>2. On a serving plate, place cucumbers matchstick pieces, and the jelly fish slivers on top of then leaving half-inch on their outside with nothing on them.<BR>3. Mix peanut butter with one tablespoon of hot water, then add vinegar, sugar, soy sauce, and sesame oil, and stir well. Next, add the hot oil and pour this mixture over the jellyfish before serving them.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DA64F5CA7FFADA48B7A6346B0CD582C7', X'437B24237E564F45AEF4D1958A812ED8', X'437B24237E564F45AEF4D1958A812ED8', 0, 'javascript:', 'Crystallized Hasma', 2, NULL, 0, 'Crystallized Hasma', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2108', '<TD><RECIPE><I><B>Ingredients</I></B><BR>6 to 8 Tablespoons white sugar crystals<BR>3 Tablespoons dried <I>hasma</I> prepared as packaged directs<BR>3 Tablespoons <I>Shao Xing</I> wine<BR>3 slices fresh peeled ginger<BR>10 crystallized cherries or another similarly prepared fruit<BR><B><I>Preparation:</B></I><BR>1. Dissolve the sugar crystals in a wok in two cups of boiing water, then add the ginger and the wine. Simmer for ten minutes efore adding the <I>hasma</I>, then steam for half an hour, changing the ater at least once.<BR>2. Add the cherries and steam for another half an hour, then serve or use as directed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BE96BC79A6E53E42A1DF18A18561DF3E', X'392D7AFB032C4540A1C862C35850FCEF', X'392D7AFB032C4540A1C862C35850FCEF', 0, 'javascript:', 'Deer Tendon Casserole', 2, NULL, 0, 'Deer Tendon Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1622', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 half pound deer tendon<BR>1 dried shrimp, soaked for half an hour, then minced<BR>1 dried scallop, soaked for half an hour, then simmered in half cup water one hour, then shredded<BR>3 Chinese black mushrooms, soaked twenty minutes, stems discarded, and quartered<BR>1/2 cup chicken, ham, or pork, slivered<BR>1 cup chicken stock<BR>1 small green pepper, spicy or not, diced<BR>1 Tablespoon Chinese rice wine<BR>2 Tablespoons cornstarch mixed with one tablespoon cold water<BR>1 teaspoon chicken fat or lard<BR>1/2 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Cut deer tendon into one inch by two inch pieces, then blanch it for one minute in boiling water, then drain and let it cool.<BR>2. Repeat this twice, and after it cools that third time, add the meat, chicken stock, prepared shrimp and scallop shreds, black mushroom and pepper pieces and simmer until tendons are tender.<BR>3. Add the cornstarch mixture, rice wine, lard, and salt and bring to the boil. When thickened, serve on a re-heated platter or bowl.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9B0F02B882CD634E85C28BB2A0F7EC0A', X'32FDFAEB47724C4EBE1FC2A02C486909', X'32FDFAEB47724C4EBE1FC2A02C486909', 0, 'javascript:', 'Duck Tongues with White Fungus', 2, NULL, 0, 'Duck Tongues with White Fungus', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=267', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1/4 cup white fungus<BR>1/2 pound duck tongues<BR>1 slice fresh ginger<BR>1 scallion<BR>1 cup chicken stock<BR>1/2 teaspoon sugar<BR>1 Tablespoon thin soy sauce<BR>1 teaspoon cornstarch<BR>1/2 teaspoon sesame oil<BR>1 small carrot, diced fine<BR>2 Tablespoons frozen green peas, defrosted<BR><I><B>Preparation:</I></B><BR>1. Soak the white fungus for two hours, then pick out and discard any dark spots and miscellaneous matter.<BR>2. Boil duck tongues with ginger and scallion in the stock for ten minutes, remove from the liquid, cool, and remove the cartilage from each of the duck tongues.<BR>3. Mix all ingredients except the carrots and peas, and simmer for one minute. Then add duck tongues and the vegetables, and heat through. 4. Remove the fungus from the pan, put the remaiing mixture in a small bowl, put the fungus on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'19ABC876EE098E4D9083F9A95C69A128', X'5CB42E939DF71144B7940D915E9B8E74', X'5CB42E939DF71144B7940D915E9B8E74', 0, 'javascript:', 'Fancy Fish Balls', 2, NULL, 0, 'Fancy Fish Balls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1125', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound Chinese spinach<BR>1/4 pound boneless and skinless flounder fillets, minced<BR>1/4 pound peeled deveined shrimp meat, minced<BR>4 Tablespoons soaked birds nest pieces, coarsely chopped<BR>1 Tablespoon low sodium chicken bouillon powder<BR>1/4 teaspoon sugar<BR>1/4 teaspoon salt<BR>2 teaspoons cornstarch<BR><B><I>Preparation:</B></I><BR>1. Steam spinach for three minutes, drain, and set on a pre-heated platter.<BR>2. Mix minced fish and shrimp and form into one-inch balls.<BR>3. Wet the mixed seafood balls and roll them in the pieces of bird''s nest, then steam them over boiling water for five minutes.<BR>4. Mix bouillon powder, sugar, slat, and cornstarch with half cup of cool water. Bring to the boil, and when it thickens, put fish balls on the spinach and pour this clear sauce over the fancy fish balls. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C749CB97741C584AAB240BF415B019A3', X'452F462CCA44CB4EAEC9C90267CA2952', X'452F462CCA44CB4EAEC9C90267CA2952', 0, 'javascript:', 'Fish Lips', 2, NULL, 0, 'Fish Lips', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=263', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>3 pounds of fish lips or cheeks<BR>1 pound bok choy or other greens<BR>1/4 cup Smithfield ham<BR>2 cups chicken stock<BR>3 Tablespoons rice wine<BR>1 teaspoon ginger root<BR>1 teaspoon minced scallion, white part only<BR>dash ground Sichuan peppercorns<BR>1 teaspoon chicken fat<BR><I><B>Preparation:</I></B><BR>1. Blanch fish lips or cheeks for ten or fifteen seconds and cool immediately in cold water, then drain.<BR>2. Blanch vegetables for one minute and chill and drain. The put into boiling water and simmer for two minutes. Drain and put on a serving plate.<BR>3. Mix ham, soup, wine, scallions, ginger root, Sichuan peppercorns, and chicken fat and heat to just below the boiling point. Add fish lips or cheeks and just barely heat through. Drain and place in the center of the greens and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CE508E58AB47094696025E705EDF3D43', X'E9B32180FB5ECF4A9FEBDE2BC3C61A14', X'E9B32180FB5ECF4A9FEBDE2BC3C61A14', 0, 'javascript:', 'Fish Lips and Barbecued Duck', 2, NULL, 0, 'Fish Lips and Barbecued Duck', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2278', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 Tablespoons vegetable oil<BR>3 sliced fresh ginger, peeled and slivered<BR>2 cloves fresh garlic, peeled and sliced thin<BR>2 scallions, angle sliced, half the greens set aside for garnish<BR>5 Chinese black mushrooms, soaked, then discard their stems and slice them thinly<BR>1 carrot, peeled and slivered, then simmered in half cup of Chicken or fish stock<BR>1/4 barbecued duck, chopped into one-inch pieces<BR>½ pound frozen fish lips, or fresh ones prepared as discussed above, and cut into one- to one and a half-inch pieces<BR>½ teaspoon coarse salt<BR>1 teaspoon granulated sugar<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon oyster sauce<BR>½ teaspoon sesame oil<BR>2 Tablespoons cornstarch in one-quarter cup cold water or stock<BR><B><I>Preparation:</B></I><BR>1. Heat oil, and when hot, add the ginger, garlic, and the white part of the scallion and stir-fry for one minute until very fragrant.<BR>2. Add the sliced black mushrooms, carrot slices, and the duck pieces, and stir well for one minute; then add the fish lips and stir for another minute or until they are hot.<BR>3. Next add the salt, sugar, soy and oyster sauces, and the sesame oil, and stir well before adding the cornstarch and water mixture. Bring this to the boil and stir until thickened, then serve in a pre-heated bowl.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A312042E77192A4DB3E2A2F8B871DBCD', X'E2AAF4DA48070E4BB7E2DCF47F022CE5', X'E2AAF4DA48070E4BB7E2DCF47F022CE5', 0, 'javascript:', 'Fish Lips Casserole Hot Pot', 2, NULL, 0, 'Fish Lips Casserole Hot Pot', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=498', '<TD><RECIPE><B><I>Ingredients: </B></I><BR>1/2 pound sharks fin, soaked for one hour<BR>2 Tablespoons corn oil<BR>3 slices fresh ginger, coarsely minced<BR>2 scallions, coarsely minced<BR>1/2 cup black mushrooms, soaked in warm water, squeezed dry, stems removed, and cut into quarters<BR>1/2 cup canned bamboo shoots, sliced<BR>2 Tablespoons rice wine<BR>1 cup lettuce or celtuce, cut into large chunks<BR>2 cups chicken broth<BR>1 teaspoon thin soy sauce<BR>2 Tablespoons mushroom soy<BR>1/2 cup peeled carrots, sliced<BR>1 Tablespoon cornstarch or another thickener mixed with two tablespoons water<BR>1 teaspoon sesame oil<BR>2 sprigs fresh coriander<BR><B><I>Preparation:</B></I><BR>1. Rinse sharks fin many times until all salt it washed away. Then cut it into pieces about two by two inches.<BR>2. Heat oil and stir-fry ginger and scallions for one minute before adding the mushrooms and stir-frying them for two minutes before adding the bamboo shoots and rice wine, stirring well, and removing this from the heat.<BR>3. Put lettuce in the bottom of a casserole and pour the mushroom mixture on top of it. Then add broth, sharks fin pieces, both soy sauces and the carrots. Cover and simmer this for half an hour covered and another half an hour uncovered.<BR>4. Add cornstarch mixture and the sesame oil and bring to the boil and thicken the sauce. Serve after putting the sprigs of coriander on the top.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C5A1A45C4E536149AA5C126F27D10B1B', X'C398DED55BA81D4989513F56AA8737AD', X'C398DED55BA81D4989513F56AA8737AD', 0, 'javascript:', 'Fish Maw and Vegetables', 2, NULL, 0, 'Fish Maw and Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=673', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon corn oil<BR>4 slices fresh ginger, peeled and slivered<BR>3 cloves garlic, peeled and sliced thin<BR>10 shallots, peeled<BR>2 ounces fish maw, soaked (as indicated in article above)<BR>2 dried scallops, soaked for two hours, then steamed for one hour<BR>3 cups chicken broth<BR>3 Tablespoons wolfberries or dried cranberries<BR>1/2 pound pea shoot leaves<BR>10 pea pods, strings removed and slivered<BR>2 Tablespoons cornstarch mixed with two tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Heat oil and fry ginger, garlic, and shallots in a wok or heat proof casserole for one minute.<BR>2. Add fish maw and scallops and fry one minute more, then add chicken broth, bring almost to the boil, reduce heat and simmer for one hour.<BR>3. Add wolfberries, pea shoot leaves, and pea pod slivers, and bring to the boil, then add half the cornstarch liquid and stir, then the rest of this mixture and stir for one minute, then serve in casserole, or preheat one and pour contents of the wok or pan into it and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B393075554C59247BF6650E6DBBE232F', X'37A7C35A4F027D4DAD25983759871781', X'37A7C35A4F027D4DAD25983759871781', 0, 'javascript:', 'Fish Maw with Eggs', 2, NULL, 0, 'Fish Maw with Eggs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2280', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 to 2 ounces fish maw<BR>2 slices fresh ginger, peeled and sliced<BR>2 scallions, one knotted, the other slivered<BR>1 Tablespoon cornstarch<BR>4 eggs, beaten <BR>1 Tablespoon sesame oil<BR>dash ground black pepper<BR>1 teaspoon thin soy sauce<BR>1 teaspoon chicken bouillon powder<BR>2 Tablespoons vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Soak fish maw for one hour in tepid water, then squeeze out the water and discard it.<BR>2. Bring three cups of cold water to the boil, add the fish maw, ginger, and the knotted scallion, and the fish maw and simmer for five minutes, then squeeze out the water in a wet towel. When cool, mince the fish maw or cut it into small pieces with a scissor.<BR>3. Mix the fish maw and the cornstarch, then add the beaten eggs, and the sesame oil, the pepper, the chicken bouillon powder, and the soy sauce.<BR>4. Heat a wok or fry pan, add the vegetable oil, then stir in the egg mixture, pouring it into the wok or fry pan and stirring it until it barely sets. Then move it to a preheated bowl, add the minced scallion, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'53002B2319A8754EB1CD4EAFA52CC34D', X'1D34F4E0456F7C4FADA3456CF9BC28BB', X'1D34F4E0456F7C4FADA3456CF9BC28BB', 0, 'javascript:', 'Fried Fresh Milk', 2, NULL, 0, 'Fried Fresh Milk', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=723', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 cup oil, he uses peanut oil, we used corn oil<BR>6 large shrimp, shelled and veins removed<BR>1/2 Tablespoon olive seeds (lam yarn) or pine nuts<BR>1/4 cup very thin rice noodles (<I>mai fun</I>)<BR>1 scallion, slivered<BR>3 Tablespoons crab meat<BR>1 Tablespoon oil<BR>2/3 cup fresh whole milk<BR>1/2 cup egg whites (about 8)<BR>2 teaspoons salt<BR>1/2 teaspoon sugar<BR>2 teaspoons cornstarch<BR>crab roe, from one crab or one tablespoon of roe<BR>1 spring cilantro<BR><B><I>Preparation:</B></I><BR>1. Heat the cup of oil. Immerse shrimp in it for half minute, drain, and set aside. Fry olive seeds for half minute, and set them aside. Set aside one tablespoon of the oil. Then reheat the rest and fry the rice noodles until puffed and expanded. This takes but seconds. If not enough oil to totally immerse them, either turn the entre batch virtually as soon as they start to expand; or add more oil before starting to cook them. Remove, drain on paper towels, and put on a platter. Very lightly crush the mixture to about one inch thick.<BR>2. Heat wok and add reserved tablespoon of oil and stir-fry scallion pieces and the crabmeat for one minute, or until almost, but not completely cooked. Remove and set aside.<BR>3. Mix milk and egg whites, salt, sugar, and cornstarch.<BR>4. Heat the wok, add the tablespoon of oil and add the milk mixture. Reduce the heat and gently mix until the mixture just barely thickens, about half minute. Then remove from heat and add shrimp and the roe, also the scallions. Pour on the rice noodles and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D304DA3A91DC354B92464BD9A549411F', X'37B9DCF2867A4C41B73998FFFBCD4016', X'37B9DCF2867A4C41B73998FFFBCD4016', 0, 'javascript:', 'Fried Iced Beancurd with Shrimp Roes', 2, NULL, 0, 'Fried Iced Beancurd with Shrimp Roes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=948', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 large cubes soft bean curd (each about one pound)<BR>2 eggs<BR>2 Tablespoons shrimp roe<BR>1 sprig fresh coriander, minced<BR>1/4 teaspoon ground white pepper<BR>1/4 teaspoon salt<BR>2 Tablespoons cornstarch<BR>1 cup vegetable oil<BR>1/4 pound fresh broccoli, blanched for one minute, but not put into ice water to chill<BR><B><I>Preparation:</B></I><BR>1. Freeze bean curd in two to three cups cold water until solid.<BR>2. Defrost and cut into large pieces.<BR>3. Beat eggs and mix with shrimp roe, coriander, pepper, and salt.<BR>4. Dry the bean curd, then coat it with cornstarch and dip it into the egg mixture.<BR>5. Heat oil and deep fry the coated bean curd until golden brown. Drain on paper towels, mix with broccoli, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E8C6915B64EE624D82FAF0A7EE686974', X'87FDBAC9659C7D41A480360EBE3B26B7', X'87FDBAC9659C7D41A480360EBE3B26B7', 0, 'javascript:', 'Fried Lotus Flowers', 2, NULL, 0, 'Fried Lotus Flowers', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=378', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>24 lotus petals<BR>6 Tablespoons sweet bean or lotus paste<BR>3 Tablespoons lotus flour<BR>4 egg whites<BR>2 cups corn oil for deep frying<BR><B><I>Preparation:</B></I><BR>1. Dry the petals and put a teaspoon sweet bean paste on each of them, then fold over the long way to cover the paste.<BR>2. Mix flour and egg whites until a nice thin batter.<BR>3. Heat the oil, then dip petal sandwiches in batter and deep fry for about two minutes. then drain and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8F0DCCC88D3181419648DAA36579A64C', X'7D4A4E596F032F4FBB6815E59E186C7D', X'7D4A4E596F032F4FBB6815E59E186C7D', 0, 'javascript:', 'Fried Milk', 2, NULL, 0, 'Fried Milk', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=686', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups corn oil<BR>1/2 pound large shrimp, shelled, veins removed, a thin slice cut off the outsides to remove the part that turns pink, and each cut in four<BR>4 ounces pine nuts or one-quarter cup olive kernels<BR>1 ounce package rice noodles<BR>1 cup whole milk<BR>6 egg whites<BR>1 teaspoon salt<BR>1 teaspoon sugar<BR>1 Tablespoon cornstarch<BR>1 sprig coriander, coarsely minced<BR><B><I>Preparation:</B></I><BR>1. Heat oil in a wok, and blanch shrimp for thirty seconds, remove and drain.<BR>2. Reheat the oil and oil blanch the olive kernels for one minute, then drain them, but keep separately from the shrimp.<BR>3. Reheat oil and deep-fry the rice noodles until they expand; take them out immediately and drain these separately, then put them on a flat serving platter. Set oil aside for another use, leaving the wok very well oiled but without a puddle on the bottom.<BR>4. Gently mix milk, egg whites, salt, sugar, and cornstarch. Heat wok and slowly but steadily pour milk mixture into the wok. Reduce the heat and stir-fry adding the shrimp after half a minute or as soon as it is about half coagulated.<BR>5. Pour over rice noodles, sprinkle the olive kernels and coriander on top, and serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A8262CDD40E56740B515F8AF54C320C7', X'C5B89B37BD15AB48AECF9CCA41CFD454', X'C5B89B37BD15AB48AECF9CCA41CFD454', 0, 'javascript:', 'Fried Rabbit', 2, NULL, 0, 'Fried Rabbit', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2113', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 rabbits, hind quarters only, meat prepared in slices, cross-cut on one surface<BR>1 Teaspoon salt<BR>2 teaspoons rice wine<BR>1 teaspoon chili sauce<BR>2 egg whites<BR>6 large shallots, peeled and each one cut in half<BR>3 slices peeled fresh ginger<BR>1 cup oil or melted lard<BR>1 small green pepper, seeded and cut into cubes<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Rub the rabbit pieces with the salt, mix with the rice wine, and set aside for ten minutes. Then mix in the chili sauce, egg whites, shallots, and ginger, and cover with plastic wrap and let this rest in the refrigerator for one hour.<BR>2. Remove from the refrigerator, remove the plastic wrap, and set aside while heating the lard or oil, then fry the meat in the lard for two minutes, and then drain on paper towels. Add the green pepper cubes and fry for half a minute, then remove the meat and vegetable from the wok and set it aside to cool.<BR>3. Return the meat to the wok or fry pan, and the pieces of green pepper, and stir-fry for one minute, then mix in the sesame oi, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1841E18F6765C34CB17E1DE8341A2A4C', X'08E479114E35374699567311C3905C87', X'08E479114E35374699567311C3905C87', 0, 'javascript:', 'Fried Rabbit Fillet', 2, NULL, 0, 'Fried Rabbit Fillet', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=107', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>2 pounds rabbit meat, sliced<BR>1/2 teaspoon salt<BR>1 Tablespoon Shaoxing wine<BR>1 teaspoon shallot<BR>1 teaspoon ginger juice<BR>1 Tablespoon cornstarch<BR>2 egg whites<BR>1 pound lard<BR>2 Tablespoons shiitake mushrooms, sliced thin<BR>1 cup chicken stock<BR>4 cloves garlic, peeled and minced<BR>4 Tablespoons cornstarch<BR>2 tablespoons green pepper, sliced thin<BR>2 Tablespoons red pepper, sliced thin<BR>1 teaspoon sesame oil<BR>1 pound Chinese noodles, boiled and drained<BR><B><I>Preparation:</B></I><BR>1. Marinate rabbit meat, mixing it well with the salt, wine, shallots, ginger juice, one tablesoon cornstarch, and the egg whites, for half an hour.<BR>2. Divide the rabbit meat in four parts and blanch each batch of slices in hot lard, then drain well on paper towels. Next blanch the mushrooms in the lard and drain them.<BR>3. Mix mushrooms, stock, and minced garlic, and bring to the boil.<BR>4. Mix the rest of the cornstarch with one-quarter cup cold water, add to the stock mixture and heat until it is warm, not hot. Then add the rabbit meat, green and red peppers, and sesame oil and bring to the boil. Keep stirring until it thickens and clears somewhat, then serve putting the meat and sauce on top of the noodles.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7E5E24D06B044848A429A6F3B468D714', X'FFC07F7CD0ADB14581F60BA473DDA4FE', X'FFC07F7CD0ADB14581F60BA473DDA4FE', 0, 'javascript:', 'Fried Rabbit in Red Wine Lees', 2, NULL, 0, 'Fried Rabbit in Red Wine Lees', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2116', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 recipe for shredded rabbit with fish flavor<BR>1/2 cup red wine lees<BR><I><B>Preparation:</B></I><BR>1. Make the shredded rabbit recipe and before putting it in the refrigerator, add the red wine lees and stir well.<BR>2. In the morning, drain well and then put it back in the refrigerator covered with plastic wrap. Remove and serve just before wanting to eat it.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'596B63E31857A747BC32680ABBFEB744', X'2C9A3B6CD0F2064D8377E0234D45359D', X'2C9A3B6CD0F2064D8377E0234D45359D', 0, 'javascript:', 'Fried Rabbit---Bamboo Slip 83', 2, NULL, 0, 'Fried Rabbit---Bamboo Slip 83', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=814', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound rabbit meat, tendons removed and cut into half-inch pieces<BR>1/2 teaspoon salt<BR>1 Tablespoon Chinese rice wine<BR>1 shallot, minced<BR>1 Tablespoon ginger juice<BR>2 egg whites<BR>1 Tablespoon cornstarch<BR>1 teaspoon sesame oil<BR>4 Tablespoon lard or vegetable oil<BR>2 black mushrooms, soaked, stems removed, and cut into triangles<BR>1/2 sweet red pepper, seeded and cut into triangles<BR>1/2 cup chicken broth<BR>2 Tablespoons cornstarch<BR><B><I>Preparation:</B></I><BR>1. Mix rabbit meat, salt, rice wine shallot, ginger juice, egg whites, cornstarch, and sesame oil. Drain after ten minutes and reserve the liquid.<BR>2. Mix the lard and stir in the rabbit mixture, and keep stirring it for one minute. Then add mushroom and pepper pieces and stir another minute.<BR>3. Mix and heat broth and cornstarch, add to the rabbit mixture, and stir for one minute, then drain and put in a pre-heated bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CC6A31D573932145B1213DBD2D6567F6', X'602EF1CE16B23449AE02103C37EA5496', X'602EF1CE16B23449AE02103C37EA5496', 0, 'javascript:', 'Frog with Dried Tangerine Peel', 2, NULL, 0, 'Frog with Dried Tangerine Peel', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2110', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>! pouind frogs legs, skinned, each leg cut in half<BR>3 ounces <I>hasma</I>, prepared as in the first recipe with this article<BR>Peel of half a dried tangerine, soaked until soft, then slivered<BR>3 slices fresh ginger, peeled and slivered<BR>2 scallions, slivered at an angle<BR>2 to 3 Tablespoons salted Sichuan vegetable, soaked in two cups of warm mater for ten minutes, then discard this water, and mince this vegetable<BR>1 quart chicken broth (optional)<BR>2 Tablespoons cornstarch<BR>2 Tablespoons thin soy sauce<BR>1 Tablespoon rice wine<BR>1 teaspoon ganulated sugar<BR><B><I>Preparation:</B></I><BR>1. Put one cup water in a wok, add the frogs legs, prepared <I>hasma</I>, and the softened tangerine peel and simmer for five minutes.<BR>2. Next add the ginger, scallions, Sichuan vegetable, soy sauce, rice wine, and the sugar and simmer for eight more minutes, adding the broth if using this as a soup.<BR>3. Finally, stir in the cornstarch minced with two tablespoons cold water, and stir for two minutes; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'56822E95C0283B4B8C49EEB9A2F7D373', X'D99E8C6F5893F64BAB685D3BF0C35A45', X'D99E8C6F5893F64BAB685D3BF0C35A45', 0, 'javascript:', 'Frogs in Black Bean Sauce', 2, NULL, 0, 'Frogs in Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2111', '<TD><RECIPE><B><I>Ingredients</B></I><BR>3 to 5 pairs of frogs legs, separated, and each cut in half<BR>1 Tablespoon oyster sauce<BR>2 Tablespoons black bean sauce<BR>2 teaspoons mushroom soy sauce<BR>a dash of chili sauce<BR>1 teaspoon vegetable oil<BR>1 cup cooked and hot rice<BR>1 Tablespoon cornstarch mixed with the same amount of cold water<BR><B><I>Preparation:</B></I><BR>1. Mix frogs leg pieces with the bean paste, oyster, and mushroom soy sauces, and the chili sauce.<BR>2. Heat wok, add the oil, and stir-fry the frogs leg mixture for two minutes, then transfer to a steamer on top of a large bowl holding the cooked rice.<BR>3. Steam for twelve minutes, then serve putting this hot bowl on a serving plate.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3CE6112B78856447B5BC8D8837F49DB7', X'483DFF80D3C1B7499608E56161E42C9C', X'483DFF80D3C1B7499608E56161E42C9C', 0, 'javascript:', 'Fruit and Bird''s Nest', 2, NULL, 0, 'Fruit and Bird''s Nest', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=216', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 ounces bird''s nest pieces <BR>2 Tablespoons small white rock sugar pieces<BR>2 almost ripe peaches, remove stone, blanch and peel, then cut each half in four slices<BR>10 blanched almonds, halved<BR><I><B>Preparations:</I></B><BR>1. Prepare bird''s nest soaking them in water overnight in the refrigerator. Drain, then bring to the boil and simmer for ten minutes; then drain and cool. Clean them of any drbris with a tweezer, and repeat as necessary.<BR>2. Boil sugar with one cup of water until it dissolves, then transfer it to the top of a double boiler and keep over boiling water.<BR>3. Add peaches, bird''s nest pieces, and the almonds and simmer over boiling water for one hour. Serve with the liquid putting some into individual bowls.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'33F32DE552DB22478956637126D3EDFC', X'07A366A9FF60A144A385F7834253E328', X'07A366A9FF60A144A385F7834253E328', 0, 'javascript:', 'Gold and Silver Venison I', 2, NULL, 0, 'Gold and Silver Venison I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1352', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds venison, cut into four pieces<BR>1 teaspoon Chinese rice wine<BR>1 teaspoon cornstarch<BR>1 teaspoon salt<BR>1 teaspoon minced onion<BR>1 knob fresh ginger, peeled cut into six pieces and each piece crushed with the side of a cleaver<BR>1 teaspoon chicken fat<BR>2 eggs, separated<BR>1 teaspoon cornstarch<BR>1 teaspoon wheat flour<BR>2 cups peanut oil<BR><B><I>Preparation:</B></I><BR>1. Put the venison into enough water to cover it and simmer for one hour. Remove and cut each piece into six to eight smaller pieces.<BR>2. In a heat-proof bowl, mix the meat with the rice wine, salt, onion and ginger pieces and the chicken fat, and put the bowl into a steamer over rapidly boiling water and steam until the meat is very tender (about an hour, depending upon the age of the deer).<BR>3. Remove the meat and cut into thin slices and put half of the meat in one bowl, half in another.<BR>4. Divide the cornstarch and wheat flour and put half into each of the bowls, and mix well with the meat.<BR>5. Beat the egg white and the yolks separately, and pour one over one bowl of meat, the other over the other bowl, and stir both of them well.<BR>6. Heat the oil. Drain the meat and fry the one mixed with the yolks first. When crisp drain on paper towels and set the meat on half the platter. Drain then fry the meat mixed with the egg whites, and when crisp drain this on paper towels and put the pieces on the other half of the platter.<BR>7. Mix any remaining yolk and egg batter, and pour into a hot wok or fry pan. Fry this egg-batter on both sides as one would an omelet. When set, remove from the pan and cut it into slivers. Put these pieces put on top of both sides of the venison, as decor, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DB53F1DF36ECDC4D8275B3EEB1B2F546', X'DDD4EC39E4F5B04F903970D78964201B', X'DDD4EC39E4F5B04F903970D78964201B', 0, 'javascript:', 'Gold and Silver Venison II', 2, NULL, 0, 'Gold and Silver Venison II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1474', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pounds venison, cut into four pieces<BR>1 teaspoon Chinese rice wine<BR>1 teaspoon cornstarch<BR>1 teaspoon salt<BR>1 teaspoon minced onion<BR>1 knob fresh ginger, peeled cut into six pieces and each piece crushed with the side of a cleaver<BR>1 teaspoon chicken fat<BR>2 eggs, separated<BR>1 teaspoon cornstarch<BR>1 teaspoon wheat flour<BR>2 cups peanut oil<BR><B><I>Preparation:</B></I><BR>1. Put the venison into enough water to cover it and simmer for one hour. Remove and cut each piece into six to eight smaller pieces.<BR>2. In a heat-proof bowl, mix the meat with the rice wine, salt, onion and ginger pieces and the chicken fat, and put the bowl into a steamer over rapidly boiling water and steam until the meat is very tender (about an hour, depending upon the age of the deer).<BR>3. Remove the meat and cut into thin slices and put half of the meat in one bowl, half in another.<BR>4. Divide the cornstarch and wheat flour and put half into each of the bowls, and mix well with the meat.<BR>5. Beat the egg white and the yolks separately, and pour one over one bowl of meat, the other over the other bowl, and stir both of them well.<BR>6. Heat the oil. Drain the meat and fry the one mixed with the yolks first. When crisp drain on paper towels and set the meat on half the platter. Drain then fry the meat mixed with the egg whites, and when crisp drain this on paper towels and put the pieces on the other half of the platter.<BR>7. Mix any remaining yolk and egg batter, and pour into a hot wok or fry pan. Fry this egg-batter on both sides as one would an omelet. When set, remove from the pan and cut it into slivers. Put these pieces put on top of both sides of the venison, as decor, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8BB812E2B1836D4094ADD5123E4C0CDE', X'CABB94CB87ABFF4BB54341C1A257E8DC', X'CABB94CB87ABFF4BB54341C1A257E8DC', 0, 'javascript:', 'Grass Jelly Chen', 2, NULL, 0, 'Grass Jelly Chen', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=933', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 Tablespoons rice flour<BR>6 Tablespoons black grass for jelly<BR>2 and 1/2 teaspoons bottled alkaline water<BR>4 Tablespoons bean flour<BR><B><I>Preparation:</B></I><BR>1. Grind rice with a little water to make a slurry. Pass through a cloth to remove all particles, then set aside.<BR>2. Put nine cups of cold water in a non-metal pot. Add black grass jelly and the alkaline water and simmer on high heat for an hour. Remove from heat an allow to cool, then rub in pan to extract sticky stuff from the grass. Pout this and the liquid back into the pot and cook twenty minutes more. Repeat the rubbing, and boil again and rub once more. Strain and discard solids, strain and boil one more time.<BR>3. Put rice liquid and bean flour in a bowl or pan and stir well, then pour into the boiling grass jelly liquid, and stir well, and while stirring, pour into a pudding pan and allow to cool and solidify. Chill in refrigerator and cut as needed before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F0602E9C84120645BBCC9624FD2C80D2', X'6919A9FE80BE3A448D7AC5CF51B55E05', X'6919A9FE80BE3A448D7AC5CF51B55E05', 0, 'javascript:', 'Grass Jelly Choi', 2, NULL, 0, 'Grass Jelly Choi', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=934', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup <I>Mesona chinensis</I>(an herb mentioned in the Grass Jelly article)<BR>2 Tablespoons alkaline water (available in Asian markets)<BR>1/2 pound cornstarch<BR><I><B>Preparation:</B></I><BR>1. Wash the herb thoroughly and put in to a pot with four and a half quarts of water and the alkaline water. Bring this to the boil, reduce heat and simmer for two hours, then cool.<BR>2. Rub the herb vigorously so that gel is extracted; save it, then boil the remaining water and the extract for half an hour. Repeat one more time, and after rubbing, filter out any solids left in the pot. About twelve cups of liquid will remain.<BR>3. Stir in cornstarch and two and a half cups of cold water. Stir quickly and then bring almost to the boil, Pour this into a bowl or pan and chill. When solidified, dice and serve with a sugar syrup, if desired.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'32FDD33F0C75DE48BC0544EE2C8F6E5F', X'6E92E2CF77927D41BFB3EABCD67939E5', X'6E92E2CF77927D41BFB3EABCD67939E5', 0, 'javascript:', 'Green and White Cold Soup', 2, NULL, 0, 'Green and White Cold Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=241', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>i ounce defrosted and soaked <I>hasma</I><BR>1 large ripe avocado<BR>4 cups of two-percent milk<BR>2 Tablespoons honey<BR><I><B>Preparation:</I></B><BR>1. Soak <I>hasma</I> in warm water for an hour, then refrigerate tightly covered, overnight. Drain and cut into small pieces.<BR>2. Peel, discard the pit, and mash the avocado. Heat the honey for fifteen seconds in a microwave, then mix and refrigerate overnight.<BR>3. Add drained <I>hasma</I> and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AAB5F3EA48E7964B9802D92AB7D38527', X'FC6FB9E0701B1348972E96266B3687EB', X'FC6FB9E0701B1348972E96266B3687EB', 0, 'javascript:', 'Hair Moss for Arhats', 2, NULL, 0, 'Hair Moss for Arhats', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1914', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 ounce hair moss soaked for twenty minutes, then drained<BR>10 dried Chinese black mushrooms, soaked until soft, stems discarded, caps sliced<BR>10 Tablespoons silken tofu, mashed<BR>1 square red bean curd, mashed<BR>1 teaspoon granulated sugar<BR>1/2 cup tree seeds, their pits removed and discarded<BR>1/2 cup drained canned gingko nuts<BR>1/2 cup melting mouth peas cut in thin slivers<BR>4 fried bean curd squares, cut into quarters<BR>2 Tablespoons vegetable oil<BR>6 slices fresh ginger, minced<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add the oil and the ginger, and in half minute, the mushroom slices and stir-fry for two minutes.<BR>2. Next, add all the other ingredients and stir-fry for two minutes stirring well, then add two tablespoons water and simmer for two more minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0FECA5BF5ABD4045814121E1C8EBEFE6', X'D41748C173DCDE42B79165F37A32F7A6', X'D41748C173DCDE42B79165F37A32F7A6', 0, 'javascript:', 'Hunan Braised Tripe', 2, NULL, 0, 'Hunan Braised Tripe', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=479', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound ox tripe, simmered for half hour in water<BR>2 Tablespoons Chinese rice wine<BR>2 Tablespoons thin soy sauce<BR>1 cup chopped scallions, white part only<BR>1/2 teaspoon ground star anise<BR>1/4 teaspoon ground cumin<BR>1/4 teaspoon ground black pepper<BR>4 tablespoons thin-sliced fresh ginger<BR>1 teaspoon dry mustard<BR>1 Tablespoon cornstarch<BR>1 teaspoon sugar<BR><B><I>Preparation:</B></I><BR>1. Bring two cups water to the boil, add tripe and simmer fifteen minutes. Then add rice wine, soy sauce, scallions, star anise, cumin, black pepper, and ginger and cover the pot. Simmer for one hour and thirty minutes. Remove tripe and reserve the sauce. Cut the tripe into two or three inch pieces and put it on a serving plate.<BR>2. Reheat the remaining sauce and when it comes to the boil, add dry mustard, cornstarch, and the sugar, and boil stirring, until it thickens. Pour over the tripe and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3810A15C9E647C4195C9BBE8D281B8F9', X'5534CF8D583AC54A9503CF98F33E349D', X'5534CF8D583AC54A9503CF98F33E349D', 0, 'javascript:', 'Jellyfish and Crabmeat, Very Old Style', 2, NULL, 0, 'Jellyfish and Crabmeat, Very Old Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=853', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 or 6 large dried jellyfish, rinsed three times, then soaked for one hour<BR>1 Tablespoon vegetable oil<BR>1 shallot, cut in quarters<BR>2 slices fresh ginger, cut into strips<BR>4 cups chicken stock<BR>1 Tablespoon Chinese rice wine<BR>2 Tablespoons lard<BR>1/4 cup Yunnan or Smithfield ham, cut in slivers<BR>1/2 pound crab meat<BR>1 Tablespoon cornstarch mixed with equal amount of cold water<BR>1/8 teaspoon ground Sichuan pepper<BR>8 trimmed stalks young bokcai or the hearts of another Chinese green, steamed for five minutes<BR><B><I>Preparation:</B></I><BR>1. Bring eight cups of water to just below the boil, drop jellyfish in and immediately take out and put in bowl of ice water. Roll, then cut into thin strips.<BR>2. Heat oil in a large pot and fry shallot and ginger just until it is fragrant, about one minute. Remove half and set aside, then put stock and wine into the pot and simmer for half hour. Add jellyfish and simmer for fifteen minutes, then remove it and set aside.<BR>3. In wok or another pan, heat lard and add the reserved shallot and ginger pieces and the ham. Stir-fry for a minute.<BR>4. Put vegetable into the stockpot and let it get hot.<BR>5. Add crab meat to the ham mixture and stir, then add jellyfish strips, and stir once or twice before adding starch mixture and Sichuan pepper. Stir, put into stock pot, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'55D9C66DBC91B24C9531A861BE986E15', X'466C18563E9C9141B13051F01184206F', X'466C18563E9C9141B13051F01184206F', 0, 'javascript:', 'Jellyfish and Greens', 2, NULL, 0, 'Jellyfish and Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1732', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon vegetable oil<BR>2 cloves garlic, peeled and slivered<BR>1 scallion, green part only, slivered<BR>1 cup mustard greens, cut into two-inch slivers<BR>3 duck or six chicken gizzards, thick part removed, then slivered<BR>8 ounce packet jellyfish soaked in cold water eight hours, blanched in boiling water for half minute, then rinsed in ice water and slivered.<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon shaoxing rice vinegar<BR>1 teaspoon sesame oil<BR>1 Tablespoon granulated sugar<BR>2 teaspoons cornstarch mixed with one tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Heat oil in wok or fry pan, then add garlic and scallion pieces and stir fry half minute before adding mustard greens and duck gizzards and stir-fry for three minutes.<BR>2. Add soy sauce, rice vinegar, and sugar and simmer for two more minutes, then add jellyfish and stir-fry one minute.<BR>3. Add sesame oil and the cornstarch mixture, and stir until sauce thickens, about half minute. Then plate and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7A6A8AA8D3C3EC4E8C0C59EA0A52AAB0', X'7F664648115283478983E6031B973864', X'7F664648115283478983E6031B973864', 0, 'javascript:', 'Jellyfish and Roast Duck', 2, NULL, 0, 'Jellyfish and Roast Duck', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=852', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 dried jellyfish, rinsed three times, then blanched in almost boiling water for fifteen seconds and removed and immediately put into cold or ice water<BR>4 Tablespoons white Chinese rice vinegar<BR>1 teaspoon sugar<BR>1 teaspoon powdered chicken bouillon<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon sesame oil<BR>1/2 pound roast duck breast, skin removed, and hand shredded<BR>1 small piece red pepper, shredded<BR>1 Tablespoon sesame seeds<BR>1 sprig Chinese parsley, torn into its leafy segments<BR><B><I>Preparation:</I></B><BR>1. Cut jellyfish in half, and roll each piece tightly. Using sharp knife, cut each half into the thinnest strips possible.<BR>2. Mix jellyfish with rice vinegar, and allow to marinate for half hour, then drain.<BR>3. Mix sugar, chicken bouillon, and soy sauce and toss with the jellyfish. Let rest fifteen minutes, then drain, and pile jellyfish high on a small platter. Top this with the shredded duck and then the red pepper shreds.<BR>4. Sprinkle sesame seeds over it all and place Chinese parsley pieces around the outside of the platter. Toss before serving.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C813031D8C55054EACB6A14F79B6735C', X'5F1DA65EA50DAB429D280A2C740B181A', X'5F1DA65EA50DAB429D280A2C740B181A', 0, 'javascript:', 'Jellyfish Head, Shrimp and Chicken', 2, NULL, 0, 'Jellyfish Head, Shrimp and Chicken', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1390', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 to 3 ounces jellyfish heads<BR>3 ounces boneless skinless chicken breasts, cut into thin strips<BR>3 ounces peeled shrimp, veins discarded, then cut in half top to bottom<BR>2 teaspoons cornstarch<BR>1 egg white<BR>2 cups vegetable oil<BR>1 slice fresh ginger, peeled and minced<BR>1 scallion, minced<BR>1/2 teaspoon salt<BR>1/2 teaspoon granulated sugar<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Soak the jellyfish heads overnight in two cups of cold water. Cover their dish and put it in the refrigerator. Change the water before going to bed, and do so again two hours before using them.<BR>2. Blanch the jellyfish heads in boiling water for thirty seconds, then put them into ice water for three to five minutes until they are cold before draining them.<BR>3. Mix chicken and shrimp with cornstarch and egg white, then separate them for cooking.<BR>4. Heat a wok or a three-quart pan and add oil, When almost smoking, oil-blanch the chicken for two minutes, then remove and drain on paper towels, then oil-blanch the shrimp for one minute, and drain them the same way. Set oil aside for another purpose, but do not wash the wok or pot.<BR>5. Reheat wok or pot, add minced ginger and scallions, and stir-fry for one minute, then add the chicken and the shrimp stir fry a half minute before adding the jellyfish, salt, sugar and rice wine. Mix well, transfer to a pre-heated bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3AAF269C23CC0A489497AF9B04EEF1DC', X'00C372249FFE7D409421ECB455EEC773', X'00C372249FFE7D409421ECB455EEC773', 0, 'javascript:', 'Jellyfish with Chili Sauce', 2, NULL, 0, 'Jellyfish with Chili Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1735', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound jellyfish, blanched for half minute, then immediately put into ice water, and when cold, slivered<BR>3 Tablespoons thin soy sauce<BR>3 Tablespoons sesame oil<BR>1 Tablespoon chili sauce; less if not wanted very spicy<BR>1 Tablespoon rice vinegar<BR>1 Tablespoon granulated sugar<BR>1 green part of one scallion, slivered<BR><B><I>Preparation:</B></I><BR>1. Mix jellyfish with the soy sauce, cover, and refrigerate if not using in the next hour or two.<BR>2. Mix sesame oil, chili sauce, rice vinegar, and the sugar, and stir until dissolved, and toss with the jellyfish and soy sauce. Put into a pre-chilled serving bowl, scatter scallion pieces on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4701243F9D2BB841A3B04241AE1525FF', X'49231A0EEAC846418487C04C55FF66A8', X'49231A0EEAC846418487C04C55FF66A8', 0, 'javascript:', 'Jellyfish with White Radish', 2, NULL, 0, 'Jellyfish with White Radish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=854', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1 dried jellyfish, rinsed three times, then blanched in almost boiling water for fifteen seconds and removed and immediately put into cold or ice water.<BR>2 cups peeled and shredded white radish (also known as <I>daikon</I>)<BR>1 teaspoon coarse salt<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon white Chinese rice vinegar<BR>1 Tablespoon Chinese red vinegar<BR>2 teaspoons sugar<BR>1 teaspoon sesame oil<BR>sprig of Chinese parsley<BR><B><I>Preparation</B></I><BR>1. Cut a jellyfish in half, then roll and slice as thinly as possible. Set aside covered and in cold water in the refrigerator for an hour, then drain.<BR>2. Mix salt with the shredded radish and set aside for an hour, then drain, and put in a serving bowl. Mix with drained jellyfish.<BR>3. Mix soy sauce, vinegars, sugar, and sesame oil, pour over jellyfish-radish mixture, and serve topping the jellyfish-radish mixture with the sprig of Chinese parsley. Toss after putting the bowl on the table.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'32FDAE508BEA65439B0323CF4C50C77A', X'0A63848C7BD88C409AA50C12BEF93196', X'0A63848C7BD88C409AA50C12BEF93196', 0, 'javascript:', 'Kelp. Chili, and Ginger', 2, NULL, 0, 'Kelp. Chili, and Ginger', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1910', '<TD><RECIPE><B><I>Ingredients: </B></I><BR>1 and 1/2 cup shredded kelp, put in boiling water for two minutes, then in ice water for one minute, then drained<BR>1 small red chili pepper, finely shredded<BR>2 slices fresh ginger, one finely shredded<BR>2 Tablespoons white soy sauce<BR>1 Tablespoon white sesame oil<BR><B><I>Preparation:</B></I><BR>1. Mix cold kelp, chili pepper slivers, ginger, and the soy sauce and refrigerate for two hours.<BR>2. Put on a pre-chilled plate, and then pour sesame oil over this mixture, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3DE88540CB77584FB53B405200CE5545', X'46A6DD441CBB1743BE3791E1B3EA99D2', X'46A6DD441CBB1743BE3791E1B3EA99D2', 0, 'javascript:', 'Lamb Stomach Soup, Sui-Yuan', 2, NULL, 0, 'Lamb Stomach Soup, Sui-Yuan', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1296', '<TD><RECIPE>Wash the lamb stomach thoroughly inside and outside. Cook until it is soft and thoroughly done. Then cut into slivers while leaving it in its original broth. You may add black pepper and vinegar. This is the way northerners prepare it, Southerners do not prepare the meat as crispy.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B9BA31CAECF0CD428208C0557540029B', X'54A214DF39E46648AC85EDFA2F26380B', X'54A214DF39E46648AC85EDFA2F26380B', 0, 'javascript:', 'Lamb Tripe with Mushrooms', 2, NULL, 0, 'Lamb Tripe with Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=480', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 fresh hot chili peppers, seeded and slivered<BR>1 pound lamb tripe, blanched three times, then cut in matchstick-size pieces<BR>1/2 cup chicken broth<BR>1 Tablespoon corn oil<BR>1/2 Green pepper, cut in thin slivers<BR>1 teaspoon sugar<BR>1 Tablespoons thin soy sauce<BR>1 Tablespoon dark soy sauce<BR>3 cloves garlic, minced<BR>18 black mushrooms, soaked half hour in one cup warm water, drained of excess water, it is squeezed out, and reserved<BR>1 cup coriander. Set six sprigs aside and mince the rest<BR>1 Tablespoon corn starch<BR>2 teaspoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Blanch slivered hot chili pieces, dry and set aside.<BR>2. Simmer tripe for ten minutes in the broth, remove from liquid and set aside, reserve the broth.<BR>3. Heat corn oil and stir-fry both peppers for one minute. Add mushrooms and tripe, the reserved mushroom water, and the minced coriander. Simmer one minute then bring to the boil.<BR>4. Mix cornstarch with the reserved chicken stock and stir into the tripe mixture. Remove from heat as soon as it thickens and pour into a serving dish. Decor with coriander sprigs and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DF267FFA6158FD468301AEB6B4C58BA9', X'19E118D0D74444439738FA45C23D8BE4', X'19E118D0D74444439738FA45C23D8BE4', 0, 'javascript:', 'Mustard-flavored Duck Tongues', 2, NULL, 0, 'Mustard-flavored Duck Tongues', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=749', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound duck tongues<BR>2 whole star anise<BR>1/2 teaspoon Sichuan peppercorns<BR>1/2 teaspoon black peppercorns<BR>1/2 teaspoon white peppercorns<BR>2 inch piece stick cinnamon<BR>1 piece of dried tangerine peel, about two-inch square<BR>1/2 teaspoon coarse salt<BR>1 teaspoon sugar<BR>2 teaspoon dry hot mustard mixed with two tablespoons cool water<BR>2 Tablespoons Chinese white rice vinegar<BR>2 Tablespoons sesame oil<BR><B><I>Preparation:</B></I><BR>1. Bring three cups cold water to the boil, then add duck tongues and return to the boil. Remove any scum, then add star anise, all the peppercorns, cinnamon stick, and tangerine peel and simmer for five minutes. Then strain reserving liquid, but discarding the spices. When they have cooled, remove cartilage and bone from each tongue.<BR>2. Mix salt, sugar, hot mustard mixture, rice vinegar, and sesame sauce, then beat well for one to two minutes. Add three tablespoons of the cooking liquid and mix before adding the duck tongues. Cover and refrigerate for four hours, strain liquid and discard, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9D48F75C14F4CA44AE3E013366D30926', X'84B6D0FADB362F4B90E4D30EA8213247', X'84B6D0FADB362F4B90E4D30EA8213247', 0, 'javascript:', 'Ostrich in Sa Cha Sauce', 2, NULL, 0, 'Ostrich in Sa Cha Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=307', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 pound ostrich meat, cut into thin strips<BR>4 teaspoons sesame oil<BR>5 drops chili oil<BR>2 Tablespoons sa cha sauce<BR>1/2 teaspoon black vinegar<BR>2 cups vegetables, cut into thin strips <BR>1/2 cup dried shiitake mushrooms, soaked for fifteen minutes, stems discarded<BR>1 scallion, minced<BR><B><I>Preparation:</I></B><BR>1. Mix meat, sesame and chili oils, the sa cha sauce, and the vinegar.<BR>2. Heat wok and fry meat mixture for half to one minute, it should still be very pink, then remove it from the wok and set aside. Do not overcook it.<BR>3. In the oil remaining in the wok, fry the vegetables until cooked through but still crisp, remove from the wok and set aside.<BR>4. Fry the mushroom in the wok until they have bled all their water, then add the vegetables and the mushrooms and stir fry for one minute until well blended. Stir in the meat and most of the scallion and fry for one minute. Put the rest of the scallions on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ED4837E5FAE59841B8D207399748BC3C', X'76B5EBA658BA474EA9633029449F0298', X'76B5EBA658BA474EA9633029449F0298', 0, 'javascript:', 'Ox Tripe, Bamboo Shoots, and Leeks', 2, NULL, 0, 'Ox Tripe, Bamboo Shoots, and Leeks', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=478', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 pound ox tripe<BR>1/2 teaspoon coarse salt<BR>1 small can bamboo shoots, sliced thin<BR>1 cup yellow chives, slivered<BR>2 Tablespoons corn oil<BR>1/4 sweet red pepper, slivered<BR>1 small dried chili pepper, seeded and slivered<BR>1 Tablespoon rice wine<BR>1 teaspoon chicken bouillon powder<BR>1 teaspoon minced coriander<BR>salt and pepper, to taste<BR><B><I>Preparation:</B></I><BR>1.Mix tripe and salt and set aside for twenty minutes. Then bring to the boil in a quart of water. For one minute. Allow to cool for an hour then boil again for another minute, and then drain, dry the tripe with paper towels, and cut it into thin strips.<BR>2. Heat oil and fry the bamboo shoots and the leeks for one minute. Add the tripe, and chili pepper and the red pepper and stir-fry for half a minute. Then add the rice wine and the chicken bouillon powder and stir-fry one minute. Mix in the coriander and the salt and pepper, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E959D4815C41A246A8051D75153671C1', X'649B1CE278FC8E49B91492437FECFEB9', X'649B1CE278FC8E49B91492437FECFEB9', 0, 'javascript:', 'Pei Gong Dog''s Meat', 2, NULL, 0, 'Pei Gong Dog''s Meat', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1574', '<TD><RECIPE><B>MAIN MATERIAL:</B> 1,2500g dog meat<BR><B>OTHER MATERIAL:</B> one soft soft-shelled tortoise (about 650g)<BR><B>CONDIMENTS:</B> 100g soy sauce, 15g salt, 5g sugar, 50g Shaoxing wine, 100g shallots and ginger, some aniseed, a little wild pepper, 100g brine<BR><B>COOKING METHOD:</B><BR>1. Clean the dog meat, marinate the meat in a plate with salt, sugar, and Shaoxing wine. Steep in clean water. Then return to boiling water in wok and boil thoroughly. Put in an earthenware pot and add some brine, salt, Shaoxing wine, sugar shallots and ginger, aniseed and wild pepper and bring it to the boil. Then move to low heat and cook throughly. Take meat out. When it is cool, bone and cut into pieces for use.<BR>2. Kill the soft-shelled tortoise. Scald it with boiling water. Scrape off the black membrane. Take out the meat and cut into pieces. Cook with the dog meat.<BR>3. Overturn the dog meat with the tortoise into a bowl. Steam for a while before eating. Take the meat out and overturn onto a plate. Pour the sauce over the dish and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'038492FE7197B141AD4463F7F1DC5DD3', X'5F9237919A2E074C919E50C3C7440474', X'5F9237919A2E074C919E50C3C7440474', 0, 'javascript:', 'Pig Tongue with Hair Seaweed', 2, NULL, 0, 'Pig Tongue with Hair Seaweed', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1911', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 ounce hair seaweed, soaked in warm water until soft, then drained and tossed with the oil. Set this aside.<BR>1 Tablespoon vegetable oil<BR>2 slices fresh ginger, one finely minced<BR>1 pig tongue, simmered for two hours in water just below the boiling point, then remove, peel and thinly slice the tongue<BR>1 Tablespoon oyster sauce<BR>1 Tablespoon Shao Xing wine<BR>1 Tablespoon cornstarch<BR>1 teaspoon rock sugar, crushed<BR>3 leaves lettuce, slivered<BR>1 Tablespoon thin soy sauce<BR><B><I>Preparation:</B></I><BR>1. Simmer the cooked tongue with slice of fresh ginger, oyster sauce, wine, and the rock sugar, then toss it with the cornstarch. Next, remove from the heat, and let it rest for half an hour. Then toss with the crushed sugar.<BR>2. Wash the lettuce, blanch it for one minute, toss it with the soy sauce, and put it on a serving platter. Put the tongue slices around the outside of the platter, the hair seaweed in the middle.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E57962A3D46C3146AB868FE14EB1E308', X'57365B4E4EF0DE41A37121036EE5C910', X'57365B4E4EF0DE41A37121036EE5C910', 0, 'javascript:', 'Pig''s Brain, Oxtail, and Eggs', 2, NULL, 0, 'Pig''s Brain, Oxtail, and Eggs', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1927', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 sets of brains from pigs<BR>2 teaspoons cornstarch<BR>1 teaspoon ginger juice<BR>1 Tablespoon scallion juice<BR>2 Tablespoons Chinese rice wine<BR>1 Tablespoon cornstarch or sweet potato flour<BR>1/2 teaspoon coarse salt<BR>1/4 teaspoon ground white pepper<BR>2 Tablespoons vegetable oil<BR>3 eggs<BR>1 oxtail cut into two-inch pieces<BR>2 Tablespoons dark soy sauce<BR>2 scallions, each tied in a knot<BR><B><I>Preparation:</B></I><BR>1. Soak the brains for half an hour in cold water, drain, then remove their membranes and any red tissue attached to them.<BR>2. Mix the brains with the cornstarch, ginger and scallion juices, cornstarch, and the salt and pepper.<BR>3. Slice, then add the brains, and put this on a heat-proof deep plate. Beat the eggs, mix with the pieces of oxtail, add the soy sauce and the scallion knots, and steam for ten minutes, before putting the slices of brains on a heat proof platter. Then steam the oxtails for another ten minutes, add them to the platter, dice the egg mixture and spread it around the meat and the brains. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'86C09B675FE68D428A63ED289B18A7E5', X'24DB9721551FC640BA74E1E5A7905BED', X'24DB9721551FC640BA74E1E5A7905BED', 0, 'javascript:', 'Piquant Pigs Knuckles', 2, NULL, 0, 'Piquant Pigs Knuckles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2099', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pigs knuckles, chopped or sawed into pieces about two-inches<BR>5 scallions, each tied in a knot<BR>1 carrot, peeled and cut into eight pieces<BR>5 slices fresh ginger, cut into pieces<BR>½ <I>daikon,</I> peeled and cut into two-inch pieces<BR>2 Tablespoons lard<BR>5 Tablespoons Sichuan bean paste<BR>5 Tablespoons dark soy sauce<BR>2 Tablespoons dark rock sugar<BR>2 tablespoons cornstarch mixed with the same amount of cold water<BR>1 Tablespoon vegetable oil<BR>1/4 pound ground pork<BR>½ pound Chinese cabbage. Cut into two-inch pieces<BR>3 Tablespoons coarsely minced cilantro<BR><B><I>Preparation:</B></I><BR>1. Put the pig knuckle pieces, the scallion knots, carrots, ginger, and <I>daikon</I> pieces, and the lard into a very large glass or ceramic bowl and cover it tightly with plastic wrap. Microwave it on high for five minutes, mixing it two or three times.<BR>2. Remove the plastic wrap and add the hot bean paste, the soy sauce, and the rock sugar and stir. Cover it tightly with fresh plastic wrap and microwave another five minutes, then take it out of that oven, let it rest for five minutes and once again microwave it for five minutes.<BR>3. Mix cornstarch mixture with all that is n the bowl, and let this rest for two minutes. Now put it in a large pot and simmer for half an hour. At this point, taste the meat around a knuckle piece and see if it s tender. If not simmer for another fifteen minutes and taste it again. It may need a third fifteen minute simmering. When it is tender, put it in a pre-heated casserole.<BR>4. In a medium-size pot, add the oil and the ground pork, and stir it continuously for three minutes, then add the cabbage pieces and stir-fry for five minutes. Put this mixture into the pork knuckle casserole, sprinkle with the cilantro, and serve.<BR>CROCODILE SOUP Ingredients: 1 Tablespoon vegetable oil 1 pound crocodile meat ½ pound roasted pork belly, sliced thin 1/4 pound fresh ginseng, sliced thin 10 red dates, pits removed and discarded 5 slices fresh ginger 6 fresh Chinese back mushrooms, soaked, their stems discarded 1 whole fried bean curd stick, broken into smalll pieces 2 teaspoons oyster sauce 2 teaspoon thin soy sauce 2 teaspoons white rock sugar, crushed Preparation: 1. Heat wok, add the oil, and the crocodile meat and the pork belly slices and stir-fry for ten minutes, then add two quarts of boiling water, reduce the heat, and simmer for half an hour or until both meats are tender and can be torn apart with chopsticks. 2. Add the rest of the ingredients and simmer another ten minutes, then serve. OYSTER PURSE OMELETTES Ingredients: 1 Tablespoon vegetable oil 5 eggs 5 teaspoons oyster sauce ½ teaspoon coarse salt 5 bowls freshly cooked hot rice Preparation: 1. Hat wok or large fry pan, then add the oil, and finally the eggs, one at a time. 2. When the whites are set, a one teaspoon of the sauce on top of each yolk, then fold the egg each egg over in half sealing in the sauce. 3. Put each folded egg on top of the hot rice, yolk side up, and serve. PORK TENDONS, BRAISED AND STUFFED Ingredients: 1/4 pound dried pork tendons, soaked for one hour in boiling water 1/4 pound boneless and skinless minced carp 2 Tablespoons lard 1 egg white 1 scallion, minced 2 slices fresh ginger, peeled and minced 2 Tablespoons minced Jinhua or Smithfield ham 1 sprig coriander, minced 1 Tablespoon Chinese rice wine 1 Tablespoon sesame oil 1 Tablespoon chicken fat ½ teaspoon ground black pepper 1 cup cold chicken stock 2 Tablespoons cornstarch 2 teaspoons <I>Shao xing</I> or another rice wine 1 teaspoon sesame oil <B><I>Preparation:</B>/i&gt;<BR>1. Squeeze water from soaked tendons, then slit each one partway making a pocket. 2. Mix carp, lard, egg white, scallion, ginger, ham, rice wine, sesame oil, chicken fat, and the ground pepper and divide into as many batches as there are tendons to stuff. Put them on an oiled heat-proof plate and steam for forty-five minutes or until t');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BD5509FE43765F4A90C48D8BA4A36831', X'5E9B7735C145CF4CAD4864F8780E1FBD', X'5E9B7735C145CF4CAD4864F8780E1FBD', 0, 'javascript:', 'Piquant Pigs Knuckles', 2, NULL, 0, 'Piquant Pigs Knuckles', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2100', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pigs knuckles, chopped or sawed into pieces about two-inches<BR>5 scallions, each tied in a knot<BR>1 carrot, peeled and cut into eight pieces<BR>5 slices fresh ginger, cut into pieces<BR>½ <I>daikon,</I> peeled and cut into two-inch pieces<BR>2 Tablespoons lard<BR>5 Tablespoons Sichuan bean paste<BR>5 Tablespoons dark soy sauce<BR>2 Tablespoons dark rock sugar<BR>2 tablespoons cornstarch mixed with the same amount of cold water<BR>1 Tablespoon vegetable oil<BR>1/4 pound ground pork<BR>½ pound Chinese cabbage, cut into two-inch pieces<BR>3 Tablespoons coarsely minced cilantro<BR><I><B>Preparation:</B></I><BR>1. Put the pig knuckle pieces, the scallion knots, carrots, ginger, and <I>daikon</I> pieces, and the lard into a very large glass or ceramic bowl and cover it tightly with plastic wrap. Microwave it on high for five minutes, mixing it two or three times.<BR>2. Remove the plastic wrap and add the hot bean paste, the soy sauce, and the rock sugar and stir. Cover it tightly with fresh plastic wrap and microwave another five minutes, then take it out of that oven, let it rest for five minutes and once again microwave it for five minutes.<BR>3. Mix cornstarch mixture with all that is n the bowl, and let this rest for two minutes. Now put it in a large pot and simmer for half an hour. At this point, taste the meat around a knuckle piece and see if it s tender. If not simmer for another fifteen minutes and taste it again. It may need a third fifteen minute simmering. When it is tender, put it in a pre-heated casserole.<BR>4. In a medium-size pot, add the oil and the ground pork, and stir it continuously for three minutes, then add the cabbage pieces and stir-fry for five minutes. Put this mixture into the pork knuckle casserole, sprinkle with the cilantro, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'544F5A566426D74DB36C90A3E722F090', X'E2C3A2A1C9F12D4D955FBE402A2B74A3', X'E2C3A2A1C9F12D4D955FBE402A2B74A3', 0, 'javascript:', 'Preparing Jellyfish', 2, NULL, 0, 'Preparing Jellyfish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1730', '<TD><RECIPE>When you buy jellyfish commercially, be they whole or slivered, rinse them well; many have been preserved with lots of salt. Then, drop them into boiling water for less than half a minute. We do ours for twenty seconds on the clock. That short amount of time will keep them from getting rubbery. Then fish them out fast and next quickly drop them into cold, preferably ice water. Then drain and slice them finely into slivers. If purchased whole, the way we prefer purchasing, they are ready to prepare. Should you purchase yours in plastic bags, whether refrigerated or frozen, before any of the above, rinse them thoroughly, then go on to the soaking step. Jellyfish can then be used blanched as above, tossed with vinegar and other flavorings, or stir-fried. If doing the latter, do that very quickly, too. Below are a few recipes to expand your use of these bobbing swimming critters. That can enable you to enjoy them more than ever before. When cooking them, do that very quickly, too. This is really a pre-preparation step; it needs to be done before any and every jellyfish recipe.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9C6F17DF6F48304E97FFB9640B83C2AB', X'05EAF4B57742B94BAC417774E69A445D', X'05EAF4B57742B94BAC417774E69A445D', 0, 'javascript:', 'Rabbit in Hot Bean Sauce', 2, NULL, 0, 'Rabbit in Hot Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2117', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>2 rabbits, bones removed, cut into bite-size pieces, fresh or dried<BR>1 large onion, peeled and cut into wedges<BR>1 teaspoon Sichuan peppercorns tied into a spice bag<BR>2 Tablespoons vegetable oil<BR>2 Tablespoons grated fresh ginger<BR>2 Tablespoons mashed fermented black beans<BR>2 Tablespoon thin soy sauce<BR>2 Tablespoon sesame oil<BR>1 Tablespoon hot sauce<BR>2 cups freshly prepared rice<BR><B><I>Preparation:</B></I><BR>1. Heat one quart water in a wok or large pot, add the rabbit, onion, and the Sichuan peppercorns in their spice bag. If usig a fresh rabbit, simmer for fifteen minutes, if using a dried one, simmer for one hour; then discard the spice bag.<BR>2. Heat oil in a wok or fry pan, and fry the ginger for half a minute, then add the black beans and soy sauce and the drained rabbit and stir-fry for one minute before adding the sesame oil and the hot sauce. Continue stir-frying for two or three minutes more. Then serve over hot cooked rice.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'892B28A709BEA94F9002046CE4020FA7', X'10EE1AE73C186A42ADD5E36ABD629E45', X'10EE1AE73C186A42ADD5E36ABD629E45', 0, 'javascript:', 'Rabbit Stew, Chinese Style', 2, NULL, 0, 'Rabbit Stew, Chinese Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=323', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 rabbit, cut into serving-size pieces<BR>salt and pepper to taste<BR>2 Tablespoons cornstarch<BR>4 Tablespoons peanut oil<BR>4 slices fresh ginger<BR>1 clove garlic<BR>1 small onion, cut into one-inch wedges<BR>2 Tablespoons oyster sauce<BR>1 Tablespoon hoisin sauce<BR>2 teaspoons dark soy sauce<BR>1 teaspoon sugar<BR>1 cup chicken stock<BR>1 medium carrot, sliced<BR>2 stalks celery, sliced<BR>1 cup Chinese radish (daikon), sliced<BR>1 teaspoon sesame oil<BR>2 teaspoons shaoxing wine<BR><B><I>Preparation:</B></I><BR>1. Dust rabbit pieces with salt and pepper and cornstarch.<BR>2. Heat oil in wok and brown the rabbit, a few pieces at a time. Remove to a plate and continue until all are done.<BR>3. Fry ginger and garlic in the same wok, add onions and stir-fry for one minute.<BR>4. Add the rabbit and the three sauces, sugar, and stock and simmer for one hour. Then transfer to a casserole and bake at 325 degrees F for another hour; then add carrots, celery, and radish slices and cook another half an hour.<BR>5. Stir in sesame oil and wine and serve with steamed rice.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'8171D3E4F18E53438D6A9618DEA2DE43', X'45D71AC3DDD1AE439163D92AD4C0277F', X'45D71AC3DDD1AE439163D92AD4C0277F', 0, 'javascript:', 'Rabbit Stew, Dongbei Style', 2, NULL, 0, 'Rabbit Stew, Dongbei Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2114', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 fresh rabbit, cut into eight to ten pieces<BR>2 Tablespoons cornstarch or potato starch<BR>2 Tablespoon oyster sauce<BR>1 Tablespoon hoisin sauce<BR>1 Tablespoon dark soy sauce<BR>1 teaspoon granulated sugar<BR>1/2 cup chicken stock<BR>2 Tablespoons vegetable oil<BR>1 onion, cut into wedges<BR>1 walnut-size knob of fresh ginger, cut into eighths<BR>2 stalks celery, cut at an angle into one-inch pieces<BR>1 large carrot. peeled and angle-cut into one-inch pieces<BR>6-inch piece of daikon, peeled and angle cut into one-inch pieces<BR>1 teaspoon sesame oil<BR>2 Tablespoons rice wine<BR><B><I>Preparation:</B></I><BR>1. Dust the rabbit with the flour and set aside for half an hour or until it wets.<BR>2. Mix the three sauces, sugar, and the stock, and set this aside.<BR>3. Heat a wok or a large fry pan, add the oil, and fry the rabbit for five minutes, turning often. Then add the ginger, celery, carrot, and all the vegetable pieces and stir-fry for two minutes, then add the daikon and stir-fry all for another minute.<BR>4. Mix the sauces and toss with the rabbit. Now simmer for one hour.<BR>5. Add the sesame oil and the wine, stir well, and serve.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'956EF82DABB65F4C9BC1E48B38C1C601', X'DE7F698EA60B054EA580E73CE1474141', X'DE7F698EA60B054EA580E73CE1474141', 0, 'javascript:', 'Red Dates and Hasma', 2, NULL, 0, 'Red Dates and Hasma', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=239', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 Tablespoon defrosted and soaked <I>hasma</I><BR>1 slice fresh ginger <BR>12 almonds, skins removed<BR>4 pitted dried red dates<BR>1 Tablespoon clear/white rock sugar<BR><I><B>Preparation:</I></B><BR>1. Soak <I>hasma</I> for two hours in fresh warm tap water, then drain and rinse well. If the pieces are large, cut them into smaller sections.<BR>2. Bring two cups of cold water to the boil, add the <I>hasma</I> with the ginger and simmer for ten minutes, drain and set aside reserving the liquid.<BR>3. Put the <I>hasma</I>, its soaking liquid, and all else but the sugar into a bowl with a tight fitting cover. Add two more cups of water and cover. Put in the steamer and steam over boiling water for two hours, then add the sugar, and steam another half hour.<BR>Serve hot or warm<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C44389EFC491AB48A5895148B3E9BB61', X'A33948B7C1E87146930DDD77BA611322', X'A33948B7C1E87146930DDD77BA611322', 0, 'javascript:', 'Red-cooked Ostrich', 2, NULL, 0, 'Red-cooked Ostrich', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=308', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 pound ostrich meat<BR>1 teaspoon cornstarch or waterchestnut flour<BR>1 teaspoon corn oil<BR>10 cloves garlic, smashed with side of a cleaver<BR>2 teaspoons sugar<BR>4 cups chicken stock <BR>2 Tablespoons black vinegar<BR>10 bay leaves<BR>2 Tablespoons Shaoxing wine<BR>1 cup thin soy sauce<BR>3 Tablespoons cornstarch (optional)<BR><I><B>Preparation:</I></B><BR>1. Cut meat into one inch cubes and dust with the cornstarch.<BR>2. Heat oil and fry the garlic for one minute, then add sugar, stock, vinegar, and the bay leaves. Bring to the boil, reduce heat, skim the surface, and simmer uncovered for an hour.<BR>3. Add the wine and soy sauce and simmer another half hour. It is optional to thicken the sauce mixing cornstarch with an equal amount of water and boiling it for half a minute or until clear. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7DBB69CC12E8AA46B0CE193445195570', X'0DB6BCBCCC9DE44397AFE1586775C8F6', X'0DB6BCBCCC9DE44397AFE1586775C8F6', 0, 'javascript:', 'Red-cooked Venison', 2, NULL, 0, 'Red-cooked Venison', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=904', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound venison leg meat, sinews removed<BR>2 Tablespoons vegetable oil<BR>2 scallions, each one tied in a knot<BR>2 slices fresh ginger, peeled and pounded<BR>3 cloves fresh garlic, peeled and smashed<BR>1/4 cup dark soy sauce<BR>3 Tablespoons Chinese rice wine<BR>1 Tablespoon brown sugar<BR>1 Tablespoon honey<BR>1 teaspoon ground black pepper<BR><B><I>Preparation:</B></I><BR>1. Cut venison into one and a half inch cubes.<BR>2. Heat wok or pot, add oil, and brown the venison on all sides, then add scallions, ginger and garlic, and stir-fry for one minute.<BR>3. Add soy sauce and stir-fry one minute then add one cup of water, the rice wine, sugar, honey, and black pepper. Simmer for two hours, stirring every ten minutes until meat is tender and almost no liquid remains. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BA6103674405AB40A15F1670270B4932', X'E3EEB569827B9447A437A301C6CC97A6', X'E3EEB569827B9447A437A301C6CC97A6', 0, 'javascript:', 'Rice in Intestine', 2, NULL, 0, 'Rice in Intestine', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1458', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound sticky rice<BR>1 small intestine of a pig<BR>2 cup of raw pigs blood<BR>2 scallions, cut in half-inch pieces<BR>1 teaspoon salt<BR>1/2 teaspoon ground pepper<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon vegetable oil<BR><B><I>Preparation:</B></I><BR>1. Soak the sticky rice overnight, then cook it for one hour or until soft.<BR>2. Put the cooked rice in a pot, add the pig`s blood, scallions, salt, pepper, oil, and rice wine and mix them.<BR>3. Fill this mixture into a pig''s small intestine until it is full then tie both ends with string.<BR>4. Oil an oven-safe dish and put small intestine on it. In a steamer, put this over rapidly boiling water and steam it for ten minutes. Then remove and cut it into two-inch pieces. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'267D5F4714071943B672D48DE16A2FE8', X'4764F5B8AB492E458570E1A9CA4AE168', X'4764F5B8AB492E458570E1A9CA4AE168', 0, 'javascript:', 'Rice Noodles with Chicken and Jellyfish', 2, NULL, 0, 'Rice Noodles with Chicken and Jellyfish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=855', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 whole skinless and boneless chicken breast<BR>2 dried jellyfish, rinse well<BR>2 fresh rice noodle sheets<BR>1 carrot<BR>2 stalks celery<BR>1/2 teaspoon salt<BR>1 teaspoon sesame oil<BR>2 Tablespoons thin soy sauce<BR>1 Tablespoon lightly toasted sesame seeds<BR><B><I>Preparation:</B></I><BR>1. Simmer chicken breast for eighteen minutes, cool enough to handle, then hand shred into very thin pieces, about two inches long.<BR>2. After immersing jellyfish in hot water, cool immediately in ice water, and roll and cut into the thinnest of slivers, as in previous instructions.<BR>3. Shred rice noodles and cut into four-inch lengths.<BR>4. Cut carrot into two-inch long slivers, then blanched for thirty seconds and cut into an ice water bath.<BR>5. String celery, cut into two inch slivers, and blanched for fifteen seconds then put into an ice water bath.<BR>6. Drain carrots and celery, mix with chicken breast and jellyfish pieces, and then toss with one quarter of the rice noodles. Mix these with salt, sesame oil, this soy sauce and sesame seeds, and toss well.<BR>7. Spread the remaining rice noodles on a serving platter and top with the chicken, jellyfish, and vegetable mixture, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4F3B196A008BA148A9A1970B43FFA6B6', X'3D6DDE586C10024E82F4898B08DA6A94', X'3D6DDE586C10024E82F4898B08DA6A94', 0, 'javascript:', 'Rice with Pork and Seaweed Sheets', 2, NULL, 0, 'Rice with Pork and Seaweed Sheets', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1909', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 Tablespoons minced pork shoulder<BR>1 teaspoon thin soy sauce<BR>1 teaspoon cornstarch<BR>1 scallion, green part only, minced<BR>1 and 1/2 cups cooked rice<BR>1/2 teaspoon coarse salt<BR>1/2 teaspoon bonito powder or fish bouillon<BR>dash ground white pepper<BR>1 sheet purple laver, also known as <I>nori</I>, thinly shredded<BR><B><I>Preparation:</B></I><BR>1. Mix pork with the soy sauce, cornstarch, scallion, bonito or fish bouillon, and the ground pepper.<BR>2. Boil three to four cups of water, add the pork and simmer for two minutes, then add all the other ingredients except for the purple laver strips, then simmer another minute.<BR>3. Serve in individual bowls, sprinkle some seaweed on top of each bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E8DB4B3554E8CC47A9A7328053B52633', X'673795026EDF9D4C8490456A26864ED4', X'673795026EDF9D4C8490456A26864ED4', 0, 'javascript:', 'Savory Bird''s Nest Soup I', 2, NULL, 0, 'Savory Bird''s Nest Soup I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=77', '<TD><RECIPE><B><I>Ingredients:</B></I> 2 ounces refined bird''s nest<BR>1 and 1/2 quarts homemade chicken broth<BR>4 slices fresh ginger<BR>1 Tablespoon Shaoxing wine<BR>1 boned chicken breast, minced<BR>1/4 cup minced Virginia ham<BR>2 teaspoons cornstarch mixed with 1/4 cup cold water<BR>1 egg white, lightly beaten<BR>salt to taste<BR><B><I>Preparation:</B></I> 1. Cover the bird''s nest with one quart of luke-warm water. Let it stand for one to two hours until the bird''s nest is fully expanded. Then drain off the water.<BR>2. Place bird''s nest in a bowl, add one quart of chicken broth and the ginger. Cover the bowl and place it in a steamer and steam over boiling water at low heat for one and a hlaf hours.<BR>3. Transfer the soup to a three-quart sauce pan, add the minced chicken and ham and the remaining broth. Boil voer medium heat for two minutes.<BR>4. Add the cornstarch mixture and stir well.<BR>5. Turn off the heat and stir in the egg white stirring constandly. Serve hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B616A1A84D68424094BAA5E25C4BF0D5', X'554F830F86DA08449137AB3F5BE57980', X'554F830F86DA08449137AB3F5BE57980', 0, 'javascript:', 'Savory Bird''s Nest Soup II', 2, NULL, 0, 'Savory Bird''s Nest Soup II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1431', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 ounces refined bird''s nest<BR>1 and 1/2 quarts homemade chicken broth<BR>1 Tablespoon Shaoxing wine<BR>1 boned chicken breast, minced<BR>1/4 cup minced Virginia ham<BR>2 teaspoons corn starch mixed with one-quarter cup of water<BR>1 egg white, lightly beaten<BR>salt to taste<BR><B><I>Preparation:</B></I><BR>1. Cover bird''s nest with one quart of luke-warm water. Let it stand for one to two hours until bird''s nest is fully expanded, then drain off the water.<BR>2. Place bird''s nest in a bowl, add one quart of chicken broth and the ginger. Cover the bowl and place it in a steamer. Steam over boiling water at low heat for one and a half hours.<BR>3. Transfer the soup to a three quart sauce pan, add the minced chicken and ham and the remaining chicken broth. Boil over medium heat for two minutes.<BR>4. Add corn starch water and mix well.<BR>5. Turn off heat and stir in the egg white with constant stirring. Serve hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'67A255D05E7D20499E28B80241163FEE', X'FE0FB245DA8DA44E90847E305BEA9BE9', X'FE0FB245DA8DA44E90847E305BEA9BE9', 0, 'javascript:', 'Savory tripe', 2, NULL, 0, 'Savory tripe', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2007', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pig tripe, pre-prepared, then cut into serving-size pieces<BR>20 black mushrooms, soaked, stems discarded<BR>10 peppercorns, crushed<BR>20 shelled gingko nuts, shelled<BR>12 water chestnuts, peeled and rinsed<BR>salt to taste<BR>4 dried bean curd sticks broken into one- to two-inch pieces<BR>1 can abalone, rinsed and thinly sliced<BR><B><I>Preparation:</B></I><BR>1. Put tripe, mushrooms, peppercorns, gingko nuts, water chestnuts, and some salt in a pot and cover it with cold water, and simmer it for and hour and a quarter which is seventy-five minutes.<BR>2. Then add the bean curd sticks and cook for another thirty minutes before adding the abalone which needs to be simmered for five minutes. Serve hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3598E04966CF614CB508BAEC60979E12', X'2B050731FABC404CAA777CA104EED794', X'2B050731FABC404CAA777CA104EED794', 0, 'javascript:', 'Scallions and Sea Cucumber', 2, NULL, 0, 'Scallions and Sea Cucumber', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1322', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound small soaked or frozen sea cucumber<BR>4 Tablespoons vegetable oil, divided in two parts<BR>2 cups two-inch pieces of scallions<BR>4 slices fresh ginger<BR>4 cloves garlic<BR>4 cups chicken broth<BR>1 teaspoon salt<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoons thin soy sauce<BR>1/2 Tablespoon sugar<BR>1 Tablespoon cornstarch mixed with one tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Soak the sea cucumber overnight, drain and soak another six to ten hours, then drain and set aside.<BR>2. Blanch sea cucumber in boiling water for one minute, then rinse in cold water.<BR>3. Heat half the oil and stir-fry the scallions, ginger, and garlic over low heat for three minutes until soft but not browned. Discard the garlic and ginger, but keep the oil.<BR>4. Heat the chicken broth, and add salt and wine, add the sea cucumber and simmer covered until tender.<BR>5. Deep fry the scallions in the reserved oil until brown, then put them and one cup of broth in a bowl, add wine and soy sauce and steam for three minutes.<BR>6. Heat the unused oil then fry the sugar until it caramelizes, and when it does, add all the steamed ingredients and the rest of the chicken broth. Bring almost to the boil, stir in the cornstarch mixture and stir until thickened and clear, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C3D322731D38574688513090F471431B', X'BFE07A947FD3174C843155B27F327BF5', X'BFE07A947FD3174C843155B27F327BF5', 0, 'javascript:', 'Sea Cucumber and Roe', 2, NULL, 0, 'Sea Cucumber and Roe', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1323', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup crab or another roe<BR>4 large whole pre-soaked sea cucumbers, preferably those with spiked exteriors, intestines and other interior parts removed<BR>1 Tablespoon vegetable oil<BR>2 scallions, minced<BR>1 slice fresh ginger, minced<BR>1 cup reduced fish broth<BR>1 teaspoon thin soy sauce<BR>1 teaspoon abalone soy sauce<BR>1 teaspoon Chinese rice wine<BR>1/2 teaspoon sesame oil<BR>1 Tablespoon cornstarch mixed with twice that amount of cold water<BR><B><I>Preparation</B></I><BR>1. Steam roe for five minutes, mash with a fork or fingers, and set them aside.<BR>2. Blanch sea cucumbers for ninety seconds, then remove and rinse well.<BR>3. Heat oil, stir-fry the scallions and ginger until fragrant (about half minute). Add fish broth, both soy sauces, and the rice wine and simmer the sea cucumbers on very low heat for three to five minutes, then remove the sea cucumbers to a platter.<BR>4. Mix cornstarch and water, stir into the fish broth, add the roe and the sesame oil and bring to the boil stirring until thickened. Pour over the sea cucumbers and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7433FB20D9F69A45A5FE5064E19ED5E3', X'526AF3C3BB6841458F208982FCF497DC', X'526AF3C3BB6841458F208982FCF497DC', 0, 'javascript:', 'Sea Cucumber for Soup', 2, NULL, 0, 'Sea Cucumber for Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1575', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 to 2 soaked sea cucumbers<BR>2 cups chicken broth<BR>1 Tablespoon rice wine<BR>1 scallion, tied in a knot<BR>3 slices fresh ginger<BR>1 chicken thigh, blanched in boiling water for two minutes, then discard the water<BR><B><I>Preparation:</B></I><BR>1. Put sea cucumber in two cups chicken stock. Add the rice wine and the scallion, and the fresh ginger slices. Bring to just below the boil, and simmer covered for one hour.<BR>2. Add the blanched chicken thigh and simmer until the sea cucumber is very soft before removing the sea cucumber and the chicken. Set them aside. Remove the scallion and the ginger and discarding them.<BR>3. Cut the chicken and the sea cucumber into half-inch cubes, and use them in the broth as a soup, or put them and their liquid into another prepared soup, heat, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'564B3CFFD78E224DA094CF51A69871FB', X'A17347E70C59324F84BCAB8A47943764', X'A17347E70C59324F84BCAB8A47943764', 0, 'javascript:', 'Sea Cucumber in Brown Sauce', 2, NULL, 0, 'Sea Cucumber in Brown Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1576', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 Tablespoon lard<BR>4 scallions, trimmed and angle sliced<BR>4 slices fresh ginger, peeled and slivered<BR>4 prepared soft sea cucumbers; follow the Sea Cucumber for Soup recipe then separate the liquid from the solids<BR>1 Tablespoon rendered chicken fat<BR>1 teaspoon sesame oil<BR>1/4 cup thin soy sauce<BR>1 Tablespoon Chinese rice wine<BR>1 teaspoon sugar<BR>1 Tablespoon and 1 teaspoon Chinese sweet flour sauce<BR>3 Tablespoons cornstarch mixed with three tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Heat the lard in a wok or fry pan, add scallions and ginger and stir-fry for one minute, then cut and add the sea cucumber pieces and stir-fry another two minutes before adding the chicken fat, sesame oil, soy sauce, rice wine, sugar, and the sweet flour sauce.<BR>2. Stir this for one minute, then add the liquid from the Sea Cucumber for Soup recipe and the chicken pieces and bring to the boil, reduce the heat, and simmer for ten minutes.<BR>3. Stir the cornstarch mixture, and simmer until the sauce thickens, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'728AD2CCB433DD439A27A9D66B9E23D3', X'3CB309A804B3894F97060716D5D5CB00', X'3CB309A804B3894F97060716D5D5CB00', 0, 'javascript:', 'Sea Cucumber with Meat Sauce', 2, NULL, 0, 'Sea Cucumber with Meat Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1578', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large or 2 small dried sea cucumbers<BR>8 scallions, six tied into knots, two cut into one-inch pieces<BR>2 large pieces of fresh ginger, one sliced thinly, the other crushed with the side of a cleaver<BR>1 pound belly pork, cut into eight pieces<BR>1 pork bone<BR>2 Tablespoons dark soy sauce<BR>2 Tablespoons Chinese sweet flour sauce<BR>6 large Chinese black mushrooms, soaked one hour, stems removed and discarded, the caps quartered<BR>2 Tablespoons Chinese rice wine<BR>2 Tablespoons vegetable oil<BR>1 can (eight and a half ounces) sliced bamboo shoots, drained<BR>2 Tablespoons rendered chicken fat<BR>2 Tablespoons cornstarch mixed with two tablespoons of cold water<BR><B><I>Preparation:</B></I><BR>1. Soak sea cucumber(s) for twenty-four hours, changing the water three of four times, then put them into a large pot, cover with two quarts of water and bring to the boil. Reduce the heat and simmer for three hours or until they are soft.<BR>2. Remove sea cucumber(s) from the pot and remove and discard the innards. Discard the water, and soak the sea cucumber(s) in cold water for one hour. Then cut into half-to one-inch pieces.<BR>3. Put sea cucumber pieces back into the pot, pour two quarts of boiling water on the pieces and add the scallion knots and crushed ginger, the pieces of pork belly, and the pork bone, and simmer everything uncovered for one hour. Allow everything to cool in the pot, and when cool, discard the scallion knots and the ginger, and remove the pork belly and cut into small cubes. Remove any meat from the bone and cut them into small pieces. Put then into the pot, add the soy sauce, sweet flour sauce, and the rice wine, and one cup of water and simmer for half an hour before returning the sea cucumber pieces. Simmer everything together another half hour stirring every ten minutes.<BR>4. Heat the vegetable oil in a small wok or fry pan, add the ginger slices, scallion pieces, and bamboo shoots and stir-fry for two minutes. Add the chicken fat, stir well, and put this mixture into the pot with the sea cucumber mixture.<BR>5. Bring all to the boil, add the cornstarch mixture, and stir until thickened before pouring everything into a deep bowl. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2AA3ADBE4AA0C547ABB030DB659D1728', X'238DAB274A6E0D4BA04A43E6FE4EA0DC', X'238DAB274A6E0D4BA04A43E6FE4EA0DC', 0, 'javascript:', 'Sea Cucumber with Pork Balls', 2, NULL, 0, 'Sea Cucumber with Pork Balls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2293', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 horny-edged sea cucumber, soaked for one to two days, water changed every for to six hours<BR>½ pound hand-chopped or ground pork loin<BR>1 teaspoon cornstarch<BR>6 Tablespoons Chinese rice wine, divided in half<BR>½ teaspoon salt and ground white pepper mixture<BR>1 cup vegetable oil for deep frying<BR>5 slices peeled fresh ginger<BR>2 scallions, each cut into one-inch pieces<BR>2 Tablespoons dark soy sauce <BR>1 Tablespoon granulated sugar<BR>3 Tablespoons chicken broth or stock<BR>2 Tablespoons cornstarch mixed with three tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Cut the sea cucumber into one-inch pieces.<BR>2. Make pork balls mixing the pork, cornstarch, rice wine and salt and pepper, and make this mixture into one-inch balls.<BR>3. Heat oil in wok or deep pan and fry the pork balls until golden, about two or three minutes; then remove and drain them and discard the oil or save it for another use.<BR>4. With oil clinging to the sides of the wok or pan, add the ginger and scallion pieces and stir-fry these for one minute. Then add sea cucumber, the rest of the wine, soy sauce, sugar, and chicken stock, and the pork balls. Bring this to the boil, cover, reduce the heat and simmer for ten minutes, remove the cover and raise the temperature and boil, stirring for another ten minutes (the liquid should be almost gone).<BR>5. Add the cornstarch water, and stir until it thickens, about a minute, then pour this over the sea cucumber-pork balls, transfer this to a pre-heated bowl, stir once or twice, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'754C99563C01AA4CADD86E1B577447F9', X'C766D248E9C1704A96574D47B78AF31E', X'C766D248E9C1704A96574D47B78AF31E', 0, 'javascript:', 'Sea Cucumber with Pork Balls', 2, NULL, 0, 'Sea Cucumber with Pork Balls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1497', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 horny- or spiked-edge sea cucumber, soaked for one to two days, water changed every six hours<BR>1/2 pound hand-chopped or ground pork loin<BR>1 teaspoon cornstarch<BR>6 Tablespoons Chinese rice wine, divided in half<BR>1/2 teaspoon salt and ground white pepper mixture<BR>1 cup vegetable oil for deep frying<BR>5 slices peeled fresh ginger<BR>2 scallions, each cut into one-inch pieces<BR>2 Tablespoons dark soy sauce<BR>1 Tablespoon granulated sugar<BR>3 Tablespoons chicken broth or stock<BR>2 Tablespoons cornstarch mixed with three tablespoons cold water<BR><B><I>Preparation:</B></I><BR>1. Cut the sea cucumber into one-inch pieces.<BR>2. Make pork balls mixing the pork, cornstarch, rice wine and salt and pepper, and make this mixture into one-inch balls.<BR>3. Heat oil in wok or deep pan and fry the pork balls until golden, about two or three minutes; then remove and drain them and discard the oil or save it for another use.<BR>4. With oil clinging to the sides of the wok or pan, add the ginger and scallion pieces and stir-fry these for one minute. Then add sea cucumber, the rest of the wine, soy sauce, sugar, and chicken stock, and the pork balls. Bring this to the boil, cover, reduce the heat and simmer for ten minutes, remove the cover and raise the temperature and boil, stirring for abour another ten minutes or until the liquid will be almost evaporated.<BR>5. Add the cornstarch water, and stir until it thickens, about a minute, then pour this over the sea cucumber-pork balls, transfer this to a pre-heated bowl, stir once or twice, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'06D9292531C19245A6E87DF4C4FAE7CC', X'CBD4C4556EC7444C88B2FF387B3B083A', X'CBD4C4556EC7444C88B2FF387B3B083A', 0, 'javascript:', 'Sea Cucumber with Vegetables', 2, NULL, 0, 'Sea Cucumber with Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1577', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup vegetable oil<BR>20 wonton wrappers, sliced into thin strips<BR>4 soaked soft sea cucumbers, innards removed and discarded. each cut into half-inch pieces<BR>2 scallions, cut into one-inch pieces<BR>5 slices fresh ginger, peeled<BR>1/4 cup canned bamboo shoots, drained and sliced<BR>1/4 cup canned straw mushrooms, each cut in half<BR>1/4 cup sliced baby <I>bok cai</I><BR>3 Tablespoons thin soy sauce<BR>1 Tablespoon rice wine<BR>1 teaspoon sesame oil<BR>1 teaspoon sugar<BR>1/2 cup chicken stock<BR>4 Tablespoons cornstarch mixed with four teaspoons cold water<BR><B><I>Preparation:</B></I><BR>1. Heat vegetable oil in a wok or fry pan and fry the wonton strips, continually stirring them until they are tan and crisp, then remove from the oil and drain them on paper towels before putting them all around the outside two or three inches of a large deep platter.<BR>2. Add the sea cucumber pieces to the wok and fry for two minutes, then remove them to paper towels.<BR>3. Add scallions and ginger to the remaining oil in the wok and stir-fry them for one minute, then remove them and add them to the sea cucumber pieces.<BR>4. Add bamboo shoots, straw mushrooms, and the bak cai into the wok and stir-fry for two minutes before draining and putting these vegetables together with the sea cucumber pieces. Next discard all but one tablespoon of the oil.<BR>5. Reheat the tablespoon of the oil, add the soy sauce, rice wine, sesame oil, sugar, and the chicken stock and bring to the boil. Return the scallions, ginger, all the vegetables, and the sea cucumber to the wok and stir, then allow to boil for one minute before adding the cornstarch mixture. Stir as it boils, and when thickened, pour this onto the platter leaving one inch of the fried wonton strips without any sauce mixture, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2D5AEF7976FBF742B6C91C1960886927', X'A0017EA20BD1B745AB139D5FBEEEE662', X'A0017EA20BD1B745AB139D5FBEEEE662', 0, 'javascript:', 'Sea Cucumber, Sui-Yuan', 2, NULL, 0, 'Sea Cucumber, Sui-Yuan', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1290', '<TD><RECIPE>Sea cucumber itself has no taste. It is hard to cook it well because it has much sand and can taste fishy. A kind with spikes is the best choice. First dip it in clean water in order to clear away sand. Use broth to stew it. Then cook it in chicken soup and broth until flaccid. Accessories are Chinese mushroom and black mo-er, because they are all a similar black color. Or prepare to stew the sea cucumber before the day of a dinner party so that it can be flaccid. I have seen a method for cooking it in observer Qians home. This excellent method is to mix mustard and chicken soup with cold sea cucumber or first cut sea cucumber into dice then stew it with diced bamboo and diced Chinese mushrooms in chicken soup. The chef in minister Jiangs home uses thin sheets of bean curd, drumstick, and Chinese mushrooms stewed with sea cucumber which is a good practice. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C876CF09889F8E48910F94C2031CE835', X'6B5E95EF14489F4A938EEB24177962CF', X'6B5E95EF14489F4A938EEB24177962CF', 0, 'javascript:', 'Seaweed Rolls', 2, NULL, 0, 'Seaweed Rolls', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1913', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup hair seaweed, soaked for fifteen minutes in cold water, drained, and then cut in half-inch pieces<BR>2 Tablespoons cornstarch<BR>1 potato, simmered until almost soft, peeled, then mashed or diced very fine<BR>1/4 cup cooked green peas<BR>1 sweet red pepper, seeded and diced<BR>10 water chestnuts, diced<BR>1/2 cup Chinese celery, diced fine<BR>2 Tablespoons cornstarch<BR>1 teaspoon coarse salt<BR>1 teaspoons mushroom powder<BR>1 teaspoon granulated sugar<BR>1/2 teaspoon ground white pepper<BR>3 cups meat or vegetable stock<BR><B><I>Preparation:</B></I><BR>1. Mix hair seaweed and cornstarch, then spread this on a sheet of plastic wrap about five inches by ten inches.<BR>2. Mix potato. Peas, red pepper, water chestnuts, and celery with the second amount of cornstarch with the salt mushroom powder, sugar, and pepper and spread this over the hear seaweed, press it down, then roll tightly and twist plastic wrap around the ends to hold it together.<BR>3. Put stock in a pot or in a steamer with the rolled materials and simmer or steam for ten minutes. Remove the roll and the plastic wrap, and allow to cool for fifteen minutes or more. Cut slices, put then on their sides on a platter and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'B068B34AD923C242915D3373509E85D7', X'97D3E67885ABCE408A2B4BFD9838DB52', X'97D3E67885ABCE408A2B4BFD9838DB52', 0, 'javascript:', 'Shark Bone Tonic Soup', 2, NULL, 0, 'Shark Bone Tonic Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=250', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 to 2 pounds shark bones, chopped into two-inch pieces<BR>1 pound spare ribs, pre-cut into two-inch pieces<BR>4 slices fresh ginger<BR>2 cloves garlic, do not peel<BR>4 tablespoons wolfberries<BR>1 spring celery, Chinese celery preferred, tied into a knot<BR>5 Chinese chives, tied in a knot<BR>1 small Chinese yam<BR>1/2 black bone or regular chicken, cut into four pieces<BR>1 teaspoon salt<BR>1/4 cup dried lily bulb petals<BR><I><B>Preparation:</I></B><BR>1. Bring large amount of water to a boil, add shark bones and pork ribs and blanch them for one minute, remove, drain and discard this water. <BR>2. Bring 10 cups of water to the boil, reduce heat and add the bones and all other ingredients except the lily bulbs, and simmer for four hours. Remove the chicken and ribs and take meat from the bones and set aside. Add the lily bulbs and cook the soup for another hour and strain, reserving them and the wolfberries.<BR>3. Add the meat, lily bulbs and wolfberries to the remaining stock, heat thoroughly, then serve.<BR><B>Note:</B> If you can not locate wolfberries, also known as goji berries, dry cranberries are an acceptable substitute, but use only half the requested quantity.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'199D02AF8E12D54DB34F9F280F3A9722', X'B79DBF6BEBEFB04D87C82BED392D104B', X'B79DBF6BEBEFB04D87C82BED392D104B', 0, 'javascript:', 'Shredded Rabbit with Fish Flavor', 2, NULL, 0, 'Shredded Rabbit with Fish Flavor', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2115', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>!/2 pound rabbit meat, shredded<BR>3 Tablespoon lard<BR>2 slices fresh ginger, peeled and slivered<BR>2 red peppers, seeded and slivered<BR>2 scallions, angle-cut<BR>2 teaspoons white vinegar<BR>1 Tablespoon sesame oil<BR>1 Tablespoon chili oil<BR>3 clove garlic, peeled and slivered<BR>2 scallions, angle cut<BR>2 Tablespoons soy sauce<BR>2 teaspoon granulated sugar<BR>1/2 teaspoon salt<BR><B><I>Preparation:</B></I><BR>1. Heat a wok or a fry pan, add the oil, and stir-fry the rabbit for two minutes, then drain removing the shreds from the oil.<BR>2. Mix all the rest of the ingredients and toss with the rabbit, marinating them in the refrigerator overnight. Drain this about two hours before planning to eat.<BR>3. Drain the meat and out on a serving plate and serve qhen ready to eat this dish.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'C5958B093D952140B8E5CFBBF7D6C177', X'937D478FD3F3744E8E882BD254B2FC75', X'937D478FD3F3744E8E882BD254B2FC75', 0, 'javascript:', 'Shredded Snake with Rainbow Vegetables', 2, NULL, 0, 'Shredded Snake with Rainbow Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=226', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 one-ounce package bean thread noodles<BR>1 cup corn oil<BR>3 wonton wrappers, cut in half then into very thin slivers.<BR>2 Tablespoons rice wine<BR>2 Tablespoons red vinegar<BR>1/4 pound Chinese celery, cut into one-inch-long pieces<BR>1 green pepper, cut into thin one-inch-long slivers<BR>1/4 sweet red pepper, cut into thin one-inch-long slivers<BR>1 small daikon, cut into thin one-inch-long slivers<BR>2 Tablespoons dried tangerine peel, soaked and slivered as are the vegetables<BR>1 tablespoon corn oil<BR>2 cloves garlic, slivered as are the vegetables<BR>2 slices fresh ginger, cit into thin slivers<BR>1 Tablespoon wine, dry sherry, or vermouth<BR>1/2 pound snake meat, cut into one-inch thin slivers<BR><I><B>Preparation:</I></B><BR>1. Pull bean threads apart in half.<BR>2. Heat cup of oil and fry half the bean threads until they expand completely, drain them, put on paper towels and repeat with the other half. Put both batches on a platter and crush them ever so slightly, just until the top seems flat.<BR>3. Fry the wonton wrapper pieces, drain and set aside on a paper towel.<BR>4. Soak snake meat in wine and vinegar for one hour, drain and discard the liquid.<BR>5. Mix celery, peppers, daikon, and tangerine peel and set aside.<BR>6. Heat oil in a wok and fry garlic and ginger until it gives off a strong aroma.<BR>7. Fry the vegetable mixture for one minute, then add the wine and the snake meat and stir-fry about three minutes, Put on the bean threads and serve topping with the fried wonton pieces.<BR>Serves six to eight.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D1077FA6D908D043BE20FF5E573439B8', X'99E5B384E5C002448FFBC990055F6392', X'99E5B384E5C002448FFBC990055F6392', 0, 'javascript:', 'Shrimp Balls with Fish Maw', 2, NULL, 0, 'Shrimp Balls with Fish Maw', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1131', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 ounce re-hydrated fish maw, diced<BR>1/4 pound peeled shrimp, veins removed, and minced fine<BR>1 egg white<BR>1/2 Tablespoon minced carrot<BR>1/4 cup chicken stock<BR>1/4 teaspoon sugar<BR>1/4 teaspoon salt<BR>dash ground white pepper<BR>1 teaspoon cornstarch mixed with one tablespoon cold water<BR><B><I>Preparation:</B></I><BR>1. Blanch fish maw for half minute, then drain, and set aside to cool.<BR>2. Divide shrimp into eight parts, and roll each part into a ball. Then roll them into the cooled fish maw to coat them.<BR>3. Steam over rapidly boiling water for four minutes, then remove to small serving bowl.<BR>4. Mix stock and egg white, bring to a boul and immediately add sugar, salt, white pepper, and cornstarch mixture. Stir until slightly thickened, about one minute, then pour over the shrimp balls, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'545B4013EB64D341AE6EA357BB346933', X'299DD930560FE148BF34CFD61E557DB7', X'299DD930560FE148BF34CFD61E557DB7', 0, 'javascript:', 'Silkworm Pupas with Cucumber', 2, NULL, 0, 'Silkworm Pupas with Cucumber', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=238', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 pound silkworm pupas<BR>2 medium-sized cucumbers<BR>2 Chinese celery stalks, cut into one-inch lengths<BR>2 Tablespoons corn oil<BR>2 slices fresh ginger<BR>1 clove garlic, mashed<BR>1 scallion, cut into one-inch lengths<BR>salt and pepper, to taste<BR>1 Tablespoon Chinese rice wine<BR><I><B>Preparations:</I></B><BR>1. Clean the silkworm pupas, rinse them several times, drain and set aside.<BR>2. Peel the cucumbers lengthwise leaving alternate strips of skin on them, then cut them lengthwise into four parts, remove and discard the seeds. Next cut the cucumber pieces diagonally into one-inch lengths and set aside.<BR>3. In a skillet, heat the oil then add the ginger, scallions, and garlic and stir-fry until the garlic turns golden. Then quickly add the celery and fry two minutes then add the cucumber strips and fry another minute.<BR>4. Add the silkworm pupas, salt and pepper, and the wine and toss quickly then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'64C1C51E477297449CA35528C57551D9', X'5F7D42367C78604695BD4B198B9C0B07', X'5F7D42367C78604695BD4B198B9C0B07', 0, 'javascript:', 'Silkworm Pupas with Ground Pork', 2, NULL, 0, 'Silkworm Pupas with Ground Pork', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=237', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>6 to 8 ounces of silkworm pupas<BR>6 ounces ground pork butt<BR>2 Tablespoons cornstarch<BR>1 teaspoon sesame oil<BR>1 Tablespoon soy sauce<BR>1 Tablespoon oyster sauce<BR>1 teaspoon sugar<BR>1 clove garlic, minced<BR>1 scallion, minced<BR>1 Tablespoon chopped fresh cilantro<BR>1/2 teaspoon ground ginger<BR>1 Tablespoon rice wine<BR>1 teaspoon solid shortening<BR><I><B>Preparation:</I></B><BR>1. Clean then chop the silkworm pupas. Then mix them with all the other ingredients except the solid shortening, and add half cup of water to thismixture. Stir only in one direction when mixing them.<BR>2. Rub solid shortening on a baking dish and pour the mixture into it. The size of the dish should be such that the contents are about one inch deep.<BR>3. Steam for ten minutes and then serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DD48B14B0A2F514AB9EC8E7D079F6D2A', X'E52BF7C16A73B849B4840B2C77F11A8A', X'E52BF7C16A73B849B4840B2C77F11A8A', 0, 'javascript:', 'Smoked Fish with Jellyfish', 2, NULL, 0, 'Smoked Fish with Jellyfish', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1734', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup dried jellyfish, blanched for half minute, immediately put into ice water, and slivered when cold<BR>1/2 pound boneless and skinless smoked eel, shredded<BR>1 carrot, rated<BR>1 stalk celery, cut into small slivers<BR>1 pickling cucumber, peeled and cut into small slivers<BR>2 scallions, slivered<BR>1/4 cup coriander leaves, coarsely chopped<BR>2 Tablespoons granulated sugar<BR>2 Tablespoons grated fresh ginger<BR>5 Tablespoons white rice vinegar<BR>1 Tablespoon vegetarian oyster sauce<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Put the jellyfish, eel, carrot, celery, and cucumber in a serving bowl and toss well, then refrigerate for one hour.<BR>2. Remove the bowl from the refrigerator, and stir in the scallions, coriander, sugar, and the ginger.<BR>3. In a small jar, put in the vinegar and the oyseter sauce, and shake well, then pour over the jellyfish mixture, toss lightly, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'4ABDDFC3F02873478238257B2A9F73FA', X'26712C0112AE934FB318AE5C2F360CB7', X'26712C0112AE934FB318AE5C2F360CB7', 0, 'javascript:', 'Snake Stew', 2, NULL, 0, 'Snake Stew', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=223', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>3 to 5 snakes<BR>1 roast duck <BR>1 chicken, freshly killed preferred<BR>4 dried scallops, cleaned and soaked in one cup of water until soft<BR>8 medium-size dried shiitake mushrooms, soaked until soft, stems discarded, and cut into thin slivers, reserving the mushroom water<BR>3 pounds fresh bamboo shoots<BR>1 teaspoon salt<BR>2 Tablespoons soy sauce<BR>1 Tablespoon cornstarch<BR>dash of white pepper<BR>2 Tablespoons corn oil<BR>1 garlic clove, crushed<BR>1 stalk freesh coriander, cut into one-inch lengths<BR>2 scallions, washed, slivered lengthwise, then cut into one-inch lengths<BR>2 Tablespoons water chestnut flour<BR>3 lemon leaves, rinsed and cut into thin slivers<BR>3 big fresh white or yellow chrysanthemums (see note below), washed and petals pulled out<BR>5 or more soft tortilla chips, sliced and fried for one minute in two tablespoons corn oil<BR><I><B>Preparation:</I></B><BR>1. Skin the snake, then separate snake meat and bones and put the bones into a cheesecloth bag.<BR>2. Bone the duck and separate the meat, bones, and skin. Discard all fat.<BR>3. Skin the chicken. Leave meat on the bones, and discard all visible fat and skin; then cut into four large pieces if it does not fit into the soup pot.<BR>4. Put the chicken, duck bones, and snake bones into a pot of cold water and boil for ten minutes. Discard the water and soak the rest of the ingredients in cold water for ten minutes, then discard this water. This is the ''Crossing the cold river'' process spoken of in the Souper Soup article in this issue.<BR>5. Add three quarts of cold water to the chicken mixture, the dried scallops and their water, and the mushroom water. Bring all of this to the boil then simmer for two hours.<BR>6. Remove chicken and bones, discard the bones or use again another time and separate the chicken meat aside.<BR>6. Prepare the bamboo shoots by peeling them and removing all fibrous material, especially the ends. Boil a quart of water and add the salt and the bamboo shoots and boil this for ten minutes. Drain and rinse under cold running water. The shoots should be ivory. If any part is yellow, taste and if bitter, discard that part. Cut the cooked shoots into slivers and set aside. You will only have about one pound of good quality crispy shoot slivers.<BR>7. Shred duck meat, scallops, and chicken meat and mix with soy sauce. Add cornstarch and white pepper and mix well. Also shred snake meat, but set that aside separately.<BR>8. Heat wok and add the corn oil and garlic and cook only half a minute until the garlic starts to turn brown. Add snake meat and toss for a few seconds then add the other meat mixture and stir-fry one minute before adding the cilantro and scallions slivers.<BR>9. Add the reserved Souper Soup made in this recipe one ladle at a time until there are equal amounts of meat and soup. Reserve the rest for another use. <BR>10. Mix the waterchestnut flour with half cup of cold water and stir well before adding it slowly to the wok. Stir until it thickens. Serve in one large bowl or in individual bowls for each diner. <BR>11. Mix lemon leaves, chrysanthemum petals and tortilla crisps and each diner can add some to own to their bowl of stew.<BR>Serves 8 to 10<BR><B>Note:</B> I use the ones called ''crabs legs'' in Chinese.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'58615284B89B124DA38D4F67C45AC659', X'97DD8F818ACC7F4BAAE759BDFF3EA850', X'97DD8F818ACC7F4BAAE759BDFF3EA850', 0, 'javascript:', 'Spicy Sea Cucumber', 2, NULL, 0, 'Spicy Sea Cucumber', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=830', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 pieces of soaked sea cucumber (approximately four ounces or one whole large sea cucumber, cut in chunks)<BR>1 scallion, cut into one-inch pieces<BR>2 slices ginger<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon vegetable oil<BR>1/2 pound cooked pork, cut into half-inch slices<BR>1 Tablespoon hot bean paste<BR>1 cup chicken broth or stock<BR>1/2 Tablespoon soy sauce<BR>1 teaspoon sugar<BR>1 teaspoon rice wine mixed with half-teaspoon sesame oil<BR>1 cucumber, cut in half the long way, then slice each on an angle half-inch thick<BR>1/4 teaspoon ground Sichuan pepper<BR>1/2 teaspoon sesame oil<BR>1 stalk fresh garlic, shredded<BR>1 Tablespoon cornstarch mixed with one tablespoon of cold water<BR><B><I>Preparation:</B></I><BR>1. Put sea cucumber in a pot with scallion, ginger, the tablespoon of wine, and three cups of water, and boil for five minutes, then drain reserving the sea cucumber pieces.<BR>2. Heat oil, and stir-fry the pork, then add hot bean paste, chicken stock, soy sauce, sugar, and the teaspoon of rice wine,. Stir broth, then add the sea cucumber and the cucumber slices and cook these for three minutes.<BR>3. Now add the ground Sichuan pepper and the sesame oil, then the shredded garlic and the cornstarch mixture. Stir until thickened, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3CCB7BC245CF6A4296EFAEDC44B9215F', X'8A2F0EBD36513942882EB8CC9FE1F18B', X'8A2F0EBD36513942882EB8CC9FE1F18B', 0, 'javascript:', 'Steamed Chicken with Angelica', 2, NULL, 0, 'Steamed Chicken with Angelica', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=78', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>I game hen, cut in half<BR>10 dried longans, cut in half<BR>4 slices of fresh ginger, 2 minced and two left whole<BR>2 cloves garlic, cut in half<BR>4 dried jujubes, cut in half <BR>1/2 teaspoon sugar<BR>3 Tablespoons Chinese angelica<BR>1/2 teaspoon salt<BR>4 cups boiling water<BR><I><B>Preparation:</I></B><BR>1. Blanch game hen in boiling water for one minute, drain and dry.<BR>2. Put all other ingredients in a casserole, put the gam hen halves on top of the solid ingredients. Cover the casserole or Yunnan pot and put into a steamer.<BR>3. Steam for three hours over rapidly boiling water. Be sure to check the level of water every fifteen minutes or as needed.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'96DA5D6F4DA9F34089C62B48582824C2', X'8E0D3D5F4071CF4CA545205A4A716024', X'8E0D3D5F4071CF4CA545205A4A716024', 0, 'javascript:', 'Steamed Fish with Tree Seeds', 2, NULL, 0, 'Steamed Fish with Tree Seeds', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=257', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 sea trout or red snapper, cleaned, rinsed, and dried<BR>1/4 teaspoon oil<BR>1 dried chili pepper, seeded and minced fine<BR>1 scallions, minced, keep green and white separate<BR>1 slice fresh ginger, cut in thin strips<BR>1 teaspoon soy sauce<BR>1 teaspoon sesame oil<BR>1 cup pickled tree seeds with three tablespoons of their pickling liquid<BR><B><I>Preparation:</I></B><BR>1. Cut five or six slashes into the flesh on each side of the fish and set it on a plate rubbed with the oil.<BR>2. Sprinkle chili peppers, the white scallion pieces, and the ginger on top and drizzle soy sauce and sesame oil on this then put the tree seeds and their juice on top.<BR>3. Steam for ten minutes, remove from steamer, spread fish with green scallion pieces and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'525CFA6F2C8FC94C9E7ECF0A6B7BD897', X'1E1A940B211BEC4A8F5C7EA7C2B996C7', X'1E1A940B211BEC4A8F5C7EA7C2B996C7', 0, 'javascript:', 'Steamed Seafood with Tree Seeds', 2, NULL, 0, 'Steamed Seafood with Tree Seeds', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=256', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1/2 pound shrimp or three or four whole crabs, cleaned<BR>2 Tablespoons shaoxing wine<BR>2 slices fresh ginger, minced<BR>1 Tablespoon sesame oil<BR>1 teaspoon sugar<BR>1 teaspoon light soy sauce<BR>1/2 cup pickled tree seeds, rinsed once<BR>1 scallion, minced<BR>dash white pepper<BR><B><I>Preparation:</I></B><BR>1. Prepare the seafood by removing the black vein from the shrimp but leave tails on or by chopping the crabs into five or six pieces.<BR>2. Put seafood on plate and drizzle with wine, ginger, sesame oil, sugar, soy sauce, and the tree seeds. Steam over boiling water for ten minutes, then remove.<BR>3. Sprinkle with scallion pieces and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BFE8E35607496C4AB7683098D84F0063', X'C3F9A6E51A6B4C46904D46AD8C5176FA', X'C3F9A6E51A6B4C46904D46AD8C5176FA', 0, 'javascript:', 'Stewed Kidneys and Chestnuts', 2, NULL, 0, 'Stewed Kidneys and Chestnuts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2104', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pair kidneys, their white tissues removed and discarded, then each cut into four pieces, hatch marks made on their smooth sides<BR>½ teaspoon coarse salt<BR>1 Tablespoon vegetable oil<BR>4 slices fresh ginger, peeled<BR>1 pound chestnuts, cooked and peeled<BR>1 carrot, peeled and wedge cut<BR>1 Tablespoons cornstarch<BR>1 Tablespoon thin soy sauce<BR><B><I>Preparation:</B></I><BR>1. Mix kidneys with the salt, then rinse then thoroughly, and dry them with paper towels.<BR>2. Heat oil in a wok or heavy fry pan, and fry the ginger for one minute, then add the chestnuts and the carrot pieces and half cup of water, and simmer for five minutes.<BR>3. Add the kidneys and stir-fry for two minutes at most, and then mix cornstarch with the soy sauce and stir until thickened; next add the sugar and the rice wine, stir well, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'CA79F964D43CAE4D83E140FDC550C6B6', X'E1C966E332F3684EB6AC9D104B9E3E5C', X'E1C966E332F3684EB6AC9D104B9E3E5C', 0, 'javascript:', 'Stir-fried Beef Tongue', 2, NULL, 0, 'Stir-fried Beef Tongue', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1218', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>9 and 1/2 ounces beef tongue (the rear section and not the tip, preferred)<BR>salt and pepper to taste<BR>2 Tablespoons beaten egg<BR>1/2 Tablespoon potato starch<BR>1/2 Tablespoon cake flour<BR>1 cup soybean oil<BR>1 teaspoon scallion oil<BR>1/4 teaspoon minced scallion<BR>1/4 teaspoon minced ginger<BR>1/4 teaspoon minced garlic<BR>1 Tablespoon Shaoxing rice wine<BR>2 teaspoons soy sauce<BR>1 teaspoon granulated sugar<BR>1 teaspoon chicken stock<BR>1 Tablespoon citrus zest, cut in fine strips<BR>1 fresh red chili pepper, seeded and cut in fine strips<BR>1 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Cover and cook tongue in boiling water for thirty minutes, then remove and shock it in cold water and peel its exterior membrane, then cut tongue into one-quarter by one-quarter by two-inch strips.<BR>2. Coat tongue with mixture of salt and pepper, beaten egg, potato starch, and cake flour.<BR>3. In a wok, heat soybean oil to 360 degrees F and deep-fry tongue strips separating the pieces until it is cooked about seventy percent through, then drain on paper towels.<BR>4. Remove all oil from the wok, add scallion oil and keeping heat low, add scallion, ginger, and garlic until fragrant; then turn heat up and add the tongue, rice wine, soy sauce, sugar, and soupin that order.<BR>5. Add the citrus zest and chili pepper strips, and finally the sesame oil, and remove to a serving plate.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2F6DACE2E4C7564E8D78291AF258D1FB', X'3EA9B670D5C73A409B8AB75B7D0A132C', X'3EA9B670D5C73A409B8AB75B7D0A132C', 0, 'javascript:', 'Stir-fried Snow Frog with Egg White', 2, NULL, 0, 'Stir-fried Snow Frog with Egg White', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=240', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 ounce defrosted and soaked <I>hasma</I><BR>2 cups chicken stock<BR>1/2 teaspoon salt<BR>1 Tablespoon corn oil<BR>5 egg whites<BR>5 ounces water or non-fat milk<BR>1 and 1/2 Tablespoons cornstarch<BR>2 ounces crab meat, all cartilage removed<BR>1 Tablespoon finely minced Smithfield ham or coriander<BR><I><B>Preparation:</I></B><BR>1. Soak <I>hasma</I> in two cups of warm water for an hour, rinse, drain, and discard the soaking liquid, and cut into smaller pieces.<BR>2. Re-soak the <I>hasma</I> in the stock for an hour, then simmer together for half an hour, and drain. The stock can be reserved and used for other purposes.<BR>3. Beat egg whites until almost stiff. Mix water or milk with the cornstarch then fold into egg whites, and finally gently fold in the crab meat and the <I>hasma</I>.<BR>4. Heat oil and discard any that does not coat the pan, then fry the egg white mixture until it is the consistency of soft scrambled eggs. <BR>5. Transfer this to a pre-warmed serving dish, garnish with ham or coriander, and serve.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'74FE3833CE96C34E8592444C38478602', X'85288A8B941CE3459B5CEA2A9D249B67', X'85288A8B941CE3459B5CEA2A9D249B67', 0, 'javascript:', 'Street-stall Tripe', 2, NULL, 0, 'Street-stall Tripe', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=482', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>12 bay leaves<BR>2 pounds honeycomb tripe<BR>2 Tablespoons corn oil<BR>8 star anise<BR>6 slices fresh ginger, each cut into four pieces<BR>1/4 cup dark soy sauce<BR>2 Tablespoons chili paste with garlic<BR>1 cup rice wine<BR>1/2 cup beef broth<BR><B><I>Preparation:</B></I><BR>1. Bring ten cups of water to the boil, add bay leaves and tripe and return it to the boil. When at a rolling boil, cover and turn off the heat. Let it rest for two hours.<BR>2. Remove trip from liquid and cut into two-inch pieces. Dry with paper towels.<BR>3. Heat oil in Chinese clay casserole, if available, and add fresh ginger pieces and the soy sauce, chili paste, rice wine, and broth, and simmer for five minutes.<BR>4. Add tripe and simmer uncovered for another ten minutes. Cover and put into the refrigerator overnight. In the morning, remove ginger pieces and the star anise.<BR>5. Before serving, reheat and mix with cornstarch water, and bring to the boil and allow to thicken.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'BB78274CE3010849A8B859E38D8E2355', X'50FF00F768DF7341AF15526B1F87D81A', X'50FF00F768DF7341AF15526B1F87D81A', 0, 'javascript:', 'Stuffed Bitter Melon Soup', 2, NULL, 0, 'Stuffed Bitter Melon Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1958', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 prepared pork, bitter melon, and black bean recipe<BR>5 dried Chinese black mushrooms, soaked, stems removed and discarded, then cut into quarters<BR>10 shrimp, peeled, veins removed and discarded, and cut in half the long way<BR>1 piece spiced bean curd, cut in half, then cut into thin strips<BR>1 quart chicken broth<BR><B><I>Preparation:</B></I><BR>1. Prepare the recipe for Bitter Melon, Pork, and Black Beans but do not use the cornstarch thickened sauce on the stuffed slices. Instead put one stuffed slice on the bottom of each bowl for each diner.<BR>2. Mix the thickened sauce from the recipe above with the quart of chicken stock and bring it to the boil, stirring often.<BR>3. Using a large spoon, pour the heated liquid into each bow deflecting it so it does not move the meat mixture from the bitter melon on the bottom of each soup bowl, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'F8FA110967AE8D48AA45D5912C992768', X'2003CF02D1C33F41AB17C1AAE1371B6B', X'2003CF02D1C33F41AB17C1AAE1371B6B', 0, 'javascript:', 'Stuffed Tripe, Swatow Style', 2, NULL, 0, 'Stuffed Tripe, Swatow Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=717', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>12 ounces beef or pig tripe, as two equal size pieces<BR>1/2 cup glutinous rice<BR>6 Chinese black mushrooms<BR>1/2 pound dried chestnuts<BR>6 ounces minced fatty roast pork<BR>2 ounces Yunnan or Smithfield ham<BR>4 ounces shrimp<BR>2 Tablespoons cornstarch or water chestnut flour<BR>1 chicken bouillon cube<BR><B><I>Preparation:</B></I><BR>1. Wash and then dry the trip and cut into two large pieces.<BR>2. Soak the rice in one cup of cold water for an hour, then drain.; and soak the mushrooms in two cups warm water, then drain and discard the stem, and reserve the liquid.<BR>3. Coarsely chop mushrooms, chestnuts, roast pork, ham, and shrimp. Keeping each item separately.<BR>4. Put water chestnut flour on the inside of the tripe pieces. Then mix pork and shrimp, and put half of it on half of each piece of tripe. Then mix mushrooms and chestnuts with the rice, and put half of it on top of each batch of the pork/shrimp mixture. Fold rest of the trip over this and pat firmly but do not squash the filling.<BR>5. Strain the mushroom water. Put the filled tripe pieces in a heat proof bowl, and pour mushroom water on top of them. Steam over simmering water for t two hours, then serve, cutting each piece of tripe into three pieces.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'6B9C29D17CB24548836974168A824DDB', X'793259AB39F3C046AAB73D99E3FB30C6', X'793259AB39F3C046AAB73D99E3FB30C6', 0, 'javascript:', 'Swallow''s Nest in Souper Soup', 2, NULL, 0, 'Swallow''s Nest in Souper Soup', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=224', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 ounces dried swallows nest (another term for birds nest)<BR>1 ounce Yunnan ham (you can substitute with Smithfield ham), finely shredded<BR>8 cups Souper Soup<BR><I><B>Preparation:</I></B><BR>1. Soak swallows nest in clear water for at least two hours. Discard any feathers and other debris and discard the water. Then rinse the nest half dozen times.<BR>2. Add two cups of the Souper Soup to the nest and steam over boiling water for one hour. Be sure to check the steamer periodically to see if there is an adequate amount of water in it; if not, add more. When done, discard all of the Souper Soup liquid and mix the ham shreds with the swallow nest pieces and put them into a soup tureen.<BR>3. Bring the rest of the Souper Soup to the boil, pour over the nest and ham mixture and serve.<BR>Serves 6 to 8.</TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DE953BFBFBABA54EA438AF6E9F85F3B7', X'00106FB2D2A42445B1A01EB2303BE324', X'00106FB2D2A42445B1A01EB2303BE324', 0, 'javascript:', 'Sweet and Sour Duck Liver', 2, NULL, 0, 'Sweet and Sour Duck Liver', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=265', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>4 duck livers (or 10 chicken livers, if duck liver is unavailable)<BR>2 teaspoon of rice wine<BR>1 teaspoon light of soy sauce<BR>1 teaspoon mushroom soy sauce<BR>1 Tablespoon cornstarch<BR>1 cup corn oil<BR>1 teaspoon sesame oil<BR>4 Tablespoons rice vinegar<BR>1 Tablespoon black vinegar<BR>1 Tablespoon light soy sauce<BR>1 Tablespoon cornstarch<BR>1 Tablespoon granulated sugar<BR>1 Tablespoon light brown sugar<BR><I><B>Preparation:</B></I><BR>1. Mix livers with rice wine, both soy sauces and the cornstarch.<BR>2. Heat oil and quickly deep fry the livers, then drain.<BR>3. Mix last six ingredients and bring to the boil. Add livers, simmer for two minutes (but only one minute if using chicken livers). Serve immediately.<BR><B>Note:</B> Garnishing with one tablespoon minced coriander looks lovely.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'79ACED363675CB48A060C7EDB692D77C', X'BD375D93DB4A1B48ABA9380061E4C72F', X'BD375D93DB4A1B48ABA9380061E4C72F', 0, 'javascript:', 'Tendon and Tripe, Shanghai Style', 2, NULL, 0, 'Tendon and Tripe, Shanghai Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=481', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound beef tendon<BR>1 pound pork tripe<BR>1 cup dark soy sauce<BR>1 Tablespoon corn oil<BR>6 green tops of scallions, minced<BR>1 teaspoon black bean and chili sauce<BR>3 slices fresh ginger, minced coarsely<BR>1 one-inch by two-inch piece of Chinese brown sugar<BR>1/2 cup red wine lees<BR>1/2 cup fresh golden needle mushrooms, separated into small clumps<BR>1 teaspoon five-spice powder<BR>1 Tablespoon chicken bouillon powder<BR>1/2 cup bean sprouts, tails removed<BR>1/2 cup peeled and sliced cucumber<BR><B><I>Preparation:</B></I><BR>1. Blanch beef tendon and tripe for two minutes each in two separate pots. Cool for one hour in the soy sauce.<BR>2. Heat corn oil and stir-fry the scallion tops for one minute, then add tendon and tripe pieces and the soy sauce. Add black bean and chili sauce, ginger pieces, Chinese brown sugar, wine lees, golden needle mushrooms five spice powder, and powdered bouillon. Bring to boil, then reduce heat to a simmer, cover, and cook for two hours. Then allow to sit in the sauce for another hour.<BR>3. Remove the tendon and tripe and cut them into very thin slices and set them on a platter. Put bean sprouts and cucumber slices around them.<BR>4. Reduce sauce mixture to about three tablespoons and pour over the tendons and tripe, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9F18E7B3FA31114FB9964065CF27A66D', X'38DC62ECEC20D14FB413EA7EB79E6AE6', X'38DC62ECEC20D14FB413EA7EB79E6AE6', 0, 'javascript:', 'Tofu and Tree Seeds', 2, NULL, 0, 'Tofu and Tree Seeds', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=258', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 piece firm tofu, cut into eight pieces<BR>1 onion, cut in triangular wedges<BR>6 shiitake mushrooms, soaked and quartered<BR>6 straw mushrooms. cut in half<BR>1 Tablespoon water chestnut flour or cornstarch<BR>1/4 cup corn oil<BR>1/2 cup pickled tree seeds, rinsed once<BR>1 teaspoon sesame oil<BR>1 teaspoon light soy sauce<BR>1 Tablespoon cornstarch mixed with three tablespoons water<BR><I><B>Preparation:</I></B><BR>1. Dry tofu, onions, and both mushrooms then toss them together with the water chestnut flour.<BR>2. Heat oil and fry half the tofu mixture for one minute then drain on paper towels. Repeat with the remaining mixture.<BR>Reserve one tablespoon of the oil.<BR>3. Heat oil, add the tree seeds and the fried ingredients and stir-fry one minute then add half cup of water, sesame oil. and soy sauce and cover, simmering this for five minutes. Remove the cover and thicken with the cornstarch and water mixture, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'76AC6E267A817D4F859158C7D91BF3D4', X'08B92EFCD1248B40AEA6206FBA72970A', X'08B92EFCD1248B40AEA6206FBA72970A', 0, 'javascript:', 'Tripe, Bamboo Shoots, and Ginger Sauce', 2, NULL, 0, 'Tripe, Bamboo Shoots, and Ginger Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=488', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>2 pounds tripe, cut into four to six pieces<BR>3 chicken bouillon cubes mixed with two quarts of boiled water<BR>2 Tablespoons soy sauce<BR>4 Tablespoons Chinese rice wine<BR>1 pound fresh bamboo shoot tips<BR>1 teaspoon dry mustard<BR>3 Tablespoons cornstarch<BR>1 teaspoon corn oil<BR>4 scallions, angle sliced thin<BR>1 teaspoon black vinegar<BR>6 slices fresh ginger, minced very, very fine<BR><B><I>Preparation:</B></I><BR>1. Put tripe in a large pot and cover it with the chicken soup. Add soy sauce and rice wine and bring to the boil and simmer for ninety minutes. Remove the cover, bring the contents to the boil and reduce until a cup of liquid remains. Remove tripe and cool; reserve the liquid.<BR>2. Peel bottoms of the bamboo shoots and put them in another pot with two quarts of water and simmer for one hour or until almost tender. Drain and thinly slice bamboo shoots on an angle.<BR>3. Slice cooled tripe pieces into thin strips and mix well with the mustard and cornstarch.<BR>4. Heat oil and stir-fry the tripe and the bamboo shoots for two minutes. Add the reserved liquid and bring to the boil. After it slightly thickens, pour into a shallow bowl.<BR>5. Mix ginger and vinegar to make a sauce are pour this on top of the tripe; gently toss and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'DEBB8F74D7795A4999DFEC2F38752BDC', X'10219F04FCF2164B87069E5B9FD2AD1B', X'10219F04FCF2164B87069E5B9FD2AD1B', 0, 'javascript:', 'Tripe, Home-style', 2, NULL, 0, 'Tripe, Home-style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1595', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 cups vegetable oil<BR>1/2 to 1 pound tripe<BR>3 slices fresh ginger, peeled and sliced then cut into thin strips<BR>3 cloves garlic, peeled and cut into thin slices<BR>1/2 to 1 Tablespoon chili paste with garlic and black beans<BR>1 Tablespoon Shaoxing wine<BR>1 Tablespoon Chinese black vinegar<BR>2 tablespoons Chinese white rice vinegar<BR>1 teaspoon sugar<BR>1 tablespoon cornstarch mixed with two tablespoons of cold water<BR>3 Tablespoons minced scallion or another green<BR><B><I>Preparation:</B></I><BR>1. Blanch the tripe in water for one minute, then put it into cold water to cool it quickly.<BR>2. Heat oil in a wok or beep fry pan, and oil blanch the tripe for two minutes, then drain and set aside on paper towels. Set the oil aside for other uses.<BR>3. In the oil left in the wok or fry pan, about a tablespoon, stir-fry the ginger and garlic for one minute, then add the chili paste, wine, and vinegars, and toss until well mixed, then add the sugar and stir once more.<BR>4. Add the tripe and mix well, then stir-fry for one minute. Add the cornstarch mixture and boil for one minute or until thick, then remove to a bowl, toss with the greens, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'269B74E4DBE94244840D86B7E0995A1E', X'9F996B8F85A13F4C970451A96C36FC41', X'9F996B8F85A13F4C970451A96C36FC41', 0, 'javascript:', 'Turtle and Pork Casserole', 2, NULL, 0, 'Turtle and Pork Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=57', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>1 large piece of dried tangerine peel (about size of two quarters)<BR>10 dry shiitake mushrooms<BR>1/4 cup Shaoxing wine or dry sherry<BR>1 pound turtle meat<BR>2 Tablespoons cornstarch<BR>2 Tablespoons corn oil<BR>2 slices ginger root, cut in thin strips<BR>2 cloves garlic, peeled and sliced thin<BR>3 Tablespoons black bean sauce with garlic<BR>2 cups chicken stock<BR>2 pounds barbecued pork, cut in two-inch pieces<BR><I>Sauce ingredients:</I><BR>1/2 teaspoon salt<BR>1 teaspoon sugar<BR>1 Tablespoon cornstarch<BR>1 teaspoon sesame oil<BR>1 Tablespoon water<BR>1 Tablespoon thick soy sauce<BR><I><B>Preparation:</I></B><BR>1. Soak Tangerine peel in warm water for 20 minutes then mince it.<BR>2. Soak mushrooms in warm water for 20 minutes, then cut cut each mushroom in half.<BR>3. Drain tangerine peel and mushrooms, squeeze out excess water and mix with the wine. Let stand ten minutes, drain reserving the wine.<BR>4. If turtle is still in shell, boil for ten minutes, remove viscera and discard with claws and the head. Cut into 2-inch pieces, blanch for 5 minutes, rinse in clear water and drain. If cut up or frozen, do not boil just blanch, as indicated.<BR>5. Saute turtle in dry wok (no oil) until dry then remove and coat with cornstarch.<BR>6. Heat the wok, add oil and return the turtle to the wok. Stir for two minutes then add the mushrooms, tangerine peel, garlic, and ginger root. Stir-fry for two minutes.<BR>7. Add black bean sauce and mix well cooking until fragrant (about two minutes). Next add chicken stock and barbecued pork. Bring to the boil. Now transfer all ingredients to a clay pot or casserole. Cover and simmer for two hours on very low heat or put in oven at 300 degrees fahrenheit for the same amount of time. <BR>8. Add all the sauce ingredients, bring to the boil and allow to cook one more minute, then serve to eight to ten people.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'E2A45CCFE7AC0F4C81F9EA0A990DE14F', X'22759232B7484A4A948D6EF03C32D895', X'22759232B7484A4A948D6EF03C32D895', 0, 'javascript:', 'Turtle Casserole', 2, NULL, 0, 'Turtle Casserole', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=683', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 to 3 small turtles, each about half a pound, internal organs removed<BR>1 teaspoon salt<BR>2 Tablespoons corn oil<BR>12 Chinese mushrooms, soaked for half an hour, their stems removed, drained, reserving one cup of mushroom water<BR>½ pound roasted pork belly, cut into one-inch cubes<BR>6 slices fresh ginger<BR>6 cloves garlic, peeled and each one cut into quarters<BR>2 squares red bean curd, mashed<BR>1 Tablespoon mushroom soy sauce<BR>1 Tablespoon Chinese rice wine<BR>½ teaspoon Chinese black vinegar<BR>2 sticks bean curd, soaked, and cut into two-inch pieces<BR>1 to 2 Tablespoons cornstarch in an equal amount of cold water<BR><B><I>Preparation:</B></I><BR>1. Prepare turtles, as in first step of the turtle soup. After draining and drying them, rub them with the salt.<BR>2. Heat oil in the bottom of a heavy casserole and fry mushrooms and pork belly for two minutes. Then add ginger and garlic and fry another minute before adding red bean curd mash, and frying one minute more.<BR>3. Add turtle, soy sauce, rice wine, and vinegar, and two cups of boiling water, mix well, and simmer for two hours.<BR>4. Next, add bean curd stick pieces and simmer another twenty minutes.<BR>5. Thicken with some or all of the cornstarch water and boil just until thickened, stirring continuously, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'705164B1810FE54BA5A0348EBA8EF148', X'3E09F0E3E9662E44B19C281979260806', X'3E09F0E3E9662E44B19C281979260806', 0, 'javascript:', 'Turtle Stew', 2, NULL, 0, 'Turtle Stew', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=684', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 turtle, two to four pounds, internal organs removed<BR>2 Tablespoons corn oil<BR>2 scallions, tie each in a loose knot<BR>4 slices fresh ginger<BR>4 cloves garlic<BR>2 Tablespoons rice wine<BR>1 Tablespoon Chinese or another hard liquor, eighty-proof or higher<BR>2 Tablespoons mushroom soy<BR>1 Tablespoon brown rock sugar<BR>1 Tablespoon cornstarch mixed with two tablespoons cold water<BR>2 Tablespoons fresh coriander, minced<BR><B><I>Preparation:</B></I><BR>1. Remove head of turtle and discard, then wash with running cool water.<BR>2. Bring three quarts of water to the boil, and blanch the turtle in this water for two minutes, then remove.<BR>3. Heat oil in a wok, and stir-fry the scallions, ginger, and garlic for one minute. Add the rice wine, liquor, mushroom soy, rock sugar, and half cup boiling water. Bring all to the boil, reduce heat to low and simmer covered for two hours or until the turtle meat feels tender.<BR>4. Remove scallions, ginger, and garlic, and add cornstarch mixture and tir the sauce mixture until thickened.<BR>5. Put turtle and sauce in a preheated casserole, put coriander on top, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7A170AD0FD2FC5429DE336AA8B4C991F', X'FF936AC1C155FA4F83C244F244D2DDBC', X'FF936AC1C155FA4F83C244F244D2DDBC', 0, 'javascript:', 'Turtle with Ham and Web', 2, NULL, 0, 'Turtle with Ham and Web', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=685', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 turtles, about a pound each, shell discarded, internal organs removed<BR>1 teaspoon corn oil<BR>2 Tablespoons fresh shallots<BR>2 slices fresh ginger<BR>3 cloves garlic<BR>2 Tablespoons Chinese rice wine<BR>12 boneless chicken or duck webs<BR>1 extra-large black mushroom, soaked in two cups warm water for half hour, stem discarded, one cup mushroom water reserved<BR>1/4 pound Smithfield ham, sliced<BR>1/2 teaspoon salt<BR>1/2 teaspoon ground black or Sichuan pepper<BR>1 Tablespoon sesame oil<BR>2 Tablespoons cornstarch mixed with one tablespoon water<BR><B><I>Preparation:</B></I><BR>1. Cut legs off, and cut rest of the meat into pieces about no larger than two inches square.<BR>2. Heat corn oil and fry shallots, ginger, and garlic until fragrant, then add wine, ham, and turtle meat and simmer for about five minutes, then drain and reserve.<BR>3. Cut duck feet in half, then simmer in the remaining liquid for fifteen to twenty minutes.<BR>4. Mix reserved mushroom water with salt and pepper.<BR>5. Spread heat-proof bowl with the sesame oil, put mushroom at the bottom, then arrange larger pieces of ham, turtle, and duck feet one after another. Fill center with any remaining pieces and pack it down with your fingers, before pouring mushroom water over this, not disturbing the design made with the solid ingredients. Cover with parchment paper and set in a steamer over boiling water and steam for forty-five minutes.<BR>6. Carefully pour liquid into a pot and bring to the boil with the cornstarch mixture, stirring until somewhat thickened.<BR>7. Turn solid contents of the bowl carefully onto a platter, pour thickened liquid over it, and serve.<BR><B>Note:</B> If they can be located, use eight cooked turtle eggs cut in half, using them for decor, placing them around the dish.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'36077A7F6061DF449E3187591910AB5F', X'41D9D323129E8E41A2829CB03C96312D', X'41D9D323129E8E41A2829CB03C96312D', 0, 'javascript:', 'Venison in the Style of Lamb', 2, NULL, 0, 'Venison in the Style of Lamb', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=687', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 cup corn oil<BR>2 pounds of venison, sliced paper thin<BR>1 teaspoon salt<BR>1/2 teaspoon finely ground black pepper<BR>2 teaspoons finely grated fresh ginger<BR>2 cups dried Chinese black mushrooms, soaked for an hour, stems removed, and finely sliced<BR>1 piece, about a foot, sugar cane, cut into four pieces<BR>1 cup vegetable stock<BR>1 Tablespoon dry sherry<BR>2 cups finely sliced water chestnuts<BR><B><I>Preparation:</B></I><BR>1. Heat wok or heavy saucepan and add the oil and the venison. Stir for one minute then add salt, pepper, and ginger and stir once or twice before adding mushrooms and the sugar cane. Continue stir-frying for four or five minutes.<BR>2. Pour in stock and bring to the boil, then sprinkle with sherry.<BR>3. Put this into a heat-proof bowl and place on rack in a steamer. Steam for an hour and three-quarters, then add water chestnuts and stir; allowing it to steam another fifteen minutes. The serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9D8E0C8A75B9284889A36C3E234EC2F9', X'B0DF1490C436D1488F8A2DF6F1833C7A', X'B0DF1490C436D1488F8A2DF6F1833C7A', 0, 'javascript:', 'Wasabi-flavored Sea Cucumber', 2, NULL, 0, 'Wasabi-flavored Sea Cucumber', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=924', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pre-soaked medium sea cucumber<BR>1 cup chicken broth<BR>1 Tablespoon Chinese rice wine<BR>1 Tablespoon fermented wine lees<BR>1 teaspoon soy sauce<BR>1 teaspoon wasabi paste<BR>1/4 teaspoon salt<BR>1 teaspoon sugar<BR>1 Tablespoon cornstarch mixed with two tablespoons of water<BR>1/2 pound wasabi or mustard greens<BR>1 scallion, minced<BR><B><I>Preparation:</B></I><BR>1. Simmer sea cucumber in six cups of water for one hour. Remove from heat and cool in the water for one hour. Remove sea cucumber, check that insides are clean, and cut into one-inch pieces.<BR>2. Heat chicken broth, rice wine, wine lees, soy sauce, wasabi paste, salt, and sugar and simmer for five minutes.<BR>3. Add cornstarch mixture, and set aside.<BR>4. Steam wasabi greens or the mustard greens just until tender. Drain and set out on a serving platter.<BR>5. Bring chicken broth mixture to the boil, add sea cucumber pieces and stir until thick, and pour over the greens. Sprinkle scallion pieces on top. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'37475D953B9BF3419567742B2FE468B2', X'48A622DFECC02E45B0114825BA37A1E0', X'48A622DFECC02E45B0114825BA37A1E0', 0, 'javascript:', 'You Tiao with Ling Nuts', 2, NULL, 0, 'You Tiao with Ling Nuts', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1816', '<TD><RECIPE><I><B>Ingredients</I></B><BR>1/8 pound shrimp, peeled, their veins removed<BR>1 Tablespoon chicken broth or water<BR>16 ling nuts, boiled for half an hour, shells removed, alf of them mashed<BR>1/8 teaspoon each, salt and sugar<BR>1 Tablespoon cornstarch<BR>1 deep-fried cruller, (<I>you tiao</I>, cut into eight peces<BR>1/2 tablespoon vegetable oil<BR>1 clove garlic, peeled and mashed<BR>1 slice fresh ginger, slivered<BR>2 pieces <I>gai lan</I>, each cut into two sections<BR>1 carrot, peeled and cut into thin slices<BR><B><I>Preparation:</B></I><BR>1. Mix minced shrimp with the broth, salt, sugar, and cornstarch and set aside.<BR>2. Cut crullers down the length, not all the way through, then stuff with the reserved shrimp mixture.<BR>3. Heat a wok or large fry-pan, add vegetable oil, and stir-fry garlic and ginger one minute, add half the ling nuts, <I>gai lan</I>, and carrot pieces and stir-fry for one minute, then cover the wok, raise the heat, and sir-fry another minute. Now serve when hot.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'887C0625B166A242A0C819FED8919F60', X'0342491BFCAA724EB9B561B629D425AC', X'0342491BFCAA724EB9B561B629D425AC', 0, 'javascript:', 'Yunnan Tripe, Pork, and Pickled Vegetables', 2, NULL, 0, 'Yunnan Tripe, Pork, and Pickled Vegetables', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=477', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound beef or lamb tripe<BR>1 teaspoon mushroom soy sauce<BR>1 teaspoon cornstarch<BR>1/2 teaspoon sesame oil<BR>1 teaspoon sugar<BR>1/2 cup pickled mustard green or other pickled vegetable<BR>1/4 pound pork loin<BR>2 Tablespoons corn oil<BR>1 Tablespoon thin soy sauce<BR>1/2 Tablespoon black vinegar<BR>1/4 teaspoon ground white pepper<BR><B><I>Preparation:</I></B><BR>1. Boil the tripe in one quart of water for two minutes. Remove the pot from the heat source but leave the tripe standing in the hot water for half an hour, then drain and discard the water. Cut the tripe into strips about the same length as the pork, but twice as wide, and then toss with the mushroom soy, cornstarch, sesame oil, and sugar.<BR>2. Rinse pickled vegetable in three changes of water, then cut into thin strips.<BR>3. Cut pork loin into thin strips, each about one-inch long and one-quarter-inch wide.<BR>4. Heat corn oil and stir-fry the pork for one minute until just before it loses its pink color, and set it aside in a bowl.<BR>5. In the oil remaining in the wok, stir fry the vegetable for two minutes, then add the tripe, thin soy, and the vinegar and stir-fry three minutes until the liquid is absorbed. Add the pork and cook another minute stirring constantly, then serve after sprinkling with white pepper.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2410C70F827EAA47B7085AF1120981F0', X'80D9DE54A4C3984688983B12BC3D2EE7', X'80D9DE54A4C3984688983B12BC3D2EE7', 0, 'javascript:', 'Astragalus and Greens', 2, NULL, 0, 'Astragalus and Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2339', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>3 Tablespoons astragalus seeds, soaked overnight, then boiled for half an hour, and drained<BR>1 cup bak cai stems, slivered, then boiled ofr five minutes in three cups of water, then drained<BR>1 cup bak cai leaves blanched, then sliced thinly<BR>2 Tablespoons Chinese black vinegar<BR>2 Tablespoons Shao Xing vinegar<BR>2 ablespoons granulated sugar<BR><I><B>Preparation:</I></B><BR>1. Mix prepared seeds and greens, and toss wel, then cover and refrigerate for two hours or overnight.<BR>2. Add both vinegar amounts and the sugar, toss again. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'ACC25898DD01A944A44CDDEFFE229ACC', X'6CB7D2FA3B0B37449D1A9A71A1B1FE41', X'6CB7D2FA3B0B37449D1A9A71A1B1FE41', 0, 'javascript:', 'Potato Disks', 2, NULL, 0, 'Potato Disks', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2342', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound ground pork<BR>1 pound small potatoes, peeled and shredded<BR>2 Tablespoons dried shrimp, soaked for one hour, remove and shells, then mince them<BR>1 teaspoon thin soy sauce<BR>2 Tablespoons potato starch<BR>1/2 teaspoon salt<BR>3 Tablespoons vegetable oil<BR><I><B>Preparation:</B></I><BR>1. Mix pork, shredded potatoes, softened shrimp, soy sauce, potato starch, and salt.<BR>2. Heat wok or fry pan and add the oil and one heaping tablespoon of the pork/potato mixture, and flatten it with a spatula. Repeat until the pan has one layer and brown the disks, then turn them over and brown their second side.<BR>3. Continue until all mixture is fried on both sides, removing them to paper-towels. Then serve on a pre-heated platter.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'82479CA6E4735F4495EDB83CA2DCB0F2', X'E8591ABAB57F864687BAB798A69D2C18', X'E8591ABAB57F864687BAB798A69D2C18', 0, 'javascript:', 'Stir-fried Green Beans with Chilli Hoisin Sauce', 2, NULL, 0, 'Stir-fried Green Beans with Chilli Hoisin Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2355', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>250g fine green beans<BR>½ onion<BR>a small knob of ginger<BR>1 fresh birds eye chilli<BR>2 garlic cloves<BR>1 Tablespoon vegetable oil<BR>chilli hoisin sauce<BR>: 1 teaspoon Chiu Chow chilli oil<BR>1 Tablespoon hoisin sauce<BR>1 Tablespoon Shao Xing rice wine<BR>A dash of dark soy sauce<BR><B><I>Preparation:</B></I><BR>1. Top and tail the beans and drop them into a pan of boiling water. Blanch them for two minutes, then drain and cool under cold running water. 2. Finely slice the onion and ginger. Dice the birds eye chilli, and using the flat side of a knife or cleaver, smash the garlic cloves to release their flavour and remove their skin.<BR>3. Mix the chilli hoisin ingredients together in a small bowl or ramekin. Build your wok clock: Lace your finely-diced onion at 12 oclock, the arrange ginger garlic blanched green beans, the chili hoisin sauce bowl clockwise around your plate.<BR>Cooking:<BR>1. Heat the vegetable oil in a wok until smoking-hot. Add the onion and stir-fry for thirty seconds until slightly softened, then add the ginger and garlic and stir-fry a further thirty seconds.<BR>2. Keeping the heat as high as possible, add the blanched green beans and stir-fry one to two minutes until the beans have blistered around the edges. Add the chilli and chilli hoisin, bring to a vigorous boil and continue to cook, stirring, for a further one to two minutes until the sauce has reduced and is just coating the beans.<BR>3. Remove from the heat and spoon into a large bowl. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'A00D11BE54B5D3408146174053EB8A69', X'11C6D4A7697FFA4D98F993B6669B5FCD', X'11C6D4A7697FFA4D98F993B6669B5FCD', 0, 'javascript:', 'Vegetarian Mapo Doufu', 2, NULL, 0, 'Vegetarian Mapo Doufu', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2354', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>1 pound soft or firm tofu (doufu), cut in one-inch cubes, boiled for two minutes, then drained<BR>2 Tablespoons vegetable oil<BR>1 Tablespoon chili paste with garlic<BR>2 Tablespoons soaked and drained black beans, mashed or lightly chopped<BR>6 Chinese black mushrooms, soaked, stems discarded, and minced<BR>2 cloves fresh garlic, peeled and minced<BR>1 Tablespoon fresh minced ginger<BR>1 Tablespoon thin soy sauce<BR>1 teaspoon sesame oil<BR>1 teaspoon fresh ground Sichuan pepper<BR>2 Tablespoons cornstarch mixed with the same amount of cool water<BR>2 scallions, chopped<BR>3 Tablespoons goji berries soaked in boiling water for two minutes, then drained<BR>2 sprigs cilantro<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add vgetable oil and the tofu, and stir-fry for two minutes, then remove and drain in a strainer basket.<BR>2. Add chili paste, black beans, and Chinese black mushroom pieces, and stir-fry for one minute before adding soy sauce, sesame oil, Sichuan pepper, and cornstarch mixture and bring it to the boil and stir until thickened.<BR>3. Now add goji berries and mix well; and put in a pre-heated bowl and stir in scallion pieces. Garnish with cilantro, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'60B2BF81EA520C4A827A7A9D26328CE3', X'3FFA566FFCD4774A9F154522D4BDF7E6', X'3FFA566FFCD4774A9F154522D4BDF7E6', 0, 'javascript:', 'Abalone and Mushrooms', 2, NULL, 0, 'Abalone and Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=319', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 to 6 ounces canned abalone<BR>3 ounces oyster mushrooms<BR>2 Tablespoons oyster sauce<BR>1 Tablespoon cornstarch<BR>1 Tablespoon chicken fat<BR>1/2 head iceberg lettuce, shredded<BR>1 Tablespoon corn oil<BR>2 green onions, cut into two inch pieces, then slivered lengthwise<BR>1 cup chicken stock<BR>1 Tablespoon mushroom soy sauce<BR>1 Tablespoon minced fresh ginger<BR>1/2 teaspoon sugar<BR>dash white pepper<BR>2 teaspoons cornstarch mixed with two tablespoons cornstarch<BR><B><I>Preparation:</I></B><BR>1. Thinly slice abalone and mushrooms and marinate them for one hour in oyster sauce, cornstarch, and chicken fat.<BR>2. Blanch lettuce for half minute in boiling water, then drain well.<BR>3. Heat corn oil, scallions and fry half a minute then add stock, abalone and oyster mushrooms. Bring to the boil for one minute then drain reserving the stock.<BR>4. Mix soy sauce, ginger, sugar, and pepper. Add to the wok along with the stock and cook until reduced by half. Return mushroom mixture to the wok, add cornstarch and water and thicken, then serve on top of the blanched and drained lettuce.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'098415DACD0E094081AAF37776860F29', X'2F55F755B252074E9E3C44E17361DAF7', X'2F55F755B252074E9E3C44E17361DAF7', 0, 'javascript:', 'Amaranth and Bamboo Shoots', 2, NULL, 0, 'Amaranth and Bamboo Shoots', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=929', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/4 pound fresh bamboo shoot tips<BR>2 teaspoons oil<BR>1/4 cup amaranth leaves, minced<BR>1 cup chicken broth<BR>1 teaspoon salt<BR>2 teaspoons cornstarch mixed with two teaspoons cold water<BR><B><I>Preparation:</B></I><BR>1. Peel bamboo shoot tips and cut them into thick strips, about two inches long.<BR>2. Heat oil, and stir-fry the bamboo shoot tips for one minute, then drain them on paper towels.<BR>3. Boil two cups of water, using a strainer, immerse the minced leaves in it for half minute, then remove, and cool them quickly under running cold water.<BR>4. Put the broth in a wok or pot and simmer the bamboo shoot tips for a few minutes before adding the amaranth leaves, salt, and cornstarch water. Bring this to the boil stirring until slightly thickened and clear. Serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EDEED5CBD79AC1429F64B09E02896532', X'6292A98B801C9F42B319FDC6BB606EA1', X'6292A98B801C9F42B319FDC6BB606EA1', 0, 'javascript:', 'Arbutus and Silk Squash', 2, NULL, 0, 'Arbutus and Silk Squash', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1055', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1/2 cup frozen arbutus<BR>1 silk squash, peeled and angle cut into two-inch sections<BR>10 large shrimp, peeled, their veins removed, cut in half the long way<BR>5 medium black mushrooms, soaked, stems removed, and each cut in quarters<BR>1 Tablespoon vegetable oil<BR>1 clove garlic, peeled and slivered<BR>1 slice fresh ginger, slivered<BR>1 Tablespoon oyster sauce<BR>1 teaspoon cornstarch<BR><B><I>Preparation:</B></I><BR>1. Mash <I>yang mei</I> and set aside in a strainer basket over a small bowl.<BR>2. Mix silk squash, shrimp, and mushrooms.<BR>3. Heat wok or fry pan, add oil, garlic, and ginger and stir-fry for one minute. Remove garlic and ginger, and discard them.<BR>4. Stir-fry the mixed vegetables and shrimp for two minutes, or until the shrimp turn pink. Remove them to a bowl.<BR>5. Put oyster sauce into the wok, and heat to the boil. Mix cornstarch and the yang mei fruit, and add this to the wok with the set aside vegetables and shrimp. Allow to boil for one minute, put into a bowl, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7FAB1406049ACC4B98948C886EC40D40', X'830F7845E4D56D4C9006827C55FB0F73', X'830F7845E4D56D4C9006827C55FB0F73', 0, 'javascript:', 'Aubergine Folders I', 2, NULL, 0, 'Aubergine Folders I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1032', '<TD><RECIPE><B>Ingredients:</B><BR>2 half-pound pieces of firm bean curd<BR>1 teaspoon black moss, soaked for twenty minutes in cold waters, then drained<BR>4 Japanese eggplants, cut into one-inch slices, then cut each of these partway through to make into a folder<BR>1 cup all-purpose flour<BR>1/2 teaspoon salt<BR>2 eggs, slightly beaten<BR>2 cups and 1 Tablespoon vegetable oil<BR>1/2 teaspoon ground white pepper<BR>1 egg white<BR>3 Tablespoons oyster-flavored sauce<BR>1 green stalk of a scallion, minced<BR>1 large clove garlic, minced<BR><B><I>Preparation:</B><BR>1. Blanch bean curd in boiling water for three minutes, drain, and pat dry. Then, cut off any hard outer layer, and mash the rest.<BR>2. Mince the black moss and mix with the bean curd.<BR>3. Press half tablespoon bean curd mixture into each of the eggplant slices.<BR>4. Mix flour and salt, make well in the center, and stir in eggs and the tablespoon of oil. Stir and add water, a tablespoon at a time, to make a thin batter. Then let this rest ten minutes.<BR>5. Heat oil in large pot, dip stuffed eggplant slices into the batter, allow to drain and deep fry several at a time for about four minutes or until puffed and brown. Drain and put them on paper towels while repeating until all are fried and drained.<BR>6. Mix oyster-flavored sauce, a tablespoon of water, scallion and garlic pieces and heat, pour over the eggplant folders and serve.<BR></I></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'0AC97722A5E21D479285269AE116FDE1', X'53C30F65F5562A4E92F2430AEE0279FB', X'53C30F65F5562A4E92F2430AEE0279FB', 0, 'javascript:', 'Aubergine Folders II', 2, NULL, 0, 'Aubergine Folders II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1517', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 half-pound pieces of firm bean curd<BR>1 teaspoon black moss, soaked in cold water for twenty minutes, then drained<BR>4 Japanese eggplants, cut into one-inch slices, each cut partway through to make into a ''folder''<BR>1 cup all-purpose flour<BR>1/2 teaspoon salt<BR>2 eggs, slightly beaten<BR>2 cups and 1 Tablespoon vegetable oil<BR>1/2 teaspoon ground white pepper<BR>1 egg white<BR>3 Tablespoons oyster-flavored sauce<BR>1 green part of a scallion, minced<BR>1 large clove garlic, minced<BR><B><I>Preparation:</B></I><BR>1. Blanch bean curd in boiling water for three minutes, drain, and pat dry. Then, cut off any hard outer layer, and mash the rest.<BR>2. Mince the black moss and mix with the bean curd.<BR>3. Press half tablespoon bean curd mixture into each of the eggplant slice folders.<BR>4. Mix flour and salt, make well in the center, and stir in eggs and the tablespoon of oil. Stir and add water, a tablespoon at a time, to make a thin batter. Let this rest ten minutes.<BR>5. Heat oil in large pot, dip stuffed eggplant slices into the batter, allow to drain and deep fry several at a time for about four minutes or until puffed and brown. Drain and put them on paper towels while repeating until all are fried and drained.<BR>6. Mix oyster-flavored sauce, a tablespoon of water, scallion and garlic pieces and heat, pour over the eggplant folders and serve.<BR><I>Approximate nutrient analysis</I> when serving six persons and assuming three tablespoons of oil is absorbed. Each serving has 296 calories, 36 g carbohydrate, 14 g protein, 416 mg sodium, 6 g fat, 2 g saturated fat, and 60 mg cholesterol.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'483324AFF66CC141800E445C5BB130D4', X'9D2A07B433CCA14EB22E20C55A5BACE9', X'9D2A07B433CCA14EB22E20C55A5BACE9', 0, 'javascript:', 'Backyard Asian Coleslaw', 2, NULL, 0, 'Backyard Asian Coleslaw', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=748', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>3 cups shredded napa cabbage<BR>1 and 1/2 cups bean sprouts (we removed the tails on ours)<BR>1/2 cup shredded carrot<BR>8 ears baby corn, cut into one-inc pieces (we preferred ours slivered)<BR>2 scallions, thinly sliced<BR>1/3 pound deveined cooked and peeled shrimp(optional)<BR>1/3 cup homemade or purchased mayonnaise<BR>2 Tablespoons rice vinegar<BR>1 Tablespoon thin soy sauce<BR>1 Tablespoon honey<BR>2 teaspoons wasabi paste<BR>1 and 1/2 teaspoons chili garlic sauce<BR>2 Tablespoons chopped toasted walnuts<BR><B><I>Preparation:</B></I><BR>1. Mix cabbage, bean sprouts, carrot, baby corn, scallions, and shrimp and toss well.<BR>2. Make dressing mixing mayonnaise, rice vinegar, soy sauce, honey, and chili garlic sauce.<BR>3. Toss all ingredients with the pre-made dressing. Sprinkle the walnuts on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'44C4FB640B669248905262A38247FE6D', X'7EAB02C0F0B1544292DFA881C654203E', X'7EAB02C0F0B1544292DFA881C654203E', 0, 'javascript:', 'Bamboo Pith with Bean Curd', 2, NULL, 0, 'Bamboo Pith with Bean Curd', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=11', '<TD><RECIPE><I><B>Ingredients:</I></B><BR>12 whole bamboo piths<BR>6 bamboo piths that can be broken, cut, or otherwise not whole<BR>3 dried shiitake mushrooms, soaked and minced fine<BR>2 scallions or several large chives or garlic shoots, minced fine<BR>6 Tablespoons frozen peas, defrosted and slightly mashed<BR>2 Tablespoons oyster sauce<BR>1 Tablespoon cornstarch mixed with one tablespoon cold water<BR>salt and white pepper, to taste<BR>1 cup chicken stock<BR>1 Tablespoon light soy sauce<BR>1 Tablespoon sesame oil<BR>1/2 teaspoon hoisin sauce<BR>2 Tablespoons cornstarch mixed with two tablespoons cold water<BR>1 cup any steamed or cooked greens (i.e.: spinach, tops of bok choy, etc.)<BR><I><B>Preparation:</I></B><BR>1. Soak both sets of bamboo pith for twenty minutes, then drain and mince the broken ones.<BR>2. Make the stuffing by mixing the shiitake mushrooms, scallions and or other greens, peas, and oyster sauce, and finally the cornstarch mixture.<BR>3. Fill the whole bamboo pith pieces with the stuffing. A pastry bag without a tube at the end works very well, and set aside for ten to fifteen minutes.<BR>4. Heat water to boiling in a steamer and put the filled piths decoratively around the outside of a heat-proof plate that is at least one inch deep. Steam them for five minutes, then remove the plate and its contents.<BR>5. Bring the stock, light soy sauce, sesame oil, and hoisin sauce to the boil. Stir in the cornstarch mixture and continue stirring until the sauce clears.<BR>6. Put the greens in the center of the bamboo piths amd por the boiling sauce over them and the pieces of stuffed pith. Serve immediately.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'39FE25C4B11BD441A568026347F28185', X'367E516B3C145B479955271419F01C4A', X'367E516B3C145B479955271419F01C4A', 0, 'javascript:', 'Bean Curd and Chicken of the Woods Mushrooms', 2, NULL, 0, 'Bean Curd and Chicken of the Woods Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1368', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>1/4 pound fresh chicken of the woods mushrooms, blanched for two minutes, then drained and coarsely chopped<BR>1 square firm bean curd, sliced horizontally into four pieces<BR>3 Tablespoons vegetable oil<BR>1/2 carrot, peeled and minced<BR>2 Chinese black mushroom, soaked, stem discarded, and minced<BR>2 Tablespoons vegetarian oyster sauce<BR>1 teaspoon sugar<BR>dash ground white pepper<BR>1 Tablespoon cornstarch mixed with two tablespoons of cold water<BR><B><I>Preparation:</B></I><BR>1. Heat wok or fry pan, add one tablespoon of the oil, and fry the chicken of the woods mushrooms for one minute, remove and set aside.<BR>2. Add the rest of the oil and fry the bean curd slices until crisp first on one side, then on the other. Remove them first to paper towels, then to a pre-heated platter. Put the chicken of the woods mushrooms o top of the bean curd slices.<BR>3. Put carrot and black mushroom pieces in the wok with any remaining oil, and stir-fry for two minutes. Then add oyster sauce, sugar, ground white pepper, and the cornstarch mixture, and stir until thickened.<BR>4. Pour the carrot-mushroom mixture over the bean curd and the chicken of the woods mushrooms, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'AEFF68AA285471468059B72C6E70A9CB', X'B883C8127866A142A28DA6F99808682C', X'B883C8127866A142A28DA6F99808682C', 0, 'javascript:', 'Bean Curd Pancakes', 2, NULL, 0, 'Bean Curd Pancakes', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1340', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>5 pieces fresh soft bean curd, mashed somewhat<BR>1 teaspoon sugar<BR>1/2 teaspoon coarse salt<BR>1 teaspoon thin soy sauce<BR>2 Tablespoons vegetable oil, divided into four batches<BR><B><I>Preparation:</B></I><BR>1. Bring half cup water to the boil, reduce the heat, add the bean curd and simmer for ten to twenty minutes, then stir vigorously until almost pureed.<BR>2. Add sugar, salt, and soy sauce and remove the pot from the heat, and stir once more.<BR>3. Heat wok or fry-pan, put in one batch of oil, then take one large spoonful of the bean curd mixture at a time, and fry it first on one side, then the other, each until golden. Drain on paper towels, keep warm, and repeat until all are fried; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'1650FE17B6D88D4AB092C2F5AC4F9AD8', X'C2BF85480E6AF146AF70DDD94CBDA0C7', X'C2BF85480E6AF146AF70DDD94CBDA0C7', 0, 'javascript:', 'Bean Curd Spoons', 2, NULL, 0, 'Bean Curd Spoons', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1342', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 square firm bean curd, mashed<BR>1 Tablespoon cornstarch<BR>1 piece tangerine peel, soaked then minced (about one spoonful)<BR>1/2 cup soaked dried mushrooms (can be a mix of Chinese black, straw, and other kinds)<BR>1 Tablespoon minced fresh ginger<BR>1 teaspoon ground white pepper<BR>1 teaspoon sugar<BR>1/2 teaspoon coarse salt<BR>1 Tablespoon thin soy sauce<BR>1/4 cup chopped iceberg lettuce<BR>1 teaspoon vegetable oil<BR>several flat lettuce or spinach leaves<BR><B><I>Preparation:</B></I><BR>1. Mix mashed bean curd and cornstarch, then add the minced tangerine peel, mushrooms, fresh ginger, pepper, sugar, salt, and soy sauce and stir for one or two minutes. Next gently add chopped lettuce and stir in.<BR>2. Using the oil, brush this on twenty ceramic soup spoons and fill each one mounding lots over the top of each spoon, gently tamping it down.<BR>3. Put filled soups spoons in a steamer basket and place it over boiling water, and stem for twelve minutes. Allow these to cool slightly, then using a thin spatula, remove their contents and put these in a circle on a lettuce or spinach topped platter, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EC4FC2CC088C204081A5AEAC6B63F837', X'383E93144F50C748A54864113EB49A11', X'383E93144F50C748A54864113EB49A11', 0, 'javascript:', 'Bean Curd Sticks and Many Mushrooms', 2, NULL, 0, 'Bean Curd Sticks and Many Mushrooms', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1540', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 dried beancurd sticks, soaked for half hour, hard parts cut away and discarded, remaining softened sticks sliced into half-inch pieces<BR>3 small cans (assorted types) mushrooms, drained<BR>1 carrot, peeled and sliced thinly on angle<BR>6 shallots, peeled and cut into quarters<BR>1 Tablespoon vegetable oil<BR>1 teaspoon Chinese rice wine<BR>1 teaspoon mushroom oyster sauce<BR>1 teaspoon thin soy sauce<BR>1 teaspoon sugar<BR>1 Tablespoon water chestnut flour<BR>1 teaspoon cornstarch<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Drain bean curd pieces and dry with a paper towel. Then mix them with mushrooms and set aside.<BR>2. Simmer carrot slices in half-cup water for two minutes, then drain and set them aside.<BR>3. Heat vegetable oil, add pieces of beancurd and all the mushrooms, and the shallots and carrot pieces and stir-fry for two minutes before adding mushroom oyster sauce, thin soy sauce and the sugar, and stir well. Next mix the water chestnut flour with the mushrooms, and the cornstarch with one tablespoon cold water before adding it to the mushroom mixture. <BR>4. Stir in the sesame oil, put the mushroom mixture in a bowl, and serve. <BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D0FB6555B507C54C862E3E40F94CEF9F', X'9EB24B580BB1C54491012EECC62EFBC9', X'9EB24B580BB1C54491012EECC62EFBC9', 0, 'javascript:', 'Bean Curd with Mustard Greens', 2, NULL, 0, 'Bean Curd with Mustard Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1834', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 small onion coarsely sliced<BR>3 Tablespoons vegetable oil or lard<BR>1 pound Swatow mustard greens, a popular variety or another kind, cut into one by half inch pieces<BR>1/4 pound ham, cubed in similar sizes<BR>1/2 teaspoon coarse salt<BR>1 package firm or soft bean curd, cut into one-inch cubes<BR>1 Tablespoon sesame oil<BR>1/2 cup chicken or other stock<BR><B><I>Preparation:</B></I><BR>1. Brown the onion in a dry wok or pan for one minute, then add the oil or lard and saute one minute more, stirring all the time.<BR>2. Add greens, ham, and salt and stir-fry for two more minutes.<BR>3. Add the bean curd, sesame oil, and the stock, turn heat to high, cover, and cook two minutes, then remove the cover and turn the heat to medium. Continue stirring and cooking for another four minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'D43C47897FC963459CE93A40CBCF0D36', X'066E0499254C9B4BBF114746CE9A5F61', X'066E0499254C9B4BBF114746CE9A5F61', 0, 'javascript:', 'Bean Curd with Salt and Spices', 2, NULL, 0, 'Bean Curd with Salt and Spices', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=630', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>4 firm pieces of bean curd (about a pound)<BR>1 envelope chicken bouillon powder<BR>1 teaspoon sugar<BR>1/2 teaspoon cumin seeds, smashed with end of cleaver handle<BR>1 Tablespoon fresh coriander, minced<BR>1 small dried chili pepper, seeded and crushed<BR>1/2 sheet green seaweed, crushed or ½ teaspoon coarse salt<BR>1/2 cup cornstarch<BR>1 cup corn oil, for frying<BR><B><I>Preparation:</B></I><BR>1. Cut each piece of bean curd into four pieces.<BR>2. Mix all rest of the ingredients well, gently toss with the cubes of bean curd. Try to have some of this mixture on all sides. Set it aside for half an hour.<BR>3. Heat oil and fry the cubes of bean curd a few at a time; do not overcrowd the wok or pan; and keep stirring them until lightly browned. The drain on paper towels, and do the next batch. It is best to do this in three or four batches. When all are fried, serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'3F64AE223CBDE043BF45162027B4B866', X'6CA6ABDC23B4C344B38A15358DF69DB2', X'6CA6ABDC23B4C344B38A15358DF69DB2', 0, 'javascript:', 'Beans and Garlic', 2, NULL, 0, 'Beans and Garlic', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2065', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 teaspoons lard or lamb fat, rendered<BR>2 cups fresh or dry beans such as soybeans, fava beans, or lima beans; if using dry ones, soak for two hours or overnight in tepid water, then drain and discard the water<BR>6 cloves fresh garlic, peeled and slivered<BR>1 star anise, broken into its segments<BR>1 Tablespoon green sauce (such as the one above)<BR>1 teaspoon coarse salt<BR>½ cup water or meat broth<BR><B><I>Preparation:</B></I><BR>1. Heat wok or pan, add lard or another fat, then add the beans and the garlic slivers and stir for one minute.<BR>2. Next add the star anise, green sauce, salt, and the liquid, and reduce the heat and simmer covered for ten minutes or longer until the beans are soft. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9166943B19C2EC4B96979041647B5875', X'5950EE23A9D3534A9CB41B7D44615AB0', X'5950EE23A9D3534A9CB41B7D44615AB0', 0, 'javascript:', 'Belly Pork and Two Kinds of Mustard Greens', 2, NULL, 0, 'Belly Pork and Two Kinds of Mustard Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1838', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound fresh belly pork<BR>2 Tablespoons dark soy sauce<BR>1 Tablespoon crushed Chinese brown sugar<BR>3 Tablespoons Chinese rice wine<BR>3 Tablespoons vegetable oil<BR>6 cloves peeled fresh garlic, smashed<BR>3 Tablespoons <I>ya cai</I> which are fermented mustard greens<BR>1 pound fresh mustard greens, cut into one-inch pieces<BR><B></I>Preparation:</B></I><BR>1. Simmer the belly pork in water for one hour, remove from the water, dry, and coat with the soy sauce, brown sugar, and the rice wine. Let this rest for half an hour.<BR>2.Heat a wok or fry pan and add the oil and fry the belly pork for three minutes on all sides. The remove and drain it, and when cool, cut into half-inch squares.<BR>3. Add the garlic to the oil in the pan, and stir-fry one minute, then add the <I>ya cai</I> and the mustard greens and stir well before adding one cup of boiling water.<BR>4. Cover, reduce the heat, and simmer for one and a half hours; then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'50127325FA9347409EF75A93619CA19A', X'3A88E518098E5946B5EA785ED82A1950', X'3A88E518098E5946B5EA785ED82A1950', 0, 'javascript:', 'Bhutanese Ema Datshi', 2, NULL, 0, 'Bhutanese Ema Datshi', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1978', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>I onion, sliced fine<BR>2 teaspoons vegetable oil<BR>10 chili peppers, seeded and sliced fine<BR>6 cloves fresh garlic, peeled and smashed<BR>3 tomatoes, peeled and diced<BR>½ pound farmer cheese<BR>1/4 cup cilantro, minced<BR>2 to 4 cups cooked rice or noodles (optional)<BR><B><I>Preparation:</B></I><BR>1. Boil onion in two cups of water for three minutes, add the chili peppers, garlic, and tomatoes, and reduce the heat and simmer for fifteen minutes, then remove from the heat source.<BR>2. Add the cheese and coriander, cover and set aside for fifteen more minutes, then mix in the coriander just before serving. If desired, serve on white rice, red rice, or noodles.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'2A1BAC99FCEED84BA9B8B46E690D1991', X'34356EA872F04147978E433AD2481D2F', X'34356EA872F04147978E433AD2481D2F', 0, 'javascript:', 'Bhutanese Spicy ma Datshi', 2, NULL, 0, 'Bhutanese Spicy ma Datshi', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=2174', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 onion, sliced fine<BR>2 teaspoons vegetable oil<BR>10 chili peppers, seeded and sliced fine<BR>6 cloves fresh garlic, peeled and smashed<BR>3 tomatoes, peeled and diced<BR>½ pound farmer cheese<BR>1/4 cup cilantro, minced<BR>2 to 4 cups cooked rice or noodles (optional)<BR><B><I>Preparation:</B></I><BR>1. Boil onion in two cups of water for three minutes, add the chili peppers, garlic, and tomatoes and reduce the heat and simmer it for fifteen minutes, then remove it from the heat source.<BR>2.Add the cheese and coriander, cover and set aside for fifteen more minutes, then mix in the coriander just before serving. If desired, serve on cooked hot white rice, red rice, or noodles.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'7215E09D426CA44AA619DBFC4E24209F', X'7E3DE0EB2D6EBB469BEE007E6D5BA5D5', X'7E3DE0EB2D6EBB469BEE007E6D5BA5D5', 0, 'javascript:', 'Bitter Melon Stuffed with Mock Pork', 2, NULL, 0, 'Bitter Melon Stuffed with Mock Pork', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=112', '<TD><RECIPE><I><B>Ingredients:</B></I><BR>8 water chestnuts, minced<BR>6 scallions, cut in half-inch pieces<BR>1 and 1/2 cups ground pork<BR>1 Tablespoon dry sherry<BR>3 Tablespoons thin soy sauce<BR>1/4 teaspoon ground pepper<BR>6 bitter melons, cut in half the long way and seeded<BR><B>For the sauce mixture:</B><BR>2 Tablespoons dry sherry<BR>1 Tablespoon dark soy sauce<BR>2 Tablespoons cornstarch<BR><I><B>Preparation</I></B><BR>1. Mix all ingredients except the bitter melon then stuff one-twelfth of the mixture into each half of the bitter melon.<BR>2. In a large diameter fry pan or pot, bring two cups of water to the boil and add the sauce mixture, then carefully put in the bitter melon pieces, stuffed side up, and simmer covered for thirty minutes. Then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'67FD038D618EDE41AAF56A3E9C15CBC1', X'F25D37BCE567D74FB1CC56E44E088F72', X'F25D37BCE567D74FB1CC56E44E088F72', 0, 'javascript:', 'Bitter Melon with Black Bean Sauce', 2, NULL, 0, 'Bitter Melon with Black Bean Sauce', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1878', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 large or two small bitter gourds<BR>1 teaspoon coarse salt<BR>1 Tablespoon fermented black beans, rinsed and mashed<BR>1 Tablespoon vegetable oil<BR>1 whole garlic clove, peeled and minced<BR>1 small chili pepper, seeded and chopped<BR>1 Tablespoon dark soy sauce<BR>1 teaspoon thin soy sauce<BR>1 Tablespoon granulated sugar<BR>1/2 teaspoon ground white pepper<BR><B><I>Preparation:</B></I><BR>1. Peel the gourd(s), cut them in half. Discard the seeds and any pith, then slice the gourd halves crosswise into half-inch slices. Toss them with coarse salt, and let them rest for half an hour.<BR>2. Blanch the slices in boiling water for one minute, then drain them.<BR>3. Heat a wok or fry pan, add the oil, and stir-fry the garlic and chili pepper pieces for one minute, then add the black beans and the bitter gourd slices and stir well for one minute.<BR>4. Add the soy sauces, sugar, and pepper and cover the pan and simmer for three minutes, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'9E2B63920EA30549B97DF6379F619EB2', X'D01FD7D6D0BE3E48A5D27437DDCB531C', X'D01FD7D6D0BE3E48A5D27437DDCB531C', 0, 'javascript:', 'Black Mushrooms and Mustard Greens', 2, NULL, 0, 'Black Mushrooms and Mustard Greens', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1837', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 large dried Chinese black mushrooms<BR>2 Tablespoons rendered chicken fat or lard<BR>2 Tablespoon cornstarch, divided<BR>1 cup chicken broth<BR>1 Tablespoon vegetable oil<BR>1 teaspoon salt<BR>1 teaspoon oyster sauce<BR>1 pound mustard greens cut into two-inch pieces<BR><B><I>Preparation:</B></I><BR>1. Soak the mushrooms in one cup of hot water for twenty minutes, take them out and squeeze out the water reserving it. Next cut off the stems and discard them, and cut the mushroom caps in quarters.<BR>2. Coat the mushrooms on their undersides with the fat, then dust them with half the cornstarch.<BR>3. Mix the rest of the cornstarch with the broth and stir it well.<BR>4. Heat vegetable oil and fry the mushroom caps, gill sides up for one minute, then add the salt and oyster sauce and stir-fry for another minute before adding the broth mixture and the greens. Bring to the boil, stir-fry for three minutes stirring all the time, then serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'50A82FB6056A4448969B2142634957BA', X'796E75FB2076AA44819B31DB19A12D83', X'796E75FB2076AA44819B31DB19A12D83', 0, 'javascript:', 'Blossom and Egg , Saute', 2, NULL, 0, 'Blossom and Egg , Saute', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=418', '<TD><RECIPE><B><I>Ingredients:</I></B><BR>16 pumpkin or squash blossoms<BR>3 eggs, beaten<BR>1/4 cup chopped onion<BR>1/8 teaspoon ground white pepper<BR>1 Tablespoon corn oil<BR>1 Tablespoon chopped coriander or coriander sprigs<BR><I><B>Preparation:</B></I><BR>1. Wash the blossoms, remove their stems, and cut them into one-inch pieces.<BR>2. Mix blossoms and the beaten eggs and ground pepper.<BR>3. Heat oil in a fry pan or wok and pour in the egg mixture and stir until set. Sprinkle coriander on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'45C8A0050AC9714C88CC6969865D0CBA', X'85DDE0255477E648AE254F114724831E', X'85DDE0255477E648AE254F114724831E', 0, 'javascript:', 'Braised Bamboo Shoots, Zhejiang Style', 2, NULL, 0, 'Braised Bamboo Shoots, Zhejiang Style', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1766', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>1 pound young bamboo shoots, thinly sliced<BR>1/4 cup lard<BR>2 Tablespoons Sichuan pepper-flavored oil<BR>2 Tablespoons sugar, granulated, crushed brown slab or mixed<BR>1 teaspoon thin soy sauce<BR>1/2 teaspoon coarse salt<BR>1 scallion, minced<BR>1 Tablespoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Crush the slices of bamboo shoots one at a time.<BR>2. Heat the lard in a wok or deep fry-pan, add the bamboo shoots, and simmer on low heat for fifteen minutes, then drain and discard any liquid in the pan.<BR>3. Add the flavored oil, sugar(s), soy sauce, and the salt and one to two tablespoons water, and simmer for five minutes.<BR>4. Put the bamboos shoots in a pre-heated bowl, add the scallion pieces and the sesame oil, stir once, and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'449FCB470D43A541B2B43432D587A712', X'F2C0555DA3E9534EBD90B13A2905D5D0', X'F2C0555DA3E9534EBD90B13A2905D5D0', 0, 'javascript:', 'Braised Eggplant with Mango', 2, NULL, 0, 'Braised Eggplant with Mango', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=388', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>2 almost ripe mangoes<BR>1 or 2 thin eggplants, cut into thin strips<BR>1 teaspoon sugar<BR>2 Tablespoons corn oil<BR>1 Tablespoon thin soy sauce<BR>1 teaspoon sesame oil<BR>dash of ground white pepper<BR>2 teaspoons sesame paste mixed with one tablespoon of hot water<BR>2 Tablespoon water chestnut or lotus flour mixed with the same amount of cold water<BR>1 teaspoon chopped coriander<BR><B><I>Preparation:</B></I><BR>1. Cut the mango flesh into thick slices.<BR>2. Mix eggplant with sugar and marinate for fifteen minutes.<BR>3. Heat oil and add eggplants and fry for two or three minutes, then add soy sauce and sesame oil and the pepper and continue to fry for another five minutes.<BR>4. Add sesame paste and mangos and stir-fry one minute then add flour mixture and cook another minute, then remove to a serving bowl. Put coriander on top and serve.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'EA6613A8B7C9B54DB99FCC0188B6FACB', X'EFF2602D8C99FF4881E1E1A87925A64D', X'EFF2602D8C99FF4881E1E1A87925A64D', 0, 'javascript:', 'Braised Gluten I', 2, NULL, 0, 'Braised Gluten I', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1004', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 cups flour<BR>1 teaspoon salt<BR>2 cups vegetable oil<BR>1 teaspoon sugar<BR>2 teaspoons thin soy sauce<BR>3 Tablespoons vegetable or mushroom stock<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Sift flour, add salt and two cups of warm water. Knead until smooth, rinse this dough under cold water and remove the starch. Repeat until water is no longer cloudy. Then knead and stretch, and repeat five times or more until all starch is removed and only the protein remains. Tear gluten into small pieces.<BR>2. Heat oil and fry gluten pieces for four minutes, or until golden. Then remove and drain them.<BR>3. Reheat oil, add gluten sugar, soy sauce, and the gluten. Then add stock and simmer until almost all liquid has evaporated.<BR>4. Sprinkle with the sesame oil, and serve hot, warm, or cold.<BR></TD>');
INSERT INTO `VWR INTERNAL Flavorandfortune3rip recipe` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, url, recipeIngredientInstructionsHTML) VALUES (X'70DA8359F1006647912112DA8314515D', X'B1D8F4F73077E845903F8A17C75A40B9', X'B1D8F4F73077E845903F8A17C75A40B9', 0, 'javascript:', 'Braised Gluten II', 2, NULL, 0, 'Braised Gluten II', 'http://www.flavorandfortune.com/dataaccess/recipe.php?ID=1456', '<TD><RECIPE><B><I>Ingredients:</B></I><BR>8 cups flour<BR>1 teaspoon salt<BR>2 cups vegetable oil<BR>1 teaspoon sugar<BR>2 teaspoons thin soy sauce<BR>3 Tablespoons vegetable or mushroom stock<BR>1/2 teaspoon sesame oil<BR><B><I>Preparation:</B></I><BR>1. Sift flour, add salt and two cups of warm water. Knead until smooth, rinse this dough under cold water and remove the starch. Repeat until water is no longer cloudy. Then knead and stretch, and repeat five times or more until all starch is removed and only the protein remains. Tear gluten into small pieces.<BR>2. Heat oil and fry gluten pieces for four minutes, or until golden. Then remove and drain them.<BR>3. Reheat oil, add gluten sugar, soy sauce, and the gluten. Then add stock and simmer until almost all liquid has evaporated.<BR>4. Sprinkle with the sesame oil, and serve hot, warm, or cold.<BR></TD>');
