INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17BA5A81967A834C90328C28F53605FD', X'4DCD1C6201970E49A7DB9931E7C12A2C', X'4DCD1C6201970E49A7DB9931E7C12A2C', 0, NULL, NULL, 2, NULL, 0, 'Slice the oranges lengthwise. Using a spoon, scoop out the pulp all the way to the pith without breaking the rind. Place all the orange peels on a muffin pan to keep them upright.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'949E777D9E86C9479ED7FB2806E9A457', X'4DCD1C6201970E49A7DB9931E7C12A2C', X'4DCD1C6201970E49A7DB9931E7C12A2C', 1, NULL, NULL, 2, NULL, 0, 'Boil one cup of water. Place the gelatin mix in a bowl and add the boiling water. Stir well until the gelatin dissolves completely. Add the cup of vodka and stir.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF20689ADA24EB40A0F89B9111B373D2', X'4DCD1C6201970E49A7DB9931E7C12A2C', X'4DCD1C6201970E49A7DB9931E7C12A2C', 2, NULL, NULL, 2, NULL, 0, 'After filling the orange halves with the gelatin, place the muffin pan in the refrigerator. Refrigerate for at least four hours or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'064CA6E72AC66B40BD961C756BC4650D', X'4DCD1C6201970E49A7DB9931E7C12A2C', X'4DCD1C6201970E49A7DB9931E7C12A2C', 3, NULL, NULL, 2, NULL, 0, 'Remove the orange halves from the refrigerator. Using a sharp knife, slice the orange halves into wedges.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4940BF79C17E234484366BBF4AB89410', X'A17ECCEFF237A3448E9750DA828644D1', X'A17ECCEFF237A3448E9750DA828644D1', 0, NULL, NULL, 2, NULL, 0, 'This recipe is for one half of a mini watermelon. If you would like to do the whole mini watermelon, double the amount of ingredients.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E39E5FAECC78B428E073D4FD8A7F06B', X'A17ECCEFF237A3448E9750DA828644D1', X'A17ECCEFF237A3448E9750DA828644D1', 1, NULL, NULL, 2, NULL, 0, 'Cut the watermelon lengthwise. Using a spoon, scoop out the meat of the watermelon all the way to the white flesh without breaking the rind. Place the watermelon on a bowl with a towel underneath to keep it upright and stable.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2BFB40314925B4DB90833E3E735E921', X'A17ECCEFF237A3448E9750DA828644D1', X'A17ECCEFF237A3448E9750DA828644D1', 2, NULL, NULL, 2, NULL, 0, 'Boil one cup of water. Place the Jell-O and the unflavored gelatin in a bowl and add the boiling water. Stir well until the gelatin dissolves completely. Add the cup of vodka and stir.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F79663404B33CB45BB66FB63343827A2', X'A17ECCEFF237A3448E9750DA828644D1', X'A17ECCEFF237A3448E9750DA828644D1', 3, NULL, NULL, 2, NULL, 0, 'Once the gelatin is firm, cut the ends of the watermelon with a warm sharp knife. Then, slice the watermelon in half from one end to the other.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98B6B22C194D404AADBEFAF5BB96457B', X'A17ECCEFF237A3448E9750DA828644D1', X'A17ECCEFF237A3448E9750DA828644D1', 4, NULL, NULL, 2, NULL, 0, 'Using a warm sharp knife, slice the two watermelon pieces into wedges. Place the watermelon jello wedges on a plate and serve.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'468B198D5565E242A118CDAEF68699D0', X'4FF94F11B76279478233018568E5C92F', X'4FF94F11B76279478233018568E5C92F', 0, NULL, NULL, 2, NULL, 0, 'Slice the lemons lengthwise. Using a spoon, scoop out the pulp all the way to the pith without breaking the rind. Place all the lemon peels on a muffin pan to keep them upright.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B93892696D26A43BF6F3AAF564D30DD', X'4FF94F11B76279478233018568E5C92F', X'4FF94F11B76279478233018568E5C92F', 1, NULL, NULL, 2, NULL, 0, 'Squeeze the juice out of the pulp of the lemons with a lemon squeezer until you get 2 ounces of lemon juice.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F148E6FCBD7EC34998CB9FE113AA5BE6', X'4FF94F11B76279478233018568E5C92F', X'4FF94F11B76279478233018568E5C92F', 2, NULL, NULL, 2, NULL, 0, 'Fill the lemon halves all the way to the top with the lemon drop gelatin. Place the muffin pan in the refrigerator. Refrigerate for at least four hours or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D72AB5F192A22D4CB5070346DD965C1D', X'4FF94F11B76279478233018568E5C92F', X'4FF94F11B76279478233018568E5C92F', 3, NULL, NULL, 2, NULL, 0, 'Remove the lemon halves from the refrigerator. Using a sharp knife, slice the lemon halves into wedges.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3E8F184028F50458C2E5FC30DD94EA9', X'515F4861D1AC1C40B5147F8D7D221D93', X'515F4861D1AC1C40B5147F8D7D221D93', 0, NULL, NULL, 2, NULL, 0, 'Note: If you can find the Piña Colada Jell-O, use it for this recipe instead of the strawberry Jell-O.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4559023B3738E54C85B8D62AC452B1B7', X'515F4861D1AC1C40B5147F8D7D221D93', X'515F4861D1AC1C40B5147F8D7D221D93', 1, NULL, NULL, 2, NULL, 0, 'Cut the top of the pineapple. Using a pineapple corer, remove the flesh of the pineapple as much as possible without breaking the skin of the pineapple. Using a knife, cut the core of the pineapple leaving the pineapple shell.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BF7071979823D4DBD1D46BA6DBA5068', X'515F4861D1AC1C40B5147F8D7D221D93', X'515F4861D1AC1C40B5147F8D7D221D93', 2, NULL, NULL, 2, NULL, 0, 'Before cooking the pineapple, make sure there are no holes in the pineapple shell by pouring water and checking for leaks. Place the pineapple in a pan and fill it with water. Boil the pineapple for a couple of minutes.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'298E452C9D21DF4BBDF85B0E54A2005C', X'515F4861D1AC1C40B5147F8D7D221D93', X'515F4861D1AC1C40B5147F8D7D221D93', 3, NULL, NULL, 2, NULL, 0, 'Warning: If you pour gelatin into an uncooked pineapple shell, the gelatin will not set.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27FCF3F8EDCB294E96AB1C12128BBFD0', X'515F4861D1AC1C40B5147F8D7D221D93', X'515F4861D1AC1C40B5147F8D7D221D93', 4, NULL, NULL, 2, NULL, 0, 'Boil one cup of water. Place the Jell-O and the unflavored gelatin in a bowl and add the boiling water. Stir well until the gelatin dissolves completely. Add the cup of vodka and stir.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51945E46199F1544AD4F20888BE6644A', X'515F4861D1AC1C40B5147F8D7D221D93', X'515F4861D1AC1C40B5147F8D7D221D93', 5, NULL, NULL, 2, NULL, 0, 'Once the gelatin is firm, cut the pineapple in half with a warm sharp knife. Then, cut the bottom part of the pineapple. Slice the pineapple halves into quarters.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FCDBF8448EECE40A2792D892406E379', X'515F4861D1AC1C40B5147F8D7D221D93', X'515F4861D1AC1C40B5147F8D7D221D93', 6, NULL, NULL, 2, NULL, 0, 'Using a warm sharp knife, slice the pineapple pieces into wedges. Place the pineapple jello wedges on a plate and serve.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6BB06F75FC13A4EAC575C5F1AA38B90', X'10ACB9BFF43EEC41BFD394F9E08A2C1E', X'10ACB9BFF43EEC41BFD394F9E08A2C1E', 0, NULL, NULL, 2, NULL, 0, 'Note: If you use the white crème de cacao, use 6 oz of chocolate vodka.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1740634F244E3D4E932C49A2A8253B03', X'10ACB9BFF43EEC41BFD394F9E08A2C1E', X'10ACB9BFF43EEC41BFD394F9E08A2C1E', 1, NULL, NULL, 2, NULL, 0, 'Slice the limes lengthwise. Using a spoon, scoop out the pulp all the way to the pith without breaking the rind. Place all the lime peels on a muffin pan or on 2 oz. plastic cups to keep them upright.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDACD8F39EE26C49BA1B0EB658D083E4', X'10ACB9BFF43EEC41BFD394F9E08A2C1E', X'10ACB9BFF43EEC41BFD394F9E08A2C1E', 2, NULL, NULL, 2, NULL, 0, 'Boil one cup of water. Place the gelatin mix in a bowl and add the boiling water. Stir well until the gelatin dissolves completely. Add the cup of chocolate vodka and white crème de cacao. Stir well.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09ACC904ACE75D4DAF9CB3FD614E44CF', X'10ACB9BFF43EEC41BFD394F9E08A2C1E', X'10ACB9BFF43EEC41BFD394F9E08A2C1E', 3, NULL, NULL, 2, NULL, 0, 'Fill the lime halves all the way to the top with the chocolate strawberry or chocolate cherry gelatin. Place the muffin pan or the plastic cups in the refrigerator. Refrigerate for at least four hours or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4B6A6BF3F595F4FAE6724DEEADD9CB5', X'10ACB9BFF43EEC41BFD394F9E08A2C1E', X'10ACB9BFF43EEC41BFD394F9E08A2C1E', 4, NULL, NULL, 2, NULL, 0, 'Remove the lime halves from the refrigerator. Using a sharp knife, slice the lime halves in half. Add chocolate sprinkles to make the limes look like mini watermelon wedges.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E22496B4B1AC064A97A72AC16D1D5ACA', X'2873A7838AD9604A8AF88A703C3493DB', X'2873A7838AD9604A8AF88A703C3493DB', 0, NULL, NULL, 2, NULL, 0, 'Note: You may use mint leaves for garnish instead of the sugar and limes.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED01CBC7C48B6D48BF739E15348E2EAE', X'2873A7838AD9604A8AF88A703C3493DB', X'2873A7838AD9604A8AF88A703C3493DB', 1, NULL, NULL, 2, NULL, 0, 'Choose the largest strawberries that you can find. Wash and dry them. Cut the bottom part of the strawberries so that they can stand upright. Then, cut the top part of the strawberries.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89D7369C86EAE54EA87DD4BE002BFBBC', X'2873A7838AD9604A8AF88A703C3493DB', X'2873A7838AD9604A8AF88A703C3493DB', 2, NULL, NULL, 2, NULL, 0, 'Boil 6 ounces of margarita mix. Place the strawberry Jell-O in a bowl and add the boiling margarita mix. Stir well until the gelatin dissolves completely. Add the tequila, tripe sec or Grand Marnier and stir.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5A6CDD83A123947B1E7C025DA0D52C4', X'2873A7838AD9604A8AF88A703C3493DB', X'2873A7838AD9604A8AF88A703C3493DB', 3, NULL, NULL, 2, NULL, 0, 'Place the strawberries on a plate and fill them with the strawberry margarita gelatin. Put the plate in the refrigerator. Refrigerate for at least four hours or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D679C9DCA086948AAC414D0998549E3', X'2873A7838AD9604A8AF88A703C3493DB', X'2873A7838AD9604A8AF88A703C3493DB', 4, NULL, NULL, 2, NULL, 0, 'Rub the rim of the strawberries with a lime wedge. Dip and rotate them in sugar, not salt, to coat the rim. Garnish with key lime wheels.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A8EB71C6EC2BE4289597CD031D1C358', X'2873A7838AD9604A8AF88A703C3493DB', X'2873A7838AD9604A8AF88A703C3493DB', 5, NULL, NULL, 2, NULL, 0, 'Follow the same instructions as the strawberry margarita jello shots recipe.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CEF06E93BE82546A2128DDEEF7AB4E3', X'2873A7838AD9604A8AF88A703C3493DB', X'2873A7838AD9604A8AF88A703C3493DB', 6, NULL, NULL, 2, NULL, 0, 'Garnish the strawberry daiquiri jello shots with whipped cream. If you have mint leaves, you may use them too.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6CC52218F70E54EBE31A6CDBDF4E32D', X'8AFE9D48A4E386418089CA47385C16C5', X'8AFE9D48A4E386418089CA47385C16C5', 0, NULL, NULL, 2, NULL, 0, 'Slice the apples lengthwise. Using a melon baller, scoop out the flesh of the apples leaving some meat on the skin of the apples. Place all the apple halves on a muffin pan to keep them upright.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA718D5021EC2E4ABF2197A6209D34DB', X'8AFE9D48A4E386418089CA47385C16C5', X'8AFE9D48A4E386418089CA47385C16C5', 1, NULL, NULL, 2, NULL, 0, 'Pour butterscotch schnapps into a bowl or container. Sprinkle the gelatin over the butterscotch. Let it stand for about one minute.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6808970B5DF9674EB304E4FA90DA1A70', X'8AFE9D48A4E386418089CA47385C16C5', X'8AFE9D48A4E386418089CA47385C16C5', 2, NULL, NULL, 2, NULL, 0, 'Fill the apple halves all the way to the top with the caramel apple gelatin and place the muffin pan in the refrigerator. Refrigerate for at least four hours or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'830549B5B511C84A9A4F356CCBA24F6B', X'8AFE9D48A4E386418089CA47385C16C5', X'8AFE9D48A4E386418089CA47385C16C5', 3, NULL, NULL, 2, NULL, 0, 'Remove the apple halves from the refrigerator. Using a sharp warm knife, slice the apple halves into wedges. Place the caramel apple jello shots on a plate and place a 2 oz. cup of caramel sauce in the middle.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5DEBC4C74372D4C805D7FFEC900FE01', X'7AD972A395156944ACC4BC14E8305655', X'7AD972A395156944ACC4BC14E8305655', 0, NULL, NULL, 2, NULL, 0, 'This recipe is for one half of a honeydew melon. If you would like to do the whole melon, double the amount of ingredients.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB2DE7331F71FC4883755DB7DC5B78C6', X'7AD972A395156944ACC4BC14E8305655', X'7AD972A395156944ACC4BC14E8305655', 1, NULL, NULL, 2, NULL, 0, 'Cut the honeydew melon lengthwise. Using a spoon, scoop out the flesh of the melon leaving about 1/2 inch (about 1 cm) of flesh in the melon. Place the honeydew melon in a bowl.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A181BF710175DA4AB34D66FA253DC9A7', X'7AD972A395156944ACC4BC14E8305655', X'7AD972A395156944ACC4BC14E8305655', 2, NULL, NULL, 2, NULL, 0, 'Pour Midori and vodka into a bowl or container. Sprinkle the gelatin over the liquors. Let it stand for about one minute.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE788FE48E2D2543B58F496AEA924978', X'7AD972A395156944ACC4BC14E8305655', X'7AD972A395156944ACC4BC14E8305655', 3, NULL, NULL, 2, NULL, 0, 'Fill the honeydew melon all the way to the top with the midori sour gelatin mixture and place it in the refrigerator. Refrigerate overnight or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8977B5E862ED0746AFB1B575FA5CD365', X'7AD972A395156944ACC4BC14E8305655', X'7AD972A395156944ACC4BC14E8305655', 4, NULL, NULL, 2, NULL, 0, 'Once the gelatin is firm, cut the ends of the honeydew melon with a warm sharp knife. Then, slice the melon in half from one end to the other.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E0E8C6679CDE3459013235B1FF2C06F', X'7AD972A395156944ACC4BC14E8305655', X'7AD972A395156944ACC4BC14E8305655', 5, NULL, NULL, 2, NULL, 0, 'Using a warm sharp knife, slice the two honeydew melon pieces into wedges. Place the midori sour jello wedges on a plate and serve.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A024486CC2BF2746877F99E744766212', X'AEBA7625256837478CEC8BE6BBA4F99C', X'AEBA7625256837478CEC8BE6BBA4F99C', 0, NULL, NULL, 2, NULL, 0, 'Slice the grapefruits lengthwise. Using a spoon, scoop out the pulp all the way to the pith without breaking the rind. Place all the grapefruit peels on a muffin pan to keep them upright.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77D76F0F387D9E4F90AE61458D3E5F46', X'AEBA7625256837478CEC8BE6BBA4F99C', X'AEBA7625256837478CEC8BE6BBA4F99C', 1, NULL, NULL, 2, NULL, 0, 'Pour vodka into a bowl or container. Sprinkle the gelatin over the vodka. Let it stand for about one minute.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D260F7833C1E7C42AD693A852F4F7552', X'AEBA7625256837478CEC8BE6BBA4F99C', X'AEBA7625256837478CEC8BE6BBA4F99C', 2, NULL, NULL, 2, NULL, 0, 'Fill the grapefruit halves all the way to the top with the pink greyhound gelatin. Place the muffin pan in the refrigerator. Refrigerate for at least four hours or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17AA2B3C2219FA4F9717DDC5DD76C8F5', X'AEBA7625256837478CEC8BE6BBA4F99C', X'AEBA7625256837478CEC8BE6BBA4F99C', 3, NULL, NULL, 2, NULL, 0, 'Remove the grapefruit halves from the refrigerator. Using a sharp warm knife, slice the grapefruit halves into wedges.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D90A818AA100D4E83BA9296463757C6', X'2C181CA5AA75E64DAA68B0BB350119F3', X'2C181CA5AA75E64DAA68B0BB350119F3', 0, NULL, NULL, 2, NULL, 0, 'Note: You can substitute the vodka for any other flavored vodka or a liqueur like White Crème de Cacao.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0815840F37C76D4B82DD130F24992036', X'2C181CA5AA75E64DAA68B0BB350119F3', X'2C181CA5AA75E64DAA68B0BB350119F3', 1, NULL, NULL, 2, NULL, 0, 'Pour 1 cup of vodka into a bowl or container. Sprinkle the gelatin over the vodka. Let it stand for about one minute.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48067D769F1F674D88B5C2477C4057C0', X'2C181CA5AA75E64DAA68B0BB350119F3', X'2C181CA5AA75E64DAA68B0BB350119F3', 2, NULL, NULL, 2, NULL, 0, 'Boil 1 cup of water and pour it into the gelatin mixture. Mix well until the gelatin completely dissolves.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23A8C84A2A97C142BA54D78C44114696', X'2C181CA5AA75E64DAA68B0BB350119F3', X'2C181CA5AA75E64DAA68B0BB350119F3', 3, NULL, NULL, 2, NULL, 0, 'Wash a brownie pop mold and dry it. Spray the inside of the mold with a small amount of vegetable oil. Wipe any excess oil. Pour the white jello into the mold and place it in the refrigerator. Refrigerate until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0B0D53437E269459267DA477C2E79C6', X'2C181CA5AA75E64DAA68B0BB350119F3', X'2C181CA5AA75E64DAA68B0BB350119F3', 4, NULL, NULL, 2, NULL, 0, 'Once the jello eyeballs are firm, take them out of the mold and place them on a plate.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AEA4EFAE2DB31489BE6F7D60B9938F8', X'2C181CA5AA75E64DAA68B0BB350119F3', X'2C181CA5AA75E64DAA68B0BB350119F3', 5, NULL, NULL, 2, NULL, 0, 'Using red food coloring and a brush, draw red lines on the eyeballs jello shots to make scary looking veins.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA19E5A95A70954FAAFA211750BE2A82', X'0F92C69D7121F74C9C65F2D202CBE043', X'0F92C69D7121F74C9C65F2D202CBE043', 0, NULL, NULL, 2, NULL, 0, 'Pour 3/4 cups of Jägermeister into a bowl or container. Sprinkle the gelatin over the liqueur. Let it stand for about one minute.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A93AF5C2C28F8468FF63CAE347CA907', X'0F92C69D7121F74C9C65F2D202CBE043', X'0F92C69D7121F74C9C65F2D202CBE043', 1, NULL, NULL, 2, NULL, 0, 'In a pan, boil the Red Bull and pour it into the Jägermeister with gelatin. Mix well until the gelatin completely dissolves.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A45AD0D43688F46838D88168E56BA83', X'0F92C69D7121F74C9C65F2D202CBE043', X'0F92C69D7121F74C9C65F2D202CBE043', 2, NULL, NULL, 2, NULL, 0, 'Wash a bat shaped baking mold and dry it. Spray the inside of the mold with a small amount of vegetable oil. Wipe any excess oil. Pour the jager bomb gelatin into the mold and place it in the refrigerator.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28294B342D195A4C96D4C04FE52CA25D', X'0F92C69D7121F74C9C65F2D202CBE043', X'0F92C69D7121F74C9C65F2D202CBE043', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate the jager bomb jello shots until they''re firm. Take them out of the mold and place them on a plate.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55169896A4774D44A05357ABDF59A5F7', X'84B2FD236F06104586EAD8BF36A9976C', X'84B2FD236F06104586EAD8BF36A9976C', 0, NULL, NULL, 2, NULL, 0, 'If you would like to make Vodka and Tonic jello shots, substitute the gin with vodka and follow the same steps.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58DD33DA6623004A94B83F15A6374771', X'84B2FD236F06104586EAD8BF36A9976C', X'84B2FD236F06104586EAD8BF36A9976C', 1, NULL, NULL, 2, NULL, 0, 'Pour gin into a bowl or container. Sprinkle the gelatin over the gin. Let it stand for about one minute.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C93A2631875B2C4CAB77C2A451D9480B', X'84B2FD236F06104586EAD8BF36A9976C', X'84B2FD236F06104586EAD8BF36A9976C', 2, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup of sugar and 1 tablespoon of fresh lime juice to the gin and tonic mixture. Stir until the sugar completely dissolves.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA6D313D14E49449996167EEC48563BA', X'84B2FD236F06104586EAD8BF36A9976C', X'84B2FD236F06104586EAD8BF36A9976C', 3, NULL, NULL, 2, NULL, 0, 'Wash a ghost baking mold and dry it. Spray the inside of the mold with a small amount of vegetable oil. Wipe any excess oil. Pour the gin and tonic gelatin into the mold and place it in the refrigerator.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D857751A93A8E14E9CAD7020D968F71B', X'84B2FD236F06104586EAD8BF36A9976C', X'84B2FD236F06104586EAD8BF36A9976C', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate the gin and tonic jello shots until they''re firm. Take them out of the mold and place them on a plate.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60AC7BC12A3AB943B03460631E1952B1', X'84B2FD236F06104586EAD8BF36A9976C', X'84B2FD236F06104586EAD8BF36A9976C', 5, NULL, NULL, 2, NULL, 0, 'Turn a black light on to make the gin and tonic jello shots glow in the dark.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD6A5E59BB96E64FBE69AC8FDDC43C0B', X'993C1B407EA1EB4EB6BB7963877DCF8F', X'993C1B407EA1EB4EB6BB7963877DCF8F', 0, NULL, NULL, 2, NULL, 0, 'If you don''t have 100 proof (50% alcohol by volume) vodka, you can use 80 proof (40% abv) vodka.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7BD049C54965F479F26086598C67356', X'993C1B407EA1EB4EB6BB7963877DCF8F', X'993C1B407EA1EB4EB6BB7963877DCF8F', 1, NULL, NULL, 2, NULL, 0, 'Pour vodka into a bowl or container. Sprinkle the gelatin over the vodka. Let it stand for about three minutes.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DC8C28E6786F14CA73B3DC2E3FA57BD', X'993C1B407EA1EB4EB6BB7963877DCF8F', X'993C1B407EA1EB4EB6BB7963877DCF8F', 2, NULL, NULL, 2, NULL, 0, 'Once the gelatin is dissolved, pour hazelnut liqueur, raspberry liqueur and the sweetened condensed milk. Stir well until everything is mixed completely.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FDF55FBE968374EAB48053EC91183D5', X'993C1B407EA1EB4EB6BB7963877DCF8F', X'993C1B407EA1EB4EB6BB7963877DCF8F', 3, NULL, NULL, 2, NULL, 0, 'Wash and dry the brain mold. Spray the inside of the brain mold with a small amount of vegetable oil. Wipe any excess oil. Place the mold in a bowl to keep it level.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6688BB59FFC9654F9A9E5C2CF777EC8B', X'993C1B407EA1EB4EB6BB7963877DCF8F', X'993C1B407EA1EB4EB6BB7963877DCF8F', 4, NULL, NULL, 2, NULL, 0, 'Fill the brain mold half way with the gelatin mixture. Place the mold in the refrigerator for about 20 minutes to let it cool. Remove the brain mold from the refrigerator and place it in the freezer for about 45 minutes or until the gelatin is firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48CA14283215F94D9EC8EC51984C951F', X'993C1B407EA1EB4EB6BB7963877DCF8F', X'993C1B407EA1EB4EB6BB7963877DCF8F', 5, NULL, NULL, 2, NULL, 0, 'Once the gelatin is firm, scoop out a small amount of gelatin from the center of the brain. Be careful not to remove too much gelatin.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'014EE1C8DE29C149AD3F51B1055C1206', X'993C1B407EA1EB4EB6BB7963877DCF8F', X'993C1B407EA1EB4EB6BB7963877DCF8F', 6, NULL, NULL, 2, NULL, 0, 'Pour the remaining gelatin mixture over the raspberry pie filling. Refrigerate the brain mold overnight or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEC8347A0629D442A84D9B64C6257D99', X'993C1B407EA1EB4EB6BB7963877DCF8F', X'993C1B407EA1EB4EB6BB7963877DCF8F', 7, NULL, NULL, 2, NULL, 0, 'Once the brain is firm, place a flat plate upside down over the open side of the mold, then flip the mold and plate together. Lift the mold off, leaving the brain on the plate.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'582967ACF83BA84E9B1BA9FBB5594BA2', X'C1B8430F0DFFB043B5EBC1D78B0EE877', X'C1B8430F0DFFB043B5EBC1D78B0EE877', 0, NULL, NULL, 2, NULL, 0, 'Pour 1 cup of vodka into a bowl or container. Sprinkle the gelatin over the vodka. Let it stand for about two minutes.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29712439A2EE3A4C990CCCA70311126A', X'C1B8430F0DFFB043B5EBC1D78B0EE877', X'C1B8430F0DFFB043B5EBC1D78B0EE877', 1, NULL, NULL, 2, NULL, 0, 'In a pan, boil 12 ounces of evaporated milk and pour it into the gelatin mixture. Mix well until the gelatin dissolves completely.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'986D943D1C010C4D82CB9653D967D8FA', X'C1B8430F0DFFB043B5EBC1D78B0EE877', X'C1B8430F0DFFB043B5EBC1D78B0EE877', 2, NULL, NULL, 2, NULL, 0, 'Add 2 teaspoons of pumpkin pie spice, 1 teaspoon of vanilla extract and 1 can (15 oz.) of 100% pure pumpkin to the gelatin mixture. Stir well until everything is mixed completely.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C70D497FB9E4674FA8B0A552DE388FFF', X'C1B8430F0DFFB043B5EBC1D78B0EE877', X'C1B8430F0DFFB043B5EBC1D78B0EE877', 3, NULL, NULL, 2, NULL, 0, 'Pour pumpkin pie jello shots mixture into mini graham cracker pie crusts and place them in the refrigerator.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDF0632734AEC94BBBB70C23FA6880A9', X'C1B8430F0DFFB043B5EBC1D78B0EE877', X'C1B8430F0DFFB043B5EBC1D78B0EE877', 4, NULL, NULL, 2, NULL, 0, 'Once the pumpkin pie jello shots are firm, garnish them with whipped cream.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'558D358CCF176443A5DB42D8E0B36AB6', X'412DBDAEBD8D934B8288667DCCD77E75', X'412DBDAEBD8D934B8288667DCCD77E75', 0, NULL, NULL, 2, NULL, 0, 'Pour vodka and coffee liqueur into a bowl or container. Sprinkle the gelatin over the liquors. Let it stand for about one minute.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'232561B6B0233F4295B69661F781EA68', X'412DBDAEBD8D934B8288667DCCD77E75', X'412DBDAEBD8D934B8288667DCCD77E75', 1, NULL, NULL, 2, NULL, 0, 'In a pan, boil the milk and pour it into the gelatin mixture. Mix well until the gelatin completely dissolves.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DBCBA37EF202E4DBD4D3951C69A16CE', X'412DBDAEBD8D934B8288667DCCD77E75', X'412DBDAEBD8D934B8288667DCCD77E75', 2, NULL, NULL, 2, NULL, 0, 'Get an ice cube tray or gelatin mold that has the figures of skulls and bones. Wash it and dry it. Spray the inside of the mold with a small amount of vegetable oil. Pour the mixture into the mold and place it in the refrigerator.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED9B5FEBF8890D419CDAB7DFAF93E643', X'412DBDAEBD8D934B8288667DCCD77E75', X'412DBDAEBD8D934B8288667DCCD77E75', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate the white russian jello shots until they''re firm. Take them out of the mold and serve.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'045A504A6C9C794D830B5663922251B5', X'2896D02885F7F047B90A2C4C820E468E', X'2896D02885F7F047B90A2C4C820E468E', 0, NULL, NULL, 2, NULL, 0, 'Pour vodka into a bowl or container. Sprinkle the gelatin over the vodka. Let it stand for about three minutes.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2638F410A09454F9EF98EAA866D5C86', X'2896D02885F7F047B90A2C4C820E468E', X'2896D02885F7F047B90A2C4C820E468E', 1, NULL, NULL, 2, NULL, 0, 'Once the gelatin is dissolved, pour amaretto, coffee liqueur and the sweetened condensed milk. Stir well until everything is mixed completely.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67AE55F7C0A2E94BBE15B1E64F3764A6', X'2896D02885F7F047B90A2C4C820E468E', X'2896D02885F7F047B90A2C4C820E468E', 2, NULL, NULL, 2, NULL, 0, 'Wash and dry the spider mold. Spray the inside of the spider mold with a small amount of vegetable oil. Wipe any excess oil. Place the mold in a bowl to keep it level.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28A5375A19D8BA478B8068069AAFD6B0', X'2896D02885F7F047B90A2C4C820E468E', X'2896D02885F7F047B90A2C4C820E468E', 3, NULL, NULL, 2, NULL, 0, 'Fill the spider mold with the toasted almond jello and place it in the refrigerator. Refrigerate the spider mold overnight or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'587F9ABEEB0A8747A4E40A56834D5B46', X'2896D02885F7F047B90A2C4C820E468E', X'2896D02885F7F047B90A2C4C820E468E', 4, NULL, NULL, 2, NULL, 0, 'Once the toasted almond spider is firm, place a flat plate upside down over the open side of the mold, then flip the mold and plate together. Lift the mold off, leaving the spider on the plate.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A13A03AB1C32E3488795E6EBBCBCBB16', X'2896D02885F7F047B90A2C4C820E468E', X'2896D02885F7F047B90A2C4C820E468E', 5, NULL, NULL, 2, NULL, 0, 'Using an artist brush and red food coloring, paint the eyes and the middle part of the spider''s legs. Then, use the black food coloring to paint the remaining spider.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'597EBAC7467C1D48BF95DD3E68C4CF89', X'1C90A3045A53C4499A6BDA8954166123', X'1C90A3045A53C4499A6BDA8954166123', 0, NULL, NULL, 2, NULL, 0, 'Note: The amount of jello that the fingers pan can hold is about 1/2 a cup of jello. I suggest using 2 pans or bigger molds.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60DE228202D56E4698E137D6C48FC9E0', X'1C90A3045A53C4499A6BDA8954166123', X'1C90A3045A53C4499A6BDA8954166123', 1, NULL, NULL, 2, NULL, 0, 'Pour vodka or vanilla vodka into a bowl or container. Sprinkle the gelatin over the vodka. Let it stand for about one minute.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'826785E054E9624B9DB33660B9A66938', X'1C90A3045A53C4499A6BDA8954166123', X'1C90A3045A53C4499A6BDA8954166123', 2, NULL, NULL, 2, NULL, 0, 'Once the gelatin is dissolved, pour 2 ounces of butterscotch liqueur and 2 ounces of Irish cream. Stir well until everything is mixed completely.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9D4D8AE1044C44FB240E864B3C75E49', X'1C90A3045A53C4499A6BDA8954166123', X'1C90A3045A53C4499A6BDA8954166123', 3, NULL, NULL, 2, NULL, 0, 'Wash a Halloween fingers pan or a witch fingers pan and dry it. Spray the inside of the mold with a small amount of vegetable oil. Wipe any excess oil. Pour the butterfinger gelatin into the mold and place it in the refrigerator.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8955EBCE86B25049B9C2B9050A3DC191', X'1C90A3045A53C4499A6BDA8954166123', X'1C90A3045A53C4499A6BDA8954166123', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate the butterfinger jello shots until they''re firm. Take them out of the mold and place them on a plate.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAF0D7BFAC2C8F4A97451ABAB821950C', X'1C90A3045A53C4499A6BDA8954166123', X'1C90A3045A53C4499A6BDA8954166123', 5, NULL, NULL, 2, NULL, 0, 'Using an artist brush and food coloring, paint all the butterfinger jello shots as you like.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'059A2A3ACF00EC4280CC8865AE53C6F5', X'489CB8844F567940B6A58E0237CC787F', X'489CB8844F567940B6A58E0237CC787F', 0, NULL, NULL, 2, NULL, 0, 'This recipe is similar to the Nog De Cacao drink. If you prefer Rum Eggnog jello shots, use 1 cup of light rum instead of the chocolate vodka and white crème de cacao.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD6F1A3D1B2FBE42AEBFAF22EA7FF91C', X'489CB8844F567940B6A58E0237CC787F', X'489CB8844F567940B6A58E0237CC787F', 1, NULL, NULL, 2, NULL, 0, 'Pour chocolate vodka and white crème de cacao into a bowl or container. Sprinkle gelatin over the liquors. Let it stand for about one minute.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B73C0EDF4EFF6B44BFB61045E4D8C355', X'489CB8844F567940B6A58E0237CC787F', X'489CB8844F567940B6A58E0237CC787F', 2, NULL, NULL, 2, NULL, 0, 'Wash a Christmas gelatin mold and dry it. Spray the inside of the mold with a small amount of vegetable oil. Wipe any excess oil. Pour the eggnog gelatin into the mold and place it in the refrigerator.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6CCEE862A345441ADDFA605D4407535', X'489CB8844F567940B6A58E0237CC787F', X'489CB8844F567940B6A58E0237CC787F', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate the eggnog jello shots until they''re firm. Take them out of the mold and put them on a plate.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'240C3264B87B904E8DCDE6CC0C147991', X'489CB8844F567940B6A58E0237CC787F', X'489CB8844F567940B6A58E0237CC787F', 4, NULL, NULL, 2, NULL, 0, 'Using an artist brush and food coloring, paint all the Christmas eggnog jello shots so that the figurines stand out.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0EEECD46BC92546A6359298EBC0632B', X'B70A489FCEF69B4399626AC691C0BB31', X'B70A489FCEF69B4399626AC691C0BB31', 0, NULL, NULL, 2, NULL, 0, 'Note: If you don''t want strong jello shots, use a lower proof peppermint schnapps.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32417266EE191742A274E3A3ED289F2E', X'B70A489FCEF69B4399626AC691C0BB31', X'B70A489FCEF69B4399626AC691C0BB31', 1, NULL, NULL, 2, NULL, 0, 'Pour 1 cup of peppermint schnapps into a bowl or container. Sprinkle 2 envelopes of unflavored gelatin over the liqueur. Let it stand for about two minutes. Add 1 cup of boiling water and stir until the gelatin completely dissolves. Pour the sweetened condensed milk and stir well.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCD566D4539D004BA799F0AE3DD21224', X'B70A489FCEF69B4399626AC691C0BB31', X'B70A489FCEF69B4399626AC691C0BB31', 2, NULL, NULL, 2, NULL, 0, 'Pour 1 cup of peppermint schnapps into another bowl or container. Sprinkle 1 envelope of unflavored gelatin over the liqueur. Let it stand for about one minute. Add cherry flavored Jell-O and pour 1 cup of boiling water. Stir until the gelatin completely dissolves.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D16786A9E32E3545B1AADAC3267414B8', X'B70A489FCEF69B4399626AC691C0BB31', X'B70A489FCEF69B4399626AC691C0BB31', 3, NULL, NULL, 2, NULL, 0, 'Wash and dry the mold. Spray the inside of the mold with a small amount of vegetable oil. Wipe any excess oil. Pour 3/4 cup of white gelatin into the mold. Place it in the freezer for about 15 to 20 minutes until the gelatin is firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AE7B44E31E3834C8DA6E4C281EA4D13', X'B70A489FCEF69B4399626AC691C0BB31', X'B70A489FCEF69B4399626AC691C0BB31', 4, NULL, NULL, 2, NULL, 0, 'Keep adding 3/4 cup of white and red gelatin to create the layers. When you get to the last layer, pour all the remaining white gelatin and place the container in the refrigerator. Let it set until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5230FCB7317D6A47A04D8E02CAAA986B', X'B70A489FCEF69B4399626AC691C0BB31', X'B70A489FCEF69B4399626AC691C0BB31', 5, NULL, NULL, 2, NULL, 0, 'Once the gelatin is firm, place a cutting board over the open side of the mold, then flip the mold and cutting board together. Lift the mold off, leaving the gelatin on the cutting board.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EA301F8A5F7D84897F8CD7BBE458232', X'B70A489FCEF69B4399626AC691C0BB31', X'B70A489FCEF69B4399626AC691C0BB31', 6, NULL, NULL, 2, NULL, 0, 'Using a warm sharp knife, slice a 3/4 inch (about 2 cm) thick piece of gelatin and gently lay it down on the cutting board.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08E0F5CF351A9A458164DD1D669E4978', X'B70A489FCEF69B4399626AC691C0BB31', X'B70A489FCEF69B4399626AC691C0BB31', 7, NULL, NULL, 2, NULL, 0, 'Using a candy cane cookie cutter, cut out candy cane jello shots out of the slices of peppermint gelatin. Make sure that the cuts are done in an angle to mimic the layers of a candy cane.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C76A7CA802D734B84408F6D73DAEB38', X'B70A489FCEF69B4399626AC691C0BB31', X'B70A489FCEF69B4399626AC691C0BB31', 8, NULL, NULL, 2, NULL, 0, 'After cutting out a candy cane jello shot, transfer the jello shot in the cookie cutter onto a plate.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF0C93B74837D64F871ACE5BCB34E7D5', X'57D7CCB6E3113144B894B75772B50803', X'57D7CCB6E3113144B894B75772B50803', 0, NULL, NULL, 2, NULL, 0, 'Pour white crème de cacao liqueur into a bowl or container. Sprinkle the gelatin over the liqueur. Let it stand for about one minute.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4A666648D7505428F7190683669004A', X'57D7CCB6E3113144B894B75772B50803', X'57D7CCB6E3113144B894B75772B50803', 1, NULL, NULL, 2, NULL, 0, 'In a pan, boil the milk and pour it into the gelatin mixture. Mix well until the gelatin completely dissolves.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D984FE6EFE94C40BC03CFC32728368A', X'57D7CCB6E3113144B894B75772B50803', X'57D7CCB6E3113144B894B75772B50803', 2, NULL, NULL, 2, NULL, 0, 'Wash a mini Christmas tree mold and dry it. Spray the inside of the mold with a small amount of vegetable oil. Wipe any excess oil. Pour the grasshopper jello into the mold and place it in the refrigerator. Refrigerate until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3056C4C02B1F864D8327CA16E1F3377E', X'57D7CCB6E3113144B894B75772B50803', X'57D7CCB6E3113144B894B75772B50803', 3, NULL, NULL, 2, NULL, 0, 'Once the grasshopper jello shots are firm, take them out of the mold and place them on a plate.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D6F03F7BE096841AEDA098CC1A3E692', X'4A42D2E741D6BF4D9A112E9D09502FA5', X'4A42D2E741D6BF4D9A112E9D09502FA5', 0, NULL, NULL, 2, NULL, 0, 'Note: You may use a berry flavored vodka like a strawberry vodka, blueberry vodka or raspberry vodka for the white jello recipe.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C9F894C7744B744BF0950F19429BC06', X'4A42D2E741D6BF4D9A112E9D09502FA5', X'4A42D2E741D6BF4D9A112E9D09502FA5', 1, NULL, NULL, 2, NULL, 0, 'Pour 1 cup of vodka into a bowl or container. Sprinkle 1 envelope of unflavored gelatin over the vodka. Let it stand for about one minute. Add berry blue flavored Jell-O and pour 1 cup of boiling water. Stir until the gelatin completely dissolves.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B03A8E9D98934B499D672BFA166095D2', X'4A42D2E741D6BF4D9A112E9D09502FA5', X'4A42D2E741D6BF4D9A112E9D09502FA5', 2, NULL, NULL, 2, NULL, 0, 'Pour 1 cup of vodka into a bowl or container. Sprinkle 2 envelopes of unflavored gelatin over the vodka. Let it stand for about two minutes. Add 1 cup of boiling water and stir until the gelatin completely dissolves. Pour the sweetened condensed milk and stir well.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAC6ABD80C5DD845BC44BBC098E27FA2', X'4A42D2E741D6BF4D9A112E9D09502FA5', X'4A42D2E741D6BF4D9A112E9D09502FA5', 3, NULL, NULL, 2, NULL, 0, 'Pour 1 cup of vodka into a bowl or container. Sprinkle 1 envelope of unflavored gelatin over the vodka. Let it stand for about one minute. Add strawberry flavored Jell-O and pour 1 cup of boiling water. Stir until the gelatin completely dissolves.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'687390E75BDABE43B0A80D1599BC7705', X'4A42D2E741D6BF4D9A112E9D09502FA5', X'4A42D2E741D6BF4D9A112E9D09502FA5', 4, NULL, NULL, 2, NULL, 0, 'Once the star is firm, place a flat plate upside down over the open side of the mold, then flip the mold and plate together. Lift the mold off, leaving the star on the plate.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E102402EB2D184DA15871492F3ADB4C', X'4A42D2E741D6BF4D9A112E9D09502FA5', X'4A42D2E741D6BF4D9A112E9D09502FA5', 5, NULL, NULL, 2, NULL, 0, 'If you have a Wilton Mini Star Silicone Mold, you may use it to make mini layered jello shot stars. Just follow the instructions from above and pour 3/4 oz. of gelatin for each layer.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B61AB2AFAFBF544282114695E9332095', X'C765B668068B574FB0D5EC046144BFF1', X'C765B668068B574FB0D5EC046144BFF1', 0, NULL, NULL, 2, NULL, 0, 'Note: You may use a berry flavored vodka like a strawberry vodka, blueberry vodka or raspberry vodka for the white jello recipe.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72D85DC8FAA5084B9650A08324908A4D', X'C765B668068B574FB0D5EC046144BFF1', X'C765B668068B574FB0D5EC046144BFF1', 1, NULL, NULL, 2, NULL, 0, 'Pour 1 cup of vodka into a bowl or container. Add the berry blue flavored Jell-O and let it stand for about one minute. Pour 1 cup of boiling water into the gelatin mix. Stir until the gelatin completely dissolves. Pour the blue jello into two lightly oiled pans and place them in the refrigerator. Refrigerate for at least two hours or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7299A524E2710C43BF6DD48C24C70FA4', X'C765B668068B574FB0D5EC046144BFF1', X'C765B668068B574FB0D5EC046144BFF1', 2, NULL, NULL, 2, NULL, 0, 'Pour 1 cup of vodka into a bowl or container. Sprinkle 2 envelopes of unflavored gelatin over the vodka. Let it stand for about two minutes. Add 1 cup of boiling water and stir until the gelatin completely dissolves. Pour the sweetened condensed milk and stir well. Place the white gelatin mixture in the refrigerator to cool down.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFCDE075021B494DB4627D48A1D28301', X'C765B668068B574FB0D5EC046144BFF1', X'C765B668068B574FB0D5EC046144BFF1', 3, NULL, NULL, 2, NULL, 0, 'Unmold the red and blue gelatin. Dice both gelatins with a warm sharp knife to create small cubes. Randomly place the red and blue gelatin cubes into a 13 x 9 inch pan sprayed with cooking spray.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A12222EF932BA4DAAE944452F5B9F35', X'C765B668068B574FB0D5EC046144BFF1', X'C765B668068B574FB0D5EC046144BFF1', 4, NULL, NULL, 2, NULL, 0, 'Once the white gelatin has cooled down, pour it over the diced red and blue gelatin cubes. Stir gently to distribute the gelatin cubes.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A924ABD55284043BFDC252F00288F5F', X'C765B668068B574FB0D5EC046144BFF1', X'C765B668068B574FB0D5EC046144BFF1', 5, NULL, NULL, 2, NULL, 0, 'Place the pan in the refrigerator. Refrigerate for at least two hours or until firm.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8874EB9F48DDCF4F969C2366B12020E4', X'C6C69A06EAFB67479D2AE0D040CEA2BE', X'C6C69A06EAFB67479D2AE0D040CEA2BE', 0, NULL, NULL, 2, NULL, 0, 'Note: Everclear is 190 proof (95% alcohol) and in some states of the U.S. a 151 proof (75.5% alcohol) is sold instead. The 190 or 151 proof is fine to make limoncello. If you can not find grain alcohol in your region, use 100 proof vodka.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BECFFA14B16D5D43A9E5F659C26A8312', X'C6C69A06EAFB67479D2AE0D040CEA2BE', X'C6C69A06EAFB67479D2AE0D040CEA2BE', 1, NULL, NULL, 2, NULL, 0, 'Wash all the lemons using a brush and warm water to remove all the pesticides and dirt. After washing the lemons, dry them with a paper towel.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D084B6EF72588E4499C637A44584FF50', X'C6C69A06EAFB67479D2AE0D040CEA2BE', X'C6C69A06EAFB67479D2AE0D040CEA2BE', 2, NULL, NULL, 2, NULL, 0, 'Place all the lemon peels in a glass jar and add the liter of grain alcohol. Let the peels and grain alcohol age for at least a month. I usually leave it for 6 weeks. Stir it once a week with a metal or wood spoon.');
