INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA04B48A485BB4394DC4C2E6E2E8983', X'C6C69A06EAFB67479D2AE0D040CEA2BE', X'C6C69A06EAFB67479D2AE0D040CEA2BE', 5, NULL, NULL, 2, NULL, 0, 'Get two empty and clean 1 liter bottles. Use a funnel to pour all the limoncello into the bottles. After bottling the limoncello, store one bottle in a cool place and store the other one in the freezer.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F9E9F05DEA15949AF99D2896FA788A6', X'C6C69A06EAFB67479D2AE0D040CEA2BE', X'C6C69A06EAFB67479D2AE0D040CEA2BE', 6, NULL, NULL, 2, NULL, 0, 'Limoncello is served chilled as an after dinner drink. It''s usually served in a small glass like a cordial glass. The glass is usually chilled as well.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7719EA5E1BA4E44FB9089BC664FDAE5E', X'7B03C2BD99B70A4DBAE760BFC78D977C', X'7B03C2BD99B70A4DBAE760BFC78D977C', 0, NULL, NULL, 2, NULL, 0, 'The basic concept of making coffee liqueur is to make a really strong coffee with sugar. Add vanilla and liquor. Let it age for at least one month. Bottle it and that''s it.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6A77A10D08A1B4D88FBECA465079050', X'7B03C2BD99B70A4DBAE760BFC78D977C', X'7B03C2BD99B70A4DBAE760BFC78D977C', 1, NULL, NULL, 2, NULL, 0, 'You don''t have to use vodka. You can use rum, dark rum, brandy or cognac. In addition, you can also combine the liquors. For example, you can do half vodka and half brandy.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46DBD979B2C78944859319D71B204EA7', X'7B03C2BD99B70A4DBAE760BFC78D977C', X'7B03C2BD99B70A4DBAE760BFC78D977C', 2, NULL, NULL, 2, NULL, 0, 'Brew a really strong coffee using a bold or extra bold ground coffee. You can use a coffee maker or a French press. I personally used a French press to get the maximum coffee flavor.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBF2D1D3FD0B0B4F8CF3F7893AFE9808', X'7B03C2BD99B70A4DBAE760BFC78D977C', X'7B03C2BD99B70A4DBAE760BFC78D977C', 3, NULL, NULL, 2, NULL, 0, 'Once you made a strong coffee, pour the coffee into a glass jar. Add brown sugar and stir well until it dissolves completely. Let the coffee cool down for a few minutes, then add the pure vanilla extract and vodka. Stir well and seal the jar.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84870297A971C1409BE68C0D56BC5501', X'7B03C2BD99B70A4DBAE760BFC78D977C', X'7B03C2BD99B70A4DBAE760BFC78D977C', 4, NULL, NULL, 2, NULL, 0, 'Store the jar of coffee liqueur in a cool place away from sunlight for at least one month. Stir the contents with a metal or wood spoon at least once a week.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE6C59E6BFADDF41B22265F17553F59D', X'7B03C2BD99B70A4DBAE760BFC78D977C', X'7B03C2BD99B70A4DBAE760BFC78D977C', 5, NULL, NULL, 2, NULL, 0, 'According to Kahlúa, its coffee liqueur is made from 100% Arabica coffee beans, sugarcane, vanilla, caramel and notes of rum.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C9234ECAF492441B330DBC197A6AF81', X'7B03C2BD99B70A4DBAE760BFC78D977C', X'7B03C2BD99B70A4DBAE760BFC78D977C', 6, NULL, NULL, 2, NULL, 0, 'You can use any instant coffee of your choice. It could be regular instant coffee or decaffeinated instant coffee.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'520F380B5DE2874E8334BEBFFDF011EB', X'7B03C2BD99B70A4DBAE760BFC78D977C', X'7B03C2BD99B70A4DBAE760BFC78D977C', 7, NULL, NULL, 2, NULL, 0, 'Dissolve the brown sugar in a pan with hot water. Add instant coffee and mix well. Let it cool down. Pour coffee into a jar. Add the pure vanilla extract and vodka. Stir well.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3586F72D49F4844F9C59B72FC12CE919', X'7B03C2BD99B70A4DBAE760BFC78D977C', X'7B03C2BD99B70A4DBAE760BFC78D977C', 8, NULL, NULL, 2, NULL, 0, 'Store the jar of coffee liqueur in a cool place away from sunlight for at least one month. Stir the contents with a metal or wood spoon at least once a week.');
