INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D24380C9CC3ECB4689D74072C7F7CA0A', X'EED5B62381E7B84CBAC1A335CD1FA9F4', X'EED5B62381E7B84CBAC1A335CD1FA9F4', 3, NULL, NULL, 2, NULL, 0, 'After at least six weeks of aging, the blackberry liqueur is ready to be strained. Get an empty jar or a container and strain the liqueur with a fine mesh strainer.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA8026867FB07B469F4ED8A2B323CC1F', X'566B024C35AF5042BDEBB7FD9A7E5D1E', X'566B024C35AF5042BDEBB7FD9A7E5D1E', 0, NULL, NULL, 2, NULL, 0, 'Use 100% pure honey to make the liqueur.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A7A00B676127446AA4E731F5DB43ABE', X'566B024C35AF5042BDEBB7FD9A7E5D1E', X'566B024C35AF5042BDEBB7FD9A7E5D1E', 1, NULL, NULL, 2, NULL, 0, 'Wash and dry a glass jar. Pour 2 cups of vodka into the jar and add 1 1/2 cups of honey. Stir well until all the honey dissolves completely.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0FC133271E36A43BF34AD7E0E81BD80', X'566B024C35AF5042BDEBB7FD9A7E5D1E', X'566B024C35AF5042BDEBB7FD9A7E5D1E', 2, NULL, NULL, 2, NULL, 0, 'Store the jar with vodka and honey in a cool place away from sunlight for at least one month. Stir the contents with a metal or wood spoon at least once a week.');
