INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B15C5BDA018EA43B36C7B3C70093F1A', X'ACEC07A4FF571A40B1D6AB471302595D', X'ACEC07A4FF571A40B1D6AB471302595D', 0, NULL, NULL, 2, NULL, 0, '');
