INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDEBD2AB43BF9644B966DB93428EF4A5', X'694A2F7DE12129458362BB4E4D2B4427', X'694A2F7DE12129458362BB4E4D2B4427', 1, NULL, NULL, 2, NULL, 0, 'Boil 2 cups of water in a pan. Add the sugar and stir constantly until all the sugar dissolves completely. Let the simple syrup cool down. Add the simple syrup to the jar of peppermint liqueur and stir.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4EC8E223E29F941A858B6D8E6752E38', X'694A2F7DE12129458362BB4E4D2B4427', X'694A2F7DE12129458362BB4E4D2B4427', 2, NULL, NULL, 2, NULL, 0, 'After one month of aging, the peppermint schnapps is ready to be bottled. Get a funnel and an empty bottle. Pour peppermint schnapps into the bottle using the funnel.');
INSERT INTO `VWR INTERNAL Goodcocktails3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'092E839167CE15438FAC4481CB9B3A26', X'0557ACA5C2F4C745BAE690B7FA8A7815', X'0557ACA5C2F4C745BAE690B7FA8A7815', 0, NULL, NULL, 2, NULL, 0, 'Wash the mint leaves in cold water and dry them completely with paper towels. Remove all the leaves from the stems and discard the stems.');
