INSERT INTO `VWR INTERNAL Honeysucklewhite recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'4759718B5DD091429506D12C1010BF25', X'EB10E73F7F6D7947A8FEC95B338871CE', X'EB10E73F7F6D7947A8FEC95B338871CE', 15, NULL, NULL, 2, NULL, 0, '1/2 cup green onions, minced');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'F02B5473B40F1643B150AC4BD0507A80', X'EB10E73F7F6D7947A8FEC95B338871CE', X'EB10E73F7F6D7947A8FEC95B338871CE', 16, NULL, NULL, 2, NULL, 0, 'Salt and freshly ground black pepper to taste');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAFFFDBEAB210A448970667BF685C8F5', X'10E9ECB03B90434CA691C3A6632D8F81', X'10E9ECB03B90434CA691C3A6632D8F81', 0, NULL, NULL, 2, NULL, 0, 'Preheat outdoor grill or charcoal grill to 325°F and lightly oil the grates.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C8DE7E505DD6B4FA5FF57B8398C92B0', X'10E9ECB03B90434CA691C3A6632D8F81', X'10E9ECB03B90434CA691C3A6632D8F81', 1, NULL, NULL, 2, NULL, 0, 'Place the turkey tenderloins on the grill and cook 10 to 12 ½ minutes then flip.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC4616AF965C54409CCE0B9D2755BA76', X'10E9ECB03B90434CA691C3A6632D8F81', X'10E9ECB03B90434CA691C3A6632D8F81', 2, NULL, NULL, 2, NULL, 0, 'Continue to cook until they reach a minimum internal temperature of 165° F (approximately 10 to 12 ½ additional minutes). Use a meat thermometer to confirm internal temperature.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'345AD38B2534704EABF6907D4159E8E9', X'10E9ECB03B90434CA691C3A6632D8F81', X'10E9ECB03B90434CA691C3A6632D8F81', 3, NULL, NULL, 2, NULL, 0, 'While turkey tenderloins are cooking, prepare the Southwest salad.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6E951E371092A4CB29A9A91E097681E', X'10E9ECB03B90434CA691C3A6632D8F81', X'10E9ECB03B90434CA691C3A6632D8F81', 4, NULL, NULL, 2, NULL, 0, 'Place the chopped Southwest salad mix, Pico, corn, black beans, cheese and tortilla strips in a mixing bowl. Add the dressing and gently toss until thoroughly coated.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEFDAADC7DF1FC409E0D3BD22343DD86', X'10E9ECB03B90434CA691C3A6632D8F81', X'10E9ECB03B90434CA691C3A6632D8F81', 5, NULL, NULL, 2, NULL, 0, 'Place the mixed salad on a large platter. Remove the tenderloins from the grill and allow to rest for 5 minutes. Slice into ¼ slices and place on top of the mixed salad.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3BBEFEDBD80D945A6538CC82D8D5EEB', X'10E9ECB03B90434CA691C3A6632D8F81', X'10E9ECB03B90434CA691C3A6632D8F81', 6, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2BAE40FB79D854690E8976D09F3476C', X'51D90F65D537B44D8756454E8625E81B', X'51D90F65D537B44D8756454E8625E81B', 0, NULL, NULL, 2, NULL, 0, 'Place a pizza stone on lowest rack in oven and preheat oven to 475°F. Alternatively use an inverted large heavy baking sheet.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D79C6A7BBCA23C4A83FEF7D110FFBB31', X'51D90F65D537B44D8756454E8625E81B', X'51D90F65D537B44D8756454E8625E81B', 1, NULL, NULL, 2, NULL, 0, 'Lightly dust a pizza peel with flour. Working quickly, dust pizza dough with flour and stretch gently into a rustic oval shape. Prick dough all over with a fork and transfer dough to the pizza stone or baking sheet in the oven. Bake until just cooked through, approximately 15 minutes, checking periodically for bubbles in the dough. Use a fork to break any large bubbles as the dough bakes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B96CDBB14AAD04408066571BC26CE74C', X'51D90F65D537B44D8756454E8625E81B', X'51D90F65D537B44D8756454E8625E81B', 2, NULL, NULL, 2, NULL, 0, 'Let pizza flatbread cool slightly, then top with meatballs, mozzarella, tomatoes, and roasted red peppers. Drizzle generously with basil pesto and serve.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'176CA941544653479AA1A678687A391B', X'51D90F65D537B44D8756454E8625E81B', X'51D90F65D537B44D8756454E8625E81B', 3, NULL, NULL, 2, NULL, 0, 'For the basil pesto, combine the garlic, basil, and pine nuts in a food processor and process until finely chopped. With the motor running, drizzle in the oil. Transfer the mixture to a small bowl and stir in Parmesan cheese. Season with Kosher salt and freshly ground pepper. If making ahead of time, top pesto with a thin layer of extra virgin olive oil to prevent browning and refrigerate. Bring to room temperature and stir before serving. Notes: You can find premade pizza dough at many grocery stores, but another fantastic option to grab one from your local pizzeria! Pizza dough straight from the refrigerator is difficult to stretch. Let dough rest at room temperature for at least 10 minutes before stretching. They are usually the same price as the ones in the supermarket and taste way better. You can also grill your pizza dough! Preheat grill to medium-high heat and oil grill grates. Carefully lay dough on grill and let cook about 2 to 4 minutes, until grill marks appear and the dough easily releases from the grill. Use tongs to flip dough over and cook on other side another 2-3 minutes, until dough is completely cooked through. Remove pizza from grill.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D89F0B3BFBA6E2448C78A19110599671', X'8E1E93552DE07946BEA93EB54D5E33B8', X'8E1E93552DE07946BEA93EB54D5E33B8', 0, NULL, NULL, 2, NULL, 0, 'Slice tomatoes into wedges (for a medium-sized tomato, slice into 6 wedges).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E7BC7F7A3DCD94796C7E28EB406C992', X'8E1E93552DE07946BEA93EB54D5E33B8', X'8E1E93552DE07946BEA93EB54D5E33B8', 1, NULL, NULL, 2, NULL, 0, 'Assemble each skewer with a meatball and piece of mozzarella in-between two tomato wedges. Serve with Basil Pesto.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'090B44AD15F1854AB1CA565337BBC728', X'8E1E93552DE07946BEA93EB54D5E33B8', X'8E1E93552DE07946BEA93EB54D5E33B8', 2, NULL, NULL, 2, NULL, 0, 'For the Basil Pesto, combine the garlic, basil, and pine nuts in a food processor and process until finely chopped. With the motor running, drizzle in the oil. Transfer the mixture to a small bowl and stir in Parmesan cheese. Season with Kosher salt and freshly ground pepper. If making ahead of time, top pesto with a thin layer of extra virgin olive oil to prevent browning and refrigerate. Bring to room temperature and stir before serving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FB210E37D83AF4B92F419586DCDC6F2', X'E2E6E3ECC5D2DA4585698C2C2825E00E', X'E2E6E3ECC5D2DA4585698C2C2825E00E', 0, NULL, NULL, 2, NULL, 0, 'Place a pizza stone on lowest rack in oven and preheat oven to 475°F. Alternatively use an inverted large heavy baking sheet.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03D49D4DF416AA44BFFA30397E8AF53F', X'E2E6E3ECC5D2DA4585698C2C2825E00E', X'E2E6E3ECC5D2DA4585698C2C2825E00E', 1, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a large skillet over medium heat. Add spinach, season with Kosher salt, and cook until wilted.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A8DB5C4318D194A92AA88A7D7E3BA04', X'E2E6E3ECC5D2DA4585698C2C2825E00E', X'E2E6E3ECC5D2DA4585698C2C2825E00E', 2, NULL, NULL, 2, NULL, 0, 'Lightly dust a pizza peel with flour. Working quickly, dust pizza dough with flour and stretch gently into a thin circle shape. Spread pizza sauce in a thin, even layer across dough. Scatter spinach, meatball halves, and ricotta on pizza. Sprinkle with mozzarella cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27599276B087954CAEB43FCB3A756709', X'E2E6E3ECC5D2DA4585698C2C2825E00E', X'E2E6E3ECC5D2DA4585698C2C2825E00E', 3, NULL, NULL, 2, NULL, 0, 'Transfer pizza to the pizza stone or baking sheet in oven and bake pizza in oven until dough is just cooked through, approximately 15 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50B07710370A334696D6AB0D59239423', X'E2E6E3ECC5D2DA4585698C2C2825E00E', X'E2E6E3ECC5D2DA4585698C2C2825E00E', 4, NULL, NULL, 2, NULL, 0, 'Remove from oven, let cool for 2-3 minutes, then slice and serve! Notes: You can find premade pizza dough at many grocery stores, but another fantastic option to grab one from your local pizzeria! Pizza dough straight from the refrigerator is difficult to stretch. Let dough rest at room temperature for at least 10 minutes before stretching. They are usually the same price as the ones in the supermarket and taste way better.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08D2CB09B2AB3E4D89CB4C38E3648F06', X'8679409DD5685C49888E213A602D99DA', X'8679409DD5685C49888E213A602D99DA', 0, NULL, NULL, 2, NULL, 0, 'In a skillet, heat the butter and 12 sage leaves over medium heat and season generously with Kosher salt. Stir the butter constantly, especially as it begins to foam and sizzle. Continually watch butter as it cooks, removing from heat once the butter turns golden brown and smells nutty. Immediately transfer brown sage butter to a bowl to stop the cooking process. Let cool until its cool to the touch.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CCD36F06297064FA49CC96E54EA3E0A', X'8679409DD5685C49888E213A602D99DA', X'8679409DD5685C49888E213A602D99DA', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F. Remove turkey from packaging and follow the listed prep directions on back of package. Pat turkey dry and place on the rack of your roasting pan. Stuff the cavity of your turkey with the onion, remaining 4 sage leaves, and season generously with Kosher salt and freshly ground black pepper. Drizzle and smear half of the browned butter all over the surface of the turkey and under the skin. Season turkey with freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA013D3BCCB3704682F167469B6FDDC6', X'8679409DD5685C49888E213A602D99DA', X'8679409DD5685C49888E213A602D99DA', 2, NULL, NULL, 2, NULL, 0, 'Cook turkey according to package directions, until the internal temperature reaches 165°F at the thickest part of the turkey. Approximately every 45 minutes, baste the turkey. To baste, remove turkey from oven and gently tilt the pan to scoop up the pan liquids and drizzle them on top of the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85FC8E4F6E05EB45B59966CCFD68C623', X'8679409DD5685C49888E213A602D99DA', X'8679409DD5685C49888E213A602D99DA', 3, NULL, NULL, 2, NULL, 0, 'Remove turkey from oven, cover with aluminum foil, and let rest for 20-30 minutes before slicing and serving. Drizzle turkey and roasted root vegetables with reserved sage brown butter.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A57623E613F50948BF0EB43A3CA3BC99', X'8679409DD5685C49888E213A602D99DA', X'8679409DD5685C49888E213A602D99DA', 4, NULL, NULL, 2, NULL, 0, 'For the Roasted Root Vegetables, place vegetables on a large baking sheet in a single layer (or divide among multiple baking sheets as needed). Toss with olive oil and season generously with Kosher salt. Roast in oven during the last 45 minutes of turkey cooking. Drizzle with sage brown butter.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F6BB21A860D2B439F868386F0179FD6', X'F699F5500272B84A91203FA52845D077', X'F699F5500272B84A91203FA52845D077', 0, NULL, NULL, 2, NULL, 0, 'Bring Turkey Injection to boil. Let cool and then inject bird. Cover bird with olive oil. Use favorite dry rub (we recommend Greek seasoning). Cover exterior of bird. Put an ice pack on the breast side of the turkey to make it cooler so that it cooks a little slower and doesnt dry out');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF55BECB09C2EB47B9A51AC6C695F669', X'F699F5500272B84A91203FA52845D077', X'F699F5500272B84A91203FA52845D077', 1, NULL, NULL, 2, NULL, 0, 'Bring smoker to 250-320°F. Put bird on. Remove when bird reaches 165°F in breast and 170°F in thigh. Let rest 30 minutes and enjoy!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1455A5FCC5085048B6F43FF1B3BB8628', X'66EE9B427D576044A80E54794BCB58D7', X'66EE9B427D576044A80E54794BCB58D7', 0, NULL, NULL, 2, NULL, 0, 'In warm water add salt and sugar, stir and allow to fully dissolve. Add remaining brine ingredients and finally ice to fully cool. Once cold, place turkey in brine and fully submerge. Store in refrigerator for 12-24 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2B6B00D69C9D04A92917D625685719D', X'66EE9B427D576044A80E54794BCB58D7', X'66EE9B427D576044A80E54794BCB58D7', 1, NULL, NULL, 2, NULL, 0, 'When turkey has finished brining, preheat oven to 350°F. Remove turkey from the brine, rinse, and pat dry. Place in roasting pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE92357720A2C04ABD47FC7235173AD7', X'66EE9B427D576044A80E54794BCB58D7', X'66EE9B427D576044A80E54794BCB58D7', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix butter with Herbs de Provence, salt and pepper, then rub under skin. Liberally season cavity with salt and pepper. I would add some aromatics to the cavity (onion, lemon, hearty herb like thyme).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09C4D4306CEA2844A1B0FD312F6F8206', X'66EE9B427D576044A80E54794BCB58D7', X'66EE9B427D576044A80E54794BCB58D7', 3, NULL, NULL, 2, NULL, 0, 'Bake at until golden brown and internal temperature is 165°F in the thickest area of the breast. Rotate turkey after roasting for approximately 1 hour and baste with juices in pan. Cover loosely with foil if turkey browns too quickly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA9EAABE24A0A546AACC806FD1CE5D58', X'66EE9B427D576044A80E54794BCB58D7', X'66EE9B427D576044A80E54794BCB58D7', 4, NULL, NULL, 2, NULL, 0, 'Allow the turkey to rest covered for 15-20 minutes before carving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DFC2013B39DA54CBF29005B8C73DF5D', X'FDAD9E768F9A024384FF53A5F430F5C7', X'FDAD9E768F9A024384FF53A5F430F5C7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Melt 2 tablespoons of butter and spread all over the outside of the turkey breast. Place on rack inside roasting pan and cook according to package directions.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE19AB5AFC7C304C992EE8727013904B', X'FDAD9E768F9A024384FF53A5F430F5C7', X'FDAD9E768F9A024384FF53A5F430F5C7', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, combine apple juice, maple syrup, and bourbon in a small saucepan. Season with Kosher salt and bring to a boil over medium-high heat. Lower heat to maintain a simmer, cooking to reduce the mixture by half. Add butter and continue to reduce for about 10 minutes. Glaze will continue to thicken as it cools.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6770AC3938576489EAC312A407397A5', X'FDAD9E768F9A024384FF53A5F430F5C7', X'FDAD9E768F9A024384FF53A5F430F5C7', 2, NULL, NULL, 2, NULL, 0, 'After 1 hour of cooking turkey breast, and in twenty minute intervals following this, brush turkey breast with the maple bourbon glaze. Remove turkey breast from oven once it reaches 160°F. Cover with aluminum foil and let rest for 10 minutes (temperature will continue to rise as it rests). Slice turkey and serve with extra glaze and roasted root vegetables.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9DD1F7FC91B3F49891C78B6C44ED5A7', X'FDAD9E768F9A024384FF53A5F430F5C7', X'FDAD9E768F9A024384FF53A5F430F5C7', 3, NULL, NULL, 2, NULL, 0, 'For the roasted root vegetables, place vegetables on a large baking sheet. Toss with olive oil and season generously with Kosher salt. Roast in oven during the last 45 minutes of turkey cooking. Drizzle with extra glaze, if desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88AAA95ABA45CD4B879C12E3EB8225C1', X'35D6882D15C12444941F005EA11AADDE', X'35D6882D15C12444941F005EA11AADDE', 0, NULL, NULL, 2, NULL, 0, 'Set your smoker to 265ºF.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E195DF198DF2F94BB5F856FF442482D0', X'35D6882D15C12444941F005EA11AADDE', X'35D6882D15C12444941F005EA11AADDE', 1, NULL, NULL, 2, NULL, 0, 'Rinse the turkey breast under cold water and pat dry.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAE41E5F4932644687BD7305CF6DEF31', X'35D6882D15C12444941F005EA11AADDE', X'35D6882D15C12444941F005EA11AADDE', 2, NULL, NULL, 2, NULL, 0, 'Mix together the salt and pepper and season the turkey breast.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB84F7D7E1AD0A439FFEDB91530A165E', X'35D6882D15C12444941F005EA11AADDE', X'35D6882D15C12444941F005EA11AADDE', 3, NULL, NULL, 2, NULL, 0, 'Release the skin on both breasts and place sage leaves under the skin.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'367534DBE519C14EA748F5200AD58353', X'35D6882D15C12444941F005EA11AADDE', X'35D6882D15C12444941F005EA11AADDE', 4, NULL, NULL, 2, NULL, 0, 'Make sure you have a water bath in your smoker to prevent the turkey from becoming dry. Smoke the turkey until golden brown - around 2.5 hours and a temperature reading of ~130ºF.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB260077259D674DBE9A3F6195B13A5A', X'35D6882D15C12444941F005EA11AADDE', X'35D6882D15C12444941F005EA11AADDE', 5, NULL, NULL, 2, NULL, 0, 'At this point wrap your turkey in a double layer of foil and pour the melted butter over the breasts. Put back in the smoker until your meat thermometer reads 160ºF. This should take about another hour of cooking. Let it rest.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF57074677E2E745A6D14F9B351FE918', X'35D6882D15C12444941F005EA11AADDE', X'35D6882D15C12444941F005EA11AADDE', 6, NULL, NULL, 2, NULL, 0, 'For the cranberry relish, heat olive oil in a pan over a medium/high heat. Add diced red onion and pine nuts and cook until golden brown. Add chopped cranberries and celery and cook for another minute or two. Add smoked paprika, balsamic, orange juice and orange zest. Cook until liquids have reduced by half.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11445BE63CAC8E45AB6A684E318787A9', X'35D6882D15C12444941F005EA11AADDE', X'35D6882D15C12444941F005EA11AADDE', 7, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in chopped parsley, jalapeño and season with salt and pepper. Can be served hot or cold.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56E4366356A4B74C880494047024E9AF', X'709F510911A57847AC4CA413ABABAFFC', X'709F510911A57847AC4CA413ABABAFFC', 0, NULL, NULL, 2, NULL, 0, 'In warm water add salt and sugar, stir and allow to fully dissolve?. Add remaining brine ingredients and finally ice to fully cool?. Once cold, place turkey in brine and fully submerge?. Store in refrigerator for 12-24 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7788BF719DD66849BE293834A22E3622', X'709F510911A57847AC4CA413ABABAFFC', X'709F510911A57847AC4CA413ABABAFFC', 1, NULL, NULL, 2, NULL, 0, 'When turkey has finished brining, preheat oven to 350°F. Remove turkey from the brine, rinse, and pat dry. Place in roasting pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD2B5CFEF2870B4B845123A0E59FC6B5', X'709F510911A57847AC4CA413ABABAFFC', X'709F510911A57847AC4CA413ABABAFFC', 2, NULL, NULL, 2, NULL, 0, 'In a bowl mix fennel, garlic, leeks, apples, thyme, orange with 1 tablespoon of olive oil, and generous amount of salt and pepper. Stuff seasoned aromatics into turkey cavity.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1460196B1858A7458C5EEA0C9E3EAD5D', X'709F510911A57847AC4CA413ABABAFFC', X'709F510911A57847AC4CA413ABABAFFC', 3, NULL, NULL, 2, NULL, 0, 'Brush turkey with remaining 2 tablespoons of olive oil, then season with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC3331A0A7478E4DA1A9231E5576D068', X'709F510911A57847AC4CA413ABABAFFC', X'709F510911A57847AC4CA413ABABAFFC', 4, NULL, NULL, 2, NULL, 0, 'Add wine to the bottom of roasting pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEB684687F7E864D80D28165BAC86B87', X'709F510911A57847AC4CA413ABABAFFC', X'709F510911A57847AC4CA413ABABAFFC', 5, NULL, NULL, 2, NULL, 0, 'Bake until golden brown and internal temperature is 165°F in the thickest area of the breast. Rotate turkey after roasting for approximately 1 hour and baste with juices in pan. Cover loosely with foil if turkey browns too quickly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B5B4480C30DF7439DCED16C749052F4', X'709F510911A57847AC4CA413ABABAFFC', X'709F510911A57847AC4CA413ABABAFFC', 6, NULL, NULL, 2, NULL, 0, 'Allow the turkey to rest covered for 15-20 minutes before carving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4A692F23F7A71439A261AE5559B25F6', X'C6EB53996DB8AE44920625FA89178BC8', X'C6EB53996DB8AE44920625FA89178BC8', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375°F. Start by roasting the garlic first. Slice the tops off the garlic heads and drizzle them with olive oil. Wrap the heads in foil and stick them in the oven for 40 minutes or so while you make everything else!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26DB39A0E947DA47A39B847055937230', X'C6EB53996DB8AE44920625FA89178BC8', X'C6EB53996DB8AE44920625FA89178BC8', 1, NULL, NULL, 2, NULL, 0, 'Heat a skillet over medium heat and add the olive oil. Stir in the onions and garlic, cooking until softened and translucent, about 5 minutes. Stir in the fresh parsley. Remove from the heat and let cool slightly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BE17F88E2E64149ABC84BC348D0A737', X'C6EB53996DB8AE44920625FA89178BC8', X'C6EB53996DB8AE44920625FA89178BC8', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the ground turkey, onion, garlic, bread crumbs, milk, egg, ketchup, Worcestershire sauce, salt, basil, parsley, garlic and pepper. Use your hands to bring the mixture together, until just combined and the ingredients are evenly distributed. Form the mixture into mini meatloaves about 1 inch in thickness and 2 inches in length.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD60462CB742A140979D55B2051F7C94', X'C6EB53996DB8AE44920625FA89178BC8', X'C6EB53996DB8AE44920625FA89178BC8', 3, NULL, NULL, 2, NULL, 0, 'Heat the same skillet back up over medium heat and add another tablespoon of olive oil. Add the meatloaves in the skillet and brown on both sides, about 3 to 4 minutes per side.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C222E440B60B684AA4C95A8C8195A765', X'C6EB53996DB8AE44920625FA89178BC8', X'C6EB53996DB8AE44920625FA89178BC8', 4, NULL, NULL, 2, NULL, 0, 'Stir together the ketchup, brown sugar and vinegar in a bowl. Spoon 1 to 2 tablespoons on top of each meatloaf. Place the skillet in the oven and bake for 20 to 25 minutes, or until the center of the meatloaves reach 165°F. When finished, sprinkle with fresh parsley.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'579166FCC3A2E0469DBCE47DD094CDBC', X'C6EB53996DB8AE44920625FA89178BC8', X'C6EB53996DB8AE44920625FA89178BC8', 5, NULL, NULL, 2, NULL, 0, 'While the meatloaves are in the oven, make your cauliflower mash. Cut the head of cauliflower into florets. Place them in a large pot or saucepan and cover them with water, just about an inch past the top of the cauliflower. Bring the mixture to a boil and cook until tender, about 10 minutes or so. Drain the cauliflower, reserving about 1/4 cup of the liquid.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BC1ADC1C35EDC4B90709B5B6F9464D6', X'C6EB53996DB8AE44920625FA89178BC8', X'C6EB53996DB8AE44920625FA89178BC8', 6, NULL, NULL, 2, NULL, 0, 'Add the cauliflower to a food processor and blend until pureed. Stream in the cooking liquid and the melted butter, along with a pinch of salt and pepper. By this time, the garlic cloves should be roasted. Squeeze them from the paper into the cauliflower puree. Puree again to combine. Taste and season with more salt and pepper if needed.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'061B1FBC03FAB64F9A5018A5B12FCC47', X'C6EB53996DB8AE44920625FA89178BC8', X'C6EB53996DB8AE44920625FA89178BC8', 7, NULL, NULL, 2, NULL, 0, 'Serve the meatloaves and cauliflower mash with a veggie of your choice! We love a green bean or brussel sprouts. Enjoy!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'214417D3F1115B42BA3FE6260DDA1978', X'8905326588C48444AFD9885042A0D147', X'8905326588C48444AFD9885042A0D147', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A25D8D478B33CB42A7846826736CB81D', X'8905326588C48444AFD9885042A0D147', X'8905326588C48444AFD9885042A0D147', 1, NULL, NULL, 2, NULL, 0, 'Heat a skillet over medium heat and add the olive oil. Add in the onions, peppers and garlic with a pinch of salt and pepper. Cook, stirring often, until the onions soften, about 5 minutes. Stir in the spinach until it wilts. Turn off the heat and let the mixture cool slightly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D1A1EF0FBCC144DB14E1FAE91958013', X'8905326588C48444AFD9885042A0D147', X'8905326588C48444AFD9885042A0D147', 2, NULL, NULL, 2, NULL, 0, 'Carefully slice the turkey tenderloins lengthwise. We like to slice it twice to open it up, so we have a lot of space to work with. I start at the bottom of the tenderloin (about 1/4 inch from the bottom) and slice it lengthwise, then continue to cut again and open up the turkey. You want to have a flat surface to work on. Once the tenderloins are sliced open, I take a meat tenderizer and pound them out to make them one even layer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A007DB33B70DB6469EE41C13033482A6', X'8905326588C48444AFD9885042A0D147', X'8905326588C48444AFD9885042A0D147', 3, NULL, NULL, 2, NULL, 0, 'Add half of the onion/pepper mixture to the top of each tenderloin leaving an inch or so around the edges so you can roll it up. Sprinkle each with half of the cheddar cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F06EF7F0683FE941AA891008A9A3CB99', X'8905326588C48444AFD9885042A0D147', X'8905326588C48444AFD9885042A0D147', 4, NULL, NULL, 2, NULL, 0, 'Tightly roll up the tenderloin and tie it together with kitchen twine if needed.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ADA3C1A46DBD94B95701089AF09C68F', X'8905326588C48444AFD9885042A0D147', X'8905326588C48444AFD9885042A0D147', 5, NULL, NULL, 2, NULL, 0, 'Place the tenderloins on the baking sheet and roast for 20 to 25 minutes, or until the inside is cooked and reads about 160°F. Let the turkey rest for 10 minutes before slicing.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D89C4AA14002CF458D90C42723D981E9', X'8905326588C48444AFD9885042A0D147', X'8905326588C48444AFD9885042A0D147', 6, NULL, NULL, 2, NULL, 0, 'Serve with the lime black bean salad.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE3593E9FCC6DC4FB61EC091A2C0D976', X'51D088064CA7DE4B8307EB8BA4F786E8', X'51D088064CA7DE4B8307EB8BA4F786E8', 0, NULL, NULL, 2, NULL, 0, 'Remove seasoned turkey breast cutlets from skillet kit and slice into long thin strips. Cut open the sauté sauce packet. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF890707CC346047A1DBD2F616C26007', X'51D088064CA7DE4B8307EB8BA4F786E8', X'51D088064CA7DE4B8307EB8BA4F786E8', 1, NULL, NULL, 2, NULL, 0, 'Heat 1 tablespoon olive oil in a large nonstick skillet over medium-high heat. Add bell peppers, onions and salt. Cook, stirring occasionally, for 8 minutes until the vegetables have softened.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0881BAA9E2590247AEE5398BF45C8B35', X'51D088064CA7DE4B8307EB8BA4F786E8', X'51D088064CA7DE4B8307EB8BA4F786E8', 2, NULL, NULL, 2, NULL, 0, 'Transfer cooked peppers and onions to a large bowl, cover with aluminum foil and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'194C74C536F8C040AE69112E4D6CEA52', X'51D088064CA7DE4B8307EB8BA4F786E8', X'51D088064CA7DE4B8307EB8BA4F786E8', 3, NULL, NULL, 2, NULL, 0, 'In the same skillet, add remaining 1 tablespoon olive oil. Add sliced turkey strips and cook for 8 minutes, until turkey is cooked through.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B56899C851C3641A78DAFCBD466E7DA', X'51D088064CA7DE4B8307EB8BA4F786E8', X'51D088064CA7DE4B8307EB8BA4F786E8', 4, NULL, NULL, 2, NULL, 0, 'Add the softened peppers and onions to the skillet, pour in the sauté sauce and toss to coat. Cook everything for 1-2 more minutes until everything has warmed through and the sauce is bubbling.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6780C91C229BC142A52333575C48B129', X'51D088064CA7DE4B8307EB8BA4F786E8', X'51D088064CA7DE4B8307EB8BA4F786E8', 5, NULL, NULL, 2, NULL, 0, 'Serve immediately in warm corn tortillas for tacos, over rice for burrito bowls or in lettuce for lettuce cups.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6BDD3DDB0841841A867D0F3B1B4EA71', X'0152191D951DB14EB480EB7DA6847035', X'0152191D951DB14EB480EB7DA6847035', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a large nonstick skillet over medium-high heat. Add onions and sauté for 5 minutes until softened.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9FCDBBA08ECA44C94222B3DD5F81792', X'0152191D951DB14EB480EB7DA6847035', X'0152191D951DB14EB480EB7DA6847035', 1, NULL, NULL, 2, NULL, 0, 'Add ground turkey and 1 teaspoon salt. Mix together and break up the meat using a spatula. Cook for 8-10 minutes, until the meat has browned and fully cooked through.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2425E6E0590BDC4D8CFC989654EC016A', X'0152191D951DB14EB480EB7DA6847035', X'0152191D951DB14EB480EB7DA6847035', 2, NULL, NULL, 2, NULL, 0, 'Transfer cooked ground turkey and onions to a large slow cooker. Add chicken broth, diced tomatoes, black beans, pinto beans, corn, the remaining 1 tablespoon salt, chili powder, ground cumin, smoked paprika and dried oregano.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FB79CC328A3B943AEF4A45BF0370896', X'0152191D951DB14EB480EB7DA6847035', X'0152191D951DB14EB480EB7DA6847035', 3, NULL, NULL, 2, NULL, 0, 'Mix together until fully combined. Cover and cook on low for 6-8 hours. The longer it cooks, the thicker and more concentrated the flavor of the broth will be.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6A2E5D4942700428D87B22CC713048C', X'0152191D951DB14EB480EB7DA6847035', X'0152191D951DB14EB480EB7DA6847035', 4, NULL, NULL, 2, NULL, 0, 'Mix in lime juice, cilantro (if desired) and cream cheese (if you want the soup a little creamy).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FCCE0421D2CFB4A92CD54C169085C3F', X'0152191D951DB14EB480EB7DA6847035', X'0152191D951DB14EB480EB7DA6847035', 5, NULL, NULL, 2, NULL, 0, 'Serve and top with tortilla strips and shredded cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD02445C194E33409D6A9722834D1003', X'F7938F7B95A835478A70EA613BBD65FF', X'F7938F7B95A835478A70EA613BBD65FF', 0, NULL, NULL, 2, NULL, 0, 'Heat olive oil in large sauce pan, with deep sides, to medium high heat. When hot, add turkey cutlets. Cook for 3-4 minutes on each side. Remove from pan and let rest for 5 minutes on cutting board.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5EE0EF5DD242F4BAB48CC2F896BE6E1', X'F7938F7B95A835478A70EA613BBD65FF', X'F7938F7B95A835478A70EA613BBD65FF', 1, NULL, NULL, 2, NULL, 0, 'Slice turkey into thin strips, and cut in half. Add back to skillet, along with skillet sauce packet, fire roasted tomatoes, and pasta. Stir to combine, then add box of chicken broth and black pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBC93B3D46EF5943957433E2183221B5', X'F7938F7B95A835478A70EA613BBD65FF', X'F7938F7B95A835478A70EA613BBD65FF', 2, NULL, NULL, 2, NULL, 0, 'Cover and cook for 8-10 minutes, until pasta is al dente. Remove from heat, and stir in spinach, vinegar, and cheese. Let sit for 5 minutes so spinach wilts. Taste, and add salt as needed. This will depend on the amount of sodium in your broth. Scoop into bowls and finish with extra cheese and fresh basil. The fresh basil adds so much freshness, don''t leave it out!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE2A664906B01E4FA66F98127E69F79A', X'394F543215017C4EA315030E4A870247', X'394F543215017C4EA315030E4A870247', 0, NULL, NULL, 2, NULL, 0, 'To prepare the seasoning, add softened butter, chipotle peppers, chopped rosemary leaves, salt, black pepper, oregano, orange zest, lime zest, and minced garlic in a food processor. Pulse until mixture comes together and resembles a paste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CF7E427F2067D49B4EF8B6DD58CB412', X'394F543215017C4EA315030E4A870247', X'394F543215017C4EA315030E4A870247', 1, NULL, NULL, 2, NULL, 0, 'To prepare the turkey, preheat oven to 450°F. Remove thawed turkey from the packaging and take out the turkey neck and bag of giblets stuffed inside. Discard or keep to use in a different recipe. Pat turkey dry with a handful of paper towels or a large kitchen towel until completely dry. Place turkey on a baking sheet and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'908063C96781234882C924367F71AF83', X'394F543215017C4EA315030E4A870247', X'394F543215017C4EA315030E4A870247', 2, NULL, NULL, 2, NULL, 0, 'Fill the bottom of the roasting pan with the juice of the zested orange, the juiced orange quarters and 2 cups of turkey stock.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECB66BC59BC6B74283C6D18256428FDE', X'394F543215017C4EA315030E4A870247', X'394F543215017C4EA315030E4A870247', 3, NULL, NULL, 2, NULL, 0, 'Fill the turkey cavity with the onion halves, the sprig of rosemary and the orange quarters. Tie the turkey legs together with kitchen twine to help the turkey cook evenly and for a beautiful presentation.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64EAC97DFD475940A5D62DCF3A638A11', X'394F543215017C4EA315030E4A870247', X'394F543215017C4EA315030E4A870247', 4, NULL, NULL, 2, NULL, 0, 'Carefully push your fingers under the skin of the turkey breast all the way to the back of the turkey to help separate the skin. Be careful not to tear the skin.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E40781ACF77ACC49BAB538A9F8EC2268', X'394F543215017C4EA315030E4A870247', X'394F543215017C4EA315030E4A870247', 5, NULL, NULL, 2, NULL, 0, 'Spread half of the compound butter under the skin and the remaining half all over the outside of the turkey. Secure the edges of the skin with some toothpicks so it doesnt ride up completely while the bird is roasting.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A340DA6CAB673D4D9E62F1AA6196B166', X'394F543215017C4EA315030E4A870247', X'394F543215017C4EA315030E4A870247', 6, NULL, NULL, 2, NULL, 0, 'To roast the turkey, place turkey breast side up in the roasting pan and insert an in-oven meat thermometer deep inside the breast of the bird. Make sure its not touching a bone. Set the in-oven meat thermometer temperature to 165°F. Place the turkey in the preheated oven and roast for 25 minutes to crisp the skin.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'399443238EC33641AEAD9635F2C1094C', X'394F543215017C4EA315030E4A870247', X'394F543215017C4EA315030E4A870247', 7, NULL, NULL, 2, NULL, 0, 'Remove turkey from oven, lower the heat to 350°F, loosely cover the breast with a tent of aluminum foil and return to the oven. Bake for 2 1/2 to 3 hours, or until the temperature of the meat reaches 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33B0FFCFFE8D574E858D68B1EC236831', X'394F543215017C4EA315030E4A870247', X'394F543215017C4EA315030E4A870247', 8, NULL, NULL, 2, NULL, 0, 'Carefully remove from oven and check the temperature of the bird in different places to make sure everything is at least 170°F. If there are spots that are under, leave the thermometer in that spot and return it to the oven to roast until it reaches 170°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'179B0DBAB86D1B4AB501800341E55FAC', X'394F543215017C4EA315030E4A870247', X'394F543215017C4EA315030E4A870247', 9, NULL, NULL, 2, NULL, 0, 'Carefully transfer the turkey to large cutting board and rest for 30 minutes. Slice and serve.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'789A1CE8E98486498479F03184074C18', X'771234F7D8AC6549BC2C8F0E2D25E40B', X'771234F7D8AC6549BC2C8F0E2D25E40B', 0, NULL, NULL, 2, NULL, 0, 'Place oven rack in the bottom third of the oven. Heat oven to 450°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1E31B832691F541B28E06E09C005D1D', X'771234F7D8AC6549BC2C8F0E2D25E40B', X'771234F7D8AC6549BC2C8F0E2D25E40B', 1, NULL, NULL, 2, NULL, 0, 'Pat the skin of the turkey breast dry with paper towels and on roasting rack. Set aside. In a small bowl, combine garlic, butter, mustard, herbs, and salt. Mix until well combined, forming a paste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'643F1C6E6225254B9EBD0B244DC60E80', X'771234F7D8AC6549BC2C8F0E2D25E40B', X'771234F7D8AC6549BC2C8F0E2D25E40B', 2, NULL, NULL, 2, NULL, 0, 'Carefully loosen the skin from the turkey breast and rub the herb mixture on the meat under the skin. Pull the skin back to cover the turkey breast. Rub any remaining herb mixture over the skin');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FB104869DC0BD43A0921E6AEEAA496B', X'771234F7D8AC6549BC2C8F0E2D25E40B', X'771234F7D8AC6549BC2C8F0E2D25E40B', 3, NULL, NULL, 2, NULL, 0, 'Place the turkey breast in the oven and lower the oven heat to 350°F . Roast the turkey breast for 1 hour, turning halfway. If the breast begins to brown too quickly, form a tent with foil. Check the internal temperature of the turkey breast at the 1-hour point, followed by every 10 minutes until it reaches an internal temperature of 165°F at the thickest part of the breast.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B93A7B9106771840BD9CFA6D906BB007', X'771234F7D8AC6549BC2C8F0E2D25E40B', X'771234F7D8AC6549BC2C8F0E2D25E40B', 4, NULL, NULL, 2, NULL, 0, 'Remove the turkey breast from the oven and let rest for 10 to 15 minutes before carving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD1FDC5038B2DA40B6E42E0E1A4339D3', X'771234F7D8AC6549BC2C8F0E2D25E40B', X'771234F7D8AC6549BC2C8F0E2D25E40B', 5, NULL, NULL, 2, NULL, 0, 'If you have enough pan drippings, make gravy: Remove the rack, and place roasting pan directly over a burner set on high heat. Add 1 tablespoon unbleached all-purpose flour to pan, and whisk for 2 minutes, scraping up the pan drippings and incorporating all of the pan juices into the flour. Add 1/2 cup water, and continue whisking until a smooth gravy forms, about 2 minutes. Transfer to a gravy boat, and serve with turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72DD221417661246A1617C1E930D0D67', X'332594B499785A45BC966346FCAE5799', X'332594B499785A45BC966346FCAE5799', 0, NULL, NULL, 2, NULL, 0, 'Brine the turkey for 6-8 hours in water, 4 tbsp of Cajun seasoning, and 4 tbsp of salt. Make sure that the brine fully covers the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2FE7482F1006C409039549D4F7EE731', X'332594B499785A45BC966346FCAE5799', X'332594B499785A45BC966346FCAE5799', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 °F. Take the innards out of the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBDB98657E68E84586ED66BC76AA5BA8', X'332594B499785A45BC966346FCAE5799', X'332594B499785A45BC966346FCAE5799', 2, NULL, NULL, 2, NULL, 0, 'Take out the turkey and pat it dry with paper towels. Place half of the orange and lemon onto a roasting pan. Place the turkey breast-side up in the roasting pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79672399FDB77948A81848DD766CFCF9', X'332594B499785A45BC966346FCAE5799', X'332594B499785A45BC966346FCAE5799', 3, NULL, NULL, 2, NULL, 0, 'Pour olive oil over the turkey and rub it in. Rub the ghee into the turkey. Make sure to rub the insides too. Sprinkle (heavily) the cajun seasoning over the turkey and rub it in.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CE2C859EFC96048AB3F18B9909690D9', X'332594B499785A45BC966346FCAE5799', X'332594B499785A45BC966346FCAE5799', 4, NULL, NULL, 2, NULL, 0, 'Place the garlic, onion, half of the thyme, half of the orange and half of the lemon into the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8CFC4DC8A563A49A8C504057B1572BF', X'332594B499785A45BC966346FCAE5799', X'332594B499785A45BC966346FCAE5799', 5, NULL, NULL, 2, NULL, 0, 'Make sure the legs are still looped together. Tuck the wings under the bird.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCB377C1D107474FAEEBB1B319C3994B', X'332594B499785A45BC966346FCAE5799', X'332594B499785A45BC966346FCAE5799', 6, NULL, NULL, 2, NULL, 0, 'Cover the turkey with foil and roast it for 20 minutes. Try to let as little steam as possible escape the pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0BEA7CBAFEC674697869B21A14F01F6', X'332594B499785A45BC966346FCAE5799', X'332594B499785A45BC966346FCAE5799', 7, NULL, NULL, 2, NULL, 0, 'Pour the broth into the pan. Add the other half of the thyme to the pan. Roast the bird for 40 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'488B4B37335F004C946FE763464E4BAB', X'332594B499785A45BC966346FCAE5799', X'332594B499785A45BC966346FCAE5799', 8, NULL, NULL, 2, NULL, 0, 'Reduce the oven temperature to 350 degrees. Take off the foil cook for 2 more hours, basting occasionally with pan juices about every 30 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80AC01074F13024B93055ADAA9D91ADF', X'332594B499785A45BC966346FCAE5799', X'332594B499785A45BC966346FCAE5799', 9, NULL, NULL, 2, NULL, 0, 'After the bird is done, put it on a platter and cover it with foil for about 20-30 minutes. Bonus tip: If your turkey starts to look dry when it''s being served, pour some olive oil on it to give it a little life. I had to do this when I hosted my Friendsgiving and it livened it right up!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5048D8B764D644E93BFC575167451E2', X'88362817C581C34591AE5A9BCBA09CF9', X'88362817C581C34591AE5A9BCBA09CF9', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C081DD61B684D541A3E22E54D6D4C2F0', X'88362817C581C34591AE5A9BCBA09CF9', X'88362817C581C34591AE5A9BCBA09CF9', 1, NULL, NULL, 2, NULL, 0, 'Next, prepare vegetables. Cut carrots in half hot dog-style and then again hamburger-style so that you quarter them. Place them on baking sheet.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10B911BD5117A448A8666623824C9BBA', X'88362817C581C34591AE5A9BCBA09CF9', X'88362817C581C34591AE5A9BCBA09CF9', 2, NULL, NULL, 2, NULL, 0, 'Slice a medium-sized onion and add that to the baking sheet. Next, slice both ends off of 2 bulbs of garlic, keeping them in their casing and place those on the baking sheet as well.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03E5C2A1C5E8D94BB2178E0EA3317093', X'88362817C581C34591AE5A9BCBA09CF9', X'88362817C581C34591AE5A9BCBA09CF9', 3, NULL, NULL, 2, NULL, 0, 'Drizzle veggies with olive oil and season with salt and pepper and toss so that everything is coated.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C60D0BC882700245A3ECD24E8C92A59D', X'88362817C581C34591AE5A9BCBA09CF9', X'88362817C581C34591AE5A9BCBA09CF9', 4, NULL, NULL, 2, NULL, 0, 'Roast vegetables at 400°F for 25 minutes. Then, remove and toss. Drizzle on honey and balsamic vinegar and toss again. Make sure to flip garlic heads over.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56BB0996FD28674BBE6E10CCBFF384D7', X'88362817C581C34591AE5A9BCBA09CF9', X'88362817C581C34591AE5A9BCBA09CF9', 5, NULL, NULL, 2, NULL, 0, 'Place back in the oven and roast for another 20 minutes at 400°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39A919F3CB8B5D49B16D1D3C64863D70', X'88362817C581C34591AE5A9BCBA09CF9', X'88362817C581C34591AE5A9BCBA09CF9', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven and let cool for 5-10 minutes. Then, carefully squeeze garlic cloves out of the bulbs and toss with roasted vegetables.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D79CF58D41B614C8A4C2132147C1EF8', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 0, NULL, NULL, 2, NULL, 0, 'First, prepare sous vide water bath by filling a 14 quart pot (or larger) with water. Youll want enough water to cover your turkey when submerged.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C23AAB709BA594C93D21E11E678B733', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 1, NULL, NULL, 2, NULL, 0, 'Set the sous vide to 165°F and bring water to temperature.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D5BAF7D1B9EFB478C31C2BA954B90BF', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 2, NULL, NULL, 2, NULL, 0, 'Next, remove thawed turkey breast from the refrigerator and remove gravy packet. Use paper towel to remove any excess moisture from the turkey breast and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E06EA840989F3E4CB0AFC676813A3027', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 3, NULL, NULL, 2, NULL, 0, 'Place dried thyme, rosemary, oregano, brown sugar, garlic powder, salt, pepper, chili powder, and paprika into a small bowl and mix until combined.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'085BE0D991400446B3867A677A593B80', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 4, NULL, NULL, 2, NULL, 0, 'Next, sprinkle the dry rub over the turkey breast and use your hands to rub it into every part of the turkey until all meat is generously covered.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'280430034D507E4DA92934DA0CC094CE', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 5, NULL, NULL, 2, NULL, 0, 'Let the turkey sit with the dry rub for at least 20 minutes or up to 2 hours (for a more intense flavor, let sit for longer).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D9A051AE246AB4C88C22B72D9D755A7', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 6, NULL, NULL, 2, NULL, 0, 'Next, sear the turkey by placing 3-4 tablespoons of butter into a large cast-iron skillet.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F58C9CFAF1515C44B0DE11B11447D7F0', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 7, NULL, NULL, 2, NULL, 0, 'When butter is fragrant, place turkey skin side down onto the cast-iron skillet. Let cook for 3-4 minutes on each side.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44C76B16DBF120458105CA60B0C57625', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 8, NULL, NULL, 2, NULL, 0, 'Remove from the cast iron skillet and place turkey breast into a large food saver bag. We used an 11-inch food saver roll, and it fit an 8 lb turkey perfectly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24C0D1754F65BD41AFD3263FBAC20B3E', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 9, NULL, NULL, 2, NULL, 0, 'Make an herb bundle with the fresh rosemary, fresh thyme, and fresh sage and then add it to the bag along with white wine.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8518B35EE95A64C8C8A1198F4EC99F5', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 10, NULL, NULL, 2, NULL, 0, 'Use your food saver machine to remove as much air out of the bag as possible and then seal. Double bag your turkey by placing it into another 11-inch bag to prevent tears and leaking. Remove air and seal.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92BE49118D149D46889DA08AA584FE91', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 11, NULL, NULL, 2, NULL, 0, 'Place the turkey into the sous vide water bath and clip it into place so it doesnt move. Make sure the turkey is submerged in water. If its not, we suggest placing a pot on top of it to hold it under water.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE6F045235611C46A671FF926773A4E3', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 12, NULL, NULL, 2, NULL, 0, 'Let the turkey breast cook in the sous vide at 165°F for at least 12 hours or up to 24 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'743CC67D3FCF944DB08FF033BD127EAB', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 13, NULL, NULL, 2, NULL, 0, 'Once your turkey is fully cooked, remove it from the bag and set it aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9624231842DFA04A9D51A6BF21245765', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 14, NULL, NULL, 2, NULL, 0, 'Then, preheat oven to 400°F');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6405107F23274947B2D22090853416ED', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 15, NULL, NULL, 2, NULL, 0, 'Discard herbs from the bag, but pour the excess liquid from the plastic bag through a sieve and into s bowl and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'090011328DAF544187FDB3810D1E740D', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 16, NULL, NULL, 2, NULL, 0, 'Check the internal temperature of the turkey and make sure it is between 163°F and 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BEC376C8DAFA54B9283E8F06ED88781', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 17, NULL, NULL, 2, NULL, 0, 'Then, melt 3-4 tablespoons of butter in a large cast iron skillet.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF74EA870A96CC45A99FD4027B2D1271', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 18, NULL, NULL, 2, NULL, 0, 'When butter is melted and very hot, place the turkey breast into the pan, skin-side down. Sear for 3-4 minutes or until browned, and then repeat on the other side.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'162175C58BF69246B091BC19EA81E832', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 19, NULL, NULL, 2, NULL, 0, 'Turn heat off and pour sauce over the turkey breast.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FE739844E442143B5BA78FAB2C0EE70', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 20, NULL, NULL, 2, NULL, 0, 'Next, place the cast iron skillet and turkey into the oven and roast at 400°F for 20 minutes, basting the turkey with the sauce every 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'115C46390E2028459BD160AD7F766982', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 21, NULL, NULL, 2, NULL, 0, 'Once golden brown, remove turkey from oven and let rest for 10-15 minutes before slicing and serving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B03233C46E2DCB4D8721F56038510743', X'E105A3BED4B9CB41A3BD37B22719756B', X'E105A3BED4B9CB41A3BD37B22719756B', 22, NULL, NULL, 2, NULL, 0, 'Transfer sauce from cast iron skillet and thicken as desired, or serve as-is on top of the sliced turkey breast.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9362B0A5CADDBF49A595853552906BE3', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 0, NULL, NULL, 2, NULL, 0, 'Make sure your oven is clean and free of debris and spills. A dirty oven will produce smoke at the high temperatures required for this recipe.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'449A713C0601504D8F6CD17F00BBC2C0', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 1, NULL, NULL, 2, NULL, 0, 'Place your oven rack at the second to last position. Placing it in the lowest position may risk burning your bird!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C02842ED7AD32A42A81CF8D07002CC43', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 475°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E06DF28A545CF419F6D9335367B7BB1', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 3, NULL, NULL, 2, NULL, 0, 'Remove the giblets and neck from the turkey. If there is a plastic truss holding the legs together, remove it. We will not truss the turkey for this recipe.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4939328F6550F64ABE6B14A6203B66D5', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 4, NULL, NULL, 2, NULL, 0, 'Rinse the turkey inside and out and drain water. Then, pat dry with paper towels.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5E81912568B2149A9729CAB56735A44', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 5, NULL, NULL, 2, NULL, 0, 'Put about one inch of water or broth in the bottom of your roasting pan. This will make sure that the oven does not produce excessive smoke during this high heat roast. Please dont skip this step!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DFF354CE3314A4085688EDF57F993EF', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 6, NULL, NULL, 2, NULL, 0, 'Place turkey on roasting pan. Make sure you are placing it on a rack, not on the bottom of the pan, which should be full of water or broth to prevent smoking.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25A43AE6E766FF4CB5462A9510D3BB68', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 7, NULL, NULL, 2, NULL, 0, 'Rub turkey with avocado oil, then sprinkle with salt, pepper, thyme and garlic powder.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B718BA30EF67554891596A81C19F0111', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 8, NULL, NULL, 2, NULL, 0, 'Make aluminum foil covers for each drumstick to prevent burning.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4689EA8714F1EE43BBCE4C7272818BB9', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 9, NULL, NULL, 2, NULL, 0, 'Roast for the following, turning the bird 180°F about halfway through your cook time. My 14 lb bird took about 1.5 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'168470A40A27CD4DB7815CC26E86AA53', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 10, NULL, NULL, 2, NULL, 0, 'The turkey is done when your instant read thermometer reads 165°F in the thickest part of BOTH the breast and the thigh.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF50FCFA6291E142A7740200C0547FCE', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 11, NULL, NULL, 2, NULL, 0, 'For the glaze, melt the butter in a small saucepan over medium-high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5D317BDF10B2E4485EC6AAEA66FFC0D', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 12, NULL, NULL, 2, NULL, 0, 'Stir in bourbon and maple syrup.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'687E20A15FA122459F00A561F8B6A841', X'12C2A89CCF568541BBB4A939D07E35B1', X'12C2A89CCF568541BBB4A939D07E35B1', 13, NULL, NULL, 2, NULL, 0, 'Once glaze comes to a boil, lower heat to low-medium heat and allow to thicken for about 10 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7627D410E9C19249BCB2367964249D3E', X'A3DD3BE7BBD18246AC2B9FEF923241B3', X'A3DD3BE7BBD18246AC2B9FEF923241B3', 0, NULL, NULL, 2, NULL, 0, 'Preheat outdoor grill or charcoal grill to 350°F and lightly oil the grates.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40DA940A09E09041A34A51D94AA20A01', X'A3DD3BE7BBD18246AC2B9FEF923241B3', X'A3DD3BE7BBD18246AC2B9FEF923241B3', 1, NULL, NULL, 2, NULL, 0, 'To prepare the stuffed tenderloins, add bacon to a sauté pan over medium heat. Cook the bacon until it becomes crispy, reserving the bacon grease. Remove cooked bacon from the pan and set aside until needed.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CB8D781F5AA4146A6309D190C122723', X'A3DD3BE7BBD18246AC2B9FEF923241B3', X'A3DD3BE7BBD18246AC2B9FEF923241B3', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic, onions, roasted red peppers and roasted corn. Cook until the onions are softened and lightly browned. Remove and allow to cool to room temperature. Once cooled, add the bacon and cheddar cheese and mix thoroughly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8EDEC5082089D41A063A972F8300EA7', X'A3DD3BE7BBD18246AC2B9FEF923241B3', X'A3DD3BE7BBD18246AC2B9FEF923241B3', 3, NULL, NULL, 2, NULL, 0, 'While cooling, butterfly the turkey tenderloins then use a meat tenderizer to pound them out to an equal thickness.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D487D61E96CC45478C108B5AA93B0FE6', X'A3DD3BE7BBD18246AC2B9FEF923241B3', X'A3DD3BE7BBD18246AC2B9FEF923241B3', 4, NULL, NULL, 2, NULL, 0, 'Add half the stuffing to each of the tenderloins, leaving an inch or so in order to roll it up. Once rolled, tie with butcher''s twine from end to end.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDA40C4A4A281943BDE2A956A6EEE8D2', X'A3DD3BE7BBD18246AC2B9FEF923241B3', X'A3DD3BE7BBD18246AC2B9FEF923241B3', 5, NULL, NULL, 2, NULL, 0, 'Place the stuffed turkey tenderloin on the grill and cook 10 to 12 ½ minutes then flip.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0123103E399FB04FAAB5F0C08ABE484D', X'A3DD3BE7BBD18246AC2B9FEF923241B3', X'A3DD3BE7BBD18246AC2B9FEF923241B3', 6, NULL, NULL, 2, NULL, 0, 'Continue to cook until they reach a minimum internal temperature of 165°F (approximately 10 to 12 ½ additional minutes). Use a meat thermometer to confirm internal temperature.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F317A7E300304A499C8E7CC141763FCA', X'A3DD3BE7BBD18246AC2B9FEF923241B3', X'A3DD3BE7BBD18246AC2B9FEF923241B3', 7, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from the grill. Allow to rest for 5 minutes then slice into 1 slices.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30B34104672AA641B7A7226DF6D0FCF9', X'A3DD3BE7BBD18246AC2B9FEF923241B3', X'A3DD3BE7BBD18246AC2B9FEF923241B3', 8, NULL, NULL, 2, NULL, 0, 'Place on a platter and serve immediately.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D250DCD66CB9314FB88F14739335587A', X'DD03987871302E41A012177F2B3FAF97', X'DD03987871302E41A012177F2B3FAF97', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F. Place a large baking sheet in the oven while it is preheating.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFA68366BD511643957051C63324FA6F', X'DD03987871302E41A012177F2B3FAF97', X'DD03987871302E41A012177F2B3FAF97', 1, NULL, NULL, 2, NULL, 0, 'Thinly slice 4 garlic cloves.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02187A99B45801439A3B0ECAFEDDB285', X'DD03987871302E41A012177F2B3FAF97', X'DD03987871302E41A012177F2B3FAF97', 2, NULL, NULL, 2, NULL, 0, 'In a spice grinder, puree the other 2 garlic cloves, fennel seed, red pepper flakes and salt.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F0563241DD0AA49B17B2E4D9818BCE1', X'DD03987871302E41A012177F2B3FAF97', X'DD03987871302E41A012177F2B3FAF97', 3, NULL, NULL, 2, NULL, 0, 'Pat tenderloins dry with paper towels.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C603AC7FCCACC4DAF588F4E554A6583', X'DD03987871302E41A012177F2B3FAF97', X'DD03987871302E41A012177F2B3FAF97', 4, NULL, NULL, 2, NULL, 0, 'Make small slits all over the turkey tenderloins and stuff garlic slices inside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E9EF446C825C94C88BD5CD503D152E0', X'DD03987871302E41A012177F2B3FAF97', X'DD03987871302E41A012177F2B3FAF97', 5, NULL, NULL, 2, NULL, 0, 'Rub spice mixture over the tenderloins.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73FC2164EC327046B8554F1291209922', X'DD03987871302E41A012177F2B3FAF97', X'DD03987871302E41A012177F2B3FAF97', 6, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix together rutabaga, carrots, oil, salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'957EE3F0257F594FB9DFD121F8D63431', X'DD03987871302E41A012177F2B3FAF97', X'DD03987871302E41A012177F2B3FAF97', 7, NULL, NULL, 2, NULL, 0, 'Take hot baking sheet out of oven and pour vegetables into a single layer on the baking sheet. Place turkey tenderloins on top.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7AB9DAF488A0C40AD18E9E59D7FCDD8', X'DD03987871302E41A012177F2B3FAF97', X'DD03987871302E41A012177F2B3FAF97', 8, NULL, NULL, 2, NULL, 0, 'Cook until the meat reaches an internal temperature of 165¬8F using a meat thermometer and vegetables are tender.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4079BE368BB78F4AB88B64251FFB5EE2', X'DD03987871302E41A012177F2B3FAF97', X'DD03987871302E41A012177F2B3FAF97', 9, NULL, NULL, 2, NULL, 0, 'Place turkey onto a cutting board and let rest for 10 minutes before slicing.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F12A15B183A0A488DF113E32147E7A5', X'4AD37581B59A84468A481AF7048B5A38', X'4AD37581B59A84468A481AF7048B5A38', 0, NULL, NULL, 2, NULL, 0, 'Place salt, brown sugar, garlic powder and dried rosemary in a small bowl. Work with fingers until incorporated. Place turkey on a wire rack set inside a rimmed baking sheet. Sprinkle and pat brine all over outside and inside of turkey, nudging some into crevices. Chill bird, uncovered, for at least 12 hours and up to 2 days.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F6A60666A6E7847A71DAE9BAE8E2096', X'4AD37581B59A84468A481AF7048B5A38', X'4AD37581B59A84468A481AF7048B5A38', 1, NULL, NULL, 2, NULL, 0, 'Remove turkey from rack and rinse baking dish. Line with 3 layers of aluminum foil and set rack back inside. Place turkey, breast side up, on rack and tuck wings underneath. Allow turkey to sit at room temperature for 2-3 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9FCA7B4B2BCAA4E9BDA9A0BFC03EB49', X'4AD37581B59A84468A481AF7048B5A38', X'4AD37581B59A84468A481AF7048B5A38', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450°F. Loosen skin on turkey breasts and spread 2 tablespoons of butter underneath skin atop both breasts. Smear outside of turkey with another 2 tablespoons of butter.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D8A95FB1E6DE14F8B878DADAA2EF984', X'4AD37581B59A84468A481AF7048B5A38', X'4AD37581B59A84468A481AF7048B5A38', 3, NULL, NULL, 2, NULL, 0, 'Tie legs with kitchen twine and transfer turkey to oven. Pour 1 cup water into baking sheet and roast, rotating pan after 15 minutes, until skin is golden brown, approximately 30 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EADCE6E4ED142848829FDF21A4E91D66', X'4AD37581B59A84468A481AF7048B5A38', X'4AD37581B59A84468A481AF7048B5A38', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat 4 remaining tablespoons of butter, vinegar, honey, Worcestershire sauce, rosemary, garlic, and orange zest over medium heat until bubbling and slightly thickened, about 7 minutes. Reduce heat to low to keep warm.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D416D1724D53540BA4BB93CC3B1B71E', X'4AD37581B59A84468A481AF7048B5A38', X'4AD37581B59A84468A481AF7048B5A38', 5, NULL, NULL, 2, NULL, 0, 'Reduce oven temperature to 300°F and continue to roast turkey, brushing glaze every 30 minutes and adding an additional 1/2 cup of water to the baking sheet to retain liquid. Continue until the thickest part of breast reads 150 degrees on an instant-read thermometer, about 60-90 minutes longer when skin reaches brown, crisp and shiny look.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F81C4527D588AA40B99E1BF12A5E3D77', X'4AD37581B59A84468A481AF7048B5A38', X'4AD37581B59A84468A481AF7048B5A38', 6, NULL, NULL, 2, NULL, 0, 'Transfer turkey to a cutting board and allow to rest 30 minutes before carving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBF857A36B3F794C9E0FE013739BDEDC', X'DC05878D2E73F84C981B0F0B03075578', X'DC05878D2E73F84C981B0F0B03075578', 0, NULL, NULL, 2, NULL, 0, 'Follow package directions of the Parmesan Garlic Meatballs.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50000A3E4AEDB24991D7278557FFF386', X'DC05878D2E73F84C981B0F0B03075578', X'DC05878D2E73F84C981B0F0B03075578', 1, NULL, NULL, 2, NULL, 0, 'While the meatballs are cooking prepare the tomato cream sauce.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA03E8DE67506B4EB4A57FC0A7CB92FC', X'DC05878D2E73F84C981B0F0B03075578', X'DC05878D2E73F84C981B0F0B03075578', 2, NULL, NULL, 2, NULL, 0, 'Place marinara and cream in a small sauce pot. Stir together then bring to a rapid boil. Reduce to a simmer and add in the ¼ cup of shredded parmesan. Keep warm over a low temperature.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F3EF11114CD9D49A1861401859B1C9E', X'DC05878D2E73F84C981B0F0B03075578', X'DC05878D2E73F84C981B0F0B03075578', 3, NULL, NULL, 2, NULL, 0, 'Using a meat thermometer, check to make sure the meatballs reach a minimum internal temperature of 165°F. Once the proper temperature has been reached, remove from oven.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'481EE7B018EB3346B81B0AF202D40B07', X'DC05878D2E73F84C981B0F0B03075578', X'DC05878D2E73F84C981B0F0B03075578', 4, NULL, NULL, 2, NULL, 0, 'On a platter, pour the heated sauce on the bottom. Randomly place the meatballs over the top of the sauce.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEC4F87FED5D2C41BFFB84A54DEEA0ED', X'DC05878D2E73F84C981B0F0B03075578', X'DC05878D2E73F84C981B0F0B03075578', 5, NULL, NULL, 2, NULL, 0, 'Garnish with chopped fresh basil and 2 tablespoons. of shredded parmesan cheese, serve immediately.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD4DBEE3C5D8B54C8B0CF6D32CBCAE2B', X'2DF3AA9FF34BA145A7695E55889A2266', X'2DF3AA9FF34BA145A7695E55889A2266', 0, NULL, NULL, 2, NULL, 0, 'In a large saute pan, heat 1 1/2 tablespoons olive oil over medium heat. Swirl to coat pan, then add in squash. Stir the squash pieces so they are coated with oil and sprinkle with 1 teaspoon salt. Shake the pan so squash spreads out in an even layer and let cook, without stirring, so that they brown a bit on one side (several minutes).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB4F4EB982B69D42B2479AD00A9F3F3B', X'2DF3AA9FF34BA145A7695E55889A2266', X'2DF3AA9FF34BA145A7695E55889A2266', 1, NULL, NULL, 2, NULL, 0, 'Stir and spread the pieces out again and let cook without stirring so more sides brown. Cover, and cook about 6 minutes, until the squash is soft, but holds its shape (This will depend on the size you cut your squash so try one to test). Once done, remove from heat and pour into a bowl.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81CF83F5A18BA3448D0663BADDF6273A', X'2DF3AA9FF34BA145A7695E55889A2266', X'2DF3AA9FF34BA145A7695E55889A2266', 2, NULL, NULL, 2, NULL, 0, 'Heat remaining 1 1/2 tablespoons olive oil of oil over medium-high heat using the same pan. When it''s very hot, place the brussels sprouts cut side down in the oil, sprinkling with 1 teaspoon salt. Turn the heat to medium, and sear on one side until nicely browned, about 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F1CCCACBE49584DB2D669195D158D05', X'2DF3AA9FF34BA145A7695E55889A2266', X'2DF3AA9FF34BA145A7695E55889A2266', 3, NULL, NULL, 2, NULL, 0, 'Turn the brussels sprouts over and cook on the other side until nicely browned and tender, three to five minutes. Some of the leaves can be charred dark brown or black.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'018659AA505A7B4193597A50E08503CD', X'2DF3AA9FF34BA145A7695E55889A2266', X'2DF3AA9FF34BA145A7695E55889A2266', 4, NULL, NULL, 2, NULL, 0, 'Turn heat down to low and add the squash back to the pan, along with the pecan mix. Stir in the glaze and cook for a few minutes, coating all ingredients and warming through.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'282AD368AA614F4BADE6B851B65F8071', X'0DBD0CA37055EA48929B9B48C03399C2', X'0DBD0CA37055EA48929B9B48C03399C2', 0, NULL, NULL, 2, NULL, 0, 'Preheat an 8- to 10-inch nonstick skillet over medium heat for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'006AF9C92426A849810C5AE936CA0401', X'0DBD0CA37055EA48929B9B48C03399C2', X'0DBD0CA37055EA48929B9B48C03399C2', 1, NULL, NULL, 2, NULL, 0, 'Add 2 teaspoons extra virgin olive oil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'132C4D01522E3A45A8EE645A9DB0CFAB', X'0DBD0CA37055EA48929B9B48C03399C2', X'0DBD0CA37055EA48929B9B48C03399C2', 2, NULL, NULL, 2, NULL, 0, 'Add turkey to pan, and cook until browned and crumbly. Ensure that turkey reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45A373171CAD4B4D8E2EFD7B04DECC66', X'0DBD0CA37055EA48929B9B48C03399C2', X'0DBD0CA37055EA48929B9B48C03399C2', 3, NULL, NULL, 2, NULL, 0, 'Stir in simmer sauce, let cook for 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD6CD76EDDB2FA4580E6BC414BA7E097', X'0DBD0CA37055EA48929B9B48C03399C2', X'0DBD0CA37055EA48929B9B48C03399C2', 4, NULL, NULL, 2, NULL, 0, 'Reduce heat to low.');
