INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E0A86B3D5A3E242B4AC02622F8411B2', X'06CBD95DF047B24DBDD5AE37F780DF20', X'06CBD95DF047B24DBDD5AE37F780DF20', 0, NULL, NULL, 2, NULL, 0, 'Preheat an 8-10-inch nonstick skillet over medium heat for 2 - 3 minutes.');
