INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A30421562DB00D49BC2C2E7EEC9E0252', X'06CBD95DF047B24DBDD5AE37F780DF20', X'06CBD95DF047B24DBDD5AE37F780DF20', 5, NULL, NULL, 2, NULL, 0, 'Cook 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FDB18AB7B33BB4EB0566CD6F0FAE790', X'06CBD95DF047B24DBDD5AE37F780DF20', X'06CBD95DF047B24DBDD5AE37F780DF20', 6, NULL, NULL, 2, NULL, 0, 'Warm tortillas and assemble tacos with meat, onions, cilantro, a dash of hot sauce and a squeeze of lime.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75B53A14245BCF439616BE0C2CDEED69', X'6B87DEFDBF868F478016030F37E87D6B', X'6B87DEFDBF868F478016030F37E87D6B', 0, NULL, NULL, 2, NULL, 0, 'Arrange rack in middle of oven; preheat to 400°F. Mix garlic, cumin, oil, paprika, pepper, oregano, 2 teaspoons salt, and finely grated zest from lemon in a medium bowl.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B84897FAFB022241B7C8071D70D424E1', X'6B87DEFDBF868F478016030F37E87D6B', X'6B87DEFDBF868F478016030F37E87D6B', 1, NULL, NULL, 2, NULL, 0, 'Cut lemon in half and squeeze out juice into spice mixture. Rub the cut lemons all over the turkey, and any remaining juice. Place lemons in cavity of turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C797DDF551CA943BF8954850DB23A73', X'6B87DEFDBF868F478016030F37E87D6B', X'6B87DEFDBF868F478016030F37E87D6B', 2, NULL, NULL, 2, NULL, 0, 'Place turkey in a roasting pan, or aluminum sheet pan with parchment underneath, breast side facing up. From both edges of cavity, loosen skin from breasts, being careful not to tear skin. Using your fingers, gently spread 3/4 of spice mixture under skin (reserve remaining spice mixture), then season turkey skin all over with remaining 1 teaspoon salt.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBF144992DF7784D8894BCEFF565EF8E', X'6B87DEFDBF868F478016030F37E87D6B', X'6B87DEFDBF868F478016030F37E87D6B', 3, NULL, NULL, 2, NULL, 0, 'Roast turkey 10 minutes, then turn heat down to 375°F. Continue roasting, basting with remaining spice mixture and pan juices every 20 minutes, until thickest part of breast is pierced with instant-read thermometer and reads 155-165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEBA464B92255E41A401CCE56444F36F', X'6B87DEFDBF868F478016030F37E87D6B', X'6B87DEFDBF868F478016030F37E87D6B', 4, NULL, NULL, 2, NULL, 0, 'Transfer turkey to a cutting board and let rest 15 minutes, where its temperature will continue to rise to 170°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1B9E1E045E802438ADEDD484A5A5440', X'6B87DEFDBF868F478016030F37E87D6B', X'6B87DEFDBF868F478016030F37E87D6B', 5, NULL, NULL, 2, NULL, 0, 'Purée cilantro, jalapeños, garlic, oil, lime juice, and salt in a blender until combined. Add mayonnaise and purée until well blended. Transfer to a small bowl, cover, and chill until ready to use.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5431D11063BC646A57CF452C8C352A4', X'F92EDBA3D685184FA4176CBBC1C1A533', X'F92EDBA3D685184FA4176CBBC1C1A533', 0, NULL, NULL, 2, NULL, 0, 'Preheat and 8- to 10-inch nonstick skillet on medium heat for 2-3 minutes.');
