INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC9F2EB947886348938FA1E98BA71976', X'F92EDBA3D685184FA4176CBBC1C1A533', X'F92EDBA3D685184FA4176CBBC1C1A533', 6, NULL, NULL, 2, NULL, 0, 'Pile on the taco meat, mango salsa, cilantro and hot sauce, and enjoy!');
