INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E321E14E9B5974D8AE82CBF616943EA', X'AA5FFEB6FFDBFD419225703BAB32DF12', X'AA5FFEB6FFDBFD419225703BAB32DF12', 8, NULL, NULL, 2, NULL, 0, 'Remove roasted vegetables from oven. To serve, plate roasted vegetables, place turkey and sauce on top.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'957431736F55DB449FA7650832FA5D9D', X'AA5FFEB6FFDBFD419225703BAB32DF12', X'AA5FFEB6FFDBFD419225703BAB32DF12', 9, NULL, NULL, 2, NULL, 0, 'Garnish with grated Cotija cheese or queso fresco and pico de gallo.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0BFCD9CC34C024F9FDB77E7999B776C', X'0EBF2F501DD54B49B5AF7408395BF4BA', X'0EBF2F501DD54B49B5AF7408395BF4BA', 0, NULL, NULL, 2, NULL, 0, 'Preheat large saucepan over medium high heat. When hot, add shallots and sauté for 4-5 minutes until translucent. Add garlic, ginger, 1/2 tablespoon curry powder and 1 teaspoon salt. Stir and combine. Then add bell pepper and continue to cook for 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C983B355C15DF04DBA97332D1DB9708F', X'0EBF2F501DD54B49B5AF7408395BF4BA', X'0EBF2F501DD54B49B5AF7408395BF4BA', 1, NULL, NULL, 2, NULL, 0, 'Push vegetables to one side of pan. Add turkey and break up using a wooden spoon or firm spatula. Once cooked through and no pink remains, add remaining 1 tablespoon curry powder and 1 teaspoon kosher salt. Stir thoroughly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DD61141558C274886C18FE9007D0310', X'0EBF2F501DD54B49B5AF7408395BF4BA', X'0EBF2F501DD54B49B5AF7408395BF4BA', 2, NULL, NULL, 2, NULL, 0, 'Then add coconut milk tamari. Take out a few tablespoons of the cooking liquid and whisk it with the arrowroot or tapioca starch. Add this mixture back to pan and stir.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5037AE38BEFEE74B84075276FAF92A53', X'0EBF2F501DD54B49B5AF7408395BF4BA', X'0EBF2F501DD54B49B5AF7408395BF4BA', 3, NULL, NULL, 2, NULL, 0, 'Add in zucchini and spinach. Cover and simmer for 10 minutes. Remove from heat and stir in lime juice. Serve over warm rice and garnish with red pepper flakes and cilantro.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'345A6B05710BB34BBDF5A233B7D9C71A', X'6C8987F075F2D64A87C37E3D59DF4CEE', X'6C8987F075F2D64A87C37E3D59DF4CEE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12B6638C3D50B146A82E64781058418B', X'6C8987F075F2D64A87C37E3D59DF4CEE', X'6C8987F075F2D64A87C37E3D59DF4CEE', 1, NULL, NULL, 2, NULL, 0, 'Add olive oil to cast iron skillet and set to medium high heat. When hot, add turkey tenderloins and garlic cloves, and cook for 4 minutes on each side, to sear. Remove from heat and place in oven. Cook until interior of meat reaches 150-155°F. Remove from oven and place turkey on cutting board to rest, covered with tented foil. Set garlic aside, to use for cauliflower.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31BF07981A570E4C812E41527AE92A11', X'6C8987F075F2D64A87C37E3D59DF4CEE', X'6C8987F075F2D64A87C37E3D59DF4CEE', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, add cauliflower, milk, bay leaves, thyme, salt, and ground pepper to a large saucepan. Bring to a simmer, and cover. Cook for 8-10 minutes, until cauliflower is easily pierced with a fork and tender. Remove bay leaves, and thyme.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7E18DF105F6A040AB069CE1712DCE05', X'6C8987F075F2D64A87C37E3D59DF4CEE', X'6C8987F075F2D64A87C37E3D59DF4CEE', 3, NULL, NULL, 2, NULL, 0, 'Using a slotted spoon, add cauliflower and roasted garlic to a blender. Then add 1 cup of warm milk. Blend for 30 seconds, adding additional milk by 1/4 cup according to how thick or thin youd like the puree to be, until completely smooth. Taste and add more salt and pepper if needed.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC2FAE5333416B49B0D1ED2546BFD417', X'6C8987F075F2D64A87C37E3D59DF4CEE', X'6C8987F075F2D64A87C37E3D59DF4CEE', 4, NULL, NULL, 2, NULL, 0, 'Slice turkey into 1/2 inch slices, and plate over the cauliflower puree. Garnish with additional olive oil and pomegranate seeds.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8649EB674C37F4898FABA10031DB1FD', X'CB571BA1C60DA74499B83ECE58453757', X'CB571BA1C60DA74499B83ECE58453757', 0, NULL, NULL, 2, NULL, 0, 'Cook pasta according to package directions. Take a large bowl and place it underneath your colander when you drain the pasta. You are going to save 1 cup of the pasta water. This is important because it has starch in it and will help the sauce thicken.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E90A1E7BA37E143B946FCD1EF4EDF64', X'CB571BA1C60DA74499B83ECE58453757', X'CB571BA1C60DA74499B83ECE58453757', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet pan, with deep sides, heat olive oil over medium high heat. Once hot, add turkey cutlets. Cook for 5 minutes on each side. Remove from pan and let rest for a few minutes until slicing into thin strips.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7ECE9765385A764E8EB994EC4CAEC8B2', X'CB571BA1C60DA74499B83ECE58453757', X'CB571BA1C60DA74499B83ECE58453757', 2, NULL, NULL, 2, NULL, 0, 'Add pasta to skillet, then turkey, beans, skillet packet, salsa, and sour cream. Stir to fully combine. Add 3/4 cup pasta water, and cook on low heat, stirring. Add up to 1/4 cup more pasta water if you''d like to make even creamier. Stir in lime juice and cilantro.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE87EA5D79B9B24EA18BC1A85E5A913B', X'CB571BA1C60DA74499B83ECE58453757', X'CB571BA1C60DA74499B83ECE58453757', 3, NULL, NULL, 2, NULL, 0, 'Add cheese and cover, cooking until all melted. Then serve with additional cilantro if you like!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D4CD80F56F959448F5135F00FE9427C', X'8E71BD546A3D384E8A24BF85D0239BAC', X'8E71BD546A3D384E8A24BF85D0239BAC', 0, NULL, NULL, 2, NULL, 0, 'For the Tenderloin, follow package instructions: Preheat oven to 325°F. Bake in pan for approximately 50 to 60 minutes. Cook until internal temperature of the meat reaches 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B25A2B34C8C0045856BCFFDE1D456A5', X'8E71BD546A3D384E8A24BF85D0239BAC', X'8E71BD546A3D384E8A24BF85D0239BAC', 1, NULL, NULL, 2, NULL, 0, 'For the Pesto, using a food processor, blend together the arugula or fresh basil, spinach, garlic and almonds and pulse until coarsely chopped. With the food processor running, slowly pour in the olive oil into the bowl, through the feed tube and blend until smooth, or to your desired consistency.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'714ED867CB7FE44CB83BD9709EB6DD57', X'8E71BD546A3D384E8A24BF85D0239BAC', X'8E71BD546A3D384E8A24BF85D0239BAC', 2, NULL, NULL, 2, NULL, 0, 'Add in the Parmesan chunks and pulse again until well combined. Taste and season with salt. Mix one more time.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79567DEC215ED942A5879EF10C5F945A', X'8E71BD546A3D384E8A24BF85D0239BAC', X'8E71BD546A3D384E8A24BF85D0239BAC', 3, NULL, NULL, 2, NULL, 0, 'To serve, slice and serve over mashed potatoes, or with a salad or roasted or steamed vegetables. Serve with homemade pesto on top. Enjoy!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3831BF107372C24895E3E5A50F9BA410', X'D1725843C3A81841AF5EB6DDBD254EC8', X'D1725843C3A81841AF5EB6DDBD254EC8', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400°F. Brush the peppers (inside and outside) with the olive oil and sprinkle with the salt, pepper and garlic powder. Place the peppers on a baking sheet and roast for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C058C2F6B47A56488B8B4AA2DDE0EF37', X'D1725843C3A81841AF5EB6DDBD254EC8', X'D1725843C3A81841AF5EB6DDBD254EC8', 1, NULL, NULL, 2, NULL, 0, 'While the peppers are roasting, make the turkey taco meat. Heat a large skillet over medium heat and add the olive oil. Add the diced peppers, shallot and garlic with a pinch of salt. Cook, stirring often, until softened, about 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9834260B3916E94296D4FD41ECDC7D94', X'D1725843C3A81841AF5EB6DDBD254EC8', X'D1725843C3A81841AF5EB6DDBD254EC8', 2, NULL, NULL, 2, NULL, 0, 'Add in the ground turkey, breaking it apart with a wooden spoon. Season it with the cumin, paprika, chili powder, garlic powder, salt and pepper. Stir well, salt and pepper. Stir well, continuing to break apart the meat into small crumbles. Cook until the turkey has browned.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DEB9AB8AD81A447BF97A26D779648F5', X'D1725843C3A81841AF5EB6DDBD254EC8', X'D1725843C3A81841AF5EB6DDBD254EC8', 3, NULL, NULL, 2, NULL, 0, 'Add the water and flour to a shaker bottle and shake for 30 seconds. Pour the mixture into the tacos, stirring well and cooking for another 5 to 6 minutes, until saucy.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63FBAC6ECF576040A3B02D07F39CD3F9', X'D1725843C3A81841AF5EB6DDBD254EC8', X'D1725843C3A81841AF5EB6DDBD254EC8', 4, NULL, NULL, 2, NULL, 0, 'Remove the peppers from the oven. Fill the peppers with the turkey taco mixture.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'432E1698226D3743BFDA841BEFC95464', X'D9DF28B82521CD439530FB9D9F0D9778', X'D9DF28B82521CD439530FB9D9F0D9778', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B853F9DDD7FD8F4C90091B2DA7DD6772', X'D9DF28B82521CD439530FB9D9F0D9778', X'D9DF28B82521CD439530FB9D9F0D9778', 1, NULL, NULL, 2, NULL, 0, 'In a high-power blender, combine roasted peppers, garlic, basil, 2 tablespoons of olive oil, cashews, and water. Season with salt and pepper, to taste. Blend the mixture on high until well blended and smooth. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'148EF5BCA0A94A4B9B06FAC359EE0BB4', X'D9DF28B82521CD439530FB9D9F0D9778', X'D9DF28B82521CD439530FB9D9F0D9778', 2, NULL, NULL, 2, NULL, 0, 'Combine the zucchini, yellow squash, and onions in a 9x9 casserole dish (or similar size). Season vegetables with salt and pepper, and toss to combine. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'181D0DFE758E8441B3017D0DE36CD1AF', X'D9DF28B82521CD439530FB9D9F0D9778', X'D9DF28B82521CD439530FB9D9F0D9778', 3, NULL, NULL, 2, NULL, 0, 'In a non-stick skillet, heat the remaining 2 tablespoons of olive oil over medium-high heat. Once the oil is hot, add the meatballs to the skillet and quickly sear on all sides, about 3-4 minutes total.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EA83C430CF11548AF1C5B7618C56077', X'D9DF28B82521CD439530FB9D9F0D9778', X'D9DF28B82521CD439530FB9D9F0D9778', 4, NULL, NULL, 2, NULL, 0, 'Transfer the meatballs to the casserole dish and pour the red pepper sauce over the meatballs and the vegetables.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6581961B1122045A9FE6AA49F0962CC', X'D9DF28B82521CD439530FB9D9F0D9778', X'D9DF28B82521CD439530FB9D9F0D9778', 5, NULL, NULL, 2, NULL, 0, 'Bake 25-35 or until the meatballs are fully cooked and the vegetables are tender. Meatballs are fully cooked when the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F58EDADB0BB0341AE6661990A04A3A7', X'D9DF28B82521CD439530FB9D9F0D9778', X'D9DF28B82521CD439530FB9D9F0D9778', 6, NULL, NULL, 2, NULL, 0, 'Garnish with fresh chopped parsley and feta, if desired. Serve over cooked brown rice or cauliflower mash for a low carb option.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98F21DAA3321DD44878BB4254DC3E22F', X'459867F56BCE584FA9409F84EDF8DBF6', X'459867F56BCE584FA9409F84EDF8DBF6', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65FA591DC9391047B3AF1E022CE26A11', X'459867F56BCE584FA9409F84EDF8DBF6', X'459867F56BCE584FA9409F84EDF8DBF6', 1, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to boil and cook egg noodles according to package directions.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'165BF4AEC88591469FD3A47942E41949', X'459867F56BCE584FA9409F84EDF8DBF6', X'459867F56BCE584FA9409F84EDF8DBF6', 2, NULL, NULL, 2, NULL, 0, 'Place meatballs in oven safe baking dish and bake for 20-25 minutes, following package instructions. Meatballs are fully cooked when meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'922D60F2BFBE0C4993A59D13A02B6B95', X'459867F56BCE584FA9409F84EDF8DBF6', X'459867F56BCE584FA9409F84EDF8DBF6', 3, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium high heat, add olive oil and heat for 1-2 minutes. Add onion and mushrooms, sauté for 3-4 minutes until they begin to get fragrant. Add garlic, sauté for another 30 seconds, do not allow to brown.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'894182A469AC5E45A805538F9437B687', X'459867F56BCE584FA9409F84EDF8DBF6', X'459867F56BCE584FA9409F84EDF8DBF6', 4, NULL, NULL, 2, NULL, 0, 'Add butter to onions and mushrooms in pan, and melt, stirring vigorously. Once melted, add flour and continue to stir vigorously until the flour has absorbed the butter and formed a paste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE69C3DC617F0E4397F90484E5947849', X'459867F56BCE584FA9409F84EDF8DBF6', X'459867F56BCE584FA9409F84EDF8DBF6', 5, NULL, NULL, 2, NULL, 0, 'Add beef broth and whisk vigorously to remove lumps on high heat, allowing to boil for 2-3 minutes until it begins to thicken.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'622379CDFBFCC940B398446E274D431B', X'459867F56BCE584FA9409F84EDF8DBF6', X'459867F56BCE584FA9409F84EDF8DBF6', 6, NULL, NULL, 2, NULL, 0, 'Turn burner off and whisk in sour cream and mushroom soup.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10AAF6670D08C44DB185452565A26540', X'459867F56BCE584FA9409F84EDF8DBF6', X'459867F56BCE584FA9409F84EDF8DBF6', 7, NULL, NULL, 2, NULL, 0, 'Stir until mixture is thoroughly incorporated.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8261338CE454B428F04488DAF933A81', X'459867F56BCE584FA9409F84EDF8DBF6', X'459867F56BCE584FA9409F84EDF8DBF6', 8, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper to your taste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E7BBCBA20489D438CD623CCBC2D8414', X'459867F56BCE584FA9409F84EDF8DBF6', X'459867F56BCE584FA9409F84EDF8DBF6', 9, NULL, NULL, 2, NULL, 0, 'Serve over egg noodles, top with meatballs.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F72BFCFBC8C3241BBFB340F6A4FBB45', X'C738FBFCA2292A45A4CC31CC76ECF54F', X'C738FBFCA2292A45A4CC31CC76ECF54F', 0, NULL, NULL, 2, NULL, 0, 'Combine the skewer ingredients and set aside in refrigerator for at least an hour up to 24 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'259211820969F0459DD793161093D78C', X'C738FBFCA2292A45A4CC31CC76ECF54F', X'C738FBFCA2292A45A4CC31CC76ECF54F', 1, NULL, NULL, 2, NULL, 0, 'Mix the jalapeño yogurt ingredients in a blender or food processor and blend until smooth. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2282B5D4318CE4591DE90EF13D9423E', X'C738FBFCA2292A45A4CC31CC76ECF54F', X'C738FBFCA2292A45A4CC31CC76ECF54F', 2, NULL, NULL, 2, NULL, 0, 'Skewer the turkey and alternate with the mushrooms. Pack them tight on the skewers.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4655E9942BD54044B807E9A4184B56BA', X'C738FBFCA2292A45A4CC31CC76ECF54F', X'C738FBFCA2292A45A4CC31CC76ECF54F', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill to high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41D7871ECD8F954EA6F49BEC1D9044BA', X'C738FBFCA2292A45A4CC31CC76ECF54F', X'C738FBFCA2292A45A4CC31CC76ECF54F', 4, NULL, NULL, 2, NULL, 0, 'Grill on high heat for about 10-15 minutes until browned and cooked through. Turkey is fully cooked when meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C46B5B5F52EF74C93124906EB9AB23B', X'C738FBFCA2292A45A4CC31CC76ECF54F', X'C738FBFCA2292A45A4CC31CC76ECF54F', 5, NULL, NULL, 2, NULL, 0, 'Fill a bowl with rice and add cucumber, mint, and chiles. Top with skewers and drizzle on the yogurt.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D6FEAC5064C5845B55589AF5226A47D', X'1FB820E9FE41AB4F9170E2FA0A4E5718', X'1FB820E9FE41AB4F9170E2FA0A4E5718', 0, NULL, NULL, 2, NULL, 0, 'Heat a large sauté pan or skillet over medium-high heat. Add vegetable oil. Once hot add the Fajita seasoning and the turkey breast cutlets. Cook for 5 minutes, then add onions and peppers to the pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99A5757D534C3A4DA5A69FD661B8AFF4', X'1FB820E9FE41AB4F9170E2FA0A4E5718', X'1FB820E9FE41AB4F9170E2FA0A4E5718', 1, NULL, NULL, 2, NULL, 0, 'Cook until the fajita strips reach a minimum internal temperature of 165 °F and the onions and peppers are soft and slightly browned (approximately 5 additional minutes).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1836CAAF87AE34CAC1786A3628E3D5D', X'1FB820E9FE41AB4F9170E2FA0A4E5718', X'1FB820E9FE41AB4F9170E2FA0A4E5718', 2, NULL, NULL, 2, NULL, 0, 'While the turkey is cooking, place the spring mix, dressing, tortilla strips and pico de gallo into a mixing bowl and gently toss.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'646F575AFCAB2842AF3537EFCEAB3975', X'1FB820E9FE41AB4F9170E2FA0A4E5718', X'1FB820E9FE41AB4F9170E2FA0A4E5718', 3, NULL, NULL, 2, NULL, 0, 'Place tossed salad on a large platter and top with cooked turkey and sautéed onions and peppers.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC830BAD32E29D4EA3E5ECFC59F9D3EE', X'1FB820E9FE41AB4F9170E2FA0A4E5718', X'1FB820E9FE41AB4F9170E2FA0A4E5718', 4, NULL, NULL, 2, NULL, 0, 'Place guacamole into separate bowl and serve on the side.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8E0F3301EDD2E4D8A6E6BAC0D37980F', X'1FB820E9FE41AB4F9170E2FA0A4E5718', X'1FB820E9FE41AB4F9170E2FA0A4E5718', 5, NULL, NULL, 2, NULL, 0, 'Serve your favorite salsa as well for an additional topping.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'478A33097497DF448477EEEB7093FB68', X'D5A3AEDA378E7B49AE453AED6A970EFD', X'D5A3AEDA378E7B49AE453AED6A970EFD', 0, NULL, NULL, 2, NULL, 0, 'Heat a large sauté pan or skillet over medium-high heat. Add vegetable oil. Once hot add the turkey breast tenderloins. Cook for 5 minutes, then add onions, peppers and garlic to the pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC514FF52EA9674E82D634D5C28DA0D2', X'D5A3AEDA378E7B49AE453AED6A970EFD', X'D5A3AEDA378E7B49AE453AED6A970EFD', 1, NULL, NULL, 2, NULL, 0, 'Add the white wine to deglaze the pan, making sure to scrape the brown bits of turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B82D063336B4A64FB49F6DB74664EE1F', X'D5A3AEDA378E7B49AE453AED6A970EFD', X'D5A3AEDA378E7B49AE453AED6A970EFD', 2, NULL, NULL, 2, NULL, 0, 'Add the diced tomatoes, chicken broth and oregano, allow to simmer for 20 minutes. Add the fresh basil and cook until the strips reach a minimum internal temperature of 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD227F23960F6244BAC999D009F028D7', X'D5A3AEDA378E7B49AE453AED6A970EFD', X'D5A3AEDA378E7B49AE453AED6A970EFD', 3, NULL, NULL, 2, NULL, 0, 'Serve with mashed potatoes, polenta or rice.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5250D1D1054FCC4789344DA7CA7D97BF', X'4E65D9EED64D3745A10D9A040F071FF0', X'4E65D9EED64D3745A10D9A040F071FF0', 0, NULL, NULL, 2, NULL, 0, 'Cook pasta according to package.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D2CDA755C29714EAF07222FB013FE2F', X'4E65D9EED64D3745A10D9A040F071FF0', X'4E65D9EED64D3745A10D9A040F071FF0', 1, NULL, NULL, 2, NULL, 0, 'While pasta is cooking, sear your meatballs in a cooking pan over medium high heat. Add one cup of spaghetti sauce and continue to simmer until meatballs are cooked thoroughly, roughly 5-7 minutes. Meatballs are fully cooked when meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE8F65D3D82F7C49997DE297246835D1', X'4E65D9EED64D3745A10D9A040F071FF0', X'4E65D9EED64D3745A10D9A040F071FF0', 2, NULL, NULL, 2, NULL, 0, 'Place your spaghetti in a large bowl, add spaghetti sauce, grated parmesan cheese and mozzarella cheese. Mix until combined.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C549BB31A2616943940070B94EFD37D9', X'4E65D9EED64D3745A10D9A040F071FF0', X'4E65D9EED64D3745A10D9A040F071FF0', 3, NULL, NULL, 2, NULL, 0, 'Spray a 12 cup muffin tin with cooking spray. Divide spaghetti into the 12 baking cups. Create a small `hole` for your meatballs. If your meatballs are large, cut in half and add to the top of your spaghetti bites with some extra sauce. Sprinkle with mozzarella cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34D302EAC8C7CF438AA1A1F16BE98FD2', X'4E65D9EED64D3745A10D9A040F071FF0', X'4E65D9EED64D3745A10D9A040F071FF0', 4, NULL, NULL, 2, NULL, 0, 'Bake at 375°F for about 20 minutes or until cheese is bubbly and melted.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD0EA0165025A9418B48819597848605', X'1F5DA0A86223CF4B8805D7D20D7B33E6', X'1F5DA0A86223CF4B8805D7D20D7B33E6', 0, NULL, NULL, 2, NULL, 0, 'Baked Sweet Potato Fries: Preheat oven to 450¬8F and line baking sheet with parchment paper. Peel sweet potatoes and slice into matchsticks, roughly the same size. Place in a large bowl and toss with olive oil and salt. Bake in the oven for 20 minutes on the parchment paper lined cookie sheet, tossing once halfway through to crisp up both sides.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A43071C790A9E84182787C13E9A1A24A', X'1F5DA0A86223CF4B8805D7D20D7B33E6', X'1F5DA0A86223CF4B8805D7D20D7B33E6', 1, NULL, NULL, 2, NULL, 0, 'Lemon Meringue Dipping Sauce: While the fries are baking mix together the mayo, garlic, lemon juice, lemon zest, and a pinch of salt.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'216C2B6C8E33004E84081A1588019EC3', X'C482CD71D9E7F843907BEC878C213B26', X'C482CD71D9E7F843907BEC878C213B26', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA0E4C68DB21C0438E1014DF51813F51', X'C482CD71D9E7F843907BEC878C213B26', X'C482CD71D9E7F843907BEC878C213B26', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl combine lemon pepper, oregano, and chili powder.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A1725388B804D459D28B5DD9662B84E', X'C482CD71D9E7F843907BEC878C213B26', X'C482CD71D9E7F843907BEC878C213B26', 2, NULL, NULL, 2, NULL, 0, 'Drizzle each turkey tenderloin with one tablespoon of olive oil and then season generously with spice mixture and salt.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AFFBF388E4FB74AB8B8F1A1132A25B5', X'C482CD71D9E7F843907BEC878C213B26', X'C482CD71D9E7F843907BEC878C213B26', 3, NULL, NULL, 2, NULL, 0, 'Arrange the seasoned turkey tenderloin on an aluminum foil lined baking sheet with place lemon slices, as desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'076C9D95358F734DA527BD20BD6EA615', X'C482CD71D9E7F843907BEC878C213B26', X'C482CD71D9E7F843907BEC878C213B26', 4, NULL, NULL, 2, NULL, 0, 'Roast for 40 minutes lightly covered with aluminum foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B989CD6D5820344B81D91462945171D', X'C482CD71D9E7F843907BEC878C213B26', X'C482CD71D9E7F843907BEC878C213B26', 5, NULL, NULL, 2, NULL, 0, 'Roast for an additional 15-20 minutes uncovered until the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19AFC1BC9C551E48A423A0179D111470', X'C482CD71D9E7F843907BEC878C213B26', X'C482CD71D9E7F843907BEC878C213B26', 6, NULL, NULL, 2, NULL, 0, 'Allow the turkey tenderloins to rest for 10 minutes. Use a sharp knife to slice and chop the tenderloins into 1/2-inch cubes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6C1A720937969488287C5194F4F1126', X'CC4F4A5E454C6F46837C2A27BA2A708A', X'CC4F4A5E454C6F46837C2A27BA2A708A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA407588E24CA74C80E89ABF83A7834D', X'CC4F4A5E454C6F46837C2A27BA2A708A', X'CC4F4A5E454C6F46837C2A27BA2A708A', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle the paprika and oregano on all sides of the tenderloins, then place on foil or a parchment-lined baking sheet. Drizzle with the olive oil, as evenly as possible.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC71F98DAF77834D9A24DB0728FBFB2E', X'CC4F4A5E454C6F46837C2A27BA2A708A', X'CC4F4A5E454C6F46837C2A27BA2A708A', 2, NULL, NULL, 2, NULL, 0, 'Roast at 350°F for 30-35 minutes or until the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE064A305DC52349A4E2784AA99448BE', X'CC4F4A5E454C6F46837C2A27BA2A708A', X'CC4F4A5E454C6F46837C2A27BA2A708A', 3, NULL, NULL, 2, NULL, 0, 'Remove from the oven and let rest for 5-10 minutes before slicing.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6DFD87E40A5754E810C8C8FC5EB5E34', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 0, NULL, NULL, 2, NULL, 0, 'Brine turkey breast in cool water and kosher salt for 4  6 hours (can also go as long as overnight).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A950374434D0F4BAAFA6D187979DFFF', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 1, NULL, NULL, 2, NULL, 0, 'To brine, place thawed turkey breast in a large bowl. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51AB49C94653554E8B36D38E832437CD', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 2, NULL, NULL, 2, NULL, 0, 'Mix 8 cups of cool water with 1/4 cup kosher salt. Stir till dissolved.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CDCDD1DA5DD924E8F16AABFAFCBF5C8', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 3, NULL, NULL, 2, NULL, 0, 'Pour salt/water mixture over turkey breast until turkey breast is covered. Cover and place in refrigerator until ready to cook.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7D29158CC07F941B80BEDBB280398F0', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 4, NULL, NULL, 2, NULL, 0, 'When ready to cook, take turkey breast out of the brine. Discard brine.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C121FEE03101BA4CB31622720408EDEB', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 5, NULL, NULL, 2, NULL, 0, 'Pat turkey dry with paper towels and remove and discard skin. Season generously with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAE324BCA2142E46A5BDFC4CF43230E8', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 6, NULL, NULL, 2, NULL, 0, 'Pour apple cider and bullion in the bottom of Instant Pot, place steaming rack inside, and put seasoned turkey breast on top.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4D5A53F4FE091428F5FF6C9BF482E3B', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 7, NULL, NULL, 2, NULL, 0, 'Lock lid, select the Meat function, and set timer for 45 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74BA5FD9E7FD034AA03C7960FFE01D5D', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 8, NULL, NULL, 2, NULL, 0, 'Quick release value after the timer beeps and remove the turkey breast from the cooker. Place on a foil-lined cookie sheet and brush barbecue sauce on the meat side of the breast.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F28E2EAF9AD90144A4B8791CE46BC225', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 9, NULL, NULL, 2, NULL, 0, 'Broil turkey breast for 5  6 minutes (watching carefully to prevent charring). Turkey is fully cooked when meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5492930DD767B408744E595F35CE81F', X'A2DE431AE3A45448AD7948F49BC58050', X'A2DE431AE3A45448AD7948F49BC58050', 10, NULL, NULL, 2, NULL, 0, 'Shred breast after cooking and place on a platter. Serve with additional barbecue sauce on the side.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E9D9E464EE83E46BD37385FE1DC0C73', X'60DD476C8B7ADC44A39E9DD4B593B427', X'60DD476C8B7ADC44A39E9DD4B593B427', 0, NULL, NULL, 2, NULL, 0, 'Balsamic Tomato Salad: In a small mixing bowl, combine the halved grape tomatoes, balsamic vinegar, and some freshly squeezed lime juice. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48C2F505A3F89A4AA29E75D538D523F7', X'60DD476C8B7ADC44A39E9DD4B593B427', X'60DD476C8B7ADC44A39E9DD4B593B427', 1, NULL, NULL, 2, NULL, 0, 'Sriracha Mayo: In a small bowl, combine either avocado oil, Sriracha, sea salt, fresh lime juice, and unsweetened almond milk and stir to combine. You can set this aside as well.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F922F2EB32F1464FADCE5525E533526E', X'60DD476C8B7ADC44A39E9DD4B593B427', X'60DD476C8B7ADC44A39E9DD4B593B427', 2, NULL, NULL, 2, NULL, 0, 'Turkey Burgers: Grab about 1/2 cup of ground turkey and form into patties. Try to make the patties as evenly sized as possible so they cook evenly and are done at the same time.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1143B3627C94A4ABF928B8D1DE8D9C9', X'60DD476C8B7ADC44A39E9DD4B593B427', X'60DD476C8B7ADC44A39E9DD4B593B427', 3, NULL, NULL, 2, NULL, 0, 'Season both sides of the patties with a mixture of sea salt, ground black pepper, and garlic powder.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5519E5343887A440BABB00C6B59ED785', X'60DD476C8B7ADC44A39E9DD4B593B427', X'60DD476C8B7ADC44A39E9DD4B593B427', 4, NULL, NULL, 2, NULL, 0, 'Heat a skillet over medium-high heat and add a little olive oil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3423F6F3FFB6D4EAAE78C977A8EE4C6', X'60DD476C8B7ADC44A39E9DD4B593B427', X'60DD476C8B7ADC44A39E9DD4B593B427', 5, NULL, NULL, 2, NULL, 0, 'Add the patties, being careful not to overcrowd them, and cook about 5-6 minutes per side, until golden brown on both sizes and the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E444FC5E3B229F46A4C6B52C324344D4', X'60DD476C8B7ADC44A39E9DD4B593B427', X'60DD476C8B7ADC44A39E9DD4B593B427', 6, NULL, NULL, 2, NULL, 0, 'To the same skillet, add the quartered red onion and cook on all sides until golden brown and tender, about 3-4 minutes per side. Then remove from the heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBD5C5A3F1A34640AF240B2955282B5B', X'60DD476C8B7ADC44A39E9DD4B593B427', X'60DD476C8B7ADC44A39E9DD4B593B427', 7, NULL, NULL, 2, NULL, 0, 'To make the zucchini noodles, heat olive oil over medium-high heat and add the spiralized zucchini noodles, sea salt, and ground black pepper, and toss to coat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE3D4D5F1E95124CAE2BA80A5649D4F6', X'60DD476C8B7ADC44A39E9DD4B593B427', X'60DD476C8B7ADC44A39E9DD4B593B427', 8, NULL, NULL, 2, NULL, 0, 'Cook until the noodles are just tender, about 6-8 minutes, tossing frequently.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0710B8F0D29A73469AC50160A2A1E16C', X'60DD476C8B7ADC44A39E9DD4B593B427', X'60DD476C8B7ADC44A39E9DD4B593B427', 9, NULL, NULL, 2, NULL, 0, 'To make the fried egg, add a little olive oil to a small skillet and heat over low heat. Add the egg and season with a little sea salt and ground black pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15CE877AED0030418AB256491A277C7E', X'0BFA9CB256C3284A94962EAC2D8B2C21', X'0BFA9CB256C3284A94962EAC2D8B2C21', 0, NULL, NULL, 2, NULL, 0, 'Remove turkey tenderloins from package. Rinse under warm water and set on paper towels, drying thoroughly. Set turkey tenderloins aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFDD3F7FA487314D8C3DB9F82C75C163', X'0BFA9CB256C3284A94962EAC2D8B2C21', X'0BFA9CB256C3284A94962EAC2D8B2C21', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together minced garlic, basil, and ground pepper. Rub over turkey tenderloins until well coated on each side. Insert one clove into each of the ends and another one in the middle. Place into a gallon-sized resealable plastic bag (or place into a shallow glass pan).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68E8239AF7455D43B70A711B5A0C4974', X'0BFA9CB256C3284A94962EAC2D8B2C21', X'0BFA9CB256C3284A94962EAC2D8B2C21', 2, NULL, NULL, 2, NULL, 0, 'In a medium-sized bowl, blend vegetable oil, soy sauce, lemon juice, and brown sugar. Pour over turkey tenderloins and seal bag (or pour over in shallow pan). Turn to coat. Place in refrigerator for at least 4 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9BC21A28E2F8140990765AA6B955C17', X'0BFA9CB256C3284A94962EAC2D8B2C21', X'0BFA9CB256C3284A94962EAC2D8B2C21', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill for high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93104BCC8A2E4B4AB0A2438E3DEB7D89', X'0BFA9CB256C3284A94962EAC2D8B2C21', X'0BFA9CB256C3284A94962EAC2D8B2C21', 4, NULL, NULL, 2, NULL, 0, 'Lightly oil grill grate with non-stick spray. Place turkey tenderloins on grill (discard marinade).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33F0585ACCA6C642B46C4A6B0A3F2966', X'0BFA9CB256C3284A94962EAC2D8B2C21', X'0BFA9CB256C3284A94962EAC2D8B2C21', 5, NULL, NULL, 2, NULL, 0, 'Close lid and grill turkey tenderloins about 15 minutes on each side (depending on thickness), or until the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF7F7CE8473FF04DA6F32D24A48AF44E', X'BB188D4348600141AD90F0F17A88B781', X'BB188D4348600141AD90F0F17A88B781', 0, NULL, NULL, 2, NULL, 0, 'Slice the onions and bell peppers and thread the vegetables onto the skewers.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C74B2DA1A2986943A25276D33D34C892', X'BB188D4348600141AD90F0F17A88B781', X'BB188D4348600141AD90F0F17A88B781', 1, NULL, NULL, 2, NULL, 0, 'Cut the tenderloins into 2 inch pieces and thread the turkey pieces onto the skewers.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'424333E78ACA5A4A86FC7094E1E7427D', X'BB188D4348600141AD90F0F17A88B781', X'BB188D4348600141AD90F0F17A88B781', 2, NULL, NULL, 2, NULL, 0, 'Reserve the extra marinade from the marinated turkey breast tenderloins and brush over the vegetable skewers.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8248754E5958049841F527A06D5393C', X'BB188D4348600141AD90F0F17A88B781', X'BB188D4348600141AD90F0F17A88B781', 3, NULL, NULL, 2, NULL, 0, 'Set the grill temperature to medium high and allow the grill pan to heat thoroughly before placing your turkey skewers on the pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E53DF54FF96E047B137A411C141C593', X'BB188D4348600141AD90F0F17A88B781', X'BB188D4348600141AD90F0F17A88B781', 4, NULL, NULL, 2, NULL, 0, 'Allow the turkey skewers to sear nicely and take on nice grill marks before flipping them over. Grill for about 10 minutes per side.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14572E440D1C9042BA29BBE22D5DC9FF', X'BB188D4348600141AD90F0F17A88B781', X'BB188D4348600141AD90F0F17A88B781', 5, NULL, NULL, 2, NULL, 0, 'Add a layer of foil to make a tent to keep the heat in place  this will allow the juices of the turkey to redistribute throughout the turkey kabob.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E01EB08597EC204CAFF471EE0E479A70', X'BB188D4348600141AD90F0F17A88B781', X'BB188D4348600141AD90F0F17A88B781', 6, NULL, NULL, 2, NULL, 0, 'Grill the vegetable skewers along side the turkey skewers. Turkey is fully cooked when meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6777A74409ED4D41AC04D76301D6B12A', X'BB188D4348600141AD90F0F17A88B781', X'BB188D4348600141AD90F0F17A88B781', 7, NULL, NULL, 2, NULL, 0, 'Serve the marinated grilled turkey kabobs with the grilled vegetable skewers on a platter with steamed rice or plate them individually, removing from the skewers.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C3F52FED74CBC4C889231DFBDBBC0B5', X'9EE65BAD728DDD47830EB046C80F51F0', X'9EE65BAD728DDD47830EB046C80F51F0', 0, NULL, NULL, 2, NULL, 0, 'Heat grill to medium high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65F55DD3A962104786910F1E01760E59', X'9EE65BAD728DDD47830EB046C80F51F0', X'9EE65BAD728DDD47830EB046C80F51F0', 1, NULL, NULL, 2, NULL, 0, 'Season turkey breast cutlet strips with fajita seasoning.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E977D9A1FE7844BA0CB539CF3C2E4C9', X'9EE65BAD728DDD47830EB046C80F51F0', X'9EE65BAD728DDD47830EB046C80F51F0', 2, NULL, NULL, 2, NULL, 0, 'Thread bell peppers, onions and turkey breast cutlet strips onto skewers, alternating the meat and vegetables. For smaller cut pieces of turkey, thread one end of the meat, make a loop, and thread the other end to double it up.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'708A57A2AA60244F84B6B96082F9593B', X'9EE65BAD728DDD47830EB046C80F51F0', X'9EE65BAD728DDD47830EB046C80F51F0', 3, NULL, NULL, 2, NULL, 0, 'Oil the grill grates with vegetable oil before placing skewers on grill.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A70EFE0D7F58BC4A98E43C1ED6771298', X'9EE65BAD728DDD47830EB046C80F51F0', X'9EE65BAD728DDD47830EB046C80F51F0', 4, NULL, NULL, 2, NULL, 0, 'Grill the kabobs for 2-3 minutes per side, covering while cooking. Cook until the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE28CC1E679BAB49B60D9625A1E0EBA2', X'F4B0206897257E4C9B86E4EE951C4EA3', X'F4B0206897257E4C9B86E4EE951C4EA3', 0, NULL, NULL, 2, NULL, 0, 'To prepare the guacamole, Place the avocado into a medium bowl and roughly mash with a fork. Stir in the onions, cilantro, and lime juice. Season with salt and pepper, to taste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96DD86BB3FC13D4080F8928E73443A0A', X'F4B0206897257E4C9B86E4EE951C4EA3', X'F4B0206897257E4C9B86E4EE951C4EA3', 1, NULL, NULL, 2, NULL, 0, 'To prepare the Pico De Gallo, in a medium bowl, stir together the tomatoes, onions, cilantro, garlic, and lime juice. Season with salt and pepper, to taste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'999C50A8AD92A945B749AB4B219DB1B8', X'F4B0206897257E4C9B86E4EE951C4EA3', X'F4B0206897257E4C9B86E4EE951C4EA3', 2, NULL, NULL, 2, NULL, 0, 'For the Turkey Patties, prepare an outdoor grill on medium-high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0161363017066840831DD9380459F31E', X'F4B0206897257E4C9B86E4EE951C4EA3', X'F4B0206897257E4C9B86E4EE951C4EA3', 3, NULL, NULL, 2, NULL, 0, 'Place the turkey patties on the grill and cook for 3 minutes, covered. Flip and continue to cook, covered, for 3-4 minutes more or until the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF450AE115EB1347A668EB8BB1E0574A', X'F4B0206897257E4C9B86E4EE951C4EA3', X'F4B0206897257E4C9B86E4EE951C4EA3', 4, NULL, NULL, 2, NULL, 0, 'Serve each turkey patty on a hamburger bun topped with guacamole.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6B3DFFA29C96248B239C3E726E8EEAA', X'03B594BCB33BA74E894D499D834B6B53', X'03B594BCB33BA74E894D499D834B6B53', 0, NULL, NULL, 2, NULL, 0, 'Preheat the grill on medium-high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD19120468FFEC4FAE6606442692205B', X'03B594BCB33BA74E894D499D834B6B53', X'03B594BCB33BA74E894D499D834B6B53', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, combine ground turkey, taco seasoning, diced chili peppers, garlic powder, salt and pepper. Mix thoroughly so that peppers are evenly distributed. Form into 4 patties.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A0A8409F824BF4EAF1B1EB044F78FB9', X'03B594BCB33BA74E894D499D834B6B53', X'03B594BCB33BA74E894D499D834B6B53', 2, NULL, NULL, 2, NULL, 0, 'Pat each side of the turkey patties with bread crumbs.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62BFEE39F13C6B419E12067A9F47F261', X'03B594BCB33BA74E894D499D834B6B53', X'03B594BCB33BA74E894D499D834B6B53', 3, NULL, NULL, 2, NULL, 0, 'Grill turkey patties for about 8 minutes per side, cooking until the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89A8238987733643AF7E86BAB554A007', X'03B594BCB33BA74E894D499D834B6B53', X'03B594BCB33BA74E894D499D834B6B53', 4, NULL, NULL, 2, NULL, 0, 'While turkey patties are grilling, prepare your guacamole.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D33358F89E7E34A86CF5F4322705C78', X'03B594BCB33BA74E894D499D834B6B53', X'03B594BCB33BA74E894D499D834B6B53', 5, NULL, NULL, 2, NULL, 0, 'Mash the avocados in a bowl with a fork or a pastry cutter, until desired the consistency is achieved. Add diced tomato, cumin, lime juice, salt and pepper to taste, set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86C7767DF147AB4A8BF9C5FD01F01B99', X'03B594BCB33BA74E894D499D834B6B53', X'03B594BCB33BA74E894D499D834B6B53', 6, NULL, NULL, 2, NULL, 0, 'Top burgers with pepper jack cheese during the last minute of grilling.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BFF6C907833E940810482F758374636', X'03B594BCB33BA74E894D499D834B6B53', X'03B594BCB33BA74E894D499D834B6B53', 7, NULL, NULL, 2, NULL, 0, 'To assemble, top each burger with guacamole and salsa.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBC381011DBF9747A4DA366D11FC8D92', X'E4EB5B7AB4F7D64C9559A1DA7751CD61', X'E4EB5B7AB4F7D64C9559A1DA7751CD61', 0, NULL, NULL, 2, NULL, 0, 'Prepare turkey burgers according to package directions. Cook until the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18A6323F5765E0498C21D9D1DCE44B54', X'E4EB5B7AB4F7D64C9559A1DA7751CD61', X'E4EB5B7AB4F7D64C9559A1DA7751CD61', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, whisk together the mayo, garlic, herbs, lemon juice and zest. Season mixture with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'039BAF90B304E746BE8C2959DCD86D0A', X'E4EB5B7AB4F7D64C9559A1DA7751CD61', X'E4EB5B7AB4F7D64C9559A1DA7751CD61', 2, NULL, NULL, 2, NULL, 0, 'Arrange turkey burgers on buns, spread with a dollop of aioli, top with lettuce and tomato as desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBE7B7A1DD6B2345A50C1A0A2203C185', X'05E4B5557822FF4BA1E7A7AAE897BE46', X'05E4B5557822FF4BA1E7A7AAE897BE46', 0, NULL, NULL, 2, NULL, 0, 'In a large skillet, cook the bacon over moderately high heat until crisp. Drain on paper towels and break each strip in half.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7356F2832EAE414E8752A32BFD637C3A', X'05E4B5557822FF4BA1E7A7AAE897BE46', X'05E4B5557822FF4BA1E7A7AAE897BE46', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle ground turkey with kosher salt and pepper. Mix gently until well combined.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'184C62E474056A4495A56B222655D0CE', X'05E4B5557822FF4BA1E7A7AAE897BE46', X'05E4B5557822FF4BA1E7A7AAE897BE46', 2, NULL, NULL, 2, NULL, 0, 'Form into four 4-ounce burger patties.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'184014A0BF9D344EB2181AAF56281603', X'05E4B5557822FF4BA1E7A7AAE897BE46', X'05E4B5557822FF4BA1E7A7AAE897BE46', 3, NULL, NULL, 2, NULL, 0, 'Heat a nonstick skillet over medium heat with olive oil. Add the patties and cook for 4 minutes, until golden brown and firm when pressed.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC38B0AAFF3BEC459880CB3D6892266F', X'05E4B5557822FF4BA1E7A7AAE897BE46', X'05E4B5557822FF4BA1E7A7AAE897BE46', 4, NULL, NULL, 2, NULL, 0, 'Flip the burgers and cook for an additional 3 to 4 minutes or until the meat reaches an internal temperature of 165°F using a meat thermometer. Top with a slice of cheese and let cheese melt for 1 minute.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55C38988F95C744DA69F3C3B6BFE2E9D', X'05E4B5557822FF4BA1E7A7AAE897BE46', X'05E4B5557822FF4BA1E7A7AAE897BE46', 5, NULL, NULL, 2, NULL, 0, 'Set aside and allow to rest.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'740EBB09D118F647B759C0055DAA4239', X'05E4B5557822FF4BA1E7A7AAE897BE46', X'05E4B5557822FF4BA1E7A7AAE897BE46', 6, NULL, NULL, 2, NULL, 0, 'Spread mayonnaise on the cut sides of the buns; set the burgers on the bottoms and top with the bacon and roasted poblano and toppings of your choice.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39A5A6DB0776F54D998463219F9238B0', X'7FC2C901B6B6804BB063C06C40351FA1', X'7FC2C901B6B6804BB063C06C40351FA1', 0, NULL, NULL, 2, NULL, 0, 'Spray a grill pan or grill with cooking spray and heat over medium high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DD40334F189E7498A4F0C3D79DC3D44', X'7FC2C901B6B6804BB063C06C40351FA1', X'7FC2C901B6B6804BB063C06C40351FA1', 1, NULL, NULL, 2, NULL, 0, 'Once hot, place the turkey patties on the grill.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5D9368AFAE10D46A5E43B6B4E79C2C4', X'7FC2C901B6B6804BB063C06C40351FA1', X'7FC2C901B6B6804BB063C06C40351FA1', 2, NULL, NULL, 2, NULL, 0, 'While the turkey patties cook, slice the baguette so you have a top and a bottom.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A0E265310A3A84ABDA10E5462CFC589', X'7FC2C901B6B6804BB063C06C40351FA1', X'7FC2C901B6B6804BB063C06C40351FA1', 3, NULL, NULL, 2, NULL, 0, 'Cook the turkey patties for 6-7 minutes per side turning once and check temperature. Cook until the meat reaches an internal temperature of 165¬8F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98057510D977AF408475F0746129CAE9', X'7FC2C901B6B6804BB063C06C40351FA1', X'7FC2C901B6B6804BB063C06C40351FA1', 4, NULL, NULL, 2, NULL, 0, 'Remove from heat and rest for 3-5 minutes. Slice in half.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A7744DDC72D364EBE96D00CCB1EB0D6', X'7FC2C901B6B6804BB063C06C40351FA1', X'7FC2C901B6B6804BB063C06C40351FA1', 5, NULL, NULL, 2, NULL, 0, 'Drizzle a generous portion of Sriracha mayonnaise onto the bottom bun.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'937130A11247E8488AE1E7635B1FB617', X'7FC2C901B6B6804BB063C06C40351FA1', X'7FC2C901B6B6804BB063C06C40351FA1', 6, NULL, NULL, 2, NULL, 0, 'Place the patty halves onto the bottom bun in a single layer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B6A21B89E1207428988DE390E9AE105', X'7FC2C901B6B6804BB063C06C40351FA1', X'7FC2C901B6B6804BB063C06C40351FA1', 7, NULL, NULL, 2, NULL, 0, 'Top with plenty of pickled vegetables, sliced jalapenos, fresh cilantro and the top bun. Serve immediately.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F35EDF4EEFF4994CB59C4B82D6147FB0', X'15EC08DB1447EC4BA041166F311F6A93', X'15EC08DB1447EC4BA041166F311F6A93', 0, NULL, NULL, 2, NULL, 0, 'Preheat your grill and grill the turkey burgers for 4-5 minutes per side as per the package directions.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABEA501F44B1EE44A2923C2070902314', X'15EC08DB1447EC4BA041166F311F6A93', X'15EC08DB1447EC4BA041166F311F6A93', 1, NULL, NULL, 2, NULL, 0, 'Cook until the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E84EC272D83A544DAEDFD42BBDB352DD', X'15EC08DB1447EC4BA041166F311F6A93', X'15EC08DB1447EC4BA041166F311F6A93', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil over medium heat in a medium sauté pan. Add the minced onion, grated ginger, diced apple and sauté until golden.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2B2BC72AF10D146984716B566A19B40', X'15EC08DB1447EC4BA041166F311F6A93', X'15EC08DB1447EC4BA041166F311F6A93', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining ingredients to the saucepan and cook until the cranberries pop, about 10 mins. Remove from the heat and cool.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB5D71C8C5A10C47BC16B3A5AD58110D', X'15EC08DB1447EC4BA041166F311F6A93', X'15EC08DB1447EC4BA041166F311F6A93', 4, NULL, NULL, 2, NULL, 0, 'The chutney can be stored in the refrigerator for several days.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'796A37AA6DB37645B5724B0984322134', X'15EC08DB1447EC4BA041166F311F6A93', X'15EC08DB1447EC4BA041166F311F6A93', 5, NULL, NULL, 2, NULL, 0, 'Trim excess leaves and ends off of the Brussels sprouts.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F58BD74754BE5D4C8DBEDFE2B4D33381', X'15EC08DB1447EC4BA041166F311F6A93', X'15EC08DB1447EC4BA041166F311F6A93', 6, NULL, NULL, 2, NULL, 0, 'Finely shred in a food processor, on a mandolin or with a sharp knife.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FE507BF0FDCB540BA665323512806BC', X'15EC08DB1447EC4BA041166F311F6A93', X'15EC08DB1447EC4BA041166F311F6A93', 7, NULL, NULL, 2, NULL, 0, 'Combine the shredded Brussels sprouts, cranberries, toasted pecans, orange zest.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2557BEE3F32FE04784083CDC01B7106D', X'15EC08DB1447EC4BA041166F311F6A93', X'15EC08DB1447EC4BA041166F311F6A93', 8, NULL, NULL, 2, NULL, 0, 'Whisk together the honey, whole grain mustard, mayonnaise, orange juice, plain yogurt, apple cider vinegar, salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'875B7C92DC13A44F9EC457C7BEB12DF2', X'79E721257F77E946B64024B674053CA2', X'79E721257F77E946B64024B674053CA2', 0, NULL, NULL, 2, NULL, 0, 'Combine chickpeas, lemon juice, garlic powder, 1/4 teaspoon salt, tahini, 6 tablespoons olive oil and water.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E5202111151294E9760BECA5C7E9429', X'79E721257F77E946B64024B674053CA2', X'79E721257F77E946B64024B674053CA2', 1, NULL, NULL, 2, NULL, 0, 'In a food processor, puree chickpea mixture until smooth and creamy to create hummus.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38DD4E8D08E137479773ABA134A4F8E0', X'79E721257F77E946B64024B674053CA2', X'79E721257F77E946B64024B674053CA2', 2, NULL, NULL, 2, NULL, 0, 'In a non-stick skillet, heat the remaining 2 tablespoons of olive oil over medium heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6EFD16221DAEA4FBB6A737CFE15D173', X'79E721257F77E946B64024B674053CA2', X'79E721257F77E946B64024B674053CA2', 3, NULL, NULL, 2, NULL, 0, 'Add ground turkey to the skillet and season with the remaining teaspoon salt and pumpkin spice. Cook, stirring until brown and fragrant and turkey is cooked to 165° F, about 5-6 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB61440067D61B4E93AA210807007B26', X'79E721257F77E946B64024B674053CA2', X'79E721257F77E946B64024B674053CA2', 4, NULL, NULL, 2, NULL, 0, 'Spread the hummus on a large flat plate or platter. Top with turkey mixture and garnish with pine nuts. Note: To toast pine nuts, bake for 5 minutes at 375° F on a baking sheet.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24B4C3958AC3A84593C90E4E85C9DD78', X'79E721257F77E946B64024B674053CA2', X'79E721257F77E946B64024B674053CA2', 5, NULL, NULL, 2, NULL, 0, 'Serve with pita or pita chips and garnish with parsley or cilantro, if desired');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B225CBD09E284B4F86B0B4887C8F79E0', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 0, NULL, NULL, 2, NULL, 0, 'Heat a skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDF11C8CE2FD0E43BD3949972AD5BF8B', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 1, NULL, NULL, 2, NULL, 0, 'Add a tablespoon of oil and the garlic to the skillet. Cook, stirring until fragrant, about 30 seconds.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E99A86530F4CE74DB462C4BBAC8830BD', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 2, NULL, NULL, 2, NULL, 0, 'Stir in tomatoes and their juices, along with Italian seasoning.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50868D56A742084891E4D85FF13097BC', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 3, NULL, NULL, 2, NULL, 0, 'Bring the mixture in skillet to a boil; reduce heat to medium and simmer gently. Stir occasionally, until sauce has thickened but is still chunky, about 20 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'258303468E636D468283E3319ED8E38B', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 4, NULL, NULL, 2, NULL, 0, 'Season the sauce with salt and pepper to taste and keep warm.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD3CBB754D3E96459E0CCD0EA8C38E8B', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 5, NULL, NULL, 2, NULL, 0, 'While sauce simmers, pat turkey cutlets dry with a paper towel and season with a pinch of salt.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DB8DE70C776384AA4CC12BD187D9764', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 6, NULL, NULL, 2, NULL, 0, 'Heat the remaining tablespoon of olive oil in a large skillet over medium heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35E190C6EF7BBC428B038435D4B5B6D1', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 7, NULL, NULL, 2, NULL, 0, 'Add the turkey cutlets to the skillet and cook for 6 minutes on one side. Flip the cutlets and cook for an additional 3 minutes, or until an internal temperature reaches 165°F, measured by a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63788E07DDD6BE4BAA1E35C769B61376', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 8, NULL, NULL, 2, NULL, 0, 'Spoon the warm tomato sauce over each cutlet. Excess sauce can be served with pasta as a side dish.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FACD6E2DB47479479DDD3F37B3C48440', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle with mozzarella. Cover skillet and cook until cheese melts, about 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BE4055256B86D4B8CE0F1E3E1E317B2', X'67E6BFF164F52A42923FC02EE996BD6A', X'67E6BFF164F52A42923FC02EE996BD6A', 10, NULL, NULL, 2, NULL, 0, 'Garnish with basil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1DDB7AD7DBD6A41BA047C460A95AF37', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together yogurt, Sriracha sauce, lime juice and water, then set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5F179E81EF27F44B033A2A756687AA2', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', 1, NULL, NULL, 2, NULL, 0, 'Heat a large, non-stick skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4441B8F4A3559D4BA57282F9E356141F', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', 2, NULL, NULL, 2, NULL, 0, 'Add ground turkey, chili powder, garlic powder and cumin to skillet. Cook and stir to crumble until meat is cooked to 165° F, about 6 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84B837DB3345264CBD58FF196408D8EA', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', 3, NULL, NULL, 2, NULL, 0, 'Season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C80A4EEDD09CA4BB5F54B6056D9299B', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', 4, NULL, NULL, 2, NULL, 0, 'Divide warm rice into four bowls. Top with turkey, black beans, corn and pico de gallo.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9F902CB197DCC40B1A8B1E0101DAF1A', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', X'D5DA011C966FA442AEE7B2CEE1A4D2FB', 5, NULL, NULL, 2, NULL, 0, 'Drizzle with yogurt sauce. Garnish with cilantro, if desired.');
