INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52D8923F2A977744BEB98F47C58DB750', X'979A31A241909C499420CD52CE97DC3F', X'979A31A241909C499420CD52CE97DC3F', 3, NULL, NULL, 2, NULL, 0, 'Grill the turkey on high heat on all four sides, about 4 minutes per side, until browned and cooked through.');
