INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BB5B622C03B234DAE30B9B96642FCBA', X'32A165D154085343866A1839FE9B7849', X'32A165D154085343866A1839FE9B7849', 0, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium-high heat, heat oil. After oil is hot, add butter.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69DB55169309574BBDD27D82E4548140', X'32A165D154085343866A1839FE9B7849', X'32A165D154085343866A1839FE9B7849', 1, NULL, NULL, 2, NULL, 0, 'Add about 6 lemon slices to the skillet.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EB0C2A9D35CC449AD2481BC1C8A604C', X'32A165D154085343866A1839FE9B7849', X'32A165D154085343866A1839FE9B7849', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the turkey breast cutlets with garlic powder, salt and pepper, and cayenne pepper (if desired).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A4CC320661AEA4E9D483BBFEC13B629', X'32A165D154085343866A1839FE9B7849', X'32A165D154085343866A1839FE9B7849', 3, NULL, NULL, 2, NULL, 0, 'Place the turkey breast cutlets into the skillet and cook for about 3 minutes on each side until the turkey is no longer pink, and is slightly browned around the edges.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40CD81CC7BDDA84294A441B20AE7A9C9', X'32A165D154085343866A1839FE9B7849', X'32A165D154085343866A1839FE9B7849', 4, NULL, NULL, 2, NULL, 0, 'Turkey is fully cooked when meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1719444CD89DA64B9AC8EECA6CFBDC70', X'32A165D154085343866A1839FE9B7849', X'32A165D154085343866A1839FE9B7849', 5, NULL, NULL, 2, NULL, 0, 'Garnish with sprigs of fresh thyme.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88C3A85638EC19458AEAB178AAA32A90', X'21392FBABCFB854AA7A4C47462397889', X'21392FBABCFB854AA7A4C47462397889', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, gently mix the ground turkey with the spinach, feta, oregano, garlic, onion, salt, and pepper. Roll into 1-inch rounds and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B18F6A22EB02214AA675E56E1D149FF0', X'21392FBABCFB854AA7A4C47462397889', X'21392FBABCFB854AA7A4C47462397889', 1, NULL, NULL, 2, NULL, 0, 'Place a skillet over medium heat and add a teaspoon or so of olive oil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E0D59FB5CCE334DA989FE280658D05C', X'21392FBABCFB854AA7A4C47462397889', X'21392FBABCFB854AA7A4C47462397889', 2, NULL, NULL, 2, NULL, 0, 'Cook the meatballs in batches, making sure to brown on all sides.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB570830B6759F4492F6FF7801932188', X'21392FBABCFB854AA7A4C47462397889', X'21392FBABCFB854AA7A4C47462397889', 3, NULL, NULL, 2, NULL, 0, 'Once all the meatballs are cooked through, divide between your plates and add your favorites - pitas, hummus, tzatziki, olives, salad, tomatoes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07F84E7F295BF84DA5C0513CF41520D8', X'3403B26B897E144C87E9713B10378EBB', X'3403B26B897E144C87E9713B10378EBB', 0, NULL, NULL, 2, NULL, 0, 'Heat a saucepan with the avocado oil over medium high heat. Cut the turkey breast into small pieces and sauté until cooked through. Midway through cooking, add the chopped onion. When the turkey is cooked, add the taco seasoning.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7780279FC43F94FA247D209732DE850', X'3403B26B897E144C87E9713B10378EBB', X'3403B26B897E144C87E9713B10378EBB', 1, NULL, NULL, 2, NULL, 0, 'Add the black beans, salsa, corn, green chilies, enchilada sauce, and rice. Stir well.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5642500975C7E64C89EC5EF14F005605', X'3403B26B897E144C87E9713B10378EBB', X'3403B26B897E144C87E9713B10378EBB', 2, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium and cook, stirring every few minutes, until the rice is cooked through. Be careful not to let the bottom scorch. If the casserole begins sticking, reduce the heat further.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A71D0EEB7DE4045B0E091E435C1055D', X'3403B26B897E144C87E9713B10378EBB', X'3403B26B897E144C87E9713B10378EBB', 3, NULL, NULL, 2, NULL, 0, 'When the rice is cooked through, remove from the heat and let sit for about 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92A7CA208F33534FB5D2472E86FF5FA3', X'3403B26B897E144C87E9713B10378EBB', X'3403B26B897E144C87E9713B10378EBB', 4, NULL, NULL, 2, NULL, 0, 'Serve over tortilla shells and top with desired toppings.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1664EB0BEFE97C48BD94CF308B2C93B6', X'8418C29A87DC1A458C6C20F28087DB80', X'8418C29A87DC1A458C6C20F28087DB80', 0, NULL, NULL, 2, NULL, 0, 'Place mushrooms into a small food processor and grind.');
