INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C03498B2ED4C8439C73F19328E98BEE', X'8418C29A87DC1A458C6C20F28087DB80', X'8418C29A87DC1A458C6C20F28087DB80', 4, NULL, NULL, 2, NULL, 0, 'Form into 4 patties. Refrigerate for 30 minutes, or overnight.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8274C359715A04D828E38F24FBAE3EB', X'8418C29A87DC1A458C6C20F28087DB80', X'8418C29A87DC1A458C6C20F28087DB80', 5, NULL, NULL, 2, NULL, 0, 'Preheat grill to high heat. Oil grates well.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8ED526DB793A740A479D6FD7DF82FF5', X'8418C29A87DC1A458C6C20F28087DB80', X'8418C29A87DC1A458C6C20F28087DB80', 6, NULL, NULL, 2, NULL, 0, 'Cook until cooked through, about 3 to 4 minutes per side. During last minute of grilling, add cheese.');
