INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACC95B6A0D9BBC44BFFBD8720C4FEFB3', X'1297E649208A154D96F27DF6944B5076', X'1297E649208A154D96F27DF6944B5076', 2, NULL, NULL, 2, NULL, 0, 'Serve your sloppy joes over lettuce or your favorite buns.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D896402C82876145A60A3C3621EF68B5', X'F584F1C9A46C76418DB41AF8B504B4DC', X'F584F1C9A46C76418DB41AF8B504B4DC', 0, NULL, NULL, 2, NULL, 0, 'Heat a sauté pan or griddle over medium high heat. Place 2 tbsp. of olive oil in pan, then add single slices of prosciutto and cook until crispy. Remove and place on a paper towel to drain.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F05C397D43F38047929A09E74953CE2D', X'F584F1C9A46C76418DB41AF8B504B4DC', X'F584F1C9A46C76418DB41AF8B504B4DC', 1, NULL, NULL, 2, NULL, 0, 'Using the same pan, add the turkey tenderloin pieces in the pan and sauté until lightly browned and heated throughout (approximately 4 minutes). Note: turkey tenderloin pieces can be served cold over the salad if desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B94AB1E35BEDC4EB467669042465FC4', X'F584F1C9A46C76418DB41AF8B504B4DC', X'F584F1C9A46C76418DB41AF8B504B4DC', 2, NULL, NULL, 2, NULL, 0, 'While the turkey bites are heating, place the chopped kale, red grapes, pistachios and apples in a mixing bowl. Add the dressing and gently toss until thoroughly coated.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4167FB2E4386A43AE3F49E280E056D0', X'F584F1C9A46C76418DB41AF8B504B4DC', X'F584F1C9A46C76418DB41AF8B504B4DC', 3, NULL, NULL, 2, NULL, 0, 'Place mixed salad on a large platter, then top with turkey bites and crispy prosciutto. Break up the parmesan crisps and sprinkle over the entire salad.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF6A28D49609434698A1F5F50250E412', X'F584F1C9A46C76418DB41AF8B504B4DC', X'F584F1C9A46C76418DB41AF8B504B4DC', 4, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E2802B463890C4ABF564E15EC76EF6A', X'F584F1C9A46C76418DB41AF8B504B4DC', X'F584F1C9A46C76418DB41AF8B504B4DC', 5, NULL, NULL, 2, NULL, 0, 'Note: to prevent apples from browning, squeeze 1 lemon into a container and add 2 cups of water. Slice the apples and place in the liquid until needed.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFE5B04CE4E0B843A0A9222C98A6915F', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 375°F degrees.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'810127AF580BD3458097AC230B23DAF3', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', 1, NULL, NULL, 2, NULL, 0, 'Begin by rinsing your quinoa thoroughly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A04D77499F0A847AFE4070B9A499215', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', 2, NULL, NULL, 2, NULL, 0, 'In a greased 9x13 pan, add the rinsed quinoa to the bottom of the pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05FEE3C522E3944991B44D63E4C1B56B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', 3, NULL, NULL, 2, NULL, 0, 'In a sauce pan, heat the spaghetti sauce and chicken broth until hot. Remove from the stovetop and pour the liquid over the quinoa, until it is fully covered.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC2C0174A4295F44848ACE09FB57F79D', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', 4, NULL, NULL, 2, NULL, 0, 'Place meatballs on top of the quinoa and finish with thin slices of mozzarella (you can put them over the meatball or just dabble on top of the dish).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C21FFCE656AC1F4CBBBD595BDD5CE400', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', 5, NULL, NULL, 2, NULL, 0, 'Cover the dish tightly with foil and bake for 35-40 minutes. During the last 5-10 minutes, remove the foil to melt the cheese fully.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'172C2EBCCA2AB64B80A07BCE9566F43F', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', 6, NULL, NULL, 2, NULL, 0, 'While the dish is baking, pour balsamic vinegar into a small saucepan. Turn heat to medium high and bring vinegar to a boil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'320932A0126071448774791CA9198BB5', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', 7, NULL, NULL, 2, NULL, 0, 'Once boiling, reduce to simmer and let simmer for about 10 minutes until vinegar has reduced down. Stir occasionally. When it begins to coat the back of a spoon, you will know that it is fully reduced. Set aside to cool while the casserole bakes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25702EDD59CD37478DDE17F275AC2DA3', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', 8, NULL, NULL, 2, NULL, 0, 'Cook until the meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EBF7F58B2B99843A82CABBCB258A16B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', X'2189D6BA4B8F1447AA5C4C5F44C26A8B', 9, NULL, NULL, 2, NULL, 0, 'Once your dish is done baking, garnish with thinly sliced basil and your balsamic reduction.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14D1B6956EAA6B40966959204A1A93D3', X'04A7E54006DB5543812C689463320420', X'04A7E54006DB5543812C689463320420', 0, NULL, NULL, 2, NULL, 0, 'Take the turkey breast strips out of the package and rinse under cold water. Pat dry lightly with paper towel and place in a bowl. Toss with olive oil and season with salt and pepper, to taste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FCD0BE412AC564481F1DD1AF55A58F0', X'04A7E54006DB5543812C689463320420', X'04A7E54006DB5543812C689463320420', 1, NULL, NULL, 2, NULL, 0, 'Thread turkey strips onto skewers and place on a platter.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5983740A60AD14FBCD4DA170C84246C', X'04A7E54006DB5543812C689463320420', X'04A7E54006DB5543812C689463320420', 2, NULL, NULL, 2, NULL, 0, 'Grill the turkey for 2 minutes per side or until fully cooked through. Turkey is fully cooked when meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25D83DB71384AA4EA147430C250BD39C', X'04A7E54006DB5543812C689463320420', X'04A7E54006DB5543812C689463320420', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining salad ingredients into a large bowl and toss with the dressing. Add salt and pepper to taste. Serve immediately.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7169798BBB74A74DB47A04B22A231678', X'343BFACEEED07747A8154F8BE0FAC091', X'343BFACEEED07747A8154F8BE0FAC091', 0, NULL, NULL, 2, NULL, 0, 'Prepare the sliders. Divide the turkey into 8 equal balls. Shape each ball into a patty approximately 3` across, or about the size of the slider buns. Cover and refrigerate until needed.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1052012B9621AE40996687F547200EE7', X'343BFACEEED07747A8154F8BE0FAC091', X'343BFACEEED07747A8154F8BE0FAC091', 1, NULL, NULL, 2, NULL, 0, 'Season the sliders with salt and pepper, as desired. On a grill or in a sauté pan, cook over medium heat for 5-8 minutes or until an internal temperature of 165°F is reached on a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3809233F1442AD459562006360A8D50A', X'343BFACEEED07747A8154F8BE0FAC091', X'343BFACEEED07747A8154F8BE0FAC091', 2, NULL, NULL, 2, NULL, 0, 'Spread the mayo evenly on the buns.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64C3A421F6252F4D802EDE3F73DC1947', X'343BFACEEED07747A8154F8BE0FAC091', X'343BFACEEED07747A8154F8BE0FAC091', 3, NULL, NULL, 2, NULL, 0, 'When the sliders are cooked, remove from heat and place on the bottom buns.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB2A70DEEF84E6458A5EF6C28110E072', X'343BFACEEED07747A8154F8BE0FAC091', X'343BFACEEED07747A8154F8BE0FAC091', 4, NULL, NULL, 2, NULL, 0, 'Place 2 slices of tomato on each sandwich. Add the bacon and lettuce. Close the sandwiches, and serve!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'749F31C131BA8E4C9276E2DFC0F6BF38', X'54A46A59F3EFF94F808D50599276C58F', X'54A46A59F3EFF94F808D50599276C58F', 0, NULL, NULL, 2, NULL, 0, 'To prep the Citrus Turkey Cutlets, combine all ingredients except turkey in a food processor. Blend to a coarse puree.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16F613F722B8B54C8965B069AB8AB41C', X'54A46A59F3EFF94F808D50599276C58F', X'54A46A59F3EFF94F808D50599276C58F', 1, NULL, NULL, 2, NULL, 0, 'Place turkey in a gallon freezer bag. Add marinade.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A35F69256BE584980797A3E776A759B', X'54A46A59F3EFF94F808D50599276C58F', X'54A46A59F3EFF94F808D50599276C58F', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 4 hours or overnight, turning occasionally.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4FD5A3FF1C90D478BDE9514CDC99908', X'54A46A59F3EFF94F808D50599276C58F', X'54A46A59F3EFF94F808D50599276C58F', 3, NULL, NULL, 2, NULL, 0, 'To cook the Citrus Turkey Cutlets and prepare a Cilantro Chimichurri, make the chimichurri. Combine the cilantro, parsley, garlic, red pepper flakes, cumin, salt, and red wine vinegar in the bowl of a food processor. Puree until smooth. Adjust seasoning with salt and red pepper as needed.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'088A11494DF7064183045E12E929B443', X'54A46A59F3EFF94F808D50599276C58F', X'54A46A59F3EFF94F808D50599276C58F', 4, NULL, NULL, 2, NULL, 0, 'Stir in olive oil. Serve at room temperature.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65918F0FB4A7BB48AA452E07CC619304', X'54A46A59F3EFF94F808D50599276C58F', X'54A46A59F3EFF94F808D50599276C58F', 5, NULL, NULL, 2, NULL, 0, 'Remove the turkey cutlets from the marinade and grill over medium heat for about 4 minutes per side, or until an internal temperature of 165°F is reached on a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E9DDBD6DCB2C940AF03C59A066D0297', X'5671D023EC2A864DA71E1A86323CABB4', X'5671D023EC2A864DA71E1A86323CABB4', 0, NULL, NULL, 2, NULL, 0, 'To prep the Citrus Turkey Cutlets, combine all ingredients except turkey in a food processor. Blend to a coarse puree.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E048660485CD54EB3281FDAFA882784', X'5671D023EC2A864DA71E1A86323CABB4', X'5671D023EC2A864DA71E1A86323CABB4', 1, NULL, NULL, 2, NULL, 0, 'Place turkey in a gallon freezer bag. Add marinade.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2686A54DC680264FB53914B2B4826451', X'5671D023EC2A864DA71E1A86323CABB4', X'5671D023EC2A864DA71E1A86323CABB4', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 4 hours or overnight, turning occasionally.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C34CB062ADF5734B825ED5BD7DF2B075', X'5671D023EC2A864DA71E1A86323CABB4', X'5671D023EC2A864DA71E1A86323CABB4', 3, NULL, NULL, 2, NULL, 0, 'To prepare the salad, make the vinaigrette. Combine the red wine vinegar, mustard, garlic, salt and pepper. Stir until salt is dissolved. Slowly whisk in the olive oil. Reserve.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB10155BED0D1244BC81122B50E22FCD', X'5671D023EC2A864DA71E1A86323CABB4', X'5671D023EC2A864DA71E1A86323CABB4', 4, NULL, NULL, 2, NULL, 0, 'Remove the turkey cutlets from the marinade and grill over medium heat for about 4 minutes per side, or until an internal temperature of 165¬8F is reached on a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEC2C093171A9949904E9AF9EB702C5A', X'5671D023EC2A864DA71E1A86323CABB4', X'5671D023EC2A864DA71E1A86323CABB4', 5, NULL, NULL, 2, NULL, 0, 'Toss the salad ingredients, including field greens, avocado, grape tomatoes, and red onion, with enough vinaigrette to lightly coat. Pile the salad in the center of plate.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FF0D25B0D48FC4E9FCCA94A6AD21208', X'C2279EFE1DBC5744A47D2F31365DC9C6', X'C2279EFE1DBC5744A47D2F31365DC9C6', 0, NULL, NULL, 2, NULL, 0, 'TIP: Skip to step 9 if using Honeysuckle White HomeStyle Turkey Meatloaf');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27E90E8A02785D4CACB6C4361CD7CFB5', X'C2279EFE1DBC5744A47D2F31365DC9C6', X'C2279EFE1DBC5744A47D2F31365DC9C6', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Spray a 9x5` loaf pan with food release spray and then line with aluminum foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1914C426943BB047BF0BE6ACB14B0ED5', X'C2279EFE1DBC5744A47D2F31365DC9C6', X'C2279EFE1DBC5744A47D2F31365DC9C6', 2, NULL, NULL, 2, NULL, 0, 'Heat oil in a sauté pan until shimmering. Add the onions and sauté over medium heat for 4-5 minutes, or until softened.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AD509304793CA41A5FAE4290F162084', X'C2279EFE1DBC5744A47D2F31365DC9C6', X'C2279EFE1DBC5744A47D2F31365DC9C6', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic and sauté for 1 minute. Add the mushrooms, salt, and pepper. Increase heat to high and sauté mushrooms, stirring frequently for about 5 minutes or until done.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8167126B660D745BF244CA34730E47E', X'C2279EFE1DBC5744A47D2F31365DC9C6', X'C2279EFE1DBC5744A47D2F31365DC9C6', 4, NULL, NULL, 2, NULL, 0, 'Transfer the onions and mushrooms to a large bowl. Stir in soy sauce and ketchup.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9BB58A1555C324B96224E7AF9FF076C', X'C2279EFE1DBC5744A47D2F31365DC9C6', X'C2279EFE1DBC5744A47D2F31365DC9C6', 5, NULL, NULL, 2, NULL, 0, 'Combine the panko breadcrumbs and milk in a small bowl. Add the eggs and stir well to combine.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB727BD9603FAE499B557423A3EA860E', X'C2279EFE1DBC5744A47D2F31365DC9C6', X'C2279EFE1DBC5744A47D2F31365DC9C6', 6, NULL, NULL, 2, NULL, 0, 'Add the breadcrumb-egg mixture to the sautéed mushrooms, and then add the ground turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A1CFE4C8E7B784AA45FA909DE2AF654', X'C2279EFE1DBC5744A47D2F31365DC9C6', X'C2279EFE1DBC5744A47D2F31365DC9C6', 7, NULL, NULL, 2, NULL, 0, 'Using a rubber spatula or your hand, gently mix all ingredients until combined. Do not over mix.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BB281F80249364D9B01D4C7B82B26ED', X'C2279EFE1DBC5744A47D2F31365DC9C6', X'C2279EFE1DBC5744A47D2F31365DC9C6', 8, NULL, NULL, 2, NULL, 0, 'Scrape the meatloaf mixture into the foil-lined pan and smooth the top.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'178B8ECE2A871E428A7AAF32938F7EC3', X'C2279EFE1DBC5744A47D2F31365DC9C6', X'C2279EFE1DBC5744A47D2F31365DC9C6', 9, NULL, NULL, 2, NULL, 0, 'Bake for 50-55 minutes or until a meat thermometer reads 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBD1252C2D893945A4DA3844399EF242', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Spray a 9x5` loaf pan with food release spray and then line with aluminum foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B74CEAB4BD8859489B56626EB1A9B7B7', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 1, NULL, NULL, 2, NULL, 0, 'To create the glaze, combine ketchup, brown sugar, white vinegar, and red pepper flakes in a small bowl. Stir until blended and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E7CEE1876796D488764107CC9FAA0EB', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 2, NULL, NULL, 2, NULL, 0, 'Heat oil in a sauté pan until shimmering. Add the onions and sauté over medium heat for 4-5 minutes, or until softened.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C878F526654DA4DBD0EFB4E7E89B912', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic and sauté for 1 minute.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2496D6DE365CA4E953B8E9E10B50718', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 4, NULL, NULL, 2, NULL, 0, 'Add the mushrooms, salt, and pepper. Increase heat to high and sauté mushrooms, stirring frequently for about 5 minutes or until done.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC5A1E2E9698954A9D7A72A35EDD67C5', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 5, NULL, NULL, 2, NULL, 0, 'Transfer the onions and mushrooms to a large bowl. Stir in soy sauce and ketchup.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'246B33C5ACA45D49AAFCD5B9F453C73D', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 6, NULL, NULL, 2, NULL, 0, 'Combine the panko breadcrumbs and milk in a small bowl. Add the eggs and stir well to combine.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B03A406DDC1BA948BFA5AEDF9CF1E3E5', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 7, NULL, NULL, 2, NULL, 0, 'Add the breadcrumb-egg mixture to the sautéed mushrooms, and then add the ground turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1A6C8B52B3A9F4E8A181186CEA33D1D', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 8, NULL, NULL, 2, NULL, 0, 'Using a rubber spatula or your hand, gently mix all ingredients until combined. Do not over mix.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'195CE27F2A07AD4B88EC03FFAB39F0F2', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 9, NULL, NULL, 2, NULL, 0, 'Scrape the meatloaf mixture into the foil-lined pan and smooth the top.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0430647E5B4A3949A8693181324E9291', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 10, NULL, NULL, 2, NULL, 0, 'Brush about 1/2 cup of glaze evenly over the meatloaf. Cover with a layer of aluminum foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D272CB8D3E928468ECF8B9F43BA3E9F', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 11, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes, then remove foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0AF19ED67F4CB4E93892F6201B95AC8', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 12, NULL, NULL, 2, NULL, 0, 'Continue baking 5-10 more minutes, or until a meat thermometer reads 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B4F060F9DCFC94ABB8263E98C629725', X'C58B489C88D5F644B5BA59074B4DFA35', X'C58B489C88D5F644B5BA59074B4DFA35', 13, NULL, NULL, 2, NULL, 0, 'Let rest 5 minutes before slicing, and serve with remaining glaze.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D0587455366724CA83E639CAFD5856D', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56C0D9E4128FB34BB7FB383766771B2E', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 1, NULL, NULL, 2, NULL, 0, 'Stir breadcrumbs and milk together in a large bowl and let sit for 5 minutes. Add the egg, cheese, parsley, oregano, and salt. Stir until well blended.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9DA7EC1F85DF441812BE3AFAC637C49', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 2, NULL, NULL, 2, NULL, 0, 'Add the ground turkey and use a fork, or your fingers, to gently mix until combined. Do not over mix.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B89079905C6BDE41B90889E0DC48D552', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 3, NULL, NULL, 2, NULL, 0, 'Divide the mixture into 12 equal portions.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'444EC22BF82D114D92A29749E96C14A9', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 4, NULL, NULL, 2, NULL, 0, 'Wet your hands. Form each turkey portion into a ball around a cube of mozzarella.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4149826F774024EA636BC8F91232A3C', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 5, NULL, NULL, 2, NULL, 0, 'Place the meatballs on a half sheet pan and bake for 10-15 minutes or until a meat thermometer reaches 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0C958678CAF754A9EFCB332B8E7D96A', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 6, NULL, NULL, 2, NULL, 0, 'While meatballs are baking, prepare the tomato sauce.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6ACB5E6D8362EA428975126454BC211A', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 7, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a sauté pan over medium high heat until shimmering. Add the diced onion and dried basil, and sauté for 2 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC27E034AFDDA442A11E838428F3DA03', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 8, NULL, NULL, 2, NULL, 0, 'Add the minced garlic and sauté for one minute.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED8E20CC447B6D4EB9036DB8A85BA17D', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 9, NULL, NULL, 2, NULL, 0, 'Add the crushed tomatoes and simmer for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C1E6EB192492D41A52F293807915C12', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 10, NULL, NULL, 2, NULL, 0, 'After removing the meatballs from the oven, transfer to simmering tomato sauce. Stir gently to coat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0D0FFAE43A1F242B38437C4AC31F420', X'9E53F1EF650E5B4582080DA48D4DE131', X'9E53F1EF650E5B4582080DA48D4DE131', 11, NULL, NULL, 2, NULL, 0, 'Sprinkle with Italian parsley and parmesan cheese, and enjoy!');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93616B84E9B92F4187D07219ABDD40D6', X'69BFB877DEE2F14CB1DA01541E9B5C47', X'69BFB877DEE2F14CB1DA01541E9B5C47', 0, NULL, NULL, 2, NULL, 0, 'To prepare the cheese-stuffed meatballs, preheat the oven to 350°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE215C5E63F3E94792A5580E1AAF6ADE', X'69BFB877DEE2F14CB1DA01541E9B5C47', X'69BFB877DEE2F14CB1DA01541E9B5C47', 1, NULL, NULL, 2, NULL, 0, 'Stir breadcrumbs and milk together in a large bowl and let sit for 5 minutes. Add the egg, cheese, parsley, oregano, and salt. Stir until well blended.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9D2F705382F7643B79F561E01B4F344', X'69BFB877DEE2F14CB1DA01541E9B5C47', X'69BFB877DEE2F14CB1DA01541E9B5C47', 2, NULL, NULL, 2, NULL, 0, 'Add the ground turkey and use a fork, or your fingers, to gently mix until combined. Do not over mix.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3DD2E4AFBCE12448722164BA3B735FB', X'69BFB877DEE2F14CB1DA01541E9B5C47', X'69BFB877DEE2F14CB1DA01541E9B5C47', 3, NULL, NULL, 2, NULL, 0, 'Divide the mixture into 12 equal portions. Wet your hands. Form each turkey portion into a ball around a cube of mozzarella.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB20315C7F319E499742586C38CD1B5B', X'69BFB877DEE2F14CB1DA01541E9B5C47', X'69BFB877DEE2F14CB1DA01541E9B5C47', 4, NULL, NULL, 2, NULL, 0, 'Place the meatballs on a half sheet pan and bake for 10-15 minutes or until a meat thermometer reaches 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'028BA0CBCC7E94459B57918C345B7CE2', X'69BFB877DEE2F14CB1DA01541E9B5C47', X'69BFB877DEE2F14CB1DA01541E9B5C47', 5, NULL, NULL, 2, NULL, 0, 'While the meatballs are cooking, spread mayonnaise evenly on hoagie buns, then add giardiniera relish (available at most grocery stores, near the pickles/olives).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F700288204085A46ABF469C89DAAB046', X'69BFB877DEE2F14CB1DA01541E9B5C47', X'69BFB877DEE2F14CB1DA01541E9B5C47', 6, NULL, NULL, 2, NULL, 0, 'Once the meatballs are finished baking, place 3 on each bun.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5749CB77BC1094458742196553890032', X'69BFB877DEE2F14CB1DA01541E9B5C47', X'69BFB877DEE2F14CB1DA01541E9B5C47', 7, NULL, NULL, 2, NULL, 0, 'Top with thinly sliced red onion rings and sliced tomatoes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82B6E2E60E7B2644AB39E34A4C03ED76', X'ED1387B6BC080C4BB3A4D28C55E821E5', X'ED1387B6BC080C4BB3A4D28C55E821E5', 0, NULL, NULL, 2, NULL, 0, 'Cook turkey tenderloins per package instructions. Place in refrigerator to cool. Once turkey is cool, shred or dice into 1/2 inch cubes. (Your preference)');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D86BFAEBD4FC6D429EA39382191E0109', X'ED1387B6BC080C4BB3A4D28C55E821E5', X'ED1387B6BC080C4BB3A4D28C55E821E5', 1, NULL, NULL, 2, NULL, 0, 'Combine turkey, cashews, celery, sour cream, cream cheese, cranberries, and onions in a bowl and mix. Salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'867B29A89242CA45A42C93F75F89DADB', X'ED1387B6BC080C4BB3A4D28C55E821E5', X'ED1387B6BC080C4BB3A4D28C55E821E5', 2, NULL, NULL, 2, NULL, 0, 'To serve, split croissant. Assemble lettuce and tomato on the bottom bun. Top with 3-4 tablespoons of turkey and place top bun.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17A3377604026346A75FA300CAF885AB', X'F52E7704AF95CC45864634BB3ED7C7D3', X'F52E7704AF95CC45864634BB3ED7C7D3', 0, NULL, NULL, 2, NULL, 0, 'For the turkey burgers, heat a saute pan or griddle over medium high heat. Once pan or griddle is hot, place the turkey burgers on the skillet or griddle and cook 4 minutes then flip. Continue to cook, approximately 4 additional minutes, until they reach a minimum internal temperature of 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'689A089893D6BB4797B43C3D543CD362', X'F52E7704AF95CC45864634BB3ED7C7D3', X'F52E7704AF95CC45864634BB3ED7C7D3', 1, NULL, NULL, 2, NULL, 0, 'While turkey burger is cooking, spread garlic mayonnaise on bottom bun.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F372EE3767D7244495A57A47DF24115B', X'F52E7704AF95CC45864634BB3ED7C7D3', X'F52E7704AF95CC45864634BB3ED7C7D3', 2, NULL, NULL, 2, NULL, 0, 'Once burgers are cooked, remove from grill/griddle. Begin assembly by placing turkey patty on top of the bottom bun. Top turkey patty with arugula, cranberry sauce, red onion and feta cheese. Place top bun and serve.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0FC1C9A54D8B3488427F3C2571D0169', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', 0, NULL, NULL, 2, NULL, 0, 'Cook pasta according to instructions on box. Set aside until ready to serve. (Do not add to soup while cooking as it will absorb the stock and expand.)');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F17C65B6B6F47B42A02457E300A247FA', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', 1, NULL, NULL, 2, NULL, 0, 'Place olive oil in large stock pot. Cook turkey and sausage until brown and internal temperature is 165°F. Do not drain any of the fat. Remove the turkey and sausage and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'537AB8C7C2A2A647AB5C7EA3015DD3E2', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', 2, NULL, NULL, 2, NULL, 0, 'In the same stock pot, add the garlic, onions, carrots and celery. Cook until softened. Add the turkey and sausage back into the pot and stir.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F43E651CA9E18543BE33184A622142C8', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', 3, NULL, NULL, 2, NULL, 0, 'Add the chicken stock, tomato sauce, diced tomatoes, basil, oregano, thyme and salt and pepper. Simmer for 30 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9B6CD5C53C16F49BDC9E6D35B996914', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', 4, NULL, NULL, 2, NULL, 0, 'After 30 minutes, add the beans and cook an additional 10 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E120400B03B594D8C075EBBEE77BA92', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', X'04D38E88B6ED8B4687BEE2C6E8D8E51B', 5, NULL, NULL, 2, NULL, 0, 'Prior to serving, add the pasta. Garnish with freshly grated parmesan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70787D29FEE40D48B2854B7CCD95FC7E', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', 0, NULL, NULL, 2, NULL, 0, 'Heat a cast iron skillet or griddle over medium high heat and when hot, grease lightly with a teaspoon of oil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABEA5624B07BAF45968F2B463D17CB46', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', 1, NULL, NULL, 2, NULL, 0, 'Transfer the turkey burgers to the skillet or griddle and cook 4 minutes then flip.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E774BF638475274BB8081CDC17208311', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', 2, NULL, NULL, 2, NULL, 0, 'Cook another 2 minutes then place a slice of cheese on each burger and cook for another 2 minutes. Use a meat thermometer to confirm internal temperature is 165°F throughout each burger.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD1E66F917BDF342BBDF9D96A15B5E2B', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat a nonstick skillet over medium heat. Add the remaining oil, then the mushrooms.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'714D5B2A66A2FB4CB2295AEAE773894D', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle mushrooms with a pinch of salt and cook until they begin to brown, about 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43EE6EBEE3F7C94F867D53A7250D603D', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', 5, NULL, NULL, 2, NULL, 0, 'Combine mustard and mayonnaise in a small bowl and mix. Spread mustard and mayonnaise mixture on the inside of each toasted bun.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'939E374682677F49AFA4C3DDE15A8B6B', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', X'E04CD3F17BC50B4C9A9BDCFA91EF8A44', 6, NULL, NULL, 2, NULL, 0, 'Place the cooked burgers on the bottom bun. Add a spoonful of the mushrooms on top of the cheese. Top with top buns and serve.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5053C869100FD94293C550EF4AFEDA15', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', 0, NULL, NULL, 2, NULL, 0, 'Cut the wings into three pieces at the joints, reserving the wing tips for another use if desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ADC5B2E4B399D4D8BD6B5B2C763ED2A', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', 1, NULL, NULL, 2, NULL, 0, 'Combine the soy sauce, maple syrup, apple cider vinegar, garlic and ginger in a bowl and reserve 1/3 cup. Transfer the 1/3 cup to a container and store in the refrigerator.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D387045134F46249A86D17E1B5142884', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', 2, NULL, NULL, 2, NULL, 0, 'Place the two larger sections of the wings into a large zip top plastic bag. Pour the rest of the marinade into the bag. Seal the bag and refrigerate the wings. Allow the wings to marinate for at least 8 hours or overnight.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0CD1014A641F949806797AC94F371EA', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', 3, NULL, NULL, 2, NULL, 0, 'Heat the oven to 300 degrees. Line a baking pan with foil, then spray the foil with non-stick spray or oil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'232576A1D5A6F64DA2BADCE208F67BA6', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', 4, NULL, NULL, 2, NULL, 0, 'Place the wings on the pan skin side up, cover with foil and bake for 2 hours then uncover, flip the wings, cover again with foil and bake for another 30 minutes, or until the turkey wings are fork tender and a meat thermometer registers 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F473CEF0E8755744AF64F71BD64E7E7E', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', X'7AC5F1B1EAB11B42BC8FFE427257BBE7', 5, NULL, NULL, 2, NULL, 0, 'Remove the foil, flip the wings again and place a few inches under the broiler. Broil for 5-10 minutes, basting frequently with the reserved marinade, until the skin is brown and sticky.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4AFC2A19AE95A438C94744876018732', X'F95C7A4BEFE7CB4BA0C578440768DE74', X'F95C7A4BEFE7CB4BA0C578440768DE74', 0, NULL, NULL, 2, NULL, 0, 'Heat a large heavy skillet over medium high heat and add the oil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFA6EE7113421F418E784C0B53B4CEC1', X'F95C7A4BEFE7CB4BA0C578440768DE74', X'F95C7A4BEFE7CB4BA0C578440768DE74', 1, NULL, NULL, 2, NULL, 0, 'Place drumsticks in the skillet and brown on all sides.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19E63A865DD7F642906BB1BBBDBDACC7', X'F95C7A4BEFE7CB4BA0C578440768DE74', X'F95C7A4BEFE7CB4BA0C578440768DE74', 2, NULL, NULL, 2, NULL, 0, 'Place the potatoes, carrots, onions and garlic in an oval 5-6 quart slow cooker. Place drumsticks on top of the vegetables.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'191BE43F40EDE349835CBA9F0C3ED4F4', X'F95C7A4BEFE7CB4BA0C578440768DE74', X'F95C7A4BEFE7CB4BA0C578440768DE74', 3, NULL, NULL, 2, NULL, 0, 'Combine the broth, Worcestershire sauce and salt in a small bowl and pour over the drumsticks. Sprinkle the drumsticks with the poultry seasoning.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C2BBC92B556C44589F1168D96681D69', X'F95C7A4BEFE7CB4BA0C578440768DE74', X'F95C7A4BEFE7CB4BA0C578440768DE74', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW until the turkey and vegetables are tender, about 6 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80C1B2078766864CB8E78CC652D857DF', X'F95C7A4BEFE7CB4BA0C578440768DE74', X'F95C7A4BEFE7CB4BA0C578440768DE74', 5, NULL, NULL, 2, NULL, 0, 'Serve the turkey with the vegetables. Spoon some of the onions and sauce over the meat and garnish with parsley if desired. Season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70DA95221EA8BB48A441452C4419BB70', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F. Cooking time will vary by size of bird.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99C47630FCD8C44D943C6EB8561A9386', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 1, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the bag. Remove giblet package underneath the neck skin area as well as the neck from the body cavity.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3BAE03ED895944996C0DE521620C44C', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 2, NULL, NULL, 2, NULL, 0, 'Place turkey, breast side up, in a shallow roasting pan. Pat the outside and inside of the turkey dry with paper towels.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0FC44631ADBD94C9AEFD5FEE9468E20', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 3, NULL, NULL, 2, NULL, 0, 'Combine salt and black pepper in a small bowl. Season the inside of the turkey with about 1/3 of the salt mixture. Fold wing tips under the bird.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1302B3ABDDA0BF47AE2B8CBC95DEFE2C', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 4, NULL, NULL, 2, NULL, 0, 'Place the apple, orange and lemon slices, onion, and 2 sprigs of each sage in the main turkey cavity (for aromatics and garnish only). Tie the legs together to hold the shape of the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C54147973A4A54AAD303C9264C3974C', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 5, NULL, NULL, 2, NULL, 0, 'Melt butter in small saucepan over medium heat until the edges begin to turn golden, about 2 minutes. Brush outside of the turkey completely with the melted butter. Season with remaining 2/3 of the salt mixture and place in oven for the recommended roasting time.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55D8FB342DBED048B7224761A6E9D3D8', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 6, NULL, NULL, 2, NULL, 0, 'If the turkey becomes golden brown before it is fully cooked, cover with a loose tent of aluminum foil to prevent over browning. Cut a hole in the foil to view the pop-up timer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7068EA2EA46DD24D87D6E8B58DCE349D', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 7, NULL, NULL, 2, NULL, 0, 'For an optimal eating experience, turkey is done when the pop-up timer has popped and a meat thermometer registers at 170°F, in the deepest part of the breast and thickest part of the inner-thigh.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D966D8E55D72F4B816D8D4A8FBD55E6', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 8, NULL, NULL, 2, NULL, 0, 'To prepare the Caramelized Apples & Oranges, heat oven to 375°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17406A1585FF4843A8EACE78AA37DAAF', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 9, NULL, NULL, 2, NULL, 0, 'In a large bowl, toss together the apples, oranges, orange juice, cinnamon, salt, and brown sugar. Mound the apple mixture in baking pan or casserole and scatter the butter over the top.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E143F29E73D0834EA35FB29B0C36CCBE', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 10, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes, then take out and toss with a large spoon to coat and place back in the oven for another 30 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'736CCD64ED088D49A3CF0B0BEC79B5CA', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 11, NULL, NULL, 2, NULL, 0, 'After taking it out of the oven, carefully stir and let dish rest roughly 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6829035FC4198B469EFFD739C9C119BF', X'9858CDB981FD8548A596CC76438DD1D8', X'9858CDB981FD8548A596CC76438DD1D8', 12, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the oven and allow to rest for 20-30 minutes before slicing. Garnish serving dish with caramelized apples & oranges, sage sprigs and fig slices.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCE627B059417B47BC809326E86957CA', X'1A6BA990A0DA064E94CDA08B62AA6C97', X'1A6BA990A0DA064E94CDA08B62AA6C97', 0, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the fridge one hour before roasting. Remove the giblets and neck. Rinse the turkey, pat dry and allow to come to room temperature.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DCC8A007F0B364588A50AB0F933E2FB', X'1A6BA990A0DA064E94CDA08B62AA6C97', X'1A6BA990A0DA064E94CDA08B62AA6C97', 1, NULL, NULL, 2, NULL, 0, 'To make the chimichurri butter, place the parsley, scallions, garlic, and oregano in the food processor. Pulse until finely chopped, stopping to scrape down the sides of the bowl as necessary. Add the butter and the vinegar, mix well to combine, making sure the butter is smooth and the herbs evenly mixed throughout, place in a bowl and add the red pepper flakes and olive oil. Mix well and set aside. Rub the chimichurri butter all over the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B774795CAE3FFC40A6A0EA6C82837A4E', X'1A6BA990A0DA064E94CDA08B62AA6C97', X'1A6BA990A0DA064E94CDA08B62AA6C97', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450°F. Position a rack in the lower third of the oven. Place the turkey in a large roasting pan. Pour 3 cups of chicken broth into the bottom of the roasting pan. Place the turkey in the oven and turn down the heat to 350°F. Cook for about 3 hours (until the turkey registers 170°F on a meat thermometer), baste the turkey with the drippings about 4 times throughout cooking.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFA9DDE88A220043B9E19E31743F20C8', X'1A6BA990A0DA064E94CDA08B62AA6C97', X'1A6BA990A0DA064E94CDA08B62AA6C97', 3, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the oven, transfer the turkey to a baking sheet or platter, tent loosely with foil and let rest for about 30 minutes before slicing.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FDA0C7513C98144B0F54B5364B973C2', X'B29DFD1245736748BB139B1F77BCF85F', X'B29DFD1245736748BB139B1F77BCF85F', 0, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat a teaspoon of the oil over medium heat. Add the ground turkey and cook, stirring often, until no pink remains. Remove the turkey to a plate.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31170F238844B1478A09B264A3F2C32B', X'B29DFD1245736748BB139B1F77BCF85F', X'B29DFD1245736748BB139B1F77BCF85F', 1, NULL, NULL, 2, NULL, 0, 'Heat the remaining oil in the skillet and add the onion, bell pepper, and squash. Sauté until the onions start to soften - 3-4 minutes. Stir in the garlic, ginger, and spices and sauté until fragrant, another minute or two.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C0C29D9B3C1304FA00A0C6B4A2A9D21', X'B29DFD1245736748BB139B1F77BCF85F', X'B29DFD1245736748BB139B1F77BCF85F', 2, NULL, NULL, 2, NULL, 0, 'Add the turkey back to the pan along with the tomatoes and coconut milk. Season with salt and pepper. Bring the mixture to a boil, then reduce the heat to a steady simmer, cover, and continue to cook, stirring occasionally, until the butternut squash is softened.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61CD9215ECABF440A52F54715EBBCD7F', X'B29DFD1245736748BB139B1F77BCF85F', X'B29DFD1245736748BB139B1F77BCF85F', 3, NULL, NULL, 2, NULL, 0, 'Serve the mixture with a swirl of Greek yogurt, a sprinkling of fresh chopped cilantro, and rice, if desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61013481BC482B4891E38DD34BD7CFC5', X'B29DFD1245736748BB139B1F77BCF85F', X'B29DFD1245736748BB139B1F77BCF85F', 4, NULL, NULL, 2, NULL, 0, 'Recipe and images courtesy of ''Neighborfood''');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D178AF366D98CC4CBAA0A1C8FBA6B95E', X'A0602080AC9E9847A3821E73081060FD', X'A0602080AC9E9847A3821E73081060FD', 0, NULL, NULL, 2, NULL, 0, 'Apple Cider Brine: In a large stock pot over medium-high heat, add the ingredients listed under the Apple Cider Brine heading. Bring the brine to a boil. Then remove the brine from the hot stove and let it cool to room temperature.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8208028C5DC26E49AC366EE24BF3BBCB', X'A0602080AC9E9847A3821E73081060FD', X'A0602080AC9E9847A3821E73081060FD', 1, NULL, NULL, 2, NULL, 0, 'Place the thawed, unwrapped turkey in a brining bag or in a large container, and pour the cooled Apple Cider Brine over the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'475CE918C88477499690DE01159647FF', X'A0602080AC9E9847A3821E73081060FD', X'A0602080AC9E9847A3821E73081060FD', 2, NULL, NULL, 2, NULL, 0, 'Seal the bag and refrigerate it for 12 to 24 hours. The larger the turkey, the longer it should remain in the brine.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E62C603F950F3841B7EF7FFE3A866977', X'A0602080AC9E9847A3821E73081060FD', X'A0602080AC9E9847A3821E73081060FD', 3, NULL, NULL, 2, NULL, 0, 'After the allotted time, remove the turkey from the brine and rinse inside and out.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF187759364B4B4E92F27DF393555B78', X'A0602080AC9E9847A3821E73081060FD', X'A0602080AC9E9847A3821E73081060FD', 4, NULL, NULL, 2, NULL, 0, 'Roast Turkey: Preheat the oven to 325°F.');
