INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3813B840F993564F99EDB9805ADA6507', X'59FFA34F3C87774CAD7946EE848DC567', X'59FFA34F3C87774CAD7946EE848DC567', 0, NULL, NULL, 2, NULL, 0, 'In saucepan, combine apple cider vinegar, Truvia sweetener, and mustard; bring to boil over medium-high heat. Cook for 10-12 minutes or until reduced by half.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE6F3565AAB1D64EA94D139EC115EF78', X'59FFA34F3C87774CAD7946EE848DC567', X'59FFA34F3C87774CAD7946EE848DC567', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat oven to 450°F. Pat turkey breast dry with paper towel. Stir together butter, thyme, garlic, salt and pepper; rub all over turkey, including under the skin.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC19BE02F1736840A8D2874555A2F387', X'59FFA34F3C87774CAD7946EE848DC567', X'59FFA34F3C87774CAD7946EE848DC567', 2, NULL, NULL, 2, NULL, 0, 'Place turkey on rack in roasting pan; roast for 40 minutes. Reduce heat to 400°F; roast, basting with apple cider glaze for about 30 minutes or until thermometer inserted into thickest part of the breast reaches 165°F. If turkey browns too quickly, cover loosely with foil. Slice turkey and serve. Delicious with cranberry sauce! Substitute rosemary or marjoram for thyme, if desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FFBD245F516A5469A3C097674A37DB9', X'6FF948D1F39EF84BA0EB8B6D06041CEF', X'6FF948D1F39EF84BA0EB8B6D06041CEF', 0, NULL, NULL, 2, NULL, 0, 'Stir together paprika, chili powder, salt, onion powder, garlic powder, mustard powder, cumin, oregano, Truvia blend and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23F9EF1AFA430D4285B7BDF66EB942B4', X'6FF948D1F39EF84BA0EB8B6D06041CEF', X'6FF948D1F39EF84BA0EB8B6D06041CEF', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F. Remove neck and giblets from turkey; reserve turkey for roasting pan. Pat turkey dry with paper towel. Rub turkey all over, including under skin and inside cavity, with butter and seasoning mixture.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46856F6149977A4DBEAB3B982B30E9D0', X'6FF948D1F39EF84BA0EB8B6D06041CEF', X'6FF948D1F39EF84BA0EB8B6D06041CEF', 2, NULL, NULL, 2, NULL, 0, 'Place on rack in roasting pan. Add neck, giblets, onion, celery and garlic to pan; roast for 30 minutes. Reduce heat to 350°F; roast for 2.5 hours or until thermometer inserted into thickest part of the breast registers 170°F. If turkey browns too quickly, cover loosely with foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C759F8DFCA72545BC0AAB10695C6685', X'6FF948D1F39EF84BA0EB8B6D06041CEF', X'6FF948D1F39EF84BA0EB8B6D06041CEF', 3, NULL, NULL, 2, NULL, 0, 'Carefully transfer turkey to cutting board and tent loosely with foil. Let stand 30 minutes before carving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8F80A46EE8C124E8E827D84070580D9', X'6FF948D1F39EF84BA0EB8B6D06041CEF', X'6FF948D1F39EF84BA0EB8B6D06041CEF', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, skim off fat in roasting pan; stir in broth and barbecue sauce. Use your favorite flavored barbecue sauces, such as spicy or sweet.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7631CF1579E6EE4FBE3AE8476E2B1794', X'6FF948D1F39EF84BA0EB8B6D06041CEF', X'6FF948D1F39EF84BA0EB8B6D06041CEF', 5, NULL, NULL, 2, NULL, 0, 'Cook over medium heat, stirring to scrape up brown bits from bottom of pan; bring to boil. Whisk cornstarch with 2 tablespoons of water; stir into pan. Reduce heat to medium-low; simmer for 6-8 minutes or until thickened and flavorful. Strain and serve with turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F7E8D3A937F8D47BFC303E588623CB8', X'4E3E38E1ED6CC5418A7182F0A269ACC6', X'4E3E38E1ED6CC5418A7182F0A269ACC6', 0, NULL, NULL, 2, NULL, 0, 'In food processor, combine parsley, cilantro, basil and garlic; pulse until finely chopped. Add olive oil, lemon zest, lemon juice, salt, pepper, paprika, cumin, coriander, and hot pepper flakes (optional); puree until smooth. Tip: Substitute 1/8 teaspoon cayenne pepper for hot pepper flakes if desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9601AF6312B2C42A3D9E42F19F216E1', X'4E3E38E1ED6CC5418A7182F0A269ACC6', X'4E3E38E1ED6CC5418A7182F0A269ACC6', 1, NULL, NULL, 2, NULL, 0, 'Remove neck and giblets from turkey (reserve for another use). Trim excess fat from turkey and discard. Pat turkey dry with paper towel. Rub turkey all over, including under skin and inside cavity, with 3/4 cup herb sauce. Refrigerate turkey for at least 1 hour or up to 4 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2C46265DAFB3747AFB290BA7655F89B', X'4E3E38E1ED6CC5418A7182F0A269ACC6', X'4E3E38E1ED6CC5418A7182F0A269ACC6', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill to high; grease grate well. Turn off burners in center of grill and reduce heat to medium (try to maintain temperature of 350°F). For grills with only two burners, cook on one side of the grill over indirect heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'837927D6C5AD3140926059D1EA4D91D8', X'4E3E38E1ED6CC5418A7182F0A269ACC6', X'4E3E38E1ED6CC5418A7182F0A269ACC6', 3, NULL, NULL, 2, NULL, 0, 'Place turkey breast side up, on center of grill. Cook, covered, over indirect heat, basting with remaining herb mixture, for 2.5-3 hours or until thermometer inserted into thickest part of the breast reaches 170°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'777852526D3951429526C4C3100FA0ED', X'4E3E38E1ED6CC5418A7182F0A269ACC6', X'4E3E38E1ED6CC5418A7182F0A269ACC6', 4, NULL, NULL, 2, NULL, 0, 'Carefully transfer turkey to cutting board and tent loosely with foil. Let stand for 30 minutes before carving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B016391F93ECFD46995C071D0747FC17', X'C3B577BD7E07824F8C5B6BC01B2983F9', X'C3B577BD7E07824F8C5B6BC01B2983F9', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Arrange half of the bacon strips side by side on parchment paper-lined baking sheet. Weave remaining strips over and under to create lattice pattern. Freeze for about 15 minutes or until firm.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83EB5A0BC3A42C44A18586A5F12656A0', X'C3B577BD7E07824F8C5B6BC01B2983F9', X'C3B577BD7E07824F8C5B6BC01B2983F9', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, remove neck and giblets from turkey; reserve for roasting pan. Pat turkey dry with paper towel. Whisk together maple syrup, mustard, thyme, salt and pepper; rub all over turkey, including under skin and inside cavity. Tie legs together with kitchen string and tuck in wing tips. Place turkey breast side up, on rack in roasting pan. Place bacon lattice over top to cover breasts and legs. Arrange onions, celery and carrots around turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37B57C376A68D8479C11F1BAD5595263', X'C3B577BD7E07824F8C5B6BC01B2983F9', X'C3B577BD7E07824F8C5B6BC01B2983F9', 2, NULL, NULL, 2, NULL, 0, 'Cover with foil and roast for 2.5 hours, basting occasionally with drippings. Remove foil; cook for 30-45 minutes or until thermometer inserted into thickest part of breast reads 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C72C9738171A1841A20B24BB836E96B7', X'C3B577BD7E07824F8C5B6BC01B2983F9', X'C3B577BD7E07824F8C5B6BC01B2983F9', 3, NULL, NULL, 2, NULL, 0, 'Remove kitchen string. Transfer turkey to cutting board and tent loosely with foil. Let rest for 30 minutes before carving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53C73CDBE4252E4FB174902D6326DC54', X'C3B577BD7E07824F8C5B6BC01B2983F9', X'C3B577BD7E07824F8C5B6BC01B2983F9', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, skim off fat in roasting pan; stir in broth. Cook over medium heat, stirring to scrape up brown bits from bottom of pan; bring to boil. Whisk cornstarch with water; slowly whisk into pan. Simmer for 6-8 minutes or until thickened. Strain and stir in parsley. Serve with turkey and cranberry sauce, if desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D6DE925AB07434D8DC6DBED273684B6', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 0, NULL, NULL, 2, NULL, 0, 'Start by thawing your turkey in the refrigerator, if frozen, for up to 3 days. If the turkey is larger than 14 lbs., it may take up to 4-5 days.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC6BE832234F9449AC5737F9182A103E', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 1, NULL, NULL, 2, NULL, 0, 'To prepare the brine, use a bowl that is larger than the turkey and combine the brine ingredients: water, olive oil, salt, Truvia Brown Sugar Blend, garlic powder, paprika, rosemary, lemon juice and lemon zest. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02553C70C22BFD41AD4C71A2189C210B', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 2, NULL, NULL, 2, NULL, 0, 'Next, prepare your turkey by remove the giblets and neck from the body cavity and neck cavity of the turkey, and set aside. Trim any fat from the edge of the skin of the turkey, but try to leave as much of the skin on the turkey as you can to protect the meat from drying out during the smoking process.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2505408CE51F834F8F9447F5F6F218AE', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 3, NULL, NULL, 2, NULL, 0, 'Rinse the turkey in cold, running water. Make sure to clean the body cavity and outside surfaces of the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CE4171B95602846B09BFC99CC1BC761', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 4, NULL, NULL, 2, NULL, 0, 'Separate the skin from the breast of the turkey and use your hands to make sure it comes loose. Begin at the back of the bird and move to the front, then down the sides towards the legs which allows the brine to reach the breast meat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'283CE878C04F30479C10DD2105D07864', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 5, NULL, NULL, 2, NULL, 0, 'Once this is complete, place the turkey `head first` into the brine mixture. Brine the turkey in the refrigerator for 12 hours or up to 24 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C64AD6A44CA094DB28B42ED336A2753', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 6, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the brine and rinse it with cold water, making sure to clean the whole bird, including the outside. Let dry for 5 minutes and pat dry with a paper towel.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA083C1589FC3240AB6B75BA93A8A6DA', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 7, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the dry rub ingredients: paprika, garlic powder, salt, crushed pepper, rosemary, and oregano. Use your hands to distribute the dry rub on the outside of the turkey. TIP: When making the dry rub, you can either use a simple salt/pepper mixture, the dry rub provided in the recipe, or any spice mixture of your choosing.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9397114005853048A3A3CC3AB9D6C7DD', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 8, NULL, NULL, 2, NULL, 0, 'When you are ready to smoke your turkey, let your smoker reach a temperature of 225°F. Oil the grate of the smoker to make sure your turkey doesn''t stick. TIP: Use a wood smoker for best results. For the wood chips, we suggest hickory chips that have been soaked prior to use.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97E9BD4FDC2FA548869342EEE41ACD6D', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 9, NULL, NULL, 2, NULL, 0, 'Maintain a temperature of 225-250°F throughout the entire smoking process. Turkeys take about 40 minutes per pound to smoke, and you want the thickest part of the breast to reach 170°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEBCFDA3E146FC49B6C13D98E3AF7B79', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 10, NULL, NULL, 2, NULL, 0, 'Once the turkey is removed from the smoker, it will continue to cook. Remove the turkey when a meat thermometer inserted into the thickest part of the breast reaches 170°F, cover with foil, and let rest for 20 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A76E07CA04FFE546977858C3C5C11A9D', X'ABEAD62E1587994EA21BBD18AAA38A58', X'ABEAD62E1587994EA21BBD18AAA38A58', 11, NULL, NULL, 2, NULL, 0, 'Once ready to serve, slice the turkey and serve with lemon slices and fresh rosemary. This smoked turkey recipe makes a great holiday turkey or a weekend meal with plenty of leftovers.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9B8EA4366ED7E47A5BEE86B3BFE7893', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450°F. Spray a wire rack with non-stick spray and place over a large rimmed baking sheet.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EAF49EAE2A51F4FAF7F22B181382D70', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix spices together and set aside. Remove giblets and any other parts from inside of the turkey and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4573A4A8D043064286BC3DF7D1008D28', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 2, NULL, NULL, 2, NULL, 0, 'Place turkey on a large, plastic cutting board and pat dry with a paper towel. Make sure your cutting board is dry during this process.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'369BE973AEE2A847B6406FD8DE1F886B', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 3, NULL, NULL, 2, NULL, 0, 'Place turkey breast side down onto the cutting board. Using sharp kitchen shears, cut along one side of the backbone. Then, cut along the other side of the breast to completely remove the backbone. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9758787A1893D24BB4357922A02AA629', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 4, NULL, NULL, 2, NULL, 0, 'Turn the turkey over and press down hard with both hands onto the center of the breastbone until you hear and feel the bone crack.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BFE72BBFA2B844AA5B8AB33E9DCE4E0', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 5, NULL, NULL, 2, NULL, 0, 'Turn the turkey over. Using your index finger, gently press between the skin of the turkey and the meat. Wiggle your finger from side to side and cut away extra fat as needed. Do not tear the skin. Be sure to push your fingers down to separate the skin from the thigh and drumstick.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5A25FE672AC6B4285297692439A295F', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 6, NULL, NULL, 2, NULL, 0, 'Spoon some of the spice rub under the skin and distribute evenly over the turkey. Turn the turkey over, and repeat underneath. Distribute remaining spice rub onto the outside of the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2697922E22B2434A8BD6F2DF3E29E78C', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 7, NULL, NULL, 2, NULL, 0, 'Place the turkey on the baking rack. Bake until a meat thermometer inserted into the thickest part of the breast reads 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC1E7F00E84D114C9AE40DD020A7D1B4', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 8, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the oven and carefully place on a heat-resistant surface. Tent with foil and let rest for 30 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'642DF34F156FBE4DA980F4E1AE39048D', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 9, NULL, NULL, 2, NULL, 0, 'While turkey is resting, place baking sheet (without wire rack) onto your stovetop. This will cover two burners.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'502B76812558F141B6735964C696DF87', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 10, NULL, NULL, 2, NULL, 0, 'Turn heat to medium high. Add brandy and scrape off all the bits on the bottom of the baking sheet. Turn off the heat, and pour into a fat separator.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5881D3D47C182E48B930BA0990471401', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 11, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together 1/4 cup of the turkey or chicken broth with cornstarch. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'703F35A467365E40BA50F4876272E4A4', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 12, NULL, NULL, 2, NULL, 0, 'In a small saucepan over medium heat, whisk together broth and brandy sauce from the fat separator (minus the fat).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A2EDC612F91834088435B26140D7D23', X'E0F5373644E5D2468092E1D40F82BC6A', X'E0F5373644E5D2468092E1D40F82BC6A', 13, NULL, NULL, 2, NULL, 0, 'Whisk in cornstarch mixture, salt and pepper. Whisk constantly until gravy has thickened slightly. Be sure to add any drippings from the cutting board into the gravy.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9495E8B201E25459A00728E580EC1E1', X'90B846864176754CA5A61E18CE164DF4', X'90B846864176754CA5A61E18CE164DF4', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 500°F. In a roasting pan, toss the melted butter, whole garlic cloves, butternut squash, apples, and onions together. Sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E37C8599A95AC4FBE94BC5C1473F888', X'90B846864176754CA5A61E18CE164DF4', X'90B846864176754CA5A61E18CE164DF4', 1, NULL, NULL, 2, NULL, 0, 'Mix together the room temperature butter, thyme, rosemary, orange zest, and minced garlic in a bowl. Place about 1/4 cup of salt and 2 tsps pepper in two small bowls. Get a piece of kitchen twine ready to tie the legs together. Place all of this close to the roasting pan, so you won''t get turkey juices everywhere during the prep.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C62AD55B0B2DD2479F6119786AA4D165', X'90B846864176754CA5A61E18CE164DF4', X'90B846864176754CA5A61E18CE164DF4', 2, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the bag. Locate and set aside neck and giblets package from inside the turkey cavity. Place the turkey on top of the vegetables. Cut a small slit in the skin above the cavity and gently work your hand between the skin and the breast meat. Spread the butter mixture under the skin, then rub it all over the breast, legs, thighs, and cavity of the bird. Sprinkle with Kosher salt. You probably won''t need the entire 1/4 cup, but it should look like a light snow has fallen on the turkey. Follow that up with a sprinkling of pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55675ACE5B3E36408E83DAE543969405', X'90B846864176754CA5A61E18CE164DF4', X'90B846864176754CA5A61E18CE164DF4', 3, NULL, NULL, 2, NULL, 0, 'Stuff the cavity with the orange halves and a few onion and apple slices from the pan. Tie the legs together with kitchen twine and tuck the wing tips under the bird to protect them.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75E1E1C316FBD644874C16435CB410C8', X'90B846864176754CA5A61E18CE164DF4', X'90B846864176754CA5A61E18CE164DF4', 4, NULL, NULL, 2, NULL, 0, 'Place the turkey in the oven uncovered for 30 minutes, or until it is golden brown. Remove the turkey from the oven and lower the temperature to 350°F. Pour the chicken broth, hard cider, and apple cider into the pan, scraping up any bits at the bottom. Cover the breast meat with a small strip of parchment paper. Wrap the pan in foil. Place an oven safe or probe thermometer in the thickest part of the breast meat then return to the oven and continue to roast for 2-2.5 hours or until turkey reaches about 160°F. At that point, remove the foil and continue to roast until the thickest part of the breast reaches 165°F. Remove the turkey from the oven, tent with foil, and let rest for about 30 minutes prior to carving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7804DC84890144CA43DF116DD21C0B1', X'90B846864176754CA5A61E18CE164DF4', X'90B846864176754CA5A61E18CE164DF4', 5, NULL, NULL, 2, NULL, 0, 'To make the gravy, strain the vegetables, reserving them and the liquid for another use. Skim the fat off the top of the liquid. You can use a gravy separator or place it in the fridge for a bit to make this process easier.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29C6813275657F4EA63622F2123C8D41', X'90B846864176754CA5A61E18CE164DF4', X'90B846864176754CA5A61E18CE164DF4', 6, NULL, NULL, 2, NULL, 0, 'Place 1/4 cup of the fat in a pot over medium heat. If you weren''t able to get a full 1/4 cup of fat, add enough butter to make 1/4 cup. Measure out 1/2 cup of the reserved liquid from the roasting pan and whisk it together with the cornstarch to form a slurry. Stir this into the fat in the pan, whisking until the mixture begins to thicken. Slowly stir in the broth and then the cider in a steady stream, alternating stirring and whisking.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8E1EEC6C7ADA342B4783F8071DFD513', X'90B846864176754CA5A61E18CE164DF4', X'90B846864176754CA5A61E18CE164DF4', 7, NULL, NULL, 2, NULL, 0, 'Continue to cook for another 5 or so minutes, or until the gravy is the consistency that you want. If desired, whisk in mustard and season to taste with salt and pepper. Serve the turkey with the gravy.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAC7AFC53B279A48B3EE37E8F31D777C', X'E2114C06951B9D458BF1E93693CF3870', X'E2114C06951B9D458BF1E93693CF3870', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325°F. Lightly coat the rack of a roasting pan with non-stick spray and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37D3C9012DB45147A72DB4C5C5317D97', X'E2114C06951B9D458BF1E93693CF3870', X'E2114C06951B9D458BF1E93693CF3870', 1, NULL, NULL, 2, NULL, 0, 'Remove the giblets and the neck from the turkey cavity, set aside, and pat the turkey dry with a paper towel. Place the turkey, breast side up, on the roasting pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EC7414395D7DE478F89C6964A971777', X'E2114C06951B9D458BF1E93693CF3870', X'E2114C06951B9D458BF1E93693CF3870', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the thyme, oregano, garlic powder, onion powder, paprika, cayenne pepper, chipotle chili powder, salt, pepper, and Truvia Brown Sugar Blend. Combine the seasoning mixture (reserving 1 tablespoon, set aside) with softened butter.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4A7898A1547214DAB0F70032B05BE34', X'E2114C06951B9D458BF1E93693CF3870', X'E2114C06951B9D458BF1E93693CF3870', 3, NULL, NULL, 2, NULL, 0, 'Cut a pocket between the skin and the breast meat. Work the butter mixture in the pocket, working it in between the legs and wings as best you can.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C7A36CBEB6FD642ADD87602FFD7E3BF', X'E2114C06951B9D458BF1E93693CF3870', X'E2114C06951B9D458BF1E93693CF3870', 4, NULL, NULL, 2, NULL, 0, 'Drizzle melted butter over the outside of the turkey and sprinkle remaining seasoning mixture over butter. Spread this over the outside of the entire turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B855BCC775F8AE4E8295FDA9013B24FF', X'E2114C06951B9D458BF1E93693CF3870', X'E2114C06951B9D458BF1E93693CF3870', 5, NULL, NULL, 2, NULL, 0, 'Place chopped green pepper, onion, celery and garlic inside the turkey cavity. Pour chicken broth into the bottom of the pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74CFF6D70F15CA4C8AEDB6C2F142D3F9', X'E2114C06951B9D458BF1E93693CF3870', X'E2114C06951B9D458BF1E93693CF3870', 6, NULL, NULL, 2, NULL, 0, 'Tent a sheet of aluminum foil over the turkey and place the turkey in the oven. Keep turkey loosely covered during the cooking time. Turkey should take 4-4.5 hours or until a meat thermometer inserted into the thickest part of the breast reads 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCB5D53E8D54024EA581E638FEEF6822', X'E2114C06951B9D458BF1E93693CF3870', X'E2114C06951B9D458BF1E93693CF3870', 7, NULL, NULL, 2, NULL, 0, 'Remove turkey and allow it to rest for 20 minutes before carving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE14905F390B2E4B918B194BECC59CF7', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'152A1D986A83804BA30EBBBCE484C7A2', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 1, NULL, NULL, 2, NULL, 0, 'Remove turkey from packaging.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48A0169D6D4A554BBCFA3AA8BBD381B5', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 2, NULL, NULL, 2, NULL, 0, 'Locate and set aside neck and giblets package from inside the turkey cavity.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA7AB36DF7435C43B3E184D376E72B37', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 3, NULL, NULL, 2, NULL, 0, 'Rinse turkey with cold water and completely pat dry with paper towels.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D90F41AE6CA724419DF4184F1902882A', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 4, NULL, NULL, 2, NULL, 0, 'Place the turkey on a rack set in a large roasting pan. Tuck the wing tips under the turkey body. Place the giblets and half of the onions in the pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E01BB60395FFEA46896D78B8B06F8EC0', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 5, NULL, NULL, 2, NULL, 0, 'Fill turkey cavity with remaining onion, apple, jalapeno, garlic, carrots, celery and herbs.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69ED6A74076964469621348231C18BDF', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 6, NULL, NULL, 2, NULL, 0, 'In a bowl, combine fig preserves, butter, oregano and paprika.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68543D2EDDDF5443B354F6C0466D5A81', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 7, NULL, NULL, 2, NULL, 0, 'Rub half of the fig mixture all over the turkey, working some of it between the breast and the skin. Season the turkey generously with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ACA99CD32578D43B6BE0F9559610F43', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 8, NULL, NULL, 2, NULL, 0, 'Add 2 cups broth to the roasting pan and roast turkey, basting every 30 minutes with drippings, for 1 hr.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC54152351CEEE4F9FE7EE2CBD496C24', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 9, NULL, NULL, 2, NULL, 0, 'Continue roasting the turkey until a meat thermometer inserted into the thickest part of the breast registers 165°F, about 1-1.5 hours longer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6959E730D2C04A48B310296F882B5B47', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 10, NULL, NULL, 2, NULL, 0, 'Cover the turkey loosely with foil if it begins to brown.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96B27A28D8620644841D347E7A306202', X'898E3FF76506AD4580992F38CAF6CA8E', X'898E3FF76506AD4580992F38CAF6CA8E', 11, NULL, NULL, 2, NULL, 0, 'Transfer to platter and brush with remaining fig mixture. Let rest for at least 20 minutes before carving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF07014AF6D7134198DC38EBA7283789', X'A0A9A1733EBFF641A18341D882E3BAAD', X'A0A9A1733EBFF641A18341D882E3BAAD', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the garlic, mustard, herbs, salt, pepper, olive oil and lemon juice to make a paste. Loosen the skin from the meat gently with your fingers and smear half of the paste directly on the meat. Spread the remaining paste evenly on the skin. Pour the wine into the bottom of your slow cooker.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA3D599EA6A05740B86A82B6D30C9E58', X'A0A9A1733EBFF641A18341D882E3BAAD', X'A0A9A1733EBFF641A18341D882E3BAAD', 1, NULL, NULL, 2, NULL, 0, 'Place the turkey carefully over the wine and cook on LOW for 5-7 hours. Turkey is done when a meat thermometer inserted into the thickest part of the breast reaches 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9127BC2F9580C4EBA43B451883F92B3', X'A0A9A1733EBFF641A18341D882E3BAAD', X'A0A9A1733EBFF641A18341D882E3BAAD', 2, NULL, NULL, 2, NULL, 0, 'Once the turkey is done, remove and put in an oven-safe dish, unless your slow cooker is oven safe. Broil for 3-5 minutes, watching the turkey carefully to make sure not to burn the skin.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66EA42B15BF0F3439D7EDF89F558AFF7', X'A0A9A1733EBFF641A18341D882E3BAAD', X'A0A9A1733EBFF641A18341D882E3BAAD', 3, NULL, NULL, 2, NULL, 0, 'When the turkey is nicely browned, cover with foil and allow it to rest at room temperature for 15 minutes. Slice and serve with the pan juices spooned over the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44D83EB7090FC84AB3C50877FB293406', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 0, NULL, NULL, 2, NULL, 0, 'Remove the neck and giblet package from the turkey cavity, set aside, and free the legs from the clamp.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68A7E735C0CDC14CBC3C648D448A89C7', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 1, NULL, NULL, 2, NULL, 0, 'Wash the turkey and dry with paper towels. Let sit in a cool place while you prepare the injection butter.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0F2FD22E268DB4BAC753A20B7941BFD', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 2, NULL, NULL, 2, NULL, 0, 'Roughly chop the seeded jalapeño pepper. Place 1/2 cup basil, chopped jalapeño pepper, salted butter, 1 TB salt, and 1/2 TB black pepper in a small pot and simmer for five minutes until butter is melted.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8B09F2389165E49BB470DD131ADD662', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 3, NULL, NULL, 2, NULL, 0, 'Let cool for 5 minutes. Strain or ladle large pieces out of cooled butter sauce. Pour into a cup for ease of injection.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1752F737FB358F4582D3A5247493E886', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 4, NULL, NULL, 2, NULL, 0, 'Using an injection needle, inject liquid into the turkey throughout the breast and thighs, avoid making too many holes. Try getting three different injection angles out of the same needle hole.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'946ADAB7DF57EB4EB73FB16204EEEA14', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 5, NULL, NULL, 2, NULL, 0, 'Once injected, the turkey can marinate for several hours to overnight in the fridge before grilling. If any injection liquid oozes out, you can rub it gently over the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A857EC943B3B3147ADB309C9A2516CDA', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 6, NULL, NULL, 2, NULL, 0, 'Preheat the grill to 425°F. Cooking time will vary based on the size of bird.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4950A5E736BF554D827D43D0E14ABF76', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 7, NULL, NULL, 2, NULL, 0, 'Baste the turkey with vegetable oil and flip. Season with herbed poultry blend on top of the bird and flip to get to the bottom of the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DCFC25482F0704ABD87B07C2B28EA56', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 8, NULL, NULL, 2, NULL, 0, 'Add 1/2 cup fresh basil, 1 lemon sliced in half, and 1 onion sliced in half to the turkey cavity.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3632857129895446831DD40962AD7C90', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 9, NULL, NULL, 2, NULL, 0, 'Line the grill with a grill liner. Place the turkey on the liner in the grill for the amount of cooking time based on the weight of the bird.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96989563D9AB1E428859D0B5F144396C', X'9DAC695B50F2814F8C918AEFF9939808', X'9DAC695B50F2814F8C918AEFF9939808', 10, NULL, NULL, 2, NULL, 0, 'The turkey is ready to eat once a meat thermometer inserted into the thickest part of the breast reads 165°F. Remove from the grill and let sit before carving, 15-20 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEA07900CE75E14992608570008CC65F', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F and remove puff pastry from freezer to thaw.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0029987143E3204F94BAC6DD300CFBB4', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, mix cranberry sauce, apples and cinnamon. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AE2D9B8FB33A049BA5AD2631169DF3A', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 2, NULL, NULL, 2, NULL, 0, 'Cook bacon over medium-high heat in a large pan. Remove cooked bacon, set aside to cool.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91E24DEFCBAFD64CAD43279A03922B01', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 3, NULL, NULL, 2, NULL, 0, 'In the same pan, heat olive oil and add garlic. Stir for a few minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CDB2C31DBB7D542A2E4C58FB34E5C04', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 4, NULL, NULL, 2, NULL, 0, 'Stir in rosemary, thyme and crumbled bacon then turn off the heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18B6598CB0045B46A4A48FD7BBBF62F1', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 5, NULL, NULL, 2, NULL, 0, 'Add the ground turkey to pan, sprinkle with salt, chipotle pepper and stir all ingredients together. Cook until ground turkey is lightly browned.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDC2EAC06F2097409C2736FEE5CEB51D', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 6, NULL, NULL, 2, NULL, 0, 'Slice the cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E001AD3E9B5FB24288A7CBFCFD2771B5', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 7, NULL, NULL, 2, NULL, 0, 'On a parchment lined baking sheet, roll out a puff pastry.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FB3B800B24A2F42ADF6E59F9C3920F3', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 8, NULL, NULL, 2, NULL, 0, 'Spoon half of the turkey mixture in the middle of the puff pastry. Form into a rectangular loaf.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84D812823DD6CE4FA82BC174EA70F495', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 9, NULL, NULL, 2, NULL, 0, 'Cover the loaf with several slices of cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'412A8869D4863647877B6DA6688D3106', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 10, NULL, NULL, 2, NULL, 0, 'Cover the cheese with 2-3 spoonfuls of the cranberry and apple sauce.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'848B81DEA04BC446A7F993E0705525A8', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 11, NULL, NULL, 2, NULL, 0, 'Cut strips along the edges of the dough. Make as many cuts along the sides as you want, just make sure to have the same amount on each side.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'688CFFAFC0E6134EAC39A69CD166ADDF', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 12, NULL, NULL, 2, NULL, 0, 'After cutting 6-8 strips of dough on the sides, you are ready to braid.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC59042EEB34634884F45200C5B43AE2', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 13, NULL, NULL, 2, NULL, 0, 'Fold the top and the bottom ends over the turkey, then cross the side strips over each other to make the braid.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F88959B0386E27449B684DDB20DDDB13', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 14, NULL, NULL, 2, NULL, 0, 'Repeat with the second puff pastry.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18ACDFC1724FC345ACA9A6753D2A0F25', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 15, NULL, NULL, 2, NULL, 0, 'Make an egg wash with one beaten egg and a splash of cool water.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'990D590AB07AD041A99BA97F5E886AA3', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 16, NULL, NULL, 2, NULL, 0, 'Brush each pastry with the egg wash.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CDA0868456D2345909D600A783D6AC4', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 17, NULL, NULL, 2, NULL, 0, 'Bake for 25-30 minutes until the braid is golden brown.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76110707E8BD944FBE71FDAE4E2EAA5A', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 18, NULL, NULL, 2, NULL, 0, 'Use a food thermometer to make sure the turkey is 165°F before eating.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C040EB7733AFB54499EA9DF00D3F5E01', X'994BC6A6F48E2E469AD394480379E038', X'994BC6A6F48E2E469AD394480379E038', 19, NULL, NULL, 2, NULL, 0, 'Let set for at least 5 minutes, then slice and serve.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A225A06B4D16348A8FAD7F739AF398F', X'C8D60D6D19150540B84F4565095F49B3', X'C8D60D6D19150540B84F4565095F49B3', 0, NULL, NULL, 2, NULL, 0, 'Prepare the sliders. Divide the ground turkey into 8 equal balls. Shape each ball into a patty approximately 3` across or about the size of the slider buns. Cover and refrigerate until needed.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F09AC978E5E73348999847CC4F71430C', X'C8D60D6D19150540B84F4565095F49B3', X'C8D60D6D19150540B84F4565095F49B3', 1, NULL, NULL, 2, NULL, 0, 'Prepare the red curry mayonnaise. Combine the mayonnaise and Thai red curry paste in a small bowl. Using a rubber spatula or whisk, stir well until combined.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58BF4B8923FA984BBCC784DA1F98DA7B', X'C8D60D6D19150540B84F4565095F49B3', X'C8D60D6D19150540B84F4565095F49B3', 2, NULL, NULL, 2, NULL, 0, 'Season the sliders with salt and pepper as desired. On a grill or in a sauté pan, cook over medium heat for 5-8 minutes or until an internal temperature of 165°F is reached on a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D44D9F3EC142F46BE60C1FC8D417BC5', X'C8D60D6D19150540B84F4565095F49B3', X'C8D60D6D19150540B84F4565095F49B3', 3, NULL, NULL, 2, NULL, 0, 'As the patties cook, spread the red curry mayonnaise on the tops of the slider buns. Place the pickles on the bottom half of buns.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0E2ACC133C29345BE513BF59985C92F', X'C8D60D6D19150540B84F4565095F49B3', X'C8D60D6D19150540B84F4565095F49B3', 4, NULL, NULL, 2, NULL, 0, 'When the sliders are cooked, remove from heat and place on the bottom buns. Drizzle the Thai sweet chili sauce on top of the patties and add the bun tops.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D48E3EAD34B0B74FBC5AE8EF16AEEE8B', X'E394C87CC634554E89C3A78DDCE5B59A', X'E394C87CC634554E89C3A78DDCE5B59A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F. Locate and set aside neck and giblets package from inside the turkey cavity. Unwrap the turkey in the sink and pat dry with paper towels.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E103FE24A3586B42850D74CC5FCEC655', X'E394C87CC634554E89C3A78DDCE5B59A', X'E394C87CC634554E89C3A78DDCE5B59A', 1, NULL, NULL, 2, NULL, 0, 'Coat the outside of the turkey with butter, sprinkle with salt and pepper, and place on a rack in a large roasting pan and roast for 1 hour.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62D142EB6962F6469B7C26DAB3614F15', X'E394C87CC634554E89C3A78DDCE5B59A', X'E394C87CC634554E89C3A78DDCE5B59A', 2, NULL, NULL, 2, NULL, 0, 'Combine the chicken broth, pomegranate juice and maple syrup in a nonstick skillet and bring to a simmer over low heat. Cook, stirring occasionally, until reduced to about 1/3 cup, about 30 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'764D6E358D63BD4C9F351D681A2F196B', X'E394C87CC634554E89C3A78DDCE5B59A', X'E394C87CC634554E89C3A78DDCE5B59A', 3, NULL, NULL, 2, NULL, 0, 'After 1 hour of roasting, begin basting with the glaze. If the bird gets too dark, cover loosely with foil. Continue roasting until a thermometer inserted into the breast reaches 170°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70F84A10B93791489F53576506A3754F', X'E394C87CC634554E89C3A78DDCE5B59A', X'E394C87CC634554E89C3A78DDCE5B59A', 4, NULL, NULL, 2, NULL, 0, 'Transfer the turkey to a platter, tent with foil and let rest 30 minutes before slicing.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB06733A4604C6429F147E492D96EEEB', X'B1A496E69D0A0D40874BB7F1E3203C3C', X'B1A496E69D0A0D40874BB7F1E3203C3C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Pat the turkey breast roast dry with paper towels and place on a roasting rack in a roasting pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE5DDE4B331B7C48A5A9A09A7CABE2CD', X'B1A496E69D0A0D40874BB7F1E3203C3C', X'B1A496E69D0A0D40874BB7F1E3203C3C', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the paprika, salt, brown sugar, onion powder and dry mustard. Spread the spice rub on the turkey on all sides.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'950D401CB6D60A419410699049F1BD72', X'B1A496E69D0A0D40874BB7F1E3203C3C', X'B1A496E69D0A0D40874BB7F1E3203C3C', 2, NULL, NULL, 2, NULL, 0, 'Transfer turkey to the oven and cook until the internal temperature reaches 165° F on an instant-read thermometer. Let the turkey rest 20 minutes before slicing.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD2C82E8F4056242AF7658B747415722', X'B1A496E69D0A0D40874BB7F1E3203C3C', X'B1A496E69D0A0D40874BB7F1E3203C3C', 3, NULL, NULL, 2, NULL, 0, 'Pulse cauliflower in a food processor until it''s riced.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9897C6983E4D29428EB309AED2490636', X'B1A496E69D0A0D40874BB7F1E3203C3C', X'B1A496E69D0A0D40874BB7F1E3203C3C', 4, NULL, NULL, 2, NULL, 0, 'Heat a large nonstick skillet over medium heat and spray with cooking spray. Add the cauliflower and gently toast, stirring occasionally, until it starts to brown in a few spots, about 5 minutes. Transfer to a bowl.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08A9849D6CBE8F43BA5DE00D6C0004E6', X'B1A496E69D0A0D40874BB7F1E3203C3C', X'B1A496E69D0A0D40874BB7F1E3203C3C', 5, NULL, NULL, 2, NULL, 0, 'In the same skillet, toast the nuts and spices over low heat. When fragrant, about a minute, add to the cauliflower. Stir in the currants and lemon juice and stir to combine.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BE1B6F38D07F74DBE2309963ADCDC77', X'B1A496E69D0A0D40874BB7F1E3203C3C', X'B1A496E69D0A0D40874BB7F1E3203C3C', 6, NULL, NULL, 2, NULL, 0, 'Serve slices of turkey fanned out on top of the cauliflower couscous.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0C18DB39F5AD34E9F24417C6DEFD814', X'2943CDA05E3F15498F60FEBE5541EB77', X'2943CDA05E3F15498F60FEBE5541EB77', 0, NULL, NULL, 2, NULL, 0, 'In a bowl, combine salt, pepper, garlic powder and flour.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0840E58A05E5F24898A438CE6FF32860', X'2943CDA05E3F15498F60FEBE5541EB77', X'2943CDA05E3F15498F60FEBE5541EB77', 1, NULL, NULL, 2, NULL, 0, 'Dredge turkey in flour mixture.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2072EEF2E5EC8843852B3ADFE5305F34', X'2943CDA05E3F15498F60FEBE5541EB77', X'2943CDA05E3F15498F60FEBE5541EB77', 2, NULL, NULL, 2, NULL, 0, 'In a large frying pan over medium heat, quickly brown turkey in melted butter, turning once.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FB5BB236DEAB6449E71B43E257D3813', X'2943CDA05E3F15498F60FEBE5541EB77', X'2943CDA05E3F15498F60FEBE5541EB77', 3, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup Marsala wine or chicken broth (reserve 1/4 cup for pan sauce).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'391301BF7475624289F676357D76A5BC', X'2943CDA05E3F15498F60FEBE5541EB77', X'2943CDA05E3F15498F60FEBE5541EB77', 4, NULL, NULL, 2, NULL, 0, 'Cook on high for 1 minute.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A3FC87B4F1ADC47A983561DA79609C2', X'2943CDA05E3F15498F60FEBE5541EB77', X'2943CDA05E3F15498F60FEBE5541EB77', 5, NULL, NULL, 2, NULL, 0, 'Remove turkey from pan and keep warm.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0649A2CD1C9AE14D801BD559E445D354', X'2943CDA05E3F15498F60FEBE5541EB77', X'2943CDA05E3F15498F60FEBE5541EB77', 6, NULL, NULL, 2, NULL, 0, 'Pour remaining wine or broth and chicken broth into pan. Scrape loose all the brown particles and bring to a boil. Add mushrooms and boil for 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5495321E920D6246A7A4BA380EB10BB7', X'2943CDA05E3F15498F60FEBE5541EB77', X'2943CDA05E3F15498F60FEBE5541EB77', 7, NULL, NULL, 2, NULL, 0, 'Pour demi-glaze over scallopini.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC6BC0375AC79B499C8FB8F41CAAA01F', X'2943CDA05E3F15498F60FEBE5541EB77', X'2943CDA05E3F15498F60FEBE5541EB77', 8, NULL, NULL, 2, NULL, 0, 'Serve over angel hair pasta, egg noodles or fettuccine (optional).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88EA144B6354FE4690631C8500A8E436', X'A0B77B10F1E748418A55A414928F7C83', X'A0B77B10F1E748418A55A414928F7C83', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F. Line a baking dish with foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0625B4D1B82B4948A3EC217900C8EFD1', X'A0B77B10F1E748418A55A414928F7C83', X'A0B77B10F1E748418A55A414928F7C83', 1, NULL, NULL, 2, NULL, 0, 'Heat a large nonstick skillet and spray with cooking spray. Add the spinach and cook just until wilted. Combine the spinach and goat cheese in a small bowl and season lightly with pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7442A87D40A0304980978E222AC3E726', X'A0B77B10F1E748418A55A414928F7C83', X'A0B77B10F1E748418A55A414928F7C83', 2, NULL, NULL, 2, NULL, 0, 'Place the turkey on a work surface and divide the spinach and cheese mixture; spread evenly across each slice. Spread the filling about 3/4 of the way across the turkey. Roll the scallopini starting with the filling end and ending with the unfilled end. Secure each roll with a wooden toothpick to hold it together.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE79011977332843BC7DA776A6FA126A', X'A0B77B10F1E748418A55A414928F7C83', X'A0B77B10F1E748418A55A414928F7C83', 3, NULL, NULL, 2, NULL, 0, 'In small bowl, combine oil, paprika and salt; gently roll the scallopini rolls in the mixture to coat. At this point, rolls can be wrapped in plastic wrap and refrigerated for up to 24 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB7F60F22424EE4BBDCA41C0117F695F', X'A0B77B10F1E748418A55A414928F7C83', X'A0B77B10F1E748418A55A414928F7C83', 4, NULL, NULL, 2, NULL, 0, 'Place rolls on the prepared pan and roast until turkey reaches 165° F, about 20 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37BBD2A83468B14788BE0AD29F1BC8FA', X'A0B77B10F1E748418A55A414928F7C83', X'A0B77B10F1E748418A55A414928F7C83', 5, NULL, NULL, 2, NULL, 0, 'When cool enough to handle remove and discard toothpicks; slice crosswise into 3/4-inch slices to serve. Pierce with toothpick, garnish with cucumber and arugula.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7633628FAE95EC4AA10F5A2D528156A7', X'A4680409655A334FAF5C50669C5B055B', X'A4680409655A334FAF5C50669C5B055B', 0, NULL, NULL, 2, NULL, 0, 'Roast turkey according to package instructions and an instant-read thermometer registers 165°F when inserted into the thickest part of the breast. Cover the breast loosely with aluminum foil if necessary to prevent overbrowning. Let cool and slice 1 pound of turkey into thin slices.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1749CC7A1F3DF1419E2610EEED2A3EB4', X'A4680409655A334FAF5C50669C5B055B', X'A4680409655A334FAF5C50669C5B055B', 1, NULL, NULL, 2, NULL, 0, 'Combine the yogurt, mayonnaise, honey, lime juice, lime zest and sriracha sauce in a small bowl.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'094FD2B43687A64DB1CB5EB02E2A3E92', X'A4680409655A334FAF5C50669C5B055B', X'A4680409655A334FAF5C50669C5B055B', 2, NULL, NULL, 2, NULL, 0, 'Spread each slice of bread with sriracha honey lime sauce.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C24F944BD18D8248916EDE06E67D3A91', X'A4680409655A334FAF5C50669C5B055B', X'A4680409655A334FAF5C50669C5B055B', 3, NULL, NULL, 2, NULL, 0, 'Top each slice of bread with arugula greens, cucumber slices and the turkey. Serve open-faced.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDAC69D1A7056A4B9C405B48300991B7', X'CE71913FFEE72C43B2450B7A934AEE0E', X'CE71913FFEE72C43B2450B7A934AEE0E', 0, NULL, NULL, 2, NULL, 0, 'Place breast cutlets on bottom of large 13 x 9-inch baking dish and cover with mushrooms.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3C1FAA216260544AD5CFD93ED995CD6', X'CE71913FFEE72C43B2450B7A934AEE0E', X'CE71913FFEE72C43B2450B7A934AEE0E', 1, NULL, NULL, 2, NULL, 0, 'Mix remaining ingredients in a medium bowl until smooth. Pour sauce over turkey and mushrooms.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8602345F0AB9BB4BB9E8BC8020A41404', X'CE71913FFEE72C43B2450B7A934AEE0E', X'CE71913FFEE72C43B2450B7A934AEE0E', 2, NULL, NULL, 2, NULL, 0, 'Bake at 350°F, uncovered, for 35 minutes (do not open oven). Cook turkey to 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECFA51505FDCED49856FEC948E10C633', X'CE71913FFEE72C43B2450B7A934AEE0E', X'CE71913FFEE72C43B2450B7A934AEE0E', 3, NULL, NULL, 2, NULL, 0, 'Remove turkey from dish, reserving sauce.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6F6C7E283740B44A076468604CD1ABB', X'CE71913FFEE72C43B2450B7A934AEE0E', X'CE71913FFEE72C43B2450B7A934AEE0E', 4, NULL, NULL, 2, NULL, 0, 'Slice turkey. Place on a bed of your favorite cooked pasta, rice or egg noodles.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25ABFA72C611E9438AD88832B88B8008', X'CE71913FFEE72C43B2450B7A934AEE0E', X'CE71913FFEE72C43B2450B7A934AEE0E', 5, NULL, NULL, 2, NULL, 0, 'Drizzle with sour cream sauce and mushrooms. Garnish with chives and black pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14A768F441C8494AA0D4E68692436CFF', X'2F62B47549FCC44BB7F119303CBE5CEA', X'2F62B47549FCC44BB7F119303CBE5CEA', 0, NULL, NULL, 2, NULL, 0, 'Heat nonstick skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0C997C0F9FB4C409DB5295CBE9E89E9', X'2F62B47549FCC44BB7F119303CBE5CEA', X'2F62B47549FCC44BB7F119303CBE5CEA', 1, NULL, NULL, 2, NULL, 0, 'Add turkey, onion, garlic and mushroom to skillet.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BADD8E9F1BBCB4D82FF67136C200AED', X'2F62B47549FCC44BB7F119303CBE5CEA', X'2F62B47549FCC44BB7F119303CBE5CEA', 2, NULL, NULL, 2, NULL, 0, 'Sauté until turkey has reached a temperature of 165°F as measured by a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'721870BB0044D34BAA1359CA16DB5848', X'2F62B47549FCC44BB7F119303CBE5CEA', X'2F62B47549FCC44BB7F119303CBE5CEA', 3, NULL, NULL, 2, NULL, 0, 'Add flour, salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2D82AECE2D77C4991AE2A949BD8A70A', X'2F62B47549FCC44BB7F119303CBE5CEA', X'2F62B47549FCC44BB7F119303CBE5CEA', 4, NULL, NULL, 2, NULL, 0, 'Cook uncovered 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9F46E775E7FCB4EB756ED89E7BD021F', X'2F62B47549FCC44BB7F119303CBE5CEA', X'2F62B47549FCC44BB7F119303CBE5CEA', 5, NULL, NULL, 2, NULL, 0, 'Add soup and simmer uncovered 10 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DAA2A2AB02D884D8DEA9373BBF6FCB1', X'2F62B47549FCC44BB7F119303CBE5CEA', X'2F62B47549FCC44BB7F119303CBE5CEA', 6, NULL, NULL, 2, NULL, 0, 'Stir in sour cream and heat thoroughly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A780B9F3ADEAE24DB0A7E8D76ECE1692', X'2F62B47549FCC44BB7F119303CBE5CEA', X'2F62B47549FCC44BB7F119303CBE5CEA', 7, NULL, NULL, 2, NULL, 0, 'Serve over buttered egg noodles or pappardelle. Garnish with parsley and black pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECBEB67496366A4A8B4FEF818DD46D9C', X'F8BEF1074125D347A7E809EABF5A878E', X'F8BEF1074125D347A7E809EABF5A878E', 0, NULL, NULL, 2, NULL, 0, 'Place tomatillos and green chiles in blender and blend until smooth. Add to an oval 4- to 6-quart slow cooker.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57E07BE6E13EA14BADEE2D4CB6711768', X'F8BEF1074125D347A7E809EABF5A878E', X'F8BEF1074125D347A7E809EABF5A878E', 1, NULL, NULL, 2, NULL, 0, 'Add the cumin, salt, pepper, broth, onions and garlic, and stir. Then add the turkey and make sure it''s completely coated in liquid. Cover; cook turkey to 165°F using a meat thermometer, about 4 hours on high or about 6 hours on low.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5E60DF2498A3A47A6704D90D8A8543C', X'F8BEF1074125D347A7E809EABF5A878E', X'F8BEF1074125D347A7E809EABF5A878E', 2, NULL, NULL, 2, NULL, 0, 'Remove the turkey from the soup and let cool. Shred the meat and return to soup; discard the bone.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B3505ACCF0FB744AE0D35D598C6FE1D', X'F8BEF1074125D347A7E809EABF5A878E', X'F8BEF1074125D347A7E809EABF5A878E', 3, NULL, NULL, 2, NULL, 0, 'Stir hominy into slow cooker. Cover; cook on high for 1 hour longer. Season to taste with salt.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8CFBA94818F344493561FB3BE5BA32E', X'F8BEF1074125D347A7E809EABF5A878E', X'F8BEF1074125D347A7E809EABF5A878E', 4, NULL, NULL, 2, NULL, 0, 'Top individual servings with avocado, cilantro, cabbage and radish slices, and serve each bowl with a wedge of lime.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0990C28891E78046A9FD0D7CDE0DF270', X'F177775E9FADEB46A21EE79DA3D57590', X'F177775E9FADEB46A21EE79DA3D57590', 0, NULL, NULL, 2, NULL, 0, 'Cook polenta according to package instructions.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CC6600D8684674DA30DF69ACF698E41', X'F177775E9FADEB46A21EE79DA3D57590', X'F177775E9FADEB46A21EE79DA3D57590', 1, NULL, NULL, 2, NULL, 0, 'When polenta is cooked, take it off the heat and add thyme, pepper, fontina and olive oil, and stir until melted and very creamy; keep warm.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D52CFD7D4DB744AB5268C81160B9656', X'F177775E9FADEB46A21EE79DA3D57590', X'F177775E9FADEB46A21EE79DA3D57590', 2, NULL, NULL, 2, NULL, 0, 'Heat a large, preferably nonstick, skillet and coat with cooking spray.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9B274EE2B04E14F98C1CE00F229D0F7', X'F177775E9FADEB46A21EE79DA3D57590', X'F177775E9FADEB46A21EE79DA3D57590', 3, NULL, NULL, 2, NULL, 0, 'Place the sausages in skillet and cook over medium-high heat, until brown on each side, about 5 minutes per side, then add 1/4 cup of water and cover, turning heat to medium-low. Cook for another 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC359500DBD5E64CAC7C5D2ED289DB04', X'F177775E9FADEB46A21EE79DA3D57590', X'F177775E9FADEB46A21EE79DA3D57590', 4, NULL, NULL, 2, NULL, 0, 'Take sausages out of pan and place on a cutting board to cool. When cool, cut sausages into 1/2-inch diagonal slices.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'304E3CB0E65B6046B107D30FD0E895EF', X'F177775E9FADEB46A21EE79DA3D57590', X'F177775E9FADEB46A21EE79DA3D57590', 5, NULL, NULL, 2, NULL, 0, 'Pour liquid out of the skillet, then add the remaining tablespoon of olive oil, onions and peppers; cook over medium-high heat until softened, about 7 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FAEC2B881C4764384A8E610F75BFEAC', X'F177775E9FADEB46A21EE79DA3D57590', X'F177775E9FADEB46A21EE79DA3D57590', 6, NULL, NULL, 2, NULL, 0, 'Add the sliced sausages and garlic to the pan; stir and cook for 2 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7A5615563BFDD46878F13E6B1CC66A7', X'F177775E9FADEB46A21EE79DA3D57590', X'F177775E9FADEB46A21EE79DA3D57590', 7, NULL, NULL, 2, NULL, 0, 'Deglaze the pan with wine and simmer until liquid is reduced to just a few tablespoons.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E72A90643D3714D992DB4D711202085', X'F177775E9FADEB46A21EE79DA3D57590', X'F177775E9FADEB46A21EE79DA3D57590', 8, NULL, NULL, 2, NULL, 0, 'Spoon polenta onto plates, top with the sausage mixture, sprinkle with Parmesan cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'844DE27AD059B64282F275B7430EF0CC', X'53C8550AF4F92941B32DA7EF060F2E3D', X'53C8550AF4F92941B32DA7EF060F2E3D', 0, NULL, NULL, 2, NULL, 0, 'Heat a large, preferably nonstick, Dutch oven on medium heat until hot and coat with cooking spray.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74000E69A5C0DA418923D197D16E3B57', X'53C8550AF4F92941B32DA7EF060F2E3D', X'53C8550AF4F92941B32DA7EF060F2E3D', 1, NULL, NULL, 2, NULL, 0, 'Add sausages and cook until well browned, about 10 minutes, turning frequently. Transfer sausages to plate, and when cool enough to handle cut sausages into 1/2-inch thick diagonal slices. Note that the sausages will not be completely cooked.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F97576E1EF98554DA870F49EA754EA7B', X'53C8550AF4F92941B32DA7EF060F2E3D', X'53C8550AF4F92941B32DA7EF060F2E3D', 2, NULL, NULL, 2, NULL, 0, 'Add the onion to the Dutch oven and cook, stirring often, until soft, about 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E052098288158E4DAC4A7254D5F1BD97', X'53C8550AF4F92941B32DA7EF060F2E3D', X'53C8550AF4F92941B32DA7EF060F2E3D', 3, NULL, NULL, 2, NULL, 0, 'Add garlic and Cajun seasoning and stir, just until fragrant, about 30 seconds.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17ABC589D6FF294EBD3286542E4681C0', X'53C8550AF4F92941B32DA7EF060F2E3D', X'53C8550AF4F92941B32DA7EF060F2E3D', 4, NULL, NULL, 2, NULL, 0, 'Add vegetable oil and flour and cook, stirring until the flour begins to brown, about 2 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99DF5D44983F6049961DD595D6838DE4', X'53C8550AF4F92941B32DA7EF060F2E3D', X'53C8550AF4F92941B32DA7EF060F2E3D', 5, NULL, NULL, 2, NULL, 0, 'Add tomatoes and broth, increase heat to high and bring to a boil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'307C6FF96619834FA07396CCD6368391', X'53C8550AF4F92941B32DA7EF060F2E3D', X'53C8550AF4F92941B32DA7EF060F2E3D', 6, NULL, NULL, 2, NULL, 0, 'Add the sausage and okra to the pot and reduce heat to a simmer. Cook until the okra is heated through and sausage is cooked, about 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5BA2635AB13924596406F52CCF061D6', X'53C8550AF4F92941B32DA7EF060F2E3D', X'53C8550AF4F92941B32DA7EF060F2E3D', 7, NULL, NULL, 2, NULL, 0, 'Serve the stew in bowls over rice with green onions on top and French bread on the side.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'723713742CFC9348A82B50D8EB70DE71', X'488466B614A6FF489B580FF5DBB811A5', X'488466B614A6FF489B580FF5DBB811A5', 0, NULL, NULL, 2, NULL, 0, 'Heat a large, preferably nonstick, Dutch oven over medium-high heat; coat with cooking spray.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F429F8477935734AA42085096CA0641B', X'488466B614A6FF489B580FF5DBB811A5', X'488466B614A6FF489B580FF5DBB811A5', 1, NULL, NULL, 2, NULL, 0, 'Add onions to the Dutch oven and cook over medium-high heat until softened, about 3 minutes');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'703EB7C8FD72554BBF7E585BDEC7C21E', X'488466B614A6FF489B580FF5DBB811A5', X'488466B614A6FF489B580FF5DBB811A5', 2, NULL, NULL, 2, NULL, 0, 'Add the sausage and crumble, cooking until no longer pink, about 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC5C97B3D0787E4EAA2E8C786A15A20C', X'488466B614A6FF489B580FF5DBB811A5', X'488466B614A6FF489B580FF5DBB811A5', 3, NULL, NULL, 2, NULL, 0, 'Add the chicken broth, tomatoes, chickpeas and Italian seasoning.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B54F006641E7D4593F5A81AE7F9DC14', X'488466B614A6FF489B580FF5DBB811A5', X'488466B614A6FF489B580FF5DBB811A5', 4, NULL, NULL, 2, NULL, 0, 'Bring to a boil, lower heat and simmer for 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3348BBA224BBEB4C8B5877129EBFB071', X'488466B614A6FF489B580FF5DBB811A5', X'488466B614A6FF489B580FF5DBB811A5', 5, NULL, NULL, 2, NULL, 0, 'Add the spinach and cook just until it wilts. Season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F15D935468A3247935EEAF31EF49AE7', X'488466B614A6FF489B580FF5DBB811A5', X'488466B614A6FF489B580FF5DBB811A5', 6, NULL, NULL, 2, NULL, 0, 'To serve, place grilled bread in bowls, pour the stew on top and sprinkle each serving with Parmesan cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8A4F0F93CA4F740AC42B403A0AB24A0', X'B5317AF8F0576B45B77989FA85CA737F', X'B5317AF8F0576B45B77989FA85CA737F', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil. Cook pasta according to the package directions, but 2 minutes before the pasta is done (the very center of the pasta should still be raw) add the broccoli rabe and continue cooking. Drain and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5069B077AFCC684292C21B04FD4A418F', X'B5317AF8F0576B45B77989FA85CA737F', X'B5317AF8F0576B45B77989FA85CA737F', 1, NULL, NULL, 2, NULL, 0, 'Return the pot to the stove over medium heat; add the oil. Add the sausage and cook until no longer pink, breaking it up as it browns, about 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC6DCA58CBC7AB4A9D651B5609A5313D', X'B5317AF8F0576B45B77989FA85CA737F', X'B5317AF8F0576B45B77989FA85CA737F', 2, NULL, NULL, 2, NULL, 0, 'Add the onion to the sausage and cook until softened, about 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7490FBEBA76C240B2A63AC4351087CE', X'B5317AF8F0576B45B77989FA85CA737F', X'B5317AF8F0576B45B77989FA85CA737F', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic and crushed red pepper. Cook until the garlic is fragrant, about 30 seconds.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07D8C197FCA216419EA0057129CDDC74', X'B5317AF8F0576B45B77989FA85CA737F', X'B5317AF8F0576B45B77989FA85CA737F', 4, NULL, NULL, 2, NULL, 0, 'Add the chicken broth and bring to a simmer and stir in black pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FA61901DD71084BB2A91555CE06A7E9', X'B5317AF8F0576B45B77989FA85CA737F', X'B5317AF8F0576B45B77989FA85CA737F', 5, NULL, NULL, 2, NULL, 0, 'Add the drained pasta and broccoli rabe to the sausage onion sauce and toss until the pasta is coated in the sauce. Serve with Parmesan cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F483A9AF044D64D883931FE8723997E', X'82613E472A59E7468A1AB3B180A4B2CA', X'82613E472A59E7468A1AB3B180A4B2CA', 0, NULL, NULL, 2, NULL, 0, 'Cook noodles according to package instructions until al dente. Drain well.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2D9E62413BF2442BC1306728DEC4B2B', X'82613E472A59E7468A1AB3B180A4B2CA', X'82613E472A59E7468A1AB3B180A4B2CA', 1, NULL, NULL, 2, NULL, 0, 'Heat a large skillet over medium-high heat and add oil. Stir in ginger and garlic and cook just until fragrant, about 15 seconds.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D46624458A9B944CB09923AF497FEF35', X'82613E472A59E7468A1AB3B180A4B2CA', X'82613E472A59E7468A1AB3B180A4B2CA', 2, NULL, NULL, 2, NULL, 0, 'Add turkey and cook to 165°F using a meat thermometer, stirring occasionally and breaking up turkey with a wooden spoon until browned, about 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1B104C2C0416D4BA9CD4FCE88135306', X'82613E472A59E7468A1AB3B180A4B2CA', X'82613E472A59E7468A1AB3B180A4B2CA', 3, NULL, NULL, 2, NULL, 0, 'Whisk broth, peanut butter, vinegar, soy sauce, sherry, sugar and crushed pepper in medium bowl. Pour into skillet and bring to a boil. Reduce heat to medium and simmer, stirring occasionally until liquid thickens and reduces by about half, about 10 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'132E9AB0E8834940AAB133E8C20C286A', X'82613E472A59E7468A1AB3B180A4B2CA', X'82613E472A59E7468A1AB3B180A4B2CA', 4, NULL, NULL, 2, NULL, 0, 'Top noodles with sauce, green onions and cucumber strips.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6022CB2269DA25439FE7C69290868ABC', X'17B2168ABFCEA140996056D7A888C7BE', X'17B2168ABFCEA140996056D7A888C7BE', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the eggs, milk, Dijon mustard, salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B848537988674489AA41BDF68212EBA', X'17B2168ABFCEA140996056D7A888C7BE', X'17B2168ABFCEA140996056D7A888C7BE', 1, NULL, NULL, 2, NULL, 0, 'Add bread, pushing it into the mixture so it absorbs it. Set aside, tossing once in a while so the bread absorbs the mixture evenly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EE2D28748A4C54D86BDC99EA566706C', X'17B2168ABFCEA140996056D7A888C7BE', X'17B2168ABFCEA140996056D7A888C7BE', 2, NULL, NULL, 2, NULL, 0, 'Heat large nonstick skillet over medium heat and add the oil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5683A1EDDA8FEE4FABAE64B9243DF368', X'17B2168ABFCEA140996056D7A888C7BE', X'17B2168ABFCEA140996056D7A888C7BE', 3, NULL, NULL, 2, NULL, 0, 'Add breakfast sausage and cook, breaking it apart and crumbling it, until no longer pink, about 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E5C60E73EE6CD46A1BCC9637BA88D7E', X'17B2168ABFCEA140996056D7A888C7BE', X'17B2168ABFCEA140996056D7A888C7BE', 4, NULL, NULL, 2, NULL, 0, 'Add the diced onion and peppers and cook, stirring occasionally, until soft, 3 to 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA4BAC1C57B55A48A3E8D8ADE0EA62AD', X'17B2168ABFCEA140996056D7A888C7BE', X'17B2168ABFCEA140996056D7A888C7BE', 5, NULL, NULL, 2, NULL, 0, 'Take off the heat and let sit until warm but not hot, about 15 minutes. Add to the bread and egg mixture.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FDA67A4C8818548A30E496CEF467510', X'17B2168ABFCEA140996056D7A888C7BE', X'17B2168ABFCEA140996056D7A888C7BE', 6, NULL, NULL, 2, NULL, 0, 'Coat 6 ramekins or small baking dishes with cooking spray. Pour in bread-sausage-egg mixture. Sprinkle with the cheese. Cover the dish securely with plastic wrap and refrigerate it overnight, or for about 8 hours.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BD851B6E29F1F41979CACB3AD0B1BF5', X'17B2168ABFCEA140996056D7A888C7BE', X'17B2168ABFCEA140996056D7A888C7BE', 7, NULL, NULL, 2, NULL, 0, 'Remove the strata from the fridge 30 minutes before putting it in the oven to take the chill off. Preheat the oven to 350°F and bake uncovered until the eggs are fully cooked, about 30 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77BF89995CD9FC4986994925D8C9AF87', X'17B2168ABFCEA140996056D7A888C7BE', X'17B2168ABFCEA140996056D7A888C7BE', 8, NULL, NULL, 2, NULL, 0, 'Let the strata rest for 5 minutes before slicing and serving. Garnish with chives.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'189ECA3BF7A4C840B7780739991134DE', X'D22E6508B21AC246AB825B23D33A2BAE', X'D22E6508B21AC246AB825B23D33A2BAE', 0, NULL, NULL, 2, NULL, 0, 'Heat a large saucepan over medium heat and add 1 tablespoon oil. Add carrot, celery, onion and garlic. Cook, stirring occasionally, until onion softens, about 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D795B7CA862ACD4C95AFEEAFC47A9A92', X'D22E6508B21AC246AB825B23D33A2BAE', X'D22E6508B21AC246AB825B23D33A2BAE', 1, NULL, NULL, 2, NULL, 0, 'Add ground turkey and cook, stirring occasionally and breaking up meat with a wooden spoon, cook to 165°F using a meat thermometer, about 4 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33448AADBA1F1A419D5F7A326D0CFB6F', X'D22E6508B21AC246AB825B23D33A2BAE', X'D22E6508B21AC246AB825B23D33A2BAE', 2, NULL, NULL, 2, NULL, 0, 'Transfer the mixture to a 4- to 6-quart slow cooker. Add the tomatoes with their puree, 1/3 cup water, tomato paste, Italian seasoning, 1 teaspoon salt, crushed red pepper and bay leaf. Stir well. Cover and cook for 3 hours on high or 6 hours on low.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1B6560AF157544191C3E4525762B2E5', X'D22E6508B21AC246AB825B23D33A2BAE', X'D22E6508B21AC246AB825B23D33A2BAE', 3, NULL, NULL, 2, NULL, 0, 'Using a spiral cutter, mandoline or large holes on a box grater, cut zucchini into long thin noodles; stop grating when you reach the seedy center.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E822C288F6208419DC5C746BE7FA5F4', X'D22E6508B21AC246AB825B23D33A2BAE', X'D22E6508B21AC246AB825B23D33A2BAE', 4, NULL, NULL, 2, NULL, 0, 'Heat the remaining tablespoon oil in a very large nonstick skillet over medium-high heat. Add the zucchini and cook, tossing often with two spoons, just until the zucchini is heated through and beginning to soften, about 3 minutes. Do not overcook. Season with 1/2 teaspoon salt.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'559ACA90B80E364899359A00F6759F77', X'D22E6508B21AC246AB825B23D33A2BAE', X'D22E6508B21AC246AB825B23D33A2BAE', 5, NULL, NULL, 2, NULL, 0, 'Transfer zucchini to bowls. Top each with sauce and serve with Parmesan cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBCDE09AB594D3488184BFEE450C6CD2', X'DBF14FC8807B1E4982AF152C05DCAF57', X'DBF14FC8807B1E4982AF152C05DCAF57', 0, NULL, NULL, 2, NULL, 0, 'Melt butter in a large skillet over medium-high heat. Add onion and cook, stirring, until soft but not brown, about 3 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'656BF60D4BA46B46A5CEA5D30D2A29EC', X'DBF14FC8807B1E4982AF152C05DCAF57', X'DBF14FC8807B1E4982AF152C05DCAF57', 1, NULL, NULL, 2, NULL, 0, 'Add peas, water and salt, and bring to a simmer. Reduce heat to medium-low, cover skillet and simmer just until tender, about 3 minutes. Stir in the lemon zest and set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF91B02A8A0B0E4C94DD502FEC2E021E', X'DBF14FC8807B1E4982AF152C05DCAF57', X'DBF14FC8807B1E4982AF152C05DCAF57', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the flour on a large plate and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6EE5405B81E1740AB066DF342F8C0AF', X'DBF14FC8807B1E4982AF152C05DCAF57', X'DBF14FC8807B1E4982AF152C05DCAF57', 3, NULL, NULL, 2, NULL, 0, 'Dredge turkey in flour. Shake off the excess flour. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEEA62E8F34BC14B992BA48C2F170206', X'DBF14FC8807B1E4982AF152C05DCAF57', X'DBF14FC8807B1E4982AF152C05DCAF57', 4, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, heat olive oil and a teaspoon of butter. Once hot, add the turkey and cook, browning on each side, about 2 minutes total. Remove to a clean plate.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B235319B07AED441A9D6E2377060312A', X'DBF14FC8807B1E4982AF152C05DCAF57', X'DBF14FC8807B1E4982AF152C05DCAF57', 5, NULL, NULL, 2, NULL, 0, 'Cook turkey in batches, adding more butter as necessary.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB8E79B0E97DA74F9D973B6DF4848DB2', X'DBF14FC8807B1E4982AF152C05DCAF57', X'DBF14FC8807B1E4982AF152C05DCAF57', 6, NULL, NULL, 2, NULL, 0, 'Once all turkey is cooked, lower the heat and add wine and lemon slices to the skillet. Deglaze the pan and simmer until slightly reduced, about 2 minutes. Add any remaining butter and the turkey, coating each piece with the pan sauce.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69CF435FDF9FF645AE3896A567280BCB', X'DBF14FC8807B1E4982AF152C05DCAF57', X'DBF14FC8807B1E4982AF152C05DCAF57', 7, NULL, NULL, 2, NULL, 0, 'Place turkey on a clean platter, sprinkle with parsley, season to taste with pepper, and serve with peas and roast potatoes. Garnish with capers if desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D681AD9894BBD4098B13F4156A7AAE8', X'0F5DA91D29350A439BA595610C902D03', X'0F5DA91D29350A439BA595610C902D03', 0, NULL, NULL, 2, NULL, 0, 'Combine the mustard, vinegar, olive oil and honey in a bowl and mix until smooth. Set aside about 1/3 of the sauce for basting.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37D84D629ED99E489654A97C1ED05F37', X'0F5DA91D29350A439BA595610C902D03', X'0F5DA91D29350A439BA595610C902D03', 1, NULL, NULL, 2, NULL, 0, 'Place sausages in a pot and cover with water. Bring to a boil then gently simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F09C42F37297A74B91D84AF9B8E2EDF4', X'0F5DA91D29350A439BA595610C902D03', X'0F5DA91D29350A439BA595610C902D03', 2, NULL, NULL, 2, NULL, 0, 'When cool enough to handle, cut each sausage into 7 chunks of equal size.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEBF8EA7D8F3374B924367257436BA1A', X'0F5DA91D29350A439BA595610C902D03', X'0F5DA91D29350A439BA595610C902D03', 3, NULL, NULL, 2, NULL, 0, 'Microwave potatoes for 10 to 12 minutes or until tender.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CD6A201803F894DA753E4E0A5225542', X'0F5DA91D29350A439BA595610C902D03', X'0F5DA91D29350A439BA595610C902D03', 4, NULL, NULL, 2, NULL, 0, 'When potatoes are cool enough to handle, cut them into 1-inch pieces and toss them along with the peppers and sausages in the mustard glaze in a large bowl.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5281258D4D95C54DBA4C9FFA7D9C89A5', X'0F5DA91D29350A439BA595610C902D03', X'0F5DA91D29350A439BA595610C902D03', 5, NULL, NULL, 2, NULL, 0, 'Thread potatoes, red pepper and sausage chunks onto 8 skewers.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2FA3276A82CF94697FAA148483193FD', X'0F5DA91D29350A439BA595610C902D03', X'0F5DA91D29350A439BA595610C902D03', 6, NULL, NULL, 2, NULL, 0, 'Place skewers on a grill or grill pan over medium-high heat; brush with the reserved mustard mixture. Grill and turn until sausages are completely cooked and reach internal temperature of 165¬8 F and red peppers are cooked, about 10 minutes. Season to taste with pepper. Garnish with frisée, if desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31AA40CC0A7B0648852FE1FC69CDD76E', X'775E1AEC6B87B44F858BCB0FBE0BC3BE', X'775E1AEC6B87B44F858BCB0FBE0BC3BE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5981DBA3274C504FB995C320A7BAF816', X'775E1AEC6B87B44F858BCB0FBE0BC3BE', X'775E1AEC6B87B44F858BCB0FBE0BC3BE', 1, NULL, NULL, 2, NULL, 0, 'Spread pesto evenly over pizza crust. Top with an even layer of mozzarella cheese followed by the meatballs.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35CF69D75F1B8141BA28C096FAEE5799', X'775E1AEC6B87B44F858BCB0FBE0BC3BE', X'775E1AEC6B87B44F858BCB0FBE0BC3BE', 2, NULL, NULL, 2, NULL, 0, 'Bake on bottom rack of oven, 8-10 minutes or until cheese is bubbly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9154D8A31B88C542AA7687F24F1BE0F8', X'FCD3836510F64049980CE77E13B9596F', X'FCD3836510F64049980CE77E13B9596F', 0, NULL, NULL, 2, NULL, 0, 'Toast buns under broiler, 1 minute.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E3FB563A3BBD8448E3750CD6CB94107', X'FCD3836510F64049980CE77E13B9596F', X'FCD3836510F64049980CE77E13B9596F', 1, NULL, NULL, 2, NULL, 0, 'Microwave meatballs in a microwave-safe dish, 2-4 minutes or until heated through.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A1B4FE14B8AE847AA51324975C234EA', X'FCD3836510F64049980CE77E13B9596F', X'FCD3836510F64049980CE77E13B9596F', 2, NULL, NULL, 2, NULL, 0, 'Place roasted pepper on each bottom bun and top with meatball followed by provolone.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B983D7517672E34FA98C4DF513F3BE96', X'FCD3836510F64049980CE77E13B9596F', X'FCD3836510F64049980CE77E13B9596F', 3, NULL, NULL, 2, NULL, 0, 'Broil, 1 minute or until bubbly.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94C78B94C230824DBAF60E2A36F52FC7', X'FCD3836510F64049980CE77E13B9596F', X'FCD3836510F64049980CE77E13B9596F', 4, NULL, NULL, 2, NULL, 0, 'Add a few leaves of arugula and close slider with top bun.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9FC8253EFB8EE4893024EA2505380F5', X'30480F07E24FF1439E20B5EB00EFA08A', X'30480F07E24FF1439E20B5EB00EFA08A', 0, NULL, NULL, 2, NULL, 0, 'Add carrots, celery, onion, potato, chicken broth, salt and thyme to a 4- to 6-quart oval slow cooker.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2250CB824F1CB4D82AC14AF040CB572', X'30480F07E24FF1439E20B5EB00EFA08A', X'30480F07E24FF1439E20B5EB00EFA08A', 1, NULL, NULL, 2, NULL, 0, 'Cover and cook on low-heat setting about 3 hours or on high setting about 1 1/2 hours or until potatoes are tender.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'851B1D225F7D574D9EB5264824E5DDC6', X'30480F07E24FF1439E20B5EB00EFA08A', X'30480F07E24FF1439E20B5EB00EFA08A', 2, NULL, NULL, 2, NULL, 0, 'Stir in corn, milk and turkey. Cover and cook about 1 hour on high or until turkey is cooked to 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'753E6F051123EE4E902B99877D313066', X'30480F07E24FF1439E20B5EB00EFA08A', X'30480F07E24FF1439E20B5EB00EFA08A', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl stir together cornstarch and water; stir into the chowder mixture. Cover and cook until chowder begins to bubble and thickens slightly, about 10-15 minutes. Season to taste with salt and pepper. Garnish with parsley, if desired, and serve with breadsticks.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD1FCF6247531542AD85DAD0AFCA000F', X'3E18F8C552CB5B40929159ECF8A3EF20', X'3E18F8C552CB5B40929159ECF8A3EF20', 0, NULL, NULL, 2, NULL, 0, 'Preheat lightly oiled grill to 500°F - 600°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4173A9A5AE318341BE4C99BAC132E827', X'3E18F8C552CB5B40929159ECF8A3EF20', X'3E18F8C552CB5B40929159ECF8A3EF20', 1, NULL, NULL, 2, NULL, 0, 'In small bowl, whisk together mayonnaise and mustard.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD471189FF5FF3498724C4B3988E7462', X'3E18F8C552CB5B40929159ECF8A3EF20', X'3E18F8C552CB5B40929159ECF8A3EF20', 2, NULL, NULL, 2, NULL, 0, 'Season turkey patties with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EB592FE2CF64A48A19D33D8F8CC660B', X'3E18F8C552CB5B40929159ECF8A3EF20', X'3E18F8C552CB5B40929159ECF8A3EF20', 3, NULL, NULL, 2, NULL, 0, 'Place patties on grill and cook for 4-5 minutes per side until cooked through and a meat thermometer reads 165°F throughout the patty.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D41583A7A4FDD646B81EC08CA6614D0F', X'3E18F8C552CB5B40929159ECF8A3EF20', X'3E18F8C552CB5B40929159ECF8A3EF20', 4, NULL, NULL, 2, NULL, 0, 'Spread mayonnaise mixture on cut sides of buns.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A32F1212562FE43AA47B49F095271A2', X'3E18F8C552CB5B40929159ECF8A3EF20', X'3E18F8C552CB5B40929159ECF8A3EF20', 5, NULL, NULL, 2, NULL, 0, 'Place turkey patties in buns and top each patty with 1 lettuce leaf, 1 tomato slice and 2 bacon strips.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9118D67C75A9464AB419FD3363C1DC93', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 0, NULL, NULL, 2, NULL, 0, 'Tip: If using Honeysuckle White HomeStyle Turkey Meatloaf, skip to step 9.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD34611A83166D44932AFED3D1E5B3EE', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Spray a 9×5? loaf pan with food release spray and then line with aluminum foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63264A40D8C72D47B8AC6053C5494EEC', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 2, NULL, NULL, 2, NULL, 0, 'Heat oil in a sauté pan until shimmering. Add the onions and sauté over medium heat for 4-5 minutes, or until softened.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27B4101A8A30034FA79061562E74A1D0', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic and sauté for 1 minute.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71F4C68FF597234F9AB9469BE088F60F', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 4, NULL, NULL, 2, NULL, 0, 'Add the mushrooms, salt, and pepper. Increase heat to high and sauté mushrooms, stirring frequently for about 5 minutes or until done.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D646B4861D529A4D9442A8329B82A522', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 5, NULL, NULL, 2, NULL, 0, 'Transfer the onions and mushrooms to a large bowl.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C3BC03C1CEA9D419DE20E1CE91A7DA3', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 6, NULL, NULL, 2, NULL, 0, 'Combine the panko breadcrumbs and milk in a small bowl. Add the eggs and stir well to combine.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD78897518D0684293EC988EDC70808D', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 7, NULL, NULL, 2, NULL, 0, 'Add the breadcrumb-egg mixture to the sautéed mushrooms, and then add the ground turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB33BFCA2288CD4592619E3341A2CAAB', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 8, NULL, NULL, 2, NULL, 0, 'Using a rubber spatula or your hand, gently mix all ingredients until combined. Do not over mix.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19B86F3DC7CE614EB5D9EA3956D5E623', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 9, NULL, NULL, 2, NULL, 0, 'Scrape the meatloaf mixture into the foil-lined pan and smooth the top.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5528DAAF4A4F4D479D045D31FE729E9F', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 10, NULL, NULL, 2, NULL, 0, 'Brush about 1/8 cup of thai chili sauce evenly over the meatloaf. Cover with a layer of aluminum foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'369624A7D8552640A7D431E83B876D20', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 11, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes, then remove foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3313B0EB013BDE4E9BCAD99E090F47D3', X'D2DFCD589BE8144F9B509303DBA56188', X'D2DFCD589BE8144F9B509303DBA56188', 12, NULL, NULL, 2, NULL, 0, 'Continue baking 5-10 more minutes. Turkey is fully cooked when meat reaches an internal temperature of 165°F using a meat thermometer.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DC7B1027DD61848BB7AF7462A15C0C0', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB5A39A625DEC4468C1EBF8AFD1DFC36', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a large nonstick skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'052143FDE89DAB4CB183B58280743017', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', 2, NULL, NULL, 2, NULL, 0, 'Cook turkey to 165°F using a meat thermometer, about 10 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'629B1A93B0AA9C4DA07EFFF3E3CBCC45', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix sour cream and ranch dressing.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDE88AAC0EC4A244B6F49BEF3B15AFDB', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', 4, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine 1 cup sour cream mixture and turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15159C2DD6315F4FA50014103E3316E0', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', 5, NULL, NULL, 2, NULL, 0, 'Divide turkey mixture, cheese and salsa evenly over tortillas.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'670C2D1156DFC247BDE82338EB421929', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', 6, NULL, NULL, 2, NULL, 0, 'Roll up and place seam-side down in a greased 9 x 11-inch baking pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2B73F8EB830F1468AA61F774D516E67', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', 7, NULL, NULL, 2, NULL, 0, 'Bake, uncovered, for 25-30 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0654BFDD327B9B42B59D35BDE4783723', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', X'BFEC1B0F66BA0B4CB9A786C5F6FDFDFD', 8, NULL, NULL, 2, NULL, 0, 'Top with tomato, green onion and any remaining salsa and/or sour cream mixture.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D80FAD2FBB94AA4492E2508CC91BCC54', X'BE0EC6603170A9458C654F60FCF9BD3D', X'BE0EC6603170A9458C654F60FCF9BD3D', 0, NULL, NULL, 2, NULL, 0, 'To make spicy cranberry sauce, place ingredients in medium saucepan. Boil 15 minutes, stirring occasionally, until cranberries burst and sauce is thickened; refrigerate.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB7D0FB821CFB04EB5B3943D69FE9DC6', X'BE0EC6603170A9458C654F60FCF9BD3D', X'BE0EC6603170A9458C654F60FCF9BD3D', 1, NULL, NULL, 2, NULL, 0, 'Sauté cutlets in lightly oiled skillet over medium heat approximately 5 minutes per side or until turkey is no longer pink in the center and reaches internal temperature of 170°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DDE3B5BFF7A80458F46863444F22BB8', X'BE0EC6603170A9458C654F60FCF9BD3D', X'BE0EC6603170A9458C654F60FCF9BD3D', 2, NULL, NULL, 2, NULL, 0, 'Cool and cut into strips.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCD4A36C7D55524580FA57FFE3660A53', X'BE0EC6603170A9458C654F60FCF9BD3D', X'BE0EC6603170A9458C654F60FCF9BD3D', 3, NULL, NULL, 2, NULL, 0, 'Spread 2 tablespoons cheese over tortillas to 1/2 inch from edge.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A8C5E6BE305724EB0EE57552B555846', X'BE0EC6603170A9458C654F60FCF9BD3D', X'BE0EC6603170A9458C654F60FCF9BD3D', 4, NULL, NULL, 2, NULL, 0, 'Layer lettuce, 1/2 cup turkey, 1/4 cup spicy cranberry sauce, 1/2 teaspoon cilantro and several apple slices. Repeat three times. Fold tortilla bottom and top over filling, then fold one side to center. Fold last side to overlap center and roll tight.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36C2EEC2557F7441AEC1ADFD0EB95B6B', X'C5F5606661F9F843897395F9984A7D25', X'C5F5606661F9F843897395F9984A7D25', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450°F and line a rimmed baking sheet with foil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F738976478492344BC800FA3E124DBEB', X'C5F5606661F9F843897395F9984A7D25', X'C5F5606661F9F843897395F9984A7D25', 1, NULL, NULL, 2, NULL, 0, 'Put the Brussels sprouts on the baking sheet, drizzle with a tablespoon of olive oil and sprinkle with salt. Transfer to the oven and roast until tender, about 25 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12BEB968607E0F4797B2E5B9BF8BFE71', X'C5F5606661F9F843897395F9984A7D25', X'C5F5606661F9F843897395F9984A7D25', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, add a tablespoon of olive oil and bacon and cook, stirring frequently, until the bacon is well browned, 8-10 minutes. Remove the bacon, drain on paper towels and crumble or roughly chop.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8889A84F1ACEEB498205892BD537B39C', X'C5F5606661F9F843897395F9984A7D25', X'C5F5606661F9F843897395F9984A7D25', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining tablespoon of olive oil and vinegar to the skillet (the vinegar will sizzle immediately), scraping up any browned bits in the pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D6A49B7DE9575458682387CD3059373', X'C5F5606661F9F843897395F9984A7D25', X'C5F5606661F9F843897395F9984A7D25', 4, NULL, NULL, 2, NULL, 0, 'Take off the heat and whisk in the honey and mustard. Set aside.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4E81BC154DCCE4E867FF76CD3956C1A', X'C5F5606661F9F843897395F9984A7D25', X'C5F5606661F9F843897395F9984A7D25', 5, NULL, NULL, 2, NULL, 0, 'Transfer sprouts to a serving dish and drizzle with the dressing. Sprinkle with the bacon. Serve immediately.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFB226ABD6516246B762B57B385DFC60', X'3681875CF3FA51499F6E7A63F183EFF4', X'3681875CF3FA51499F6E7A63F183EFF4', 0, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, whisk together the garlic, yogurt, cucumber, dill, lemon juice, salt and pepper, and mix in the cucumber and dill. Refrigerate until serving.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89FCFB4E8B270A49B0ACE32B0250B267', X'3681875CF3FA51499F6E7A63F183EFF4', X'3681875CF3FA51499F6E7A63F183EFF4', 1, NULL, NULL, 2, NULL, 0, 'Combine boiling water and bulgur in a medium bowl. Let stand until bulgur is tender, about 15 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FD6FA458D4BA74DBAFF9776AA9A7A18', X'3681875CF3FA51499F6E7A63F183EFF4', X'3681875CF3FA51499F6E7A63F183EFF4', 2, NULL, NULL, 2, NULL, 0, 'Drain in a fine mesh strainer and cool completely. Squeeze any excess liquid from bulgur.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6282532DE197224296F3B584A3AAF5AF', X'3681875CF3FA51499F6E7A63F183EFF4', X'3681875CF3FA51499F6E7A63F183EFF4', 3, NULL, NULL, 2, NULL, 0, 'In a food processor combine the garlic, bulgur, onion, parsley, yogurt, cumin, paprika, salt and pepper, and pulse until the onion is minced.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCD749777EB463438C8F7F8B2549756A', X'3681875CF3FA51499F6E7A63F183EFF4', X'3681875CF3FA51499F6E7A63F183EFF4', 4, NULL, NULL, 2, NULL, 0, 'Add turkey and process, stopping occasionally to stir, until well combined. (Or mince onion, parsley and garlic by hand, and stir ingredients together in a large bowl.)');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14D4CCBC37A8864B858E0E13988B50D8', X'3681875CF3FA51499F6E7A63F183EFF4', X'3681875CF3FA51499F6E7A63F183EFF4', 5, NULL, NULL, 2, NULL, 0, 'Drain soaked skewers. Divide turkey mixture equally into 12 sausage-shaped kofta, each about 4 inches long and 1 inch wide. Thread each onto a skewer and brush with oil.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55BD7DACBB4D194791E65AA6CC577E00', X'3681875CF3FA51499F6E7A63F183EFF4', X'3681875CF3FA51499F6E7A63F183EFF4', 6, NULL, NULL, 2, NULL, 0, 'Preheat grill for direct cooking over medium heat (about 400° F). Lightly oil grill grate.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B842B4646B57643ACF20F0F3EC4C729', X'3681875CF3FA51499F6E7A63F183EFF4', X'3681875CF3FA51499F6E7A63F183EFF4', 7, NULL, NULL, 2, NULL, 0, 'Place kofta on grill, perpendicular to grate. Cook, with lid closed, turning once or twice with a thin metal spatula, until firm when pressed, 10 to 12 minutes. To discourage burned skewers, slip a strip of aluminum foil under exposed skewer handles. (The kofta can also be cooked in a broiler for 10 to 12 minutes. To keep the handles from burning, wrap each in a small strip of aluminum foil.)');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D03EB3FCBD39D444B666ADC922CC96A1', X'3681875CF3FA51499F6E7A63F183EFF4', X'3681875CF3FA51499F6E7A63F183EFF4', 8, NULL, NULL, 2, NULL, 0, 'Remove kofta from grill and serve with tzatziki sauce and thinly sliced vegetables in pita.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7928340AEB87B74D98F0AA3FB7A763F1', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E67492534A940841BD0A38963A160240', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the miso and honey to make a thick paste.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E2F8D5A89F72D4BA229383285115B87', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', 2, NULL, NULL, 2, NULL, 0, 'Place turkey on a roasting rack in a roasting pan and rub the surface with the miso mixture, using just enough to coat it.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A80D1BE3490F9940A688ADAFBC62821C', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', 3, NULL, NULL, 2, NULL, 0, 'Roast the turkey according to package instructions, until an instant-read thermometer registers 165° F when inserted into the thickest part of the breast. Cover the breast loosely with aluminum foil if necessary to prevent overbrowning.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C44989103B7AC64FB60594BA06F60671', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', 4, NULL, NULL, 2, NULL, 0, 'Let roast rest 15 minutes, then slice 8 thin slices for the soup.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8518E10E3BE88C42898EEF7F4BBE747A', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', 5, NULL, NULL, 2, NULL, 0, 'Whisk together the chicken broth, gravy, miso and soy sauce in a saucepan. Heat gently over medium-low heat until very hot.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB3764BE7125A041B48287617B1EA1B0', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', 6, NULL, NULL, 2, NULL, 0, 'Cook noodles in a large pot of boiling water according to package directions until al dente; drain.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E3774F0FA709D4095FE1D1B070DCE93', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', 7, NULL, NULL, 2, NULL, 0, 'Divide noodles among bowls. Top each bowl with 2 overlapping slices of turkey, placing it off to one side. Ladle broth over turkey and noodles.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1B489CACBE723439225433CA21627F2', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', 8, NULL, NULL, 2, NULL, 0, 'Cut the eggs in half lengthwise and place a half, yolk side up, on top of noodles in each bowl.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66AD0C63BDB1D9438B25F3F1E366CDEA', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', X'6EBD8F2F308FEA4CAABFFA3F6EB07241', 9, NULL, NULL, 2, NULL, 0, 'Garnish each bowl with a pile of green onions.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63E5D6C0AC248D43B4C9191E1AE9E2E4', X'4B203FCD42BD7A4EA7DF77CE316D70D5', X'4B203FCD42BD7A4EA7DF77CE316D70D5', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Place sausages on a greased sheet pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F76E3176161C74CB5E128E80BDCF0E3', X'4B203FCD42BD7A4EA7DF77CE316D70D5', X'4B203FCD42BD7A4EA7DF77CE316D70D5', 1, NULL, NULL, 2, NULL, 0, 'Roast sausages for 12-15 minutes, or until an internal temperature of 165° F is reached.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D6C47E78289F244856E7C3FE7A61F69', X'4B203FCD42BD7A4EA7DF77CE316D70D5', X'4B203FCD42BD7A4EA7DF77CE316D70D5', 2, NULL, NULL, 2, NULL, 0, 'Remove cooked sausages from oven and reserve.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB8136C0CB495B4DAB5796C73CFEE96C', X'4B203FCD42BD7A4EA7DF77CE316D70D5', X'4B203FCD42BD7A4EA7DF77CE316D70D5', 3, NULL, NULL, 2, NULL, 0, 'Make the tomato sauce. Heat the olive oil in a 4-quart saucepan over medium-high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D773838BD49B644596A2DA2E96C7A47F', X'4B203FCD42BD7A4EA7DF77CE316D70D5', X'4B203FCD42BD7A4EA7DF77CE316D70D5', 4, NULL, NULL, 2, NULL, 0, 'Add onions and garlic and stir constantly until onions are soft and garlic is light gold, about 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FA28873C2076A4FB72A53DF4294B1C8', X'4B203FCD42BD7A4EA7DF77CE316D70D5', X'4B203FCD42BD7A4EA7DF77CE316D70D5', 5, NULL, NULL, 2, NULL, 0, 'Add the crushed tomatoes to the sautéed onions and garlic. Bring sauce to a boil, and then reduce heat to low and simmer while you cut the sausages.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0370BC90944F624EAD04C1CFA85750E4', X'4B203FCD42BD7A4EA7DF77CE316D70D5', X'4B203FCD42BD7A4EA7DF77CE316D70D5', 6, NULL, NULL, 2, NULL, 0, 'When sausages are cool enough to handle, cut on the bias into 1/2-inch thick slices.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03CECEEB73EE564EBE36788C4D06391E', X'4B203FCD42BD7A4EA7DF77CE316D70D5', X'4B203FCD42BD7A4EA7DF77CE316D70D5', 7, NULL, NULL, 2, NULL, 0, 'Add sausages to tomato sauce and simmer for 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C12C732B1A84A347A78071495FAF5024', X'4B203FCD42BD7A4EA7DF77CE316D70D5', X'4B203FCD42BD7A4EA7DF77CE316D70D5', 8, NULL, NULL, 2, NULL, 0, 'Season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCF7ECCEC604EF49A73FE5D2C1E46610', X'4B203FCD42BD7A4EA7DF77CE316D70D5', X'4B203FCD42BD7A4EA7DF77CE316D70D5', 9, NULL, NULL, 2, NULL, 0, 'Ladle sauce over hot rigatoni. Serve with shaved Parmesan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D487BA3D44B6D44EACC49C9DE5912B99', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', 0, NULL, NULL, 2, NULL, 0, 'Cut each turkey thigh into three pieces.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B75D8ADB306F9346BCF308798ECB049C', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a large nonstick skillet over medium heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40936172C688C042862BABE5AB69540F', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', 2, NULL, NULL, 2, NULL, 0, 'Cook turkey until no longer pink, about 2 to 3 minutes per side for each piece.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80522B850FE1004CA5760CC134A7F11F', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', 3, NULL, NULL, 2, NULL, 0, 'Combine tomatoes, garlic, Italian seasoning, salt and pepper; mix well.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE90D1A6332988478BD5390ABBE242CA', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', 4, NULL, NULL, 2, NULL, 0, 'Spoon over turkey. Reduce heat; simmer partially covered for 25 to 30 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2742E8019870274692824CDF22F52DBD', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', X'7D28A73CF0EB814EB1E23E76DE7E8A9C', 5, NULL, NULL, 2, NULL, 0, 'Add mushrooms, zucchini, yellow squash and onion. Continue simmering partially covered for 7 to 10 minutes or until vegetables are tender-crisp and turkey is fork-tender.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FC6A755A33A6B4897F5CEB300C6C28E', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', 0, NULL, NULL, 2, NULL, 0, 'In small bowl, combine cumin, salt, black pepper, cinnamon and cayenne pepper; rub turkey with spice mixture.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F739DA30152899449EAE8C4B0D0F7199', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', 1, NULL, NULL, 2, NULL, 0, 'In a large-size slow cooker, combine garbanzos, tomatoes, onion, carrots and ginger; place turkey on top.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80FF59A3AEA12A4C99A236816CEBAC19', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', 2, NULL, NULL, 2, NULL, 0, 'Cook on HIGH 3 to 4 hours or until a thermometer inserted into the thickest part of the breast registers 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69FB71889F1E45429E94A05CCA5CC5E3', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', 3, NULL, NULL, 2, NULL, 0, 'Remove skin and slice turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70C08E5792328C4AB9754CB3B9B21705', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', 4, NULL, NULL, 2, NULL, 0, 'Arrange turkey on a serving platter; spoon vegetables and sauce on top');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15927DC144EFEA4985942472502F76AC', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle with cilantro.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48E0A984012EE846A099381B13529CCA', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', X'70CFB6B7794EAC48BEFC1CDEC6F114AB', 6, NULL, NULL, 2, NULL, 0, 'Serve with couscous mixed with cilantro (optional).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4687E64AEE8794D82558EE538D0E652', X'84425E5B25FBEC47A3A40BCB7433C13C', X'84425E5B25FBEC47A3A40BCB7433C13C', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF07DBDB51C8ED4DB853B204B0F30D53', X'84425E5B25FBEC47A3A40BCB7433C13C', X'84425E5B25FBEC47A3A40BCB7433C13C', 1, NULL, NULL, 2, NULL, 0, 'Whisk together apricot jam, orange juice and ginger.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76FC951285ED6146A0FFA7D6F78C39BD', X'84425E5B25FBEC47A3A40BCB7433C13C', X'84425E5B25FBEC47A3A40BCB7433C13C', 2, NULL, NULL, 2, NULL, 0, 'Season turkey with salt and pepper; brush with apricot jam mixture.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2FC0FF959B17B41BB71C66A575ECE0A', X'84425E5B25FBEC47A3A40BCB7433C13C', X'84425E5B25FBEC47A3A40BCB7433C13C', 3, NULL, NULL, 2, NULL, 0, 'Roast about 30 minutes, basting occasionally with pan juices, until internal temperature reaches 170°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF920D5678FC3347BB25DE5540E139C7', X'84425E5B25FBEC47A3A40BCB7433C13C', X'84425E5B25FBEC47A3A40BCB7433C13C', 4, NULL, NULL, 2, NULL, 0, 'Transfer turkey to platter; keep warm.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56E5D7B1E95F9A4194488C0B06224D59', X'84425E5B25FBEC47A3A40BCB7433C13C', X'84425E5B25FBEC47A3A40BCB7433C13C', 5, NULL, NULL, 2, NULL, 0, 'Pour pan juices into skillet over medium heat, add apricots and simmer until apricots are warm.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9E8CC6B26AB47438D3A5AB6AC69DAF0', X'84425E5B25FBEC47A3A40BCB7433C13C', X'84425E5B25FBEC47A3A40BCB7433C13C', 6, NULL, NULL, 2, NULL, 0, 'Spoon sauce and apricots over turkey. Serve with asparagus roasted with olive oil and salt and pepper (optional).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85C56A309E86724AA85E88E7736D3978', X'731F97BD6EE74C4E8CD8BB460183D5BF', X'731F97BD6EE74C4E8CD8BB460183D5BF', 0, NULL, NULL, 2, NULL, 0, 'Preheat charcoal or gas grill.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C23B16C75330946AADE7DDEAC002AC2', X'731F97BD6EE74C4E8CD8BB460183D5BF', X'731F97BD6EE74C4E8CD8BB460183D5BF', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a large nonstick skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'550806BBC3A6AE47AF4C8B4269427F04', X'731F97BD6EE74C4E8CD8BB460183D5BF', X'731F97BD6EE74C4E8CD8BB460183D5BF', 2, NULL, NULL, 2, NULL, 0, 'Cook onion and mushroom until softened, about 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11EA9667FFA98346B1207D3AAC5C26FF', X'731F97BD6EE74C4E8CD8BB460183D5BF', X'731F97BD6EE74C4E8CD8BB460183D5BF', 3, NULL, NULL, 2, NULL, 0, 'Wrap each tenderloin with 3 strips of bacon; secure with toothpick.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07959E690E0D344597696F9BCF32E43E', X'731F97BD6EE74C4E8CD8BB460183D5BF', X'731F97BD6EE74C4E8CD8BB460183D5BF', 4, NULL, NULL, 2, NULL, 0, 'Grill turkey for 5 minutes on each side.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEBD01824B15FE4684459CD7D9C6ECBD', X'731F97BD6EE74C4E8CD8BB460183D5BF', X'731F97BD6EE74C4E8CD8BB460183D5BF', 5, NULL, NULL, 2, NULL, 0, 'Cover and grill for 10 more minutes or until no longer pink and internal temperature reaches 165°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8FA8667D87F804E8703FC9C737AAECB', X'731F97BD6EE74C4E8CD8BB460183D5BF', X'731F97BD6EE74C4E8CD8BB460183D5BF', 6, NULL, NULL, 2, NULL, 0, 'Remove turkey; top with onions, mushrooms and cheese.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B3423988F48E941919D44B9978D189E', X'731F97BD6EE74C4E8CD8BB460183D5BF', X'731F97BD6EE74C4E8CD8BB460183D5BF', 7, NULL, NULL, 2, NULL, 0, 'Garnish with parsley.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B8CC6656C443E4590C45708CAE0E9A5', X'F7A3EC893D4C504B993E006A08ABD0F7', X'F7A3EC893D4C504B993E006A08ABD0F7', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350°F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2800C47C8FE0604198CA8F58A1D40961', X'F7A3EC893D4C504B993E006A08ABD0F7', X'F7A3EC893D4C504B993E006A08ABD0F7', 1, NULL, NULL, 2, NULL, 0, 'Pat the turkey breast dry with paper towels and place on a rack in the roasting pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D427B19D740AC44D9B06087EA352B151', X'F7A3EC893D4C504B993E006A08ABD0F7', X'F7A3EC893D4C504B993E006A08ABD0F7', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl combine the olive oil, lemon juice, sage, parsley, lemon zest and salt.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'755647B8F0595947BA5B4FF1B757A729', X'F7A3EC893D4C504B993E006A08ABD0F7', X'F7A3EC893D4C504B993E006A08ABD0F7', 3, NULL, NULL, 2, NULL, 0, 'Slide a few whole sage leaves under the skin of the roast and rub the top and sides of the turkey breast with the herb mixture.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A680FBB3A827A43919A0953A23CAC31', X'F7A3EC893D4C504B993E006A08ABD0F7', X'F7A3EC893D4C504B993E006A08ABD0F7', 4, NULL, NULL, 2, NULL, 0, 'Roast for 30 minutes, then add the lemon slices to the roasting pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBE304A34A6EC348A9A4F1B7BDE571BC', X'F7A3EC893D4C504B993E006A08ABD0F7', X'F7A3EC893D4C504B993E006A08ABD0F7', 5, NULL, NULL, 2, NULL, 0, 'Continue roasting about another 20 minutes or until juices run clear and a meat thermometer reads 165° F.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'104B6D8C33203F46BF6BBA590614BD9A', X'F7A3EC893D4C504B993E006A08ABD0F7', X'F7A3EC893D4C504B993E006A08ABD0F7', 6, NULL, NULL, 2, NULL, 0, 'Let stand for 10-15 minutes before slicing.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A38B3F8FD8335D46B41911DEB8B344F3', X'F7A3EC893D4C504B993E006A08ABD0F7', X'F7A3EC893D4C504B993E006A08ABD0F7', 7, NULL, NULL, 2, NULL, 0, 'Garnish with thyme sprigs, if desired.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B13D4CAB038F7B4A9220CB979C696619', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', 0, NULL, NULL, 2, NULL, 0, 'In a large sauté pan, brown turkey sausage over medium-high heat until no pink remains.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8223F3FFA6323A4B9FB5D9D5D151BF5D', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', 1, NULL, NULL, 2, NULL, 0, 'Add butter to the pan and melt.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9777B5DF484B744583AFA5AAE128AEA5', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', 2, NULL, NULL, 2, NULL, 0, 'Add diced onion and sauté until softened, about 3-4 minutes. Add minced garlic and sauté 30-45 seconds. Do not brown garlic.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDC1321F6E71EA41AD4AD46E07ED0098', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', 3, NULL, NULL, 2, NULL, 0, 'Stir in flour. Reduce heat to medium and cook the roux for 4-5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13F5BEAE9E8D49409F968E7C04C3AB92', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', 4, NULL, NULL, 2, NULL, 0, 'Using a wire whisk, slowly stir in milk, half and half, kosher salt and black pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D28FBAC2235AD40B5416C2BE40853A9', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', 5, NULL, NULL, 2, NULL, 0, 'Stir constantly until gravy has thickened.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F36A075314CE8744A0CF59315003D5ED', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', 6, NULL, NULL, 2, NULL, 0, 'Reduce heat to low and simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7E4DAE57575DF4E86AC91E3235E3655', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', 7, NULL, NULL, 2, NULL, 0, 'Remove from heat. Add Worcestershire sauce and Tabasco to taste. Adjust seasoning.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37DCD5FF87BB16489FC6E11A6BA12EAA', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', X'41ABD18AAA8A7B4EA4E1FA536B364FF8', 8, NULL, NULL, 2, NULL, 0, 'Serve over hot thyme buttermilk biscuits.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D954C22F44EB5E498E5F9CDCDF35B587', X'3E8D836B3929C84AA53DB4A3581FDDB3', X'3E8D836B3929C84AA53DB4A3581FDDB3', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F. In a medium bowl or resealable plastic bag combine the lime juice, oil and salt. Add the turkey cutlets and set aside for 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E090C15F6D56F14C9F689B79B0DDCAD6', X'3E8D836B3929C84AA53DB4A3581FDDB3', X'3E8D836B3929C84AA53DB4A3581FDDB3', 1, NULL, NULL, 2, NULL, 0, 'Lightly spray both sides of the tortillas with cooking spray and arrange them in a single layer on a rimmed baking sheet. Lightly season the tortillas with salt and bake until crisp, about 10 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDBDAFB2A73E624EACCC9CF945DC25A2', X'3E8D836B3929C84AA53DB4A3581FDDB3', X'3E8D836B3929C84AA53DB4A3581FDDB3', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat a grill or grill pan over medium-high heat. Remove the turkey from marinade, pat dry with paper towels and grill until browned and cooked through, about 5 minutes per side. Let rest until cool enough to handle, about 5 minutes; slice thinly or shred.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8F2143B71B6FD4D822DF2C932F2F129', X'3E8D836B3929C84AA53DB4A3581FDDB3', X'3E8D836B3929C84AA53DB4A3581FDDB3', 3, NULL, NULL, 2, NULL, 0, 'Heat the refried beans in a small nonstick saucepan over low heat or in the microwave in a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66C4D978E80E96449865E1A7AD0C25C3', X'3E8D836B3929C84AA53DB4A3581FDDB3', X'3E8D836B3929C84AA53DB4A3581FDDB3', 4, NULL, NULL, 2, NULL, 0, 'Spread each tortilla with refried beans, turkey and a tablespoon of salsa, top with avocado and lettuce, plus a dollop of sour cream, if you like.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1201AF91694CE544B8FDB35C05CD5605', X'B3299B24C361FE47849B30DFCDFB6FEE', X'B3299B24C361FE47849B30DFCDFB6FEE', 0, NULL, NULL, 2, NULL, 0, 'Combine the onion, curry powder and ginger in a 4- to 6-quart oval slow cooker.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'900EBF61AFDD1F4784FEED98E42B3A9A', X'B3299B24C361FE47849B30DFCDFB6FEE', X'B3299B24C361FE47849B30DFCDFB6FEE', 1, NULL, NULL, 2, NULL, 0, 'Stir in the chicken broth, coconut milk and the turkey wing. Cover and cook for 4 hours on low or 2 hours on high or until turkey is fork tender.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFDC911D991A5247BCF0A2768E2B5C56', X'B3299B24C361FE47849B30DFCDFB6FEE', X'B3299B24C361FE47849B30DFCDFB6FEE', 2, NULL, NULL, 2, NULL, 0, 'Remove the turkey wing from the broth. When cool enough to handle strip off the skin and discard. Pull the meat from the bones and shred into small pieces. Add the meat back to the slow cooker and discard the bones.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCE477E9E417AB4D9D90B2DCA4A58701', X'B3299B24C361FE47849B30DFCDFB6FEE', X'B3299B24C361FE47849B30DFCDFB6FEE', 3, NULL, NULL, 2, NULL, 0, 'Break up the rice noodles and add them and the red pepper to the slow cooker. Cover and cook until noodles are cooked, about 20 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE5CE880CDA3DE47A85C54AD239516FB', X'B3299B24C361FE47849B30DFCDFB6FEE', X'B3299B24C361FE47849B30DFCDFB6FEE', 4, NULL, NULL, 2, NULL, 0, 'Season with soy sauce to taste. Serve each bowl of soup with bean sprouts, lime, cilantro and jalapeños.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFF95EFC3E8B3D42B469B705F7797932', X'EB011050B2A07748B6FE58CBDFB4A8A4', X'EB011050B2A07748B6FE58CBDFB4A8A4', 0, NULL, NULL, 2, NULL, 0, 'Combine the onion, cranberry sauce, chicken broth, vinegar and salt in a 4- to 6-quart oval slow cooker.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B862A71FD626F4E8EFA6E48CF77380D', X'EB011050B2A07748B6FE58CBDFB4A8A4', X'EB011050B2A07748B6FE58CBDFB4A8A4', 1, NULL, NULL, 2, NULL, 0, 'Add the turkey drumsticks to the slow cooker and coat with the cranberry mixture.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7E579C2AF0AB449B9974FE02320CC36', X'EB011050B2A07748B6FE58CBDFB4A8A4', X'EB011050B2A07748B6FE58CBDFB4A8A4', 2, NULL, NULL, 2, NULL, 0, 'Cover and cook until tender, about 4 hours on high, 6 hours on low.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FC5CDBBCBE25F479FD002D3CE614E2C', X'EB011050B2A07748B6FE58CBDFB4A8A4', X'EB011050B2A07748B6FE58CBDFB4A8A4', 3, NULL, NULL, 2, NULL, 0, 'Add the frozen cranberries and cook for another 15 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'745D80456F421F4EBF67D2E1EB5CFDC2', X'EB011050B2A07748B6FE58CBDFB4A8A4', X'EB011050B2A07748B6FE58CBDFB4A8A4', 4, NULL, NULL, 2, NULL, 0, 'Combine the cornstarch and water and gently stir into the slow cooker. Cover and cook for an additional 10 minutes or until sauce bubbles and thickens.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA590551D592054EA2E20155741A95BC', X'EB011050B2A07748B6FE58CBDFB4A8A4', X'EB011050B2A07748B6FE58CBDFB4A8A4', 5, NULL, NULL, 2, NULL, 0, 'Pull the meat from the bones. Serve with mashed potatoes and roasted Brussels sprouts (optional).');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F31830241A3784ABC000AD2CD47E494', X'E14601FBF7F15840B799AF07BDD92E54', X'E14601FBF7F15840B799AF07BDD92E54', 0, NULL, NULL, 2, NULL, 0, 'In large soup pot, heat oil over medium heat. Add turkey; cook to 165°F using a meat thermometer, stirring occasionally to break up turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7B5106A2C6DB54781A30413B57599F4', X'E14601FBF7F15840B799AF07BDD92E54', X'E14601FBF7F15840B799AF07BDD92E54', 1, NULL, NULL, 2, NULL, 0, 'Add onion, flour and chili powder; cook about 5 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'853FAC67FF526B40A03491C21B909B22', X'E14601FBF7F15840B799AF07BDD92E54', X'E14601FBF7F15840B799AF07BDD92E54', 2, NULL, NULL, 2, NULL, 0, 'Add beans, chicken broth and chiles; bring to a simmer and cook, covered, about 30 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8C3913197F2E845B41CFA77CC20AE12', X'E14601FBF7F15840B799AF07BDD92E54', X'E14601FBF7F15840B799AF07BDD92E54', 3, NULL, NULL, 2, NULL, 0, 'Stir in cilantro.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F775EC7FFFB81540A6DAAB258920507D', X'E14601FBF7F15840B799AF07BDD92E54', X'E14601FBF7F15840B799AF07BDD92E54', 4, NULL, NULL, 2, NULL, 0, 'Serve with tortilla chips or warm corn tortillas and sour cream.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'435C0CDC157C2C409AFC54241F5CC504', X'EB10E73F7F6D7947A8FEC95B338871CE', X'EB10E73F7F6D7947A8FEC95B338871CE', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350° F. Place the turkey breast on a rack in a roasting pan.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5B9FB4FEE2F4D45A88E2F87EFD9E74B', X'EB10E73F7F6D7947A8FEC95B338871CE', X'EB10E73F7F6D7947A8FEC95B338871CE', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the garlic, mustard, salt, lemon zest, onion powder and lemon juice. Spread the paste evenly on the turkey.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0158EFE7D5927F41AC611040E903D813', X'EB10E73F7F6D7947A8FEC95B338871CE', X'EB10E73F7F6D7947A8FEC95B338871CE', 2, NULL, NULL, 2, NULL, 0, 'Roast the turkey according to package instructions, until the skin is golden brown and an instant-read thermometer registers 165° F when inserted into the thickest part of the breast.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9F77F62879D794793873A92E3C76E61', X'EB10E73F7F6D7947A8FEC95B338871CE', X'EB10E73F7F6D7947A8FEC95B338871CE', 3, NULL, NULL, 2, NULL, 0, 'Allow the turkey to rest at room temperature for 10-15 minutes before slicing.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB177FF91131B046A949EF5A0153FA10', X'EB10E73F7F6D7947A8FEC95B338871CE', X'EB10E73F7F6D7947A8FEC95B338871CE', 4, NULL, NULL, 2, NULL, 0, 'Place quinoa in a fine mesh sieve. Rinse thoroughly under cold water.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A37B8F1FB1961242A2062927C6C315D0', X'EB10E73F7F6D7947A8FEC95B338871CE', X'EB10E73F7F6D7947A8FEC95B338871CE', 5, NULL, NULL, 2, NULL, 0, 'Add quinoa to a medium saucepan; add a pinch of salt and the water. Bring to a boil, cover and reduce to a simmer. Cook until water is completely absorbed, about 20 minutes.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BF3A5219CF3974995E755DC7F4C1105', X'EB10E73F7F6D7947A8FEC95B338871CE', X'EB10E73F7F6D7947A8FEC95B338871CE', 6, NULL, NULL, 2, NULL, 0, 'Transfer quinoa to a medium bowl along with chickpeas, lemon zest, parsley, sun-dried tomatoes, green onions, lemon juice and olive oil. Season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Honeysucklewhite recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F919B28719B38048BB4BC097E3CC33C6', X'EB10E73F7F6D7947A8FEC95B338871CE', X'EB10E73F7F6D7947A8FEC95B338871CE', 7, NULL, NULL, 2, NULL, 0, 'Serve slices of turkey over a bed of the quinoa.');
