INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E597B8A28D58B149AFAFB08508FEFF08', X'23E2A63A9E38C14799D6F0031DECF8F9', X'23E2A63A9E38C14799D6F0031DECF8F9', 0, NULL, NULL, 2, NULL, 0, 'Remove the skin from your eggplant and zucchini then place both on a roasting pan or baking pan and drizzle each with olive oil and season with salt and pepper. Roast at 400 for 10-12 minutes. Remove and allow to cool.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B2808BEB4527C49B41E5121CF8334A4', X'23E2A63A9E38C14799D6F0031DECF8F9', X'23E2A63A9E38C14799D6F0031DECF8F9', 1, NULL, NULL, 2, NULL, 0, 'Dice up the eggplant and zucchini and place into your food processor. Add in the remaining ingredients except for the olive oil. Pulse a few times until everything is smooth and combined. Now slowly with the processor running add in your olive oil- process one minute or until your desired consistency. Store in an airtight container in the refrigerator for up to a week ( if it lasts that long:)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8A8622D13D02D4C90E178E455238FA0', X'874FCC9EC4215C47BF73DA30E0B47746', X'874FCC9EC4215C47BF73DA30E0B47746', 0, NULL, NULL, 2, NULL, 0, 'Place black-eyed peas in a bowl and mash with a fork. Beat the egg with the mustard and garlic and add to the peas. Stir and add in the remaining ingredients and stir together to combine. Form into golf-ball size balls and set aside. Heat ½ inch of oil in a large skillet or cast iron pan over medium to high heat. (Note: A little trick I learned from my mom- if you sprinkle a little flour over the oil and it bubbles you know the oil is ready). Add the fritters to the oil gently and fry for abut 2- 21/2 minutes per side (for total of 4-5 minutes). Drain on a paper towel-lined plate and enjoy with the dipping sauce.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5130382A254E0C4683863B477E35FB15', X'2942DD395038EE449FC9211C7178476B', X'2942DD395038EE449FC9211C7178476B', 0, NULL, NULL, 2, NULL, 0, 'In a food processor or high speed blender, pulse all ingredients together until smooth and creamy. Refrigerate for at least 30 minutes or overnight and garnish with toasted pine nuts.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C41B23FFF78F8842815A5C80F1CAF192', X'6BBEC7419E02D0408D14964BB8E11264', X'6BBEC7419E02D0408D14964BB8E11264', 0, NULL, NULL, 2, NULL, 0, 'Place the butter, brown sugar, whole and minced garlic, diced jalapeno and Worcestershire sauce in a medium sauce pan. Stir to combine , allowing the butter and brown sugar to melt. Once melted turn the heat to low and cook for an additional 5 minutes. Remove from heat.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83938F1F5A365B46A37B06C32509E119', X'6BBEC7419E02D0408D14964BB8E11264', X'6BBEC7419E02D0408D14964BB8E11264', 1, NULL, NULL, 2, NULL, 0, 'Toss the glaze mixture with the mixed nuts and shelled edamame. Pour out onto a parchment or wax lined baking sheet and lightly dust with salt and pepper. Bake at 250 for an hour. Allow to cool completely, loosely covered with foil. Just before time to serve cook your un-shelled edamame according to the package directions and toss with the mixed nuts.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5F512C632195E40B87CCC694AE609CB', X'5C4DFBB68B416C4EA288770536C0BBCC', X'5C4DFBB68B416C4EA288770536C0BBCC', 0, NULL, NULL, 2, NULL, 0, 'Mix all the ingredients together in a large bowl except for the fish. Once the ingredients are all combined add in the fish and toss. Cover and chill for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3C2C0AA5DF5584AB2960E3B56D8B10F', X'5C4DFBB68B416C4EA288770536C0BBCC', X'5C4DFBB68B416C4EA288770536C0BBCC', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle with extra cilantro and an avocado just before serving.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAAEA2F99A35554EBE9BE58D2D885F9F', X'550A323743893A478DD36163A7BADF65', X'550A323743893A478DD36163A7BADF65', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees. Cover the bottom of the 9x13 pan with foil. Cut each dinner roll in half. Fold the slice roast beef in half and top with sliced cheese. Place them in the pan close together. You may need 2 pans depending on the size of your rolls. My pan fit 12 sandwiches and I used an additional pan for the remaining 4-5 rolls.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5599ECB1F108F42BE0D00A6D63E7705', X'550A323743893A478DD36163A7BADF65', X'550A323743893A478DD36163A7BADF65', 1, NULL, NULL, 2, NULL, 0, 'Whisk all of the dressing ingredients together. Brush the sauce generously over the top of the rolls, stirring the dressing in between brushing to make sure all flavors and components stay combined. Allow to sit at room temperature for 10 minutes. Bake in the oven for 12-15 minutes until cheese is melted and the tops are slightly crispy.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF04A99CA1F5864AAE93D9B7C9409D22', X'0C72D805B534144EAC54975CD8699B7D', X'0C72D805B534144EAC54975CD8699B7D', 0, NULL, NULL, 2, NULL, 0, 'Combine all the ingredients in a medium size sauce pot. Bring to a boil and reduce and simmer for five minutes or until all the sugar has dissolved. Allow it to simmer until thickened to your preference. Serve warm or cover tightly in a plastic or glass container and store in fridge.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBA1D045C1D3E3438F05E16451A83AB0', X'2BF1A82FFD31314999585E0B45D4A58B', X'2BF1A82FFD31314999585E0B45D4A58B', 0, NULL, NULL, 2, NULL, 0, 'Rinse cauliflower, separate into florets. Place cauliflower in a food processor and process until very fine. You should have 2 cups of finely grated cauliflower. Transfer to a bowl and cover with plastic wrap. Microwave for 6 minutes. Transfer the cauliflower once it has cooled to a cheesecloth or sheer tea towel and squeeze all of the the liquid out, You don''t want any liquid left in your cauliflower because it will make the crust crispy (hence break apart) instead of chewy (we want chewy)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAB672BE05EC4141A0FD4AB931BE54B8', X'2BF1A82FFD31314999585E0B45D4A58B', X'2BF1A82FFD31314999585E0B45D4A58B', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450. Once the liquid is removed from the cauliflower place the cauliflower in a large bowl and add in the remaining ingredients- leaving the egg for last. Give your arm a workout and start stirring, really working the batter with your hands until it forms a nice, slightly sticky dough. Spread the dough out onto a lightly greased pizza stone or parchment lined baking sheet. Bake for 10 minutes until it starts to turn a golden brown. Remove from the oven and top with your favorite toppings. Return to the oven and bake until the toppings are warm and the cheese is melted.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A64E39633788941B30A2D3F5134F888', X'2BF1A82FFD31314999585E0B45D4A58B', X'2BF1A82FFD31314999585E0B45D4A58B', 2, NULL, NULL, 2, NULL, 0, 'NOTE: Because this pizza crust does not take a long time to bake in the oven the toppings you decide to use will need to already be cooked. So for example I used grilled chicken, peppers and onions on mine. I sweated the onions and peppers before hand and roasted the chicken before cutting it up. Once the crust was ready I topped it with my toppings and placed it in the oven just long enough to melt the cheese and heat the chicken and other toppings all the way through.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15339406FA9C204D9C8BD2B1FA142F5C', X'65752B8EC21E86498D9647BD10A9501F', X'65752B8EC21E86498D9647BD10A9501F', 0, NULL, NULL, 2, NULL, 0, 'Place the flour in the food processor and then slowly drizzle in the hot water. Start off by pulsing and then process the dough until it comes together and forms a ball and rides the blade.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06C2EFC1ECC89041BBEDFC3D44694CE1', X'65752B8EC21E86498D9647BD10A9501F', X'65752B8EC21E86498D9647BD10A9501F', 1, NULL, NULL, 2, NULL, 0, 'Transfer to a floured work surface and knead to form a smooth ball. Cover with plastic wrap and allow to rest for 30 minutes at room temperature. Divide the dough into 8 separate balls and roll it out into a thin disk. Sprinkle with a dash (pinch) of salt and dash (pinch) of ground ginger then place your scallions and bacon in the center then roll it up like a cigar and then knead it out again into a flat thin pancake about ? of an inch. Repeat for the rest of the dough.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71136353592B73438E3C5EA293CD0B84', X'65752B8EC21E86498D9647BD10A9501F', X'65752B8EC21E86498D9647BD10A9501F', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a non-stick skillet and carefully place the pancake in the hot oil. Cook for 1-2 minutes and then flip to the other side and cook another 1-2 minutes until golden brown. Drizzle with the Crown Maple and ENJOY!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B49E65FECB316A4AB98DDC4869F6D730', X'AF532929132FC049B5DB44240895CB65', X'AF532929132FC049B5DB44240895CB65', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 400. Slice the bread about 1 inch thick without cutting all the way through. Place the bread on the cookie sheet. Pour butter between the slices, then stuff the cheese in between the slices as well as the chopped jalapenos. Sprinkle with the garlic powder and onion salt and top the bread with the bacon slices without overlapping them. Brush the top with the jalapeno juice.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0963950A9F3D5A47815D54966AA51D40', X'AF532929132FC049B5DB44240895CB65', X'AF532929132FC049B5DB44240895CB65', 1, NULL, NULL, 2, NULL, 0, 'Bake until cheese is melted at 400 about 5 minutes. The set to broil and broil until the bacon is to your liking. I did another about 3 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1980D870C3A7840B702C58FE80AD160', X'0DBE540D5FBC9D46A7973A00B2ECD460', X'0DBE540D5FBC9D46A7973A00B2ECD460', 0, NULL, NULL, 2, NULL, 0, 'Toss the shrimp with the 1 tsp of cajun seasoning and melt 4 TBSP butter in cast iron skillet. Cook the shrimp, garlic and onions for 5-6 minutes. Set aside to cool. Once cool to the touch transfer the shrimp, onions and garlic into a food processor and pulse until the shrimp are nicely minced.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F7B730F8CD186498F5A73F4B1FDC28F', X'0DBE540D5FBC9D46A7973A00B2ECD460', X'0DBE540D5FBC9D46A7973A00B2ECD460', 1, NULL, NULL, 2, NULL, 0, 'Transfer the minced shrimp mixture back to the skillet and add in the white wine and lemon juice. Allow the white wine to reduce over medium to low heat (1-2minutes) then stir in the cream cheese and remaining ingredients.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6029F8EC01B2E47A631BB4C661CD2FD', X'75AA420F5DDCBF4C8DF3FABBA9999377', X'75AA420F5DDCBF4C8DF3FABBA9999377', 0, NULL, NULL, 2, NULL, 0, 'For the Pecans: mix together the all the ingredients then toss with the pecans. Place pecans on a roasting pan and bake in the oven at 300 for 30 minutes. Remove and set aside to cool. Once cool, roughly chop the pecans.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAC6FD4D139718459163EE1F2FABD148', X'75AA420F5DDCBF4C8DF3FABBA9999377', X'75AA420F5DDCBF4C8DF3FABBA9999377', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring a large pot of water to a boil and quickly add the broccoli in batches and boil until bright green, 1 to 2 minutes. Add to a colander in the sink and run with cold water to stop cooking. Drain well and place in a large bowl.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48C3D86712421446BDF9F1EF8C84B957', X'75AA420F5DDCBF4C8DF3FABBA9999377', X'75AA420F5DDCBF4C8DF3FABBA9999377', 2, NULL, NULL, 2, NULL, 0, 'Combine the pecans with the broccoli, tomatoes, raisins and steamed edamame. Whisk together your dressing ingredients and toss with the salad. Keep at room temperature until time to eat!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78A68583332B024B8BB263C7D737AA3D', X'2C4DE803255C0140AAB656BF1D269647', X'2C4DE803255C0140AAB656BF1D269647', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees. Spray your cookie sheet with olive oil spray. Then wash and slice sweet potatoes into ¼ inch rounds (no need to peel unless you want to). Spread in single layer on cookie sheet and brush melted butter on the top of each round. Sprinkle each round individually with a dash of salt, pepper, cinnamon and paprika. Then a dash of Panola Hot Sauce. Top with a piece of cheese and roast in oven about 20 minutes or until golden brown. Before serving place the bacon on top of each round and sprinkle the whole plate with the chopped cilantro.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'580BE7BB7113B349A40BFEA963CA9D7D', X'2C4DE803255C0140AAB656BF1D269647', X'2C4DE803255C0140AAB656BF1D269647', 1, NULL, NULL, 2, NULL, 0, 'Enjoy and Happy Thanksgiving!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70E2BBD5C0EAAD41B31A9FE5315DB508', X'BB97FD0051A6D043AF39FEB5E130575B', X'BB97FD0051A6D043AF39FEB5E130575B', 0, NULL, NULL, 2, NULL, 0, 'Take your nice cold or room temp mashed potatoes and add in the eggs and mustard. Stir together your salt, pepper, panko and all purpose flour. Then stir the panko/flour mixture into the potatoes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49F21262D13E134E9DC3A000800E22BD', X'BB97FD0051A6D043AF39FEB5E130575B', X'BB97FD0051A6D043AF39FEB5E130575B', 1, NULL, NULL, 2, NULL, 0, 'Note: Sometimes people make their mashed potatoes more thin and creamy or thick and chunky. The 3½ cups measurement in the ingredient list relies more on the thick and chunkier style-so if you find that after combining all your ingredients your mashed potato mixture is a little runny than add in a little more mashed potatoes- DO NOT double the flour or eggs but you can then season it accordingly if need be!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AAB868707290B44B60C2FA9AD714095', X'BB97FD0051A6D043AF39FEB5E130575B', X'BB97FD0051A6D043AF39FEB5E130575B', 2, NULL, NULL, 2, NULL, 0, 'Place in the refrigerator to firm up a bit. Drop a spoonful of potatoes either on a silicon lined or non-stick baking sheet or roll the potato mixture in the cornmeal then place on the sheet. YOUR CALL:) Both ways are excellent. Pop the sheet back in the freezer for 10-15 minutes before frying. Heat 1½ inch of oil in a large cast iron skillet and fry on medium to high heat for 2-3 minutes before flipping. Repeat with remaining batches. Helpful Hint: You want the temp around 350 degrees so if the tots are cooking too fat then you need to turn the temp down or if not cooking fast enough then turn the temp up:) Drain cooked tater tots on paper towel before serving.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6A410EEE5CC2B4DB6329ED3C8B1D894', X'2CEB1A12B70F42478ED3139BD1524DA8', X'2CEB1A12B70F42478ED3139BD1524DA8', 0, NULL, NULL, 2, NULL, 0, 'With a grater, grate zucchini and onion then place grated zucchini and onion in paper towel or kitchen towel and squeeze out liquid. Add to a bowl and combine the remaining ingredients.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C1750AA3AEEB74F959CCBFA798936E7', X'2CEB1A12B70F42478ED3139BD1524DA8', X'2CEB1A12B70F42478ED3139BD1524DA8', 1, NULL, NULL, 2, NULL, 0, 'Drop medium rounds on a lightly greased parchment lined baking sheet. Place in the refrigerator for 10-15 minutes. Then preheat oven to 425. Bake for 15 minutes flip them then bake again for another 10-15 minutes. We like ours nice and crispy!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EB7292B53B9FF4381941E0DBF0AA4C4', X'2465C7C397DC134DA003C1282F6F9FB5', X'2465C7C397DC134DA003C1282F6F9FB5', 0, NULL, NULL, 2, NULL, 0, 'First of all pop your popcorn according to the package directions. Set aside. In a medium saucepan melt your butter then stir in the sugar, honey 3-4 tsp (more or less depending on how spicy you want it) of the jalapeno juice from the jar. Bring to a boil then reduce heat to low and allow to simmer for another 5-7 minutes, stirring every 2 minutes until the sauce is a nice golden color.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A10C3AD0164E7C4FB0BF2B649429CFB0', X'2465C7C397DC134DA003C1282F6F9FB5', X'2465C7C397DC134DA003C1282F6F9FB5', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat and add in the baking soda, salt and garlic powder, vanilla and pecans. Remove a handful of jalapenos from the jar and roughly chop them. Add them to the pot and combine. Now place your popcorn in a large bowl and drizzle the caramel sauce over it. Toss to coat as much of the popcorn as you can. Transfer the popcorn to a baking sheet and bake at 375 for 15-20 minutes, stirring often until the popcorn is lightly golden. Allow to cool completely. ENJOY');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F85990D4F4348B49926A1F9FB003F747', X'CDA441C1F15975478C04A045C1BBA8AF', X'CDA441C1F15975478C04A045C1BBA8AF', 0, NULL, NULL, 2, NULL, 0, 'Remove tops and bottoms from the eggplants and slice vertically into very thin pieces. Thinly slice the onion (and zucchini-optional) and arrange it with the sliced eggplant and garlic on a baking sheet. Drizzle with olive oil, season with ½ tsp salt and pepper each and ½ tsp Spanish thyme. Roast at 400 for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'890B90F895299846B9F3FFD23F082F3C', X'CDA441C1F15975478C04A045C1BBA8AF', X'CDA441C1F15975478C04A045C1BBA8AF', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, thaw out your puff pastry according to the package directions. Cut the dough into small rectangles (should have 3 long layers for sheet once rolled out- cut each long layer into 3 separate triangles) and lay each rectangle of dough flat on a baking sheet poked with holes from a fork. Bake according to the package directions then place aside to let it cool.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B127CB80E0C9946B2517424C4991CB3', X'CDA441C1F15975478C04A045C1BBA8AF', X'CDA441C1F15975478C04A045C1BBA8AF', 2, NULL, NULL, 2, NULL, 0, 'To make the red pepper cream (the glue that holds it all together) mix the ricotta cheese, minced roasted red pepper strips, remaining salt, pepper and Spanish thyme, red pepper flakes and ½ cup grated (not shredded) Parmesan cheese. Mix well and refrigerate until time to assemble.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3695B66D4075D14FACB3E04A6430B43C', X'CDA441C1F15975478C04A045C1BBA8AF', X'CDA441C1F15975478C04A045C1BBA8AF', 3, NULL, NULL, 2, NULL, 0, 'Assembly:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C55C09535DE04490F358DEFED82FC0', X'CDA441C1F15975478C04A045C1BBA8AF', X'CDA441C1F15975478C04A045C1BBA8AF', 4, NULL, NULL, 2, NULL, 0, 'Start with the one piece of puff pastry as the base, and then add on a layer of the red pepper cream, then eggplant, layer of basil, layer of zucchini, layer of onions and finally another sheet of puff pastry. Repeat this process once more (or twice if you prefer) ending with the puff pastry.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'785BECFABEAD6947B89DFD107B80E607', X'AE79EC18036D71418A95AC0B74196D17', X'AE79EC18036D71418A95AC0B74196D17', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together both vinegars, olive oil, lime juice,orange zest, jalapeno juice,hot sauce and salt and pepper. Add beans, corn, onion and basil and toss until just incorporated. Cover and chill for about 2 hours.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5918292793CB384BAD2DCB599E3E01BF', X'61334BB84AEEFC43B8D0B0301E329467', X'61334BB84AEEFC43B8D0B0301E329467', 0, NULL, NULL, 2, NULL, 0, 'Remove pit from avocados. Slice and peel. Preheat oven to 450. Spray rack with cooking spray and place on a baking pan.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C225C733EFFE064C8DD58F6666773B61', X'61334BB84AEEFC43B8D0B0301E329467', X'61334BB84AEEFC43B8D0B0301E329467', 1, NULL, NULL, 2, NULL, 0, 'Mix together the flour with 1 tsp cajun seasoning and the paprika and cayenne pepper. Mix together in a separate bowl the eggs and 1 tsp of cajun seasoning with the salt and pepper. In the third separate bowl mix together the garlic powder and panko. Dip each avocado in the flour then the egg then the panko. Transfer to a the baking rack and cook for 15-20 minutes. Serve with my Jalapeno Lime Ailo');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8CE11FAF30B9A4DB677FD5DA7504EFD', X'37D7BDC05F78CD46B742060D9B867E23', X'37D7BDC05F78CD46B742060D9B867E23', 0, NULL, NULL, 2, NULL, 0, 'Saute the thawed spinach in the olive oil along with the garlic, red peppers, white wine, ½ tsp sea salt, ½ tsp pepper and the roasted red peppers. Cook on medium low for 5-7 minutes. Set aside to allow spinach to soak up all moisture then ring out spinach using a kitchen towel. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'894251EF3EC9374C8E16520AC129CAAA', X'37D7BDC05F78CD46B742060D9B867E23', X'37D7BDC05F78CD46B742060D9B867E23', 1, NULL, NULL, 2, NULL, 0, 'Boil the potatoes in a large pot of water along with 1 tsp sea salt for 10-12 minutes. Drain and pat potatoes dry. Using a potato ricer or masher mash potatoes to a fine consistency. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A8CE78F3DF77844B3DB50D1EA1954D8', X'37D7BDC05F78CD46B742060D9B867E23', X'37D7BDC05F78CD46B742060D9B867E23', 2, NULL, NULL, 2, NULL, 0, 'In a food processor puree the spinach mixture then add in the egg, egg yolk, ½ salt, ½ pepper, nutmeg and parmesan. Add in the flour and pulse until mixture is all combined. Place into a large bowl and gently fold in the riced potatoes, gently stirring until just combined. You will have very soft dough. Turn out onto a lightly floured board and roll into a 1½-inch thick snake. Dust the dough lightly with flour and cut into 1½-inch pieces. Roll each piece gently with your thumb, leaving a small indentation on 1 side of the gnocchi. Place the finished gnocchi on a lightly floured, parchment paper-lined baking sheet. (if you have time, let the gnocchi rest in the refrigerator or freezer until chilled prior to cooking. It will help the gnocchi hold its shape better).');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9469E43C1118AF4BA0264CFF195ADE33', X'37D7BDC05F78CD46B742060D9B867E23', X'37D7BDC05F78CD46B742060D9B867E23', 3, NULL, NULL, 2, NULL, 0, 'Heat a couple tablespoons of butter (or olive oil) in a large well-seasoned (or non-stick) skillet over medium-high heat. Add the gnocchi in a single layer and toss to coat with the butter. Let them cook until they are golden brown on the bottoms and then use a metal spatula to flip the gnocchi. Cook until they are golden all over.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'624F68B2E833B940B6D2AC7A80A3826D', X'913979BE9D94FA45953099AF8A6FBFDA', X'913979BE9D94FA45953099AF8A6FBFDA', 0, NULL, NULL, 2, NULL, 0, 'Mix the the oil with the seasonings- pour over the crackers and stir nicely to coat all the cracker pieces. Allow to sit for 10 minutes to soak up the seasoning. Bake in the oven at 200 for 15-20 minutes until golden brown. Allow to cool completely before storing in container or ziploc bag.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EF35C28DDB7F24880066FE67CBD8E2F', X'D95E125FACEF364FA6EFAFB1AF1B858B', X'D95E125FACEF364FA6EFAFB1AF1B858B', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a mixing bowl and incorporate evenly. To make a drink, fill cup with ? cup of cocoa mixture and pour in 1 cup hot milk. Stir to combine and enjoy! You can also use water if you prefer.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BDC7E3550612548AB8F36344DB92AD3', X'B55E91FDFA5E47489CBA716360D043B4', X'B55E91FDFA5E47489CBA716360D043B4', 0, NULL, NULL, 2, NULL, 0, 'Spray a large nonstick skilled with oil and heat over medium-high head. Add onions and bell peppers and sauté 3 minutes until tender. Set aside. Add corn to a small saucepan and season with liquid smoke, salt and pepper. Cook until heated through and remove from heat.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9455507BB691DF4F988A92F6C0ECB203', X'B55E91FDFA5E47489CBA716360D043B4', X'B55E91FDFA5E47489CBA716360D043B4', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan add in the beans and heat for 5 minutes. With a potato masher mash the beans until only bite-size pieces remain and it is spreadable. Add in the red pepper flakes, adobe sauce, cumin, chili powder and lime juice. Stir to combine and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A193BFC08139E4D8DDF083A1977F6B8', X'B55E91FDFA5E47489CBA716360D043B4', X'B55E91FDFA5E47489CBA716360D043B4', 2, NULL, NULL, 2, NULL, 0, 'Spread each tostada shell with 2 tablespoons of heated beans and top corn, bell pepper and onion rings, then the tomatoes and lastly the freshly diced tomatoes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00AE2A915A30EB41B017CBC5A25BF7F7', X'6E1C8164D7D83A40B03B27874DA38466', X'6E1C8164D7D83A40B03B27874DA38466', 0, NULL, NULL, 2, NULL, 0, 'Mix together the dry ingredients and wet ingredients in separate bowls. Now add the wet to the dry and mix well. Heat your oil to 375 and drop spoonfuls of batter in the hot oil. Fry until golden brown 1-2 minutes. Transfer to paper towels to drain and season with more salt.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B25EE512BC9D044298DDF57FC118F2FE', X'151810B222485B40A7A7270FE6D0142E', X'151810B222485B40A7A7270FE6D0142E', 0, NULL, NULL, 2, NULL, 0, 'For the Croutons:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5F2BECDA3EA184C8C0B9208A5B16060', X'151810B222485B40A7A7270FE6D0142E', X'151810B222485B40A7A7270FE6D0142E', 1, NULL, NULL, 2, NULL, 0, 'Slice the bread into eight slices, then cube to desired size. Place bread cubes in a medium deep bowl. In a small bowl add the oil, garlic powder and Kay''s lemon pepper seasoning mix. Mix together and toss with the bread cubes.Be gently as to not tear the bread. Spread onto a parchment lined baking sheet in a single layer. Bake at 375 for 15 minutes until nice and golden brown.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D21FD9F9CD0FF44B8EB311CC234AAD36', X'151810B222485B40A7A7270FE6D0142E', X'151810B222485B40A7A7270FE6D0142E', 2, NULL, NULL, 2, NULL, 0, 'In a food processor or medium bowl, whisk together all the ingredients except for the olive oil. Once combined if using a food processor, with the motor running, add in the oil in a stream to let the dressing emulsify together. If you are not using a food processor then slowly stream in the olive oil and use a whisk to combine. Whisk until the oil is mixed throughout and the dressing thickens. Taste and season with extra sea salt and pepper if needed. Store in airtight container in the refrigerator for 3-5 days.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'244FD882D1AAF3418075D02BD9A70726', X'E601FBC872BD014E91F806AE6596F17C', X'E601FBC872BD014E91F806AE6596F17C', 0, NULL, NULL, 2, NULL, 0, 'Remove the skin from your eggplant and zucchini then place both on a roasting pan or baking pan and drizzle each with olive oil and season with salt and pepper. Roast at 400 for 10-12 minutes. Remove and allow to cool.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65339AD43399654EB6D3D8466917B576', X'E601FBC872BD014E91F806AE6596F17C', X'E601FBC872BD014E91F806AE6596F17C', 1, NULL, NULL, 2, NULL, 0, 'Dice up the eggplant and zucchini and place into your food processor. Add in the remaining ingredients except for the olive oil. Pulse a few times until everything is smooth and combined. Now slowly with the processor running add in your olive oil- process one minute or until your desired consistency. Store in an airtight container in the refrigerator for up to a week ( if it lasts that long:)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A2F0B9C0A9DAA489EC84F3BD7124A30', X'5498999B5A501541A69C2087E3F59D21', X'5498999B5A501541A69C2087E3F59D21', 0, NULL, NULL, 2, NULL, 0, 'Stir 4 TBSP of flour with ¼ tsp salt and ¼ tsp pepper. Toss with the chopped onions. Heat 3 TBSP of oil in a large cast iron skillet over medium to high heat. Pan-fry the onions for 5-7 minutes, stirring every so often to keep them from sticking to the bottom of the pan. Once they have a nice golden brown layer then remove from pan and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F55A4DB9306EB24BABD9ACD3C674377F', X'5498999B5A501541A69C2087E3F59D21', X'5498999B5A501541A69C2087E3F59D21', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter in the same skillet. Stir in the ¼ cup of flour and stir to combine. Cook for 1 minute. Add the broth and simmer for 1-2 minutes. Lower the heat to medium-low and add the cream.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55596A98EB70434199FF5B207837020F', X'5498999B5A501541A69C2087E3F59D21', X'5498999B5A501541A69C2087E3F59D21', 2, NULL, NULL, 2, NULL, 0, 'Cook until the mixture thickens, stirring often, approximately 6 to 8 minutes. Add in the remaining ingredients including the cooked onions and chopped bacon leaving the green beans for last. Stir to combine and cover. Cook on medium low for 15-20 minutes. Top with extra layer of crispy onions and the pecans.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6738FEBF5A394A4F8D8470F8DA3D3C3D', X'1ACC226634A71742892E05352D20BA73', X'1ACC226634A71742892E05352D20BA73', 0, NULL, NULL, 2, NULL, 0, 'Rinse cauliflower, separate into florets. Place cauliflower in a food processor and process until very fine. You should have 2 cups of finely grated cauliflower. Transfer to a bowl and cover with plastic wrap. Microwave for 6 minutes. Transfer the cauliflower once it has cooled to a cheesecloth or sheer tea towel and squeeze all of the the liquid out, You don''t want any liquid left in your cauliflower because it will make the crust crispy (hence break apart) instead of chewy (we want chewy)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1696F5FF0068A248AA36C40F52C79EF4', X'1ACC226634A71742892E05352D20BA73', X'1ACC226634A71742892E05352D20BA73', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450. Once the liquid is removed from the cauliflower place the cauliflower in a large bowl and add in the remaining ingredients- leaving the egg for last. Give your arm a workout and start stirring, really working the batter with your hands until it forms a nice, slightly sticky dough. Spread the dough out onto a lightly greased pizza stone or parchment lined baking sheet. Bake for 10 minutes until it starts to turn a golden brown. Remove from the oven and top with your favorite toppings. Return to the oven and bake until the toppings are warm and the cheese is melted.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFCAC10A757598469A92486AABCB82B9', X'1ACC226634A71742892E05352D20BA73', X'1ACC226634A71742892E05352D20BA73', 2, NULL, NULL, 2, NULL, 0, 'NOTE: Because this pizza crust does not take a long time to bake in the oven the toppings you decide to use will need to already be cooked. So for example I used grilled chicken, peppers and onions on mine. I sweated the onions and peppers before hand and roasted the chicken before cutting it up. Once the crust was ready I topped it with my toppings and placed it in the oven just long enough to melt the cheese and heat the chicken and other toppings all the way through.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'901031B5A2A03D4DB500D663B67026F6', X'0016D4F746554C4296EA309FA15AAC8A', X'0016D4F746554C4296EA309FA15AAC8A', 0, NULL, NULL, 2, NULL, 0, 'Slice the peppers in half and or cut a wide circle around each stem hollow out the inside in order to stuff them entirely. Remove the seeds and ribs.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09312F5DD13D6D46A02BDD1C701207FB', X'0016D4F746554C4296EA309FA15AAC8A', X'0016D4F746554C4296EA309FA15AAC8A', 1, NULL, NULL, 2, NULL, 0, 'Place the peppers in a baking dish skin side up. Broil about 5-7 minutes, then flip the peppers and broil 5 more minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37EE0F584D6C2E46BDC513917BB6AD2F', X'0016D4F746554C4296EA309FA15AAC8A', X'0016D4F746554C4296EA309FA15AAC8A', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook your ground beef in a large skillet, when almost brown add in the chopped onions, bell pepper and green chiles. Cook for another 5-6 minutes until the onions and pepper are translucent. Stir in the cilantro, corn, black beans and seasonings. Remove from heat and allow to cool slightly. Stir in the cheeses, tortilla strips and tamale sauce. Mix well and then begin stuffing the peppers regardless of whether you cut them in half or left them whole. Replace caps to the peppers if you left them whole and secure with a toothpick.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9752964A98B0DB4EA58841E94FC5CF95', X'0016D4F746554C4296EA309FA15AAC8A', X'0016D4F746554C4296EA309FA15AAC8A', 3, NULL, NULL, 2, NULL, 0, 'If you are grilling then heat grill to 400 and grill the peppers approximately 30 minutes. If you are roasting them then place the stuffed peppers back into the baking dish and roast at 400 for 15-20 minutes. Remember, we already pre-roasted the peppers to a certain degree to save a little time in the end. So the goal of roasting them a second time already stuffed is to give them that nice char and have the pepper skin be soft to the touch. The filling is already cooked.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'726875FF24E8194BAAF6D53BD840E2E4', X'87C49473AD0F6C4FBFE4E58C1B8E676C', X'87C49473AD0F6C4FBFE4E58C1B8E676C', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter and olive oil together in cast iron skillet over medium to low heat. Add in the brown sugar, molasses and red wine. Season with the salt, pepper and garlic powder. Allow the brown sugar and molasses to melt and combine nicely. Toss in the carrots and cover with marinade/glaze. Watch carefully and cook for 10-12 minutes turning over the carrots at intervals of 3-4 minutes. Remove from heat and place in your serving dish. Toss with the fresh dill and serve:)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2865EA736DC9E48BDEA9525D1F17D37', X'68F4DD59198C51438C59BDADE39E2965', X'68F4DD59198C51438C59BDADE39E2965', 0, NULL, NULL, 2, NULL, 0, 'Toss the meat with the canola oil and season blend. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54A323B93C67D24787965E771CD88E4B', X'68F4DD59198C51438C59BDADE39E2965', X'68F4DD59198C51438C59BDADE39E2965', 1, NULL, NULL, 2, NULL, 0, 'In large saute pan heat 1 TBSP canola oil and saute the bag of frozen veggies for 5-6 minutes. Remove from the pan and place the meat in the pan and stir in the soy sauce, ginger and garlic oil. Cook over medium to high heat for 3 minutes per side. No more than 7 minutes. Add back the veggies to the pan, quickly add in the cornstarch liquid,t urn heat to medium low and allow to sit covered for another 3--4 minutes until thickened. Serve over brown or white rice.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'137C5325F8D67B419A36BB3534477E7D', X'646308DCDD91134ABA6E287907925199', X'646308DCDD91134ABA6E287907925199', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400° and lightly grease a small baking sheet with vegetable oil. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEA0FB2C3C16FB48B109996025ABF110', X'646308DCDD91134ABA6E287907925199', X'646308DCDD91134ABA6E287907925199', 1, NULL, NULL, 2, NULL, 0, '2. Trim the base of the cauliflower to remove any green leaves and the woody stem.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D4C44767DBA924BBD670FA2C4A01205', X'646308DCDD91134ABA6E287907925199', X'646308DCDD91134ABA6E287907925199', 2, NULL, NULL, 2, NULL, 0, '3. In a medium bowl, combine the yogurt with the lime zest and juice, black pepper marinade, chile powder, cumin, garlic powder, curry powder, salt and pepper.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B023073966FC3049B9EC26534CC54D34', X'646308DCDD91134ABA6E287907925199', X'646308DCDD91134ABA6E287907925199', 3, NULL, NULL, 2, NULL, 0, '4. Dunk the cauliflower into the bowl and use a brush or your hands to smear the marinade evenly over its surface. (Excess marinade can be stored in the refrigerator in an airtight container for up to three days and used with meat, fish or other veggies.)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94E7477E7CE7E84C92DFEAABFA7C0D80', X'646308DCDD91134ABA6E287907925199', X'646308DCDD91134ABA6E287907925199', 4, NULL, NULL, 2, NULL, 0, '5. Place the cauliflower on the prepared baking sheet and roast until the surface is dry and lightly browned, 30 to 40 minutes. The marinade will make a crust on the surface of the cauliflower.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B78349A6F26B64E907ADEBC57B394C9', X'646308DCDD91134ABA6E287907925199', X'646308DCDD91134ABA6E287907925199', 5, NULL, NULL, 2, NULL, 0, '6. Let the cauliflower cool for 10 minutes before cutting it into wedges');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44B865B3AC50614EBB2978F8300884B5', X'F96FBB4FE3AF34468A5A15483E1C3B0B', X'F96FBB4FE3AF34468A5A15483E1C3B0B', 0, NULL, NULL, 2, NULL, 0, 'For the Pecans: mix together the all the ingredients then toss with the pecans. Place pecans on a roasting pan and bake in the oven at 300 for 30 minutes. Remove and set aside to cool. Once cool, roughly chop the pecans.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7ECEA2CBDEA7842BC3F7E73F400D203', X'F96FBB4FE3AF34468A5A15483E1C3B0B', X'F96FBB4FE3AF34468A5A15483E1C3B0B', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring a large pot of water to a boil and quickly add the broccoli in batches and boil until bright green, 1 to 2 minutes. Add to a colander in the sink and run with cold water to stop cooking. Drain well and place in a large bowl.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6ACBFBF8E6D1AD4AB6F184F48FEB1F41', X'F96FBB4FE3AF34468A5A15483E1C3B0B', X'F96FBB4FE3AF34468A5A15483E1C3B0B', 2, NULL, NULL, 2, NULL, 0, 'Combine the pecans with the broccoli, tomatoes, raisins and steamed edamame. Whisk together your dressing ingredients and toss with the salad. Keep at room temperature until time to eat!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44CD6F6BBD6F3E47B08355DFA2E71C2E', X'FC2A601537CD384F93ED5908FBA1ED24', X'FC2A601537CD384F93ED5908FBA1ED24', 0, NULL, NULL, 2, NULL, 0, 'With a grater, grate zucchini and onion then place grated zucchini and onion in paper towel or kitchen towel and squeeze out liquid. Add to a bowl and combine the remaining ingredients.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07EA53EB8EDB134D8C2E172718C892BB', X'FC2A601537CD384F93ED5908FBA1ED24', X'FC2A601537CD384F93ED5908FBA1ED24', 1, NULL, NULL, 2, NULL, 0, 'Drop medium rounds on a lightly greased parchment lined baking sheet. Place in the refrigerator for 10-15 minutes. Then preheat oven to 425. Bake for 15 minutes flip them then bake again for another 10-15 minutes. We like ours nice and crispy!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7DF6C9F0F627F4D80815081BA4F22B1', X'8E9B6B65BAF6CC4197AF21711D940E23', X'8E9B6B65BAF6CC4197AF21711D940E23', 0, NULL, NULL, 2, NULL, 0, 'Heat a cast-iron grill pan. In a medium bowl, toss the green beans with 1 tablespoon of the olive oil and season with salt and pepper. Grill over high heat, turning once, until lightly charred, 2 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4974461F410CD847B80FB42ECAC482BC', X'8E9B6B65BAF6CC4197AF21711D940E23', X'8E9B6B65BAF6CC4197AF21711D940E23', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat a saute pan with the remaining 2 TBSP of oil. Toss in the tomatoes, potatoes, lemons and garlic. Cook over medium to low heat for 8-10 minutes until nice and tender. Remove from heat. Transfer to a large glass bowl and combine the grilled beans with the tomatoes, lemons and potatoes. Toss with the Pacific Rim Ginger Sauce and sprinkle the crumbled bacon all over the top.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A34BB5807F99BB4AB691BFB8A72803ED', X'F0047CE98371594FBEE09149B96CA502', X'F0047CE98371594FBEE09149B96CA502', 0, NULL, NULL, 2, NULL, 0, 'Cut the dark green, woody part of the stalk off each leek and discard. You only need to use the white and pale green portions of the leek. Now chopped the white and green reserved portion and place in a large bowl of cold water. Swish the leeks with your hand to remove any sand or dirt embedded in the layers. Rinse, drain and pat dry. You may also substitute green onions for the leeks and simply chop the green onions and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D01CA32F03552C4D9157DA3804236B6D', X'F0047CE98371594FBEE09149B96CA502', X'F0047CE98371594FBEE09149B96CA502', 1, NULL, NULL, 2, NULL, 0, 'Next rinse your zucchini and then slice into ½ inch rings. Place zucchini, leeks and garlic in a large saute pan with 2 TBSP of olive oil. Saute until slightly tender, 5-7 minutes. Set aside to cool.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDB872FBFB23864EAD4B9686F384ED15', X'F0047CE98371594FBEE09149B96CA502', X'F0047CE98371594FBEE09149B96CA502', 2, NULL, NULL, 2, NULL, 0, 'Place the zucchini, leeks and cheese in a large bowl. Mix to combine and season with the salt, pepper and paprika. Now its time to assemble our quesadilla. Heat a large cast iron frying pan to medium high heat. Add a small amount of oil and allow it to spread around the bottom of the pan. Take two flour tortilla''s or one depending on the size of your pan and place it in the oil. Gently arrange 2 layers of prosciutto on top of the bottom tortilla then a heaping spoonful or two of the veggie/cheese mixture then top with the top tortilla. (I like to assemble my quesadilla''s in the pan itself so I don''t to worry about any of the filling falling out as I transfer it to the pan.) Repeat the assembly process and cook the quesadilla''s, 2 minutes per side on medium to low heat or flip the quesadilla over every 10 seconds or so until your desired crispiness is achieved. Remove from pan and cut into wedges.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2697887A85C7A940A15D08BFAA9A27E0', X'34DD5DB8E2D8BE468F242FE5B7DDE314', X'34DD5DB8E2D8BE468F242FE5B7DDE314', 0, NULL, NULL, 2, NULL, 0, 'Remove tops and bottoms from the eggplants and slice vertically into very thin pieces. Thinly slice the onion (and zucchini-optional) and arrange it with the sliced eggplant and garlic on a baking sheet. Drizzle with olive oil, season with ½ tsp salt and pepper each and ½ tsp Spanish thyme. Roast at 400 for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'718FCD9981CAA54B9ECC65FB37824BF4', X'34DD5DB8E2D8BE468F242FE5B7DDE314', X'34DD5DB8E2D8BE468F242FE5B7DDE314', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, thaw out your puff pastry according to the package directions. Cut the dough into small rectangles (should have 3 long layers for sheet once rolled out- cut each long layer into 3 separate triangles) and lay each rectangle of dough flat on a baking sheet poked with holes from a fork. Bake according to the package directions then place aside to let it cool.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52B1B044024E214DB1D1194BDC7F586C', X'34DD5DB8E2D8BE468F242FE5B7DDE314', X'34DD5DB8E2D8BE468F242FE5B7DDE314', 2, NULL, NULL, 2, NULL, 0, 'To make the red pepper cream (the glue that holds it all together) mix the ricotta cheese, minced roasted red pepper strips, remaining salt, pepper and Spanish thyme, red pepper flakes and ½ cup grated (not shredded) Parmesan cheese. Mix well and refrigerate until time to assemble.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3E78716FC6AB8459D9836B86753C5D0', X'34DD5DB8E2D8BE468F242FE5B7DDE314', X'34DD5DB8E2D8BE468F242FE5B7DDE314', 3, NULL, NULL, 2, NULL, 0, 'Assembly:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E77A424056E41742AD091E071D41900A', X'34DD5DB8E2D8BE468F242FE5B7DDE314', X'34DD5DB8E2D8BE468F242FE5B7DDE314', 4, NULL, NULL, 2, NULL, 0, 'Start with the one piece of puff pastry as the base, and then add on a layer of the red pepper cream, then eggplant, layer of basil, layer of zucchini, layer of onions and finally another sheet of puff pastry. Repeat this process once more (or twice if you prefer) ending with the puff pastry.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30453D5D89C0954E81225A366BA276DA', X'B35F0D831AA3CD42978ADFE2C96DB93B', X'B35F0D831AA3CD42978ADFE2C96DB93B', 0, NULL, NULL, 2, NULL, 0, 'In a large skillet, sauté onions, garlic and bell pepper in ½ tbsp oil. Saute for 5-7 minutes until tender. Place the steam-able bag of edamame in the microwave and cook following package directions. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED3B5254B8670042A8DEDE7B2823ACE8', X'B35F0D831AA3CD42978ADFE2C96DB93B', X'B35F0D831AA3CD42978ADFE2C96DB93B', 1, NULL, NULL, 2, NULL, 0, 'Take your corn on the cobs and rub them with a little olive oil. Place on a baking sheet. Roast on 400 for 10-15 minutes until slightly charred and crispy. Set aside to allow to cool. If you are not roasting your corn and just using canned corn- then drain your corn and proceed to next step. If you are roasting the corn then once they are cool move to a pan and using a knife remove the kernels from the cob.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62AAAF61AE71D241A3B74B63C59909B8', X'B35F0D831AA3CD42978ADFE2C96DB93B', X'B35F0D831AA3CD42978ADFE2C96DB93B', 2, NULL, NULL, 2, NULL, 0, 'Place all ingredients into a large mixing bowl and stir very well. Using a potato masher roughly mash the pattie mixture to your desired consistency. The mixture will stick to your masher so you may have to use wet fingers and slide the mixture off before you continue mashing.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63D4CBC40479EF4BB356EF1C9B0B78AC', X'B35F0D831AA3CD42978ADFE2C96DB93B', X'B35F0D831AA3CD42978ADFE2C96DB93B', 3, NULL, NULL, 2, NULL, 0, 'With slightly wet hands, shape dough into patties. Pack dough tightly as this will help it stick together. This recipe makes approximately 6-8 burgers.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7B87360D3E6B24CAAA0D8700AC9C2E9', X'B35F0D831AA3CD42978ADFE2C96DB93B', X'B35F0D831AA3CD42978ADFE2C96DB93B', 4, NULL, NULL, 2, NULL, 0, 'Pan fry the burgers in a bit of olive oil in a skillet over medium heat for about 5 minutes on each side until golden and crisp on the outside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47CFDB4E4B8AEB4695FCDF2479D9E9F2', X'F408AEDA604D3E45BD4E514241247910', X'F408AEDA604D3E45BD4E514241247910', 0, NULL, NULL, 2, NULL, 0, 'Cut a half inch thick slice off the top and bottom of your onion so that the onion will sit up straight. Use a knife and carefully carve out a small circle in the top of the onion. Then with a spoon scoop out the center carefully, leaving the onion layers intact. Place each onion on its separate sheet of foil. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1731EA02F17E0E40A7C625B61BFB2C53', X'F408AEDA604D3E45BD4E514241247910', X'F408AEDA604D3E45BD4E514241247910', 1, NULL, NULL, 2, NULL, 0, 'Saute the zucchini and garlic in the butter for 5-7 minutes. Turn off heat and add in the panko, cheese, and remaining seasonings. Mix well and then spoon the filling into the center of each onion.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD419B8A2C73F742B27E9523A40D7A7B', X'F408AEDA604D3E45BD4E514241247910', X'F408AEDA604D3E45BD4E514241247910', 2, NULL, NULL, 2, NULL, 0, 'Wrap the onion up in the foil, making sure to pat down the top to seal the stuffing inside and place on either a baking sheet and bake for 20-30 minutes at 450 or place the individually wrapped onions on the grill over high heat and grill for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D94980021040E74581ACFB7A1843BDF2', X'05A63113DD85144C897868F5C26B338C', X'05A63113DD85144C897868F5C26B338C', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350° F. Spray 2 8x8 pans with cooking spray then place parchment paper in the pan pressing so it sticks well to the bottom and sides.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3416609C34B5C84183EE347D017D0C67', X'05A63113DD85144C897868F5C26B338C', X'05A63113DD85144C897868F5C26B338C', 1, NULL, NULL, 2, NULL, 0, 'Whisk the flour, baking powder, baking soda, salt, and spices into a medium bowl and set aside. In an electric mixer fitted with the paddle attachment, beat the eggs, sugar and olive oil together on medium speed until light and fluffy, about 3 minutes, then beat in the vanilla extract. Scrape down the sides of the bowl as needed. Beat in the dry ingredients all at once on low speed until they are thoroughly combined, then switch to medium speed and mix for 30 seconds. Mix in the zucchini until completely incorporated.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E8E4B6504FEB346B417593CA22E7EA8', X'05A63113DD85144C897868F5C26B338C', X'05A63113DD85144C897868F5C26B338C', 2, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared pans, smoothing the top with a spatula. Bake the cakes for 40 to 45 minutes or until a tester inserted in the cakes comes out clean and the cakes have begun to pull away from the sides of the pans. Once the pans are cool, place them in the freezer for 15-30 minutes. Meanwhile, take out your frozen yogurt and allow it to come to room temperature so it evenly spreads.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F173E6A10B0D47489177BFA51478C194', X'05A63113DD85144C897868F5C26B338C', X'05A63113DD85144C897868F5C26B338C', 3, NULL, NULL, 2, NULL, 0, 'Remove one pan and spread the yogurt on top of the zucchini cake. You can do as much or as little yogurt as you prefer. Place the pan back in the freeze for an hour. Now remove both pans and pull out the second zucchini cake from the pan using the parchment paper to help you get it out. Invert it on top of the yogurt layer and press down slightly. Wrap the whole pan with plastic wrap and freezer overnight. You can either cut out your ''sandwiches'' in the pan itself or remove the whole zucchini cake from the pan using the parchment paper and cut out your sandwiches.( That is what I did)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4111435093A37548B95BE74A169841DB', X'05A63113DD85144C897868F5C26B338C', X'05A63113DD85144C897868F5C26B338C', 4, NULL, NULL, 2, NULL, 0, 'Store in ziploc freezer bags for up to 2 week- if that last that long.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06517D98EDADB44FAEE62AE48A0CD67B', X'DB62A17DE3BE594EBD16E0BE3432758C', X'DB62A17DE3BE594EBD16E0BE3432758C', 0, NULL, NULL, 2, NULL, 0, 'Saute the onion, garlic and red bell peppers in the olive oil for 5-6 minutes until slightly translucent. Place in the bottom of a round casserole dish or tart pan. Sprinkle with romano cheese and the basil.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0D5CEA5C253EE4CA7F390B06CE3722A', X'DB62A17DE3BE594EBD16E0BE3432758C', X'DB62A17DE3BE594EBD16E0BE3432758C', 1, NULL, NULL, 2, NULL, 0, 'Slice the potatoes, tomatoes, squash and zucchini in round ¼ inch thick slices. In a large mixing bowl stir together the mustard, balsamic vinegar, salt, pepper,paprika, thyme and oregano. Layer them alternatively in a circular motion on top of the onion mixture. Make sure to fit them in tightly and create a spiral. Bake at 350 covered with foil for 30-40 minutes, remove the foil and bake for another 10-15 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBC3E39AD96D1148A00C2F57D604F0B1', X'8EF4C0AD5E4AC641922C3C46D6E600E3', X'8EF4C0AD5E4AC641922C3C46D6E600E3', 0, NULL, NULL, 2, NULL, 0, 'Put the broccoli and pecans in a bowl, drizzle with olive oil, toss with minced garlic, sprinkle with salt and pepper and red pepper flakes and transfer to a gratin dish or casserole dish. Cook until the tops are browning and the stems are crisp-tender, 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A3D4A7E47A2B4499072BAF04D6D0696', X'8EF4C0AD5E4AC641922C3C46D6E600E3', X'8EF4C0AD5E4AC641922C3C46D6E600E3', 1, NULL, NULL, 2, NULL, 0, 'Remove from the oven and set aside. Mix together in a large bowl the remaining ingredients. Place small mounds of the cheese mixture on top of the broccoli, gently pressing down. Return to the oven and cook until the cheese is bubbling and the bread crumbs are golden. Serve hot or warm.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69A01B659DE4384C9AADA9F834B83F41', X'2AFEE8837E85FF4A9B6A78846AD6C874', X'2AFEE8837E85FF4A9B6A78846AD6C874', 0, NULL, NULL, 2, NULL, 0, 'First prepare the spicy tomato sauce. Heat the olive oil in a medium saucepan and cook the onions and garlic for 5-6 minutes. Add in the can of diced tomatoes, salt, pepper, adobe powder, cumin and oregano. Cover and turn to medium-low. Cook for 10-15 minutes. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D98B1BB103E81C4C98BFEDAEBD93ABF1', X'2AFEE8837E85FF4A9B6A78846AD6C874', X'2AFEE8837E85FF4A9B6A78846AD6C874', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the canola oil in a large skillet and lightly fry the tortillas one at a time until slightly brown on each side. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D71056C6D813D048AA76928512EBB5D7', X'2AFEE8837E85FF4A9B6A78846AD6C874', X'2AFEE8837E85FF4A9B6A78846AD6C874', 2, NULL, NULL, 2, NULL, 0, 'To assemble, place the tortilla in the bottom of each ramekin and push down to make a bowl shape. Place a heaping spoonful of the spicy tomato sauce in the bottom. Next add in some cheese then crack the egg on top. Bake at 375 for 10 minutes. Remove and place remaining shredded cheese on top and a spoonful of the spicy tomato sauce. Return to the oven and turn oven to broil-bake until cheese is melted- 1-2 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38AF58E0B0E02443A842B5B36697448E', X'5CA80D9474C9F94780DF3FA837FE5DE7', X'5CA80D9474C9F94780DF3FA837FE5DE7', 0, NULL, NULL, 2, NULL, 0, 'In a large pot, fill ¾ full with water and bring to a gentle boil. Add husks to water, cover and remove from heat. Let stand for 30 minutes or until softened and pliable. Rinse husks one at a time under cold running water. Pile husks on a plate and cover with a damp, clean kitchen towel while you make the dough.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'988A37EE3862E34F8F5A416818B9A4FA', X'5CA80D9474C9F94780DF3FA837FE5DE7', X'5CA80D9474C9F94780DF3FA837FE5DE7', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl combine the masa, baking powder and salt. Roughly stir in the shortening, cilantro and spices until just combined. Slowly add in the vegetable broth ½ cup at a time until the dough begins to come together. Add in the cheeses and continue stirring until you have a smooth spreadable consistency- like you would with peanut butter.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0565D0B63641B4297899641CBF381EF', X'5CA80D9474C9F94780DF3FA837FE5DE7', X'5CA80D9474C9F94780DF3FA837FE5DE7', 2, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and refrigerate. Meanwhile, saute your garlic, tomatoes, zucchini and onions in 2 TBSP of butter until slightly tender- approximately 5-7 minutes. Set aside to cool COMPLETELY!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D3E9820B1721940BCDFDA27BF54C262', X'5CA80D9474C9F94780DF3FA837FE5DE7', X'5CA80D9474C9F94780DF3FA837FE5DE7', 3, NULL, NULL, 2, NULL, 0, 'Now its time to assemble: Place 2 husks on a work surface, pointed end at the top. Spread them flat and spoon approximately ¼ cup of the dough into the center of each, leaving about a 1 – 1½ inch border on both sides. Now place a spoonful of the filling in the middle. Fold the wide end of each husk over the filling/dough to cover, then fold in the sides. Fold pointed end of each husk over to form a packet, trimming the end with kitchen shears, if necessary. Arrange, folded side down and set aside. Continue with the remaining husks using the same method.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'696891EF0C6D3D42AA16F9AC263D7316', X'5CA80D9474C9F94780DF3FA837FE5DE7', X'5CA80D9474C9F94780DF3FA837FE5DE7', 4, NULL, NULL, 2, NULL, 0, 'Arrange tamales in two pots on steamer racks/baskets with enough water to simmer (just below each basket). Stand the tamales up — like fallen dominos — ensuring the folded edge remains closed. Steam, covered lightly, until firm — about one hour.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36729A3660E62B4581C2A03F21D266D4', X'5CA80D9474C9F94780DF3FA837FE5DE7', X'5CA80D9474C9F94780DF3FA837FE5DE7', 5, NULL, NULL, 2, NULL, 0, 'After 50 minutes, remove one tamale to check for doneness. If the filling is firm and separates easily from the husk, they are done. If not, place back in steamer for another 5-10 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'724C863D24A1E749A52930576A91F6A2', X'E879D429FEBB1F4283A3F33ED90AD54F', X'E879D429FEBB1F4283A3F33ED90AD54F', 0, NULL, NULL, 2, NULL, 0, 'Cook your couscous according to the package directions substituting the water with chicken broth. This is optional- you can just use water.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19B95216E35DA540A73589C784C93800', X'E879D429FEBB1F4283A3F33ED90AD54F', X'E879D429FEBB1F4283A3F33ED90AD54F', 1, NULL, NULL, 2, NULL, 0, 'Saute the celery, garlic, bell pepper and artichokes in the oil for approximately 4-5 minutes on medium-low. Add in the chopped pecans and toast for another 1-2 minutes. Stir in the cooled couscous and bring heat up to high, cook for 5-7 minutes, stirring constantly til slightly toasted. Season with the salt, pepper and squeeze of lemon juice. Toss with parmesan cheese. Serve at room temperature.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EA59B0AA934F44490EF7FA860C980EF', X'E32F78D7B70D834392B021E264DA64DD', X'E32F78D7B70D834392B021E264DA64DD', 0, NULL, NULL, 2, NULL, 0, 'Combine ham, bacon, beef broth and beans in crock pot or dutch oven. Turn slow cooker on high and add in the remaining ingredients. Cook for 6-8 hours on high. 30 minutes before time to serve the soup, add in the pearl couscous. If you are not using a slow cooker then bring the soup to a boil then reduce heat to medium low and cook for 2-3 hours. The longer it cooks the better it gets!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'770F3145FE98FE479843422E622C8A74', X'A53D8F8747E47D47922118074801D2F4', X'A53D8F8747E47D47922118074801D2F4', 0, NULL, NULL, 2, NULL, 0, 'Saute the thawed spinach in the olive oil along with the garlic, red peppers, white wine, ½ tsp sea salt, ½ tsp pepper and the roasted red peppers. Cook on medium low for 5-7 minutes. Set aside to allow spinach to soak up all moisture then ring out spinach using a kitchen towel. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E1E30432CAD1940917B3E81475FB03D', X'A53D8F8747E47D47922118074801D2F4', X'A53D8F8747E47D47922118074801D2F4', 1, NULL, NULL, 2, NULL, 0, 'Boil the potatoes in a large pot of water along with 1 tsp sea salt for 10-12 minutes. Drain and pat potatoes dry. Using a potato ricer or masher mash potatoes to a fine consistency. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FB3286648F7F94A897EDAD3F7CCABD7', X'A53D8F8747E47D47922118074801D2F4', X'A53D8F8747E47D47922118074801D2F4', 2, NULL, NULL, 2, NULL, 0, 'In a food processor puree the spinach mixture then add in the egg, egg yolk, ½ salt, ½ pepper, nutmeg and parmesan. Add in the flour and pulse until mixture is all combined. Place into a large bowl and gently fold in the riced potatoes, gently stirring until just combined. You will have very soft dough. Turn out onto a lightly floured board and roll into a 1½-inch thick snake. Dust the dough lightly with flour and cut into 1½-inch pieces. Roll each piece gently with your thumb, leaving a small indentation on 1 side of the gnocchi. Place the finished gnocchi on a lightly floured, parchment paper-lined baking sheet. (if you have time, let the gnocchi rest in the refrigerator or freezer until chilled prior to cooking. It will help the gnocchi hold its shape better).');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF84F03D1EDEDE40824C6F0C2D39DDE5', X'A53D8F8747E47D47922118074801D2F4', X'A53D8F8747E47D47922118074801D2F4', 3, NULL, NULL, 2, NULL, 0, 'Heat a couple tablespoons of butter (or olive oil) in a large well-seasoned (or non-stick) skillet over medium-high heat. Add the gnocchi in a single layer and toss to coat with the butter. Let them cook until they are golden brown on the bottoms and then use a metal spatula to flip the gnocchi. Cook until they are golden all over.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB8E7582B45B8C48AB02C64651179248', X'CB5E98E7E8B8CB4ABBEC913295F7D0DB', X'CB5E98E7E8B8CB4ABBEC913295F7D0DB', 0, NULL, NULL, 2, NULL, 0, 'Combine all marinade ingredients in a large glass bowl or large Ziploc bag. Place the fish in the marinade, toss to coat each fish evenly and cover. Allow to marinate in the refrigerator for at least 3 hours or longer.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'338DAFAD3BD11241917ED87583E17892', X'CB5E98E7E8B8CB4ABBEC913295F7D0DB', X'CB5E98E7E8B8CB4ABBEC913295F7D0DB', 1, NULL, NULL, 2, NULL, 0, '30 minutes per to baking allow the puff pastry sheets to come to room temperature. As you set the pastry out go ahead and break each sheet into 3 pieces for a total of 6 puff pastry strips. You will notice when you remove the pastry from the box that it is already folded into 3 parts so this should be fairly easy to break apart. Once thawed and ready to bake remove the fish from the refrigerator.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D6E1C7D6C98D14BA7F36DC4D8FF721F', X'CB5E98E7E8B8CB4ABBEC913295F7D0DB', X'CB5E98E7E8B8CB4ABBEC913295F7D0DB', 2, NULL, NULL, 2, NULL, 0, 'Pat off excess marinade from each fillet (this is important as if you leave too much marinade on the fish the puff pastry will be soggy) and place in the center of each puff pastry strip. Lay one basil leaf on top of the fish and enclose the puff pastry around it. Sealing it with your fingers at the seam by pinching it together. Mix together the egg wash and almond oil and brush the tops of the puff pastry. Bake at 425 for 15 minutes-20 minutes until the puff pastry is nice and golden brown. Optional: With 5 minutes remaining in the baking time place a nice juicy slice of lemon on top of each puff pastry.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B96A64FF28436D4F86A943EC22705FA0', X'FD1D5CAAE421544BB12CFE7DC40A12E3', X'FD1D5CAAE421544BB12CFE7DC40A12E3', 0, NULL, NULL, 2, NULL, 0, 'Spray a large nonstick skilled with oil and heat over medium-high head. Add onions and bell peppers and sauté 3 minutes until tender. Set aside. Add corn to a small saucepan and season with liquid smoke, salt and pepper. Cook until heated through and remove from heat.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'927A7798E5A7E940AA460DF60464FFAB', X'FD1D5CAAE421544BB12CFE7DC40A12E3', X'FD1D5CAAE421544BB12CFE7DC40A12E3', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan add in the beans and heat for 5 minutes. With a potato masher mash the beans until only bite-size pieces remain and it is spreadable. Add in the red pepper flakes, adobe sauce, cumin, chili powder and lime juice. Stir to combine and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D06306961B60B45B3CFC31564514278', X'FD1D5CAAE421544BB12CFE7DC40A12E3', X'FD1D5CAAE421544BB12CFE7DC40A12E3', 2, NULL, NULL, 2, NULL, 0, 'Spread each tostada shell with 2 tablespoons of heated beans and top corn, bell pepper and onion rings, then the tomatoes and lastly the freshly diced tomatoes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB66ED1D0D4E1A4F95CD1618FC22C70E', X'F652AB02C97CD444BC03A9D509261D6E', X'F652AB02C97CD444BC03A9D509261D6E', 0, NULL, NULL, 2, NULL, 0, 'Place yeast, 2 tablespoons of sugar, and 2 tablespoons of flour in a medium sized bowl and stir to combine. Be sure that water is between 105 and 110 degrees F and pour it over mixture. Stir to combine and set aside for 10 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'355AA267C5471C4B890C16890A68A32C', X'F652AB02C97CD444BC03A9D509261D6E', X'F652AB02C97CD444BC03A9D509261D6E', 1, NULL, NULL, 2, NULL, 0, 'Mix butter, cream cheese and milk in a small saucepan over medium low heat. Stir and mash the cream cheese until all ingredients are smooth and blended together. Remove from heat and set aside to cool. Mixture should be cool to room temperature before using. Combine 3 cups of flour, salt and cinnamon in a large bowl. Add eggs, yeast mixture and cooled butter mixture. Stir to combine. Add additional flour until mixture is tacky but not sticky (you may add as much a 1 full cup). Knead dough for about 10 minutes, adding additional flour if necessary, until smooth. Form dough into a ball.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C507E496E1F9C43BBE2ED8936D13B51', X'F652AB02C97CD444BC03A9D509261D6E', X'F652AB02C97CD444BC03A9D509261D6E', 2, NULL, NULL, 2, NULL, 0, 'Butter the bottom and sides of a large bowl. Place dough in bowl and roll it in butter on all sides. Cover with a towel and let dough rise for 1½ hours, until doubled in bulk. Punch the dough down and then roll it out on a large work surface to form a 20` x 14` rectangle. Brush the surface with the 3 tablespoons of melted butter. In a small mixing bowl, mix together the ingredients for the Praline Filling until it forms a paste. Press the paste gently all over the rectangle of dough.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A61B865E31EEE54ABE17DF3BF4590D88', X'F652AB02C97CD444BC03A9D509261D6E', X'F652AB02C97CD444BC03A9D509261D6E', 3, NULL, NULL, 2, NULL, 0, 'Starting on one edge, roll the dough into a 20` long tube. PInch the seams to seal the tube along the length. Twist the dough starting on one end and ending with pinching both ends together and shaping the dough into a circle. Place on baking sheet -cover with a towel and let dough rise for an additional 45 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14B028B6A95F51478C1BE9F2895D0B32', X'F652AB02C97CD444BC03A9D509261D6E', X'F652AB02C97CD444BC03A9D509261D6E', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F. Once dough has risen, bake for 30-35 minutes, or until golden brown and cake has a hollow sound when you thump it. Remove from oven and allow to cool completely.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5978F961583CBD4AA64C8E4C36BC0932', X'F652AB02C97CD444BC03A9D509261D6E', X'F652AB02C97CD444BC03A9D509261D6E', 5, NULL, NULL, 2, NULL, 0, 'To make colored sugars, place ½ cup of granulated sugar in each of three sealable plastic bags (or containers). Add 12 drops of yellow food coloring in one bag. Seal the bag and shake vigorously to evenly color the sugar, being sure to break up any clumps with your fingers. Repeat the process with a second bag using 12 drops of green food coloring. With the last bag, combine 10 drops of red and 6 drops of blue food coloring to create purple. Set sugars aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D336DFE52FB59F418853F63E421CA152', X'F652AB02C97CD444BC03A9D509261D6E', X'F652AB02C97CD444BC03A9D509261D6E', 6, NULL, NULL, 2, NULL, 0, 'In a medium sized bowl, combine powdered sugar, vanilla and cream. If it''s too thin, add more powdered sugar.Using a pastry brush, drizzle and liberally brush the icing on all sides of the cake. Use all of the icing, even though some with drip onto the parchment. Immediately sprinkle sugars over icing in alternating rows of green, yellow and purple.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BDADC83FCD36E419284D9465BF8ED08', X'A916FC07A41CFD458777BC54BEB19F78', X'A916FC07A41CFD458777BC54BEB19F78', 0, NULL, NULL, 2, NULL, 0, 'Roast the garlic cloves in the oven at 375 for 10-12 minutes. Remove and allow to cool. Slice the bread in half and rub both halves down with the roasted garlic. Drizzle with a touch of evvo and broil until slightly golden brown but not too much because you are going to put everything back into the oven soon to melt the cheese.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A06D50114A91B942BB4E180D57AFF0BA', X'A916FC07A41CFD458777BC54BEB19F78', X'A916FC07A41CFD458777BC54BEB19F78', 1, NULL, NULL, 2, NULL, 0, 'Brush the bread with the pesto then add a layer of the basil leaves. Next add on the slices of cheese and finally top with the slices of tomatoes. Sprinkle with sea salt and pepper and place back in the oven for another 5-6 minutes or until the cheese is melted.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B499384F9CEB864CB79393B472895342', X'30145961D906684496B642D8CD5C166C', X'30145961D906684496B642D8CD5C166C', 0, NULL, NULL, 2, NULL, 0, 'If you are making the Boudin filled king cake then remove the boudin from its casing and heat in a skillet over medium to low heat for 5 minutes.Stir in the mayo, cream cheese, salt and pepper, olives and parsley. Set aside to cool.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1ECD00B69577D4595D45824637465F2', X'30145961D906684496B642D8CD5C166C', X'30145961D906684496B642D8CD5C166C', 1, NULL, NULL, 2, NULL, 0, 'Place yeast, 2 tablespoons of sugar, and 2 tablespoons of flour in a medium sized bowl and stir to combine. Be sure that water is between 105 and 110 degrees F and pour it over mixture. Stir to combine and set aside for 10 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACF54A454D71224E9E300E385F7F59B6', X'30145961D906684496B642D8CD5C166C', X'30145961D906684496B642D8CD5C166C', 2, NULL, NULL, 2, NULL, 0, 'Mix butter, cream cheese and milk in a small saucepan over medium low heat. Stir and mash the cream cheese until all ingredients are smooth and blended together. Remove from heat and set aside to cool. Mixture should be cool to room temperature before using. Combine 3 cups of flour, salt and cinnamon (omit cinnamon if doing the boudin cake) in a large bowl. Add eggs, yeast mixture and cooled butter mixture. Stir to combine. Add additional flour until mixture is tacky but not sticky (you may add as much a 1 full cup). Knead dough for about 10 minutes, adding additional flour if necessary, until smooth. Form dough into a ball.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E6846EF1C94F34A8BAE4E4677EDB372', X'30145961D906684496B642D8CD5C166C', X'30145961D906684496B642D8CD5C166C', 3, NULL, NULL, 2, NULL, 0, 'Butter the bottom and sides of a large bowl. Place dough in bowl and roll it in butter on all sides. Cover with a towel and let dough rise for 1½ hours, until doubled in bulk. Punch the dough down and then roll it out on a large work surface to form a 20` x 14` rectangle. Brush the surface with the 3 tablespoons of melted butter. Place heaping spoonfuls of the pecan pie on the dough and spread it around gently at from the top to mid center of the dough. If you are doing boudin cake then place the boudin mixture and spread it out over the dough.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05B7EF86FF8AEE418B6FD80B3C531C3C', X'30145961D906684496B642D8CD5C166C', X'30145961D906684496B642D8CD5C166C', 4, NULL, NULL, 2, NULL, 0, 'Starting on one edge, roll the dough into a 20` long tube. Pinch the seams to seal the tube along the length. Twist the dough starting on one end and ending with pinching both ends together and shaping the dough into a circle. Place on baking sheet -cover with a towel and let dough rise for an additional 45 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCCBBFA6D0645245857A50B26DD9973B', X'30145961D906684496B642D8CD5C166C', X'30145961D906684496B642D8CD5C166C', 5, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F. Once dough has risen, Brush with butter if you are doing the king cake and brush with (2 tbsp butter melted mixed with 2 tbsp mustard and 1 tbsp poppy seeds). Bake for 30-35 minutes, or until golden brown and cake has a hollow sound when you thump it. Remove from oven and allow to cool completely.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F84F6285CC49B429686440B16A49839', X'30145961D906684496B642D8CD5C166C', X'30145961D906684496B642D8CD5C166C', 6, NULL, NULL, 2, NULL, 0, 'For the Pecan Pie King Cake:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA189902D1678E4FA067720458BB4604', X'30145961D906684496B642D8CD5C166C', X'30145961D906684496B642D8CD5C166C', 7, NULL, NULL, 2, NULL, 0, 'To make colored sugars, place ½ cup of granulated sugar in each of three sealable plastic bags (or containers). Add 12 drops of yellow food coloring in one bag. Seal the bag and shake vigorously to evenly color the sugar, being sure to break up any clumps with your fingers. Repeat the process with a second bag using 12 drops of green food coloring. With the last bag, combine 10 drops of red and 6 drops of blue food coloring to create purple. Set sugars aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAAD8E2E6D114A419D823249CB3ED879', X'30145961D906684496B642D8CD5C166C', X'30145961D906684496B642D8CD5C166C', 8, NULL, NULL, 2, NULL, 0, 'In a medium sized bowl, combine powdered sugar, vanilla and cream. If it''s too thin, add more powdered sugar.Using a pastry brush, drizzle and liberally brush the icing on all sides of the cake. Use all of the icing, even though some with drip onto the parchment. Immediately sprinkle sugars over icing in alternating rows of green, yellow and purple.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEAA0C2659F69B44A888B222C63706D6', X'CEF8B7392EAC014693D4E1917840100B', X'CEF8B7392EAC014693D4E1917840100B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees. Cover the bottom of the 9x13 pan with foil. Cut each dinner roll in half. Fold the slice roast beef in half and top with sliced cheese. Place them in the pan close together. You may need 2 pans depending on the size of your rolls. My pan fit 12 sandwiches and I used an additional pan for the remaining 4-5 rolls.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'544464ACCA835446843D7491E1E4D378', X'CEF8B7392EAC014693D4E1917840100B', X'CEF8B7392EAC014693D4E1917840100B', 1, NULL, NULL, 2, NULL, 0, 'Whisk all of the dressing ingredients together. Brush the sauce generously over the top of the rolls, stirring the dressing in between brushing to make sure all flavors and components stay combined. Allow to sit at room temperature for 10 minutes. Bake in the oven for 12-15 minutes until cheese is melted and the tops are slightly crispy.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2667008F3C95B43AE8EAAE6A21E2E9E', X'D1997BFD680BAA459AA02FBFFBEC2B7E', X'D1997BFD680BAA459AA02FBFFBEC2B7E', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 400. Slice the bread about 1 inch thick without cutting all the way through. Place the bread on the cookie sheet. Pour butter between the slices, then stuff the cheese in between the slices as well as the chopped jalapenos. Sprinkle with the garlic powder and onion salt and top the bread with the bacon slices without overlapping them. Brush the top with the jalapeno juice.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3088832511278F43B55978E12074B92D', X'D1997BFD680BAA459AA02FBFFBEC2B7E', X'D1997BFD680BAA459AA02FBFFBEC2B7E', 1, NULL, NULL, 2, NULL, 0, 'Bake until cheese is melted at 400 about 5 minutes. The set to broil and broil until the bacon is to your liking. I did another about 3 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A63B24EC62D1DF4D911E63C81600C097', X'7E07EB905CE19946A38AE83D1C8AB3D0', X'7E07EB905CE19946A38AE83D1C8AB3D0', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375. Unroll pizza dough on the surface of your baking pan allowing the edges to slightly fall off the side (as it will shrink up in the oven) place bake in refrigerator until time to cook. Meanwhile cook your sausage for 5 to 7 minutes breaking it up into small crumbles and then toss in the chopped onion and chopped artichoke hearts. Toss in the spinach and garlic and cook until garlic is browned. Remove and set aside to cool. Mix in the pizza sauce. Set aside. When cool to the touch top the first layer of pizza dough/crust with the filling the sprinkle on the pizza cheese. Use the second pizza dough and cut into 10-12 1 inch slices. Arrange the slices first horizontally then vertically on top of the filling. Sprinkle with Parmesan cheese and Bake at 375 for 15 minutes or 20');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0A3C3C36735BE44B8DDB85C76470347', X'01D9BAB324EB434383D83FB914733933', X'01D9BAB324EB434383D83FB914733933', 0, NULL, NULL, 2, NULL, 0, 'Saute the onions, garlic and peppers in a large cast iron skillet with 2 TBSP vegetable oil for 5-7 minutes. Remove, set aside and allow to cool. Butter each slice of bread on both sides. Place the bread slices in the bottom of a 9×13 casserole dish.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A4569BF47A1C64FAACAC22C5B52B50C', X'01D9BAB324EB434383D83FB914733933', X'01D9BAB324EB434383D83FB914733933', 1, NULL, NULL, 2, NULL, 0, 'Whisk together your eggs,milk, cheese and seasonings. Toss in the sauteed onions, peppers and garlic. Pour over the bread slices and top with the crumbled sausage. Refrigerate over night or at least 6 hours. I prepared this in the morning and left it in the fridge until dinner-time rolled around. Bake at 350 until eggs are set, about 45 to 50 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE9665573718B14B8CBF95D8443A2D4D', X'74F58EA35DBCDF4887B28ECCBAAB34D0', X'74F58EA35DBCDF4887B28ECCBAAB34D0', 0, NULL, NULL, 2, NULL, 0, 'Mix bisquick and salt together. Add in the sour cream and stir until nice and lumpy then slowly pour in the seven up. Stir until the dough comes together nicely. It is going to be sticky at first and then slightly soft once you pat it out. Take about ¼ cup of flour and spinkle onto your wooden board and pat out your dough. DO NOT KNEAD IT! Melt ¼ cup of butter in bottom of the baking pan (9 inch pan). Now at this point some roll out it gently and cut using their preferred biscuit cutter, I find it just as easier and more homemade looking if I just pat out the dough in my buttered baking pan (9 inch pan). I like to bake it that way and then cut them. Either way is fine. Sprinkle the cubes of butter across the top of the dough and bake at 425 for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E536B6ED23E2694A9BD6E231F80A2910', X'EBE33809BE772C4D90E8C627C4A13651', X'EBE33809BE772C4D90E8C627C4A13651', 0, NULL, NULL, 2, NULL, 0, 'Remove tops and bottoms from the eggplants and slice vertically into very thin pieces. Thinly slice the onion (and zucchini-optional) and arrange it with the sliced eggplant and garlic on a baking sheet. Drizzle with olive oil, season with ½ tsp salt and pepper each and ½ tsp Spanish thyme. Roast at 400 for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'487CB00642780E47A5430F195807136E', X'EBE33809BE772C4D90E8C627C4A13651', X'EBE33809BE772C4D90E8C627C4A13651', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, thaw out your puff pastry according to the package directions. Cut the dough into small rectangles (should have 3 long layers for sheet once rolled out- cut each long layer into 3 separate triangles) and lay each rectangle of dough flat on a baking sheet poked with holes from a fork. Bake according to the package directions then place aside to let it cool.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81737F9472706B47875DE2C4A5C051A0', X'EBE33809BE772C4D90E8C627C4A13651', X'EBE33809BE772C4D90E8C627C4A13651', 2, NULL, NULL, 2, NULL, 0, 'To make the red pepper cream (the glue that holds it all together) mix the ricotta cheese, minced roasted red pepper strips, remaining salt, pepper and Spanish thyme, red pepper flakes and ½ cup grated (not shredded) Parmesan cheese. Mix well and refrigerate until time to assemble.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7D41407E35B8E48AE4CDEC616223E29', X'EBE33809BE772C4D90E8C627C4A13651', X'EBE33809BE772C4D90E8C627C4A13651', 3, NULL, NULL, 2, NULL, 0, 'Assembly:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B03CE3229172724B961DEBE5EAA2D284', X'EBE33809BE772C4D90E8C627C4A13651', X'EBE33809BE772C4D90E8C627C4A13651', 4, NULL, NULL, 2, NULL, 0, 'Start with the one piece of puff pastry as the base, and then add on a layer of the red pepper cream, then eggplant, layer of basil, layer of zucchini, layer of onions and finally another sheet of puff pastry. Repeat this process once more (or twice if you prefer) ending with the puff pastry.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58AA67C668C2B04DAD0ACE55A6D2B7C6', X'2B1DF30F2746EE4B8A44497664487506', X'2B1DF30F2746EE4B8A44497664487506', 0, NULL, NULL, 2, NULL, 0, 'For the Cheese Batter: Bthe butter in a small saucepan over medium heat. Stir the pan continually for about 5 to 7 minutes, until the crackling, and foaming has subsided, the butter has browned, and has a nutty aroma. Watch it closely so that it doesn’t go from browned and nutty to burnt. Transfer to a large mixing bowl and allow to cool for 10-15 minutes. Combine the remaining cream cheese frosting ingredients into the same bowl as the butter and using a handheld electric mixer beat until smooth and creamy.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36360F66BEB58A4B876B359290455995', X'2B1DF30F2746EE4B8A44497664487506', X'2B1DF30F2746EE4B8A44497664487506', 1, NULL, NULL, 2, NULL, 0, 'For the Pumpkin Spice Batter: Preheat oven to 350F; lightly grease 3 mini loaf pans with butter.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBF8ED413D2D104E8748CF62A78E456A', X'2B1DF30F2746EE4B8A44497664487506', X'2B1DF30F2746EE4B8A44497664487506', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together the brown sugar and eggs until light and fluffy, then stir in the pumpkin, canola oil, and vanilla. In a separate bowl, whisk or sift together the flour, baking powder, baking soda, salt, cinnamon, nutmeg, ginger, and cloves. Gradually stir the dry ingredients into the wet, being careful not to over-mix. Divide the pumpkin batter between the 3 loaf pans, then pour the cheese batter on top.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F0A95ABD8C0554DB6C3C9421A9442CD', X'2B1DF30F2746EE4B8A44497664487506', X'2B1DF30F2746EE4B8A44497664487506', 3, NULL, NULL, 2, NULL, 0, 'Bake until golden around the edges, about 35 to 40 minutes, or until a toothpick inserted inside comes out clean or with just a couple crumbs. Cool 10 minutes in the pans, then remove from the pans and transfer to a wire rack to finish cooling.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DE65003052A9E498D8C7C3011477903', X'4A8A9018B49C464480393548E9D6E763', X'4A8A9018B49C464480393548E9D6E763', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350° F. Spray 2 8x8 pans with cooking spray then place parchment paper in the pan pressing so it sticks well to the bottom and sides.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28BF96800E9EFF47B088BC4527DC18CD', X'4A8A9018B49C464480393548E9D6E763', X'4A8A9018B49C464480393548E9D6E763', 1, NULL, NULL, 2, NULL, 0, 'Whisk the flour, baking powder, baking soda, salt, and spices into a medium bowl and set aside. In an electric mixer fitted with the paddle attachment, beat the eggs, sugar and olive oil together on medium speed until light and fluffy, about 3 minutes, then beat in the vanilla extract. Scrape down the sides of the bowl as needed. Beat in the dry ingredients all at once on low speed until they are thoroughly combined, then switch to medium speed and mix for 30 seconds. Mix in the zucchini until completely incorporated.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6EFA6FF2BA33D41939A8FE872EB8F59', X'4A8A9018B49C464480393548E9D6E763', X'4A8A9018B49C464480393548E9D6E763', 2, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared pans, smoothing the top with a spatula. Bake the cakes for 40 to 45 minutes or until a tester inserted in the cakes comes out clean and the cakes have begun to pull away from the sides of the pans. Once the pans are cool, place them in the freezer for 15-30 minutes. Meanwhile, take out your frozen yogurt and allow it to come to room temperature so it evenly spreads.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B581503DC27A9F42A2874AD9C00699BB', X'4A8A9018B49C464480393548E9D6E763', X'4A8A9018B49C464480393548E9D6E763', 3, NULL, NULL, 2, NULL, 0, 'Remove one pan and spread the yogurt on top of the zucchini cake. You can do as much or as little yogurt as you prefer. Place the pan back in the freeze for an hour. Now remove both pans and pull out the second zucchini cake from the pan using the parchment paper to help you get it out. Invert it on top of the yogurt layer and press down slightly. Wrap the whole pan with plastic wrap and freezer overnight. You can either cut out your ''sandwiches'' in the pan itself or remove the whole zucchini cake from the pan using the parchment paper and cut out your sandwiches.( That is what I did)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28D08043085BAF45B0A55A4A4BBDC923', X'4A8A9018B49C464480393548E9D6E763', X'4A8A9018B49C464480393548E9D6E763', 4, NULL, NULL, 2, NULL, 0, 'Store in ziploc freezer bags for up to 2 week- if that last that long.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4BC27EA1D5F904481C57439BE98BA70', X'337A2788C704424C87939F659749ABB8', X'337A2788C704424C87939F659749ABB8', 0, NULL, NULL, 2, NULL, 0, 'Mix seasonings together along with the olive oil and toss with the chicken tenders. Roast in oven for 15-20 minutes (depending on size of tenders) at 375. Set aside to cool. Once cool cut into nice size chunks.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7481C8D4415DBD4DAEB3B6955B4C2A03', X'337A2788C704424C87939F659749ABB8', X'337A2788C704424C87939F659749ABB8', 1, NULL, NULL, 2, NULL, 0, 'Generously butter one side of a slice of bread. Place bread butter-side-down onto skillet bottom and add slice of pepperjack and slice of swiss cheese, next add the chicken and avocado slices. Butter a second slice of bread on one side and place butter-side-up on top of sandwich. Grill until lightly browned and flip over; continue grilling until cheese is melted. Repeat with remaining slices of bread. Top with the spicy tomato sauce and call it a night!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58D487606E443C4588CE2BDC93F3208D', X'2C9D886367FE5A4682AE794D452C9989', X'2C9D886367FE5A4682AE794D452C9989', 0, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 350°F (175°C).');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'596BDE22474CBC42B3883AA7791B0957', X'2C9D886367FE5A4682AE794D452C9989', X'2C9D886367FE5A4682AE794D452C9989', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the eggs, milk and vanilla seeds. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17F6F720D6BAC14C91D7059B4BB16474', X'2C9D886367FE5A4682AE794D452C9989', X'2C9D886367FE5A4682AE794D452C9989', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, sift together the flour, baking powder, cinnamon and salt. Stir in the sugar and coconut. Make a well in the centre of the dry ingredients, and slowly add the egg mixture, stirring until just combined. Fold in the melted butter, sunflower seeds and chocolate chips, being careful not to overmix.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C323B38EBF75374F9E91D0804AF42772', X'2C9D886367FE5A4682AE794D452C9989', X'2C9D886367FE5A4682AE794D452C9989', 3, NULL, NULL, 2, NULL, 0, 'Grease and flour a 8-by-4-inch loaf pan or individual panettone or muffin liners. Pour in the batter and bake in the preheated oven until the loaf is golden and a cake tester inserted into the middle comes out clean, around 1 hour or 20-25 minutes for individual liners. * Make sure to fill batter ¾ to the top of your panettone liners so you get a nice raised loaf of bread peaking out of the top.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05B7B4934534434ABFD40D7E6BDCF83A', X'09EB1A5202093249B7F6C6283F5B294C', X'09EB1A5202093249B7F6C6283F5B294C', 0, NULL, NULL, 2, NULL, 0, 'Grease shallow 3-quart ceramic baking dish. In microwave-safe small bowl, reserve 2 tablespoons dulce de leche; cover and refrigerate.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F27557196E9EEF48838D8F5EB7E1249A', X'09EB1A5202093249B7F6C6283F5B294C', X'09EB1A5202093249B7F6C6283F5B294C', 1, NULL, NULL, 2, NULL, 0, 'Lightly butter both sides of all bread slices. In 12-in. skillet, cook bread slices on medium-high 1 to 2 minutes per side or until golden brown and toasted. Cool on wire rack. In same skillet, combine milk, butter and remaining dulce de leche. Heat on medium-high 6 to 8 minutes or until bubbles form around edge of pan, stirring occasionally.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03C1A4642FB00D40B1E25A31BDF259BC', X'09EB1A5202093249B7F6C6283F5B294C', X'09EB1A5202093249B7F6C6283F5B294C', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in large bowl, with wire whisk, combine eggs, brown sugar, cinnamon, vanilla, and salt. Continue whisking and add ½ cup hot milk mixture in slow, steady stream. Whisk in remaining milk mixture.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89CB912C961DEA4DA0CB4EF15D42DC93', X'09EB1A5202093249B7F6C6283F5B294C', X'09EB1A5202093249B7F6C6283F5B294C', 3, NULL, NULL, 2, NULL, 0, 'Cut toasted bread slices into 1-in. cubes; arrange in single layer in prepared baking dish. Sprinkle with even layer of chocolate. Pour egg mixture over bread and chocolate. Let stand at least 30 minutes or overnight.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'424E5E709EF31548976669BE9FD63A6B', X'09EB1A5202093249B7F6C6283F5B294C', X'09EB1A5202093249B7F6C6283F5B294C', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Bake 25 to 30 minutes or until just set and dark golden brown on top. Cool bread pudding in pan on wire rack 20 minutes. Microwave reserved dulce de leche on High 20 seconds to warm. Drizzle over bread pudding to serve.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DDF8A8D1371AA4DBCCAB85C48EF8FFC', X'2CB45CB28E8EAA4D952DA11FAF25EC36', X'2CB45CB28E8EAA4D952DA11FAF25EC36', 0, NULL, NULL, 2, NULL, 0, 'Saute the shallots, garlic, red pepper flakes, thyme and oregano in the oil over medium to high heat for 3-4 minutes (until slightly toasted). Add in the beer, hot sauce, vinegar and lemon juice. Season with salt, pepper and cajun seasoning. Bring to a boil, add in the cream then reduce to simmer for another 5-7 minutes, until liquid is reduced and slightly thick. Add in the crawfish and cover for 10 minutes (the crawfish will cook during this time).');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE2B21453AB4054EB164BC3077E8F77B', X'2CB45CB28E8EAA4D952DA11FAF25EC36', X'2CB45CB28E8EAA4D952DA11FAF25EC36', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, make your shortcakes.Preheat your oven to 425 degrees. Sift together your dry ingredients (this includes the salt and garlic) then using your hands (who needs a pastry blender!) cut in the chilled butter until your mixtures resembles small peas. Using a fork stir in the buttermilk and mix until just combined(very important to NOT overmix). Turn out the dough onto a lightly floured surface and gently fold then gather about three times until the dough comes together. Now roll the dough out using your rolling pin to approx ½ inch thick. Cut out the biscuits using a biscuit cutter and place them on a large baking sheet as close together as you can get them. Bake until slightly golden brown, about 20 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5270ADE2407F164581ED197C2F5D14B4', X'2CB45CB28E8EAA4D952DA11FAF25EC36', X'2CB45CB28E8EAA4D952DA11FAF25EC36', 2, NULL, NULL, 2, NULL, 0, 'Top shortcakes with the crawfish mixture and sauce. Serve and ENJOY a job well done!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'684B8B24893D774BB657912D65D8C3C4', X'A51114F8B2001C448BAA091BE5F87943', X'A51114F8B2001C448BAA091BE5F87943', 0, NULL, NULL, 2, NULL, 0, 'Melt butter with milk. Heat until very warm, but don’t boil. Allow to cool until still warm to the touch, but not hot. Sprinkle yeast over the top, stir gently, and allow to sit for 10 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95C991451AE72346BA5B3747ACCE58E3', X'A51114F8B2001C448BAA091BE5F87943', X'A51114F8B2001C448BAA091BE5F87943', 1, NULL, NULL, 2, NULL, 0, 'Combine both flours, 1½ TBSP cinnamon and salt.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1241B737C9D8F34C8144BB4F33B768E4', X'A51114F8B2001C448BAA091BE5F87943', X'A51114F8B2001C448BAA091BE5F87943', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of an electric mixer, mix ½ cup sugar and eggs with the paddle attachment until combined. Pour in milk/butter/yeast mixture and stir to combine. Add half the flour and beat on medium speed until combined. Add the other half along with the raisins and beat until combined.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB6ED44F925E13448830B2EECB92C6E8', X'A51114F8B2001C448BAA091BE5F87943', X'A51114F8B2001C448BAA091BE5F87943', 3, NULL, NULL, 2, NULL, 0, 'Switch to the dough hook attachment and beat/knead dough on medium speed for ten minutes. If dough is overly sticky, add ¼ cup flour and beat again for 5 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64200389C0E17B4CBA8F207A0CC2CCEE', X'A51114F8B2001C448BAA091BE5F87943', X'A51114F8B2001C448BAA091BE5F87943', 4, NULL, NULL, 2, NULL, 0, 'Heat a metal or glass mixing bowl so it’s warm. Drizzle in a little canola oil, then toss the dough in the oil to coat. Cover bowl in plastic wrap and set it in a warm, hospitable place for at least 2 hours.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D1A47B859BC1E43A33C58D4C50DD81A', X'A51114F8B2001C448BAA091BE5F87943', X'A51114F8B2001C448BAA091BE5F87943', 5, NULL, NULL, 2, NULL, 0, 'Turn dough out onto the work surface. Roll into a neat rectangle no wider than the loaf pan you’re going to use, and about 18 to 24 inches long. Smear with 2 tablespoons melted butter. Mix remaining sugar and cinnamon together, then sprinkle evenly over the butter-smeared dough. Starting at the far end, roll dough toward you, keeping it tight and contained. Pinch seam to seal.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E14130BB4F2CAA48A14B6C1AAA43C79B', X'A51114F8B2001C448BAA091BE5F87943', X'A51114F8B2001C448BAA091BE5F87943', 6, NULL, NULL, 2, NULL, 0, 'Smear loaf pan with softened butter. Place dough, seam down, in the pan. Cover with plastic wrap and allow to rise for 2 hours. Preheat oven to 350 degrees. Mix a little egg with milk, and smear over the top. Bake for 40 minutes on a middle/lower rack in the oven. Remove from the pan and allow bread to cool.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'668761638676E94C890C0E2A3D8E393E', X'3C7EAD662EFA2642BC1A21B459FB41EB', X'3C7EAD662EFA2642BC1A21B459FB41EB', 0, NULL, NULL, 2, NULL, 0, 'Mix together the first 6 ingredients and refrigerate for 2 hours. Mix together the mustard and horseradish and spread on the bread. Butter the outside of the bread and toast on low heat until slightly crispy. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'159B322C488CBB4A9C74AD25F92511DA', X'3C7EAD662EFA2642BC1A21B459FB41EB', X'3C7EAD662EFA2642BC1A21B459FB41EB', 1, NULL, NULL, 2, NULL, 0, 'Cut the bread into 2 layers horizontally. Layer half of the provolone on the bottom half of bread. Then the mozzarella and swiss cheese if you choose. Then the Mortadella, then the Capicola, salami, and the remainder of provolone. Top this with the olive salad mixture we did at the beginning. Put the top half of the bread on and press it down. Wrap in foil tightly and place on a baking sheet. Sometimes I even place a brick wrapped in foil on top to really get those flavors soaked into all the parts of the bread. Bake at 170 for 3-4 hours or 250 for 1-2 hours.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E9B1D8454610044A94773DC46B8C109', X'00C6F75529350E4DB85977123E3B8A7F', X'00C6F75529350E4DB85977123E3B8A7F', 0, NULL, NULL, 2, NULL, 0, 'Combine the flour, baking powder, spices and salt in a medium bowl. Whisk to combine. Incorporate the butter into the flour with your hands, a mixer or a pastry blender. Mix in the cranberries and raisins. Form a well in the center and pour in the eggnog. Using a wooden spoon slowly mix well just until combined. Flour your work surface and turn out the dough. Form it into a rough rectangle with your hands. Flour the top surface lightly, then roll it to a thickness of about ½ inch thick. Fold it into thirds, then roll it again and fold in thirds. Roll it to about ½- to ¾-inch thick then use a 2½-inch biscuit cutter to cut rounds from the dough.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CACAA3DD575AD49A216AC1C5B0A4D2F', X'00C6F75529350E4DB85977123E3B8A7F', X'00C6F75529350E4DB85977123E3B8A7F', 1, NULL, NULL, 2, NULL, 0, 'Place the rounds on a baking sheet. If you prefer crisp edges, keep them separated. If you prefer tender edges, arrange them on the sheet so the edges touch.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECAD5AD906DFD94986609D1DC0467F1F', X'00C6F75529350E4DB85977123E3B8A7F', X'00C6F75529350E4DB85977123E3B8A7F', 2, NULL, NULL, 2, NULL, 0, 'Bake at 400 degrees until the biscuits are lightly browned, 12-15 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'476602E9B01A6047ABA644C63962F92C', X'22892FBBA800264192324C9C9DD1500C', X'22892FBBA800264192324C9C9DD1500C', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 degrees.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF6B5B2FC85D494CAFF86671F7800870', X'22892FBBA800264192324C9C9DD1500C', X'22892FBBA800264192324C9C9DD1500C', 1, NULL, NULL, 2, NULL, 0, 'Cut the crusts from a slice of bread and roll until very thin. Spread both sides of the bread with melted butter and then sprinkle one side with the cinnamon sugar, making sure to cover very well. Fold each side into the middle and then gently roll up and place in a buttered mini muffin tin. Sprinkle with a little more cinnamon sugar. Alternately, you can fold the bread into a triangle, and then fold into another smaller triangle and place on a parchment-covered baking sheet.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C36E91CA956E2E42845245E9EFD8F0BD', X'22892FBBA800264192324C9C9DD1500C', X'22892FBBA800264192324C9C9DD1500C', 2, NULL, NULL, 2, NULL, 0, 'When all of the rolls are made, bake for 15 to 20 minutes, or until they are just starting to turn golden brown and get a little crisp. Remove from the muffin tin to cool slightly before serving.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00D5FCCE8B5C0149AE743A80CD4D7F07', X'22892FBBA800264192324C9C9DD1500C', X'22892FBBA800264192324C9C9DD1500C', 3, NULL, NULL, 2, NULL, 0, 'If desired, drizzle with some icing made with powdered sugar that''s been moistened with a little milk.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BBF42398EB9A940ADA6A3DE0E5E82CB', X'22892FBBA800264192324C9C9DD1500C', X'22892FBBA800264192324C9C9DD1500C', 4, NULL, NULL, 2, NULL, 0, 'These freeze really well. To reheat, place briefly in a microwave to thaw and then bake in a low oven (250 or 300 until warm and slightly crisp. Alternately, you can reheat them in foil in the oven.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1BC6B590BDD7849BA22236432608A4F', X'4516B820680B204184D2E0B7A5D291C5', X'4516B820680B204184D2E0B7A5D291C5', 0, NULL, NULL, 2, NULL, 0, 'Toss the shrimp in ¼ cup of the garlic oil and ½ tsp of salt. Set aside');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'498957CD0E1A104FA9E061F308F13227', X'4516B820680B204184D2E0B7A5D291C5', X'4516B820680B204184D2E0B7A5D291C5', 1, NULL, NULL, 2, NULL, 0, 'Boil your pasta according to the package directions. Saute the onion and garlic in 2 TBSP of olive oil until tender, approximately 5 minutes. Toss in the shrimp and cook for 5-7 minutes until pink. Add in the crushed red pepper flakes, pepper and salt. Toss the pasta with the shrimp and then toss it all again with the remaining garlic oil. Sprinkle with chopped parsley or basil.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7683D4736BEDB54C9AF5B9B5D968E167', X'9034D6AEBCC8554091243B82DE2C5EFA', X'9034D6AEBCC8554091243B82DE2C5EFA', 0, NULL, NULL, 2, NULL, 0, 'Combine ¾ of the artichokes in a blender or food processor with the pecans, salt, pepper, parsley, basil, lemon juice and garlic. Pulse until combined then slowly being pouring in the oil pulsing until pesto is nice and smooth. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03F75202CAAFC44C828FBB8E7EE67D65', X'9034D6AEBCC8554091243B82DE2C5EFA', X'9034D6AEBCC8554091243B82DE2C5EFA', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, boil the orzo according to the package directions, drain. If you are grilling/roasting your corn then butter each side and place on high heat or 400 for 10 minutes. If you are boiling the corn, place corn cobs in a large stockpot of boiling water and boil for 5-7 minutes. Regardless of method allow corn to cool and then remove kernels from the cob.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6FDDBA8BA443D4D9B6E7C1AFBD29C78', X'9034D6AEBCC8554091243B82DE2C5EFA', X'9034D6AEBCC8554091243B82DE2C5EFA', 2, NULL, NULL, 2, NULL, 0, 'Combine the corn, orzo, artichoke pesto, parmesan cheese, cherry tomatoes and the remaining ¼ cup of artichoke hearts. Toss and enjoy.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65DA51E6EC7AA64BB50469F7B6B2C8EB', X'9212D1A3CD9B48428A338C7E1BC983A2', X'9212D1A3CD9B48428A338C7E1BC983A2', 0, NULL, NULL, 2, NULL, 0, 'In the same pot or a large sauté pan or skillet, melt the butter over medium heat. Add the flour and stir constantly for about three minutes, until completely smooth and incorporated. Pour in 1 cup of the milk in a thin stream while whisking. It might start out lumpy, but keep whisking and it will smooth out into a thick paste. Add the rest of the milk and whisk until smooth. Add the mustard and white wine, stir until combined and then add in the remaining seasonings and then cheeses ( a little cheese at a time) until all cheese is melted. Adjust taste if necessary with more or less salt.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2BCE63F1905C94B9698ACD46E81650D', X'9212D1A3CD9B48428A338C7E1BC983A2', X'9212D1A3CD9B48428A338C7E1BC983A2', 1, NULL, NULL, 2, NULL, 0, 'Add the drained pasta and gently mix. Enjoy it HOT off the stove or pour into greased 9x13 inch pan and bake at 350 for 20 minutes (if you prefer baked mac n cheese- but don''t forget to top it with more cheese if you bake it-)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65ED0F2AD30CB74EBE80FA13D3BAEE9A', X'7D6AF8F4615EA2429151B27F3BC75E9C', X'7D6AF8F4615EA2429151B27F3BC75E9C', 0, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat the 6 TBSP evoo over medium to low heat and saute the onion for 8-10 minutes. Add in the radicchio and minced garlic. Cook for another 5-7 minutes uncovered then cover and turn to low and cook for another 5 minutes until the radicchio is wilted and slightly charred, turning occasionally, about 5 minutes and the onions caramelized.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7885D7FDD2F1E747AD804FE532D28CFB', X'7D6AF8F4615EA2429151B27F3BC75E9C', X'7D6AF8F4615EA2429151B27F3BC75E9C', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, boil your pasta according to the package directions making sure to salt the pot while you are bringing the water to a boil. Once the noodles are tender and ready, reserve ? cup of pasta water and then drain the rest.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B26713654E7D5640A9DE33782EAF4951', X'7D6AF8F4615EA2429151B27F3BC75E9C', X'7D6AF8F4615EA2429151B27F3BC75E9C', 2, NULL, NULL, 2, NULL, 0, 'When the onions and radicchio are ready add in the seasonings and basil infused oil. Stir. Next add in the butter and allow it to melt. Then add in the cream, 4 TBSP (1/4 cup) pasta water and the cheese. Toss with the noodles and serve with additional cheese on top!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EED7C2AA7C7ECD49B90D2AD65E228CF3', X'72BAFCB4FAD6A44697E11BE17C4D953C', X'72BAFCB4FAD6A44697E11BE17C4D953C', 0, NULL, NULL, 2, NULL, 0, 'Boil pasta according to package directions. Meanwhile, drain the chickpeas on a paper towel and place on a baking sheet. Drizzle with 2 TBSP olive oil, ½ tsp salt and pepper each, ½ tsp paprika. Toss well and bake at 400 for 10-12 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'440A29E90DB8894F8A0176CABBD5E2D7', X'72BAFCB4FAD6A44697E11BE17C4D953C', X'72BAFCB4FAD6A44697E11BE17C4D953C', 1, NULL, NULL, 2, NULL, 0, 'Saute the garlic in 2 TBSP of olive oil for 3-5 minutes until tender and toasted. Remove from heat and transfer to a glass bowl or jar. Whisk in the remaining olive oil, balsamic vinegar, mustard and salt and pepper.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2AF3CAAC250344BB40321F7FE4B4EE5', X'72BAFCB4FAD6A44697E11BE17C4D953C', X'72BAFCB4FAD6A44697E11BE17C4D953C', 2, NULL, NULL, 2, NULL, 0, 'Mix together the drained pasta with the capers, olives, tomatoes, fried chickpeas and balsamic parmesan sauce. Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A65FF8BA02E54647957D16FA63CB0B31', X'9609F3C340881346A04EF1FEB52492BD', X'9609F3C340881346A04EF1FEB52492BD', 0, NULL, NULL, 2, NULL, 0, 'Saute the thawed spinach in the olive oil along with the garlic, red peppers, white wine, ½ tsp sea salt, ½ tsp pepper and the roasted red peppers. Cook on medium low for 5-7 minutes. Set aside to allow spinach to soak up all moisture then ring out spinach using a kitchen towel. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC4646391E289E4BBB25741B8E1412B5', X'9609F3C340881346A04EF1FEB52492BD', X'9609F3C340881346A04EF1FEB52492BD', 1, NULL, NULL, 2, NULL, 0, 'Boil the potatoes in a large pot of water along with 1 tsp sea salt for 10-12 minutes. Drain and pat potatoes dry. Using a potato ricer or masher mash potatoes to a fine consistency. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E91C2FB5842A7E49AAC5AC9247F133A4', X'9609F3C340881346A04EF1FEB52492BD', X'9609F3C340881346A04EF1FEB52492BD', 2, NULL, NULL, 2, NULL, 0, 'In a food processor puree the spinach mixture then add in the egg, egg yolk, ½ salt, ½ pepper, nutmeg and parmesan. Add in the flour and pulse until mixture is all combined. Place into a large bowl and gently fold in the riced potatoes, gently stirring until just combined. You will have very soft dough. Turn out onto a lightly floured board and roll into a 1½-inch thick snake. Dust the dough lightly with flour and cut into 1½-inch pieces. Roll each piece gently with your thumb, leaving a small indentation on 1 side of the gnocchi. Place the finished gnocchi on a lightly floured, parchment paper-lined baking sheet. (if you have time, let the gnocchi rest in the refrigerator or freezer until chilled prior to cooking. It will help the gnocchi hold its shape better).');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDD74FFFB5C0124D8C9E225E7B63FCD3', X'9609F3C340881346A04EF1FEB52492BD', X'9609F3C340881346A04EF1FEB52492BD', 3, NULL, NULL, 2, NULL, 0, 'Heat a couple tablespoons of butter (or olive oil) in a large well-seasoned (or non-stick) skillet over medium-high heat. Add the gnocchi in a single layer and toss to coat with the butter. Let them cook until they are golden brown on the bottoms and then use a metal spatula to flip the gnocchi. Cook until they are golden all over.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'701F9956A7552046821D6056D08A893E', X'F78185686C55B84A922A3C8801738FB4', X'F78185686C55B84A922A3C8801738FB4', 0, NULL, NULL, 2, NULL, 0, 'Toast pecans in a nonstick skillet over a medium-low flame, stirring occasionally, until golden and fragrant—about 5 minutes. Transfer to a shallow bowl or plate in a single layer to cool completely.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFBDE7B91436EB4BB91F68C13F1C9501', X'F78185686C55B84A922A3C8801738FB4', X'F78185686C55B84A922A3C8801738FB4', 1, NULL, NULL, 2, NULL, 0, 'Transfer pecans to a food processor and pulse until you have fine pecan crumbs. Reserve ½ cup of pecan crumbs and set aside. Mince garlic and mix with ½-teaspoon salt in a small bowl, mashing with a fork to form a paste of sorts. Combine basil, sage, pecan crumbs, garlic and Parmesan in bowl of food processor. Pulse several times to coarsely chop and combine everything. Add olive oil and 2 tablespoons hot water `you can steal some from the boiling pasta water` and blend to proper consistency. Don’t overprocess—you want a little texture to the pesto.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F596A2AAFAB6A14DA397E49C608BC1E3', X'F78185686C55B84A922A3C8801738FB4', X'F78185686C55B84A922A3C8801738FB4', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, boil your pasta according to package directions. Heat olive oil in medium skillet and saute the broccoli and garlic until slightly tender. Stir in ¼ cup of reserved pasta water. Mix in the pasta and pesto. Toss with parmesan cheese and remaining pecan crumbs.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DC75078456A0C46947A0FAAE402BF86', X'F27FC3B3D17E924886CCEDA119D822EB', X'F27FC3B3D17E924886CCEDA119D822EB', 0, NULL, NULL, 2, NULL, 0, 'Place the ground meat, uncooked quinoa, black beans, tomatoes,corn, onions and peppers into the slow cooker. Arrange in sections or just toss them all in.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A843FB1639341C458FC50715EE36DB92', X'F27FC3B3D17E924886CCEDA119D822EB', X'F27FC3B3D17E924886CCEDA119D822EB', 1, NULL, NULL, 2, NULL, 0, 'In a small measuring cup stir together the adobe sauce, salsa and enchilada sauce. Add in the chopped chipotle peppers, minced garlic, lime zest and all of the remaining seasonings. Pour over the meat and veggies in the slow cooker and give it one good stir. Add in the water and chicken broth. Set the slow cooker to low and cook for 6 hours. Just before serving stir in the cheese and fresh cilantro and ENJOY!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0FD0AD3F34A9A4D9082D2142604428E', X'F27FC3B3D17E924886CCEDA119D822EB', X'F27FC3B3D17E924886CCEDA119D822EB', 2, NULL, NULL, 2, NULL, 0, 'Save the leftovers to use as a filling for enchiladas or empanadas.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'680FAABC24E33E409ED694DE5298E9B4', X'451C3F2418522045A9C8581CB0D48368', X'451C3F2418522045A9C8581CB0D48368', 0, NULL, NULL, 2, NULL, 0, 'Cook the bacon in a non-stick skillet over medium to high heat. Remove the bacon from the pan once it is cooked, approximately 5-7 minutes. Leave the bacon grease in the pan and cook the venison, onions, celery and bell pepper over medium to low heat. Meanwhile, roughly chop the cooled bacon slices and set aside. Once the venison is cooked through add in the rest of the ingredients- making sure to cover the meat and rice with the water and chicken stock- adding more if necessary. Cook over low heat until rice is done.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC7AFACB53351440A4748C677979EF05', X'9A385EFCB6E1064E82025590A5D27DFE', X'9A385EFCB6E1064E82025590A5D27DFE', 0, NULL, NULL, 2, NULL, 0, 'Place the onion along the bottom of your slow cooker. Combine the mustard, butter, adobe sauce, chopped chipotle peppers and molasses in a small bowl then rub the pork butt down with the wet mixture. Now mix together your dry ingredients very well until everything is incorporate and then rub down the pork butt with the dry seasoning mix.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D9DD282852F814CAEAC81D68D24F247', X'9A385EFCB6E1064E82025590A5D27DFE', X'9A385EFCB6E1064E82025590A5D27DFE', 1, NULL, NULL, 2, NULL, 0, 'Place the pork butt in the slow cooker and cook on med-low for 6-8 hours until moist and fork tender. At this point you can remove the pork and shred it and place it back into the slow cooker to soak up more flavor on the warm setting or you can serve it as is and spoon remaining sauce in the slow cooker over the pork before serving.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EB7A58DEB1E8B49BB4B17D5853B9B7B', X'5CFC9C291896B641AC9BF299275633B2', X'5CFC9C291896B641AC9BF299275633B2', 0, NULL, NULL, 2, NULL, 0, 'Slice the peppers in half and or cut a wide circle around each stem hollow out the inside in order to stuff them entirely. Remove the seeds and ribs.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DDB6629AF02F341897914A0C573AC22', X'5CFC9C291896B641AC9BF299275633B2', X'5CFC9C291896B641AC9BF299275633B2', 1, NULL, NULL, 2, NULL, 0, 'Place the peppers in a baking dish skin side up. Broil about 5-7 minutes, then flip the peppers and broil 5 more minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FE649A076ED774A92DB218E712BDD50', X'5CFC9C291896B641AC9BF299275633B2', X'5CFC9C291896B641AC9BF299275633B2', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook your ground beef in a large skillet, when almost brown add in the chopped onions, bell pepper and green chiles. Cook for another 5-6 minutes until the onions and pepper are translucent. Stir in the cilantro, corn, black beans and seasonings. Remove from heat and allow to cool slightly. Stir in the cheeses, tortilla strips and tamale sauce. Mix well and then begin stuffing the peppers regardless of whether you cut them in half or left them whole. Replace caps to the peppers if you left them whole and secure with a toothpick.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'244A891655FC0A49AF322F251D792D83', X'5CFC9C291896B641AC9BF299275633B2', X'5CFC9C291896B641AC9BF299275633B2', 3, NULL, NULL, 2, NULL, 0, 'If you are grilling then heat grill to 400 and grill the peppers approximately 30 minutes. If you are roasting them then place the stuffed peppers back into the baking dish and roast at 400 for 15-20 minutes. Remember, we already pre-roasted the peppers to a certain degree to save a little time in the end. So the goal of roasting them a second time already stuffed is to give them that nice char and have the pepper skin be soft to the touch. The filling is already cooked.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'213F5A2D17E85F41A393F40CDC7C70F6', X'06D4BEF9F9289D41979503B56C569C35', X'06D4BEF9F9289D41979503B56C569C35', 0, NULL, NULL, 2, NULL, 0, 'Toss the meat with the canola oil and season blend. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'634A6928EFAE5C4D895B317B14CE54C6', X'06D4BEF9F9289D41979503B56C569C35', X'06D4BEF9F9289D41979503B56C569C35', 1, NULL, NULL, 2, NULL, 0, 'In large saute pan heat 1 TBSP canola oil and saute the bag of frozen veggies for 5-6 minutes. Remove from the pan and place the meat in the pan and stir in the soy sauce, ginger and garlic oil. Cook over medium to high heat for 3 minutes per side. No more than 7 minutes. Add back the veggies to the pan, quickly add in the cornstarch liquid,t urn heat to medium low and allow to sit covered for another 3--4 minutes until thickened. Serve over brown or white rice.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ED426CD469F79489073A4E735B4F008', X'1C7275EA4EE443498CF2355C5D212D00', X'1C7275EA4EE443498CF2355C5D212D00', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375. Unroll pizza dough on the surface of your baking pan allowing the edges to slightly fall off the side (as it will shrink up in the oven) place bake in refrigerator until time to cook. Meanwhile cook your sausage for 5 to 7 minutes breaking it up into small crumbles and then toss in the chopped onion and chopped artichoke hearts. Toss in the spinach and garlic and cook until garlic is browned. Remove and set aside to cool. Mix in the pizza sauce. Set aside. When cool to the touch top the first layer of pizza dough/crust with the filling the sprinkle on the pizza cheese. Use the second pizza dough and cut into 10-12 1 inch slices. Arrange the slices first horizontally then vertically on top of the filling. Sprinkle with Parmesan cheese and Bake at 375 for 15 minutes or 20');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA795A07BE7C6B4D89E038E1B9495134', X'A092C2FBB1D15745A2207CD817FB2527', X'A092C2FBB1D15745A2207CD817FB2527', 0, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together except for the meat. Bring mixture to a simmer. Reduce heat to low and simmer, stirring occasionally, until the liquid has evaporated and the mixture is thick, about 40 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCE7E735B869A547B5F82C150D758EE1', X'A092C2FBB1D15745A2207CD817FB2527', X'A092C2FBB1D15745A2207CD817FB2527', 1, NULL, NULL, 2, NULL, 0, 'Add the broken up meat to the sauce and cook for another 5-10 minutes just until the meat is nice and warm. If you are not using leftover meatloaf. Then while the sauce is simmering cook your beef in a skillet and saute in a separate skillet the following. Add both the meat and veggies to the sauce as stated above.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8659029DE558E747959B928BA3028D76', X'A092C2FBB1D15745A2207CD817FB2527', X'A092C2FBB1D15745A2207CD817FB2527', 2, NULL, NULL, 2, NULL, 0, '- 1 onion chopped');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26C817C6DB1CD74EB42F604D5290084D', X'A092C2FBB1D15745A2207CD817FB2527', X'A092C2FBB1D15745A2207CD817FB2527', 3, NULL, NULL, 2, NULL, 0, '- 1 red bell pepper chopped');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23A1057589FF6B4F9F11A9A497B014C6', X'A092C2FBB1D15745A2207CD817FB2527', X'A092C2FBB1D15745A2207CD817FB2527', 4, NULL, NULL, 2, NULL, 0, '- 2 cloves of garlic');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9BED2F3C2F088429F0F5EDF963C8B72', X'6EDE6F457A0E6642BAB5AA4939715310', X'6EDE6F457A0E6642BAB5AA4939715310', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter in a heavy cast iron skillet. Saute the onions, bell peppers and garlic until soft and tender. Remove from the pan and pat to take away some of the excess moisture. Combine the meat with the veggies and remaining ingredients, leaving the eggs and buttermilk until last. Mix well and either form into a mound on a foiled lined baking sheet or place into a mini bar pan or a 12 count miniature loaf pan. Slather with half of the glaze and bake at 350 for 15-20 minutes. Remove and spread remaining glaze on top- bake for another 15 minutes until slightly crispy on top.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'713E2DC85392D74E8C53ECC27BE42D7A', X'6EDE6F457A0E6642BAB5AA4939715310', X'6EDE6F457A0E6642BAB5AA4939715310', 1, NULL, NULL, 2, NULL, 0, 'For the Glaze:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0085716FBF6F949852884BC4E9BA5D3', X'6EDE6F457A0E6642BAB5AA4939715310', X'6EDE6F457A0E6642BAB5AA4939715310', 2, NULL, NULL, 2, NULL, 0, 'Stir together all ingredients in a medium sauce pan and bring to a boil. Reduce heat and allow to simmer for 10-15 minutes until reduced by half and thick.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB6293FECFC5174CBAB91C96C96FE073', X'09835CEEDA0D7F45AC51A0AC21828F5C', X'09835CEEDA0D7F45AC51A0AC21828F5C', 0, NULL, NULL, 2, NULL, 0, 'Rinse and pat dry the beef. Place the flour, 1 tsp salt and pepper each and the cajun seasoning in a large bowl. Dredge the corn beef in the flour mixture making sure to coat the meat evenly and on all sides. In a large cast iron skillet, heat 2 teaspoons canola oil over medium-high heat. Add the beef to the pan and cook until browned on all sides, 2-4 minutes. Transfer to a slow cooker.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BFD9AD6A787984594B5DB7EE8369097', X'09835CEEDA0D7F45AC51A0AC21828F5C', X'09835CEEDA0D7F45AC51A0AC21828F5C', 1, NULL, NULL, 2, NULL, 0, 'Add the remaining ingredients including the water and beer. Cover and cook for 8 hours on low in your slow cooker. Serve topped with my spicy twist on Sauerkraut (aka coleslaw-recipe below), swiss cheese and rye bread (or GF bread). Don''t forget the slather of Henry Weinhard''s Pub Beer Mustard and a drizzle of Roasted Walnut Oil! Its a must!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31A5EBA73D7B3244938EC06A41CEA818', X'09835CEEDA0D7F45AC51A0AC21828F5C', X'09835CEEDA0D7F45AC51A0AC21828F5C', 2, NULL, NULL, 2, NULL, 0, 'Sauerkraut Ingredients:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'813593D9B9F4B04AA8DD4F195935E99E', X'09835CEEDA0D7F45AC51A0AC21828F5C', X'09835CEEDA0D7F45AC51A0AC21828F5C', 3, NULL, NULL, 2, NULL, 0, '1 lime, juiced');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C18922E887C7AB43B204A36A8B29DC6B', X'09835CEEDA0D7F45AC51A0AC21828F5C', X'09835CEEDA0D7F45AC51A0AC21828F5C', 4, NULL, NULL, 2, NULL, 0, '3 tablespoons rice vinegar');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EE6962080AA314E8E0D6508D1D3FBBD', X'09835CEEDA0D7F45AC51A0AC21828F5C', X'09835CEEDA0D7F45AC51A0AC21828F5C', 5, NULL, NULL, 2, NULL, 0, '3 tablespoon horseradish mayo');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EE2393A2A494848B847AE997ABD1403', X'09835CEEDA0D7F45AC51A0AC21828F5C', X'09835CEEDA0D7F45AC51A0AC21828F5C', 6, NULL, NULL, 2, NULL, 0, '1 tsp fish sauce');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFF17C4D0BCF3644B6FDAF79F7FD913F', X'09835CEEDA0D7F45AC51A0AC21828F5C', X'09835CEEDA0D7F45AC51A0AC21828F5C', 7, NULL, NULL, 2, NULL, 0, '1 bag of shredded coleslaw mix');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF231831B3516440A6C9ECEFB8D72269', X'09835CEEDA0D7F45AC51A0AC21828F5C', X'09835CEEDA0D7F45AC51A0AC21828F5C', 8, NULL, NULL, 2, NULL, 0, '1 tsp spicy dijon mustard');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFA29B343C06C54CBA79A51BA47259E6', X'09835CEEDA0D7F45AC51A0AC21828F5C', X'09835CEEDA0D7F45AC51A0AC21828F5C', 9, NULL, NULL, 2, NULL, 0, 'Salt and freshly ground black pepper to taste');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3D099C04CEA524CB2DA9748D7E70F7A', X'256EBF3BAEBCD34BBF83FC10C97AA8C6', X'256EBF3BAEBCD34BBF83FC10C97AA8C6', 0, NULL, NULL, 2, NULL, 0, 'Heat the canola oil over medium to high heat in your dutch oven. Mix together the flour with the garlic powder, paprika and 1 tsp each of salt and pepper. Dredge your ribs in the flour and place in the hot oil. Brown ribs on all sides, about 45 seconds per side. Remove ribs and set aside. Turn heat down to medium-low. Toss in the onions, garlic and mushrooms. Cook for 6-8 minutes then add in the wine and balsamic vinegar, stirring the bottom of the pan to release any bits from browning the meat. Stir in the remaining seasonings including cinnamon and brown sugar. Add back the ribs to the dutch oven. Bake at 350 for 2 hours then lower temperature to 300 for another 30-45 minutes (got that tip from Pioneer Woman herself)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13D7D2BCC7440A459E32C65BB3D7AFD5', X'A2E5BA20067352428084EA0A63C96848', X'A2E5BA20067352428084EA0A63C96848', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the water with the red pepper and 1½ teaspoons of salt and stir until the salt dissolves. Add the pork chops and let brine at room temperature for 1 hour.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AA20B3D6A4E3F4EAFB5EF9E0E5BFF48', X'A2E5BA20067352428084EA0A63C96848', X'A2E5BA20067352428084EA0A63C96848', 1, NULL, NULL, 2, NULL, 0, 'Light a grill and create a cool zone. (For a charcoal grill, rake the coals to one side; for a gas grill, leave one side unlit.) In a bowl, mix the cocoa, sugar and ancho powder with 1 tablespoon of salt.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C94F805901A4A4DB0250E85FCA2F89C', X'A2E5BA20067352428084EA0A63C96848', X'A2E5BA20067352428084EA0A63C96848', 2, NULL, NULL, 2, NULL, 0, 'Drain the pork chops and pat dry, removing any bits of crushed pepper. Brush generously with olive oil. Roll the pork chops in the cocoa rub and pat to help it adhere.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16E874A3AC708B458551044693FE5124', X'A2E5BA20067352428084EA0A63C96848', X'A2E5BA20067352428084EA0A63C96848', 3, NULL, NULL, 2, NULL, 0, 'Grill over moderately high heat for 4 minutes, turning the chops once or twice until lightly browned. Transfer the chops to the cool zone, cover and grill for about 15 minutes, until an instant-read thermometer inserted in the center of the chops registers 135° for medium meat. Let the chops rest for 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'451193C51E6D1F4CA0318B1B665A6E1C', X'084819223AE5884F92ADD6B920CD5A24', X'084819223AE5884F92ADD6B920CD5A24', 0, NULL, NULL, 2, NULL, 0, 'Mix together the first 6 ingredients and refrigerate for 2 hours. Mix together the mustard and horseradish and spread on the bread. Butter the outside of the bread and toast on low heat until slightly crispy. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FCF0E57FF2B8D478DA0659DE644103B', X'084819223AE5884F92ADD6B920CD5A24', X'084819223AE5884F92ADD6B920CD5A24', 1, NULL, NULL, 2, NULL, 0, 'Cut the bread into 2 layers horizontally. Layer half of the provolone on the bottom half of bread. Then the mozzarella and swiss cheese if you choose. Then the Mortadella, then the Capicola, salami, and the remainder of provolone. Top this with the olive salad mixture we did at the beginning. Put the top half of the bread on and press it down. Wrap in foil tightly and place on a baking sheet. Sometimes I even place a brick wrapped in foil on top to really get those flavors soaked into all the parts of the bread. Bake at 170 for 3-4 hours or 250 for 1-2 hours.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D5DFFFEC004E44C896AA10AE2B98F13', X'D5C1D113F9138341B9EEDE79622A8764', X'D5C1D113F9138341B9EEDE79622A8764', 0, NULL, NULL, 2, NULL, 0, 'Preheat a large skillet over medium high heat. Add oil to pan with beef, prosciutto and onions. Season meat with salt and pepper. Brown and crumble meat for 3 or 4 minutes. Add chopped carrot and peas. Cook veggies with meat 5 minutes, stirring frequently. In a second small skillet over medium heat cook butter and flour together 2 minutes. Whisk in broth and Worcestershire sauce. Thicken gravy 1 minute. Add gravy to meat and vegetables.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8085FF50D583D04EAC6AB9C911052F65', X'D5C1D113F9138341B9EEDE79622A8764', X'D5C1D113F9138341B9EEDE79622A8764', 1, NULL, NULL, 2, NULL, 0, 'Place the meat mixture in the bottom of a greased 9x13 casserole pan. Toss the sliced potatoes with salt, pepper and a dash of olive oil. Arrange evenly on top of the meat mixture in 3 different layers. Sprinkle with the shredded cheese. Bake at 350 for 15 minutes then increase temperature to 400 and bake another 15 minutes until the potatoes are nice and golden, crispy brown.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59A1E59C929A1845805A8CB7D00194A5', X'AE57C6293EAE1B49A349A2E68AAD36C7', X'AE57C6293EAE1B49A349A2E68AAD36C7', 0, NULL, NULL, 2, NULL, 0, 'Season the chicken tenders with 1 tsp salt and pepper each, paprika, sugar and oregano. Pan-sear the chicken tenders in 1 TBSP of olive oil in a large dutch oven or pot over medium to high heat until cooked. Remove the chicken and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'134E96F24E6D6942B80C7AAD761D04DA', X'AE57C6293EAE1B49A349A2E68AAD36C7', X'AE57C6293EAE1B49A349A2E68AAD36C7', 1, NULL, NULL, 2, NULL, 0, 'Saute the onions, bell peppers and garlic in 2 TBSP of olive oil until slightly tender (they will continue to cook throughout the process). Add in the green chiles. Mix in the flour and stir until your veggies and flour are all incorporated (note we are not doing the traditional roux here). Pour in the warm chicken stock and whisk continually until there are no lumps. Season with the remaining salt, pepper and cumin.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D551E115737FC44BCA07ADE5E8A18DB', X'AE57C6293EAE1B49A349A2E68AAD36C7', X'AE57C6293EAE1B49A349A2E68AAD36C7', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil add in the frozen corn and beans.Simmer for 20-30 minutes. Before serving stir in the cilantro and top each individual bowl with shredded cheese.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C24FF2858DE807418C2893D9AC3D4263', X'8800AC3EADCA9640A09E829DC5E7F010', X'8800AC3EADCA9640A09E829DC5E7F010', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter in a heavy cast iron skillet. Saute the onions, bell peppers and garlic until soft and tender. Remove from the pan and pat to take away some of the excess moisture. Combine the meat with the veggies and remaining ingredients, leaving the eggs and buttermilk until last. Mix well and either form into a mound on a foiled lined baking sheet or place into a foiled line loaf pan. Slather with half of the glaze and bake at 350 for 15-20 minutes. Remove and spread remaining glaze on top- bake for another 15-20 minutes until slightly crispy on top.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5A5C2F29CE4AA40A8FE60C0AE9F5345', X'8800AC3EADCA9640A09E829DC5E7F010', X'8800AC3EADCA9640A09E829DC5E7F010', 1, NULL, NULL, 2, NULL, 0, 'For the Glaze:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DB6E856AF650140920172D93982D1B4', X'8800AC3EADCA9640A09E829DC5E7F010', X'8800AC3EADCA9640A09E829DC5E7F010', 2, NULL, NULL, 2, NULL, 0, 'In a saucepan, melt the butter and add in the brown sugar, ketchup and mustard. Stir to combine and bring to a boil. Reduce heat and add in the remaining ingredients. Allow to simmer for 10-15 minutes until reduced by half and thick.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A381DB33ABA49E48A55753D53AB70406', X'11F20EA7D2D54647B3BA77F9E8084560', X'11F20EA7D2D54647B3BA77F9E8084560', 0, NULL, NULL, 2, NULL, 0, 'Directions:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E92C6D2A231484A9E80E018C4DE7A6D', X'11F20EA7D2D54647B3BA77F9E8084560', X'11F20EA7D2D54647B3BA77F9E8084560', 1, NULL, NULL, 2, NULL, 0, 'Gently combine the crab meat, celery and remaining 11 ingredients in a large bowl. Gently stir in the eggs until thoroughly combined and then add in the bread crumbs. Shape into 6-8 crab cakes and place on a baking sheet. Cover and refrigerate the cakes for at least 1 hour up to 4 hours.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC21CB5D55B75B4B907643418EB62D04', X'11F20EA7D2D54647B3BA77F9E8084560', X'11F20EA7D2D54647B3BA77F9E8084560', 2, NULL, NULL, 2, NULL, 0, 'Heat a large cast iron skillet to medium heat with 2-3 TBSP of canola oil. Once the oil is hot, place the cakes in the pan and cook 3-3 minutes per side until slightly crispy and golden brown.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC280F7D37181B4AAF1E9543BBD90A42', X'11F20EA7D2D54647B3BA77F9E8084560', X'11F20EA7D2D54647B3BA77F9E8084560', 3, NULL, NULL, 2, NULL, 0, 'Stir together ingredients for remoulade and serve alongside the crab cakes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC07704235211E41ACA90714A5066AA5', X'6960BF84A2101542952A01901334E587', X'6960BF84A2101542952A01901334E587', 0, NULL, NULL, 2, NULL, 0, 'Season the Shrimp with 1 tsp each of salt and pepper and place in a large roasting pan. Preheat oven to 350.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8E6C637267DF3468A6ED510F721E918', X'6960BF84A2101542952A01901334E587', X'6960BF84A2101542952A01901334E587', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, sauté the onion, celery, bell pepper, garlic, shallots and parsley in the 2 tsp of olive oil over medium to high heat in a large saucepan for 5-8 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0606EA58EC0B52408BDD631DDC841581', X'6960BF84A2101542952A01901334E587', X'6960BF84A2101542952A01901334E587', 2, NULL, NULL, 2, NULL, 0, 'Add in the butter and once melted, stir in the remaining ingredients including the remaking 2 tsp of salt and pepper. Leave the beer for last as you will want to slowly pour the beer into the pan stirring as you pour. Bring to a simmer for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96D6B85A18D3B64CAE271F8590309579', X'6960BF84A2101542952A01901334E587', X'6960BF84A2101542952A01901334E587', 3, NULL, NULL, 2, NULL, 0, 'Pour the sauce over the shrimp making sure Ll shrimp are coated in sauce. It’s okay if they are fully emerged!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB7AD6466CA6B94C9CEBE9ECC2B55C2E', X'6960BF84A2101542952A01901334E587', X'6960BF84A2101542952A01901334E587', 4, NULL, NULL, 2, NULL, 0, 'Bake at 350 for 15-20 minutes until the shrimp are done (baking time may vary depending on the shrimp size) Enjoy!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'298B6DAD20C8614D981BAF4AF2E4EEB1', X'173FCDBD7F589941B0A79F6D52496ADB', X'173FCDBD7F589941B0A79F6D52496ADB', 0, NULL, NULL, 2, NULL, 0, 'For the Pie Crust:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65F1C70D62C1084F94C2E14EEA3CC202', X'173FCDBD7F589941B0A79F6D52496ADB', X'173FCDBD7F589941B0A79F6D52496ADB', 1, NULL, NULL, 2, NULL, 0, 'Make sure your butter is COLD COLD COLD and diced! Combine your flour and salt in food processor. Pulse. Add the diced cold butter and pulse until it resembles coarse crumbs. Add 3 tablespoons of ice water and pulse until the dough forms a ball. Turn out the dough onto a lightly floured surface for rolling. Roll the dough to ¼ inch thickness and a few inches in diameter larger than your pie plate. Place the dough in pie plate and press to fit. Tuck under any overhang. Fill with pie weights and or dried beans and bake at 350 for 10 minutes. Set aside');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'391A7BBDB8620E48833B6B54FC57F5AF', X'173FCDBD7F589941B0A79F6D52496ADB', X'173FCDBD7F589941B0A79F6D52496ADB', 2, NULL, NULL, 2, NULL, 0, 'For the Filling:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9382848DE64FAC40A2EBB2E4D3735AF6', X'173FCDBD7F589941B0A79F6D52496ADB', X'173FCDBD7F589941B0A79F6D52496ADB', 3, NULL, NULL, 2, NULL, 0, 'Make the roux by melting the butter in a medium cast iron skillet over medium to low heat. Add in the flour and whisk until it is incorporated. Cook, stirring frequently with a wooden spoon, until the roux is lightly browned, about 10 minutes. Then add the garlic, onions, celery and bell pepper and cook for a few minutes more until the roux becomes golden brown. Add in the seasonings, herbs, Worcestershire sauce and hot sauce. Stir to combine and allow to cook for another 10 minutes. Toss in the crawfish meat, stir and then spoon the filling into the prebaked pie shell. Bake at 350 covered for 30-40 minutes until the top is golden brown and set. Allow to cool before trying to slice and serve.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'117BCCBBE3749F458107A2D1BBE591A7', X'21D416499E873B498EA958ACDF5227B1', X'21D416499E873B498EA958ACDF5227B1', 0, NULL, NULL, 2, NULL, 0, 'Toss the shrimp in ¼ cup of the garlic oil and ½ tsp of salt. Set aside');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3131909215EB64E80F3625DD0A56C50', X'21D416499E873B498EA958ACDF5227B1', X'21D416499E873B498EA958ACDF5227B1', 1, NULL, NULL, 2, NULL, 0, 'Boil your pasta according to the package directions. Saute the onion and garlic in 2 TBSP of olive oil until tender, approximately 5 minutes. Toss in the shrimp and cook for 5-7 minutes until pink. Add in the crushed red pepper flakes, pepper and salt. Toss the pasta with the shrimp and then toss it all again with the remaining garlic oil. Sprinkle with chopped parsley or basil.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12BD230FAF577547B3B920143684A236', X'E5D5A40AF222FA448A9FFB24B8E53119', X'E5D5A40AF222FA448A9FFB24B8E53119', 0, NULL, NULL, 2, NULL, 0, 'Heat oil to 375. Whisk together the milk, buttermilk and sriracha sauce. Place shrimp in the milk mixture and allow to sit in the refrigerator, covered, for at least 3 hours. Meanwhile, mix together the remaining ingredients. Now, dredge your shrimp in the dry mixture, shaking off excess and place in the oil. Fry for 2-3 minutes, until golden brown. Watch for the bubbles and when they float to the top but still bubbling then that means they are ready!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6A0E60D69D91C40A670D1FA94FE0D79', X'E5D5A40AF222FA448A9FFB24B8E53119', X'E5D5A40AF222FA448A9FFB24B8E53119', 1, NULL, NULL, 2, NULL, 0, 'For the Aioli:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F098DC59224BB4CA783578ED528558E', X'E5D5A40AF222FA448A9FFB24B8E53119', X'E5D5A40AF222FA448A9FFB24B8E53119', 2, NULL, NULL, 2, NULL, 0, 'Stir all aioli ingredients together except for the olive oils. Combine both olive oils and slowly whisk the mixture together using just a spoonful of olive oil at a time. This sounds time consuming but it really isn''t. The whole process shouldn''t take more than 5 minutes. Just go slow and whisk, whisk, whisk! Stored in a tightly covered container in the refrigerator, this keeps for about 2 weeks.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2715D97514B8A146AA1EDBB5014CEEAB', X'330C8582B43BE34FB314DE5FFAC6F73D', X'330C8582B43BE34FB314DE5FFAC6F73D', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400° and grease a large baking dish with olive oil. In a large bowl, mix all of the ingredients, leaving the eggs and herbs for last. Form the mixture into twenty four 1-inch meatballs or larger and smaller depending on your preference. Place the meatballs in the baking dish, cover with foil and roast for 25 minutes. Remove the foil and roast for another 5-7 minutes until slightly golden brown.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8A95761477D884AA98387A1619C8F7C', X'330C8582B43BE34FB314DE5FFAC6F73D', X'330C8582B43BE34FB314DE5FFAC6F73D', 1, NULL, NULL, 2, NULL, 0, 'For the Sauce:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFC669A020250C4D9B511C6FC448411F', X'330C8582B43BE34FB314DE5FFAC6F73D', X'330C8582B43BE34FB314DE5FFAC6F73D', 2, NULL, NULL, 2, NULL, 0, 'Whisk together all ingredients in a small bowl. Serve at room temperature.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14E084DE91C9F94DB3C04C4E554CD0E4', X'E5D891F5BC726049A0D95875FE294555', X'E5D891F5BC726049A0D95875FE294555', 0, NULL, NULL, 2, NULL, 0, 'Stir together the sugar, salt, pepper, paprika and mustard. Pat the fillets with olive oil and then generously rub down the salmon fillets with the sugar and salt mixture.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F20F767B901A804C9FE32C8090504A5D', X'E5D891F5BC726049A0D95875FE294555', X'E5D891F5BC726049A0D95875FE294555', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450°. Place salmon in a large baking dish skin side down and arrange the lemon slices and green onions on the salmon and around it. Bake the salmon for 20 minutes or so depending on the size and thickness of the salmon you are using.Serve with a nice salad or roasted potatoes');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93B75AB2BACCB844BCED1D218550762F', X'98DFF8997D4A904C8491CD23614F97D9', X'98DFF8997D4A904C8491CD23614F97D9', 0, NULL, NULL, 2, NULL, 0, 'In a heavy-bottomed pot or cast iron dutch oven, melt the butter and cook over low heat until it foams. Add the flour and cook, stirring until the roux reaches a nice golden brown color. At this point season your roux with the seasonings (cajun, salt, pepper, thyme). Next add in the onions, celery, green bell pepper and minced garlic. Cook in the roux for 5 minutes, or until they vegetables start to soften. Whisk in the stock, diced tomatoes, lemon juice, hot sauce, bay leaf, and Worcestershire sauce; turn the heat up to high and bring to a simmer.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55C5F544D3B3754C9FB0462A2B7F3EC1', X'98DFF8997D4A904C8491CD23614F97D9', X'98DFF8997D4A904C8491CD23614F97D9', 1, NULL, NULL, 2, NULL, 0, 'Reduce heat to low and simmer uncovered for 25 minutes. Stir in the crawfish and green onions and simmer for 5 minutes. Serve with rice.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'203EA58686C07A449A4BCFD35C359091', X'B3014BAD14AD1B4D862884839DBB76AC', X'B3014BAD14AD1B4D862884839DBB76AC', 0, NULL, NULL, 2, NULL, 0, 'In a large skillet or Dutch oven heat the oil and butter. Saute the onion and garlic for 5-6 minutes. Add in the flour and stir til combined. Slowly incorporate the chicken stock until the mixture is smooth and there are no lumps. Season with salt, pepper, cajun seasoning and herbs. Bring to a boil and add in the shrimp and green onions. Simmer for 3-4 minutes until the shrimp are pink and firm.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E118CFBC4A11AA46BE6F19DE3C22BB20', X'B3014BAD14AD1B4D862884839DBB76AC', X'B3014BAD14AD1B4D862884839DBB76AC', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, to make the grits. Combine all ingredients in a medium sauce pan over medium to low heat. Bring to a boil then simmer for 10-15 minutes until the grits are smooth but still thick. We want them still thick in order to make the muffins. Grease a standard 12 cup muffin tin or a large 6 cup muffin tin and pour the grits into each cup ¾ full. Bake at 350 for 20-30 minutes until nice and golden brown. Allow to cool completely before removing them from the pan. I typically slide a knife around the grits and then invert them onto a plate. Serve with the shrimp mixture and enjoy');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1B9C2924DD04E459922A3BA8373866C', X'85523EFD1FAD5A4BB60E06FCA519DB17', X'85523EFD1FAD5A4BB60E06FCA519DB17', 0, NULL, NULL, 2, NULL, 0, 'First mix together your cajun seasoning marinade in a large ziploc bag or large bowl. Toss your shrimp (unpeeled) in the marinade and allow to sit room temperature while you prepare the rest of the recipe.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89D9219B8BF4B14F8054B8317FFAC8D0', X'85523EFD1FAD5A4BB60E06FCA519DB17', X'85523EFD1FAD5A4BB60E06FCA519DB17', 1, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil and add a dash of crab boil. (be careful to add just a dash) Once at a boil add in your corn and cook for 6-8 minutes until nice and tender. Remove and allow to cool (dot with butter-optional)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C351D7268617D245A22DE9476AC9A7F8', X'85523EFD1FAD5A4BB60E06FCA519DB17', X'85523EFD1FAD5A4BB60E06FCA519DB17', 2, NULL, NULL, 2, NULL, 0, 'Place potatoes on a roasting or baking pan with a drizzle of olive oil and dash of salt. Roast at 400 for 15-20 minutes until nice and slightly brown on the edges (this is personal preference however as we love our potatoes really charred). With 10 minutes left for the potatoes remove and place the shrimp and sliced sausage onto the pan- return the pan to the oven and allow to finish cooking for last 10 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F03BCE6E39DE34BB67B0DE1148DBD09', X'85523EFD1FAD5A4BB60E06FCA519DB17', X'85523EFD1FAD5A4BB60E06FCA519DB17', 3, NULL, NULL, 2, NULL, 0, 'Now everything is ready to placed on the skewer. Slice each corn on the cob into 2 or 3 portions depending on how big you want them. Layer the skewer with the corn, shrimp, potato and finally sausage. Repeat:)) and Enjoy:)))');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03005192CE04CA42B69146CC3271E979', X'89D8C68BF1BBE14D983DF33F4AD40243', X'89D8C68BF1BBE14D983DF33F4AD40243', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees. To a saucepan on medium heat, add butter and olive oil. Add garlic and sauté until lightly browned, 2-3 minutes. Remove pan from heat and add wine, oregano and salt and pepper, to taste. Return pan to heat and sauté contents for another 1-2 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2335B0567A2F743A654D9071A67DBD5', X'89D8C68BF1BBE14D983DF33F4AD40243', X'89D8C68BF1BBE14D983DF33F4AD40243', 1, NULL, NULL, 2, NULL, 0, 'Place cleaned crabs on a baking sheet, belly-up. Spoon contents of garlic and oil mixture evenly onto each crab. Place baking sheet in oven and bake until meat becomes bright white and claws turn red in color, about 20 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6ACE8CDEE3033B4BA2012FC101A36B83', X'6CFE5F7DBBAF0F4E92453F932F36C27F', X'6CFE5F7DBBAF0F4E92453F932F36C27F', 0, NULL, NULL, 2, NULL, 0, 'Mash together your potatoes and fish. Add in the seasonings then the egg and panko. Mix well. Form into small patties, approximately 2-3 inches. Cover and refrigerate for 30 minutes or all day. I like to do this in the morning so I can can breathe as dinner approaches knowing I already have it all together.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97C6D46904B768458A13D52C01B4770A', X'6CFE5F7DBBAF0F4E92453F932F36C27F', X'6CFE5F7DBBAF0F4E92453F932F36C27F', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, add ¼ cup oil. Once the oil is hot ( you can drop a small crumb from your patties to see if the oil sizzles, if so then your ready) and pan-fry the patties 2-3 minutes on each side until they reach a golden brown. You may have to do this in batches of 3-4 patties as you don''t want to overcrowd the pan.Serve hot and enjoy!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ECE61F8FED1994BAF7984ECB860FC0C', X'6CFE5F7DBBAF0F4E92453F932F36C27F', X'6CFE5F7DBBAF0F4E92453F932F36C27F', 2, NULL, NULL, 2, NULL, 0, 'For the potatoes:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A006F0CBF62B40488500FEDD0BBD1088', X'6CFE5F7DBBAF0F4E92453F932F36C27F', X'6CFE5F7DBBAF0F4E92453F932F36C27F', 3, NULL, NULL, 2, NULL, 0, 'Boil potatoes in a large stockpot until you can insert a fork easily into the center of a potato. Drain, toss in the butter, cheese, seasoning, sour cream, green onions and milk. Mash until everything is combined.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5A22F481D2CA3418CE9F678E09971D5', X'F907D3BB36E6EB4083FC503ABAE7CB77', X'F907D3BB36E6EB4083FC503ABAE7CB77', 0, NULL, NULL, 2, NULL, 0, 'Season you shrimp generously with salt and pepper and spread out evenly in a skillet, casserole dish or your choice of pan (as long as it has at least 2 inches depth). Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'728CC3DF0866BF44AFAA047A58A5ADA8', X'F907D3BB36E6EB4083FC503ABAE7CB77', X'F907D3BB36E6EB4083FC503ABAE7CB77', 1, NULL, NULL, 2, NULL, 0, 'Melt butter in a medium saucepan and add in the onions and garlic and saute til tender (5-6 minutes). Add in the seasonings, hot sauce and herbs. Slowly pour in the lemon juice, beer and Worcestershire and bring to a simmer. Simmer-covered for 10-20 minutes (the longer the better the flavor). Stir in the green onions and pour mixture over shrimp or salmon. Bake in a 350 oven 15 minutes for shrimp and 20-25 minutes for salmon (depending on thickness of the fillets)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'859A278B48B1F74C996882FCA15E9921', X'1C7BA2B56401BA4CAAE72BDB24F2F4C1', X'1C7BA2B56401BA4CAAE72BDB24F2F4C1', 0, NULL, NULL, 2, NULL, 0, 'Saute the shallots, garlic, red pepper flakes, thyme and oregano in the oil over medium to high heat for 3-4 minutes (until slightly toasted). Add in the beer, hot sauce, vinegar and lemon juice. Season with salt, pepper and cajun seasoning. Bring to a boil, add in the cream then reduce to simmer for another 5-7 minutes, until liquid is reduced and slightly thick. Add in the crawfish and cover for 10 minutes (the crawfish will cook during this time).');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'900ABF71B899614F9394521CB0D512BC', X'1C7BA2B56401BA4CAAE72BDB24F2F4C1', X'1C7BA2B56401BA4CAAE72BDB24F2F4C1', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, make your shortcakes.Preheat your oven to 425 degrees. Sift together your dry ingredients (this includes the salt and garlic) then using your hands (who needs a pastry blender!) cut in the chilled butter until your mixtures resembles small peas. Using a fork stir in the buttermilk and mix until just combined(very important to NOT overmix). Turn out the dough onto a lightly floured surface and gently fold then gather about three times until the dough comes together. Now roll the dough out using your rolling pin to approx ½ inch thick. Cut out the biscuits using a biscuit cutter and place them on a large baking sheet as close together as you can get them. Bake until slightly golden brown, about 20 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9066375A2C7A374EA75410202ED6560B', X'1C7BA2B56401BA4CAAE72BDB24F2F4C1', X'1C7BA2B56401BA4CAAE72BDB24F2F4C1', 2, NULL, NULL, 2, NULL, 0, 'Top shortcakes with the crawfish mixture and sauce. Serve and ENJOY a job well done!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6870DDCAF8151748A3670D6653624E02', X'BFFF02DCC8A2D24691C9D5FE7E00D321', X'BFFF02DCC8A2D24691C9D5FE7E00D321', 0, NULL, NULL, 2, NULL, 0, 'Combine all marinade ingredients in a large glass bowl or large Ziploc bag. Place the fish in the marinade, toss to coat each fish evenly and cover. Allow to marinate in the refrigerator for at least 3 hours or longer.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7957CA1E33C9E946AC9FFC7C0D9EFC6D', X'BFFF02DCC8A2D24691C9D5FE7E00D321', X'BFFF02DCC8A2D24691C9D5FE7E00D321', 1, NULL, NULL, 2, NULL, 0, '30 minutes per to baking allow the puff pastry sheets to come to room temperature. As you set the pastry out go ahead and break each sheet into 3 pieces for a total of 6 puff pastry strips. You will notice when you remove the pastry from the box that it is already folded into 3 parts so this should be fairly easy to break apart. Once thawed and ready to bake remove the fish from the refrigerator.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8533F166F4339B4CADD6617A0E7ABDD4', X'BFFF02DCC8A2D24691C9D5FE7E00D321', X'BFFF02DCC8A2D24691C9D5FE7E00D321', 2, NULL, NULL, 2, NULL, 0, 'Pat off excess marinade from each fillet (this is important as if you leave too much marinade on the fish the puff pastry will be soggy) and place in the center of each puff pastry strip. Lay one basil leaf on top of the fish and enclose the puff pastry around it. Sealing it with your fingers at the seam by pinching it together. Mix together the egg wash and almond oil and brush the tops of the puff pastry. Bake at 425 for 15 minutes-20 minutes until the puff pastry is nice and golden brown. Optional: With 5 minutes remaining in the baking time place a nice juicy slice of lemon on top of each puff pastry.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E23AE8D87F0954892B2F702FE22D3AC', X'3F3282E1E738B74A9F3C4346AE34B335', X'3F3282E1E738B74A9F3C4346AE34B335', 0, NULL, NULL, 2, NULL, 0, 'Saute the onions in olive oil until soft. Add the garlic and cook about 30 seconds. Then add the wine. Let it simmer for about 2 or 3 minutes to reduce the wine a bit. Add the tomatoes and seasonings. Let simmer for 20 minutes then add in the stock, beans, artichokes, capers and olives. Simmer for another 10 minutes then place the fish in the stew along with the juice of 1 lemon. Cover with lid and turn hear to low. Allow to cook for 10 more minutes. You do not want to overcook the fish because it will become flaky and tough.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA7D90B7173DD14782C2F035E94AD8F1', X'02CB723D1718E140B7621880676C23FF', X'02CB723D1718E140B7621880676C23FF', 0, NULL, NULL, 2, NULL, 0, 'Mix together the dry ingredients and wet ingredients in separate bowls. Now add the wet to the dry and mix well. Heat your oil to 375 and drop spoonfuls of batter in the hot oil. Fry until golden brown 1-2 minutes. Transfer to paper towels to drain and season with more salt.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E29F6907E3E2840ADF5C8651BC2E3AE', X'E7AD05CC94B7D342AECE463E22AFF66D', X'E7AD05CC94B7D342AECE463E22AFF66D', 0, NULL, NULL, 2, NULL, 0, 'Warm up your tortilla. Toss the chicken with the olive oil, thyme, salt and pepper. Spoon 1 TBSP of the hummus on the center of the tortilla and spread it out. Place a handful of chicken next on the hummus then the watercress and finally a squeeze of lemon juice.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'807134A18ED6F94BB915B4DE9896468F', X'E7AD05CC94B7D342AECE463E22AFF66D', X'E7AD05CC94B7D342AECE463E22AFF66D', 1, NULL, NULL, 2, NULL, 0, 'Lift the two sides of the tortilla to center the food in the middle. Then, lift up the side closest to you tuck the top flap of the tortilla over and up underneath the filling. Starting from your body and moving outward, roll the burrito forward in 2 or 3 rolls leaving one roll left. With the last roll tuck over the left and right sides of the tortilla towards the center and then give it one last roll, using both hands to keep the left and right sides of the tortillas folded over until the roll is completed. Brush with olive oil (or in this case a mixture of 1 TBSP olive oil and 1 tsp lemon juice) and brush over the chimichanga. It is now ready to be baked. Bake at 325 for 20 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0966E18EB7503F458D6B1E4CBB3F0448', X'242098179E19304D8D145B42DD9853E0', X'242098179E19304D8D145B42DD9853E0', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large dutch oven. Toss in the onions and garlic. Saute on medium-low for 4-5 minutes. Add in the carrots, celery and mushrooms. Stir to combine and allow to cook for another 5 minutes. Now add in all of the seasonings, including the herbs. Stir to combine. Pour in the chicken broth and noodles and cook covered on medium-low until the noodles are nice and tender. Add in the cheese, coconut milk, heavy cream and chicken. Cover and cook for an additional 10 minutes then serve and enjoy.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'968FD10019F8A242A4CB77C33416564B', X'ECBCEC3CDB356448A08F31A8FC7CD033', X'ECBCEC3CDB356448A08F31A8FC7CD033', 0, NULL, NULL, 2, NULL, 0, 'Rinse cauliflower, separate into florets. Place cauliflower in a food processor and process until very fine. You should have 2 cups of finely grated cauliflower. Transfer to a bowl and cover with plastic wrap. Microwave for 6 minutes. Transfer the cauliflower once it has cooled to a cheesecloth or sheer tea towel and squeeze all of the the liquid out, You don''t want any liquid left in your cauliflower because it will make the crust crispy (hence break apart) instead of chewy (we want chewy)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDDB5CEFF293774AA7B68A659B2FE9C9', X'ECBCEC3CDB356448A08F31A8FC7CD033', X'ECBCEC3CDB356448A08F31A8FC7CD033', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450. Once the liquid is removed from the cauliflower place the cauliflower in a large bowl and add in the remaining ingredients- leaving the egg for last. Give your arm a workout and start stirring, really working the batter with your hands until it forms a nice, slightly sticky dough. Spread the dough out onto a lightly greased pizza stone or parchment lined baking sheet. Bake for 10 minutes until it starts to turn a golden brown. Remove from the oven and top with your favorite toppings. Return to the oven and bake until the toppings are warm and the cheese is melted.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9679692E20CB394DA2DD8B41D4D69624', X'ECBCEC3CDB356448A08F31A8FC7CD033', X'ECBCEC3CDB356448A08F31A8FC7CD033', 2, NULL, NULL, 2, NULL, 0, 'NOTE: Because this pizza crust does not take a long time to bake in the oven the toppings you decide to use will need to already be cooked. So for example I used grilled chicken, peppers and onions on mine. I sweated the onions and peppers before hand and roasted the chicken before cutting it up. Once the crust was ready I topped it with my toppings and placed it in the oven just long enough to melt the cheese and heat the chicken and other toppings all the way through.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D90996FB08C3C45BB322D97C49CB63B', X'B23FE72EA74A4B42BFE2FEB02CEB0911', X'B23FE72EA74A4B42BFE2FEB02CEB0911', 0, NULL, NULL, 2, NULL, 0, 'For the Chicken');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21A0AF49F7DD93468E6CD3D3AFB6EB90', X'B23FE72EA74A4B42BFE2FEB02CEB0911', X'B23FE72EA74A4B42BFE2FEB02CEB0911', 1, NULL, NULL, 2, NULL, 0, 'Place the trimmed chicken breast in the salsa, push to bottom and cover with some of the salsa. Pour in the rest of the ingredients and mix the chicken around a little to get everything nice and coated. Start the slow cooker on low for six to eight hours or on high for four hours.. Within the last hour of cooking, remove the chicken to a chopping board and shred with two forks and place back in crock pot for the last hour.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59A4BF32E0A62040BD1917B99021F717', X'B23FE72EA74A4B42BFE2FEB02CEB0911', X'B23FE72EA74A4B42BFE2FEB02CEB0911', 2, NULL, NULL, 2, NULL, 0, 'For the Dressing:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88EB8FA05B758447BE906131E7D0A04D', X'B23FE72EA74A4B42BFE2FEB02CEB0911', X'B23FE72EA74A4B42BFE2FEB02CEB0911', 3, NULL, NULL, 2, NULL, 0, 'Place all ingredients in a food processor or blender and pulse until combined (1 minute). Chill until ready to use!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0956A8CD1626A46B4D78A7142478724', X'CACE3A7B2DC9E242ADF0CCAF97A73B6F', X'CACE3A7B2DC9E242ADF0CCAF97A73B6F', 0, NULL, NULL, 2, NULL, 0, 'Place all the ingredients in a blender or food processor and pulse to combine. Place 1 Tbsp of butter in under the skin of each chicken and then rub down each chicken with the wet jerk rub. Cover with plastic wrap and allow to refrigerate for 4 hours or more.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2426AC72EE7FC478C6EF0FE334A9627', X'CACE3A7B2DC9E242ADF0CCAF97A73B6F', X'CACE3A7B2DC9E242ADF0CCAF97A73B6F', 1, NULL, NULL, 2, NULL, 0, 'When its time to cook, heat a large cast-iron skillet or grill pan to high heat and sear each chicken leg until slightly charred on each side. Place the dish of chicken in a preheated 375 degree oven and bake for another 20-30 minutes depending on the size of legs until the juices run clear and the chicken is cooked through.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AD51672E6947B438B153CFFADB00B12', X'677CB02C80317C44A49695D9CC8B75D2', X'677CB02C80317C44A49695D9CC8B75D2', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large pot or Dutch oven over medium heat. Add the veggies and sauté until the vegetables start to turn translucent. About 10 minutes. Add the flour and stir briskly to combine it with the butter and veggies. Cook for one minute. Slowly add the wine, chicken bouillon and chicken broth while stirring constantly. Add in the chicken then the remaining ingredients. Cover and turn heat to low to sit while you prepare the topping.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1C59009AB6B6140ADAA95DBABC30692', X'677CB02C80317C44A49695D9CC8B75D2', X'677CB02C80317C44A49695D9CC8B75D2', 1, NULL, NULL, 2, NULL, 0, 'For the Crust:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48D57704D8614D469696490B299E5C86', X'677CB02C80317C44A49695D9CC8B75D2', X'677CB02C80317C44A49695D9CC8B75D2', 2, NULL, NULL, 2, NULL, 0, 'Pour the flour, salt, and pepper into the bowl of a food processor and pulse a few times to combine. Cut in the cold cream cheese and cold butter with your hands. Pulse for a few times until the mixture begins to form small pea sized balls. Remove the lid and add in 2 tsp of apple cider vinegar. Replace the lid and continuing pulsing until mixture begins to form one large ball.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'008EC67BCCF4524DB35C23A3E32A111D', X'677CB02C80317C44A49695D9CC8B75D2', X'677CB02C80317C44A49695D9CC8B75D2', 3, NULL, NULL, 2, NULL, 0, 'Note: The dough may still be a bit crumbly but as long as you pinch a section in your hand and it comes together fine you are right on track. If not, and still to crumbly add in another tsp or two of cold water.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'226109C5A8194D49BD142F846DABEA57', X'677CB02C80317C44A49695D9CC8B75D2', X'677CB02C80317C44A49695D9CC8B75D2', 4, NULL, NULL, 2, NULL, 0, 'Set the dough on a flat-surface dusted with flour. Use a floured rolling pin roll the dough out into 2 disks both ¼ inch thick. Cut out each disk into desired size depending on the diameter of your baking pan/ramekin, etc. Place the dough on top of your chicken pot pie dish, making sure the dough hangs evenly over the dish. Brush the tops of the crust with egg wash and a sprinkle of sea salt. Bake at 375 for 25-30 minutes or until the top is nice and golden brown.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'775067B8C2B31349BF002088F4FCD36D', X'97D624C8F202E44CAEB48F6E4F732250', X'97D624C8F202E44CAEB48F6E4F732250', 0, NULL, NULL, 2, NULL, 0, 'Cream together the cream cheese with the marshmallow fluff. Add in the yogurt and cool whip. Stir in the vanilla then the cake batter mix and finally the dark chocolate pieces. Store in the refrigerator in a container for up to 5 days.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF485BD382077E4BB1F184AC8BD66823', X'67A57EA04B17714BA210B7FFDF497E55', X'67A57EA04B17714BA210B7FFDF497E55', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees, and grease the pan of your choice. Preferably 8 or 9 inch pan or cupcake pan. Cream together the sugar and butter, then add the eggs one at a time. Add the vanilla and pudding mix along with the zest of the orange. Stir in the flour. Add a ½ cup of orange juice to the cake mixture and mix well.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E1D52AAF7E51B46A226D498E219AF63', X'67A57EA04B17714BA210B7FFDF497E55', X'67A57EA04B17714BA210B7FFDF497E55', 1, NULL, NULL, 2, NULL, 0, 'Pour into the pan, and bake for 45-50 minutes to an hour. It may need more time depending on your oven.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1ED67479E4B9E4F807A4E18071B2482', X'67A57EA04B17714BA210B7FFDF497E55', X'67A57EA04B17714BA210B7FFDF497E55', 2, NULL, NULL, 2, NULL, 0, 'To make the frosting: Beat the confectioners'' sugar and butter. Add in the vanilla and orange juice and milk to achieve desired consistency.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'667D93E2A6D05845890F6FFD0ECA530A', X'97900EA4BC1A434597F4B0FEFF24F302', X'97900EA4BC1A434597F4B0FEFF24F302', 0, NULL, NULL, 2, NULL, 0, 'To Make the dough:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5933FC8F66CB245B6F9682EC798C780', X'97900EA4BC1A434597F4B0FEFF24F302', X'97900EA4BC1A434597F4B0FEFF24F302', 1, NULL, NULL, 2, NULL, 0, 'Cream together the butter and coconut sugar. Add in the flour, baking soda and salt. Stir and mix together. Add in the milk and vanilla extract. Mix until the dough comes together and forms a soft ball. It will be soft but not sticky. Place a spoonful of dough forming it into a small ball and place on a cookie sheet. Take the back of a spoon or spatula and flatten it slightly to form a disk shape. Repeat with the remaining dough. Bake at 350 for 11 minutes or until slightly golden brown.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'577B0672D8413043A41C32B1229B5487', X'97900EA4BC1A434597F4B0FEFF24F302', X'97900EA4BC1A434597F4B0FEFF24F302', 2, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool while you make the filling. Cream together the peanut butter and powdered sugar slowly in a mixer (low speed as to not throw sugar all over the place). Once the peanut butter and sugar start coming together add in the salt and vanilla. Cream together for 1 minute. Place small spoonfuls (about 1 tsp) of the filling onto each cookie round. Allow to cool in the refrigerator for 30 minutes before dipping them in chocolate.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D3C7F0C3A3D994A829F45EF11FCD40A', X'97900EA4BC1A434597F4B0FEFF24F302', X'97900EA4BC1A434597F4B0FEFF24F302', 3, NULL, NULL, 2, NULL, 0, 'Melt the chocolate in a microwavable bowl heating at 30 second intervals and stirring each time between. Remove the pan of cookies from the refrigerator and begin dipping each cookie into the melted chocolate. Place on a cookie sheet lined with wax paper and allow to cool completely before eating by placing them back in the refrigerator for up to an hour. Store in a airtight container in the fridge .');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C5129584E7C6B4C9C7C43C0642A9FED', X'7EE948E2426F3240859E424AEEEE4073', X'7EE948E2426F3240859E424AEEEE4073', 0, NULL, NULL, 2, NULL, 0, 'For the Caramel: Line a baking sheet with parchment or wax paper and set it aside.Place butter in a large microwave safe bowl and melt. Add in the rest of the ingredients except for the vanilla.Return to microwave and cook mixture on HIGH power for 6 minutes until it reaches 240 degrees with a candy thermometer. If it isn''t done after 6 minutes return to the microwave and cook for increments of 30 seconds until it does reach 240 degrees.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'366245FEF1D2384F9F5F3204EF0E1098', X'7EE948E2426F3240859E424AEEEE4073', X'7EE948E2426F3240859E424AEEEE4073', 1, NULL, NULL, 2, NULL, 0, 'Remove bowl from microwave. Add in vanilla extract and mix well. Pour mixture into prepared baking sheet. Place in refrigerator until set, about 20 - 30 minutes. Break up the caramel into as many pieces are you want in your mix ( I chose about ? cup of caramel pieces.)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA3B2835F787EB4C94AABD19D0D01E96', X'7EE948E2426F3240859E424AEEEE4073', X'7EE948E2426F3240859E424AEEEE4073', 2, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a food processor and blend until powdery. To prepare: use 3 TBSP of the hot chocolate mix to 1 cup of boiling milk or water.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E3F9E0EB5F09B4097A0557D08CC6E02', X'CF0D7DD62CC3B64987A4DBE4BCE9DE6C', X'CF0D7DD62CC3B64987A4DBE4BCE9DE6C', 0, NULL, NULL, 2, NULL, 0, 'Mix the peanut butter and biscoff together until well blended. Add in the melted butter and coffee extract-stir to combine. Add the powdered sugar and mix well. Easy so far:) Roll the mixture into 1 inch balls and insert toothpick into each ball. Chill for at least 2-4 hours or overnight is even better. Melt the chocolate chips and paraffin wax in a double broiler ( or you can use butter if you prefer that over wax). Once the chocolate is melted dip the balls into the chocolate and place on waxed baking sheet. Allow to cool completely.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7826C148A1B72E4DAC1CEF0534CFFB99', X'757183B29A2E7048B76A855A36BEE146', X'757183B29A2E7048B76A855A36BEE146', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Combine flours, cocoa, sugar, baking soda, chai mix and salt in the bowl of a food processor and pulse until thoroughly combined. Add butter and pulse, scraping down sides as necessary until pea-sized chunks remain, 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20A57AE1E4BB1B4CA715619338716925', X'757183B29A2E7048B76A855A36BEE146', X'757183B29A2E7048B76A855A36BEE146', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl whisk together honey, milk, and vanilla until thoroughly combined. Stream mixture into the food processor while running; dough should come together in a ball after a minute or two. Divide dough into two balls, flatten into large disks, wrap in plastic wrap and refrigerate until dough is slightly firm, about 30 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32F3331788ED1E46934ED72E05AB8CF2', X'757183B29A2E7048B76A855A36BEE146', X'757183B29A2E7048B76A855A36BEE146', 2, NULL, NULL, 2, NULL, 0, 'On a large surface lightly floured with cocoa powder roll dough out to ?” thick. Using your cutter of choice, cut out rounds or rectangles and transfer to parchment lined baking sheets. Prick cookies with a fork and bake for 8-10 minutes or until dry to the touch. Halfway through baking remove cookies from oven and sprinkle with granulated sugar, if desired. When cookies are done remove from baking sheet and transfer to wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C78726ED3F93F745B32B5D972F15418D', X'8FBB1FC72238284B9B269997CB42E3F5', X'8FBB1FC72238284B9B269997CB42E3F5', 0, NULL, NULL, 2, NULL, 0, 'Prepare a foil lined greased 8x8 or 9x11 pan (depending on the thickness you prefer) for the fudge and set aside. Melt the milk and chocolate in a large saucepan over medium to low heat until the chocolate is all melted. Remove from stove and stir in the pumpkin spice, vanilla and cinnamon extract. Separate the mixture into 3 separate bowls and working quickly. Pour the first `white` mixture into the pan, then color the 2nd bowl of chocolate with yellow food coloring and then pour onto the top of the first white layer. With the 3rd bowl color it with the orange food coloring then pour onto the 2nd yellow layer. Allow 2-4 hours to set. Cut into triangles resembling candy corn with the white layer as the top/point of the rectangle.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'428B7726C9431C4EADBE6EB5AB82B49E', X'E0F6A24D9C0187479C708DA72ADF1CAD', X'E0F6A24D9C0187479C708DA72ADF1CAD', 0, NULL, NULL, 2, NULL, 0, 'Step One: Grate zest from lemons to equal 2 Tbsp. Cut lemons in half; squeeze juice into a measuring cup to equal 1 cup.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC9E07E6DCFF3B4ABA29972551F52519', X'E0F6A24D9C0187479C708DA72ADF1CAD', X'E0F6A24D9C0187479C708DA72ADF1CAD', 1, NULL, NULL, 2, NULL, 0, 'Step Two: Beat butter and sugar at medium speed with an electric mixer until blended. Add eggs, 1 at a time, beating just until blended after each addition. Gradually add lemon juice to butter mixture, beating at low speed just until blended after each addition; stir in zest. (Mixture will look curdled.) Transfer to a 3-qt. microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2745EC3CE374AC4C99B240A31A2F7DB0', X'E0F6A24D9C0187479C708DA72ADF1CAD', X'E0F6A24D9C0187479C708DA72ADF1CAD', 2, NULL, NULL, 2, NULL, 0, 'Step Three: Microwave at HIGH 5 minutes, stirring at 1-minute intervals. Microwave, stirring at 30-second intervals, 1 to 2 more minutes or until mixture thickens, coats the back of a spoon, and starts to mound slightly when stirred.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'118A075A49B09C4D93F3C024F5ACCBCC', X'E0F6A24D9C0187479C708DA72ADF1CAD', X'E0F6A24D9C0187479C708DA72ADF1CAD', 3, NULL, NULL, 2, NULL, 0, 'Step Four: Place heavy-duty plastic wrap directly on warm curd (to prevent a film from forming), and chill 4 hours or until firm. Store in an airtight container in refrigerator up to 2 weeks.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE3AF5EA6B4440459DCE055625D32AEB', X'E0F6A24D9C0187479C708DA72ADF1CAD', X'E0F6A24D9C0187479C708DA72ADF1CAD', 4, NULL, NULL, 2, NULL, 0, 'For the Cake Pops:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B7C809065AF3A428848F319365F4F3F', X'E0F6A24D9C0187479C708DA72ADF1CAD', X'E0F6A24D9C0187479C708DA72ADF1CAD', 5, NULL, NULL, 2, NULL, 0, 'Cut your pound cake into 4 layers about 1 inch thick each. Add a spoonful or two of lemon curd on the top of the bottom layer, add another layer of pound cake to that then lemon curd, repeat until you end up with 4 layers and the top will have no curd. Press gently with your hands on the top and sides of the cake layers to help layers stick together. Transfer to the freezer until nice and firm (30 minutes). Remove the cake and cut it into bite size pieces. I chose to do squares but you could use a circle cutter if you would like. Place the cake pieces back into the freezer while you melt your chocolate.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F57CBA230FC534F93D112B1C27734FC', X'E0F6A24D9C0187479C708DA72ADF1CAD', X'E0F6A24D9C0187479C708DA72ADF1CAD', 6, NULL, NULL, 2, NULL, 0, 'In a medium to large saucepan add an inch of water and bring to a very gentle simmer. Place a heat-safe bowl or saucepan on the top of the large saucepan or pot making sure the pot is not touching the water beneath. Add the dove chocolate pieces to the bowl and stir occasionally with a spatula until smooth and melted.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FC38FA3ECE80C459660A81FABDDE9A5', X'E0F6A24D9C0187479C708DA72ADF1CAD', X'E0F6A24D9C0187479C708DA72ADF1CAD', 7, NULL, NULL, 2, NULL, 0, 'Remove your cake pieces/pops from the freezer. Stick a lollipop stick or in my case a straw into the center of the pop. Dip it in the melted chocolate, tapping off any excess chocolate. You may have to use a spoon to spoon chocolate over and on top of the pops. Place on a baking sheet lined with parchment or wax paper. Allow to cool until chocolate is hardened. ENJOY!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE27CDC99A540D448A59B6F9C2EBD897', X'DC6D7140745B454590E2FBC5C9FDBF45', X'DC6D7140745B454590E2FBC5C9FDBF45', 0, NULL, NULL, 2, NULL, 0, 'Cream together your butter and sugars. Add in the egg and vanilla. Sift together the flours, baking soda, salt and baking powder. Slowly stir in the dry ingredients to the wet and mix well. Add in the chocolate chips and place in plastic wrap. Refrigerate for at least 2 hours. Bake in preheated oven at 350 for 10-12 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4B0447E405B6A42A30B29F6BE418BAB', X'C59E46B1ECAFBC4FBF920227762DA44E', X'C59E46B1ECAFBC4FBF920227762DA44E', 0, NULL, NULL, 2, NULL, 0, 'For the Sherbet:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C7C2A4886D10642AD23180B2AB23B99', X'C59E46B1ECAFBC4FBF920227762DA44E', X'C59E46B1ECAFBC4FBF920227762DA44E', 1, NULL, NULL, 2, NULL, 0, 'Combine all ingredients except for the buttermilk into in a large sauce pan. Heat just until the sugar is dissolved. Remove and allow to cool completely. Stir in the buttermilk and then place into a freezer safe container. Cover and freeze for 1 hour. Stir every hour for 3 hours.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6D616C7EFF9B344854A2754ACD4E4AC', X'C59E46B1ECAFBC4FBF920227762DA44E', X'C59E46B1ECAFBC4FBF920227762DA44E', 2, NULL, NULL, 2, NULL, 0, 'For the Crust:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DA6C8E1E5BC13438C981A5E4699378C', X'C59E46B1ECAFBC4FBF920227762DA44E', X'C59E46B1ECAFBC4FBF920227762DA44E', 3, NULL, NULL, 2, NULL, 0, 'Mix together in a large bowl your graham cracker crumbs, pistachio crumbs, butter and sugar. Press into a parchment lined 8x8 pan. Place in freezer for just 10-15 minutes until very firm. Then gently spread your sherbet onto the crust. Put it back into the freezer for another 30 minutes until firm. Melt your chocolate in a double broiler on medium to low heat stirring until the chocolate is completely melted. Immediately pour over the pineapple sherbet layer-spread to all four corners. Wrap your pan tightly in plastic wrap and return to the freezer for another 30 minutes. Cut and serve cold on a nice hot summer day!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87486C3E71D13B41A154CA18A6B49F0E', X'10D1D6F0017E5A46AB5CADE642947136', X'10D1D6F0017E5A46AB5CADE642947136', 0, NULL, NULL, 2, NULL, 0, 'Beat butter and sugar in bowl of a heavy-duty mixer until creamy. Add eggs, then coconut extract, vanilla extract and last sour cream. Beat until smooth. Scrape down sides of bowl. Add in the coconut flakes and then the flour mixture. Pour into greased 9x13 inch pan or a greased bundt pan. Top with the remaining shredded coconut and mini chocolate chips.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91E3CD2218DC0049AD9067FF999A22E8', X'10D1D6F0017E5A46AB5CADE642947136', X'10D1D6F0017E5A46AB5CADE642947136', 1, NULL, NULL, 2, NULL, 0, 'Bake the cake until the top is golden and a skewer inserted in the center comes out clean, 30-40 minutes. Let cool for about 10-15 minutes. Serve warm!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8233247D3408CA4B97672B2BB185570D', X'233A06F56ABD9342A37E7C47A364F7C1', X'233A06F56ABD9342A37E7C47A364F7C1', 0, NULL, NULL, 2, NULL, 0, 'HOW TO MAKE BROWN BUTTER FOR BAKING:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B82B147FDFE0147AEBDC0FA550B66B8', X'233A06F56ABD9342A37E7C47A364F7C1', X'233A06F56ABD9342A37E7C47A364F7C1', 1, NULL, NULL, 2, NULL, 0, 'Place butter in saucepan on medium-high heat to melt. Swirl or stir the butter with a wooden spoon as it starts to foam. Continue to melt until butter begins to bubble and turn brown. Scrape melted butter into a oblong small pan or rectangular Tupperware and refrigerate for at least 4 hours or overnight. When the brown butter is solid again, slice into ½-inch cubes and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FA8FF4D8BC8EC47BDD87CD17C063F1E', X'233A06F56ABD9342A37E7C47A364F7C1', X'233A06F56ABD9342A37E7C47A364F7C1', 2, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl combine your washed berries with the cornstarch, chopped basil leaves and 1 TBSP maple sugar. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83E6509D4894234CAB76FD6B663A250F', X'233A06F56ABD9342A37E7C47A364F7C1', X'233A06F56ABD9342A37E7C47A364F7C1', 3, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, sift together flour, baking powder, nutmeg, salt, zest and maple sugar. Cut in the cold brown butter (using your fingers or a pastry cutter) until mixture resembles a coarse meal. Work the butter into the dry ingredients until some of the butter flakes are the size of gravel. Stir together the buttermilk and vanilla and then add to the flour mixture, stirring enough to make a soft dough.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02C46179D04FAC40A69527CADBACA6DF', X'233A06F56ABD9342A37E7C47A364F7C1', X'233A06F56ABD9342A37E7C47A364F7C1', 4, NULL, NULL, 2, NULL, 0, 'Lightly grease either 4 miniature cast-iron skillets or one large cast iron pan or baking dish. Pour berry mixture among skillets or into your preferred baking pan. Place large drops/spoonfuls of the biscuit mixture over the berries.Bake at 400° for 20 to 24 minutes or until fruit bubbles and crust is golden brown.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0354108681E64409A7C31EB2836B2F5', X'8C0254DF96CE1C478812A4EFBE2E9AFF', X'8C0254DF96CE1C478812A4EFBE2E9AFF', 0, NULL, NULL, 2, NULL, 0, '1. Position a rack in the center of the oven and heat to 350 degrees F. Butter and flour a 10 or 12-cup Bundt pan and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'396BF0FEB5A27D40A3116A3B17DC28DF', X'8C0254DF96CE1C478812A4EFBE2E9AFF', X'8C0254DF96CE1C478812A4EFBE2E9AFF', 1, NULL, NULL, 2, NULL, 0, '2. In a small saucepan, combine the butter, cocoa powder, salt, and water and place over medium heat. Cook, stirring, just until melted and combined. Remove from the heat and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC599C4CE1BBA5468B09BCB19EC50C84', X'8C0254DF96CE1C478812A4EFBE2E9AFF', X'8C0254DF96CE1C478812A4EFBE2E9AFF', 2, NULL, NULL, 2, NULL, 0, '3. In a large bowl, whisk together the flour, sugar, and baking soda. Add half of the melted butter mixture and whisk until completely blended. The mixture will be thick. Add the remaining butter mixture and whisk until combined. Add the eggs, one at a time, whisking until completely blended. Whisk in the sour cream (or Greek yogurt) and the vanilla extract. Whisk until smooth.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F430C7B1B0D684C86D6A30B94B8D02D', X'8C0254DF96CE1C478812A4EFBE2E9AFF', X'8C0254DF96CE1C478812A4EFBE2E9AFF', 3, NULL, NULL, 2, NULL, 0, '4. Scrape the batter into the prepared pan and bake until a toothpick inserted into the center of the cake comes out clean, 40 to 45 minutes. Let the cake cool in the pan for 15 minutes and then invert onto a rack. Let cool completely before glazing.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C178E68205F61A49AE75B5FEAC842ECB', X'8C0254DF96CE1C478812A4EFBE2E9AFF', X'8C0254DF96CE1C478812A4EFBE2E9AFF', 4, NULL, NULL, 2, NULL, 0, '5. While the cake is cooling, make the chocolate glaze. Place the chopped chocolate and corn syrup (or agave) in a medium bowl and set aside. Combine the heavy cream and sugar in a small saucepan and put over medium heat. Stir until the cream is hot and the sugar is dissolved. Pour the hot cream over the chocolate and whisk until smooth.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0492087B1AC82E47BD4E07E7C045F6C3', X'8C0254DF96CE1C478812A4EFBE2E9AFF', X'8C0254DF96CE1C478812A4EFBE2E9AFF', 5, NULL, NULL, 2, NULL, 0, '6. Generously drizzle the glaze over the cooled cake, allowing it to drip down the sides. Cut into pieces and serve.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37E210205D35594298B143EFD11BE4D7', X'DBAD26557CF46E4485ACFDE25E402EB3', X'DBAD26557CF46E4485ACFDE25E402EB3', 0, NULL, NULL, 2, NULL, 0, 'For the Cookie Layer:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CE7E688CF484047AF0313E2BBD9DFF3', X'DBAD26557CF46E4485ACFDE25E402EB3', X'DBAD26557CF46E4485ACFDE25E402EB3', 1, NULL, NULL, 2, NULL, 0, 'Cream together the butter and sugars. Add in the egg and vanilla. Sift together the dry ingredients and slowly add to the wet ingredients in batches of 2. Cover the bowl with plastic wrap and refrigerate for 15-30 minutes until the dough has become slightly hard and easier to spread out. Using a rubber spatula, spread the dough in the bottom of the baking sheet, smoothing out.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D9583EBEF2FCD45A728E3679C4BDA02', X'DBAD26557CF46E4485ACFDE25E402EB3', X'DBAD26557CF46E4485ACFDE25E402EB3', 2, NULL, NULL, 2, NULL, 0, 'For the Brownie Layer:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C22801B38E89F46851F0216EE978322', X'DBAD26557CF46E4485ACFDE25E402EB3', X'DBAD26557CF46E4485ACFDE25E402EB3', 3, NULL, NULL, 2, NULL, 0, 'Follow my brownie directions or follow the directions on the box. Pour batter over the cookie dough layer. Bake at 350 for 30-35 minutes until a toothpick comes out clean from the middle.Allow to cool before slicing into pieces');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C7F8B7218EBB04E953D2379694C80AB', X'B0DB99EE28839145A127916350BC56D5', X'B0DB99EE28839145A127916350BC56D5', 0, NULL, NULL, 2, NULL, 0, 'Stir all the dry ingredients in a large mixing bowl. Add in the wet ingredients and stir until everything is combined. Using a large spoon or tablespoon measure out ½ inch cookies balls and place onto baking sheet. Bake at 350 for 10-13 minutes. Mine take 12 minutes exactly in my oven but in my mom''s its 10 so just check on them once you reach 10 minutes. Remove when they are slightly golden brown.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2755B2F5E176C46BE51CFEDB8DB5EF5', X'8B905C240FFF3249B2299D1391DE45A5', X'8B905C240FFF3249B2299D1391DE45A5', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the sugar and corn starch in a medium saucepan over medium to low heat. Heat the milk in the microwave for 15-20 seconds. Slowly add the milk and whisk until combined and smooth- 2 minutes. Now its time to temper so we don''t end up with scrambled eggs. Take ½ cup to 1 cup of the milk mixture and add to the bowl of beaten eggs, whisking continously for 1-2 minutes. Now return the mixture back to the saucepan and finish whisking it til combined. Add in the salt and lemon zest then reduce heat to low and while continuing to whisk the pudding until thick in texture (it should coat the back of a spoon).');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'818B2D69DF19694DA4CC684EEABB309C', X'8B905C240FFF3249B2299D1391DE45A5', X'8B905C240FFF3249B2299D1391DE45A5', 1, NULL, NULL, 2, NULL, 0, 'Add the butter and lemon juice and whisk until combined. Do you get the theme here? WHISK! Allow to cool then refrigerate for at least 2 hours before you begin assembling your pies.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C501E6F264A6D4D822528F847126E38', X'8B905C240FFF3249B2299D1391DE45A5', X'8B905C240FFF3249B2299D1391DE45A5', 2, NULL, NULL, 2, NULL, 0, 'To assemble: Sprinkle spoonful or two of graham cracker crumbs (or crumbs of your choosing) into the bottom of your jar. Arrange the sliced strawberries around the outer edge. Top with a spoonful or two of the lemon pudding. Repeat with the graham cracker crumbs then blueberries then pudding then blueberries again. If you decided to use different fruit then by all means you can and may assemble to your liking based on if you want more or less of each component.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A960EDCD0000334EB6193FA4538FC387', X'35CE2CA2433E4043B5DDFE5C63D034B1', X'35CE2CA2433E4043B5DDFE5C63D034B1', 0, NULL, NULL, 2, NULL, 0, 'Melt the butter and marshmallows in a large microwavable bowl or in a double boiler over med to low heat. Remove from heat and add in the green food coloring. Add in the rice krispies and stir until the cereal is completely coated. Press into a greased 14x3 inch pan ( or 2 8x3 pans) and press up against the sides to form a crust (about ½ inch tall and ¼ inch thick).');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'414B5B202888874985E7FE75A2F36285', X'35CE2CA2433E4043B5DDFE5C63D034B1', X'35CE2CA2433E4043B5DDFE5C63D034B1', 1, NULL, NULL, 2, NULL, 0, 'Repeat steps above for the red potion using either green food coloring or the box of watermelon flavoring. Press into the center of the pan working your way outward. You may have to use cooking spray on your hands to keep the cereal from sticking to it. Top with raisins, chocolate almonds, chocolate sunflower seeds or chocolate chips.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7333E26F32B7D4A81CB45C2B7C30F68', X'35CE2CA2433E4043B5DDFE5C63D034B1', X'35CE2CA2433E4043B5DDFE5C63D034B1', 2, NULL, NULL, 2, NULL, 0, 'Allow to rest for 1-2 hours before cutting and serving! Slice it up like a pie and ENJOY!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E85445CF17CE2348A811F9018AD75FA4', X'E043773E6076AB4FBEE45486DEA70D9F', X'E043773E6076AB4FBEE45486DEA70D9F', 0, NULL, NULL, 2, NULL, 0, 'Sift together dry ingredients in a large bowl. Cut in the butter. Whisk together the wet ingredients including the basil olive oil and pour into the dry ingredients. Stir until a dough begins to form.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81D23614CCF4BE4EA67E2AE1FE87EB29', X'E043773E6076AB4FBEE45486DEA70D9F', X'E043773E6076AB4FBEE45486DEA70D9F', 1, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a lightly GF or non GF floured surface. Knead the dough until it forms a ball. Roll it out into ½ inch thick and using a biscuit cutter cut out as many circles as you can, Place on a non-stick baking sheet and brush the tops of the biscuits with a combination of 1 tbsp of milk and 1 tsp of basil olive oil and sprinkle with coarse sugar. Bake biscuits in 350 preheated oven for 12-14 minutes, or until a nice golden brown on top.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85F98ECB8A0E184B9EA4AC82BA186F28', X'E043773E6076AB4FBEE45486DEA70D9F', X'E043773E6076AB4FBEE45486DEA70D9F', 2, NULL, NULL, 2, NULL, 0, 'Allow to cool. Mix the cup of strawberry mash with 2-3 TBSP of sugar and spoon on top of the shortcakes with a dollop of whipping cream (Optional)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD0EA81EAAAE7A4882B8990B9EF6ABD0', X'C7639D01FF9CC245AD4359BBC3EFE158', X'C7639D01FF9CC245AD4359BBC3EFE158', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'114308B00F5FF340BF6990C8D5B86171', X'C7639D01FF9CC245AD4359BBC3EFE158', X'C7639D01FF9CC245AD4359BBC3EFE158', 1, NULL, NULL, 2, NULL, 0, 'Set aside 2 tablespoons of the butter. Beat the remaining butter with mixer on medium speed for 30 seconds. Add brown sugar and beat until well mixed. Beat in eggs and 1 teaspoon vanilla. In another large bowl stir together flour and baking soda then stir in oats. Gradually stir dry mixture into beaten mixture. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'345F850B8C85954AB7913A2240A085F9', X'C7639D01FF9CC245AD4359BBC3EFE158', X'C7639D01FF9CC245AD4359BBC3EFE158', 2, NULL, NULL, 2, NULL, 0, 'In a medium saucepan combine the 2 tablespoons butter, the sweetened condensed milk, and chocolate . Cook over low heat until chocolate melts, stirring occasionally. Remove from heat. Stir in 1 teaspoon vanilla and ½ teaspoon of pure coffee extract.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F2D59944C1B6449810C4F7C6EA0F859', X'C7639D01FF9CC245AD4359BBC3EFE158', X'C7639D01FF9CC245AD4359BBC3EFE158', 3, NULL, NULL, 2, NULL, 0, 'Press three quarters of the oat mixture into the bottom of an ungreased 15×10x1inch baking pan (or something close to it). Spread chocolate mixture over the oat mixture. Use your fingers and dot the remaining oat mixture over the chocolate. Tip: Wet your fingers slightly to help prevent sticking.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E20B98F5BA7A4E4EA8DF4DEF25C24FF3', X'C7639D01FF9CC245AD4359BBC3EFE158', X'C7639D01FF9CC245AD4359BBC3EFE158', 4, NULL, NULL, 2, NULL, 0, 'Bake for about 25 minutes or until top is lightly browned (chocolate mixture will still look moist). Cool on a wire rack. Cut into 2×2 inch inch bars.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'502D9AF1F9B8204280CA9EB220BDFA22', X'C7639D01FF9CC245AD4359BBC3EFE158', X'C7639D01FF9CC245AD4359BBC3EFE158', 5, NULL, NULL, 2, NULL, 0, 'Beat together the butter and sugars until light and fluffy. Beat in the caramel sauce, eggs and vanilla extract. In a separate bowl, whisk together the flour, salt and baking soda. Slowly add to wet ingredients. Stir in the white chocolate pieces.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A6F2CF2BBE91F46995F52D9B55CDD72', X'C7639D01FF9CC245AD4359BBC3EFE158', X'C7639D01FF9CC245AD4359BBC3EFE158', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate dough for at least 2 hours. Scoop out cookies and place on a parchment lined baking sheet. Press down slightly and sprinkle with a pinch or two of salt. Bake for 12-15 minutes or until just slightly golden.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBBF28C63A970A408FA8C77B61CB5775', X'691A97E97BD71D4D8565A825CB643953', X'691A97E97BD71D4D8565A825CB643953', 0, NULL, NULL, 2, NULL, 0, 'Preheat 350°');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5FE6F841BC0B84C9D22C608635A99EF', X'691A97E97BD71D4D8565A825CB643953', X'691A97E97BD71D4D8565A825CB643953', 1, NULL, NULL, 2, NULL, 0, 'Spray a 9x13 baking pan lightly with cooking spray. In a large bowl cream together shortening, butter, granulated sugar, 1 whole egg plus 2 yolks and vanilla until smooth.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E8699C395BFA64D9351A4890747B75E', X'691A97E97BD71D4D8565A825CB643953', X'691A97E97BD71D4D8565A825CB643953', 2, NULL, NULL, 2, NULL, 0, 'Add in flour, baking powder and salt and mix until just combined. Spread in prepared pan.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7BB7616A8A51B4A9C4FBF9B5FF719C6', X'691A97E97BD71D4D8565A825CB643953', X'691A97E97BD71D4D8565A825CB643953', 3, NULL, NULL, 2, NULL, 0, 'Top with chocolate chips and mini marshmallows.In a clean mixing bowl with whisk attachment on your mixer, beat the 2 remaining egg whites until stiff peaks form, about 2 minutes on high.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6EE646EE6181E4090B3E8D404459AC2', X'691A97E97BD71D4D8565A825CB643953', X'691A97E97BD71D4D8565A825CB643953', 4, NULL, NULL, 2, NULL, 0, 'Fold in 1 light brown sugar and stir until combined and there are no lumps.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C692D5BC8B3AD4093576F4B30D5B42D', X'691A97E97BD71D4D8565A825CB643953', X'691A97E97BD71D4D8565A825CB643953', 5, NULL, NULL, 2, NULL, 0, 'Spread on top of chocolate chips and marshmallows. Bake 30-35 minutes until top is golden brown.Allow to cool completely before cutting into squares');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33C94F0A33F2F04991753AE29880A50B', X'99B31B99A2573940B51183D2035F90CC', X'99B31B99A2573940B51183D2035F90CC', 0, NULL, NULL, 2, NULL, 0, 'For the Shortbread (easiest recipe ever)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD541580A22FF6418684DF099B02A451', X'99B31B99A2573940B51183D2035F90CC', X'99B31B99A2573940B51183D2035F90CC', 1, NULL, NULL, 2, NULL, 0, 'Cream together your butter and sugar. Slowly add in the extract and then the dry ingredients. Beat until just combined. Place dough into a greased 8x10 pan and press dough down. Bake at 350 for 15 minutes. Remove and allow to cool completely as you do the next step.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7682E881E293974CAFB02B74D4E2D71C', X'99B31B99A2573940B51183D2035F90CC', X'99B31B99A2573940B51183D2035F90CC', 2, NULL, NULL, 2, NULL, 0, 'For the Sauce:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7863C19D97AB246BBDAA384AA4C9974', X'99B31B99A2573940B51183D2035F90CC', X'99B31B99A2573940B51183D2035F90CC', 3, NULL, NULL, 2, NULL, 0, 'Put all ingredients into a medium size saucepan. Turn heat to medium and watch the magic begin. Stir the mixture until the butter is melted then turn heat to high, put down the spoon or whisk and be patient while the mixture comes to a boil. Think happy thoughts! Once the mixture is boiling reduce heat back to medium low and allow to cook for another 8-10 minutes while you are constantly stirring it. Remove, your done! Yeah- next step is below.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE312CEC5AFE214FA1B52265BFA1BA03', X'99B31B99A2573940B51183D2035F90CC', X'99B31B99A2573940B51183D2035F90CC', 4, NULL, NULL, 2, NULL, 0, 'Back to the bars: Drizzle or spoon ½ cup or more of the caramel sauce over the top of the bottom shortbread layer. Allow to cool for a few minutes then crumble the remaining shortbread dough over the top of the caramel. I do this by pulling apart the dough into bite-sized balls and placing them on top of the dough. Then I run a toothpick in and through the dough (barely scraping the top) to mix it all in with the caramel. Bake at 350 for another 15 minutes or until the top is nice and golden brow.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7CD8FEC67473D46B1EDFD5B0244F5B2', X'F30C3971C61A5542A1DBA7FD575DEEC3', X'F30C3971C61A5542A1DBA7FD575DEEC3', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F. In a large bowl, whisk flour, baking soda, sugar, and salt together and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA0B9B5FC7BC0344AA554160B721D93B', X'F30C3971C61A5542A1DBA7FD575DEEC3', X'F30C3971C61A5542A1DBA7FD575DEEC3', 1, NULL, NULL, 2, NULL, 0, 'In a 10-inch cast iron skillet, bring the butter, vegetable oil, cocoa powder, and water to a boil. Remove it from the heat and whisk in the dry ingredients well. Mix in the buttermilk, egg, and vanilla. Bake the skillet cake at 350 degrees F for about 15-20 minutes or until a toothpick comes out with just a few moist crumbs.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'909ACA1C985E3C4AACA5C306B6B766F2', X'F30C3971C61A5542A1DBA7FD575DEEC3', X'F30C3971C61A5542A1DBA7FD575DEEC3', 2, NULL, NULL, 2, NULL, 0, 'While the cake starts to cool, make the frosting. In a medium saucepan, bring the butter, cocoa, and milk to a boil. Remove them from heat and add the icing sugar, nuts, and vanilla. Stir to combine. Pour over the warm cake, spread with a spatula.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4693DB3464F2A04B9511A5F5B549245C', X'95D8FE01B64CD543910057AFBF576296', X'95D8FE01B64CD543910057AFBF576296', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the condensed milk, cold brewed leftover coffee, whole milk and a pinch of salt. When it is all blended up, pour into your ice cream maker and let it go. When it is nearly done, sprinkle in the chopped chocolate. When ready, pour into a lidded container and freeze until frozen. See, I said it was easy!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F8963CC8D1DA24283B79F37510C2F18', X'A0C33E7B56738F41AEA114D9AFCE9175', X'A0C33E7B56738F41AEA114D9AFCE9175', 0, NULL, NULL, 2, NULL, 0, 'If using the ancho chile peppers- stick them in the oven for 5 minutes to toast them. If using the powder skip this step.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6663888029EF64E9E733CDB7916C347', X'A0C33E7B56738F41AEA114D9AFCE9175', X'A0C33E7B56738F41AEA114D9AFCE9175', 1, NULL, NULL, 2, NULL, 0, 'In a 2 quart pot over medium heat combine the chile pepper, cream, milk, sugar, cocoa and red pepper flakes. Bring to a scald (not a boil- just heat until film forms on the top of the liquid)');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E17EC484A73E1B4F948450A3C4E123B9', X'A0C33E7B56738F41AEA114D9AFCE9175', X'A0C33E7B56738F41AEA114D9AFCE9175', 2, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the egg yolks with the cornstarch. Now we need to temper. Ladle two scoops of the chocolate milk mixture into the eggs slowly, whisking constantly. Slowly whisk the now-tempered egg mixture back into the rest of the chocolate in the pot. Continue to stir on low heat 3-4 minutes. Mixture will come together and begin to thicken. It will become even thicker as it cools.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4053D419A3FB15499729D52E01D44FE4', X'A0C33E7B56738F41AEA114D9AFCE9175', X'A0C33E7B56738F41AEA114D9AFCE9175', 3, NULL, NULL, 2, NULL, 0, 'Strain the mixture to remove the chile peppers (if using) and pour into your choice of container or bowl. Refrigerate to cool or serve warm with whipped cream.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7801D4D5363E13438A713A8370E8BD14', X'A0C33E7B56738F41AEA114D9AFCE9175', X'A0C33E7B56738F41AEA114D9AFCE9175', 4, NULL, NULL, 2, NULL, 0, 'For the cream- Combine the heavy cream and salt in a large bowl. Beat with a hand mixer for by hand for 2-3 minutes (if using mixer or 8 minutes if using hand). Beat until medium peaks form. Serve on top of the pudding. Enjoy!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'268086E7335D5943AFF3EDAE6DC71C3F', X'276ACF3689639B4CBCD4FFE8F2E23F58', X'276ACF3689639B4CBCD4FFE8F2E23F58', 0, NULL, NULL, 2, NULL, 0, '1. Break graham crackers into small pieces and line the bottom of jars with them.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1F52DC86AEAE54CB55F79F62EB3FF54', X'276ACF3689639B4CBCD4FFE8F2E23F58', X'276ACF3689639B4CBCD4FFE8F2E23F58', 1, NULL, NULL, 2, NULL, 0, '2. Mix up the cream.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C683F6A324BE74995ECEDF66B1CCF18', X'276ACF3689639B4CBCD4FFE8F2E23F58', X'276ACF3689639B4CBCD4FFE8F2E23F58', 2, NULL, NULL, 2, NULL, 0, 'Bring 2 cups of the milk to a boil in a medium saucepan. While it is heating, combine sugar, cornstarch, salt and vanilla sugar in the bottom of a medium, heatproof bowl. Gradually whisk in the remaining ? cup whole milk, a little at a time so lumps do not form, then whisk in the egg. Once milk is boiling, very gradually add it to the cornstarch mixture in the bowl, whisking the whole time.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6760EA35D0A594784109FEE87B8F238', X'276ACF3689639B4CBCD4FFE8F2E23F58', X'276ACF3689639B4CBCD4FFE8F2E23F58', 3, NULL, NULL, 2, NULL, 0, 'Return the mixture back to the saucepan, stirring constantly with a silicon spatula or wooden spoon. Once it comes to a simmer, cook it for one minute longer (which will cook the cornstarch and egg fully). Stir in vanilla extract and whipping cream. Divide cream mixture among jars. Fill about ? to ¾ full.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81FF5A0F4354064FABFAB330672D15C3', X'276ACF3689639B4CBCD4FFE8F2E23F58', X'276ACF3689639B4CBCD4FFE8F2E23F58', 4, NULL, NULL, 2, NULL, 0, '3. Top with another layer of grahams. Break up here and cover entire surface as well as possible.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93C3433C8338134097AD7ED732F8C0B4', X'276ACF3689639B4CBCD4FFE8F2E23F58', X'276ACF3689639B4CBCD4FFE8F2E23F58', 5, NULL, NULL, 2, NULL, 0, '4. Finish off with a thick layer of frosting spread across the top like a lid.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B4A9088321493448E1B3502A14B9CB8', X'276ACF3689639B4CBCD4FFE8F2E23F58', X'276ACF3689639B4CBCD4FFE8F2E23F58', 6, NULL, NULL, 2, NULL, 0, '5. Refrigerate for 8 hours with no lid then cover with lid for another 10 hours.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40249124FBBC1743A0667930914B0801', X'7C7017DE34FD294E95E8A0EAC508873B', X'7C7017DE34FD294E95E8A0EAC508873B', 0, NULL, NULL, 2, NULL, 0, '1.In a large bowl with mixer beat butter, peanut butter, and sugars until light and fluffy, about 30 seconds on medium-high speed. Lower mixer to medium and add eggs and vanilla continuing to mix until combined. Turn mixer to low and add in flour, baking soda and baking powder. Mix until combined. Refrigerate dough for 1 hour. Preheat oven to 375°');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D3307D5CDA61B4C97A34716E4E177D4', X'7C7017DE34FD294E95E8A0EAC508873B', X'7C7017DE34FD294E95E8A0EAC508873B', 1, NULL, NULL, 2, NULL, 0, '2.Form chilled dough first into 1 inch balls and then form into a log shape. Roll the log-shaped dough in extra granulated sugar and place on baking sheet about 2 inches apart. Using a fork press down on cookie creating a criss-cross pattern. Pinch the center to create the peanut shape.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C84B9B13B6463E448F960E66E39879CF', X'7C7017DE34FD294E95E8A0EAC508873B', X'7C7017DE34FD294E95E8A0EAC508873B', 2, NULL, NULL, 2, NULL, 0, '3.Bake for 6-8 minutes until edges are lightly golden. Cool on baking sheet 2-3 minutes and then transfer to wire rack to continue cooling.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F4059C6EFF06D42AB253476D61D1D55', X'7C7017DE34FD294E95E8A0EAC508873B', X'7C7017DE34FD294E95E8A0EAC508873B', 3, NULL, NULL, 2, NULL, 0, '4.For the Filling:In a large bowl beat butter and peanut butter together until smooth. Slowly add in your powdered sugar and turn mixer speed up to medium and beat until filling is smooth. Add your milk, one tablespoon at a time until desired consistency is reached.Spread a heaping tablespoon of filling onto the bottom of one of the peanut butter cookies and then top with another cookie to create sandwich.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12704125C5EDED48AD714F8D0D4FDB4F', X'7C7017DE34FD294E95E8A0EAC508873B', X'7C7017DE34FD294E95E8A0EAC508873B', 4, NULL, NULL, 2, NULL, 0, 'Notes: store in an airtight container for up to 5 days.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67358BF8F7CCBA42BAAEF9B3CD3D6053', X'B7875FA39347ED42AF1F84827F51E859', X'B7875FA39347ED42AF1F84827F51E859', 0, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 350°F (175°C).');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'095F575686F49D40901745B3EDFE1CC2', X'B7875FA39347ED42AF1F84827F51E859', X'B7875FA39347ED42AF1F84827F51E859', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the eggs, milk and vanilla seeds. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A54049B2DC2A44C9A7B3C12D520DD6A', X'B7875FA39347ED42AF1F84827F51E859', X'B7875FA39347ED42AF1F84827F51E859', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, sift together the flour, baking powder, cinnamon and salt. Stir in the sugar and coconut. Make a well in the centre of the dry ingredients, and slowly add the egg mixture, stirring until just combined. Fold in the melted butter, sunflower seeds and chocolate chips, being careful not to overmix.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A4E9CDE8760B24C9C4642A8678B14B7', X'B7875FA39347ED42AF1F84827F51E859', X'B7875FA39347ED42AF1F84827F51E859', 3, NULL, NULL, 2, NULL, 0, 'Grease and flour a 8-by-4-inch loaf pan or individual panettone or muffin liners. Pour in the batter and bake in the preheated oven until the loaf is golden and a cake tester inserted into the middle comes out clean, around 1 hour or 20-25 minutes for individual liners. * Make sure to fill batter ¾ to the top of your panettone liners so you get a nice raised loaf of bread peaking out of the top.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92B8925921CFB541A866937738896505', X'9A92C55D9C42D34082D146B541DBE23A', X'9A92C55D9C42D34082D146B541DBE23A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375Â°. Line baking sheets with parchment paper or silicone liners.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72B3538A964A124B9B252C27BADC3424', X'9A92C55D9C42D34082D146B541DBE23A', X'9A92C55D9C42D34082D146B541DBE23A', 1, NULL, NULL, 2, NULL, 0, 'Whisk together flours, baking soda, salt, and cinnamon. Stir in oats. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42258EC99B7A5E4FAA9D38E1AE5E1014', X'9A92C55D9C42D34082D146B541DBE23A', X'9A92C55D9C42D34082D146B541DBE23A', 2, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat butter, brown sugar, and sugar until creamy. Add egg and vanilla, and mix well. Gradually add flour mixture, stirring until well blended.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'424771588B727B4CAFE4179C8FBAFDF5', X'9A92C55D9C42D34082D146B541DBE23A', X'9A92C55D9C42D34082D146B541DBE23A', 3, NULL, NULL, 2, NULL, 0, 'Drop dough by rounded tablespoonfuls, 2 inches apart, onto prepared baking sheets. Bake 8-10 minutes. Cool on pan for 2-3 minutes, then remove to wire racks to cool completely.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ECA7CF7F2885442861F43A5F3CE9A35', X'9A92C55D9C42D34082D146B541DBE23A', X'9A92C55D9C42D34082D146B541DBE23A', 4, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a medium bowl. Using an electric mixer on low speed, beat until combined. Then, increase mixer speed to high, and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5170D0005DAF094C895EA88D1951C381', X'9A92C55D9C42D34082D146B541DBE23A', X'9A92C55D9C42D34082D146B541DBE23A', 5, NULL, NULL, 2, NULL, 0, 'Spread about 1 tablespoon of filling over the bottom side of half of the cookies. Top with remaining cookies so that the bottoms of the cookies are facing the filling.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8815F984EB7A39468DDBAF8693E02C6D', X'04926D152DA302429173D9199A89DEE0', X'04926D152DA302429173D9199A89DEE0', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350? F. Line cupcake pans with paper liners. In a medium bowl, combine the flour, baking soda, salt and cinnamon; whisk together and set aside. In the bowl of an electric mixer, combine the butter, vanilla, and sugar and beat until blended and smooth. Beat in the eggs one at a time, mixing well after each addition. Mix in the mashed banana. With the mixer on low speed, mix in the dry ingredients just until incorporated. Gently fold in the pineapple, nuts (if using), and coconut with a spatula until evenly mixed.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA2FA8C29108E7468808BEA92323ED7D', X'04926D152DA302429173D9199A89DEE0', X'04926D152DA302429173D9199A89DEE0', 1, NULL, NULL, 2, NULL, 0, 'Divide the batter between the prepared liners, filling each about ¾ full. Bake for 20-22 minutes, until a toothpick inserted in the center comes out clean. Let cool in the pans 5-10 minutes, then transfer to a wire rack to cool completely.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7B71972908DC34C8BD0823A51F20417', X'04926D152DA302429173D9199A89DEE0', X'04926D152DA302429173D9199A89DEE0', 2, NULL, NULL, 2, NULL, 0, 'To make the frosting, combine the cream cheese and butter in the bowl of an electric mixer. Beat on medium-high speed until well combined and smooth, about 2-3 minutes. Mix in the vanilla extract. Gradually beat in the confectioners’ sugar until totally incorporated, increase the speed and then beat until smooth. Frost cooled cupcakes as desired.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD910760735A514898EDC5B1012193F2', X'5F44C36BBC6B6C429142C737C1329E48', X'5F44C36BBC6B6C429142C737C1329E48', 0, NULL, NULL, 2, NULL, 0, 'Grease shallow 3-quart ceramic baking dish. In microwave-safe small bowl, reserve 2 tablespoons dulce de leche; cover and refrigerate.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'285D4E5B3B6C914D8302E35E19320C6B', X'5F44C36BBC6B6C429142C737C1329E48', X'5F44C36BBC6B6C429142C737C1329E48', 1, NULL, NULL, 2, NULL, 0, 'Lightly butter both sides of all bread slices. In 12-in. skillet, cook bread slices on medium-high 1 to 2 minutes per side or until golden brown and toasted. Cool on wire rack. In same skillet, combine milk, butter and remaining dulce de leche. Heat on medium-high 6 to 8 minutes or until bubbles form around edge of pan, stirring occasionally.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74BDB7959BA3B64E9A9F478807968823', X'5F44C36BBC6B6C429142C737C1329E48', X'5F44C36BBC6B6C429142C737C1329E48', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in large bowl, with wire whisk, combine eggs, brown sugar, cinnamon, vanilla, and salt. Continue whisking and add ½ cup hot milk mixture in slow, steady stream. Whisk in remaining milk mixture.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A66956726529B744BB1124560A2D6211', X'5F44C36BBC6B6C429142C737C1329E48', X'5F44C36BBC6B6C429142C737C1329E48', 3, NULL, NULL, 2, NULL, 0, 'Cut toasted bread slices into 1-in. cubes; arrange in single layer in prepared baking dish. Sprinkle with even layer of chocolate. Pour egg mixture over bread and chocolate. Let stand at least 30 minutes or overnight.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDB0476A031E3B4D8746A227BDDAB3B5', X'5F44C36BBC6B6C429142C737C1329E48', X'5F44C36BBC6B6C429142C737C1329E48', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Bake 25 to 30 minutes or until just set and dark golden brown on top. Cool bread pudding in pan on wire rack 20 minutes. Microwave reserved dulce de leche on High 20 seconds to warm. Drizzle over bread pudding to serve.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17D6E16F3578F24DAF87ADB8256CB27A', X'C74C8C9D1FE02E4CAE053706517F0F70', X'C74C8C9D1FE02E4CAE053706517F0F70', 0, NULL, NULL, 2, NULL, 0, 'In medium bowl, combine flour, sugar, baking powder, hot chocolate,soda and salt, set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C5AD83E72C3EF4BA1EB4B7681DD8D3F', X'C74C8C9D1FE02E4CAE053706517F0F70', X'C74C8C9D1FE02E4CAE053706517F0F70', 1, NULL, NULL, 2, NULL, 0, 'In small bowl, combine milk, sour cream, and egg then stir the egg mixture in to the flour mixture with margarine until just blended. Gently stir in the cookies and spoon batter 12 greased 2½ inch muffin pan cups.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2EF4EB169AA494EA03224568C1DCDD7', X'C74C8C9D1FE02E4CAE053706517F0F70', X'C74C8C9D1FE02E4CAE053706517F0F70', 2, NULL, NULL, 2, NULL, 0, 'Bake at 400 degrees F for 20-25 minutes or until toothpick inserted in center comes out clean.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'073F468A8B117640BE95979AAF1B7653', X'C74C8C9D1FE02E4CAE053706517F0F70', X'C74C8C9D1FE02E4CAE053706517F0F70', 3, NULL, NULL, 2, NULL, 0, 'Remove from pan, cool on wire rack.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'248446F23D96B645832C7C2F3F108DD2', X'5942A49E7C8A5945A302C09022F34F64', X'5942A49E7C8A5945A302C09022F34F64', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425°. Brush four 6-ounce ramekins with melted butter.Transfer the ramekins to a sturdy baking sheet. In a medium saucepan, melt 1 stick of butter with the chocolate over very low heat, stirring occasionally. Let cool slightly and set aside, Melt the ¼ cup of chocolate chips with the biscoff spread before adding it to the chocolate butter mixture. Set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'941DBB47D1BB5F4DB8B212895AF8532E', X'5942A49E7C8A5945A302C09022F34F64', X'5942A49E7C8A5945A302C09022F34F64', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, using an electric mixer, beat the granulated sugar with the eggs and salt at medium-high speed until thick and pale yellow, 3 minutes. Using a rubber spatula, fold in the melted chocolate until no streaks remain. Add in the espresso and vanilla. Fold in the ¼ cup of flour.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F51562B7BB25148BB21A23C99664877', X'5942A49E7C8A5945A302C09022F34F64', X'5942A49E7C8A5945A302C09022F34F64', 2, NULL, NULL, 2, NULL, 0, 'Spoon two-thirds of the batter into the prepared ramekins, then spoon the peanut butter mixture on top. Cover with the remaining chocolate batter. Bake in the center of the oven for 16 minutes, until the tops are cracked but the centers are still slightly jiggly. Transfer the ramekins to a rack and let cool for 5 to 8 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBCC70B112B60B42B6FE690B403D8E6A', X'E3848F3E70F48C43A1E08546D1B32A66', X'E3848F3E70F48C43A1E08546D1B32A66', 0, NULL, NULL, 2, NULL, 0, 'Fit the pie dough to a tart pan or pie tin and pierce the bottom in several dozen places places with the tines of a fork. Cut the chocolate into bite size chunks and place on the bottom of the tart pan. Place the sliced pears on top of the chocolate.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EF24688689193429C5218D1AEC3521F', X'E3848F3E70F48C43A1E08546D1B32A66', X'E3848F3E70F48C43A1E08546D1B32A66', 1, NULL, NULL, 2, NULL, 0, 'Beat the eggs with the sugar then add in the cream and vanilla, liqueur and zest. Pour over the pears. Sprinkle with a the 2 TBSP of sugar and place in a preheated 400 degree oven for 10 minutes. Reduce temperature to 375 and bake another 30 minutes or until the top is nice and golden and the custard is set.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59B66624149D8D4F8C0C3CD19923BE61', X'5618A3F335C1284DB9953E5118BA9180', X'5618A3F335C1284DB9953E5118BA9180', 0, NULL, NULL, 2, NULL, 0, 'Combine the dry ingredients together and set aside. Mix together the remaining ingredients in a large bowl leaving the bananas, pears for last. Stir in the dry ingredients and pour your mixture into individual greased muffin tins. Bake at 350 for 15-20 minutes until nice and golden brown. Top with Caramel Frosting');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5FF102CF0059449A4E4661167FB4DD2', X'5618A3F335C1284DB9953E5118BA9180', X'5618A3F335C1284DB9953E5118BA9180', 1, NULL, NULL, 2, NULL, 0, 'For the Frosting:');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69F57CD1CC3FD44E9E067BD77F7CFFC9', X'5618A3F335C1284DB9953E5118BA9180', X'5618A3F335C1284DB9953E5118BA9180', 2, NULL, NULL, 2, NULL, 0, 'To prepare the caramel, combine the sugar and water in a saucepan. Heat over medium-high heat, stirring with a wooden spoon as the sugar melts. Stop stirring when the sugar starts to boil. Let the sugar boil until it is amber colored (but not too dark!). Remove the saucepan from the heat, and add the butter and the half and half. The caramel will foam up and steam, but that’s OK (just keep your face and hands clear and be careful!). Add the sea salt, and whisk vigorously until the caramel comes together. Cool to room temperature.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5F110030061504396EFF555BB07D478', X'5618A3F335C1284DB9953E5118BA9180', X'5618A3F335C1284DB9953E5118BA9180', 3, NULL, NULL, 2, NULL, 0, 'Transfer the caramel to a large mixing bowl. Add the shortening (or softened butter), and beat on medium speed until light and fluffy. Gradually add the powdered sugar, beating on medium speed, until the icing reaches a spreadable consistency.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5B7A88A9BC56D41828F5411BA8F6FD2', X'6DEA1154DDC3AE4C9A13A36113199724', X'6DEA1154DDC3AE4C9A13A36113199724', 0, NULL, NULL, 2, NULL, 0, 'Combine the flour, baking powder, spices and salt in a medium bowl. Whisk to combine. Incorporate the butter into the flour with your hands, a mixer or a pastry blender. Mix in the cranberries and raisins. Form a well in the center and pour in the eggnog. Using a wooden spoon slowly mix well just until combined. Flour your work surface and turn out the dough. Form it into a rough rectangle with your hands. Flour the top surface lightly, then roll it to a thickness of about ½ inch thick. Fold it into thirds, then roll it again and fold in thirds. Roll it to about ½- to ¾-inch thick then use a 2½-inch biscuit cutter to cut rounds from the dough.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'018BE1168E39184291EFE5C705727243', X'6DEA1154DDC3AE4C9A13A36113199724', X'6DEA1154DDC3AE4C9A13A36113199724', 1, NULL, NULL, 2, NULL, 0, 'Place the rounds on a baking sheet. If you prefer crisp edges, keep them separated. If you prefer tender edges, arrange them on the sheet so the edges touch.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EA1D6212F4C7E4AB27FEACEC899242F', X'6DEA1154DDC3AE4C9A13A36113199724', X'6DEA1154DDC3AE4C9A13A36113199724', 2, NULL, NULL, 2, NULL, 0, 'Bake at 400 degrees until the biscuits are lightly browned, 12-15 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'693B48302BAB7149B8EAEA80D1EA6E9C', X'48FC78B1CB893845ADF7D398B503B76A', X'48FC78B1CB893845ADF7D398B503B76A', 0, NULL, NULL, 2, NULL, 0, 'Follow directions according to the sugar cookie recipe found here. Wrap dough in plastic wrap and refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A62EC108F35204A9FFB9C58F7B021EE', X'48FC78B1CB893845ADF7D398B503B76A', X'48FC78B1CB893845ADF7D398B503B76A', 1, NULL, NULL, 2, NULL, 0, 'Roll dough into large individual balls and then place a caramel in the center. Roll the dough again through your hands to seal the caramel piece inside making sure there is none to be seen. Repeat with remaining dough.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC31192D61259B49A680B17EEE3ECA6B', X'48FC78B1CB893845ADF7D398B503B76A', X'48FC78B1CB893845ADF7D398B503B76A', 2, NULL, NULL, 2, NULL, 0, 'Bake on parchment lined baking sheet at 350 for 8-10 minutes until lightly and slightly:) golden. Sprinkle with sugar crystals, cinnamon and sugar mixture or caster sugar and STUFF STUFF STUFF them into your mouth!');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E087086006971D468EB399D4726BCB8F', X'E0E59DEFD7779848BFEE462914B979E4', X'E0E59DEFD7779848BFEE462914B979E4', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, sift together flour, cocoa, espresso, baking powder, and salt. With an electric mixer, cream butter and brown sugar until light and fluffy. Beat in egg until well combined; mix in cooled chocolate. With mixer on low, gradually add flour mixture; beat in milk until just combined. Flatten dough into a disk; wrap in plastic. Freeze until firm, about 45 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56B060A1C9D88D46A953C3BE1B6D539C', X'E0E59DEFD7779848BFEE462914B979E4', X'E0E59DEFD7779848BFEE462914B979E4', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees. Line two baking sheets with parchment. Shape dough into 1-inch balls. Pour confectioners'' sugar (about ½ cup) into a medium bowl; working in batches, roll balls in sugar two times, letting them sit in sugar between coatings.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D94AC9CBFCB6CB46BD6DF06A80B8803B', X'E0E59DEFD7779848BFEE462914B979E4', X'E0E59DEFD7779848BFEE462914B979E4', 2, NULL, NULL, 2, NULL, 0, 'Place on prepared baking sheets, 2 inches apart. Bake until cookies have spread and coating is cracked, 12 to 14 minutes; cookies will still be soft to the touch. Cool cookies on a wire rack.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'776867F88ED17A47B239217E16DB65BF', X'88C41CC235CDB64A99C141FA31FE6BF6', X'88C41CC235CDB64A99C141FA31FE6BF6', 0, NULL, NULL, 2, NULL, 0, 'Follow instructions for making my sugar cookie recipe adding in an extra 4 TBSP of flour to the dry ingredients. Press mixture evenly onto the bottom of prepared baking pan. Bake in preheated oven at 350 for 12 to 15 minutes or until edges are lightly browned.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'852D3FF1A2C7A745B653E2B3F7835594', X'88C41CC235CDB64A99C141FA31FE6BF6', X'88C41CC235CDB64A99C141FA31FE6BF6', 1, NULL, NULL, 2, NULL, 0, 'For filling, in a medium bowl, whisk together egg yolks, sweetened condensed milk, and peppermint extract. Stir in crushed candies. Carefully pour filling over hot crust. Bake for 15 to 20 minutes or until filling is set. Cool in pan on a wire rack for 1 hour. Cover; chill for 30 minutes or until cool.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64EFF9EA71C2FB48AD1B93C491EA7F36', X'88C41CC235CDB64A99C141FA31FE6BF6', X'88C41CC235CDB64A99C141FA31FE6BF6', 2, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, bring whipping cream just to boiling over medium-high heat. Remove from heat. Add white chocolate (do not stir) and let stand for 5 minutes. Stir until smooth. Cool about 5 minutes before pouring over bars.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17BC48E38D99BB4EA6A88FD0BC6FB9E3', X'88C41CC235CDB64A99C141FA31FE6BF6', X'88C41CC235CDB64A99C141FA31FE6BF6', 3, NULL, NULL, 2, NULL, 0, 'Pour White Chocolate Ganache over top of baked mixture, spreading evenly. Cover and chill 1 hour or until firm. Using the edges of the foil, lift cookies out of pan. Cut into bars. Makes approx. 36 bars.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABC4E35C2122094E921BD67C3D2EDC2D', X'028C5CF367BF364E837FDC7C154B1374', X'028C5CF367BF364E837FDC7C154B1374', 0, NULL, NULL, 2, NULL, 0, 'Cream the butter with sugar using a handheld mixer. Mix in vanilla then salt, orange zest and flour. Sprinkle in mini chocolate chips and stir with hand. Roll the dough out into ½ inch rectangle and cut into strips, rectangles or bars. Prick the tops with a fork and bake at 300 for 25 minutes.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AE37651C3CAFD48AA6AD7272D4B872B', X'F50C126E8171834AB241094F381D686D', X'F50C126E8171834AB241094F381D686D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees. Place brown sugar in a large bowl and work out lumps. Add flour and stir together until the flour disappears. Add corn syrup and vanilla. Stir until thoroughly until combined. Add melted butter. Fold into mixture until it disappears.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABFFDC3D45621F40AABBAAE8F65DA2CB', X'F50C126E8171834AB241094F381D686D', X'F50C126E8171834AB241094F381D686D', 1, NULL, NULL, 2, NULL, 0, 'Loosely beat two eggs in a separate bowl and fold into mixture until the eggs disappear. Stir in pecans and set aside.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'225AE23095B5834CB8099BDBAE6609EA', X'F50C126E8171834AB241094F381D686D', X'F50C126E8171834AB241094F381D686D', 2, NULL, NULL, 2, NULL, 0, 'Prepare brownie batter as instructed on package. Remove two frozen pies shells from freezer and divide brownie batter evenly between the two. (Deep dish for fudgy version and regular-size for the brownie version.) Spoon pecan pie mixture evenly between the two pie shells.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9A947D0145A4A4092470F6B17C57487', X'F50C126E8171834AB241094F381D686D', X'F50C126E8171834AB241094F381D686D', 3, NULL, NULL, 2, NULL, 0, 'Bake for about 35-40 minutes. Place a large sheet of foil across the top of the two pies for the last 10-15 minutes to avoid pie crust from browning too much. Top with the pecan mixture you set aside earlier and bake for another 10 minutes. Cool before slicing.');
INSERT INTO `VWR INTERNAL Kitchenbelleicious recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'850355A69315E146BAF5A82974DEA046', X'48E1B309E3E6AF418490DE34D308DC01', X'48E1B309E3E6AF418490DE34D308DC01', 0, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together just like you would normal pancakes. Grease your muffin tin and pour in the batter ? full into each cup. Bake for 15 minutes at 400 degrees until puffy. Using a knife simply slip them out onto a platter and enjoy!');
