INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1CE66042662C14494ED540C3C0A2DB3', X'BD029B97097A7D448F1E111524BB932D', X'BD029B97097A7D448F1E111524BB932D', 2, NULL, NULL, 2, NULL, 0, 'Add water, spinach, and remaining ingredients, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE218488F37B634AB933747A0791ED42', X'BD029B97097A7D448F1E111524BB932D', X'BD029B97097A7D448F1E111524BB932D', 3, NULL, NULL, 2, NULL, 0, 'Cover and steam for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB5686C2960A13499071DF9410413C7C', X'BD029B97097A7D448F1E111524BB932D', X'BD029B97097A7D448F1E111524BB932D', 4, NULL, NULL, 2, NULL, 0, 'Remove lid, mix and re-cover.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF7D4EE43CE3C14C973FD194510A2C65', X'BD029B97097A7D448F1E111524BB932D', X'BD029B97097A7D448F1E111524BB932D', 5, NULL, NULL, 2, NULL, 0, 'Cook the sautéed asparagus with spinach another 3-5 minutes, or until asparagus is to your liking (firm or soft).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86CD133846CF8A468CB5AA7C1B3ECB2C', X'BD029B97097A7D448F1E111524BB932D', X'BD029B97097A7D448F1E111524BB932D', 6, NULL, NULL, 2, NULL, 0, 'Once done, remove from heat, plate and serve the sautéed asparagus with spinach hot.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0F3F180AC6BAB41A9C9AC2D3664452B', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 0, NULL, NULL, 2, NULL, 0, 'If you need to active yeast, do so according to directions on packet and then add in all ingredients.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48FA33B884B0F647BB134133500AD61D', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, add: flour, salt, olive oil, sugar, yeast, warm water - mix to form dough.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F99B35B2F631248B63084AA0250C9F6', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 2, NULL, NULL, 2, NULL, 0, 'On a floured surface, knead for 10 minutes to incorporate everything.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9EC6EA23D9B65438686E037868CE6C6', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 3, NULL, NULL, 2, NULL, 0, 'Once done kneading, place back in bowl that’s been coated with some olive oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'839FE12D422F2346B5781A7632765639', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 4, NULL, NULL, 2, NULL, 0, 'Cover and let rise until double in size, about 90 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BA56B467B5ECB4C87264BFEEB0956BA', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 5, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A08CE0A65E3D14CB91C0C085CDC0BF6', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 6, NULL, NULL, 2, NULL, 0, 'Place dough on floured surface, and roll out into rectangle about ½ to ¾ inch thick and about 12 inches across.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15BE7C30503FA74B9F9B12564DAE620A', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 7, NULL, NULL, 2, NULL, 0, 'Cut dough lengthwise in about ¾ inch strips.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACD624AE26F93C4B92AD1FC0FECD655E', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 8, NULL, NULL, 2, NULL, 0, 'Roll each strip on a floured surface to form a string.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE5EC9A7A6FE9949BDE4094A0B090C3E', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 9, NULL, NULL, 2, NULL, 0, 'Brush the dough strips with water and connect both ends to form a ring.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'152DEC16514B0A49A49B866360A2B476', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 10, NULL, NULL, 2, NULL, 0, 'Roll the wet greek bread rings in sesame seeds.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E18411E751A3F84596ACB5003A0CA5CC', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 11, NULL, NULL, 2, NULL, 0, 'Place the koulouri on a baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8D130B4F7113C4786C7763056C08D14', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 12, NULL, NULL, 2, NULL, 0, 'Repeat process until all dough is used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'164958EF5DF0D041A43D4257BF596C78', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 13, NULL, NULL, 2, NULL, 0, 'Place in oven for 10-12 minutes, or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39B81EF3F2A90841B694BB713BBE2E57', X'835B6F02688B124E969738077ADF3C88', X'835B6F02688B124E969738077ADF3C88', 14, NULL, NULL, 2, NULL, 0, 'Once done, remove and enjoy your greek sesame bread rings!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C622917AB66B434FB8ECA264F054B3D5', X'68682879D35C6C419DB71DD695762A55', X'68682879D35C6C419DB71DD695762A55', 0, NULL, NULL, 2, NULL, 0, 'In a pot over medium-heat, add garlic cloves and cook until fragrant.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FE68F43280E60479D3E54ABCC327006', X'68682879D35C6C419DB71DD695762A55', X'68682879D35C6C419DB71DD695762A55', 1, NULL, NULL, 2, NULL, 0, 'Next, add in all remaining ingredients.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C25EF931C3DD8B49BB524E09823C429A', X'68682879D35C6C419DB71DD695762A55', X'68682879D35C6C419DB71DD695762A55', 2, NULL, NULL, 2, NULL, 0, 'Cook until all the cheese has melted, stirring often.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'575164B08EF85F4DB567C13B623C0E6A', X'68682879D35C6C419DB71DD695762A55', X'68682879D35C6C419DB71DD695762A55', 3, NULL, NULL, 2, NULL, 0, 'Once everything is well incorporated and mixed together, let simmer for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07D83E3D22775A4E8040305E58EA4B64', X'68682879D35C6C419DB71DD695762A55', X'68682879D35C6C419DB71DD695762A55', 4, NULL, NULL, 2, NULL, 0, 'Remove, pour onto a plate, and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA2368D08ECA914AB3E5BAF0A99AEC08', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 0, NULL, NULL, 2, NULL, 0, 'We''ll make the syrup first, as its cool syrup is poured over hot galaktoboureko.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34CEA5E15916084A980AFB599505440B', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 1, NULL, NULL, 2, NULL, 0, 'In a pot, add: sugar, water, and lemon juice, mix well. Bring to boil over medium heat, stir occasionally.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF99C3E9BD2D8D4A81D12B03AA8D620B', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 2, NULL, NULL, 2, NULL, 0, 'Once boiling, add in lemon and orange peels - simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'523D3893D4D4B04683AECE4843D39C69', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 3, NULL, NULL, 2, NULL, 0, 'When simmering time is done, turn off and remove from heat to allow to cool.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0893ED9C2E5668408DC5288F44F8FB9D', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 4, NULL, NULL, 2, NULL, 0, '- Now, let’s start on the custard for galaktoboureko-');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33DF2FFFC97864448B74D27CD7BD57B5', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 5, NULL, NULL, 2, NULL, 0, 'In a large pot, add milk and bring to slight simmer then reduce heat to medium.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56B03C91CC60384E92D9F56B4555781A', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 6, NULL, NULL, 2, NULL, 0, 'As the milk heats, in a bowl, beat eggs and then add sugar, vanilla, and lemon zest, mix thoroughly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'020C3FF4F2C8084BAFD4A9144C21188B', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 7, NULL, NULL, 2, NULL, 0, 'When the milk is at a slight simmer, take 1 cup worth of it and slowly add it to bowl with the egg/sugar mixture--stirring entire time (this is to temper eggs), and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'758F9BA3B6549F4AA02C1158E18C4E6B', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 8, NULL, NULL, 2, NULL, 0, 'Now, grab your semolina and slowly add it to the pot with milk, stirring the entire time. Do this in stages. Add a little semolina, stir, little more, stir, etc..');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91A30DB5038F5D40A6F6FEFB262DB065', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 9, NULL, NULL, 2, NULL, 0, 'Once all semolina is incorporated into the milk, add in egg/sugar mixture that was tempered earlier, stir until it begins to thicken.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3B385C2EC2C864C8EAE1C9C460C0839', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 10, NULL, NULL, 2, NULL, 0, 'Once the galaktoboureko custard gets thick (think cake batter), turn off and remove from heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED1524063AF7D74DA5D80103F2E73CC6', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 11, NULL, NULL, 2, NULL, 0, '--Grab your phyllo and let’s assemble the galaktoboureko--');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88A61D46FF9E2B4ABACAD8FC227303B3', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 12, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3994B97510ED0D4A8580BBDC35414CA7', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 13, NULL, NULL, 2, NULL, 0, 'Unwrap phyllo, unroll and cover with a damp towel.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8261651969E78F4F97F353C764676AC7', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 14, NULL, NULL, 2, NULL, 0, 'Take your baking dish (we use 9 X 9) and brush with some melted butter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F496EB0B59D6F44D91A225DAD45FE2BA', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 15, NULL, NULL, 2, NULL, 0, 'Take one sheet of phyllo and place it in the dish (you may need to cut them to make them fit).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72A3643FD663A540BBD1708739527F97', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 16, NULL, NULL, 2, NULL, 0, 'Brush top of phyllo with some melted butter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'761E2499FED7954997F560B3BC5190CC', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 17, NULL, NULL, 2, NULL, 0, 'Add another sheet on top, brush with butter -- repeat this 5 more times (total of 7 sheets: phyllo, melted butter, phyllo, etc.,)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3668D689DEB1F45AA0777BCD3EC3539', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 18, NULL, NULL, 2, NULL, 0, 'After the 7th sheet, pour the custard over the top of the phyllo sheets, and spread evenly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5862798859D23A4DA63987ED7D2E327A', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 19, NULL, NULL, 2, NULL, 0, 'Repeat step 17-18 with 7 more phyllo sheets.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09F4B1BD2DD2E8489C57828DF2BA00D1', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 20, NULL, NULL, 2, NULL, 0, 'Brush top sheet with some melted butter, slightly score, and place in oven for 30-40 minutes or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F60D649C6047F44C82CEFB8B474169DC', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 21, NULL, NULL, 2, NULL, 0, 'Once galaktoboureko is finished, remove from oven and immediately pour the cool syrup over the top. Let it sit to cool for at least 30-40 minutes, or until the sugar is absorbed.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A9A14744E23904E811B60053B0CE0F2', X'FEBF85BB310C124BBC64144DA8FE65E9', X'FEBF85BB310C124BBC64144DA8FE65E9', 22, NULL, NULL, 2, NULL, 0, 'Cut into squares and enjoy your freshly baked galaktoboureko!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD5100D2E4004B41BADAEF0044E651AE', X'94BD648CF99BD14E9C685705C0AD86E7', X'94BD648CF99BD14E9C685705C0AD86E7', 0, NULL, NULL, 2, NULL, 0, 'Add arugula to a medium sized serving bowl, top with rest of ingredients, except for the avocado and chia seeds. Line the ingredients up in rows for a pretty awesome presentation.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36FA34EF3CB58D4B88258ED6B010F376', X'94BD648CF99BD14E9C685705C0AD86E7', X'94BD648CF99BD14E9C685705C0AD86E7', 1, NULL, NULL, 2, NULL, 0, 'Top with avocado and chia seeds.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F51E82CCB5B254D8B532AF7B01D1F12', X'94BD648CF99BD14E9C685705C0AD86E7', X'94BD648CF99BD14E9C685705C0AD86E7', 2, NULL, NULL, 2, NULL, 0, 'For vinaigrette, blend all of the ingredients together in a food processor or Nutribullet and taste to adjust seasonings as preferred.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AA5A33AA8C06041A20E7A42718E3E88', X'94BD648CF99BD14E9C685705C0AD86E7', X'94BD648CF99BD14E9C685705C0AD86E7', 3, NULL, NULL, 2, NULL, 0, 'Drizzle The Valentine’s Day Salad with the homemade Raspberry Vinaigrette and serve. From our hearts to yours, Happy Valentine’s Day!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C106EF34B81E4F80E469A56EE9D079', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 0, NULL, NULL, 2, NULL, 0, 'Lightly brush one sheet of phyllo with olive oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E771F83E45395449B22A89CE85DD34A0', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 1, NULL, NULL, 2, NULL, 0, 'Place one piece of sliced feta at the bottom edge of the phyllo sheet.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'399CE709A8266C4DA35C6233AAAF5CC4', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 2, NULL, NULL, 2, NULL, 0, 'Fold in each side, covering the feta slice.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F55F920069524B46A90751DE7CBD0030', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 3, NULL, NULL, 2, NULL, 0, 'Now, fold up starting with the feta side until you completely wrap the sliced feta with the phyllo sheet, Brush each side with a bit more olive oil and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D066A6A65673445B0B0D68B51CA147E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 4, NULL, NULL, 2, NULL, 0, 'Repeat with the 2 other phyllo slices and feta slices.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C84D351A6BCC554C9632073E396B4B3F', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 5, NULL, NULL, 2, NULL, 0, 'Heat a pan over medium-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF3D785455F1A14C88DE864F5C0105D9', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 6, NULL, NULL, 2, NULL, 0, 'Once pan is hot, add each phyllo wrapped feta slice to pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07B99D2DBDAA584F8D55DC6694868914', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 7, NULL, NULL, 2, NULL, 0, 'Fry for 3-5 minutes on each side, or until golden brown on both sides.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D610D8EBCF37404680B58D867223794C', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 8, NULL, NULL, 2, NULL, 0, 'Place on paper towel to absorb excess oil and then place on serving plate.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD3263AE55BDC348B8DD9EBFCA35CEA2', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 9, NULL, NULL, 2, NULL, 0, 'Drizzle with honey and sprinkle with sesame seeds.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E1249276E7D764BAD8C8F74A6A670A6', X'DE8622C6AE4BC740AB8A95E72C19FD8E', X'DE8622C6AE4BC740AB8A95E72C19FD8E', 10, NULL, NULL, 2, NULL, 0, 'Enjoy your phyllo wrapped feta cheese with honey and sesame seeds appetizer!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FC455077988BF4887D4DDCD76221F2F', X'00EF56107BC9A244A8F0EF6DC1720A52', X'00EF56107BC9A244A8F0EF6DC1720A52', 0, NULL, NULL, 2, NULL, 0, '*First, we need to blanch the almonds so we can easily remove their skin.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'930A0CCDF3060843AFEF1E5EE8D05AB9', X'00EF56107BC9A244A8F0EF6DC1720A52', X'00EF56107BC9A244A8F0EF6DC1720A52', 1, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil on high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05D52F1FA9145144A2C10B3231890583', X'00EF56107BC9A244A8F0EF6DC1720A52', X'00EF56107BC9A244A8F0EF6DC1720A52', 2, NULL, NULL, 2, NULL, 0, 'Add almonds and let boil for 60 seconds.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2D42DAC195B44438BD317E06E96B729', X'00EF56107BC9A244A8F0EF6DC1720A52', X'00EF56107BC9A244A8F0EF6DC1720A52', 3, NULL, NULL, 2, NULL, 0, 'Pour over strainer to catch almonds and rinse them under cold water to cool and stop cooking.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57CC4D3991A128469F497143B46FD1FF', X'00EF56107BC9A244A8F0EF6DC1720A52', X'00EF56107BC9A244A8F0EF6DC1720A52', 4, NULL, NULL, 2, NULL, 0, 'Remove the skin from the almonds. You’ll find you can more or less pinch them and they should slide right out.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6475A1E0BE0CE346BC7C137C6C40FAC9', X'00EF56107BC9A244A8F0EF6DC1720A52', X'00EF56107BC9A244A8F0EF6DC1720A52', 5, NULL, NULL, 2, NULL, 0, 'Dry with paper towel, should be as dry as possible.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33F691E19F9EA24EABCE626FA7B4CA58', X'00EF56107BC9A244A8F0EF6DC1720A52', X'00EF56107BC9A244A8F0EF6DC1720A52', 6, NULL, NULL, 2, NULL, 0, 'Add almonds and 2 tablespoons honey to a saucepan over medium-low heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E9019AB9C976242B967C4C5F39AD61A', X'00EF56107BC9A244A8F0EF6DC1720A52', X'00EF56107BC9A244A8F0EF6DC1720A52', 7, NULL, NULL, 2, NULL, 0, 'Cook until almonds get a little golden, and remove.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B933DB4724CFD41B2F64BB3EC3D13B8', X'00EF56107BC9A244A8F0EF6DC1720A52', X'00EF56107BC9A244A8F0EF6DC1720A52', 8, NULL, NULL, 2, NULL, 0, 'Pour half of almonds with honey into 2 small bowls, top with extra honey if desired.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FED7C750074E294FB4E7213B800EDF4C', X'00EF56107BC9A244A8F0EF6DC1720A52', X'00EF56107BC9A244A8F0EF6DC1720A52', 9, NULL, NULL, 2, NULL, 0, 'Let almonds and honey cool, about 10-15 minutes, and enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AF1491A13DD004F95ABD75D0F719A79', X'C0935D69665CFC498032786B74842C17', X'C0935D69665CFC498032786B74842C17', 0, NULL, NULL, 2, NULL, 0, 'Rub the top of each mushroom with the lemon half that you have reserved');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D04035427DA42E45A609B0B96B7780DA', X'C0935D69665CFC498032786B74842C17', X'C0935D69665CFC498032786B74842C17', 1, NULL, NULL, 2, NULL, 0, 'In a skillet on medium heat, add olive oil, mushrooms, garlic, peppercorns, thyme, bay leaf and sauté until mushrooms become fragrant and start to release some of their juices, 3-5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C84664B2EEF3FE4E8CBF8EAF9E68EC77', X'C0935D69665CFC498032786B74842C17', X'C0935D69665CFC498032786B74842C17', 2, NULL, NULL, 2, NULL, 0, 'Add water and lemon juice, sauté for another 2-3 minutes or until mushrooms are soft, but not mushy.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB1521660E10894383629F2F38CAF42E', X'C0935D69665CFC498032786B74842C17', X'C0935D69665CFC498032786B74842C17', 3, NULL, NULL, 2, NULL, 0, 'Top with dried parsley and serve as a side or on a salad once they have cooled. Enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6682A35E54F8E6428E4EC7EF7266E0C9', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C25DC9488D03A047A1458DC6A78836DA', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 1, NULL, NULL, 2, NULL, 0, 'Cook noodles per package instructions, making sure they are al dente.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19613543595269489A681611B057942C', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 2, NULL, NULL, 2, NULL, 0, 'While noodles are cooking, melt butter in frying pan on medium high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'428852D8D0A6964799211896DF02078F', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 3, NULL, NULL, 2, NULL, 0, 'Next, add in ground beef and onion - cook together until meat is browned and onions are softened.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBADFC9C31E1134188E2041DB7AC7850', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 4, NULL, NULL, 2, NULL, 0, 'Next, add tomato sauce and simmer for five minutes. When done, turn off heat and remove from burner.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C59A53560AAEBD4A9D208296AC998B76', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 5, NULL, NULL, 2, NULL, 0, 'Combine meat sauce and noodles in a bowl. Add in feta cheese and 1 egg, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92EE6498138EA14189E3ABBBF538BFE7', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 6, NULL, NULL, 2, NULL, 0, 'Pour mixture equally into a greased 6 tin muffin pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36A609A4F0F6B742A54EC9238815B884', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 7, NULL, NULL, 2, NULL, 0, 'Set aside and make Béchamel.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9FFFC4A8DE8304790DFB7CDE7DA88B0', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 8, NULL, NULL, 2, NULL, 0, 'Melt 2 tbsp butter in saucepan on medium heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D75E38C4B39A8F48BC8331D6206B1FB2', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 9, NULL, NULL, 2, NULL, 0, 'Add milk, and heat until warmed through - do not allow to simmer/boil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3FFA7A50C68B14BB62196F17C17FE7D', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 10, NULL, NULL, 2, NULL, 0, 'Slowly add semolina, mixing continuously. Add salt to taste if desired.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1B8A1457CA25A49A93D6A272F77EBB4', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 11, NULL, NULL, 2, NULL, 0, 'Once mixture gets thick, remove from heat (trick: try scooping some of the mixture up with a spoon and then drizzle it back into the pot. The mixture should fall slowly enough from the spoon but still have good movement. It should NOT get stuck to the spoon).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FF226DA9007B8458863723F2B214911', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 12, NULL, NULL, 2, NULL, 0, 'Pour some béchamel over the top of each pastitsio muffin.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18672CCA74545B45AB33AEAABF19CEB8', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 13, NULL, NULL, 2, NULL, 0, 'Evenly sprinkle feta cheese and cinnamon over each piece.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDA801DBB2B5B8418F4CA7D204EBEB17', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 14, NULL, NULL, 2, NULL, 0, 'Bake for 20-30 minutes, or until tip of feta becomes golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43A3553C86E5D5429168E1D86BD6AFE2', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 15, NULL, NULL, 2, NULL, 0, 'Let cool before serving.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9AC9E0B0FBDD849B8B18A2B1A47B30C', X'D2C0633B619EC745B6F0A6C44DFF3D2B', X'D2C0633B619EC745B6F0A6C44DFF3D2B', 16, NULL, NULL, 2, NULL, 0, 'Enjoy');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C68A5759E0615E469E3488FE545FB883', X'1DB0FAF9AC837040AEAB1D057F4C2CEE', X'1DB0FAF9AC837040AEAB1D057F4C2CEE', 0, NULL, NULL, 2, NULL, 0, 'Remove seeds/filling from the center of each cucumber. Slice and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55935E5DC5F983448992FFD0C7483681', X'1DB0FAF9AC837040AEAB1D057F4C2CEE', X'1DB0FAF9AC837040AEAB1D057F4C2CEE', 1, NULL, NULL, 2, NULL, 0, 'Take half of the filling you removed and dice, place in mixing bowl.');
