INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D19795907FF6FD4DA292671EB492F5C1', X'1DB0FAF9AC837040AEAB1D057F4C2CEE', X'1DB0FAF9AC837040AEAB1D057F4C2CEE', 3, NULL, NULL, 2, NULL, 0, 'Take Greek yogurt mixture and spoon into cucumber slices, spreading evenly.');
