INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B27B6F43324F74C8A281DAF90FC326C', X'1DB0FAF9AC837040AEAB1D057F4C2CEE', X'1DB0FAF9AC837040AEAB1D057F4C2CEE', 5, NULL, NULL, 2, NULL, 0, 'Serve as is or cut cucumber slices into thirds and serve.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'169A92D8EFBC0845B0AC8C70FCFC005C', X'C1B8194AD3E6C44FAF542DDECEDD8980', X'C1B8194AD3E6C44FAF542DDECEDD8980', 0, NULL, NULL, 2, NULL, 0, 'Add instant coffee, sugar, and 1 tablespoon water to a container.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA211C1E379BFC498160729C8299C58D', X'C1B8194AD3E6C44FAF542DDECEDD8980', X'C1B8194AD3E6C44FAF542DDECEDD8980', 1, NULL, NULL, 2, NULL, 0, 'Seal and shake vigorously for 20-30 seconds or until a nice foam develops.');
