INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'775D15975BCCF345B7105AC0449FF7CE', X'C1B8194AD3E6C44FAF542DDECEDD8980', X'C1B8194AD3E6C44FAF542DDECEDD8980', 3, NULL, NULL, 2, NULL, 0, 'Add some water to container, tighten lid, shake a few times to gather rest of coffee.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B88929D3DB98094882A9050F13F63C08', X'C1B8194AD3E6C44FAF542DDECEDD8980', X'C1B8194AD3E6C44FAF542DDECEDD8980', 4, NULL, NULL, 2, NULL, 0, 'Empty into glass with coffee foam.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'545B8E630C634745B95AD9D1226B1A5E', X'C1B8194AD3E6C44FAF542DDECEDD8980', X'C1B8194AD3E6C44FAF542DDECEDD8980', 5, NULL, NULL, 2, NULL, 0, 'Add a splash of milk and fill rest of glass with water, mix.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FA960B063DE6348AFE6A3C82E174E3D', X'C1B8194AD3E6C44FAF542DDECEDD8980', X'C1B8194AD3E6C44FAF542DDECEDD8980', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'157548A16D33AE44ACFF2D092656A730', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 0, NULL, NULL, 2, NULL, 0, '-Artichoke Dip-');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECB2A32FCF8CF1459D2C6381DD4D7209', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 1, NULL, NULL, 2, NULL, 0, 'Add diced jalapeño to medium saucepan over medium-high heat, stirring occasionally until fragrant and a bit softer in texture.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB447E6CFF595A4D872B6A66E8874C23', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 2, NULL, NULL, 2, NULL, 0, 'Add remaining ingredients to the pan. Cook until cheese has melted, stirring often.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D05C8314247504408525BE7A619167BC', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 3, NULL, NULL, 2, NULL, 0, 'Once everything has blended together and has become easier to stir, allow to slightly bubble for a few minutes then remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A0AD9AC5F978242965E3DBBB1BB0261', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 4, NULL, NULL, 2, NULL, 0, '-Cod-');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D7D165679805B47B3BFCF7C3D3C7556', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 5, NULL, NULL, 2, NULL, 0, 'Heat olive oil large skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2174874652761A48A28D117CF7A79A15', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 6, NULL, NULL, 2, NULL, 0, 'Add cod to pan and cook for 4-5 minutes, then flip and cook for another 4-5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3932D53B48A4944B43D98F5F45AD040', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 7, NULL, NULL, 2, NULL, 0, 'When cod is finished, remove and plate.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01FF6136B10223419B70F7BFFBD175E1', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 8, NULL, NULL, 2, NULL, 0, 'Now, take a spoonful (or as much as you’d like!) of the spinach artichoke spread and place it over the top of each cod.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17F54998196C7949986C4CE20437703D', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle kalamata olives over each piece of fish.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99D7DEFCAF1FF64999AC3B2DFA506C60', X'3B7A1B37AB362B46970B5D4A0BC99ACB', X'3B7A1B37AB362B46970B5D4A0BC99ACB', 10, NULL, NULL, 2, NULL, 0, 'Serve with your favorite side.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'767944F745A6C24D8D0104AED3B0E2F9', X'A38FE4CF5ADEE1448141EE08E34BB1F4', X'A38FE4CF5ADEE1448141EE08E34BB1F4', 0, NULL, NULL, 2, NULL, 0, 'Place everything on a plate and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4034F4686755A40A8DA82FE5861BBD0', X'E7DF7004D7FFCB40B0526AFCDDB84633', X'E7DF7004D7FFCB40B0526AFCDDB84633', 0, NULL, NULL, 2, NULL, 0, 'Add ice to a tall glass.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECC322F2BFAF3943AE2058B654459BF4', X'E7DF7004D7FFCB40B0526AFCDDB84633', X'E7DF7004D7FFCB40B0526AFCDDB84633', 1, NULL, NULL, 2, NULL, 0, 'Add in lemon juice, Metaxa, and ouzo, mix.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'364F2D0D0496274FB35F341801E50C76', X'E7DF7004D7FFCB40B0526AFCDDB84633', X'E7DF7004D7FFCB40B0526AFCDDB84633', 2, NULL, NULL, 2, NULL, 0, 'Fill glass with ginger ale (you can add less if you prefer), mix.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'828D0AFABF713343A89BB4990A57C213', X'E7DF7004D7FFCB40B0526AFCDDB84633', X'E7DF7004D7FFCB40B0526AFCDDB84633', 3, NULL, NULL, 2, NULL, 0, 'Garnish with lemon slice.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5918C57DD38BD4408C6BE5FCC9263C3F', X'E7DF7004D7FFCB40B0526AFCDDB84633', X'E7DF7004D7FFCB40B0526AFCDDB84633', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5640D4D96BDDB498E120BC975FD59D4', X'ECAC650717A3844296FD50F9C61DE228', X'ECAC650717A3844296FD50F9C61DE228', 0, NULL, NULL, 2, NULL, 0, 'Add the kale to a mixing bowl.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5D9017AFEE36A498363EEA2B507F83B', X'ECAC650717A3844296FD50F9C61DE228', X'ECAC650717A3844296FD50F9C61DE228', 1, NULL, NULL, 2, NULL, 0, 'Add in all remaining ingredients, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52892F8AA48AE740A2C9F4918C21D5C2', X'ECAC650717A3844296FD50F9C61DE228', X'ECAC650717A3844296FD50F9C61DE228', 2, NULL, NULL, 2, NULL, 0, 'Plate and enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C31F525BB40A54D991673410B0315B0', X'F37308AAD2A35C4FBA622D96DBB036FE', X'F37308AAD2A35C4FBA622D96DBB036FE', 0, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, combine the following: feta, gouda, cheddar, eggs, panko bread crumbs, milk and pepper, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F1B99B24798FA48AB394361001A2014', X'F37308AAD2A35C4FBA622D96DBB036FE', X'F37308AAD2A35C4FBA622D96DBB036FE', 1, NULL, NULL, 2, NULL, 0, 'Once everything is thoroughly mixed, cover the bowl with plastic wrap (cling film) and place in the refrigerator for 30 minutes, or until mixture becomes a bit firm.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE3952FEAD29D441BC14D5774B7C185A', X'F37308AAD2A35C4FBA622D96DBB036FE', X'F37308AAD2A35C4FBA622D96DBB036FE', 2, NULL, NULL, 2, NULL, 0, 'When the cheese mixture is ready, add some flour to a bowl. This is what we’ll use to coat each cheese ball.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A0F0C143E5D524BA0CB90E33C6CB730', X'F37308AAD2A35C4FBA622D96DBB036FE', X'F37308AAD2A35C4FBA622D96DBB036FE', 3, NULL, NULL, 2, NULL, 0, 'Next, heat the vegetable oil in a pan. Once that’s nice and hot, we are good to go.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1078C905DE2CE34F9771BD2EBDAD0386', X'F37308AAD2A35C4FBA622D96DBB036FE', X'F37308AAD2A35C4FBA622D96DBB036FE', 4, NULL, NULL, 2, NULL, 0, 'Grab about a spoonful of the cheese mixture and roll it into a ball. Use more or less to achieve desired size.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31E5D5FF127B1A4F9CCF72EAD3B6471A', X'F37308AAD2A35C4FBA622D96DBB036FE', X'F37308AAD2A35C4FBA622D96DBB036FE', 5, NULL, NULL, 2, NULL, 0, 'Roll the cheese ball in the flour bowl to evenly coat and shake off excess and place in pan. Repeat until all cheese mixture has been used. Make sure not to overcrowd your pan, so fry in stages if needed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87F5B923A81A2548B57A126A88EFF5DA', X'F37308AAD2A35C4FBA622D96DBB036FE', X'F37308AAD2A35C4FBA622D96DBB036FE', 6, NULL, NULL, 2, NULL, 0, 'Cook evenly for 3-5 minutes, rotating each ball. They’re done when they are a nice light brown/golden color.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BD534892CFCD04D93ED83B7F7E8B582', X'F37308AAD2A35C4FBA622D96DBB036FE', X'F37308AAD2A35C4FBA622D96DBB036FE', 7, NULL, NULL, 2, NULL, 0, 'When they are done, remove with a slotted spoon and place on plate lined with a paper towel to absorb excess oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B653D58D38CACB44895ED80D6EC99253', X'F37308AAD2A35C4FBA622D96DBB036FE', X'F37308AAD2A35C4FBA622D96DBB036FE', 8, NULL, NULL, 2, NULL, 0, 'Squeeze a little lemon juice over (option) and serve hot!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB058B766837414BA7B95F6AF70800EA', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 0, NULL, NULL, 2, NULL, 0, 'Take each onion half and slice vertically about 4-5 times.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98AB6D3E9B02E945898AC685B7A6221F', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 1, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a large pan over medium-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4619F2E2478C414C8A99304D05B20142', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 2, NULL, NULL, 2, NULL, 0, 'Once pan is hot, add in onions, and cook for 7-10 minutes. You want them to be soft and have a nice color.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABE77B15BCE51C469EDE9D0B16F6C600', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 3, NULL, NULL, 2, NULL, 0, 'Next, add in tomato paste, salt, pepper, and paprika, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D20FBAB327B0114EA3D44566BDCAC1A5', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 4, NULL, NULL, 2, NULL, 0, 'Cook for 5-10 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70017878ACC72A47BF5BF031A97A0AE7', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 5, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40AE2BBBDDF599489BFC4589AE9A7147', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 6, NULL, NULL, 2, NULL, 0, 'Line a 9 x 9 baking dish with some olive oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'391E5A8D48E6ED4FB4B08584FA4DAEE1', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 7, NULL, NULL, 2, NULL, 0, 'Add one puff pastry sheet to bottom of pan, you may need to trim to line pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31578D7EC74ECB418D2CFC662991B791', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 8, NULL, NULL, 2, NULL, 0, 'When onions are done, pour onion mixture over pastry sheet.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00CD85CF28CEAC42A4B466DF61BF6FBB', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 9, NULL, NULL, 2, NULL, 0, 'Add remaining pastry sheet over top of onions, again trimming to fit top.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FA66F3904889F448FF01F1F7C53DCFD', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 10, NULL, NULL, 2, NULL, 0, 'Score with a knife diagonally 3-4 times.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'123FCA27F289534F9902DF20580CBD24', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 11, NULL, NULL, 2, NULL, 0, 'Place in oven for 35-45 minutes, or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD2A2493B845054DABDB1358E0775FF8', X'0E152F7AB881D94DA0820664E0F98395', X'0E152F7AB881D94DA0820664E0F98395', 12, NULL, NULL, 2, NULL, 0, 'Remove, let cool a few minutes, slice and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E71D413E2F7D64096B4B4E5F3183C27', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, add flour, yeast, and warm water, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73A9D8BBEC6DFA49B26701560187248D', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 1, NULL, NULL, 2, NULL, 0, 'Add in salt, sugar, milk, and eggs, mix until thick batter is formed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA17794AEDA68144813DF181A98C6970', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 2, NULL, NULL, 2, NULL, 0, 'Cover and let rest for minutes 60 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65E41EFA0C991F4E84591DE9B0F9E6FA', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 3, NULL, NULL, 2, NULL, 0, 'With about 20 minutes left of resting time, combine water and honey in pot and bring to simmer; continue simmering for 5 minutes. When done, remove from heat and empty into large bowl.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E150421E2CF0240BEFA3A605D7A614A', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 4, NULL, NULL, 2, NULL, 0, 'Add safflower oil to a soup pot and heat over medium-high.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69447D4CD7612F43964F04290737CF79', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 5, NULL, NULL, 2, NULL, 0, 'Once oil is hot, take 1 tablespoon of batter, form a ball, and gently drop it into the hot oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3097EC652E9AC468B5AE68E31939693', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 6, NULL, NULL, 2, NULL, 0, 'Fry for 3 to 4 minutes on each side - should be a nice golden brown color.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CC32DFC79962743942578C6DB44CCA9', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 7, NULL, NULL, 2, NULL, 0, 'Remove and place on plate lined with a paper towel to absorb excess oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B47328AD9C59964581638FB5FF49E139', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 8, NULL, NULL, 2, NULL, 0, 'Repeat until all batter is used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'611F37965E19EE4D8E955995287FA49A', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 9, NULL, NULL, 2, NULL, 0, 'After excess oil is removed, add balls to syrup bowl and toss to coat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98A7DD4248141442B4FD4B10E2E5EA52', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 10, NULL, NULL, 2, NULL, 0, 'Place honey soaked loukoumades on plate and top with cinnamon and nuts if using.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C013E373AAD4A49B82FFA1323142C47', X'7DE79895702CCC49966C35E0E4FA3770', X'7DE79895702CCC49966C35E0E4FA3770', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47B636D312AECC4EAF990164BF2444CF', X'1E4D7557DE67E44F9661C64EDB326582', X'1E4D7557DE67E44F9661C64EDB326582', 0, NULL, NULL, 2, NULL, 0, 'Brown bread to your liking in toaster oven/toaster.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E79A371E53155A4A95D2BFAEE637813C', X'1E4D7557DE67E44F9661C64EDB326582', X'1E4D7557DE67E44F9661C64EDB326582', 1, NULL, NULL, 2, NULL, 0, 'While baking the bread, add the avocado, paprika, salt, pepper, and lemon juice in a small bowl, and mash.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'480DBAC02155B34DA6DABBF7AE3835B7', X'1E4D7557DE67E44F9661C64EDB326582', X'1E4D7557DE67E44F9661C64EDB326582', 2, NULL, NULL, 2, NULL, 0, 'When toast is ready, spread avocado mixture over each each slice.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1078FAEABA8DF6438A43DD4FC4CA0C9B', X'1E4D7557DE67E44F9661C64EDB326582', X'1E4D7557DE67E44F9661C64EDB326582', 3, NULL, NULL, 2, NULL, 0, 'Top with feta and cherry tomatoes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'884BA945B924C24BB6DE1749D4960147', X'1E4D7557DE67E44F9661C64EDB326582', X'1E4D7557DE67E44F9661C64EDB326582', 4, NULL, NULL, 2, NULL, 0, 'Enjoy your Avocado And Feta Toast!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9EF599E75A824489192CF863FBA5322', X'BC715EB2F1247046BBCBCE14A43A8F7D', X'BC715EB2F1247046BBCBCE14A43A8F7D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25DD90615D4C364DA05E5570346DEE45', X'BC715EB2F1247046BBCBCE14A43A8F7D', X'BC715EB2F1247046BBCBCE14A43A8F7D', 1, NULL, NULL, 2, NULL, 0, 'Lightly oil the bottom of a six cup muffin pan. Dust muffin cups with flour using a sifter or just sprinkle some on with your hands.**');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'710242330290084FACA1AFD2FAB09367', X'BC715EB2F1247046BBCBCE14A43A8F7D', X'BC715EB2F1247046BBCBCE14A43A8F7D', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, beat the egg whites with ¼ cup of sugar until well mixed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF4934F12AAC574BACABB3C5B7615F47', X'BC715EB2F1247046BBCBCE14A43A8F7D', X'BC715EB2F1247046BBCBCE14A43A8F7D', 3, NULL, NULL, 2, NULL, 0, 'In a separate medium sized bowl, beat egg yolks with the remaining sugar. Now add your small bowl of egg white mixture into your egg yolk mixture along with the rest of the torte ingredients. Mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6E229A99597B543AC0312D5D31082F3', X'BC715EB2F1247046BBCBCE14A43A8F7D', X'BC715EB2F1247046BBCBCE14A43A8F7D', 4, NULL, NULL, 2, NULL, 0, 'Pour mixture into the 6 cups as evenly as possible (should fill to just below halfway of each cup). Bake for 20 mins or until toothpick placed in center comes out clean.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52D52C4618681D43B12FB4C709023D46', X'BC715EB2F1247046BBCBCE14A43A8F7D', X'BC715EB2F1247046BBCBCE14A43A8F7D', 5, NULL, NULL, 2, NULL, 0, 'With 5 minutes left on the tortes, boil all three ingredients for the syrup for about 2-3 minutes, stirring occasionally. Turn off heat and remove from burner. Let sit for a few moments.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC68C86AB348DD4C8B250F9A02E8994C', X'BC715EB2F1247046BBCBCE14A43A8F7D', X'BC715EB2F1247046BBCBCE14A43A8F7D', 6, NULL, NULL, 2, NULL, 0, 'When the tortes are ready, take the syrup and drizzle a little over each muffin. Let them soak up all of that sweet goodness and serve immediately.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE671E59B4537E4EAE403808C9C2A47F', X'A430FF6B031AF64EB2451ED8EB98912A', X'A430FF6B031AF64EB2451ED8EB98912A', 0, NULL, NULL, 2, NULL, 0, 'Add the olive oil to a bowl.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'205AACE5E720F245BF2A8F94E67B4D25', X'A430FF6B031AF64EB2451ED8EB98912A', X'A430FF6B031AF64EB2451ED8EB98912A', 1, NULL, NULL, 2, NULL, 0, 'Add in remaining ingredients.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF2A3786BFCAF84D952F4EA062E4EE29', X'A430FF6B031AF64EB2451ED8EB98912A', X'A430FF6B031AF64EB2451ED8EB98912A', 2, NULL, NULL, 2, NULL, 0, 'Mix and taste to adjust seasoning.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C594C2D667E5E4F96125F51F2E05535', X'A430FF6B031AF64EB2451ED8EB98912A', X'A430FF6B031AF64EB2451ED8EB98912A', 3, NULL, NULL, 2, NULL, 0, 'Drizzle over salad and enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54D8AB585CABC74BA720A399C6D9DA9A', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 0, NULL, NULL, 2, NULL, 0, 'Add oil to a large skillet and heat over medium-high');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C68A695A9B80A14488695256156CCDF4', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 1, NULL, NULL, 2, NULL, 0, 'Once hot, add in leeks, shallot, garlic, salt and pepper, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43F97488D0B10F458EF97885EE41C20A', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 2, NULL, NULL, 2, NULL, 0, 'Cook for about 10 minutes then remove and empty into bowl and set aside to cool for another 10 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87B1976E5FA4DC44A8D3DA6B0BA348BA', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'862D827C4AF03D4591F4C11FE13AC4E6', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 4, NULL, NULL, 2, NULL, 0, 'After the leek mixture has cooled a bit, add in dill, parsley, feta, parmesan, and eggs - mix everything together.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78B380AFD2793C448D8840B6477226C5', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 5, NULL, NULL, 2, NULL, 0, 'Brush a 9 x 9 baking dish with some melted butter');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85B6AA875004F540803A5621A62109E8', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 6, NULL, NULL, 2, NULL, 0, 'Cut phyllo to fit pan, and then add one sheet to baking dish and brush top with melted butter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'609C922A0C7F8C49BFA0F80A9B3BB424', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 7, NULL, NULL, 2, NULL, 0, 'Add another sheet and brush with more butter - repeat this 6 more times, so you have 8 sheets total.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1529BE920B254E4783042D5E49F35671', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 8, NULL, NULL, 2, NULL, 0, 'After the 8th phyllo sheet, empty the leek mixture into the pan, spreading it out evenly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4C3552A763FDD40852B59B365F8953D', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 9, NULL, NULL, 2, NULL, 0, 'Repeat the phyllo, butter, phyllo process like before until you have 8 sheets covering the leek mixture.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A482C394323ED48A6284055B32533AA', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 10, NULL, NULL, 2, NULL, 0, 'Score top and place in oven for 30 - 40 minutes, until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BCD2CAECAA5B847A6416883A3F73812', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 11, NULL, NULL, 2, NULL, 0, 'Once golden brown, remove and let cool slightly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D43B54DDBAF0614492874CDBC60A5197', X'631FF4063BC64B44B4CFCF5EE75B1C44', X'631FF4063BC64B44B4CFCF5EE75B1C44', 12, NULL, NULL, 2, NULL, 0, 'Cut and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31EC6E7C9CC83E4EA00A27F329939FE3', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 0, NULL, NULL, 2, NULL, 0, 'Add olive oil to pan and heat over medium-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FE3BD9D656741468C4EE67334ED8916', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 1, NULL, NULL, 2, NULL, 0, 'Add onions to hot pan and cook until soft and translucent, about 5 minutes.');
