INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8549024F749F4C4BB33DCE3E2F12AB4A', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 3, NULL, NULL, 2, NULL, 0, 'Add in oregano, dill, pepper, a little salt, and tomato paste, mix.');
