INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E33D19BA089A640B4FF120F062BA732', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 5, NULL, NULL, 2, NULL, 0, 'Add chicken broth and reduce.');
