INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F75D0AB383B244C929E8FB1D37224A4', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 7, NULL, NULL, 2, NULL, 0, 'Empty into bowl, add feta and egg - mix everything together.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'812008E774A42B43996361301FCF481D', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 8, NULL, NULL, 2, NULL, 0, 'Set meat aside and prepare baking dish.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFED2EAB3F485844B389F1CEACA830DB', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 9, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39A78C71C6406748A868EE3B195AD18C', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 10, NULL, NULL, 2, NULL, 0, 'Brush a square baking pan with some melted butter**');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CB34514FAB8AF47AE745F72D3FB67C3', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 11, NULL, NULL, 2, NULL, 0, 'Unroll the phyllo sheets and lay them flat on the plastic.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27F4798D0FAEA341A506BF5B13D04EDD', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 12, NULL, NULL, 2, NULL, 0, 'Cut the sheets to match the size of the pan you’re using.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C699CFA3411B443B0B611A167E152E5', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 13, NULL, NULL, 2, NULL, 0, 'Take one phyllo sheet and lay it in the baking pan.***');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3808B87A18291A4ABA466D190310D775', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 14, NULL, NULL, 2, NULL, 0, 'Brush with some melted butter and lay another sheet of phyllo down.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47D7EC6899744447A138CCF6980FD251', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 15, NULL, NULL, 2, NULL, 0, 'Brush that with butter and repeat 6 more times (phyllo sheet, butter, phyllo sheet).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5114201C2B2494E837948A5F0CACB07', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 16, NULL, NULL, 2, NULL, 0, 'After the 8th phyllo sheet, pour the meat mixture into the pan, and smooth it out evenly with a spoon.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAEA9C202B51BB4D8962500470846F32', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 17, NULL, NULL, 2, NULL, 0, 'Now, repeat steps 14 -15, placing 8 more phyllo sheets over the meat mixture, spreading melted butter in between each sheet.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'854400E05F4F9A469E2F68BCF2034D88', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 18, NULL, NULL, 2, NULL, 0, 'When done, score top of Kreatopita and place in oven for 20-30 minutes, or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F86A490A66F2BB42A85A72BF282A1E18', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', X'2A6EF4E4B4B9CA4EAC12AC18BDBF70B1', 19, NULL, NULL, 2, NULL, 0, 'Remove, cut, and serve hot!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBD3659B7036F2449D1920586B1E0574', X'FF12CBB8FDB7A645AC89C2CF21753367', X'FF12CBB8FDB7A645AC89C2CF21753367', 0, NULL, NULL, 2, NULL, 0, 'Heat grapeseed oil in pan over medium-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCF2C4A9861AA149BB39C54AB3B6C4B9', X'FF12CBB8FDB7A645AC89C2CF21753367', X'FF12CBB8FDB7A645AC89C2CF21753367', 1, NULL, NULL, 2, NULL, 0, 'Add green pepper (or any other peppers you’ll be using) to pan and fry until they get a little black and soft, about 5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C75D53EBCD6B2A40BB0A22D409FBB2EC', X'FF12CBB8FDB7A645AC89C2CF21753367', X'FF12CBB8FDB7A645AC89C2CF21753367', 2, NULL, NULL, 2, NULL, 0, 'Remove pepper(s) and place in food processor.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'050BC036DAAB1844A38261153C30BDEE', X'FF12CBB8FDB7A645AC89C2CF21753367', X'FF12CBB8FDB7A645AC89C2CF21753367', 3, NULL, NULL, 2, NULL, 0, 'Add feta, Greek yogurt, garlic powder, chili powder, lemon juice, and pepper to food processor as well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBDC8ECE7E2E4C4EBEF9E2ECD0BF613F', X'FF12CBB8FDB7A645AC89C2CF21753367', X'FF12CBB8FDB7A645AC89C2CF21753367', 4, NULL, NULL, 2, NULL, 0, 'Turn on and blend everything together. While blending, slowly pour in enough olive oil to achieve a dip like consistency. About 3 tablespoons or so.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F70E58C610C7C54CA728DE8C86DE1416', X'FF12CBB8FDB7A645AC89C2CF21753367', X'FF12CBB8FDB7A645AC89C2CF21753367', 5, NULL, NULL, 2, NULL, 0, 'Once you’re happy with consistency, remove and taste. Adjust any seasonings as necessary and re-blend if needed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EAFFF776A906E40A21277CC2EA06E22', X'FF12CBB8FDB7A645AC89C2CF21753367', X'FF12CBB8FDB7A645AC89C2CF21753367', 6, NULL, NULL, 2, NULL, 0, 'When done, pour into bowl and serve with pita, vegetables, or whatever you like!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4DA6B4D174BD44DB65D4E4B66033BBF', X'AD4BF16C09D12D478DAC617B1D2B073D', X'AD4BF16C09D12D478DAC617B1D2B073D', 0, NULL, NULL, 2, NULL, 0, 'The first thing we need to do is heat the peppers so they blister up and get a little black. You can do this by placing them in the oven under the broiler, on the BBQ, on a gas stove burner, or using a hand torch. Use whatever method is easiest for you.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C68E5571828C84585B4433E3284C82D', X'AD4BF16C09D12D478DAC617B1D2B073D', X'AD4BF16C09D12D478DAC617B1D2B073D', 1, NULL, NULL, 2, NULL, 0, 'Allow them to cool.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BCD46CAAEDB6A4F83FA9EA9AF26A107', X'AD4BF16C09D12D478DAC617B1D2B073D', X'AD4BF16C09D12D478DAC617B1D2B073D', 2, NULL, NULL, 2, NULL, 0, 'Cut the chili peppers in half and remove the seeds*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEE1AC7ACB486B49AEADFA3F6C6BBE5B', X'AD4BF16C09D12D478DAC617B1D2B073D', X'AD4BF16C09D12D478DAC617B1D2B073D', 3, NULL, NULL, 2, NULL, 0, 'In a food processor, add in the garlic, chili peppers, and olive oil, blend to a paste.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A768580227A8AE408E9DADE1130B4AD5', X'AD4BF16C09D12D478DAC617B1D2B073D', X'AD4BF16C09D12D478DAC617B1D2B073D', 4, NULL, NULL, 2, NULL, 0, 'Add in feta and blend everything to a paste, you may need to add some extra olive oil to help it achieve the right consistency.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'112EBFF9C8A72D4EAAEC084711209AB7', X'AD4BF16C09D12D478DAC617B1D2B073D', X'AD4BF16C09D12D478DAC617B1D2B073D', 5, NULL, NULL, 2, NULL, 0, 'Taste and add more peppers/adjust as needed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFA7B1423A45BF4796CF15F47B1826FF', X'AD4BF16C09D12D478DAC617B1D2B073D', X'AD4BF16C09D12D478DAC617B1D2B073D', 6, NULL, NULL, 2, NULL, 0, 'Once you''re satisfied, scoop out onto plate and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EAD3343EA591A47B44B37F6B19B2080', X'649A87F04392DD4AABEEF3DE6D3B1187', X'649A87F04392DD4AABEEF3DE6D3B1187', 0, NULL, NULL, 2, NULL, 0, 'Heat a soup pot over medium-high heat and add olive oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6659E3D67499C440B30729DF55E5F94E', X'649A87F04392DD4AABEEF3DE6D3B1187', X'649A87F04392DD4AABEEF3DE6D3B1187', 1, NULL, NULL, 2, NULL, 0, 'Once hot, add onion, shallots, and garlic. Cook until onions/shallots are soft, about 5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'325947E69706B7478D262DCF7EDFFFA6', X'649A87F04392DD4AABEEF3DE6D3B1187', X'649A87F04392DD4AABEEF3DE6D3B1187', 2, NULL, NULL, 2, NULL, 0, 'Now, add octopus. Cover and reduce heat to low. Let octopus cook until soft and a little chewy, about 30 minutes. The octopus should expel a lot of water and shrink down in size. Don’t worry, this is good.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3DD557C77F0D24BA5392BFB53419FC0', X'649A87F04392DD4AABEEF3DE6D3B1187', X'649A87F04392DD4AABEEF3DE6D3B1187', 3, NULL, NULL, 2, NULL, 0, 'After about 30 minutes, add in tomatoes, ouzo, bay leaf, and oregano.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F92827BFF144704E9D69B30731F61B66', X'649A87F04392DD4AABEEF3DE6D3B1187', X'649A87F04392DD4AABEEF3DE6D3B1187', 4, NULL, NULL, 2, NULL, 0, 'Cook slightly covered for another 35 minutes, or until octopus is tender.**');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3695E50311E23F42AABE89B88EF3547C', X'649A87F04392DD4AABEEF3DE6D3B1187', X'649A87F04392DD4AABEEF3DE6D3B1187', 5, NULL, NULL, 2, NULL, 0, 'During last few minutes of cooking, add kalamata olives.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51B91AB485E4CA409FD51E6EFC9FD398', X'649A87F04392DD4AABEEF3DE6D3B1187', X'649A87F04392DD4AABEEF3DE6D3B1187', 6, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper to taste. Taste first, as it will be naturally salty from the octopus so adjust accordingly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'069F8FB4C82BC9488B9B652702C56E05', X'649A87F04392DD4AABEEF3DE6D3B1187', X'649A87F04392DD4AABEEF3DE6D3B1187', 7, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B03A79CEA18A5D4AA6280EA605CF88E3', X'56AE2BF17A03D445A33DE7FFC54C6D3B', X'56AE2BF17A03D445A33DE7FFC54C6D3B', 0, NULL, NULL, 2, NULL, 0, 'Add sliced cucumbers to bowl.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85A792E3676D064FB5E9CF5BC7DE1222', X'56AE2BF17A03D445A33DE7FFC54C6D3B', X'56AE2BF17A03D445A33DE7FFC54C6D3B', 1, NULL, NULL, 2, NULL, 0, 'Add lemon juice and some salt and pepper - mix');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'125020B54E73A844A7CEAFFE50E668B8', X'56AE2BF17A03D445A33DE7FFC54C6D3B', X'56AE2BF17A03D445A33DE7FFC54C6D3B', 2, NULL, NULL, 2, NULL, 0, 'Drizzle with olive oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'400097873F04D74BA753869D7F6B06E6', X'56AE2BF17A03D445A33DE7FFC54C6D3B', X'56AE2BF17A03D445A33DE7FFC54C6D3B', 3, NULL, NULL, 2, NULL, 0, 'Taste to adjust anything if needed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7AED07934EA854789E378BE057FC752', X'56AE2BF17A03D445A33DE7FFC54C6D3B', X'56AE2BF17A03D445A33DE7FFC54C6D3B', 4, NULL, NULL, 2, NULL, 0, 'Serve.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88D1E7E0D7420846A8C0F7C79287F9B9', X'97DEC230E69A914686FA2907845CE221', X'97DEC230E69A914686FA2907845CE221', 0, NULL, NULL, 2, NULL, 0, 'Empty olives from jar into a strainer. Give a quick rinse to remove brine.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B344D2ABF7BE264AA809015C25BD99AE', X'97DEC230E69A914686FA2907845CE221', X'97DEC230E69A914686FA2907845CE221', 1, NULL, NULL, 2, NULL, 0, 'We can use the jar the olives came in.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD4BE998C94B6B46976E6769A599901A', X'97DEC230E69A914686FA2907845CE221', X'97DEC230E69A914686FA2907845CE221', 2, NULL, NULL, 2, NULL, 0, 'Add the oregano, garlic, lemon zest, lemon, bay leaf, pepper, and olive oil to the jar - mix.');
