INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AC681710EB642429F1C0C8AFC47724A', X'ACF8279D535EE74AA85F50710BC03A7F', X'ACF8279D535EE74AA85F50710BC03A7F', 12, NULL, NULL, 2, NULL, 0, 'Place back in oven for 1hr, check center to see if done to your liking - may need 30 min or so more depending on preference (see below for meat temperatures).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'929671D36D465C4188C5B688B32E3FC4', X'42205F6CC191AF4EAF55A3877E2F111D', X'42205F6CC191AF4EAF55A3877E2F111D', 0, NULL, NULL, 2, NULL, 0, 'Peel bananas, and cut into bite size slices');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72B1F02A6D9F0545934C750601025888', X'42205F6CC191AF4EAF55A3877E2F111D', X'42205F6CC191AF4EAF55A3877E2F111D', 1, NULL, NULL, 2, NULL, 0, 'Peel orange, break into slices, and cut each slice into bite size pieces.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'925378B2E20CAC4F8E2A1264CB32AA3E', X'42205F6CC191AF4EAF55A3877E2F111D', X'42205F6CC191AF4EAF55A3877E2F111D', 2, NULL, NULL, 2, NULL, 0, 'Add half the banana slices to a bowl, had half orange pieces, repeat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7413037D1F53E04DB14ADBFCF1E18349', X'42205F6CC191AF4EAF55A3877E2F111D', X'42205F6CC191AF4EAF55A3877E2F111D', 3, NULL, NULL, 2, NULL, 0, 'Mix to coat bananas in orange juice');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75ACEFF6FECA5A4BA59B8F010F949B7A', X'42205F6CC191AF4EAF55A3877E2F111D', X'42205F6CC191AF4EAF55A3877E2F111D', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle sugar, mix, taste, add more sugar if desired.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D07D55879E17342A0E28FE84FF44742', X'42205F6CC191AF4EAF55A3877E2F111D', X'42205F6CC191AF4EAF55A3877E2F111D', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F16D30E17CE4224985195CB4A171A803', X'DDAAC872735C984BBC8A760BCA27A099', X'DDAAC872735C984BBC8A760BCA27A099', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine: flour, water, olive oil, lemon juice, brandy, and salt. Mix until dough ball is formed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9658C24D6E139E45823A06B7863FADC0', X'DDAAC872735C984BBC8A760BCA27A099', X'DDAAC872735C984BBC8A760BCA27A099', 1, NULL, NULL, 2, NULL, 0, 'Place dough ball on floured surface and knead for 10 minutes. If too wet/sticky, knead in some extra flour. You will probably need to do this. If too dry, add some water.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FB804B7D30D254F9BAA042B10770EED', X'DDAAC872735C984BBC8A760BCA27A099', X'DDAAC872735C984BBC8A760BCA27A099', 2, NULL, NULL, 2, NULL, 0, 'Once dough is kneaded, break into 5 equal parts and roll each piece into a ball.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D59841FF20BAD4C8D5A7039B37974C8', X'DDAAC872735C984BBC8A760BCA27A099', X'DDAAC872735C984BBC8A760BCA27A099', 3, NULL, NULL, 2, NULL, 0, 'Take one dough piece and roll it out flat into a pancake shape, about 6 inches width and ¼ inch thick.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'061D098AD29D4241962AEBEFCB372308', X'DDAAC872735C984BBC8A760BCA27A099', X'DDAAC872735C984BBC8A760BCA27A099', 4, NULL, NULL, 2, NULL, 0, 'Place a tablespoon (or more) of feta cheese in center, fold side over, and knead the dough and cheese together, until the cheese is well mixed in.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84D5A3EEF83B224FA7727D8AF88BC3ED', X'DDAAC872735C984BBC8A760BCA27A099', X'DDAAC872735C984BBC8A760BCA27A099', 5, NULL, NULL, 2, NULL, 0, 'Once the cheese is well incorporated, roll back out flat to the same pancake shape as in step #4, and set aside. Repeat with other dough pieces.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB81FAAE7F82A8418FE394657A9BD907', X'DDAAC872735C984BBC8A760BCA27A099', X'DDAAC872735C984BBC8A760BCA27A099', 6, NULL, NULL, 2, NULL, 0, 'Add a few tablespoons of olive oil to a pan, and heat over medium-high heat.');
