INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D4B1454464F04448E93A0858B0C1625', X'DDAAC872735C984BBC8A760BCA27A099', X'DDAAC872735C984BBC8A760BCA27A099', 9, NULL, NULL, 2, NULL, 0, 'When ready, sprinkle some of your favorite chopped nuts over each Sfakianopita. Sprinkle with some cinnamon, and drizzle with honey.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EB09E137C834F45AF4DE770361CD384', X'DDAAC872735C984BBC8A760BCA27A099', X'DDAAC872735C984BBC8A760BCA27A099', 10, NULL, NULL, 2, NULL, 0, 'Serve warm!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F07EAA759CEE64DBCACA9977C259C75', X'9C8CB8318534F94D81A6B3F68E5769FE', X'9C8CB8318534F94D81A6B3F68E5769FE', 0, NULL, NULL, 2, NULL, 0, 'In soup pot, melt butter over medium-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F1635F06410FD4AB1BF818843F76466', X'9C8CB8318534F94D81A6B3F68E5769FE', X'9C8CB8318534F94D81A6B3F68E5769FE', 1, NULL, NULL, 2, NULL, 0, 'Add lamb pieces, some salt and pepper, and brown on all sides.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4943CF2C7756C54094152ACD50821076', X'9C8CB8318534F94D81A6B3F68E5769FE', X'9C8CB8318534F94D81A6B3F68E5769FE', 2, NULL, NULL, 2, NULL, 0, 'Next add in, onions, oregano, garlic, and wine, cook until onions are soft, about 5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F907BA43EE81CF4287125ABE075D2F91', X'9C8CB8318534F94D81A6B3F68E5769FE', X'9C8CB8318534F94D81A6B3F68E5769FE', 3, NULL, NULL, 2, NULL, 0, 'Now add in tomato sauce, a little more salt and pepper, and enough hot water to cover meat (about 2 cups), Bring to boil, cover, and reduce heat to simmer for 1 hour.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E09BD1CBB73E2348AD6D713E81203B59', X'9C8CB8318534F94D81A6B3F68E5769FE', X'9C8CB8318534F94D81A6B3F68E5769FE', 4, NULL, NULL, 2, NULL, 0, 'With 10 minutes left of simmering, cut vegetables into bite size pieces.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB18226D782B9A4497BF70236495A555', X'9C8CB8318534F94D81A6B3F68E5769FE', X'9C8CB8318534F94D81A6B3F68E5769FE', 5, NULL, NULL, 2, NULL, 0, 'After the 1 hour, add in vegetables and an additional 2 cups of water, or enough to cover everything.');
