INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB49455AF2036741A3FCD295EF3FDB55', X'9C8CB8318534F94D81A6B3F68E5769FE', X'9C8CB8318534F94D81A6B3F68E5769FE', 7, NULL, NULL, 2, NULL, 0, 'Taste and add salt and pepper to preference.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A911AEBD372B1479F00030E4E12270B', X'9C8CB8318534F94D81A6B3F68E5769FE', X'9C8CB8318534F94D81A6B3F68E5769FE', 8, NULL, NULL, 2, NULL, 0, 'Serve!');
