INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0E47A5FD2B59745A5E9C52EE3D49C7A', X'129E0157652E6341BCABA938266F5434', X'129E0157652E6341BCABA938266F5434', 7, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450F.');
