INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B9F35F69A217B4D9B0853AE68F6788E', X'02A494A63206934A884DF7C15B966BD1', X'02A494A63206934A884DF7C15B966BD1', 0, NULL, NULL, 2, NULL, 0, 'Add olive oil to pan and heat over medium heat.');
