INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31D5B2F81A6C11499D30436F82586F43', X'E681873BFB150846B8438E62D2A8E8DC', X'E681873BFB150846B8438E62D2A8E8DC', 4, NULL, NULL, 2, NULL, 0, 'Add chicken stock, marjoram, salt, pepper, and lemon zest, simmer for 20 minutes.');
