INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DBB1A6EE225414CB6EF5CF7B600E9D8', X'E681873BFB150846B8438E62D2A8E8DC', X'E681873BFB150846B8438E62D2A8E8DC', 6, NULL, NULL, 2, NULL, 0, 'Use a hand blender to blend or pour soup into table blender and blend that way.');
