INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50036675DCD1724187502DCA1D04919E', X'58F214DD94BE8B48878C46B14D8091D7', X'58F214DD94BE8B48878C46B14D8091D7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'326CE05FB40A334B8443D8423FCBCA21', X'58F214DD94BE8B48878C46B14D8091D7', X'58F214DD94BE8B48878C46B14D8091D7', 1, NULL, NULL, 2, NULL, 0, 'Cut peppers in half, remove seeds, and wash.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AC79D83EE5FD1449664C1FE21B93ADF', X'58F214DD94BE8B48878C46B14D8091D7', X'58F214DD94BE8B48878C46B14D8091D7', 2, NULL, NULL, 2, NULL, 0, 'Brush outsides of each pepper with olive oil and place on baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D593C23BE0ABAC4E88F7F13F27652B33', X'58F214DD94BE8B48878C46B14D8091D7', X'58F214DD94BE8B48878C46B14D8091D7', 3, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, combine: goat cheese, parsley, lemon juice, lemon rind, garlic, dill, paprika, salt and pepper, mix well.');
