INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54142847B7DF5441A4A17B4E79AC3F22', X'58F214DD94BE8B48878C46B14D8091D7', X'58F214DD94BE8B48878C46B14D8091D7', 6, NULL, NULL, 2, NULL, 0, 'Repeat until all peppers are used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B46642990956843BCDEFC78FEA11BAC', X'58F214DD94BE8B48878C46B14D8091D7', X'58F214DD94BE8B48878C46B14D8091D7', 7, NULL, NULL, 2, NULL, 0, 'Place in oven for 30 minutes or until peppers are tender.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1A463A6852B1A41A63C2D1365F56E53', X'58F214DD94BE8B48878C46B14D8091D7', X'58F214DD94BE8B48878C46B14D8091D7', 8, NULL, NULL, 2, NULL, 0, 'Remove once done and serve hot.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85E8D2D82799754EBF44D4DBD7BCFE50', X'B56F67C9A206DD4CB82780C6530CB85E', X'B56F67C9A206DD4CB82780C6530CB85E', 0, NULL, NULL, 2, NULL, 0, 'Take each zucchini cut ends off, cut in half, cut lengthwise, and cut in half lengthwise again to create strips (see video above for demonstration).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E36CE6C1B43A1743986DCF3D13A51718', X'B56F67C9A206DD4CB82780C6530CB85E', X'B56F67C9A206DD4CB82780C6530CB85E', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle zucchini strips with salt.');
