INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC8B42C9849F9C419238FAD4DAF3EF87', X'B56F67C9A206DD4CB82780C6530CB85E', X'B56F67C9A206DD4CB82780C6530CB85E', 4, NULL, NULL, 2, NULL, 0, 'Add zucchini strips, and mix to coat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FFF63AE63B38142A165423B5F01F24E', X'B56F67C9A206DD4CB82780C6530CB85E', X'B56F67C9A206DD4CB82780C6530CB85E', 5, NULL, NULL, 2, NULL, 0, 'Cook for 4 - 5 minutes.');
