INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7D4EA15767EC54C82415F535B5C215F', X'B56F67C9A206DD4CB82780C6530CB85E', X'B56F67C9A206DD4CB82780C6530CB85E', 7, NULL, NULL, 2, NULL, 0, 'Mix one to two times during simmering.');
