INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0162551327C0A4EA57B37BE034905D3', X'B56F67C9A206DD4CB82780C6530CB85E', X'B56F67C9A206DD4CB82780C6530CB85E', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle oregano and thyme over zucchini.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60336BC36D02BD44BA435DA9892C4F0C', X'B56F67C9A206DD4CB82780C6530CB85E', X'B56F67C9A206DD4CB82780C6530CB85E', 10, NULL, NULL, 2, NULL, 0, 'Top with feta and serve hot!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97B6A71A68FD7844B609DD8661F51134', X'117BD969F56C6F45B2204287807BA0F0', X'117BD969F56C6F45B2204287807BA0F0', 0, NULL, NULL, 2, NULL, 0, 'Bring some water to a boil, add chicken breast and cook for 20 minutes, or until no longer pink in middle.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D143E45CD461FC48B5454D788233887B', X'117BD969F56C6F45B2204287807BA0F0', X'117BD969F56C6F45B2204287807BA0F0', 1, NULL, NULL, 2, NULL, 0, 'While chicken is cooking, bring a second pot of salted water to boil, add pasta shells and cook until al dente (about 11-13 minutes).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED896E4E1CAD4E4D8D7758F9A06EE895', X'117BD969F56C6F45B2204287807BA0F0', X'117BD969F56C6F45B2204287807BA0F0', 2, NULL, NULL, 2, NULL, 0, 'Once pasta and chicken are done, drain and set both aside to cool.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CC7B89F97BC1C4CB1CA3B61FC3A86F3', X'117BD969F56C6F45B2204287807BA0F0', X'117BD969F56C6F45B2204287807BA0F0', 3, NULL, NULL, 2, NULL, 0, 'While they are cooling, combine all ingredients listed under the filling section into a bowl.');
