INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6690375416D4C14E9A4B4A0816E00002', X'117BD969F56C6F45B2204287807BA0F0', X'117BD969F56C6F45B2204287807BA0F0', 5, NULL, NULL, 2, NULL, 0, 'Chop chicken and add to bowl with filling, mix.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56E34EB1782BA845B1FC1A57A5753C88', X'117BD969F56C6F45B2204287807BA0F0', X'117BD969F56C6F45B2204287807BA0F0', 6, NULL, NULL, 2, NULL, 0, 'Pour dressing into chicken bowl, mix.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D63708804F822842B5FFBC5454D5E8EB', X'117BD969F56C6F45B2204287807BA0F0', X'117BD969F56C6F45B2204287807BA0F0', 7, NULL, NULL, 2, NULL, 0, 'Take a spoon full of the chicken mixture and add it to each jumbo pasta shell.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB711183B56D3046BA3DBB68CACDB42E', X'117BD969F56C6F45B2204287807BA0F0', X'117BD969F56C6F45B2204287807BA0F0', 8, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE4EE83CFF9B25428C56CE857EC90EDB', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 0, NULL, NULL, 2, NULL, 0, 'In a sauce pan, add evaporated milk, 4 tablespoons butter, and sugar.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E015E2F093FB8345970FB5D29E20B63A', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 1, NULL, NULL, 2, NULL, 0, 'Heat and stir until sugar is dissolved and butter has melted. Once done, remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62329C3F5C2B2749ADD3F098CB9B829E', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 2, NULL, NULL, 2, NULL, 0, 'If needed, activate yeast according to packet.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'477717799AD4B1469BC365846522FB40', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 3, NULL, NULL, 2, NULL, 0, 'In mixing bowl, combine: 2 cups flour, yeast, eggs, salt, vanilla, orange extract, brandy, and water.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3134093B52FD3F4AB4C1A7FD9DC4C1A0', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 4, NULL, NULL, 2, NULL, 0, 'Mix together.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'071DF4D854425741BADAC52F5DEA78FB', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 5, NULL, NULL, 2, NULL, 0, 'Add in butter sugar mixture from step one, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78CCB98233CBE743BA1327BD0E7FC6A8', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 6, NULL, NULL, 2, NULL, 0, 'Slowly add in remaining 2 cups flour, knead until well mixed and dough is formed, about 10 minutes.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55943F5F49D35D43A5D2B227F6D6139D', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 7, NULL, NULL, 2, NULL, 0, 'Form into a ball and place in bowl, cover, and let rise until double in size, about 90 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F977B85A37A9AD41A27DFD8DF537D108', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 8, NULL, NULL, 2, NULL, 0, 'Once done, place dough ball on floured surface and divide into 3 parts.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ED62B9DDB85254693645ED2FEA7CF42', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 9, NULL, NULL, 2, NULL, 0, 'Take each part and roll out in the shape of a rectangle, so you will be left with three rectangle sheets that are all the same size, about 8 x 12.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'056E80011F80B9469AB99E128A8C93C5', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 10, NULL, NULL, 2, NULL, 0, 'Take some room temp butter and spread it out over the top of one of the rectangle dough sheets.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA3DC328390ED3478138133A4B16FAC1', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 11, NULL, NULL, 2, NULL, 0, 'Take some sugar and cover entire sheet, then sprinkle cinnamon over.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1F1E03B37C81343AC07EC564E4DBA3C', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 12, NULL, NULL, 2, NULL, 0, 'Place the 2nd dough sheet on top of it.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39F0B94B32F44A49B1DEB2F536DFE214', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 13, NULL, NULL, 2, NULL, 0, 'Add butter to the 2nd sheet, top with sugar and cinnamon (just like the 1st sheet), and place the final dough sheet on top of that, so you are left with 3 layers.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F8D484B624BB648860376486EC77042', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 14, NULL, NULL, 2, NULL, 0, 'Top the final layer with some melted butter only.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F3F896285A9F64FA24FA008F881F310', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 15, NULL, NULL, 2, NULL, 0, 'Now, take a sharp knife, and cut strips length wise, about 1 inch or so wide.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E8F66D9B7723147A61B7B48D0DCCE27', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 16, NULL, NULL, 2, NULL, 0, 'Take a strip, pinch the ends, and twist 2 or 3 times to create the classic `twist` look.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC177E8A6DAD8149A37F63B308735ECE', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 17, NULL, NULL, 2, NULL, 0, 'Place on baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF02F4C3A2AD3C42AC9803AA7C114337', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 18, NULL, NULL, 2, NULL, 0, 'Repeat until all strips are used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39DD6D87D4D4C543832A407BF5969A79', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 19, NULL, NULL, 2, NULL, 0, 'Brush each strip with melted butter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EFB25DEACCA9C49A0C56776FAFD40E7', X'200352C5701D8A4EB23974F114C4D939', X'200352C5701D8A4EB23974F114C4D939', 20, NULL, NULL, 2, NULL, 0, 'Place in oven at 400F for 15 minutes, or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B31C3EF53920448EBA10560F5D7C09', X'CDD89C2A0A874C43822021CC56E4663C', X'CDD89C2A0A874C43822021CC56E4663C', 0, NULL, NULL, 2, NULL, 0, 'Begin boiling your two cups of water and preheat oven to 350F');
