INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53C28BE568C8AD46828FBDD9B780EC7A', X'CDD89C2A0A874C43822021CC56E4663C', X'CDD89C2A0A874C43822021CC56E4663C', 2, NULL, NULL, 2, NULL, 0, 'Once onions are done, remove from pan and empt into bowl, set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57530A995422BE4BA8702976C785B17E', X'CDD89C2A0A874C43822021CC56E4663C', X'CDD89C2A0A874C43822021CC56E4663C', 3, NULL, NULL, 2, NULL, 0, 'Season lamb pieces with oregano, salt, and a pepper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8D25415726D41418E9498ABF6A4B9E8', X'CDD89C2A0A874C43822021CC56E4663C', X'CDD89C2A0A874C43822021CC56E4663C', 4, NULL, NULL, 2, NULL, 0, 'Add more oil to pan used for onions, and heat over medium-high heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C76E8E362E378641A89DC9B983081491', X'CDD89C2A0A874C43822021CC56E4663C', X'CDD89C2A0A874C43822021CC56E4663C', 5, NULL, NULL, 2, NULL, 0, 'Add lamb, brown, and cook for 5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F18E7BF7FEA3C14E83B9D6DE83E441B0', X'CDD89C2A0A874C43822021CC56E4663C', X'CDD89C2A0A874C43822021CC56E4663C', 6, NULL, NULL, 2, NULL, 0, 'When lamb is done, empty into oven safe bowl.');
