INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1466B6059BE73B468723905C44540FA0', X'CDD89C2A0A874C43822021CC56E4663C', X'CDD89C2A0A874C43822021CC56E4663C', 11, NULL, NULL, 2, NULL, 0, 'Place in oven and cook for 1 hour**');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0D09ABEFDC897458FDABD087BCBAE14', X'CDD89C2A0A874C43822021CC56E4663C', X'CDD89C2A0A874C43822021CC56E4663C', 12, NULL, NULL, 2, NULL, 0, 'A few minutes before lamb is done, empty yogurt into bowl, add egg, and mix well.');
