INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43CBCF90D03E12478182E0D48138FA69', X'CDD89C2A0A874C43822021CC56E4663C', X'CDD89C2A0A874C43822021CC56E4663C', 15, NULL, NULL, 2, NULL, 0, 'Cook for another 20 minutes, or until slight color forms on top');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A6EF2947673B842864D73CDC6C9C8CA', X'CDD89C2A0A874C43822021CC56E4663C', X'CDD89C2A0A874C43822021CC56E4663C', 16, NULL, NULL, 2, NULL, 0, 'Remove, cut, and serve!');
