INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'072129395C512A44B78AABF3C35E5C16', X'F66932842E7FAE4987F23B4C6217E293', X'F66932842E7FAE4987F23B4C6217E293', 2, NULL, NULL, 2, NULL, 0, 'During this time, make yogurt sauce');
