INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C80E6EBF953434A964F9EF26AD1CE47', X'ADBC8B191922264CAEF925E84D1D7D71', X'ADBC8B191922264CAEF925E84D1D7D71', 0, NULL, NULL, 2, NULL, 0, 'Add oil to pan, heat.');
