INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE9828EA9C6E3246BF00DF674E0394E1', X'ADBC8B191922264CAEF925E84D1D7D71', X'ADBC8B191922264CAEF925E84D1D7D71', 4, NULL, NULL, 2, NULL, 0, 'Grease baking dish with some butter*');
