INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6EFE343140C114DBB532E0FEE869F77', X'ADBC8B191922264CAEF925E84D1D7D71', X'ADBC8B191922264CAEF925E84D1D7D71', 10, NULL, NULL, 2, NULL, 0, 'Place in preheated oven at 350F for 40min, or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57A700AA0B3F934080E37AF4B163E20D', X'ADBC8B191922264CAEF925E84D1D7D71', X'ADBC8B191922264CAEF925E84D1D7D71', 11, NULL, NULL, 2, NULL, 0, 'Remove, cut, and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B06B3D4DB357984695D79BE92E43D32C', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 0, NULL, NULL, 2, NULL, 0, 'Slice off tops of each tomato, use a spoon and scoop out the insides (carefully to avoid piercing sides).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A576FAE5A23F0428E6E162E4ACB9E79', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 1, NULL, NULL, 2, NULL, 0, 'Add all insides from tomatoes into a food processor, and blend until a smooth puree, set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'122269B89DE28C4C850F68EF3E30F6A8', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 2, NULL, NULL, 2, NULL, 0, 'Add some oil to a pan and heat. Add meat and brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61F743AB42BD614694370A35CF40DD91', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 3, NULL, NULL, 2, NULL, 0, 'Once brown, add: onion, zucchini, rice, parsley, oregano, garlic, broth, tomato paste, salt and pepper, and ½ of tomato puree, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C13767F052556A4AA561CDC0196BFDAC', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 4, NULL, NULL, 2, NULL, 0, 'Simmer for 5 minutes, reducing a little.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EBB1FDA48D697448D6964B2EF978EE6', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 5, NULL, NULL, 2, NULL, 0, 'Align tomatoes in oven safe dishes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66C01289CB977141A8E749FD46395F21', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 6, NULL, NULL, 2, NULL, 0, 'Drizzle insides with oil and sprinkle some salt and pepper in each tomato.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4335B7DDFD7AE4408964C3FDC4E7C6E2', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 7, NULL, NULL, 2, NULL, 0, 'Add meat filling to each tomato. Spoon in as much as possible, leaving a slight gap remaining on top. Cover with tomato top.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01A0635DB4CF6B4DA62985307C87A840', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 8, NULL, NULL, 2, NULL, 0, 'Repeat for all tomatoes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6445ECA4CCD54C448A6102EBB4EB4B02', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 9, NULL, NULL, 2, NULL, 0, 'Add remaining tomato puree to pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5878D80BCBA0624FBD950FE8B988EBA5', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 10, NULL, NULL, 2, NULL, 0, 'Place in preheated oven at 350F for 1 hour or until rice is cooked and vegetables are soft.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A351A318A694E4DAFE89CD9F2B70419', X'13E3B66223625347B04CA215525AA6E2', X'13E3B66223625347B04CA215525AA6E2', 11, NULL, NULL, 2, NULL, 0, 'Plate and serve with a side of feta and some warm bread.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEF99A1DC2AD014E83FC1AD28942BE04', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 0, NULL, NULL, 2, NULL, 0, 'Add milk, lemon zest, and orange zest to pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0FE792EFB5E9E45ACD8796382CB983D', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 1, NULL, NULL, 2, NULL, 0, 'Heat over medium heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'925ED85475DEA94ABA1ECD59072489D8', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 2, NULL, NULL, 2, NULL, 0, 'Once it gets hot, not boiling, begin slowing adding the semolina, stirring constantly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B47457056BF4C45AC0014BD79B3817A', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 3, NULL, NULL, 2, NULL, 0, 'As soon as it starts to get thick, turn off heat and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5561E9A6C26CC643AFA819839E9D9804', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl, combine: eggs and egg yolks, vanilla, sugar, and brandy, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E56F5AA69EF5CC4D91D9E947DABC4730', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 5, NULL, NULL, 2, NULL, 0, 'Add to semolina mixture, mix until well combined.');
