INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76DF13657D2B604CB4B6896B1CECC97B', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 7, NULL, NULL, 2, NULL, 0, 'Grease baking dish with some of the melted butter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F347629E331B1446B2B9F67949DB6DA2', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 8, NULL, NULL, 2, NULL, 0, 'Remove phyllo from wrapper and cut phyllo sheets to fit baking pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6027FACACE001342AD5AF7E3D4DEAF65', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 9, NULL, NULL, 2, NULL, 0, 'Take one piece of phyllo and place it in baking pan, brush with melted butter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1608EC0EC295334BA00896A5D6FEE144', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 10, NULL, NULL, 2, NULL, 0, 'Repeat this 6 more times, brushing each sheet with butter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD51A1D78193F04895048A23A949DFA3', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 11, NULL, NULL, 2, NULL, 0, 'After 7 sheets, add semolina mixture and spread evenly, it''s ok if its thick.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'167EBE34673CE54A87C1C81B257CEE39', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 12, NULL, NULL, 2, NULL, 0, 'Repeat step 10, adding 7 more sheets over top.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFFDC99691177848A6D0E4DEB177D289', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 13, NULL, NULL, 2, NULL, 0, 'Brush top sheet with butter and place in preheated oven at 350F for 35-40 minutes, or until top is golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F768DDF39EACC848B437455433D1EF27', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 14, NULL, NULL, 2, NULL, 0, 'When finished, cut into squares, place a square on a plate, top with some ground cinnamon and powdered sugar.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EA3A0B6804F614AB07B329624F4647A', X'81EE4B900BF5284FB871A9A18D62F3B2', X'81EE4B900BF5284FB871A9A18D62F3B2', 15, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EF3E19B702FC7498E83ABAC7A2B0648', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 0, NULL, NULL, 2, NULL, 0, 'Add ground pistachios and ¼ cup of the sugar to a pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADC20555054E9445BD2CAB08995F34D6', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 1, NULL, NULL, 2, NULL, 0, 'Add milk and bring to boil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D94A86AA0D58C4C9EFE36F7D3886414', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 2, NULL, NULL, 2, NULL, 0, 'Add vanilla, stir and remove from heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DD0F7867AAF1E4199F6844452FF9C52', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 3, NULL, NULL, 2, NULL, 0, 'Add the 5 egg yolks and remaining ½ cup sugar to a bowl, and mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F029EA45925E1E41B3FBE2FFE3EB6EF3', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 4, NULL, NULL, 2, NULL, 0, 'Temper the egg yolks by taking a ladle''s worth of hot milk from the pan and pouring into the bowl with the eggs, constantly stirring.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A964F2E8EB66944942EC25CD24335B9', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 5, NULL, NULL, 2, NULL, 0, 'Add back to heat and cook for 7-10 minutes over medium-low heat, don''t let it boil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4C7654616591B4EB21C9BB54EC7F7E6', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 6, NULL, NULL, 2, NULL, 0, 'Once done, strain into bowl*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43E064952082664E866AD976A5CBF13A', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 7, NULL, NULL, 2, NULL, 0, 'Place in refrigerator to chill for 2 hours.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DD7FAB57872994FBE7C65D41FD023AD', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 8, NULL, NULL, 2, NULL, 0, 'Remove from fridge, and mix in heavy cream and the ½ cup of whole pistachios.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABDE6FC1A9EA16489F0EAC0159E464E2', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 9, NULL, NULL, 2, NULL, 0, 'Turn on ice cream maker, slowly pour mixture into ice cream maker and proceed as directed, churning for about 10-12 minutes .');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04BDA88B52EB6F449C0071A60C847CF2', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 10, NULL, NULL, 2, NULL, 0, 'Empty into freezer safe bowl and let chill for 2 hours (or as long as you can manage)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B7C3124D5D70E46B713249E053CAA10', X'2307106C896C0A42B2F1D3CE12DBE9D1', X'2307106C896C0A42B2F1D3CE12DBE9D1', 11, NULL, NULL, 2, NULL, 0, 'Remove from freezer and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29BE921647C7904199F6A7D88E10C4CC', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 0, NULL, NULL, 2, NULL, 0, 'Fill a soup pot ? of the way full with water, add some salt, and bring to boil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C6E16A58D1F7144B028D83E7A0A0645', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 1, NULL, NULL, 2, NULL, 0, 'Core both heads of cabbage using a sharp knife (be careful).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47DA8F44F610894EAD6E6C2A65464F0D', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 2, NULL, NULL, 2, NULL, 0, 'Add cabbage heads to pot (use more than one pot if needed).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46A6F33DD7068C4BA00172B7DA051E50', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 3, NULL, NULL, 2, NULL, 0, 'Stick a fork in the hole created by coring, this helps keep them down and is easy to pull out.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'794A16BF7E0808468BEE3E5E3CAA9F40', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 4, NULL, NULL, 2, NULL, 0, 'After about 5 minutes, begin shaking the fork (with a glove) to start having the outside cabbage layers come off.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0B1ED4F0C11794095962F72F05DFCFC', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 5, NULL, NULL, 2, NULL, 0, 'As the cabbage leaves fall off, remove them and add them to a strainer. Continue doing this until all leaves are removed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2742E805A21114EA6D1909DFD95765F', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 6, NULL, NULL, 2, NULL, 0, 'Once finished, empty water from pot, and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F50E5C3100885A4B812A405BFC631380', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 7, NULL, NULL, 2, NULL, 0, 'Run some cold water under the leaves to cool down and stop cooking.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACC060D6271CC04F8DB1DD9F74D980F9', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 8, NULL, NULL, 2, NULL, 0, 'Combine ingredients listed under the filling section in a bowl and mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA25F95834FCB34C9268EEC26B9AF215', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 9, NULL, NULL, 2, NULL, 0, 'Take a spoon size amount of the meat mixture and place it at the base of a cabbage leaf. Fold in sides, and roll up. Place in soup pot.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E13B6ACDE47E5A41A5735338BF08A521', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 10, NULL, NULL, 2, NULL, 0, 'Repeat until all meat or cabbage is used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9D26467F5B9C94AAD20BB8CA97E093D', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 11, NULL, NULL, 2, NULL, 0, 'Fill pot with remaining 10 cups of chicken broth.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23728AAE50F3674BA4F6A6043EA73CE1', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 12, NULL, NULL, 2, NULL, 0, 'Bring to a boil, cover and reduce heat to simmer.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B3743AECCBEDC4A8DC9CC839F7B8A6F', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 13, NULL, NULL, 2, NULL, 0, 'Simmer for 45 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'311D1CAE124E3342B0837834AB77159F', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 14, NULL, NULL, 2, NULL, 0, '-Avgolemono-');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'946FE5F4A5C149408433F2C4E9C130C5', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 15, NULL, NULL, 2, NULL, 0, 'With 5 minutes left, crack 3 eggs into a bowl.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B43B39AAA90B5644A6C067D2C67CE73D', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 16, NULL, NULL, 2, NULL, 0, 'Beat until froth, by hand about 10 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC6BBEA623F67749A7863BA7923AE616', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 17, NULL, NULL, 2, NULL, 0, 'Add lemon juice.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E28251D6924434C899A91764569E56B', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 18, NULL, NULL, 2, NULL, 0, 'While stirring, slowly temper the eggs by adding 1 cups worth of hot broth to the bowl. Mix, and empty everything back into the soup pot with the cabbage.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD8E733E6853824DA837B7C7C181B29B', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 19, NULL, NULL, 2, NULL, 0, 'Cook for another minute or so.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'885DC60E0542CD4490C6955FAE8F9BBE', X'99E70D86DE982E4CB319C409A26FB9F5', X'99E70D86DE982E4CB319C409A26FB9F5', 20, NULL, NULL, 2, NULL, 0, 'Remove from heat and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'321FD0DA6A2CE047A4D036783A80755F', X'CA42EC25374F9B43800DB20DEDEF8170', X'CA42EC25374F9B43800DB20DEDEF8170', 0, NULL, NULL, 2, NULL, 0, 'Heat some oil in a large pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46FEFDBB0F0B7B4CBF345ED01AFAF7A3', X'CA42EC25374F9B43800DB20DEDEF8170', X'CA42EC25374F9B43800DB20DEDEF8170', 1, NULL, NULL, 2, NULL, 0, 'Add diced onion, and cook until translucent, about 5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C0502F22B5A0E4AA66C14BD19392ECC', X'CA42EC25374F9B43800DB20DEDEF8170', X'CA42EC25374F9B43800DB20DEDEF8170', 2, NULL, NULL, 2, NULL, 0, 'Add: orzo, diced tomatoes, chicken stock, garlic, oregano, salt and pepper, and brandy if using, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52573FD8CC0F3944A566FF3EB5C3A179', X'CA42EC25374F9B43800DB20DEDEF8170', X'CA42EC25374F9B43800DB20DEDEF8170', 3, NULL, NULL, 2, NULL, 0, 'Bring to boil, reduce heat, cover and simmer for 7-10 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1D3E55BD00AC744B47DC8E939C85D8D', X'CA42EC25374F9B43800DB20DEDEF8170', X'CA42EC25374F9B43800DB20DEDEF8170', 4, NULL, NULL, 2, NULL, 0, 'Add chicken to pan, cover and continue simmering for another 20-25 minutes, until chicken is cooked and orzo is finished.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A2585D98B976E4282CC825311710815', X'CA42EC25374F9B43800DB20DEDEF8170', X'CA42EC25374F9B43800DB20DEDEF8170', 5, NULL, NULL, 2, NULL, 0, 'With 5 minutes left, add olives.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16996709D63A4D48ACC434BD6132186D', X'CA42EC25374F9B43800DB20DEDEF8170', X'CA42EC25374F9B43800DB20DEDEF8170', 6, NULL, NULL, 2, NULL, 0, 'Once done, remove from heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2948041F0423EB4B9E3C720B2C1D94A4', X'CA42EC25374F9B43800DB20DEDEF8170', X'CA42EC25374F9B43800DB20DEDEF8170', 7, NULL, NULL, 2, NULL, 0, 'Plate chicken, and then a spoonful of tomato orzo mixture over.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40AB656CD1F7F343949A6F630D1C6BCD', X'CA42EC25374F9B43800DB20DEDEF8170', X'CA42EC25374F9B43800DB20DEDEF8170', 8, NULL, NULL, 2, NULL, 0, 'Top with feta and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3AA97BACEDAB84982B438A27DECA0B5', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine flour, baking powder, and sugar, mix.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'469CE763D1D4294EA2768B7A0932301D', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 1, NULL, NULL, 2, NULL, 0, 'Add in all remaining ingredients under dough sections in order listed, one at a time.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D460F7022CEE1469BAFFDB4BEADAB8D', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 2, NULL, NULL, 2, NULL, 0, 'Mix until all ingredients are combined then place on floured surface and knead until soft dough is formed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B77B14414583184F9C2861E91417AA28', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 3, NULL, NULL, 2, NULL, 0, 'Set dough aside. Start on filling.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F8F45602E90C04384935FBC68161631', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 4, NULL, NULL, 2, NULL, 0, 'In a bowl, add all filling ingredients, mix until well combined.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68D29A6D062D3942A79A8EC133195B34', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 5, NULL, NULL, 2, NULL, 0, 'Take dough ball and place on floured surface, roll out to rectangle, about ½ inch thick.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F123495CF85F9D40891D7091F5B9151E', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 6, NULL, NULL, 2, NULL, 0, 'Taking a cup, create circle cutouts in the dough. Remove remaining dough so you are left with just the circle cutouts.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89634003D3F24E4AACA3E60D3DC4506F', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 7, NULL, NULL, 2, NULL, 0, 'Add 1 tablespoon of cheese filling to center of each circle, and either fold over to close, or pinch sides to create shapes - it''s up to you here, be creative.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8CE1A3226E171439286DD91880E558F', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 8, NULL, NULL, 2, NULL, 0, 'Place on baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CADD45D5245C2468048964E2FAB76CF', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 9, NULL, NULL, 2, NULL, 0, 'Roll remaining dough back out and repeat until all dough is used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C17935FB1487A46857F287A33744DA8', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 10, NULL, NULL, 2, NULL, 0, 'Once done, brush each cheese pie with egg wash (combine egg and water, mix).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6D3B4A2DE259E4BA19E0B6B32DFC334', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 11, NULL, NULL, 2, NULL, 0, 'Place in preheated oven at 350F for 20-25 minutes, or until golden brown.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34BAFED93FC9A545AADE5F660DEBFFED', X'E96E166B78295842AC553105FC22A121', X'E96E166B78295842AC553105FC22A121', 12, NULL, NULL, 2, NULL, 0, 'Remove, sprinkle with cinnamon. Enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE67C6BF271675498964BCDA17D3DC03', X'57C64AE471C90C47A7BB94E1DFF23B6F', X'57C64AE471C90C47A7BB94E1DFF23B6F', 0, NULL, NULL, 2, NULL, 0, 'Melt butter in pan, add rice and mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CC27ECBE6AD5D4BA0686BA11DB8511F', X'57C64AE471C90C47A7BB94E1DFF23B6F', X'57C64AE471C90C47A7BB94E1DFF23B6F', 1, NULL, NULL, 2, NULL, 0, 'Cook until rice turns opaque, about 3-4 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5516C21EC66474F86879F04B67E0378', X'57C64AE471C90C47A7BB94E1DFF23B6F', X'57C64AE471C90C47A7BB94E1DFF23B6F', 2, NULL, NULL, 2, NULL, 0, 'Add chicken 2 cups broth, bring to boil, reduce heat cover and simmer for 15 minutes, or until liquid is absorbed.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E730C1128ED17240ADD11A3A68BCCCAA', X'57C64AE471C90C47A7BB94E1DFF23B6F', X'57C64AE471C90C47A7BB94E1DFF23B6F', 3, NULL, NULL, 2, NULL, 0, 'When rice is done, mix crushed saffron with ¼ cup chicken broth.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3225003C13F034EAAE057E76DE76673', X'57C64AE471C90C47A7BB94E1DFF23B6F', X'57C64AE471C90C47A7BB94E1DFF23B6F', 4, NULL, NULL, 2, NULL, 0, 'Pour over rice and mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0873FE67FAB2B64D8AFF30DE3332ACA7', X'57C64AE471C90C47A7BB94E1DFF23B6F', X'57C64AE471C90C47A7BB94E1DFF23B6F', 5, NULL, NULL, 2, NULL, 0, 'Remove from heat, cover with towel, and place lid over towel.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C53B2E2EE2EE314C9F880B0A97BFF10E', X'57C64AE471C90C47A7BB94E1DFF23B6F', X'57C64AE471C90C47A7BB94E1DFF23B6F', 6, NULL, NULL, 2, NULL, 0, 'Let sit for 5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99DD071E850AD9409B76C66013D3DC6E', X'57C64AE471C90C47A7BB94E1DFF23B6F', X'57C64AE471C90C47A7BB94E1DFF23B6F', 7, NULL, NULL, 2, NULL, 0, 'Serve topped with feta!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CCA7882CF6B744EA5FC64CDDE31876D', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400F.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F946183DDB80E4BA3538E6917AD87D5', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 1, NULL, NULL, 2, NULL, 0, 'Cut off top of garlic head, and place on sheet of aluminum foil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'480721098C37E943B92E2384E207CDA0', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 2, NULL, NULL, 2, NULL, 0, 'Drizzle with olive oil, and wrap in foil, sealing tightly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB64EE6B5346244BA129AAB29379C468', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 3, NULL, NULL, 2, NULL, 0, 'Place in oven for 30 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB0748452829AB48BF944342FD34A73A', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 4, NULL, NULL, 2, NULL, 0, 'When done, remove, and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A454765631FECB4D9658C7F8D6B51091', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 5, NULL, NULL, 2, NULL, 0, 'Once you take garlic out of oven, prepare the aioli.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A827EA521F1CCD4BB33FC9EB18CBCC68', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 6, NULL, NULL, 2, NULL, 0, 'Empty yogurt into bowl.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13BEA2F2FA487840AE027F50EC4E55D0', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 7, NULL, NULL, 2, NULL, 0, 'Add finely chopped kalamata olives, lemon juice, press garlic clove, salt, pepper, and feta if using.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6B6DB31361F2246BC8F3AE18280AE2A', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 8, NULL, NULL, 2, NULL, 0, 'Open foil with garlic head in it.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40641E83AACE3A4ABB6C01EE39B4899F', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 9, NULL, NULL, 2, NULL, 0, 'You should be able to simply squeeze out the garlic from within the cloves, take 2 tablespoons worth and add it to yogurt bowl.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0F9D239BCE37B419FA826F9D3B2AD6D', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 10, NULL, NULL, 2, NULL, 0, 'Mix everything together.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E338BBD7D3BF748BE6CF8254AE8E6EA', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 11, NULL, NULL, 2, NULL, 0, 'Taste and see if more salt/pepper is needed (or any other item).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CC2504EEAD71C44925C837779915F6D', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 12, NULL, NULL, 2, NULL, 0, 'Let chill in refrigerator for 30 minutes or so.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02559A834D71AA4586FCA2326C8B5F84', X'ADE2D292D7402D4A94F349FEDF365D97', X'ADE2D292D7402D4A94F349FEDF365D97', 13, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'715C848460AA3F458DDE304EAC4BBD8E', X'D4CB6AF51DA0D44F908AD83A35903C14', X'D4CB6AF51DA0D44F908AD83A35903C14', 0, NULL, NULL, 2, NULL, 0, 'Peel and cut onion into slices.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47173D2AD788614A9C52F5827DF01DE9', X'D4CB6AF51DA0D44F908AD83A35903C14', X'D4CB6AF51DA0D44F908AD83A35903C14', 1, NULL, NULL, 2, NULL, 0, 'Melt butter in pan, add onions, and mix to coat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD0FCC0B5C526D4BAA4CACD2BE23E605', X'D4CB6AF51DA0D44F908AD83A35903C14', X'D4CB6AF51DA0D44F908AD83A35903C14', 2, NULL, NULL, 2, NULL, 0, 'Cook for 15 minutes and then add sugar.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9CD9538E914CB46B29DE5B5F10D8217', X'D4CB6AF51DA0D44F908AD83A35903C14', X'D4CB6AF51DA0D44F908AD83A35903C14', 3, NULL, NULL, 2, NULL, 0, 'Continuing cooking for another 15 to 20 minutes, or until caramelized.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A0C11E3D4CFF6479DF4DF0573A67F4E', X'D4CB6AF51DA0D44F908AD83A35903C14', X'D4CB6AF51DA0D44F908AD83A35903C14', 4, NULL, NULL, 2, NULL, 0, 'Once done, add Metaxa brandy and cook for 1 minute, or until liquid is gone.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A0A5EC72CBCED4AA9D2778235A74E09', X'D4CB6AF51DA0D44F908AD83A35903C14', X'D4CB6AF51DA0D44F908AD83A35903C14', 5, NULL, NULL, 2, NULL, 0, 'Remove from heat, put on top of your favorite dish, and serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'752129E0D0ADC04AB5DD2692E5F38FC8', X'E8DAE997C278EB4CA8C9FE5222479731', X'E8DAE997C278EB4CA8C9FE5222479731', 0, NULL, NULL, 2, NULL, 0, 'Add syrup to glass.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'600B4C6A43903F4E84DC2BB865D5FDA6', X'E8DAE997C278EB4CA8C9FE5222479731', X'E8DAE997C278EB4CA8C9FE5222479731', 1, NULL, NULL, 2, NULL, 0, 'Add ice cubes to water, stir to chill.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'422D9B3E17718F41BEAD01F7BC34A6CC', X'E8DAE997C278EB4CA8C9FE5222479731', X'E8DAE997C278EB4CA8C9FE5222479731', 2, NULL, NULL, 2, NULL, 0, 'Remove ice cubes and pour cold water into glass with syrup, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6673D42B161D584FA0630D11C8ED9C35', X'E8DAE997C278EB4CA8C9FE5222479731', X'E8DAE997C278EB4CA8C9FE5222479731', 3, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4B815A33FE9F94A98A338025BC1AB33', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 0, NULL, NULL, 2, NULL, 0, 'In a pan, layer cherries and sugar: add ½ of the cherries, 1 cup sugar, remaining cherries, and remaining sugar.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5CD51818C626B4EB4A993B1FBEA719C', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 1, NULL, NULL, 2, NULL, 0, 'Pour water over cherries/sugar mixture.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D4A91BD84F3354DB9182074E0BA00BB', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 2, NULL, NULL, 2, NULL, 0, 'Place in refrigerator overnight.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44A6DC0744058A48B3910112585CAD0F', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 3, NULL, NULL, 2, NULL, 0, 'Remove, gently stir to mix everything together and make sure sugar is dissolved.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EAEC961067ED24FA1A282DED2D94748', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 4, NULL, NULL, 2, NULL, 0, 'Bring to boil over high heat, then reduce heat to simmer.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3278C9AF5981A9459C6089EE65D98662', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 5, NULL, NULL, 2, NULL, 0, 'Simmer for 25 minutes, scooping off foam with a slotted spoon**');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E260FFACDF7B3E4D9933F72350BC41FE', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 6, NULL, NULL, 2, NULL, 0, 'At the 25 minutes mark, add in vanilla extract.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AED5CC3E4606F14FB9F0CEF4EE882EAA', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 7, NULL, NULL, 2, NULL, 0, 'Continuing simmering for another 10-15 minutes, or until it passed the spoon test (see above).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF79C7A64AF087419FEE51488C9436D7', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 8, NULL, NULL, 2, NULL, 0, 'When done, add lemon juice, simmer for 1 minute, turn off heat, and let cool.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A865CD957F59A9499E3802416133C3CC', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 9, NULL, NULL, 2, NULL, 0, 'Once cool, pour into mason jar or whatever you''re using to store.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EEB106F1C4ECE4393E0BA80828EF645', X'C058FBB40F6B834199AFCBEC5DA2CDC4', X'C058FBB40F6B834199AFCBEC5DA2CDC4', 10, NULL, NULL, 2, NULL, 0, 'You''re now ready to serve. Spoon some onto a plate and enjoy!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDDD954B3CFF2D43980E9CA8F758780E', X'13996A4875B73744A3AC08EF4BC5F847', X'13996A4875B73744A3AC08EF4BC5F847', 0, NULL, NULL, 2, NULL, 0, 'Add diced tomatoes to mixing bowl.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A97D4C80AB6E94EAC123878B5B321D8', X'13996A4875B73744A3AC08EF4BC5F847', X'13996A4875B73744A3AC08EF4BC5F847', 1, NULL, NULL, 2, NULL, 0, 'Add diced onions, oregano, thyme, parsley, garlic, salt and pepper, mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D34F257E9A7C6439028CB91BE93E72F', X'13996A4875B73744A3AC08EF4BC5F847', X'13996A4875B73744A3AC08EF4BC5F847', 2, NULL, NULL, 2, NULL, 0, 'Add 1 cup flour, mix well and place in fridge for 30 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71CA8A74596C46458BBA32EAFA0429FB', X'13996A4875B73744A3AC08EF4BC5F847', X'13996A4875B73744A3AC08EF4BC5F847', 3, NULL, NULL, 2, NULL, 0, 'Remove and add remaining flour little by little. You want it to resemble a pancake style batter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD49B31A17D2F249A8B5C2631D743825', X'13996A4875B73744A3AC08EF4BC5F847', X'13996A4875B73744A3AC08EF4BC5F847', 4, NULL, NULL, 2, NULL, 0, 'If using bread crumbs, mix in now. This is optional and gives it an extra crunch.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E3E650ABC35B74B98A35F21C52E91F1', X'13996A4875B73744A3AC08EF4BC5F847', X'13996A4875B73744A3AC08EF4BC5F847', 5, NULL, NULL, 2, NULL, 0, 'Heat oil in pan and add one spoonful of the tomato mixture to create one fritter.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57D26BDDF2EFBB47A0249FEB7EDF8015', X'13996A4875B73744A3AC08EF4BC5F847', X'13996A4875B73744A3AC08EF4BC5F847', 6, NULL, NULL, 2, NULL, 0, 'Cook 2-3 minutes on each side, remove and place on a plate lined with a paper towel to absorb excess oil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AE4709B0BC3344782587E57B999A4E4', X'13996A4875B73744A3AC08EF4BC5F847', X'13996A4875B73744A3AC08EF4BC5F847', 7, NULL, NULL, 2, NULL, 0, 'Repeat until all mixture is used.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1A20E9E2B0D464E96D1F1963E3343BC', X'13996A4875B73744A3AC08EF4BC5F847', X'13996A4875B73744A3AC08EF4BC5F847', 8, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3EB7A2254D544478539E0A27ADDF3A6', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients listed under syrup in pan and bring to boil. Mix well and simmer for 10 minutes. Sugar will dissolve and you''ll be left with a clear liquid.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21C690779539E54AB86B0B4E6A9D2539', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 1, NULL, NULL, 2, NULL, 0, 'Once 10 minutes have passed turn off heat and set aside.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10304CB5F3808B4383BC20CE39A501DD', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 2, NULL, NULL, 2, NULL, 0, 'In a soup pot add olive oil and semolina.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81C662F75434C14A97400C0C4B6A0639', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 3, NULL, NULL, 2, NULL, 0, 'Cook for about 7-10 minutes, until a nice golden color is achieved, stir continuously during entire cooking time.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'292C84D85F4AB24A8B27C96BFDE83104', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 4, NULL, NULL, 2, NULL, 0, 'Once the semolina has a nice golden color, remove from heat.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1570409CE5B174C90CB3B8AE18C8CC5', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 5, NULL, NULL, 2, NULL, 0, 'Remove cinnamon sticks and lemon peels from syrup.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20C70CEDBABFE3438D057D3A80A5BF4F', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 6, NULL, NULL, 2, NULL, 0, 'Pour syrup into pot with flour, be careful as it may splatter, and steam up.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B0FFF9DB76F334987A8B216072A44EE', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 7, NULL, NULL, 2, NULL, 0, 'Add in chopped nuts.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37E745753E25014980A29E5509983124', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 8, NULL, NULL, 2, NULL, 0, 'Stir continuously for 1 to 2 minutes, until everything is well incorporated.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BBBCDE92CCBCE4B8CD5F2CDED213BBA', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 9, NULL, NULL, 2, NULL, 0, 'Line bundt pan (or other cake pan) with olive oil, and sprinkle with ground cinnamon.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77748EC908A85747B1E0EB17D1B26922', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 10, NULL, NULL, 2, NULL, 0, 'Spoon in semolina batter and spread evenly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A6BF7E11B96EF47B2CCC8BD004AEADA', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 11, NULL, NULL, 2, NULL, 0, 'Let cool at room temperature for about 1-2 hours.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81707A27860E4B42A670BDD9B0E3F841', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 12, NULL, NULL, 2, NULL, 0, 'When ready, flip bundt pan over, sprinkle with more ground cinnamon.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C83DAF00D138CF40A6DBC096410ED6F3', X'C1479743674A634F814C3137F0B1758B', X'C1479743674A634F814C3137F0B1758B', 13, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6051024D3FE5848AD3F2C81487A81C8', X'D71A0F4AF05BDD46A40A866F793C12F5', X'D71A0F4AF05BDD46A40A866F793C12F5', 0, NULL, NULL, 2, NULL, 0, 'Bring some salted water to a boil in a sauce pan.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE50E5ABA7F1714291F37762778B966B', X'D71A0F4AF05BDD46A40A866F793C12F5', X'D71A0F4AF05BDD46A40A866F793C12F5', 1, NULL, NULL, 2, NULL, 0, 'Add sliced zucchinis, and cook for 3 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F085B29688B044C80FC94F1169A3B78', X'D71A0F4AF05BDD46A40A866F793C12F5', X'D71A0F4AF05BDD46A40A866F793C12F5', 2, NULL, NULL, 2, NULL, 0, 'Remove, drain, and run cold water over them to stop them from cooking.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CFEB0F5BFE3F0418CE48F4162839548', X'D71A0F4AF05BDD46A40A866F793C12F5', X'D71A0F4AF05BDD46A40A866F793C12F5', 3, NULL, NULL, 2, NULL, 0, 'Arrange them in oven safe dish.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'310868DA78AB0943A7C6B1BC995BCD50', X'D71A0F4AF05BDD46A40A866F793C12F5', X'D71A0F4AF05BDD46A40A866F793C12F5', 4, NULL, NULL, 2, NULL, 0, 'In mixing bowl, combine: greek yogurt, eggs, feta, and garlic - mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D666022F1858BD46B7EA6443D96C65DC', X'D71A0F4AF05BDD46A40A866F793C12F5', X'D71A0F4AF05BDD46A40A866F793C12F5', 5, NULL, NULL, 2, NULL, 0, 'Pour mixture over zucchini slices, spread out evenly.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45EDE538A3AA0B4888C6FF52F5C21D32', X'D71A0F4AF05BDD46A40A866F793C12F5', X'D71A0F4AF05BDD46A40A866F793C12F5', 6, NULL, NULL, 2, NULL, 0, 'Top with some extra feta (optional)');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7858236FFEA2C46B41AFFE798714B59', X'D71A0F4AF05BDD46A40A866F793C12F5', X'D71A0F4AF05BDD46A40A866F793C12F5', 7, NULL, NULL, 2, NULL, 0, 'Place in preheated oven at 325F for 45 minutes, or until zucchini is soft.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAD868D97BAEE2489B257105D507B4FC', X'D71A0F4AF05BDD46A40A866F793C12F5', X'D71A0F4AF05BDD46A40A866F793C12F5', 8, NULL, NULL, 2, NULL, 0, 'Remove and serve hot.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44C7028EE648784380CBECEED14B916A', X'8AD8DAC81422374D9AED82C7C2745372', X'8AD8DAC81422374D9AED82C7C2745372', 0, NULL, NULL, 2, NULL, 0, 'Add onions and garlic to an oiled pan. Cook until onions are soft, about 5 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02885BB9E8A8D04D9026F2825BF0682C', X'8AD8DAC81422374D9AED82C7C2745372', X'8AD8DAC81422374D9AED82C7C2745372', 1, NULL, NULL, 2, NULL, 0, 'Add ground beef and cook for another 10 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DAF495381394742A81A00E7E2083F7A', X'8AD8DAC81422374D9AED82C7C2745372', X'8AD8DAC81422374D9AED82C7C2745372', 2, NULL, NULL, 2, NULL, 0, 'Next add: zucchini, chicken broth, thyme, oregano, parsley, and some salt and pepper. Cover and simmer for 30 minutes or until zucchini is soft.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D58A969E0804C4A9FB315F5C91AA262', X'8AD8DAC81422374D9AED82C7C2745372', X'8AD8DAC81422374D9AED82C7C2745372', 3, NULL, NULL, 2, NULL, 0, 'When done, using a slotted spoon, take meat mixture and spoon into a mixing bowl to cool.*');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7B60C75D04E35498C768B35F4AC273B', X'8AD8DAC81422374D9AED82C7C2745372', X'8AD8DAC81422374D9AED82C7C2745372', 4, NULL, NULL, 2, NULL, 0, 'Beat eggs in separate bowl and pour into meat mixture, add feta, and mix well.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEB016DCB43CF14985786D95D51D785F', X'8AD8DAC81422374D9AED82C7C2745372', X'8AD8DAC81422374D9AED82C7C2745372', 5, NULL, NULL, 2, NULL, 0, 'Pour into an oven safe dish.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D7AA8885D509240A9F9120BD4649452', X'8AD8DAC81422374D9AED82C7C2745372', X'8AD8DAC81422374D9AED82C7C2745372', 6, NULL, NULL, 2, NULL, 0, 'Top with more feta (optional).');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E27D1D920C4D148840DDCACC814C7AB', X'8AD8DAC81422374D9AED82C7C2745372', X'8AD8DAC81422374D9AED82C7C2745372', 7, NULL, NULL, 2, NULL, 0, 'Place in preheated oven at 350F for 25-30 minutes, or until eggs are cooked and dish is set.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'723E240614E7E74BB2CECA4912420E91', X'8AD8DAC81422374D9AED82C7C2745372', X'8AD8DAC81422374D9AED82C7C2745372', 8, NULL, NULL, 2, NULL, 0, 'Serve warm!');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4816CC47D0EA5943A02EB831F9D32390', X'A8DC137EE49CC044BF51EC3828198C9D', X'A8DC137EE49CC044BF51EC3828198C9D', 0, NULL, NULL, 2, NULL, 0, 'Rinse quinoa under cold water and place in pot.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD07DE1DC5C6B046A5B0A22095480BDD', X'A8DC137EE49CC044BF51EC3828198C9D', X'A8DC137EE49CC044BF51EC3828198C9D', 1, NULL, NULL, 2, NULL, 0, 'Add chicken broth and bring to boil.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81FD5BEFBF3121418D153F6B543020D6', X'A8DC137EE49CC044BF51EC3828198C9D', X'A8DC137EE49CC044BF51EC3828198C9D', 2, NULL, NULL, 2, NULL, 0, 'Reduce heat, cover, and simmer for 15 minutes.');
INSERT INTO `VWR INTERNAL Lemonandolives recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C030FB786FAE4F4997CA077E8A95BB78', X'A8DC137EE49CC044BF51EC3828198C9D', X'A8DC137EE49CC044BF51EC3828198C9D', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and set aside for 5 minutes.');
